import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_16" tabindex="-1"><a class="header-anchor" href="#_16" aria-hidden="true">#</a> 16</h1><p>\u970D\u72C2\u7130\u8138\u8272\u7070\u6697\uFF0C\u65E0\u590D\u5F80\u65E5\u7684\u56A3\u5F20\u3002\u51E0\u540D\u706B\u5802\u5E2E\u4F17\u62AC\u7740\u4ED6\u9001\u5230\u9A6C\u8F66\u4E0A\uFF0C\u695A\u8FDE\u96C4\u548C\u4E00\u4E2A\u9887\u6709\u51E0\u5206\u59FF\u8272\u7684\u6A59\u886B\u5973\u5B50\u8BDA\u60F6\u8BDA\u6050\u8DDF\u5728\u540E\u9762\u3002\u65C1\u8FB9\u4E24\u540D\u7537\u5973\u8EAB\u7740\u9ED1\u8863\uFF0C\u867D\u7136\u9762\u8272\u8083\u7136\uFF0C\u4F46\u773C\u4E2D\u90FD\u6D41\u9732\u51FA\u51E0\u5206\u4E0D\u5C51\u3002</p><p>\u8EAB\u7740\u6A59\u886B\u7684\u4F55\u5C0F\u82B8\u6084\u6084\u628A\u4E00\u4E2A\u5C0F\u5305\u585E\u5230\u70C8\u7130\u624B\u4E2D\uFF0C\u70C8\u7130\u7EF7\u7740\u8138\u628A\u970D\u957F\u8001\u7684\u9633\u5177\u653E\u8FDB\u8F66\u53A2\uFF0C\u53E6\u4E00\u53EA\u624B\u5374\u5728\u5C0F\u82B8\u5706\u81C0\u4E0A\u91CD\u91CD\u634F\u4E86\u4E00\u628A\u3002\u9ED1\u8863\u5973\u5B50\u770B\u5230\u8FD9\u4E00\u5E55\uFF0C\u5DEE\u70B9\u513F\u7B11\u4E86\u51FA\u6765\uFF0C\u8FDE\u5FD9\u626D\u8138\u8F7B\u54B3\u4E00\u58F0\u63A9\u9970\u8FC7\u53BB\u3002</p><p>\u300C\u7384\u51B0\uFF0C\u5BAB\u4E3B\u600E\u4E48\u8BF4\u7684\uFF1F\u300D\u970D\u72C2\u7130\u5728\u8F66\u91CC\u54D1\u58F0\u95EE\u9053\u3002</p><p>\u300C\u5BAB\u4E3B\u5429\u5490\uFF0C\u65E0\u8BBA\u662F\u5426\u64D2\u5230\u6155\u5BB9\u7D2B\u73AB\uFF0C\u4E94\u884C\u8BF8\u5802\u957F\u8001\u3001\u9999\u4E3B\u56DB\u6708\u521D\u4E09\u5FC5\u987B\u8FD4\u56DE\u795E\u6BBF\u3002\u300D\u9ED1\u8863\u7537\u5B50\u7B54\u9053\u3002</p><p>\u300C\u51E0\u4F4D\u62A4\u6CD5\u5462\uFF1F\u300D</p><p>\u300C\u53F6\u62A4\u6CD5\u4ECD\u5728\u5BAB\u4E2D\uFF0C\u6731\u90AA\u62A4\u6CD5\u548C\u5C48\u62A4\u6CD5\u5C5E\u4E0B\u4E0D\u77E5\u3002\u300D</p><p>\u970D\u72C2\u7130\u677E\u4E86\u53E3\u6C14\uFF0C\u53EA\u8981\u53F6\u884C\u5357\u5728\u5BAB\u91CC\u5C31\u6709\u6551\u4E86\u3002\u4ED6\u66FE\u4EB2\u773C\u89C1\u8FC7\u53F6\u62A4\u6CD5\u5C06\u4E00\u4E2A\u5973\u4EBA\u56DB\u80A2\u780D\u65AD\uFF0C\u628A\u817F\u63A5\u5230\u80A9\u4E0A\u3002\u8FD9\u7B49\u5077\u5929\u6362\u65E5\u7684\u672C\u9886\uFF0C\u63A5\u4E0A\u81EA\u5DF1\u7684\u9633\u5177\u53EA\u662F\u5C0F\u4E8B\u4E00\u6869\u3002\u4ED6\u6709\u6C14\u65E0\u529B\u5730\u626C\u4E86\u626C\u624B\uFF0C\u9A6C\u8F66\u7F13\u7F13\u542F\u7A0B\u3002\u70C8\u7130\u3001\u7384\u51B0\u7B49\u4EBA\u968F\u884C\u800C\u51FA\u3002</p><p>\u98CE\u665A\u534E\u51B7\u51B7\u770B\u4E86\u795E\u8272\u59D4\u9761\u7684\u695A\u8FDE\u96C4\u4E00\u773C\uFF0C\u5F53\u65E5\u4F0F\u9F99\u6DA7\u88AB\u88AD\uFF0C\u591A\u534A\u5C31\u662F\u4ED6\u901A\u98CE\u62A5\u8BAF\u3002\u4F46\u6B64\u65F6\u8FFD\u8E2A\u970D\u72C2\u7130\u8981\u7D27\uFF0C\u56DE\u5934\u518D\u627E\u4ED6\u62A5\u4EC7\u3002</p><p>\u98CE\u665A\u534E\u907F\u8FC7\u767D\u6C99\u6D3E\u5DE1\u903B\u5F1F\u5B50\uFF0C\u8D8A\u5899\u800C\u51FA\u3002\u91CC\u8BB8\u5BBD\u7684\u6C60\u5858\u4E00\u6643\u800C\u8FC7\uFF0C\u5F85\u5954\u5230\u5CB8\u8FB9\uFF0C\u5979\u5FFD\u7136\u505C\u4E86\u4E0B\u6765\uFF0C\u4E00\u811A\u8E0F\u5728\u7FE0\u7EFF\u7684\u8377\u6897\u4E0A\uFF0C\u957F\u5251\u7AD6\u5728\u80CC\u540E\uFF0C\u968F\u98CE\u8F7B\u8F7B\u6447\u52A8\u3002</p><p>\u6C34\u9762\u7EC6\u6CE2\u7CBC\u7CBC\uFF0C\u6620\u51FA\u4E00\u4E2A\u4ED9\u5B50\u51CC\u6CE2\u822C\u7684\u4F18\u7F8E\u5012\u5F71\u3002\u98CE\u665A\u534E\u4F38\u51FA\u7EC6\u767D\u7684\u624B\u6307\u64A9\u4E86\u64A9\u79C0\u53D1\uFF0C\u7389\u5BB9\u606C\u6DE1\u81EA\u82E5\uFF0C\u50CF\u89C2\u8D4F\u98CE\u666F\u822C\u60A0\u7136\u770B\u7740\u6C34\u9762\u3002</p><p>\u4E0D\u591A\u65F6\uFF0C\u6C60\u6C34\u5FAE\u5FAE\u4E00\u52A8\uFF0C\u4E00\u4E2A\u4EBA\u5F71\u76F4\u76F4\u4ECE\u6C34\u5E95\u5347\u8D77\uFF0C\u5148\u662F\u4E4C\u4EAE\u7684\u5934\u53D1\uFF0C\u7136\u540E\u6D01\u767D\u79C0\u7F8E\u7684\u9762\u5B54\u3001\u66F2\u7EBF\u73B2\u73D1\u7684\u8EAB\u6750\u6D6E\u73B0\u3002\u5F85\u819D\u76D6\u9732\u51FA\u6C34\u9762\uFF0C\u90A3\u5973\u5B50\u8F7B\u8F7B\u4E00\u7EB5\uFF0C\u843D\u5728\u98CE\u665A\u534E\u5BF9\u9762\u7684\u8377\u53F6\u4E0A\u3002\u6676\u83B9\u7684\u6C34\u73E0\u5E26\u7740\u6D41\u6DCC\u7684\u9633\u5149\u4ECE\u9ED1\u8272\u4E1D\u886B\u4E0A\u4E0D\u4F4F\u6EDA\u843D\uFF0C\u50CF\u4E00\u4E32\u660E\u73E0\u6389\u5728\u6C60\u4E2D\u3002</p><p>\u5FAE\u98CE\u4E4D\u8D77\uFF0C\u5439\u76B1\u4E00\u6E56\u6625\u6C34\uFF0C\u4E5F\u5439\u8D77\u4E24\u5973\u7684\u8863\u8882\u3002</p><p>\u9ED1\u8863\u5973\u9732\u51FA\u4E00\u62B9\u6B23\u8D4F\u7684\u773C\u795E\uFF0C\u300C\u6D41\u971C\u5251\u679C\u7136\u540D\u4E0D\u865A\u4F20\uFF0C\u8FD9\u6837\u7684\u5B9A\u529B\uFF0C\u59D0\u59D0\u4E5F\u8981\u7518\u62DC\u4E0B\u98CE\u5462\u3002\u300D</p><p>\u98CE\u665A\u534E\u795E\u8272\u4E0D\u53D8\uFF0C\u6DE1\u6DE1\u9053\uFF1A\u300C\u4F55\u65B9\u5996\u5B7D\uFF0C\u7ADF\u6562\u4E0E\u6211\u8C2E\u79F0\u59D0\u59B9\uFF1F\u300D</p><p>\u6C34\u67D4\u4ED9\u987F\u65F6\u6012\u6C14\u52C3\u53D1\uFF0C\u5979\u8EAB\u4E3A\u661F\u6708\u6E56\u4E94\u957F\u8001\u4E4B\u4E00\uFF0C\u4E00\u5411\u5FC3\u9AD8\u6C14\u50B2\uFF0C\u8FD9\u6B21\u89C1\u5230\u6D41\u971C\u5251\u7684\u98CE\u91C7\u6697\u6697\u5FC3\u6298\uFF0C\u5FCD\u4E0D\u4F4F\u79F0\u8D5E\u4E86\u4E00\u53E5\uFF0C\u5374\u4E0D\u6599\u6C14\u8D28\u8131\u4FD7\u7684\u98CE\u665A\u534E\u5982\u6B64\u76DB\u6C14\u51CC\u4EBA\u3002\u79C0\u7709\u4E00\u6311\uFF0C\u7ACB\u5373\u51FA\u624B\u3002</p><p>\u98CE\u665A\u534E\u6B63\u662F\u8981\u6FC0\u6012\u8FD9\u4E2A\u5927\u654C\uFF0C\u4E0D\u5F85\u5979\u624B\u638C\u6325\u51FA\uFF0C\u957F\u5251\u540E\u53D1\u5148\u81F3\uFF0C\u6D41\u971C\u822C\u7684\u5BD2\u5149\u70B9\u70B9\u98DE\u51FA\u3002</p><p>\u6C34\u67D4\u4ED9\u6321\u4E86\u51E0\u62DB\u7ACB\u77E5\u4E0D\u5999\u3002\u91D1\u3001\u6728\u3001\u706B\u3001\u571F\u56DB\u5802\u4E3A\u64D2\u4E2A\u6155\u5BB9\u7D2B\u73AB\u635F\u5175\u6298\u5C06\uFF0C\u5979\u672C\u4EE5\u4E3A\u53E6\u6709\u7F18\u6545\uFF0C\u4E00\u4EA4\u624B\u624D\u77E5\u9053\u98CE\u665A\u534E\u7684\u529F\u529B\u4E0E\u81EA\u5DF1\u76F8\u5DEE\u65E0\u51E0\u3002\u6B64\u65F6\u88AB\u5979\u5360\u4E86\u4E0A\u98CE\uFF0C\u7A0D\u6709\u4E0D\u614E\u53EA\u6015\u4F1A\u996E\u6068\u6B64\u5730\u3002</p><p>\u98CE\u665A\u534E\u7075\u53F0\u6E05\u6F88\uFF0C\u62DB\u62DB\u62A2\u653B\uFF0C\u4E0D\u7ED9\u6C34\u67D4\u4ED9\u4E00\u4E1D\u673A\u4F1A\u3002\u7247\u523B\u95F4\u4E24\u4EBA\u5DF2\u4EA4\u4E86\u6570\u5341\u62DB\uFF0C\u6C34\u67D4\u4ED9\u8D77\u521D\u5FC3\u6D6E\u6C14\u71E5\uFF0C\u88AB\u5979\u4E00\u8F6E\u6025\u653B\u4E00\u53E3\u6C14\u59CB\u7EC8\u7F13\u4E0D\u8FC7\u6765\uFF0C\u8FDE\u5FD9\u811A\u4E0B\u4E00\u6C89\u6CA1\u5165\u6C34\u4E2D\u3002\u98CE\u665A\u534E\u968F\u5373\u6F5C\u8EAB\u5165\u6C34\u3002</p><p>\u6C34\u9762\u6CE2\u5149\u8D77\u4F0F\uFF0C\u4E00\u7247\u8377\u53F6\u7A81\u7136\u51CC\u7A7A\u98DE\u8D77\uFF0C\u8FDC\u8FDC\u843D\u5728\u4E00\u53CC\u7EA4\u8DB3\u65C1\u3002\u6155\u5BB9\u7D2B\u73AB\u4E0D\u8C19\u6C34\u6027\uFF0C\u53EA\u80FD\u6525\u7740\u5251\u9798\u7126\u6025\u5730\u5728\u5CB8\u8FB9\u5B88\u5019\u3002</p><p>\u4E0D\u591A\u65F6\uFF0C\u6C34\u4E0B\u4F20\u6765\u4E00\u58F0\u95F7\u96F7\u822C\u7684\u5DE8\u54CD\uFF0C\u4E24\u9053\u6C34\u7EBF\u7BAD\u4E00\u822C\u5206\u5F00\u3002\u98CE\u665A\u534E\u4ECE\u6C34\u9762\u8DC3\u51FA\uFF0C\u7ACB\u5728\u6155\u5BB9\u7D2B\u73AB\u8EAB\u65C1\u3002\u6C34\u67D4\u4ED9\u4ECE\u6C60\u5858\u53E6\u4E00\u7AEF\u4E0A\u5CB8\uFF0C\u811A\u4E0B\u4E00\u4E2A\u8E09\u8DC4\uFF0C\u8DCC\u8DCC\u649E\u649E\u7684\u671D\u695A\u8FDE\u96C4\u5B85\u4E2D\u5954\u53BB\u3002</p><p>\u6155\u5BB9\u7D2B\u73AB\u6B63\u5F85\u6E21\u6C34\u622A\u6740\uFF0C\u5374\u88AB\u5E08\u59D0\u4E00\u628A\u62C9\u4F4F\u3002\u98CE\u665A\u534E\u624B\u6307\u5FAE\u98A4\uFF0C\u9762\u8272\u82CD\u767D\uFF0C\u4F4E\u58F0\u9053\uFF1A\u300C\u5FEB\u8D70\u3002\u300D</p><p>\u6155\u5BB9\u7D2B\u73AB\u8FD9\u624D\u77E5\u9053\u5E08\u59D0\u4E5F\u53D7\u91CD\u4F24\uFF0C\u8FDE\u5FD9\u6258\u8D77\u5979\u62D4\u8DB3\u98DE\u5954\u3002\u8FD9\u8FB9\u695A\u8FDE\u96C4\u7B49\u4EBA\u8FFD\u51FA\u6765\uFF0C\u5DF2\u4E0D\u89C1\u4EBA\u5F71\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u6155\u5BB9\u9F99\u76EF\u7740\u6BCD\u4EB2\u534E\u7F8E\u7684\u9762\u5B54\uFF0C\u773C\u795E\u4E0D\u4F4F\u53D8\u5E7B\uFF0C\u826F\u4E45\u624D\u8D77\u8EAB\u79BB\u5F00\u3002\u4ED6\u56F4\u7740\u77F3\u5385\u4E2D\u592E\u7684\u592A\u6781\u56FE\u8D70\u4E86\u4E24\u5708\uFF0C\u7A81\u7136\u4F4E\u9A82\u4E86\u4E00\u53E5\uFF0C\u5FEB\u6B65\u8D70\u5165\u901A\u5F80\u795E\u6BBF\u7684\u752C\u9053\u3002</p><p>\u53F6\u884C\u5357\u5BA4\u5185\u836F\u9999\u6251\u9F3B\uFF0C\u4E24\u4EBA\u4EA4\u8C08\u534A\u5929\uFF0C\u53F6\u884C\u5357\u5475\u5475\u4E00\u7B11\uFF0C\u70B9\u4E86\u70B9\u5934\u3002</p><p>\u767E\u82B1\u89C2\u97F3\u8F7B\u54B3\u7740\u7F13\u7F13\u7741\u5F00\u773C\u775B\u3002</p><p>\u300C\u5A18\uFF0C\u4F60\u9192\u4E86\uFF1F\u300D\u6155\u5BB9\u9F99\u67D4\u58F0\u7684\u8BF4\u7740\uFF0C\u6258\u8D77\u6BCD\u4EB2\u7684\u67D4\u9888\uFF0C\u5C06\u4E00\u53EA\u7389\u7897\u9001\u5230\u5507\u8FB9\uFF0C\u300C\u5A18\uFF0C\u559D\u53E3\u6C34\u2026\u2026\u300D</p><p>\u8427\u4F5B\u5974\u6447\u4E86\u6447\u5934\uFF0C\u63A8\u5F00\u7389\u7897\uFF0C\u4E00\u8FB9\u54B3\u55FD\u4E00\u8FB9\u51C4\u7136\u8BF4\uFF1A\u300C\u4F60\u8BA9\u6211\u6B7B\u2026\u2026\u300D</p><p>\u300C\u5A18\uFF0C\u4F60\u6211\u6BCD\u5B50\u597D\u4E0D\u5BB9\u6613\u56E2\u805A\u4E86\uFF0C\u4E3A\u4EC0\u4E48\u8981\u6B7B\u5462\uFF1F\u300D</p><p>\u8427\u4F5B\u5974\u7EA2\u5507\u98A4\u6296\u7740\u8BF4\u9053\uFF1A\u300C\u4F60\u2026\u2026\u4F60\u505A\u51FA\u90A3\u79CD\u4E8B\u2026\u2026\u6211\u8FD8\u600E\u4E48\u6D3B\u2026\u2026\u300D\u5979\u70ED\u6CEA\u6EC2\u6CB1\u5730\u54ED\u9053\uFF1A\u300C\u4F60\u600E\u4E48\u5BF9\u5F97\u8D77\u4F60\u7239\u7239\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u9634\u68EE\u68EE\u9053\uFF1A\u300C\u4F60\u662F\u8BF4\u90A3\u4E2A\u8FDE\u624B\u4E0B\u90FD\u7BA1\u4E0D\u4F4F\u7684\u8001\u5BB6\u4F19\u5417\uFF1F\u8BA9\u6211\u4EEC\u5B64\u513F\u5BE1\u6BCD\u6D41\u79BB\u5931\u6240\u5403\u5C3D\u82E6\u5934\u2014\u2014\u662F\u90A3\u8001\u4E1C\u897F\u5BF9\u4E0D\u8D77\u6211\u5427\u3002\u300D</p><p>\u8427\u4F5B\u5974\u6CEA\u773C\u8FF7\u8499\u5730\u770B\u7740\u81EA\u5DF1\u7684\u4EB2\u751F\u9AA8\u8089\uFF0C\u534A\u664C\u624D\u51C4\u58F0\u9053\uFF1A\u300C\u4F60\u600E\u4E48\u80FD\u8FD9\u4E48\u8BF4\u2026\u2026\u4ED6\u662F\u4F60\u4EB2\u7239\u554A\u2026\u2026\u300D</p><p>\u300C\u90A3\u5F53\u7136\uFF0C\u6211\u53C8\u6CA1\u4E0D\u8BA4\u4ED6\u2014\u2014\u4F60\u8FD8\u662F\u6211\u4EB2\u5A18\u5462\u3002\u300D\u4ED6\u50CF\u662F\u7A81\u7136\u60F3\u8D77\u4EC0\u4E48\u4F3C\u7684\u5174\u594B\u5730\u95EE\u9053\uFF1A\u300C\u5A18\uFF0C\u5B69\u513F\u8DDF\u7239\u7684\u9E21\u5DF4\u8C01\u7684\u5927\uFF1F\u300D</p><p>\u8427\u4F5B\u5974\u5589\u5934\u987F\u65F6\u564E\u4F4F\uFF0C\u8BF4\u4E0D\u51FA\u8BDD\u6765\u3002</p><p>\u6155\u5BB9\u9F99\u5174\u81F4\u52C3\u52C3\u5730\u8BF4\uFF1A\u300C\u80AF\u5B9A\u662F\u5B69\u513F\u7684\u5927\u4E86\uFF0C\u5A18\uFF0C\u4F60\u6BCF\u6B21\u90FD\u6D41\u597D\u591A\u6C34\u5462\uFF01\u300D</p><p>\u8427\u4F5B\u5974\u4FCF\u8138\u7C89\u767D\uFF0C\u5FFD\u7136\u4E00\u5934\u671D\u77F3\u684C\u78B0\u53BB\u3002\u5979\u5B9E\u5728\u65E0\u6CD5\u5FCD\u53D7\u513F\u5B50\u7684\u595A\u843D\u8C03\u620F\uFF0C\u53EA\u60F3\u4E00\u6B7B\u89E3\u8131\u3002\u989D\u5934\u91CD\u91CD\u843D\u4E0B\uFF0C\u78B0\u5230\u7684\u5374\u662F\u4E00\u53EA\u70BD\u70ED\u7684\u624B\u638C\u3002</p><p>\u6155\u5BB9\u9F99\u5598\u7740\u6C14\u628A\u6BCD\u4EB2\u538B\u8EAB\u4E0B\uFF0C\u4FCA\u8138\u6FC0\u52A8\u5F97\u626D\u66F2\u8D77\u6765\uFF0C\u8427\u4F5B\u5974\u54ED\u53EB\u7740\u62FC\u547D\u626D\u52A8\u8EAB\u4F53\uFF0C\u4F46\u5979\u7684\u6323\u624E\u53EA\u80FD\u4F7F\u513F\u5B50\u66F4\u52A0\u5174\u594B\u3002</p><p>\u53C8\u4E00\u6B21\u88AB\u513F\u5B50\u5F3A\u66B4\uFF0C\u767E\u82B1\u89C2\u97F3\u773C\u6CEA\u6D41\u5E72\u4E86\uFF0C\u55D3\u5B50\u54ED\u54D1\u4E86\uFF0C\u5FC3\u4E5F\u788E\u4E86\u3002</p><p>\u6155\u5BB9\u9F99\u4E00\u8FB9\u62BD\u9001\uFF0C\u4E00\u8FB9\u5438\u542E\u7740\u6BCD\u4EB2\u7684\u4E73\u623F\uFF0C\u542B\u7CCA\u4E0D\u6E05\u5730\u8BF4\uFF1A\u300C\u5A18\u2026\u2026\u4F60\u8EAB\u5B50\u771F\u9999\u2026\u2026\u5976\u5B50\u771F\u8F6F\u2026\u2026\u5C41\u80A1\u771F\u5706\u2026\u2026\u5A18\uFF0C\u8BA9\u513F\u5B50\u64CD\u4F60\u7684\u5C41\u773C\u513F\u597D\u5417\uFF1F\u300D</p><p>\u8427\u4F5B\u5974\u5589\u4E2D\u53D1\u51FA\u4E00\u9635\u4F3C\u54ED\u4F3C\u53F9\u7684\u60B2\u9E23\uFF0C\u8EAB\u4F53\u5C31\u88AB\u513F\u5B50\u7FFB\u8F6C\u8FC7\u6765\u3002\u6155\u5BB9\u9F99\u63B0\u5F00\u4E30\u6EE1\u7684\u80A5\u81C0\uFF0C\u628A\u5934\u57CB\u5728\u96EA\u767D\u7684\u81C0\u8089\u95F4\uFF0C\u820C\u5934\u987A\u7740\u4F18\u7F8E\u7684\u80A1\u6C9F\u6765\u56DE\u8214\u8210\uFF0C\u7136\u540E\u5438\u4F4F\u6D45\u7C89\u8272\u7684\u809B\u7AA6\uFF0C\u5C06\u820C\u5C16\u633A\u5165\u809B\u5185\u3002</p><p>\u8427\u4F5B\u5974\u6D51\u8EAB\u9178\u8F6F\uFF0C\u5728\u513F\u5B50\u7684\u8214\u5F04\u4E0B\u4E0D\u4F4F\u98A4\u6296\u3002</p><p>\u6155\u5BB9\u9F99\u8214\u4E86\u7247\u523B\uFF0C\u62D4\u51FA\u6E7F\u6DCB\u6DCB\u7684\u9633\u5177\u62B5\u5728\u83CA\u809B\u4E0A\uFF0C\u8427\u4F5B\u5974\u5A07\u8EAF\u7ACB\u523B\u7EF7\u7D27\uFF0C\u5C4F\u4F4F\u547C\u5438\uFF0C\u5FC3\u72C2\u8DF3\u4E0D\u5DF2\u3002\u7C97\u5927\u7684\u9F9F\u5934\u7F13\u7F13\u6324\u5165\u809B\u6D1E\uFF0C\u5E03\u6EE1\u9897\u7C92\u7684\u68D2\u8EAB\u522E\u5728\u83CA\u7EB9\u4E0A\uFF0C\u6709\u79CD\u5F02\u6837\u7684\u5FEB\u611F\u3002</p><p>\u6155\u5BB9\u9F99\u5F00\u59CB\u8FD8\u62C5\u5FC3\u5F04\u4F24\u6BCD\u4EB2\uFF0C\u4F46\u6BCD\u4EB2\u6ED1\u817B\u80A5\u5AE9\u7684\u81C0\u8089\u4F7F\u4ED6\u6B32\u706B\u9AD8\u6DA8\u2014\u2014\u6015\u4EC0\u4E48\uFF01\u6709\u53F6\u884C\u5357\u5728\uFF0C\u4F24\u5F97\u518D\u72E0\u4E5F\u80FD\u6CBB\u597D\uFF01\u5FF5\u5934\u4E00\u8D77\uFF0C\u8170\u95F4\u7ACB\u523B\u594B\u529B\u4E00\u633A\u3002\u72F0\u72DE\u7684\u8089\u68D2\u987F\u65F6\u6495\u88C2\u4E86\u8106\u5F31\u7684\u83CA\u809B\u3002\u6BCD\u4EB2\u540E\u5EAD\u7684\u9C9C\u8840\u67D3\u7EA2\u4E86\u513F\u5B50\u7684\u9633\u5177\u3002</p><p>\u8427\u4F5B\u5974\u809B\u4E2D\u5267\u75DB\uFF0C\u51E0\u4E4E\u6655\u4E86\u8FC7\u53BB\u3002\u6CA1\u60F3\u5230\u6155\u5BB9\u9F99\u7ADF\u7136\u8FD8\u628A\u6CBE\u8840\u7684\u8089\u68D2\u9012\u5230\u5979\u9762\u524D\uFF0C\u7B11\u563B\u563B\u5730\u8BF4\uFF1A\u300C\u5A18\uFF0C\u6211\u7239\u7ED9\u4F60\u5F00\u82DE\u7684\u65F6\u5019\u662F\u4E0D\u662F\u8FD9\u6837\u5B50\uFF1F\u300D\u767E\u82B1\u89C2\u97F3\u5446\u5446\u770B\u7740\u6076\u9B54\u822C\u7684\u513F\u5B50\uFF0C\u67D4\u9888\u4E00\u5782\uFF0C\u53C8\u4E0D\u7701\u4EBA\u4E8B\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u98CE\u665A\u534E\u523A\u4E2D\u6C34\u67D4\u4ED9\u4E00\u5251\uFF0C\u4E5F\u88AB\u5979\u7684\u53CD\u51FB\u9707\u4F24\u4E86\u7ECF\u8109\u3002\u5E78\u597D\u5979\u672C\u8EAB\u529F\u529B\u65E2\u9AD8\uFF0C\u53C8\u6709\u7D2B\u73AB\u76F8\u52A9\uFF0C\u8C03\u606F\u4E24\u4E2A\u65F6\u8FB0\u5DF2\u538B\u4F4F\u4E86\u4F24\u52BF\u3002\u8FD9\u91CC\u662F\u767D\u6C99\u6D3E\u7684\u5730\u9762\uFF0C\u4E24\u4EBA\u4E0D\u6562\u4E45\u7559\uFF0C\u987A\u7740\u970D\u72C2\u7130\u79BB\u5F00\u7684\u65B9\u5411\u4E00\u8DEF\u671D\u897F\u5317\u8FDB\u53D1\u3002\u4E09\u65E5\u540E\u8FDB\u5165\u5927\u5C71\u6DF1\u5904\u3002</p><p>\u6155\u5BB9\u7D2B\u73AB\u4E00\u8DEF\u6276\u7740\u5E08\u59D0\uFF0C\u6B64\u65F6\u989D\u5934\u4E0D\u7981\u6E17\u51FA\u7EC6\u7EC6\u9999\u6C57\uFF0C\u770B\u4E0A\u53BB\u500D\u52A0\u5A07\u8273\u3002\u5728\u5C71\u6797\u4E2D\u627E\u4E86\u5904\u7A7A\u5730\u6B47\u5BBF\uFF0C\u5979\u4FBF\u7AD9\u8D77\u6765\u62FF\u8D77\u957F\u5251\uFF0C\u300C\u5927\u5E08\u59D0\uFF0C\u6211\u53BB\u627E\u4E9B\u5403\u7684\u3002\u300D</p><p>\u98CE\u665A\u534E\u4E00\u6014\uFF0C\u300C\u4E0D\u662F\u5E26\u7740\u5E72\u7CAE\u5417\uFF1F\u300D</p><p>\u6155\u5BB9\u7D2B\u73AB\u4F5C\u4E86\u4E2A\u9B3C\u8138\uFF0C\u7B11\u9765\u5982\u82B1\u5730\u8BF4\uFF1A\u300C\u80CC\u7740\u592A\u7D2F\uFF0C\u8DEF\u4E0A\u6211\u628A\u5B83\u6254\u4E86\u3002\u300D</p><p>\u98CE\u665A\u534E\u54D1\u7136\u5931\u7B11\uFF0C\u300C\u6DF1\u5C71\u91CE\u6797\uFF0C\u4F60\u53BB\u54EA\u513F\u627E\u554A\uFF1F\u300D</p><p>\u6155\u5BB9\u7D2B\u73AB\u5F2F\u8170\u624E\u7D27\u5C0F\u86EE\u9774\uFF0C\u300C\u6CA1\u5173\u7CFB\uFF0C\u8DEF\u4E0A\u6211\u770B\u5230\u597D\u591A\u91CE\u5154\u5462\u3002\u300D\u5979\u8F7B\u8F7B\u8DFA\u4E86\u8DFA\u811A\uFF0C\u5174\u9AD8\u91C7\u70C8\u5730\u8BF4\uFF1A\u300C\u665A\u4E0A\u6211\u4EEC\u5C31\u5403\u5154\u5B50\u8089\uFF01\u300D</p><p>\u98CE\u665A\u534E\u9053\uFF1A\u300C\u522B\u8DD1\u8FDC\u4E86\uFF0C\u5FEB\u53BB\u5FEB\u56DE\u3002\u300D</p><p>\u7D2B\u73AB\u300C\u54CE\u300D\u4E86\u4E00\u58F0\uFF0C\u7EB5\u8EAB\u8DC3\u5165\u5BC6\u6797\u3002</p><p>\u521A\u624D\u89C1\u7684\u91CE\u5154\u8FD9\u4F1A\u513F\u5374\u4E00\u53EA\u90FD\u78B0\u4E0D\u4E0A\uFF0C\u6155\u5BB9\u7D2B\u73AB\u5728\u5468\u56F4\u7ED5\u4E86\u4E00\u5708\uFF0C\u65E0\u5948\u4E4B\u4E0B\u53EA\u597D\u5411\u5C71\u6797\u8FB9\u7F18\u8D70\u53BB\u3002</p><p>\u8FDC\u5904\u9690\u9690\u54CD\u8D77\u9A6C\u8E44\u58F0\uFF0C\u6155\u5BB9\u7D2B\u73AB\u7ACB\u523B\u8B66\u89C9\u8D77\u6765\uFF0C\u98DE\u8EAB\u6500\u4E0A\u5927\u6811\uFF0C\u4ECE\u5BC6\u53F6\u95F4\u5411\u5916\u5F20\u671B\u3002</p><p>\u4E00\u4E2A\u767D\u8863\u5C11\u5973\u4E58\u7740\u767D\u9A6C\u6CBF\u5C71\u8DEF\u7F13\u7F13\u884C\u6765\uFF0C\u4E00\u8FB9\u8D70\u4E00\u8FB9\u56DB\u5904\u5F20\u671B\u3002</p><p>\u770B\u6E05\u5979\u7684\u9762\u5B54\uFF0C\u6155\u5BB9\u7D2B\u73AB\u987F\u65F6\u5FC3\u82B1\u6012\u653E\uFF0C\u4ECE\u6811\u4E0A\u4E00\u8DC3\u800C\u4E0B\uFF0C\u6B23\u559C\u5730\u5927\u53EB\u9053\uFF1A\u300C\u5C0F\u2026\u2026\u4F60\u662F\u5C0F\u83BA\u8FD8\u662F\u5C0F\u9E42\uFF1F\u300D</p>',58);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
