import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_17" tabindex="-1"><a class="header-anchor" href="#_17" aria-hidden="true">#</a> 17</h1><p>\u90A3\u5C11\u5973\u88AB\u4ECE\u5929\u800C\u964D\u7684\u6155\u5BB9\u7D2B\u73AB\u5413\u4E86\u4E00\u8DF3\uFF0C\u534A\u664C\u624D\u56DE\u8FC7\u795E\u6765\uFF0C\u671F\u671F\u827E\u827E\u8BF4\u9053\uFF1A\u300C\u662F\u6155\u5BB9\u59D0\u59D0\u554A\uFF0C\u6211\u662F\u767D\u7389\u83BA\u3002\u300D</p><p>\u5F53\u65E5\u767D\u6C0F\u59D0\u59B9\u4ED7\u4E49\u76F8\u6551\u3001\u8D60\u8863\u8D60\u9A6C\u7684\u4FA0\u4E3E\uFF0C\u6155\u5BB9\u7D2B\u73AB\u4E00\u523B\u4E5F\u4E0D\u66FE\u5FD8\u6000\u3002\u6B64\u65F6\u5728\u8352\u5C71\u4E2D\u78B0\u5230\u767D\u7389\u83BA\uFF0C\u7D2B\u73AB\u4E0D\u7531\u7B11\u9010\u989C\u5F00\uFF0C\u62C9\u7740\u5979\u4E00\u540C\u53BB\u89C1\u5927\u5E08\u59D0\u3002\u98CE\u665A\u534E\u65E9\u5DF2\u542C\u7D2B\u73AB\u8BF4\u8FC7\u767D\u6C0F\u59D0\u59B9\uFF0C\u5F88\u611F\u6FC0\u5979\u4EEC\u7684\u63F4\u624B\u4E4B\u5FB7\uFF0C\u8FDE\u5FD9\u542B\u7B11\u81F4\u610F\u3002</p><p>\u767D\u7389\u83BA\u89C1\u5230\u8FD9\u4F4D\u540D\u9707\u6C5F\u6E56\u7684\u4FA0\u5973\uFF0C\u4E0D\u7981\u6709\u4E9B\u7D27\u5F20\u3002\u5979\u4E0E\u59B9\u59B9\u7389\u9E42\u56DE\u5BB6\u6CA1\u4F4F\u4E0A\u51E0\u5929\uFF0C\u6D3B\u6CFC\u597D\u52A8\u7684\u767D\u7389\u83BA\u5C31\u8010\u4E0D\u4F4F\u6027\u5B50\uFF0C\u5546\u91CF\u597D\u8BA9\u59B9\u59B9\u5148\u4F3A\u5019\u53CC\u4EB2\uFF0C\u81EA\u5DF1\u5230\u5357\u65B9\u6E38\u5386\u4E00\u756A\uFF0C\u6CA1\u60F3\u5230\u5728\u5927\u5C71\u91CC\u8FF7\u4E86\u8DEF\u3002\u5979\u5728\u5C71\u91CC\u8F6C\u4E86\u4E00\u6574\u5929\uFF0C\u6B63\u5FC3\u6025\u5982\u711A\uFF0C\u53EF\u5DE7\u7ADF\u53C8\u9047\u4E0A\u4E86\u73AB\u7470\u4ED9\u5B50\u3002</p><p>\u6155\u5BB9\u7D2B\u73AB\u597D\u4E0D\u5BB9\u6613\u7528\u5C0F\u5F29\u5C04\u4E2D\u4E00\u53EA\u91CE\u5154\u3002\u5174\u51B2\u51B2\u62CE\u56DE\u5BBF\u5904\u3002\u6B64\u65F6\u5929\u8272\u5DF2\u665A\uFF0C\u7EA2\u5F64\u5F64\u7684\u7BDD\u706B\u6447\u66F3\u4E2D\uFF0C\u6620\u51FA\u4E09\u5F20\u5404\u5177\u7F8E\u6001\u7684\u4FCF\u8138\uFF0C\u4E09\u5973\u70E7\u70E4\u5154\u8089\uFF0C\u8A00\u7B11\u664F\u664F\u3002</p><p>\u767D\u7389\u83BA\u542C\u5230\u98CE\u665A\u534E\u51FB\u4F24\u661F\u6708\u6E56\u6C34\u5802\u957F\u8001\uFF0C\u695A\u695A\u52A8\u4EBA\u7684\u79C0\u7738\u4E2D\u4E0D\u7531\u6D41\u9732\u51FA\u5D07\u6155\u4E4B\u8272\u3002</p><p>\u98CE\u665A\u534E\u82E6\u7B11\u9053\uFF1A\u300C\u661F\u6708\u6E56\u957F\u8001\u679C\u7136\u5389\u5BB3\uFF0C\u6211\u5360\u4E86\u5148\u673A\u8FD8\u53EA\u662F\u4E24\u8D25\u4FF1\u4F24\u3002\u4E0B\u6B21\u518D\u9047\u4E0A\u5979\uFF0C\u53EA\u6015\u96BE\u4EE5\u8BA8\u597D\u3002\u300D</p><p>\u767D\u7389\u83BA\u7740\u95EE\u9053\uFF1A\u300C\u4E24\u4F4D\u59D0\u59D0\u51C6\u5907\u5F80\u54EA\u91CC\u53BB\u5462\uFF1F\u300D</p><p>\u5B9B\u5982\u767D\u7389\u7684\u624B\u6307\u634F\u7740\u6811\u679D\u6162\u6162\u8F6C\u52A8\uFF0C\u6155\u5BB9\u7D2B\u73AB\u9053\uFF1A\u300C\u5E08\u59D0\uFF0C\u4F60\u4F24\u52BF\u672A\u7652\uFF0C\u4E0D\u5982\u5148\u627E\u4E2A\u5730\u65B9\u4F11\u606F\u4E00\u756A\u3002\u300D</p><p>\u98CE\u665A\u534E\u601D\u7D22\u7247\u523B\uFF0C\u70B9\u4E86\u70B9\u5934\u3002\u73B0\u5728\u5373\u4F7F\u8FFD\u4E0A\u970D\u72C2\u7130\u7B49\u4EBA\uFF0C\u5979\u4E5F\u96BE\u4EE5\u52A8\u624B\uFF0C\u8FD8\u662F\u5148\u89C5\u5730\u4F11\u517B\uFF0C\u6CBB\u597D\u4F24\u52BF\u3002</p><p>\u6155\u5BB9\u7D2B\u73AB\u548C\u767D\u7389\u83BA\u90FD\u662F\u521D\u5165\u6C5F\u6E56\uFF0C\u98CE\u665A\u534E\u7528\u5251\u9798\u5728\u5730\u4E0A\u5212\u7740\uFF0C\u300C\u6211\u4EEC\u73B0\u5728\u5728\u6E58\u897F\uFF0C\u661F\u6708\u6E56\u5996\u4EBA\u4E00\u8DEF\u662F\u671D\u897F\u5317\u8D70\u7684\u3002\u7B49\u51FA\u4E86\u5927\u5C71\uFF0C\u79BB\u6B66\u9675\u5DF2\u7ECF\u4E0D\u8FDC\u3002\u4E0D\u5982\u6211\u4EEC\u5148\u5230\u6B66\u9675\uFF0C\u600E\u4E48\u6837\uFF1F\u300D\u8BF4\u7740\u542B\u7B11\u770B\u4E86\u7D2B\u73AB\u4E00\u773C\u3002</p><p>\u6155\u5BB9\u7D2B\u73AB\u8138\u4E0A\u4E00\u7EA2\uFF0C\u65CB\u5373\u7B11\u9053\uFF1A\u300C\u90A3\u54B1\u4EEC\u5C31\u53BB\u6CAE\u6E20\u5C55\u626C\u5BB6\u6270\u4ED6\u51E0\u65E5\u3002\u300D</p><p>\u6CAE\u6E20\u5C55\u626C\u662F\u98D8\u6885\u5CF0\u7684\u5E38\u5BA2\uFF0C\u4E0E\u795E\u5C3C\u95E8\u4E0B\u5C3D\u7686\u76F8\u719F\u3002\u98CE\u665A\u534E\u7B11\u9053\uFF1A\u300C\u73AB\u7470\u4ED9\u5B50\u5927\u9A7E\u5149\u4E34\uFF0C\u5C55\u626C\u53EF\u662F\u6C42\u4E4B\u4E0D\u5F97\u3002\u300D</p><p>\u6155\u5BB9\u7D2B\u73AB\u8EAB\u4E0A\u6D41\u7740\u4E00\u534A\u9C9C\u5351\u4EBA\u7684\u8840\u6DB2\uFF0C\u5E76\u65E0\u5BFB\u5E38\u513F\u5973\u7684\u7F9E\u6001\u3002\u5979\u4E0E\u6CAE\u6E20\u5C55\u626C\u9752\u6885\u7AF9\u9A6C\uFF0C\u65E9\u5DF2\u82B3\u5FC3\u6697\u8BB8\u3002\u60F3\u5230\u5C55\u626C\u54E5\u54E5\u6E29\u5B58\u7684\u7B11\u5BB9\uFF0C\u7D2B\u73AB\u5403\u5403\u7B11\u4E86\u8D77\u6765\uFF0C\u534A\u664C\u624D\u8BF4\uFF1A\u300C\u4ED6\u4EEC\u8FC1\u5230\u6B66\u9675\u4E03\u516B\u5E74\u4E86\uFF0C\u4E0D\u77E5\u9053\u4F2F\u7236\u4F2F\u6BCD\u600E\u4E48\u6837\u4E86\u2026\u2026\u300D\u8A00\u4E0B\u9887\u6709\u51E0\u5206\u60C6\u6005\u3002</p><p>\u98CE\u665A\u534E\u6015\u5979\u53C8\u60F3\u8D77\u7236\u6BCD\uFF0C\u8FDE\u5FD9\u5C94\u5F00\u8BDD\u9898\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u8427\u4F5B\u5974\u60A0\u60A0\u9192\u6765\uFF0C\u53EA\u89C9\u809B\u4E2D\u4F3C\u4E4E\u6D82\u4E86\u836F\uFF0C\u53C8\u9EBB\u53C8\u51C9\uFF0C\u4F46\u8FD8\u6709\u9690\u9690\u7684\u75DB\u610F\u3002\u7ADF\u7136\u88AB\u4EB2\u751F\u9AA8\u8089\u5F3A\u884C\u5978\u6DEB\u540E\u5EAD\uFF0C\u767E\u82B1\u89C2\u97F3\u5FC3\u5982\u5200\u5272\uFF0C\u8EAB\u5B50\u4E00\u52A8\uFF0C\u4FBF\u6B32\u518D\u6B21\u5BFB\u6B7B\u2014\u2014\u5373\u4F7F\u6B7B\u4E86\uFF0C\u4E5F\u518D\u65E0\u9762\u76EE\u53BB\u89C1\u7941\u54E5\u2026\u2026</p><p>\u6323\u624E\u7247\u523B\uFF0C\u8427\u4F5B\u5974\u624D\u53D1\u73B0\u81EA\u5DF1\u624B\u811A\u88AB\u7D27\u7D27\u7F1A\u4F4F\uFF0C\u5979\u5598\u606F\u7740\u7741\u5F00\u773C\uFF0C\u6B63\u770B\u5230\u6155\u5BB9\u9F99\u6076\u9B54\u822C\u7684\u7B11\u5BB9\u3002</p><p>\u300C\u5A18\uFF0C\u4F60\u9192\u4E86\u3002\u300D</p><p>\u8427\u4F5B\u5974\u5FC3\u5934\u9707\u64BC\uFF0C\u98A4\u58F0\u9053\uFF1A\u300C\u4F60\u60F3\u505A\u4EC0\u4E48\uFF1F\u5FEB\u653E\u5F00\u6211\uFF01\u300D</p><p>\u6155\u5BB9\u9F99\u5728\u5979\u8033\u73E0\u4E0A\u8F7B\u543B\u4E00\u4E0B\uFF0C\u67D4\u58F0\u9053\uFF1A\u300C\u5A18\uFF0C\u5B69\u513F\u662F\u6015\u4F60\u5BFB\u6B7B\u2026\u2026\u300D</p><p>\u8427\u4F5B\u5974\u53EB\u9053\uFF1A\u300C\u4F60\u96BE\u9053\u80FD\u6346\u6211\u4E00\u751F\u4E00\u4E16\u5417\uFF1F\u300D</p><p>\u6155\u5BB9\u9F99\u6447\u4E86\u6447\u5934\u3002</p><p>\u8427\u4F5B\u5974\u6CE3\u58F0\u9053\uFF1A\u300C\u6C42\u6C42\u4F60\u4E86\uFF0C\u9F99\u513F\uFF0C\u4F60\u5C31\u8BA9\u5A18\u6B7B\u5427\u2026\u2026\u300D\u5979\u65E0\u529B\u7684\u6447\u7740\u5934\uFF0C\u73E0\u6CEA\u7EB7\u7EB7\u800C\u4E0B\uFF0C\u51C4\u5A49\u7684\u795E\u60C5\u4EE4\u4EBA\u671B\u4E4B\u751F\u601C\u3002</p><p>\u6155\u5BB9\u9F99\u53F9\u606F\u9053\uFF1A\u300C\u5B69\u513F\u600E\u4E48\u820D\u5F97\uFF1F\u300D\u4ED6\u634F\u7740\u767E\u82B1\u89C2\u97F3\u80A5\u5AE9\u7684\u5706\u4E73\uFF0C\u626C\u58F0\u9053\uFF1A\u300C\u5A18\u7684\u8EAB\u4F53\u53C8\u9999\u53C8\u751C\uFF0C\u5C41\u773C\u513F\u7D27\u51D1\u51D1\u3001\u8F6F\u7EF5\u7EF5\uFF0C\u5B69\u513F\u8FD8\u6CA1\u6709\u64CD\u591F\uFF01\u300D</p><p>\u8427\u4F5B\u5974\u653E\u58F0\u5C16\u53EB\uFF0C\u81FB\u9996\u62FC\u547D\u5728\u6795\u4E0A\u731B\u78D5\u3002</p><p>\u6155\u5BB9\u9F99\u51B7\u51B7\u770B\u4E86\u534A\u664C\uFF0C\u53EB\u9053\uFF1A\u300C\u53F6\u62A4\u6CD5\uFF01\u300D</p><p>\u53F6\u884C\u5357\u5E26\u7740\u4E00\u4E2A\u5305\u88F9\u8D70\u5165\u5BA4\u5185\uFF0C\u76EE\u5149\u95EA\u95EA\u5730\u76EF\u7740\u767E\u82B1\u89C2\u97F3\u67D4\u7F8E\u7684\u8EAF\u4F53\u3002</p><p>\u300C\u52A8\u624B\u5427\uFF01\u300D</p><p>\u53F6\u884C\u5357\u7FFB\u5F00\u5305\u88F9\uFF0C\u53D6\u51FA\u4E00\u67C4\u7EC6\u957F\u7684\u5C16\u5200\uFF0C\u8F7B\u5DE7\u5730\u5728\u8427\u4F5B\u5974\u5DE6\u8155\u4E0A\u5212\u4E86\u4E00\u9053\u3002</p><p>\u4ED6\u624B\u4E0B\u6781\u6709\u5206\u5BF8\uFF0C\u5200\u53E3\u5BBD\u4E0D\u8FC7\u4E00\u6307\uFF0C\u867D\u7136\u6DF1\u53EF\u81F3\u9AA8\uFF0C\u5374\u907F\u5F00\u4E86\u5BC6\u5E03\u7684\u8840\u8109\uFF0C\u53EA\u5207\u65AD\u4E86\u8155\u4E0A\u7684\u7B4B\u7EDC\u3002</p><p>\u4E0D\u7B49\u767E\u82B1\u89C2\u97F3\u60CA\u53EB\u51FA\u58F0\uFF0C\u53F6\u884C\u5357\u5DF2\u7ECF\u53C8\u5212\u5F00\u4E86\u5979\u7684\u5DE6\u80A9\u3002\u96EA\u767D\u7684\u808C\u80A4\u95F4\u7ACB\u523B\u5192\u51FA\u4E00\u62B9\u8840\u73E0\uFF0C\u7EA2\u5982\u739B\u7459\u3002\u53F6\u884C\u5357\u5341\u6307\u5982\u98DE\uFF0C\u8FC5\u901F\u62FF\u4E00\u4E2A\u94A2\u954A\u63A2\u8FDB\u80A9\u4E0A\u7684\u4F24\u53E3\uFF0C\u51ED\u7740\u6307\u5C16\u7075\u654F\u7684\u89E6\u89C9\u6444\u4F4F\u767E\u82B1\u89C2\u97F3\u5DE6\u81C2\u7684\u624B\u7B4B\u5411\u5916\u62FD\u51FA\u3002\u4E00\u624B\u5728\u8D35\u5987\u81C2\u4E0A\u4E0D\u65AD\u5730\u63C9\u634F\u7740\uFF0C\u4F7F\u7B4B\u8171\u677E\u5F00\u3002</p><p>\u67D4\u767D\u7684\u7389\u81C2\u4E0A\u808C\u80A4\u9690\u9690\u62BD\u52A8\uFF0C\u96BE\u8A00\u7684\u5267\u75DB\u5E2D\u5377\u800C\u6765\uFF0C\u8427\u4F5B\u5974\u79C0\u7709\u62E7\u7D27\uFF0C\u75DB\u82E6\u4E07\u72B6\u3002\u4E0D\u591A\u65F6\uFF0C\u4E00\u6839\u5E26\u7740\u8840\u819C\u7684\u7389\u767D\u624B\u7B4B\u4FBF\u4ECE\u5200\u53E3\u4E2D\u7F13\u7F13\u9732\u51FA\uFF0C\u8D8A\u6765\u8D8A\u957F\u3002\u53F6\u884C\u5357\u795E\u8272\u51DD\u91CD\uFF0C\u5DE6\u624B\u5939\u7740\u6570\u5341\u679A\u94F6\u9488\u4E00\u4E00\u63D2\u8FDB\u767E\u82B1\u89C2\u97F3\u81C2\u4E0A\uFF0C\u4ED4\u7EC6\u6311\u4F4F\u7B4B\u7EDC\u6162\u6162\u62E8\u52A8\u3002</p><p>\u8427\u4F5B\u5974\u5DE6\u81C2\u624B\u7B4B\u5DF2\u65AD\uFF0C\u53EA\u80FD\u6B7B\u6B7B\u63E1\u7D27\u53F3\u62F3\uFF0C\u62B5\u6297\u624B\u7B4B\u4ECE\u81C2\u4E2D\u4E00\u4E1D\u4E1D\u62BD\u79BB\u7684\u75DB\u82E6\u3002\u5979\u75DB\u5F97\u4E24\u773C\u7FFB\u767D\uFF0C\u6B7B\u6B7B\u54AC\u4F4F\u7259\u5173\uFF0C\u4E00\u4E2A\u5B57\u90FD\u8BF4\u4E0D\u51FA\u6765\u3002\u7EA4\u7F8E\u7684\u811A\u5C16\u5728\u767D\u7EEB\u4E2D\u7EF7\u5F97\u7B14\u76F4\uFF0C\u7247\u523B\u95F4\u6D51\u8EAB\u4FBF\u5E03\u6EE1\u7EC6\u5BC6\u7684\u6C57\u73E0\u3002</p><p>\u6155\u5BB9\u9F99\u6EE1\u8138\u5174\u594B\uFF0C\u5FCD\u4E0D\u4F4F\u628A\u624B\u4F38\u5230\u6BCD\u4EB2\u4E0B\u4F53\u638F\u5F04\u8D77\u6765\u3002\u9634\u8482\u4E0A\u4E00\u80A1\u6709\u5982\u5B9E\u8D28\u7684\u52B2\u6C14\u4F20\u6765\uFF0C\u8427\u4F5B\u5974\u79D8\u5904\u7ACB\u523B\u6DEB\u6C34\u5954\u6D8C\u3002</p><p>\u4E00\u67F1\u9999\u5DE5\u592B\u540E\uFF0C\u767E\u82B1\u89C2\u97F3\u7684\u624B\u81C2\u5916\u89C2\u770B\u6765\u4E00\u65E0\u5F02\u72B6\uFF0C\u4F46\u6574\u6839\u624B\u7B4B\u5DF2\u88AB\u5B8C\u6574\u53D6\u51FA\u3002\u53F6\u884C\u5357\u5C06\u5F2F\u66F2\u7684\u7EC6\u7B4B\u653E\u5728\u76D8\u4E2D\uFF0C\u7ACB\u5373\u6577\u4E0A\u836F\u7269\uFF0C\u88F9\u4F4F\u4F24\u53E3\uFF0C\u63A5\u7740\u5207\u5F00\u53F3\u81C2\u3002</p><p>\u4E24\u6761\u957F\u7EA6\u5C3A\u534A\u7684\u7EC6\u7B4B\u9759\u9759\u653E\u5728\u76D8\u4E2D\uFF0C\u4E73\u767D\u8272\u7684\u8868\u9762\u4E0A\u6CBE\u7740\u4E1D\u4E1D\u8840\u8FF9\uFF0C\u8FD8\u6709\u4E00\u4E9B\u96F6\u96F6\u788E\u788E\u7684\u8840\u819C\u3002</p><p>\u8427\u4F5B\u5974\u4E24\u817F\u95F4\u6DEB\u6C34\u6DCB\u6F13\uFF0C\u80A5\u539A\u5A07\u5AE9\u7684\u82B1\u74E3\u5728\u513F\u5B50\u6307\u4E0B\u98A4\u6296\u4E0D\u5DF2\uFF0C\u82B1\u857E\u822C\u7684\u9634\u8482\u9AD8\u9AD8\u6311\u8D77\uFF0C\u8272\u6CFD\u8D64\u7EA2\u3002\u5979\u88AB\u5F3A\u70C8\u7684\u75DB\u695A\u548C\u540C\u6837\u5F3A\u70C8\u7684\u5FEB\u611F\u51B2\u51FB\u5730\u9B42\u98DE\u9B44\u6563\uFF0C\u53EA\u80FD\u4ECE\u7259\u7F1D\u91CC\u6025\u4FC3\u5730\u5598\u7740\u6C14\u3002</p><p>\u53F6\u884C\u5357\u5C06\u767E\u82B1\u89C2\u97F3\u7FFB\u8F6C\u8FC7\u6765\uFF0C\u63E1\u4F4F\u5149\u6DA6\u5982\u7389\u7684\u811A\u8E1D\uFF0C\u6307\u95F4\u5BD2\u5149\u4E00\u95EA\uFF0C\u5DF2\u5272\u5F00\u811A\u8155\u7684\u808C\u80A4\u3002</p><p>\u8584\u5203\u4ECE\u811A\u7B4B\u4E0B\u7A7F\u8FC7\uFF0C\u300C\u5D29\u300D\u7684\u4E00\u58F0\u8F7B\u54CD\u6311\u65AD\u811A\u7B4B\u3002\u7136\u540E\u518D\u5256\u5F00\u8179\u80A1\u6C9F\uFF0C\u5C06\u817F\u7B4B\u4E24\u5934\u5207\u65AD\u3002\u817F\u4E0A\u7684\u7B4B\u8171\u592A\u957F\uFF0C\u4ED6\u53C8\u5728\u819D\u5F2F\u540E\u5E73\u5207\u4E00\u5200\uFF0C\u7EC6\u7389\u822C\u7684\u808C\u80A4\u4E0A\u7ACB\u523B\u7559\u4E0B\u4E00\u4E2A\u6574\u9F50\u7684\u5200\u53E3\u3002</p><p>\u53F6\u884C\u5357\u624B\u6307\u4E00\u5206\uFF0C\u9C9C\u7EA2\u7684\u808C\u8089\u5C3D\u6570\u7EFD\u5F00\uFF0C\u9732\u51FA\u5176\u4E2D\u7684\u7B4B\u7EDC\u3002\u4ED6\u624B\u6307\u5982\u98DE\uFF0C\u8FC5\u901F\u62FF\u8D77\u94A2\u94A9\u52FE\u4F4F\u811A\u7B4B\uFF0C\u5411\u4E0A\u4E00\u63D0\u3002\u8427\u4F5B\u5974\u66F2\u7EBF\u4F18\u7F8E\u7684\u5C0F\u817F\u5E94\u624B\u800C\u8D77\uFF0C\u4ECE\u81C0\u540E\u67D4\u67D4\u659C\u7FD8\u8D77\u6765\u3002</p><p>\u53F6\u884C\u5357\u4E00\u624B\u6309\u4F4F\u767E\u82B1\u89C2\u97F3\u7684\u7389\u8DB3\uFF0C\u4E00\u624B\u63E1\u7740\u94A2\u94A9\u7F13\u7F13\u4F7F\u529B\uFF0C\u4ECE\u4FEE\u957F\u7684\u7389\u817F\u4E2D\u95F4\u628A\u817F\u7B4B\u6574\u4E2A\u62BD\u51FA\u3002\u811A\u7B4B\u6BD4\u624B\u624B\u7B4B\u7565\u7C97\u4E00\u4E9B\uFF0C\u4E0D\u5FC5\u518D\u7528\u94F6\u9488\u76F8\u52A9\u3002\u4F46\u76F8\u5E94\u7684\u75BC\u75DB\u4E5F\u66F4\u52A0\u5F3A\u70C8\u3002</p><p>\u8427\u4F5B\u5974\u79D8\u5904\u655E\u9732\uFF0C\u6D53\u767D\u7684\u9634\u7CBE\u5728\u6155\u5BB9\u9F99\u5185\u529B\u6467\u53D1\u4E0B\u6709\u5982\u6CC9\u6D8C\uFF0C\u4E0D\u5F85\u56DB\u80A2\u7684\u7B4B\u8171\u5C3D\u88AB\u62BD\u5B8C\uFF0C\u5979\u65E9\u5DF2\u4E0D\u77E5\u662F\u56E0\u4E3A\u75BC\u75DB\u8FD8\u662F\u5FEB\u611F\u800C\u660F\u8FF7\u8FC7\u53BB\uFF0C\u817F\u95F4\u7684\u9526\u88AB\u4E0A\u9ECF\u4E4E\u4E4E\u5C3D\u662F\u6DEB\u6DB2\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u7B2C\u4E8C\u5929\u4E09\u5973\u4E00\u65E9\u5C31\u51FA\u53D1\uFF0C\u8D1F\u4F24\u7684\u98CE\u665A\u534E\u4E58\u9A6C\uFF0C\u6155\u5BB9\u7D2B\u73AB\u548C\u767D\u7389\u83BA\u6B65\u884C\u8DDF\u5728\u540E\u9762\u3002</p><p>\u4E2D\u5348\u65F6\u5206\uFF0C\u4E09\u4EBA\u5DF2\u8D70\u5230\u5C71\u8170\uFF0C\u8FDC\u8FDC\u80FD\u770B\u5230\u5C71\u4E0B\uFF08\uFF1A\u6587\u3002\uFF09\u7684\u519C\u7530\u3002\u518D\u8D70\uFF08\uFF1A\u4EBA\u3002\uFF09\u91CC\u8BB8\uFF0C\u8033\u8FB9\u9690\u9690\u4F20\uFF08\uFF1A\u4E66\u3002\uFF09\u6765\u6C34\u58F0\u8F70\u9E23\u3002\u8D70\u8FD1\uFF08\uFF1A\u5C4B\u3002\uFF09\u4E00\u770B\uFF0C\u539F\u6765\u662F\u4E00\u6761\u5DE8\u9F99\u822C\u7684\u7011\u5E03\u3002\u6D69\u6D69\u8361\u8361\u7684\u6CE2\u6D9B\u4ECE\u9AD8\u8FD1\u767E\u4E08\u7684\u60AC\u5D16\u4E0A\u98DE\u6D41\u76F4\u4E0B\uFF0C\u50CF\u4E07\u77F3\u96EA\u7389\u843D\u5165\u6DF1\u6F6D\uFF0C\u6FC0\u8D77\u6F2B\u5929\u6C34\u96FE\u3002</p><p>\u65F6\u503C\u4E09\u6708\uFF0C\u5929\u6C14\u6E10\u70ED\uFF0C\u6155\u5BB9\u7D2B\u73AB\u4E00\u8DEF\u5954\u884C\uFF0C\u8EAB\u4E0A\u5DF2\u662F\u9999\u6C57\u6DCB\u6F13\uFF0C\u770B\u5230\u6E05\u6F88\u7684\u6F6D\u6C34\u4E0D\u7531\u5FC3\u4E0B\u4E00\u52A8\uFF0C\u300C\u5C0F\u83BA\uFF0C\u54B1\u4EEC\u4E0B\u53BB\u6D17\u4E2A\u6FA1\u5427\uFF01\u300D</p><p>\u767D\u7389\u83BA\u5FAE\u5FAE\u4E00\u6123\uFF0C\u65CB\u5373\u542B\u7B11\u5E94\u5141\u3002</p><p>\u6DF1\u5C71\u7A7A\u65E0\u4EBA\u8FF9\uFF0C\u6155\u5BB9\u7D2B\u73AB\u5927\u80C6\u5730\u9664\u53BB\u5916\u8863\uFF0C\u53EA\u7740\u4E00\u4EF6\u84DD\u8272\u7684\u62B9\u80F8\u8D70\u5165\u6F6D\u4E2D\uFF0C\u300C\u5440\uFF01\u771F\u51C9\u3002\u5927\u5E08\u59D0\uFF0C\u4F60\u4E5F\u4E0B\u6765\u5427\uFF0C\u563B\u563B\uFF0C\u8FD9\u91CC\u597D\u591A\u9C7C\u5462\u3002\u300D</p><p>\u98CE\u665A\u534E\u501A\u5728\u6811\u65C1\u5631\u5490\u9053\uFF1A\u300C\u4F60\u6C34\u6027\u4E0D\u597D\uFF0C\u8FD9\u4E48\u51C9\u7684\u6C34\u5C0F\u5FC3\u62BD\u7B4B\u3002\u300D</p><p>\u767D\u7389\u83BA\u7F9E\u7EA2\u4E86\u8138\uFF0C\u6162\u6162\u8131\u53BB\u5916\u8863\uFF0C\u671D\u56DB\u5468\u5F20\u671B\u4E86\u4E00\u4E0B\uFF0C\u624D\u5C0F\u5FC3\u5730\u8E29\u8FDB\u6C34\u91CC\u3002</p><p>\u6155\u5BB9\u7D2B\u73AB\u5DF2\u8D70\u5230\u9F50\u8170\u6DF1\u7684\u6C34\u4E2D\uFF0C\u5979\u88AB\u51B0\u51B7\u7684\u6F6D\u6C34\u6FC0\u7684\u5A07\u547C\u4E00\u58F0\uFF0C\u63AC\u8D77\u4E00\u6367\u6C34\u6D12\u5411\u5929\u7A7A\u3002</p><p>\u6C34\u82B1\u56DB\u6563\uFF0C\u6620\u51FA\u4E00\u9053\u5C0F\u5C0F\u7684\u5F69\u8679\uFF0C\u7D2B\u73AB\u8138\u4E0A\u5145\u6EE1\u4E86\u6B23\u559C\uFF0C\u770B\u7740\u8FD9\u9053\u5C0F\u5DE7\u5F2F\u8679\u6E10\u6E10\u6D88\u6563\u3002\u6B64\u65F6\u5979\u8138\u4E0A\u6CBE\u7740\u6676\u83B9\u7684\u6C34\u73E0\uFF0C\u8FD9\u4E00\u7B11\u76F4\u5982\u73AB\u7470\u542B\u9732\uFF0C\u5A07\u7F8E\u65E0\u5339\u3002</p><p>\u7D2B\u73AB\u5438\u4E86\u53E3\u6C14\uFF0C\u6F5C\u5230\u6C34\u5E95\uFF0C\u8BD5\u56FE\u7ED5\u7740\u6DF1\u6F6D\u6E38\u4E0A\u4E00\u5708\u3002\u5979\u60F3\u85C9\u673A\u60F3\u7EC3\u4E60\u6C34\u6027\uFF0C\u514D\u5F97\u50CF\u4E0A\u6B21\u822C\u53EA\u80FD\u89C2\u6218\u3002\u96EA\u767D\u7684\u808C\u80A4\u6620\u5728\u5FAE\u84DD\u7684\u6F6D\u6C34\u4E2D\uFF0C\u4EFF\u4F5B\u4E00\u5177\u66F2\u7EBF\u73B2\u73D1\u7684\u7F8E\u4EBA\u9C7C\u3002\u53EF\u8FD9\u6761\u7F8E\u4EBA\u9C7C\u53EA\u6E38\u4E86\u5341\u4E08\u5DE6\u53F3\uFF0C\u5C31\u618B\u4E0D\u4F4F\u62AC\u8D77\u4E86\u5934\u3002\u770B\u5230\u767D\u7389\u83BA\u8FD8\u7A7F\u7740\u4EB5\u88E4\uFF0C\u7D2B\u73AB\u6076\u4F5C\u5267\u7684\u6E38\u4E86\u8FC7\u53BB\uFF0C\u60F3\u628A\u5B83\u626F\u4E0B\u6765\u3002</p><p>\u767D\u7389\u83BA\u540C\u662F\u5317\u65B9\u4EBA\uFF0C\u6C34\u6027\u8F83\u7D2B\u73AB\u4E5F\u597D\u4E0D\u4E86\u591A\u5C11\uFF0C\u89C1\u72B6\u8FDE\u5FD9\u95EA\u907F\u3002\u4E00\u65F6\u95F4\u7A7A\u5C71\u5BD2\u6F6D\u4E2D\u5145\u6EE1\u4E24\u540D\u5C11\u5973\u7684\u60CA\u547C\u548C\u5A07\u7B11\uFF0C\u67D4\u7F8E\u7684\u80A2\u4F53\u5728\u6C34\u9762\u4E0D\u4F4F\u8D77\u4F0F\uFF0C\u6625\u60C5\u65E0\u8FB9\u3002</p><p>\u6155\u5BB9\u7D2B\u73AB\u6700\u64C5\u8F7B\u529F\uFF0C\u773C\u89C1\u76F8\u6301\u4E0D\u4E0B\uFF0C\u5E72\u8106\u8FD0\u529F\u6D6E\u8D77\u3002\u7389\u624B\u4E00\u5708\u4E00\u7FFB\uFF0C\u5DF2\u4F7F\u51FA\u98D8\u6885\u5CF0\u7684\u7EDD\u6280\uFF0C\u5A07\u7B11\u58F0\u4E2D\u62FF\u4F4F\u767D\u7389\u83BA\u7684\u811A\u8E1D\u3002\u63A5\u7740\u7D2B\u73AB\u6F5C\u5230\u6C34\u4E0B\u626F\u4F4F\u88E4\u811A\u4E00\u62FD\uFF0C\u767D\u7389\u83BA\u5149\u6D01\u7684\u96EA\u81C0\u987F\u65F6\u66B4\u9732\u51FA\u6765\u3002</p><p>\u767D\u7389\u83BA\u6025\u6025\u626D\u52A8\u8EAB\u4F53\uFF0C\u7389\u817F\u5F00\u5408\u95F4\u9732\u51FA\u4E00\u62B9\u9ED1\u8272\u3002\u6155\u5BB9\u7D2B\u73AB\u5728\u6C34\u4E2D\u5AE3\u7136\u4E00\u7B11\uFF0C\u6B63\u5F85\u6D6E\u51FA\u6C34\u9762\uFF0C\u5374\u89C9\u817F\u4E0A\u4E00\u9EBB\uFF0C\u4FBF\u76F4\u76F4\u6C89\u4E86\u4E0B\u53BB\u3002</p><p>\u767D\u7389\u83BA\u614C\u5FD9\u62C9\u8D77\u7D2B\u73AB\uFF0C\u53EB\u9053\uFF1A\u300C\u98CE\u59D0\u59D0\uFF0C\u6155\u5BB9\u59D0\u59D0\u62BD\u7B4B\u4E86\u3002\u300D</p><p>\u98CE\u665A\u534E\u8FDE\u5FD9\u8DC3\u5165\u6F6D\u4E2D\uFF0C\u521A\u521A\u6293\u4F4F\u7D2B\u73AB\u7684\u624B\u8155\uFF0C\u7A81\u7136\u8138\u8272\u4E00\u53D8\uFF0C\u62AC\u638C\u683C\u5F00\u767D\u7389\u83BA\u88AD\u6765\u7684\u624B\u6307\u3002</p>',59);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
