import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_18" tabindex="-1"><a class="header-anchor" href="#_18" aria-hidden="true">#</a> 18</h1><p>\u767D\u7389\u83BA\u8138\u8272\u60E8\u767D\uFF0C\u4E00\u8FB9\u4E0E\u98CE\u665A\u534E\u76F8\u6597\uFF0C\u4E00\u8FB9\u671D\u6F6D\u8FB9\u6E38\u53BB\u3002\u98CE\u665A\u534E\u633D\u7740\u88AB\u5C01\u4E86\u7A74\u9053\u7684\u5E08\u59B9\uFF0C\u5728\u6C34\u4E2D\u4E5F\u65E0\u6CD5\u65BD\u5C55\u3002\u5979\u987E\u4E0D\u5F97\u53BB\u60F3\u767D\u7389\u83BA\u4E3A\u4F55\u7A81\u7136\u88AD\u51FB\u81EA\u5DF1\u59D0\u59B9\uFF0C\u53EA\u89C1\u62DB\u62C6\u62DB\uFF0C\u529B\u4FDD\u4E0D\u5931\u3002</p><p>\u4E24\u5973\u540C\u65F6\u8E0F\u5728\u6F6D\u5CB8\uFF0C\u767D\u7389\u83BA\u81EA\u77E5\u4E0D\u654C\uFF0C\u626D\u5934\u4FBF\u5954\u3002\u98CE\u665A\u534E\u600E\u5BB9\u5979\u9003\u8D70\uFF0C\u653E\u4E0B\u7D2B\u73AB\u7ACB\u523B\u8FFD\u4E86\u8FC7\u53BB\u3002</p><p>\u767D\u7389\u83BA\u4E00\u8A00\u4E0D\u53D1\uFF0C\u8FF3\u76F4\u6CA1\u5165\u5BC6\u6797\u3002\u98CE\u665A\u534E\u817E\u8EAB\u800C\u8D77\uFF0C\u7247\u523B\u4FBF\u62A2\u5728\u4E86\u767D\u7389\u83BA\u8EAB\u524D\uFF0C\u56DE\u638C\u671D\u5979\u80F8\u524D\u62CD\u53BB\u3002</p><p>\u8EAB\u540E\u98CE\u58F0\u54CD\u8D77\uFF0C\u51E0\u6761\u4EBA\u5F71\u540C\u65F6\u4ECE\u6811\u4E0A\u6251\u4E0B\uFF0C\u5251\u3001\u9524\u3001\u6307\u76F4\u5954\u6D41\u971C\u5251\u80CC\u5FC3\u3002\u98CE\u665A\u534E\u62DB\u5F0F\u4E0D\u53D8\uFF0C\u67D4\u80A9\u5FAE\u95EA\uFF0C\u7A81\u7136\u52A0\u901F\u4E0E\u767D\u7389\u83BA\u5BF9\u4E86\u4E00\u638C\u3002\u540E\u8005\u7ACB\u523B\u5634\u89D2\u6EA2\u8840\uFF0C\u5411\u540E\u629B\u8DCC\u3002\u98CE\u665A\u534E\u4E00\u62DB\u4F24\u4E86\u767D\u7389\u83BA\uFF0C\u7ACB\u5373\u501F\u529B\u8DC3\u8D77\uFF0C\u7ACB\u5728\u6811\u5DC5\u3002</p><p>\u6797\u4E2D\u4EBA\u5F71\u95EA\u52A8\uFF0C\u9664\u4E86\u521A\u624D\u51FA\u624B\u7684\u4E09\u4EBA\uFF0C\u8FD8\u6709\u5341\u4F59\u540D\u9ED1\u3001\u7EA2\u670D\u8272\u7684\u6C49\u5B50\uFF0C\u6B63\u662F\u661F\u6708\u6E56\u6C34\u3001\u706B\u4E24\u5802\u5E2E\u4F17\u3002</p><p>\u70C8\u7130\u3001\u7384\u51B0\u3001\u6E05\u9732\u4E09\u540D\u9999\u4E3B\u54C1\u5B57\u5F62\u628A\u98CE\u665A\u534E\u56F4\u5728\u4E2D\u95F4\uFF0C\u9759\u9ED8\u7247\u523B\u540E\u7A81\u7136\u540C\u65F6\u51FA\u624B\u3002</p><p>\u8FD9\u4E09\u4EBA\u6B66\u529F\u5404\u5F02\uFF0C\u70C8\u7130\u7684\u94DC\u9524\u529B\u9053\u521A\u731B\uFF0C\u6E05\u9732\u7684\u5F2F\u5251\u9634\u67D4\u72E0\u8FA3\uFF0C\u7384\u51B0\u867D\u662F\u8D64\u624B\uFF0C\u4F46\u4ED6\u7684\u51DD\u795E\u6307\u52B2\u82E5\u5BD2\u51B0\uFF0C\u53D8\u5316\u4E07\u7AEF\u3002\u5373\u4F7F\u662F\u5E73\u65F6\u98CE\u665A\u534E\u5BF9\u4ED8\u8FD9\u4E09\u4EBA\u7684\u5408\u51FB\u4E5F\u9887\u611F\u5403\u529B\uFF0C\u51B5\u4E14\u6B64\u523B\u8D64\u624B\u7A7A\u62F3\uFF0C\u4F24\u52BF\u672A\u7652. \u6597\u4E86\u4E94\u5341\u4F59\u62DB\uFF0C\u5DF2\u7ECF\u662F\u9669\u8C61\u73AF\u751F\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u300C\u5A18\uFF0C\u6765\uFF0C\u559D\u70B9\u7CA5\u3002\u300D\u6155\u5BB9\u9F99\u67D4\u58F0\u8BF4\u3002</p><p>\u767E\u82B1\u89C2\u97F3\u56DB\u80A2\u762B\u8F6F\uFF0C\u65E0\u529B\u5730\u501A\u5728\u513F\u5B50\u624B\u81C2\u4E0A\uFF0C\u773C\u775B\u6728\u7136\u770B\u7740\u81EA\u5DF1\u7684\u624B\u6307\u3002</p><p>\u80A9\u817F\u7684\u4F24\u53E3\u5DF2\u7136\u7652\u5408\uFF0C\u8427\u4F5B\u5974\u770B\u4E0A\u53BB\u4ECD\u5982\u5F80\u65E5\u822C\u7AEF\u5E84\u7F8E\u8273\u3002\u5979\u65E0\u6570\u6B21\u52AA\u529B\u7740\u60F3\u62AC\u8D77\u624B\u6307\uFF0C\u53EF\u59CB\u7EC8\u6BEB\u65E0\u53CD\u5E94\u3002\u5DF2\u7ECF\u4E03\u5929\u4E86\uFF0C\u8427\u4F5B\u5974\u4ECD\u4E0D\u6562\u76F8\u4FE1\u513F\u5B50\u7ADF\u7136\u4F1A\u6B8B\u5FCD\u5730\u5C06\u628A\u81EA\u5DF1\u624B\u7B4B\u811A\u7B4B\u5B8C\u5168\u62BD\u53BB\u3002</p><p>\u300C\u5A18\uFF0C\u542C\u8BDD\uFF0C\u5F20\u5F00\u5634\u2026\u2026\u300D</p><p>\u5979\u5589\u5934\u62BD\u52A8\u4E00\u4E0B\uFF0C\u7F13\u7F13\u5408\u4E0A\u7F8E\u76EE\u3002</p><p>\u6155\u5BB9\u9F99\u4F4E\u4F4E\u7B11\u9053\uFF1A\u300C\u662F\u4E0D\u662F\u60F3\u8BA9\u5B69\u513F\u90A3\u6837\u5582\u4F60\u5462\uFF1F\u300D</p><p>\u5BC6\u5BC6\u7684\u776B\u6BDB\u4E0B\u6C81\u51FA\u4E00\u6EF4\u6676\u83B9\u7684\u6CEA\u6C34\uFF0C\u4EFF\u4F5B\u6708\u8272\u4E0B\u51C4\u8FF7\u7684\u73E0\u5149\u3002</p><p>\u6155\u5BB9\u9F99\u4FEF\u5728\u6BCD\u4EB2\u5634\u4E0A\u75DB\u543B\u4E00\u756A\uFF0C\u7136\u540E\u5C06\u5996\u5F02\u7684\u8089\u68D2\u6345\u8FDB\u5A07\u7F8E\u7684\u7EA2\u5507\u3002\u89E6\u624B\u56DB\u9762\u652F\u8D77\uFF0C\u6491\u5F00\u767E\u82B1\u89C2\u97F3\u7684\u7259\u5173\u3002\u8427\u4F5B\u5974\u5C0F\u5634\u5706\u5F20\uFF0C\u76F4\u76F4\u8EBA\u5728\u69BB\u4E0A\u3002\u81EA\u4ECE\u513F\u5B50\u7B2C\u4E00\u6B21\u5F3A\u8FEB\u5979\u53E3\u4EA4\u6765\uFF0C\u767E\u82B1\u89C2\u97F3\u65E0\u6570\u6B21\u8BD5\u8FC7\u60F3\u628A\u8FD9\u6839\u7F6A\u6076\u7684\u9633\u5177\u54AC\u65AD\uFF0C\u4F46\u5979\u53EA\u662F\u4E2A\u5F31\u8D28\u5973\u5B50\uFF0C\u9762\u5BF9\u5996\u9B54\u822C\u7684\u6155\u5BB9\u9F99\uFF0C\u6EE1\u53E3\u7EC6\u767D\u7684\u94F6\u7259\u8FDE\u90A3\u4E9B\u89E6\u624B\u90FD\u62B5\u6297\u4E0D\u4E86\u3002</p><p>\u7A00\u7CA5\u987A\u7740\u8089\u68D2\u6D41\u5165\u53E3\u4E2D\uFF0C\u767E\u82B1\u89C2\u97F3\u5589\u4E2D\u4E00\u545B\uFF0C\u54B3\u55FD\u8D77\u6765\u3002\u6155\u5BB9\u9F99\u653E\u4E0B\u74F7\u7897\uFF0C\u8089\u68D2\u67D4\u67D4\u8FDB\u51FA\u51E0\u4E0B\uFF0C\u4ED6\u5C0F\u5FC3\u4E0D\u538B\u7D27\u820C\u6839\uFF0C\u514D\u5F97\u6BCD\u4EB2\u5455\u5410\u3002</p><p>\u8FD9\u6837\u4E00\u53E3\u4E00\u53E3\u704C\u4E86\u8BB8\u4E45\uFF0C\u6700\u540E\u89E6\u624B\u4E00\u6536\uFF0C\u8089\u68D2\u6DF1\u6DF1\u9876\u5165\u8427\u4F5B\u5974\u54BD\u4E2D\uFF0C\u5C06\u6D53\u7CBE\u6FC0\u5C04\u5165\u5185\uFF0C\u6155\u5BB9\u9F99\u624D\u7B11\u563B\u563B\u76F4\u8D77\u8EAB\u5B50\uFF0C\u300C\u5A18\uFF0C\u662F\u7CA5\u597D\u559D\u8FD8\u662F\u5B69\u513F\u7684\u7CBE\u6DB2\u597D\u559D\uFF1F\u300D</p><p>\u767E\u82B1\u89C2\u97F3\u51C4\u7136\u7741\u5F00\u773C\u775B\uFF0C\u60B2\u58F0\u9053\uFF1A\u300C\u4F60\u4E3A\u4EC0\u4E48\u8981\u8FD9\u6837\u5BF9\u6211\uFF1F\u4F60\u7A76\u7ADF\u60F3\u8981\u4EC0\u4E48\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u4FCA\u7F8E\u7684\u9762\u5B54\u4E0A\u63A0\u8FC7\u4E00\u4E1D\u590D\u6742\u7684\u795E\u8272\uFF0C\u8F7B\u8F7B\u9053\uFF1A\u300C\u5341\u516D\u5E74\u4E86\uFF0C\u5988\uFF0C\u90A3\u65F6\u5019\u5B69\u513F\u624D\u4E94\u5C81\uFF0C\u6BCF\u5929\u4F5C\u68A6\u90FD\u4F1A\u68A6\u5A18\u62B1\u7740\u6211\u5582\u7740\u5403\u996D\u3002\u300D</p><p>\u4ED6\u5C0F\u5FC3\u5730\u64E6\u51C0\u6BCD\u4EB2\u7684\u5507\u89D2\uFF0C\u300C\u6709\u6B21\u4E0B\u5927\u96EA\uFF0C\u6211\u5077\u5077\u8DD1\u5230\u6BBF\u5916\u73A9\u96EA\u2026\u2026\u540E\u6765\u5C3F\u6025\uFF0C\u624D\u53D1\u73B0\u624B\u90FD\u6728\u4E86\u2026\u2026\u6211\u54ED\u7740\u8DD1\u56DE\u53BB\u627E\u4F60\u2026\u2026\u4F60\u7B11\u7740\u5E2E\u6211\u89E3\u5F00\u8863\u670D\uFF0C\u628A\u6211\u62B1\u5728\u6000\u91CC\u2026\u2026\u300D</p><p>\u4ED6\u6402\u4F4F\u8427\u4F5B\u5974\u9999\u8F6F\u7684\u8EAF\u4F53\uFF0C\u95ED\u4E0A\u773C\uFF0C\u5462\u54DD\u822C\u8BF4\u9053\uFF1A\u300C\u2026\u2026\u6211\u8FD8\u8BB0\u5F97\u5A18\u8EAB\u4E0A\u5F88\u9999\u2014\u2014\u5C31\u50CF\u73B0\u5728\u4E00\u6837\uFF1B\u624B\u5F88\u8F6F\uFF0C\u5F88\u6696\u548C\u2026\u2026\u6211\u8EBA\u5728\u5A18\u6000\u91CC\u6492\u5C3F\uFF0C\u90A3\u4E48\u5C0F\u2026\u2026\u300D</p><p>\u767E\u82B1\u89C2\u97F3\u98A4\u6296\u7740\u54AC\u4F4F\u7EA2\u5507\uFF0C\u6CEA\u5982\u96E8\u4E0B\u3002</p><p>\u6155\u5BB9\u9F99\u7A81\u7136\u53CC\u81C2\u4E00\u7D27\uFF0C\u5389\u58F0\u9053\uFF1A\u300C\u53EF\u662F\u4F60\u540E\u6765\u628A\u6211\u6254\u4E0B\u4E86\uFF0C\u81EA\u5DF1\u8DDF\u7740\u522B\u4EBA\u8DD1\u4E86\uFF01\u300D</p><p>\u767E\u82B1\u89C2\u97F3\u60CA\u547C\u9053\uFF1A\u300C\u4E0D\u662F\u8FD9\u6837\uFF0C\u6211\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u63A9\u4F4F\u5979\u7684\u5634\uFF0C\u6025\u4FC3\u5598\u4E86\u51E0\u53E3\u6C14\uFF0C\u6162\u6162\u5E73\u9759\u4E0B\u6765\uFF0C\u300C\u4E0D\u7BA1\u600E\u4E48\u8BF4\uFF0C\u6211\u88AB\u4E00\u4E2A\u4EBA\u6254\u5728\u8FD9\u91CC\uFF0C\u88AB\u5996\u5987\u6298\u78E8\u5F97\u4EBA\u4E0D\u4EBA\u9B3C\u4E0D\u9B3C\u2014\u2014\u5A18\uFF0C\u6211\u5E76\u4E0D\u6068\u4F60\uFF0C\u6211\u77E5\u9053\u4F60\u4E5F\u6CA1\u529E\u6CD5\u3002\u4F46\u4F60\u4E3A\u4EC0\u4E48\u8981\u6B7B\u5462\uFF1F\u96BE\u9053\u5B69\u513F\u4E0D\u75BC\u4F60\u5417\uFF1F\u6211\u2026\u2026\u300D</p><p>\u300C\u53EE\u5F53\u300D\uFF0C\u94F6\u94C3\u58F0\u8F7B\u54CD\u3002\u6155\u5BB9\u9F99\u542C\u51FA\u662F\u795E\u6BBF\u5B88\u536B\u6709\u4E8B\u7980\u62A5\uFF0C\u5306\u5306\u62AB\u8863\u8D77\u8EAB\uFF0C\u8BF4\u9053\uFF1A\u300C\u53EA\u8981\u4F60\u4E56\u4E56\u542C\u8BDD\uFF0C\u6211\u80AF\u5B9A\u4F1A\u597D\u597D\u7167\u6599\u4F60\uFF0C\u5C31\u5C0F\u65F6\u5019\u4F60\u7167\u987E\u6211\u4E00\u6837\u3002\u7ED9\u4F60\u5582\u996D\u3001\u6D17\u6FA1\u3001\u4FBF\u6EBA\u2026\u2026\u8FD8\u80FD\u8BA9\u4F60\u4F53\u4F1A\u5230\u4EBA\u4E16\u83AB\u5927\u7684\u5FEB\u611F\u2026\u2026\u300D</p><p>\u4ED6\u5728\u6BCD\u4EB2\u4E0B\u4F53\u62D4\u5F04\u4E00\u4E0B\uFF0C\u6DEB\u7B11\u9053\uFF1A\u300C\u6709\u6CA1\u6709\u624B\u811A\u90FD\u65E0\u6240\u8C13\u3002\u300D</p><p>\u8427\u4F5B\u5974\u8111\u4E2D\u8F70\u7136\u4E00\u54CD\uFF0C\u5979\u8FD9\u624D\u77E5\u9053\u513F\u5B50\u662F\u8981\u628A\u81EA\u5DF1\u5F53\u6210\u4E2A\u5A74\u513F\u822C\u7684\u73A9\u7269\u2026\u2026</p><p>\u300C\u542F\u7980\u5BAB\u4E3B\u3002\u91D1\u957F\u8001\u98DE\u9E3D\u4F20\u4E66\uFF0C\u5468\u79E6\u4E24\u56FD\u6B63\u5728\u6F7C\u5173\u4EA4\u6218\uFF0C\u4ED6\u62BC\u7740\u5BD2\u6708\u5200\u6797\u9999\u8FDC\u7ED5\u9053\u6C49\u5DDD\uFF0C\u56DB\u6708\u521D\u4E00\u8FD4\u5BAB\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u70B9\u70B9\u5934\uFF0C\u300C\u970D\u72C2\u7130\u5462\uFF1F\u300D</p><p>\u300C\u695A\u8FDE\u96C4\u9001\u6765\u6D88\u606F\uFF0C\u4E09\u6708\u4E8C\u5341\u4E8C\u65E5\u3001\u4E8C\u5341\u4E09\u65E5\u970D\u957F\u8001\u4E0E\u6C34\u957F\u8001\u4E0E\u98CE\u665A\u534E\u548C\u73AB\u7470\u4ED9\u5B50\u63A5\u8FDE\u4EA4\u624B\uFF0C\u4E24\u4F4D\u957F\u8001\u8EAB\u8D1F\u91CD\u4F24\u3002\u767D\u6C99\u6D3E\u6B63\u5728\u52A0\u7D27\u8FFD\u8E2A\u4E24\u4EBA\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u8EAB\u5B50\u4E00\u9707\uFF0C\u6D41\u971C\u5251\u7ADF\u8FD9\u4E48\u5389\u5BB3\uFF1F</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u6155\u5BB9\u7D2B\u73AB\u7A74\u9053\u88AB\u5236\uFF0C\u53EA\u80FD\u773C\u7741\u7741\u770B\u7740\u51E0\u540D\u661F\u6708\u6E56\u5E2E\u4F17\u56F4\u4F4F\u81EA\u5DF1\u534A\u88F8\u7684\u8EAB\u4F53\u3002</p><p>\u5B9D\u84DD\u8272\u7684\u62B9\u80F8\u5DF2\u88AB\u6F6D\u6C34\u6D78\u900F\uFF0C\u6E7F\u6DCB\u6DCB\u8D34\u5728\u8EAB\u4E0A\uFF0C\u66F2\u7EBF\u5C3D\u9732\u3002\u56DB\u80A2\u7684\u96EA\u808C\u7389\u80A4\u8FD8\u4E0D\u65AD\u6DCC\u4E0B\u6C34\u73E0\uFF0C\u679C\u771F\u662F\u5A07\u8273\u6B32\u6EF4\u3002</p><p>\u98CE\u665A\u534E\u6C89\u5FC3\u9759\u6C14\uFF0C\u53CC\u638C\u7EF5\u7EF5\u5BC6\u5BC6\u5B88\u4F4F\u8981\u5BB3\u3002\u582A\u582A\u6597\u4E86\u767E\u4F59\u62DB\uFF0C\u771F\u6C14\u8FD0\u8F6C\u5DF2\u7565\u6709\u4E0D\u7545\u3002\u5979\u53CC\u76EE\u5982\u51B0\uFF0C\u4E00\u638C\u51FB\u5012\u70C8\u7130\uFF0C\u540C\u65F6\u4E5F\u88AB\u7384\u51B0\u70B9\u4E2D\u4E00\u6307\uFF0C\u5DE6\u81C2\u987F\u65F6\u9634\u51B7\u523A\u9AA8\uFF0C\u62AC\u4E0D\u8D77\u6765\u3002</p><p>\u518D\u6597\u5341\u4F59\u62DB\uFF0C\u4F24\u52BF\u518D\u65E0\u6CD5\u538B\u6291\uFF0C\u4E0D\u7531\u6A31\u5507\u4E00\u5F20\uFF0C\u5410\u51FA\u4E00\u53E3\u9C9C\u8840\u3002\u98CE\u665A\u534E\u81EA\u77E5\u96BE\u4EE5\u5E78\u514D\uFF0C\u6700\u540E\u8FD0\u8D77\u6BD5\u751F\u529F\u529B\u4E0E\u7384\u51B0\u786C\u62FC\u4E86\u4E00\u638C\u3002\u8FD9\u4E00\u638C\u53EA\u53CA\u5979\u5E73\u65F6\u4E03\u6210\u529B\u9053\uFF0C\u4F46\u5DF2\u5C06\u7384\u51B0\u9707\u5F97\u53E3\u5410\u9C9C\u8840\u3002\u6E05\u9732\u85C9\u673A\u6B3A\u8EAB\u62A2\u4E0A\uFF0C\u70B9\u4E86\u5979\u7684\u7A74\u9053\u3002</p><p>\u4E00\u573A\u5267\u6597\uFF0C\u661F\u6708\u6E56\u4E24\u540D\u9999\u4E3B\u8EAB\u8D1F\u91CD\u4F24\uFF0C\u4F46\u7EC8\u4E8E\u64D2\u5230\u4E86\u98CE\u665A\u534E\u548C\u6155\u5BB9\u7D2B\u73AB\uFF0C\u98D8\u6885\u5CF0\u56DB\u5927\u5F1F\u5B50\u5DF2\u5C3D\u843D\u9B54\u638C\u3002</p><p>\u70C8\u7130\u548C\u7384\u51B0\u88AB\u5C5E\u4E0B\u6276\u8D77\u7597\u4F24\uFF0C\u767D\u7389\u83BA\u8FD8\u8EBA\u5728\u5730\u4E0A\u4E0D\u4F4F\u5598\u7740\u6C14\uFF0C\u661F\u661F\u70B9\u70B9\u7684\u8840\u8FF9\u968F\u7740\u5979\u7684\u547C\u5438\u98DE\u6E85\u51FA\u6765\uFF0C\u6CBE\u5728\u90A3\u5F20\u53EF\u7231\u7684\u5706\u8138\u4E0A\u3002</p><p>\u300C\u88C5\u4EC0\u4E48\u6B7B\uFF1F\u8FD8\u4E0D\u8D76\u5FEB\u722C\u8D77\u6765\uFF01\u300D\u6E05\u9732\u4E00\u58F0\u5389\u559D\uFF0C\u767D\u7389\u83BA\u8FDE\u5FD9\u6323\u624E\u7740\u6491\u8D77\u8EAB\u5B50\u3002</p><p>\u300C\u970D\u957F\u8001\u7ED9\u4F60\u7684\u4E1C\u897F\u5462\uFF1F\u300D\u6E05\u9732\u6DE1\u6DE1\u8BF4\u3002</p><p>\u767D\u7389\u83BA\u5782\u4E0B\u81FB\u9996\uFF0C\u5F53\u7740\u5468\u56F4\u5341\u4F59\u540D\u7537\u5B50\u7684\u9762\u892A\u4E0B\u6E7F\u6F09\u6F09\u7684\u4EB5\u88E4\uFF0C\u7136\u540E\u655E\u5F00\u53CC\u817F\u3002</p><p>\u6155\u5BB9\u7D2B\u73AB\u8FD9\u65F6\u624D\u770B\u6E05\uFF0C\u521A\u624D\u5979\u817F\u95F4\u7684\u90A3\u62B9\u9ED1\u8272\u7ADF\u7136\u662F\u4E00\u4E2A\u6728\u585E\u2014\u2014\u5979\u7ADF\u662F\u5E26\u7740\u8FD9\u4E2A\u6728\u585E\u5728\u5C71\u4E2D\u8DCB\u6D89\u7EC8\u65E5\u3002</p><p>\u300C\u53BB\u62B1\u7740\u90A3\u68F5\u6811\u3002\u300D\u6E05\u9732\u8BF4\u5B8C\u4FBF\u4E0D\u518D\u7406\u4F1A\uFF0C\u8FF3\u76F4\u8D70\u5230\u98CE\u665A\u534E\u8EAB\u8FB9\u5FAE\u7B11\u7740\u4F38\u51FA\u4E24\u624B\uFF0C\u6293\u4F4F\u8863\u9886\u5411\u4E24\u8FB9\u6162\u6162\u62C9\u5F00\u3002</p><p>\u9752\u8863\u6E10\u6E10\u5206\u5F00\uFF0C\u9732\u51FA\u6D01\u767D\u7684\u80F8\u812F\u3002\u4E24\u62B9\u6D51\u5706\u7684\u4E73\u8089\u95F4\u5939\u7740\u4E00\u9053\u6DF1\u6DF1\u7684\u4E73\u6C9F\uFF0C\u6563\u53D1\u7740\u5904\u5B50\u7684\u5E7D\u9999\u3002</p><p>\u767D\u7389\u83BA\u4E56\u4E56\u62B1\u7740\u4E00\u68F5\u5927\u6811\uFF0C\u4FEF\u8EAB\u5F13\u8170\uFF0C\u4E24\u817F\u5FAE\u5206\uFF0C\u9AD8\u9AD8\u7FD8\u8D77\u96EA\u81C0\uFF0C\u4EFB\u661F\u6708\u6E56\u7FA4\u4F17\u8F6E\u756A\u5978\u6DEB\u3002\u770B\u7740\u5979\u9165\u4E73\u5728\u7C97\u7CD9\u7684\u6811\u76AE\u4E0A\u4E0D\u65AD\u78B0\u649E\uFF0C\u6155\u5BB9\u7D2B\u73AB\u7EC8\u4E8E\u660E\u767D\u8FC7\u6765\u3002</p><p>\u767D\u7389\u83BA\u4E0E\u59B9\u59B9\u6839\u672C\u672A\u66FE\u5230\u5BB6\u3002\u4E0E\u7D2B\u73AB\u5206\u624B\u540E\uFF0C\u8FD9\u5BF9\u6BEB\u65E0\u6C5F\u6E56\u7ECF\u9A8C\u7684\u59D0\u59B9\u82B1\u5F53\u65E5\u4E0B\u5348\u88AB\u970D\u72C2\u7130\u4EB2\u81EA\u5E26\u961F\u8FFD\u4E0A\u3002\u970D\u72C2\u7130\u89C1\u767D\u6C0F\u59D0\u59B9\u96BE\u5F97\u7684\u76F8\u8C8C\u5982\u4E00\uFF0C\u7389\u96EA\u53EF\u7231\uFF0C\u820D\u4E0D\u5F97\u50CF\u4EE5\u5F80\u822C\u8FA3\u624B\u6467\u82B1\uFF0C\u4FBF\u628A\u4E24\u5973\u5E26\u5728\u8EAB\u8FB9\u5C3D\u65E5\u6DEB\u8FB1\u3002</p><p>\u6155\u5BB9\u9F99\u6025\u4E8E\u751F\u64D2\u8427\u4F5B\u5974\u548C\u6155\u5BB9\u7D2B\u73AB\uFF0C\u4E94\u5802\u4ED3\u4FC3\u51FA\u624B\uFF0C\u8BA1\u5212\u4E0D\u5468\uFF0C\u7ED3\u679C\u88AB\u7D2B\u73AB\u4E00\u8DEF\u9003\u81F3\u98D8\u6885\u5CF0\u3002\u96EA\u5CF0\u795E\u5C3C\u5A01\u540D\u8D6B\u8D6B\uFF0C\u91D1\u5F00\u7532\u4E5F\u4E0D\u6562\u8F7B\u6613\u5165\u5C71\u3002\u6D88\u606F\u4F20\u6765\uFF0C\u6155\u5BB9\u9F99\u4FBF\u547D\u4E94\u884C\u8BF8\u5802\u56DE\u5BAB\u5546\u8BAE\u3002</p><p>\u5176\u65F6\u5C60\u6000\u6C89\u4F24\u91CD\u4E0D\u8D77\uFF1B\u6C90\u58F0\u4F20\u5DF2\u8FD4\u661F\u6708\u6E56\uFF1B\u91D1\u5802\u8BF8\u4EBA\u5E26\u7740\u6797\u9999\u8FDC\u6B63\u5728\u9014\u4E2D\uFF0C\u53EA\u6709\u6C34\u3001\u706B\u4E24\u5802\u5728\u6E58\u897F\u4E00\u5E26\uFF0C\u63A5\u5230\u695A\u8FDE\u96C4\u7684\u60C5\u62A5\uFF0C\u970D\u72C2\u7130\u7ACB\u529F\u5FC3\u5207\uFF0C\u62A2\u5148\u8D76\u5230\u767D\u6C99\u5858\u3002\u6CA1\u60F3\u5230\u4E00\u62DB\u5C31\u4F24\u5728\u98CE\u665A\u534E\u5251\u4E0B\u3002</p><p>\u4E94\u957F\u8001\u9664\u6C90\u58F0\u4F20\u5916\uFF0C\u5C3D\u6570\u53D7\u4F24\uFF0C\u53C8\u642D\u4E0A\u6570\u540D\u9999\u4E3B\uFF0C\u661F\u6708\u6E56\u6B64\u5F79\u53EF\u8C13\u662F\u635F\u5175\u6298\u5C06\u3002</p><p>\u6C34\u3001\u706B\u4E24\u5802\u6599\u60F3\u98CE\u665A\u534E\u548C\u6155\u5BB9\u7D2B\u73AB\u5FC5\u7136\u4F1A\u6CBF\u9014\u8FFD\u8E2A\uFF0C\u4FBF\u6263\u4F4F\u59B9\u59B9\u767D\u7389\u9E42\uFF0C\u8BA9\u7389\u83BA\u8BC8\u4F5C\u5076\u9047\uFF0C\u4E00\u8DEF\u7559\u4E0B\u6807\u8BB0\u3002\u5C31\u5728\u7D2B\u73AB\u5728\u6F6D\u8FB9\u620F\u800D\u65F6\uFF0C\u4E24\u5802\u5DF2\u7CBE\u82F1\u5C3D\u51FA\uFF0C\u6F5C\u4F0F\u5728\u4FA7\u3002</p><p>\u6E05\u9732\u6EE1\u8138\u5FAE\u7B11\u7684\u628A\u624B\u4F38\u8FDB\u98CE\u665A\u534E\u6000\u4E2D\u3002\u9752\u8863\u95F4\u767D\u5149\u95EA\u52A8\uFF0C\u9732\u51FA\u4E24\u53EA\u9971\u6EE1\u575A\u633A\u7684\u7389\u4E73\u3002\u98CE\u665A\u534E\u53CC\u76EE\u5FAE\u95ED\uFF0C\u604D\u82E5\u672A\u89C9\u3002\u6E05\u9732\u4F38\u51FA\u5C16\u5229\u7684\u6307\u7532\u5728\u5979\u4E73\u5C16\u4E0A\u91CD\u91CD\u6390\u4E86\u4E00\u628A\uFF0C\u51B7\u7B11\u9053\uFF1A\u300C\u6D41\u971C\u5251\u597D\u5927\u7684\u540D\u58F0\uFF0C\u539F\u6765\u4E5F\u4E0D\u8FC7\u5C14\u5C14\u3002\u5012\u662F\u8FD9\u5BF9\u5976\u5B50\u633A\u62DB\u4EBA\u75BC\u7684\u2026\u2026\u300D</p><p>\u6155\u5BB9\u7D2B\u73AB\u4E0A\u5C71\u65F6\uFF0C\u98CE\u665A\u534E\u5DF2\u7ECF\u540D\u52A8\u6C5F\u6E56\u3002\u4ECE\u5165\u95E8\u90A3\u5929\u8D77\uFF0C\u5979\u5C31\u628A\u8FD9\u4E2A\u98CE\u91C7\u7167\u4EBA\uFF0C\u6280\u827A\u8D85\u7FA4\u7684\u5927\u5E08\u59D0\u89C6\u4E3A\u5076\u50CF\u3002\u53EF\u73B0\u5728\u5927\u5E08\u59D0\u5374\u5728\u81EA\u5DF1\u9762\u524D\u7389\u4E73\u5766\u9732\uFF0C\u4EFB\u4EBA\u73A9\u5F04\u3002\u770B\u5230\u90A3\u5973\u4EBA\u50CF\u6446\u5F04\u73A9\u5076\u822C\u7528\u5F2F\u5251\u6311\u5F00\u98CE\u665A\u534E\u7684\u88D9\u88E4\uFF0C\u7D2B\u73AB\u4E0D\u7981\u5FC3\u5982\u5200\u5272\uFF0C\u624B\u811A\u51B0\u51C9\u3002\u4E00\u9635\u5BD2\u610F\u88AD\u6765\uFF0C\u7D2B\u73AB\u624D\u60F3\u8D77\u81EA\u5DF1\u53EA\u6709\u4EF6\u62B9\u80F8\u906E\u4F53\uFF0C\u4E0D\u7981\u82B3\u5FC3\u63EA\u7D27\u3002</p><p>\u65C1\u8FB9\u7A81\u7136\u4F20\u6765\u4E00\u58F0\u75DB\u53EB\u3002\u53D7\u4E86\u5185\u4F24\u7684\u767D\u7389\u83BA\u5DF2\u7ECF\u652F\u6301\u4E0D\u4F4F\uFF0C\u8DEA\u5728\u5730\u4E0A\uFF0C\u4E00\u4E2A\u7537\u4EBA\u6B63\u62B1\u7740\u5979\u7684\u5706\u81C0\uFF0C\u628A\u4E4C\u9ED1\u7684\u8089\u68D2\u6345\u8FDB\u7A84\u5C0F\u7684\u809B\u6D1E\u3002\u767D\u7389\u83BA\u5634\u89D2\u6EA2\u8840\uFF0C\u5706\u8138\u4E0A\u6302\u6EE1\u6CEA\u73E0\u3002</p><p>\u4E00\u4E2A\u9ED1\u8863\u4EBA\u671D\u81EA\u5DF1\u8D70\u8FC7\u6765\uFF0C\u7D2B\u73AB\u5FC3\u810F\u4E00\u9635\u6025\u8DF3\u3002\u867D\u7136\u8EAB\u4E3A\u80E1\u4EBA\uFF0C\u4F46\u5979\u4ECE\u5C0F\u4F4F\u5728\u98D8\u6885\u5CF0\uFF0C\u5BF9\u8D1E\u6D01\u7684\u73CD\u89C6\u4E00\u5982\u51E0\u4F4D\u5E08\u59D0\u3002\u5047\u5982\u50CF\u767D\u7389\u83BA\u90A3\u6837\u4EFB\u4EBA\u51CC\u8FB1\uFF0C\u5979\u5B81\u80AF\u81EA\u5C3D\u3002</p><p>\u84E6\u5730\uFF0C\u4E00\u4E2A\u4E1C\u897F\u843D\u5728\u8EAB\u4E0A\u3002\u5374\u662F\u81EA\u5DF1\u7684\u7EA2\u886B\u3002</p>',58);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
