import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_19" tabindex="-1"><a class="header-anchor" href="#_19" aria-hidden="true">#</a> 19</h1><p>\u90A3\u4E2A\u9ED1\u8863\u4EBA\u6258\u8D77\u6155\u5BB9\u7D2B\u73AB\u8F6F\u7EF5\u7EF5\u7684\u8EAB\u4F53\uFF0C\u5E2E\u5979\u5957\u4E0A\u8863\u670D\u3002\u5C3D\u7BA1\u5BAB\u4E3B\u6709\u4E25\u4EE4\uFF0C\u4F46\u90A3\u4EBA\u8FD8\u662F\u5FCD\u4E0D\u4F4F\u5728\u73AB\u7470\u4ED9\u5B50\u5E7D\u9999\u56DB\u6EA2\u7684\u96EA\u80A4\u4E0A\u6084\u6084\u634F\u4E86\u51E0\u628A\u3002\u7EA4\u8DB3\u88AB\u964C\u751F\u4EBA\u6525\u4F4F\u624B\u5FC3\u91CC\u63C9\u634F\uFF0C\u6155\u5BB9\u7D2B\u73AB\u53C8\u662F\u6076\u5FC3\u53C8\u662F\u607C\u6012\uFF0C\u6068\u6068\u77AA\u4E86\u4ED6\u4E00\u773C\u3002\u90A3\u4EBA\u8FDE\u5FD9\u653E\u624B\uFF0C\u82E5\u65E0\u5176\u4E8B\u7684\u7AD9\u4E86\u8D77\u6765\uFF0C\u8D70\u5230\u767D\u7389\u83BA\u8EAB\u8FB9\uFF0C\u628A\u65E9\u5DF2\u52C3\u8D77\u7684\u9633\u5177\u6345\u8FDB\u5979\u7684\u5C0F\u5634\u91CC\u3002</p><p>\u867D\u7136\u4E0D\u77E5\u9053\u4ED6\u4E3A\u4F55\u5927\u53D1\u5584\u5FC3\uFF0C\u4F46\u7A7F\u4E0A\u8863\u670D\uFF0C\u6155\u5BB9\u7D2B\u73AB\u5FC3\u91CC\u987F\u65F6\u5B89\u5B89\u4E86\u8BB8\u591A\u3002</p><p>\u98CE\u665A\u534E\u6B64\u65F6\u5374\u662F\u8EAB\u65E0\u5BF8\u7F15\u3002\u6E05\u9732\u7528\u5251\u810A\u5728\u5979\u8D64\u88F8\u7684\u8089\u4F53\u4E0A\u6765\u56DE\u62D6\u52A8\uFF0C\u6162\u6162\u4F38\u5230\u8179\u4E0B\u3002\u767D\u5AE9\u7684\u9634\u961C\u5E03\u6EE1\u4E4C\u4EAE\u7684\u6BDB\u53D1\uFF0C\u5FAE\u9F13\u9F13\u8D77\uFF0C\u6B63\u4E2D\u4E24\u7247\u7A84\u7A84\u7684\u7EA2\u8089\u7D27\u7D27\u5408\u62E2\u7740\uFF0C\u6CA1\u6709\u4E00\u70B9\u7F1D\u9699\u3002\u6E05\u9732\u628A\u5F2F\u66F2\u7684\u957F\u5251\u5E73\u653E\u5728\u98CE\u665A\u534E\u4E24\u817F\u95F4\uFF0C\u5251\u5C16\u62E8\u5F00\u82B1\u74E3\uFF0C\u7F13\u7F13\u5411\u5185\u63A2\u5165\u3002</p><p>\u98CE\u665A\u534E\u4ECD\u662F\u53CC\u76EE\u5FAE\u95ED\uFF0C\u65E0\u52A8\u4E8E\u8877\u7684\u6A21\u6837\u3002\u6E05\u9732\u5FC3\u5934\u706B\u8D77\uFF0C\u624B\u4E2D\u4E00\u7D27\uFF0C\u4FBF\u6B32\u523A\u5165\u6D41\u971C\u5251\u5904\u5B50\u4F53\u5185\u3002</p><p>\u300C\u4F4F\u624B\u3002\u300D\u7384\u51B0\u52C9\u529B\u53EB\u9053\u3002\u4ED6\u4E0E\u98CE\u665A\u534E\u786C\u62FC\u4E00\u638C\uFF0C\u53D7\u4F24\u4E0D\u8F7B\uFF0C\u8C03\u606F\u591A\u65F6\u4E5F\u672A\u80FD\u5236\u4F4F\u4E39\u7530\u7684\u5267\u75DB\u3002\u4F46\u770B\u5230\u6E05\u9732\u5982\u6B64\u66B4\u6B84\u5929\u7269\uFF0C\u5FCD\u4E0D\u4F4F\u5F00\u58F0\u559D\u6B62\u3002</p><p>\u7384\u51B0\u4F4D\u5728\u6E05\u9732\u4E4B\u4E0A\uFF0C\u6E05\u9732\u4E5F\u4E0D\u6562\u608D\u7136\u4E0B\u624B\uFF0C\u51B7\u7B11\u4E00\u58F0\u6536\u8D77\u5F2F\u5251\u3002</p><p>\u7384\u51B0\u62AC\u4E86\u62AC\u624B\uFF0C\u81EA\u6709\u51E0\u540D\u5E2E\u4F17\u8FC7\u6765\u6400\u8D77\u4ED6\u548C\u70C8\u7130\u3002</p><p>\u5438\u4E86\u53E3\u6C14\uFF0C\u7384\u51B0\u7F13\u7F13\u9053\uFF1A\u300C\u64D2\u4E0B\u73AB\u7470\u4ED9\u5B50\u548C\u6D41\u971C\u5251\uFF0C\u5927\u4F19\u529F\u52B3\u4E0D\u5C0F\u3002\u5148\u8D76\u4E0A\u970D\u957F\u8001\uFF0C\u4E00\u9053\u56DE\u5BAB\u3002\u300D</p><p>\u4F17\u4EBA\u9F50\u58F0\u5E94\u558F\u3002</p><p>\u661F\u6708\u6E56\u5E2E\u4F17\u5728\u6797\u91CC\u8FFD\u8E2A\u4E86\u4E09\u5929\uFF0C\u6B64\u65F6\u5927\u529F\u544A\u6210\uFF0C\u90FD\u662F\u5FC3\u82B1\u6012\u653E\u3002\u6155\u5BB9\u7D2B\u73AB\u662F\u5BAB\u4E3B\u7981\u8114\uFF0C\u98CE\u665A\u534E\u65E2\u662F\u5927\u540D\u9F0E\u9F0E\u7684\u5973\u4FA0\uFF0C\u53C8\u662F\u5904\u5B50\u4E4B\u8EAB\uFF0C\u8981\u7B49\u957F\u8001\u53D1\u8BDD\uFF0C\u8FD9\u53EF\u82E6\u4E86\u767D\u7389\u83BA\u3002\u5979\u62B1\u7740\u9A6C\u9888\uFF0C\u9634\u961C\u9876\u5728\u9A6C\u978D\u8FB9\u7F18\uFF0C\u4E24\u817F\u5F2F\u66F2\uFF0C\u655E\u9732\u7684\u79D8\u5904\u6B63\u5BF9\u7740\u7537\u4EBA\u7684\u9633\u5177\u3002</p><p>\u90A3\u4EBA\u4E5F\u4E0D\u52A8\u4F5C\uFF0C\u53EA\u9760\u9A6C\u5339\u5954\u9A70\u65F6\u5FEB\u65F6\u6162\u7684\u968F\u610F\u62BD\u9001\u3002\u7B49\u4ED6\u53D1\u6CC4\u5B8C\uFF0C\u4FBF\u62CE\u8D77\u767D\u7389\u83BA\u51CC\u7A7A\u6254\u7ED9\u9644\u8FD1\u5E2E\u4F17\u3002\u8FD9\u4E00\u8DEF\u5979\u5728\u9A6C\u80CC\u4E0A\u4E0D\u505C\u7A7F\u68AD\uFF0C\u4F46\u6CA1\u6709\u4E00\u6B21\u662F\u5750\u5728\u4E0A\u9762\u3002</p><p>\u65F6\u800C\u4EF0\u8EAB\u5E73\u8EBA\uFF0C\u65F6\u800C\u88AB\u7537\u4EBA\u6402\u5728\u6000\u4E2D\uFF0C\u65F6\u800C\u8E72\u5728\u978D\u4E0A\u2014\u2014\u65E0\u8BBA\u90A3\u79CD\u59FF\u52BF\uFF0C\u8089\u7A74\u4E2D\u59CB\u7EC8\u62BD\u7740\u9633\u5177\u3002</p><p>\u6E05\u9732\u6709\u610F\u6298\u8FB1\u98CE\u665A\u534E\uFF0C\u7279\u610F\u6311\u4E86\u5339\u9A6C\uFF0C\u53BB\u6389\u9A6C\u978D\uFF0C\u5C06\u98CE\u665A\u534E\u8D64\u8EAB\u653E\u5728\u4E0A\u9762\uFF0C\u7528\u7EF3\u7D22\u7A7F\u8FC7\u9A6C\u8179\u5C06\u5979\u4E24\u811A\u6346\u5728\u4E00\u8D77\u3002\u9A6C\u5339\u7A81\u8D77\u7684\u810A\u9AA8\u5361\u5728\u8089\u7F1D\u4E2D\uFF0C\u6B63\u538B\u4F4F\u82B1\u8482\u3002</p><p>\u67D4\u5AE9\u7684\u79D8\u5904\u9876\u6BDB\u53D1\u8038\u7136\u7684\u9A6C\u80CC\u4E0A\uFF0C\u4E0D\u4F4F\u6469\u64E6\u3002</p><p>\u5929\u8272\u6E10\u66AE\uFF0C\u4E00\u884C\u4EBA\u5954\u5230\u5927\u5C71\u8FB9\u7F18\u3002\u70C8\u7130\u548C\u7384\u51B0\u4F24\u52BF\u96BE\u4EE5\u6291\u5236\uFF0C\u8138\u8272\u53D1\u7070\uFF0C\u4F17\u4EBA\u53EA\u597D\u505C\u4E0B\u6B47\u5BBF\u3002</p><p>\u300C\u546F\u300D\u7684\u4E00\u58F0\uFF0C\u767D\u7389\u83BA\u88AB\u4EBA\u4ECE\u9A6C\u4E0A\u6254\u4E0B\u6765\u3002\u5979\u4E0D\u4F4F\u54AF\u7740\u8840\uFF0C\u624B\u811A\u62BD\u6410\u3002\u500D\u53D7\u6298\u78E8\u7684\u4E0B\u4F53\u53C8\u7EA2\u53C8\u80BF\uFF0C\u5728\u817F\u95F4\u9F13\u6210\u4E00\u56E2\u3002\u6D4A\u767D\u7684\u7CBE\u6DB2\u4E0D\u4F4F\u6D8C\u51FA\uFF0C\u6CBE\u5728\u8EAB\u4E0B\u5AE9\u7EFF\u7684\u9752\u8349\u4E0A\u3002</p><p>\u4F17\u4EBA\u5BF9\u5979\u6BEB\u4E0D\u7406\u4F1A\uFF0C\u53EA\u5FD9\u7740\u5B89\u7F6E\u5404\u4EBA\u7684\u5BBF\u5904\u3002</p><p>\u300C\u563F\uFF0C\u6D41\u971C\u5251\u771F\u591F\u9A9A\u7684\uFF0C\u6D41\u4E86\u8FD9\u4E48\u591A\u6C34\u513F\uFF01\u300D\u4E00\u4E2A\u6C49\u5B50\u602A\u53EB\u7740\u63B0\u5F00\u98CE\u665A\u534E\u53CC\u817F\uFF0C\u628A\u7389\u6237\u4E3E\u5230\u4F17\u4EBA\u9762\u524D\u3002</p><p>\u98CE\u665A\u534E\u8EAB\u4F53\u654F\u611F\uFF0C\u4E00\u8DEF\u98A0\u7C38\uFF0C\u4F7F\u5979\u5BC6\u95ED\u7684\u82B1\u74E3\u7FFB\u5377\u5F00\u6765\uFF0C\u6E7F\u6DCB\u6DCB\u4E00\u7247\u6C34\u5149\u3002</p><p>\u4E00\u7FA4\u7537\u4EBA\u56F4\u7740\u8D64\u88F8\u7684\u98CE\u665A\u534E\u6307\u6307\u70B9\u70B9\u6DEB\u58F0\u6D6A\u8BED\u4E0D\u7EDD\u4E8E\u8033\uFF0C\u300C\u4EC0\u4E48\u6D41\u971C\u5251\uFF1F\u4E0D\u5C31\u662F\u4E2A\u6D41\u6C34\u513F\u7684\u8D31\u4EBA\u561B\u3002\u300D</p><p>\u300C\u6CA1\u4EBA\u78B0\u5462\uFF0C\u90FD\u6E7F\u6210\u8FD9\u6837\uFF0C\u8BA9\u5927\u7237\u64CD\u51E0\u4E0B\uFF0C\u8FD8\u4E0D\u723D\u5F97\u5C3F\u51FA\u6765\uFF1F\u300D</p><p>\u300C\u5BD2\u6708\u5200\u662F\u5979\u5E08\u59B9\u5427\uFF0C\u4E5F\u6D6A\u7740\u5462\u3002\u51E0\u5341\u53F7\u4EBA\u64CD\u4E86\u4E00\u6574\u5929\uFF0C\u5979\u8FD8\u76F4\u54FC\u54FC\u5462\u2026\u300D</p><p>\u5341\u53EA\u624B\u5728\u98CE\u665A\u534E\u8EAB\u4E0A\u56DB\u5904\u4E71\u6478\uFF0C\u6D51\u5706\u7684\u4E73\u623F\u88AB\u634F\u5F97\u4E0D\u4F4F\u53D8\u5F62\u3002\u6709\u4EBA\u751A\u81F3\u628A\u624B\u6307\u4F38\u8FDB\u5979\u6ED1\u817B\u7684\u4E0B\u4F53\uFF0C\u6D45\u6D45\u6363\u5F04\u3002</p><p>\u6155\u5BB9\u7D2B\u73AB\u4F7F\u52B2\u7728\u4E86\u7728\u773C\uFF0C\u628A\u6CEA\u6C34\u786C\u538B\u56DE\u53BB\uFF0C\u4FA7\u8138\u770B\u5411\u4E00\u8FB9\u3002\u767D\u7389\u83BA\u7684\u76EE\u5149\u6B63\u671D\u8FD9\u8FB9\u770B\u6765\uFF0C\u4E0E\u5979\u773C\u775B\u4E00\u89E6\uFF0C\u7ACB\u523B\u5782\u4E86\u4E0B\u53BB\u3002</p><p>\u770B\u5230\u5979\u773C\u4E2D\u7684\u6CEA\u6C34\u548C\u5507\u89D2\u7684\u9C9C\u8840\uFF0C\u7D2B\u73AB\u7684\u6068\u610F\u6E10\u6E10\u6D88\u6563\u3002\u867D\u7136\u51FA\u5356\u4E86\u81EA\u5DF1\uFF0C\u4F46\u5C0F\u83BA\u4E5F\u5F88\u53EF\u601C\u2026\u2026</p><p>\u6E05\u9732\u5206\u5F00\u4F17\u4EBA\uFF0C\u62D6\u8D77\u98CE\u665A\u534E\u653E\u5230\u7D2B\u73AB\u8EAB\u65C1\uFF0C\u7B11\u9053\uFF1A\u300C\u522B\u6015\u90A3\u4E9B\u5BB6\u4F19\u3002\u98CE\u5973\u4FA0\u9609\u4E86\u54B1\u4EEC\u970D\u957F\u8001\uFF0C\u6CA1\u4ED6\u8001\u4EBA\u5BB6\u53D1\u8BDD\uFF0C\u8C01\u90FD\u4E0D\u6562\u52A8\u4F60\u5462\u3002\u300D\u8BF4\u7740\u5728\u98CE\u665A\u534E\u4E0B\u4F53\u6390\u4E86\u4E00\u628A\u3002</p><p>\u4E00\u8DEF\u6C89\u9ED8\u7684\u98CE\u665A\u534E\u7A81\u7136\u7741\u5F00\u53CC\u773C\uFF0C\u76EE\u4E2D\u7CBE\u5149\u5927\u76DB\u3002</p><p>\u6E05\u9732\u4E00\u60CA\uFF0C\u8FDE\u5FD9\u62BD\u8EAB\u9000\u5F00\uFF0C\u624B\u5374\u88AB\u4E24\u6761\u6D51\u5706\u7684\u5927\u817F\u7D27\u7D27\u5939\u4F4F\u3002\u63A5\u7740\u80F8\u53E3\u5982\u53D7\u96F7\u51FB\uFF0C\u4E00\u80A1\u521A\u731B\u7684\u529B\u9053\u9707\u788E\u62A4\u4F53\u771F\u6C14\uFF0C\u76F4\u5165\u5FC3\u8109\u3002</p><p>\u98CE\u665A\u534E\u4E00\u638C\u51FB\u5012\u6E05\u9732\uFF0C\u7389\u6307\u7531\u80A9\u81F3\u8E1D\u5FEB\u6377\u65E0\u4F26\u7684\u62C2\u8FC7\u7D2B\u73AB\u4E03\u5904\u88AB\u5C01\u7684\u5927\u7A74\uFF0C\u7136\u540E\u6258\u5728\u5E08\u59B9\u8170\u95F4\u7528\u529B\u7529\u51FA\u3002</p><p>\u7D2B\u73AB\u6D51\u8EAB\u4E00\u9707\uFF0C\u51E4\u51F0\u5B9D\u5178\u7684\u771F\u6C14\u7ACB\u523B\u6D41\u52A8\u8D77\u6765\u3002\u5979\u5728\u7A7A\u4E2D\u56DE\u5934\u671B\u53BB\uFF0C\u6B63\u770B\u5230\u5E08\u59D0\u53E3\u4E2D\u9C9C\u8840\u72C2\u55B7\uFF0C\u4EF0\u8EAB\u5012\u4E0B\u3002</p><p>\u6155\u5BB9\u7D2B\u73AB\u67D4\u8EAF\u4E00\u65CB\uFF0C\u5728\u7A7A\u4E2D\u5212\u4E86\u4E2A\u4F18\u7F8E\u7684\u5706\u5F27\uFF0C\u5408\u8EAB\u6251\u4E0B\uFF0C\u638C\u8D77\u8098\u843D\uFF0C\u5DF2\u51FB\u5012\u4E24\u540D\u661F\u6708\u6E56\u5E2E\u4F17\u3002\u5979\u51E4\u51F0\u5B9D\u5178\u5DF2\u6709\u5C0F\u6210\uFF0C\u867D\u7136\u96BE\u4E0E\u957F\u8001\u7EA7\u9AD8\u624B\u5BF9\u6297\uFF0C\u4F46\u5BF9\u4ED8\u8FD9\u4E9B\u5E2E\u4F17\u5374\u662F\u6E38\u5203\u6709\u4F59\u3002\u6B64\u65F6\u542B\u6012\u51FA\u624B\uFF0C\u62DB\u672F\u66F4\u662F\u72E0\u8FA3\u5F02\u5E38\u3002\u7EA4\u624B\u4E00\u6C89\u51FB\u5728\u4E00\u4EBA\u4E0B\u9634\uFF0C\u90A3\u4EBA\u6839\u672C\u6CA1\u60F3\u5230\u8FD9\u4E2A\u4ED9\u5B50\u822C\u7684\u5C0F\u5973\u5B69\u4F1A\u4E0B\u6B64\u6BD2\u624B\uFF0C\u4E24\u773C\u4E00\u7FFB\uFF0C\u4FBF\u5373\u6BD5\u547D\u3002</p><p>\u98D8\u6885\u5CF0\u8BF8\u5F1F\u5B50\u4EE5\u98CE\u665A\u534E\u6B66\u529F\u6700\u9AD8\uFF0C\u4FE8\u7136\u6709\u5B97\u5E08\u98CE\u8303\uFF1B\u6797\u9999\u8FDC\u82F1\u6C14\u8FC7\u4EBA\uFF0C\u4E0E\u4EBA\u5BF9\u654C\u51CC\u5389\u800C\u4E0D\u72E0\u8FA3\uFF1B\u7EAA\u7709\u59A9\u6027\u60C5\u6E29\u548C\uFF0C\u7275\u4E1D\u624B\u62DB\u672F\u7EC6\u817B\u7EF5\u5BC6\uFF0C\u76F4\u5982\u6625\u98CE\uFF0C\u6E29\u5A49\u6709\u4F59\uFF0C\u521A\u52B2\u4E0D\u8DB3\uFF1B\u800C\u7D2B\u73AB\u8EAB\u4E0A\u5374\u6D41\u7740\u6155\u5BB9\u5BB6\u65CF\u7684\u8840\u6DB2\u3002</p><p>\u8FD9\u4E2A\u5BB6\u65CF\u767E\u5E74\u6765\u6570\u8D77\u6570\u706D\uFF0C\u8BF4\u4E0D\u5C3D\u7684\u8363\u8FB1\u60B2\u6B22\u3002\u4F46\u6BCF\u4E2A\u59D3\u6155\u5BB9\u7684\u4E0D\u4EC5\u76F8\u8C8C\u4FCA\u7F8E\uFF0C\u800C\u4E14\u90FD\u6709\u79CD\u5947\u5F02\u7684\u6D3B\u529B\u3002\u6155\u5BB9\u5BB6\u66FE\u6709\u4E00\u4F4D\u5148\u7956\uFF0C\u5341\u5C81\u88AB\u5C01\u4E3A\u5927\u53F8\u9A6C\uFF1B\u5341\u4E8C\u5C81\u56FD\u7834\u88AB\u63B3\u5165\u654C\u5BAB\uFF0C\u5F53\u505A\u5A08\u7AE5\uFF1B\u5341\u4F59\u5E74\u540E\u8D77\u5175\u53CD\u53DB\uFF0C\u81EA\u7ACB\u4E3A\u5E1D\uFF0C\u6570\u5E74\u4E2D\u6740\u63A0\u65E0\u6570\u2014\u2014\u6155\u5BB9\u7D2B\u73AB\u5E76\u4E0D\u77E5\u9053\u8FD9\u4E9B\uFF0C\u4F46\u8FD9\u80A1\u4E0E\u751F\u4FF1\u6765\u7684\u91CE\u6027\u8840\u6DB2\u5374\u4F7F\u5979\u8FE5\u5F02\u4E8E\u4E09\u4F4D\u5E08\u59D0\u3002</p><p>\u5750\u5728\u98CE\u665A\u534E\u8EAB\u524D\u7684\u70C8\u7130\u4F24\u52BF\u672A\u7652\uFF0C\u6B64\u65F6\u89C1\u5230\u6155\u5BB9\u7D2B\u73AB\u6765\u52BF\u51F6\u731B\uFF0C\u53EA\u597D\u51FA\u624B\u62B5\u6321\u3002</p><p>\u7D2B\u73AB\u5288\u624B\u62E7\u4F4F\u4ED6\u7684\u624B\u8155\uFF0C\u7693\u8155\u4E00\u7FFB\u5DF2\u5C06\u4ED6\u7684\u8155\u9AA8\u62E7\u65AD\uFF0C\u63A5\u7740\u8098\u5C16\u649E\u4F4F\u80F8\u53E3\u3002\u70C8\u7130\u4F24\u4E0A\u52A0\u4F24\uFF0C\u987F\u65F6\u4E00\u547D\u545C\u547C\u3002</p><p>\u6155\u5BB9\u7D2B\u73AB\u8E22\u5F00\u70C8\u7130\u7684\u5C38\u4F53\uFF0C\u6B63\u5F85\u633D\u8D77\u5E08\u59D0\uFF0C\u5374\u89C1\u773C\u524D\u5BD2\u5149\u95EA\u52A8\uFF0C\u4F59\u4E0B\u7684\u516B\u540D\u5E2E\u4F17\u62FE\u8D77\u5175\u5203\u56F4\u4E86\u8FC7\u6765\u3002\u7D2B\u73AB\u659C\u8EAB\u62A2\u5165\uFF0C\u6625\u8471\u822C\u7684\u7389\u6307\u5982\u5170\u82B1\u7EFD\u653E\uFF0C\u786C\u751F\u751F\u671D\u5176\u4E2D\u4E00\u628A\u957F\u5251\u593A\u53BB\u3002</p><p>\u300C\u4F4F\u624B\uFF01\u300D\u65C1\u8FB9\u4F20\u6765\u4E00\u58F0\u5389\u559D\u3002</p><p>\u7384\u51B0\u62FF\u7740\u6E05\u9732\u7684\u5F2F\u5251\u62B5\u5728\u98CE\u665A\u534E\u5589\u5934\uFF0C\u300C\u52A8\u4E00\u4E0B\u6211\u5C31\u6740\u4E86\u5979\uFF01\u300D</p><p>\u6155\u5BB9\u7D2B\u73AB\u4E24\u624B\u63E1\u62F3\uFF0C\u7F8E\u76EE\u55B7\u706B\u822C\u6012\u89C6\u4ED6\u3002\u5E08\u59D0\u62FC\u7740\u91CD\u4F24\u51B2\u5F00\u7A74\u9053\u6551\u51FA\u81EA\u5DF1\uFF0C\u5374\u56E0\u6B64\u843D\u4EBA\u654C\u624B\uFF0C\u6BEB\u65E0\u62B5\u6297\u4E4B\u529B\u3002</p><p>\u7384\u51B0\u624B\u4E00\u7D27\uFF0C\u5251\u950B\u523A\u5165\u96EA\u767D\u7684\u808C\u80A4\uFF0C\u300C\u9000\u5230\u4E00\u8FB9\uFF01\u300D</p><p>\u7D2B\u73AB\u6068\u4E0D\u5F97\u6390\u6B7B\u4ED6\uFF0C\u4F46\u53EA\u80FD\u4F9D\u8A00\u9000\u5F00\u3002</p><p>\u300C\u8DEA\u4E0B\uFF01\u300D</p><p>\u98CE\u665A\u534E\u5589\u4E0A\u6E17\u51FA\u4E00\u7F15\u9C9C\u8840\uFF0C\u7D2B\u73AB\u6162\u6162\u8DEA\u5728\u5730\u4E0A\u3002</p><p>\u300C\u5C01\u4E86\u81EA\u5DF1\u4E24\u817F\u548C\u53F3\u81C2\u7684\u7A74\u9053\uFF01\u300D</p><p>\u6155\u5BB9\u7D2B\u73AB\u79C0\u7709\u4E00\u6311\uFF0C\u5374\u6CA1\u6709\u52A8\u4F5C\u3002</p><p>\u300C\u5C01\u4E0D\u5C01\uFF01\u300D\u7384\u51B0\u5389\u559D\u7740\uFF0C\u5F2F\u5251\u53C8\u523A\u5165\u4E00\u5206\u3002</p><p>\u7D2B\u73AB\u6076\u72E0\u72E0\u76EF\u7740\u4ED6\uFF0C\u54AC\u7259\u9053\uFF1A\u300C\u6562\u4F24\u6211\u5E08\u59D0\u4E00\u6839\u6C57\u6BDB\uFF0C\u6211\u5B9A\u628A\u4F60\u4EEC\u90FD\u788E\u5C38\u4E07\u6BB5\uFF01\u300D</p><p>\u7384\u51B0\u51B7\u7B11\u4E00\u58F0\uFF0C\u5DE6\u624B\u63A2\u5230\u98CE\u665A\u534E\u4E0B\u8179\u7528\u529B\u4E00\u62FD\uFF0C\u63EA\u4E0B\u4E00\u628A\u5E26\u7740\u8840\u73E0\u7684\u9634\u6BDB\u6254\u5230\u4E00\u8FB9\uFF0C\u7136\u540E\u62FF\u8D77\u5251\u9798\u62B5\u4F4F\u98CE\u665A\u534E\u4E0B\u4F53\uFF0C\u53EB\u9053\uFF1A\u300C\u518D\u4E0D\u5C01\u4F4F\u7A74\u9053\uFF0C\u6211\u53EF\u8981\u7ED9\u4F60\u5E08\u59D0\u5F00\u82DE\u4E86\uFF0C\u6D41\u971C\u5251\u5728\u6C5F\u6E56\u597D\u5927\u7684\u540D\u6C14\uFF0C\u88AB\u5251\u9798\u5F00\u4E86\u82DE\uFF0C\u4E5F\u662F\u6B66\u6797\u4E2D\u4E00\u6869\u8DA3\u4E8B\u3002\u300D</p><p>\u300C\u738B\u516B\u86CB\uFF01\u6211\u6155\u5BB9\u7D2B\u73AB\u53D1\u8A93\uFF1A\u5B9A\u8981\u628A\u4F60\u4EEC\u4E00\u4E2A\u4E2A\u788E\u5C38\u4E07\u6BB5\uFF01\u632B\u9AA8\u626C\u7070\uFF01\u300D\u6155\u5BB9\u7D2B\u73AB\u4E00\u62B9\u773C\u6CEA\uFF0C\u817E\u8EAB\u800C\u8D77\uFF0C\u7EA2\u8863\u98D8\u98D8\u5B9B\u5982\u9C9C\u82B1\u822C\u98DE\u5165\u5BC6\u6797\u3002</p><p>\u5F85\u6155\u5BB9\u7D2B\u73AB\u98DE\u8FDC\uFF0C\u7384\u51B0\u6EE1\u8138\u7684\u72DE\u7B11\u7ACB\u523B\u6D88\u5931\u5F97\u65E0\u5F71\u65E0\u8E2A\u3002\u624B\u6307\u4E00\u677E\uFF0C\u5F2F\u5251\u6389\u5728\u5730\u4E0A\uFF0C\u5598\u7740\u6C14\u8BF4\uFF1A\u300C\u5FEB\u8D70\u2026\u2026\u300D</p>',51);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
