import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_20" tabindex="-1"><a class="header-anchor" href="#_20" aria-hidden="true">#</a> 20</h1><p>\u6155\u5BB9\u7D2B\u73AB\u542B\u6CEA\u6025\u884C\u6570\u5341\u91CC\uFF0C\u4E00\u8DEF\u51FA\u4E86\u5927\u5C71\u3002\u5979\u5E76\u975E\u4E0D\u60F3\u5728\u65C1\u4F3A\u673A\u76F8\u6551\uFF0C\u4F46\u661F\u6708\u6E56\u4F17\u4EBA\u6709\u4E86\u6212\u5907\uFF0C\u4E07\u96BE\u4E0B\u624B\u3002\u51B5\u4E14\u970D\u72C2\u7130\u3001\u6C34\u67D4\u4ED9\u7B49\u4EBA\u8FD8\u5728\u9644\u8FD1\uFF0C\u81EA\u5DF1\u9053\u8DEF\u4E0D\u719F\u53C8\u662F\u5B64\u8EAB\u4E00\u4EBA\uFF0C\u52BF\u96BE\u76F8\u654C\uFF0C\u4E00\u65E6\u9732\u4E86\u5F71\u8E2A\u53EA\u6015\u96BE\u4EE5\u8131\u8EAB\u3002</p><p>\u4E00\u8F6E\u6B8B\u6708\uFF0C\u6EE1\u5929\u7E41\u661F\u3002\u4F4E\u5782\u7684\u591C\u5E55\u4E0B\uFF0C\u7A7A\u65F7\u7684\u539F\u91CE\u65E0\u8FB9\u65E0\u9645\uFF0C\u5E73\u4F38\u5411\u8FDC\u65B9\u3002</p><p>\u7D2B\u73AB\u501F\u661F\u5149\u8FA8\u660E\u65B9\u5411\uFF0C\u671D\u6B66\u9675\u5954\u53BB\uFF0C\u5230\u62C2\u6653\u65F6\u5206\u5DF2\u662F\u771F\u5143\u6E10\u5C3D\uFF0C\u75B2\u60EB\u4E0D\u582A\u3002</p><p>\u73AB\u7470\u4ED9\u5B50\u54AC\u7D27\u7259\u5173\u7AED\u529B\u652F\u6491\uFF0C\u4F46\u901F\u5EA6\u5374\u6162\u4E86\u8BB8\u591A\u3002</p><p>\u8EAB\u540E\u9A6C\u8E44\u58F0\u54CD\uFF0C\u5979\u51DD\u795E\u542C\u53BB\uFF0C\u8FA8\u51FA\u53EA\u6709\u4E24\u5339\uFF0C\u6599\u60F3\u5E76\u975E\u661F\u6708\u6E56\u8FFD\u5175\uFF0C\u4E5F\u672A\u653E\u5728\u5FC3\u4E0A\u3002</p><p>\u6765\u9A91\u6E10\u8FD1\uFF0C\u4E00\u4E2A\u4EBA\u4F4E\u58F0\u7B11\u9053\uFF1A\u300C\u5947\u602A\uFF0C\u5927\u6E05\u65E9\u8FD9\u524D\u4E0D\u7740\u6751\u540E\u4E0D\u7740\u5E97\u7684\uFF0C\u90A3\u513F\u6765\u7684\u5C0F\u5A18\u5B50\uFF1F\u300D</p><p>\u300C\u8170\u4E00\u626D\u4E00\u626D\u7684\uFF0C\u8FD8\u633A\u597D\u770B\u2026\u2026\u300D</p><p>\u6155\u5BB9\u7D2B\u73AB\u5FC3\u4E0B\u5927\u6012\uFF0C\u500F\u7136\u6B62\u6B65\uFF0C\u671D\u540E\u770B\u53BB\u3002</p><p>\u4E24\u4E2A\u8EAB\u7740\u9526\u8863\u7684\u7EA8\u88E4\u5B50\u5F1F\u6B63\u7B11\u563B\u563B\u6253\u91CF\u7740\u5979\uFF0C\u5F85\u770B\u6E05\u7D2B\u73AB\u7684\u503E\u57CE\u8273\u8272\u90FD\u6123\u4F4F\u4E86\u3002</p><p>\u7D2B\u73AB\u98DE\u8EAB\u800C\u8D77\uFF0C\u62AC\u811A\u5C06\u4E00\u4EBA\u8E22\u4E86\u4E0B\u53BB\uFF0C\u5212\u4E86\u4E2A\u4F18\u7F8E\u7684\u5F27\u7EBF\u843D\u5728\u978D\u4E0A\u3002</p><p>\u53E6\u4E00\u4EBA\u5927\u60CA\u5931\u8272\uFF0C\u8FDE\u5FD9\u52D2\u9A6C\u95EA\u907F\uFF0C\u5374\u88AB\u4E00\u53EA\u6B3A\u971C\u8D5B\u96EA\u7684\u7389\u624B\u62E7\u4F4F\u8863\u895F\uFF0C\u6CA1\u7B49\u4ED6\u9192\u8FC7\u795E\u6765\uFF0C\u4FBF\u817E\u4E91\u9A7E\u96FE\u822C\u98DE\u5230\u6C34\u7530\u91CC\uFF0C\u6E85\u4E86\u4E00\u8EAB\u6CE5\u6C34\u3002</p><p>\u7D2B\u73AB\u4E00\u5939\u9A6C\u8179\uFF0C\u7275\u7740\u90A3\u5339\u7A7A\u9A6C\u7EB5\u9A6C\u6025\u9A70\u3002</p><p>\u5176\u65F6\u5357\u5317\u8FDE\u5E74\u4EA4\u6218\uFF0C\u6DEE\u6CB3\u4EE5\u5357\u9A6C\u5339\u5947\u7F3A\uFF0C\u80FD\u4E58\u9A6C\u51FA\u884C\uFF0C\u8FD9\u4E24\u4E2A\u4E5F\u975E\u540C\u5BFB\u5E38\u8DEF\u4EBA\uFF0C\u4F46\u9047\u4E0A\u73AB\u7470\u4ED9\u5B50\u8FD9\u7B49\u5F3A\u5F92\uFF0C\u4E24\u4EBA\u76F4\u5982\u505A\u68A6\u4E00\u822C\u5C31\u88AB\u52AB\u4E86\u3002</p><p>\u4E09\u6708\u4E8C\u5341\u4E03\uFF0C\u5348\u65F6\u3002\u6155\u5BB9\u7D2B\u73AB\u8FDB\u5165\u6B66\u9675\u57CE\u3002</p><p>\u6CAE\u6E20\u5BB6\u539F\u672C\u4E5F\u5728\u4F0F\u9F99\u6DA7\uFF0C\u6570\u5E74\u524D\u624D\u8FC1\u81F3\u6B64\u5730\u3002\u6B66\u9675\u4F4D\u4E8E\u6C85\u6C5F\u4E4B\u5317\uFF0C\u5730\u65B9\u867D\u975E\u5BCC\u9976\uFF0C\u4F46\u8FDC\u79BB\u4E2D\u539F\uFF0C\u8FDE\u5E74\u7684\u5F81\u6218\u5E76\u672A\u5F71\u54CD\u5230\u8FD9\u91CC\u7684\u5E73\u9759\u3002</p><p>\u9752\u77F3\u94FA\u5C31\u7684\u8857\u9053\u5BBD\u4E0D\u8FC7\u4E24\u4E08\uFF0C\u4E0E\u6D1B\u9633\u3001\u957F\u5B89\u7B49\u540D\u57CE\u6570\u5341\u4E08\u7684\u5927\u8857\u76F8\u5F62\u89C1\u62D9\u3002</p><p>\u51E0\u4E2A\u8001\u4EBA\u61D2\u6D0B\u6D0B\u5750\u5728\u95E8\u524D\u534A\u772F\u7740\u773C\u6709\u4E00\u53E5\u6CA1\u4E00\u53E5\u5730\u8C08\u7740\u53E4\u8BB0\uFF0C\u9AD8\u5927\u7684\u6768\u6811\u65E0\u98CE\u800C\u52A8\uFF0C\u7FE0\u7EFF\u7684\u6811\u53F6\u5149\u8292\u95EA\u52A8\u3002</p><p>\u6155\u5BB9\u7D2B\u73AB\u4E0D\u7531\u5F97\u653E\u7F13\u811A\u6B65\u3002\u6E05\u8106\u7684\u9A6C\u8E44\u58F0\u5728\u5348\u540E\u5B81\u9759\u7684\u9633\u5149\u91CC\u60A0\u7136\u54CD\u8D77\uFF0C\u5979\u7A81\u7136\u611F\u89C9\u5230\u4E00\u79CD\u4E45\u8FDD\u5730\u5B89\u8BE6\u3002</p><p>\u8840\u8165\u7684\u4E71\u4E16\u4E2D\uFF0C\u8FD9\u91CC\u5C31\u50CF\u662F\u6C89\u7761\u4E2D\u7684\u4E50\u571F\u3002\u4E5F\u8BB8\u8FD9\u6B63\u662F\u6CAE\u6E20\u4F2F\u7236\u8131\u79BB\u6B66\u6797\uFF0C\u9690\u5C45\u6B64\u5904\u7684\u7F18\u6545\u4E86\u3002</p><p>\u6CAE\u6E20\u592B\u4EBA\u5306\u5306\u8FCE\u51FA\uFF0C\u60CA\u559C\u9053\uFF1A\u300C\u7D2B\u73AB\u4F60\u600E\u4E48\u6765\u4E86\uFF1F\u4F60\u4E00\u4E2A\u4EBA\u5417\uFF1F\u4F60\u5A18\u5462\uFF1F\u300D</p><p>\u300C\u2026\u2026\u6211\u8DEF\u8FC7\u8FD9\u91CC\uFF0C\u6765\u770B\u770B\u4F2F\u7236\u4F2F\u6BCD\u2026\u2026\u300D</p><p>\u6CAE\u6E20\u592B\u4EBA\u559C\u51FA\u671B\u5916\u5730\u62C9\u4F4F\u7D2B\u73AB\uFF0C\u300C\u516D\u5E74\u4E0D\u89C1\uFF0C\u7D2B\u73AB\u957F\u6210\u5927\u59D1\u5A18\u4E86\uFF0C\u4F60\u5A18\u5462\uFF1F\u8FD8\u597D\u5417\uFF1F\u300D</p><p>\u300C\u2026\u2026\u8FD8\u597D\u2026\u2026\u300D</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u98CE\u665A\u534E\u90A3\u4E00\u638C\u5DF2\u662F\u5F3A\u5F29\u4E4B\u672B\uFF0C\u867D\u7136\u91CD\u521B\u4E86\u6E05\u9732\uFF0C\u5374\u672A\u80FD\u53D6\u5979\u6027\u547D\u3002\u661F\u6708\u6E56\u4E09\u540D\u9999\u4E3B\u4E00\u6B7B\u4E8C\u4F24\uFF0C\u72FC\u72C8\u8FFD\u4E0A\u5927\u961F\u3002</p><p>\u970D\u72C2\u7130\u6C14\u8272\u7565\u6709\u597D\u8F6C\uFF0C\u95FB\u8BF4\u751F\u64D2\u4E86\u6D41\u971C\u5251\uFF0C\u987F\u65F6\u72C2\u58F0\u53EB\u9053\uFF1A\u300C\u628A\u6B7B\u5A4A\u5B50\u5E26\u8FC7\u6765\uFF01\u300D</p><p>\u4F17\u4EBA\u628A\u4E94\u82B1\u5927\u7ED1\u7684\u98CE\u665A\u534E\u62D6\u5230\u8F66\u5185\uFF0C\u4E00\u540D\u5E2E\u4F17\u5C0F\u58F0\u9053\uFF1A\u300C\u6D41\u971C\u5251\u9014\u4E2D\u51B2\u5F00\u7A74\u9053\uFF0C\u4F24\u4E86\u6E05\u9732\u9999\u4E3B\u2026\u2026\u300D</p><p>\u970D\u72C2\u7130\u4ECE\u90A3\u4EBA\u8170\u95F4\u62D4\u51FA\u957F\u5251\uFF0C\u4E00\u5251\u5C06\u98CE\u665A\u534E\u7684\u53F3\u81C2\u9F50\u8098\u780D\u65AD\u3002\u98CE\u665A\u534E\u6D51\u8EAB\u4E00\u7D27\uFF0C\u7389\u8138\u53D8\u5F97\u60E8\u767D\uFF0C\u65AD\u81C2\u5782\u5728\u80F8\u524D\u8840\u5982\u6CC9\u6D8C\u3002\u970D\u72C2\u7130\u72DE\u7B11\u7740\u626F\u51FA\u7F20\u5728\u8170\u95F4\u6B8B\u80A2\uFF0C\u5728\u98CE\u665A\u534E\u768E\u6D01\u7684\u8EAB\u4F53\u4E0A\u7EC6\u7EC6\u6D82\u62B9\uFF0C\u300C\u6B7B\u5A4A\u5B50\uFF0C\u6211\u770B\u4F60\u8FD8\u600E\u4E48\u4F7F\u5251\uFF01\u300D</p><p>\u98CE\u665A\u534E\u8EAB\u4E0A\u6CBE\u6EE1\u81EA\u5DF1\u7684\u9C9C\u8840\uFF0C\u808C\u80A4\u66F4\u663E\u5F97\u6676\u83B9\u5982\u7389\u3002\u5979\u5341\u516B\u5C81\u4ED7\u5251\u8D70\u6C5F\u6E56\uFF0C\u516B\u5E74\u6765\u65A9\u5996\u9664\u9B54\uFF0C\u4FA0\u540D\u8FDC\u64AD\u3002\u5982\u4ECA\u88AB\u4EBA\u780D\u65AD\u624B\u81C2\uFF0C\u4E0D\u7981\u5FC3\u5E95\u6EF4\u8840\u3002</p><p>\u970D\u72C2\u7130\u62FF\u7740\u6B8B\u80A2\u5728\u98CE\u665A\u534E\u8138\u4E0A\u3001\u5507\u4E0A\u4E00\u9635\u4E71\u62B9\uFF0C\u300C\u6B7B\u5A4A\u5B50\uFF0C\u5473\u9053\u597D\u4E0D\u597D\uFF1F\u5F20\u5634\uFF0C\u54AC\u4E00\u53E3\u3002\u300D</p><p>\u9C9C\u8840\u4ECE\u5507\u95F4\u6D41\u5165\u5589\u4E2D\uFF0C\u98CE\u665A\u534E\u9999\u820C\u5FAE\u98A4\u3002</p><p>\u970D\u72C2\u7130\u5C06\u624B\u81C2\u653E\u5728\u5979\u4E24\u4E73\u95F4\uFF0C\u4F24\u53E3\u538B\u5728\u5507\u4E0A\uFF0C\u7136\u540E\u63D0\u8D77\u957F\u5251\u523A\u5165\u98CE\u665A\u534E\u80A9\u5934\uFF0C\u7A7F\u8FC7\u7435\u7436\u9AA8\uFF0C\u9489\u5728\u5730\u4E0A\u3002\u4E00\u76F4\u51DD\u805A\u529F\u529B\u7684\u98CE\u665A\u534E\u7ACB\u523B\u771F\u6C14\u6DA3\u6563\u3002</p><p>\u767D\u7389\u83BA\u5413\u5F97\u9762\u65E0\u8840\u8272\uFF0C\u5782\u7740\u5934\u4E00\u58F0\u4E5F\u4E0D\u6562\u542D\u3002</p><p>\u98CE\u665A\u534E\u6D51\u8EAB\u6D74\u8840\uFF0C\u4E24\u80A9\u5404\u63D2\u7740\u4E00\u67C4\u957F\u5251\uFF0C\u80F8\u53E3\u653E\u7740\u81EA\u5DF1\u7684\u65AD\u80A2\uFF0C\u770B\u4E0A\u53BB\u51C4\u60E8\u4E07\u7AEF\u3002\u970D\u72C2\u7130\u6012\u706B\u7A0D\u89E3\uFF0C\u659C\u773C\u770B\u89C1\u767D\u7389\u83BA\uFF0C\u51B7\u54FC\u9053\uFF1A\u300C\u81ED\u5A4A\u5B50\uFF0C\u8001\u5B50\u7ED9\u4F60\u7684\u5C44\u585E\u5462\uFF1F\u300D</p><p>\u767D\u7389\u83BA\u5A07\u8EAF\u4E00\u98A4\uFF0C\u4F4E\u58F0\u9053\uFF1A\u300C\u5927\u7237\u4EEC\u8981\u7528\u5974\u5A62\u7684\u8D31\u5C44\u2026\u2026\u585E\u5B50\u2026\u2026\u4E22\u4E86\uFF01\u300D</p><p>\u970D\u72C2\u7130\u6D53\u7709\u5012\u7AD6\uFF0C\u5486\u54EE\u9053\uFF1A\u300C\u6562\u4E22\u4E86\uFF1F\u628A\u9E42\u5A4A\u5B50\u62C9\u8FC7\u6765\uFF0C\u64CD\u6B7B\u5979\uFF01\u300D</p><p>\u767D\u7389\u83BA\u54ED\u9053\uFF1A\u300C\u5974\u5A62\u4E0D\u662F\u6709\u610F\u7684\u2026\u2026\u5927\u7237\u9976\u547D\u2026\u2026\u300D</p><p>\u767D\u7389\u9E42\u88AB\u4EBA\u62C9\u7740\u8DCC\u8DCC\u649E\u649E\u7684\u8D70\u8FC7\u6765\uFF0C\u4E0E\u59D0\u59D0\u62B1\u5934\u75DB\u54ED\u3002\u5979\u4E0B\u8EAB\u8FD8\u6709\u672A\u5E72\u7684\u7CBE\u6DB2\uFF0C\u663E\u7136\u521A\u88AB\u4EBA\u5978\u6DEB\u8FC7\u3002</p><p>\u661F\u6708\u6E56\u5E2E\u4F17\u626F\u5F00\u767D\u6C0F\u59D0\u59B9\uFF0C\u4E00\u7FA4\u4EBA\u628A\u767D\u7389\u9E42\u6309\u5728\u5730\u4E0A\uFF0C\u8F6E\u756A\u63D2\u8FDB\u5979\u7EA2\u80BF\u7684\u79D8\u5904\uFF0C\u72E0\u72E0\u64CD\u5F04\u3002\u767D\u7389\u9E42\u88AB\u4ED6\u4EEC\u7C97\u66B4\u7684\u52A8\u4F5C\u6345\u5F97\u54ED\u53EB\u4E0D\u6B62\u3002\u767D\u7389\u83BA\u76F4\u76F4\u8DEA\u5728\u4E00\u65C1\uFF0C\u60F3\u8D77\u56E0\u4E3A\u81EA\u5DF1\u8FC7\u9519\u800C\u8BA9\u59B9\u59B9\u53D7\u82E6\uFF0C\u54ED\u5F97\u66F4\u662F\u4F24\u5FC3\u3002</p><p>\u5931\u53BB\u624B\u81C2\u7684\u98CE\u665A\u534E\u5374\u6CA1\u6D41\u4E00\u6EF4\u773C\u6CEA\u3002\u970D\u72C2\u7130\u6709\u5FC3\u51CC\u8FB1\u6D41\u971C\u5251\uFF0C\u53EF\u4ED6\u7684\u5B9D\u8D1D\u9E21\u5DF4\u8FD8\u5728\u8F66\u5185\u7684\u5C0F\u5E03\u5305\u91CC\u3002\u773C\u89C1\u98CE\u665A\u534E\u8FD8\u662F\u5904\u5B50\u4E4B\u8EAF\uFF0C\u60F3\u6765\u60F3\u53BB\uFF0C\u8FD8\u662F\u7B49\u56DE\u5BAB\u8BF7\u53F6\u62A4\u6CD5\u51FA\u624B\u6551\u6CBB\uFF0C\u7136\u540E\u518D\u4EB2\u81EA\u7ED9\u5979\u5F00\u82DE\u2014\u2014\u5230\u65F6\u975E\u628A\u8FD9\u4E2A\u8D31\u4EBA\u64CD\u5F97\u6B7B\u53BB\u6D3B\u6765\uFF01</p><p>\u970D\u72C2\u7130\u8FD9\u65F6\u624D\u60F3\u8D77\u6B63\u4E8B\uFF0C\u95EE\u9053\uFF1A\u300C\u6155\u5BB9\u7D2B\u73AB\u5462\uFF1F\u300D</p><p>\u7384\u51B0\u3001\u6E05\u9732\u90FD\u5728\u8F66\u5185\u517B\u4F24\uFF0C\u5F53\u65F6\u5728\u573A\u7684\u4E00\u4E2A\u5E2E\u4F17\u7B54\u9053\uFF1A\u300C\u6D41\u971C\u5251\u51B2\u5F00\u7A74\u9053\uFF0C\u628A\u73AB\u7470\u4ED9\u5B50\u653E\u8D70\u4E86\u2026\u2026\u300D</p><p>\u300C\u4ED6\u5988\u7684\uFF0C\u4E00\u7FA4\u5E9F\u7269\uFF01\u9003\u54EA\u513F\u4E86\uFF1F\u300D</p><p>\u90A3\u5E2E\u4F17\u6BD4\u5212\u4E86\u4E00\u4E0B\uFF0C\u300C\u671D\u90A3\u8FB9\u53BB\u4E86\u2014\u2014\u4E24\u4F4D\u9999\u4E3B\u4F24\u5F97\u592A\u91CD\uFF0C\u5C5E\u4E0B\u65E0\u6CD5\u53BB\u8FFD\uFF0C\u8BF7\u957F\u8001\u6CBB\u7F6A\u3002\u300D</p><p>\u8DEA\u5728\u4E00\u65C1\u7684\u767D\u7389\u83BA\u7A81\u7136\u9053\uFF1A\u300C\u5974\u5A62\u77E5\u9053\u3002\u5979\u8BF4\u53BB\u6B66\u9675\u627E\u4E00\u4E2A\u53EB\u6CAE\u6E20\u5C55\u626C\u7684\u4EBA\u2026\u2026\u957F\u8001\uFF0C\u6C42\u4F60\u9976\u4E86\u6211\u59B9\u59B9\u5427\u3002\u300D</p><p>\u970D\u72C2\u7130\u76EE\u5149\u4E00\u95EA\uFF0C\u7247\u523B\u540E\u7B54\u9053\uFF1A\u300C\u4F60\u4E5F\u53BB\u6328\u64CD\u5427\u3002\u300D</p><p>\u767D\u7389\u83BA\u677E\u4E86\u53E3\u6C14\uFF0C\u8FDE\u5FD9\u4FEF\u8EAB\u63B0\u5F00\u96EA\u81C0\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u6CAE\u6E20\u5C55\u626C\u6025\u6025\u5954\u5165\u540E\u5B85\uFF0C\u300C\u7D2B\u73AB\uFF0C\u771F\u7684\u662F\u4F60\uFF1F\u300D</p><p>\u6155\u5BB9\u7D2B\u73AB\u6B3E\u6B3E\u8D77\u8EAB\uFF0C\u53EB\u4E86\u58F0\uFF1A\u300C\u5C55\u626C\u54E5\u54E5\u2026\u2026\u300D</p><p>\u6CAE\u6E20\u592B\u4EBA\u542B\u7B11\u7AD9\u4E86\u8D77\u6765\uFF0C\u62C9\u8D77\u5973\u513F\u660E\u5170\uFF0C\u300C\u4F60\u4EEC\u5148\u804A\uFF0C\u6211\u53BB\u7ED9\u4F60\u505A\u996D\u3002\u300D</p><p>\u660E\u5170\u53EA\u6709\u5341\u56DB\u5C81\uFF0C\u5077\u5077\u505A\u4E86\u4E2A\u9B3C\u8138\uFF0C\u7B11\u563B\u563B\u53BB\u4E86\u3002</p><p>\u5385\u4E2D\u987F\u65F6\u9759\u9ED8\u4E0B\u6765\uFF0C\u6CAE\u6E20\u5C55\u626C\u4E0A\u524D\u4E00\u6B65\uFF0C\u62C9\u8D77\u7D2B\u73AB\u7684\u5C0F\u624B\uFF0C\u4F4E\u58F0\u9053\uFF1A\u300C\u4F60\u600E\u4E48\u4E00\u4E2A\u4EBA\u6765\u4E86\uFF1F\u300D</p><p>\u6155\u5BB9\u7D2B\u73AB\u5FC3\u5934\u4E00\u9178\uFF0C\u6CEA\u73E0\u6EDA\u6EDA\u800C\u4E0B\u3002</p><p>\u6CAE\u6E20\u5C55\u626C\u6025\u5207\u5730\u8BF4\uFF1A\u300C\u522B\u54ED\u3002\u7D2B\u73AB\uFF0C\u600E\u4E48\u4E86\uFF1F\u300D</p><p>\u6CAE\u6E20\u5C55\u626C\u8EAB\u957F\u7389\u7ACB\uFF0C\u8EAB\u4E0A\u6709\u79CD\u9633\u5149\u822C\u660E\u5A9A\u7684\u7537\u5B50\u6C14\u606F\u3002\u6155\u5BB9\u7D2B\u73AB\u62B9\u4E86\u62B9\u773C\u6CEA\uFF0C\u300C\u6CA1\u4E8B\u3002\u6211\u53EA\u662F\u60F3\u4F60\u4E86\u2026\u2026\u300D</p><p>\u6CAE\u6E20\u5C55\u626C\u8111\u4E2D\u4E00\u6655\uFF0C\u6014\u5728\u5F53\u573A\u3002\u867D\u7136\u4E24\u4EBA\u9752\u6885\u7AF9\u9A6C\uFF0C\u76F8\u8BC6\u591A\u5E74\uFF0C\u4F46\u8FD9\u4E2A\u86EE\u6A2A\u53EF\u7231\u7684\u5C0F\u4E2B\u5934\u8FD8\u662F\u5934\u4E00\u6B21\u8BF4\u51FA\u8FD9\u79CD\u60C5\u610F\u7EF5\u7EF5\u7684\u8BDD\uFF0C\u5FC3\u91CC\u987F\u65F6\u4E50\u7FFB\u4E86\u5929\u3002</p><p>\u6155\u5BB9\u7D2B\u73AB\u54ED\u51FA\u5FC3\u4E2D\u90C1\u6C14\uFF0C\u62AC\u5934\u5FAE\u5FAE\u4E00\u7B11\uFF0C\u300C\u5C55\u626C\u54E5\u54E5\uFF0C\u6211\u4EEC\u51FA\u53BB\u8D70\u8D70\u5427\u3002\u300D</p><p>\u8FD9\u4E00\u7B11\u76F4\u5982\u5947\u82B1\u521D\u7EFD\uFF0C\u8273\u5149\u56DB\u5C04\uFF0C\u6CAE\u6E20\u5C55\u626C\u773C\u4E2D\u4E00\u4EAE\uFF0C\u8FDE\u5FD9\u70B9\u5934\uFF0C\u300C\u540E\u9762\u6709\u4E2A\u82B1\u56ED\u2026\u2026\u300D</p><p>\u65F6\u503C\u4E09\u6708\u672B\uFF0C\u5C0F\u5DE7\u7684\u82B1\u56ED\u5185\u8471\u830F\u6EE1\u76EE\uFF0C\u7E41\u82B1\u4F3C\u9526\u3002\u7D2B\u8346\u5DF2\u7ECF\u7565\u6709\u51CB\u96F6\uFF0C\u4F46\u6EE1\u67B6\u8537\u8587\u5F00\u5F97\u6B63\u65FA\uFF0C\u9636\u524D\u6D77\u68E0\u6012\u653E\uFF0C\u6C60\u4E2D\u8377\u5982\u78A7\u7389\uFF0C\u7261\u4E39\u3001\u828D\u836F\u3001\u837C\u863C\u5404\u5404\u5410\u9732\u82AC\u82B3\uFF0C\u7A7A\u6C14\u4E2D\u6D41\u6DCC\u7740\u9189\u4EBA\u7684\u9999\u6C14\u3002</p><p>\u6155\u5BB9\u7D2B\u73AB\u6DF1\u6DF1\u5438\u4E86\u53E3\u6C14\uFF0C\u7F13\u7F13\u5410\u51FA\uFF0C\u534A\u664C\u624D\u8BF4\uFF1A\u300C\u8FD9\u91CC\u771F\u7F8E\u2026\u2026\u300D</p><p>\u5341\u516D\u5C81\u7684\u5C11\u5973\u7ACB\u5728\u7FA4\u82B1\u4E4B\u4E2D\uFF0C\u96EA\u80A4\u82B1\u8C8C\uFF0C\u987E\u76FC\u751F\u59FF\u3002\u90A3\u79CD\u6D3B\u8272\u751F\u9999\u76F4\u7FA4\u82B3\u5931\u8272\u3002\u6CAE\u6E20\u5C55\u626C\u5FC3\u795E\u4FF1\u9189\uFF0C\u67D4\u58F0\u9053\uFF1A\u300C\u5728\u8FD9\u91CC\u591A\u4F4F\u51E0\u65E5\uFF0C\u6211\u5E26\u4F60\u53BB\u6B66\u9675\u6E90\u770B\u770B\u3002\u300D</p><p>\u6155\u5BB9\u7D2B\u73AB\u6447\u4E86\u6447\u5934\uFF0C\u300C\u6211\u660E\u5929\u5C31\u8D70\u3002\u300D</p><p>\u300C\u8FD9\u4E48\u6025\uFF1F\u300D\u6CAE\u6E20\u5C55\u626C\u7565\u89C9\u5931\u671B\uFF0C\u65CB\u5373\u9053\uFF1A\u300C\u6211\u8DDF\u7236\u4EB2\u8BF4\u4E00\u58F0\uFF0C\u660E\u5929\u9001\u4F60\u53BB\u98D8\u6885\u5CF0\u3002\u300D</p><p>\u6155\u5BB9\u7D2B\u73AB\u4E5F\u662F\u6101\u80A0\u767E\u7ED3\uFF0C\u5979\u6253\u5B9A\u4E3B\u610F\u9690\u7792\u5BB6\u4E2D\u7684\u60E8\u53D8\uFF0C\u4E0D\u6253\u6270\u8FD9\u91CC\u5B81\u9759\u7684\u751F\u6D3B\u3002\u53EA\u662F\u6CAE\u6E20\u5C55\u626C\u5E76\u975E\u5916\u4EBA\uFF0C\u7A76\u7ADF\u544A\u4E0D\u544A\u8BC9\u4ED6\u5462\uFF1F</p><p>\u4E00\u9635\u67D4\u98CE\u5439\u6765\uFF0C\u82B1\u4E1B\u4E2D\u4E24\u4EBA\u8863\u8882\u98DE\u626C\uFF0C\u5B9B\u5982\u4E00\u5BF9\u753B\u4E2D\u74A7\u4EBA\u3002\u6155\u5BB9\u7D2B\u73AB\u4F38\u51FA\u7389\u638C\uFF0C\u628A\u4E00\u7247\u843D\u82B1\u8F7B\u8F7B\u63A5\u5728\u624B\u5FC3\uFF0C\u8F7B\u58F0\u9053\uFF1A\u300C\u4F60\u613F\u4E0D\u613F\u610F\u5728\u98D8\u6885\u5CF0\u966A\u6211\u4E00\u5E74\u5462\uFF1F\u300D</p><p>\u6CAE\u6E20\u5C55\u626C\u4E00\u6115\uFF0C\u300C\u8FD9\u4E48\u4E45\uFF1F\u660E\u5170\u8FD8\u5C0F\uFF0C\u660E\u5E74\u6211\u53BB\u98D8\u6885\u5CF0\u966A\u4F60\u534A\u5E74\u597D\u5417\uFF1F\u300D</p><p>\u7D2B\u73AB\u5E7D\u5E7D\u53F9\u4E86\u53E3\u6C14\uFF0C\u70B9\u4E86\u70B9\u5934\u3002</p><p>\u6CAE\u6E20\u5C55\u626C\u8BDA\u5FC3\u5B9E\u610F\u5730\u8BF4\uFF1A\u300C\u966A\u4F60\u591A\u4E45\u90FD\u884C\uFF0C\u4F46\u6211\u662F\u5BB6\u91CC\u552F\u4E00\u7684\u513F\u5B50\uFF0C\u79BB\u5F00\u4E00\u5E74\uFF0C\u6211\u5B9E\u5728\u653E\u5FC3\u4E0D\u4E0B\u2026\u2026\u300D</p><p>\u7D2B\u73AB\u5C55\u989C\u7B11\u9053\uFF1A\u300C\u6211\u662F\u9017\u4F60\u5462\u3002\u5C55\u626C\u54E5\u54E5\uFF0C\u7EC8\u5357\u5C71\u79BB\u8FD9\u91CC\u6709\u591A\u8FDC\uFF1F\u300D</p><p>\u300C\u4F60\u8981\u53BB\u7EC8\u5357\u5C71\uFF1F\u300D</p><p>\u7D2B\u73AB\u628A\u73A9\u7740\u8170\u95F4\u7684\u5C0F\u5F13\u5F29\uFF0C\u67D4\u58F0\u9053\uFF1A\u300C\u4E0D\u662F\u3002\u542C\u8BF4\u7EC8\u5357\u5C71\u6709\u795E\u4ED9\uFF0C\u6211\u53EA\u662F\u597D\u5947\u2026\u2026\u300D</p>',73);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
