import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_21" tabindex="-1"><a class="header-anchor" href="#_21" aria-hidden="true">#</a> 21</h1><p>\u6B21\u65E5\u6E05\u6668\uFF0C\u6155\u5BB9\u7D2B\u73AB\u79BB\u5F00\u6B66\u9675\u3002\u6CAE\u6E20\u5C55\u626C\u4E00\u8DEF\u9001\u51FA\u6570\u5341\u91CC\uFF0C\u76F4\u5230\u5929\u8272\u5C06\u5348\u624D\u52D2\u9A6C\u6B62\u6B65\uFF0C\u4F9D\u4F9D\u4E0D\u820D\u7684\u6325\u624B\u4F5C\u522B\u3002</p><p>\u7B49\u79BB\u5F00\u4ED6\u7684\u89C6\u7EBF\uFF0C\u6155\u5BB9\u7D2B\u73AB\u7ACB\u5373\u6539\u9053\u897F\u5317\uFF0C\u76F4\u5954\u7EC8\u5357\u5C71\u3002\u72EC\u81EA\u5954\u9A70\u5728\u8FBD\u9614\u7684\u5E73\u539F\u4E0A\uFF0C\u7D2B\u73AB\u611F\u5230\u4E00\u79CD\u7A7A\u8361\u8361\u6BEB\u65E0\u7740\u843D\u7684\u75DB\u695A\u3002</p><p>\u5979\u73B0\u5728\u6BEB\u65E0\u529E\u6CD5\uFF0C\u5E08\u7236\u4E0D\u5728\u5C71\u4E0A\uFF0C\u6240\u6709\u7684\u4EB2\u4EBA\u3001\u5E08\u59D0\u90FD\u843D\u5165\u654C\u624B\uFF0C\u5929\u5730\u5982\u6B64\u4E4B\u5927\uFF0C\u5374\u4F3C\u4E4E\u53EA\u5269\u4E0B\u5979\u5B64\u96F6\u96F6\u4E00\u4E2A\u4EBA\u3002\u7D2B\u73AB\u5FCD\u4E0D\u4F4F\u5FC3\u4E2D\u7684\u90C1\u95F7\u548C\u9178\u695A\uFF0C\u7A81\u7136\u653E\u58F0\u5C16\u5578\uFF0C\u53EF\u65E0\u8FB9\u7684\u65F7\u91CE\u4E2D\u8FDE\u56DE\u58F0\u90FD\u6CA1\u6709\u3002</p><p>\u6709\u51E0\u6B21\u5979\u6068\u4E0D\u5F97\u7ACB\u5373\u56DE\u5230\u6B66\u9675\uFF0C\u628A\u4E00\u5207\u90FD\u544A\u8BC9\u6CAE\u6E20\u5C55\u626C\u3002\u4F46\u6BCF\u6B21\u62C9\u4F4F\u7F30\u7EF3\uFF0C\u5979\u5C31\u72B9\u8C6B\u8D77\u6765\u3002</p><p>\u8FD8\u662F\u5148\u5BFB\u5230\u5B9D\u85CF\uFF0C\u770B\u91CC\u9762\u7A76\u7ADF\u6709\u4EC0\u4E48\u503C\u5F97\u661F\u6708\u6E56\u5782\u6D8E\u7684\u4E1C\u897F\u3002\u7136\u540E\u518D\u56DE\u98D8\u6885\u5CF0\u2014\u2014\u4E0D\u884C\u5C31\u53EB\u4E0A\u5C55\u626C\u54E5\u54E5\u4E00\u8D77\u53BB\u5357\u6D77\u627E\u5E08\u7236\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u6CAE\u6E20\u5C55\u626C\u534A\u662F\u60C6\u6005\u534A\u662F\u751C\u5BC6\u5730\u56DE\u5230\u5BB6\u4E2D\uFF0C\u5374\u89C1\u5927\u95E8\u534A\u63A9\uFF0C\u95E8\u524D\u7684\u5C0F\u65AF\u4E0D\u77E5\u8DD1\u5230\u4EC0\u4E48\u5730\u65B9\u73A9\u53BB\u4E86\u3002\u4ED6\u7FFB\u8EAB\u4E0B\u9A6C\uFF0C\u4E3E\u6B65\u8FDB\u5927\u5385\uFF0C\u521A\u53EB\u4E86\u4E00\u58F0\u300C\u7239\u300D\uFF0C\u4FBF\u6123\u4F4F\u4E86\u3002</p><p>\u4E00\u4E2A\u7EA2\u888D\u6C49\u5B50\u6EE1\u76EE\u51F6\u5149\u5730\u9AD8\u5750\u5802\u4E2D\uFF0C\u811A\u4E0B\u8E29\u7740\u4E00\u4E2A\u8840\u8FF9\u6591\u6591\u7684\u82B1\u767D\u5934\u9885\u3002</p><p>\u300C\u7239\uFF01\u300D\u6CAE\u6E20\u5C55\u626C\u5931\u58F0\u53EB\u9053\u3002</p><p>\u7EA2\u888D\u6C49\u5B50\u7F13\u7F13\u62AC\u8D77\u811A\uFF0C\u7136\u540E\u7528\u529B\u8E0F\u4E0B\uFF0C\u90A3\u9897\u5934\u9885\u7ACB\u523B\u8111\u6D46\u56DB\u6E85\uFF0C\u7206\u88C2\u5F00\u6765\u3002</p><p>\u6CAE\u6E20\u5C55\u626C\u5446\u5446\u770B\u7740\u7236\u4EB2\u8840\u8089\u6A21\u7CCA\u7684\u5934\u9885\uFF0C\u731B\u7136\u80F8\u53E3\u5267\u75DB\uFF0C\u811A\u4E0B\u4E00\u865A\uFF0C\u534A\u8DEA\u5728\u5730\u4E0A\u3002\u51E0\u6761\u4EBA\u5F71\u56F4\u8FC7\u6765\u5200\u67AA\u9F50\u65BD\uFF0C\u4ED6\u88AB\u7A81\u5982\u5176\u6765\u7684\u60E8\u72B6\u60CA\u5446\u4E86\uFF0C\u52C9\u529B\u6321\u4E86\u51E0\u62DB\uFF0C\u51FA\u624B\u7EF5\u8F6F\u65E0\u529B\uFF0C\u8FDE\u5E73\u65F6\u7684\u56DB\u6210\u529F\u529B\u90FD\u65BD\u4E0D\u51FA\u6765\u3002</p><p>\u7EA2\u888D\u6C49\u5B50\u817E\u8EAB\u800C\u8D77\uFF0C\u731B\u6251\u6CAE\u6E20\u5C55\u626C\u3002\u70C8\u706B\u822C\u70BD\u70ED\u7684\u52B2\u6C14\u76F4\u903C\u5934\u9876\uFF0C\u6CAE\u6E20\u5C55\u626C\u62AC\u638C\u5C01\u6321\uFF0C\u53EA\u89C9\u53F3\u624B\u4E00\u9635\u5267\u75DB\uFF0C\u56DB\u6839\u624B\u6307\u5DF2\u88AB\u706B\u7130\u4EE4\u9F50\u638C\u5207\u65AD\u3002\u7EA2\u888D\u6C49\u5B50\u4E0B\u624B\u6781\u8FA3\uFF0C\u53CC\u81C2\u4E00\u5708\u4E00\u5408\uFF0C\u5C06\u6CAE\u6E20\u5C55\u626C\u53F3\u81C2\u7EDE\u5F97\u7C89\u788E\uFF0C\u63A5\u7740\u5C01\u4E86\u4ED6\u7684\u7A74\u9053\u3002</p><p>\u300C\u6155\u5BB9\u7D2B\u73AB\u5462\uFF1F\u300D\u7EA2\u888D\u6C49\u5B50\u5BD2\u58F0\u9053\u3002</p><p>\u4ED6\u7684\u58F0\u97F3\u7C97\u65F7\u4E2D\u5E26\u7740\u4E00\u4E1D\u5C16\u97F3\uFF0C\u542C\u4E0A\u53BB\u4E0D\u4F26\u4E0D\u7C7B\u3002\u6CAE\u6E20\u5C55\u626C\u5267\u75DB\u5F7B\u9AA8\uFF0C\u54AC\u7259\u9053\uFF1A\u300C\u4F60\u4EEC\u662F\u4EC0\u4E48\u4EBA\uFF1F\u300D</p><p>\u970D\u72C2\u7130\u89C1\u4ED6\u5014\u5F3A\uFF0C\u6325\u4E86\u6325\u624B\uFF0C\u5E2E\u4F17\u7ACB\u523B\u4ECE\u540E\u5802\u5E26\u51FA\u4E24\u540D\u5973\u5B50\u3002</p><p>\u6CAE\u6E20\u592B\u4EBA\u5E74\u8FD1\u56DB\u65EC\uFF0C\u8EAB\u4F53\u7565\u4E3A\u53D1\u798F\u3002\u770B\u5230\u4E08\u592B\u60E8\u6B7B\uFF0C\u987F\u65F6\u5413\u5F97\u5C16\u53EB\u8D77\u6765\u3002</p><p>\u970D\u72C2\u7130\u9633\u5177\u88AB\u5272\u91CE\u6027\u672A\u6539\uFF0C\u4ED6\u6025\u7740\u56DE\u5BAB\u8BF7\u53F6\u884C\u5357\u6551\u6CBB\uFF0C\u4E5F\u4E0D\u591A\u8BF4\u5E9F\u8BDD\uFF0C\u51E0\u628A\u626F\u5C3D\u6CAE\u6E20\u592B\u4EBA\u7684\u8863\u886B\uFF0C\u62E7\u4F4F\u4E30\u6EE1\u7684\u4E73\u623F\uFF0C\u72DE\u58F0\u9053\uFF1A\u300C\u5C0F\u5B50\uFF0C\u8BF4\u4E0D\u8BF4\uFF1F\u300D</p><p>\u6CAE\u6E20\u5C55\u626C\u60CA\u6012\u4EA4\u52A0\uFF0C\u7259\u5173\u683C\u683C\u4F5C\u54CD\u3002</p><p>\u300C\u5657\u53FD\u300D\u4E00\u58F0\uFF0C\u970D\u72C2\u7130\u5341\u6307\u5982\u94A9\uFF0C\u5C06\u90A3\u53EA\u80A5\u4E73\u6293\u5F97\u7C89\u788E\u3002\u8840\u8089\u4ECE\u4ED6\u6307\u95F4\u7A00\u6CE5\u822C\u6EA2\u51FA\uFF0C\u6D60\u6D60\u6CA5\u6CA5\u843D\u5728\u5730\u4E0A\u3002\u6CAE\u6E20\u592B\u4EBA\u60E8\u53EB\u534A\u58F0\uFF0C\u6655\u4E86\u8FC7\u53BB\u3002</p><p>\u6CAE\u6E20\u5C55\u626C\u76EE\u7726\u6B32\u88C2\uFF0C\u53CC\u76EE\u901A\u7EA2\u3002\u773C\u89C1\u970D\u72C2\u7130\u53C8\u6293\u4F4F\u6BCD\u4EB2\u53E6\u4E00\u53EA\u4E73\u623F\uFF0C\u5636\u58F0\u9053\uFF1A\u300C\u7D2B\u73AB\u56DE\u4F0F\u9F99\u6DA7\u4E86\uFF01\u300D</p><p>\u970D\u72C2\u7130\u5C06\u53E6\u4E00\u53EA\u4E73\u623F\u786C\u751F\u751F\u63EA\u4E0B\uFF0C\u6296\u624B\u6254\u5230\u6CAE\u6E20\u5C55\u626C\u9762\u524D\uFF0C\u300C\u4F0F\u9F99\u6DA7\u65E9\u5C31\u6210\u4E86\u767D\u5730\uFF0C\u5979\u8FD8\u80FD\u56DE\u54EA\u513F\uFF1F\u300D</p><p>\u7834\u788E\u7684\u4E73\u623F\u5728\u773C\u524D\u5FAE\u5FAE\u62BD\u52A8\uFF0C\u6CAE\u6E20\u5C55\u626C\u53EA\u89C9\u8033\u4E2D\u8F70\u9E23\uFF0C\u5929\u65CB\u5730\u8F6C\uFF0C\u5F20\u5F20\u5634\uFF0C\u5374\u6CA1\u6709\u53D1\u51FA\u4E00\u4E1D\u58F0\u97F3\u3002\u4ED6\u6A21\u6A21\u7CCA\u7CCA\u770B\u5230\u90A3\u4E2A\u7EA2\u8863\u4EBA\u628A\u624B\u4F38\u8FDB\u6BCD\u4EB2\u80EF\u4E0B\uFF0C\u5634\u5DF4\u4E00\u5F00\u4E00\u5408\uFF0C\u4F3C\u4E4E\u5728\u8BF4\u4E9B\u4EC0\u4E48\u3002\u6BCD\u4EB2\u5012\u5728\u5730\u4E0A\uFF0C\u4E24\u624B\u63E1\u4F4F\u80F8\u524D\u5DE8\u5927\u7684\u4F24\u53E3\uFF0C\u4E24\u817F\u4E0D\u4F4F\u626D\u52A8\u3002</p><p>\u6CAE\u6E20\u5C55\u626C\u5446\u5446\u76EF\u7740\u5782\u6B7B\u7684\u6BCD\u4EB2\u3002\u4ED6\u6E05\u695A\u5730\u770B\u5230\u6BCD\u4EB2\u80A5\u539A\u7684\u9634\u5507\u7A81\u7136\u6DA8\u5927\uFF0C\u8089\u7A74\u5185\u7EA2\u5149\u95EA\u52A8\u3002\u63A5\u7740\u4E24\u6761\u5927\u817F\u731B\u7136\u5206\u5F00\uFF0C\u626D\u66F2\u7740\u7FD8\u5728\u4F53\u4FA7\u3002\u80A5\u539A\u591A\u6C41\u7684\u5AE9\u8089\u77AC\u95F4\u88AB\u4E00\u80A1\u5DE8\u5927\u7684\u529B\u91CF\u6495\u6210\u788E\u7247\uFF0C\u8840\u8089\u96E8\u6C34\u822C\u661F\u661F\u70B9\u70B9\u98DE\u5411\u7A7A\u4E2D\u3002</p><p>\u5DE8\u54CD\u8FC7\u540E\uFF0C\u6CAE\u6E20\u592B\u4EBA\u7684\u79D8\u5904\u5DF2\u7ECF\u8361\u7136\u65E0\u5B58\uFF0C\u53EA\u5269\u534A\u622A\u8840\u6DCB\u6DCB\u7684\u4E0B\u8179\u3002\u6A21\u7CCA\u7684\u8840\u8089\u95F4\uFF0C\u4E00\u5757\u6B8B\u5B58\u7684\u5B50\u5BAB\u8F6F\u8F6F\u6ED1\u4E0B\u3002</p><p>\u970D\u72C2\u7130\u4ED4\u7EC6\u5BA1\u89C6\u534A\u664C\uFF0C\u51B3\u5B9A\u4EE5\u540E\u706B\u836F\u53EA\u7528\u4E09\u5206\uFF0C\u7B49\u64CD\u5B8C\u98CE\u665A\u534E\u4E4B\u540E\uFF0C\u628A\u5979\u7684\u5C44\u70B8\u6210\u4E2A\u8840\u6D1E\u3002\u5343\u4E07\u4E0D\u80FD\u4E00\u4E0B\u70B8\u6B7B\u3002\u4ED6\u8F6C\u8EAB\u95EE\u9053\uFF1A\u300C\u6155\u5BB9\u7D2B\u73AB\u5728\u54EA\u91CC\uFF1F\u300D</p><p>\u660F\u660F\u6C89\u6C89\u95F4\uFF0C\u6CAE\u6E20\u5C55\u626C\u5DF2\u7ECF\u54AC\u7834\u4E86\u81EA\u5DF1\u7684\u5634\u5507\uFF0C\u4ED6\u4E00\u8FB9\u5410\u8840\uFF0C\u4E00\u8FB9\u65E0\u529B\u5730\u6447\u4E86\u6447\u5934\u3002</p><p>\u970D\u72C2\u7130\u5634\u89D2\u6311\u8D77\u4E00\u4E1D\u72DE\u7B11\uFF0C\u5288\u624B\u6293\u8D77\u6CAE\u6E20\u660E\u5170\u3002</p><p>\u5341\u56DB\u5C81\u7684\u6CAE\u6E20\u660E\u5170\u5413\u5F97\u6C14\u90FD\u4E0D\u6562\u51FA\uFF0C\u4E4C\u6E9C\u6E9C\u7684\u5927\u773C\u775B\u50BB\u50BB\u770B\u7740\u8FD9\u4E2A\u6076\u9B54\u3002</p><p>\u6CAE\u6E20\u5C55\u626C\u6EE1\u5934\u51B7\u6C57\uFF0C\u300C\u54C7\u300D\u7684\u5410\u4E86\u53E3\u9C9C\u8840\uFF0C\u5636\u58F0\u9053\uFF1A\u300C\u653E\u4E0B\u660E\u5170\uFF01\u5979\u3001\u5979\u3001\u5979\u2026\u2026\u5979\u53BB\u4E86\u98D8\u6885\u5CF0\u2026\u2026\u300D\u8A00\u7F62\u5FC3\u5982\u5272\uFF0C\u4ED6\u4ECE\u6CA1\u60F3\u8FC7\u81EA\u5DF1\u4F1A\u80CC\u53DB\u7D2B\u73AB\uFF0C\u4F46\u2026\u2026</p><p>\u970D\u72C2\u7130\u6495\u788E\u660E\u5170\u7684\u8863\u886B\uFF0C\u6293\u5F04\u7740\u5979\u767D\u7F8A\u822C\u8089\u4F53\uFF0C\u5389\u58F0\u9053\uFF1A\u300C\u8FD8\u6562\u9A97\u6211\uFF01\u628A\u90A3\u6B7B\u5A4A\u5B50\u62C9\u4E0A\u6765\uFF01\u300D</p><p>\u95E8\u5916\u4F20\u6765\u4E00\u9635\u94C1\u94FE\u54CD\u52A8\uFF0C\u4E00\u540D\u7EA2\u8863\u4EBA\u534A\u62D6\u534A\u62FD\u5730\u62C9\u7740\u4E00\u4E2A\u6D51\u8EAB\u8840\u8FF9\u7684\u8D64\u88F8\u5973\u4EBA\u8D70\u4E86\u8FDB\u6765\u3002</p><p>\u4E24\u6839\u7C97\u903E\u4EBA\u6307\u7684\u94C1\u94FE\u4ECE\u5973\u4EBA\u5DE6\u53F3\u7435\u7436\u9AA8\u7A7F\u8FC7\uFF0C\u9EDD\u9ED1\u7684\u94C1\u94FE\u6CBE\u6EE1\u8840\u8089\u5728\u4F24\u53E3\u4E0D\u4F4F\u6469\u64E6\uFF0C\u94EE\u7136\u4F5C\u54CD\u3002\u5979\u9888\u4E0A\u6302\u7740\u4E00\u622A\u624B\u81C2\uFF0C\u81C2\u4FEE\u957F\u7684\u624B\u6307\u5149\u6ED1\u7EC6\u767D\uFF0C\u5728\u80F8\u53E3\u6765\u56DE\u6643\u8361\u3002\u53F3\u81C2\u53EA\u5269\u4E0B\u534A\u622A\u6B8B\u80A2\uFF0C\u521B\u53E3\u8FD8\u88F9\u7740\u8840\u6DCB\u6DCB\u7684\u767D\u5E03\uFF0C\u663E\u7136\u662F\u521A\u88AB\u4EBA\u780D\u65AD\u3002</p><p>\u867D\u7136\u80A2\u4F53\u4E0D\u5168\uFF0C\u904D\u4F53\u662F\u8840\uFF0C\u4F46\u9AD8\u8038\u7684\u4E73\u623F\uFF0C\u7EC6\u81F4\u7684\u8170\u8EAB\uFF0C\u4EE5\u53CA\u8840\u8FF9\u4E0B\u7247\u7247\u767D\u51C0\uFF0C\u4ECD\u80FD\u770B\u51FA\u5979\u4F18\u7F8E\u7684\u4F53\u5F62\u548C\u767D\u7699\u7684\u76AE\u80A4\u3002\u5979\u5634\u4E2D\u76F4\u76F4\u63D2\u7740\u4E00\u4E2A\u7AF9\u7B52\uFF0C\u906E\u4F4F\u4E86\u9762\u5B54\uFF0C\u7B52\u53E3\u5FAE\u5FAE\u9732\u51FA\u4E00\u70B9\u67D4\u5AE9\u7684\u7EA2\u8272\uFF0C\u4F9D\u7A00\u662F\u820C\u5C16\u7684\u6837\u5B50\u3002</p><p>\u970D\u72C2\u7130\u4F3C\u4E4E\u5BF9\u5979\u6068\u4E4B\u5165\u9AA8\uFF0C\u6293\u4F4F\u94C1\u94FE\u4E24\u7AEF\u6765\u56DE\u62BD\u52A8\u3002\u7C97\u7CD9\u7684\u94C1\u94FE\u7A7F\u8FC7\u80A9\u4E0A\u672A\u903E\u5408\u7684\u4F24\u53E3\uFF0C\u7ACB\u523B\u5E26\u51FA\u4E1D\u7CCA\u72B6\u7684\u7F15\u7F15\u8840\u8089\u3002\u90A3\u5973\u4EBA\u6D51\u8EAB\u98A4\u6296\uFF0C\u5589\u4E2D\u4E0D\u4F4F\u95F7\u54CD\u3002\u5FFD\u7136\u81FB\u9996\u4E00\u5782\uFF0C\u9732\u51FA\u79C0\u7F8E\u7684\u9762\u5B54\u3002</p><p>\u300C\u98CE\u5E08\u59D0\uFF01\u300D\u6CAE\u6E20\u5C55\u626C\u5931\u58F0\u53EB\u9053\u3002\u8FD9\u4E2A\u88AB\u4EBA\u6B8B\u8650\u7684\u51C4\u60E8\u5973\u5B50\uFF0C\u7ADF\u7136\u662F\u540D\u9707\u6C5F\u6E56\u7684\u6D41\u971C\u5251\uFF01\u90A3\u4E2A\u5149\u5F69\u7167\u4EBA\u7684\u98D8\u6885\u5CF0\u9996\u5F92\u98CE\u665A\u534E\uFF01</p><p>\u970D\u72C2\u7130\u4E00\u811A\u628A\u98CE\u665A\u534E\u8E22\u5012\uFF0C\u5288\u5F00\u660E\u5170\u7684\u53CC\u817F\uFF0C\u5728\u6CAE\u6E20\u5C55\u626C\u773C\u524D\u628A\u624B\u6307\u6345\u8FDB\u660E\u5170\u7565\u663E\u5E7C\u7A1A\u7684\u7389\u6237\u5185\u3002\u660E\u5170\u4E24\u817F\u62FC\u547D\u5408\u62E2\uFF0C\u75DB\u53EB\u8FDE\u58F0\u3002\u59B9\u59B9\u5904\u5B50\u7684\u9C9C\u8840\u987A\u7740\u7C97\u9ED1\u7684\u624B\u6307\u4E00\u6EF4\u6EF4\u843D\u5728\u5730\u4E0A\uFF0C\u6CAE\u6E20\u5C55\u626C\u5FC3\u75BC\u6B32\u88C2\uFF0C\u5583\u5583\u9053\uFF1A\u300C\u5979\u771F\u662F\u53BB\u4E86\u98D8\u6885\u5CF0\u2026\u300D</p><p>\u970D\u72C2\u7130\u62FF\u51FA\u4E00\u7C92\u6307\u5C16\u5927\u5C0F\u7684\u9ED1\u8272\u5706\u7403\uFF0C\u8638\u7740\u9C9C\u8840\u585E\u8FDB\u660E\u5170\u79D8\u5904\u3002\u8089\u7A74\u8840\u5982\u6CC9\u6D8C\uFF0C\u90A3\u7C92\u9ED1\u8272\u7684\u5706\u7403\u5D4C\u5728\u7EFD\u88C2\u7684\u5AE9\u8089\u5185\uFF0C\u65F6\u9690\u65F6\u73B0\uFF0C\u5B9B\u5982\u4E00\u53EA\u8BE1\u5F02\u7684\u773C\u7403\uFF0C\u6563\u53D1\u7740\u6076\u6BD2\u7684\u5149\u8292\uFF0C\u300C\u8FD9\u4E48\u5AE9\u7684\u5C0F\u7F8E\u5C44\uFF0C\u70B8\u788E\u4E86\u602A\u53EF\u60DC\u7684\u3002\u6CA1\u529E\u6CD5\uFF0C\u8C01\u8BA9\u5979\u54E5\u54E5\u662F\u4E2A\u591A\u60C5\u79CD\u5B50\uFF0C\u5B81\u613F\u770B\u7740\u59B9\u59B9\u7684\u5C0F\u5904\u5973\uFF0C\u88AB\u4E24\u6839\u624B\u6307\u5934\u6345\u7834\u4E5F\u4E0D\u5F00\u53E3\u5462\uFF1F\u300D</p><p>\u6CAE\u6E20\u5C55\u626C\u773C\u524D\u4E00\u9ED1\uFF0C\u4E0D\u7701\u4EBA\u4E8B\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u6C34\u67D4\u4ED9\u53D7\u7684\u662F\u5185\u4F24\uFF0C\u56E0\u6B64\u6CA1\u6709\u968F\u970D\u72C2\u7130\u540C\u53BB\u6B66\u9675\u3002\u56DB\u6708\u521D\u4E00\uFF0C\u5979\u5E26\u7740\u53D7\u4F24\u7684\u5E2E\u4F17\u56DE\u5230\u661F\u6708\u6E56\u3002</p><p>\u5BAB\u4E3B\u5BF9\u767D\u6C0F\u59D0\u59B9\u5F88\u6EE1\u610F\uFF0C\u53C8\u542C\u8BF4\u751F\u64D2\u4E86\u98CE\u665A\u534E\uFF0C\u51E0\u4E4E\u5C06\u98D8\u6885\u5CF0\u4E00\u7F51\u6253\u5C3D\uFF0C\u548C\u989C\u60A6\u8272\u7684\u6E29\u8A00\u5609\u5956\u4E86\u51E0\u53E5\u3002\u8FDE\u970D\u72C2\u7130\u64C5\u81EA\u5E26\u7740\u4FD8\u864F\u884C\u52A8\u4E5F\u672A\u52A0\u8FFD\u7A76\u3002</p><p>\u91D1\u5F00\u7532\u6BD4\u6C34\u67D4\u4ED9\u65E9\u4E86\u534A\u65E5\u56DE\u5BAB\uFF0C\u521A\u7531\u53F6\u884C\u5357\u770B\u4E86\u773C\u4E0A\u7684\u4F24\u52BF\u3002\u5F97\u77E5\u81EA\u5DF1\u773C\u775B\u5DF2\u7ECF\u65E0\u8BA1\u53EF\u6CBB\uFF0C\u4ED6\u6EE1\u8179\u6012\u706B\uFF0C\u6B63\u51C6\u5907\u53BB\u72E0\u72E0\u6298\u78E8\u6797\u9999\u8FDC\u4E00\u756A\u51FA\u6C14\uFF0C\u4E0D\u6599\u6C34\u67D4\u4ED9\u7A81\u7136\u6765\u8BBF\u3002</p><p>\u4E24\u4EBA\u90FD\u662F\u6DF1\u8D44\u957F\u8001\uFF0C\u76F8\u8BC6\u5DF2\u4E45\uFF0C\u6C34\u67D4\u4ED9\u5F00\u95E8\u89C1\u5C71\u5730\u8BF4\uFF1A\u300C\u6211\u89C9\u5F97\u65B0\u4EFB\u5BAB\u4E3B\u4E0D\u59A5\u3002\u300D</p><p>\u91D1\u5F00\u7532\u72EC\u76EE\u5BD2\u5149\u4E00\u95EA\uFF0C\u300C\u6709\u4F55\u4E0D\u59A5\uFF1F\u300D</p><p>\u300C\u5F53\u65E5\u5BAB\u4E3B\u4F20\u4F4D\u65F6\uFF0C\u4F60\u3001\u6211\u3001\u6C90\u957F\u8001\u90FD\u4E0D\u5728\u5BAB\u4E2D\u3002\u8FD9\u4F4D\u65B0\u5BAB\u4E3B\u672C\u6765\u4E0D\u8FC7\u662F\u4E2A\u65E0\u540D\u65E0\u59D3\u7684\u5A08\u7AE5\uFF0C\u4E3A\u4F55\u5BAB\u4E3B\u4F1A\u4F20\u4F4D\u4E8E\u4ED6\uFF1F\u300D</p><p>\u91D1\u5F00\u7532\u6C89\u58F0\u9053\uFF1A\u300C\u6211\u66FE\u95EE\u8FC7\uFF0C\u6B64\u4E8B\u6709\u53F6\u62A4\u6CD5\u3001\u6731\u90AA\u62A4\u6CD5\u4E24\u4F4D\u4F5C\u8BC1\uFF0C\u786E\u5B9E\u662F\u5BAB\u4E3B\u4EB2\u81EA\u4F20\u4F4D\u2014\u2014\u4E09\u4E2A\u6708\u524D\u65B0\u5BAB\u4E3B\u683C\u6740\u571F\u5802\u957F\u8001\uFF0C\u6211\u6B63\u5728\u573A\uFF0C\u4ED6\u7528\u7684\u6B66\u529F\u786E\u5B9E\u662F\u5BAB\u4E3B\u4EB2\u4F20\u3002\u300D</p><p>\u6C34\u67D4\u4ED9\u62AC\u8D77\u7389\u638C\uFF0C\u5C48\u4E0B\u4E00\u6839\u624B\u6307\uFF0C\u300C\u6211\u6709\u4E94\u4E2A\u7591\u95EE\uFF1A\u7B2C\u4E00\uFF0C\u5BAB\u4E3B\u4E3A\u4F55\u4F20\u4F4D\u4E4B\u540E\u5C31\u4E0D\u518D\u51FA\u73B0\uFF1F\u300D</p><p>\u91D1\u5F00\u7532\u6C89\u541F\u9053\uFF1A\u300C\u4E5F\u8BB8\u662F\u5BAB\u4E3B\u95ED\u5173\u4FEE\u70BC\uFF0C\u9887\u8D39\u65F6\u65E5\u2014\u2014\u5BAB\u4E3B\u4E00\u76F4\u5728\u4FEE\u70BC\u672C\u6559\u9547\u6559\u795E\u529F\u592A\u4E00\u7ECF\u3002\u300D</p><p>\u6C34\u67D4\u4ED9\u53C8\u5C48\u4E0B\u4E00\u6839\u624B\u6307\uFF1A\u300C\u7B2C\u4E8C\uFF0C\u65B0\u4EFB\u5BAB\u4E3B\u4E3A\u4F55\u8981\u6740\u6389\u706B\u3001\u571F\u4E24\u5802\u957F\u8001\uFF1F\u300D</p><p>\u300C\u8D64\u3001\u6D82\u4E24\u4F4D\u957F\u8001\u529E\u4E8B\u4E0D\u529B\u4E5F\u662F\u6709\u7684\uFF0C\u5BAB\u4E3B\u65B0\u4EFB\uFF0C\u6740\u4EBA\u7ACB\u5A01\u4E5F\u662F\u5E38\u7406\u3002\u300D</p><p>\u300C\u7B2C\u4E09\uFF0C\u65B0\u4EFB\u5BAB\u4E3B\u4E3A\u4F55\u8981\u706D\u6389\u4F0F\u9F99\u6DA7\uFF1F\u300D</p><p>\u300C\u6155\u5BB9\u536B\u624B\u4E2D\u6709\u85CF\u5B9D\u56FE\uFF0C\u6B63\u662F\u6000\u74A7\u4E4B\u7F6A\u3002\u300D</p><p>\u300C\u7B2C\u56DB\uFF0C\u65B0\u4EFB\u5BAB\u4E3B\u4E3A\u4F55\u8981\u5343\u65B9\u767E\u8BA1\u5F97\u5230\u7D2B\u73AB\uFF1F\u751A\u81F3\u4E0D\u60DC\u4E0E\u98D8\u6885\u5CF0\u4E3A\u654C\uFF1F\u300D</p><p>\u91D1\u5F00\u7532\u5475\u5475\u7B11\u9053\uFF1A\u300C\u6C34\u957F\u8001\u8FD9\u5C31\u662F\u4E0D\u61C2\u7537\u4EBA\u4E86\u3002\u73AB\u7470\u4ED9\u5B50\u751F\u5F97\u5343\u5A07\u767E\u5A9A\uFF0C\u5982\u82B1\u4F3C\u7389\uFF0C\u6211\u89C1\u4E4B\u72B9\u601C\uFF0C\u4F55\u51B5\u662F\u5BAB\u4E3B\u8FD9\u6837\u7684\u5E74\u8F7B\u4EBA\uFF1F\u300D</p><p>\u6C34\u67D4\u4ED9\u4E0D\u52A8\u58F0\u8272\uFF0C\u5C48\u4E0B\u6700\u540E\u4E00\u6839\u624B\u6307\uFF0C\u300C\u6731\u90AA\u62A4\u6CD5\u548C\u5C48\u62A4\u6CD5\u5728\u54EA\u91CC\uFF1F\u300D</p><p>\u300C\u534A\u6708\u524D\u63A5\u5230\u53EF\u9760\u6D88\u606F\uFF0C\u96EA\u5CF0\u795E\u5C3C\u5728\u5357\u6D77\u4E00\u5E26\u51FA\u73B0\u3002\u4E24\u4F4D\u62A4\u6CD5\u8D74\u5357\u6D77\u622A\u51FB\u795E\u5C3C\u3002\u300D</p><p>\u6C34\u67D4\u4ED9\u6C89\u9ED8\u7247\u523B\uFF0C\u5FAE\u5FAE\u6447\u4E86\u6447\u5934\uFF0C\u300C\u4F60\u9519\u4E86\u3002\u300D</p>',58);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
