import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_22" tabindex="-1"><a class="header-anchor" href="#_22" aria-hidden="true">#</a> 22</h1><p>\u91D1\u5F00\u7532\u9762\u8272\u51DD\u91CD\uFF0C\u5012\u4E86\u7897\u9152\uFF0C\u7F13\u7F13\u996E\u5E72\u3002</p><p>\u300C\u5BAB\u4E3B\u7EDD\u975E\u95ED\u5173\u3002\u4F20\u4F4D\u662F\u5E2E\u4E2D\u5927\u4E8B\uFF0C\u600E\u4E48\u53EF\u80FD\u8D81\u4E09\u4F4D\u957F\u8001\u4E0D\u5728\u5BAB\u4E2D\u7684\u65F6\u5019\u7A81\u7136\u4F20\u4F4D\uFF1F\u51B5\u4E14\u5E2E\u4E2D\u8FD9\u4E48\u591A\u9AD8\u624B\uFF0C\u53C8\u600E\u4E48\u4F1A\u4F20\u4F4D\u7ED9\u4E00\u4E2A\u5A08\u7AE5\uFF1F\u6211\u6562\u8BF4\uFF0C\u5BAB\u4E3B\u5DF2\u7ECF\u51F6\u591A\u5409\u5C11\uFF01\u300D</p><p>\u300C\u7B2C\u4E8C\uFF0C\u571F\u3001\u706B\u4E24\u5802\u957F\u8001\u7D20\u6765\u529F\u52F3\u5353\u7740\uFF0C\u5E76\u65E0\u5927\u8FC7\uFF0C\u53EA\u4E3A\u4E00\u70B9\u5C0F\u4E8B\u5C31\u6740\u4E86\u4E24\u4EBA\uFF0C\u7EDD\u975E\u662F\u4E3A\u4E86\u7ACB\u5A01\uFF1B\u300D</p><p>\u300C\u7B2C\u4E09\uFF0C\u85CF\u5B9D\u56FE\u4E4B\u4E8B\u865A\u65E0\u7F25\u7F08\uFF0C\u5373\u4F7F\u8981\u593A\u4E5F\u4E0D\u5FC5\u706D\u5176\u6EE1\u95E8\uFF1B\u300D</p><p>\u300C\u7B2C\u56DB\uFF0C\u5F53\u521D\u4ED6\u4E0B\u4EE4\u8FFD\u8E2A\u6155\u5BB9\u536B\u65F6\u73AB\u7470\u4ED9\u5B50\u7684\u540D\u58F0\u8FD8\u672A\u4F20\u64AD\u6C5F\u6E56\uFF0C\u4E3A\u4F55\u5BAB\u4E3B\u4F1A\u77E5\u9053\u6155\u5BB9\u7D2B\u73AB\u8273\u8272\u503E\u57CE\uFF1F\u4ED6\u539F\u672C\u5C31\u662F\u5BAB\u4E3B\u7684\u6DEB\u5177\uFF0C\u4EC0\u4E48\u6837\u7684\u5973\u4EBA\u6CA1\u89C1\u8FC7\uFF1F\u4F55\u51B5\u98D8\u6885\u5CF0\u5C82\u662F\u597D\u60F9\uFF01\u96EA\u5CF0\u795E\u5C3C\u6B66\u529F\u76D6\u4E16\uFF0C\u6D41\u971C\u5251\u3001\u5BD2\u6708\u5200\u4E5F\u4E0D\u5728\u4F60\u6211\u4E4B\u4E0B\uFF0C\u8FD9\u6B21\u9664\u4E86\u6C90\u957F\u8001\uFF0C\u56DB\u5802\u957F\u8001\u90FD\u8D1F\u4E86\u4F24\uFF0C\u5341\u4E8C\u540D\u9999\u4E3B\u4E94\u6B7B\u4E24\u4F24\uFF0C\u5E2E\u4E2D\u5B9E\u529B\u5927\u635F\uFF0C\u90FD\u662F\u4E3A\u4E86\u533A\u533A\u4E00\u4E2A\u73AB\u7470\u4ED9\u5B50\u2014\u2014\u91D1\u5927\u54E5\uFF0C\u4ED6\u4E0D\u662F\u5782\u6D8E\u7F8E\u8272\uFF0C\u800C\u662F\u85C9\u673A\u6E05\u9664\u6211\u4EEC\u4E94\u5802\uFF01\u5341\u4E94\u65E5\u5BAB\u4E2D\u5927\u4F1A\uFF0C\u6216\u8005\u662F\u6211\uFF0C\u6216\u8005\u662F\u4F60\uFF0C\u6216\u8005\u662F\u6C90\u957F\u8001\uFF0C\u5FC5\u4F1A\u88AB\u4ED6\u9664\u6389\uFF01\u300D</p><p>\u91D1\u5F00\u7532\u53C8\u5012\u4E86\u7897\u9152\uFF0C\u4E00\u53E3\u559D\u4E0B\uFF0C\u634F\u7740\u74F7\u7897\u4E00\u52A8\u4E0D\u52A8\u3002</p><p>\u6C34\u67D4\u4ED9\u82E6\u53E3\u5A46\u5FC3\u5730\u8BF4\uFF1A\u300C\u91D1\u5927\u54E5\uFF0C\u4F60\u8FD8\u4E0D\u660E\u767D\u5417\uFF1F\u4ED6\u8BBE\u8BA1\u6740\u4E86\u5BAB\u4E3B\uFF0C\u9A97\u8FC7\u4E24\u4F4D\u62A4\u6CD5\uFF0C\u7BE1\u5F97\u5BAB\u4E3B\u4E4B\u4F4D\uFF1B\u7136\u540E\u53C8\u501F\u53E3\u85CF\u5B9D\u56FE\u548C\u6155\u5BB9\u7D2B\u73AB\u903C\u6211\u4EEC\u4E0E\u98D8\u6885\u5CF0\u786C\u62FC\uFF0C\u82E5\u8BBA\u7F8E\u8272\uFF0C\u7EAA\u7709\u59A9\u3001\u6797\u9999\u8FDC\u53EA\u6BD4\u6155\u5BB9\u7D2B\u73AB\u7565\u900A\u4E00\u7B79\uFF0C\u4E3A\u4F55\u4ED6\u5F85\u4E4B\u5982\u540C\u72AC\u8C56\uFF1F\u5F00\u7532\uFF0C\u4F60\u96BE\u9053\u8981\u5750\u4EE5\u5F85\u6BD9\u5417\uFF1F\u300D</p><p>\u91D1\u5F00\u7532\u628A\u74F7\u7897\u8F7B\u8F7B\u653E\u5728\u51E0\u4E0A\uFF0C\u6162\u6162\u8BF4\u9053\uFF1A\u300C\u67D4\u4ED9\uFF0C\u4F60\u6211\u76F8\u4EA4\u591A\u5E74\uFF0C\u96BE\u5F97\u4F60\u8FD9\u4E48\u770B\u5F97\u8D77\u6211\uFF0C\u63A8\u5FC3\u7F6E\u8179\u8BF4\u4E86\u8FD9\u756A\u8BDD\u3002\u6211\u5C31\u660E\u8BF4\u4E86\u5427\u2014\u2014\u4F60\u60F3\u7684\uFF0C\u6211\u90FD\u60F3\u8FC7\u3002\u4F46\u65B0\u5BAB\u4E3B\u80F8\u6000\u5927\u5FD7\uFF0C\u5904\u4E8B\u575A\u5FCD\u52C7\u51B3\uFF0C\u5B9E\u662F\u4E00\u4F4D\u826F\u4E3B\u3002\u6211\u661F\u6708\u6E56\u80FD\u4EBA\u65E0\u6570\uFF0C\u4F46\u539F\u5BAB\u4E3B\u53EA\u6EE1\u8DB3\u4E8E\u5C71\u4E2D\u79F0\u738B\uFF0C\u56FA\u6B65\u81EA\u5C01\uFF0C\u8352\u5E9F\u4E86\u591A\u5C11\u65F6\u673A\uFF1F\u503C\u6B64\u4E71\u4E16\uFF0C\u6B63\u662F\u7537\u513F\u7ACB\u529F\u4E4B\u65F6\uFF0C\u6211\u91D1\u5F00\u7532\u4E00\u8EAB\u672C\u9886\uFF0C\u5C82\u80FD\u57CB\u6CA1\u4E8E\u8349\u83BD\u4E4B\u4E2D\uFF1F\u300D</p><p>\u6C34\u67D4\u4ED9\u77A0\u76EE\u7ED3\u820C\uFF0C\u7EA4\u624B\u4E00\u62CD\u957F\u51E0\uFF0C\u6B63\u5F85\u8BF4\u8BDD\uFF0C\u5374\u89C1\u90A3\u53EA\u74F7\u7897\u77AC\u95F4\u5316\u4E3A\u4E00\u5806\u96EA\u767D\u7684\u7C89\u672B\uFF0C\u7EC6\u7802\u822C\u5747\u5300\u3002\u5979\u5FC3\u795E\u5927\u9707\uFF0C\u91D1\u5F00\u7532\u5411\u6765\u4EE5\u786C\u529F\u7740\u79F0\uFF0C\u73B0\u5728\u7ADF\u7EC3\u5230\u521A\u67D4\u76F8\u6D4E\u7684\u5730\u6B65\uFF0C\u4E0D\u6127\u4F4D\u5C45\u4E94\u957F\u8001\u9996\u5E2D\u3002</p><p>\u91D1\u5F00\u7532\u6BEB\u4E0D\u7406\u4F1A\u5979\u7684\u8BB6\u8272\uFF0C\u8FF3\u81EA\u8BF4\u9053\uFF1A\u300C\u6211\u662F\u6B7B\u5FC3\u8E4B\u5730\u8DDF\u8FD9\u4F4D\u5BAB\u4E3B\u5E72\u4E86\uFF0C\u4F46\u4F60\u653E\u5FC3\uFF0C\u6211\u91D1\u5F00\u7532\u5149\u660E\u78CA\u843D\uFF0C\u4ECA\u65E5\u4E4B\u4E8B\uFF0C\u7EDD\u4E0D\u4F1A\u6CC4\u9732\u53EA\u8A00\u7247\u8BED\u3002\u65E0\u8BBA\u4F60\u505A\u4EC0\u4E48\uFF0C\u6211\u90FD\u4E0D\u7406\u4F1A\u3002\u5FF5\u5728\u76F8\u8BC6\u591A\u5E74\u7684\u4EFD\u4E0A\uFF0C\u6211\u4E24\u4E0D\u76F8\u5E2E\uFF0C\u5982\u4F55\uFF1F\u300D</p><p>\u6C34\u67D4\u4ED9\u77E5\u9053\u591A\u8BF4\u65E0\u76CA\uFF0C\u5F53\u4E0B\u62B1\u4E86\u62B1\u62F3\uFF0C\u8F6C\u8EAB\u79BB\u5F00\u3002</p><p>\u6C90\u58F0\u4F20\u50CF\u6BB5\u67AF\u6728\u822C\u5750\u5728\u6905\u4E2D\u3002\u542C\u7F62\u6C34\u67D4\u4ED9\u7684\u6765\u610F\uFF0C\u5E72\u7626\u7684\u8138\u4E0A\u6CA1\u6709\u4E00\u4E1D\u8868\u60C5\uFF0C\u534A\u664C\u540E\u6162\u541E\u541E\u5408\u4E0A\u773C\u775B\uFF0C\u4E00\u8A00\u4E0D\u53D1\u3002\u6C34\u67D4\u4ED9\u5FC3\u4E2D\u5927\u5B9A\uFF0C\u77E5\u9053\u4ED6\u5DF2\u5E94\u5141\u3002</p><p>\u53F6\u884C\u5357\u6B66\u529F\u5E73\u5E73\uFF0C\u6731\u90AA\u9752\u6811\u3001\u5C48\u82E6\u85E4\u4E0D\u5728\u5BAB\u4E2D\uFF0C\u65F6\u673A\u5927\u597D\u3002\u571F\u3001\u706B\u4E24\u5802\u9999\u4E3B\u5C3D\u6CA1\uFF0C\u4E24\u540D\u957F\u8001\u867D\u662F\u65B0\u5BAB\u4E3B\u7684\u5FC3\u8179\uFF0C\u4E5F\u4E0D\u8DB3\u4E3A\u8651\u3002\u5B9E\u529B\u6700\u5F3A\u7684\u91D1\u5F00\u7532\u4E24\u4E0D\u76F8\u5E2E\uFF0C\u6728\u5802\u5B9E\u529B\u4E1D\u6BEB\u672A\u635F\uFF0C\u5982\u4ECA\u6709\u6C90\u58F0\u4F20\u652F\u6301\u81EA\u5DF1\uFF0C\u5BF9\u4ED8\u4E00\u4E2A\u5A08\u7AE5\u4E0D\u5728\u8BDD\u4E0B\uFF01</p><p>\u6C34\u67D4\u4ED9\u5306\u5306\u8FD4\u56DE\u6C34\u5802\uFF0C\u5148\u62DB\u6765\u4F24\u52BF\u8F83\u8F7B\u7684\u6E05\u9732\uFF0C\u547D\u5979\u7ACB\u5373\u79BB\u5BAB\uFF0C\u5C06\u672C\u5802\u6240\u5C5E\u7684\u5341\u4E8C\u4E2A\u5E2E\u4F1A\u9AD8\u624B\u5C3D\u6570\u62DB\u6765\uFF0C\u56DB\u6708\u5341\u56DB\u52A1\u5FC5\u8D76\u5230\u661F\u6708\u6E56\u3002</p><p>\u6C34\u5802\u63A7\u5236\u7684\u5E2E\u6D3E\u5206\u6563\u8BF8\u5904\uFF0C\u6E05\u6C5F\u4F1A\u66F4\u662F\u8FDC\u5728\u5CAD\u5357\uFF0C\u81EA\u5DF1\u4EB2\u53BB\u901A\u77E5\uFF0C\u4E00\u6765\u4E00\u56DE\u53EA\u6709\u5341\u4E09\u5929\u65F6\u95F4\uFF0C\u5982\u4F55\u80FD\u5230\uFF1F\u6E05\u9732\u5C0F\u5FC3\u5730\u95EE\u9053\uFF1A\u300C\u9644\u8FD1\u53EA\u6709\u4E03\u4E2A\u5E2E\u4F1A\uFF0C\u8981\u4E0D\u8981\u98DE\u9E3D\u4F20\u4E66\u901A\u77E5\u5176\u4F59\u4E94\u5E2E\uFF1F\u300D</p><p>\u300C\u4E03\u4E2A\u5C31\u4E03\u4E2A\u5427\u3002\u8FD9\u662F\u5802\u4E2D\u79C1\u4E8B\uFF0C\u4F60\u4EB2\u81EA\u53BB\u8BF4\uFF0C\u4E0D\u5FC5\u8BA9\u5BAB\u91CC\u77E5\u9053\u3002\u300D</p><p>\u5915\u9633\u5982\u8840\uFF0C\u661F\u6708\u6E56\u6C34\u6CE2\u4E0D\u5174\uFF0C\u5B9B\u5982\u7FA4\u5CF0\u4E00\u5757\u6C89\u74A7\u3002\u6C34\u67D4\u4ED9\u671B\u7740\u6E56\u4E2D\u90A3\u6839\u5149\u79C3\u79C3\u7684\u65D7\u6746\uFF0C\u5FC3\u91CC\u6697\u53F9\u3002\u5F53\u5E74\u661F\u6708\u6E56\u4E24\u4EEA\u4F7F\u8005\u3001\u4E09\u624D\u62A4\u6CD5\u3001\u56DB\u9547\u795E\u5C06\u3001\u4E94\u884C\u957F\u8001\u3001\u516D\u5408\u4F9B\u5949\uFF0C\u5355\u4E00\u7B49\u4E00\u7684\u9AD8\u624B\u5E94\u6709\u4E8C\u5341\u540D\uFF0C\u9AD8\u624B\u4E91\u96C6\uFF0C\u5982\u4ECA\u5374\u53EA\u5269\u516B\u4EBA\u3002\u8FD9\u4E00\u756A\u52AB\u96BE\u540E\uFF0C\u53EA\u6015\u661F\u6708\u6E56\u7CBE\u82F1\u5C3D\u4E27\u2026\u2026</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u56DB\u6708\u521D\u4E00\u591C\uFF0C\u6155\u5BB9\u7D2B\u73AB\u8D76\u5230\u7EC8\u5357\u5C71\u3002</p><p>\u7EC8\u5357\u53C8\u79F0\u79E6\u5CAD\uFF0C\u6A2A\u4E98\u5357\u5317\uFF0C\u4E1C\u897F\u7EF5\u5EF6\u6570\u767E\u91CC\u3002\u5C71\u5357\u5C71\u5317\u6C14\u5019\u8FE5\u5F02\uFF0C\u5F80\u5F80\u5C71\u5357\u7FA4\u82B3\u5410\u8273\uFF0C\u5C71\u5317\u8FD8\u662F\u98CE\u96EA\u4EA4\u52A0\u3002</p><p>\u7D2B\u73AB\u95EE\u8D77\u5F2F\u5C9B\uFF0C\u5C71\u6C11\u90FD\u9732\u51FA\u6050\u60E7\u4E4B\u8272\uFF0C\u4F17\u53E3\u4E00\u8F9E\u8BF4\u90A3\u91CC\u6709\u5996\u602A\u3002\u53BB\u5E74\u5317\u6751\u91CC\u7684\u674E\u5341\u4E09\u4E0A\u5C71\u6253\u730E\uFF0C\u5C31\u88AB\u5996\u602A\u5403\u4E86\uFF0C\u53EA\u5269\u534A\u4E2A\u8111\u888B\u2026\u2026</p><p>\u7D2B\u73AB\u5BF9\u8FD9\u4E9B\u5C71\u91CE\u4F20\u95FB\u4ED8\u4E4B\u4E00\u7B11\uFF0C\u95EE\u660E\u8DEF\u5F84\uFF0C\u4E0D\u987E\u5929\u8272\u5DF2\u665A\uFF0C\u76F4\u5165\u6DF1\u5C71\u3002\u65E9\u4E00\u65E5\u5BFB\u5230\u5B9D\u85CF\uFF0C\u5C31\u80FD\u65E9\u4E00\u65E5\u89E3\u5F00\u5FC3\u91CC\u7684\u79D8\u56E2\u3002\u5982\u679C\u53EF\u80FD\uFF0C\u5979\u5B81\u80AF\u7528\u8FD9\u4E9B\u5B9D\u85CF\u6765\u6362\u56DE\u51E0\u4F4D\u5E08\u59D0\u2014\u2014\u6216\u8005\u8FD8\u6709\u5A18\u3002\u5C06\u8FD1\u4E24\u4E2A\u6708\u6CA1\u6709\u5A18\u7684\u6D88\u606F\u4E86\uFF0C\u4E0D\u77E5\u9053\u5A18\u73B0\u5728\u600E\u4E48\u6837\u2026\u2026</p><p>\u5979\u60F3\u8D77\u7236\u4EB2\u66FE\u8BF4\u8FC7\u5A18\u300C\u4E00\u65F6\u4E0D\u4F1A\u4FBF\u6B7B\u3002\u300D\u5F53\u521D\u7D2B\u73AB\u8FD8\u4E0D\u5F88\u62C5\u5FC3\u3002\u4F46\u8033\u95FB\u6797\u5E08\u59D0\u7684\u906D\u9047\uFF0C\u76EE\u7779\u767D\u6C0F\u59D0\u59B9\u88AB\u6DEB\u8650\u60E8\u72B6\uFF0C\u5979\u4F3C\u4E4E\u660E\u767D\u4E86\u7236\u4EB2\u5F53\u65F6\u4E3A\u4EC0\u4E48\u90A3\u4E48\u4F24\u75DB\u3002</p><p>\u5F53\u591C\u65E0\u661F\u65E0\u6708\uFF0C\u534A\u591C\u65F6\u5206\u53C8\u6D60\u6D60\u6CA5\u6CA5\u4E0B\u8D77\u5C0F\u96E8\u3002\u6155\u5BB9\u7D2B\u73AB\u8863\u886B\u5C3D\u6E7F\uFF0C\u773C\u89C1\u9A6C\u513F\u4E00\u6B65\u4E00\u6ED1\u96BE\u4EE5\u884C\u8FDB\uFF0C\u53EA\u597D\u5F03\u9A6C\u6B65\u884C\u3002\u5954\u51FA\u91CC\u8BB8\uFF0C\u5FFD\u7136\u542C\u5230\u8FDC\u5904\u4E00\u58F0\u8F7B\u54B3\uFF0C\u7D2B\u73AB\u5FC3\u4E0B\u5927\u5947\u3002</p><p>\u300C\u2026\u2026\u6C34\u5AE9\u5AE9\u7684\uFF0C\u91CC\u8FB9\u53C8\u7D27\u53C8\u6ED1\uFF0C\u9E21\u5DF4\u90FD\u5316\u4E86\u2026\u2026\u300D</p><p>\u300C\u542C\u8BF4\u8FD8\u662F\u4E2A\u5343\u91D1\u5C0F\u59D0\uFF1F\u300D</p><p>\u300C\u6709\u5BB6\u6559\u5C31\u662F\u4E0D\u4E00\u6837\uFF0C\u4E56\u7740\u5462\uFF0C\u64CD\u5B8C\u4E86\u8FD8\u77E5\u9053\u628A\u9E21\u5DF4\u5506\u5E72\u51C0\uFF0C\u820C\u5934\u8F6F\u6E9C\u6E9C\u7684\u2026\u2026\u300D</p><p>\u300C\u6211\u4ED6\u5988\u53BB\u770B\u90A3\u4E2A\u59D3\u6797\u7684\uFF0C\u6CA1\u8D76\u4E0A\u2026\u2026\u300D</p><p>\u300C\u54CE\uFF0C\u6797\u5A4A\u5B50\u600E\u4E48\u6837\uFF1F\u300D</p><p>\u300C\u7EDD\u8272\uFF01\u4E0D\u8FC7\u90A3\u5A4A\u5B50\u5014\u5F97\u5F88\uFF0C\u5BAB\u4E3B\u628A\u5979\u6B66\u529F\u5E9F\u4E86\uFF0C\u8FD8\u60F3\u6253\u4EBA\u5462\u3002\u8001\u4E03\u5C31\u88AB\u5979\u8E22\u4E86\u4E00\u5BB6\u4F19\u3002\u563F\uFF0C\u54B1\u4EEC\u8FD9\u4F4D\u9999\u4E3B\u4E0B\u624B\u4E5F\u72E0\uFF0C\u90A3\u4E48\u7C97\u7684\u68CD\u5B50\u786C\u5F80\u91CC\u8FB9\u513F\u6345\u3002\u5C41\u773C\u90FD\u6363\u51FA\u8840\u4E86\uFF0C\u90A3\u5A4A\u5B50\u54FC\u90FD\u4E0D\u54FC\u2026\u2026\u300D</p><p>\u300C\u8C01\u8BA9\u5979\u6CA1\u9E21\u5DF4\u5462\u2026\u2026\u300D</p><p>\u4E24\u4EBA\u538B\u7740\u55D3\u5B50\u563F\u563F\u76F4\u4E50\uFF0C\u5FFD\u7136\u5BD2\u5149\u4E00\u95EA\uFF0C\u4E24\u9897\u5E76\u5728\u4E00\u8D77\u7684\u4EBA\u5934\u76F4\u98DE\u534A\u7A7A\u3002</p><p>\u6E05\u9732\u6B63\u5728\u76D8\u819D\u8FD0\u529F\uFF0C\u80CC\u5FC3\u4E00\u9EBB\uFF0C\u5DF2\u88AB\u4EBA\u5236\u4F4F\u7A74\u9053\u3002\u6F06\u9ED1\u7684\u591C\u8272\u4E2D\uFF0C\u663E\u51FA\u4E00\u5F20\u660E\u7389\u65E0\u7455\u822C\u59E3\u4E3D\u7684\u9762\u5B54\u3002\u9ED1\u767D\u5206\u660E\u7684\u5927\u773C\u4EAE\u5982\u591C\u661F\uFF0C\u4F46\u773C\u795E\u4E2D\u68EE\u5BD2\u7684\u6740\u610F\u4EE4\u6E05\u9732\u4E0D\u5BD2\u800C\u6817\u3002</p><p>\u300C\u98CE\u5E08\u59D0\u5462\uFF1F\u300D\u6155\u5BB9\u7D2B\u73AB\u51B7\u51B7\u95EE\u9053\u3002</p><p>\u6E05\u9732\u98A4\u58F0\u9053\uFF1A\u300C\u5979\u8DDF\u7740\u970D\u957F\u8001\u2026\u2026\u53BB\u6B66\u9675\u4E86\u2026\u2026\u300D</p><p>\u6155\u5BB9\u7D2B\u73AB\u5A07\u8EAF\u5267\u9707\uFF0C\u5C55\u626C\u54E5\u54E5\u2026\u2026</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u5929\u8272\u5FAE\u4EAE\uFF0C\u6155\u5BB9\u7D2B\u73AB\u4F0F\u5728\u5CB8\u8FB9\u5F20\u671B\u7740\u591C\u7A7A\u822C\u78A7\u84DD\u7684\u6E56\u6C34\u3002\u6CA1\u60F3\u5230\u7236\u4EB2\u8BF4\u7684\u5F2F\u5C9B\u5C31\u662F\u661F\u6708\u6E56\u3002</p><p>\u5927\u6982\u7236\u4EB2\u89C1\u81EA\u5DF1\u662F\u4E2A\u5973\u5B69\uFF0C\u590D\u56FD\u65E0\u671B\uFF0C\u5BF9\u5B9D\u85CF\u4E5F\u4E0D\u653E\u5728\u5FC3\u4E0A\uFF0C\u8FDE\u5F2F\u5C9B\u90FD\u6CA1\u6765\u8FC7\u3002\u53EF\u7956\u4E0A\u7684\u5B9D\u85CF\u4E3A\u4EC0\u4E48\u4F1A\u85CF\u5728\u8FD9\u91CC\uFF1F\u661F\u6708\u6E56\u4E3A\u4F55\u8FD8\u8981\u8FFD\u67E5\u5B9D\u85CF\u7684\u4E0B\u843D\uFF1F\u6B64\u4E2D\u6709\u8BB8\u591A\u96BE\u660E\u4E4B\u4E8B\uFF0C\u4F46\u77E5\u60C5\u8005\u5747\u5DF2\u4E0D\u5728\u4E16\u95F4\uFF0C\u8FD9\u4E2A\u79D8\u4E5F\u8BB8\u6C38\u8FDC\u90FD\u89E3\u4E0D\u5F00\u4E86\u3002</p><p>\u5C9B\u4E0A\u7684\u5C71\u5CF0\u5728\u6668\u96FE\u95F4\u6726\u6726\u80E7\u80E7\u65F6\u9690\u65F6\u73B0\u3002\u7D2B\u73AB\u6697\u81EA\u76D8\u7B97\uFF1A\u5F2F\u5C9B\u8DDD\u6E56\u8FB9\u8DB3\u6709\u5341\u91CC\u8FDC\u8FD1\uFF0C\u4F9D\u5979\u7684\u6C34\u6027\uFF0C\u518D\u52A0\u4E0A\u8F7B\u529F\uFF0C\u5E94\u8BE5\u80FD\u6E38\u8FC7\u53BB\u3002\u4F46\u90A3\u4E2A\u6B7B\u5973\u4EBA\u8BF4\u5C9B\u4E0A\u6212\u5907\u68EE\u4E25\uFF0C\u5149\u5929\u5316\u65E5\u4E0B\uFF0C\u6E05\u6F88\u7684\u6E56\u6C34\u65E0\u6CD5\u9690\u8EAB\uFF0C\u53EA\u597D\u7B49\u591C\u91CC\u518D\u8BBE\u6CD5\u4E0A\u5C9B\u3002</p><p>\u7D2B\u73AB\u8BA1\u8F83\u5DF2\u5B9A\uFF0C\u7EA2\u886B\u4E00\u95EA\u6CA1\u5165\u5BC6\u6797\u3002</p><p>\u6E05\u9732\u8EAB\u65E0\u5BF8\u7F15\uFF0C\u8F6F\u8F6F\u8EBA\u5728\u6811\u4E0B\u3002\u767D\u7699\u7684\u5C0F\u817F\u548C\u811A\u5E95\u904D\u5E03\u4F24\u75D5\uFF0C\u8FD9\u662F\u6628\u591C\u5728\u5C71\u4E2D\u8DCB\u6D89\u7559\u4E0B\u7684\u3002</p><p>\u6155\u5BB9\u7D2B\u73AB\u6253\u91CF\u7740\u8FD9\u4E2A\u72E0\u6BD2\u7684\u5973\u4EBA\uFF0C\u5FC3\u5E95\u6068\u610F\u6E10\u6E10\u6ECB\u957F\u3002\u5979\u7EB5\u8EAB\u4ECE\u6811\u4E0A\u780D\u4E0B\u4E00\u6839\u6811\u679D\uFF0C\u5C06\u65AD\u53E3\u524A\u5C16\uFF0C\u7136\u540E\u62AC\u811A\u5C06\u6E05\u9732\u7FFB\u8F6C\u8FC7\u6765\u3002</p><p>\u6E05\u9732\u4FCF\u8138\u4F0F\u5728\u8349\u77F3\u95F4\uFF0C\u4E24\u773C\u5706\u6E9C\u6E9C\u770B\u7740\u7D2B\u73AB\u7684\u7EA4\u8DB3\u3002\u5FFD\u7136\u81C0\u95F4\u4E00\u75DB\uFF0C\u4E00\u4E2A\u575A\u786C\u7684\u7269\u4F53\u91CD\u91CD\u9876\u5728\u83CA\u809B\u4E0A\uFF0C\u5979\u5413\u5F97\u5927\u53EB\u8D77\u6765\uFF0C\u300C\u4ED9\u5B50\u9976\u547D\u554A\u2026\u2026\u300D</p><p>\u9752\u7FE0\u7684\u6811\u679D\u63D2\u5728\u96EA\u767D\u7684\u81C0\u8089\u95F4\uFF0C\u5728\u98CE\u4E2D\u5FAE\u5FAE\u6447\u6643\u3002\u7D2B\u73AB\u867D\u7136\u607C\u6068\u5979\u6298\u8FB1\u98CE\u5E08\u59D0\u3001\u6797\u5E08\u59D0\uFF0C\u4F46\u8FD9\u6837\u5BF9\u5F85\u4E00\u4E2A\u6BEB\u65E0\u53CD\u6297\u80FD\u529B\u7684\u5973\u4EBA\uFF0C\u5979\u4E0B\u4E0D\u53BB\u624B\u3002</p><p>\u80A5\u5AE9\u7684\u81C0\u8089\u4E0D\u4F4F\u98A4\u6296\uFF0C\u7A81\u7136\u4E00\u80A1\u6DE1\u9EC4\u7684\u6DB2\u4F53\u4ECE\u82B1\u74E3\u95F4\u55B7\u5C04\u51FA\u6765\uFF0C\u7565\u505C\u7247\u523B\uFF0C\u53C8\u6E85\u51FA\u51E0\u6EF4\u3002</p><p>\u6E05\u9732\u4EB2\u624B\u8650\u6740\u8FC7\u4E0D\u5C11\u5973\u4EBA\uFF0C\u4F46\u8FD8\u662F\u7B2C\u4E00\u6B21\u88AB\u4EBA\u5236\u4F4F\u3002\u6B64\u65F6\u60F3\u8D77\u81EA\u5DF1\u7684\u624B\u6BB5\uFF0C\u7ADF\u5413\u5F97\u5931\u7981\u4E86\u3002</p><p>\u7D2B\u73AB\u7389\u624B\u4E00\u677E\uFF0C\u6811\u679D\u6389\u5728\u5730\u4E0A\u3002</p><p>\u6E05\u9732\u677E\u4E86\u53E3\u6C14\uFF0C\u53C8\u88AB\u8F7B\u8F7B\u7FFB\u8F6C\u8FC7\u6765\uFF0C\u63A5\u7740\u4E00\u67C4\u5F2F\u5251\u62B5\u5728\u80F8\u53E3\u3002\u7D2B\u73AB\u80CC\u8FC7\u8138\uFF0C\u624B\u4E0A\u4E00\u63A8\uFF0C\u5F2F\u5251\u523A\u5165\u6E05\u9732\u5FC3\u53E3\u3002</p>',50);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
