import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_23" tabindex="-1"><a class="header-anchor" href="#_23" aria-hidden="true">#</a> 23</h1><p>\u6C34\u9762\u8361\u8D77\u4E00\u5708\u6D9F\u6F2A\uFF0C\u4E00\u4E2A\u8EAB\u6750\u73B2\u73D1\u7684\u5973\u5B69\u4ECE\u5C0F\u5C9B\u5C16\u89D2\u6500\u4E0A\uFF0C\u8DB3\u4E0D\u70B9\u5730\u63A0\u8FC7\u6C99\u6EE9\u3002</p><p>\u5C9B\u4E0A\u9053\u8DEF\u7EB5\u6A2A\uFF0C\u904D\u5E03\u5DE8\u6811\u3002\u9ED1\u9EDD\u9EDD\u7684\u679D\u5F71\u95F4\uFF0C\u98DE\u6A90\u6311\u89D2\u4E00\u6D3E\u72F0\u72DE\u3002</p><p>\u6155\u5BB9\u7D2B\u73AB\u6BD5\u7ADF\u53EA\u662F\u4E2A\u5341\u516D\u5C81\u5C0F\u59D1\u5A18\uFF0C\u4E0D\u7531\u5FC3\u4E2D\u5FD0\u5FD1\u3002\u5979\u4F0F\u5728\u679D\u95F4\u5598\u4E86\u53E3\u6C14\uFF0C\u7A33\u4F4F\u5FC3\u795E\u3002\u7136\u540E\u6309\u7740\u6E05\u9732\u8BF4\u7684\u65B9\u4F4D\uFF0C\u907F\u5F00\u6697\u54E8\u671D\u795E\u6BBF\u63A0\u53BB\u3002</p><p>\u6BBF\u524D\u706F\u706B\u901A\u660E\uFF0C\u6570\u5341\u4EBA\u56F4\u6210\u4E00\u5708\uFF0C\u4E0D\u65F6\u53D1\u51FA\u9707\u8033\u7684\u8F70\u7B11\u3002\u7D2B\u73AB\u6084\u6084\u62E8\u5F00\u679D\u53F6\u770B\u4E86\u4E00\u773C\uFF0C\u987F\u65F6\u80F8\u53E3\u62BD\u75DB\u3002</p><p>\u4E00\u4E2A\u8D64\u88F8\u7684\u5C11\u5987\u8DEA\u4F0F\u5728\u9ED1\u8272\u5927\u7406\u77F3\u94FA\u5C31\u7684\u6BBF\u9636\u4E0A\uFF0C\u706F\u706B\u4E0B\u767D\u5AE9\u7684\u8EAB\u4F53\u5206\u5916\u593A\u76EE\u3002\u5979\u4E24\u8098\u5957\u7740\u7AF9\u7B52\uFF0C\u624B\u81C2\u65E0\u6CD5\u5F2F\u66F2\uFF0C\u53EA\u80FD\u5E73\u4F38\u5728\u8EAB\u524D\u3002\u8138\u90E8\u548C\u80F8\u4E73\u8D34\u7740\u5730\u9762\uFF0C\u5F2F\u66F2\u7684\u4E24\u819D\u88AB\u4E00\u6839\u6A2A\u6728\u6491\u5F00\uFF0C\u4E24\u817F\u659C\u653E\u3002\u4ECE\u7D2B\u73AB\u8FD9\u91CC\uFF0C\u6B63\u80FD\u770B\u5230\u5979\u9AD8\u9AD8\u7FD8\u8D77\u7684\u96EA\u81C0\uFF0C\u5973\u6027\u6700\u9690\u5BC6\u7684\u90E8\u4F4D\u5C3D\u6570\u66B4\u9732\u5728\u5916\u3002</p><p>\u7C89\u5AE9\u7684\u81C0\u90E8\u96EA\u7403\u822C\u6D51\u5706\uFF0C\u6B63\u4E2D\u4E00\u9053\u51F9\u4E0B\u8089\u7F1D\uFF0C\u4E0A\u4E0B\u5E76\u5217\u7740\u4E24\u4E2A\u8089\u7A74\u3002\u7C89\u7EA2\u8272\u7684\u83CA\u809B\u8840\u8FF9\u6591\u6591\uFF0C\u83CA\u857E\u7A81\u8D77\uFF0C\u9690\u9690\u80FD\u770B\u5230\u9C9C\u7EA2\u7684\u80A0\u58C1\u3002\u67D4\u7F8E\u7684\u82B1\u74E3\u80BF\u80C0\u7740\u7FFB\u5377\u5F00\u6765\uFF0C\u8089\u7A74\u91CC\u76F4\u76F4\u63D2\u7740\u6839\u767D\u8272\u7684\u6839\u5B50\u3002\u4E00\u4E2A\u7537\u4EBA\u7AD9\u5728\u5979\u8EAB\u540E\uFF0C\u6B63\u63E1\u7740\u68CD\u5B50\u5728\u8089\u7A74\u5185\u72E0\u72E0\u6345\u5F04\u3002</p><p>\u68CD\u5B50\u63D2\u5728\u4F53\u5185\u7684\u90E8\u5206\u4F3C\u4E4E\u5F88\u5927\uFF0C\u62D4\u51FA\u65F6\u82B1\u74E3\u5411\u56DB\u5904\u7EFD\u5F00\uFF0C\u6CDB\u8D77\u4E00\u7247\u8273\u7EA2\uFF0C\u90A3\u7537\u4EBA\u628A\u68CD\u5B50\u5F80\u4E0B\u4E00\u6309\uFF0C\u63E1\u8D77\u7C97\u9ED1\u7684\u8089\u68D2\uFF0C\u731B\u7136\u63D2\u8FDB\u540E\u5EAD\uFF0C\u6DEB\u7B11\u9053\uFF1A\u300C\u5927\u7237\u8DDF\u4F60\u8001\u516C\u4E00\u5757\u513F\u64CD\u4F60\uFF0C\u723D\u4E0D\u723D\uFF1F\u300D</p><p>\u5C11\u5987\u4E00\u58F0\u4E0D\u542D\uFF0C\u4E24\u624B\u7D27\u7D27\u6525\u6210\u4E00\u56E2\uFF0C\u62AB\u6563\u7684\u79C0\u53D1\u5728\u77F3\u4E0A\u7EC6\u6CE2\u822C\u5FAE\u5FAE\u6643\u52A8\u3002</p><p>\u6155\u5BB9\u7D2B\u73AB\u95ED\u4E0A\u773C\u775B\uFF0C\u62FC\u547D\u601D\u7D22\u8FD9\u5973\u4EBA\u662F\u8C01\uFF1F\u767D\u6C0F\u59D0\u59B9\uFF1F\u7EAA\u5E08\u59D0\uFF1F\u6797\u5E08\u59D0\uFF1F\u8FD8\u662F\u5176\u4ED6\u4EBA\uFF1F\u2026\u2026\u8001\u516C\uFF1F</p><p>\u5FFD\u7136\u4EBA\u7FA4\u91CC\u6709\u4EBA\u53EB\u9053\uFF1A\u300C\u8001\u4E03\uFF0C\u4F60\u90FD\u5E72\u4E09\u56DE\u4E86\uFF0C\u6B47\u7740\u5427\u3002\u7528\u8FD9\u4E2A\u6765\uFF01\u300D</p><p>\u767D\u5F71\u4E00\u95EA\uFF0C\u8001\u4E03\u4F38\u624B\u63A5\u8FC7\u3002\u5F85\u770B\u6E05\u90A3\u4E2A\u7269\u4F53\uFF0C\u7D2B\u73AB\u773C\u5149\u970D\u7136\u4E00\u8DF3\u3002\u68CD\u5B50\u957F\u6709\u5C3A\u534A\uFF0C\u4E24\u7AEF\u5404\u6709\u4E00\u4E2A\u7855\u5927\u7684\u7A81\u8D77\uFF0C\u5F62\u72B6\u5947\u5F02\uFF0C\u4E0D\u89C4\u5219\u7684\u68CD\u8EAB\u6563\u53D1\u7740\u6DE1\u6DE1\u7684\u767D\u8272\u8424\u5149\u2014\u2014\u7ADF\u662F\u4EBA\u7684\u817F\u9AA8\uFF01</p><p>\u8001\u4E03\u54C8\u54C8\u4E00\u7B11\uFF0C\u628A\u7C97\u5927\u7684\u9AA8\u8282\u5BF9\u51C6\u5C11\u5987\u7684\u809B\u95E8\u7528\u529B\u6345\u5165\u3002\u7EC6\u5BC6\u7684\u83CA\u7EB9\u4E4D\u7136\u7834\u88C2\uFF0C\u9C9C\u8840\u987A\u7740\u767D\u9AA8\u873F\u8712\u800C\u4E0B\uFF0C\u540C\u65F6\u67D3\u7EA2\u4E86\u9634\u4E2D\u7684\u53E6\u4E00\u6839\u817F\u9AA8\u3002\u6447\u66F3\u7684\u706F\u706B\u91CC\uFF0C\u4E24\u6839\u767D\u68EE\u68EE\u7684\u4EBA\u9AA8\u63D2\u5728\u7EDD\u7F8E\u7684\u7389\u81C0\u4E2D\uFF0C\u5E26\u7740\u7F15\u7F15\u9C9C\u8840\u659C\u659C\u7FD8\u8D77\uFF0C\u5996\u8273\u800C\u53C8\u51C4\u7F8E\u3002</p><p>\u4F17\u4EBA\u4E00\u9635\u602A\u7B11\uFF0C\u300C\u4F60\u8001\u516C\u771F\u5389\u5BB3\uFF0C\u4FE9\u6D1E\u4E00\u5757\u513F\u64CD\uFF01\u300D</p><p>\u300C\u8001\u516C\u7684\u817F\u8DDF\u9E21\u5DF4\u54EA\u4E2A\u7C97\uFF1F\u300D</p><p>\u300C\u53EF\u60DC\u4F60\u8001\u516C\u8BA9\u72D7\u5543\u5F97\u53EA\u5269\u8FD9\u4E24\u6839\u9AA8\u5934\uFF0C\u518D\u591A\u4E00\u6839\u8FDE\u5634\u91CC\u8214\u7684\u4E5F\u6709\u4E86\u2026\u2026\u300D</p><p>\u6155\u5BB9\u7D2B\u73AB\u5FC3\u5934\u6068\u6781\uFF0C\u8FD9\u5E2E\u65E0\u803B\u4E4B\u5F92\uFF0C\u6740\u4E86\u4EBA\u5BB6\u4E08\u592B\u4E0D\u4EC5\u629B\u5C38\u5582\u72D7\uFF0C\u7ADF\u7136\u8FD8\u8981\u7528\u5C38\u9AA8\u6765\u6298\u78E8\u8FD9\u4E2A\u53EF\u601C\u7684\u5973\u4EBA\u3002</p><p>\u5FFD\u7136\u6709\u4EBA\u7B11\u9053\uFF1A\u300C\u5BD2\u6708\u5200\u7684\u5C41\u773C\u513F\u2026\u2026\u300D</p><p>\u6155\u5BB9\u7D2B\u73AB\u8033\u4E2D\u8F70\u7136\u4E00\u54CD\uFF0C\u51E0\u4E4E\u4ECE\u6811\u4E0A\u8DCC\u4E86\u4E0B\u6765\u3002\u5979\u6B7B\u6B7B\u6293\u4F4F\u6811\u679D\uFF0C\u5446\u5446\u770B\u7740\u9636\u4E0A\u90A3\u4E2A\u5973\u4EBA\u3002</p><p>\u82F1\u59FF\u633A\u62D4\u7684\u54E5\u54E5\u6B64\u65F6\u53EA\u5269\u4E0B\u4E24\u6839\u817F\u9AA8\uFF0C\u800C\u8FD9\u4E24\u6839\u7C97\u5927\u7684\u817F\u9AA8\u6B63\u6DF1\u6DF1\u63D2\u5728\u5AC2\u5AC2\u4F53\u5185\u2026\u2026</p><p>\u575A\u786C\u7684\u767D\u9AA8\u4E0E\u80A5\u7F8E\u7684\u96EA\u81C0\u7D27\u7D27\u7ED3\u5408\u5728\u4E00\u8D77\uFF0C\u83CA\u809B\u7EFD\u88C2\u7684\u5AE9\u8089\u95F4\uFF0C\u6CC9\u6C34\u822C\u5192\u51FA\u80A1\u80A1\u9C9C\u8840\u3002\u8FD9\u662F\u5AC2\u5AC2\u3001\u662F\u4E8C\u5E08\u59D0\u6797\u9999\u8FDC\u7684\u9C9C\u8840\u2026\u2026</p><p>\u6155\u5BB9\u7D2B\u73AB\u62FC\u547D\u6291\u5236\u4F4F\u72C2\u558A\u7684\u51B2\u52A8\uFF0C\u7389\u8138\u8D34\u5728\u7C97\u7CD9\u6811\u76AE\u4E0A\uFF0C\u6CEA\u6C34\u6A21\u7CCA\u4E86\u53CC\u773C\u3002</p><p>\u300C\u56DB\u5341\u4E94\u3001\u56DB\u5341\u516D\u3001\u56DB\u5341\u4E03\uFF0C\u4E00\u5171\u56DB\u5341\u4E03\u4E2A\u3002\u300D\u6155\u5BB9\u7D2B\u73AB\u7AED\u529B\u4E0D\u53BB\u770B\u5AC2\u5AC2\u7684\u60E8\u72B6\uFF0C\u9ED8\u9ED8\u8BA1\u7B97\u4EBA\u6570\u3002\u7EB5\u4F7F\u53EA\u6709\u56DB\u5206\u4E4B\u4E00\uFF0C\u5F3A\u884C\u6551\u4EBA\u4E5F\u4E0D\u53EF\u80FD\u3002\u8FD9\u91CC\u4E0E\u9B54\u6BBF\u8FD1\u5728\u54AB\u5C3A\uFF0C\u82E5\u8D38\u7136\u73B0\u8EAB\uFF0C\u53EA\u4F1A\u6253\u8349\u60CA\u86C7\u3002\u60F3\u7740\uFF0C\u5979\u62AC\u773C\u671B\u5411\u795E\u6BBF\u3002</p><p>\u9AD8\u5927\u7684\u5BAB\u5B87\u6BBF\u95E8\u7D27\u95ED\uFF0C\u50CF\u53EA\u9EDD\u9ED1\u7684\u5DE8\u517D\u6C89\u9ED8\u7740\u3002\u6CA1\u6709\u4E00\u4E2A\u4EBA\u51FA\u6765\u7406\u4F1A\u6BBF\u524D\u7684\u55A7\u95F9\u3002</p><p>\u6298\u78E8\u4E86\u4E00\u4E2A\u65F6\u8FB0\uFF0C\u5341\u51E0\u540D\u8EAB\u7740\u9ED1\u8863\u7684\u6C49\u5B50\u79BB\u5F00\u4EBA\u7FA4\uFF0C\u563B\u7B11\u7740\u671D\u5317\u8D70\u53BB\uFF0C\u90A3\u4E2A\u8001\u4E03\u6B63\u5728\u5176\u4E2D\u3002</p><p>\u4ED6\u5927\u6982\u662F\u5E72\u7684\u592A\u72E0\uFF0C\u811A\u6B65\u7565\u6709\u865A\u6D6E\uFF0C\u843D\u5728\u4E86\u540E\u9762\u3002\u60F3\u8D77\u6797\u9999\u8FDC\u5A07\u7F8E\u7684\u8089\u4F53\uFF0C\u4E0D\u7531\u5F97\u610F\u7684\u54FC\u8D77\u5C0F\u66F2\u6765\u3002\u521A\u54FC\u4E86\u4E24\u53E5\uFF0C\u7A81\u7136\u5589\u5934\u4E00\u7D27\uFF0C\u4E24\u811A\u62D4\u5730\u800C\u8D77\u3002</p><p>\u6155\u5BB9\u7D2B\u73AB\u7EF7\u7740\u8138\u5C01\u4E86\u4ED6\u7684\u7A74\u9053\uFF0C\u628A\u4ED6\u62CE\u5230\u6811\u5DC5\uFF0C\u7136\u540E\u62D4\u51FA\u7247\u7389\uFF0C\u6162\u6162\u5207\u4E0B\u4ED6\u7684\u624B\u81C2\u3002\u8001\u4E03\u4E24\u773C\u7A81\u8D77\uFF0C\u5589\u4E2D\u5443\u5443\u5FAE\u54CD\u3002\u7D2B\u73AB\u5C0F\u5FC3\u5730\u628A\u65AD\u81C2\u5361\u5728\u6811\u6748\u95F4\uFF0C\u63A5\u7740\u4ED4\u7EC6\u5207\u53BB\u4ED6\u7684\u56DB\u80A2\u3002\u98DE\u6E85\u7684\u9C9C\u8840\u6CBE\u6EE1\u679D\u53F6\uFF0C\u7D2B\u73AB\u79C0\u7738\u901A\u7EA2\uFF0C\u52A8\u4F5C\u5374\u6CA1\u6709\u4E00\u4E1D\u72B9\u8C6B\u3002</p><p>\u65E9\u77E5\u5982\u6B64\uFF0C\u600E\u4E48\u4E5F\u4E0D\u80FD\u8BA9\u6E05\u9732\u6B7B\u90A3\u4E48\u75DB\u5FEB\uFF01</p><p>\u4E00\u523B\u949F\u540E\uFF0C\u6155\u5BB9\u7D2B\u73AB\u8F7B\u70DF\u822C\u843D\u56DE\u6BBF\u524D\u7684\u6811\u679D\u95F4\u3002\u8EAB\u4E0A\u9ED1\u8272\u6C34\u9760\u4E0D\u4F4F\u6EF4\u8840\u3002</p><p>\u5DF2\u7ECF\u8FC7\u4E86\u5B50\u65F6\uFF0C\u56F4\u89C2\u7684\u4EBA\u6563\u4E86\u4E00\u534A\uFF0C\u8FD8\u5269\u5341\u51E0\u4EBA\u7EE7\u7EED\u6298\u78E8\u6797\u9999\u8FDC\u3002</p><p>\u817F\u9AA8\u88AB\u6254\u5230\u4E00\u8FB9\uFF0C\u4E00\u4E2A\u9EC4\u8863\u6C49\u5B50\u6B63\u88F8\u7740\u4E0B\u8EAB\u5728\u6797\u9999\u8FDC\u81C0\u540E\u4F7F\u52B2\u633A\u52A8\u3002\u4E0D\u591A\u65F6\u4ED6\u7A81\u7136\u62E7\u8D77\u5BD2\u6708\u5200\u7684\u957F\u53D1\uFF0C\u5C06\u5C11\u5987\u7684\u4FCF\u8138\u62AC\u8D77\u6765\uFF0C\u63A5\u7740\u628A\u7CBE\u6DB2\u55B7\u5230\u5979\u5634\u91CC\u7684\u7AF9\u7B52\u4E2D\u3002</p><p>\u6797\u9999\u8FDC\u628A\u7AF9\u7B52\u54AC\u5730\u683C\u683C\u4F5C\u54CD\uFF0C\u820C\u5C16\u4F38\u7F29\u7740\u62FC\u547D\u5410\u51FA\u767D\u8272\u7684\u7CBE\u6DB2\u3002\u9EC4\u8863\u4EBA\u4ECE\u5979\u8138\u4FA7\u62E3\u8D77\u4E00\u4E2A\u767D\u8272\u7684\u534A\u5706\u5F62\uFF0C\u63A5\u4F4F\u7CBE\u6DB2\uFF0C\u563F\u563F\u7B11\u9053\uFF1A\u300C\u5BAB\u4E3B\u5429\u5490\uFF0C\u6797\u5A4A\u5B50\u4ECA\u540E\u5403\u4EC0\u4E48\u90FD\u5F97\u7528\u8FD9\u4E2A\u3002\u5567\u5567\uFF0C\u771F\u662F\u6069\u7231\u592B\u59BB\uFF0C\u751F\u6B7B\u4E0D\u79BB\u2026\u2026\u300D</p><p>\u6155\u5BB9\u7D2B\u73AB\u7A33\u4F4F\u5FC3\u795E\uFF0C\u503E\u542C\u9636\u4E0A\u7684\u52A8\u9759\u3002\u4E0D\u77E5\u8FC7\u4E86\u591A\u4E45\uFF0C\u811A\u6B65\u58F0\u6E10\u6E10\u6563\u5F00\uFF0C\u5E2E\u4F17\u563B\u7B11\u7740\u53BB\u4E86\u3002\u5979\u7741\u5F00\u773C\uFF0C\u53D1\u73B0\u573A\u4E2D\u53EA\u5269\u4E86\u4E24\u540D\u7D2B\u8863\u4EBA\u3002</p><p>\u7D2B\u73AB\u6DF1\u6DF1\u5438\u4E86\u53E3\u6C14\uFF0C\u633D\u8D77\u5C0F\u5F29\uFF0C\u673A\u62EC\u4E00\u677E\uFF0C\u7ACB\u5373\u98DE\u8EAB\u800C\u4E0B\u3002\u4E24\u679D\u5C0F\u5F29\u7A7F\u8FDB\u7D2B\u8863\u4EBA\u80F8\u3001\u5589\u7684\u540C\u65F6\uFF0C\u7247\u7389\u4E5F\u5207\u65AD\u4E86\u53E6\u4E00\u4EBA\u7684\u8116\u9888\u3002\u7D2B\u73AB\u63A5\u4F4F\u5934\u9885\uFF0C\u7528\u811A\u6311\u4F4F\u4E24\u5177\u5C38\u4F53\u7F13\u7F13\u653E\u4E0B\uFF0C\u7136\u540E\u8FC5\u901F\u62B1\u8D77\u5AC2\u5AC2\u817E\u8EAB\u800C\u8D77\u3002\u5730\u4E0A\u6155\u5BB9\u80DC\u7684\u5934\u76D6\u9AA8\u5FAE\u5FAE\u6447\u6643\uFF0C\u6EE1\u6EA2\u7684\u7CBE\u6DB2\u56DB\u4E0B\u629B\u6D12\uFF0C\u706F\u706B\u4E2D\u6CDB\u51FA\u51C4\u51B7\u7684\u767D\u5149\u3002</p><p>\u7D2B\u73AB\u4E00\u8FB9\u5728\u679D\u95F4\u5BFB\u627E\u843D\u811A\u5904\uFF0C\u4E00\u8FB9\u60F6\u6025\u5730\u770B\u7740\u5E08\u59D0\u3002\u6797\u9999\u8FDC\u776B\u6BDB\u4E00\u52A8\uFF0C\u4F3C\u4E4E\u60F3\u770B\u770B\u662F\u8C01\u6551\u4E86\u5979\u3002\u4F46\u7741\u5F00\u773C\uFF0C\u5374\u89C1\u773C\u7403\u6B63\u4E2D\u5404\u6709\u4E00\u4E2A\u5C0F\u5B54\uFF0C\u4EFF\u4F5B\u8FD8\u7559\u7740\u9ECF\u7A20\u7684\u6DB2\u4F53\u3002</p><p>\u7D2B\u73AB\u6CEA\u5982\u96E8\u4E0B\uFF0C\u98A4\u58F0\u9053\uFF1A\u300C\u5E08\u59D0\uFF0C\u662F\u6211\uFF0C\u7D2B\u73AB\u2026\u2026\u300D</p><p>\u6797\u9999\u8FDC\u60E8\u767D\u7684\u4FCF\u8138\u987F\u65F6\u4E00\u4EAE\u3002</p><p>\u817F\u95F4\u7684\u6A2A\u6728\u5E94\u624B\u800C\u65AD\uFF0C\u5706\u6DA6\u7684\u5927\u817F\u5408\u5728\u4E00\u8D77\uFF0C\u53D1\u51FA\u4E00\u58F0\u6E05\u54CD\u3002\u7D2B\u73AB\u6258\u4F4F\u5AC2\u5AC2\u6EE1\u662F\u7CBE\u6DB2\u7684\u5706\u81C0\uFF0C\u4F38\u624B\u63E1\u4F4F\u6DF1\u5165\u4F53\u5185\u7684\u817F\u9AA8\uFF0C\u6B63\u5F85\u62D4\u51FA\uFF0C\u7A81\u7136\u8EAB\u540E\u4F20\u6765\u4E00\u58F0\u5C16\u9510\u7684\u5389\u5578\u3002</p><p>\u8FDC\u8FD1\u4EAE\u8D77\u706F\u706B\uFF0C\u661F\u6708\u6E56\u5E2E\u4F17\u7EB7\u7EB7\u6D8C\u51FA\uFF0C\u628A\u5B88\u5404\u5904\u8DEF\u53E3\u3002\u6155\u5BB9\u7D2B\u73AB\u6CA1\u60F3\u5230\u5BF9\u65B9\u53CD\u5E94\u5982\u6B64\u4E4B\u5FEB\uFF0C\u5F53\u4E0B\u7389\u624B\u4E00\u7D27\uFF0C\u62D4\u51FA\u817F\u9AA8\u3002\u60F3\u5230\u8FD9\u662F\u54E5\u54E5\u7684\u9057\u9AA8\uFF0C\u5979\u72B9\u8C6B\u4E00\u4E0B\uFF0C\u8D81\u94BB\u8FC7\u4E00\u68F5\u5927\u6811\u7684\u65F6\u5019\u5C06\u817F\u9AA8\u653E\u5728\u6811\u6D1E\u5185\u3002</p><p>\u6797\u9999\u8FDC\u542C\u51FA\u60C5\u51B5\u4E0D\u59A5\uFF0C\u5589\u4E2D\u53D1\u51FA\u65AD\u65AD\u7EED\u7EED\u7684\u58F0\u97F3\uFF0C\u60F3\u8BA9\u7D2B\u73AB\u628A\u81EA\u5DF1\u653E\u4E0B\uFF0C\u72EC\u81EA\u9003\u751F\u3002</p><p>\u6155\u5BB9\u7D2B\u73AB\u633D\u7D27\u5979\u7684\u8170\u80A2\uFF0C\u6D41\u661F\u822C\u6295\u5411\u6700\u8FD1\u7684\u6E56\u5CB8\u3002</p><p>\u6B64\u65F6\u5DF2\u6709\u4EBA\u53D1\u73B0\u4E86\u5979\u4EEC\u7684\u8E2A\u8FF9\uFF0C\u6570\u767E\u4EBA\u9F50\u6D8C\u800C\u81F3\uFF0C\u5305\u56F4\u5708\u8D8A\u6765\u8D8A\u5C0F\u3002\u6E56\u8FB9\u7ACB\u7740\u4E00\u4E2A\u5F6A\u5F62\u5927\u6C49\uFF0C\u7D2B\u73AB\u8BA4\u51FA\u662F\u91D1\u5F00\u7532\uFF0C\u7ACB\u523B\u8F6C\u8EAB\u671D\u53E6\u4E00\u4FA7\u9003\u53BB\u3002</p><p>\u6155\u5BB9\u7D2B\u73AB\u8F7B\u529F\u5C3D\u5C55\uFF0C\u98DE\u9E1F\u822C\u7A7F\u6797\u8FC7\u6811\uFF0C\u770B\u5230\u654C\u4EBA\u4FBF\u5148\u884C\u907F\u5F00\uFF0C\u66F2\u66F2\u6298\u6298\u6A2A\u7A7F\u8FC7\u661F\u6708\u5C9B\u4E2D\u90E8\u3002</p><p>\u8EAB\u540E\u52B2\u98CE\u54CD\u8D77\uFF0C\u6765\u4EBA\u8D8A\u8FFD\u8D8A\u8FD1\uFF0C\u4E00\u542C\u4FBF\u77E5\u5FC5\u662F\u957F\u8001\u7EA7\u9AD8\u624B\u3002\u6155\u5BB9\u7D2B\u73AB\u94F6\u7259\u4E00\u54AC\uFF0C\u628A\u5AC2\u5AC2\u653E\u5728\u679D\u4E0A\uFF0C\u8F6C\u8EAB\u671D\u6765\u4EBA\u6251\u53BB\u3002\u6C34\u67D4\u4ED9\u8EAB\u5F62\u5FAE\u6643\uFF0C\u6D41\u6C34\u822C\u907F\u5F00\u7247\u7389\u7684\u950B\u8292\uFF0C\u540C\u65F6\u8896\u4E2D\u98DE\u51FA\u4E24\u6839\u8F6F\u97AD\uFF0C\u7F20\u5411\u7D2B\u73AB\u7684\u4E00\u53EA\u7693\u8155\u3002</p><p>\u7D2B\u73AB\u5728\u7A7A\u4E2D\u7EA4\u8170\u4E00\u626D\uFF0C\u77ED\u5200\u780D\u5728\u8F6F\u97AD\u4E0A\uFF0C\u8F6F\u97AD\u5E94\u624B\u800C\u65AD\u3002\u6C34\u67D4\u4ED9\u5C48\u6307\u5F39\u5F00\u52B2\u5F29\uFF0C\u53E6\u4E00\u6839\u8F6F\u97AD\u7075\u86C7\u822C\u6602\u8D77\u5934\u6765\uFF0C\u76F4\u70B9\u7D2B\u73AB\u80F8\u53E3\u3002</p><p>\u53EA\u4EA4\u4E00\u62DB\uFF0C\u7D2B\u73AB\u4FBF\u77E5\u5979\u529F\u529B\u5C3D\u590D\uFF0C\u96BE\u4EE5\u62B5\u6321\u3002\u4F53\u5185\u771F\u6C14\u6D41\u8F6C\uFF0C\u67F3\u7D6E\u822C\u8FCE\u98CE\u800C\u8D77\uFF0C\u843D\u5728\u6797\u9999\u8FDC\u8EAB\u65C1\uFF0C\u53CD\u624B\u4E00\u5200\uFF0C\u76F4\u523A\u5E08\u59D0\u80F8\u53E3\u3002\u5200\u98CE\u53CA\u4F53\uFF0C\u5931\u660E\u7684\u5C11\u5987\u4F3C\u4E4E\u77E5\u9053\u5979\u8981\u505A\u4EC0\u4E48\uFF0C\u8270\u96BE\u5730\u9732\u51FA\u4E00\u4E2A\u7B11\u5BB9\u3002</p><p>\u7D2B\u73AB\u5FC3\u5934\u9178\u75DB\uFF0C\u624B\u4E00\u7FFB\uFF0C\u7247\u7389\u7684\u5200\u67C4\u78D5\u5728\u6811\u5E72\u4E0A\uFF0C\u501F\u529B\u8DC3\u8D77\uFF0C\u4ECE\u4E00\u7FA4\u6C34\u5802\u5E2E\u4F17\u5934\u9876\u63A0\u8FC7\u3002</p><p>\u65E0\u8BBA\u5982\u4F55\uFF0C\u8FD8\u6709\u89E3\u6551\u5AC2\u5AC2\u7684\u5E0C\u671B\u3002</p><p>\u6C34\u67D4\u4ED9\u8FD8\u662F\u7B2C\u4E00\u6B21\u89C1\u8BC6\u6155\u5BB9\u7D2B\u73AB\u8D85\u4EBA\u7684\u8F7B\u529F\uFF0C\u4E0D\u7981\u76EE\u9732\u8BB6\u8272\u3002\u5979\u6CA1\u6709\u518D\u8FFD\uFF0C\u4FEF\u8EAB\u62CE\u8D77\u6797\u9999\u8FDC\uFF0C\u98D8\u7136\u79BB\u53BB\u3002</p><p>\u6155\u5BB9\u7D2B\u73AB\u5728\u5C9B\u4E0A\u4E1C\u8EB2\u897F\u85CF\uFF0C\u59CB\u7EC8\u65E0\u6CD5\u9760\u8FD1\u6E56\u5CB8\u3002\u6B64\u65F6\u52BF\u6210\u9A91\u864E\uFF0C\u7EB5\u7136\u9003\u5230\u6E56\u4E2D\u4EE5\u5979\u7684\u6C34\u6027\u4E5F\u96BE\u4EE5\u8131\u8EAB\u3002\u7D2B\u73AB\u5FC3\u4E00\u6A2A\uFF0C\u7EB5\u8EAB\u671D\u6000\u6708\u5CF0\u5954\u53BB\uFF0C\u51C6\u5907\u4ECE\u5CF0\u540E\u7684\u5CED\u58C1\u4F5C\u51FA\u8DC3\u5165\u6E56\u4E2D\u7684\u5047\u8C61\uFF0C\u8BD5\u8BD5\u80FD\u5426\u5728\u5D16\u95F4\u627E\u5230\u4E00\u5904\u85CF\u8EAB\u4E4B\u5730\u3002</p><p>\u5929\u8272\u5FAE\u660E\uFF0C\u6155\u5BB9\u7D2B\u73AB\u8FDE\u7EED\u8D8A\u8FC7\u571F\u3001\u6728\u4E24\u5802\u4E94\u961F\u4EBA\u9A6C\uFF0C\u4ECE\u6811\u68A2\u5212\u8FC7\u8FD1\u5341\u4E08\u7684\u8DDD\u79BB\uFF0C\u8F7B\u8F7B\u843D\u5728\u4E00\u5757\u5D99\u5CCB\u7684\u5DE8\u77F3\u4E0A\uFF0C\u6B63\u5F85\u817E\u8EAB\u800C\u8D77\uFF0C\u7A81\u7136\u811A\u4E0A\u4E00\u7D27\u3002</p><p>\u7D2B\u73AB\u5927\u9A87\u8F6C\u8EAB\uFF0C\u900F\u5165\u773C\u4E2D\u7684\u662F\u4E00\u5F20\u82F1\u4FCA\u7684\u9762\u5B54\u3002\u4ED6\u5E74\u7EAA\u4F3C\u4E4E\u6BD4\u5C55\u626C\u5C0F\u4E00\u4E9B\uFF0C\u8138\u8272\u82CD\u767D\uFF0C\u50CF\u662F\u591A\u5E74\u4E0D\u89C1\u5929\u65E5\uFF0C\u6DE1\u6DE1\u7684\u7B11\u5BB9\u4E2D\u5E26\u7740\u4E00\u4E1D\u9634\u51B7\u7684\u90AA\u610F\u3002\u604D\u604D\u60DA\u60DA\u6709\u79CD\u4F3C\u66FE\u76F8\u8BC6\u7684\u5F02\u6837\u611F\u89C9\u2026\u2026</p><p>\u867D\u7136\u4E00\u811A\u8E0F\u5728\u7D2B\u73AB\u5C0F\u5DE7\u73B2\u73D1\u7684\u7EA4\u8DB3\u4E0A\uFF0C\u4F46\u90A3\u7537\u5B50\u5374\u50CF\u7AD9\u5728\u5BB6\u4E2D\u822C\u6F47\u6D12\u81EA\u5982\u3002</p><p>\u80F8\u8179\u8D34\u7740\u7D2B\u73AB\u7684\u7C89\u80CC\uFF0C\u773C\u4E2D\u5F02\u5F69\u6D9F\u6D9F\uFF0C\u663E\u7136\u5BF9\u5979\u7684\u7F8E\u8C8C\u5927\u51FA\u610F\u5916\u3002</p><p>\u4ED6\u8E29\u5F97\u5E76\u4E0D\u91CD\uFF0C\u4F46\u65E0\u8BBA\u7D2B\u73AB\u600E\u4E48\u4F7F\u529B\u90FD\u65E0\u6CD5\u6323\u8131\u3002\u7D2B\u73AB\u60CA\u6012\u4EA4\u52A0\uFF0C\u4E00\u8098\u51FB\u5411\u4ED6\u80F8\u53E3\uFF0C\u540C\u65F6\u56DE\u5200\u671D\u4ED6\u8170\u95F4\u75BE\u523A\u3002\u8098\u5C16\u4E00\u8F6F\uFF0C\u50CF\u843D\u5728\u68C9\u82B1\u4E0A\u822C\u6BEB\u4E0D\u53D7\u529B\uFF0C\u7D2B\u73AB\u5FC3\u53EB\u4E0D\u5999\uFF0C\u6267\u5200\u7684\u624B\u4E0A\u53C8\u52A0\u4E86\u4E09\u5206\u529B\u9053\u3002\u90A3\u7537\u5B50\u811A\u5C16\u4E00\u62E7\uFF0C\u811A\u5C16\u70B9\u5728\u7D2B\u73AB\u811A\u80CC\u4E0A\u8F7B\u98CE\u822C\u65CB\u5230\u9762\u524D\uFF0C\u4E0B\u5DF4\u51E0\u4E4E\u78B0\u5230\u5979\u7684\u9F3B\u5C16\u3002</p><p>\u7D2B\u73AB\u7528\u9519\u529B\u9053\uFF0C\u5A07\u8EAF\u76F4\u5F80\u540E\u4EF0\uFF0C\u90A3\u4EBA\u5FAE\u7B11\u7740\u633D\u4F4F\u5979\u7684\u67D4\u8170\uFF0C\u4FEF\u5230\u5979\u773C\u524D\u8F7B\u58F0\u9053\uFF1A\u300C\u4F60\u662F\u7D2B\u73AB\u5427\uFF1F\u300D</p><p>\u8170\u80A2\u521A\u521A\u78B0\u5230\u4ED6\u7684\u624B\u81C2\uFF0C\u7D2B\u73AB\u8EAB\u4E0A\u5341\u4F59\u5904\u5927\u7A74\u540C\u65F6\u4E00\u9EBB\u3002\u7247\u7389\u94EE\u7136\u4E00\u58F0\uFF0C\u6389\u5728\u77F3\u4E0A\u3002</p>',57);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
