import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_24" tabindex="-1"><a class="header-anchor" href="#_24" aria-hidden="true">#</a> 24</h1><p>\u7EA2\u65E5\u521D\u5347\uFF0C\u8FF7\u8499\u7684\u70DF\u96FE\u50CF\u88AB\u5DE8\u624B\u4E00\u62B9\uFF0C\u77AC\u95F4\u6D88\u5931\u5730\u65E0\u5F71\u65E0\u8E2A\u3002\u56DB\u5468\u78A7\u84DD\u7684\u6E56\u6C34\u5C3D\u6536\u773C\u5E95\uFF0C\u84DD\u5B9D\u77F3\u822C\u707C\u707C\u751F\u8F89\u3002</p><p>\u6000\u4E2D\u5A07\u5C0F\u7684\u7389\u4EBA\u67D4\u82E5\u65E0\u9AA8\uFF0C\u8F7B\u76C8\u9999\u8F6F\uFF0C\u9633\u5149\u5728\u7CBE\u81F4\u7684\u4E94\u5B98\u95F4\u4E0D\u4F4F\u6D41\u6DCC\uFF0C\u5E26\u7740\u871C\u6C41\u822C\u7684\u9999\u751C\u3002</p><p>\u65E9\u5C31\u6212\u5907\u68EE\u4E25\u3001\u9AD8\u624B\u4E91\u96C6\u7684\u661F\u6708\u6E56\uFF0C\u7ADF\u8BA9\u8FD9\u4E2A\u9C9C\u82B1\u822C\u7684\u5C0F\u5973\u5B69\u6765\u53BB\u81EA\u5982\u2026\u2026</p><p>\u6155\u5BB9\u9F99\u62B1\u7740\u7D2B\u73AB\u7ACB\u5728\u6BBF\u524D\u9AD8\u5927\u7684\u7389\u9636\u4E0A\uFF0C\u51DD\u89C6\u534A\u664C\uFF0C\u5F90\u5F90\u62AC\u8D77\u5934\uFF0C\u671B\u7740\u4E0B\u9762\u7684\u5E2E\u4F17\u50B2\u7136\u4E00\u7B11\uFF0C\u300C\u4F20\u8C15\u5929\u4E0B\uFF1A\u56DB\u6708\u5341\u516D\uFF0C\u672C\u5BAB\u4E0E\u73AB\u7470\u4ED9\u5B50\u6210\u5A5A\uFF01\u300D</p><p>\u6155\u5BB9\u7D2B\u73AB\u77AA\u5927\u773C\u775B\uFF0C\u50CF\u662F\u8981\u7528\u773C\u5149\u628A\u4ED6\u6495\u788E\u3002\u540C\u6837\u5FC3\u4E0B\u8BB6\u5F02\uFF0C\u542C\u7236\u4EB2\u79F0\u661F\u6708\u5BAB\u4E3B\u4E3A\u300C\u5996\u5987\u300D\uFF0C\u6CA1\u60F3\u5230\u5374\u662F\u4E2A\u5E74\u8F7B\u7537\u5B50\u3002</p><p>\u6C89\u91CD\u7684\u6BBF\u95E8\u7F13\u7F13\u5408\u7D27\uFF0C\u5C06\u9633\u5149\u9694\u5728\u6BBF\u5916\uFF0C\u6155\u5BB9\u7D2B\u73AB\u7A81\u7136\u89C9\u5F97\u4E00\u80A1\u5BD2\u610F\u4FB5\u5165\u808C\u80A4\uFF0C\u5FCD\u4E0D\u4F4F\u5A07\u8EAF\u8F7B\u98A4\u3002</p><p>\u5BAB\u4E3B\u62B1\u7740\u5979\u7A7F\u8FC7\u4E00\u6761\u957F\u957F\u7684\u752C\u9053\uFF0C\u7ED5\u8FC7\u4E00\u4E2A\u5706\u684C\u5927\u5C0F\u7684\u592A\u6781\u56FE\uFF0C\u6765\u5230\u77F3\u7A9F\u6DF1\u5904\u3002</p><p>\u77F3\u5BA4\u5BBD\u8FBE\u4E94\u4E08\uFF0C\u6E29\u6696\u5982\u6625\u3002\u56DB\u58C1\u9648\u8BBE\u7CBE\u7F8E\u96C5\u81F4\uFF0C\u886C\u7740\u767D\u4E91\u822C\u8F7B\u8F6F\u7684\u5730\u6BEF\uFF0C\u5B9B\u5982\u4ED9\u5883\u3002</p><p>\u5BAB\u4E3B\u62E5\u7740\u5979\u501A\u5728\u5E8A\u4E0A\uFF0C\u9759\u9759\u51DD\u89C6\u5979\u7684\u4FCF\u8138\u3002</p><p>\u5FFD\u7136\u9888\u4E0B\u4E00\u677E\uFF0C\u7D2B\u73AB\u51B2\u53E3\u800C\u51FA\uFF1A\u300C\u6211\u5A18\u5462\uFF1F\u300D</p><p>\u300C\u542C\u8BF4\u4F60\u6765\u4E86\uFF0C\u6211\u8BA9\u5A18\u5148\u4F4F\u5728\u9694\u58C1\u2026\u2026\u300D</p><p>\u7D2B\u73AB\u5FC3\u5934\u53C8\u6CDB\u8D77\u90A3\u79CD\u5F02\u6837\u7684\u611F\u89C9\u3002</p><p>\u300C\u2026\u2026\u5982\u679C\u4F60\u559C\u6B22\uFF0C\u548C\u5A18\u4F4F\u5728\u4E00\u8D77\u4E5F\u884C\u3002\u300D</p><p>\u7D2B\u73AB\u6C89\u9ED8\u7247\u523B\uFF0C\u76EF\u7740\u4ED6\u7684\u773C\u775B\u8BF4\u9053\uFF1A\u300C\u6211\u8981\u89C1\u5979\u3002\u300D</p><p>\u300C\u653E\u5F00\u6211\uFF01\u6211\u81EA\u5DF1\u8D70\uFF01\u300D</p><p>\u5BAB\u4E3B\u8F7B\u7B11\u4E00\u58F0\uFF0C\u89E3\u5F00\u5979\u7684\u7A74\u9053\u3002\u6155\u5BB9\u7D2B\u73AB\u5438\u4E86\u53E3\u6C14\uFF0C\u6697\u6697\u51DD\u805A\u529F\u529B\uFF0C\u968F\u4ED6\u8D70\u5230\u4FA7\u5BA4\u3002</p><p>\u4FA7\u5BA4\u5C0F\u4E86\u8BB8\u591A\uFF0C\u9760\u5899\u6446\u7740\u4E00\u5F20\u77F3\u69BB\uFF0C\u67D4\u6BEF\u4E0A\u9732\u51FA\u4E00\u5F20\u7AEF\u5E84\u7684\u9762\u5B54\u3002\u767E\u82B1\u89C2\u97F3\u95ED\u7740\u773C\uFF0C\u8138\u8272\u53D1\u7EA2\uFF0C\u4F3C\u4E4E\u5728\u627F\u53D7\u4EC0\u4E48\u75DB\u82E6\u3002</p><p>\u6155\u5BB9\u7D2B\u73AB\u5FC3\u4E0B\u6697\u60CA\uFF0C\u751F\u6015\u6BCD\u4EB2\u4F1A\u53D7\u5230\u5AC2\u5AC2\u90A3\u6837\u7684\u6298\u78E8\uFF0C\u5979\u987E\u4E0D\u5F97\u53EB\u558A\uFF0C\u6251\u8FC7\u53BB\u6B63\u5F85\u7EC6\u770B\u7AEF\u502A\uFF0C\u5374\u89C1\u4EBA\u5F71\u4E00\u95EA\uFF0C\u5BAB\u4E3B\u62A2\u5148\u4E00\u6B65\u63A0\u5230\u5E8A\u5934\uFF0C\u6258\u8D77\u6BCD\u4EB2\u7684\u81FB\u9996\uFF0C\u8D23\u602A\u9053\uFF1A\u300C\u600E\u4E48\u4E0D\u53EB\u4EBA\u5462\uFF1F\u300D\u8BF4\u7740\u6380\u5F00\u6BDB\u6BEF\u3002</p><p>\u770B\u5230\u6BCD\u4EB2\u8EAB\u4E0A\u6BEB\u65E0\u5F02\u72B6\uFF0C\u7D2B\u73AB\u677E\u4E86\u53E3\u6C14\uFF0C\u65CB\u5373\u6EE1\u8138\u98DE\u7EA2\u3002\u6BCD\u4EB2\u7389\u4F53\u88F8\u88CE\uFF0C\u56DB\u80A2\u8F6F\u7EF5\u7EF5\u642D\u5728\u90A3\u4EBA\u81C2\u4E0A\uFF0C\u819D\u5F2F\u88AB\u4ED6\u7528\u4E24\u624B\u5206\u5F00\uFF0C\u79D8\u5904\u5C3D\u9732\u3002</p><p>\u5BAB\u4E3B\u50CF\u62B1\u7740\u5A74\u513F\u822C\u62B1\u7740\u6210\u719F\u7F8E\u8273\u7684\u8D35\u5987\uFF0C\u8D70\u5230\u5BA4\u4FA7\u4E00\u4E2A\u6728\u6876\u65C1\uFF0C\u62AC\u811A\u6311\u4E0B\u6728\u76D6\uFF0C\u628A\u6BCD\u4EB2\u4E0B\u4F53\u653E\u5728\u6876\u4E0A\u3002\u6BCD\u4EB2\u7259\u9F7F\u54AC\u7740\u7EA2\u5507\uFF0C\u75DB\u82E6\u4E07\u72B6\uFF0C\u56DB\u80A2\u5374\u4E00\u52A8\u4E0D\u52A8\u3002</p><p>\u7D2B\u73AB\u5446\u5446\u770B\u7740\u8FD9\u4E00\u5207\uFF0C\u534A\u664C\u624D\u53EB\u9053\uFF1A\u300C\u5A18\uFF01\u300D</p><p>\u8427\u4F5B\u5974\u8EAB\u5B50\u4E00\u9707\uFF0C\u7741\u5F00\u7F8E\u76EE\uFF0C\u6D41\u9732\u51FA\u60CA\u60E7\u7684\u795E\u60C5\u3002\u7A81\u7136\u6C34\u58F0\u664C\u8D77\uFF0C\u4E00\u80A1\u6D45\u9EC4\u7684\u6DB2\u4F53\u4ECE\u67D4\u7F8E\u7684\u82B1\u74E3\u95F4\u55B7\u51FA\uFF0C\u843D\u5728\u6728\u6876\u4E2D\u3002</p><p>\u7D2B\u73AB\u7D27\u7D27\u6342\u7740\u5C0F\u5634\uFF0C\u96BE\u4EE5\u7F6E\u4FE1\u5730\u770B\u7740\u7AEF\u5E84\u534E\u8D35\u7684\u6BCD\u4EB2\u5728\u4E00\u4E2A\u964C\u751F\u7537\u4EBA\u6000\u4E2D\u6392\u51FA\u5C3F\u6DB2\u2026\u2026</p><p>\u300C\u73AB\u513F\uFF01\u4F60\u2026\u2026\u300D\u6BCD\u4EB2\u4E24\u76EE\u542B\u6CEA\uFF0C\u62AC\u5934\u5BF9\u90A3\u4E2A\u7537\u5B50\u5636\u58F0\u53EB\u9053\uFF1A\u300C\u4F60\u8FD9\u755C\u7272\uFF01\u4F60\u4E0D\u5F97\u597D\u6B7B\u2026\u2026\u300D</p><p>\u5BAB\u4E3B\u4E0D\u4EE5\u4E3A\u610F\u7684\u6DE1\u7136\u7B11\u7740\uFF0C\u5F85\u4E45\u84C4\u7684\u5C3F\u6DB2\u6392\u5C3D\uFF0C\u6258\u7740\u8427\u4F5B\u5974\u7684\u7F8E\u81C0\u8F7B\u8F7B\u6643\u4E86\u4E24\u4E0B\uFF0C\u7136\u540E\u53D6\u8FC7\u4E00\u5757\u67D4\u8F6F\u7684\u6BDB\u5DFE\uFF0C\u5728\u5979\u80EF\u4E0B\u6162\u6162\u64E6\u62ED\uFF0C\u5634\u91CC\u53F9\u9053\uFF1A\u300C\u4F60\u53C8\u4E0D\u4E56\u4E86\u2026\u2026\u300D</p><p>\u7D2B\u73AB\u542B\u6012\u51FA\u638C\uFF0C\u76F4\u51FB\u5BAB\u4E3B\u80CC\u5FC3\u3002\u624B\u638C\u521A\u521A\u6325\u51FA\uFF0C\u80F8\u53E3\u4E00\u9EBB\uFF0C\u8F6F\u8F6F\u6B6A\u5728\u5730\u4E0A\u3002</p><p>\u5BAB\u4E3B\u65E9\u6709\u6212\u5907\uFF0C\u4E00\u62DB\u5236\u4F4F\u7D2B\u73AB\uFF0C\u628A\u5979\u6276\u5230\u6905\u4E2D\u5750\u597D\uFF0C\u7136\u540E\u5C06\u767E\u82B1\u89C2\u97F3\u653E\u5728\u5E8A\u4E0A\uFF0C\u62FF\u51FA\u4E00\u6839\u67D4\u97E7\u7684\u767D\u8272\u8F6F\u97AD\u3002\u90A3\u6839\u8F6F\u97AD\u7531\u4E24\u957F\u4E24\u77ED\u56DB\u6839\u8D28\u5730\u5947\u5F02\u7684\u7EF3\u7D22\u7EDE\u6210\uFF0C\u8272\u5982\u767D\u7389\uFF0C\u5F39\u6027\u5341\u8DB3\u3002</p><p>\u5BAB\u4E3B\u7B11\u563B\u563B\u9053\uFF1A\u300C\u4E0D\u4E56\u53EF\u662F\u8981\u6328\u6253\u7684\u2026\u2026\u300D</p><p>\u300C\u556A\u300D\u7684\u4E00\u58F0\u8F7B\u54CD\uFF0C\u8F6F\u97AD\u843D\u5728\u8427\u4F5B\u5974\u9999\u4E73\u4E0A\uFF0C\u767D\u5AE9\u808C\u80A4\u7ACB\u523B\u663E\u51FA\u4E00\u9053\u7EA2\u5370\u3002</p><p>\u767E\u82B1\u89C2\u97F3\u75DB\u53EB\u4E00\u58F0\uFF0C\u79C0\u7709\u62E7\u7D27\u3002</p><p>\u300C\u5341\u4E2A\u5B57\uFF0C\u8981\u6328\u5341\u4E0B\u5462\u2026\u2026\u300D</p><p>\u97AD\u5F71\u7FFB\u98DE\uFF0C\u843D\u5728\u767E\u82B1\u89C2\u97F3\u80F8\u524D\u8179\u4E0B\uFF0C\u8D2F\u6EE1\u771F\u6C14\u7684\u8F6F\u97AD\u50CF\u624B\u6307\u822C\u62C2\u5F04\u7740\u5973\u6027\u654F\u611F\u7684\u90E8\u4F4D\uFF0C\u534A\u662F\u75DB\u695A\uFF0C\u534A\u662F\u6311\u9017\u3002\u5F85\u5341\u97AD\u62BD\u5B8C\uFF0C\u5979\u80A1\u95F4\u5DF2\u662F\u6DEB\u6C34\u8FDE\u8FDE\u3002\u767E\u82B1\u89C2\u97F3\u7741\u5F00\u773C\uFF0C\u770B\u4E86\u770B\u7D2B\u73AB\uFF0C\u53C8\u770B\u4E86\u770B\u5BAB\u4E3B\uFF0C\u84E6\u5730\u653E\u58F0\u5927\u54ED\u3002</p><p>\u5BAB\u4E3B\u633D\u7740\u8F6F\u97AD\u8D70\u5230\u7D2B\u73AB\u8EAB\u524D\uFF0C\u4ED4\u7EC6\u770B\u7740\u5979\uFF0C\u773C\u4E2D\u795E\u8272\u4E0D\u4F4F\u53D8\u5E7B\uFF0C\u300C\u4F60\u4E5F\u4E0D\u4E56\u5462\u2026\u2026\u300D</p><p>\u5BAB\u4E3B\u8FD8\u672A\u8BF4\u5B8C\uFF0C\u8427\u4F5B\u5974\u4FBF\u51C4\u58F0\u9053\uFF1A\u300C\u4E0D\u8981\u78B0\u5979\u2026\u2026\u5979\u662F\u4F60\u4EB2\u59B9\u59B9\u2026\u2026\u300D</p><p>\u6155\u5BB9\u7D2B\u73AB\u5927\u60CA\u5931\u8272\uFF0C\u7236\u4EB2\u4E34\u7EC8\u524D\u6240\u8BF4\u7684\u8BDD\u77AC\u95F4\u6D41\u8FC7\u5FC3\u5934\u3002\u4E8B\u60C5\u867D\u96BE\u4EE5\u7F6E\u4FE1\uFF0C\u4F46\u5979\u5929\u4EFD\u8FC7\u4EBA\uFF0C\u8F6C\u5FF5\u95F4\u4FBF\u9690\u7EA6\u660E\u767D\u4E86\u5176\u4E2D\u7684\u539F\u59D4\u3002\u5979\u8270\u96BE\u5730\u5598\u4E86\u53E3\u6C14\uFF0C\u7A33\u4F4F\u5FC3\u795E\u6C89\u58F0\u9053\uFF1A\u300C\u4F60\u7A76\u7ADF\u60F3\u600E\u6837\uFF1F\u300D</p><p>\u300C\u5AC1\u7ED9\u54E5\u54E5\uFF0C\u7ED9\u6211\u751F\u5B69\u5B50\u3002\u300D</p><p>\u7D2B\u73AB\u76EF\u7740\u6155\u5BB9\u9F99\u770B\u4E86\u534A\u664C\uFF0C\u7A81\u7136\u72E0\u72E0\u5550\u4E86\u4ED6\u4E00\u53E3\u3002</p><p>\u6155\u5BB9\u9F99\u62FF\u8D77\u5979\u8F6F\u7EF5\u7EF5\u7684\u7389\u638C\uFF0C\u6162\u6162\u64E6\u51C0\u8138\u4E0A\u7684\u9999\u553E\uFF0C\u82F1\u4FCA\u7684\u8138\u4E0A\u6BEB\u65E0\u6240\u52A8\uFF0C\u6DE1\u6DE1\u9053\uFF1A\u300C\u4F60\u60F3\u8DDF\u5A18\u4E00\u6837\u5417\uFF1F\u300D</p><p>\u300C\u4F60\u628A\u6211\u5A18\u600E\u4E48\u4E86\uFF01\uFF1F\u300D</p><p>\u6155\u5BB9\u9F99\u62AC\u8D77\u767E\u82B1\u89C2\u97F3\u7684\u811A\u8E1D\uFF0C\u8427\u4F5B\u5974\u5F62\u5BB9\u51C4\u695A\uFF0C\u6BEB\u65E0\u53CD\u6297\u4E4B\u529B\u5730\u5728\u513F\u5B50\u548C\u5973\u513F\u9762\u524D\uFF0C\u655E\u9732\u51FA\u8D64\u88F8\u7684\u7389\u80A1\u3002\u79D8\u5904\u8273\u7EA2\u7684\u5AE9\u8089\u968F\u7740\u7389\u817F\u5F00\u5408\u65F6\u9690\u65F6\u73B0\u3002\u624B\u4E00\u677E\uFF0C\u5149\u6DA6\u7684\u7389\u817F\u7ACB\u5373\u76F4\u76F4\u843D\u4E86\u4E0B\u6765\uFF0C\u8F6F\u8F6F\u642D\u5728\u5E8A\u4FA7\u3002</p><p>\u300C\u5A18\u592A\u4E0D\u542C\u8BDD\uFF0C\u4E0D\u60F3\u8BA9\u54E5\u54E5\u64CD\u5979\uFF0C\u6211\u53EA\u597D\u628A\u5A18\u7684\u624B\u7B4B\u811A\u7B4B\u62BD\u4E86\u2014\u2014\u5476\uFF0C\u8FD9\u97AD\u5B50\u597D\u770B\u5417\uFF1F\u300D</p><p>\u7D2B\u73AB\u50CF\u843D\u5728\u51B0\u7A9F\u4E2D\uFF0C\u5468\u8EAB\u7684\u8840\u8109\u90FD\u51BB\u50F5\u4E86\uFF0C\u534A\u664C\u624D\u54ED\u9053\uFF1A\u300C\u4F60\u8FD9\u4E2A\u6DF7\u86CB\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u773C\u4E2D\u5BD2\u8292\u4E00\u95EA\uFF0C\u9053\uFF1A\u300C\u628A\u7EAA\u5A4A\u5B50\u5E26\u5230\u6BBF\u524D\uFF0C\u8DDF\u6797\u5A4A\u5B50\u4E00\u5757\u8BA9\u4EBA\u64CD\uFF01\u300D</p><p>\u56DE\u8FC7\u795E\u6765\u7684\u7D2B\u73AB\u54ED\u9A82\u8FDE\u58F0\uFF0C\u6155\u5BB9\u9F99\u5FC3\u4E0B\u6697\u6068\uFF0C\u4F46\u770B\u7740\u59B9\u59B9\u7684\u7F8E\u6001\uFF0C\u5FF5\u53CA\u534A\u6708\u540E\u5C31\u8981\u4E0E\u5979\u6210\u5A5A\uFF0C\u4E0D\u597D\u4E0B\u624B\u6298\u78E8\u3002\u5FF5\u5934\u4E00\u8F6C\uFF0C\u628A\u7D2B\u73AB\u653E\u5728\u5E8A\u4E0A\uFF0C\u5FAE\u7B11\u9053\uFF1A\u300C\u59B9\uFF0C\u770B\u7740\u54E5\u54E5\u662F\u600E\u4E48\u8BA9\u5A18\u5FEB\u6D3B\u7684\u2026\u2026\u300D</p><p>\u5149\u6DA6\u7684\u5927\u817F\u88AB\u4E24\u53EA\u5927\u624B\u63B0\u5F00\uFF0C\u7D2B\u7EA2\u8272\u7684\u9F9F\u5934\u76F4\u633A\u633A\u4F38\u5411\u8427\u4F5B\u5974\u8179\u4E0B\u3002\u513F\u81C2\u7C97\u7EC6\u7684\u8089\u68D2\u5E03\u6EE1\u9897\u7C92\u8089\u523A\uFF0C\u72F0\u72DE\u65E0\u6BD4\u3002\u767E\u82B1\u89C2\u97F3\u4FCF\u8138\u4FA7\u5728\u4E00\u65C1\uFF0C\u6CEA\u5982\u96E8\u4E0B\u3002\u7D2B\u73AB\u4E24\u773C\u5706\u7741\uFF0C\u9ED1\u767D\u5206\u660E\u7684\u5927\u773C\u96BE\u4EE5\u7F6E\u4FE1\u5730\u76EF\u7740\u8FD9\u4E2A\u602A\u7269\u3002\u6155\u5BB9\u9F99\u5634\u89D2\u9732\u51FA\u4E00\u4E1D\u7B11\u5BB9\uFF0C\u50B2\u7136\u6324\u8FDB\u79D8\u7A74\u3002</p><p>\u773C\u7741\u7741\u770B\u7740\u6740\u6C14\u817E\u817E\u7684\u8089\u68D2\u6324\u5165\u6BCD\u4EB2\u4F53\u5185\uFF0C\u7D2B\u73AB\u5FC3\u5934\u4E00\u7A92\uFF0C\u5F85\u56DE\u8FC7\u795E\u6765\uFF0C\u53CD\u800C\u9A82\u5F97\u66F4\u54CD\u4E86\u3002\u6155\u5BB9\u9F99\u8170\u8EAB\u4E00\u633A\u72E0\u72E0\u5730\u5168\u6839\u800C\u5165\uFF0C\u90A3\u79CD\u88AB\u8089\u68D2\u80C0\u6EE1\u7684\u5145\u5B9E\u611F\u4F7F\u8427\u4F5B\u5974\u7981\u4E0D\u4F4F\u95F7\u54FC\u4E00\u58F0\u3002\u9633\u5177\u62BD\u9001\u8D8A\u6765\u8D8A\u5FEB\uFF0C\u51E0\u4E4E\u662F\u6BEB\u65E0\u505C\u987F\u5730\u649E\u51FB\u7740\u82B1\u5FC3\uFF0C\u90A3\u4E9B\u602A\u5F02\u7684\u89E6\u624B\u5728\u82B1\u74E3\u95F4\u56DB\u5904\u629A\u5F04\uFF0C\u4E0D\u591A\u65F6\u767E\u82B1\u89C2\u97F3\u4FBF\u5931\u795E\u5730\u53EB\u51FA\u58F0\u6765\u3002</p><p>\u5FFD\u7136\u95E8\u5916\u4E00\u4E2A\u5973\u5B50\u8F7B\u58F0\u7980\u9053\uFF1A\u300C\u970D\u957F\u8001\u62BC\u7740\u6D41\u971C\u5251\u98CE\u5974\u5DF2\u7136\u56DE\u5BAB\u3002\u300D</p><p>\u7F8A\u7F94\u822C\u6E29\u987A\u7684\u767D\u7389\u83BA\u8DEA\u5728\u752C\u9053\u5185\uFF0C\u8EAB\u4E0A\u53EA\u62AB\u4E86\u5C42\u6D45\u7EA2\u7684\u8F7B\u7EB1\uFF0C\u767D\u5AE9\u7684\u808C\u80A4\u65F6\u9690\u65F6\u73B0\uFF0C\u5A07\u7F8E\u7684\u66F2\u7EBF\u5C3D\u6536\u773C\u5E95\u3002\u5979\u4E0E\u59B9\u59B9\u767D\u7389\u9E42\u5165\u5BAB\u5DF2\u7ECF\u4E24\u65E5\uFF0C\u6155\u5BB9\u9F99\u89C1\u59D0\u59B9\u4FE9\u4E56\u5DE7\u67D4\u987A\uFF0C\u4FBF\u8BA9\u5979\u4EEC\u5728\u540E\u5BAB\u4F3A\u5019\u3002</p><p>\u7D2B\u73AB\u6B7B\u6B7B\u76EF\u7740\u5979\uFF0C\u6068\u4E0D\u5F97\u628A\u5979\u634F\u6B7B\uFF01\u53EF\u7B11\u81EA\u5DF1\u66FE\u7ECF\u90A3\u4E48\u4FE1\u4EFB\u5979\uFF0C\u7ED3\u679C\u7D2F\u5F97\u5927\u5E08\u59D0\u4E2D\u8BA1\u88AB\u64D2\u3002\u53EA\u770B\u5AC2\u5AC2\u6240\u53D7\u7684\u6298\u78E8\uFF0C\u4FBF\u77E5\u9053\u5927\u5E08\u59D0\u4F1A\u6709\u4EC0\u4E48\u6837\u7684\u906D\u9047\u2026\u2026</p><p>\u767D\u7389\u83BA\u5C0F\u5FC3\u7FFC\u7FFC\u5730\u62AC\u773C\u671D\u5979\u770B\u53BB\uFF0C\u6B63\u770B\u5230\u7D2B\u73AB\u5145\u6EE1\u6068\u610F\u7684\u76EE\u5149\uFF0C\u5FD9\u5782\u4E0B\u5934\uFF0C\u6025\u6B65\u8DDF\u5728\u6155\u5BB9\u9F99\u8EAB\u540E\u3002</p><p>\u6BCD\u4EB2\u8FD8\u5728\u8F7B\u58F0\u547B\u541F\u7740\uFF0C\u8138\u4E0A\u7684\u6F6E\u7EA2\u4E45\u4E45\u672A\u892A\u3002\u6155\u5BB9\u7D2B\u73AB\u54AC\u7D27\u7259\u5173\uFF0C\u62FC\u547D\u8FD0\u6C14\u51B2\u7A74\u3002\u4F46\u5979\u529F\u529B\u592A\u6D45\uFF0C\u6DA3\u6563\u7684\u771F\u6C14\u9759\u9759\u505C\u6EDE\u5728\u56DB\u80A2\u7ECF\u8109\u5185\uFF0C\u4E00\u52A8\u4E0D\u52A8\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u542C\u5B8C\u970D\u72C2\u7130\u7684\u53D9\u8FF0\uFF0C\u6155\u5BB9\u9F99\u988C\u9996\u9053\uFF1A\u300C\u970D\u957F\u8001\u4ECA\u8D9F\u7ACB\u4E0B\u5927\u529F\u3002\u65E2\u7136\u4F60\u4E0E\u6D41\u971C\u5251\u6709\u6B64\u5927\u6068\uFF0C\u53BB\u8BF7\u53F6\u62A4\u6CD5\u6551\u6CBB\u4E4B\u540E\u518D\u4EB2\u624B\u7ED9\u5979\u5F00\u82DE\u597D\u4E86\u3002\u300D</p><p>\u970D\u72C2\u7130\u5927\u559C\u8FC7\u671B\uFF0C\u8FDE\u5FD9\u53BB\u4E86\u3002</p><p>\u6155\u5BB9\u9F99\u6253\u91CF\u7740\u5944\u5944\u4E00\u606F\u7684\u98CE\u665A\u534E\uFF0C\u72DE\u7136\u4E00\u7B11\uFF0C\u9053\uFF1A\u300C\u8BF7\u5C11\u592B\u4EBA\u51FA\u6765\uFF0C\u89C1\u89C1\u98CE\u5973\u4FA0\u3002\u300D</p><p>\u767D\u7389\u83BA\u5FC3\u91CC\u6709\u6127\uFF0C\u4E0D\u6562\u518D\u89C1\u73AB\u7470\u4ED9\u5B50\uFF0C\u6084\u6084\u7ED9\u59B9\u59B9\u4F7F\u4E86\u4E2A\u773C\u8272\u3002\u767D\u7389\u9E42\u786C\u7740\u5934\u76AE\u53BB\u4E86\u3002</p><p>\u6D41\u971C\u5251\u65E0\u590D\u6614\u65E5\u7EB5\u6A2A\u6C5F\u6E56\u7684\u98CE\u91C7\uFF0C\u5979\u7684\u53F3\u81C2\u9F50\u8098\u800C\u65AD\uFF0C\u96EA\u767D\u7684\u67D4\u80A9\u5DE6\u53F3\u5404\u6709\u4E00\u4E2A\u4E4C\u9ED1\u7684\u8840\u6D1E\uFF0C\u4E24\u6839\u94C1\u7D22\u4ECE\u4E2D\u7A7F\u8FC7\uFF0C\u5C06\u5979\u540A\u5728\u6BBF\u9876\u3002\u4E24\u819D\u8DDD\u5730\u9762\u534A\u5C3A\u9AD8\u4F4E\uFF0C\u53EA\u9760\u811A\u80CC\u652F\u6301\u8EAB\u4F53\u3002\u4E00\u8DEF\u4E0A\u98CE\u665A\u534E\u88AB\u970D\u72C2\u7130\u50CF\u72D7\u4E00\u822C\u6813\u5728\u8F66\u4E2D\uFF0C\u8EAB\u5FC3\u500D\u53D7\u6298\u78E8\uFF0C\u4F46\u773C\u4E2D\u7684\u521A\u6BC5\u5374\u4E00\u5982\u5F80\u65E5\u3002</p><p>\u5C4F\u98CE\u540E\u4F20\u6765\u4E00\u58F0\u5C16\u53EB\uFF0C\u90A3\u662F\u6155\u5BB9\u7D2B\u73AB\u770B\u5230\u5927\u5E08\u59D0\u7684\u65AD\u81C2\uFF0C\u5931\u58F0\u53D1\u51FA\u7684\uFF0C\u300C\u5E08\u59D0\uFF01\u4F60\u7684\u624B\u81C2\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u5934\u4E5F\u4E0D\u56DE\uFF0C\u53EA\u6258\u8D77\u98CE\u665A\u534E\u575A\u633A\u7684\u4E73\u623F\u653E\u5728\u624B\u4E2D\u6382\u4E86\u6382\uFF0C\u7B11\u9053\uFF1A\u300C\u4F60\u7684\u5C44\u8D4F\u7ED9\u4E86\u970D\u957F\u8001\uFF0C\u672C\u5BAB\u5C31\u52C9\u5F3A\u7ED9\u4F60\u5976\u5B50\u5F00\u82DE\u597D\u4E86\u3002\u300D\u8BF4\u7740\u62FF\u8D77\u4E00\u628A\u624B\u6307\u5BBD\u7A84\u7684\u8584\u5203\uFF0C\u5728\u4E73\u6655\u4E0B\u6A2A\u5207\u4E00\u5200\u3002</p><p>\u63A5\u7740\u634F\u7740\u6BB7\u7EA2\u7684\u4E73\u5934\u5411\u4E0A\u4E00\u7FFB\uFF0C\u4E73\u5C16\u7ACB\u523B\u79BB\u5F00\u4E73\u7403\uFF0C\u9732\u51FA\u5E73\u6574\u7684\u4F24\u53E3\u3002\u6EF4\u8840\u7684\u5AE9\u8089\u4E0D\u4F4F\u98A4\u52A8\uFF0C\u4E00\u7247\u8D64\u7EA2\u4E2D\u9690\u9690\u9732\u51FA\u4E73\u4E2D\u4EA4\u9519\u7684\u8109\u7EDC\u3002\u6155\u5BB9\u9F99\u5C06\u8584\u5200\u76F4\u76F4\u6345\u5165\u4F24\u53E3\uFF0C\u5728\u4E73\u7403\u5185\u5207\u4E86\u4E2A\u5BF8\u8BB8\u6DF1\u6D45\u7684\u5C0F\u5C0F\u5341\u5B57\u3002</p><p>\u4E73\u623F\u662F\u5973\u4EBA\u6700\u5A07\u5AE9\u7684\u5668\u5B98\u4E4B\u4E00\uFF0C\u98CE\u665A\u534E\u75BC\u5F97\u6D51\u8EAB\u4E71\u98A4\uFF0C\u80A9\u4E2D\u94C1\u94FE\u94EE\u7136\u4F5C\u54CD\u3002</p><p>\u8840\u8165\u67D3\u7EA2\u4E86\u73AB\u7470\u4ED9\u5B50\u6C34\u7075\u7075\u7684\u5927\u773C\uFF0C\u7D2B\u73AB\u7EA2\u5507\u5FAE\u5206\uFF0C\u67D4\u8EAF\u50F5\u5728\u767D\u7389\u9E42\u81C2\u4E2D\u3002</p><p>\u6155\u5BB9\u9F99\u80EF\u4E0B\u72F0\u72DE\u7684\u5DE8\u7269\u7F13\u7F13\u633A\u7ACB\uFF0C\u7D2B\u7EA2\u9F9F\u5934\u5728\u8840\u6DCB\u6DCB\u7684\u4E73\u5C16\u5468\u56F4\u78E8\u64E6\u7247\u523B\uFF0C\u7136\u540E\u8170\u8EAB\u4E00\u633A\uFF0C\u6345\u8FDB\u4E73\u7403\u3002</p><p>\u5AE9\u8089\u4E4D\u88C2\uFF0C\u5341\u5B57\u5F62\u7684\u4F24\u53E3\u88AB\u5B8C\u5168\u6491\u5F00\uFF0C\u53EA\u5269\u5468\u56F4\u4E00\u5708\u767D\u817B\u7684\u76AE\u80A4\u88F9\u7D27\u8089\u68D2\uFF0C\u8FDE\u5954\u6D8C\u7684\u9C9C\u8840\u4E5F\u88AB\u5835\u5728\u4E73\u623F\u5185\u3002</p><p>\u8089\u68D2\u9876\u5230\u4F24\u53E3\u5C3D\u5934\uFF0C\u56DB\u4E0B\u4E00\u7247\u67D4\u97E7\u3002\u6155\u5BB9\u9F99\u633A\u8170\u76F4\u5165\uFF0C\u5C06\u4E73\u8089\u5C3D\u6570\u6495\u788E\uFF0C\u5728\u575A\u633A\u7684\u4E73\u623F\u5185\u786C\u751F\u751F\u6345\u51FA\u4E00\u4E2A\u8840\u6D1E\uFF0C\u6DF1\u5165\u5176\u4E2D\uFF0C\u9F9F\u5934\u51E0\u4E4E\u89E6\u5230\u808B\u9AA8\u3002</p><p>\u6155\u5BB9\u9F99\u677E\u5F00\u624B\uFF0C\u9633\u5177\u4E0A\u4E0B\u6311\u52A8\uFF0C\u770B\u7740\u6D51\u5706\u7684\u4E73\u623F\u968F\u4E4B\u4E0A\u4E0B\u6447\u6643\uFF0C\u4E0D\u7531\u54C8\u54C8\u5927\u7B11\u3002</p><p>\u98CE\u665A\u534E\u75DB\u5F7B\u5FC3\u80BA\uFF0C\u7EF7\u7D27\u7684\u811A\u5C16\u5FFD\u7136\u4E00\u8F6F\uFF0C\u5408\u4E0A\u773C\u775B\uFF0C\u96EA\u767D\u7684\u8EAB\u4F53\u60AC\u5728\u94C1\u7D22\u4E0A\u65E0\u529B\u5730\u6446\u52A8\u7740\u3002</p><p>\u8FD9\u65F6\u9633\u5177\u5FAE\u5FAE\u4E00\u56DE\uFF0C\u6EE1\u6EA2\u9C9C\u8840\u7ACB\u523B\u4ECE\u4E73\u5C16\u98DE\u6E85\u51FA\u6765\u3002</p><p>\u770B\u7740\u4EB2\u5982\u624B\u8DB3\u7684\u5927\u5E08\u59D0\u88AB\u4EBA\u5982\u6B64\u51CC\u8650\uFF0C\u7D2B\u73AB\u5FC3\u75BC\u5730\u900F\u4E0D\u8FC7\u6C14\u6765\uFF0C\u773C\u524D\u4E00\u9ED1\uFF0C\u4E5F\u6655\u4E86\u8FC7\u53BB\u3002\u767D\u6C0F\u59D0\u59B9\u611F\u540C\u8EAB\u53D7\uFF0C\u53CC\u53CC\u5750\u5012\u5728\u5730\uFF0C\u8EAB\u4E0B\u4E00\u7247\u6F6E\u6E7F\uFF0C\u7ADF\u662F\u5413\u5F97\u5931\u7981\u4E86\u3002</p><p>\u6155\u5BB9\u9F99\u5374\u662F\u5341\u5206\u5F00\u5FC3\uFF0C\u8FD9\u4E2A\u65B0\u751F\u7684\u8089\u6D1E\u6BD4\u4EFB\u4F55\u4E00\u4E2A\u8089\u7A74\u90FD\u7D27\uFF0C\u4E5F\u66F4\u5408\u9002\u3002\u4ED6\u63E1\u7D27\u5F39\u6027\u5341\u8DB3\u7684\u4E73\u623F\uFF0C\u9633\u5177\u63D2\u5728\u6E29\u70ED\u7684\u4E73\u8089\u6765\u56DE\u62BD\u9001\u3002</p><p>\u98CE\u665A\u534E\u5931\u795E\u5730\u62BD\u6410\u7740\uFF0C\u96EA\u7403\u822C\u7684\u4E73\u623F\u65F6\u7F29\u65F6\u9F13\uFF0C\u4E0D\u65F6\u75C9\u631B\u7740\u6536\u7D27\u3002\u90A3\u7C92\u6BB7\u7EA2\u7684\u4E73\u5934\u5728\u5E03\u6EE1\u9897\u7C92\u548C\u8089\u523A\u7684\u68D2\u8EAB\u4E0A\u4E0D\u4F4F\u8DF3\u8DC3\u3002\u6BCF\u6B21\u62BD\u51FA\u8089\u68D2\uFF0C\u90FD\u4F34\u968F\u7740\u55B7\u6D8C\u7684\u9C9C\u8840\uFF0C\u67D3\u7EA2\u4E86\u767D\u5AE9\u7684\u5904\u5B50\u4E4B\u8EAF\u3002</p><p>\u633A\u52A8\u591A\u65F6\uFF0C\u6155\u5BB9\u9F99\u628A\u7CBE\u6DB2\u5C04\u8FDB\u4E73\u7403\u6DF1\u5904\uFF0C\u5927\u7B11\u7740\u62D4\u51FA\u8840\u6DCB\u6DCB\u7684\u9633\u5177\u3002\u9AD8\u8038\u7684\u96EA\u4E73\u663E\u51FA\u4E00\u4E2A\u60E8\u7EDD\u4EBA\u5BF0\u7684\u8840\u6D1E\uFF0C\u5B9B\u5982\u4E00\u5F20\u6D51\u5706\u7684\u5C0F\u5634\uFF0C\u8D2F\u7A7F\u4E86\u6574\u53EA\u4E73\u623F\u2026\u2026</p><p>\u6155\u5BB9\u9F99\u624B\u6307\u8F7B\u8F7B\u4E00\u6311\uFF0C\u53EA\u5269\u4E00\u70B9\u76AE\u8089\u76F8\u8FDE\u7684\u4E73\u5C16\u98A4\u6296\u7740\u7FFB\u843D\u4E0B\u6765\uFF0C\u4E4D\u7136\u770B\u53BB\u575A\u633A\u7684\u7389\u4E73\u5B9B\u5982\u5E73\u6614\u3002</p><p>\u5E7D\u6697\u7684\u5927\u6BBF\u4E2D\uFF0C\u660F\u53A5\u7684\u5973\u4F53\u4E0D\u4F4F\u75C9\u631B\u3002\u6BB7\u7EA2\u7684\u4E73\u5C16\u54C6\u55E6\u7740\u60AC\u5728\u4E73\u524D\uFF0C\u4ECE\u4E2D\u6D8C\u51FA\u7684\u9C9C\u8840\u67D3\u7EA2\u4E86\u534A\u4E2A\u8EAB\u5B50\u3002</p><p>\u6155\u5BB9\u9F99\u633A\u7740\u6EF4\u8840\u7684\u9633\u5177\u7AD9\u7ACB\u5728\u6BBF\u4E2D\uFF0C\u7709\u5934\u5FAE\u6311\uFF0C\u50B2\u7136\u770B\u7740\u8138\u8272\u60E8\u767D\u7684\u73AB\u7470\u4ED9\u5B50\u3002</p><p>\u300C\u5982\u679C\u4F60\u4E0D\u662F\u6211\u4EB2\u59B9\u59B9\uFF0C\u54E5\u54E5\u65E9\u5C31\u628A\u4F60\u64CD\u7684\u6B7B\u53BB\u6D3B\u6765\u4E86\uFF01\u2026\u2026\u53EA\u8981\u4F60\u7ED9\u54E5\u54E5\u751F\u5B69\u5B50\uFF0C\u4E56\u4E56\u542C\u8BDD\uFF0C\u54E5\u54E5\u4E00\u5B9A\u4F1A\u597D\u597D\u5BF9\u4F60\u2026\u2026\u300D</p><p>\u6BBF\u95E8\u5FAE\u5F00\uFF0C\u55A7\u95F9\u58F0\u4F34\u7740\u9633\u5149\u6D8C\u5165\u5927\u6BBF\u3002\u4E00\u4E2A\u4EBA\u6B65\u5C65\u4E0D\u7A33\u5730\u8D70\u4E86\u8FDB\u6765\uFF0C\u53CC\u76EE\u55B7\u706B\u5730\u76EF\u7740\u98CE\u665A\u534E\u3002</p>',78);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
