import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_25" tabindex="-1"><a class="header-anchor" href="#_25" aria-hidden="true">#</a> 25</h1><p>\u6155\u5BB9\u7D2B\u73AB\u5624\u549B\u4E00\u58F0\uFF0C\u957F\u957F\u7684\u776B\u6BDB\u7F13\u7F13\u5F20\u5F00\uFF0C\u53C8\u88AB\u9633\u5149\u523A\u75DB\uFF0C\u8FDE\u5FD9\u5408\u7D27\u3002\u7247\u523B\u540E\u5979\u5FAE\u5FAE\u772F\u7740\u773C\uFF0C\u8FF7\u60D8\u5730\u770B\u7740\u9636\u4E0B\u55A7\u95F9\u7684\u4EBA\u7FA4\u3002</p><p>\u767E\u4F59\u540D\u6C49\u5B50\u5206\u6210\u4E24\u7FA4\uFF0C\u5404\u56F4\u6210\u4E00\u5708\u3002\u5DE6\u8FB9\u4EBA\u7FA4\u4E2D\u4E00\u4E2A\u76AE\u80A4\u767D\u7699\u7684\u5973\u5B50\u624B\u819D\u6491\u5730\uFF0C\u8DE8\u5750\u5728\u4E00\u4E2A\u7537\u4EBA\u8170\u4E0A\uFF0C\u66FC\u5999\u7684\u8EAB\u4F53\u7AED\u529B\u8D77\u4F0F\u3002\u957F\u53D1\u8986\u9762\uFF0C\u770B\u4E0D\u6E05\u5BB9\u8C8C\u3002</p><p>\u300C\u62AC\u8D77\u6765\uFF0C\u62AC\u8D77\u6765\uFF0C\u8BA9\u5927\u4F19\u770B\u6E05\u4E9B\u2026\u2026\u300D\u8F70\u7B11\u4E2D\u6709\u4EBA\u558A\u9053\u3002</p><p>\u90A3\u5973\u5B50\u8FDE\u5FD9\u4E0A\u8EAB\u540E\u4EF0\uFF0C\u67D4\u81C2\u6491\u5728\u80CC\u540E\uFF0C\u8BA9\u56F4\u89C2\u8005\u770B\u6E05\u4EA4\u5408\u7684\u90E8\u4F4D\u3002\u7389\u6237\u5149\u6DA6\u5982\u8102\uFF0C\u6CA1\u6709\u4E00\u6839\u6BDB\u53D1\uFF0C\u67D4\u7F8E\u7684\u82B1\u74E3\u4E0A\u4E0B\u8D77\u843D\uFF0C\u6CBE\u6EE1\u6DEB\u6C34\u9633\u7CBE\u3002\u79C0\u53D1\u6296\u52A8\u7740\u62AB\u6563\u5F00\u6765\uFF0C\u7EAA\u7709\u59A9\u5F2F\u7709\u98A6\u7D27\uFF0C\u54AC\u7740\u7EA2\u5507\uFF0C\u5C48\u8FB1\u800C\u53C8\u65E0\u5948\u5730\u7528\u6ED1\u817B\u6E29\u6DA6\u7684\u8089\u7A74\u6765\u4F8D\u5949\u8EAB\u4E0B\u7684\u964C\u751F\u4EBA\u3002</p><p>\u7D2B\u73AB\u8EAB\u4F53\u6296\u4E86\u4E00\u4E0B\uFF0C\u7F8E\u76EE\u987F\u65F6\u6EA2\u51FA\u6E05\u6CEA\u3002\u5979\u6025\u5FD9\u8F6C\u8FC7\u5934\uFF0C\u770B\u5411\u53F3\u4FA7\u3002\u5AC2\u5AC2\u4ECD\u548C\u6628\u665A\u4E00\u6837\u8DEA\u4F0F\u5728\u5730\uFF0C\u4E0D\u540C\u7684\u662F\u5979\u8EAB\u4E0B\u591A\u4E86\u4E00\u6761\u9EDD\u9ED1\u7684\u94C1\u94FE\u3002\u968F\u7740\u81C0\u540E\u7684\u633A\u52A8\uFF0C\u94C1\u94FE\u88AB\u62C9\u5F97\u7B14\u76F4\uFF0C\u94EE\u7136\u4F5C\u54CD\u3002\u6BCF\u6B21\u62C9\u76F4\uFF0C\u6797\u9999\u8FDC\u7684\u8EAB\u4F53\u5C31\u4F1A\u98A4\u6296\u4E00\u4E0B\uFF0C\u7389\u6307\u731B\u7136\u62E7\u7D27\u3002</p><p>\u6155\u5BB9\u7D2B\u73AB\u624B\u81C2\u4E0D\u52A8\uFF0C\u5355\u9760\u624B\u6307\u89E3\u4E0B\u94DC\u5236\u7684\u8170\u73AF\uFF0C\u5957\u5728\u638C\u4E0A\u63E1\u7D27\u3002\u5F85\u7EAA\u7709\u59A9\u88AB\u4EBA\u63EA\u7740\u4E73\u5934\u63D0\u8D77\u6765\uFF0C\u4EBA\u7FA4\u66B4\u53D1\u51FA\u4E00\u9635\u8F70\u7B11\u65F6\uFF0C\u7A81\u7136\u671D\u8EAB\u540E\u5212\u53BB\u3002</p><p>\u6155\u5BB9\u9F99\u62B1\u7740\u59B9\u59B9\u6B63\u770B\u5F97\u5F00\u5FC3\uFF0C\u731D\u4E0D\u53CA\u9632\u624B\u81C2\u7ACB\u65F6\u88AB\u5212\u51FA\u4E00\u9053\u5BF8\u8BB8\u957F\u7684\u4F24\u53E3\u3002</p><p>\u4ED6\u5927\u60CA\u5931\u8272\uFF0C\u624B\u81C2\u4E00\u6296\uFF0C\u8FDE\u5FD9\u5C06\u7D2B\u73AB\u629B\u51FA\u3002</p><p>\u5A07\u5C0F\u7684\u8EAB\u4F53\u5728\u7A7A\u4E2D\u8F7B\u76C8\u5730\u7FFB\u4E86\u4E24\u4E2A\u65A4\u6597\uFF0C\u501F\u529B\u63A0\u5230\u5341\u4E08\u5916\u7684\u5927\u6811\u4E0A\uFF0C\u65CB\u5373\u4E00\u8DC3\uFF0C\u6D88\u5931\u5728\u5BC6\u6797\u4E2D\u3002</p><p>\u6155\u5BB9\u7D2B\u73AB\u6CA1\u6709\u4E00\u6BEB\u8131\u79BB\u9B54\u638C\u7684\u559C\u60A6\u3002\u4E09\u4F4D\u5E08\u59D0\u65E0\u4E0D\u8EAB\u8D1F\u7EDD\u6280\uFF0C\u8C8C\u7F8E\u5982\u82B1\uFF0C\u5728\u6C5F\u6E56\u4E2D\u5F15\u6765\u591A\u5C11\u7FA1\u6155\u7684\u76EE\u5149\u3002\u53EF\u5728\u8FD9\u91CC\u5374\u88AB\u4EBA\u5F53\u6210\u73A9\u7269\u822C\u5C3D\u60C5\u51CC\u8650\uFF0C\u8FD9\u4E9B\u79BD\u517D\u54EA\u91CC\u8FD8\u6709\u4E00\u70B9\u4EBA\u6027\uFF01</p><p>\u8EAB\u540E\u53C8\u54CD\u8D77\u5C16\u9510\u7684\u5578\u58F0\uFF0C\u6563\u5C45\u5C9B\u5185\u7684\u661F\u6708\u6E56\u5E2E\u4F17\u5373\u523B\u4FBF\u4F1A\u5C01\u9501\u9053\u8DEF\u3002\u7D2B\u73AB\u4E00\u8FB9\u98DE\u9A70\uFF0C\u4E00\u8FB9\u6DF1\u6DF1\u5438\u4E86\u53E3\u6C14\uFF0C\u8BD5\u56FE\u538B\u4E0B\u7EB7\u4E71\u7684\u60C5\u7EEA\u3002</p><p>\u7EA4\u8DB3\u4E00\u70B9\uFF0C\u6A2A\u8FC7\u4E94\u4E08\u7684\u8DDD\u79BB\u3002\u7D2B\u73AB\u8F7B\u529F\u5C3D\u5C55\uFF0C\u4E00\u4E3E\u8D8A\u8FC7\u4E86\u4E24\u68F5\u5927\u6811\uFF0C\u843D\u5728\u679D\u95F4\u3002</p><p>\u4E00\u8F6C\u773C\uFF0C\u6B63\u770B\u5230\u6628\u591C\u653E\u5728\u6811\u6D1E\u4E2D\u7684\u817F\u9AA8\u3002\u767D\u68EE\u68EE\u7684\u9AA8\u9AB8\u4E0A\u8FD8\u5E26\u7740\u4E1D\u4E1D\u8840\u8FF9\uFF0C\u886C\u5728\u7EFF\u679D\u7FE0\u53F6\u95F4\uFF0C\u5206\u5916\u8BE1\u5F02\u3002\u7D2B\u73AB\u5FC3\u795E\u4E00\u52A8\uFF0C\u4F38\u624B\u62FF\u8D77\u817F\u9AA8\u3002\u6B63\u5F85\u98DE\u8EAB\u8DC3\u8D77\uFF0C\u5FFD\u7136\u80C1\u4E0B\u4E00\u75DB\u3002</p><p>\u7EFF\u53F6\u95F4\u65E0\u58F0\u65E0\u5730\u6D6E\u73B0\u51FA\u4E00\u5F20\u67AF\u7626\u7684\u9762\u5B54\uFF0C\u6C90\u58F0\u4F20\u6162\u541E\u541E\u6258\u4F4F\u7D2B\u73AB\uFF0C\u9012\u7ED9\u8D76\u6765\u7684\u6155\u5BB9\u9F99\u3002</p><p>\u7D2B\u73AB\u7F8E\u76EE\u55B7\u706B\uFF0C\u53EB\u9053\uFF1A\u300C\u6740\u4E86\u6211\u5427\uFF01\u300D</p><p>\u6155\u5BB9\u9F99\u4ECE\u5979\u624B\u4E2D\u53D6\u4E0B\u817F\u9AA8\uFF0C\u5728\u5979\u8138\u4E0A\u78E8\u64E6\u7740\u5BD2\u58F0\u9053\uFF1A\u300C\u843D\u5230\u8FD9\u91CC\u7684\u5973\u4EBA\uFF0C\u80FD\u6B7B\u90A3\u662F\u6700\u5927\u7684\u798F\u6C14\u2026\u2026\u300D</p><p>\u6BBF\u524D\u4E00\u7247\u9759\u9ED8\uFF0C\u770B\u5230\u5BAB\u4E3B\u62B1\u7740\u73AB\u7470\u4ED9\u5B50\u56DE\u6765\uFF0C\u5E2E\u4F17\u90FD\u677E\u4E86\u53E3\u6C14\u3002</p><p>\u6155\u5BB9\u9F99\u547D\u4EBA\u642C\u6765\u5B9D\u5EA7\uFF0C\u62E5\u7740\u7D2B\u73AB\u659C\u8EBA\u6905\u4E2D\uFF0C\u6DE1\u6DE1\u9053\uFF1A\u300C\u628A\u6D41\u971C\u5251\u5E26\u8FC7\u6765\uFF0C\u5927\u4F19\u770B\u770B\u970D\u957F\u8001\u600E\u4E48\u7ED9\u5979\u5F00\u82DE\u2014\u2014\u6797\u5A4A\u5B50\u778E\u4E86\u773C\uFF0C\u7EAA\u5A4A\u5B50\uFF0C\u4F60\u7ED9\u5979\u8BF4\u7740\u4E9B\u3002\u300D</p><p>\u7EAA\u7709\u59A9\u76F4\u76F4\u8DEA\u5728\u5730\u4E0A\uFF0C\u67D4\u5A9A\u7684\u8EAB\u4F53\u5FAE\u5FAE\u98A4\u6296\u3002</p><p>\u4E24\u540D\u7D2B\u8863\u4EBA\u62FD\u7740\u94C1\u7D22\uFF0C\u5C06\u98CE\u665A\u534E\u62C9\u5230\u6BBF\u524D\u3002\u770B\u5230\u8FD9\u4E2A\u540D\u9707\u6C5F\u6E56\u7684\u5973\u4FA0\uFF0C\u4F17\u4EBA\u90FD\u53D1\u51FA\u4E00\u58F0\u60CA\u53F9\uFF0C\u7FD8\u9996\u671B\u7740\u5979\u90A3\u9AD8\u8038\u7684\u4E73\u623F\u3002\u65B9\u624D\u53F6\u884C\u5357\u6D82\u4E86\u836F\u540E\uFF0C\u9C9C\u8840\u5DF2\u7ECF\u6B62\u4F4F\uFF0C\u53EA\u7559\u4E0B\u4E00\u4E2A\u8840\u7EA2\u7684\u8089\u6D1E\u3002</p><p>\u7B49\u4E86\u7247\u523B\u8FD8\u4E0D\u89C1\u970D\u72C2\u7130\u7684\u8E2A\u5F71\uFF0C\u6155\u5BB9\u9F99\u4E0D\u8010\u70E6\u8D77\u6765\uFF0C\u628A\u6155\u5BB9\u80DC\u7684\u817F\u9AA8\u817F\u5F80\u7EAA\u7709\u59A9\u9762\u524D\u4E00\u4E22\uFF0C\u300C\u6345\u51E0\u4E0B\uFF0C\u8BA9\u4E3B\u5B50\u4EEC\u4E50\u4E50\u3002\u300D</p><p>\u7EAA\u7709\u59A9\u9ED8\u4E0D\u4F5C\u58F0\u7684\u62FF\u8D77\u817F\u9AA8\uFF0C\u4EF0\u8EAB\u8EBA\u5728\u5927\u7406\u77F3\u9636\u4E0A\uFF0C\u4E24\u817F\u7B14\u76F4\u5206\u5F00\uFF0C\u628A\u7C97\u5927\u7684\u9AA8\u8282\u5BF9\u51C6\u4E0B\u4F53\u6162\u6162\u6345\u5165\u3002\u867D\u7136\u8089\u7A74\u5DF2\u88AB\u8E42\u8E8F\u65E0\u6570\u6B21\uFF0C\u4F46\u8FD8\u662F\u65E0\u6CD5\u5BB9\u7EB3\u4E0B\u62F3\u5934\u5927\u5C0F\u7684\u9AA8\u8282\u3002\u767D\u82B1\u82B1\u7684\u9AA8\u5934\u5C06\u6ED1\u817B\u7684\u82B1\u74E3\u6324\u6210\u8584\u8584\u4E00\u5708\u7EA2\u8089\uFF0C\u7EAA\u7709\u59A9\u54AC\u7D27\u7259\u5173\uFF0C\u7528\u529B\u4E00\u9001\u3002\u75DB\u53EB\u58F0\u4E2D\uFF0C\u9AA8\u8282\u500F\u5FFD\u6CA1\u5165\u8089\u7A74\u3002\u4E0D\u7B49\u75BC\u75DB\u7A0D\u7F13\uFF0C\u7EAA\u7709\u59A9\u7ACB\u5373\u62BD\u9001\u8D77\u6765\u3002</p><p>\u5A07\u602F\u602F\u7684\u8C6A\u95E8\u5343\u91D1\u7ADF\u7136\u5728\u5149\u5929\u5316\u65E5\u4E0B\u7528\u4EBA\u9AA8\u81EA\u6170\uFF0C\u4F17\u4EBA\u90FD\u770B\u5F97\u76EE\u4E0D\u8F6C\u775B\uFF0C\u6B32\u706B\u9AD8\u70BD\u3002\u6155\u5BB9\u7D2B\u73AB\u5374\u60F3\u8D77\u4E09\u5E08\u59D0\u5F53\u65E5\u7684\u60C5\u666F\u3002\u7EAA\u5E08\u59D0\u597D\u6D01\u6210\u7656\uFF0C\u7528\u810F\u7684\u624B\u7EE2\u5B81\u80AF\u4E22\u5F03\u4E5F\u4E0D\u613F\u968F\u8EAB\u643A\u5E26\u3002\u53EF\u73B0\u5728\u2026\u2026</p><p>\u6155\u5BB9\u9F99\u81C2\u4E0A\u4F24\u53E3\u9690\u9690\u4F5C\u75DB\uFF0C\u4F4E\u5934\u770B\u7740\u7D2B\u73AB\uFF0C\u5BD2\u58F0\u9053\uFF1A\u300C\u6797\u5A4A\u5B50\u7684\u7537\u4EBA\uFF0C\u4F60\u4E5F\u4E0D\u80FD\u72EC\u541E\uFF0C\u7ED9\u4F60\u5E08\u59D0\u5206\u4E00\u534A\u3002\u300D</p><p>\u7D2B\u73AB\u5A07\u8EAF\u4E00\u9707\uFF0C\u7247\u523B\u540E\u62AC\u8D77\u4FCF\u8138\uFF0C\u773C\u6CEA\u6C6A\u6C6A\u5730\u8BF4\uFF1A\u300C\u4E0D\u8981\u6298\u78E8\u6211\u5AC2\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u9762\u9732\u6740\u6C14\uFF0C\u4E00\u53E3\u622A\u65AD\u5979\u7684\u8BDD\uFF0C\u300C\u4F60\u54EA\u513F\u6765\u7684\u5AC2\u5AC2\uFF1F\u4F60\u54E5\u54E5\u662F\u6211\uFF01\u4F60\u5AC2\u5AC2\u5C31\u662F\u4F60\uFF01\u6211\u52A8\u4F60\u4E00\u6839\u6C57\u6BDB\u4E86\u5417\uFF1F\u4E56\u4E56\u770B\u7740\uFF01\u4E0D\u542C\u8BDD\u5C31\u662F\u8FD9\u79CD\u4E0B\u573A\uFF01\u300D</p><p>\u7D2B\u73AB\u5C48\u610F\u54C0\u6C42\u5374\u88AB\u6570\u843D\u4E00\u901A\uFF0C\u7C89\u9762\u6DA8\u5F97\u901A\u7EA2\uFF0C\u5F20\u53E3\u671D\u6155\u5BB9\u9F99\u80A9\u5934\u54AC\u53BB\u3002\u6155\u5BB9\u9F99\u4E00\u52A8\u4E0D\u52A8\uFF0C\u773C\u4E2D\u5374\u7CBE\u5149\u5927\u76DB\u3002\u7D2B\u73AB\u7A74\u9053\u88AB\u5236\uFF0C\u4F7F\u4E0D\u51FA\u529B\u6C14\uFF0C\u54AC\u4E86\u534A\u5929\uFF0C\u6155\u5BB9\u9F99\u7A81\u7136\u5931\u7B11\u9053\uFF1A\u300C\u59B9\u59B9\u5C0F\u5634\u771F\u8212\u670D\uFF0C\u518D\u9017\u6211\uFF0C\u4E0D\u7136\u7B49\u4E0D\u5230\u6210\u4EB2\u54E5\u54E5\u5C31\u8981\u64CD\u4F60\u4E86\u3002\u300D</p><p>\u7D2B\u73AB\u4E00\u60CA\uFF0C\u8FDE\u5FD9\u677E\u53E3\u3002</p><p>\u6155\u5BB9\u9F99\u51B7\u7B11\u4E00\u58F0\uFF0C\u628A\u76EE\u5149\u6295\u5230\u573A\u4E2D\u3002</p><p>\u6797\u9999\u8FDC\u5E76\u6CA1\u6709\u50CF\u5F80\u5E38\u90A3\u6837\u6323\u624E\uFF0C\u4EFB\u5E08\u59B9\u6402\u8D77\u81EA\u5DF1\u7684\u8170\u8EAB\uFF0C\u7AD6\u8D77\u5706\u81C0\u3002\u7EAA\u7709\u59A9\u6389\u7740\u773C\u6CEA\u63E1\u7D27\u9634\u4E2D\u7684\u817F\u9AA8\uFF0C\u62B5\u5728\u5E08\u59D0\u9971\u53D7\u6298\u78E8\u7684\u8089\u7A74\u4E0A\uFF0C\u7F13\u7F13\u633A\u5165\u3002</p><p>\u4EBA\u7FA4\u5916\u4F20\u6765\u4E00\u9635\u9A9A\u52A8\uFF0C\u970D\u72C2\u7130\u6324\u8FDB\u573A\u4E2D\uFF0C\u6740\u6C14\u817E\u817E\u5730\u76EF\u7740\u5730\u4E0A\u7684\u98CE\u665A\u534E\u3002</p><p>\u8DEF\u4E0A\u803D\u6401\u7684\u65F6\u95F4\u592A\u957F\uFF0C\u90A3\u6839\u88AB\u4F55\u5C0F\u82B8\u7EC6\u5FC3\u5305\u597D\u7684\u9E21\u5DF4\u5DF2\u7ECF\u5E72\u762A\u65E0\u6CD5\u590D\u690D\u3002\u970D\u72C2\u7130\u542C\u53F6\u884C\u5357\u8BF4\u5B8C\uFF0C\u5DEE\u70B9\u513F\u6655\u4E86\u8FC7\u53BB\u3002\u7B49\u56DE\u8FC7\u795E\u6765\uFF0C\u7ACB\u5373\u6765\u627E\u98CE\u665A\u534E\u7B97\u5E10\u3002\u5BAB\u4E3B\u5012\u662F\u5F88\u6177\u6168\uFF0C\u65E2\u7136\u5DF2\u5C06\u98CE\u665A\u534E\u7684\u5904\u5B50\u8D4F\u7ED9\u4ED6\uFF0C\u968F\u4ED6\u5982\u4F55\u7834\u5904\u90FD\u884C\u3002</p><p>\u970D\u72C2\u7130\u5F53\u65F6\u5C31\u60F3\u76F4\u63A5\u7528\u624B\u7ED9\u6D41\u971C\u5251\u5F00\u82DE\uFF0C\u53C8\u89C9\u5F97\u592A\u4FBF\u5B9C\u4E86\u5272\u5C4C\u4EC7\u4EBA\uFF0C\u4E8E\u662F\u4FBF\u56DB\u5904\u5BFB\u627E\u5408\u9002\u7684\u4E1C\u897F\u3002</p><p>\u7EAA\u7709\u59A9\u505C\u4F4F\u4E86\u52A8\u4F5C\uFF0C\u7247\u523B\u540E\u5FFD\u7136\u98A4\u6296\u8D77\u6765\uFF0C\u300C\u4ED6\u2026\u2026\u4ED6\u4EEC\u2026\u2026\u7275\u6765\u2026\u2026\u4E00\u5934\u91CE\u732A\u2026\u2026\u300D</p><p>\u970D\u72C2\u7130\u54AC\u7259\u5207\u9F7F\u5730\u8BF4\uFF1A\u300C\u8FD9\u662F\u970D\u7237\u521A\u4ECE\u5C71\u91CC\u902E\u6765\u7684\u3002\u98CE\u665A\u534E\uFF0C\u8BA9\u5934\u91CE\u732A\u7834\u4E86\u8EAB\u5B50\uFF0C\u4F60\u662F\u524D\u65E0\u53E4\u4EBA\u540E\u65E0\u6765\u8005\u3002\u4ECA\u540E\u6B66\u6797\u4E2D\u4EBA\u8C08\u8D77\u6765\u5B9A\u4F1A\u5927\u5927\u7684\u53EB\u58F0\u597D\uFF01\u300D</p><p>\u7EAA\u7709\u59A9\u54FD\u54BD\u7740\u8BF4\uFF1A\u300C\u4ED6\u4EEC\u628A\u98CE\u5E08\u59D0\u6309\u5728\u5730\u4E0A\u2026\u2026\u98CE\u5E08\u59D0\u624B\u4E0A\u6709\u4F24\u2026\u2026\u4ED6\u4EEC\u628A\u91CE\u732A\u7275\u8FC7\u6765\u4E86\u2026\u2026\u300D</p><p>\u5904\u5B50\u7684\u5E7D\u5904\u4EBA\u88AB\u7C97\u66B4\u5730\u63B0\u5F00\uFF0C\u82B1\u74E3\u4E0B\u9732\u51FA\u4E2A\u7EC6\u7EC6\u7684\u6D1E\u53E3\uFF0C\u7EA2\u7389\u822C\u7EC6\u817B\u52A8\u4EBA\u3002</p><p>\u98CE\u665A\u534E\u795E\u60C5\u60E8\u6DE1\uFF0C\u5E73\u5766\u7684\u5C0F\u8179\u5FAE\u5FAE\u7EF7\u7D27\uFF0C\u88AB\u5206\u5F00\u6210\u76F4\u7EBF\u7684\u7389\u817F\u7EB9\u4E1D\u4E0D\u52A8\u3002</p><p>\u65C1\u8FB9\u7684\u5E2E\u4F17\u62E8\u5F04\u51E0\u4E0B\uFF0C\u91CE\u732A\u7684\u8089\u68D2\u4FBF\u7F13\u7F13\u633A\u76F4\uFF0C\u9633\u5177\u72B6\u5982\u94C1\u9525\uFF0C\u9876\u7AEF\u5C16\u9510\uFF0C\u4E2D\u90E8\u9F13\u8D77\u4E24\u5BF8\u7C97\u7EC6\uFF0C\u751F\u94C1\u94F8\u6210\u822C\u7C97\u9ED1\u72F0\u72DE\u3002\u5B83\u4F53\u5F62\u6BD4\u666E\u901A\u5BB6\u732A\u5927\u5F97\u591A\uFF0C\u904D\u4F53\u9ED1\u8272\u7684\u9B03\u6BDB\uFF0C\u50CF\u4E00\u6839\u6839\u76F4\u7AD6\u7684\u94A2\u9488\u3002\u4E24\u6839\u96EA\u4EAE\u7684\u7360\u7259\u4ECE\u957F\u5634\u4E2D\u6311\u51FA\uFF0C\u53CC\u76EE\u8840\u7EA2\uFF0C\u4E0D\u65F6\u53D1\u51FA\u4F4E\u54D1\u7684\u5636\u9E23\u3002</p><p>\u970D\u72C2\u7130\u6258\u8D77\u98CE\u665A\u534E\u7684\u96EA\u81C0\u9001\u5230\u91CE\u732A\u8179\u4E0B\uFF0C\u4E00\u624B\u63E1\u8D77\u91CE\u732A\u7684\u9633\u5177\uFF0C\u72DE\u7B11\u7740\u9053\uFF1A\u300C\u98CE\u5973\u4FA0\uFF0C\u4F60\u732A\u8001\u516C\u8981\u7ED9\u4F60\u5F00\u82DE\u4E86\u3002\u300D</p><p>\u4E00\u6839\u575A\u786C\u7684\u8089\u68CD\uFF0C\u7F13\u7F13\u6345\u5165\u4ECE\u672A\u88AB\u4EBA\u78B0\u8FC7\u7684\u795E\u79D8\u4E4B\u5904\u3002\u6ED1\u817B\u7684\u5AE9\u8089\u6E10\u6E10\u5730\u5206\u5F00\u2014\u2014\u300C\u8FDB\u6765\u4E86\u2026\u2026\u300D\u98CE\u665A\u534E\u5FC3\u91CC\u4F4E\u53EB\u4E00\u58F0\u3002\u4E8C\u5341\u4F59\u5E74\u5B88\u8EAB\u5982\u7389\uFF0C\u5374\u8981\u88AB\u4E00\u5934\u91CE\u732A\u7834\u53BB\u5904\u5B50\u4E4B\u8EAF\uFF0C\u5979\u518D\u575A\u5F3A\u4E5F\u4E0D\u7981\u5FC3\u5934\u6EF4\u8840\u3002</p><p>\u5168\u573A\u9759\u9ED8\uFF0C\u8FDE\u547C\u5438\u58F0\u90FD\u505C\u987F\u4E86\u3002</p><p>\u4E4C\u9ED1\u7684\u91CE\u732A\u8EAB\u4E0B\u9732\u51FA\u4E24\u6761\u5149\u6DA6\u7684\u7389\u817F\uFF0C\u817F\u95F4\u7684\u79D8\u5904\u6B63\u5BF9\u7740\u91CE\u732A\u80EF\u95F4\u3002\u96EA\u767D\u7684\u8EAB\u4F53\u7F13\u7F13\u5347\u8D77\uFF0C\u7528\u5904\u5B50\u82AC\u82B3\u7684\u7389\u6237\u8FCE\u5411\u91CE\u517D\u7684\u6027\u5668\u3002</p><p>\u970D\u72C2\u7130\u76EF\u7740\u98CE\u665A\u534E\u98A4\u6296\u7684\u7EA2\u5507\uFF0C\u7528\u529B\u4E00\u63A8\u3002\u517D\u6839\u7ACB\u523B\u6495\u88C2\u67D4\u97E7\u7684\u8584\u819C\uFF0C\u8FDB\u5165\u6E29\u6DA6\u6ED1\u817B\u7684\u8089\u7A74\u5185\u3002\u4E00\u80A1\u9C9C\u8840\u4ECE\u82B1\u74E3\u8FB9\u7F18\u6DCC\u51FA\uFF0C\u8273\u7EA2\u593A\u76EE\u3002</p><p>\u98CE\u665A\u534E\u95F7\u54FC\u4E00\u58F0\uFF0C\u7389\u4F53\u5267\u98A4\uFF0C\u70ED\u6CEA\u593A\u7736\u800C\u51FA\u3002</p><p>\u5468\u56F4\u7684\u4EBA\u7FA4\u55E1\u7684\u4E00\u58F0\u70B8\u5F00\u4E86\uFF0C\u541E\u54BD\u58F0\u3001\u5567\u5567\u58F0\u3001\u8C03\u7B11\u58F0\u3001\u53F9\u6C14\u58F0\u54CD\u6210\u4E00\u7247\u3002</p><p>\u300C\u771F\u662F\u5904\u5973\u5462\uFF0C\u64CD\u51FA\u8840\u4E86\u2026\u2026\u300D</p><p>\u300C\u563F\uFF01\u6D41\u773C\u6CEA\u2014\u2014\u4F60\u77A7\uFF01\u5927\u540D\u9F0E\u9F0E\u7684\u6D41\u971C\u5251\u98CE\u5973\u4FA0\uFF0C\u4ED6\u5988\u7684\u8BA9\u5934\u91CE\u732A\u64CD\u5F97\u6D41\u773C\u6CEA\u4E86\u2026\u2026\u300D</p><p>\u300C\u5E9F\u8BDD\uFF01\u5C44\u90FD\u88AB\u91CE\u732A\u64CD\u4E86\uFF0C\u8FD8\u80FD\u4E0D\u6D41\u6CEA\u5417\uFF1F\u300D</p><p>\u300C\u5567\u5567\u2026\u2026\u5904\u5973\u554A\uFF0C\u5C31\u8FD9\u4E48\u5B8C\u4E86\u2026\u2026\u300D</p><p>\u300C\u8D76\u660E\u513F\u95EE\u95EE\u6C5F\u6E56\u91CC\u7684\u4EBA\uFF0C\u98D8\u6885\u5CF0\u5927\u5F1F\u5B50\uFF0C\u5728\u54B1\u4EEC\u773C\u76AE\u5E95\u4E0B\u8BA9\u91CE\u732A\u7ED9\u64CD\u4E86\uFF0C\u6253\u6B7B\u4ED6\u4EEC\u90FD\u4E0D\u4F1A\u4FE1\uFF01\u300D</p><p>\u970D\u72C2\u7130\u54C8\u54C8\u5927\u7B11\uFF0C\u6258\u7740\u98CE\u665A\u534E\u7684\u8170\u81C0\u4E0D\u4F4F\u633A\u52A8\u3002\u90A3\u5934\u91CE\u732A\u4E5F\u5C1D\u5230\u8089\u7A74\u7684\u7F8E\u5999\uFF0C\u4E00\u8FB9\u54FC\u54FC\uFF0C\u4E00\u8FB9\u62BD\u9001\u3002</p><p>\u53EA\u6709\u98D8\u6885\u5CF0\u56DB\u5927\u5F1F\u5B50\u9ED8\u9ED8\u6D41\u6CEA\u3002</p><p>\u8D70\u5230\u8FD1\u5904\u89C2\u770B\u7684\u6155\u5BB9\u9F99\u6325\u624B\u7ED9\u4E86\u7EAA\u7709\u59A9\u4E00\u4E2A\u8033\u5149\uFF0C\u300C\u8BF4\u554A\uFF01\u4E0D\u77E5\u9053\u4F60\u6797\u5E08\u59D0\u662F\u4E2A\u778E\u5B50\uFF01\u300D</p><p>\u7EAA\u7709\u59A9\u54ED\u5F97\u4E0A\u6C14\u4E0D\u63A5\u4E0B\u6C14\uFF0C\u5598\u7740\u6C14\u9053\uFF1A\u300C\u98CE\u5E08\u59D0\u2026\u2026\u98CE\u5E08\u59D0\u2026\u2026\u6D41\u4E86\u597D\u591A\u8840\u2026\u2026\u4ED6\u4EEC\u2026\u2026\u8FD8\u5728\u63A8\u2026\u2026\u300D</p>',56);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
