import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_26" tabindex="-1"><a class="header-anchor" href="#_26" aria-hidden="true">#</a> 26</h1><p>\u300C\u4E03\u3001\u516B\u3001\u4E5D\u2026\u2026\u300D\u4E24\u540D\u706B\u5802\u5E2E\u4F17\u5404\u62B1\u7740\u98CE\u665A\u534E\u4E00\u6761\u7389\u817F\uFF0C\u4E00\u624B\u6258\u7D27\u817F\u6839\u628A\u5979\u80A1\u95F4\u62B5\u5728\u91CE\u732A\u8179\u4E0B\uFF0C\u4E00\u624B\u6309\u7740\u811A\u8E1D\uFF0C\u558A\u7740\u6570\u540C\u65F6\u5411\u4E0B\u538B\u3002\u4FEE\u957F\u5149\u6D01\u7684\u53CC\u817F\u5728\u91CE\u732A\u8EAB\u4FA7\u65F6\u5F00\u65F6\u5408\uFF0C\u5F20\u5F00\u65F6\u7389\u6237\u655E\u9732\u5347\u8D77\uFF0C\u517D\u6839\u6DF1\u5165\u4F53\u5185\u3002</p><p>\u6D41\u971C\u5251\u957F\u53D1\u59D4\u5730\uFF0C\u80A9\u4E0A\u7684\u94C1\u94FE\u62D6\u5728\u77F3\u9762\u4E0A\u300C\u5802\u5577\u5802\u5577\u300D\u54CD\u4E2A\u4E0D\u505C\u3002\u4E00\u65C1\u6797\u9999\u8FDC\u548C\u7EAA\u7709\u59A9\u4E5F\u662F\u7389\u4F53\u6A2A\u9648\uFF0C\u88AB\u6570\u767E\u540D\u6076\u6C49\u56F4\u7740\u8086\u610F\u51CC\u8FB1\u3002</p><p>\u5C60\u6000\u6C89\u4F24\u52BF\u81F3\u4ECA\u672A\u7652\uFF0C\u91D1\u5F00\u7532\u548C\u6C34\u67D4\u4ED9\u90FD\u672A\u9732\u9762\uFF0C\u6C90\u58F0\u4F20\u8FDC\u8FDC\u5750\u5728\u6811\u836B\u4E2D\uFF0C\u770B\u4E0D\u6E05\u4ED6\u7684\u795E\u8272\uFF0C\u53EA\u6709\u7EA2\u888D\u5927\u8896\u7684\u970D\u72C2\u7130\u72E0\u547D\u6298\u78E8\u7740\u98CE\u665A\u534E\uFF0C\u72DE\u6076\u7684\u9762\u5BB9\u56E0\u4EC7\u6068\u800C\u53D8\u5F62\u3002</p><p>\u534A\u4E2A\u65F6\u8FB0\u540E\uFF0C\u633A\u5F04\u5343\u4F59\u6B21\u7684\u91CE\u732A\u7EC8\u4E8E\u5267\u70C8\u5730\u55B7\u5C04\u8D77\u6765\u3002\u4E24\u540D\u5E2E\u4F17\u4E3E\u7740\u98CE\u665A\u534E\u6DCC\u8840\u7684\u8089\u7A74\u7ED5\u573A\u5C55\u793A\uFF0C\u8BA9\u4F17\u4EBA\u770B\u6E05\u6D41\u971C\u5251\u4F53\u5185\u7684\u517D\u7CBE\u3002</p><p>\u66AE\u8272\u6E10\u6D53\uFF0C\u4EBA\u7FA4\u4E2D\u4E09\u5177\u53D7\u5C3D\u51CC\u8FB1\u7684\u96EA\u767D\u8089\u4F53\u6162\u6162\u53D8\u5F97\u6726\u80E7\uFF0C\u53EA\u5269\u4E0B\u4E00\u7247\u6A21\u6A21\u7CCA\u7CCA\u7684\u5AE9\u767D\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u624B\u6307\u629A\u6478\u7740\u7EA2\u5507\u67D4\u7F8E\u7684\u8F6E\u5ED3\uFF0C\u6155\u5BB9\u9F99\u5BF9\u7D2B\u73AB\u67D4\u58F0\u8BF4\u9053\uFF1A\u300C\u98D8\u6885\u5CF0\u679C\u7136\u4F73\u4E3D\u5982\u4E91\uFF0C\u6D41\u971C\u5251\u3001\u5BD2\u6708\u5200\u548C\u7275\u4E1D\u624B\u90FD\u662F\u4E07\u91CC\u6311\u4E00\u7684\u7F8E\u4EBA\u513F\u3002\u5982\u4ECA\u4E0E\u54B1\u4EEC\u661F\u6708\u6E56\u7ED3\u4EB2\uFF0C\u51E0\u767E\u591A\u4EBA\u64CD\u5979\u4EEC\u4E09\u4E2A\uFF0C\u4E5F\u4E0D\u8D1F\u4E86\u4E0A\u82CD\u8D50\u4E88\u8FD9\u51E0\u4F4D\u7684\u8EAB\u4F53\u2026\u2026\u300D</p><p>\u4E09\u540D\u6C49\u5B50\u62B5\u4F4F\u7EAA\u7709\u59A9\u4E0A\u4E0B\u4E09\u4E2A\u6D1E\u7A74\u540C\u65F6\u8FDB\u5165\uFF0C\u6B66\u529F\u88AB\u5E9F\u7684\u7EAA\u5E08\u59D0\u6BEB\u65E0\u53CD\u6297\u4E4B\u529B\uFF0C\u53EA\u80FD\u6EE1\u8138\u75DB\u82E6\u5730\u627F\u53D7\u8FD9\u4E00\u5207\u3002\u7D2B\u73AB\u5A07\u8EAF\u8F7B\u8F7B\u4E00\u98A4\uFF0C\u628A\u8138\u57CB\u5728\u6155\u5BB9\u9F99\u80F8\u524D\uFF0C\u5475\u6C14\u5982\u5170\u7684\u8F7B\u58F0\u8BF4\uFF1A\u300C\u4ED6\u4EEC\u597D\u91CE\u86EE\u2026\u2026\u54E5\u54E5\uFF0C\u4F60\u653E\u8FC7\u5979\u4EEC\u597D\u5417\uFF1F\u300D</p><p>\u6155\u5BB9\u9F99\u542C\u5230\u8FC7\u65E0\u6570\u5973\u5B50\u7684\u54C0\u6C42\u3001\u732E\u5A9A\u3001\u54ED\u53EB\u2026\u2026\u4F46\u8FD9\u58F0\u300C\u54E5\u54E5\u300D\u5374\u4F7F\u4ED6\u5FC3\u5934\u9707\u6C64\u3002\u51DD\u795E\u770B\u4E86\u7D2B\u73AB\u7247\u523B\uFF0C\u7A81\u7136\u53D1\u51FA\u4E00\u58F0\u96C4\u6D51\u7684\u957F\u5578\u3002</p><p>\u4E0B\u9762\u4E00\u4F17\u6C49\u5B50\u7ACB\u523B\u505C\u4F4F\u52A8\u4F5C\uFF0C\u5782\u624B\u542C\u4EE4\u3002\u9707\u8033\u7684\u55A7\u95F9\u563B\u7B11\u5E94\u58F0\u800C\u6B62\uFF0C\u53EA\u5269\u4E0B\u51E0\u540D\u5973\u5B50\u4F4E\u4F4E\u7684\u547B\u541F\u3002</p><p>\u6C90\u58F0\u4F20\u795E\u8272\u4E0D\u53D8\uFF0C\u5FC3\u5E95\u5374\u5FAE\u53F9\u4E00\u58F0\u3002</p><p>\u6155\u5BB9\u9F99\u957F\u58F0\u9053\uFF1A\u300C\u6B64\u756A\u6B7C\u706D\u98D8\u6885\u5CF0\u3001\u4F0F\u9F99\u6DA7\uFF0C\u4E94\u884C\u95E8\u7ACB\u4E0B\u5927\u529F\uFF0C\u5982\u4ECA\u5148\u4EE5\u98D8\u6885\u5CF0\u4E09\u5F92\u7292\u8D4F\u5404\u4F4D\uFF0C\u7A0D\u540E\u518D\u884C\u8BBA\u529F\u884C\u8D4F\u3002\u8BF7\u51E0\u4F4D\u957F\u8001\u5165\u6BBF\u3002\u300D</p><p>\u970D\u72C2\u7130\u559C\u5F62\u4E8E\u8272\uFF0C\u7ACB\u5373\u8DC3\u4E0A\u7389\u9636\uFF0C\u6C90\u58F0\u4F20\u7F13\u7F13\u8D77\u8EAB\uFF0C\u81EA\u6709\u4EBA\u53BB\u901A\u77E5\u5176\u4ED6\u4E09\u4F4D\u957F\u8001\u3002</p><p>\u6155\u5BB9\u9F99\u62B1\u7740\u7D2B\u73AB\u8F6C\u8EAB\u5165\u5185\uFF0C\u5BF9\u65C1\u8FB9\u7684\u7D2B\u8863\u8FD1\u4F8D\u6DE1\u6DE1\u8BF4\u9053\uFF1A\u300C\u98CE\u665A\u534E\u4E0E\u7EAA\u7709\u59A9\u540C\u4F8B\u5904\u7F6E\u3002\u300D</p><p>\u7D2B\u73AB\u4ECE\u80A9\u4FA7\u770B\u5230\u5944\u5944\u4E00\u606F\u7684\u5927\u5E08\u59D0\u4E0E\u4E09\u5E08\u59D0\u88AB\u8FD1\u4F8D\u5E26\u8D70\uFF0C\u7565\u5FAE\u677E\u4E86\u53E3\u6C14\uFF0C\u53C8\u95EE\u9053\uFF1A\u300C\u5AC2\u2026\u2026\u4E8C\u5E08\u59D0\u5462\uFF1F\u300D</p><p>\u6155\u5BB9\u9F99\u773C\u795E\u950B\u5229\u5982\u5200\uFF0C\u300C\u6CA1\u80FD\u4EB2\u624B\u6740\u4E86\u90A3\u4E2A\u6742\u79CD\uFF0C\u5DF2\u7ECF\u4FBF\u5B9C\u4ED6\u4E86\u3002\u96BE\u9053\u8FD8\u653E\u8FC7\u4ED6\u8001\u5A46\uFF1F\u300D</p><p>\u7D2B\u73AB\u521A\u60F3\u5F00\u53E3\uFF0C\u5DF2\u88AB\u6155\u5BB9\u9F99\u62C2\u4F4F\u4E86\u7761\u7A74\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u300C\u5C60\u957F\u8001\u4F24\u52BF\u5982\u4F55\uFF1F\u300D</p><p>\u5C60\u6000\u6C89\u62B1\u62F3\u9053\uFF1A\u300C\u5C5E\u4E0B\u7ECF\u53F6\u62A4\u6CD5\u6551\u6CBB\uFF0C\u5DF2\u65E0\u5927\u788D\uFF0C\u518D\u6709\u534A\u6708\uFF0C\u5373\u53EF\u4E3A\u5BAB\u4E3B\u6548\u529B\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u70B9\u4E86\u70B9\u5934\uFF0C\u53F9\u606F\u9053\uFF1A\u300C\u60F3\u5F53\u5E74\u6211\u661F\u6708\u6E56\u7EB5\u6A2A\u5929\u4E0B\uFF0C\u65E0\u5F80\u4E0D\u5229\u3002\u6B64\u756A\u4EC5\u4EC5\u4E00\u4E2A\u540D\u4E0D\u89C1\u7ECF\u4F20\u7684\u4F0F\u9F99\u6DA7\uFF0C\u4E00\u4E2A\u5C3D\u5C5E\u5973\u6D41\u4E4B\u8F88\u7684\u98D8\u6885\u5CF0\uFF0C\u5C31\u4F7F\u6211\u661F\u6708\u6E56\u635F\u5175\u6298\u5C06\uFF0C\u56DB\u4F4D\u957F\u8001\u8D1F\u4F24\u2026\u2026\u300D</p><p>\u5C60\u970D\u7B49\u4EBA\u9762\u9732\u6127\u610F\uFF0C\u91D1\u5F00\u7532\u8D77\u8EAB\u9053\uFF1A\u300C\u5C5E\u4E0B\u65E0\u80FD\uFF0C\u8BF7\u5BAB\u4E3B\u6CBB\u7F6A\u3002\u300D</p><p>\u4ECE\u6765\u90FD\u9ED8\u4E0D\u4F5C\u58F0\u7684\u6C90\u58F0\u4F20\u5374\u4E00\u53CD\u5E38\u6001\u5730\u5F00\u53E3\u9053\uFF1A\u300C\u98D8\u6885\u5CF0\u540D\u5782\u5929\u4E0B\u8FD1\u767E\u5E74\uFF0C\u81EA\u6709\u8FC7\u4EBA\u4E4B\u5904\u3002\u5BAB\u4E3B\u660E\u9274\u3002\u300D</p><p>\u6C34\u67D4\u4ED9\u8170\u80CC\u5FAE\u5FAE\u4E00\u52A8\uFF0C\u65CB\u5373\u6309\u637A\u4E0B\u6765\u3002\u8DDD\u5BAB\u4E2D\u5927\u4F1A\u8FD8\u6709\u5341\u4F59\u65E5\uFF0C\u8FC7\u65E9\u66B4\u9732\u4E24\u4EBA\u95F4\u7684\u540C\u76DF\u6709\u5F0A\u65E0\u5229\u3002</p><p>\u6155\u5BB9\u9F99\u6DE1\u6DE1\u7B11\u9053\uFF1A\u300C\u6C90\u957F\u8001\u8BF4\u7684\u4E0D\u9519\u3002\u98D8\u6885\u5CF0\u58F0\u52BF\u867D\u4E0D\u53CA\u5927\u5B5A\u7075\u9E6B\u5BFA\u548C\u4E5D\u534E\u5251\u6D3E\uFF0C\u4F46\u5730\u4F4D\u8D85\u5353\uFF0C\u9690\u9690\u4E3A\u767D\u9053\u7B2C\u4E00\u540D\u95E8\uFF0C\u5982\u4ECA\u5F1F\u5B50\u5C3D\u6CA6\u4E3A\u6211\u661F\u6708\u6E56\u5974\u5F79\uFF0C\u53EF\u8C13\u989C\u9762\u5C3D\u4E27\u3002\u5475\u5475\uFF0C\u660E\u65E5\u5BA3\u626C\u5929\u4E0B\uFF0C\u4EE5\u5F70\u6211\u661F\u6708\u6E56\u5A01\u540D\uFF01\u300D</p><p>\u6C34\u67D4\u4ED9\u6C89\u541F\u9053\uFF1A\u300C\u96EA\u5CF0\u795E\u5C3C\u867D\u6781\u5C11\u51FA\u624B\uFF0C\u4F46\u6D41\u971C\u5251\u4E0E\u5BD2\u6708\u5200\u8FD9\u540D\u5F92\u5F1F\u5DF2\u5982\u6B64\u4E86\u5F97\u2026\u2026\u4E0D\u77E5\u6731\u90AA\u62A4\u6CD5\u4E0E\u5C48\u62A4\u6CD5\u53EF\u6709\u6D88\u606F\uFF1F\u300D</p><p>\u6155\u5BB9\u9F99\u773C\u4E2D\u5149\u8292\u4E00\u95EA\uFF0C\u7B11\u9053\uFF1A\u300C\u4E24\u4F4D\u62A4\u6CD5\u8054\u624B\u51FA\u51FB\uFF0C\u4E07\u65E0\u4E00\u5931\uFF0C\u6C34\u957F\u8001\u4E0D\u5FC5\u6302\u6000\u3002\u300D</p><p>\u970D\u72C2\u7130\u8214\u4E86\u8214\u5634\u5507\u72DE\u7B11\u9053\uFF1A\u300C\u542C\u8BF4\u90A3\u4E2A\u9A9A\u5C3C\u59D1\u6F02\u4EAE\u5F97\u7D27\u5462\u2026\u2026\u4E0D\u77E5\u6BD4\u73AB\u2014\u2014</p><p>\u98CE\u5A4A\u5B50\u5979\u4EEC\u5982\u4F55\u3002\u300D\u4ED6\u672C\u60F3\u8BF4\u6BD4\u7D2B\u73AB\u5982\u4F55\uFF0C\u8BDD\u5230\u5634\u8FB9\u60F3\u8D77\u5BAB\u4E3B\u6709\u610F\u5A36\u73AB\u7470\u4ED9\u5B50\uFF0C\u8FDE\u5FD9\u6539\u53E3\u3002</p><p>\u6C34\u67D4\u4ED9\u6697\u76B1\u7709\u5934\uFF0C\u5341\u4F59\u5E74\u6765\u5BAB\u4E2D\u8BAE\u4E8B\u867D\u7136\u4E86\u65E0\u5FCC\u8BB3\uFF0C\u4F46\u9634\u5BAB\u4E3B\u8EAB\u4E3A\u5973\u5B50\uFF0C\u5C11\u6709\u4EBA\u6562\u53E3\u51FA\u4E0D\u656C\u4E4B\u8A00\u3002\u4E0D\u77E5\u65B0\u5BAB\u4E3B\u600E\u4E48\u4F1A\u770B\u4E0A\u8FD9\u4E2A\u8F7B\u72C2\u7C97\u4FD7\u7684\u5BB6\u4F19\u3002</p><p>\u6155\u5BB9\u9F99\u4E0D\u4EE5\u4E3A\u610F\u5730\u8BF4\uFF1A\u300C\u6559\u4E2D\u4E24\u4EEA\u4E4B\u4F4D\u7A7A\u7F6E\u591A\u5E74\uFF0C\u5F85\u4E24\u4F4D\u62A4\u6CD5\u529F\u6210\uFF0C\u5373\u664B\u7EA7\u9634\u9633\u4F7F\u8005\u3002\u300D\u4ED6\u5475\u5475\u4E00\u7B11\uFF0C\u300C\u7A7A\u51FA\u6765\u7684\u4E24\u4F4D\u62A4\u6CD5\uFF0C\u4FBF\u4ECE\u4E94\u4F4D\u957F\u8001\u4E2D\u63A8\u9009\u4E86\u3002\u300D</p><p>\u4F17\u4EBA\u773C\u4E2D\u90FD\u662F\u4E00\u4EAE\uFF0C\u6C34\u67D4\u4ED9\u5374\u60F3\u5230\u8FD9\u80AF\u5B9A\u662F\u4E8C\u6843\u6740\u4E09\u58EB\u7684\u8BA1\u7B56\uFF0C\u4F7F\u4E94\u4EBA\u4E89\u6743\u593A\u5229\u3002</p><p>\u91D1\u5F00\u7532\u8EAB\u4E3A\u957F\u8001\u4E4B\u9996\uFF0C\u53C8\u65A9\u6740\u6155\u5BB9\u80DC\uFF0C\u751F\u64D2\u6797\u9999\u8FDC\uFF0C\u8BAE\u529F\u4E0D\u505A\u7B2C\u4E8C\u4EBA\u60F3\uFF0C\u773C\u89C1\u4F17\u4EBA\u90FD\u65E0\u6CD5\u5F00\u53E3\uFF0C\u4FBF\u9053\uFF1A\u300C\u62A4\u6CD5\u4E43\u6211\u6559\u5C0A\u4F4D\u3002\u5C5E\u4E0B\u7B49\u4EBA\u4E3A\u5BAB\u4E3B\u6548\u529B\uFF0C\u4E0D\u6562\u6709\u975E\u4EFD\u4E4B\u60F3\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u6446\u4E86\u6446\u624B\uFF0C\u300C\u4E0D\u4EC5\u4E24\u4EEA\u4E4B\u4F4D\u8981\u590D\uFF0C\u8FDE\u540C\u56DB\u9547\u795E\u5C06\u3001\u516D\u5408\u4F9B\u5949\u90FD\u8981\u4E00\u5E76\u91CD\u8BBE\u3002\u5982\u4ECA\u5929\u4E0B\u7EB7\u4E89\uFF0C\u6709\u529B\u8005\u7ADE\u76F8\u9010\u9E7F\u4E2D\u539F\uFF0C\u79F0\u738B\u79F0\u5E1D\u3002\u6211\u661F\u6708\u6E56\u7CBE\u82F1\u65E0\u6570\uFF0C\u6240\u5C5E\u5404\u5E2E\u4F1A\u65E0\u4E0D\u627C\u5B88\u8981\u51B2\uFF0C\u5374\u767D\u767D\u6D6A\u8D39\u4E86\u5341\u4F59\u5E74\u7684\u5927\u597D\u65F6\u673A\u2026\u2026\u300D</p><p>\u6B64\u8A00\u4E00\u51FA\uFF0C\u91D1\u5F00\u7532\u3001\u970D\u72C2\u7130\u3001\u5C60\u6000\u6C89\u56FA\u7136\u662F\u546F\u7136\u5FC3\u52A8\uFF0C\u6C34\u67D4\u4ED9\u4E0E\u6C90\u58F0\u4F20\u5374\u5FC3\u5934\u5FAE\u9707\u3002</p><p>\u661F\u6708\u6E56\u7ACB\u6559\u6570\u767E\u5E74\uFF0C\u5947\u4EBA\u5F02\u58EB\u4E91\u96C6\uFF0C\u4E0B\u9762\u53C8\u63A7\u5236\u7740\u6570\u5341\u4E2A\u5927\u5927\u5C0F\u5C0F\u7684\u5E2E\u4F1A\uFF0C\u503C\u6B64\u4E71\u4E16\u60F3\u79F0\u96C4\u4E00\u65B9\u751A\u81F3\u5272\u88C2\u5929\u4E0B\u90FD\u975E\u96BE\u4E8B\u3002\u53EF\u8BE5\u6559\u7CFB\u51FA\u9053\u5BB6\uFF0C\u5386\u4EE3\u5BAB\u4E3B\u5747\u900D\u9065\u4E16\u5916\uFF0C\u4EE5\u70BC\u4E39\u3001\u5BFC\u5F15\u3001\u5FA1\u5973\u4E4B\u672F\u4FEE\u771F\u957F\u751F\u3002\u4E00\u6765\u65E0\u6B64\u91CE\u5FC3\uFF0C\u4E8C\u6765\u4E5F\u4E0D\u5C51\u4E8E\u6B64\u7B49\u7410\u788E\u65E0\u76CA\u4E4B\u4E8B\uFF0C\u56E0\u6B64\u867D\u6709\u504C\u5927\u52BF\u529B\uFF0C\u6559\u5916\u5374\u5C11\u6709\u4EBA\u77E5\u95FB\u3002</p><p>\u6B64\u65F6\u542C\u5230\u65B0\u5BAB\u4E3B\u96C4\u5FC3\u52C3\u52C3\u8981\u9010\u9E7F\u5929\u4E0B\uFF0C\u4E0E\u661F\u6708\u6E56\u5386\u4EE3\u5BAB\u4E3B\u7684\u5FD7\u5411\u80CC\u9053\u800C\u9A70\uFF0C\u6C34\u67D4\u4ED9\u4E0D\u7981\u5FC3\u6000\u9690\u5FE7\uFF0C\u72B9\u8C6B\u7740\u8981\u4E0D\u8981\u5373\u65F6\u6311\u660E\u7ACB\u573A\uFF0C\u4E0E\u6C90\u58F0\u4F20\u636E\u7406\u529B\u4E89\u3002\u4F46\u6B64\u65F6\u7B79\u5907\u8FD8\u672A\u505C\u5F53\uFF0C\u81EA\u5DF1\u5C5E\u4E0B\u4E24\u540D\u9999\u4E3B\u4E00\u4E2A\u91CD\u4F24\uFF0C\u4E00\u4E2A\u4E0D\u5728\u5BAB\u4E2D\u2026\u2026</p><p>\u6B63\u76D8\u7B97\u95F4\uFF0C\u53EA\u542C\u6155\u5BB9\u9F99\u53C8\u9053\uFF1A\u300C\u56DB\u9547\u795E\u5C06\u4E0E\u516D\u5408\u4F9B\u5949\u4E4B\u4F4D\u4E0D\u9650\u4E8E\u672C\u6559\u4E2D\u4EBA\u3002\u672C\u5BAB\u4E0E\u73AB\u7470\u4ED9\u5B50\u7ED3\u4EB2\u4E4B\u4E8B\u5B9A\u4E8E\u56DB\u6708\u5341\u516D\uFF0C\u5C4A\u65F6\u9080\u96C6\u4E09\u5C71\u4E94\u5CB3\u540C\u9053\u9F50\u805A\u5BAB\u4E2D\u3002\u4E94\u4F4D\u957F\u8001\u591A\u52A0\u7559\u610F\uFF0C\u82E5\u6709\u5408\u9002\u8005\u5373\u53EF\u62DB\u63FD\u3002\u300D</p><p>\u6C90\u58F0\u4F20\u54B3\u55FD\u4E00\u58F0\uFF0C\u300C\u6728\u5802\u5C5E\u4E0B\u5404\u5E2E\u662F\u5426\u540C\u65F6\u4F20\u5524\uFF1F\u300D</p><p>\u8FD9\u4E5F\u662F\u6C34\u67D4\u4ED9\u5173\u5FC3\u7684\u4E8B\uFF0C\u5F53\u4E0B\u51DD\u795E\u9759\u542C\u3002</p><p>\u9664\u51E0\u4E2A\u65B0\u589E\u5E2E\u4F1A\u5916\uFF0C\u6155\u5BB9\u9F99\u5BF9\u6559\u4E0B\u6240\u5C5E\u5404\u5E2E\u4E86\u5982\u6307\u638C\u3002\u661F\u6708\u6E56\u4E0B\u5C5E\u5E2E\u4F1A\u90FD\u662F\u6559\u4E2D\u9690\u79D8\uFF0C\u8FDE\u540C\u5802\u7684\u5E2E\u4F1A\u4E5F\u672A\u5FC5\u77E5\u9053\u5F7C\u6B64\u3002\u82E5\u5C3D\u6570\u62DB\u6765\uFF0C\u5F92\u589E\u53D8\u6570\u3002\u4E8E\u662F\u6447\u5934\u9053\uFF1A\u300C\u4E0D\u5FC5\u4F20\u5524\u3002\u70C8\u7130\u3001\u731B\u70BD\u3001\u5DE8\u77F3\u3001\u8F7B\u5C18\u3001\u660E\u9521\u4E94\u540D\u9999\u4E3B\u4E4B\u4F4D\uFF0C\u7531\u4E09\u5802\u6311\u9009\u540E\u81EA\u884C\u62A5\u4E0A\u5373\u53EF\u3002\u300D</p><p>\u6C34\u67D4\u4ED9\u5FC3\u4E2D\u6697\u559C\uFF0C\u5404\u5E2E\u9AD8\u624B\u6B66\u529F\u8DB3\u4EE5\u4E0E\u9999\u4E3B\u6BD4\u80A9\uFF0C\u5230\u65F6\u81EA\u5DF1\u5E73\u6DFB\u516D\u4E03\u540D\u5F97\u529B\u81C2\u52A9\uFF0C\u6B64\u6D88\u5F7C\u957F\uFF0C\u80DC\u7B97\u5927\u589E\u3002</p><p>\u6155\u5BB9\u9F99\u5E76\u672A\u628A\u6C34\u67D4\u4ED9\u653E\u5728\u773C\u5185\uFF0C\u6EE1\u5FC3\u90FD\u5728\u76D8\u7B97\u91D1\u3001\u6728\u4E24\u5802\u3002\u4F46\u91D1\u5F00\u7532\u4F4D\u9AD8\u6743\u91CD\uFF0C\u6C90\u58F0\u4F20\u662F\u6559\u4E2D\u5143\u8001\uFF0C\u53C8\u5FC3\u673A\u6DF1\u6C89\uFF0C\u5BF9\u8FD9\u4E24\u4EBA\u4E0D\u597D\u4E0B\u624B\uFF0C\u4E8E\u662F\u7B11\u9053\uFF1A\u300C\u6C34\u957F\u8001\u91CD\u4F24\u98CE\u665A\u534E\uFF0C\u4E3A\u751F\u64D2\u98D8\u6885\u5CF0\u9996\u5F92\u7ACB\u4E0B\u5927\u529F\uFF0C\u597D\u3001\u597D\uFF01\u300D</p><p>\u679C\u7136\u970D\u72C2\u7130\u76EE\u5149\u4E00\u8DF3\uFF0C\u51B7\u54FC\u4E00\u58F0\u3002\u4F46\u6C90\u58F0\u4F20\u8FD8\u662F\u90A3\u5E45\u6728\u7136\u7684\u795E\u60C5\uFF0C\u4F3C\u4E4E\u6CA1\u6709\u542C\u51FA\u4ED6\u7684\u6697\u793A\u3002</p><p>\u6C34\u67D4\u4ED9\u5FC3\u77E5\u809A\u660E\uFF0C\u8FD9\u662F\u5BAB\u4E3B\u73A9\u5F04\u624B\u6BB5\uFF0C\u501F\u62A4\u6CD5\u4E4B\u4F4D\u628A\u81EA\u5DF1\u63A8\u5230\u53F0\u524D\u53D7\u51B7\u7BAD\uFF0C\u8FDE\u5FD9\u8D77\u8EAB\u9053\uFF1A\u300C\u80FD\u64D2\u5F97\u98CE\u665A\u534E\u662F\u970D\u957F\u8001\u6307\u6325\u5F97\u5F53\uFF0C\u5BAB\u4E3B\u6240\u8A00\uFF0C\u5C5E\u4E0B\u5B9E\u4E0D\u6562\u5F53\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u7B11\u9053\uFF1A\u300C\u5F85\u4E24\u4F4D\u62A4\u6CD5\u56DE\u5BAB\uFF0C\u518D\u884C\u7EC6\u8BAE\u3002\u300D</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u719F\u7761\u7684\u7D2B\u73AB\u50CF\u4E00\u6735\u59E3\u4E3D\u7684\u73AB\u7470\uFF0C\u82AC\u82B3\u56DB\u6EA2\u3002\u633A\u76F4\u4FCF\u7F8E\u7684\u9F3B\u6881\u5728\u5C3D\u5934\u5FAE\u5FAE\u7FD8\u8D77\u2014\u2014\u8FD9\u5C31\u662F\u6155\u5BB9\u5BB6\u65CF\u7684\u8840\u7EDF\u554A\u3002</p><p>\u6155\u5BB9\u9F99\u8F7B\u8F7B\u629A\u6478\u7740\u5979\u7684\u8138\u5E9E\uFF0C\u6709\u80A1\u6696\u6696\u7684\u6E29\u70ED\u5728\u5FC3\u5E95\u6D41\u52A8\u3002\u5FFD\u7136\u51DD\u9759\u7684\u773C\u76AE\u6CE2\u52A8\u8D77\u6765\uFF0C\u7D2B\u73AB\u7259\u5173\u54AC\u7D27\uFF0C\u7EF7\u7D27\u7684\u7389\u8138\u6CDB\u8D77\u4E00\u80A1\u51B7\u5389\uFF0C\u4F3C\u4E4E\u5728\u68A6\u4E2D\u89C1\u5230\u4E86\u4EC0\u4E48\u53EF\u6068\u7684\u4E8B\u7269\uFF0C\u63A5\u7740\u776B\u6BDB\u5FAE\u98A4\uFF0C\u4E00\u6EF4\u6676\u83B9\u7684\u6C34\u73E0\u4ECE\u773C\u89D2\u6D8C\u51FA\uFF0C\u4EFF\u4F5B\u82B1\u74E3\u4E0A\u7684\u9732\u73E0\u6EDA\u843D\u4E0B\u6765\u3002\u6CEA\u6C34\u8F7B\u76C8\u5730\u6EF4\u5728\u6155\u5BB9\u9F99\u7684\u8863\u8896\u4E0A\uFF0C\u5149\u6DA6\u7684\u9762\u5E9E\u4E0A\u5374\u6CA1\u6709\u4E00\u4E1D\u6C34\u75D5\u3002</p><p>\u6155\u5BB9\u9F99\u7740\u8FF7\u5730\u770B\u7740\u5979\u65F6\u6012\u65F6\u559C\u7684\u8FF7\u4EBA\u7761\u8C8C\uFF0C\u7A81\u7136\u607C\u6012\u8D77\u6765\uFF0C\u300C\u8FD9\u5C0F\u4E2B\u5934\u8FD8\u5728\u6068\u6211\u5462\u2026\u2026\u300D</p><p>\u7D2B\u73AB\u5507\u4E0A\u4E00\u75BC\uFF0C\u8FDE\u5FD9\u7741\u5F00\u773C\uFF0C\u53D1\u73B0\u662F\u6155\u5BB9\u9F99\u54AC\u4E86\u81EA\u5DF1\u4E00\u53E3\uFF0C\u8D76\u7D27\u300C\u5478\u5478\u300D\u5410\u4E86\u8D77\u6765\u3002</p><p>\u6155\u5BB9\u9F99\u7768\u89C6\u7740\u5979\uFF0C\u60F3\u5230\u8981\u4EB2\u624B\u628A\u9A84\u50B2\u5014\u5F3A\u7684\u5AE1\u4EB2\u59B9\u59B9\u8C03\u6559\u6210\u4E56\u5DE7\u542C\u8BDD\u7684\u5C0F\u7F8E\u5974\uFF0C\u5B89\u5B89\u5206\u5206\u7ED9\u81EA\u5DF1\u751F\u5B69\u5B50\uFF0C\u4E0D\u7531\u4E00\u9635\u5174\u594B\u3002\u4ED6\u707F\u70C2\u5730\u7B11\u4E86\u4E00\u4E0B\uFF0C\u62C9\u8D77\u5979\u7684\u624B\u3002</p><p>\u7D2B\u73AB\u76F4\u8D77\u8170\uFF0C\u7565\u4E00\u8FD0\u529F\uFF0C\u53D1\u73B0\u81EA\u5DF1\u624B\u811A\u884C\u52A8\u81EA\u5982\uFF0C\u53EF\u4E39\u7530\u5185\u5374\u7A7A\u7A7A\u5982\u91CE\uFF0C\u4E0D\u7531\u60CA\u6012\u4EA4\u52A0\u5730\u53EB\u9053\uFF1A\u300C\u4F60\u6562\u5E9F\u4E86\u6211\u7684\u6B66\u529F\uFF1F\u4F60\u8FD9\u4E2A\u6DF7\u86CB\uFF01\u300D</p><p>\u6155\u5BB9\u9F99\u4EFB\u5979\u7C89\u62F3\u6414\u75D2\u4F3C\u7684\u6253\u5728\u80CC\u4E0A\uFF0C\u6D51\u4E0D\u5728\u610F\u7684\u563B\u563B\u4E00\u7B11\uFF0C\u6B63\u5F85\u8BF4\u8BDD\uFF0C\u7A81\u7136\u8138\u8272\u4E00\u53D8\uFF0C\u4E24\u817F\u8FDE\u5FD9\u5408\u7D27\uFF0C\u5939\u4F4F\u7D2B\u73AB\u4ECE\u80EF\u95F4\u731B\u8E22\u6765\u7684\u4E00\u811A\uFF0C\u300C\u4ED6\u5988\u7684\uFF01\u6562\u671D\u8FD9\u513F\u8E22\uFF1F\u300D</p><p>\u867D\u7136\u4ED6\u6B66\u529F\u8FC7\u4EBA\uFF0C\u777E\u4E38\u8981\u5BB3\u731D\u4E0D\u53CA\u9632\u6328\u4E0A\u4E00\u811A\u4E5F\u7EDD\u4E0D\u597D\u53D7\u3002\u4E24\u817F\u5FAE\u4E00\u4F7F\u529B\uFF0C\u7D2B\u73AB\u7684\u53F3\u8DB3\u987F\u65F6\u50CF\u88AB\u5927\u77F3\u538B\u4F4F\u822C\u75DB\u5F7B\u5FC3\u80BA\u3002\u4F46\u5979\u54AC\u7259\u6B7B\u6B7B\u5FCD\u4F4F\uFF0C\u4E00\u58F0\u4E0D\u542D\u3002</p><p>\u6155\u5BB9\u9F99\u9634\u6D4B\u6D4B\u5730\u8BF4\uFF1A\u300C\u4E0D\u7528\u6025\uFF0C\u7B49\u65B0\u5A5A\u4E4B\u591C\u54E5\u54E5\u7ED9\u4F60\u5F00\u82DE\u7684\u65F6\u5019\uFF0C\u518D\u5E9F\u4F60\u7684\u6B66\u529F\u597D\u4E86\u3002\u300D</p><p>\u7D2B\u73AB\u6B64\u65F6\u4E5F\u53D1\u89C9\u81EA\u5DF1\u5185\u529B\u5C1A\u5B58\uFF0C\u53EA\u662F\u4E39\u7530\u50CF\u4E0E\u7ECF\u7EDC\u9694\u7EDD\u822C\u65E0\u6CD5\u6C47\u805A\u771F\u6C14\u3002\u542C\u5230\u6155\u5BB9\u9F99\u5982\u6B64\u8BF4\uFF0C\u4E0D\u7531\u4FCF\u8138\u901A\u7EA2\uFF0C\u6068\u6068\u5730\u5550\u4E86\u4E00\u53E3\u3002</p><p>\u6155\u5BB9\u9F99\u89C1\u5979\u4E0D\u518D\u6323\u624E\uFF0C\u5F90\u5F90\u95EE\u9053\uFF1A\u300C\u85CF\u5B9D\u56FE\u5462\uFF1F\u300D</p>',59);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
