import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_27" tabindex="-1"><a class="header-anchor" href="#_27" aria-hidden="true">#</a> 27</h1><p>\u5F53\u521D\u6155\u5BB9\u7687\u65CF\u4E0E\u661F\u6708\u6E56\u8FC7\u5F80\u751A\u5BC6\uFF0C\u6155\u5BB9\u9F99\u4ECE\u661F\u6708\u5BAB\u4E3B\u53EA\u8A00\u7247\u8BED\u91CC\u542C\u51FA\u5927\u71D5\u706D\u4EA1\u524D\u7236\u7687\u66FE\u628A\u5927\u6279\u8D22\u5B9D\u85CF\u5165\u6DF1\u5C71\uFF0C\u4F46\u5175\u8352\u9A6C\u4E71\u4E2D\u6240\u7ED8\u85CF\u5B9D\u56FE\u4E0B\u843D\u4E0D\u660E\u3002\u9634\u59EC\u5E76\u672A\u628A\u8D22\u5B9D\u653E\u5728\u5FC3\u4E0A\uFF0C\u4EB2\u624B\u6740\u4E86\u71D5\u5E1D\u6155\u5BB9\u7941\uFF0C\u63B3\u5230\u4ED6\u7684\u513F\u5B50\u5DF2\u7ECF\u5FC3\u6EE1\u610F\u8DB3\u3002\u5982\u4ECA\u6155\u5BB9\u9F99\u6709\u5FC3\u590D\u56FD\uFF0C\u4E9F\u9700\u8D22\u7269\uFF0C\u5BF9\u8FD9\u7B14\u5C5E\u4E8E\u81EA\u5DF1\u5BB6\u65CF\u7684\u5B9D\u85CF\u81EA\u7136\u5206\u5916\u5173\u6CE8\u3002</p><p>\u300C\u4EC0\u4E48\u5B9D\u85CF\uFF1F\u6CA1\u542C\u8BF4\u8FC7\u3002\u300D\u6155\u5BB9\u7D2B\u73AB\u773C\u90FD\u4E0D\u7728\u5730\u4E00\u53E3\u54AC\u5B9A\u3002</p><p>\u300C\u4F60\u5F88\u4E0D\u542C\u8BDD\u554A\u2026\u2026\u300D\u6155\u5BB9\u9F99\u62C9\u8D77\u7D2B\u73AB\uFF0C\u5FEB\u6B65\u51FA\u6BBF\u3002</p><p>\u7D2B\u73AB\u5FC3\u5FF5\u7535\u8F6C\uFF0C\u4E09\u4F4D\u5E08\u59D0\u5C3D\u6570\u843D\u5165\u654C\u624B\uFF0C\u81EA\u5DF1\u6B66\u529F\u53C8\u88AB\u5236\u4F4F\uFF0C\u60F3\u9003\u51FA\u661F\u6708\u6E56\u5343\u96BE\u4E07\u96BE\uFF0C\u552F\u4E00\u7684\u5E0C\u671B\u5C31\u662F\u7B49\u5E08\u7236\u6765\u6551\u4E86\u2014\u2014\u597D\u5728\u8FD9\u4E2A\u6DF7\u86CB\u8981\u4F20\u626C\u5929\u4E0B\uFF0C\u5E08\u7236\u81EA\u7136\u4F1A\u542C\u8BF4\u6B64\u4E8B\u3002</p><p>\u60F3\u5230\u521A\u624D\u4ED6\u8BF4\u7684\u300C\u5F00\u82DE\u300D\uFF0C\u7D2B\u73AB\u5C31\u82B3\u5FC3\u6697\u98A4\uFF0C\u9664\u4E86\u5931\u8EAB\u548C\u4E71\u4F26\u7684\u62C5\u5FC3\u5916\uFF0C\u8FD8\u6709\u5E08\u7236\u7684\u5C61\u5C61\u544A\u8BEB\uFF1A\u51E4\u51F0\u5B9D\u5178\u672A\u81F3\u5927\u6210\uFF0C\u4E00\u65E6\u7834\u4F53\u4F1A\u6709\u6027\u547D\u4E4B\u5FE7\u2026\u2026</p><p>\u8FD9\u4E9B\u4EE5\u540E\u518D\u60F3\uFF0C\u4E00\u65F6\u7247\u523B\u81EA\u5DF1\u5E76\u6CA1\u6709\u4EC0\u4E48\u5371\u9669\uFF0C\u773C\u4E0B\u600E\u4E48\u62A4\u5F97\u4E09\u4F4D\u5E08\u59D0\u5E73\u5B89\u5462\uFF1F</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u53CC\u76EE\u5931\u660E\u7684\u5BD2\u6708\u5200\u4ECD\u4F0F\u5728\u6BBF\u5916\uFF0C\u7D2B\u73AB\u6323\u8131\u6155\u5BB9\u9F99\u7684\u624B\u638C\uFF0C\u62FC\u547D\u63A8\u5F00\u4E00\u4E2A\u6B63\u5728\u5AC2\u5AC2\u4F53\u5185\u62BD\u9001\u7684\u6C49\u5B50\u3002\u90A3\u6C49\u5B50\u6B63\u5728\u4E50\u5934\u4E0A\uFF0C\u7A81\u7136\u88AB\u4EBA\u63A8\u5F00\uFF0C\u4E0D\u7531\u52C3\u7136\u5927\u6012\uFF0C\u5F85\u8BA4\u51FA\u662F\u73AB\u7470\u4ED9\u5B50\u8FDE\u5FD9\u628A\u53EB\u9A82\u54BD\u5230\u809A\u91CC\u3002</p><p>\u7D2B\u73AB\u62B9\u7740\u773C\u6CEA\u62B1\u8D77\u5AC2\u5AC2\uFF0C\u5FAE\u4E00\u7528\u529B\uFF0C\u53EA\u542C\u300C\u94EE\u300D\u7684\u4E00\u58F0\u54CD\uFF0C\u5AC2\u5AC2\u88AB\u6298\u78E8\u81F3\u9EBB\u6728\u7684\u8138\u4E0A\u9732\u51FA\u6781\u7AEF\u75DB\u695A\u7684\u8868\u60C5\u3002\u5979\u8FDE\u5FD9\u677E\u624B\uFF0C\u5C06\u6797\u9999\u8FDC\u5C0F\u5FC3\u5730\u7FFB\u8F6C\u8FC7\u6765\u3002\u6CBE\u6EE1\u9633\u7CBE\u7684\u9EDD\u9ED1\u94C1\u94FE\u4E00\u7AEF\u710A\u6B7B\u5728\u77F3\u680F\u5E95\u90E8\uFF0C\u4E00\u7AEF\u4F38\u5230\u5C11\u5987\u8179\u4E0B\u3002\u672B\u7AEF\u53D8\u6210\u9497\u8EAB\u7C97\u7EC6\uFF0C\u6D88\u5931\u5728\u7EA2\u80BF\u7684\u82B1\u74E3\u4E4B\u95F4\u3002</p><p>\u7D2B\u73AB\u98A4\u6296\u7740\u624B\u6307\u5206\u5F00\u5AC2\u5AC2\u7684\u79D8\u5904\uFF0C\u53EA\u89C1\u8273\u7EA2\u7684\u5AE9\u8089\u88AB\u4EBA\u6B8B\u5FCD\u5730\u523A\u51FA\u4E00\u4E2A\u8840\u6DCB\u6DCB\u7684\u4F24\u53E3\uFF0C\u94C1\u94FE\u4ECE\u4E2D\u7A7F\u5165\uFF0C\u7ED5\u7740\u5A07\u5AE9\u7684\u82B1\u8482\u6253\u4E86\u4E2A\u6CBE\u8840\u7684\u94C1\u7ED3\u3002\u7D2B\u73AB\u50F5\u7ACB\u5F53\u573A\uFF0C\u624B\u811A\u51B0\u51B7\u3002</p><p>\u94C1\u94FE\u94EE\u7136\u7EF7\u7D27\uFF0C\u6797\u9999\u8FDC\u7ACB\u751F\u611F\u5E94\uFF0C\u82B1\u74E3\u54C6\u55E6\u7740\u6EF4\u51FA\u6DEB\u6DB2\uFF0C\u7389\u6237\u9AD8\u4E3E\uFF0C\u4E0B\u8179\u88AB\u94C1\u94FE\u62D6\u4E86\u8D77\u6765\u3002\u6155\u5BB9\u9F99\u63D0\u7740\u94C1\u94FE\u7B11\u9053\uFF1A\u300C\u597D\u4E0D\u597D\u73A9\uFF1F\u6797\u5A4A\u5B50\u5C45\u7136\u6562\u9003\u8DD1\uFF0C\u54E5\u54E5\u53EA\u597D\u7ED9\u5979\u5E26\u4E0A\u6839\u94C1\u94FE\u2014\u2014\u4F60\u77A7\uFF0C\u8FD9\u94C1\u94FE\u5728\u5979\u8D31\u5C44\u4E0A\u9762\u7684\u8089\u91CC\u7ED5\u4E86\u4E00\u5708\uFF0C\u7F20\u7740\u803B\u9AA8\uFF0C\u4E0D\u4F46\u8DD1\u4E0D\u4E86\uFF0C\u4E00\u52A8\u8FD8\u4F1A\u53D1\u6D6A\u5462\u2026\u2026\u300D</p><p>\u7D2B\u73AB\u63E1\u7D27\u94C1\u94FE\u672B\u7AEF\uFF0C\u514D\u5F97\u5AC2\u5AC2\u5403\u75BC\uFF0C\u54ED\u9053\uFF1A\u300C\u6211\u4E8C\u5E08\u59D0\u53C8\u6CA1\u6709\u5F97\u7F6A\u4F60\uFF0C\u4F60\u4E3A\u4EC0\u4E48\u8981\u8FD9\u6837\u5BF9\u5979\uFF1F\u300D</p><p>\u6155\u5BB9\u9F99\u7528\u529B\u4E00\u6323\uFF0C\u94C1\u94FE\u6323\u8131\u7D2B\u73AB\u7684\u7389\u624B\uFF0C\u9AD8\u9AD8\u63D0\u8D77\u3002\u6797\u9999\u8FDC\u4E0B\u4F53\u60AC\u7A7A\uFF0C\u4E24\u817F\u5782\u5728\u8EAB\u4FA7\uFF0C\u79D8\u5904\u671D\u5929\u655E\u9732\uFF0C\u7CBE\u6DB2\u548C\u6DEB\u6C34\u4ECE\u5FAE\u5F20\u7684\u8089\u7A74\u5185\u6DCB\u6F13\u6D8C\u51FA\u3002\u4ECE\u82B1\u8482\u65C1\u7A7F\u51FA\u7684\u94C1\u94FE\u76F4\u76F4\u7AD6\u5728\u7389\u6237\u6B63\u4E2D\uFF0C\u6DF1\u5165\u4F53\u5185\u7684\u94FE\u8EAB\u78E8\u5728\u803B\u9AA8\u4E0A\uFF0C\u9690\u9690\u4F5C\u54CD\u3002\u6797\u9999\u8FDC\u5267\u75DB\u653B\u5FC3\uFF0C\u80A1\u95F4\u808C\u8089\u75C9\u631B\uFF0C\u5931\u660E\u7684\u773C\u775B\u731B\u7136\u7741\u5F00\uFF0C\u9632\u5979\u54AC\u820C\u81EA\u5C3D\u7684\u7AF9\u7B52\u51E0\u4E4E\u88AB\u94F6\u7259\u54AC\u788E\u3002</p><p>\u7D2B\u73AB\u54ED\u53EB\u7740\u8DF3\u8D77\u6765\uFF0C\u671D\u6155\u5BB9\u9F99\u7684\u624B\u81C2\u72E0\u72E0\u54AC\u4E0B\u3002\u773C\u524D\u767D\u5149\u4E00\u95EA\uFF0C\u4E00\u6839\u8165\u81ED\u575A\u786C\u7684\u7269\u4F53\u6321\u5728\u5507\u4E0A\u3002\u7D2B\u73AB\u4E00\u5B9A\u795E\uFF0C\u53D1\u73B0\u90A3\u662F\u54E5\u54E5\u6155\u5BB9\u80DC\u7684\u817F\u9AA8\uFF0C\u4E0D\u7531\u4F0F\u5730\u5455\u5410\u8D77\u6765\u3002</p><p>\u6155\u5BB9\u9F99\u8138\u6C89\u5982\u6C34\uFF0C\u4E00\u624B\u62CE\u7740\u94C1\u94FE\uFF0C\u4E00\u624B\u62FF\u7740\u817F\u9AA8\u6345\u5165\u6797\u9999\u8FDC\u8089\u7A74\u5185\uFF0C\u72E0\u72E0\u6363\u5F04\u3002</p><p>\u7D2B\u73AB\u62BD\u564E\u7740\u8BF4\u9053\uFF1A\u300C\u653E\u4E86\u5AC2\u5AC2\u2026\u2026\u6211\u542C\u8BDD\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u72DE\u7136\u4E00\u7B11\uFF0C\u300C\u653E\u4E86\u5979\u662F\u4E0D\u53EF\u80FD\u7684\uFF0C\u6797\u5A4A\u5B50\u8FD9\u8F88\u5B50\u53EA\u80FD\u50CF\u72D7\u4E00\u6837\u6813\u5728\u8FD9\u91CC\u3002\u4F46\u53EA\u8981\u4F60\u542C\u8BDD\uFF0C\u8FD9\u8D31\u4EBA\u5C31\u80FD\u5C11\u5403\u4E9B\u82E6\u5934\u3002\u300D\u624B\u4E00\u677E\uFF0C\u5939\u7740\u817F\u9AA8\u7684\u5706\u81C0\u91CD\u91CD\u843D\u5728\u5730\u4E0A\uFF0C\u300C\u4F60\u4E00\u5929\u4E0D\u8BF4\uFF0C\u5C31\u5272\u4E0B\u5979\u4E00\u7247\u8089\u3002\u5475\u5475\uFF0C\u54E5\u54E5\u7B49\u5F97\u8D77\u3002\u4EE5\u524D\u6709\u4E2A\u5973\u4EBA\u54E5\u54E5\u6740\u4E86\u4E24\u4E2A\u6708\u624D\u6740\u5B8C\u2026\u2026\u300D</p><p>\u6155\u5BB9\u7D2B\u73AB\u7259\u5173\u98A4\u6296\u8D77\u6765\uFF0C\u5FFD\u7136\u5931\u58F0\u53EB\u9053\uFF1A\u300C\u5B9D\u85CF\u5728\u2026\u2026\u300D\u8BDD\u672A\u8BF4\u5B8C\u4FBF\u6655\u5012\u5728\u5730\u3002</p><p>\u6155\u5BB9\u9F99\u5FC3\u4E0B\u6697\u559C\uFF0C\u83AB\u8BF4\u5979\u53EA\u662F\u4E2A\u5341\u516D\u5C81\u7684\u5C0F\u59D1\u5A18\uFF0C\u5C31\u662F\u6C5F\u6E56\u4E0A\u6210\u540D\u7684\u4FA0\u5973\u89C1\u8BC6\u8FC7\u4ED6\u7684\u624B\u6BB5\u4E5F\u65E0\u4E0D\u5FC3\u60CA\u80C6\u6218\uFF0C\u51DB\u7136\u542C\u547D\u3002\u5B9D\u85CF\u4E8B\u5173\u673A\u5BC6\uFF0C\u4ED6\u8FDE\u5FD9\u6402\u8D77\u7D2B\u73AB\u63A0\u56DE\u795E\u6BBF\u540E\u5BAB\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u7D2B\u73AB\u60A0\u60A0\u9192\u8F6C\uFF0C\u832B\u7136\u770B\u7740\u5BA4\u5185\u3002</p><p>\u6155\u5BB9\u9F99\u9012\u6765\u4E00\u676F\u8336\u6C34\uFF0C\u67D4\u58F0\u9053\uFF1A\u300C\u6765\uFF0C\u559D\u53E3\u6C34\uFF0C\u628A\u4E8B\u60C5\u90FD\u544A\u8BC9\u54E5\u54E5\u2026\u2026\u300D</p><p>\u7D2B\u73AB\u6447\u4E86\u6447\u5934\uFF0C\u6709\u6C14\u65E0\u529B\u5730\u8BF4\uFF1A\u300C\u5B9D\u85CF\u5728\u8FBD\u4E1C\u2026\u2026\u300D\u5979\u8BC8\u4F5C\u660F\u8FF7\uFF0C\u85C9\u673A\u7F16\u4E86\u4E00\u809A\u5B50\u7684\u8C0E\u8BDD\uFF0C\u5F53\u4E0B\u6162\u6162\u9053\u6765\u3002</p><p>\u6155\u5BB9\u9F99\u8D8A\u542C\u8D8A\u662F\u7591\u5FC3\uFF0C\u6D53\u7709\u4E00\u626C\uFF0C\u5BD2\u58F0\u9053\uFF1A\u300C\u5927\u71D5\u7ACB\u56FD\u6CB3\u6D1B\uFF0C\u600E\u4E48\u53EF\u80FD\u628A\u8D22\u7269\u85CF\u5230\u8FBD\u4E1C\uFF1F\u300D</p><p>\u7D2B\u73AB\u5BF9\u71D5\u56FD\u4E00\u65E0\u6240\u77E5\uFF0C\u60F3\u7F16\u4E5F\u7F16\u4E0D\u51FA\u6765\u3002\u4F46\u5979\u4E5F\u4E0D\u5E9F\u5FC3\u53BB\u7F16\uFF0C\u957F\u957F\u7684\u776B\u6BDB\u67D4\u67D4\u4E00\u7728\uFF0C\u9ED1\u767D\u5206\u660E\u7684\u5927\u773C\u775B\u6EE1\u662F\u65E0\u8F9C\u5730\u671B\u7740\u6155\u5BB9\u9F99\uFF0C\u695A\u695A\u53EF\u601C\u5730\u8BF4\uFF1A\u300C\u6211\u2026\u2026\u6211\u600E\u4E48\u77E5\u9053\u2026\u2026\u7239\u7239\u6CA1\u8BF4\u5B8C\u2026\u2026\u5C31\u88AB\u90A3\u4E2A\u7EA2\u5934\u53D1\u7684\u6076\u4EBA\u70B8\u6B7B\u4E86\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u51B7\u51B7\u76EF\u7740\u7D2B\u73AB\uFF0C\u773C\u89C1\u5979\u7389\u5BB9\u5149\u8F6C\uFF0C\u79C0\u7738\u6E05\u6C34\u822C\u7EAF\u6D01\u79C0\u7F8E\uFF0C\u600E\u4E48\u770B\u90FD\u662F\u4E2A\u5929\u771F\u65E0\u90AA\u7684\u5C0F\u5973\u5B69\uFF0C\u5012\u662F\u6709\u4E03\u5206\u76F8\u4FE1\u3002</p><p>\u7D2B\u73AB\u4E0E\u4ED6\u5BF9\u89C6\u7247\u523B\uFF0C\u7A81\u7136\u8138\u4E0A\u5FAE\u5FAE\u4E00\u7EA2\uFF0C\u81FB\u9996\u4FA7\u5230\u4E00\u65C1\uFF0C\u56DE\u907F\u4ED6\u5484\u5484\u8FEB\u4EBA\u7684\u76EE\u5149\u3002\u51B0\u51B7\u7684\u76EE\u5149\u50CF\u88AB\u718F\u98CE\u62C2\u8FC7\u822C\u8F6F\u8F6F\u5316\u5F00\uFF0C\u6155\u5BB9\u9F99\u5728\u7D2B\u73AB\u8138\u4E0A\u8F7B\u8F7B\u4E00\u543B\uFF0C\u6E29\u8A00\u9053\uFF1A\u300C\u542C\u8BDD\u5C31\u597D\u3002\u55EF\uFF0C\u8FBD\u4E1C\u2026\u2026\u8FBD\u4E1C\u2026\u2026\u4F1A\u4E0D\u4F1A\u662F\u9F99\u57CE\uFF1F\u300D</p><p>\u300C\u662F\u4E86\u662F\u4E86\uFF0C\u5C31\u662F\u9F99\u57CE\uFF01\u6211\u8BB0\u9519\u4E86\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u770B\u4E86\u5979\u4E00\u773C\uFF0C\u6C89\u541F\u9053\uFF1A\u300C\u9F99\u57CE\u662F\u6211\u6155\u5BB9\u6C0F\u9F99\u5174\u4E4B\u5730\uFF0C\u5B9D\u85CF\u5728\u90A3\u91CC\u4E5F\u5927\u6709\u53EF\u80FD\u3002\u6210\u4EB2\u540E\u54B1\u4EEC\u4E00\u8D77\u53BB\u9F99\u57CE\u628A\u5B9D\u85CF\u8D77\u51FA\u6765\u2026\u2026\u300D</p><p>\u7D2B\u73AB\u7565\u5E26\u5A07\u7F9E\u5730\u70B9\u4E86\u70B9\u5934\uFF0C\u5FC3\u91CC\u5374\u6068\u6068\u5550\u4E86\u4E00\u53E3\u3002\u6DF7\u86CB\uFF01\u8BA9\u585E\u5916\u7684\u72FC\u7FA4\u628A\u4F60\u5403\u4E86\uFF01</p><p>\u6155\u5BB9\u9F99\u4EE5\u4E3A\u5979\u5DF2\u7ECF\u5C48\u670D\uFF0C\u4E0D\u7981\u7B11\u9010\u989C\u5F00\uFF0C\u5F97\u610F\u5730\u671D\u77F3\u5BA4\u6325\u4E86\u6325\u624B\uFF0C\u300C\u59B9\u59B9\u4F60\u770B\uFF0C\u8FD9\u91CC\u5F53\u4F60\u6211\u7684\u6D1E\u623F\u5982\u4F55\uFF1F\u300D</p><p>\u7D2B\u73AB\u773C\u6CE2\u5FAE\u8F6C\uFF0C\u7F9E\u6DA9\u5730\u5782\u4E0B\u76EE\u5149\uFF0C\u8F7B\u58F0\u8BF4\uFF1A\u300C\u90FD\u662F\u77F3\u5934\u2026\u2026\u51B7\u51B0\u51B0\u7684\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u7B11\u9053\uFF1A\u300C\u4E0D\u59A8\u3002\u6211\u7ACB\u523B\u547D\u4EBA\u6302\u4E0A\u6BE1\u6BEF\uFF01\u300D</p><p>\u7D2B\u73AB\u8BA1\u4E0A\u5FC3\u6765\uFF0C\u58F0\u82E5\u868A\u5450\u5730\u8BF4\u9053\uFF1A\u300C\u2026\u2026\u6C5F\u5357\u6709\u5904\u6843\u6797\u2026\u2026\u6211\u4E00\u76F4\u60F3\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u5212\u4E86\u534A\u5708\u7684\u624B\u987F\u65F6\u50F5\u534A\u7A7A\u3002\u6210\u4EB2\u662F\u5973\u5B69\u5BB6\u5927\u4E8B\uFF0C\u59B9\u59B9\u65E2\u7136\u6709\u6B64\u5FC3\u613F\u4E5F\u4E0D\u597D\u65AD\u7136\u62D2\u7EDD\uFF0C\u4F46\u2026\u2026\u534A\u664C\u624D\u5C34\u5C2C\u5730\u7B11\u4E86\u7B11\uFF0C\u8BD5\u63A2\u5730\u8BF4\uFF1A\u300C\u6210\u4EB2\u540E\u54E5\u54E5\u5E26\u4F60\u53BB\u4F4F\u4E0A\u4E00\u5E74\uFF0C\u5982\u4F55\uFF1F\u300D</p><p>\u6CA1\u60F3\u5230\u7D2B\u73AB\u5E76\u672A\u575A\u6301\uFF0C\u53CD\u800C\u5F88\u5E72\u8106\u5730\u70B9\u5934\u540C\u610F\u3002\u6155\u5BB9\u9F99\u5FC3\u82B1\u6012\u653E\uFF0C\u62B1\u8D77\u59B9\u59B9\u62AC\u624B\u8F7B\u8F7B\u4E00\u629B\uFF0C\u53C8\u63A5\u5728\u6000\u91CC\uFF0C\u72E0\u72E0\u543B\u4E0A\u5979\u9999\u751C\u7684\u5C0F\u5634\u3002</p><p>\u6155\u5BB9\u7D2B\u73AB\u54AC\u7D27\u7259\u5173\u963B\u6321\u4ED6\u820C\u5934\u7684\u8FDB\u5165\uFF0C\u773C\u4E2D\u5149\u8292\u95EA\u52A8\uFF0C\u7D27\u7D27\u76EF\u7740\u77F3\u5BA4\u58C1\u89D2\u3002</p><p>\u90A3\u662F\u521A\u624D\u6155\u5BB9\u9F99\u65E0\u610F\u8BC6\u6307\u5230\u7684\u5730\u65B9\u3002\u82E5\u975E\u7D2B\u73AB\u65E5\u591C\u8BB0\u6302\u7740\u7236\u4EB2\u5F53\u65E5\u6240\u753B\u7684\u56FE\u5F62\uFF0C\u4E5F\u65E0\u6CD5\u4ECE\u904D\u5E03\u7EB9\u9970\u7684\u77F3\u58C1\u5E95\u90E8\u4E00\u773C\u8BA4\u51FA\u90A3\u4E2A\u6307\u5C16\u5927\u5C0F\uFF0C\u4F3C\u4E91\u4F3C\u82B1\u7684\u56FE\u6848\u3002</p><p>\u7D2B\u73AB\u5A07\u8EAF\u8F7B\u98A4\uFF0C\u6155\u5BB9\u9F99\u5374\u4EE5\u4E3A\u5979\u662F\u7F9E\u6DA9\uFF0C\u4FBF\u677E\u4E86\u5634\uFF0C\u7B11\u9053\uFF1A\u300C\u4F60\u8EAB\u4F53\u91CC\u91CC\u5916\u5916\u90FD\u662F\u54E5\u54E5\u7684\uFF0C\u4EB2\u4EB2\u6709\u4EC0\u4E48\u5927\u7D27\uFF1F\u8FC7\u4E0D\u4E86\u51E0\u65E5\uFF0C\u54E5\u54E5\u4FBF\u8981\u5230\u73AB\u7470\u4ED9\u5B50\u6700\u7F8E\u5999\u7684\u2026\u2026\u300D\u8BF4\u7740\u5728\u5979\u817F\u95F4\u8F7B\u8F7B\u6478\u4E86\u4E00\u628A\u3002</p><p>\u7D2B\u73AB\u6C14\u6068\u4EA4\u52A0\uFF0C\u7C89\u8138\u6DA8\u5F97\u901A\u7EA2\uFF0C\u5A07\u559D\u9053\uFF1A\u300C\u653E\u6211\u4E0B\u6765\uFF01\u4F60\u51FA\u53BB\uFF01\u6211\u8981\u7761\u4E00\u4F1A\u513F\uFF01\u300D</p><p>\u6155\u5BB9\u9F99\u53EA\u5F53\u662F\u6253\u60C5\u9A82\u4FCF\uFF0C\u54C8\u54C8\u4E00\u7B11\uFF0C\u628A\u7D2B\u73AB\u653E\u5230\u5E8A\u4E0A\uFF0C\u620F\u8C11\u5730\u7728\u7728\u773C\uFF0C\u300C\u8981\u4E0D\u8981\u54E5\u54E5\u966A\u4F60\uFF1F\u300D</p><p>\u7D2B\u73AB\u7528\u9526\u88AB\u906E\u4F4F\u8138\uFF0C\u5FC3\u91CC\u5374\u4E0D\u4F4F\u76D8\u7B97\u3002\u56FE\u5F62\u7ADF\u7136\u4F1A\u5728\u8FD9\u91CC\u51FA\u73B0\uFF0C\u5B9D\u85CF\u96BE\u9053\u4F1A\u85CF\u5728\u9B54\u5BAB\u4E4B\u5185\uFF1F\u8D3E\u94F6\u601D\u3001\u4E01\u8D35\u5FE0\u7A76\u7ADF\u662F\u4EC0\u4E48\u4EBA\uFF1F\u96BE\u9053\u4E5F\u662F\u661F\u6708\u6E56\u5996\u5B7D\uFF1F</p><p>\u6155\u5BB9\u9F99\u7684\u58F0\u97F3\u4ECE\u88AB\u5916\u4F20\u6765\uFF0C\u300C\u83BA\u5974\uFF0C\u4F3A\u5019\u5C11\u592B\u4EBA\u3002\u300D</p><p>\u767D\u7389\u83BA\u8106\u751F\u751F\u5730\u7B54\u5E94\u4E00\u58F0\uFF0C\u63A5\u7740\u7389\u95E8\u5408\u7D27\uFF0C\u53D1\u51FA\u4E00\u58F0\u95F7\u54CD\u3002</p><p>\u7D2B\u73AB\u6162\u6162\u62C9\u5F00\u88AB\u5B50\uFF0C\u89C1\u6155\u5BB9\u9F99\u679C\u7136\u4E0D\u5728\u5BA4\u5185\uFF0C\u4FBF\u76D8\u819D\u5750\u4E0B\uFF0C\u624B\u634F\u8BC0\u8981\uFF0C\u8BD5\u7740\u51DD\u805A\u529F\u529B\u3002\u4E0D\u77E5\u4ED6\u4EEC\u4F7F\u4E86\u4EC0\u4E48\u836F\u7269\uFF0C\u6EE1\u6EA2\u7684\u771F\u6C14\u51DD\u6EDE\u5982\u51B0\uFF0C\u59CB\u7EC8\u65E0\u6CD5\u6CE8\u5165\u4E39\u7530\u3002</p><p>\u7D2B\u73AB\u5E9F\u7136\u6B62\u624B\uFF0C\u4FCF\u76EE\u4E00\u7741\uFF0C\u51B7\u51B7\u770B\u7740\u767D\u7389\u83BA\u3002</p><p>\u767D\u7389\u83BA\u8DEA\u5728\u5BA4\u4FA7\uFF0C\u611F\u53D7\u5230\u65E0\u58F0\u7684\u538B\u529B\uFF0C\u6D45\u7EA2\u8272\u7684\u8F7B\u7EB1\u4E0D\u7981\u98A4\u6296\u8D77\u6765\u3002</p><p>\u7D2B\u73AB\u51B7\u51B7\u9053\uFF1A\u300C\u53EF\u7B11\u6211\u8FD8\u628A\u4F60\u4EEC\u5F53\u6210\u59D0\u59B9\uFF0C\u6CA1\u60F3\u5230\u4F60\u7ADF\u4F1A\u6765\u9A97\u6211\uFF01\u300D</p><p>\u767D\u7389\u83BA\u6CE3\u58F0\u9053\uFF1A\u300C\u5C11\u592B\u4EBA\uFF0C\u5974\u5A62\u4E5F\u662F\u88AB\u903C\u2026\u2026\u300D</p><p>\u300C\u4E0D\u8BB8\u53EB\u6211\u5C11\u592B\u4EBA\uFF01\u300D</p><p>\u300C\u662F\uFF0C\u5C11\u592B\u4EBA\u2026\u2026\u73AB\u7470\u4ED9\u5B50\u3002\u300D</p><p>\u300C\u628A\u7ECF\u8FC7\u90FD\u544A\u8BC9\u6211\u3002\u300D</p><p>\u300C\u2026\u2026\u90A3\u65E5\u4E0E\u4ED9\u5B50\u5206\u624B\uFF0C\u6CA1\u591A\u4E45\u970D\u957F\u8001\u5C31\u8FFD\u4E86\u4E0A\u6765\u2026\u2026\u540E\u6765\u4ED6\u4EEC\u6263\u4F4F\u5C0F\u9E42\uFF0C\u903C\u5974\u5A62\u53BB\u5C71\u91CC\u627E\u4ED9\u5B50\u548C\u98CE\u5973\u4FA0\u2026\u2026\u6211\uFF0C\u6211\u2026\u2026\u300D\u767D\u7389\u83BA\u5931\u58F0\u75DB\u54ED\u8D77\u6765\u3002</p><p>\u7D2B\u73AB\u7565\u4E00\u601D\u7D22\u4FBF\u5DF2\u660E\u767D\uFF0C\u8BF4\u5230\u5E95\u5176\u5B9E\u662F\u81EA\u5DF1\u8FDE\u7D2F\u4E86\u767D\u6C0F\u59D0\u59B9\u3002\u60F3\u8D77\u90A3\u65E5\u5728\u5C71\u4E2D\u4E24\u5973\u53D7\u8FB1\u7684\u60E8\u72B6\uFF0C\u767D\u7389\u83BA\u8EAB\u8D1F\u91CD\u4F24\u8FD8\u88AB\u90A3\u4E9B\u79BD\u517D\u51CC\u8FB1\u2026\u2026\u7D2B\u73AB\u5FC3\u5934\u6012\u6C14\u6E10\u6E10\u6D88\u6563\uFF0C\u534A\u664C\u540E\u67D4\u58F0\u9053\uFF1A\u300C\u8FD8\u75BC\u5417\uFF1F\u300D</p><p>\u767D\u7389\u83BA\u5FAE\u5FAE\u4E00\u6014\uFF0C\u8FDE\u5FD9\u6447\u5934\u3002</p><p>\u6155\u5BB9\u7D2B\u73AB\u5927\u7740\u80C6\u5B50\u8DF3\u8D77\u6765\uFF0C\u300C\u8BA9\u6211\u770B\u4E00\u4E0B\u3002\u300D</p><p>\u767D\u7389\u83BA\u9762\u7EA2\u8FC7\u8033\uFF0C\u4F46\u8FD8\u662F\u4F9D\u8A00\u62C9\u8D77\u8F7B\u7EB1\u3002\u7EA2\u7EB1\u4E0B\u8D64\u88F8\u7684\u808C\u80A4\u518D\u65E0\u5BF8\u7F15\uFF0C\u867D\u7136\u5E74\u7EAA\u5C1A\u5E7C\uFF0C\u4F46\u88AB\u6DEB\u5F04\u591A\u65E5\uFF0C\u67D4\u7F8E\u7684\u82B1\u74E3\u5DF2\u5B8C\u5168\u6210\u719F\uFF0C\u6CDB\u7740\u9C9C\u4EAE\u7684\u8273\u7EA2\u3002</p><p>\u300C\u5F00\u59CB\u75BC\u5417\uFF1F\u300D</p><p>\u767D\u7389\u83BA\u70B9\u4E86\u70B9\u5934\uFF0C\u773C\u6CEA\u53C8\u6D41\u4E86\u51FA\u6765\u3002\u4F55\u6B62\u662F\u75BC\uFF0C\u5F53\u65F6\u59D0\u59B9\u4FE9\u521A\u521A\u7834\u8EAB\u5C31\u88AB\u51E0\u5341\u6761\u5927\u6C49\u8F6E\u756A\u5978\u6DEB\u7ADE\u65E5\uFF0C\u7B80\u76F4\u662F\u75BC\u4E0D\u6B32\u751F\u3002\u8DEF\u4E0A\u5954\u8D70\u7684\u6708\u4F59\u65F6\u95F4\uFF0C\u968F\u65F6\u968F\u5730\u90FD\u8981\u655E\u5F00\u8EAB\u4F53\u4EFB\u4EBA\u73A9\u5F04\uFF0C\u90A3\u79CD\u803B\u8FB1\u6BD4\u75DB\u82E6\u66F4\u5F3A\u70C8\u2026\u2026</p><p>\u7D2B\u73AB\u770B\u51FA\u5979\u773C\u91CC\u7684\u60E7\u610F\uFF0C\u4E0D\u7531\u6253\u4E86\u4E2A\u5BD2\u5664\u3002\u98CE\u5E08\u59D0\u88AB\u91CE\u517D\u5978\u6DEB\u540E\u8840\u8089\u6A21\u7CCA\u7684\u79D8\u5904\u5728\u773C\u524D\u4E00\u95EA\u800C\u8FC7\uFF0C\u5979\u6025\u6025\u5598\u4E86\u53E3\u6C14\uFF0C\u6309\u637A\u4E0B\u5FC3\u91CC\u7684\u6050\u60E7\u3002</p><p>\u300C\u4F60\u5148\u51FA\u53BB\u5427\u3002\u300D\u7D2B\u73AB\u67D4\u58F0\u9053\uFF1A\u300C\u6211\u60F3\u72EC\u81EA\u4F11\u606F\u4E00\u4F1A\u513F\u3002\u300D</p>',62);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
