import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_28" tabindex="-1"><a class="header-anchor" href="#_28" aria-hidden="true">#</a> 28</h1><p>\u6155\u5BB9\u7D2B\u73AB\u4F0F\u5728\u5BA4\u89D2\u4ED4\u7EC6\u7684\u89C2\u5BDF\u90A3\u4E2A\u56FE\u5F62\u3002\u8DDD\u5730\u534A\u5C3A\u7684\u58C1\u811A\u5C3D\u662F\u8FDE\u7EF5\u4E0D\u65AD\u7684\u7EB9\u9970\uFF0C\u82B1\u8349\u5C71\u77F3\u8BF8\u8272\u6742\u9648\u3002\u90A3\u4E2A\u4E0E\u7236\u4EB2\u6307\u4E0B\u4E00\u822C\u65E0\u4E8C\u7684\u56FE\u5F62\u50CF\u6735\u4E0D\u89C4\u5219\u7684\u6885\u82B1\uFF0C\u6241\u6241\u5206\u6210\u4E94\u74E3\uFF0C\u6B63\u4E2D\u6709\u4E00\u4E2A\u7EC6\u5C0F\u7684\u5B54\u6D1E\u3002\u6BCF\u74E3\u5927\u5C0F\u4E0D\u4E00\uFF0C\u5374\u6709\u79CD\u5947\u5F02\u7684\u89C4\u5F8B\uFF0C\u8BA9\u4EBA\u8FC7\u76EE\u96BE\u5FD8\uFF0C\u663E\u7136\u4E0D\u662F\u968F\u624B\u9542\u523B\u3002</p><p>\u5979\u8BD5\u7740\u6309\u4E86\u6309\uFF0C\u56FE\u5F62\u7EB9\u4E1D\u4E0D\u52A8\u3002\u7D2B\u73AB\u7ACB\u523B\u4ECE\u5934\u4E0A\u62E8\u4E0B\u94F6\u9497\uFF0C\u7528\u9497\u5C16\u671D\u5B54\u6D1E\u4E2D\u5FC3\u63A2\u53BB\u3002\u624B\u4E0A\u4F3C\u4E4E\u5FAE\u5FAE\u4E00\u52A8\uFF0C\u4FBF\u518D\u6CA1\u6709\u52A8\u9759\u3002\u7D2B\u73AB\u4ECD\u4E0D\u6B7B\u5FC3\uFF0C\u53C8\u6CBF\u7740\u82B1\u7EB9\u8FB9\u7F18\u7EC6\u7EC6\u5212\u8FC7\uFF0C\u751A\u81F3\u8FDE\u5176\u5B83\u56FE\u5F62\u4E5F\u4E00\u4E00\u68C0\u67E5\uFF0C\u4ECD\u7136\u6BEB\u65E0\u5934\u7EEA\u3002</p><p>\u752C\u9053\u91CC\u4F20\u6765\u4E00\u9635\u5FAE\u54CD\uFF0C\u7D2B\u73AB\u8FDE\u5FD9\u8DF3\u4E0A\u5E8A\uFF0C\u88C5\u4F5C\u719F\u7761\u3002</p><p>\u7247\u523B\u540E\u6155\u5BB9\u9F99\u63A8\u95E8\u800C\u5165\uFF0C\u63D0\u7740\u4E00\u4E2A\u5305\u88F9\u65BD\u65BD\u7136\u8D70\u5230\u5E8A\u8FB9\uFF0C\u8F7B\u8F7B\u62C9\u5F00\u4E86\u9526\u88AB\u4E00\u89D2\u3002</p><p>\u7D2B\u73AB\u4F3C\u4E4E\u6709\u4E9B\u70ED\uFF0C\u7EA2\u6251\u6251\u7684\u4FCF\u8138\u5A07\u8273\u6B32\u6EF4\u3002\u6155\u5BB9\u9F99\u7B11\u7740\u634F\u4E86\u634F\u5979\u7684\u9F3B\u5C16\uFF0C\u7389\u4EBA\u5624\u549B\u4E00\u58F0\uFF0C\u7F13\u7F13\u7741\u5F00\u773C\u3002</p><p>\u300C\u795E\u5BAB\u56DB\u5B63\u6052\u6625\uFF0C\u4E0D\u5FC5\u76D6\u8FD9\u4E48\u7D27\u3002\u300D\u6155\u5BB9\u9F99\u8BF4\u7740\u6380\u5F00\u4E86\u88AB\u5B50\uFF0C\u76EE\u5149\u843D\u5728\u5979\u624B\u4E0A\uFF0C\u4E0D\u7531\u4E00\u6123\u3002</p><p>\u7D2B\u73AB\u8FD9\u624D\u60F3\u8D77\u81EA\u5DF1\u8FD8\u63E1\u7740\u94F6\u9497\uFF0C\u53EA\u597D\u88C5\u505A\u4E0D\u77E5\u9053\u4F3C\u7684\u7528\u624B\u80CC\u63C9\u4E86\u63C9\u773C\uFF0C\u817B\u58F0\u9053\uFF1A\u300C\u4EC0\u4E48\u65F6\u5019\u4E86\uFF1F\u300D</p><p>\u6155\u5BB9\u9F99\u62FF\u8FC7\u94F6\u9497\uFF0C\u77A5\u4E86\u5979\u4E00\u773C\uFF0C\u300C\u7761\u89C9\u8FD8\u62FF\u8FD9\u4E2A\uFF1F\u300D</p><p>\u7D2B\u73AB\u4F38\u4F38\u61D2\u8170\uFF0C\u6253\u4E86\u4E2A\u5C0F\u5DE7\u7684\u5475\u6B20\uFF0C\u300C\u6211\u6015\u624E\u4E86\u5934\u561B\uFF0C\u7761\u7740\u5C31\u5FD8\u4E86\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u51B7\u7B11\u4E00\u4E0B\uFF0C\u663E\u7136\u662F\u4E0D\u4FE1\u5979\u7684\u89E3\u91CA\uFF0C\u4F46\u5728\u5BA4\u5185\u770B\u4E86\u4E00\u904D\uFF0C\u5E76\u672A\u53D1\u73B0\u6709\u4F55\u5F02\u6837\uFF0C\u4FBF\u4E5F\u4E0D\u518D\u8FFD\u7A76\uFF0C\u300C\u8D77\u6765\u6D17\u6D17\u8138\uFF0C\u5403\u4E9B\u4E1C\u897F\u3002\u300D\u8BDD\u521A\u51FA\u53E3\uFF0C\u6155\u5BB9\u9F99\u5C31\u4E0D\u7531\u4E00\u6123\u3002\u6709\u591A\u5C11\u5E74\u672A\u66FE\u5982\u6B64\u548C\u989C\u60A6\u8272\u5730\u8BF4\u8FC7\u8BDD\u4E86\u2014\u2014\u5C24\u5176\u662F\u5BF9\u4E00\u4E2A\u5973\u4EBA\u3002</p><p>\u7D2B\u73AB\u5374\u672A\u7559\u610F\u4ED6\u7684\u53E3\u6C14\uFF0C\u4E56\u4E56\u8DDF\u7740\u8D77\u8EAB\uFF0C\u5FC3\u91CC\u8FD8\u5728\u601D\u7D22\u8D3E\u3001\u4E01\u8FD9\u4E24\u4E2A\u5173\u952E\u4EBA\u7269\u3002</p><p>\u6155\u5BB9\u9F99\u628A\u5305\u88F9\u653E\u5728\u5E8A\u4E0A\uFF0C\u6CA1\u6709\u8BF4\u8BDD\u3002\u7D2B\u73AB\u7591\u60D1\u5730\u89E3\u5F00\u5305\u88F9\uFF0C\u53EA\u89C1\u91CC\u9762\u653E\u7740\u51E0\u4EF6\u7F57\u8863\u3002</p><p>\u7EA2\u8272\u672C\u6765\u6781\u591A\uFF0C\u4F46\u8FD9\u51E0\u4EF6\u4E0D\u77E5\u4F55\u7269\u7EC7\u6210\u7684\u7EC6\u7EF8\u65E0\u8BBA\u7EA2\u8272\u6DF1\u6D45\uFF0C\u90FD\u6709\u79CD\u8131\u4FD7\u7684\u4EAE\u4E3D\u3002\u4ED4\u7EC6\u770B\u53BB\uFF0C\u53C8\u4F3C\u4E4E\u5E73\u5E73\u65E0\u5947\u3002\u5F85\u773C\u73E0\u4E00\u8F6C\uFF0C\u89C6\u91CE\u672A\u53CA\u5904\u5374\u9690\u9690\u95EA\u4EAE\u3002\u7D2B\u73AB\u559C\u6ECB\u6ECB\u62FF\u8D77\u8863\u670D\u5728\u8EAB\u4E0A\u6BD4\u4E86\u6BD4\uFF0C\u8F6C\u5934\u9053\uFF1A\u300C\u4F60\u5148\u51FA\u53BB\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u5FAE\u5FAE\u4E00\u7B11\uFF0C\u8D70\u51FA\u77F3\u5BA4\uFF0C\u987A\u624B\u63A9\u4E0A\u623F\u95E8\u3002</p><p>\u4ED6\u521A\u5173\u4E0A\u95E8\uFF0C\u7D2B\u73AB\u7684\u7B11\u8138\u7ACB\u523B\u6D88\u5931\u5730\u65E0\u5F71\u65E0\u8E2A\uFF0C\u4FCF\u7ACB\u5BA4\u4E2D\u9759\u9ED8\u7247\u523B\uFF0C\u54AC\u7259\u8131\u53BB\u6C34\u9760\u3002</p><p>\u4E0D\u591A\u65F6\u623F\u95E8\u62C9\u5F00\u4E00\u6761\u7EC6\u7F1D\uFF0C\u7D2B\u73AB\u5C0F\u58F0\u8BF4\uFF1A\u300C\u600E\u4E48\u5C11\u4E86\u4E00\u4EF6\uFF1F\u300D</p><p>\u300C\u6CA1\u5C11\u554A\uFF1F\u300D</p><p>\u7D2B\u73AB\u987F\u8DB3\u9053\uFF1A\u300C\u5C11\u4E86\u4EB5\u88E4\uFF01\u300D</p><p>\u300C\u54E6\u2026\u2026\u661F\u6708\u6E56\u7684\u5973\u4EBA\u90FD\u4E0D\u7A7F\u4EB5\u88E4\u2014\u2014\u5979\u4EEC\u4E00\u822C\u53EA\u7A7F\u6700\u5916\u9762\u7684\u8584\u7EB1\uFF0C\u90A3\u4E5F\u662F\u4E3A\u4E86\u8BA9\u4E3B\u5B50\u64CD\u8D77\u6765\u6709\u5174\u81F4\u3002\u300D\u6155\u5BB9\u9F99\u7B11\u563B\u563B\u76EF\u7740\u95E8\u7F1D\u4E2D\u7684\u4FCF\u8138\uFF0C\u300C\u4F60\u8FD8\u591A\u4E86\u597D\u51E0\u4EF6\u5462\u2026\u2026\u300D</p><p>\u7D2B\u73AB\u300C\u546F\u300D\u5730\u7838\u4E0A\u95E8\u3002</p><p>\u597D\u5728\u8FD8\u6709\u4EB5\u8863\u3001\u7EE3\u8966\uFF0C\u518D\u5957\u4E0A\u5916\u886B\uFF0C\u62AB\u4E0A\u7F69\u7EB1\uFF0C\u8D70\u8DEF\u5C0F\u5FC3\u4E9B\u4E5F\u770B\u4E0D\u51FA\u6765\u3002\u53EA\u662F\u4EB5\u8863\u672A\u514D\u592A\u4F4E\u4E86\uFF0C\u5979\u62FC\u547D\u5F80\u4E0A\u63D0\uFF0C\u624D\u52C9\u5F3A\u63A9\u4F4F\u4E73\u6C9F\u3002</p><p>\u6155\u5BB9\u9F99\u773C\u524D\u4E00\u4EAE\uFF0C\u4E0A\u4E0B\u6253\u91CF\u73AB\u7470\u542B\u82DE\u822C\u7684\u7D2B\u73AB\u3002\u867D\u7136\u90FD\u662F\u7EA2\u8272\uFF0C\u4F46\u6731\u3001\u8D64\u3001\u4E39\u3001\u5F64\u3001\u7C89\u3001\u7EEF\u8BF8\u8272\u53C2\u5DEE\uFF0C\u53D8\u5316\u65E0\u7A77\u3002\u5BBD\u7A84\u5408\u5EA6\u7684\u7F57\u8863\u886C\u7740\u6D3B\u8272\u751F\u9999\u7684\u5A07\u8EAF\uFF0C\u66F4\u663E\u5F97\u5A40\u5A1C\u591A\u59FF\uFF0C\u5145\u6EE1\u5C11\u5973\u82AC\u82B3\u7684\u6C14\u606F\u3002</p><p>\u7D2B\u73AB\u62E2\u7D27\u7F69\u7EB1\uFF0C\u767D\u4E86\u4ED6\u4E00\u773C\uFF0C\u5176\u5B9E\u5374\u5728\u7559\u610F\u8FD9\u5EA7\u795E\u79D8\u7684\u77F3\u5BAB\u3002\u752C\u9053\u9AD8\u516B\u5C3A\uFF0C\u5BBD\u6709\u4E09\u5C3A\uFF0C\u5468\u906D\u4E0D\u89C1\u780C\u75D5\uFF0C\u5206\u660E\u662F\u76F4\u63A5\u4ECE\u77F3\u4E2D\u5F00\u51FA\u7684\u6D1E\u7A74\u3002\u752C\u9053\u4E24\u65C1\u5DE6\u56DB\u53F3\u4E94\u5171\u6709\u4E5D\u95F4\u77F3\u5BA4\uFF0C\u5404\u5BA4\u95E8\u6963\u4E0A\u5206\u522B\u9542\u523B\u5C0F\u5C0F\u7684\u300C\u4E59\u3001\u4E19\u3001\u4E01\u300D\u7B49\u5B57\u6837\uFF0C\u4EE5\u5929\u5E72\u4E3A\u5E8F\u3002</p><p>\u5979\u4E00\u56DE\u5934\uFF0C\u81EA\u5DF1\u6240\u5728\u7684\u679C\u7136\u662F\u300C\u7532\u300D\u5BA4\uFF0C\u800C\u5DE6\u9996\u7B2C\u4E00\u95F4\u300C\u5E9A\u300D\u5BA4\u4E43\u662F\u6BCD\u4EB2\u6240\u5C45\u3002</p><p>\u7D2B\u73AB\u9759\u4E0B\u5FC3\u6765\uFF0C\u95EE\u9053\uFF1A\u300C\u5C0F\u83BA\u5462\uFF1F\u300D</p><p>\u300C\u5979\u6562\u60F9\u4F60\u751F\u6C14\uFF0C\u6211\u8BA9\u5979\u53BB\u53D7\u7F5A\u4E86\u3002\u300D</p><p>\u7D2B\u73AB\u4E00\u60CA\uFF0C\u300C\u60F9\u6211\u751F\u6C14\uFF1F\u6CA1\u6709\u554A\uFF1F\u300D</p><p>\u6155\u5BB9\u9F99\u56DE\u6765\u65F6\u770B\u5230\u767D\u7389\u83BA\u7AD9\u5728\u95E8\u5916\uFF0C\u8BEF\u4EE5\u4E3A\u662F\u60F9\u5F97\u7D2B\u73AB\u4E0D\u5FEB\uFF0C\u4E0D\u7531\u5206\u8BF4\u5C31\u628A\u5979\u8D76\u5230\u53F6\u884C\u5357\u5904\uFF0C\u81EA\u884C\u53D7\u7F5A\u3002\u7A76\u7ADF\u662F\u5426\u51A4\u6789\uFF0C\u6155\u5BB9\u9F99\u4E5F\u4E0D\u653E\u5728\u5FC3\u4E0A\uFF0C\u4ED6\u51B7\u7B11\u9053\uFF1A\u300C\u4E0D\u5728\u5BA4\u5185\u4F3A\u5019\u4FBF\u662F\u6709\u7F6A\uFF0C\u4E0D\u5FC5\u7406\u5979\u4E86\u3002\u300D</p><p>\u7D2B\u73AB\u6CA1\u60F3\u5230\u4E3A\u4E00\u70B9\u7B97\u4E0D\u5F97\u4E8B\u7684\u7F18\u7531\u53C8\u8BA9\u767D\u7389\u83BA\u53D7\u82E6\uFF0C\u5FC3\u91CC\u4E00\u4E2A\u52B2\u7684\u540E\u6094\u3002\u4EFB\u6155\u5BB9\u9F99\u600E\u4E48\u62C9\u5979\u90FD\u4E0D\u8FC8\u6B65\uFF0C\u8D4C\u6C14\u8BF4\uFF1A\u300C\u4F60\u5148\u628A\u5C0F\u83BA\u53EB\u6765\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u65E0\u5948\u4E4B\u4E0B\uFF0C\u53EA\u597D\u51FB\u94C3\u53EC\u5524\u3002\u4E0D\u591A\u65F6\u767D\u7389\u83BA\u6162\u6162\u8D70\u8FC7\u6765\uFF0C\u7D2B\u73AB\u89C1\u5979\u56DB\u80A2\u65E0\u6059\uFF0C\u5148\u677E\u4E86\u53E3\u6C14\u3002</p><p>\u968F\u7740\u811A\u6B65\u7684\u632A\u52A8\uFF0C\u4E00\u9635\u6E05\u8106\u7684\u94C3\u58F0\u4ECE\u767D\u7389\u83BA\u8EAB\u4E0A\u9690\u9690\u4F20\u6765\u3002\u8D70\u8FD1\u624D\u53D1\u73B0\u7EA2\u7EB1\u4E0B\u767D\u5AE9\u7684\u53CC\u4E73\u8840\u8FF9\u5B9B\u7136\uFF0C\u53EA\u7247\u523B\u5DE5\u592B\u4E73\u5C16\u4FBF\u5DF2\u591A\u4E86\u4E24\u4E2A\u94C3\u94DB\u3002\u8EAB\u4E0A\u9707\u6C64\u7684\u75BC\u75DB\u4F7F\u767D\u7389\u83BA\u67F3\u7709\u7D27\u98A6\uFF0C\u521A\u624D\u53F6\u884C\u5357\u786C\u751F\u751F\u628A\u4E09\u53EA\u91D1\u73AF\u5206\u522B\u7A7F\u5728\u5979\u7684\u4E73\u5934\u7684\u82B1\u8482\u4E0A\uFF0C\u53C8\u60AC\u4E0A\u94C3\u94DB\u3002\u6700\u5A07\u5AE9\u89E6\u611F\u7684\u90E8\u4F4D\u7A7F\u4E0A\u6C89\u7538\u7538\u7684\u7269\u4F53\uFF0C\u6BCF\u4E00\u6B65\u5BF9\u5979\u6765\u8BF4\u90FD\u662F\u6298\u78E8\u3002</p><p>\u7D2B\u73AB\u6CEA\u5149\u76C8\u7136\uFF0C\u62C9\u8D77\u5979\u7684\u624B\u54ED\u9053\uFF1A\u300C\u5BF9\u4E0D\u8D77\u2026\u2026\u300D</p><p>\u767D\u7389\u83BA\u8FD8\u672A\u4F5C\u58F0\uFF0C\u4FBF\u88AB\u6155\u5BB9\u9F99\u8E22\u4E86\u4E00\u811A\uFF0C\u300C\u5979\u4EEC\u53EA\u662F\u5974\u5A62\uFF0C\u4E3B\u5B50\u7684\u73A9\u7269\uFF0C\u751F\u6B7B\u7531\u6211\uFF01\u8BF4\u4EC0\u4E48\u5BF9\u4E0D\u8D77\uFF01\u300D</p><p>\u7D2B\u73AB\u54AC\u4F4F\u7EA2\u5507\uFF0C\u534A\u664C\u624D\u6162\u6162\u677E\u5F00\uFF0C\u4F4E\u58F0\u8BF4\uFF1A\u300C\u5C0F\u83BA\uFF0C\u4F60\u5148\u53BB\u4F11\u606F\u5427\u3002\u300D</p><p>\u867D\u662F\u540C\u7236\u540C\u6BCD\uFF0C\u6155\u5BB9\u9F99\u5374\u6BD4\u7D2B\u73AB\u9AD8\u4E86\u4E00\u4E2A\u5934\uFF0C\u4ED6\u63FD\u4F4F\u7D2B\u73AB\u7684\u7EA4\u8170\uFF0C\u62E5\u7740\u59B9\u59B9\u8D70\u5230\u53F3\u4FA7\u7B2C\u4E09\u95F4\u300C\u4E01\u300D\u5BA4\u3002\u7D2B\u73AB\u538B\u4F4F\u5FC3\u5E95\u7684\u6012\u6C14\uFF0C\u4E0D\u52A8\u58F0\u8272\u5730\u8DDF\u5728\u540E\u9762\u3002</p><p>\u8FD9\u4E2A\u6DF7\u86CB\u5206\u660E\u4E0D\u628A\u5973\u4EBA\u5F53\u4EBA\u3002\u65E0\u8BBA\u662F\u4E09\u4F4D\u5E08\u59D0\u8FD8\u662F\u767D\u6C0F\u59D0\u59B9\uFF0C\u751A\u81F3\u662F\u751F\u8EAB\u6BCD\u4EB2\uFF0C\u4ED6\u90FD\u5BF9\u4E4B\u90FD\u5982\u5BF9\u72AC\u8C55\u3002\u773C\u4E0B\u65E0\u8BBA\u5982\u4F55\u90FD\u8981\u4FDD\u4F4F\u81EA\u5DF1\uFF0C\u8FD9\u6837\u624D\u80FD\u60F3\u529E\u6CD5\u6551\u51FA\u4F17\u4EBA\u3002</p><p>\u63A8\u5F00\u95E8\uFF0C\u4FBF\u542C\u5230\u6DD9\u6DD9\u7684\u6D41\u6C34\u58F0\u3002\u6E05\u6F88\u7684\u6CC9\u6C34\u4ECE\u77F3\u58C1\u7684\u7F1D\u9699\u4E2D\u6D8C\u51FA\uFF0C\u843D\u5728\u4E00\u4E2A\u4E24\u4E08\u6709\u4F59\u7684\u6C34\u6C60\u5185\u3002\u5BA4\u5185\u6C34\u6C7D\u7F2D\u7ED5\uFF0C\u5374\u662F\u4E00\u6CE8\u6E29\u6CC9\u3002</p><p>\u6155\u5BB9\u9F99\u4F4E\u7B11\u9053\uFF1A\u300C\u5F80\u540E\u54E5\u54E5\u548C\u4F60\u5C31\u5728\u8FD9\u91CC\u9E33\u9E2F\u540C\u6D74\uFF0C\u5982\u4F55\uFF1F\u300D</p><p>\u7D2B\u73AB\u6743\u5F53\u6CA1\u542C\u89C1\u4ED6\u7684\u98CE\u8A00\u98CE\u8BED\uFF0C\u85C9\u7740\u5F2F\u8170\u63A5\u6C34\uFF0C\u6084\u6084\u626B\u89C6\u5899\u811A\u3002\u4E00\u6837\u7684\u7EB9\u9970\u5BC6\u5E03\uFF0C\u5374\u6CA1\u6709\u90A3\u4E2A\u56FE\u5F62\u3002\u7D2B\u73AB\u6162\u60A0\u60A0\u6D17\u7740\u624B\uFF0C\u53C8\u4ED4\u7EC6\u770B\u4E86\u4E00\u904D\u3002\u534A\u664C\u540E\uFF0C\u5979\u5FC3\u6709\u4E0D\u7518\u76F4\u8D77\u8170\uFF0C\u53C8\u88C5\u4F5C\u6574\u7406\u978B\u5B50\uFF0C\u5C06\u8EAB\u540E\u90E8\u5206\u4E5F\u7EC6\u770B\u4E86\u4E00\u904D\u3002</p><p>\u6155\u5BB9\u9F99\u8010\u7740\u6027\u5B50\u7B49\u4E86\u8DB3\u8DB3\u4E00\u523B\u949F\uFF0C\u7D2B\u73AB\u597D\u4E0D\u5BB9\u6613\u6574\u7406\u505C\u5F53\uFF0C\u626C\u8138\u5AE3\u7136\u4E00\u7B11\uFF0C\u628A\u5C0F\u624B\u67D4\u67D4\u9012\u5230\u4ED6\u638C\u4E2D\u3002</p><p>\u9178\u9178\u751C\u751C\u7684\u5947\u5F02\u611F\u89C9\u6D8C\u4E0A\u5FC3\u5934\uFF0C\u957F\u5E74\u751F\u6D3B\u5728\u9634\u6697\u7684\u5730\u7A9F\u4E2D\u800C\u626D\u66F2\u7684\u5FC3\u7075\uFF0C\u50CF\u88AB\u8FD9\u53EA\u5149\u6DA6\u5982\u7389\u7684\u5C0F\u624B\u8F7B\u8F7B\u634F\u4E86\u4E00\u628A\uFF0C\u6155\u5BB9\u9F99\u6123\u4E86\u7247\u523B\u624D\u60F3\u8D77\u63E1\u7D27\u5979\u7684\u624B\u638C\u3002</p><p>\u7D2B\u73AB\u5374\u60F3\u8D77\u6CAE\u6E20\u5C55\u626C\u2014\u2014\u4EE5\u524D\u4E5F\u662F\u8FD9\u6837\uFF0C\u6709\u65F6\u5019\u60F9\u4ED6\u751F\u6C14\u4E86\uFF0C\u53EA\u8981\u8FD9\u6837\u4E00\u7B11\u4E00\u4F38\u624B\uFF0C\u5C55\u626C\u54E5\u54E5\u5C31\u4F1A\u8F6C\u6012\u4E3A\u559C\u3002\u9876\u591A\u518D\u8BF4\u4E24\u53E5\u8F6F\u8BDD\u2026\u2026\u7D2B\u73AB\u5FC3\u91CC\u4E00\u9178\uFF0C\u4E0D\u77E5\u9053\u5C55\u626C\u54E5\u54E5\u73B0\u5728\u600E\u4E48\u6837\u4E86\u3002\u970D\u72C2\u7130\u8FFD\u5230\u6B66\u9675\u4F1A\u4E0D\u4F1A\u4F24\u5BB3\u4ED6\u5462\uFF1F</p><p>\u6155\u5BB9\u9F99\u5947\u602A\u5730\u95EE\u9053\uFF1A\u300C\u4F60\u51B7\u5417\uFF1F\u300D</p><p>\u7D2B\u73AB\u8FDE\u5FD9\u6447\u4E86\u6447\u5934\uFF0C\u6B62\u4F4F\u98A4\u6296\uFF0C\u65CB\u5373\u76B1\u8D77\u7709\u5934\u8BF4\u9053\uFF1A\u300C\u771F\u662F\u6709\u4E9B\u51B7\u5462\u2026\u2026\u6CA1\u6709\u771F\u6C14\u62A4\u4F53\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u7B11\u9053\uFF1A\u300C\u4E0D\u7528\u62C5\u5FC3\uFF0C\u53EA\u662F\u53F6\u62A4\u6CD5\u7ED9\u4F60\u7528\u4E86\u6563\u529F\u7684\u836F\u7269\u2014\u2014\u53EA\u8981\u4E00\u76F4\u8FD9\u4E48\u542C\u8BDD\uFF0C\u8FC7\u51E0\u5929\u5C31\u7ED9\u4F60\u89E3\u836F\u3002\u300D</p><p>\u7D2B\u73AB\u4E56\u4E56\u70B9\u4E86\u70B9\u5934\uFF0C\u4E0E\u4ED6\u5E76\u80A9\u8D70\u51FA\u957F\u957F\u7684\u752C\u9053\u3002</p><p>\u4E00\u62AC\u773C\uFF0C\u7D2B\u73AB\u987F\u65F6\u6123\u4F4F\u4E86\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u53D1\u89C9\u9B54\u5BAB\u4E0E\u5B9D\u85CF\u6709\u8054\u7CFB\u540E\uFF0C\u6155\u5BB9\u7D2B\u73AB\u5904\u5904\u7559\u610F\u3002\u6B64\u65F6\u81EA\u5DF1\u7AD9\u5728\u9B54\u5BAB\u5927\u5385\u4E2D\uFF0C\u9762\u524D\u662F\u90A3\u4E2A\u7855\u5927\u7684\u592A\u6781\u56FE\uFF0C\u5468\u56F4\u4E94\u6761\u752C\u9053\uFF0C\u9752\u7389\u95E8\u6963\u4E0A\u5206\u522B\u523B\u7740\u300C\u5929\u3001\u5730\u3001\u541B\u3001\u4EB2\u3001\u5E08\u300D\u5B57\u6837\uFF0C\u6B63\u5408\u7236\u4EB2\u4E34\u7EC8\u7559\u4E0B\u7684\u9057\u8A00\u3002</p><p>\u7D2B\u73AB\u62FC\u547D\u538B\u4E0B\u5FC3\u91CC\u7684\u6FC0\u52A8\uFF0C\u6B22\u6B23\u5730\u8BF4\uFF1A\u300C\u8FD9\u4E48\u591A\u623F\u95F4\u2014\u2014\u54E5\u54E5\uFF0C\u4F60\u7ED9\u6211\u8BB2\u8BB2\u8FD9\u4E2A\u77F3\u5BAB\u5427\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u89C1\u59B9\u59B9\u5982\u6B64\u9AD8\u5174\uFF0C\u60F3\u5230\u4ECA\u540E\u8981\u4E0E\u5979\u5728\u6B64\u53CC\u5BBF\u53CC\u98DE\uFF0C\u4E0D\u7531\u6B23\u7136\u9053\uFF1A\u300C\u8FD9\u662F\u661F\u6708\u6E56\u795E\u5BAB\uFF0C\u4E5F\u662F\u5386\u4EE3\u5BAB\u4E3B\u5C45\u5904\uFF0C\u975E\u62A4\u6CD5\u4EE5\u4E0A\u8005\u4E0D\u5F97\u64C5\u5165\u3002\u4F60\u770B\uFF0C\u8FD9\u4E2A\u592A\u6781\u56FE\u662F\u795E\u5BAB\u4E2D\u5FC3\u6240\u5728\u3002\u65C1\u8FB9\u4E94\u6761\u752C\u9053\u5206\u522B\u662F\u5929\u5730\u541B\u4EB2\u5E08\u3002\u5929\u4EB2\u5E08\u4E09\u6761\u5404\u6709\u5341\u95F4\u77F3\u5BA4\uFF0C\u4EE5\u5929\u5E72\u4E3A\u5E8F\u3002</p><p>\u6211\u4EEC\u4F4F\u7684\u5929\u5B57\u7532\u5BA4\uFF0C\u4E43\u795E\u5BAB\u81F3\u9AD8\u65E0\u4E0A\u7684\u5723\u5730\u3002\u5E08\u5B57\u752C\u9053\u662F\u8BF8\u4F4D\u4F7F\u8005\u3001\u62A4\u6CD5\u5C45\u5904\uFF0C\u73B0\u5728\u795E\u6559\u4E24\u4EEA\u4F7F\u8005\u7A7A\u7F3A\uFF0C\u6731\u90AA\u62A4\u6CD5\u4E0E\u5C48\u62A4\u6CD5\u2026\u2026\u55EF\uFF0C\u4E0D\u5728\u5BAB\u4E2D\u3002\u53EA\u6709\u53F6\u62A4\u6CD5\u4E00\u4EBA\u4F4F\u5728\u8F9B\u5BA4\uFF0C\u4ED6\u662F\u6559\u4E2D\u795E\u533B\u3002\u6709\u4EC0\u4E48\u4E0D\u8212\u670D\u7684\uFF0C\u627E\u4ED6\u5C31\u884C\u4E86\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u6307\u7740\u5DE6\u9996\u523B\u7740\u300C\u5730\u300D\u5B57\u7684\u752C\u9053\u8BF4\uFF1A\u300C\u8FD9\u4E0E\u541B\u5B57\u752C\u9053\u5404\u5BA4\u4EE5\u5730\u652F\u4E3A\u5E8F\uFF0C\u7528\u6765\u5904\u7406\u6559\u5185\u53DB\u5F92\u548C\u6559\u5916\u8FDD\u547D\u8005\u3002\u5475\u5475\uFF0C\u8FD9\u4E2A\u4F60\u5C31\u4E0D\u5FC5\u53BB\u770B\u4E86\u3002\u300D</p><p>\u7D2B\u73AB\u8BD5\u63A2\u7740\u95EE\u9053\uFF1A\u300C\u795E\u6559\u65E2\u7136\u662F\u9053\u5BB6\u4E00\u8109\uFF0C\u4E3A\u4F55\u8981\u7528\u5929\u5730\u541B\u4EB2\u5E08\u8FD9\u4E9B\u5112\u5BB6\u5B57\u6837\u5462\uFF1F\u300D</p><p>\u300C\u54C8\uFF0C\u8FD9\u4E2A\u6211\u5C31\u4E0D\u77E5\u9053\u4E86\u3002\u5730\u5982\u72F1\uFF0C\u541B\u5982\u5A01\uFF0C\u5E08\u5982\u5BBE\uFF0C\u4EB2\u5982\u53CB\uFF0C\u5929\u4E3A\u5723\uFF0C\u4E5F\u4E0D\u4EC5\u9650\u4E8E\u5112\u95E8\u5427\u3002\u300D</p><p>\u7D2B\u73AB\u542C\u51FA\u4ED6\u5E76\u4E0D\u77E5\u60C5\uFF0C\u4FBF\u8F6C\u8FC7\u8BDD\u9898\uFF0C\u300C\u4EB2\u5B57\u662F\u505A\u4EC0\u4E48\u7528\u7684\uFF1F\u300D</p><p>\u6155\u5BB9\u9F99\u773C\u5149\u4E00\u95EA\uFF0C\u300C\u5E08\u4EB2\u4E24\u9053\u662F\u795E\u5BAB\u7684\u4E24\u6761\u51FA\u53E3\u3002\u4EB2\u5B57\u4E5F\u662F\u672C\u6559\u624B\u8DB3\u4F11\u95F2\u4E4B\u6240\u3002\u73B0\u5728\u98CE\u5A4A\u5B50\u548C\u7EAA\u5A4A\u5B50\u6B63\u5728\u91CC\u9762\u63A5\u5BA2\u5462\u2026\u2026\u300D</p><p>\u7D2B\u73AB\u4E00\u6123\uFF0C\u8FD9\u624D\u660E\u767D\u4ED6\u5F53\u521D\u8BF4\u7684\u300C\u540C\u4F8B\u300D\u662F\u4EC0\u4E48\u610F\u601D\u3002\u60F3\u5230\u5E08\u59D0\u6240\u53D7\u5C48\u8FB1\uFF0C\u5979\u4E0D\u7531\u7684\u5931\u58F0\u60CA\u53EB\u3002\u7D2B\u73AB\u987E\u4E0D\u5F97\u53BB\u601D\u7D22\u752C\u9053\u4E0E\u5B9D\u85CF\u7684\u8054\u7CFB\uFF0C\u8F6C\u8EAB\u4FBF\u671D\u4EB2\u5B57\u752C\u9053\u5954\u53BB\u3002</p><p>\u521A\u8FC8\u51FA\u4E24\u6B65\uFF0C\u771F\u6C14\u88AB\u5236\u7684\u7D2B\u73AB\u5C31\u88AB\u4E00\u628A\u62C9\u4F4F\u3002\u6155\u5BB9\u9F99\u5389\u58F0\u9053\uFF1A\u300C\u544A\u8BC9\u4F60\uFF0C\u8FD9\u4E24\u4E2A\u5A4A\u5B50\u5DF2\u7ECF\u662F\u4F18\u5F85\u4E86\uFF01\u5176\u4ED6\u5BA4\u91CC\u4ECE\u6765\u90FD\u672A\u9650\u5236\u8FC7\u4EBA\u6570\uFF01\u300D</p><p>\u7D2B\u73AB\u54FD\u54BD\u9053\uFF1A\u300C\u4F60\u8981\u600E\u4E48\u624D\u653E\u8FC7\u5979\u4EEC\uFF1F\u300D</p><p>\u6155\u5BB9\u9F99\u65AD\u7136\u6447\u5934\uFF0C\u300C\u63B3\u5165\u795E\u5BAB\u7684\u5973\u5B50\u4E00\u5F8B\u7EC8\u8EAB\u4E3A\u5974\u2014\u2014\u4F60\u662F\u552F\u4E00\u7684\u4F8B\u5916\u3002\u300D</p><p>\u6155\u5BB9\u7D2B\u73AB\u5C16\u53EB\u9053\uFF1A\u300C\u4F60\u628A\u6211\u4E5F\u6254\u90A3\u91CC\u597D\u4E86\uFF01\u300D</p><p>\u6155\u5BB9\u9F99\u773C\u4E2D\u5BD2\u5149\u4E00\u95EA\uFF0C\u54AC\u7259\u9053\uFF1A\u300C\u4F60\u4EE5\u4E3A\u6211\u4E0D\u6562\uFF1F\u300D</p><p>\u4E0E\u4ED6\u51B7\u5389\u7684\u76EE\u5149\u4E00\u89E6\uFF0C\u7D2B\u73AB\u4E0D\u7531\u6253\u4E86\u4E2A\u54C6\u55E6\uFF0C\u534A\u664C\u624D\u8BF4\uFF1A\u300C\u80FD\u4E0D\u80FD\u8BA9\u5979\u4EEC\u50CF\u5C0F\u83BA\u5C0F\u9E42\u4E00\u6837\uFF0C\u4F4F\u5728\u5BAB\u91CC\uFF1F\u300D</p><p>\u6155\u5BB9\u9F99\u8FDF\u7591\u7247\u523B\uFF0C\u7EAA\u5A4A\u5B50\u6B66\u529F\u5DF2\u5E9F\uFF0C\u53C8\u6E29\u987A\u9A6F\u670D\uFF0C\u7559\u5728\u77F3\u5BAB\u4E5F\u65E0\u5927\u788D\u3002\u98CE\u5A4A\u5B50\u5165\u5BAB\u4E0D\u8FC7\u4E24\u65E5\uFF0C\u53EA\u6015\u91CE\u6027\u672A\u9664\u3002</p><p>\u300C\u660E\u65E5\u8BA9\u7EAA\u5A4A\u5B50\u5148\u8FDB\u6765\u3002\u7B49\u4F60\u4E56\u4E56\u4E0E\u6211\u6210\u4EB2\u540E\uFF0C\u518D\u8BA9\u98CE\u5A4A\u5B50\u4E5F\u5165\u5BAB\u4E2D\uFF0C\u600E\u4E48\u6837\uFF1F\u300D</p><p>\u300C\u4E0D\u884C\uFF01\u9A6C\u4E0A\u5C31\u8BA9\u5979\u4EEC\u4E24\u4E2A\u8FDB\u6765\uFF01\u300D</p><p>\u6155\u5BB9\u9F99\u626C\u8D77\u8138\u4ECE\u9F3B\u5C16\u50B2\u89C6\u8FD9\u4E2A\u6124\u6012\u7684\u5C0F\u59D1\u5A18\u3002</p><p>\u5BF9\u89C6\u7247\u523B\u540E\uFF0C\u7D2B\u73AB\u89C1\u4ED6\u5FC3\u610F\u5DF2\u51B3\uFF0C\u53EA\u597D\u8BA9\u6B65\uFF0C\u6EE1\u8138\u4E5E\u6C42\u5730\u8F7B\u58F0\u9053\uFF1A\u300C\u7EAA\u5E08\u59D0\u9A6C\u4E0A\u6765\uFF0C\u98CE\u5E08\u59D0\u5148\u6CBB\u4F24\u597D\u4E0D\u597D\uFF1F\u300D</p><p>\u6155\u5BB9\u9F99\u51B7\u7B11\u9053\uFF1A\u300C\u6D41\u971C\u5251\u540D\u5934\u54CD\u4EAE\uFF0C\u5927\u4F19\u8FD8\u6CA1\u6709\u64CD\u591F\u2014\u2014\u56DB\u6708\u5341\u4E94\uFF0C\u6210\u4EB2\u5934\u4E00\u5929\u3002\u300D</p><p>\u7D2B\u73AB\u5E9F\u4E86\u534A\u5929\u53E3\u820C\uFF0C\u53EA\u7ED9\u98CE\u5E08\u59D0\u4E89\u53D6\u4E86\u4E00\u5929\uFF0C\u4E0D\u7531\u6C14\u6068\u4EA4\u52A0\uFF0C\u4E0D\u7531\u5F97\u73E0\u6CEA\u6EDA\u6EDA\u800C\u4E0B\u3002</p><p>\u6155\u5BB9\u9F99\u5FC3\u4E2D\u4E00\u8F6F\uFF0C\u300C\u90A3\u5C31\u56DB\u6708\u5341\u65E5\u5427\uFF0C\u518D\u6709\u4E03\u5929\u3002\u300D</p><p>\u7D2B\u73AB\u64E6\u7740\u773C\u6CEA\uFF0C\u4E00\u8A00\u4E0D\u53D1\u5730\u671D\u752C\u9053\u8D70\u53BB\u3002</p><p>\u6155\u5BB9\u9F99\u5BD2\u58F0\u9053\uFF1A\u300C\u4F60\u8FD8\u60F3\u600E\u4E48\u6837\uFF1F\u300D</p><p>\u7D2B\u73AB\u56D4\u7740\u9F3B\u5B50\u8BF4\uFF1A\u300C\u6211\u53BB\u627E\u7EAA\u5E08\u59D0\u2026\u2026\u300D</p>',76);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
