import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_29" tabindex="-1"><a class="header-anchor" href="#_29" aria-hidden="true">#</a> 29</h1><p>\u4E00\u540D\u6C49\u5B50\u5927\u54A7\u54A7\u5750\u5728\u5E8A\u4FA7\uFF0C\u7EAA\u7709\u59A9\u8DEA\u4F0F\u5728\u4ED6\u957F\u6EE1\u9ED1\u6BDB\u7684\u4E24\u817F\u95F4\uFF0C\u57CB\u5934\u5438\u542E\u3002</p><p>\u81FB\u9996\u8D77\u4F0F\u95F4\uFF0C\u7C97\u5927\u7684\u8089\u68D2\u5728\u67D4\u7F8E\u7684\u7EA2\u5507\u4E2D\u8FDB\u8FDB\u51FA\u51FA\uFF0C\u5145\u6EE1\u6DEB\u8361\u610F\u5473\u3002</p><p>\u5927\u6C49\u8212\u670D\u7684\u772F\u7740\u773C\uFF0C\u6307\u70B9\u9053\uFF1A\u300C\u7528\u70B9\u529B\u2026\u2026\u820C\u5934\u4F7F\u52B2\u513F\u2026\u2026\u7EAA\u5A4A\u5B50\u8FD9\u5C0F\u5634\u771F\u4E0D\u9519\u2026\u2026\u597D\u597D\u8214\uFF01\u300D\u4ED6\u659C\u659C\u8EAB\u5B50\u60F3\u6362\u4E2A\u59FF\u52BF\uFF0C\u6B63\u770B\u5230\u5BAB\u4E3B\u548C\u73AB\u7470\u4ED9\u5B50\u7AD9\u5728\u95E8\u65C1\u3002</p><p>\u5927\u6C49\u7ACB\u5373\u7AD9\u8D77\u8EAB\u6765\uFF0C\u8089\u68D2\u300C\u5575\u300D\u7684\u4E00\u58F0\u4ECE\u6E29\u6DA6\u7684\u53E3\u8154\u5185\u8DF3\u51FA\uFF0C\u6CBE\u6EE1\u53E3\u6C34\u7684\u68D2\u8EAB\u4E0D\u4F4F\u6447\u6643\u3002\u7EAA\u7709\u59A9\u5C0F\u5634\u5F20\u5728\u534A\u7A7A\uFF0C\u8BB6\u5F02\u5730\u7741\u5F00\u773C\u775B\u3002\u5F85\u770B\u6E05\u7D2B\u73AB\uFF0C\u5979\u8FDE\u5FD9\u5782\u4E0B\u5934\uFF0C\u6CEA\u6C34\u5728\u773C\u7736\u91CC\u6253\u7740\u6643\u3002</p><p>\u6155\u5BB9\u9F99\u6DE1\u7136\u7B11\u9053\uFF1A\u300C\u4F60\u662F\u6C34\u5802\u90E8\u5C5E\u2014\u2014\u662F\u4E0D\u662F\u53EB\u738B\u540D\u6CFD\uFF1F\u300D</p><p>\u738B\u540D\u6CFD\u6CA1\u60F3\u5230\u5E73\u65F6\u6781\u5C11\u5F97\u89C1\u7684\u5BAB\u4E3B\u5C45\u7136\u8BA4\u8BC6\u81EA\u5DF1\u8FD9\u4E2A\u65E0\u540D\u5C0F\u5352\uFF0C\u4E0D\u7531\u53C8\u60CA\u53C8\u559C\uFF0C\u8FDE\u5FD9\u8EAC\u8EAB\u5927\u58F0\u9053\uFF1A\u300C\u5C5E\u4E0B\u738B\u540D\u6CFD\uFF0C\u53C2\u89C1\u5BAB\u4E3B\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u6446\u4E86\u6446\u624B\uFF0C\u300C\u63A5\u7740\u5FD9\u5427\uFF0C\u64CD\u5B8C\u518D\u8BF4\u3002\u300D</p><p>\u738B\u540D\u6CFD\u5174\u594B\u5F97\u6EE1\u8138\u7EA2\u5149\uFF0C\u4ED6\u6709\u610F\u5728\u5BAB\u4E3B\u9762\u524D\u65BD\u5C55\u624B\u6BB5\uFF0C\u633D\u4F4F\u7EAA\u7709\u59A9\u7684\u67D4\u80A9\u6254\u5230\u5E8A\u4E0A\uFF0C\u63A5\u7740\u56DE\u624B\u63E1\u4F4F\u4E24\u53EA\u7EA4\u7EC6\u7684\u811A\u8E1D\u5DE6\u53F3\u4E00\u5206\uFF0C\u5411\u4E0A\u4E00\u63A8\uFF0C\u52A8\u4F5C\u5E72\u51C0\u5229\u7D22\u3002\u7728\u773C\u95F4\u7EAA\u7709\u59A9\u5DF2\u662F\u4E24\u817F\u5927\u5F20\uFF0C\u79D8\u5904\u9AD8\u4E3E\u7684\u6A21\u6837\u3002</p><p>\u72F0\u72DE\u7684\u8089\u68D2\u5939\u7740\u98CE\u58F0\u4E00\u8D2F\u5230\u5E95\uFF0C\u7EAA\u7709\u59A9\u95F7\u54FC\u4E00\u58F0\uFF0C\u7EC6\u767D\u7259\u9F7F\u7D27\u7D27\u54AC\u4F4F\u7EA2\u5507\u3002</p><p>\u738B\u540D\u6CFD\u52A8\u4F5C\u6781\u731B\uFF0C\u6BCF\u6B21\u90FD\u662F\u5C3D\u6839\u800C\u5165\uFF0C\u649E\u5F97\u82B1\u5FC3\u9178\u75BC\u3002\u66F4\u75DB\u82E6\u7684\u662F\u4ED6\u7B2C\u4E00\u4E0B\u8FDB\u5F97\u592A\u72E0\uFF0C\u8089\u7A74\u5916\u7684\u4E00\u7247\u5AE9\u8089\u88AB\u8089\u68D2\u5E26\u5165\u4F53\u5185\uFF0C\u5377\u5728\u7565\u6709\u5E72\u6DA9\u7684\u8089\u58C1\u4E0A\uFF0C\u65E0\u6CD5\u6323\u8131\uFF0C\u62BD\u9001\u95F4\u5AE9\u8089\u626F\u5F97\u751F\u75BC\u3002\u7EAA\u7709\u59A9\u65E0\u5948\u4F38\u51FA\u7389\u624B\uFF0C\u624B\u6307\u6478\u5230\u8179\u4E0B\uFF0C\u5C06\u82B1\u74E3\u7FFB\u5377\u8FC7\u6765\u3002</p><p>\u7D2B\u73AB\u6CEA\u5982\u96E8\u4E0B\uFF0C\u5979\u5C0F\u5634\u88AB\u6155\u5BB9\u9F99\u6342\u4F4F\uFF0C\u53EA\u80FD\u773C\u7741\u7741\u770B\u7740\u6E29\u96C5\u79C0\u7F8E\u7684\u5E08\u59D0\u88AB\u4EBA\u5F53\u6210\u5993\u5973\u822C\u5978\u6DEB\u3002</p><p>\u738B\u540D\u6CFD\u62BD\u9001\u8D8A\u6765\u8D8A\u5FEB\uFF0C\u8089\u7A74\u5185\u6DEB\u6C34\u6E10\u751F\uFF0C\u53D1\u51FA\u6DEB\u9761\u7684\u300C\u53FD\u53FD\u300D\u58F0\u54CD\u3002\u5BAB\u4E3B\u5728\u65C1\uFF0C\u4ED6\u4E5F\u4E0D\u6562\u592A\u5C3D\u5174\uFF0C\u7247\u523B\u540E\u4FBF\u4E00\u6CC4\u5982\u6CE8\u3002\u5931\u53BB\u652F\u6491\u7684\u53CC\u817F\u65E0\u529B\u7684\u6389\u843D\u4E0B\u6765\u3002\u7EAA\u7709\u59A9\u6EE1\u8138\u6CEA\u5149\uFF0C\u98A4\u58F0\u8BF4\u9053\uFF1A\u300C\u591A\u8C22\u2026\u2026\u54E5\u54E5\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u89C1\u7EAA\u7709\u59A9\u8FD9\u4E48\u5B88\u89C4\u77E9\uFF0C\u4E0D\u7531\u5F97\u5F00\u6000\u5927\u7B11\uFF0C\u5C4F\u9000\u738B\u540D\u6CFD\uFF0C\u8BF4\u9053\uFF1A\u300C\u7EAA\u5A4A\u5B50\uFF0C\u5C11\u592B\u4EBA\u547D\u4F60\u5230\u5BAB\u5185\u4F3A\u5019\u2014\u2014\u8FD8\u4E0D\u591A\u8C22\u5C11\u592B\u4EBA\uFF1F\u300D</p><p>\u7EAA\u7709\u59A9\u8DEA\u5728\u7D2B\u73AB\u8EAB\u524D\u4F4E\u58F0\u9053\uFF1A\u300C\u591A\u8C22\u5C11\u592B\u4EBA\u2026\u2026\u300D</p><p>\u6155\u5BB9\u7D2B\u73AB\u60F3\u8D77\u5F53\u65E5\u4E24\u4EBA\u540C\u95E8\u5B66\u827A\uFF0C\u60C5\u540C\u624B\u8DB3\u7684\u5F80\u4E8B\uFF0C\u5FC3\u5934\u53C8\u9178\u53C8\u6DA9\uFF0C\u5E26\u7740\u54ED\u8154\u558A\u9053\uFF1A\u300C\u7EAA\u5E08\u59D0\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u5BD2\u58F0\u9053\uFF1A\u300C\u5979\u53EA\u662F\u4E2A\u6DEB\u5974\uFF0C\u5524\u5979\u7EAA\u5974\u597D\u4E86\u3002\u4EE5\u540E\u518D\u542C\u5230\u4F60\u53EB\u5E08\u59D0\uFF0C\u6211\u7ACB\u523B\u5C31\u628A\u5979\u9001\u56DE\u6765\uFF01\u300D</p><p>\u7D2B\u73AB\u54FD\u54BD\u7740\u70B9\u4E86\u70B9\u5934\uFF0C\u300C\u6211\u60F3\u89C1\u98CE\u5E08\u59D0\u2026\u2026\u5979\u53D7\u4E86\u90A3\u4E48\u91CD\u7684\u4F24\u2026\u2026\u300D</p><p>\u300C\u4ED6\u5988\u7684\uFF01\u8FD9\u91CC\u6CA1\u6709\u4EC0\u4E48\u98CE\u5E08\u59D0\uFF01\u53EA\u6709\u4E2A\u6328\u64CD\u7684\u98CE\u5A4A\u5B50\uFF01\u300D\u6155\u5BB9\u9F99\u5389\u559D\u9053\u3002</p><p>\u7D2B\u73AB\u773C\u4E2D\u6012\u706B\u95EA\u52A8\uFF0C\u300C\u6211\u8981\u89C1\u5979\uFF01\u300D</p><p>\u300C\u4E0D\u8BB8\u89C1\uFF01\u5979\u6CA1\u90A3\u4E48\u5BB9\u6613\u5C31\u6B7B\uFF01\u300D</p><p>\u300C\u6211\u5C31\u8981\u89C1\uFF01\u300D\u7D2B\u73AB\u50CF\u53EA\u5C0F\u8C79\u5B50\u822C\u63E1\u7D27\u5C0F\u62F3\u5934\uFF0C\u7F8E\u76EE\u55B7\u706B\u76EF\u7740\u6155\u5BB9\u9F99\u3002</p><p>\u7389\u4EBA\u5A07\u4FCF\u7684\u6124\u6012\u522B\u6709\u4E00\u756A\u60CA\u8273\uFF0C\u6155\u5BB9\u9F99\u5FFD\u7136\u4E00\u7B11\uFF0C\u300C\u8981\u89C1\u4E5F\u4E0D\u662F\u4E0D\u53EF\u4EE5\uFF0C\u4E0D\u8FC7\u2026\u2026\u300D</p><p>\u7D2B\u73AB\u80F8\u53E3\u8D77\u4F0F\u4E24\u773C\u4E00\u77AC\u4E0D\u77AC\u5730\u7B49\u5F85\u4ED6\u7684\u6761\u4EF6\u3002</p><p>\u6155\u5BB9\u9F99\u6DEB\u7B11\u7740\u628A\u624B\u4F38\u8FDB\u888D\u5185\uFF0C\u638F\u6478\u7740\u62C9\u51FA\u5C3A\u8BB8\u957F\u77ED\uFF0C\u7C97\u5982\u513F\u81C2\u5E03\u6EE1\u9897\u7C92\u8089\u523A\u7684\u9633\u5177\u6765\u3002</p><p>\u7D2B\u73AB\u5FC3\u5934\u62BD\u7D27\uFF0C\u5F3A\u6491\u7740\u6CA1\u6709\u626D\u5934\u56DE\u907F\u3002</p><p>\u300C\u2026\u2026\u53EA\u8981\u4F60\u4EB2\u4EB2\u54E5\u54E5\u7684\u9633\u7269\uFF0C\u6211\u5C31\u8BA9\u4F60\u89C1\u5979\u3002\u300D</p><p>\u7D2B\u73AB\u7C89\u8138\u731B\u7136\u6DA8\u5F97\u901A\u7EA2\uFF0C\u5C16\u53EB\u9053\uFF1A\u300C\u4F60\u53BB\u6B7B\uFF01\u300D\u8BF4\u7740\u5954\u51FA\u77F3\u5BA4\u3002</p><p>\u6155\u5BB9\u9F99\u957F\u81C2\u4E00\u5C55\uFF0C\u4ECE\u8EAB\u540E\u62E6\u8170\u62B1\u4F4F\u7D2B\u73AB\uFF0C\u6012\u52C3\u7684\u8089\u68D2\u76F4\u633A\u633A\u9876\u5728\u5FAE\u7FD8\u7684\u5706\u81C0\u4E0B\u3002\u4E3A\u4E86\u6311\u4E2A\u597D\u65E5\u5B50\u7ED9\u59B9\u59B9\u7834\u5904\uFF0C\u4ED6\u5DF2\u7ECF\u5FCD\u8010\u591A\u65F6\uFF0C\u521A\u624D\u88AB\u5BA4\u5185\u7684\u8273\u666F\u52FE\u8D77\u6B32\u706B\uFF0C\u6B64\u65F6\u518D\u4E5F\u6309\u637A\u4E0D\u4F4F\u3002</p><p>\u9694\u7740\u8863\u670D\u7D2B\u73AB\u8FD8\u80FD\u611F\u89C9\u5230\u8089\u68D2\u7684\u70BD\u70ED\uFF0C\u5979\u62FC\u547D\u626D\u52A8\u8EAB\u4F53\uFF0C\u60F3\u6446\u8131\u817F\u95F4\u786C\u6886\u6886\u7684\u5F02\u7269\u3002\u7EC6\u6ED1\u7684\u8089\u4F53\u5728\u9F9F\u5934\u4E0A\u4E0D\u4F4F\u78E8\u64E6\uFF0C\u4F20\u6765\u9635\u9635\u5FEB\u611F\u3002\u6155\u5BB9\u9F99\u547C\u5438\u8D8A\u6765\u8D8A\u6025\u4FC3\uFF0C\u6068\u4E0D\u5F97\u5C31\u6B64\u4E00\u633A\uFF0C\u8FDB\u5165\u8FD9\u5177\u7F8E\u5999\u7684\u8089\u4F53\u5185\u3002</p><p>\u7D2B\u73AB\u4E5F\u611F\u89C9\u5230\u4E0D\u59A5\uFF0C\u8089\u68D2\u8D8A\u6765\u8D8A\u7528\u529B\uFF0C\u786C\u786C\u9876\u7740\u80A1\u95F4\u67D4\u5AE9\u7684\u79D8\u5904\uFF0C\u9690\u9690\u4F5C\u75DB\u3002</p><p>\u5979\u987E\u4E0D\u5F97\u5FC3\u91CC\u7684\u538C\u6076\uFF0C\u8FDE\u5FD9\u4F38\u624B\u53BB\u6321\uFF0C\u4F46\u8EAB\u5B50\u88AB\u6155\u5BB9\u9F99\u7D27\u7D27\u62E5\u4F4F\uFF0C\u600E\u4E48\u4E5F\u591F\u4E0D\u5230\u81C0\u540E\u3002</p><p>\u5BC6\u95ED\u7684\u8089\u7F1D\u88AB\u9F9F\u5934\u7F13\u7F13\u6324\u5F00\uFF0C\u7F57\u8863\u76F4\u63A5\u78E8\u64E6\u5728\u6CA1\u6709\u4EB5\u88E4\u906E\u63A9\u7684\u79D8\u5904\uFF0C\u7EC6\u7EB1\u4E00\u70B9\u4E00\u70B9\u5D4C\u5165\u80A1\u95F4\u5AE9\u8089\u5185\u3002\u7D2B\u73AB\u5FC3\u5934\u72C2\u8DF3\uFF0C\u80F8\u53E3\u5982\u540C\u538B\u7740\u4E00\u5757\u5DE8\u77F3\uFF0C\u5598\u4E0D\u8FC7\u6C14\u6765\u3002</p><p>\u6B63\u60F6\u6025\u95F4\uFF0C\u5FFD\u7136\u8089\u68D2\u4E00\u987F\uFF0C\u63A5\u7740\u4ECE\u817F\u6839\u6ED1\u5230\u4E00\u65C1\uFF0C\u80A1\u95F4\u7684\u538B\u529B\u6D88\u5931\u4E86\u3002</p><p>\u7D2B\u73AB\u60CA\u9B42\u672A\u5B9A\u5730\u56DE\u8FC7\u5934\uFF0C\u770B\u5230\u4E09\u5E08\u59D0\u6B63\u8DEA\u5728\u6155\u5BB9\u9F99\u817F\u65C1\uFF0C\u4E24\u624B\u63E1\u7740\u8089\u68D2\u671D\u5706\u5F20\u7684\u7EA2\u5507\u4E2D\u9001\u53BB\u3002</p><p>\u7D2B\u7EA2\u53D1\u4EAE\u7684\u9F9F\u5934\u8DB3\u6709\u5C0F\u513F\u62F3\u5934\u5927\u5C0F\uFF0C\u7EAA\u7709\u59A9\u53EA\u52C9\u5F3A\u541E\u4E0B\u9F9F\u5934\uFF0C\u53E3\u8154\u4FBF\u88AB\u585E\u6EE1\u3002</p><p>\u5979\u4E0A\u8EAB\u5FAE\u5FAE\u524D\u503E\uFF0C\u4F38\u76F4\u5589\u5499\uFF0C\u62FC\u547D\u541E\u54BD\u3002</p><p>\u6155\u5BB9\u9F99\u53EA\u662F\u6B32\u706B\u5347\u817E\uFF0C\u4E5F\u4E0D\u613F\u8FC7\u65E9\u7834\u4E86\u7D2B\u73AB\u7684\u5904\u5B50\uFF0C\u6B64\u65F6\u5A07\u7F8E\u5982\u82B1\u7684\u7EAA\u7709\u59A9\u81EA\u613F\u4EE5\u8EAB\u76F8\u4EE3\uFF0C\u4E5F\u4E50\u5F97\u5728\u5979\u8EAB\u4E0A\u53D1\u6CC4\u4E00\u756A\uFF0C\u5F53\u4E0B\u633A\u8170\u4EFB\u5979\u5438\u542E\uFF0C\u4F46\u624B\u81C2\u8FD8\u7D27\u7D27\u6402\u7740\u7D2B\u73AB\uFF0C\u4EAB\u53D7\u5979\u8170\u80A2\u7684\u67D4\u8F6F\u3002</p><p>\u8089\u68D2\u4E0A\u7684\u7A81\u8D77\u4E00\u9897\u9897\u6324\u5165\u9C9C\u7EA2\u7684\u5507\u74E3\uFF0C\u4F46\u8DDD\u79BB\u90A3\u5708\u8089\u523A\u8FD8\u6709\u4E00\u6307\u5BBD\u7A84\uFF0C\u9F9F\u5934\u5DF2\u6DF1\u5165\u5589\u5185\uFF0C\u5835\u5F97\u900F\u4E0D\u8FC7\u6C14\u6765\u3002\u7EAA\u7709\u59A9\u9999\u820C\u4F38\u76F4\uFF0C\u4E0E\u5634\u5507\u4E00\u9053\u7D27\u7D27\u88F9\u7740\u8089\u68D2\uFF0C\u7AED\u529B\u541E\u5410\u3002</p><p>\u6155\u5BB9\u9F99\u5728\u7D2B\u73AB\u5C0F\u5DE7\u7684\u9165\u4E73\u4E0A\u634F\u4E86\u4E00\u628A\uFF0C\u677E\u5F00\u5979\u7684\u8170\u8EAB\uFF0C\u515C\u624B\u5C06\u7EAA\u7709\u59A9\u6284\u4E86\u8D77\u6765\u3002</p><p>\u6155\u5BB9\u9F99\u6602\u7136\u800C\u7ACB\uFF0C\u628A\u7EAA\u7709\u59A9\u767D\u51C0\u7684\u53CC\u8DB3\u642D\u5728\u81C2\u4FA7\uFF0C\u6258\u7740\u5979\u7684\u8170\u81C0\uFF0C\u5C06\u8089\u7A74\u5BF9\u51C6\u9633\u5177\u7528\u529B\u4E00\u6309\u3002</p><p>\u7D2B\u73AB\u6C34\u7075\u7075\u7684\u5927\u773C\u77AA\u5F97\u6D51\u5706\uFF0C\u5979\u600E\u4E48\u90FD\u4E0D\u76F8\u4FE1\u5982\u6B64\u7C97\u58EE\u7684\u7269\u4F53\u80FD\u63D2\u8FDB\u5E08\u59D0\u67D4\u5F31\u7684\u8EAB\u4F53\u91CC\u9762\u3002</p><p>\u7EAA\u7709\u59A9\u5931\u58F0\u75DB\u53EB\u4E2D\uFF0C\u706B\u70ED\u7684\u8089\u68D2\u5DF2\u8D2F\u4F53\u800C\u5165\u3002\u9ECF\u6E7F\u7684\u82B1\u74E3\u91CD\u91CD\u649E\u5728\u9633\u5177\u6839\u90E8\u7684\u89E6\u624B\u95F4\uFF0C\u8089\u7A74\u5185\u84C4\u79EF\u7684\u7CBE\u6DB2\u3001\u6DEB\u6C34\u5C3D\u6570\u6324\u6E85\u51FA\u6765\u3002\u811A\u5C16\u56E0\u4E3A\u75BC\u75DB\u800C\u7EF7\u7D27\uFF0C\u7EAA\u7709\u59A9\u4E24\u624B\u6309\u5728\u8179\u4FA7\u5F3A\u5FCD\u7740\u5DE8\u9633\u7684\u8086\u8650\u3002</p><p>\u4E0D\u4EC5\u8089\u7A74\uFF0C\u751A\u81F3\u6574\u4E2A\u8179\u8154\u90FD\u88AB\u9633\u5177\u6491\u6EE1\uFF0C\u523A\u5165\u65F6\u51E0\u4E4E\u5C06\u5B50\u5BAB\u5B8C\u5168\u6324\u6241\uFF0C\u786C\u751F\u751F\u9876\u5230\u80C3\u888B\u3002\u4F3C\u4E4E\u6240\u6709\u7684\u5185\u810F\u90FD\u88AB\u8089\u68D2\u6405\u52A8\uFF0C\u82B1\u5F84\u5185\u67D4\u5AE9\u7684\u8089\u58C1\u4EFF\u4F5B\u88AB\u90A3\u4E9B\u6EE1\u5E03\u7684\u9897\u7C92\u8089\u523A\u52FE\u5F97\u7FFB\u81F3\u4F53\u5916\u2026\u2026</p><p>\u53EA\u62BD\u9001\u6570\u4E0B\uFF0C\u65E9\u5DF2\u75B2\u60EB\u4E0D\u582A\u7684\u7EAA\u7709\u59A9\u4FBF\u88AB\u5978\u6DEB\u7684\u660F\u4E86\u8FC7\u53BB\u3002\u6155\u5BB9\u9F99\u6D51\u4E0D\u5728\u610F\uFF0C\u50CF\u62B1\u7740\u4E00\u4E2A\u6CA1\u6709\u751F\u547D\u7684\u73A9\u5076\uFF0C\u5C3D\u60C5\u5957\u5F04\u3002\u6BCF\u4E00\u6B21\u90FD\u4F38\u76F4\u624B\u81C2\uFF0C\u5C06\u5973\u4F53\u9AD8\u9AD8\u4E3E\u8D77\uFF0C\u7136\u540E\u518D\u72E0\u72E0\u62FD\u843D\uFF0C\u5C31\u50CF\u7528\u4E00\u5757\u67D4\u8F6F\u7684\u767D\u7EF8\u64E6\u62ED\u957F\u67AA\u822C\u73A9\u5F04\u7740\u660F\u53A5\u7684\u7F8E\u5973\u3002</p><p>\u7EAA\u7709\u59A9\u4E0A\u8EAB\u540E\u4EF0\uFF0C\u843D\u4E0B\u65F6\u62AB\u6563\u7684\u79C0\u53D1\u51E0\u4E4E\u89E6\u5230\u5730\u9762\u3002\u5979\u4E24\u773C\u7D27\u95ED\uFF0C\u56DB\u80A2\u968F\u7740\u8EAB\u4F53\u7684\u4E0A\u4E0B\u8D77\u843D\uFF0C\u8F6F\u7EF5\u7EF5\u5782\u5728\u8EAB\u4E0B\u6643\u6765\u6643\u53BB\u3002</p><p>\u7D2B\u73AB\u53C8\u60CA\u53C8\u75BC\uFF0C\u5982\u679C\u7247\u7389\u5728\u624B\uFF0C\u5979\u80AF\u5B9A\u4F1A\u6BEB\u4E0D\u72B9\u8C6B\u7684\u4E00\u5200\u6325\u4E0B\uFF0C\u65A9\u65AD\u8FD9\u6839\u53EF\u618E\u53EF\u6015\u7684\u602A\u5F02\u9633\u5177\u3002</p><p>\u4E00\u67F1\u9999\u5DE5\u592B\u540E\uFF0C\u5979\u518D\u4E5F\u5FCD\u4E0D\u4F4F\uFF0C\u6251\u8FC7\u53BB\u62B1\u4F4F\u5E08\u59D0\uFF0C\u53EB\u9053\uFF1A\u300C\u522B\u5F04\u4E86\uFF01\u2026\u2026\u5979\u4F1A\u6B7B\u7684\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u7B11\u9053\uFF1A\u300C\u4E00\u4E2A\u8D31\u5A4A\u5B50\uFF0C\u6B7B\u5C31\u6B7B\u4E86\uFF01\u6709\u4EC0\u4E48\u5927\u4E0D\u4E86\u7684\uFF1F\u2014\u2014\u54E5\u54E5\u7684\u9633\u7269\u5982\u4F55\uFF1F\u80AF\u5B9A\u4F1A\u8BA9\u4F60\u6B32\u4ED9\u6B32\u6B7B\u2026\u2026\u54C8\u54C8\uFF0C\u4E0D\u4FE1\u4F60\u95EE\u95EE\u5A18\uFF0C\u5979\u54EA\u6B21\u4E0D\u662F\u88AB\u54E5\u54E5\u6211\u64CD\u5F97\u6B7B\u53BB\u6D3B\u6765\uFF0C\u6DEB\u6C34\u4E71\u6D41\uFF1F\u300D</p><p>\u300C\u6DF7\u86CB\uFF01\u755C\u7272\uFF01\u300D</p><p>\u6155\u5BB9\u9F99\u624B\u4E00\u677E\uFF0C\u5168\u9760\u6DF1\u5165\u79D8\u5904\u7684\u8089\u68D2\u6311\u7740\u7EAA\u7709\u59A9\uFF0C\u51B7\u51B7\u9053\uFF1A\u300C\u4F60\u518D\u6562\u8FD9\u6837\u5BF9\u54E5\u54E5\u8BF4\u8BDD\uFF0C\u6211\u5C31\u628A\u8FD9\u4E9B\u5973\u4EBA\u4E00\u4E2A\u4E2A\u64CD\u6B7B\u2014\u2014\u653E\u5FC3\uFF0C\u54E5\u54E5\u6211\u5BF9\u4ED8\u5973\u4EBA\u8FD8\u662F\u6709\u4E9B\u624B\u6BB5\u7684\u3002\u300D</p><p>\u7EAA\u7709\u59A9\u88AB\u4E0B\u4F53\u6495\u88C2\u822C\u7684\u5267\u75DB\u60CA\u9192\u8FC7\u6765\uFF0C\u4E24\u624B\u5728\u7A7A\u4E2D\u6325\u821E\u7740\u8BD5\u56FE\u6491\u4F4F\u8EAB\u4F53\u3002\u7D2B\u73AB\u8FDE\u5FD9\u6258\u4F4F\u5E08\u59D0\u7684\u8170\u80CC\uFF0C\u5FC3\u4E71\u5982\u9EBB\uFF0C\u4E0D\u77E5\u8BE5\u600E\u4E48\u5BF9\u4ED8\u66B4\u8650\u7684\u5BAB\u4E3B\u3002</p><p>\u300C\u8FD9\u5C31\u5BF9\u4E86\u3002\u300D\u6155\u5BB9\u9F99\u9053\uFF1A\u300C\u62B1\u7D27\uFF0C\u8BA9\u54E5\u54E5\u8212\u8212\u670D\u670D\u5730\u64CD\u5B8C\u7EAA\u5A4A\u5B50\u3002\u300D</p><p>\u7D2B\u73AB\u65B9\u5BF8\u5927\u4E71\uFF0C\u53EA\u597D\u5446\u5446\u62B1\u7740\u4E09\u5E08\u59D0\uFF0C\u627F\u53D7\u7740\u4ED6\u72C2\u731B\u7684\u5978\u6DEB\u3002</p>',54);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
