import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_30" tabindex="-1"><a class="header-anchor" href="#_30" aria-hidden="true">#</a> 30</h1><p>\u6155\u5BB9\u7D2B\u73AB\u5230\u5E95\u8FD8\u662F\u6CA1\u80FD\u89C1\u5230\u5927\u5E08\u59D0\uFF0C\u5979\u6400\u7740\u7EAA\u7709\u59A9\u79BB\u5F00\u4EB2\u5B57\u4E19\u5BA4\u3002\u9694\u58C1\u95E8\u524D\u7AD9\u7740\u4E09\u4E94\u4E2A\u6C49\u5B50\uFF0C\u6B63\u5728\u7B49\u5F85\u4E00\u5C1D\u6D41\u971C\u5251\u98CE\u5973\u4FA0\u7684\u6ECB\u5473\u3002</p><p>\u6C89\u91CD\u7684\u94C1\u95E8\u6162\u6162\u5408\u4E0A\uFF0C\u9694\u65AD\u4E86\u7D2B\u73AB\u56DE\u671B\u7684\u76EE\u5149\u3002\u6155\u5BB9\u9F99\u5728\u5899\u4E0A\u4E00\u6273\uFF0C\u77F3\u58C1\u5347\u8D77\uFF0C\u5C06\u6D12\u6EE1\u65E0\u6570\u5973\u5B50\u8840\u6CEA\u7684\u4EB2\u5B57\u752C\u9053\u9694\u7EDD\u5728\u795E\u5BAB\u4E4B\u5916\u3002</p><p>\u65B9\u624D\u6155\u5BB9\u9F99\u6545\u610F\u65BD\u5C55\u624B\u6BB5\uFF0C\u6697\u65BD\u91C7\u8865\u4E4B\u672F\uFF0C\u7EAA\u7709\u59A9\u88AB\u5978\u7684\u4E00\u8FDE\u6570\u6B21\u9AD8\u6F6E\uFF0C\u6B64\u65F6\u5DF2\u6781\u7AEF\u865A\u5F31\u3002\u7D2B\u73AB\u521A\u628A\u5979\u653E\u5230\u7678\u5BA4\u7684\u69BB\u4E0A\uFF0C\u4FBF\u6C89\u6C89\u7761\u53BB\u3002\u7D2B\u73AB\u5728\u65C1\u89C2\u5BDF\u534A\u5929\uFF0C\u89C1\u5E08\u59D0\u547C\u5438\u5E73\u7A33\uFF0C\u60AC\u7740\u7684\u5FC3\u624D\u653E\u4E86\u4E0B\u6765\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u6E29\u67D4\u534E\u8D35\u7684\u8427\u4F5B\u5974\u6B64\u65F6\u5374\u50CF\u4E2A\u5A74\u513F\uFF0C\u8FDE\u5403\u996D\u4E5F\u9700\u4EBA\u5582\u98DF\u3002\u5403\u4E86\u4E24\u53E3\uFF0C\u5979\u6447\u6447\u5934\uFF0C\u6CEA\u773C\u5A46\u5A11\u5730\u770B\u7740\u5973\u513F\u3002</p><p>\u300C\u5A18\uFF0C\u4F60\u518D\u5403\u4E00\u70B9\u2026\u2026\u300D\u7D2B\u73AB\u8F7B\u58F0\u8BF4\u3002</p><p>\u767E\u82B1\u89C2\u97F3\u626D\u5934\u671B\u7740\u6155\u5BB9\u9F99\uFF0C\u98A4\u58F0\u9053\uFF1A\u300C\u6211\u6C42\u6C42\u4F60\u4E86\uFF0C\u522B\u4F24\u5BB3\u7D2B\u73AB\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u7B11\u9053\uFF1A\u300C\u5B69\u513F\u600E\u4E48\u4F1A\u4F24\u5BB3\u59B9\u59B9\u5462\uFF1F\u5A18\uFF0C\u4F60\u592A\u591A\u8651\u4E86\u3002\u59B9\u59B9\u8FDF\u65E9\u8981\u5AC1\u4EBA\uFF0C\u4E0E\u5176\u5AC1\u7ED9\u5916\u4EBA\uFF0C\u4E0D\u5982\u5AC1\u7ED9\u5B69\u513F\u3002\u5B69\u513F\u4E00\u5B9A\u4F1A\u597D\u597D\u75BC\u5979\uFF0C\u65E9\u4E9B\u8BA9\u5A18\u62B1\u4E0A\u5B59\u5B50\u2026\u2026\u5662\uFF0C\u53EF\u60DC\u5A18\u4E0D\u542C\u8BDD\uFF0C\u60F3\u62B1\u4E5F\u62B1\u4E0D\u6210\u4E86\u3002\u300D</p><p>\u767E\u82B1\u89C2\u97F3\u54ED\u9053\uFF1A\u300C\u5979\u8FD8\u662F\u4E2A\u5B69\u5B50\u2026\u2026\u653E\u8FC7\u5979\uFF0C\u4F60\u8981\u5A18\u600E\u4E48\u6837\u90FD\u53EF\u4EE5\u2026\u2026\u300D</p><p>\u300C\u54C8\u54C8\uFF0C\u73B0\u5728\u6211\u60F3\u600E\u4E48\u6837\u96BE\u9053\u4E0D\u884C\u5417\uFF1F\u5A18\u7684\u5C41\u773C\u513F\u53C8\u7D27\u53C8\u8F6F\u5E72\u8D77\u6765\u597D\u8212\u670D\uFF0C\u4E00\u5929\u6CA1\u73A9\uFF0C\u5B69\u513F\u8FD8\u771F\u6709\u4E9B\u60F3\u5462\u3002\u300D</p><p>\u7D2B\u73AB\u6C89\u7740\u7684\u653E\u4E0B\u7897\uFF0C\u7A81\u7136\u62AC\u624B\u671D\u6155\u5BB9\u9F99\u8138\u4E0A\u6253\u53BB\u3002\u6155\u5BB9\u9F99\u4E0D\u95EA\u4E0D\u907F\uFF0C\u786C\u751F\u751F\u6328\u4E86\u4E00\u638C\u3002\u8427\u4F5B\u5974\u5927\u60CA\u5931\u8272\uFF0C\u751F\u6015\u4ED6\u4F1A\u53D1\u4F5C\u5973\u513F\u3002</p><p>\u6155\u5BB9\u9F99\u5634\u89D2\u6311\u51FA\u4E00\u4E1D\u51B7\u7B11\uFF0C\u559D\u9053\uFF1A\u300C\u9E42\u5974\uFF01\u4F20\u6211\u5429\u5490\uFF0C\u98CE\u5A4A\u5B50\u6BCF\u65E5\u63A5\u5BA2\u4EBA\u6570\u589E\u52A0\u4E00\u500D\uFF01\u300D</p><p>\u300C\u4E0D\u8981\uFF01\u300D\u7D2B\u73AB\u53EB\u9053\u3002</p><p>\u300C\u665A\u4E86\uFF01\u300D\u6155\u5BB9\u9F99\u8138\u5BD2\u4F3C\u51B0\uFF0C\u54AC\u7259\u9053\uFF1A\u300C\u518D\u6709\u4E00\u6B21\uFF0C\u98CE\u5A4A\u5B50\u6BCF\u5929\u5C31\u8981\u88AB\u516B\u5341\u4E2A\u4EBA\u64CD\uFF01\u300D</p><p>\u300C\u5351\u9119\u65E0\u803B\uFF01\u300D\u8BDD\u5DF2\u7ECF\u5230\u4E86\u5634\u8FB9\uFF0C\u7D2B\u73AB\u53C8\u54BD\u4E86\u4E0B\u53BB\u3002\u8FD9\u4E00\u53E5\u9A82\u51FA\u6765\uFF0C\u53D7\u82E6\u7684\u53EA\u4F1A\u662F\u81EA\u5DF1\u7684\u4EB2\u4EBA\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u767D\u96FE\u7F2D\u7ED5\u7684\u6C34\u9762\u4E0A\u6D6E\u7740\u4E00\u4E1B\u4E4C\u4EAE\u7684\u79C0\u53D1\uFF0C\u987A\u7740\u6C34\u6D41\u7684\u65B9\u5411\u8F7B\u8F7B\u6F02\u8361\u3002\u4E0D\u77E5\u8FC7\u4E86\u591A\u4E45\uFF0C\u4E00\u5F20\u660E\u7389\u822C\u7684\u4FCF\u8138\u731B\u7136\u62AC\u8D77\uFF0C\u6025\u4FC3\u5730\u5598\u7740\u6C14\u3002</p><p>\u6C34\u73E0\u4ECE\u53D1\u4E0A\u8138\u4E0A\u6EDA\u6EDA\u800C\u843D\uFF0C\u63A9\u76D6\u4E86\u7D2B\u73AB\u6EE1\u8138\u7684\u6CEA\u5149\u3002\u521A\u624D\u6155\u5BB9\u9F99\u7ADF\u5F53\u7740\u5979\u7684\u9762\u6345\u5165\u6BCD\u4EB2\u7684\u809B\u6D1E\u3002\u5979\u5B9E\u5728\u65E0\u6CD5\u518D\u770B\u4E0B\u53BB\uFF0C\u4FBF\u8EB2\u5230\u8FD9\u91CC\u6765\u3002</p><p>\u6155\u5BB9\u7D2B\u73AB\u8EBA\u5728\u6E29\u6696\u7684\u6CC9\u6C34\u4E2D\uFF0C\u4EF0\u9762\u770B\u7740\u5BA4\u9876\u6697\u6697\u601D\u7D22\u3002\u73B0\u5728\u6BCD\u4EB2\u3001\u4E09\u4F4D\u5E08\u59D0\uFF0C\u5305\u62EC\u5C0F\u83BA\u5C0F\u9E42\uFF0C\u6240\u6709\u7684\u5E0C\u671B\u5C31\u5BC4\u6258\u5728\u81EA\u5DF1\u8EAB\u4E0A\uFF0C\u65E0\u8BBA\u5982\u4F55\u4E5F\u4E0D\u80FD\u518D\u6FC0\u6012\u6155\u5BB9\u9F99\u3002</p><p>\u5982\u4ECA\u53EA\u6709\u4E0E\u8FD9\u4E2A\u79BD\u517D\u865A\u4E0E\u59D4\u86C7\uFF0C\u85C9\u673A\u9003\u79BB\uFF0C\u5BFB\u5E08\u7236\u76F8\u52A9\u3002\u5373\u4F7F\u9003\u4E0D\u4E86\uFF0C\u4E5F\u8981\u5C3D\u91CF\u62D6\u5230\u56DB\u6708\u5341\u516D\uFF0C\u5C4A\u65F6\u5E08\u7236\u6709\u4E5D\u6210\u53EF\u80FD\u4F1A\u95FB\u8BAF\u8D76\u6765\uFF0C\u5E08\u7236\u795E\u529F\u76D6\u4E16\uFF0C\u80AF\u5B9A\u80FD\u628A\u5927\u5BB6\u6551\u51FA\u82E6\u6D77\u3002</p><p>\u7D2B\u73AB\u6DF1\u6DF1\u5438\u4E86\u53E3\u6C14\uFF0C\u6F5C\u5230\u6C34\u5E95\uFF0C\u4E00\u8FB9\u7EC3\u4E60\u6C34\u6027\uFF0C\u4E00\u8FB9\u60F3\u7740\u4ECA\u5929\u6240\u89C1\u7684\u56FE\u5F62\u548C\u5B57\u6837\u3002\u5FFD\u7136\u8111\u4E2D\u7075\u5149\u4E00\u95EA\u3002</p><p>\u7D2B\u73AB\u72FC\u72C8\u5730\u54B3\u55FD\u7740\uFF0C\u5410\u51FA\u545B\u5230\u80BA\u91CC\u7684\u6CC9\u6C34\u3002\u8111\u4E2D\u98DE\u5FEB\u5730\u65CB\u8F6C\u3002</p><p>\u7236\u4EB2\u8BF4\u5B8C\u300C\u5929\u5730\u541B\u4EB2\u5E08\u300D\u540E\uFF0C\u7D27\u63A5\u7740\u5C31\u662F\u300C\u8D3E\u94F6\u601D\u300D\u548C\u300C\u4E01\u8D35\u5FE0\u300D\u3002\u83AB\u975E\u8FD9\u4E24\u4E2A\u5E76\u975E\u4EBA\u540D\uFF0C\u800C\u662F\u6307\u77F3\u5BA4\u7684\u5929\u5E72\u5730\u652F\uFF1F\u4E0B\u5348\u5728\u5929\u5B57\u7532\u5BA4\u53D1\u73B0\u4E86\u4E00\u4E2A\u56FE\u5F62\uFF0C\u5269\u4E0B\u7684\u56FE\u5F62\u4F1A\u4E0D\u4F1A\u662F\u5728\u5730\u5B57\u620C\u5BA4\u3001\u541B\u5B57\u5DF3\u5BA4\u3001\u4EB2\u5B57\u4E01\u5BA4\u548C\u5E08\u5B57\u7678\u5BA4\uFF1F</p><p>\u5F53\u65F6\u7236\u4EB2\u91CD\u4F24\uFF0C\u58F0\u97F3\u53D8\u5F97\u5C16\u7EC6\uFF0C\u5410\u5B57\u5E76\u4E0D\u6E05\u6670\uFF0C\u4F46\u5929\u5E72\u5730\u652F\u4E0D\u8FC7\u5BE5\u5BE5\u4E8C\u5341\u4E8C\u5B57\uFF0C\u8FD9\u7532\u3001\u5BC5\u3001\u5DF3\u3001\u4E01\u3001\u7678\u4E94\u5B57\u4E00\u4E00\u76F8\u7B26\uFF0C\u96BE\u9053\u53EA\u662F\u5DE7\u5408\uFF1F\u591A\u51FA\u6765\u7684\u300C\u5FE0\u300D\u5B57\uFF0C\u4F1A\u4E0D\u4F1A\u662F\u6307\u5927\u5385\u6B63\u4E2D\u7684\u592A\u6781\u56FE\uFF1F</p><p>\u7D2B\u73AB\u7ACB\u523B\u8D77\u8EAB\u62AB\u4E0A\u8863\u670D\uFF0C\u63A2\u5934\u770B\u770B\u752C\u9053\u5185\u5E76\u65E0\u4EBA\u8FF9\uFF0C\u8FDE\u5FD9\u8D70\u5230\u592A\u6781\u56FE\u65C1\u3002</p><p>\u592A\u6781\u56FE\u9AD8\u51FA\u5730\u9762\u4E24\u5C3A\uFF0C\u5F62\u72B6\u6D51\u5706\uFF0C\u96D5\u523B\u6781\u5176\u7CBE\u81F4\u3002\u5947\u602A\u7684\u662F\u592A\u6781\u9C7C\u9ED1\u767D\u4E24\u8272\u6D51\u82E5\u5929\u6210\uFF0C\u4E2D\u95F4\u5E76\u65E0\u62FC\u63A5\u7684\u75D5\u8FF9\u3002\u7D2B\u73AB\u4E0A\u4E0B\u4ED4\u7EC6\u5BA1\u89C6\uFF0C\u751A\u81F3\u8FDE\u9634\u9633\u9C7C\u7684\u9C7C\u773C\u4E5F\u6309\u4E86\u51E0\u904D\uFF0C\u4F46\u59CB\u7EC8\u6CA1\u6709\u627E\u5230\u90A3\u4E2A\u56FE\u5F62\u3002\u5979\u6BEB\u4E0D\u6C14\u9981\uFF0C\u53C8\u6084\u6084\u8D70\u5230\u5730\u5B57\u752C\u9053\uFF0C\u63A8\u95E8\u800C\u5165\u3002</p><p>\u752C\u9053\u9876\u4E0A\u73E0\u8F89\u6DE1\u6DE1\u6D12\u843D\uFF0C\u5341\u4E8C\u4E2A\u77F3\u95E8\u4EA4\u9519\u6392\u5217\uFF0C\u4F9D\u6B21\u523B\u7740\u5730\u652F\u5B57\u6837\u3002\u7D2B\u73AB\u627E\u5230\u5DE6\u5BC5\u5BA4\uFF0C\u7528\u529B\u4E00\u63A8\uFF0C\u77F3\u95E8\u7EB9\u4E1D\u4E0D\u52A8\u3002</p><p>\u5979\u7EC6\u770B\u534A\u5929\uFF0C\u53D1\u73B0\u77F3\u95E8\u8DDD\u5730\u534A\u5C3A\u7684\u5730\u65B9\uFF0C\u6709\u4E00\u4E2A\u624B\u638C\u5BBD\u7684\u7F1D\u9699\uFF0C\u91CC\u9762\u6321\u7740\u6728\u677F\u3002\u8F7B\u8F7B\u4E00\u63A8\uFF0C\u6728\u677F\u5E94\u624B\u7FFB\u8D77\uFF0C\u4E00\u80A1\u81ED\u5473\u6251\u9F3B\u800C\u6765\u3002\u7D2B\u73AB\u5C4F\u4F4F\u547C\u5438\uFF0C\u5FC3\u4E0B\u7EB3\u7F55\u3002</p><p>\u77F3\u5BAB\u867D\u7136\u6DF1\u5165\u5C71\u8179\uFF0C\u4F46\u901A\u6C14\u6781\u597D\uFF0C\u5E76\u6CA1\u6709\u4EC0\u4E48\u5F02\u5473\u3002\u800C\u4E14\u8FD9\u80A1\u5473\u9053\u4E5F\u4E0D\u50CF\u662F\u7269\u4F53\u9648\u8150\u6240\u53D1\u51FA\u7684\u545B\u9F3B\u9709\u81ED\u3002</p><p>\u7D2B\u73AB\u51DD\u795E\u542C\u4E86\u7247\u523B\uFF0C\u6CA1\u6709\u542C\u5230\u4EC0\u4E48\u58F0\u97F3\u3002\u5979\u4FEF\u5728\u5730\u4E0A\uFF0C\u5C0F\u5FC3\u5730\u671D\u5185\u5F20\u671B\u3002\u91CC\u9762\u9ED1\u6C89\u6C89\u6CA1\u6709\u4E00\u4E1D\u5149\u7EBF\u3002\u53EA\u6068\u81EA\u5DF1\u6B64\u65F6\u5185\u529B\u88AB\u5236\uFF0C\u65E0\u8BBA\u89C6\u529B\u542C\u529B\u90FD\u4E0E\u5E38\u4EBA\u76F8\u540C\uFF0C\u65E0\u6CD5\u83B7\u5F97\u66F4\u591A\u7684\u7EBF\u7D22\u3002</p><p>\u7A81\u7136\u624B\u4E0A\u4E00\u9707\uFF0C\u4E00\u4E2A\u5E9E\u5927\u7684\u7269\u4F53\u91CD\u91CD\u7838\u5728\u6728\u677F\u4E0A\uFF0C\u53D1\u51FA\u4E00\u58F0\u95F7\u54CD\u3002\u7D2B\u73AB\u5413\u4E86\u4E00\u8DF3\uFF0C\u8FDE\u5FD9\u7F29\u624B\u3002\u53EA\u542C\u95E8\u5185\u4F20\u6765\u4E00\u9635\u6781\u4F4E\u6C89\u7684\u5486\u54EE\uFF0C\u5145\u6EE1\u51F6\u6076\u610F\u5473\uFF0C\u4EE4\u4EBA\u6BDB\u9AA8\u609A\u7136\u3002</p><p>\u7D2B\u73AB\u601D\u7D22\u7247\u523B\uFF0C\u8F6C\u5934\u6253\u91CF\u5176\u4ED6\u51E0\u95F4\u77F3\u5BA4\u3002\u6BCF\u4E2A\u95E8\u4E0B\u90FD\u6709\u6216\u5927\u6216\u5C0F\u6216\u957F\u6216\u6241\u751A\u81F3\u7F51\u72B6\u7684\u5F00\u53E3\u3002\u5979\u4E0D\u7518\u5FC3\u5730\u9010\u4E00\u63A8\u52A8\u77F3\u95E8\uFF0C\u8BD5\u7740\u80FD\u5426\u627E\u5230\u4E00\u6247\u80FD\u591F\u6253\u5F00\u7684\u3002</p><p>\u521A\u63A8\u4E86\u4E24\u4E0B\uFF0C\u752C\u9053\u5916\u4F20\u6765\u4E00\u58F0\u623F\u95E8\u5F00\u542F\u7684\u8F7B\u54CD\uFF0C\u7D2B\u73AB\u8FC5\u901F\u7AD9\u8D77\u8EAB\uFF0C\u8F7B\u624B\u8F7B\u811A\u8D70\u51FA\u752C\u9053\uFF0C\u6765\u5230\u5927\u5385\uFF0C\u88C5\u4F5C\u597D\u5947\u5730\u4EF0\u671B\u9876\u4E0A\u7684\u661F\u6708\u56FE\u3002</p><p>\u77F3\u5BAB\u6C89\u5BC2\u4E0B\u6765\u3002\u77F3\u58C1\u4E0A\u523B\u6EE1\u79CD\u79CD\u5145\u6EE1\u795E\u79D8\u610F\u5473\u7684\u56FE\u50CF\uFF0C\u8FD9\u5EA7\u9971\u8574\u5386\u4EE3\u661F\u6708\u5BAB\u4E3B\u5FC3\u8840\u7684\u77F3\u5BAB\uFF0C\u4EFF\u4F5B\u4E00\u4E2A\u65CB\u8F6C\u7740\u7684\u65E0\u5E95\u65CB\u6DA1\uFF0C\u6084\u65E0\u58F0\u606F\u5730\u541E\u566C\u7740\u4E00\u5207\uFF0C\u65E0\u8BBA\u662F\u667A\u6167\u8FD8\u662F\u8089\u4F53\uFF0C\u65E0\u8BBA\u8840\u6CEA\u4E0E\u6B22\u7B11\u2026\u2026</p><p>\u7247\u523B\u540E\uFF0C\u767D\u7389\u9E42\u4ECE\u6BCD\u4EB2\u6240\u5728\u7684\u5E9A\u5BA4\u7F13\u7F13\u8D70\u51FA\uFF0C\u7ECF\u8FC7\u5927\u5385\u65F6\u5411\u7D2B\u73AB\u8E72\u8EAB\u65BD\u793C\uFF0C\u7136\u540E\u671D\u53F6\u957F\u8001\u5C45\u4F4F\u7684\u8F9B\u5BA4\u8D70\u53BB\u3002</p><p>\u7D2B\u73AB\u89C1\u5979\u795E\u60C5\u51C4\u695A\uFF0C\u773C\u542B\u6CEA\u5149\uFF0C\u5FC3\u5934\u987F\u65F6\u4E00\u7D27\uFF0C\u8FDE\u5FD9\u8DDF\u5728\u540E\u9762\u3002</p><p>\u300C\u9E42\u5974\u6C42\u89C1\u62A4\u6CD5\u3002\u300D</p><p>\u77F3\u95E8\u62C9\u5F00\uFF0C\u767D\u7389\u9E42\u5782\u9996\u5165\u5185\u3002</p><p>\u77F3\u5BA4\u5185\u6EE1\u662F\u6D53\u90C1\u7684\u836F\u9999\uFF0C\u53F6\u884C\u5357\u89C1\u73AB\u7470\u4ED9\u5B50\u4ECE\u540E\u9762\u8DDF\u4E86\u8FDB\u6765\uFF0C\u4E5F\u672A\u9732\u8BB6\u8272\u3002</p><p>\u767D\u7389\u9E42\u4F4E\u58F0\u9053\uFF1A\u300C\u5BAB\u4E3B\u547D\u5974\u5A62\u6765\u89C1\u62A4\u6CD5\uFF0C\u8BF7\u62A4\u6CD5\u7ED9\u5974\u5A62\u7A7F\u73AF\u2026\u2026\u300D</p><p>\u7D2B\u73AB\u6025\u9053\uFF1A\u300C\u5C0F\u9E42\uFF0C\u4ED6\u4E3A\u4EC0\u4E48\u8BA9\u4F60\u8FD9\u6837\uFF1F\u300D</p><p>\u300C\u5974\u5A62\u4E0E\u59D0\u59D0\u65B9\u624D\u4F3A\u5019\u5BAB\u4E3B\uFF0C\u5BAB\u4E3B\u8BF4\u8981\u6211\u4EEC\u4E00\u6A21\u4E00\u6837\u2026\u2026\u300D\u8BF4\u7740\u4E00\u6EF4\u6CEA\u6C34\u4ECE\u8138\u4E0A\u6ED1\u843D\u3002</p><p>\u53EA\u4E3A\u4E86\u597D\u73A9\u4FBF\u6B8B\u4EBA\u808C\u80A4\uFF0C\u7D2B\u73AB\u6068\u5F97\u54AC\u7259\u5207\u9F7F\uFF0C\u5374\u53C8\u65E0\u53EF\u5948\u4F55\u3002</p><p>\u53F6\u884C\u5357\u4E0D\u52A8\u58F0\u8272\uFF0C\u624B\u6307\u5728\u77F3\u684C\u4E0A\u6572\u4E86\u6572\u3002\u767D\u7389\u9E42\u6E29\u987A\u5730\u8DEA\u5728\u684C\u524D\uFF0C\u6367\u8D77\u4E73\u623F\u653E\u5728\u684C\u4E0A\u3002\u4E00\u5BF9\u67D4\u5AE9\u7684\u9999\u4E73\u5E76\u6392\u800C\u9648\uFF0C\u6ED1\u817B\u4E73\u8089\u8F6F\u8F6F\u6401\u5728\u51B0\u51B7\u7684\u77F3\u5934\u4E0A\uFF0C\u6BB7\u7EA2\u7684\u4E73\u5934\u5FAE\u5FAE\u7FD8\u8D77\uFF0C\u4FCF\u4E3D\u751F\u59FF\u3002</p><p>\u53F6\u884C\u5357\u62FF\u8D77\u4E00\u679A\u7C97\u957F\u7684\u91D1\u9488\u653E\u5728\u71C3\u70E7\u7684\u9F0E\u7089\u4E2D\u7099\u4E86\u7247\u523B\uFF0C\u7136\u540E\u634F\u4F4F\u4E73\u5934\u62FD\u4E86\u62FD\uFF0C\u624B\u4E00\u52A8\uFF0C\u91D1\u9488\u5DF2\u4ECE\u7D27\u8D34\u7740\u4E73\u5934\u6839\u90E8\u7684\u4E73\u6655\u4E2D\u7A7F\u8FC7\u3002\u767D\u7389\u9E42\u4E24\u624B\u7D27\u7D27\u6293\u7740\u819D\u76D6\uFF0C\u75DB\u5F97\u5A07\u8EAF\u5FAE\u98A4\u3002\u5BAB\u4E3B\u7ED9\u6D41\u971C\u5251\u4E73\u623F\u5F00\u82DE\u7684\u60E8\u8C61\u5386\u5386\u5728\u76EE\uFF0C\u5979\u6B64\u523B\u624D\u77E5\u9053\u98CE\u5973\u4FA0\u5F53\u65F6\u7684\u75DB\u695A\u3002\u60F3\u8D77\u90A3\u53EA\u88AB\u9C9C\u8840\u6D78\u6CA1\u7684\u575A\u4E73\uFF0C\u767D\u7389\u9E42\u6296\u5F97\u66F4\u5389\u5BB3\u4E86\u3002</p><p>\u53F6\u884C\u5357\u637B\u52A8\u91D1\u9488\uFF0C\u5C06\u4F24\u53E3\u6269\u5927\uFF0C\u63A5\u7740\u53D6\u51FA\u4E00\u5BF9\u91D1\u73AF\uFF0C\u6263\u5728\u8840\u8FF9\u6591\u6591\u7684\u4E73\u5934\u4E0A\u3002</p><p>\u4ED6\u7684\u52A8\u4F5C\u4F3C\u4E4E\u5E76\u4E0D\u5FEB\uFF0C\u4F46\u7D2B\u73AB\u53EA\u7728\u4E86\u4E24\u4E0B\u773C\uFF0C\u767D\u7389\u9E42\u4E73\u5C16\u5DF2\u7ECF\u591A\u4E86\u4E24\u4E2A\u91D1\u73AF\u3002</p><p>\u53F6\u884C\u5357\u53C8\u6572\u4E86\u6572\u684C\u9762\u3002\u767D\u7389\u9E42\u6491\u8D77\u8EAB\u4F53\uFF0C\u4EF0\u8EAB\u8EBA\u5728\u684C\u4E0A\uFF0C\u4E24\u817F\u653E\u5728\u684C\u4FA7\uFF0C\u5C06\u5C11\u5973\u6700\u9690\u79D8\u7684\u7389\u6237\u66B4\u9732\u51FA\u6765\u3002</p><p>\u53F6\u884C\u5357\u58F0\u97F3\u7565\u5E26\u6C99\u54D1\uFF0C\u6DE1\u6DE1\u9053\uFF1A\u300C\u63B0\u5F00\u3002\u300D</p><p>\u767D\u7389\u9E42\u8FDE\u5FD9\u628A\u624B\u4F38\u5230\u8179\u4E0B\uFF0C\u6309\u4F4F\u67D4\u7F8E\u7684\u82B1\u74E3\u5DE6\u53F3\u5206\u5F00\u3002\u5185\u5C42\u82B1\u74E3\u67D4\u67D4\u7ED5\u8FC7\u5149\u6DA6\u7684\u524D\u5EAD\uFF0C\u5728\u7389\u6237\u4E0A\u65B9\u5212\u51FA\u4E24\u6761\u4F18\u7F8E\u7684\u66F2\u7EBF\u8FDE\u5728\u4E00\u8D77\u3002\u82B1\u74E3\u7ED3\u5408\u5904\u9732\u51FA\u4E00\u4E2A\u5C0F\u5C0F\u7684\u82B1\u8482\uFF0C\u7EA2\u5B9D\u77F3\u822C\u593A\u76EE\u9C9C\u8273\u593A\u76EE\u3002</p><p>\u53F6\u884C\u5357\u76B1\u4E86\u76B1\u7709\uFF0C\u62FF\u51FA\u4E00\u4E2A\u5C0F\u74F6\uFF0C\u5C06\u4E00\u70B9\u767D\u8272\u7684\u7C89\u672B\u5012\u5728\u82B1\u8482\u4E0A\uFF0C\u7136\u540E\u5750\u5728\u6905\u4E2D\u95ED\u76EE\u517B\u795E\u3002</p><p>\u767D\u8272\u7684\u836F\u7C89\u843D\u5728\u8273\u7EA2\u7684\u5AE9\u8089\u4E0A\uFF0C\u4EFF\u4F5B\u88AB\u5438\u6536\u822C\u6E10\u6E10\u6D88\u5931\u3002\u4E0E\u6B64\u540C\u65F6\uFF0C\u82B1\u8482\u4F3C\u4E4E\u6DA8\u5927\u4E86\u4E00\u4E9B\u3002</p><p>\u767D\u7389\u9E42\u4E73\u5C16\u970D\u970D\u4F5C\u75DB\uFF0C\u8FD8\u8981\u803B\u8FB1\u5730\u63B0\u7740\u6027\u5668\uFF0C\u7B49\u5F85\u7740\u7ED9\u81EA\u5DF1\u9634\u8482\u7A7F\u73AF\u3002\u5979\u6697\u6697\u62BD\u6CE3\u7740\uFF0C\u65E0\u9650\u6094\u6068\u5730\u770B\u4E86\u7D2B\u73AB\u4E00\u773C\uFF0C\u53C8\u614C\u5FD9\u8F6C\u8FC7\u773C\u775B\u3002\u82E5\u4E0D\u662F\u56E0\u4E3A\u8FD9\u4E2A\u73AB\u7470\u4ED9\u5B50\uFF0C\u81EA\u5DF1\u548C\u59D0\u59D0\u600E\u4E48\u4F1A\u843D\u5F97\u5982\u6B64\u5730\u6B65\u2026\u2026</p><p>\u4E0D\u591A\u65F6\uFF0C\u82B1\u8482\u4FBF\u6DA8\u5927\u4E00\u500D\u6709\u4F59\uFF0C\u767D\u7389\u9E42\u53EA\u89C9\u79D8\u5904\u9635\u9635\u9EBB\u75D2\uFF0C\u8089\u7A74\u5185\u6DEB\u6DB2\u4E0D\u4F4F\u6CCC\u51FA\u3002</p><p>\u53F6\u884C\u5357\u7F13\u7F13\u7741\u5F00\u773C\uFF0C\u5C06\u90A3\u6839\u91D1\u9488\u70E7\u81F3\u5FAE\u7EA2\uFF0C\u7136\u540E\u637B\u4F4F\u82B1\u8482\u8F7B\u8F7B\u523A\u7A7F\u3002</p><p>\u624B\u6307\u521A\u637B\u4F4F\u82B1\u8482\uFF0C\u767D\u7389\u9E42\u4FBF\u5A07\u8EAF\u5267\u98A4\uFF0C\u8089\u7A74\u50CF\u5C0F\u5634\u822C\u62BD\u52A8\u8D77\u6765\u3002\u70BD\u70ED\u7684\u91D1\u9488\u523A\u5165\u82B1\u8482\u7684\u4E00\u5239\u90A3\uFF0C\u5979\u4E24\u817F\u731B\u7136\u7EF7\u76F4\uFF0C\u53D1\u51FA\u4E00\u58F0\u4F3C\u82E6\u4F3C\u751C\u7684\u5C16\u53EB\u3002\u91D1\u9488\u5728\u82B1\u8482\u5185\u6162\u6162\u65CB\u8F6C\uFF0C\u767D\u7389\u9E42\u624B\u6307\u6B7B\u6B7B\u6309\u7740\u82B1\u74E3\uFF0C\u655E\u9732\u7684\u8089\u7A74\u65F6\u9F13\u65F6\u7F29\uFF0C\u5FFD\u7136\u54C6\u55E6\u55B7\u51FA\u4E00\u80A1\u6D53\u767D\u7684\u9634\u7CBE\u3002</p><p>\u7D2B\u73AB\u5C0F\u5634\u5FAE\u5F20\uFF0C\u6123\u6123\u770B\u7740\u51E0\u4E4E\u5931\u795E\u7684\u5C11\u5973\uFF0C\u5FC3\u91CC\u300C\u546F\u546F\u300D\u76F4\u8DF3\u3002</p><p>\u53F6\u884C\u5357\u7A7F\u5B8C\u4E09\u53EA\u91D1\u73AF\uFF0C\u6296\u624B\u5C06\u51E0\u53EA\u91D1\u94C3\u6254\u5728\u684C\u4E0A\uFF0C\u6DE1\u6DE1\u9053\uFF1A\u300C\u81EA\u5DF1\u5E26\u4E0A\u3002\u300D</p><p>\u767D\u7389\u9E42\u4F59\u6CE2\u672A\u6B62\uFF0C\u98A4\u6296\u7740\u722C\u8D77\u6765\uFF0C\u62FF\u8D77\u91D1\u94C3\uFF0C\u6162\u6162\u5E26\u5230\u4E73\u4E0A\u3002\u9634\u8482\u4E0A\u7684\u91D1\u73AF\u5FAE\u5FAE\u4E00\u52A8\uFF0C\u5979\u4FBF\u50CF\u89E6\u7535\u822C\u4E24\u817F\u4E00\u8F6F\uFF0C\u8DEA\u5750\u5728\u5730\u4E0A\u3002\u867D\u7136\u6CC4\u4E86\u8EAB\uFF0C\u6625\u836F\u7684\u6548\u529B\u8FD8\u672A\u892A\u53BB\uFF0C\u6781\u7AEF\u89E6\u611F\u7684\u82B1\u8482\uFF0C\u6BCF\u4E00\u4E2A\u7EC6\u5C0F\u7684\u9707\u52A8\u90FD\u76F4\u5165\u5FC3\u5E95\u3002\u7B49\u767D\u7389\u9E42\u6296\u98A4\u7740\u6302\u597D\u91D1\u94C3\uFF0C\u8EAB\u4E0B\u5DF2\u7ECF\u662F\u6DEB\u6C34\u6A2A\u6D41\u3002</p>',60);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
