import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_31" tabindex="-1"><a class="header-anchor" href="#_31" aria-hidden="true">#</a> 31</h1><p>\u6BBF\u5916\u4F20\u6765\u4E00\u58F0\u6C89\u95F7\u7684\u96F7\u9E23\u3002\u6155\u5BB9\u9F99\u628A\u76EE\u5149\u4ECE\u6447\u66F3\u7684\u706B\u7130\u4E0A\u7F13\u7F13\u6536\u56DE\u3002\u82F1\u4FCA\u7684\u8138\u578B\u7EBF\u6761\u5206\u660E\uFF0C\u5B9B\u5982\u5927\u7406\u77F3\u96D5\u6210\uFF0C\u6CA1\u6709\u4E00\u4E1D\u8868\u60C5\u3002</p><p>\u7D2B\u73AB\u5165\u5BAB\u5DF2\u7ECF\u56DB\u5929\u4E86\u3002\u9664\u4E86\u7B2C\u4E00\u5929\u7565\u6709\u6323\u624E\u4E4B\u5916\uFF0C\u8FD9\u4E9B\u65E5\u5B50\u90FD\u663E\u5F97\u5F88\u542C\u8BDD\u5F88\u4E56\u5DE7\u3002\u521A\u624D\u81EA\u5DF1\u5F53\u7740\u5979\u7684\u9762\u63A5\u8FDE\u5C06\u6BCD\u4EB2\u548C\u7EAA\u7709\u59A9\u5E72\u5F97\u4E0D\u7701\u4EBA\u4E8B\uFF0C\u5979\u4E5F\u6CA1\u6709\u50CF\u4ECE\u524D\u90A3\u6837\u54ED\u9A82\uFF0C\u751A\u81F3\u8FDE\u773C\u6CEA\u90FD\u6CA1\u6709\u3002\u5982\u6B64\u67D4\u987A\u5012\u662F\u51FA\u4E4E\u610F\u6599\u3002</p><p>\u6155\u5BB9\u9F99\u5634\u89D2\u7EFD\u51FA\u4E00\u4E2A\u6D45\u6D45\u7684\u5FAE\u7B11\u3002\u5C0F\u4E2B\u5934\u8FD9\u6837\u5B50\u591A\u534A\u662F\u88C5\u7684\uFF0C\u53EF\u80FD\u8FD8\u5728\u5E7B\u60F3\u96EA\u5CF0\u795E\u5C3C\u80FD\u6765\u6551\u5979\u5462\u3002\u6731\u90AA\u9752\u6811\u548C\u5C48\u82E6\u85E4\u8EAB\u4E3A\u6559\u4E2D\u62A4\u6CD5\uFF0C\u5404\u6709\u60CA\u4EBA\u827A\u4E1A\uFF0C\u4E24\u4EBA\u8054\u624B\u53EF\u8C13\u5929\u4E0B\u65E0\u654C\uFF0C\u96EA\u5CF0\u795E\u5C3C\u5C31\u7B97\u6709\u4E09\u5934\u516D\u81C2\u4E5F\u96BE\u4EE5\u9003\u8131\uFF01</p><p>\u6155\u5BB9\u9F99\u50B2\u7136\u4E00\u7B11\uFF0C\u7BA1\u5979\u771F\u7684\u4E5F\u597D\uFF0C\u5047\u7684\u4E5F\u597D\uFF0C\u53EA\u8981\u4E56\u4E56\u7ED9\u81EA\u5DF1\u751F\u5B69\u5B50\u5C31\u597D\uFF01</p><p>\u60F3\u8D77\u7D2B\u73AB\u8FF7\u4EBA\u7684\u8EAB\u4F53\uFF0C\u4ED6\u4E0D\u7981\u5FC3\u5934\u5FAE\u8361\u3002\u82E5\u4E0D\u662F\u4E3A\u4E86\u5927\u4E8B\uFF0C\u5C82\u5BB9\u5979\u5B8C\u74A7\u81F3\u4ECA\uFF1F</p><p>\u8FD9\u6B21\u4E0E\u73AB\u7470\u4ED9\u5B50\u7684\u5A5A\u793C\u5927\u5F20\u65D7\u9F13\uFF0C\u4E00\u662F\u5728\u6C5F\u6E56\u626C\u5A01\uFF0C\u5F70\u663E\u661F\u6708\u6E56\u7684\u65E0\u654C\u58F0\u540D\uFF0C\u4E8C\u662F\u85C9\u673A\u62C9\u62E2\u5929\u4E0B\u8C6A\u96C4\u3002\u6155\u5BB9\u9F99\u91CD\u8BBE\u4F7F\u8005\u3001\u62A4\u6CD5\u3001\u4F9B\u5949\u7B49\u804C\u6B63\u662F\u4E3A\u4E86\u57F9\u690D\u81EA\u5DF1\u7684\u52BF\u529B\uFF0C\u4E0E\u6559\u4E2D\u539F\u6709\u7684\u4E94\u884C\u95E8\u6297\u8861\u3002\u7136\u540E\u518D\u60F3\u6CD5\u9664\u6389\u91D1\u5F00\u7532\u3001\u6C90\u58F0\u4F20\u548C\u6C34\u67D4\u4ED9\u7B49\u5143\u8001\uFF0C\u5B8C\u5168\u63A7\u5236\u661F\u6708\u6E56\u3002\u6709\u8FD9\u4E9B\u7CBE\u5175\u5F3A\u5C06\u5728\u624B\uFF0C\u9010\u9E7F\u5929\u4E0B\u590D\u56FD\u5927\u4E1A\u6307\u65E5\u53EF\u5F85\uFF01</p><p>\u6155\u5BB9\u9F99\u500F\u7136\u8D77\u8EAB\uFF0C\u79BB\u5F00\u91D1\u78A7\u8F89\u714C\u7684\u5B9D\u5EA7\u3002</p><p>\u8C6A\u96E8\u547C\u5578\u7740\u6D8C\u5165\u6BBF\u5185\u3002\u9EC4\u8C46\u5927\u5C0F\u7684\u96E8\u70B9\u6253\u5728\u8138\u4E0A\uFF0C\u7ACB\u523B\u98DE\u6E85\u5F00\u6765\uFF0C\u6CA1\u6709\u7559\u4E0B\u4E00\u4E1D\u6C34\u75D5\u3002</p><p>\u4E00\u8FDE\u4E32\u70B8\u96F7\u8F70\u7136\u54CD\u8FC7\uFF0C\u4F4E\u5782\u7684\u5929\u5E55\u7535\u5149\u5BC6\u5E03\uFF0C\u4EFF\u4F5B\u4E00\u5F20\u5DE8\u7F51\u7B3C\u7F69\u7740\u5927\u6BBF\u3002\u5DE8\u6811\u679D\u53F6\u98D8\u6447\uFF0C\u6574\u4E2A\u5C9B\u5C7F\u90FD\u6D78\u6CA1\u5728\u65E0\u8FB9\u65E0\u9645\u7684\u98CE\u96E8\u4E2D\uFF0C\u50CF\u8981\u88AB\u5929\u5730\u7684\u65E0\u7A77\u795E\u5A01\u8FDE\u6839\u62D4\u8D77\u3002\u5FFD\u7136\u53C8\u662F\u4E00\u58F0\u60CA\u96F7\uFF0C\u7535\u5149\u4ECE\u7A7A\u4E2D\u5212\u51FA\u523A\u773C\u7684\u5149\u8292\uFF0C\u731B\u51FB\u5728\u77F3\u9636\u4E0A\uFF0C\u6E85\u8D77\u4E00\u9053\u8FC5\u731B\u7684\u706B\u5149\u3002</p><p>\u7535\u5149\u95EA\u70C1\u4E2D\uFF0C\u6155\u5BB9\u9F99\u7684\u77B3\u5B54\u731B\u7136\u6536\u7F29\uFF0C\u76EF\u7740\u77F3\u4E0A\u96F7\u51FB\u7684\u7126\u9ED1\u5370\u8FF9\u3002\u7535\u5149\u5C55\u773C\u5373\u8FC7\uFF0C\u56DB\u5468\u53C8\u6062\u590D\u4E86\u9ED1\u6697\u3002\u9636\u4E0B\u5F25\u6F2B\u7684\u6C34\u96FE\u4E2D\uFF0C\u9690\u9690\u7EA6\u7EA6\u6709\u4E00\u5177\u96EA\u767D\u7684\u8EAB\u4F53\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u6797\u9999\u8FDC\u8098\u819D\u90FD\u88AB\u56FA\u5B9A\uFF0C\u53EA\u80FD\u8DEA\u4F0F\u3002\u9AD8\u4E3E\u7684\u96EA\u81C0\u4E2D\uFF0C\u8FD8\u63D2\u7740\u4E08\u592B\u7684\u817F\u9AA8\u3002\u5634\u4E2D\u7684\u7AF9\u7B52\u6DF1\u5165\u54BD\u5589\uFF0C\u8FD9\u672C\u662F\u9632\u5979\u54AC\u820C\u81EA\u6B8B\uFF0C\u4F46\u63D2\u4E0A\u540E\u5374\u6210\u4E86\u7537\u4EBA\u53D6\u4E50\u7684\u5DE5\u5177\u3002\u9633\u7CBE\u3001\u5C3F\u6DB2\u751A\u81F3\u5979\u81EA\u5DF1\u7684\u6DEB\u6C34\u3001\u7CAA\u4FBF\u2026\u2026\u79CD\u79CD\u6C61\u7269\u90FD\u901A\u8FC7\u7AF9\u7B52\u704C\u5165\u6797\u9999\u8FDC\u5589\u4E2D\uFF0C\u800C\u82F1\u6C14\u8FEB\u4EBA\u7684\u5BD2\u6708\u5200\u53EA\u80FD\u5F20\u7740\u5634\u5DF4\u4EFB\u4EBA\u620F\u5F04\u3002</p><p>\u66B4\u96E8\u6D47\u5728\u8D64\u88F8\u7684\u8EAB\u4F53\u4E0A\uFF0C\u51B0\u51B7\u5F7B\u9AA8\u3002\u8138\u4FA7\u7684\u5934\u76D6\u9AA8\u88AB\u96E8\u70B9\u6253\u5F97\u4E0D\u4F4F\u6447\u6643\uFF0C\u91CC\u9762\u7684\u4E00\u5C0F\u534A\u7CBE\u6DB2\u88AB\u96E8\u6C34\u7A00\u91CA\uFF0C\u4E00\u7247\u6D51\u6D4A\u3002</p><p>\u6797\u9999\u8FDC\u8270\u96BE\u5730\u5410\u7740\u6C14\uFF0C\u867D\u7136\u8EAB\u53D7\u98CE\u5439\u96E8\u6253\u4E4B\u82E6\uFF0C\u4F46\u81F3\u5C11\u6B64\u65F6\u6CA1\u6709\u4EBA\u6765\u6298\u78E8\u5979\u3002</p><p>\u6708\u4F59\u6765\u5979\u53D7\u5C3D\u975E\u4EBA\u7684\u6B8B\u9177\u6DEB\u8650\uFF0C\u51E0\u4E4E\u6BCF\u65F6\u6BCF\u523B\u90FD\u5728\u75BC\u75DB\u548C\u803B\u8FB1\u4E2D\u5EA6\u8FC7\u3002\u53EF\u6797\u9999\u8FDC\u5FC3\u5E95\u7684\u4EC7\u6068\u4E0D\u4EC5\u6CA1\u6709\u88AB\u75DB\u82E6\u63A9\u76D6\uFF0C\u53CD\u800C\u8D8A\u6765\u8D8A\u5F3A\u70C8\u3002</p><p>\u817F\u9AA8\u4E00\u52A8\uFF0C\u5728\u8089\u7A74\u5185\u65CB\u8F6C\u8D77\u6765\u3002\u65B0\u5A5A\u4E0D\u8FC7\u534A\u6708\uFF0C\u521A\u521A\u4EAB\u53D7\u5230\u592B\u59BB\u4E4B\u95F4\u7684\u6069\u7231\uFF0C\u81EA\u5DF1\u4FBF\u88AB\u4EBA\u523A\u778E\u53CC\u773C\uFF0C\u8EAB\u9677\u6DEB\u7A9F\uFF0C\u4E08\u592B\u66F4\u662F\u53EA\u5269\u4E0B\u817F\u9AA8\u3002\u4E00\u4E2A\u591A\u6708\u6765\uFF0C\u81EA\u5DF1\u88AB\u4E08\u592B\u9057\u9AB8\u6345\u5F04\u7684\u65F6\u5019\uFF0C\u8FDC\u8FDC\u591A\u4E8E\u592B\u59BB\u76F8\u805A\u7684\u65E5\u5B50\u2026\u2026\u6797\u9999\u8FDC\u5FC3\u5185\u6EF4\u8840\uFF0C\u6D01\u767D\u7684\u624B\u6307\u5728\u5149\u6ED1\u7684\u9ED1\u8272\u5927\u7406\u77F3\u4E0A\u62FC\u547D\u5C48\u4F38\u3002</p><p>\u7C97\u5927\u7684\u9AA8\u8282\u72E0\u72E0\u5F80\u5185\u4E00\u9001\uFF0C\u65CB\u5373\u62D4\u51FA\u3002\u98CE\u96E8\u7ACB\u523B\u4ECE\u8089\u7A74\u655E\u9732\u7684\u6D1E\u53E3\u704C\u5165\u4F53\u5185\uFF0C\u51B0\u51B7\u7684\u96E8\u6C34\u76F4\u63A5\u6253\u5728\u8089\u58C1\u4E0A\uFF0C\u5BD2\u610F\u76F4\u5165\u5FC3\u5E95\u3002\u6797\u9999\u8FDC\u6D51\u8EAB\u6218\u6817\uFF0C\u6025\u4FC3\u5730\u5598\u606F\u7740\u3002</p><p>\u6155\u5BB9\u9F99\u6F47\u6D12\u81EA\u5982\u5730\u5750\u5728\u6C34\u4E2D\uFF0C\u4F38\u624B\u64A9\u8D77\u6797\u9999\u8FDC\u6E7F\u6DCB\u6DCB\u7684\u79C0\u53D1\uFF0C\u4ED4\u7EC6\u7AEF\u8BE6\u8FD9\u4F4D\u672A\u66FE\u8C0B\u9762\u7684\u300C\u5144\u5F1F\u300D\u7684\u9057\u5B40\u3002\u5979\u957F\u5F97\u5F88\u7F8E\u3002\u4E0D\u540C\u4E8E\u98CE\u665A\u534E\u7684\u98CE\u91C7\u7167\u4EBA\uFF0C\u7EAA\u7709\u59A9\u7684\u67D4\u7F8E\u96C5\u81F4\uFF0C\u4E5F\u4E0D\u540C\u4E8E\u7D2B\u73AB\u7684\u503E\u57CE\u79C0\u8272\uFF0C\u7EB5\u7136\u767E\u822C\u51CC\u8FB1\uFF0C\u5979\u773C\u89D2\u7709\u68A2\u8FD8\u6709\u79CD\u98DE\u626C\u7684\u6C14\u8D28\u3002</p><p>\u53EF\u60DC\u5979\u5343\u4E0D\u8BE5\u4E07\u4E0D\u8BE5\u65E9\u5AC1\u4E86\u534A\u6708\uFF0C\u800C\u4E14\u8FD8\u662F\u5AC1\u7ED9\u4E86\u6155\u5BB9\u80DC\u8FD9\u4E2A\u593A\u53BB\u81EA\u5DF1\u6BCD\u7231\u7684\u5047\u513F\u5B50\u3002\u8FD9\u5C31\u6CE8\u5B9A\u5979\u53EA\u80FD\u50CF\u72D7\u4E00\u6837\u6813\u5728\u8FD9\u91CC\uFF0C\u6C42\u751F\u4E0D\u5F97\u6C42\u6B7B\u4E0D\u80FD\u5730\u7EC8\u751F\u4EFB\u4EBA\u6DEB\u8650\u3002</p><p>\u6155\u5BB9\u9F99\u62FF\u8D77\u5934\u76D6\u9AA8\u770B\u4E86\u4E00\u773C\uFF0C\u76B1\u4E86\u76B1\u7709\uFF0C\u968F\u624B\u6CFC\u5728\u6797\u9999\u8FDC\u8138\u4E0A\uFF0C\u7136\u540E\u62C9\u8D77\u5979\u7684\u4E73\u623F\u5C06\u9AA8\u7897\u4E2D\u7684\u7070\u571F\u6162\u6162\u64E6\u51C0\u3002</p><p>\u6797\u9999\u8FDC\u76EE\u4E0D\u89C1\u7269\uFF0C\u5931\u53BB\u6B66\u529F\u540E\u4E5F\u65E0\u6CD5\u5728\u66B4\u96E8\u4E2D\u542C\u6E05\u6765\u4EBA\u7684\u4E3E\u52A8\uFF0C\u6B63\u60CA\u7591\u4E0D\u5B9A\u95F4\uFF0C\u8111\u540E\u7684\u675F\u7F1A\u4E00\u677E\uFF0C\u5589\u4E2D\u7684\u7AF9\u7B52\u88AB\u4EBA\u62D4\u4E86\u51FA\u6765\u3002\u6797\u9999\u8FDC\u54B3\u55FD\u7740\u5408\u4E0A\u50F5\u786C\u7684\u7259\u5173\uFF0C\u5FFD\u7136\u5507\u4E0A\u4E00\u51C9\uFF0C\u90A3\u4EBA\u58F0\u97F3\u4E2D\u6DE1\u6DE1\u98D8\u6765\uFF1A\u300C\u559D\u53E3\u6C34\u2026\u2026\u300D</p><p>\u8FD9\u4E2A\u58F0\u97F3\u5979\u600E\u4E48\u4E5F\u5FD8\u4E0D\u4E86\uFF0C\u6B63\u662F\u8FD9\u4E2A\u58F0\u97F3\u628A\u81EA\u5DF1\u7F6E\u4E8E\u5982\u6B64\u51C4\u60E8\u7684\u5883\u5730\u3002\u6797\u9999\u8FDC\u5934\u4E00\u626D\uFF0C\u671D\u6155\u5BB9\u9F99\u72E0\u72E0\u5550\u53BB\u3002</p><p>\u6155\u5BB9\u9F99\u62AC\u624B\u63A5\u4F4F\uFF0C\u7136\u540E\u634F\u5F00\u6797\u9999\u8FDC\u7684\u4E0B\u5DF4\uFF0C\u5C06\u5934\u76D6\u9AA8\u4E2D\u7684\u96E8\u6C34\u548C\u553E\u6DB2\u6162\u6162\u5012\u5728\u5979\u5634\u4E2D\u3002\u4ED6\u7684\u58F0\u97F3\u53C8\u8F7B\u53C8\u8FDC\uFF0C\u6DF7\u5728\u98CE\u96E8\u4E2D\u5B9B\u5982\u70DF\u96FE\u822C\u6E3A\u832B\uFF0C\u300C\u544A\u8BC9\u6211\uFF0C\u6155\u5BB9\u80DC\u662F\u4E2A\u4EC0\u4E48\u6837\u7684\u4EBA\u2026\u2026\u300D</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u5404\u6761\u752C\u9053\u90FD\u6709\u673A\u62EC\u5173\u9501\uFF0C\u541B\u5B57\u752C\u9053\u4F3C\u4E4E\u4E45\u672A\u5F00\u542F\uFF0C\u8FDE\u9053\u53E3\u7684\u94C1\u95E8\u90FD\u9501\u5F97\u4E25\u4E25\u5B9E\u5B9E\u3002\u7D2B\u73AB\u5DF2\u7ECF\u628A\u80FD\u8FDB\u7684\u77F3\u5BA4\u4E00\u4E00\u770B\u8FC7\uFF0C\u751A\u81F3\u88C5\u4F5C\u5BF9\u53F6\u884C\u5357\u70BC\u4E39\u6709\u5174\u8DA3\uFF0C\u5728\u4ED6\u7684\u623F\u95F4\u4E5F\u5F85\u4E86\u4E24\u65E5\u3002\u4F46\u9664\u4E86\u5929\u5B57\u7532\u5BA4\uFF0C\u5176\u4ED6\u4E00\u65E0\u6240\u5F97\u3002\u773C\u770B\u5A5A\u671F\u6E10\u8FD1\uFF0C\u7D2B\u73AB\u5FC3\u91CC\u53D1\u6025\uFF0C\u8138\u4E0A\u5374\u4E0D\u6562\u6D41\u9732\u51FA\u4E1D\u6BEB\u5F02\u6837\u3002</p><p>\u300C\u53F6\u62A4\u6CD5\uFF0C\u5927\u5E08\u59D0\u4ECA\u65E5\u597D\u4E9B\u4E86\u5417\uFF1F\u300D</p><p>\u300C\u55EF\uFF0C\u4E0B\u5348\u53C8\u8E22\u4F24\u4E86\u4E00\u4EBA\uFF0C\u6B7B\u4E0D\u4E86\u3002\u300D</p><p>\u98CE\u5E08\u59D0\u6027\u683C\u521A\u70C8\uFF0C\u6240\u53D7\u7684\u6298\u78E8\u4E5F\u6700\u5389\u5BB3\uFF0C\u8FD9\u4E9B\u65E5\u5B50\u771F\u4E0D\u77E5\u5979\u662F\u600E\u4E48\u633A\u8FC7\u6765\u7684\u2026\u2026\u518D\u6709\u4E09\u5929\uFF0C\u7B49\u79BB\u5F00\u77F3\u5BA4\uFF0C\u81EA\u5DF1\u5C31\u53EF\u4EE5\u7167\u6599\u5979\u4E86\u3002\u7D2B\u73AB\u6C89\u9ED8\u534A\u5929\uFF0C\u79FB\u5F00\u8BDD\u9898\uFF0C\u7F20\u7740\u53F6\u884C\u5357\u65C1\u6572\u4FA7\u51FB\uFF0C\u5F97\u77E5\u5E08\u5B57\u7678\u5BA4\u662F\u62A4\u6CD5\u6731\u90AA\u9752\u6811\u7684\u5C45\u5BA4\uFF0C\u518D\u95EE\u6731\u90AA\u9752\u6811\u4E3A\u4F55\u4E0D\u5728\u5BAB\u4E2D\uFF0C\u53F6\u884C\u5357\u5C31\u677F\u7740\u8138\u4E00\u8A00\u4E0D\u53D1\u3002</p><p>\u300C\u6B7B\u8001\u5934\uFF01\u300D\u7D2B\u73AB\u5FC3\u91CC\u6697\u9A82\uFF0C\u8138\u4E0A\u5374\u6302\u7740\u751C\u751C\u7684\u7B11\u5BB9\uFF0C\u8F6C\u5F00\u8BDD\u9898\uFF0C\u300C\u53F6\u62A4\u6CD5\uFF0C\u8FD9\u662F\u4EC0\u4E48\uFF1F\u300D\u5979\u62FF\u8D77\u4E00\u4E2A\u7EA2\u8272\u7684\u5C0F\u74F6\u95EE\u9053\u3002</p><p>\u53F6\u884C\u5357\u773C\u73E0\u4E00\u7FFB\uFF0C\u9634\u9633\u602A\u6C14\u5730\u8BF4\uFF1A\u300C\u90A3\u662F\u7ED9\u5C11\u592B\u4EBA\u914D\u7684\u836F\uFF0C\u7528\u6765\u53D7\u7CBE\u5B89\u80CE\u3002\u300D</p><p>\u7D2B\u73AB\u7F8E\u76EE\u5BD2\u5149\u4E00\u95EA\u5373\u655B\uFF0C\u300C\u54CE\u5440\u300D\u4E00\u58F0\u4F4E\u53EB\uFF0C\u7389\u638C\u4E00\u7FFB\uFF0C\u74F7\u74F6\u8131\u624B\u800C\u51FA\u3002</p><p>\u773C\u770B\u74F7\u74F6\u5C31\u8981\u843D\u5728\u5730\u4E0A\uFF0C\u53F6\u884C\u5357\u888D\u8896\u5377\u8D77\uFF0C\u7A33\u7A33\u63A5\u4F4F\uFF0C\u6162\u541E\u541E\u9053\uFF1A\u300C\u6B64\u836F\u914D\u5236\u4E0D\u6613\uFF0C\u5C11\u592B\u4EBA\u5C0F\u5FC3\u4E86\u3002\u300D</p><p>\u7D2B\u73AB\u6EE1\u8138\u6B49\u610F\u5730\u8BF4\uFF1A\u300C\u771F\u662F\u5BF9\u4E0D\u8D77\uFF0C\u6211\u4E00\u5B9A\u4F1A\u5C0F\u5FC3\u7684\u2014\u2014\u8FD9\u4E2A\u662F\u4EC0\u4E48\uFF1F\u300D</p><p>\u53F6\u884C\u5357\u628A\u74F7\u74F6\u653E\u5728\u684C\u4E0A\uFF0C\u626C\u58F0\u9053\uFF1A\u300C\u90A3\u662F\u98A4\u58F0\u5A07\uFF0C\u5C11\u592B\u4EBA\u4E0E\u5BAB\u4E3B\u6210\u5A5A\u4E4B\u540E\u4FBF\u53EF\u7528\u4E86\u3002\u300D</p><p>\u7D2B\u73AB\u88C5\u4F5C\u6CA1\u542C\u61C2\u4ED6\u7684\u5632\u8BBD\uFF0C\u5929\u771F\u5730\u7728\u7728\u773C\uFF0C\u968F\u624B\u653E\u5728\u4E00\u65C1\uFF0C\u53C8\u62FF\u8D77\u53E6\u4E00\u4E2A\u836F\u74F6\u3002</p><p>\u76F4\u95EE\u4E86\u5C0F\u534A\u65F6\u8FB0\uFF0C\u7D2B\u73AB\u624D\u7B11\u563B\u563B\u8BF4\uFF1A\u300C\u6253\u6270\u53F6\u62A4\u6CD5\u4E86\u3002\u544A\u8F9E\u3002\u300D</p><p>\u53F6\u884C\u5357\u65E9\u5C31\u70E6\u900F\u4E86\uFF0C\u95FB\u8A00\u53EA\u6446\u4E86\u6446\u624B\uFF0C\u4FEF\u8EAB\u7528\u5FC3\u89C2\u5BDF\u9F0E\u7089\u7684\u706B\u5019\u3002</p><p>\u7D2B\u73AB\u4FCF\u751F\u751F\u8D70\u5230\u684C\u65C1\uFF0C\u6293\u4F4F\u90A3\u4E2A\u7EA2\u8272\u74F7\u74F6\u4E00\u628A\u6454\u5230\u5899\u4E0A\u3002\u300C\u546F\u300D\u7684\u4E00\u58F0\uFF0C\u836F\u6C41\u56DB\u6E85\uFF0C\u74F7\u74F6\u7838\u5F97\u7C89\u788E\u3002</p><p>\u7D2B\u73AB\u50CF\u4EC0\u4E48\u4E8B\u90FD\u6CA1\u53D1\u751F\u822C\u5934\u4E5F\u4E0D\u56DE\u5730\u8D70\u51FA\u77F3\u5BA4\uFF0C\u5269\u4E0B\u53F6\u884C\u5357\u5728\u80CC\u540E\u5E72\u77AA\u773C\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u521A\u8D70\u51FA\u77F3\u5BA4\uFF0C\u5C31\u770B\u5230\u4E00\u4E2A\u9AD8\u5927\u7684\u8EAB\u5F71\u3002\u6155\u5BB9\u9F99\u6D51\u8EAB\u6E7F\u900F\uFF0C\u888D\u89D2\u8FD8\u6EF4\u7740\u6C34\u3002\u4ED6\u8D70\u5230\u5BA4\u5185\uFF0C\u6DE1\u6DE1\u9053\uFF1A\u300C\u53F6\u62A4\u6CD5\uFF0C\u94A9\u820C\u7684\u5DE5\u5177\u501F\u6765\u4E00\u7528\u3002\u300D</p><p>\u53F6\u884C\u5357\u4E0D\u52A8\u58F0\u8272\uFF0C\u5F2F\u8170\u4ECE\u67DC\u5E95\u62FF\u51FA\u4E00\u4E2A\u7EC6\u7EC6\u7684\u94C1\u94A9\u548C\u4E00\u67C4\u624B\u6307\u5BBD\u7684\u8584\u5200\u3002</p><p>\u7D2B\u73AB\u6123\u6123\u770B\u7740\u4ED6\uFF0C\u300C\u8C01\u7684\u820C\u5934\uFF1F\u300D</p><p>\u6155\u5BB9\u9F99\u51B7\u54FC\u4E00\u58F0\uFF0C\u63A5\u8FC7\u5DE5\u5177\u8F6C\u8EAB\u79BB\u5F00\u3002\u7D2B\u73AB\u8FDE\u5FD9\u8DDF\u5728\u540E\u9762\uFF0C\u89C1\u4ED6\u662F\u671D\u6BBF\u5916\u8D70\u53BB\uFF0C\u60CA\u9053\uFF1A\u300C\u6797\u5E08\u59D0\uFF1F\u300D</p><p>\u5FC3\u91CC\u4E00\u6025\uFF0C\u7D2B\u73AB\u987F\u65F6\u54ED\u4E86\u8D77\u6765\uFF0C\u300C\u6797\u5E08\u59D0\u52A8\u90FD\u4E0D\u80FD\u52A8\uFF0C\u600E\u4E48\u4F1A\u5F97\u7F6A\u4F60\u5462\uFF1F\u5979\u773C\u775B\u5DF2\u7ECF\u770B\u4E0D\u89C1\u4E86\uFF0C\u4F60\u4E3A\u4EC0\u4E48\u8FD8\u8981\u5272\u5979\u7684\u820C\u5934\uFF1F\u6C42\u4F60\u653E\u8FC7\u6797\u5E08\u59D0\uFF0C\u6211\u2026\u2026\u6211\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u4FA7\u8138\u77A5\u4E86\u5979\u4E00\u773C\uFF0C\u4F38\u624B\u62C9\u5F00\u6BBF\u95E8\u3002</p><p>\u4E00\u4E2A\u8D64\u88F8\u7684\u5973\u5B50\u8DEA\u5728\u95E8\u5916\uFF0C\u89C1\u6709\u4EBA\u51FA\u6765\u4FBF\u53E9\u9996\u9053\uFF1A\u300C\u5974\u5A62\u536B\u79C0\u7EB9\uFF0C\u6C34\u5802\u5C5E\u4E0B\u4E39\u9633\u6D3E\u638C\u95E8\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u6253\u91CF\u4E86\u5979\u4E00\u773C\uFF0C\u811A\u6B65\u4E0D\u505C\u5730\u8D70\u5230\u9636\u4E0B\u3002\u536B\u79C0\u7EB9\u62AC\u8D77\u8138\uFF0C\u76EE\u5149\u4E0E\u7D2B\u73AB\u4E00\u89E6\uFF0C\u53C8\u8FDE\u5FD9\u5782\u4E0B\u5934\u3002</p><p>\u6155\u5BB9\u9F99\u6258\u8D77\u6797\u9999\u8FDC\u7684\u4E0B\u5DF4\uFF0C\u5C06\u94C1\u94A9\u671D\u5979\u5634\u5185\u63A2\u53BB\u3002\u7D2B\u73AB\u6B7B\u6B7B\u6402\u7740\u4ED6\u7684\u80F3\u818A\uFF0C\u987F\u8DB3\u9053\uFF1A\u300C\u6211\u4E00\u76F4\u90FD\u5F88\u542C\u8BDD\u2026\u2026\u521A\u624D\u90A3\u836F\u74F6\u771F\u662F\u4E0D\u5C0F\u5FC3\u6253\u7834\u7684\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u9762\u6C89\u4F3C\u6C34\uFF0C\u8098\u5C16\u4E00\u62AC\uFF0C\u5DF2\u5C01\u4E86\u7D2B\u73AB\u7684\u7A74\u9053\u3002\u94C1\u94A9\u78B0\u5728\u7259\u9F7F\u4E0A\uFF0C\u53D1\u51FA\u60A6\u8033\u7684\u8F7B\u54CD\u3002\u7EA2\u8273\u8273\u7684\u5C0F\u820C\u9759\u9759\u8EBA\u5728\u5634\u4E2D\uFF0C\u53C8\u6ED1\u53C8\u8F6F\uFF0C\u68A6\u4E00\u822C\u9999\u751C\u3002\u6797\u9999\u8FDC\u4E00\u65E0\u6240\u60E7\uFF0C\u6BEB\u4E0D\u5728\u610F\u5B83\u9A6C\u4E0A\u5C31\u4F1A\u88AB\u4EBA\u6B8B\u5FCD\u5730\u5272\u53BB\u3002</p><p>\u94A9\u5C16\u63A2\u5165\u7EA2\u5507\u4E2D\uFF0C\u6B63\u5F85\u7FFB\u624B\u94A9\u4F4F\u9999\u820C\uFF0C\u5FFD\u7136\u4E00\u9053\u767D\u5149\u75BE\u5C04\u800C\u81F3\uFF0C\u76F4\u523A\u6155\u5BB9\u9F99\u54BD\u5589\u3002</p><p>\u5371\u6025\u4E2D\u6155\u5BB9\u9F99\u7AD6\u8D77\u5DE6\u624B\u7EC6\u7A84\u7684\u8584\u5203\uFF0C\u51DD\u805A\u5168\u8EAB\u529F\u529B\u5E94\u4ED8\u8FD9\u96F7\u9706\u4E07\u94A7\u7684\u4E00\u88AD\u3002</p><p>\u300C\u53EE\u300D\u7684\u4E00\u58F0\uFF0C\u5C0F\u5200\u8131\u624B\u800C\u51FA\uFF0C\u6155\u5BB9\u9F99\u6574\u4E2A\u8EAB\u4F53\u50CF\u6295\u51FA\u7684\u77F3\u5B50\u822C\u5012\u5F39\u5230\u7D2B\u73AB\u8EAB\u540E\u3002\u90A3\u9053\u767D\u5149\u53BB\u52BF\u4E0D\u51CF\uFF0C\u786C\u751F\u751F\u523A\u5165\u77F3\u680F\uFF0C\u4EFF\u4F5B\u7A7F\u900F\u8150\u6728\uFF0C\u76F4\u5165\u534A\u5C3A\u3002\u957F\u5251\u5982\u4E00\u6CD3\u79CB\u6C34\uFF0C\u6F84\u51C0\u593A\u76EE\uFF0C\u674F\u9EC4\u8272\u7684\u5251\u7A57\u98D8\u8361\u7740\u6E10\u6E10\u9759\u6B62\u3002</p><p>\u6155\u5BB9\u9F99\u4FCA\u8138\u4E0A\u8840\u8272\u4E00\u95EA\u5373\u6CA1\uFF0C\u957F\u5251\u867D\u7136\u662F\u8131\u624B\u63B7\u51FA\uFF0C\u4F46\u52B2\u529B\u51CC\u5389\uFF0C\u76F4\u5982\u95EA\u7535\u8FC5\u96F7\u3002\u82E5\u975E\u4ED6\u8EAB\u5177\u5947\u529F\uFF0C\u8FD9\u4E00\u5251\u4FBF\u8981\u4E86\u4ED6\u7684\u5C0F\u547D\u3002</p><p>\u503E\u76C6\u5927\u96E8\u6E10\u6E10\u6B62\u6B47\uFF0C\u70DF\u96E8\u51C4\u6726\u4E2D\uFF0C\u4E00\u4E2A\u8EAB\u7740\u767D\u8863\u7684\u5973\u5B50\u7ACB\u5728\u6811\u68A2\uFF0C\u51B7\u51B7\u770B\u7740\u6155\u5BB9\u9F99\u3002\u811A\u4E0B\u7684\u6811\u679D\u53EA\u6709\u5C0F\u6307\u7C97\u7EC6\uFF0C\u4F46\u5979\u5374\u50CF\u7247\u7FBD\u6BDB\u822C\u8D34\u5728\u679D\u4E0A\uFF0C\u6D51\u4E0D\u7740\u529B\u3002\u679D\u53F6\u8F7B\u6447\uFF0C\u5973\u5B50\u767D\u8863\u98D8\u626C\uFF0C\u76F4\u6B32\u51CC\u7A7A\u98DE\u53BB\u3002</p><p>\u7D2B\u73AB\u626C\u8D77\u8138\uFF0C\u60CA\u559C\u5730\u53EB\u9053\uFF1A\u300C\u5E08\u7236\uFF01\u300D</p>',57);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
