import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_32" tabindex="-1"><a class="header-anchor" href="#_32" aria-hidden="true">#</a> 32</h1><p>\u96EA\u5CF0\u795E\u5C3C\u5934\u6234\u5C3C\u5E3D\uFF0C\u80F8\u524D\u6302\u7740\u4E00\u4E32\u5FF5\u73E0\uFF0C\u51B7\u51B0\u51B0\u8D1F\u624B\u800C\u7ACB\u3002\u96EA\u767D\u7684\u50E7\u888D\u4E00\u5C18\u4E0D\u67D3\uFF0C\u6563\u53D1\u7740\u6DE1\u6DE1\u7684\u5149\u8292\uFF0C\u98D8\u98DE\u7684\u96E8\u96FE\u8DDD\u8EAB\u4F53\u8FD8\u6709\u5BF8\u8BB8\u4FBF\u88AB\u52B2\u6C14\u8FEB\u5F00\u3002</p><p>\u5979\u6210\u540D\u5DF2\u4E8C\u5341\u5E74\uFF0C\u4F46\u770B\u4E0A\u53BB\u76F4\u5982\u4E09\u5341\u8BB8\u4EBA\uFF0C\u6A31\u5507\u661F\u76EE\uFF0C\u7709\u679D\u5982\u753B\uFF0C\u867D\u7136\u80F8\u90E8\u5E73\u6574\uFF0C\u4F46\u7F01\u8863\u98D8\u626C\u4E2D\uFF0C\u4ECD\u80FD\u770B\u51FA\u8170\u80A2\u7EC6\u8F6F\uFF0C\u4F53\u6001\u52A8\u4EBA\u3002\u53EA\u662F\u8138\u4E0A\u6302\u7740\u4E00\u5C42\u5BD2\u971C\uFF0C\u8083\u6740\u51CC\u5389\uFF0C\u4F7F\u4EBA\u5FFD\u89C6\u4E86\u5979\u7684\u7F8E\u8C8C\u3002</p><p>\u6155\u5BB9\u9F99\u4E0D\u6562\u6020\u6162\uFF0C\u8FDE\u5FD9\u4ECE\u8170\u95F4\u62D4\u51FA\u957F\u5251\uFF0C\u6C89\u8170\u659C\u80A9\uFF0C\u5251\u950B\u9065\u6307\u96EA\u5CF0\u795E\u5C3C\u3002\u4ED6\u4E07\u4E07\u60F3\u4E0D\u5230\u5408\u6731\u90AA\u9752\u6811\u548C\u5C48\u82E6\u85E4\u4E24\u4EBA\u4E4B\u529B\uFF0C\u7ADF\u7136\u8FD8\u6321\u4E0D\u4F4F\u8FD9\u540D\u5353\u7136\u9057\u4E16\u7684\u7EDD\u9876\u9AD8\u624B\u3002</p><p>\u6797\u9999\u8FDC\u542C\u5230\u7D2B\u73AB\u7684\u60CA\u547C\uFF0C\u7ACB\u523B\u6323\u624E\u7740\u62AC\u8D77\u5934\uFF0C\u832B\u7136\u7684\u56DB\u5904\u5F20\u671B\u3002\u96EA\u5CF0\u795E\u5C3C\u770B\u5230\u7231\u5F92\u53D7\u6B64\u5947\u8FB1\uFF0C\u5DF2\u662F\u5FC3\u5185\u9707\u6012\uFF0C\u8FD9\u65F6\u770B\u51FA\u5979\u53CC\u76EE\u5931\u660E\uFF0C\u66F4\u662F\u6012\u706B\u4E07\u4E08\uFF0C\u7709\u89D2\u4E00\u6311\uFF0C\u6EE1\u9762\u715E\u6C14\u7684\u817E\u8EAB\u800C\u8D77\u3002</p><p>\u6155\u5BB9\u9F99\u624B\u4E2D\u7684\u957F\u5251\u7075\u86C7\u822C\u6602\u8D77\uFF0C\u523A\u5411\u795E\u5C3C\u8170\u4FA7\u3002\u795E\u5C3C\u9762\u5982\u5BD2\u51B0\uFF0C\u957F\u8896\u4E00\u7FFB\uFF0C\u4E00\u53EA\u6B3A\u971C\u8D5B\u96EA\u7684\u7389\u6307\u84E6\u5730\u63A2\u51FA\uFF0C\u70B9\u5728\u5251\u810A\u4E0A\u3002\u6155\u5BB9\u9F99\u624B\u4E0A\u4E00\u9707\uFF0C\u957F\u5251\u53BB\u52BF\u53D7\u963B\u3002</p><p>\u4ED6\u6C72\u53D6\u529F\u529B\u867D\u591A\uFF0C\u4F46\u5E76\u975E\u6BCF\u4EBA\u7684\u5185\u529B\u90FD\u53EF\u5B8C\u5168\u5438\u6536\uFF0C\u5341\u6709\u516D\u6210\u90FD\u635F\u8017\u5728\u4EA4\u6362\u4E2D\uFF0C\u56E0\u6B64\u4E0D\u4EC5\u6BD4\u4E0D\u4E0A\u88AB\u4ED6\u643E\u5E72\u5185\u529B\u7684\u9634\u59EC\uFF0C\u8F83\u4E4B\u6731\u90AA\u9752\u6811\u4E5F\u6709\u4E0D\u8DB3\u3002\u4ED6\u53CD\u624B\u64CE\u51FA\u7247\u7389\uFF0C\u53F3\u5251\u5DE6\u5200\u7AED\u529B\u62B5\u6321\u3002</p><p>\u795E\u5C3C\u4E00\u8FB9\u51FA\u62DB\uFF0C\u4E00\u8FB9\u633D\u8D77\u7D2B\u73AB\uFF0C\u771F\u6C14\u5230\u5904\uFF0C\u7ACB\u523B\u89E3\u5F00\u88AB\u5C01\u7684\u7A74\u9053\u3002\u5979\u5165\u624B\u4FBF\u77E5\u7D2B\u73AB\u5185\u529F\u88AB\u6563\uFF0C\u65E0\u6CD5\u805A\u62E2\uFF0C\u800C\u53D7\u8FB1\u7684\u6797\u9999\u8FDC\u66F4\u662F\u6B66\u529F\u5C3D\u5931\u3002\u8FD9\u51E0\u540D\u5F1F\u5B50\u5728\u98D8\u6885\u5CF0\u5B66\u827A\u591A\u5E74\uFF0C\u60C5\u540C\u6BCD\u5973\uFF0C\u6CA1\u60F3\u5230\u77ED\u77ED\u6708\u4F59\u65F6\u95F4\u5C31\u53D7\u6B64\u51CC\u8FB1\u3002\u96EA\u5CF0\u795E\u5C3C\u53CC\u76EE\u55B7\u706B\uFF0C\u76DB\u6012\u51FA\u624B\uFF0C\u52B2\u6C14\u51CC\u7A7A\u76F4\u5982\u98CE\u5377\u6B8B\u4E91\uFF0C\u62DB\u62DB\u7D27\u903C\u3002</p><p>\u6B64\u65F6\u66B4\u96E8\u521D\u6B47\uFF0C\u7A7A\u65F7\u7684\u5E7F\u573A\u7A7A\u65E0\u4E00\u4EBA\uFF0C\u6155\u5BB9\u9F99\u6709\u5FC3\u5524\u4EBA\u63F4\u624B\uFF0C\u4F46\u88AB\u795E\u5C3C\u903C\u5F97\u5598\u4E0D\u8FC7\u6C14\u6765\uFF0C\u4F5C\u58F0\u4E0D\u5F97\u3002</p><p>\u536B\u79C0\u7EB9\u867D\u662F\u4E00\u6D3E\u638C\u95E8\uFF0C\u4F46\u53EA\u662F\u9760\u59FF\u8272\u5F97\u6B64\u9AD8\u4F4D\uFF0C\u4E0D\u8FC7\u661F\u6708\u6E56\u624B\u4E2D\u7684\u5080\u5121\u73A9\u7269\uFF0C\u6B66\u529F\u5E73\u5E73\u3002\u773C\u89C1\u5BAB\u4E3B\u8FDE\u9022\u9669\u62DB\uFF0C\u8FDF\u7591\u8BB8\u4E45\u624D\u5F20\u53E3\u9AD8\u547C\uFF0C\u300C\u6765\u2014\u2014\u300D</p><p>\u96EA\u5CF0\u795E\u5C3C\u89C1\u536B\u79C0\u7EB9\u8EAB\u4E0A\u8D64\u88F8\uFF0C\u672C\u4EE5\u4E3A\u5979\u4E5F\u662F\u53D7\u8FB1\u7684\u5973\u5B50\uFF0C\u542C\u5230\u5979\u7A81\u7136\u5F00\u53E3\uFF0C\u7ACB\u5373\u624B\u6307\u4E00\u5F39\uFF0C\u4E00\u7C92\u5FF5\u73E0\u91CD\u91CD\u6253\u5728\u536B\u79C0\u7EB9\u80F8\u4FA7\uFF0C\u5C01\u4E86\u5979\u7684\u7A74\u9053\u3002\u63A5\u7740\u4E24\u624B\u4E00\u4E3E\uFF0C\u56DB\u5341\u4E03\u9897\u5927\u5927\u5C0F\u5C0F\u7684\u5FF5\u73E0\u6563\u6210\u4E00\u4E2A\u5706\u5708\uFF0C\u6C34\u94F6\u6CC4\u5730\u822C\u671D\u6155\u5BB9\u9F99\u653B\u53BB\u3002</p><p>\u6155\u5BB9\u9F99\u773C\u89C1\u65E0\u6CD5\u62B5\u6321\uFF0C\u5408\u8EAB\u6251\u5230\u5730\u4E0A\uFF0C\u72FC\u72C8\u5730\u6EDA\u5230\u4E00\u8FB9\u3002\u5FFD\u7136\u80A9\u540E\u4E00\u75BC\uFF0C\u5DF2\u4E2D\u4E86\u4E00\u679A\u5FF5\u73E0\u3002</p><p>\u96EA\u5CF0\u795E\u5C3C\u6B63\u5F85\u75DB\u4E0B\u6740\u624B\uFF0C\u8FDC\u5904\u5374\u4F20\u6765\u4E00\u9635\u7CDF\u6742\u7684\u811A\u6B65\u58F0\u3002\u4E00\u7FA4\u5E2E\u4F17\u8FB9\u8DD1\u8FB9\u53EB\u3002</p><p>\u300C\u6731\u90AA\u62A4\u6CD5\u53D7\u4E86\u91CD\u4F24\u2026\u2026\u300D</p><p>\u300C\u96EA\u5CF0\u795E\u5C3C\u6740\u4E86\u5C48\u62A4\u6CD5\uFF0C\u4E00\u8DEF\u671D\u5BAB\u91CC\u6765\u4E86\u2026\u2026\u300D</p><p>\u5F85\u770B\u6E05\u573A\u4E2D\u4E24\u4EBA\u7684\u6076\u6597\uFF0C\u4F17\u4EBA\u6123\u4E86\u4E00\u4E0B\uFF0C\u614C\u5FD9\u64CE\u51FA\u5175\u5203\uFF0C\u6251\u4E0A\u6551\u6388\u3002</p><p>\u96EA\u5CF0\u795E\u5C3C\u5728\u6155\u5BB9\u9F99\u80CC\u4E0A\u91CD\u51FB\u4E00\u638C\uFF0C\u7FFB\u8EAB\u63A0\u5411\u77F3\u680F\uFF0C\u62D4\u51FA\u957F\u5251\u3002\u5251\u5149\u541E\u5410\uFF0C\u5C06\u4E00\u540D\u5E2E\u4F17\u62E6\u8170\u65A9\u65AD\u3002\u300C\u5F6D\u300D\u7684\u4E00\u58F0\uFF0C\u66B4\u8D77\u4E00\u7BF7\u8840\u96E8\uFF0C\u65AD\u80A2\u4E71\u98DE\u3002</p><p>\u661F\u6708\u6E56\u5C3D\u662F\u51F6\u6076\u4E4B\u5F92\uFF0C\u55DC\u8840\u6210\u6027\uFF0C\u98DE\u6E85\u9C9C\u8840\u53CD\u800C\u6FC0\u8D77\u4E86\u4F17\u4EBA\u7684\u75AF\u72C2\uFF0C\u72C2\u558A\u7740\u820D\u547D\u76F8\u6597\u3002\u96EA\u5CF0\u795E\u5C3C\u7ACB\u5728\u6797\u9999\u8FDC\u548C\u6155\u5BB9\u7D2B\u73AB\u4E2D\u95F4\uFF0C\u957F\u5251\u98DE\u821E\uFF0C\u6BCF\u4E00\u5251\u90FD\u5E26\u8D77\u6F2B\u5929\u7684\u8840\u96FE\uFF0C\u4F46\u654C\u4EBA\u5374\u8D8A\u6740\u8D8A\u591A\uFF0C\u4E0D\u4EC5\u6B66\u529F\u4E0D\u5F31\uFF0C\u800C\u4E14\u4E00\u4E2A\u4E2A\u53CC\u76EE\u8840\u7EA2\uFF0C\u5168\u662F\u4EE5\u547D\u640F\u547D\u7684\u6253\u6CD5\u3002</p><p>\u518D\u6597\u7247\u523B\uFF0C\u5FFD\u7136\u91D1\u98CE\u5927\u632F\uFF0C\u4E00\u67C4\u5DE8\u65A7\u5E26\u7740\u5389\u5578\u731B\u5288\u4E0B\u6765\u3002\u795E\u5C3C\u6A2A\u5251\u6321\u4F4F\uFF0C\u867D\u7136\u628A\u6765\u4EBA\u9707\u5F00\uFF0C\u81EA\u5DF1\u4E5F\u4E0D\u7531\u6C14\u8840\u7FFB\u817E\u3002</p><p>\u91D1\u5F00\u7532\u9762\u8272\u51DD\u91CD\uFF0C\u5468\u8EAB\u52B2\u6C14\u6D41\u8F6C\uFF0C\u94F6\u767D\u7684\u957F\u8863\u4E0D\u4F4F\u9F13\u80C0\uFF0C\u808C\u8089\u866C\u7ED3\u7684\u624B\u81C2\u7D27\u7D27\u63E1\u4F4F\u5DE8\u65A7\uFF0C\u7F13\u7F13\u7ED5\u7740\u5708\u5B50\u3002\u63A5\u7740\u4E00\u540D\u7EA2\u8863\u6C49\u5B50\u6A2A\u98DE\u800C\u81F3\uFF0C\u5728\u7A7A\u4E2D\u4E00\u4E2A\u7FFB\u6EDA\uFF0C\u624B\u4E0A\u5DF2\u591A\u4E86\u5BF9\u5947\u5F62\u602A\u72B6\u7684\u5175\u5668\u3002\u56F4\u653B\u7684\u5E2E\u4F17\u5411\u540E\u6563\u5F00\uFF0C\u4E09\u540D\u5206\u7740\u7EFF\u888D\u3001\u9ED1\u8863\u3001\u9EC4\u886B\u7684\u7537\u5973\u6162\u6162\u8D70\u8FD1\u3002</p><p>\u96EA\u5CF0\u795E\u5C3C\u5BD2\u76EE\u4E00\u8F6C\uFF0C\u4FBF\u770B\u51FA\u94F6\u8863\u5927\u6C49\u6B66\u529F\u4E0D\u4FD7\uFF0C\u7EFF\u888D\u8001\u8005\u529F\u529B\u6DF1\u6E5B\uFF0C\u7EDD\u4E0D\u5728\u6731\u90AA\u9752\u6811\u4E4B\u4E0B\uFF0C\u9ED1\u8863\u5973\u5B50\u4E5F\u662F\u52B2\u654C\uFF0C\u53EA\u6709\u90A3\u4E2A\u9EC4\u886B\u80D6\u5B50\u50CF\u662F\u5185\u4F24\u521D\u7652\uFF0C\u811A\u6B65\u7565\u6709\u865A\u6D6E\u3002</p><p>\u6B64\u523B\u661F\u6708\u6E56\u9AD8\u624B\u9F50\u81F3\uFF0C\u58F0\u52BF\u9A87\u4EBA\u3002\u96EA\u5CF0\u795E\u5C3C\u4ED7\u5251\u800C\u7ACB\uFF0C\u8138\u4E0A\u4EFF\u4F5B\u4E07\u53E4\u7384\u51B0\u6CA1\u6709\u4E00\u4E1D\u6CE2\u6F9C\u3002</p><p>\u5C60\u6000\u6C89\u8001\u8FDC\u4FBF\u62B1\u8D77\u62F3\uFF0C\u6EE1\u8138\u7B11\u5BB9\u5730\u8BF4\u9053\uFF1A\u300C\u4E0D\u77E5\u540D\u9707\u5929\u4E0B\u7684\u795E\u5C3C\u5927\u9A7E\u5149\u4E34\uFF0C\u6709\u5931\u8FDC\u8FCE\uFF0C\u544A\u7F6A\u544A\u7F6A\u3002\u300D</p><p>\u96EA\u5CF0\u795E\u5C3C\u51B7\u51B7\u9053\uFF1A\u300C\u4EA4\u51FA\u6155\u5BB9\u7D2B\u73AB\uFF0C\u8FB1\u6211\u5F92\u513F\u8005\u5C3D\u6570\u81EA\u88C1\uFF0C\u52A8\u624B\u8005\u5E9F\u53BB\u6B66\u529F\uFF0C\u8D2B\u5C3C\u4FBF\u9976\u8FC7\u5176\u4ED6\u4EBA\u3002\u300D</p><p>\u5C60\u6000\u6C89\u7B11\u5BB9\u4E0D\u6539\uFF0C\u300C\u5E08\u592A\u8FD9\u6761\u4EF6\u592A\u82DB\u4E86\uFF0C\u6559\u4E2D\u8FD1\u5343\u540D\u5F1F\u5144\u90FD\u4E0A\u8FC7\u5BD2\u6708\u5200\uFF0C\u5C3D\u6570\u81EA\u88C1\uFF0C\u8C01\u6765\u966A\u5927\u5E08\u5FEB\u6D3B\u5462\uFF1F\u300D</p><p>\u96EA\u5CF0\u795E\u5C3C\u540D\u9707\u6C5F\u6E56\uFF0C\u8C01\u89C1\u4E86\u5979\u90FD\u662F\u6BD5\u606D\u6BD5\u656C\uFF0C\u4F55\u66FE\u542C\u8FC7\u5982\u6B64\u8C03\u620F\u7684\u8BDD\u8BED\uFF0C\u95FB\u8A00\u67F3\u7709\u4E00\u6311\uFF0C\u4FBF\u5F85\u51FA\u624B\u3002</p><p>\u65C1\u8FB9\u970D\u72C2\u7130\u66B4\u559D\u4E00\u58F0\uFF0C\u706B\u7130\u4EE4\u8131\u624B\u800C\u51FA\u3002\u5F85\u5230\u96EA\u5CF0\u795E\u5C3C\u8EAB\u524D\u5C3A\u8BB8\u5FFD\u7136\u4E00\u6C89\uFF0C\u5212\u5411\u8179\u4FA7\u3002\u8FD9\u4E00\u63B7\u770B\u4F3C\u4E00\u5F80\u65E0\u524D\uFF0C\u5176\u5B9E\u8FD8\u7559\u6709\u4E24\u5206\u56DE\u529B\uFF0C\u5373\u4F7F\u8FD9\u53D8\u5411\u7684\u4E00\u51FB\u4E5F\u65E0\u6CD5\u594F\u529F\uFF0C\u53EA\u8981\u903C\u5F97\u96EA\u5CF0\u795E\u5C3C\u51FA\u5251\uFF0C\u91D1\u5F00\u7532\u4FBF\u53EF\u8D81\u865A\u800C\u5165\u3002\u4ED6\u7684\u7834\u5C71\u65A7\u521A\u67D4\u5408\u6D4E\uFF0C\u53EA\u8981\u80FD\u7F20\u4F4F\u96EA\u5CF0\u795E\u5C3C\uFF0C\u4E94\u4EBA\u9F50\u4E0A\uFF0C\u5373\u4F7F\u662F\u5927\u7F57\u91D1\u521A\u4E5F\u63D2\u7FC5\u96BE\u9003\u3002</p><p>\u7B49\u4E24\u679A\u706B\u7130\u4EE4\u89E6\u5230\u50E7\u888D\uFF0C\u96EA\u5CF0\u795E\u5C3C\u624D\u52A8\u4F5C\u8D77\u6765\uFF0C\u5979\u8EAB\u5B50\u4E00\u4FA7\uFF0C\u957F\u5251\u500F\u7136\u671D\u4E0B\u6025\u523A\uFF0C\u5C06\u75BE\u5C04\u706B\u7130\u4EE4\u7A7F\u5728\u5251\u4E0A\u3002\u5947\u602A\u7684\u662F\u5251\u4EE4\u76F8\u51FB\uFF0C\u7ADF\u7136\u6CA1\u6709\u53D1\u51FA\u4E00\u70B9\u58F0\u97F3\u3002</p><p>\u91D1\u5F00\u7532\u864E\u6B65\u751F\u98CE\uFF0C\u8DE8\u8FC7\u4E24\u4E08\u8DDD\u79BB\u3002\u4E00\u62DB\u5343\u519B\u8F9F\u6613\uFF0C\u5DE8\u8F6E\u822C\u7684\u94DC\u65A7\u6A2A\u626B\u795E\u5C3C\u8170\u8179\u3002\u795E\u5C3C\u4E0D\u907F\u4E0D\u8BA9\uFF0C\u90A3\u67C4\u575A\u53EF\u88C2\u77F3\u7684\u957F\u5251\u7A81\u7136\u4E00\u5F2F\uFF0C\u5DE6\u53F3\u4E00\u6446\uFF0C\u65CB\u5373\u5F39\u76F4\u3002\u5957\u5728\u5251\u4E0A\u7684\u4E24\u679A\u706B\u7130\u4EE4\u6D41\u661F\u822C\u98DE\u51FA\uFF0C\u5206\u51FB\u91D1\u970D\u4E24\u4EBA\u3002</p><p>\u63A5\u7740\u6563\u843D\u5728\u5730\u7684\u6570\u5341\u7C92\u5FF5\u73E0\u5982\u540C\u72C2\u98CE\u5377\u8FC7\u4E00\u6837\u65CB\u8F6C\u8DC3\u8D77\uFF0C\u4EE5\u4E0D\u900A\u4E8E\u624B\u63B7\u6307\u5F39\u7684\u529B\u5EA6\uFF0C\u9AD8\u9AD8\u4F4E\u4F4E\u51FB\u5411\u5468\u56F4\u4F17\u4EBA\u3002\u4E00\u65F6\u95F4\uFF0C\u6C34\u67D4\u4ED9\u7B49\u4EBA\u90FD\u88AB\u8FD9\u7A81\u5982\u5176\u6765\u7684\u4E00\u62DB\u903C\u5F97\u624B\u5FD9\u811A\u4E71\u3002</p><p>\u5F53\u65E5\u6731\u90AA\u9752\u6811\u548C\u5C48\u82E6\u85E4\u4F0F\u51FB\u96EA\u5CF0\u795E\u5C3C\uFF0C\u5374\u88AB\u5979\u8D1F\u4F24\u9003\u8131\u3002\u5F85\u96EA\u5CF0\u795E\u5C3C\u517B\u597D\u4F24\u52BF\uFF0C\u4F0F\u9F99\u6DA7\u88AB\u706D\u95E8\u4E4B\u4E8B\u5DF2\u7ECF\u98CE\u4F20\u6C5F\u6E56\uFF0C\u63A5\u7740\u53C8\u4F20\u6765\u98D8\u6885\u5CF0\u5F1F\u5B50\u88AB\u4EBA\u62BC\u7740\u4E00\u8DEF\u4EFB\u4EBA\u5978\u6DEB\u7684\u6D88\u606F\u3002\u96EA\u5CF0\u795E\u5C3C\u52C3\u7136\u9707\u6012\uFF0C\u5148\u8D81\u5C48\u82E6\u85E4\u843D\u5355\u65F6\u5C06\u4ED6\u51FB\u6BD9\uFF0C\u7136\u540E\u53C8\u51FB\u4F24\u6731\u90AA\u9752\u6811\uFF0C\u5374\u672A\u53D6\u4ED6\u6027\u547D\uFF0C\u800C\u662F\u4E00\u8DEF\u8FFD\u6740\uFF0C\u5C3E\u968F\u8FFD\u81F3\u661F\u6708\u6E56\u3002</p><p>\u773C\u524D\u4E94\u957F\u8001\u5404\u5177\u5947\u529F\uFF0C\u82E5\u4ED6\u4EEC\u7F20\u4F4F\uFF0C\u8F83\u4E4B\u4E24\u540D\u62A4\u6CD5\u56F4\u653B\u66F4\u4E3A\u51F6\u9669\u3002\u4F55\u51B5\u8EAB\u8FB9\u8FD8\u6709\u4E24\u540D\u65E0\u529B\u76F8\u6297\u7684\u5F92\u5F1F\u3002\u96EA\u5CF0\u795E\u5C3C\u8FEB\u5F00\u4F17\u4EBA\uFF0C\u4E00\u5251\u65A9\u65AD\u6797\u9999\u8FDC\u8EAB\u4E0B\u7684\u94C1\u94FE\uFF0C\u631F\u8D77\u4E24\u7231\u5F92\u671D\u573A\u5916\u7684\u9AD8\u6811\u63A0\u53BB\u3002\u4EE5\u5979\u7684\u529F\u529B\uFF0C\u631F\u7740\u4E24\u5973\u4ECD\u662F\u75BE\u82E5\u98DE\u9E1F\uFF0C\u53EA\u8981\u8E0F\u4E0A\u6811\u68A2\uFF0C\u51E0\u4E2A\u7EB5\u843D\u4FBF\u53EF\u63A0\u81F3\u6E56\u5CB8\u3002</p><p>\u4E94\u957F\u8001\u62D4\u5730\u800C\u8D77\uFF0C\u6C34\u67D4\u4ED9\u6700\u64C5\u8F7B\u529F\uFF0C\u62A2\u5728\u4F17\u4EBA\u4E4B\u524D\uFF0C\u8DDD\u96EA\u5CF0\u795E\u5C3C\u4E0D\u8DB3\u4E00\u4E08\uFF0C\u4F46\u5979\u63E1\u4F4F\u8896\u5185\u7684\u8F6F\u97AD\uFF0C\u5374\u672A\u51FA\u624B\u3002\u6709\u96EA\u5CF0\u795E\u5C3C\u8FD9\u4E2A\u52B2\u654C\u5728\u4FA7\uFF0C\u5BF9\u81EA\u5DF1\u53EA\u6709\u597D\u5904\u3002</p><p>\u6700\u597D\u5979\u80FD\u6551\u8D70\u4E24\u5973\uFF0C\u518D\u56DE\u6765\u4E0E\u6155\u5BB9\u9F99\u62FC\u4E2A\u4F60\u6B7B\u6211\u6D3B\u3002</p><p>\u601D\u7D22\u95F4\uFF0C\u96EA\u5CF0\u795E\u5C3C\u5DF2\u8DC3\u81F3\u573A\u8FB9\u3002\u5FFD\u7136\u8EAB\u540E\u98CE\u58F0\u4E00\u7D27\uFF0C\u6C90\u58F0\u4F20\u540E\u53D1\u5148\u81F3\uFF0C\u62A2\u5728\u6C34\u67D4\u4ED9\u8EAB\u524D\u3002\u624B\u81C2\u4E00\u5C55\uFF0C\u4E00\u6839\u957F\u4EC5\u4E24\u5C3A\u7684\u6728\u68CD\u642D\u5728\u4E86\u7D2B\u73AB\u811A\u4E0A\u3002</p><p>\u7247\u523B\u95F4\u96EA\u5CF0\u795E\u5C3C\u63A5\u8FDE\u5316\u89E3\u80C1\u4E0B\u4F20\u6765\u7684\u4E5D\u9053\u771F\u6C14\uFF0C\u5F53\u5979\u8E0F\u4E0A\u679D\u5934\uFF0C\u5DF2\u65E0\u4F59\u529B\u518D\u5373\u884C\u8DC3\u8D77\u3002\u795E\u5C3C\u5C06\u7D2B\u73AB\u501A\u5728\u6000\u4E2D\uFF0C\u817E\u51FA\u53F3\u624B\u4E0E\u6C90\u58F0\u4F20\u8FDE\u4EA4\u4E03\u62DB\u3002\u6700\u540E\u4E00\u5251\u70B9\u5728\u6728\u68CD\u4E0A\uFF0C\u5C06\u6C90\u58F0\u4F20\u903C\u843D\u6811\u4E0B\u3002</p><p>\u63A5\u7740\u91D1\u5F00\u7532\u7684\u5DE8\u65A7\u6325\u81F3\uFF0C\u5979\u77E5\u9053\u6B64\u4EBA\u529F\u529B\u9AD8\u7EDD\uFF0C\u4E00\u65E6\u8BA9\u4F17\u4EBA\u5F62\u6210\u5408\u56F4\uFF0C\u52BF\u65E0\u5E78\u514D\u3002\u65E0\u5948\u4E0B\u53EA\u597D\u653E\u5F00\u7D2B\u73AB\uFF0C\u4E00\u624B\u6267\u5251\uFF0C\u4E00\u624B\u631F\u7740\u6797\u9999\u8FDC\uFF0C\u671D\u8FDC\u5904\u63A0\u53BB\u3002</p><p>\u6797\u4E2D\u5341\u4F59\u540D\u5E2E\u4F17\u6B63\u62A4\u5728\u4E00\u4E2A\u904D\u8EAB\u8840\u6C61\u7684\u6C49\u5B50\u8EAB\u65C1\uFF0C\u96EA\u5CF0\u795E\u5C3C\u8EAB\u5F71\u4E00\u6643\uFF0C\u4ECE\u4EBA\u7FA4\u4E2D\u4E00\u95EA\u800C\u51FA\u3002\u8EAB\u540E\u4E00\u9897\u5377\u53D1\u9EC4\u987B\u7684\u5934\u9885\u51B2\u5929\u800C\u8D77\uFF0C\u826F\u4E45\u624D\u843D\u5728\u5730\u4E0A\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u6155\u5BB9\u9F99\u8138\u8272\u60E8\u767D\uFF0C\u5F20\u53E3\u53C8\u5410\u51FA\u4E00\u80A1\u9C9C\u8840\u3002\u5728\u4ED6\u9762\u524D\uFF0C\u662F\u6CEB\u7136\u6B32\u6EF4\u7684\u6155\u5BB9\u7D2B\u73AB\u548C\u6731\u90AA\u9752\u6811\u7684\u5934\u9885\u3002</p><p>\u7D2B\u73AB\u65F6\u5FE7\u65F6\u559C\uFF0C\u867D\u7136\u81EA\u5DF1\u672A\u80FD\u9003\u8131\uFF0C\u4F46\u5E08\u7236\u6BD5\u7ADF\u5DF2\u7ECF\u8D76\u5230\u6B64\u5904\uFF0C\u800C\u4E14\u8FD8\u5927\u5C55\u795E\u5A01\uFF0C\u5728\u661F\u6708\u6E56\u9AD8\u624B\u5C3D\u51FA\u7684\u60C5\u51B5\u4E0B\u6551\u8D70\u4E86\u4E8C\u5E08\u59D0\u3002\u8FDF\u65E9\u5E08\u7236\u80FD\u6740\u6389\u8FD9\u4E9B\u5996\u5B7D\uFF0C\u628A\u6BCD\u4EB2\u3001\u5927\u5E08\u59D0\u3001\u4E09\u5E08\u59D0\u3001\u767D\u6C0F\u59D0\u59B9\u7EDF\u7EDF\u6551\u51FA\u53BB\u3002\u7D2B\u73AB\u8D8A\u60F3\u8D8A\u9AD8\u5174\uFF0C\u4ECE\u773C\u89D2\u770B\u7740\u6155\u5BB9\u9F99\uFF0C\u5FC3\u9053\uFF1A\u300C\u6DF7\u86CB\uFF0C\u600E\u4E48\u6CA1\u4E00\u638C\u62CD\u6B7B\u4F60\uFF01\u300D</p><p>\u6155\u5BB9\u9F99\u670D\u4E0B\u7597\u4F24\u7684\u4E39\u836F\uFF0C\u8C03\u606F\u534A\u664C\uFF0C\u8138\u4E0A\u989C\u8272\u6E10\u590D\u3002\u60F3\u5230\u5F53\u65F6\u7684\u51F6\u9669\uFF0C\u4ED6\u4ECD\u5FC3\u6709\u4F59\u60B8\uFF0C\u7F13\u7F13\u9053\uFF1A\u300C\u5404\u5802\u6D3E\u51FA\u4EBA\u9A6C\uFF0C\u641C\u5BFB\u96EA\u5CF0\u795E\u5C3C\u3002\u4E0D\u5FC5\u4E0E\u5979\u4EA4\u624B\uFF0C\u4E00\u65E6\u53D1\u73B0\u8E2A\u8FF9\uFF0C\u7ACB\u523B\u56DE\u62A5\u3002\u300D\u53C8\u9053\uFF1A\u300C\u795E\u6BBF\u4E43\u6211\u6559\u5723\u5730\uFF0C\u6709\u52B3\u8BF8\u4F4D\u957F\u8001\u5728\u6BBF\u5916\u8F6E\u756A\u770B\u5B88\u3002\u300D</p><p>\u4F17\u4EBA\u70B9\u5934\u5E94\u8BFA\uFF0C\u6C34\u67D4\u4ED9\u770B\u7740\u6731\u90AA\u9752\u6811\u7684\u5934\u9885\u5FC3\u4E0B\u6697\u559C\u3002\u6731\u90AA\u9752\u6811\u662F\u6155\u5BB9\u9F99\u6700\u6709\u529B\u7684\u652F\u6301\u8005\uFF0C\u5982\u4ECA\u4E09\u540D\u62A4\u6CD5\u5DF2\u53BB\u5176\u4E8C\uFF0C\u53EA\u5269\u4E0B\u4E94\u4F4D\u957F\u8001\uFF0C\u81EA\u5DF1\u5DF2\u7ECF\u5360\u4E86\u516D\u6210\u8D62\u9762\u3002</p><p>\u6155\u5BB9\u9F99\u5E76\u672A\u7559\u5FC3\u5979\u7684\u795E\u8272\uFF0C\u53EA\u662F\u51B7\u51B7\u770B\u7740\u536B\u79C0\u7EB9\uFF0C\u82E5\u662F\u5979\u65E9\u4E9B\u5F00\u58F0\u672A\u8B66\uFF0C\u81EA\u5DF1\u4E5F\u4E0D\u4F1A\u8EAB\u8D1F\u91CD\u4F24\u3002\u4ED6\u5598\u4E86\u53E3\u6C14\uFF0C\u6C89\u58F0\u9053\uFF1A\u300C\u65E2\u7136\u8BE5\u4F60\u8F6E\u503C\uFF0C\u53BB\u77F3\u5BAB\u5427\u3002\u300D</p><p>\u536B\u79C0\u7EB9\u5FC3\u91CC\u53D1\u5BD2\uFF0C\u6B64\u65F6\u89C1\u5BAB\u4E3B\u672A\u52A0\u60E9\u5904\uFF0C\u4E0D\u7531\u5927\u559C\u8FC7\u671B\uFF0C\u8FDE\u5FD9\u5E94\u662F\u3002</p><p>\u6155\u5BB9\u9F99\u505C\u4E86\u7247\u523B\uFF0C\u6DE1\u6DE1\u9053\uFF1A\u300C\u6C34\u957F\u8001\uFF0C\u4E39\u9633\u6D3E\u662F\u8D35\u5802\u5C5E\u4E0B\uFF0C\u5C31\u8BF7\u6C34\u957F\u8001\u4F20\u8C15\uFF0C\u4E39\u9633\u6D3E\u7531\u526F\u638C\u95E8\u6682\u7406\u5E2E\u52A1\u3002\u300D</p><p>\u536B\u79C0\u7EB9\u79C0\u76EE\u731B\u7136\u77AA\u5927\uFF0C\u53EA\u542C\u5BAB\u4E3B\u53C8\u9053\uFF1A\u300C\u5F85\u660E\u5E74\u6B64\u65F6\u536B\u638C\u95E8\u79BB\u5BAB\uFF0C\u518D\u884C\u79FB\u4EA4\u3002\u300D</p><p>\u4EE5\u5F80\u5165\u5BAB\u6700\u591A\u53EA\u662F\u5341\u65E5\uFF0C\u8FD9\u6B21\u5C45\u7136\u8981\u4E00\u5E74\u4E4B\u4E45\uFF0C\u536B\u79C0\u7EB9\u6050\u60E7\u4E07\u5206\uFF0C\u62FC\u547D\u78D5\u5934\u54ED\u6C42\u9053\uFF1A\u300C\u6C42\u5BAB\u4E3B\u5F00\u6069\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u5BD2\u58F0\u9053\uFF1A\u300C\u536B\u638C\u95E8\u53EF\u662F\u4E0D\u613F\u5728\u5BAB\u4E2D\u4F3A\u5019\uFF1F\u300D</p><p>\u536B\u79C0\u7EB9\u8EAB\u5B50\u4E00\u98A4\uFF0C\u6B65\u5C65\u4E0D\u7A33\u5730\u968F\u7D2B\u8863\u4F8D\u8005\u53BB\u4E86\u3002</p><p>\u6155\u5BB9\u9F99\u53F9\u606F\u9053\uFF1A\u300C\u6731\u90AA\u62A4\u6CD5\u4E0E\u5C48\u62A4\u6CD5\u4E3A\u672C\u6559\u6B89\u804C\uFF0C\u53EF\u5C06\u5176\u9057\u4F53\u7F6E\u4E8E\u7384\u5BAB\u2026\u2026\u300D</p><p>\u970D\u72C2\u7130\u5FCD\u4E0D\u4F4F\u9053\uFF1A\u300C\u4E24\u4F4D\u62A4\u6CD5\u8EAB\u6545\uFF0C\u6559\u4E2D\u4EBA\u624D\u51CB\u96F6\uFF0C\u8BF7\u5BAB\u4E3B\u793A\u4E0B\u3002\u300D</p><p>\u4ED6\u7684\u8BDD\u4E0D\u4F26\u4E0D\u7C7B\uFF0C\u4F46\u4EBA\u4EBA\u90FD\u542C\u51FA\u4E86\u4ED6\u7684\u610F\u601D\u3002</p><p>\u6155\u5BB9\u9F99\u6C89\u541F\u7247\u523B\uFF0C\u626C\u58F0\u9053\uFF1A\u300C\u660E\u65E5\u5348\u65F6\uFF0C\u5404\u5802\u9999\u4E3B\u4EE5\u4E0A\u9F50\u96C6\u795E\u6BBF\uFF0C\u5546\u8BA8\u65E5\u540E\u5927\u8BA1\uFF01\u300D</p>',54);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
