import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},t=p('<h1 id="_33" tabindex="-1"><a class="header-anchor" href="#_33" aria-hidden="true">#</a> 33</h1><p>\u300C\u597D\u4E9B\u4E86\u5417\uFF1F\u300D\u6155\u5BB9\u7D2B\u73AB\u67D4\u58F0\u9053\u3002</p><p>\u6155\u5BB9\u9F99\u5BF9\u5979\u7684\u6BB7\u52E4\u534A\u4FE1\u534A\u7591\uFF0C\u4F46\u8FD8\u662F\u63A5\u8FC7\u7D2B\u73AB\u624B\u91CC\u7684\u7389\u7897\u559D\u4E86\u4E00\u53E3\u3002\u6C34\u4E00\u6CBE\u5507\uFF0C\u6155\u5BB9\u9F99\u5C31\u76B1\u8D77\u7709\u5934\uFF0C\u300C\u600E\u4E48\u662F\u51C9\u6C34\uFF1F\u300D</p><p>\u300C\u4E0D\u5BF9\u5417\uFF1F\u6211\u770B\u4F60\u5934\u4E0A\u90FD\u662F\u6C57\uFF0C\u597D\u50CF\u5F88\u70ED\u5462\u2026\u2026\u300D</p><p>\u5185\u4F24\u6700\u5FCC\u51C9\u6C34\uFF0C\u8FD9\u4E2B\u5934\u4E60\u6B66\u591A\u5E74\u600E\u80FD\u4E0D\u77E5\uFF1F\u6155\u5BB9\u9F99\u5FC3\u4E0B\u6697\u9A82\uFF0C\u628A\u7897\u91CD\u91CD\u4E00\u653E\uFF0C\u5BD2\u58F0\u9053\uFF1A\u300C\u6CA1\u8DDF\u8D3C\u5C3C\u9003\u51FA\u53BB\uFF0C\u4F60\u662F\u4E0D\u662F\u5F88\u5931\u671B\u554A\uFF1F\u300D</p><p>\u7D2B\u73AB\u79C0\u8DB3\u4E00\u987F\uFF0C\u59D4\u5C48\u4E07\u5206\u5730\u8BF4\uFF1A\u300C\u4F60\u53D7\u4E86\u4F24\u4EBA\u5BB6\u597D\u5FC3\u597D\u610F\u6765\u7167\u987E\u4F60\uFF0C\u4F60\u8FD8\u8FD9\u6837\u8BF4\u2026\u2026\u4E0D\u559D\u7B97\u4E86\uFF01\u300D\u62FF\u8D77\u7389\u7897\u5F80\u5730\u4E0A\u4E00\u6CFC\uFF0C\u8F6C\u8EAB\u5C31\u8D70\u3002</p><p>\u4E00\u51FA\u95E8\uFF0C\u7D2B\u73AB\u773C\u73E0\u5C31\u6EF4\u6E9C\u6E9C\u8F6C\u4E86\u8D77\u6765\u3002\u601D\u7D22\u7247\u523B\uFF0C\u5979\u7406\u7406\u53D1\u9B13\uFF0C\u6B63\u5BB9\u671D\u53F6\u884C\u5357\u623F\u5185\u8D70\u53BB\uFF0C\u51C6\u5907\u987A\u624B\u7275\u7F8A\uFF0C\u4E0D\u7BA1\u4EC0\u4E48\u836F\u968F\u4FBF\u5077\u4E0A\u4E00\u4E9B\uFF0C\u63BA\u8FDB\u6C34\u91CC\u518D\u5582\u90A3\u6DF7\u86CB\u4E00\u6B21\u3002\u80FD\u836F\u6B7B\u4ED6\u6700\u597D\uFF0C\u836F\u4E0D\u6B7B\u4E5F\u8BA9\u4ED6\u96BE\u53D7\u96BE\u53D7\u3002</p><p>\u53F6\u884C\u5357\u6B63\u5728\u7F1D\u5408\u6731\u90AA\u9752\u6811\u76AE\u5934\u9885\u3002\u4ED6\u4E0E\u6731\u90AA\u9752\u6811\u662F\u751F\u6B7B\u4E4B\u4EA4\uFF0C\u6B64\u65F6\u5FC3\u795E\u6FC0\u8361\uFF0C\u8FDE\u5C11\u592B\u4EBA\u8FDB\u6765\u4E5F\u672A\u7559\u610F\u3002</p><p>\u7D2B\u73AB\u4E0D\u52A8\u58F0\u8272\u5730\u6478\u4E86\u4E24\u74F6\u836F\uFF0C\u85CF\u5728\u8896\u91CC\u3002\u5E72\u54B3\u4E00\u58F0\uFF0C\u606D\u656C\u5730\u8BF4\uFF1A\u300C\u53F6\u8001\u5E08\uFF0C\u8FD9\u4F4D\u5927\u4FA0\u662F\u4EC0\u4E48\u4EBA\uFF1F\u300D</p><p>\u53F6\u884C\u5357\u534A\u664C\u624D\u9053\uFF1A\u300C\u2026\u2026\u6211\u6559\u6731\u90AA\u62A4\u6CD5\u2026\u2026\u300D</p><p>\u7D2B\u73AB\u5FC3\u4E0B\u4E00\u52A8\uFF0C\u987E\u4E0D\u5F97\u4E0B\u836F\uFF0C\u8FDE\u5FD9\u51D1\u8FC7\u53BB\u4ED4\u7EC6\u6253\u91CF\u3002\u53D1\u4E1D\u63A0\u5230\u53F6\u884C\u5357\u8138\u4E0A\uFF0C\u540E\u8005\u76B1\u4E86\u76B1\u7709\uFF0C\u5F80\u65C1\u8FB9\u9000\u4E86\u4E00\u6B65\u3002</p><p>\u7D2B\u73AB\u5145\u6EE1\u60CB\u60DC\u5730\u8BF4\uFF1A\u300C\u6731\u90AA\u62A4\u6CD5\u8FD9\u4E48\u9AD8\u5927\u2026\u2026\u662F\u6C10\u4EBA\u5427\uFF1F\u300D</p><p>\u300C\u9C9C\u5351\u3002\u300D\u53F6\u884C\u5357\u7F1D\u597D\u6700\u540E\u4E00\u9488\uFF0C\u6C89\u7740\u8138\u7ED9\u5C38\u4F53\u62C9\u4E0A\u8863\u670D\u3002</p><p>\u7D2B\u73AB\u4E00\u4F1A\u513F\u8D5E\u6731\u90AA\u9752\u6811\u9F3B\u5B50\u751F\u5F97\u9AD8\uFF0C\u4E00\u4F1A\u513F\u53C8\u8D5E\u4ED6\u7684\u9EC4\u987B\u5A01\u731B\uFF0C\u600E\u4E48\u770B\u751F\u524D\u90FD\u662F\u4E2A\u82F1\u98CE\u4FA0\u4E49\u7684\u76D6\u4E16\u82F1\u8C6A\u3002\u76F4\u8BF4\u5F97\u53F6\u884C\u5357\u5FC3\u5934\u53D1\u9178\uFF0C&lt;\u8D3C\u5427\uFF3AEi\uFF18\u3002\uFF23\uFF2F\uFF2D\u7535\u5B50\u4E66&gt;\u7136\u540E\u8BED\u6C14\u4E00\u8F6C\uFF0C\u300C\u6731\u90AA\u5927\u4FA0\u5DF2\u7136\u5982\u6B64\uFF0C\u53F6\u8001\u5E08\u4E5F\u4E0D\u8981\u592A\u4F24\u5FC3\u4E86\u2026\u2026\u300D</p><p>\u53F6\u884C\u5357\u62B9\u4E86\u628A\u8001\u6CEA\uFF0C\u53F9\u9053\uFF1A\u300C\u6731\u90AA\u62A4\u6CD5\u4E49\u8584\u4E91\u5929\uFF0C\u4E8E\u6211\u66FE\u6709\u5927\u6069\uFF0C\u6CA1\u60F3\u5230\u2026\u2026\u300D</p><p>\u7D2B\u73AB\u6015\u4ED6\u5F80\u5E08\u7236\u8EAB\u4E0A\u8BF4\uFF0C\u8FDE\u5FD9\u8F6C\u5F00\u8BDD\u9898\uFF0C\u300C\u53F6\u843D\u5F52\u6839\uFF0C\u6731\u90AA\u62A4\u6CD5\u4EE5\u795E\u6559\u4E3A\u5BB6\uFF0C\u4E0D\u5982\u628A\u9057\u4F53\u9001\u56DE\u4ED6\u7684\u623F\u95F4\uFF0C\u4E5F\u597D\u8BA9\u4ED6\u80FD\u5B89\u5B89\u7A33\u7A33\u7761\u4E0A\u4E00\u665A\u2026\u2026\u300D</p><p>\u542C\u4E86\u8FD9\u756A\u8BDD\u53F6\u884C\u5357\u809D\u80A0\u5BF8\u65AD\uFF0C\u4FEF\u8EAB\u6258\u8D77\u6731\u90AA\u9752\u6811\uFF0C\u7F13\u6B65\u671D\u4E01\u5BA4\u8D70\u53BB\u3002</p><p>\u6155\u5BB9\u7D2B\u73AB\u5728\u540E\u9762\u5174\u594B\u7684\u6325\u4E86\u6325\u5C0F\u62F3\u5934\uFF0C\u8FDE\u5FD9\u8DDF\u4E0A\u3002</p><p>\u53F6\u884C\u5357\u5728\u95E8\u4FA7\u7684\u673A\u62EC\u4E0A\u6273\u4E86\u51E0\u4E0B\uFF0C\u7136\u540E\u62FF\u51FA\u6731\u90AA\u9752\u6811\u7684\u94A5\u5319\u6253\u5F00\u623F\u95E8\u3002\u6155\u5BB9\u7D2B\u73AB\u5C06\u4ED6\u7684\u624B\u6CD5\u4E00\u4E00\u8BB0\u5728\u5FC3\u91CC\uFF0C\u6697\u81EA\u76D8\u7B97\u5982\u4F55\u5077\u628A\u94A5\u5319\u6765\u3002</p><p>\u4E01\u5BA4\u7ED3\u6784\u4E0E\u53F6\u884C\u5357\u7684\u623F\u95F4\u4E00\u822C\u65E0\u4E8C\uFF0C\u7D2B\u73AB\u4E00\u9762\u5728\u5BA4\u5185\u7684\u7EB9\u9970\u4E2D\u56DB\u4E0B\u641C\u5BFB\uFF0C\u4E00\u9762\u70ED\u60C5\u5730\u5E2E\u7740\u628A\u5C38\u4F53\u653E\u5728\u5E8A\u4E0A\u3002</p><p>\u5F85\u773C\u775B\u626B\u5230\u5E8A\u4FA7\uFF0C\u7D2B\u73AB\u76EE\u5149\u970D\u7136\u4E00\u8DF3\u2014\u2014\u8D6B\u7136\u6B63\u662F\u90A3\u6735\u4E94\u74E3\u6885\u82B1\uFF01\u5C31\u5728\u8FD9\u65F6\uFF0C\u300C\u546F\u300D\u7684\u4E00\u58F0\uFF0C\u4E00\u4E2A\u5C0F\u74F6\u4ECE\u7D2B\u73AB\u8896\u5185\u6ED1\u51FA\uFF0C\u5728\u5730\u4E0A\u6454\u5F97\u7C89\u788E\u3002</p><p>\u4E24\u4E2A\u4EBA\u987F\u65F6\u90FD\u6123\u4F4F\u4E86\u3002</p><p>\u53F6\u884C\u5357\u8BA4\u51FA\u90A3\u662F\u81EA\u5DF1\u521A\u521A\u70AE\u5236\u7684\u7597\u4F24\u5723\u836F\u901A\u795E\u6563\uFF0C\u6068\u6068\u77AA\u4E86\u7D2B\u73AB\u4E00\u773C\uFF0C\u98DE\u8EAB\u63A0\u51FA\uFF0C\u8D76\u56DE\u53BB\u770B\u8FD8\u5C11\u4E86\u4EC0\u4E48\u4E1C\u897F\u3002</p><p>\u7D2B\u73AB\u61CA\u607C\u5730\u770B\u7740\u5730\u4E0A\uFF0C\u65CB\u5373\u8DF3\u8D77\u6765\uFF0C\u62D4\u4E0B\u94F6\u9497\u4E0D\u7BA1\u4E09\u4E03\u4E8C\u5341\u4E00\uFF0C\u5148\u671D\u56FE\u5F62\u4E2D\u95F4\u7684\u5C0F\u5B54\u523A\u4E86\u4E00\u4E0B\u3002\u624B\u4E0A\u53C8\u662F\u5FAE\u5FAE\u4E00\u52A8\uFF0C\u7D2B\u73AB\u5FC3\u4E0B\u5927\u559C\uFF0C\u516B\u6210\u662F\u627E\u5BF9\u4E86\u5730\u65B9\uFF0C\u5979\u80E1\u4E71\u6363\u9F13\u7247\u523B\uFF0C\u542C\u5230\u5916\u9762\u98CE\u58F0\u4F20\u6765\uFF0C\u8FDE\u5FD9\u7AD9\u8D77\u8EAB\u3002</p><p>\u53F6\u884C\u5357\u8138\u8272\u9634\u6C89\uFF0C\u7F13\u7F13\u4F38\u51FA\u67AF\u5E72\u7684\u624B\u638C\u3002\u7D2B\u73AB\u4E0D\u7B49\u4ED6\u5429\u5490\uFF0C\u4FBF\u4ECE\u8896\u91CC\u638F\u51FA\u53E6\u4E00\u74F6\u836F\uFF0C\u4E56\u5DE7\u5730\u9012\u5230\u4ED6\u638C\u4E2D\u3002\u6084\u6084\u5410\u4E86\u5410\u820C\u5934\uFF0C\u4E00\u6E9C\u70DF\u8D70\u4E86\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u6C89\u7761\u4E2D\u7684\u7F8E\u5987\u4ECD\u662F\u90A3\u4E48\u8273\u4E3D\u593A\u76EE\u3002\u660E\u9EC4\u8272\u7684\u9526\u88AB\u906E\u5728\u9165\u8F6F\u7684\u4E73\u4E0A\uFF0C\u67D4\u9888\u7C89\u5AE9\u7684\u808C\u80A4\u5439\u5F39\u53EF\u7834\uFF0C\u5A07\u8273\u7684\u7EA2\u5507\u5410\u9732\u82AC\u82B3\uFF0C\u7AEF\u5E84\u96C5\u81F4\u7684\u7709\u773C\u95F4\u81EA\u7136\u800C\u7136\u6D41\u9732\u51FA\u4E00\u80A1\u534E\u8D35\u4E4B\u6C14\u3002\u5979\u4E24\u81C2\u67D4\u67D4\u6401\u5728\u88AB\u5916\uFF0C\u7FE0\u8896\u4E0B\u7693\u8155\u6676\u83B9\u5982\u7389\uFF0C\u6D01\u767D\u7684\u5341\u6307\u4FEE\u957F\u7EC6\u81F4\uFF0C\u53EF\u4EE5\u60F3\u50CF\u5979\u62C2\u5728\u8EAB\u4E0A\u7684\u751C\u7F8E\u6ECB\u5473\u2026\u2026\u7136\u800C\u5B83\u4EEC\u5374\u4E0D\u53EF\u80FD\u518D\u9760\u81EA\u5DF1\u7684\u529B\u91CF\u62AC\u8D77\u4E86\u3002</p><p>\u300C\u5A18\u3002\u300D</p><p>\u767E\u82B1\u89C2\u97F3\u7F13\u7F13\u7741\u5F00\u773C\uFF0C\u534A\u664C\u624D\u8BF4\uFF1A\u300C\u73AB\u513F\u2026\u2026\u4F60\u53C8\u8DD1\u51FA\u53BB\u73A9\u4E86\u2026\u2026\u300D</p><p>\u7D2B\u73AB\u6447\u4E86\u6447\u5934\u3002</p><p>\u767E\u82B1\u89C2\u97F3\u6148\u7231\u5730\u770B\u7740\u7D2B\u73AB\uFF0C\u300C\u80DC\u513F\u5462\uFF1F\u4F60\u54E5\u54E5\u2026\u2026\u300D\u8BF4\u7740\u8138\u8272\u4E00\u53D8\uFF0C\u60CA\u9192\u8FC7\u6765\u3002</p><p>\u300C\u5A18\uFF01\u300D\u7D2B\u73AB\u54AC\u4F4F\u5634\u5507\uFF0C\u6251\u5230\u6BCD\u4EB2\u6000\u91CC\u5931\u58F0\u75DB\u54ED\u3002</p><p>\u6BCD\u4EB2\u96BE\u4EE5\u5FCD\u53D7\u8FD9\u79CD\u6B8B\u5FCD\u7684\u6298\u78E8\uFF0C\u65F6\u5E38\u6C89\u6D78\u5728\u4EE5\u5F80\u7684\u56DE\u5FC6\u4E2D\uFF0C\u867D\u7136\u6E05\u9192\u7684\u65F6\u5019\u5C45\u591A\uFF0C\u4F46\u8FD9\u6837\u4E0B\u53BB\u8FDF\u65E9\u4F1A\u75AF\u6389\u3002\u6155\u5BB9\u7D2B\u73AB\u62BD\u6CE3\u7740\u62AC\u8D77\u5934\uFF0C\u542B\u6CEA\u9732\u51FA\u4E2A\u7B11\u8138\uFF0C\u5C06\u5E08\u7236\u521A\u624D\u6551\u8D70\u5AC2\u5AC2\u7684\u4E8B\u4E00\u4E00\u544A\u8BC9\u4E86\u6BCD\u4EB2\u3002</p><p>\u8427\u4F5B\u5974\u65F6\u5FE7\u65F6\u559C\uFF0C\u4F4E\u4F4E\u5FF5\u4E86\u58F0\u4F5B\u53F7\uFF0C\u8BF4\u9053\uFF1A\u300C\u73AB\u513F\uFF0C\u6709\u673A\u4F1A\u8D76\u7D27\u79BB\u5F00\u8FD9\u91CC\uFF0C\u4E0D\u8981\u7BA1\u5A18\u2014\u2014\u5A18\u73B0\u5728\u2026\u2026\u300D\u60F3\u8D77\u81EA\u5DF1\u88AB\u4EB2\u751F\u513F\u5B50\u56DA\u7981\u6DEB\u8FB1\uFF0C\u4E0D\u7981\u51C4\u7136\u6CEA\u4E0B\u3002</p><p>\u7D2B\u73AB\u8BED\u6C14\u8F7B\u677E\u5730\u8BF4\uFF1A\u300C\u5E08\u7236\u795E\u529F\u76D6\u4E16\uFF0C\u80AF\u5B9A\u80FD\u628A\u90A3\u4E2A\u6DF7\u86CB\u4E00\u5251\u4E24\u622A\uFF01\u628A\u54B1\u4EEC\u90FD\u6551\u51FA\u53BB\uFF01\u300D</p><p>\u767E\u82B1\u89C2\u97F3\u795E\u8272\u4E00\u9EEF\uFF0C\u6C89\u9ED8\u7247\u523B\uFF0C\u4F4E\u58F0\u9053\uFF1A\u300C\u4ED6\u662F\u4F60\u4EB2\u54E5\u54E5\u2026\u2026\u300D</p><p>\u7D2B\u73AB\u94F6\u7259\u54AC\u7D27\uFF0C\u300C\u90A3\u4E2A\u79BD\u517D\uFF01\u662F\u4EB2\u54E5\u54E5\u66F4\u8BE5\u6740\uFF01\u300D</p><p>\u767E\u82B1\u89C2\u97F3\u7EA2\u5507\u5FAE\u98A4\uFF0C\u671B\u7740\u7D2B\u73AB\u7684\u53CC\u773C\u9053\uFF1A\u300C\u8BA9\u795E\u5C3C\u628A\u4ED6\u5173\u8D77\u6765\uFF0C\u8BA9\u4ED6\u4E0D\u80FD\u518D\u51FA\u53BB\u5BB3\u4EBA\u2026\u2026\u597D\u5417\uFF1F\u300D</p><p>\u7D2B\u73AB\u5589\u5934\u564E\u4F4F\uFF0C\u770B\u7740\u6BCD\u4EB2\u671F\u5F85\u7684\u773C\u795E\uFF0C\u6014\u6014\u8BF4\u4E0D\u51FA\u8BDD\u6765\u3002\u826F\u4E45\u624D\u9053\uFF1A\u300C\u5A18\uFF0C\u4ED6\u8FD9\u6837\u5BB3\u4F60\u2026\u2026\u300D</p><p>\u767E\u82B1\u89C2\u97F3\u95ED\u4E0A\u773C\uFF0C\u6676\u83B9\u7684\u6CEA\u73E0\u4ECE\u773C\u89D2\u4E32\u4E32\u6EDA\u843D\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u767D\u7389\u83BA\u8DEA\u5728\u5730\u6BEF\u4E0A\uFF0C\u5927\u6C14\u4E5F\u4E0D\u6562\u5598\u4E00\u53E3\u3002\u767D\u7389\u9E42\u6B63\u8DE8\u5750\u5728\u6155\u5BB9\u9F99\u8179\u4E0A\uFF0C\u54AC\u7259\u5FCD\u8010\u3002\u4F53\u5185\u7684\u8089\u68D2\u5FFD\u51B7\u5FFD\u70ED\uFF0C\u9F9F\u5934\u7D27\u7D27\u9876\u5728\u82B1\u5FC3\u4E0A\uFF0C\u50CF\u4E00\u5F20\u8D2A\u5A6A\u7684\u5C0F\u5634\u643E\u53D6\u5979\u4F53\u5185\u7684\u5143\u7CBE\u3002</p><p>\u767D\u7389\u9E42\u8138\u8272\u8D8A\u6765\u8D8A\u767D\uFF0C\u7A81\u7136\u5A07\u8EAF\u5267\u98A4\uFF0C\u725D\u5185\u7684\u9634\u7CBE\u55B7\u6D8C\u800C\u51FA\u3002\u8089\u68D2\u5728\u6ED1\u817B\u7684\u8089\u7A74\u5185\u4E0D\u4F4F\u8DF3\u52A8\uFF0C\u9634\u7CBE\u4E00\u70B9\u4E00\u6EF4\u5438\u5165\u7384\u6CC9\uFF0C\u4E0E\u9633\u706B\u4EA4\u878D\uFF0C\u6CBF\u7763\u8109\u3001\u6CE5\u4E38\u5BAB\u3001\u4EFB\u8109\u3001\u4F1A\u9634\u8FD0\u884C\u4E00\u4E2A\u5C0F\u5468\u5929\uFF0C\u5316\u6210\u8F7B\u6E05\u65E0\u8D28\u7684\u7CBE\u5143\u4E4B\u6C14\u3002</p><p>\u592A\u4E00\u7ECF\u529F\u5206\u4E94\u5C42\uFF0C\u4EE5\u7384\u725D\u4E4B\u95E8\u5165\u624B\uFF0C\u70BC\u7CBE\u5316\u6C14\u3001\u70BC\u6C14\u5316\u795E\uFF0C\u5F85\u4E94\u6C14\u671D\u5143\u4E4B\u540E\uFF0C\u518D\u53D6\u574E\u586B\u79BB\uFF0C\u6700\u540E\u70BC\u795E\u8FD8\u865A\uFF0C\u590D\u5F52\u65E0\u6781\u3002\u5176\u4E2D\u5B58\u7CBE\u3001\u517B\u795E\u3001\u70BC\u6C14\u4E3A\u4E09\u5FB7\u4E4B\u795E\u3002</p><p>\u6155\u5BB9\u9F99\u7ECF\u8109\u53D7\u521B\uFF0C\u6B64\u65F6\u5984\u884C\u6C72\u53D6\u771F\u6C14\u6709\u635F\u65E0\u76CA\uFF0C\u53EA\u662F\u501F\u767D\u7389\u9E42\u7684\u9634\u7CBE\u70BC\u7CBE\u5316\u6C14\uFF0C\u57F9\u6839\u56FA\u5143\u3002</p><p>\u8170\u8EAB\u4E00\u633A\uFF0C\u767D\u7389\u9E42\u8F6F\u8F6F\u8DCC\u4E0B\u5E8A\u6765\uFF0C\u50CF\u5927\u75C5\u4E00\u573A\u822C\u4F0F\u5728\u5730\u4E0A\uFF0C\u6709\u6C14\u65E0\u529B\u5730\u4F4E\u5598\u7740\u3002\u80F8\u524D\u7684\u91D1\u94C3\u5FAE\u5FAE\u8F7B\u6643\uFF0C\u53D1\u51FA\u9635\u9635\u60A6\u8033\u7684\u8F7B\u54CD\u3002</p><p>\u6155\u5BB9\u9F99\u51B7\u51B7\u770B\u4E86\u7D2B\u73AB\u4E00\u773C\uFF0C\u671D\u767D\u7389\u83BA\u52FE\u4E86\u52FE\u624B\u6307\u3002</p><p>\u767D\u7389\u83BA\u8FDE\u5FD9\u8D77\u8EAB\uFF0C\u722C\u5230\u69BB\u4E0A\uFF0C\u63E1\u4F4F\u706B\u70ED\u7684\u8089\u68D2\uFF0C\u6162\u6162\u7EB3\u5165\u4F53\u5185\u3002\u9633\u5177\u4E0A\u9897\u7C92\u78E8\u64E6\u5728\u5AE9\u8089\u4E0A\uFF0C\u9635\u9635\u80C0\u75DB\u3002\u5F85\u5B8C\u5168\u8FDB\u5165\u67D4\u97E7\u7684\u79D8\u7A74\u3002\u8089\u68D2\u4FBF\u8DF3\u52A8\u8D77\u6765\uFF0C\u65F6\u4F38\u65F6\u7F29\u4E0D\u4F4F\u649E\u51FB\u82B1\u5FC3\u3002\u767D\u7389\u83BA\u8138\u4E0A\u6E10\u6E10\u6CDB\u8D77\u4E00\u5C42\u6F6E\u7EA2\uFF0C\u547C\u5438\u53D8\u5F97\u65AD\u65AD\u7EED\u7EED\u3002</p><p>\u7D2B\u73AB\u7B49\u6155\u5BB9\u9F99\u884C\u529F\u4E2D\u9014\uFF0C\u6162\u60A0\u60A0\u8D70\u8FC7\u6765\uFF0C\u62FF\u8D77\u4E00\u6761\u6BDB\u5DFE\u6309\u5728\u4ED6\u8138\u4E0A\uFF0C\u67D4\u58F0\u9053\uFF1A\u300C\u54E5\u54E5\uFF0C\u6211\u7ED9\u4F60\u64E6\u64E6\u6C57\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u6C89\u7740\u8138\u4E0D\u4E3A\u6240\u52A8\uFF0C\u8089\u68D2\u8D8A\u52A8\u8D8A\u5FEB\uFF0C\u5FFD\u800C\u53D8\u5F97\u5176\u51B7\u5982\u51B0\u3002\u767D\u7389\u83BA\u5A07\u8EAF\u5FAE\u98A4\uFF0C\u8138\u4E0A\u7684\u7EA2\u6655\u4E00\u4E1D\u4E1D\u6D88\u6563\u3002\u7D2B\u73AB\u80E1\u4E71\u62B9\u4E86\u51E0\u628A\uFF0C\u89C1\u4ED6\u8FD8\u4E0D\u8D70\u706B\u5165\u9B54\uFF0C\u624B\u8098\u4E00\u62AC\uFF0C\u5C06\u4E00\u4E2A\u7855\u5927\u7684\u82B1\u74F6\u78B0\u5230\u5730\u4E0A\uFF0C\u53D1\u51FA\u4E00\u58F0\u5DE8\u54CD\u3002</p><p>\u6155\u5BB9\u9F99\u773C\u90FD\u4E0D\u7728\uFF0C\u4E00\u9F13\u4F5C\u6C14\u903C\u51FA\u767D\u7389\u83BA\u7684\u9634\u7CBE\uFF0C\u624D\u63A8\u5F00\u865A\u5F31\u7684\u5C11\u5973\uFF0C\u6DE1\u6DE1\u9053\uFF1A\u300C\u4F60\u53BB\u628A\u7EAA\u5974\u53EB\u6765\u3002\u300D</p><p>\u7D2B\u73AB\u8EAB\u5B50\u4E00\u50F5\uFF0C\u677F\u7740\u8138\u53BB\u4E86\u3002</p><p>\u7D2B\u73AB\u8FD9\u4E00\u53BB\u76F4\u5982\u77F3\u6C89\u5927\u6D77\uFF0C\u518D\u4E5F\u4E0D\u89C1\u56DE\u6765\u3002\u6155\u5BB9\u9F99\u8D8A\u7B49\u8D8A\u607C\uFF0C\u5927\u9A82\u4E00\u58F0\u8DF3\u4E0B\u5E8A\u3002\u4ED6\u5148\u63A8\u5F00\u9694\u58C1\u7684\u623F\u95F4\uFF0C\u5E38\u966A\u5728\u6BCD\u4EB2\u8EAB\u8FB9\u8BF4\u8BDD\u7684\u59B9\u59B9\u5374\u4E0D\u5728\u8FD9\u91CC\uFF0C\u53EA\u6709\u767E\u82B1\u89C2\u97F3\u4E00\u4E2A\u4EBA\u7761\u5F97\u6B63\u719F\u3002\u6155\u5BB9\u9F99\u6DF1\u6DF1\u770B\u4E86\u5979\u4E00\u773C\uFF0C\u7F13\u7F13\u5408\u4E0A\u95E8\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u5BA4\u5185\u56DE\u6C64\u7740\u6E05\u8106\u7684\u6B4C\u58F0\uFF0C\u6155\u5BB9\u7D2B\u73AB\u6B63\u5728\u6E29\u6CC9\u6C60\u4E2D\u6D17\u5F97\u9AD8\u5174\u3002\u89C1\u6155\u5BB9\u9F99\u7A81\u7136\u63A8\u95E8\u800C\u5165\uFF0C\u5979\u8FDE\u5FD9\u6E38\u5230\u6C60\u8FB9\uFF0C\u53EA\u9732\u51FA\u4E00\u4E2A\u8111\u888B\uFF0C\u6C34\u7075\u7075\u7684\u5927\u773C\u4E00\u7728\u4E00\u7728\u5929\u771F\u5730\u770B\u7740\u4ED6\u3002\u6C34\u6C7D\u7F15\u7F15\u98D8\u8FC7\uFF0C\u6CBE\u7740\u6C34\u73E0\u7684\u4FCF\u8138\u5B9B\u5982\u51FA\u6C34\u8299\u84C9\uFF0C\u5A07\u7F8E\u65E0\u5339\u3002</p><p>\u6155\u5BB9\u9F99\u88F8\u7740\u8EAB\u5B50\u62B1\u81C2\u800C\u7ACB\uFF0C\u4E24\u773C\u864E\u89C6\u7708\u7708\uFF0C\u5FFD\u7136\u5927\u559D\u9053\uFF1A\u300C\u7EAA\u5974\uFF01\u300D\u58F0\u97F3\u6EDA\u6EDA\u8FDC\u53BB\uFF0C\u9707\u5F97\u7D2B\u73AB\u4E24\u8033\u53D1\u9EBB\u3002</p><p>\u7247\u523B\u540E\u7EAA\u7709\u59A9\u5782\u9996\u5165\u5185\uFF0C\u8EAB\u4E0A\u7684\u8F7B\u7EB1\u88AB\u6C34\u6C7D\u4E00\u84B8\uFF0C\u7ACB\u523B\u7D27\u7D27\u8D34\u5728\u8EAB\u4E0A\uFF0C\u4EFF\u4F5B\u900F\u660E\u4E00\u822C\uFF0C\u67D4\u7F8E\u7684\u8EAB\u4F53\u7EA4\u6BEB\u6BD5\u9732\u3002</p><p>\u300C\u8DB4\u8FC7\u53BB\uFF0C\u628A\u5C44\u63B0\u5F00\uFF01\u300D\u6155\u5BB9\u9F99\u5389\u559D\u9053\u3002</p><p>\u7EAA\u7709\u59A9\u65E0\u8A00\u5730\u8D70\u5230\u6C34\u6C60\u8FB9\uFF0C\u5F2F\u8170\u4F0F\u5728\u6C60\u6CBF\uFF0C\u4E24\u624B\u7ED5\u5230\u8EAB\u540E\uFF0C\u64A9\u8D77\u6E7F\u6DCB\u6DCB\u7684\u8F7B\u7EB1\uFF0C\u9732\u51FA\u6D51\u5706\u767D\u5AE9\u7684\u7F8E\u81C0\u3002\u7389\u6307\u8F7B\u5206\uFF0C\u7EC6\u6ED1\u7684\u81C0\u8089\u6CB9\u8102\u822C\u6D41\u52A8\u5F00\u6765\uFF0C\u817F\u95F4\u4E24\u7247\u7EA2\u6DA6\u7684\u8F6F\u8089\u67D4\u67D4\u7EFD\u653E\u3002\u6155\u5BB9\u9F99\u4E00\u628A\u6293\u4F4F\u5979\u7684\u67D4\u9888\uFF0C\u5F80\u6C60\u4E2D\u4E00\u6309\uFF0C\u8089\u68D2\u76F4\u633A\u633A\u6345\u5165\u5E72\u71E5\u7684\u79D8\u7A74\u3002</p><p>\u7EAA\u7709\u59A9\u731D\u4E0D\u53CA\u9632\uFF0C\u987F\u65F6\u545B\u4E86\u53E3\u6C34\uFF0C\u8FDE\u5FD9\u5C4F\u4F4F\u547C\u5438\u3002\u4E0B\u4F53\u5267\u75DB\u9635\u9635\u4F20\u6765\uFF0C\u53EF\u5979\u6D78\u5728\u6C34\u4E2D\uFF0C\u60F3\u5598\u53E3\u6C14\u4E5F\u529E\u4E0D\u5230\uFF0C\u53EA\u597D\u4E24\u624B\u6B7B\u6B7B\u6293\u7D27\u81C0\u8089\uFF0C\u62FC\u547D\u5FCD\u8010\u3002</p><p>\u7D2B\u73AB\u7B49\u4E86\u7247\u523B\uFF0C\u89C1\u6155\u5BB9\u9F99\u8FD8\u6CA1\u6709\u653E\u624B\u7684\u610F\u601D\uFF0C\u4E0D\u7531\u60F6\u6025\u8D77\u6765\uFF0C\u62B1\u4F4F\u4ED6\u7684\u624B\u81C2\u4F7F\u52B2\u6447\u64BC\uFF0C\u300C\u677E\u624B\uFF01\u5FEB\u4E9B\u677E\u624B\uFF01\u300D</p><p>\u6155\u5BB9\u9F99\u9762\u6C89\u4F3C\u6C34\uFF0C\u4E0B\u8179\u7528\u529B\u4E00\u9876\uFF0C\u7EAA\u7709\u59A9\u5A07\u8EAF\u524D\u503E\uFF0C\u8FDE\u80A9\u5934\u4E5F\u6CA1\u5165\u6C34\u4E2D\u3002\u5979\u518D\u4E5F\u5FCD\u4E0D\u4F4F\uFF0C\u4E24\u624B\u6491\u4F4F\u6C60\u6CBF\u60F3\u62AC\u8D77\u5934\u6765\u3002\u53EF\u6155\u5BB9\u9F99\u624B\u81C2\u5982\u540C\u5DE8\u77F3\uFF0C\u7EB9\u4E1D\u4E0D\u52A8\u3002</p><p>\u79C0\u53D1\u6D6E\u5728\u6E05\u6F88\u7684\u6C34\u9762\u4E1D\u4E1D\u98D8\u8361\uFF0C\u5FFD\u7136\u4E00\u4E32\u6C14\u6CE1\u4ECE\u53D1\u4E1D\u95F4\u6EDA\u51FA\uFF0C\u7EAA\u7709\u59A9\u7684\u8EAB\u4F53\u75C9\u631B\u8D77\u6765\u3002</p>',63);function a(i,_){return t}var d=e(r,[["render",a]]);export{d as default};
