import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_34" tabindex="-1"><a class="header-anchor" href="#_34" aria-hidden="true">#</a> 34</h1><p>\u773C\u770B\u5E08\u59D0\u5C31\u8981\u88AB\u6D3B\u6D3B\u6DF9\u6B7B\uFF0C\u53EA\u7A7F\u4E00\u6761\u9C9C\u7EA2\u62B9\u80F8\u7684\u7D2B\u73AB\u4ECE\u6C34\u4E2D\u8DF3\u8D77\u6765\uFF0C\u4E00\u53E3\u72E0\u72E0\u54AC\u5728\u6155\u5BB9\u9F99\u81C2\u4E0A\u3002\u53EF\u5979\u5185\u529F\u672A\u805A\uFF0C\u4E00\u53E3\u54AC\u4E0B\uFF0C\u5982\u540C\u54AC\u4F4F\u6728\u77F3\u3002\u7EAA\u7709\u59A9\u4FEE\u957F\u7684\u5927\u817F\u731B\u7136\u5408\u7D27\uFF0C\u5C0F\u817F\u5C48\u8D77\u53C8\u5FFD\u7136\u4F38\u76F4\uFF0C\u663E\u7136\u5230\u4E86\u706F\u67AF\u6CB9\u5C3D\u7684\u5730\u6B65\u3002</p><p>\u8089\u68D2\u5728\u8089\u7A74\u5185\u51F6\u731B\u5730\u649E\u51FB\u7740\uFF0C\u5C06\u4F53\u5185\u6700\u540E\u4E00\u70B9\u7A7A\u6C14\u90FD\u6324\u643E\u5E72\u51C0\u3002\u7EAA\u7709\u59A9\u4E24\u8033\u8F70\u7136\u4F5C\u54CD\uFF0C\u773C\u524D\u53D1\u9ED1\uFF0C\u6E05\u6F88\u7684\u6CC9\u6C34\u4E0B\u4E00\u53CC\u4E4C\u4EAE\u7684\u7F8E\u76EE\u7F13\u7F13\u5408\u4E0A\u3002</p><p>\u6155\u5BB9\u9F99\u4F24\u5728\u96EA\u5CF0\u795E\u5C3C\u624B\u4E0B\uFF0C\u7D2B\u73AB\u4ECA\u5929\u53C8\u7279\u522B\u4E0D\u5B89\u5206\uFF0C\u6B64\u65F6\u6012\u6C14\u52C3\u53D1\uFF0C\u5E72\u8106\u5F53\u7740\u5979\u7684\u9762\u5F04\u6B7B\u7EAA\u7709\u59A9\uFF0C\u4E00\u6765\u51FA\u53E3\u6076\u6C14\uFF0C\u4E8C\u6765\u597D\u9707\u6151\u7D2B\u73AB\u3002</p><p>\u5939\u7740\u9633\u5177\u7684\u5AE9\u8089\u8D8A\u6765\u8D8A\u7D27\uFF0C\u8089\u58C1\u50CF\u6CE2\u6D6A\u822C\u4F20\u6765\u9635\u9635\u6536\u7F29\u7684\u6218\u6817\uFF0C\u5E26\u7740\u8D85\u4E4E\u5BFB\u5E38\u7684\u5FEB\u611F\u3002\u4E0E\u6B64\u540C\u65F6\u7EAA\u7709\u59A9\u7684\u6323\u624E\u8D8A\u6765\u8D8A\u5F31\u3002\u6155\u5BB9\u9F99\u54AC\u7740\u7259\u9F7F\uFF0C\u9732\u51FA\u4E00\u4E2A\u72F0\u72DE\u7684\u7B11\u5BB9\u3002</p><p>\u7D2B\u73AB\u614C\u5F97\u624B\u8DB3\u65E0\u63AA\uFF0C\u5FC3\u91CC\u540E\u6094\u4E0D\u8FED\uFF0C\u6CA1\u60F3\u5230\u81EA\u5DF1\u4E00\u65F6\u4F7F\u6027\u5B50\u7ADF\u4F1A\u65AD\u9001\u4E86\u5E08\u59D0\u7684\u6027\u547D\u2026\u2026</p><p>\u7D2B\u73AB\u59E3\u4E3D\u7684\u9762\u988A\u4E00\u7247\u60E8\u767D\u3002\u5FFD\u7136\u6DF1\u5438\u4E00\u53E3\u6C14\uFF0C\u6F5C\u5230\u6C34\u5E95\uFF0C\u4E0E\u7EAA\u7709\u59A9\u5507\u9F7F\u76F8\u63A5\uFF0C\u628A\u6C14\u606F\u5410\u7ED9\u5782\u6B7B\u7684\u5E08\u59D0\u3002</p><p>\u53EF\u5979\u60F3\u5230\u7684\u592A\u665A\u4E86\uFF0C\u7EAA\u7709\u59A9\u80BA\u4E2D\u5438\u5165\u6C60\u6C34\uFF0C\u6B64\u65F6\u5DF2\u7136\u660F\u8FF7\u3002\u6BB7\u7EA2\u7684\u4E73\u5934\u6D78\u5728\u6C60\u4E2D\uFF0C\u968F\u7740\u6E29\u6CC9\u6EDA\u6D8C\uFF0C\u8840\u8272\u4E00\u70B9\u70B9\u6C64\u5F00\uFF0C\u6E10\u6E10\u53D1\u6697\u3002</p><p>\u6B63\u5728\u6B64\u65F6\uFF0C\u752C\u9053\u5916\u7A81\u7136\u54CD\u8D77\u4E00\u9635\u6025\u4FC3\u7684\u94C3\u58F0\u3002\u6155\u5BB9\u9F99\u542C\u51FA\u6709\u654C\u6765\u88AD\uFF0C\u7ACB\u523B\u677E\u5F00\u624B\uFF0C\u63A0\u5F80\u795E\u6BBF\u3002</p><p>\u7D2B\u73AB\u6025\u5FD9\u628A\u5E08\u59D0\u6276\u5230\u6C60\u6CBF\u8EBA\u5E73\u3002\u7EAA\u7709\u59A9\u8138\u8272\u82CD\u767D\uFF0C\u624B\u811A\u8F6F\u8F6F\u644A\u5728\u8EAB\u4E0B\uFF0C\u4E0D\u7701\u4EBA\u4E8B\u3002\u96EA\u767D\u7684\u5C0F\u8179\u5FAE\u5FAE\u9F13\u80C0\uFF0C\u80F8\u53E3\u5374\u6BEB\u65E0\u8D77\u4F0F\u3002\u7D2B\u73AB\u4E00\u8FB9\u5728\u5979\u7A74\u4F4D\u4E0A\u6309\u6469\uFF0C\u6FC0\u8D77\u5E08\u59D0\u4F53\u5185\u6240\u5269\u65E0\u51E0\u7684\u6B8B\u5B58\u771F\u6C14\uFF0C\u4E00\u8FB9\u6309\u4F4F\u5C0F\u8179\uFF0C\u5411\u4E0A\u7F13\u63A8\u3002\u7EAA\u7709\u59A9\u7EA2\u5507\u7D27\u95ED\uFF0C\u5634\u89D2\u6D41\u51FA\u4E00\u80A1\u6E05\u6C34\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u5DF2\u662F\u5B50\u591C\u65F6\u5206\uFF0C\u795E\u6BBF\u5916\u91D1\u3001\u571F\u4E24\u5802\u8FD1\u767E\u540D\u5E2E\u4F17\u4E3E\u7740\u706B\u628A\u56F4\u6210\u4E00\u4E2A\u5927\u5708\uFF0C\u573A\u4E2D\u96EA\u5CF0\u795E\u5C3C\u767D\u8863\u98D8\u98DE\uFF0C\u4E00\u67C4\u957F\u5251\u5927\u5F00\u5927\u5408\uFF0C\u4E0E\u91D1\u5F00\u7532\u3001\u5C60\u6000\u6C89\u4E24\u540D\u957F\u8001\u548C\u91D1\u5802\u8CF8\u4F59\u7684\u4E09\u540D\u9999\u4E3B\u6597\u5F97\u6B63\u6025\u3002\u795E\u5C3C\u7389\u8138\u5982\u51B0\uFF0C\u5FFD\u7136\u8EAB\u5F62\u4E00\u95EA\uFF0C\u907F\u5F00\u767D\u94F6\u9999\u4E3B\u7684\u70C2\u94F6\u67AA\uFF0C\u6325\u8896\u6253\u5728\u4ED6\u8170\u95F4\u3002</p><p>\u4E2D\u5348\u6551\u51FA\u6797\u9999\u8FDC\u4E4B\u540E\uFF0C\u5979\u624D\u77E5\u9053\u81EA\u5DF1\u7684\u56DB\u540D\u5F1F\u5B50\u5C3D\u6570\u88AB\u63B3\uFF0C\u53D7\u5C3D\u8BF8\u822C\u6B8B\u8650\u3002</p><p>\u96EA\u5CF0\u795E\u5C3C\u9762\u51B7\u5FC3\u70ED\uFF0C\u5BF9\u8FD9\u51E0\u540D\u5F1F\u5B50\u7231\u5982\u5DF1\u51FA\uFF0C\u65E0\u8BBA\u662F\u4ECE\u5C0F\u629A\u517B\u7684\u98CE\u665A\u534E\uFF0C\u8FD8\u662F\u82F1\u59FF\u52C3\u52C3\u7684\u6797\u9999\u8FDC\uFF1B\u65E0\u8BBA\u662F\u6E29\u5A49\u548C\u987A\u7684\u7EAA\u7709\u59A9\uFF0C\u8FD8\u662F\u806A\u6167\u4F36\u4FD0\u7684\u6155\u5BB9\u7D2B\u73AB\uFF0C\u6BCF\u4E00\u4E2A\u90FD\u662F\u5979\u4EB2\u624B\u8C03\u6559\u591A\u5E74\u7684\u7231\u5F92\u3002\u4E4D\u95FB\u5669\u8017\uFF0C\u96EA\u5CF0\u795E\u5C3C\u5FC3\u5982\u5200\u5272\uFF0C\u5B89\u7F6E\u597D\u6797\u9999\u8FDC\uFF0C\u5979\u4FBF\u7ACB\u523B\u8FD4\u56DE\u661F\u6708\u6E56\uFF0C\u76F4\u95EF\u795E\u6BBF\u3002</p><p>\u767D\u94F6\u95F7\u54FC\u7740\u4FA7\u8EAB\u6251\u8DCC\uFF0C\u96EA\u5CF0\u795E\u5C3C\u957F\u5251\u5212\u4E2A\u534A\u5706\uFF0C\u950B\u8292\u6240\u53CA\uFF0C\u9ED1\u94C1\u817F\u4E0A\u6E85\u8840\uFF0C\u8DCC\u5230\u4E00\u65C1\u3002\u91D1\u5F00\u7532\u7331\u8EAB\u4E0A\u524D\uFF0C\u864E\u638C\u4E00\u6325\uFF0C\u4E0E\u96EA\u5CF0\u795E\u5C3C\u786C\u751F\u751F\u62FC\u4E86\u4E00\u62DB\u3002\u96EA\u5CF0\u795E\u5C3C\u9762\u4E0A\u4E00\u7EA2\u5373\u901D\uFF0C\u91D1\u5F00\u7532\u5374\u8FDE\u9000\u4E94\u6B65\uFF0C\u8138\u8272\u60E8\u767D\u3002</p><p>\u96EA\u8702\u795E\u5C3C\u6B64\u756A\u4EE5\u6740\u654C\u4E3A\u4E3B\uFF0C\u6B64\u65F6\u9707\u4F24\u91D1\u5F00\u7532\uFF0C\u81EA\u5DF1\u7684\u7ECF\u8109\u4E5F\u88AB\u4ED6\u7684\u53CD\u51FB\u4E4B\u529B\u903C\u5F97\u6C14\u606F\u4E0D\u7545\u3002</p><p>\u6155\u5BB9\u9F99\u62A2\u8FC7\u65C1\u8FB9\u5E2E\u4F17\u7684\u94C1\u80CE\u52B2\u5F13\uFF0C\u624B\u4E00\u6296\uFF0C\u4E24\u652F\u957F\u7BAD\u76F4\u5954\u96EA\u5CF0\u795E\u5C3C\u80F8\u524D\u5C0F\u8179\u3002</p><p>\u96EA\u5CF0\u795E\u5C3C\u5F39\u6307\u51FB\u5F00\uFF0C\u987F\u65F6\u8EAB\u4F53\u4E00\u9707\uFF0C\u5979\u6CA1\u60F3\u5230\u6155\u5BB9\u9F99\u8FD9\u4E48\u5FEB\u5C31\u4F24\u52BF\u5927\u6108\uFF0C\u4E0D\u7531\u5403\u4E86\u6697\u4E8F\u3002</p><p>\u5578\u58F0\u8FDC\u8FD1\u54CD\u8D77\uFF0C\u8863\u895F\u7834\u7A7A\u4E4B\u58F0\u5927\u632F\uFF0C\u970D\u72C2\u7130\u7B49\u4EBA\u7EB7\u7EB7\u63A0\u81F3\u3002\u96EA\u5CF0\u795E\u5C3C\u6325\u5251\u6321\u4F4F\u9752\u94DC\u7684\u72FC\u7259\u68D2\uFF0C\u501F\u52BF\u540E\u8DC3\uFF0C\u53C8\u4E0E\u91D1\u5F00\u7532\u5BF9\u4E86\u4E00\u638C\u3002</p><p>\u91D1\u5F00\u7532\u55B7\u51FA\u4E00\u53E3\u9C9C\u8840\uFF0C\u773C\u4E2D\u7CBE\u5149\u5927\u76DB\uFF0C\u638C\u529B\u6392\u5C71\u5012\u6D77\u72C2\u6D8C\u800C\u81F3\u3002\u53CC\u638C\u4E00\u89E6\u5373\u5206\uFF0C\u96EA\u5CF0\u795E\u5C3C\u767D\u8863\u4E00\u95EA\uFF0C\u523A\u5012\u4E24\u540D\u5E2E\u4F17\uFF0C\u6D88\u5931\u5728\u591C\u8272\u4E2D\u3002</p><p>\u6155\u5BB9\u9F99\u795E\u8272\u51DD\u91CD\uFF0C\u6B64\u4EBA\u4E0D\u9664\uFF0C\u7EC8\u7A76\u662F\u5FC3\u8179\u5927\u60A3\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u6628\u65E5\u7684\u66B4\u96E8\u672A\u7559\u4E0B\u4E1D\u6BEB\u75D5\u8FF9\u3002\u707F\u70C2\u7684\u9633\u5149\u4E0B\uFF0C\u661F\u6708\u795E\u6BBF\u5DCD\u7136\u77D7\u7ACB\uFF0C\u91D1\u78A7\u8F89\u714C\u3002</p><p>\u795E\u6BBF\u5185\u6EE1\u662F\u5E2E\u4F17\uFF0C\u5374\u5B89\u9759\u5F97\u6CA1\u6709\u4E00\u70B9\u58F0\u97F3\u3002\u5802\u4E2D\u6446\u7740\u516D\u5F20\u6905\u5B50\uFF0C\u5206\u522B\u662F\u53F6\u884C\u5357\u548C\u4E94\u4F4D\u957F\u8001\u3002\u91D1\u5F00\u7532\u8EAB\u540E\u7AD9\u7740\u767D\u94F6\u3001\u9752\u94DC\u3001\u9ED1\u94C1\u4E09\u540D\u9999\u4E3B\uFF1B\u6C90\u58F0\u4F20\u8EAB\u540E\u4E24\u540D\u7537\u5B50\uFF0C\u5206\u522B\u662F\u67AF\u679D\u3001\u65B0\u53F6\u4E24\u540D\u9999\u4E3B\uFF1B\u6C34\u67D4\u4ED9\u8EAB\u540E\u53EA\u6709\u7384\u51B0\u4E00\u4EBA\uFF0C\u800C\u970D\u72C2\u7130\u548C\u5C60\u6000\u6C89\u5802\u4E2D\u9999\u4E3B\u5DF2\u5C3D\u6570\u6B7B\u5728\u98D8\u6885\u5CF0\u5F1F\u5B50\u624B\u4E0B\u3002</p><p>\u6155\u5BB9\u9F99\u76EE\u5149\u626B\u8FC7\u5168\u573A\uFF0C\u626C\u58F0\u9053\uFF1A\u300C\u6E05\u9732\u9999\u4E3B\u4F55\u5728\uFF1F\u300D</p><p>\u6C34\u67D4\u4ED9\u8D77\u8EAB\u9053\uFF1A\u300C\u6E05\u9732\u65E5\u524D\u8D74\u5CAD\u5357\u5904\u7406\u5E2E\u52A1\uFF0C\u65F6\u95F4\u4ED3\u4FC3\uFF0C\u672A\u80FD\u8D76\u56DE\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u70B9\u4E86\u70B9\u5934\uFF0C\u5148\u53F9\u606F\u4E00\u58F0\uFF0C\u7F13\u7F13\u9053\uFF1A\u300C\u4ECA\u65E5\u6559\u4E2D\u9AD8\u624B\u9F50\u805A\uFF0C\u5374\u53EA\u6709\u5BE5\u5BE5\u5341\u4F59\u4EBA\u2026\u2026\u300D</p><p>\u91D1\u5F00\u7532\u611F\u6168\u4E07\u5343\uFF0C\u4ED6\u5E74\u8F7B\u65F6\u66FE\u89C1\u8FC7\u661F\u6708\u6E56\u7684\u6781\u76DB\u573A\u9762\u3002\u4E24\u4EEA\u4F7F\u8005\u3001\u4E09\u624D\u62A4\u6CD5\u3001\u56DB\u9547\u795E\u5C06\u3001\u4E94\u884C\u957F\u8001\u3001\u516D\u5408\u4F9B\u5949\uFF0C\u5355\u662F\u4E00\u7B49\u4E00\u7684\u9AD8\u624B\u5C31\u6709\u4E8C\u5341\u4EBA\u3002\u8C01\u80FD\u60F3\u5230\u77ED\u77ED\u5341\u4F59\u5E74\u7ADF\u4F1A\u843D\u5230\u5982\u6B64\u5730\u6B65\u2026\u2026</p><p>\u6155\u5BB9\u9F99\u957F\u8EAB\u800C\u8D77\uFF0C\u58F0\u97F3\u6FC0\u6602\u5730\u8BF4\uFF1A\u300C\u6211\u661F\u6708\u6E56\u7EB5\u6A2A\u5929\u4E0B\u8FD1\u5343\u5E74\uFF0C\u6559\u4E2D\u9AD8\u624B\u5982\u4E91\uFF0C\u8C6A\u6770\u65E0\u6570\uFF01\u672C\u8BE5\u5927\u6709\u4F5C\u4E3A\u2014\u2014\u300D\u4ED6\u58F0\u97F3\u4E00\u6C89\uFF0C\u300C\u53EF\u60DC\u4E0A\u4EFB\u5BAB\u4E3B\u56FA\u6B65\u81EA\u5C01\uFF0C\u767D\u767D\u6D6A\u8D39\u4E86\u591A\u5C11\u673A\u4F1A\u2026\u2026\u300D</p><p>\u6C34\u67D4\u4ED9\u79C0\u7738\u4E00\u95EA\uFF0C\u770B\u5411\u6C90\u58F0\u4F20\u3002\u6C90\u58F0\u4F20\u795E\u60C5\u6728\u7136\uFF0C\u534A\u772F\u7740\u773C\uFF0C\u4EFF\u4F5B\u6CA1\u542C\u89C1\u5BAB\u4E3B\u7684\u8BDD\u3002\u5979\u63E1\u4F4F\u8896\u5185\u7684\u8F6F\u97AD\uFF0C\u624B\u5FC3\u4E0D\u7531\u5FAE\u5FAE\u51FA\u6C57\u3002</p><p>\u6E05\u9732\u79BB\u5BAB\u5DF2\u4E03\u5929\uFF0C\u6700\u5C11\u5E94\u8BE5\u901A\u77E5\u4E86\u8FD1\u5904\u7684\u4E03\u4E2A\u5E2E\u4F1A\u3002\u53EF\u5982\u4ECA\u552F\u4E00\u8D76\u5230\u7684\u4E39\u9633\u6D3E\u53EA\u662F\u638C\u95E8\u5165\u5BAB\u8F6E\u503C\uFF0C\u5E76\u975E\u63A5\u5230\u901A\u77E5\uFF0C\u5176\u4ED6\u5E2E\u4F1A\u66F4\u662F\u97F3\u8BAF\u7686\u65E0\u3002\u518D\u7B49\u4E0A\u51E0\u65E5\uFF0C\u53C2\u52A0\u5BAB\u4E3B\u5A5A\u793C\u7684\u6C5F\u6E56\u4EBA\u58EB\u9646\u7EED\u8D76\u5230\uFF0C\u5F92\u589E\u53D8\u6570\u3002</p><p>\u6628\u591C\u91D1\u5F00\u7532\u53D7\u4F24\uFF0C\u6B63\u662F\u5929\u8D50\u826F\u673A\uFF0C\u5979\u79D8\u5BC6\u5429\u5490\u4E86\u81EA\u5DF1\u7684\u5FC3\u8179\uFF0C\u5E76\u77E5\u4F1A\u6C90\u58F0\u4F20\uFF0C\u51B3\u5B9A\u5728\u5927\u4F1A\u4E0A\u5F53\u573A\u7FFB\u8138\u3002\u91D1\u5F00\u7532\u5DF2\u7ECF\u58F0\u660E\u4E24\u4E0D\u76F8\u5E2E\uFF0C\u51B5\u4E14\u6628\u591C\u53C8\u5728\u96EA\u5CF0\u795E\u5C3C\u624B\u4E0B\u5403\u4E86\u5927\u4E8F\uFF1B\u970D\u72C2\u7130\u3001\u5C60\u6000\u6C89\u3001\u53F6\u884C\u5357\u4E0D\u8DB3\u4E3A\u8651\uFF0C\u7B97\u6765\u81EA\u5DF1\u5360\u4E86\u4E03\u6210\u8D62\u9762\u3002</p><p>\u601D\u7D22\u95F4\uFF0C\u6155\u5BB9\u9F99\u7684\u58F0\u97F3\u9690\u9690\u4F20\u6765\uFF0C\u300C\u2026\u2026\u6811\u6211\u661F\u6708\u6E56\u5A01\u540D\u3002\u56E0\u6B64\u672C\u5BAB\u51B3\u5B9A\uFF0C\u4EE5\u7EC8\u5357\u4E3A\u57FA\uFF0C\u8054\u7EDC\u516B\u65B9\u8C6A\u6770\uFF0C\u5C3D\u8865\u6559\u4E2D\u7A7A\u7F3A\u3002\u4E00\u65E6\u5175\u9A6C\u9F50\u5907\uFF0C\u4EE5\u6211\u661F\u6708\u6E56\u7CBE\u5F3A\uFF0C\u6A2A\u626B\u5929\u4E0B\u8C01\u4EBA\u80FD\u654C\uFF01\u300D</p><p>\u300C\u6B64\u8A00\u5DEE\u77E3\uFF01\u300D\u6C34\u67D4\u4ED9\u6717\u58F0\u9053\uFF1A\u300C\u6211\u661F\u6708\u6E56\u4EE5\u4FEE\u771F\u70BC\u6C14\u4E3A\u6839\u672C\uFF0C\u9010\u9E7F\u5929\u4E0B\u4E0E\u6211\u6559\u5B97\u65E8\u5927\u76F8\u8FF3\u5EAD\uFF01\u5BAB\u4E3B\u6B64\u4E3E\u5927\u662F\u4E0D\u59A5\uFF01\u300D</p><p>\u6155\u5BB9\u9F99\u51B7\u51B7\u76EF\u7740\u6C34\u67D4\u4ED9\uFF0C\u624B\u6307\u7F13\u7F13\u634F\u7D27\u3002\u6CA1\u60F3\u5230\u5979\u7ADF\u4F1A\u516C\u7136\u53D1\u96BE\uFF0C\u800C\u4E14\u76F4\u6307\u81EA\u5DF1\u79BB\u7ECF\u53DB\u9053\u3002\u7565\u4E00\u6014\u795E\uFF0C\u6155\u5BB9\u9F99\u5BD2\u58F0\u9053\uFF1A\u300C\u672C\u5BAB\u4E00\u7247\u8BDA\u5FC3\uFF0C\u4E0E\u4F17\u5144\u5F1F\u540C\u8C0B\u5BCC\u8D35\u3002\u4E0D\u77E5\u6C34\u957F\u8001\u6B64\u8A00\u4F55\u610F\uFF1F\u300D</p><p>\u6C34\u67D4\u4ED9\u633A\u8EAB\u800C\u8D77\uFF0C\u53F3\u81C2\u653E\u5728\u8170\u540E\uFF0C\u5DE6\u624B\u5E73\u5E73\u4F38\u51FA\uFF0C\u659C\u6309\u5730\u9762\uFF0C\u626C\u58F0\u9053\uFF1A\u300C\u9634\u5BAB\u4E3B\u4F20\u4F4D\u4E4B\u4E3E\u4E0D\u660E\u4E0D\u767D\uFF0C\u4E0E\u98D8\u6885\u5CF0\u8FDE\u756A\u6076\u6597\uFF0C\u672C\u6559\u9AD8\u624B\u6298\u635F\u8FC7\u534A\uFF0C\u4E0D\u77E5\u5BAB\u4E3B\u6781\u529B\u7FE6\u9664\u6211\u4E94\u884C\u95E8\u662F\u4F55\u7528\u610F\uFF01\u300D</p><p>\u6B64\u8A00\u4E00\u51FA\uFF0C\u5802\u4E2D\u5E2E\u4F17\u987F\u65F6\u5927\u4E71\u3002\u4E0E\u6C34\u67D4\u4ED9\u6BD4\u90BB\u800C\u5750\u7684\u970D\u72C2\u7130\u773C\u4E2D\u51F6\u5149\u95EA\u52A8\uFF0C\u4ED6\u80FD\u767B\u4E0A\u706B\u5802\u957F\u8001\u4E4B\u4F4D\u5168\u662F\u6155\u5BB9\u9F99\u4E00\u624B\u63D0\u62D4\uFF0C\u5BF9\u65B0\u5BAB\u4E3B\u6B7B\u5FC3\u584C\u5730\uFF0C\u6155\u5BB9\u9F99\u82E5\u662F\u5012\u53F0\uFF0C\u4ED6\u4E0D\u4EC5\u96BE\u4FDD\u957F\u8001\u4E4B\u4F4D\uFF0C\u80FD\u5426\u7559\u4F4F\u6027\u547D\u4E5F\u5728\u4E24\u53EF\u4E4B\u95F4\u3002</p><p>\u970D\u72C2\u7130\u63E1\u4F4F\u706B\u7130\u4EE4\u6B63\u5F85\u51FA\u624B\uFF0C\u5FFD\u7136\u80A9\u4E0A\u4E00\u7D27\u3002\u6C34\u67D4\u4ED9\u85CF\u5728\u8EAB\u540E\u7684\u53F3\u8896\u98DE\u51FA\u4E00\u6839\u8F6F\u97AD\uFF0C\u4ECE\u4ED6\u80A9\u5934\u76F4\u7ED5\u5230\u8170\u95F4\u3002\u957F\u97AD\u4E00\u6536\u5373\u653E\uFF0C\u5239\u90A3\u95F4\u4FBF\u5C01\u4E86\u970D\u72C2\u7130\u7684\u7A74\u9053\u3002</p><p>\u6C34\u67D4\u4ED9\u4E00\u62DB\u5F97\u624B\uFF0C\u7ACB\u5373\u98D8\u8EAB\u800C\u8D77\uFF0C\u671D\u6BBF\u4E0A\u5B9D\u5EA7\u653B\u53BB\uFF0C\u8F6F\u97AD\u76D8\u65CB\u98DE\u821E\uFF0C\u53D8\u5E7B\u65E0\u65B9\u3002</p><p>\u6155\u5BB9\u9F99\u4E24\u638C\u5288\u524A\u63A8\u6321\uFF0C\u63A5\u8FDE\u7528\u4E86\u5341\u4F59\u79CD\u4E0D\u540C\u95E8\u6D3E\u7684\u638C\u6CD5\uFF0C\u6700\u540E\u4E00\u62CD\u4E00\u7FFB\uFF0C\u500F\u5FFD\u5C06\u97AD\u68A2\u7F20\u5728\u6307\u95F4\uFF0C\u8FD9\u4E00\u62DB\u6B63\u662F\u661F\u6708\u6E56\u7EDD\u6280\u6458\u661F\u6307\u3002</p><p>\u97AD\u6307\u76F8\u4EA4\uFF0C\u6155\u5BB9\u9F99\u987F\u65F6\u4E00\u9707\u3002\u6C34\u67D4\u4ED9\u7684\u52B2\u6C14\u4ECE\u6307\u95F4\u76F4\u5165\u80A9\u5934\uFF0C\u81EA\u5DF1\u6574\u6761\u80A9\u8180\u4F3C\u4E4E\u6D78\u5728\u6E29\u878D\u9500\u9AA8\u7684\u70ED\u6C34\u4E2D\uFF0C\u61D2\u6D0B\u6D0B\u6CA1\u6709\u4E1D\u6BEB\u529B\u6C14\u3002\u4ED6\u5927\u9A87\u677E\u624B\uFF0C\u4F46\u8F6F\u97AD\u5374\u5982\u9644\u9AA8\u4E4B\u86C6\uFF0C\u8FDE\u7529\u6570\u4E0B\u90FD\u672A\u80FD\u6323\u8131\uFF0C\u53CD\u800C\u4F7F\u80F8\u524D\u7A7A\u95E8\u5927\u9732\u3002</p><p>\u91D1\u5F00\u7532\u867D\u8BF4\u4E24\u4E0D\u76F8\u5E2E\uFF0C\u4F46\u7EC8\u7A76\u4E0D\u613F\u89C1\u6155\u5BB9\u9F99\u6A2A\u6B7B\uFF0C\u8FDE\u5FD9\u4E00\u638C\u62CD\u51FA\uFF0C\u53EB\u9053\uFF1A\u300C\u4E14\u542C\u6211\u4E00\u8A00\u3002\u300D</p><p>\u6C34\u67D4\u4ED9\u5F2F\u7709\u4E00\u6311\uFF0C\u53CD\u8DB3\u8E22\u5728\u91D1\u5F00\u7532\u638C\u5FC3\u3002\u5979\u607C\u91D1\u5F00\u7532\u4E0D\u5B88\u8BFA\u8A00\uFF0C\u8FD9\u4E00\u811A\u7528\u4E0A\u4E86\u5341\u6210\u529B\u9053\u3002\u91D1\u5F00\u7532\u91CD\u4F24\u5728\u8EAB\uFF0C\u65E0\u529B\u76F8\u6297\uFF0C\u987F\u65F6\u96C4\u8EAF\u5267\u9707\uFF0C\u8FDE\u9000\u6570\u6B65\uFF0C\u8BF4\u4E0D\u51FA\u8BDD\u6765\u3002</p><p>\u5C60\u6000\u6C89\u4E0E\u970D\u72C2\u7130\u4E00\u822C\u5FC3\u601D\uFF0C\u89C1\u72B6\u62D4\u8EAB\u800C\u8D77\uFF0C\u80A5\u8EAF\u7F29\u6210\u4E00\u56E2\u6295\u5411\u4E24\u4EBA\u3002\u7384\u51B0\u672C\u6765\u4E00\u76F4\u72B9\u8C6B\uFF0C\u6B64\u65F6\u672C\u5802\u957F\u8001\u7A33\u64CD\u80DC\u5238\uFF0C\u7ACB\u523B\u62AC\u6307\u671D\u5C60\u6000\u6C89\u8170\u95F4\u70B9\u53BB\u3002</p><p>\u5FFD\u7136\u4E00\u4E2A\u6DE1\u7EFF\u8272\u7684\u8EAB\u5F71\u95EA\u7535\u822C\u63A0\u8FC7\uFF0C\u7384\u51B0\u80F8\u53E3\u5982\u53D7\u96F7\u6B9B\uFF0C\u55B7\u8840\u629B\u8DCC\u3002</p><p>\u5C31\u5728\u8F6F\u97AD\u53CA\u4F53\u7684\u4E00\u5239\u90A3\uFF0C\u4E24\u6839\u67AF\u7626\u7684\u624B\u6307\u5E73\u5E73\u4F38\u6765\uFF0C\u5939\u4F4F\u97AD\u8EAB\u3002\u7075\u86C7\u822C\u7684\u8F6F\u97AD\u4EFF\u4F5B\u88AB\u4E00\u5200\u65A9\u65AD\uFF0C\u7B14\u76F4\u7684\u97AD\u68A2\u5E94\u6307\u800C\u6298\uFF0C\u5728\u6155\u5BB9\u9F99\u8863\u895F\u4E0A\u4E00\u64E6\uFF0C\u8F6F\u8F6F\u5782\u5728\u6C90\u58F0\u4F20\u638C\u4E2D\u3002</p><p>\u6C34\u67D4\u4ED9\u77AA\u5927\u4FCF\u76EE\uFF0C\u96BE\u4EE5\u7F6E\u4FE1\u5730\u770B\u7740\u6C90\u58F0\u4F20\u624B\u638C\u4F3C\u5FEB\u4F3C\u6162\u5730\u6309\u5230\u81EA\u5DF1\u80F8\u53E3\u3002\u4E00\u8FDE\u4E32\u9690\u5FAE\u7684\u7206\u54CD\u4ECE\u661F\u6708\u6E56\u552F\u4E00\u7684\u5973\u957F\u8001\u80F8\u8179\u4F20\u51FA\uFF0C\u4F53\u5185\u6EE1\u6EA2\u7684\u771F\u6C14\u88AB\u4FB5\u5165\u7684\u6C14\u65CB\u7EDE\u788E\u51FB\u6563\u3002</p><p>\u6155\u5BB9\u9F99\u5634\u89D2\u8840\u8FF9\u9690\u73B0\uFF0C\u4FCA\u76EE\u5374\u6DF1\u82E5\u5BD2\u6F6D\u3002\u5E72\u7626\u7684\u7EFF\u888D\u8001\u8005\u9762\u65E0\u8868\u60C5\uFF0C\u624B\u638C\u4E00\u524D\u4E00\u540E\u5939\u4F4F\u9ED1\u8863\u7F8E\u5987\u7684\u80F8\u80CC\u3002\u6C34\u67D4\u4ED9\u773C\u4E2D\u7CBE\u5149\u6E10\u6563\uFF0C\u7A7A\u6D1E\u6D1E\u671B\u7740\u5730\u9762\uFF0C\u7EC6\u767D\u7684\u624B\u6307\u4E00\u70B9\u70B9\u677E\u5F00\u3002\u8F6F\u97AD\u5782\u5728\u8155\u4E0B\uFF0C\u8F7B\u98D8\u98D8\u6447\u6765\u8361\u53BB\u3002</p>',48);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
