import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_35" tabindex="-1"><a class="header-anchor" href="#_35" aria-hidden="true">#</a> 35</h1><p>\u6C90\u58F0\u4F20\u4E24\u638C\u4E00\u677E\uFF0C\u6C34\u67D4\u4ED9\u987F\u65F6\u8F6F\u7EF5\u7EF5\u5012\u5728\u5730\u4E0A\u3002\u5979\u5FAE\u5FAE\u5598\u7740\u6C14\uFF0C\u8270\u96BE\u5730\u62AC\u8D77\u5934\uFF0C\u54AC\u7259\u76EF\u7740\u6C90\u58F0\u4F20\u3002</p><p>\u6C90\u58F0\u4F20\u6DE1\u6DE1\u9053\uFF1A\u300C\u672C\u6559\u5411\u6765\u7537\u8005\u81F3\u5C0A\uFF0C\u5973\u4EBA\u53EA\u80FD\u4E3A\u5974\u4E3A\u5A62\u3002\u524D\u4EFB\u5BAB\u4E3B\u7BE1\u4F4D\u800C\u7ACB\uFF0C\u98A0\u5012\u7EB2\u5E38\uFF0C\u6B7B\u4E0D\u8DB3\u60DC\u3002\u300D</p><p>\u6C34\u67D4\u4ED9\u5589\u5934\u6EDA\u52A8\uFF0C\u300C\u54C7\u300D\u7684\u5410\u51FA\u4E00\u53E3\u9C9C\u8840\u3002</p><p>\u53F6\u884C\u5357\u53D6\u51FA\u836F\u74F6\uFF0C\u5012\u51FA\u4E24\u4E38\u4F24\u836F\uFF0C\u7ED9\u6155\u5BB9\u9F99\u3001\u91D1\u5F00\u7532\u670D\u4E0B\u3002\u53C8\u62D4\u51FA\u94F6\u9488\uFF0C\u5728\u5C60\u6000\u6C89\u80F8\u8179\u5904\u8FDE\u523A\u6570\u9488\uFF0C\u5236\u4F4F\u51DD\u795E\u6307\u7684\u5BD2\u610F\u3002\u4ED6\u7684\u901A\u795E\u6563\u6628\u5929\u88AB\u6155\u5BB9\u7D2B\u73AB\u6253\u788E\uFF0C\u8FD9\u4F1A\u513F\u6240\u7528\u836F\u7269\u6548\u679C\u5DEE\u5F97\u4E86\u8BB8\u591A\u3002</p><p>\u4E00\u67F1\u9999\u5DE5\u592B\u540E\u6155\u5BB9\u9F99\u7741\u5F00\u773C\uFF0C\u5148\u8D77\u8EAB\u5BF9\u6C90\u58F0\u4F20\u4E00\u63D6\u3002\u4ED6\u77E5\u9053\u6C90\u58F0\u4F20\u5411\u6765\u6C89\u9ED8\u5BE1\u8A00\uFF0C\u56E0\u6B64\u867D\u7136\u5FC3\u4E2D\u611F\u6FC0\uFF0C\u5374\u6CA1\u6709\u8BF4\u8BDD\u3002</p><p>\u6C90\u58F0\u4F20\u6B66\u529F\u8F83\u4E4B\u6731\u90AA\u9752\u6811\u6BEB\u4E0D\u900A\u8272\uFF0C\u53C8\u7D2F\u7ACB\u5927\u529F\uFF0C\u4F46\u7531\u4E8E\u4ED6\u4E00\u5411\u89C6\u5973\u4EBA\u5982\u65E0\u7269\uFF0C\u56E0\u6B64\u5728\u9634\u59EC\u624B\u4E0B\u90C1\u90C1\u591A\u5E74\u3002\u534A\u5E74\u524D\u6155\u5BB9\u9F99\u767B\u4E0A\u5BAB\u4E3B\u4E4B\u4F4D\uFF0C\u4ED6\u4E00\u773C\u4FBF\u770B\u51FA\u5FC5\u662F\u6731\u90AA\u9752\u6811\u4E0E\u53F6\u884C\u5357\u7B49\u4EBA\u5408\u8C0B\u5236\u4F4F\u9634\u5BAB\u4E3B\u3002</p><p>\u6C90\u58F0\u4F20\u66FE\u53C2\u4E0E\u5341\u516D\u5E74\u524D\u7A81\u88AD\u71D5\u5BAB\u4E4B\u5F79\uFF0C\u6DF1\u77E5\u6155\u5BB9\u9F99\u6765\u5386\uFF0C\u56E0\u6B64\u5BF9\u6731\u90AA\u9752\u6811\u8FD9\u4E2A\u9C9C\u5351\u4EBA\u4F1A\u5012\u5411\u672C\u65CF\u7687\u5BA4\u6BEB\u4E0D\u5947\u602A\u3002\u6155\u5BB9\u9F99\u5F53\u4E0A\u5BAB\u4E3B\u4E4B\u540E\u6740\u4F10\u51B3\u65AD\u4E00\u610F\u6E05\u9664\u4E94\u884C\u95E8\uFF0C\u4ED6\u4E5F\u5FC3\u6000\u9690\u5FE7\uFF0C\u8FD9\u6B21\u6C34\u67D4\u4ED9\u8C0B\u53CD\uFF0C\u6B63\u7ED9\u4ED6\u4E00\u4E2A\u8868\u660E\u5FE0\u5FC3\u7684\u826F\u673A\u3002</p><p>\u6155\u5BB9\u9F99\u6309\u7740\u91D1\u5F00\u7532\u8109\u95E8\u63A2\u4E86\u63A2\u4ED6\u7684\u4F24\u52BF\uFF0C\u7136\u540E\u5982\u91CA\u91CD\u8D1F\u5730\u677E\u4E86\u53E3\u6C14\uFF0C\u62B1\u62F3\u9053\uFF1A\u300C\u91D1\u957F\u8001\u4E0D\u987E\u81EA\u8EAB\u4F24\u52BF\uFF0C\u63F4\u624B\u76F8\u52A9\uFF0C\u672C\u5BAB\u94ED\u8BB0\u5728\u5FC3\uFF01\u300D</p><p>\u91D1\u5F00\u7532\u5FC3\u4E0B\u611F\u6FC0\uFF0C\u8FDE\u5FD9\u8EAC\u8EAB\u9053\uFF1A\u300C\u5BAB\u4E3B\u65E0\u6059\u4FBF\u662F\u6211\u6559\u5927\u5E78\u3002\u300D</p><p>\u6253\u6597\u4E4B\u58F0\u5FFD\u6B62\uFF0C\u7384\u51B0\u7B49\u516D\u540D\u6C34\u5802\u5E2E\u4F17\u5DF2\u5C3D\u6570\u6210\u64D2\u3002\u6155\u5BB9\u9F99\u8D70\u5230\u7384\u51B0\u8EAB\u65C1\uFF0C\u4E00\u811A\u5C06\u4ED6\u819D\u76D6\u8E29\u5F97\u7C89\u788E\u3002\u7384\u51B0\u60E8\u53EB\u9053\uFF1A\u300C\u5BAB\u4E3B\u5F00\u2026\u2026\u5F00\u6069\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u7B11\u800C\u4E0D\u7B54\uFF0C\u5C06\u4ED6\u56DB\u80A2\u9AA8\u9ABC\u5C3D\u6570\u8E29\u788E\uFF0C\u7136\u540E\u677E\u5F00\u811A\uFF0C\u8BF4\u9053\uFF1A\u300C\u6254\u5230\u6BBF\u5916\uFF0C\u6BCF\u5929\u7ED9\u4ED6\u4E00\u7897\u6C34\u3002\u6B7B\u540E\u5582\u72D7\u3002\u300D</p><p>\u7D2B\u8863\u4F8D\u8005\u5E94\u58F0\u800C\u51FA\uFF0C\u62D6\u8D77\u624B\u811A\u88AB\u5E9F\u7684\u7384\u51B0\u3002\u5176\u4ED6\u4E94\u540D\u5E2E\u4F17\u89C1\u5BAB\u4E3B\u624B\u6BB5\u5982\u6B64\u6B8B\u5FCD\uFF0C\u90FD\u662F\u9762\u65E0\u4EBA\u8272\uFF0C\u6155\u5BB9\u9F99\u770B\u7740\u5176\u4E2D\u7684\u4E24\u540D\u5973\u5B50\uFF0C\u6DE1\u7B11\u9053\uFF1A\u300C\u6C90\u62A4\u6CD5\u8BF4\u5F97\u597D\uFF0C\u661F\u6708\u6E56\u7537\u8005\u81F3\u5C0A\u3002\u4F20\u672C\u5BAB\u65E8\u8C15\uFF0C\u6559\u4E2D\u6240\u6709\u5973\u5B50\u5373\u523B\u5E9F\u53BB\u6B66\u529F\uFF0C\u4F9B\u5E2E\u4F17\u4EAB\u7528\u3002\u300D\u8BF4\u7740\u58F0\u97F3\u8F6C\u5BD2\uFF0C\u300C\u5982\u5C5E\u53DB\u9006\u540C\u515A\uFF0C\u4E00\u5F8B\u5978\u6B7B\uFF01\u300D</p><p>\u661F\u6708\u6E56\u5973\u5B50\u4E0D\u8FC7\u4E8C\u5341\u4F59\u4EBA\uFF0C\u800C\u4E14\u90FD\u5728\u6C34\u3001\u571F\u4E24\u5802\uFF0C\u5F53\u4E0B\u51E0\u540D\u9999\u4E3B\u7ACB\u523B\u9886\u547D\u51FA\u6BBF\u3002</p><p>\u6155\u5BB9\u9F99\u7F13\u7F13\u89E3\u5F00\u6C34\u67D4\u4ED9\u8170\u4E0A\u4E1D\u5E26\uFF0C\u7B11\u9053\uFF1A\u300C\u6C34\u957F\u8001\u82B1\u5BB9\u6708\u8C8C\uFF0C\u6559\u4E2D\u5E2E\u4F17\u8273\u7FA1\u5DF2\u4E45\u3002\u96BE\u5F97\u6709\u6B64\u826F\u673A\uFF0C\u8BA9\u5927\u5BB6\u5206\u4EAB\uFF0C\u4F60\u53EF\u8981\u597D\u751F\u4F3A\u5019\u2026\u2026\u300D</p><p>\u6C34\u67D4\u4ED9\u795E\u60C5\u51C4\u5A49\uFF0C\u80F8\u53E3\u8D77\u4F0F\u95F4\uFF0C\u53E3\u4E2D\u9C9C\u8840\u4E0D\u4F4F\u6D8C\u51FA\u3002</p><p>\u9ED1\u886B\u4E2D\u5206\uFF0C\u9732\u51FA\u8D34\u8EAB\u7684\u6C34\u84DD\u52B2\u88C5\u3002\u6155\u5BB9\u9F99\u6B63\u8981\u5F53\u573A\u5978\u6DEB\u53DB\u6559\u957F\u8001\uFF0C\u7A81\u7136\u8EAB\u540E\u4F20\u6765\u4E00\u4E2A\u58F0\u97F3\uFF0C\u300C\u2026\u2026\u6C42\u5BAB\u4E3B\u7559\u60C5\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u8F6C\u8FC7\u5934\uFF0C\u53EA\u89C1\u91D1\u5F00\u7532\u4E00\u819D\u652F\u5730\uFF0C\u4FEF\u8EAB\u6C42\u9053\uFF1A\u300C\u6C34\u67D4\u4ED9\u53DB\u6559\u4F5C\u4E71\u7F6A\u4E0D\u5BB9\u8D66\uFF0C\u4F46\u8BF7\u5BAB\u4E3B\u770B\u5728\u5979\u66FE\u4E3A\u672C\u6559\u6548\u529B\u5341\u4F59\u5E74\u7684\u8F9B\u82E6\u4E0A\uFF0C\u514D\u53BB\u8FB1\u5211\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u6C89\u541F\u9053\uFF1A\u300C\u91D1\u5802\u53EF\u662F\u4E0D\u613F\u542C\u4EE4\uFF1F\u300D</p><p>\u91D1\u5F00\u7532\u62AC\u5934\u6073\u58F0\u9053\uFF1A\u300C\u5C5E\u4E0B\u5BF9\u5BAB\u4E3B\u5FE0\u5FC3\u803F\u803F\uFF0C\u91D1\u5802\u4E8C\u767E\u56DB\u5341\u540D\u5B50\u5F1F\u5C3D\u542C\u5BAB\u4E3B\u5429\u5490\uFF0C\u600E\u6562\u6000\u6709\u4E8C\u5FC3\u3002\u4F46\u5C5E\u4E0B\u4E0E\u6C34\u957F\u8001\u76F8\u8BC6\u591A\u5E74\uFF0C\u5979\u72AF\u6B64\u5927\u7F6A\uFF0C\u5C5E\u4E0B\u4E0D\u6562\u5984\u6C42\u4FDD\u5979\u6027\u547D\uFF0C\u53EA\u662F\u5979\u8EAB\u4E3A\u672C\u6559\u957F\u8001\uFF0C\u5730\u4F4D\u5C0A\u5D07\u2026\u2026\u5B9E\u6709\u8FB1\u6211\u6559\u5C0A\u8363\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u6C89\u9ED8\u7247\u523B\u9053\uFF1A\u300C\u91D1\u957F\u8001\u7ACB\u4E0B\u5927\u529F\uFF0C\u672C\u8BE5\u5347\u4E3A\u62A4\u6CD5\u2014\u2014\u5982\u6B64\u4F60\u8FD8\u662F\u91D1\u5802\u957F\u8001\uFF0C\u4EE5\u529F\u52B3\u51B2\u62B5\u5982\u4F55\uFF1F\u300D</p><p>\u91D1\u5F00\u7532\u5927\u559C\u9053\uFF1A\u300C\u591A\u8C22\u5BAB\u4E3B\uFF01\u300D</p><p>\u6155\u5BB9\u9F99\u77B3\u5B54\u5FAE\u6536\uFF0C\u53C8\u9053\uFF1A\u300C\u82E5\u662F\u9634\u5BAB\u4E3B\u5462\uFF1F\u300D</p><p>\u91D1\u5F00\u7532\u4E00\u6014\uFF0C\u6C89\u58F0\u9053\uFF1A\u300C\u9634\u59EC\u6C89\u7F05\u58F0\u8272\uFF0C\u4F7F\u6211\u6559\u4EBA\u6750\u51CB\u96F6\uFF0C\u58F0\u5A01\u5927\u8DCC\uFF0C\u5B9E\u662F\u6211\u6559\u7F6A\u4EBA\uFF01\u300D</p><p>\u6155\u5BB9\u9F99\u76EE\u5149\u626B\u8FC7\u795E\u6BBF\uFF0C\u970D\u72C2\u7130\u7B49\u4EBA\u90FD\u7EB7\u7EB7\u8868\u793A\u6548\u5FE0\uFF0C\u5BF9\u9634\u5BAB\u4E3B\u7EDD\u65E0\u7559\u604B\u3002\u53EA\u6709\u6C90\u58F0\u4F20\u4E00\u8A00\u4E0D\u53D1\uFF0C\u4ED6\u8363\u5347\u62A4\u6CD5\uFF0C\u8138\u4E0A\u4ECD\u65E0\u534A\u70B9\u559C\u8272\u3002</p><p>\u6155\u5BB9\u9F99\u51B2\u53F6\u884C\u5357\u70B9\u4E86\u70B9\u5934\uFF0C\u53F6\u884C\u5357\u8EAB\u5F71\u4E00\u6643\uFF0C\u6D88\u5931\u5728\u5C4F\u98CE\u4E4B\u540E\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u6628\u65E5\u5E08\u7236\u4E24\u5EA6\u6765\u88AD\uFF0C\u6155\u5BB9\u7D2B\u73AB\u5FC3\u91CC\u5145\u6EE1\u5E0C\u671B\uFF0C\u966A\u6BCD\u4EB2\u8BF4\u4E86\u4F1A\u513F\u8BDD\uFF0C\u4FBF\u4F0F\u5230\u95E8\u540E\u5077\u542C\u3002\u8033\u95FB\u6BBF\u4E2D\u5185\u54C4\uFF0C\u6B63\u5174\u9AD8\u91C7\u70C8\uFF0C\u6CA1\u60F3\u5230\u77F3\u95E8\u7A81\u7136\u6253\u5F00\u3002\u5979\u907F\u65E0\u53EF\u907F\uFF0C\u5E72\u8106\u626C\u8D77\u8138\uFF0C\u6EE1\u4E0D\u5728\u4E4E\u5730\u77A5\u4E86\u53F6\u884C\u5357\u4E00\u773C\u3002</p><p>\u53F6\u884C\u5357\u6728\u7740\u8138\u4E0E\u5979\u64E6\u80A9\u800C\u8FC7\uFF0C\u4ECE\u9694\u58C1\u623F\u95F4\u63A8\u51FA\u4E00\u4E2A\u9AD8\u8FD1\u4E00\u4EBA\uFF0C\u5BBD\u8FD1\u516D\u5C3A\u7684\u7269\u4F53\u3002\u752C\u9053\u672C\u5C31\u72ED\u7A84\uFF0C\u7D2B\u73AB\u4E0D\u5F97\u4E0D\u4E00\u8DEF\u9000\u5230\u95E8\u5916\u3002\u5979\u8D81\u673A\u6380\u5F00\u4E0A\u9762\u76D6\u7684\u539A\u6BEF\u8FC5\u901F\u770B\u4E86\u4E00\u773C\uFF0C\u4F46\u773C\u524D\u53EA\u767D\u5149\u4E00\u95EA\uFF0C\u5C31\u88AB\u957F\u957F\u7684\u9B03\u6BDB\u63A9\u76D6\u3002</p><p>\u7D2B\u73AB\u89C1\u5E08\u7236\u8FD8\u6CA1\u6740\u5230\u9B54\u6BBF\uFF0C\u4E0D\u7531\u6709\u4E9B\u5931\u671B\u3002\u6155\u5BB9\u9F99\u773C\u775B\u4E00\u77AA\uFF0C\u5979\u7EF7\u7740\u8138\u626D\u5934\u6C14\u9F13\u9F13\u56DE\u5230\u5C4F\u98CE\u540E\u9762\u3002</p><p>\u7269\u4F53\u5341\u5206\u6C89\u91CD\uFF0C\u56DB\u540D\u6C49\u5B50\u9F50\u529B\u624D\u628A\u5B83\u62AC\u5230\u6BBF\u4E2D\u3002\u6155\u5BB9\u9F99\u5750\u5728\u6905\u4E2D\u6697\u81EA\u8FD0\u6C14\uFF0C\u53F6\u884C\u5357\u7F13\u7F13\u63ED\u5F00\u6BDB\u6BEF\u3002</p><p>\u6BBF\u4E2D\u987F\u65F6\u54CD\u8D77\u4E00\u7247\u60CA\u53F9\uFF0C\u8FD1\u767E\u540D\u804C\u4EFD\u8F83\u9AD8\u7684\u5E2E\u4F17\u77A0\u76EE\u7ED3\u820C\u5730\u770B\u7740\u4E4D\u7136\u51FA\u73B0\u7684\u9634\u5BAB\u4E3B\u3002</p><p>\u8EAB\u65E0\u5BF8\u7F15\u7684\u9634\u59EC\u4ECD\u5982\u4EE5\u5F80\u90A3\u6837\u9999\u8273\u52A8\u4EBA\uFF0C\u7F8E\u76EE\u534A\u95ED\uFF0C\u8138\u4E0A\u6EE1\u662F\u67D4\u5A9A\u8FF7\u4EBA\u7684\u5FAE\u7B11\uFF0C\u4F3C\u4E4E\u9676\u9189\u5728\u6781\u5927\u7684\u6B22\u6109\u4E2D\u3002\u5979\u4E00\u624B\u66F2\u8098\u652F\u5728\u80F8\u524D\uFF0C\u80A5\u767D\u7684\u5706\u4E73\u8FD8\u5728\u5FAE\u5FAE\u6643\u52A8\uFF0C\u9999\u8F6F\u7684\u5A07\u8EAF\u66F2\u7EBF\u73B2\u73D1\uFF0C\u4E30\u6EE1\u7684\u5927\u817F\u5DE6\u53F3\u5FAE\u5206\uFF0C\u808C\u80A4\u6676\u83B9\u5982\u7389\u3002\u4F46\u8170\u81C0\u5374\u88AB\u7C97\u9ED1\u7684\u957F\u9B03\u8986\u76D6\uFF0C\u770B\u4E0D\u6E05\u695A\u3002</p><p>\u4F17\u4EBA\u54BD\u4E86\u53E3\u5410\u6CAB\uFF0C\u773C\u5149\u5411\u4E0A\u770B\u53BB\u3002\u7F8E\u8273\u7684\u5973\u4F53\u4E0A\u8D6B\u7136\u662F\u4E00\u5934\u58EE\u7855\u7684\u516C\u725B\uFF0C\u7C97\u9888\u5DE8\u773C\uFF0C\u89D2\u5982\u5F2F\u5200\u3002\u94C1\u67F1\u822C\u7684\u56DB\u817F\u8E0F\u5728\u94C1\u76D8\u4E0A\uFF0C\u5C06\u8273\u5987\u67D4\u7F8E\u7684\u8EAB\u4F53\u5708\u5728\u8179\u4E0B\u3002</p><p>\u4E3A\u4E86\u4FDD\u5B58\u9634\u5BAB\u4E3B\u7684\u5C38\u8EAB\uFF0C\u70AE\u5236\u8FD9\u5934\u516C\u725B\uFF0C\u53F6\u884C\u5357\u8D39\u4E86\u4E0D\u5C11\u529B\u6C14\uFF0C\u6B64\u65F6\u5FC3\u4E0B\u5F97\u610F\uFF0C\u7B11\u5475\u5475\u64A9\u8D77\u957F\u9B03\uFF0C\u9732\u51FA\u9634\u5BAB\u4E3B\u6ED1\u5AE9\u6D01\u767D\u7684\u80A5\u81C0\u3002</p><p>\u9634\u5BAB\u4E3B\u53E6\u4E00\u53EA\u624B\u6B63\u4F38\u5728\u8EAB\u540E\uFF0C\u63B0\u7740\u80A5\u7F8E\u7684\u81C0\u8089\uFF0C\u50CF\u662F\u6B63\u5728\u7AED\u529B\u633A\u52A8\u3002\u4E00\u6839\u624B\u81C2\u7C97\u7EC6\u7684\u5DE8\u9633\u6DF1\u6DF1\u63D2\u5165\u8089\u7A74\uFF0C\u5C06\u5979\u80A1\u95F4\u79D8\u5883\u5B8C\u5168\u6491\u5F00\uFF0C\u88AB\u6324\u6210\u4E00\u5708\u7EC6\u7A84\u7EA2\u8089\u7684\u82B1\u74E3\u4E0A\uFF0C\u4EFF\u4F5B\u8FD8\u6CBE\u7740\u6DEB\u6C34\uFF0C\u9690\u9690\u95EA\u4EAE\u3002</p><p>\u4F17\u4EBA\u770B\u5F97\u77EB\u820C\u96BE\u4E0B\uFF0C\u8C01\u80FD\u60F3\u5230\u6614\u65E5\u6559\u4E2D\u81F3\u5C0A\u65E0\u4E0A\u7684\u9634\u5BAB\u4E3B\u4F1A\u88AB\u4EBA\u5236\u6210\u8273\u5C38\uFF0C\u800C\u4E14\u6B7B\u540E\u8FD8\u88AB\u516C\u725B\u5978\u6DEB\uFF1F</p><p>\u53F6\u884C\u5357\u4E00\u63A8\u5706\u76D8\uFF0C\u5973\u5C38\u548C\u505A\u6210\u6807\u672C\u7684\u516C\u725B\u7ACB\u523B\u65CB\u8F6C\u8D77\u6765\u3002\u4E1D\u53D1\u98D8\u626C\uFF0C\u9634\u59EC\u5A07\u8273\u7684\u7389\u8138\u5149\u6655\u95EA\u52A8\uFF0C\u6829\u6829\u5982\u751F\u3002</p><p>\u7A81\u7136\u4E00\u4E2A\u4EBA\u8EAB\u5F71\u9AD8\u9AD8\u8DC3\u8D77\uFF0C\u300C\u546F\u300D\u7684\u4E00\u638C\u51FB\u5728\u4E00\u540D\u9762\u9732\u4E0D\u5FCD\u4E4B\u8272\u7684\u5E2E\u4F17\u5934\u9876\uFF0C\u90A3\u4EBA\u8111\u9AA8\u5C3D\u788E\uFF0C\u987F\u65F6\u5012\u5730\u6C14\u7EDD\u3002</p><p>\u91D1\u5F00\u7532\u7F13\u7F13\u6536\u56DE\u94C1\u638C\uFF0C\u864E\u76EE\u751F\u5A01\uFF0C\u6C89\u58F0\u9053\uFF1A\u300C\u9634\u5BAB\u4E3B\u8352\u4E8E\u5E2E\u52A1\uFF0C\u884C\u4E8B\u4E56\u623E\uFF0C\u6B7B\u4E0D\u8DB3\u60DC\u3002\u6562\u6709\u5F02\u5FC3\u8005\u683C\u6740\u52FF\u8BBA\uFF01\u300D</p><p>\u6155\u5BB9\u9F99\u5634\u89D2\u5FAE\u6311\uFF0C\u987E\u76FC\u95F4\u96C4\u59FF\u82F1\u53D1\uFF0C\u795E\u91C7\u98DE\u626C\u3002\u4ECA\u65E5\u632B\u8D25\u6C34\u67D4\u4ED9\u53DB\u4E71\uFF0C\u53C8\u8D62\u5F97\u4F17\u4EBA\u6548\u5FE0\uFF0C\u4ED6\u4FE1\u5FC3\u5927\u589E\u3002\u4E8E\u662F\u8D81\u673A\u5C55\u793A\u9634\u5BAB\u4E3B\u7684\u6B7B\u72B6\uFF0C\u6311\u660E\u7BE1\u4F4D\u4E4B\u4E3E\uFF0C\u8BA9\u4F17\u4EBA\u6B7B\u5FC3\u584C\u5730\u627F\u8BA4\u81EA\u5DF1\u7684\u5BAB\u4E3B\u8EAB\u4EFD\u3002\u4E8C\u6765\u53C8\u85C9\u673A\u6E05\u7406\u5FC3\u5411\u65E7\u4E3B\u7684\u90E8\u5C5E\uFF0C\u53EF\u8C13\u4E00\u77F3\u4E8C\u9E1F\u3002</p><p>\u970D\u72C2\u7130\u56F4\u7740\u9634\u59EC\u7684\u5C38\u4F53\u5567\u5567\u8D5E\u53F9\uFF0C\u602A\u7B11\u9053\uFF1A\u300C\u65E9\u77E5\u5BAB\u4E3B\u6709\u6B64\u795E\u725B\uFF0C\u5C31\u8BA9\u5B83\u7ED9\u98CE\u5A4A\u5B50\u5F00\u82DE\u597D\u4E86\u2014\u2014\u98CE\u5A4A\u5B50\u7684\u5C44\u8981\u8BA9\u8FD9\u4E48\u7C97\u7684\u5BB6\u4F19\u6345\u51E0\u4E0B\uFF0C\u80AF\u5B9A\u4E00\u8F88\u5B50\u90FD\u5FD8\u4E0D\u4E86\u2026\u2026\u300D</p><p>\u5C60\u6000\u6C89\u4E5F\u5475\u5475\u7B11\u9053\uFF1A\u300C\u9634\u5BAB\u4E3B\u6700\u559C\u91C7\u8865\uFF0C\u8FD9\u5934\u725B\u53EF\u591F\u5979\u91C7\u4E9B\u65E5\u5B50\u4E86\u3002\u300D</p><p>\u6BBF\u4E0B\u7684\u5E2E\u4F17\u89C1\u6559\u4E2D\u51E0\u4F4D\u957F\u8001\u5982\u6B64\u8BF4\uFF0C\u987F\u65F6\u90FD\u653E\u4E0B\u5FC3\u4E8B\u563B\u7B11\u8D77\u6765\uFF0C\u300C\u8FD9\u5973\u4EBA\u5929\u5929\u677F\u7740\u4E2A\u8138\uFF0C\u52A8\u4E0D\u52A8\u5C31\u558A\u6253\u558A\u6740\uFF0C\u8FD9\u4F1A\u513F\u6D6A\u8D77\u6765\u8FD8\u633A\u597D\u770B\u5462\u2026\u2026\u300D</p><p>\u300C\u90A3\u4E48\u5927\u7684\u5BB6\u4F19\u90FD\u80FD\u585E\u8FDB\u53BB\uFF0C\u4E5F\u4E0D\u77E5\u9053\u88AB\u591A\u5C11\u4EBA\u64CD\u8FC7\uFF0C\u5728\u8001\u5B50\u9762\u524D\u8FD8\u88C5\u5F97\u4EBA\u4E94\u4EBA\u516D\uFF0C\u770B\u4E00\u773C\u90FD\u4E0D\u884C\u2026\u2026\u300D</p><p>\u300C\u8FD9\u4F1A\u513F\u597D\u4E86\uFF0C\u4F60\u60F3\u600E\u4E48\u770B\uFF0C\u5C31\u600E\u4E48\u770B\uFF0C\u8FD8\u80FD\u770B\u7740\u8FD9\u5A4A\u5B50\u8DDF\u725B\u5E72\u5462\u2026\u2026\u6211\u64CD\uFF0C\u8FD9\u662F\u4EC0\u4E48\u725B\u554A\uFF1F\u4F60\u77A7\u90A3\u4FE9\u86CB\u5B50\u513F\uFF0C\u6BD4\u4F60\u62F3\u5934\u90FD\u5927\u3002\u300D</p><p>\u300C\u8FD9\u9E21\u5DF4\u8D77\u7801\u4E00\u5C3A\u591A\u957F\uFF0C\u602A\u4E0D\u5F97\u5979\u5728\u7B11\u5462\u2026\u2026\u300D</p><p>\u6C90\u58F0\u4F20\u5FC3\u4E0B\u6697\u53F9\uFF0C\u9634\u59EC\u7684\u6B66\u529F\u667A\u6167\uFF0C\u4E5F\u662F\u4E0D\u4E16\u51FA\u7684\u9876\u5C16\u4EBA\u7269\uFF0C\u82E5\u975E\u5982\u6B64\u4E5F\u4E0D\u53EF\u80FD\u5728\u7537\u5C0A\u5973\u5351\u7684\u661F\u6708\u6E56\u5750\u4E0A\u5BAB\u4E3B\u4E4B\u4F4D\uFF0C\u53EF\u5979\u6BD5\u7ADF\u53EA\u662F\u4E2A\u5973\u4EBA\u2026\u2026</p><p>\u6155\u5BB9\u9F99\u4E0D\u52A8\u58F0\u8272\u5730\u770B\u7740\u6BCF\u4E2A\u4EBA\u7684\u795E\u8272\uFF0C\u6700\u540E\u77A7\u5411\u5730\u4E0A\u7684\u6C34\u67D4\u4ED9\u3002\u6C34\u67D4\u4ED9\u4FCF\u8138\u96EA\u767D\uFF0C\u80F8\u524D\u6EE1\u662F\u9C9C\u8840\uFF0C\u5979\u6B66\u529F\u672C\u5C31\u900A\u4E8E\u6C90\u58F0\u4F20\uFF0C\u53C8\u662F\u6BEB\u65E0\u63D0\u9632\u4E0B\u7A81\u906D\u6BD2\u624B\u3002\u7ECF\u8109\u5C3D\u53D7\u91CD\u521B\uFF0C\u6D51\u8EAB\u529F\u529B\u635F\u5F97\u4E03\u4E03\u516B\u516B\uFF0C\u60F3\u62AC\u8D77\u624B\u6307\u4E5F\u662F\u4E0D\u80FD\u3002\u6B64\u65F6\u773C\u89C1\u81EA\u5DF1\u656C\u6155\u7684\u5BAB\u4E3B\u88AB\u4EBA\u5982\u6B64\u73A9\u5F04\u66F4\u662F\u5FC3\u788E\u6B32\u7EDD\u3002</p><p>\u5979\u611F\u6FC0\u5730\u770B\u4E86\u91D1\u5F00\u7532\u4E00\u773C\uFF0C\u82E5\u975E\u4ED6\u51FA\u8A00\u6C42\u6073\uFF0C\u81EA\u5DF1\u6240\u53D7\u7684\u6298\u8FB1\u4F1A\u6BD4\u9634\u5BAB\u4E3B\u66F4\u591A\u3002\u5598\u4E86\u53E3\u6C14\uFF0C\u6C34\u67D4\u4ED9\u6CEA\u773C\u6A21\u7CCA\u5730\u76EF\u7D27\u6C90\u58F0\u4F20\u3001\u53F6\u884C\u5357\u3001\u6155\u5BB9\u9F99\u7B49\u4EBA\uFF0C\u5FC3\u91CC\u72C2\u547C\u9053\uFF1A\u300C\u6211\u505A\u9B3C\u4E5F\u4E0D\u653E\u8FC7\u4F60\u4EEC\uFF01\u300D\u5FC3\u4E00\u6A2A\uFF0C\u5F20\u53E3\u54AC\u4F4F\u820C\u5934\u3002</p><p>\u6155\u5BB9\u9F99\u7FFB\u638C\u4ECE\u6905\u80CC\u62CD\u4E0B\u4E00\u89D2\uFF0C\u9694\u7A7A\u5F39\u51FA\uFF0C\u5C01\u4E86\u5979\u7A74\u9053\u3002\u7136\u540E\u817E\u8EAB\u800C\u8D77\uFF0C\u53C9\u7740\u5979\u67D4\u9888\u4E3E\u8FC7\u5934\u9876\u3002\u6C34\u67D4\u4ED9\u820C\u5C16\u6697\u5410\uFF0C\u9C9C\u8840\u6EF4\u6EF4\u6D41\u4E0B\uFF0C\u51C4\u7136\u5408\u7D27\u7F8E\u76EE\u3002</p><p>\u6155\u5BB9\u9F99\u671D\u91D1\u5F00\u7532\u5FAE\u5FAE\u4E00\u7B11\uFF0C\u70B9\u4E86\u70B9\u5934\u3002</p><p>\u7D2B\u73AB\u8EB2\u5728\u5C4F\u98CE\u540E\u770B\u4E86\u4E24\u773C\uFF0C\u6CA1\u6709\u751F\u547D\u7684\u7F8E\u5987\u548C\u516C\u725B\u4EA4\u548C\u7684\u666F\u8C61\uFF0C\u4F7F\u5979\u4E0D\u5FCD\u76EE\u7779\uFF0C\u4E8E\u662F\u501A\u5728\u752C\u9053\u4E0A\u95ED\u76EE\u6C89\u601D\u3002\u8DDF\u6BCD\u4EB2\u8C08\u8BDD\u4E2D\u5979\u542C\u8BF4\u8FC7\u9634\u5BAB\u4E3B\u7684\u4E8B\u60C5\uFF0C\u4F46\u6CA1\u60F3\u5230\u8FD9\u4E2A\u4E0E\u7236\u4EB2\u4EC7\u6DF1\u4F3C\u6D77\u7684\u5973\u4EBA\u7ADF\u4F1A\u843D\u5F97\u5982\u6B64\u4E0B\u573A\uFF0C\u53D8\u6210\u4E00\u5177\u6DEB\u7325\u4E0D\u582A\u7684\u8273\u5C38\uFF0C\u4EFB\u4EBA\u89C2\u8D4F\u3002</p><p>\u8863\u895F\u58F0\u54CD\uFF0C\u6155\u5BB9\u9F99\u63D0\u7740\u6C34\u67D4\u4ED9\u8D70\u5165\u752C\u9053\u3002\u7D2B\u73AB\u62AC\u773C\u770B\u7740\u5944\u5944\u4E00\u606F\u7684\u6C34\u67D4\u4ED9\uFF0C\u5FC3\u91CC\u767E\u5473\u6742\u9648\u3002\u5979\u5F53\u65E5\u4E0E\u5927\u5E08\u59D0\u4EA4\u624B\u65F6\u662F\u5426\u60F3\u5230\u4F1A\u6709\u4ECA\u5929\u5462\uFF1F</p><p>\u6155\u5BB9\u9F99\u63A9\u4E0A\u77F3\u95E8\uFF0C\u659C\u773C\u770B\u4E86\u770B\u7D2B\u73AB\uFF0C\u671D\u77F3\u5BAB\u7684\u5927\u5385\u8D70\u53BB\u3002\u7D2B\u73AB\u8DDF\u5728\u540E\u9762\uFF0C\u63A2\u5934\u77A7\u77A7\u4ED6\u7684\u8138\u8272\uFF0C\u6084\u58F0\u95EE\u9053\uFF1A\u300C\u4F60\u8981\u600E\u4E48\u5904\u7F6E\u5979\uFF1F\u300D</p><p>\u6155\u5BB9\u9F99\u6DE1\u6DE1\u9053\uFF1A\u300C\u6559\u4E2D\u53DB\u9006\u4E00\u5411\u5728\u5730\u5B57\u77F3\u5BAB\u5904\u7F6E\u2014\u2014\u4F60\u8BF4\u54EA\u4E00\u95F4\u5408\u9002\uFF1F\u300D</p><p>\u6155\u5BB9\u7D2B\u73AB\u4E00\u6115\uFF0C\u8FDE\u5FD9\u5782\u4E0B\u5934\uFF0C\u88C5\u4F5C\u601D\u7D22\u7684\u6837\u5B50\u63A9\u9970\u5FC3\u4E2D\u7684\u72C2\u559C\u3002</p>',57);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
