import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_36" tabindex="-1"><a class="header-anchor" href="#_36" aria-hidden="true">#</a> 36</h1><p>\u795E\u6BBF\u7684\u55A7\u95F9\u88AB\u9694\u5728\u95E8\u5916\uFF0C\u77F3\u5BAB\u53C8\u6062\u590D\u4E86\u4EE5\u5F80\u6C89\u5BC2\u3002\u6155\u5BB9\u9F99\u5728\u5730\u5B57\u752C\u9053\u524D\u505C\u4E0B\u811A\u6B65\uFF0C\u4F4E\u5934\u6CE8\u89C6\u5A07\u7F8E\u5982\u82B1\u7684\u7389\u4EBA\u3002</p><p>\u7D2B\u73AB\u54AC\u4F4F\u7EA2\u5507\u4E00\u89D2\uFF0C\u7528\u529B\u60F3\u4E86\u534A\u5929\uFF0C\u6700\u540E\u53EF\u7231\u5730\u76B1\u4E86\u76B1\u5C0F\u5DE7\u7684\u9F3B\u5B50\uFF0C\u4E0B\u51B3\u5FC3\u8BF4\uFF1A\u300C\u80FD\u4E0D\u80FD\u6BCF\u4E2A\u623F\u95F4\u90FD\u53BB\uFF1F\u300D</p><p>\u6155\u5BB9\u9F99\u54C8\u54C8\u5927\u7B11\uFF0C\u7768\u89C6\u7740\u6C34\u67D4\u4ED9\u9053\uFF1A\u300C\u4E00\u95F4\u5C31\u591F\u54B1\u4EEC\u6C34\u957F\u8001\u5F00\u5FC3\u4E86\uFF0C\u6BCF\u95F4\u90FD\u53BB\uFF0C\u53EA\u6015\u5979\u6CA1\u8FD9\u4E2A\u798F\u6C14\u2026\u2026\u300D</p><p>\u7D2B\u73AB\u8D70\u8FDB\u752C\u9053\uFF0C\u4E00\u8FB9\u4E1C\u5F20\u897F\u671B\uFF0C\u4E00\u8FB9\u597D\u5947\u5730\u8BF4\uFF1A\u300C\u8FD9\u91CC\u9762\u90FD\u6709\u4EC0\u4E48\uFF1F\u300D</p><p>\u300C\u4E1C\u897F\u4E5F\u4E0D\u591A\uFF0C\u6BCF\u4E2A\u623F\u95F4\u53EA\u6709\u4E00\u6837\uFF0C\u4F60\u731C\u731C\u3002\u300D</p><p>\u7D2B\u73AB\u88C5\u505A\u4E0D\u7ECF\u610F\u5730\u8FD9\u95F4\u62CD\u62CD\uFF0C\u90A3\u95F4\u63A8\u63A8\uFF0C\u5A07\u61A8\u5730\u8BF4\uFF1A\u300C\u4EBA\u5BB6\u731C\u4E0D\u51FA\u561B\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u8DDF\u5728\u540E\u9762\u4F4E\u7B11\u9053\uFF1A\u300C\u6253\u5F00\u4E00\u95F4\u4F60\u5C31\u77E5\u9053\u4E86\u3002\u300D</p><p>\u7D2B\u73AB\u8D70\u5230\u752C\u9053\u5C3D\u5934\uFF0C\u53C8\u8D70\u4E86\u56DE\u6765\uFF0C\u72B9\u8C6B\u534A\u5929\u624D\u6307\u7740\u4E00\u95F4\u65AD\u7136\u9053\uFF1A\u300C\u5C31\u5B83\u5427\u3002\u300D</p><p>\u7389\u624B\u6240\u6307\u6B63\u662F\u5BC5\u5BA4\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u8F67\u8F67\u58F0\u54CD\uFF0C\u77F3\u95E8\u7F13\u7F13\u63A8\u5F00\u3002\u8FD9\u6761\u752C\u9053\u5927\u6982\u6DF1\u5165\u5C71\u8179\uFF0C\u900F\u6C14\u6027\u4E0D\u5982\u5176\u5B83\u752C\u9053\uFF0C\u4E00\u5F00\u95E8\uFF0C\u90A3\u80A1\u81ED\u6C14\u7ACB\u523B\u6251\u9F3B\u800C\u6765\u3002\u7D2B\u73AB\u5FC3\u91CC\u546F\u546F\u76F4\u8DF3\uFF0C\u4F7F\u52B2\u77A7\u5411\u5BA4\u5185\u3002\u9ED1\u6697\u4E2D\u53EA\u89C1\u4E24\u9897\u7855\u5927\u7684\u9752\u9ED1\u8272\u660E\u73E0\u4E00\u95EA\u4E00\u95EA\uFF0C\u6563\u53D1\u7740\u5E7D\u84DD\u7684\u5149\u8292\u3002</p><p>\u7D2B\u73AB\u6025\u6B32\u770B\u4E2A\u7A76\u7ADF\uFF0C\u95E8\u4E00\u5F00\u7ACB\u5373\u94BB\u4E86\u8FDB\u53BB\u3002\u6155\u5BB9\u9F99\u4E00\u628A\u62C9\u4F4F\u5979\u624B\u81C2\u3002\u7D2B\u73AB\u4F5C\u8D3C\u5FC3\u865A\uFF0C\u4EE5\u4E3A\u4ED6\u53D1\u89C9\u4E86\u81EA\u5DF1\u7684\u7528\u610F\uFF0C\u8FDE\u5FD9\u505C\u4F4F\u3002\u6B63\u56DE\u9996\u4FCF\u89C6\uFF0C\u5FFD\u7136\u8033\u65C1\u98CE\u58F0\u5927\u632F\uFF0C\u9ED1\u6697\u4E2D\u4E24\u6392\u95EA\u4EAE\u7684\u7259\u9F7F\u6076\u72E0\u72E0\u671D\u54BD\u5589\u54AC\u6765\u3002\u7D2B\u73AB\u60CA\u53EB\u4E00\u58F0\uFF0C\u9999\u8EAF\u540E\u4EF0\uFF0C\u5012\u5728\u4E00\u4E2A\u6E29\u6696\u7684\u6000\u62B1\u4E2D\u3002</p><p>\u6155\u5BB9\u9F99\u5410\u6C14\u5F00\u58F0\uFF0C\u4E00\u638C\u51FB\u4E2D\u90A3\u4E2A\u5E9E\u5927\u7684\u8EAB\u5F71\uFF0C\u81EA\u5DF1\u4E5F\u88AB\u9707\u5F97\u540E\u9000\u4E00\u6B65\u3002\u4ED6\u7ACB\u5728\u95E8\u65C1\uFF0C\u4ECE\u6000\u4E2D\u638F\u51FA\u7167\u4EAE\u7684\u660E\u73E0\u3002</p><p>\u90A3\u6761\u9EC4\u5F71\u843D\u5728\u5730\u4E0A\u6253\u4E86\u4E2A\u6EDA\uFF0C\u7ACB\u523B\u722C\u8D77\u6765\uFF0C\u5F13\u8170\u7F29\u9888\uFF0C\u53D1\u51FA\u51F6\u731B\u7684\u4F4E\u543C\u3002\u968F\u7740\u73E0\u8F89\u6E10\u6E10\u95EA\u4EAE\uFF0C\u9ED1\u6697\u4E2D\u663E\u51FA\u4E00\u4E2A\u7855\u5927\u7684\u5934\u9885\uFF0C\u989D\u4E0A\u6761\u7EB9\u9ED1\u9EC4\u4EA4\u9519\uFF0C\u5F62\u6210\u4E00\u4E2A\u300C\u738B\u300D\u5B57\uFF0C\u5374\u662F\u4E00\u53EA\u6591\u6593\u731B\u864E\u3002</p><p>\u5B83\u4F53\u5F62\u957F\u5927\uFF0C\u51E0\u4E4E\u5360\u4E86\u534A\u95F4\u77F3\u5BA4\uFF0C\u4F4E\u543C\u7247\u523B\uFF0C\u94C1\u97AD\u4F3C\u7684\u864E\u5C3E\u4E00\u7529\uFF0C\u91CD\u91CD\u6253\u5728\u77F3\u58C1\u4E0A\u3002\u731B\u864E\u6602\u8D77\u5934\u5F20\u5F00\u8840\u76C6\u5927\u53E3\uFF0C\u53D1\u51FA\u4E00\u58F0\u9707\u8033\u6B32\u804B\u7684\u72C2\u543C\u3002</p><p>\u4E00\u9635\u8165\u98CE\u6251\u9762\u800C\u6765\uFF0C\u7D2B\u73AB\u5413\u5F97\u8138\u8272\u96EA\u767D\uFF0C\u7D27\u7D27\u504E\u5728\u6155\u5BB9\u9F99\u8EAB\u8FB9\uFF0C\u8FDE\u56FE\u5F62\u4E5F\u987E\u4E0D\u5F97\u5BFB\u627E\u3002</p><p>\u90A3\u53EA\u731B\u864E\u4E5F\u8BA4\u51FA\u4E3B\u4EBA\u7684\u6C14\u5473\uFF0C\u53EA\u543C\u4E86\u4E00\u58F0\uFF0C\u5374\u6CA1\u6709\u6251\u8FC7\u6765\u3002\u6155\u5BB9\u9F99\u5766\u7136\u8D70\u5165\u5BA4\u5185\uFF0C\u626F\u51C0\u6C34\u67D4\u4ED9\u7684\u8863\u7269\uFF0C\u6258\u8D77\u5979\u7684\u817F\u6839\u5DE6\u53F3\u4E00\u5206\uFF0C\u4E24\u6839\u62C7\u6307\u5265\u5F00\u82B1\u74E3\uFF0C\u51D1\u4E0A\u53BB\u770B\u4E86\u4E00\u773C\uFF0C\u7B11\u9053\uFF1A\u300C\u6211\u4EE5\u4E3A\u6C34\u957F\u8001\u5B88\u8EAB\u5982\u7389\uFF0C\u8FD8\u662F\u4E2A\u5904\u5B50\uFF0C\u6CA1\u60F3\u5230\u4E5F\u662F\u88AB\u73A9\u8FC7\u7684\u70C2\u8D27\u2014\u2014\u8D31\u4EBA\uFF0C\u8C01\u7ED9\u4F60\u5F00\u7684\u82DE\u554A\uFF1F\u300D</p><p>\u82E5\u662F\u5E73\u65F6\u6C34\u67D4\u4ED9\u81EA\u7136\u4E0D\u60E7\uFF0C\u4F46\u6B64\u65F6\u624B\u811A\u6CA1\u6709\u4E1D\u6BEB\u529B\u6C14\uFF0C\u83AB\u8BF4\u662F\u731B\u864E\uFF0C\u5C31\u662F\u4E00\u6761\u91CE\u72D7\u5979\u4E5F\u65E0\u6CD5\u6323\u624E\u95EA\u907F\uFF0C\u53EA\u80FD\u4EFB\u5176\u9C7C\u8089\u3002\u5979\u81EA\u77E5\u65E0\u53EF\u5E78\u514D\uFF0C\u5FC3\u4E00\u6A2A\uFF0C\u6012\u89C6\u6155\u5BB9\u9F99\uFF0C\u6CA1\u6709\u9732\u51FA\u4E1D\u6BEB\u4E5E\u6C42\u4E4B\u8272\u3002</p><p>\u8FD9\u79CD\u70C8\u6027\u5973\u5B50\u6155\u5BB9\u9F99\u89C1\u5F97\u4E5F\u591A\u4E86\uFF0C\u4ED6\u63E1\u4F4F\u6C34\u67D4\u4ED9\u7684\u4E73\u623F\u53F9\u9053\uFF1A\u300C\u6C34\u957F\u8001\u8FD9\u8EAB\u5B50\u767D\u767D\u5AE9\u5AE9\uFF0C\u6CA1\u8BA9\u5927\u4F19\u90FD\u6765\u5C1D\u5C1D\u5B9E\u5728\u662F\u53EF\u60DC\u3002\u5C11\u592B\u4EBA\u65E2\u7136\u7ED9\u4F60\u6311\u4E86\u8FD9\u95F4\uFF0C\u4F60\u5C31\u597D\u597D\u966A\u8FD9\u5934\u731B\u864E\u4E50\u4E50\u2026\u2026\u300D\u8BF4\u7740\u6254\u51FA\u4E00\u7C92\u836F\u4E38\uFF0C\u731B\u864E\u8840\u53E3\u5F20\u5F00\uFF0C\u624B\u638C\u5927\u7684\u820C\u5934\u4E00\u5377\uFF0C\u5C06\u836F\u4E38\u541E\u4E86\u4E0B\u53BB\u3002</p><p>\u6C34\u67D4\u4ED9\u5999\u76EE\u5706\u77AA\uFF0C\u50BB\u50BB\u770B\u7740\u731B\u864E\uFF0C\u4E00\u9635\u6050\u60E7\u6D41\u8FC7\u5FC3\u5E95\uFF0C\u7981\u4E0D\u4F4F\u6218\u6817\u8D77\u6765\u3002</p><p>\u9EC4\u5E95\u9ED1\u7AE0\u7684\u6BDB\u76AE\u4E0B\uFF0C\u8840\u7EA2\u7684\u9633\u5177\u7F13\u7F13\u633A\u76F4\u3002\u8089\u68D2\u867D\u7136\u7565\u900A\u4E8E\u5DE8\u725B\u7684\u7C97\u7EC6\uFF0C\u4F46\u72F0\u72DE\u72B9\u6709\u8FC7\u4E4B\u3002\u5C24\u5176\u662F\u864E\u97AD\u4E0A\u7684\u5012\u523A\uFF0C\u8840\u5149\u95EA\u52A8\uFF0C\u4EE4\u4EBA\u809D\u80C6\u4FF1\u788E\u3002</p><p>\u6155\u5BB9\u7D2B\u73AB\u54BD\u4E86\u53E3\u5410\u6CAB\uFF0C\u8D34\u7740\u5899\u5C0F\u5FC3\u7FFC\u7FFC\u5730\u8D70\u5230\u6155\u5BB9\u9F99\u8EAB\u8FB9\uFF0C\u4E24\u773C\u8FC5\u901F\u626B\u8FC7\u77F3\u58C1\u3002\u73E0\u8F89\u5149\u8292\u6709\u9650\uFF0C\u8BB8\u591A\u5730\u65B9\u66F4\u88AB\u731B\u864E\u957F\u5927\u7684\u8EAB\u5F71\u6321\u4F4F\uFF0C\u65E0\u6CD5\u770B\u6E05\u3002\u5979\u538B\u4F4F\u6050\u60E7\uFF0C\u52C9\u5F3A\u7B11\u9053\uFF1A\u300C\u8FD9\u91CC\u7ADF\u7136\u517B\u4E86\u5934\u8001\u864E\u2026\u2026\u771F\u597D\u73A9\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u95FB\u8A00\u5927\u7B11\u9053\uFF1A\u300C\u8FD8\u6709\u66F4\u597D\u73A9\u7684\u5462\u2014\u2014\u77A7\u77A7\u8001\u864E\u662F\u600E\u4E48\u64CD\u5973\u4EBA\u7684\uFF01\u300D</p><p>\u7D2B\u73AB\u5012\u62BD\u4E00\u53E3\u51C9\u6C14\uFF0C\u5979\u6839\u672C\u6CA1\u60F3\u5230\u81EA\u5DF1\u4F1A\u9009\u4E2D\u4E00\u5934\u8001\u864E\uFF0C\u6B64\u65F6\u4E00\u60F3\u624D\u77E5\u8FD9\u4E9B\u77F3\u5BA4\u5B9A\u662F\u4EE5\u5730\u652F\u4E3A\u5E8F\uFF0C\u8C62\u517B\u5341\u4E8C\u751F\u8096\u3002\u6B64\u65F6\u65E0\u8BBA\u5982\u4F55\u4E5F\u4E0D\u80FD\u9003\u907F\uFF0C\u53EA\u80FD\u770B\u7740\u6155\u5BB9\u9F99\u5C06\u65E0\u529B\u53CD\u6297\u7684\u5973\u5B50\u6446\u6210\u8DEA\u4F0F\u7684\u59FF\u52BF\uFF0C\u7136\u540E\u62AC\u624B\u5728\u5979\u81C0\u4E0A\u91CD\u51FB\u4E00\u638C\u3002</p><p>\u731B\u864E\u7F13\u7F13\u8FC8\u6B65\uFF0C\u65E0\u58F0\u65E0\u606F\u5730\u79FB\u4E86\u8FC7\u6765\u3002\u7D2B\u73AB\u5A07\u8EAF\u7D27\u7D27\u8D34\u7740\u77F3\u58C1\uFF0C\u6068\u4E0D\u5F97\u53D8\u6210\u7EB8\u90A3\u4E48\u8584\u3002</p><p>\u6C34\u67D4\u4ED9\u6025\u4FC3\u5730\u5410\u7740\u6C14\uFF0C\u5634\u89D2\u8840\u6CAB\u98DE\u6E85\u3002\u5FFD\u7136\u8EAB\u4F53\u4E00\u9707\uFF0C\u5374\u662F\u88AB\u6155\u5BB9\u9F99\u637B\u4F4F\u82B1\u8482\u3002\u9EBB\u75D2\u7684\u52B2\u6C14\u900F\u4F53\u800C\u5165\uFF0C\u5408\u5728\u4E00\u8D77\u7684\u82B1\u74E3\u4E4D\u7136\u5206\u5F00\uFF0C\u98A4\u6296\u7740\u6D8C\u51FA\u80A1\u80A1\u6E7F\u6ED1\u7684\u9ECF\u6DB2\u3002\u5A07\u8EAF\u98A4\u6296\u672A\u6B62\uFF0C\u4E00\u4E2A\u706B\u70ED\u7684\u7269\u4F53\u4FBF\u786C\u786C\u9876\u5728\u79D8\u5904\u3002</p><p>\u6155\u5BB9\u9F99\u63E1\u7740\u864E\u97AD\u626D\u5934\u7B11\u563B\u563B\u770B\u5411\u7D2B\u73AB\u4E0B\u4F53\u3002\u7D2B\u73AB\u5FC3\u91CC\u546F\u546F\u76F4\u8DF3\uFF0C\u5C0F\u624B\u4E0D\u7531\u81EA\u4E3B\u6309\u4F4F\u8179\u4E0B\u3002\u5979\u8138\u4E0A\u4E00\u7EA2\uFF0C\u8FDE\u5FD9\u677E\u5F00\u624B\uFF0C\u5014\u5F3A\u5730\u4EF0\u8D77\u8138\u3002</p><p>\u6155\u5BB9\u9F99\u7B11\u9053\uFF1A\u300C\u864E\u4E43\u767E\u517D\u4E4B\u738B\uFF0C\u6C34\u957F\u8001\u4ECA\u5929\u80FD\u5F53\u4E00\u56DE\u767E\u517D\u4E4B\u540E\uFF0C\u4E5F\u662F\u524D\u751F\u4FEE\u6765\u7684\u798F\u6C14\u2026\u2026\u300D\u624B\u4E00\u62AC\uFF0C\u628A\u864E\u97AD\u9001\u5230\u6C34\u67D4\u4ED9\u4F53\u5185\u3002\u6C34\u67D4\u4ED9\u5589\u5934\u5443\u5443\u4F5C\u54CD\uFF0C\u67D4\u5AE9\u7684\u82B1\u74E3\u6324\u5411\u4E24\u8FB9\uFF0C\u6162\u6162\u53D8\u7EC6\u53D8\u957F\u3002</p><p>\u731B\u864E\u611F\u53D7\u5230\u8089\u7A74\u5185\u7684\u6E29\u6DA6\u6ED1\u817B\uFF0C\u4F4E\u58F0\u5486\u54EE\u7740\u8170\u8179\u4E00\u52A8\uFF0C\u864E\u97AD\u7834\u4F53\u800C\u5165\u3002\u6C34\u67D4\u4ED9\u867D\u975E\u5904\u5B50\uFF0C\u4F46\u4E45\u672A\u4E0E\u4EBA\u4EA4\u5408\uFF0C\u8089\u7A74\u987F\u65F6\u88AB\u751F\u751F\u6495\u88C2\u3002</p><p>\u95FB\u5230\u8840\u8165\u6C14\uFF0C\u731B\u864E\u66F4\u662F\u864E\u5A01\u5927\u632F\uFF0C\u8170\u8EAB\u4E00\u6380\uFF0C\u6C34\u67D4\u4ED9\u5706\u81C0\u50CF\u88AB\u6CBE\u5230\u731B\u864E\u8179\u4E0B\uFF0C\u88AB\u9876\u5F97\u4E24\u819D\u60AC\u7A7A\uFF0C\u5C0F\u817F\u659C\u659C\u5206\u5F00\u3002\u4E00\u5BF9\u80A5\u5AE9\u7684\u96EA\u4E73\u64E6\u5728\u5730\u4E0A\uFF0C\u538B\u6210\u6241\u6241\u7684\u5F62\u72B6\u3002</p><p>\u5979\u9999\u80A9\u88AB\u8001\u864E\u4E24\u6761\u524D\u817F\u6321\u4F4F\uFF0C\u4E00\u9876\u4E4B\u4E0B\uFF0C\u67D4\u8EAF\u6298\u8D77\uFF0C\u8170\u90E8\u75BC\u75DB\u6B32\u65AD\u3002</p><p>\u5F85\u864E\u8EAF\u4E00\u9000\uFF0C\u6C34\u67D4\u4ED9\u53CC\u819D\u91CD\u91CD\u843D\u5728\u5730\u4E0A\uFF0C\u4E0D\u4F4F\u98A4\u6296\u3002\u864E\u97AD\u56DE\u62BD\u65F6\uFF0C\u7EC6\u5AE9\u591A\u8936\u7684\u8089\u58C1\u7ACB\u523B\u88AB\u575A\u786C\u7684\u5012\u523A\u522E\u51FA\u6570\u9053\u4F24\u75D5\uFF0C\u8840\u6DCB\u6DCB\u7684\u864E\u97AD\u50CF\u4E00\u6746\u957F\u67AA\u4ECE\u7C89\u81C0\u95F4\u7F13\u7F13\u62BD\u51FA\uFF0C\u8273\u7EA2\u7684\u5AE9\u8089\u968F\u4E4B\u7FFB\u5377\u3002</p><p>\u864E\u97AD\u8FD8\u672A\u5B8C\u5168\u62D4\u51FA\uFF0C\u731B\u864E\u96C4\u8EAF\u4E00\u987F\uFF0C\u53C8\u52A0\u529B\u524D\u9876\u3002\u6C34\u67D4\u4ED9\u81C0\u90E8\u51E0\u4E4E\u88AB\u6380\u6210\u671D\u5929\u5E73\u4E3E\u7684\u6A21\u6837\uFF0C\u4E24\u817F\u633A\u76F4\uFF0C\u53EA\u6709\u811A\u5C16\u70B9\u7740\u5730\u9762\u3002\u6155\u5BB9\u9F99\u5F39\u6307\u89E3\u5F00\u5979\u7684\u54D1\u7A74\uFF0C\u51C4\u5A49\u7684\u75DB\u53EB\u987F\u65F6\u54CD\u5F7B\u77F3\u5BA4\u3002\u521A\u53EB\u4E86\u534A\u58F0\uFF0C\u864E\u97AD\u5C3D\u6839\u800C\u5165\uFF0C\u6C34\u67D4\u4ED9\u7684\u75DB\u53EB\u7ACB\u523B\u4FBF\u53D8\u6210\u95F7\u54FC\u3002\u575A\u786C\u7684\u8089\u68D2\u4F3C\u4E4E\u8D2F\u7A7F\u4E86\u5C0F\u8179\uFF0C\u6240\u5230\u4E4B\u5904\u65E0\u4E0D\u5267\u75DB\u8FDE\u8FDE\u3002</p><p>\u6ED1\u5AE9\u7684\u80A5\u81C0\u5FFD\u8D77\u5FFD\u843D\uFF0C\u50CF\u4E00\u4E2A\u6CA1\u6709\u91CD\u91CF\u73A9\u5177\u822C\u5728\u731B\u864E\u8179\u4E0B\u4E0A\u4E0B\u8DF3\u52A8\u3002\u6C34\u67D4\u4ED9\u7684\u53EB\u58F0\u8D8A\u6765\u8D8A\u4F4E\uFF0C\u6700\u540E\u53EA\u5269\u4E0B\u75DB\u82E6\u7684\u547B\u541F\u3002\u8089\u7A74\u7684\u4F24\u53E3\u524D\u540E\u51E0\u4E4E\u5EF6\u4F38\u5230\u82B1\u8482\u548C\u83CA\u809B\uFF0C\u96EA\u767D\u7684\u5927\u817F\u5185\u4FA7\u5B8C\u5168\u88AB\u9C9C\u8840\u67D3\u7EA2\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u300C\u8FD9\u4E2A\u8D31\u4EBA\u7ADF\u6562\u9020\u53CD\uFF0C\u771F\u662F\u5403\u4E86\u718A\u5FC3\u8C79\u80C6\u3002\u6709\u9053\u662F\u597D\u5403\u96BE\u6D88\u5316\uFF0C\u8BA9\u5979\u5C1D\u5C1D\u864E\u97AD\u7684\u6ECB\u5473\uFF0C\u4E5F\u597D\u628A\u718A\u5FC3\u8C79\u80C6\u6D88\u5316\u4E86\u2026\u2026\u300D\u6155\u5BB9\u9F99\u6402\u4F4F\u7D2B\u73AB\uFF0C\u8D34\u5728\u5979\u8033\u8FB9\u8BF4\uFF1A\u300C\u7B49\u54B1\u4EEC\u64D2\u5230\u4F60\u5E08\u7236\uFF0C\u4F60\u518D\u7ED9\u6311\u4E00\u95F4\uFF0C\u5982\u4F55\uFF1F\u300D</p><p>\u7D2B\u73AB\u6C14\u6068\u4EA4\u52A0\uFF0C\u671D\u4ED6\u811A\u80CC\u4E0A\u91CD\u91CD\u8E29\u4E86\u4E00\u811A\u3002\u6155\u5BB9\u9F99\u54C8\u54C8\u7B11\u9053\uFF1A\u300C\u54E5\u54E5\u6700\u559C\u6B22\u4F60\u7684\u5C0F\u811A\uFF0C\u6765\uFF0C\u518D\u8E29\u4E00\u4E0B\u3002\u300D</p><p>\u7D2B\u73AB\u6C14\u5F97\u4FCF\u8138\u901A\u7EA2\uFF0C\u6068\u6068\u626D\u5934\u770B\u5411\u4E00\u65C1\u3002</p><p>\u59E3\u4E3D\u65E0\u7455\u7684\u8138\u5E9E\u5149\u6DA6\u5982\u7389\uFF0C\u4E00\u98A6\u4E00\u7B11\u65E0\u4E0D\u5A49\u8F6C\u8FF7\u4EBA\uFF0C\u6155\u5BB9\u9F99\u8D8A\u770B\u8D8A\u662F\u5FC3\u75D2\uFF0C\u7A81\u7136\u4FEF\u8EAB\u95EA\u7535\u822C\u5728\u7D2B\u73AB\u5507\u4E0A\u4E00\u543B\u3002</p><p>\u7D2B\u73AB\u62AC\u624B\u6342\u7740\u5C0F\u5634\uFF0C\u5F2F\u7709\u62E7\u7D27\uFF0C\u9ED1\u767D\u5206\u660E\u7684\u4FCF\u76EE\u51E0\u4E4E\u55B7\u706B\u7684\u6012\u89C6\u6155\u5BB9\u9F99\u3002</p><p>\u6155\u5BB9\u9F99\u7B11\u541F\u541F\u8FCE\u4E0A\u5979\u7684\u76EE\u5149\uFF0C\u67D4\u58F0\u9053\uFF1A\u300C\u54E5\u54E5\u4EB2\u4E00\u4E0B\u5C31\u53D1\u8FD9\u4E48\u5927\u7684\u813E\u6C14\uFF0C\u518D\u8FC7\u516B\u5929\uFF0C\u54E5\u54E5\u5360\u4E86\u4F60\u7684\u8EAB\u5B50\uFF0C\u4E00\u5929\u64CD\u4E0A\u4F60\u4E03\u516B\u6B21\u2026\u2026\u300D\u8BF4\u7740\u5F20\u5F00\u53CC\u81C2\u3002</p><p>\u7D2B\u73AB\u8FDE\u5FD9\u9000\u5230\u58C1\u89D2\uFF0C\u751F\u6015\u4ED6\u517D\u6027\u5927\u53D1\uFF0C\u4E5F\u53D8\u6210\u4E00\u5934\u997F\u864E\u3002</p><p>\u6155\u5BB9\u9F99\u4E0D\u613F\u903C\u5979\u592A\u7D27\uFF0C\u4E8E\u662F\u626D\u8FC7\u5934\u6B23\u8D4F\u731B\u864E\u4E0E\u7F8E\u5987\u7684\u4EA4\u5408\u3002\u72F0\u72DE\u7684\u864E\u97AD\u6CBE\u6EE1\u9C9C\u8840\uFF0C\u75AF\u72C2\u5730\u6345\u5F04\u7740\u3002\u6C34\u67D4\u4ED9\u67D4\u7F8E\u7684\u79D8\u5904\uFF0C\u88AB\u6363\u6210\u4E00\u4E2A\u6A21\u7CCA\u7684\u8840\u6D1E\u3002\u864E\u97AD\u523A\u5165\uFF0C\u53D1\u51FA\u6CE5\u6CDE\u7684\u53FD\u53FD\u58F0\u3002</p><p>\u6155\u5BB9\u9F99\u80EF\u4E0B\u94C1\u786C\uFF0C\u65E2\u7136\u8FD8\u4E0D\u80FD\u67D3\u6307\u4EB2\u59B9\uFF0C\u6BCD\u4EB2\u8FD8\u4E0D\u662F\u600E\u4E48\u73A9\u90FD\u53EF\u4EE5\uFF1F\u60F3\u8D77\u767E\u82B1\u89C2\u97F3\u9999\u8F6F\u7684\u8EAB\u4F53\uFF0C\u4ED6\u987F\u65F6\u6B32\u706B\u5347\u817E\uFF0C\u671D\u7D2B\u73AB\u62DB\u4E86\u62DB\u624B\uFF0C\u300C\u8D70\u5427\u3002\u300D</p><p>\u7D2B\u73AB\u5374\u6447\u4E86\u6447\u5934\uFF0C\u8BA4\u771F\u5730\u770B\u7740\u731B\u864E\u7684\u52A8\u4F5C\u3002</p><p>\u6155\u5BB9\u9F99\u6CA1\u60F3\u5230\u5979\u5BF9\u6B64\u8FD9\u4E48\u6709\u5174\u8DA3\uFF0C\u4E0D\u7531\u54D1\u7136\u5931\u7B11\u9053\uFF1A\u300C\u4F60\u8981\u559C\u6B22\uFF0C\u660E\u5929\u518D\u62C9\u6765\u51E0\u4E2A\u5973\u5B50\u8BA9\u4F60\u770B\u4E2A\u75DB\u5FEB\uFF01\u300D</p><p>\u300C\u6211\u5C31\u60F3\u770B\u5979\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u8F6C\u5FF5\u4E00\u60F3\uFF0C\u4FBF\u5DF2\u4E86\u7136\uFF0C\u300C\u5475\u5475\uFF0C\u83AB\u975E\u662F\u56E0\u4E3A\u5979\u4F24\u4E86\u98CE\u5A4A\u5B50\uFF1F\u300D</p><p>\u6709\u4EBA\u66FF\u81EA\u5DF1\u627E\u7406\u7531\uFF0C\u7D2B\u73AB\u4E5F\u4E0D\u5BA2\u6C14\uFF0C\u8FDE\u5FD9\u70B9\u5934\u79F0\u662F\u3002</p><p>\u6155\u5BB9\u9F99\u53C8\u7B49\u7247\u523B\uFF0C\u89C1\u5979\u8FD8\u5174\u81F4\u4E0D\u51CF\uFF0C\u4ECD\u662F\u4E00\u5E45\u5168\u795E\u8D2F\u6CE8\u7684\u6837\u5B50\uFF0C\u4E8E\u662F\u7B11\u9053\uFF1A\u300C\u4F60\u8981\u770B\u81EA\u5DF1\u770B\uFF0C\u54E5\u54E5\u5148\u8D70\u4E86\u3002\u300D</p><p>\u7D2B\u73AB\u521A\u8981\u70B9\u5934\uFF0C\u65CB\u5373\u60F3\u8D77\u4E0E\u731B\u864E\u72EC\u5904\u7684\u53EF\u6015\uFF0C\u8FDE\u5FD9\u8DF3\u8FC7\u6765\u62C9\u4F4F\u7684\u8863\u8896\uFF0C\u695A\u695A\u53EF\u601C\u5730\u6447\u6447\u4ED6\u7684\u624B\u81C2\u3002\u5979\u4E0D\u662F\u4E0D\u60F3\u5F00\u53E3\uFF0C\u5B9E\u5728\u662F\u627E\u4E0D\u5230\u7406\u7531\u80FD\u8BA9\u6155\u5BB9\u9F99\u79BB\u5F00\uFF0C\u968F\u4FBF\u628A\u8001\u864E\u4E5F\u4E00\u5E76\u5E26\u8D70\uFF0C\u597D\u8BA9\u81EA\u5DF1\u80FD\u5B89\u5B89\u7A33\u7A33\u5730\u5BFB\u627E\u5B9D\u85CF\u3002</p><p>\u6155\u5BB9\u9F99\u54C2\u9053\uFF1A\u300C\u80C6\u5B50\u8FD9\u4E48\u5C0F\uFF0C\u8FD8\u770B\u4EC0\u4E48\uFF1F\u8D70\u5427\u3002\u300D</p><p>\u7D2B\u73AB\u6B7B\u6B7B\u6293\u4F4F\u4ED6\u7684\u624B\u81C2\uFF0C\u534A\u664C\u624D\u6324\u51FA\u4E00\u53E5\uFF0C\u300C\u6211\u60F3\u770B\u561B\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u5FC3\u91CC\u4E00\u52A8\uFF0C\u6CA1\u60F3\u5230\u5A07\u6EF4\u6EF4\u7684\u59B9\u59B9\u8EAB\u4E0A\u7ADF\u7136\u4E5F\u5E26\u7740\u4E0E\u81EA\u5DF1\u76F8\u540C\u7684\u55DC\u8650\u8840\u7EDF\uFF0C\u8FD9\u5012\u662F\u4EF6\u597D\u4E8B\u3002</p><p>\u7D2B\u73AB\u6015\u6015\u5730\u5C0F\u58F0\u8BF4\uFF1A\u300C\u4F60\u80FD\u4E0D\u80FD\u8BA9\u5B83\u522B\u54AC\u6211\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u7B11\u9053\uFF1A\u300C\u8FD9\u4F1A\u8001\u864E\u6B63\u73A9\u5F97\u9AD8\u5174\u2014\u2014\u7B49\u5B83\u73A9\u591F\u4E86\u624D\u4F1A\u60F3\u6765\u54AC\u4EBA\uFF0C\u5230\u65F6\u5019\u9000\u5230\u95E8\u5916\u5C31\u884C\u4E86\uFF0C\u5B83\u4E0D\u4F1A\u51FA\u6765\u3002\u300D</p><p>\u7D2B\u73AB\u72B9\u8C6B\u7740\u677E\u5F00\u624B\uFF0C\u8B66\u544A\u9053\uFF1A\u300C\u4E0D\u8BB8\u9A97\u6211\uFF01\u300D</p><p>\u6155\u5BB9\u9F99\u557C\u7B11\u7686\u975E\uFF0C\u300C\u6700\u5FC3\u75BC\u4F60\u7684\u5C31\u662F\u6211\u4E86\uFF0C\u54E5\u54E5\u8FD8\u6CA1\u64CD\u8FC7\u59B9\u59B9\u7684\u5C0F\u5AE9\u5C44\uFF0C\u600E\u4E48\u4F1A\u8BA9\u4F60\u53BB\u5582\u8001\u864E\u5462\uFF1F\u300D</p><p>\u7D2B\u73AB\u4E00\u628A\u5C06\u4ED6\u63A8\u5230\u95E8\u5916\uFF0C\u300C\u53BB\u6B7B\uFF01\u300D</p><p>\u6155\u5BB9\u9F99\u5927\u7B11\u7740\u53BB\u4E86\u3002</p>',61);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
