import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_37" tabindex="-1"><a class="header-anchor" href="#_37" aria-hidden="true">#</a> 37</h1><p>\u7D2B\u73AB\u6DF1\u6DF1\u5438\u4E86\u53E3\u6C14\uFF0C\u4ECE\u58C1\u4E0A\u53D6\u4E0B\u660E\u73E0\uFF0C\u54AC\u7259\u7ED5\u8FC7\u4E0D\u4F4F\u633A\u52A8\u7684\u6591\u6593\u731B\u864E\uFF0C\u4E00\u8FB9\u6CE8\u610F\u8EAB\u65C1\u7684\u52A8\u9759\uFF0C\u4E00\u8FB9\u5728\u58C1\u4E0A\u56DB\u5904\u5BFB\u627E\u3002\u77F3\u58C1\u4E0A\u8840\u8FF9\u6591\u6591\uFF0C\u8FD8\u6CBE\u7740\u661F\u661F\u70B9\u70B9\u7684\u788E\u8089\uFF0C\u53D1\u51FA\u523A\u9F3B\u7684\u6076\u81ED\u3002\u7D2B\u73AB\u76B1\u7D27\u7709\u5934\uFF0C\u634F\u7740\u9F3B\u5B50\u538B\u4E0B\u80F8\u53E3\u7FFB\u7FFB\u6EDA\u6EDA\u7684\u5455\u5410\u611F\uFF0C\u5306\u5306\u770B\u8FC7\u3002</p><p>\u5FFD\u7136\u864E\u5C3E\u4E00\u7529\uFF0C\u7D2B\u73AB\u5FC3\u810F\u987F\u65F6\u8DF3\u5230\u55D3\u5B50\u91CC\uFF0C\u624B\u4E00\u98A4\uFF0C\u660E\u73E0\u6EF4\u6E9C\u6E9C\u6EDA\u5230\u4E00\u65C1\u3002</p><p>\u5BA4\u5185\u4E00\u6697\uFF0C\u7247\u523B\u540E\u53C8\u4EAE\u4E86\u8D77\u6765\u3002\u7D2B\u73AB\u4E00\u624B\u6342\u5634\uFF0C\u4E00\u624B\u6309\u4F4F\u80F8\u53E3\uFF0C\u5413\u5F97\u6C14\u90FD\u5598\u4E0D\u8FC7\u6765\u3002\u534A\u664C\u624D\u770B\u51FA\u660E\u73E0\u539F\u6765\u662F\u6EDA\u5230\u6C34\u67D4\u4ED9\u4E24\u4E73\u4E4B\u95F4\u3002\u731B\u864E\u4E00\u633A\u8170\uFF0C\u6C34\u67D4\u4ED9\u8EAB\u5B50\u62AC\u8D77\uFF0C\u660E\u73E0\u6620\u5728\u7C89\u5AE9\u7684\u53CC\u4E73\u95F4\uFF0C\u5149\u534E\u95EA\u70C1\u3002\u5F85\u864E\u97AD\u62BD\u51FA\uFF0C\u80F8\u4E73\u843D\u4E0B\uFF0C\u5BA4\u5185\u518D\u65E0\u4E00\u4E1D\u5149\u4EAE\u3002</p><p>\u7D2B\u73AB\u54AC\u4F4F\u5634\u5507\uFF0C\u4E00\u8FB9\u5728\u5FC3\u91CC\u4E5E\u6C42\u8001\u864E\u5343\u4E07\u522B\u54AC\u81EA\u5DF1\uFF0C\u4E00\u8FB9\u4FEF\u4E0B\u8EAB\u5B50\uFF0C\u6162\u6162\u4F38\u51FA\u7389\u624B\u3002\u624B\u4E0A\u4E00\u51C9\uFF0C\u4E24\u56E2\u51B0\u51B7\u6ED1\u817B\u7684\u8089\u56E2\u538B\u5728\u8155\u4E0A\u3002\u7D2B\u73AB\u5C4F\u4F4F\u547C\u5438\uFF0C\u7B49\u4E73\u623F\u518D\u5EA6\u62AC\u8D77\u8FDE\u5FD9\u6293\u4F4F\u660E\u73E0\u3002</p><p>\u5C31\u5728\u8FD9\u65F6\uFF0C\u4E00\u76F4\u57CB\u5934\u62BD\u9001\u7684\u731B\u864E\u7A81\u7136\u626D\u5934\u51B2\u5979\u4E00\u58F0\u4F4E\u543C\u3002\u767D\u68EE\u68EE\u7684\u864E\u7259\u5229\u82E5\u5F2F\u5200\uFF0C\u5C16\u786C\u7684\u80E1\u987B\u51E0\u4E4E\u64E6\u5230\u8138\u4E0A\u3002\u7D2B\u73AB\u300C\u545C\u300D\u7684\u54ED\u51FA\u58F0\u6765\uFF0C\u82B3\u540D\u4F20\u64AD\u6C5F\u6E56\u7684\u73AB\u7470\u4ED9\u5B50\u7ADF\u50CF\u4E2A\u5C0F\u5973\u5B69\u822C\u88AB\u5413\u5F97\u773C\u6CEA\u76F4\u6D41\u3002</p><p>\u5E78\u597D\u731B\u864E\u53EA\u543C\u4E86\u4E00\u58F0\uFF0C\u4FBF\u53C8\u95F7\u5934\u633A\u5F04\u3002\u7D2B\u73AB\u60CA\u9B42\u672A\u5B9A\uFF0C\u6293\u7740\u660E\u73E0\u7684\u624B\u4E0D\u4F4F\u98A4\u6296\uFF0C\u4E00\u8FB9\u54ED\u7740\u4E00\u8FB9\u5728\u58C1\u4E0A\u641C\u5DE1\u3002\u7B49\u770B\u5230\u90A3\u4E2A\u56FE\u5F62\uFF0C\u5979\u5FC3\u91CC\u6CA1\u6709\u4E00\u70B9\u6599\u60F3\u4E2D\u7684\u5F00\u5FC3\uFF0C\u53CD\u800C\u5145\u6EE1\u65E0\u540D\u7684\u59D4\u5C48\u3002</p><p>\u5149\u6D01\u7684\u8138\u86CB\u4E0A\u6302\u6EE1\u6676\u83B9\u7684\u6CEA\u73E0\uFF0C\u6D41\u6DCC\u7740\u4E0E\u660E\u73E0\u65E0\u5F02\u7684\u8F89\u5149\u3002\u7D2B\u73AB\u6241\u7740\u5C0F\u5634\u62D4\u51FA\u94F6\u9497\uFF0C\u6C14\u607C\u5730\u671D\u5C0F\u5B54\u5185\u4E00\u523A\u3002\u5F85\u62AC\u8EAB\u7AD9\u8D77\u65F6\uFF0C\u5979\u624D\u53D1\u73B0\u81EA\u5DF1\u4E24\u817F\u7ADF\u4E5F\u5413\u5F97\u53D1\u8F6F\u3002</p><p>\u7D2B\u73AB\u5E72\u8106\u501A\u7740\u77F3\u58C1\u65E0\u58F0\u7684\u5927\u54ED\u8D77\u6765\uFF0C\u75DB\u6068\u81EA\u5DF1\u600E\u4E48\u8FD9\u4E48\u6CA1\u7528\u3002</p><p>\u54ED\u4E86\u4E00\u9635\uFF0C\u7D2B\u73AB\u6E10\u6E10\u56DE\u8FC7\u795E\uFF0C\u4E00\u626D\u5934\uFF0C\u53EA\u89C1\u731B\u864E\u7684\u62BD\u9001\u8D8A\u6765\u8D8A\u5FEB\uFF0C\u5DF2\u7ECF\u6FD2\u6B7B\u7684\u6C34\u67D4\u4ED9\u4E5F\u53C8\u5F00\u59CB\u547B\u541F\u8D77\u6765\u3002\u5979\u60F3\u8D77\u6155\u5BB9\u9F99\u8BF4\u7684\u8BDD\uFF0C\u8FDE\u5FD9\u6323\u624E\u7740\u8DD1\u5230\u5BA4\u5916\u3002</p><p>\u731B\u864E\u4E00\u58F0\u4F4E\u543C\uFF0C\u505C\u4F4F\u52A8\u4F5C\u3002\u7C97\u58EE\u7684\u864E\u97AD\u5728\u6C34\u67D4\u4ED9\u4F53\u5185\u8DF3\u52A8\u7740\u55B7\u51FA\u5927\u56E2\u5927\u56E2\u7684\u6D53\u7CBE\u3002\u7247\u523B\u540E\u864E\u97AD\u4ECE\u6ED1\u51FA\uFF0C\u8F6F\u8F6F\u5782\u4E0B\u3002</p><p>\u6C34\u67D4\u4ED9\u4E0B\u4F53\u8FF7\u4EBA\u7684\u79D8\u5904\u5DF2\u65E0\u590D\u5F80\u65E5\u7684\u67D4\u7F8E\u7CBE\u81F4\u3002\u5A07\u5AE9\u7684\u82B1\u74E3\u51E0\u4E4E\u88AB\u5C3D\u6570\u6495\u788E\uFF0C\u788E\u8089\u822C\u6302\u5728\u80A1\u95F4\uFF0C\u96EA\u767D\u7684\u5706\u81C0\u4E0B\u9732\u51FA\u4E00\u4E2A\u9ED1\u6D1E\u6D1E\u8840\u6DCB\u6DCB\u7684\u5DE8\u5927\u5165\u53E3\uFF0C\u88AB\u8089\u523A\u5250\u788E\u5E26\u51FA\u7684\u5AE9\u8089\u4E00\u7F15\u7F15\u60AC\u5728\u8089\u7A74\u4E0A\uFF0C\u767D\u8272\u7684\u7CBE\u6DB2\u6D51\u7740\u5927\u91CF\u7684\u9C9C\u8840\uFF0C\u6C69\u6C69\u76F4\u6D41\u3002\u67D4\u8F6F\u767D\u7699\u7684\u5A07\u8EAF\u4E0B\uFF0C\u662F\u4E00\u7247\u4EE4\u4EBA\u89E6\u76EE\u60CA\u5FC3\u7684\u9C9C\u7EA2\u8840\u6DB2\u3002</p><p>\u731B\u864E\u79FB\u5F00\u8EAB\u4F53\uFF0C\u5931\u53BB\u652F\u6491\u7684\u6C34\u67D4\u4ED9\u7ACB\u523B\u6251\u5012\u5728\u81EA\u5DF1\u7684\u8840\u6CCA\u4E2D\u3002\u661F\u6708\u6E56\u5973\u957F\u8001\u88AB\u731B\u864E\u5978\u6DEB\u5F97\u4E0D\u7701\u4EBA\u4E8B\uFF0C\u53EA\u6709\u5149\u6ED1\u7684\u7389\u817F\u8FD8\u5FAE\u5FAE\u62BD\u6410\u3002</p><p>\u731B\u864E\u5728\u5BA4\u5185\u76D8\u65CB\u534A\u5468\uFF0C\u62AC\u638C\u5C06\u660F\u8FF7\u7684\u5973\u4F53\u7FFB\u8F6C\u8FC7\u6765\u3002\u6C34\u67D4\u4ED9\u4E24\u53EA\u9AD8\u8038\u7684\u4E73\u623F\u6CBE\u6EE1\u9C9C\u8840\uFF0C\u4E00\u534A\u4E73\u7403\u88AB\u67D3\u5F97\u901A\u7EA2\uFF0C\u53E6\u4E00\u534A\u4E73\u7403\u5374\u767D\u817B\u5982\u6614\u3002</p><p>\u731B\u864E\u4F38\u51FA\u5E03\u6EE1\u8089\u523A\u7684\u820C\u5934\u8214\u4E86\u4E00\u4E0B\uFF0C\u6C34\u67D4\u4ED9\u80A5\u4E73\u4E71\u98A4\uFF0C\u7EC6\u5AE9\u7684\u808C\u80A4\u51E0\u4E4E\u88AB\u950B\u5229\u7684\u8089\u523A\u52FE\u788E\u3002\u5165\u53E3\u7684\u8840\u8165\u6FC0\u53D1\u4E86\u731B\u864E\u7684\u91CE\u6027\uFF0C\u6BD4\u624B\u638C\u8FD8\u5927\u4E0A\u4E00\u4E9B\u7684\u5DE8\u820C\u4E00\u7FFB\uFF0C\u5377\u4F4F\u4E00\u53EA\u4E73\u623F\uFF0C\u63A5\u7740\u5229\u9F7F\u5408\u7D27\u3002\u767D\u817B\u7684\u4E73\u6839\u987F\u65F6\u5728\u9F7F\u95F4\u7C89\u788E\uFF0C\u8840\u8FF9\u8FF8\u6D8C\u3002</p><p>\u6C34\u67D4\u4ED9\u51C4\u58F0\u60E8\u53EB\uFF0C\u4E00\u53EA\u96EA\u4E73\u5DF2\u7ECF\u9F50\u6839\u800C\u65AD\u3002\u6ED1\u5AE9\u7684\u4E73\u7403\u88AB\u731B\u864E\u4E00\u53E3\u541E\u4E0B\uFF0C\u80F8\u524D\u53EA\u5269\u4E0B\u4E00\u4E2A\u9F7F\u75D5\u5B9B\u7136\u7684\u5DE8\u5927\u4F24\u53E3\u3002</p><p>\u731B\u864E\u5C1D\u5F97\u7F8E\u8089\uFF0C\u5934\u9885\u4E00\u4FEF\u4E00\u62AC\uFF0C\u53C8\u5C06\u53E6\u4E00\u53EA\u4E73\u623F\u751F\u751F\u54AC\u6389\u3002\u6C34\u67D4\u4ED9\u5999\u76EE\u77AA\u5F97\u6D51\u5706\uFF0C\u4E00\u76F4\u8F6F\u5782\u7684\u624B\u81C2\u7A81\u7136\u62AC\u8D77\uFF0C\u4F3C\u4E4E\u60F3\u4F38\u5230\u80F8\u524D\uFF0C\u770B\u81EA\u5DF1\u7684\u4E73\u623F\u662F\u4E0D\u662F\u771F\u7684\u88AB\u8001\u864E\u54AC\u6389\u3002\u624B\u6307\u521A\u89E6\u5230\u4E73\u6839\u7834\u788E\u7684\u5AE9\u8089\uFF0C\u4FBF\u67D4\u9888\u4E00\u4FA7\uFF0C\u82B3\u9B42\u6773\u7136\u3002</p><p>\u864E\u820C\u7FFB\u5377\uFF0C\u4E00\u8DEF\u4ECE\u80F8\u524D\u8214\u81F3\u80A1\u95F4\uFF0C\u79D8\u5904\u5C42\u5C42\u53E0\u53E0\u7684\u82B1\u74E3\u8FDE\u540C\u82B1\u8482\u5C3D\u6570\u88AB\u8089\u523A\u522E\u5C3D\uFF0C\u521A\u624D\u4FBF\u5DF2\u8840\u8089\u6A21\u7CCA\u7684\u4E0B\u4F53\uFF0C\u987F\u65F6\u53D8\u6210\u4E00\u7247\u8840\u6DCB\u6DCB\u7684\u788E\u8089\u3002\u864E\u53E3\u5927\u5F20\uFF0C\u54AC\u4F4F\u6C34\u67D4\u4ED9\u4E00\u6761\u5927\u817F\uFF0C\u5229\u9F7F\u4E00\u7D27\uFF0C\u4E30\u6EE1\u7684\u80A2\u4F53\u5E94\u9F7F\u800C\u65AD\u3002\u864E\u53E3\u5916\u9732\u51FA\u4E00\u622A\u767D\u751F\u751F\u7684\u5C0F\u817F\uFF0C\u655B\u6307\u5E73\u5E95\u7684\u7389\u8DB3\u659C\u659C\u5411\u4E0A\u7FD8\u8D77\uFF0C\u968F\u7740\u864E\u9F7F\u7684\u566C\u54AC\u4E00\u7FD8\u4E00\u7FD8\uFF0C\u5B9B\u5982\u6D3B\u7269\u3002</p><p>\u6B8B\u7F3A\u7684\u5973\u4F53\u9759\u9759\u8EBA\u5728\u5E7D\u6697\u7684\u77F3\u5BA4\u4E2D\uFF0C\u4E00\u6761\u5B8C\u6574\u7684\u7389\u817F\u66F2\u7EBF\u73B2\u73D1\uFF0C\u7EC6\u767D\u7684\u808C\u80A4\u4ECE\u811A\u5C16\u76F4\u5230\u817F\u6839\uFF0C\u5149\u6ED1\u7EC6\u817B\uFF0C\u5145\u6EE1\u5973\u6027\u7684\u9B45\u529B\u3002\u4F46\u53E6\u4E00\u6761\u817F\u5374\u8E2A\u5F71\u5168\u65E0\uFF0C\u53EA\u5269\u624B\u638C\u5BBD\u7684\u4E00\u622A\u6B8B\u80A2\u3002\u80A1\u95F4\u67D4\u7F8E\u8FF7\u4EBA\u7684\u6027\u5668\u66F4\u662F\u9762\u76EE\u5168\u975E\uFF0C\u4EFF\u4F5B\u88AB\u94C1\u5237\u5237\u8FC7\u822C\u96F6\u4E71\u4E0D\u582A\u3002</p><p>\u7D2B\u73AB\u624B\u8DB3\u9178\u8F6F\uFF0C\u773C\u7741\u7741\u770B\u7740\u731B\u864E\u7684\u8840\u76C6\u5927\u53E3\u51F6\u6076\u4E00\u4E00\u5F00\u4E00\u5408\uFF0C\u7531\u817F\u53CA\u8179\uFF0C\u4ECE\u8170\u5230\u80F8\uFF0C\u4E00\u70B9\u70B9\u54AC\u788E\u66FC\u5999\u7684\u80A2\u4F53\uFF0C\u8FDE\u9AA8\u5E26\u8089\u5C3D\u6570\u541E\u5165\u809A\u5185\u3002\u6700\u540E\u864E\u5934\u4E00\u626C\uFF0C\u4E00\u9897\u5B64\u96F6\u96F6\u7684\u5934\u9885\u6EDA\u5230\u7D2B\u73AB\u811A\u65C1\u3002\u7A7A\u6D1E\u7684\u773C\u775B\u76F4\u52FE\u52FE\u4E0E\u5979\u5BF9\u89C6\uFF0C\u7EA2\u5507\u626D\u66F2\uFF0C\u7709\u76EE\u95F4\u6D41\u9732\u51FA\u65E0\u8FB9\u65E0\u9645\u7684\u75DB\u82E6\u548C\u6068\u610F\u3002\u7D2B\u73AB\u9999\u8EAF\u4E00\u8F6F\uFF0C\u660F\u5012\u5728\u5730\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u6708\u5149\u4E0B\uFF0C\u78A7\u84DD\u7684\u6E56\u6C34\u6F84\u82E5\u660E\u955C\uFF0C\u606C\u7136\u81EA\u82E5\u5730\u5D4C\u5728\u7FA4\u5CF0\u4E4B\u95F4\uFF0C\u65E0\u58F0\u65E0\u606F\u5730\u5438\u6536\u7740\u5929\u5730\u7CBE\u534E\u3002</p><p>\u738B\u540D\u6CFD\u4F0F\u5728\u6E56\u7554\u957F\u8349\u4E2D\uFF0C\u5FC3\u5934\u63A0\u8FC7\u4E00\u4E1D\u4E0D\u8BE6\u7684\u9884\u611F\u3002\u4ED6\u6084\u6084\u632A\u52A8\u8EAB\u4F53\uFF0C\u6F5C\u5230\u6C34\u4E0B\u7684\u6CE5\u6C99\u4E2D\uFF0C\u53EA\u4F59\u53E3\u9F3B\u9732\u5728\u5916\u9762\u3002</p><p>\u4E2D\u5348\u53D1\u751F\u7684\u4E8B\u60C5\u771F\u662F\u8FC5\u96F7\u4E0D\u53CA\u63A9\u8033\uFF0C\u4ED6\u662F\u6C34\u5802\u5C5E\u4E0B\uFF0C\u5F53\u65F6\u6B63\u5728\u5802\u5185\u58EE\u7740\u80C6\u5B50\u8DDF\u804C\u4EFD\u5728\u5DF1\u4E4B\u4E0A\u7684\u660E\u971C\u8C03\u7B11\u4E24\u53E5\uFF0C\u8FD8\u5403\u4E86\u5979\u4E24\u4E2A\u767D\u773C\u3002\u5FFD\u7136\u6728\u5802\u7684\u4E24\u540D\u9999\u4E3B\u5C31\u51B2\u4E86\u8FDB\u6765\uFF0C\u4E8C\u8BDD\u4E0D\u8BF4\uFF0C\u5148\u628A\u660E\u971C\u6309\u5728\u5730\u4E0A\u4E00\u901A\u66B4\u5978\u3002</p><p>\u77DC\u6301\u7684\u660E\u971C\u88AB\u51E0\u5341\u6761\u6C49\u5B50\u6298\u78E8\u5F97\u6B7B\u53BB\u6D3B\u6765\uFF0C\u6700\u540E\u65B0\u53F6\u9999\u4E3B\u624D\u8BF4\u662F\u5BAB\u4E3B\u8C15\u65E8\uFF0C\u6559\u4E2D\u6240\u6709\u5973\u5B50\u65E0\u8BBA\u804C\u4EFD\u9AD8\u4F4E\u4E00\u5F8B\u964D\u4E3A\u5974\u5A62\u3002</p><p>\u738B\u540D\u6CFD\u770B\u5F97\u8FF7\u8FF7\u7CCA\u7CCA\uFF0C\u534A\u664C\u624D\u542C\u51FA\u6765\u6C34\u957F\u8001\u7ADF\u4F1A\u7A81\u7136\u53CD\u53DB\uFF0C\u88AB\u5BAB\u4E3B\u4E00\u4E3E\u6210\u64D2\u3002</p><p>\u540C\u8C0B\u7684\u7384\u51B0\u9999\u4E3B\u88AB\u6253\u65AD\u56DB\u80A2\u6254\u5728\u6BBF\u5916\u3002\u4ED6\u548C\u672C\u5802\u5E2E\u4F17\u88AB\u8D76\u53BB\u770B\u65F6\uFF0C\u9999\u4E3B\u8FD8\u5728\u4E0D\u4F4F\u54C0\u53F7\uFF0C\u6C42\u5144\u5F1F\u4EEC\u7ED9\u4ED6\u4E2A\u75DB\u5FEB\u3002</p><p>\u800C\u5802\u4E2D\u5411\u6765\u98CE\u5149\u7684\u5341\u51E0\u540D\u5973\u5F1F\u5B50\u5C3D\u6570\u88AB\u5E9F\u53BB\u6B66\u529F\uFF0C\u6253\u8FDB\u77F3\u5BA4\uFF0C\u8BA9\u4EBA\u968F\u4FBF\u73A9\u5F04\uFF0C\u7136\u540E\u4ED6\u4EEC\u8FD9\u4E9B\u6C34\u5802\u5E2E\u4F17\u90FD\u88AB\u9063\u5230\u5916\u56F4\uFF0C\u8BF4\u662F\u6234\u7F6A\u7ACB\u529F\uFF0C\u5176\u5B9E\u8FD8\u4E0D\u662F\u8BA9\u4ED6\u4EEC\u53BB\u9001\u6B7B\u2026\u2026</p><p>\u4E00\u7F15\u4E4C\u4E91\u98D8\u6765\uFF0C\u63A9\u4F4F\u534A\u8F6E\u660E\u6708\uFF0C\u6E05\u8F89\u7ACB\u51CF\uFF0C\u5929\u5730\u77AC\u65F6\u6697\u4E86\u4E0B\u6765\u3002\u738B\u540D\u6CFD\u5B9A\u4E86\u5B9A\u795E\uFF0C\u53C8\u5F80\u6C34\u4E0B\u6C89\u4E86\u51E0\u5206\uFF0C\u53EA\u9732\u51FA\u4E24\u4E2A\u9F3B\u5B54\u3002</p><p>\u5FFD\u7136\u65C1\u8FB9\u4F20\u6765\u4E00\u58F0\u95F7\u54CD\uFF0C\u4ED6\u8BA4\u51FA\u662F\u5802\u4E2D\u8463\u94C1\u62D0\u7684\u58F0\u97F3\uFF0C\u5FC3\u91CC\u546F\u546F\u76F4\u8DF3\uFF0C\u8FDE\u5FD9\u5C4F\u4F4F\u547C\u606F\u6C89\u5230\u6C34\u4E0B\u3002\u8001\u5929\u7237\uFF0C\u661F\u6708\u6E56\u5468\u906D\u6570\u5341\u91CC\uFF0C\u600E\u4E48\u5C31\u8BA9\u81EA\u5DF1\u78B0\u4E0A\u8FD9\u4E2A\u715E\u661F\u4E86\u2026\u2026</p><p>\u6C34\u4E0A\u5FAE\u5FAE\u4E00\u52A8\uFF0C\u4E00\u6839\u624B\u6307\u7C97\u7EC6\u7684\u6811\u679D\u843D\u5728\u6E56\u9762\uFF0C\u63A5\u7740\u4E00\u4E2A\u767D\u8863\u5973\u5B50\u5982\u5F71\u800C\u81F3\uFF0C\u4E00\u8DB3\u8F7B\u8E0F\u7EC6\u679D\uFF0C\u98CE\u4E00\u822C\u63A0\u5411\u6E56\u4E2D\u7684\u5C0F\u5C9B\u3002\u4E4C\u4E91\u6563\u5F00\uFF0C\u5B9B\u5982\u94F6\u971C\u7684\u6708\u5149\u6084\u7136\u6492\u843D\uFF0C\u6620\u51FA\u6E56\u9762\u4E0A\u767D\u8863\u98D8\u98D8\u7684\u96EA\u5CF0\u795E\u5C3C\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u91D1\u5F00\u7532\u638C\u529B\u96C4\u6D51\uFF0C\u96EA\u5CF0\u795E\u5C3C\u6628\u65E5\u4E5F\u53D7\u4E86\u4E0D\u8F7B\u7684\u5185\u4F24\uFF0C\u5979\u8BB0\u6302\u7740\u4E09\u4E2A\u5F92\u5F1F\uFF0C\u53EA\u8C03\u606F\u4E86\u4E00\u65E5\u4E0D\u987E\u4F24\u52BF\u672A\u590D\u4FBF\u53C8\u786C\u95EF\u9B54\u5BAB\u3002\u5979\u5E76\u975E\u4E0D\u81EA\u91CF\u529B\uFF0C\u800C\u662F\u770B\u51FA\u661F\u6708\u6E56\u53EA\u5269\u4E0B\u4E09\u56DB\u540D\u4E00\u6D41\u9AD8\u624B\uFF0C\u53EA\u8981\u80FD\u89C5\u673A\u6740\u6389\u90A3\u4E2A\u7EFF\u888D\u8001\u8005\uFF0C\u9B54\u5BAB\u518D\u65E0\u4EBA\u53EF\u4E0E\u81EA\u5DF1\u6297\u8861\u3002</p><p>\u4E00\u767B\u5CB8\uFF0C\u96EA\u5CF0\u795E\u5C3C\u7ACB\u523B\u75DB\u4E0B\u6740\u624B\uFF0C\u8FD9\u7B49\u5996\u5B7D\u9664\u4E4B\u4E43\u662F\u65E0\u4E0A\u529F\u5FB7\uFF0C\u964D\u5996\u9664\u9B54\u5373\u662F\u6211\u4F5B\u6148\u60B2\uFF01\u5979\u5728\u5C9B\u4E0A\u66F2\u66F2\u6298\u6298\u7ED5\u4E86\u4E00\u4E2A\u5927\u5708\uFF0C\u957F\u5251\u5BD2\u5149\u51DB\u51BD\uFF0C\u6240\u8FC7\u5904\u4E0D\u7559\u4E00\u4E2A\u6D3B\u53E3\u3002\u6700\u540E\u767D\u8863\u4E00\u5C55\uFF0C\u76F4\u6251\u795E\u6BBF\u3002</p><p>\u795E\u6BBF\u5927\u95E8\u6D1E\u5F00\uFF0C\u8FD1\u767E\u540D\u5E2E\u4F17\u5404\u633A\u5175\u5203\u4E25\u9635\u4EE5\u5F85\uFF0C\u89C1\u96EA\u5CF0\u795E\u5C3C\u4E00\u8DEF\u6740\u8FC7\u6765\uFF0C\u5374\u6CA1\u6709\u4E00\u4E2A\u4EBA\u4E0A\u524D\u963B\u6321\uFF0C\u6446\u660E\u4E86\u662F\u8981\u8BF7\u541B\u5165\u74EE\u3002</p><p>\u96EA\u5CF0\u795E\u5C3C\u7F8E\u76EE\u751F\u5BD2\uFF0C\u7EB5\u8EAB\u800C\u4E0B\uFF0C\u8F7B\u98D8\u98D8\u843D\u5728\u6BBF\u524D\u3002\u5982\u6C34\u7684\u957F\u5251\u659C\u63D0\u8EAB\u540E\uFF0C\u6708\u5149\u4E0E\u9C9C\u8840\u5728\u5251\u950B\u4E0A\u6FC0\u8361\u7740\uFF0C\u6DF7\u6210\u4E00\u56E2\uFF0C\u70B9\u70B9\u6EF4\u6EF4\u843D\u5728\u9EDD\u9ED1\u7684\u77F3\u9762\u4E0A\u3002\u767D\u8272\u7684\u5C3C\u5E3D\u4E0B\uFF0C\u6676\u83B9\u7684\u7389\u8138\uFF0C\u56E0\u591A\u5E74\u4FEE\u70BC\u5185\u5BB6\u771F\u6C14\u800C\u900F\u51FA\u4E00\u5C42\u73CD\u73E0\u822C\u7684\u5149\u8292\u3002</p><p>\u300C\u5E08\u7236\uFF01\u300D\u4E00\u4E2A\u8D64\u88F8\u7684\u5973\u5B50\u54ED\u53EB\u7740\u5954\u51FA\u795E\u6BBF\u3002</p><p>\u300C\u7709\u513F\uFF01\u300D\u96EA\u5CF0\u795E\u5C3C\u4E4D\u89C1\u7231\u5F92\uFF0C\u4E0D\u7531\u5931\u58F0\u60CA\u547C\u3002\u7709\u513F\u51FA\u8EAB\u5BCC\u8D35\uFF0C\u4ECE\u672A\u5403\u8FC7\u82E6\u5934\uFF0C\u4E00\u5411\u6E29\u5A49\u67D4\u987A\uFF0C\u53C8\u6709\u6D01\u7656\u2026\u2026\u5728\u8FD9\u91CC\u53EF\u600E\u4E48\u53D7\u5F97\u4E86\uFF1F</p><p>\u7EAA\u7709\u59A9\u521A\u8DD1\u51FA\u4E24\u6B65\uFF0C\u7A81\u7136\u9888\u4E0A\u4E00\u7D27\uFF0C\u88AB\u4E00\u6839\u94C1\u94FE\u5012\u626F\u56DE\u53BB\u3002\u5979\u67D4\u8EAF\u540E\u4EF0\uFF0C\u5A07\u8273\u7684\u4FCF\u8138\u63A9\u5728\u98DE\u6A90\u7684\u9634\u5F71\u4E2D\uFF0C\u53EA\u5269\u4E24\u6761\u5149\u6D01\u7389\u817F\u6323\u624E\u7740\u4E00\u70B9\u70B9\u88AB\u9ED1\u6697\u7684\u6BBF\u95E8\u541E\u566C\u3002</p><p>\u96EA\u767D\u7684\u5C0F\u8179\u4E0B\uFF0C\u8D6B\u7136\u63D2\u7740\u4E00\u679D\u7C97\u9ED1\u7684\u68D2\u72B6\u7269\u4F53\u3002</p><p>\u96EA\u5CF0\u795E\u5C3C\u5FC3\u5982\u5200\u5272\uFF0C\u5389\u5578\u4E00\u58F0\uFF0C\u817E\u8EAB\u800C\u8D77\u3002</p><p>\u6BBF\u95E8\u4E24\u4FA7\u7684\u516D\u540D\u5E2E\u4F17\u4E3E\u8D77\u94C1\u76FE\u6321\u4F4F\u52B2\u6C14\u8FEB\u4EBA\u7684\u957F\u5251\uFF0C\u7136\u540E\u8FC5\u901F\u8BA9\u540E\u9000\u5F00\u3002\u7B49\u795E\u5C3C\u8FDB\u5165\u795E\u6BBF\uFF0C\u5B88\u5728\u6BBF\u5916\u7684\u5E2E\u4F17\u7ACB\u523B\u7ED3\u6210\u9635\u52BF\u5C42\u5C42\u53E0\u53E0\u56F4\u4F4F\u6BBF\u95E8\u3002</p><p>\u795E\u6BBF\u5185\u6CA1\u6709\u4E00\u4E1D\u5149\u4EAE\uFF0C\u5979\u7684\u767D\u8863\u6210\u4E86\u6700\u597D\u7684\u76EE\u6807\uFF0C\u6570\u5341\u679A\u5F62\u5F62\u8272\u8272\u7684\u6697\u5668\u4ECE\u6697\u5904\u6FC0\u5C04\u800C\u6765\uFF0C\u4E00\u7A9D\u8702\u5730\u98DE\u5411\u795E\u5C3C\u3002\u96EA\u5CF0\u795E\u5C3C\u50B2\u7136\u800C\u7ACB\uFF0C\u5FFD\u7136\u767D\u8863\u4E00\u95EA\uFF0C\u7ADF\u5982\u6D41\u661F\u822C\u5C55\u773C\u5373\u901D\uFF0C\u500F\u5FFD\u6D88\u5931\u5F97\u65E0\u5F71\u65E0\u8E2A\u3002</p><p>\u5931\u53BB\u76EE\u6807\u7684\u5E2E\u4F17\u8FDF\u7591\u7740\u505C\u4E0B\u624B\uFF0C\u5F20\u60F6\u56DB\u987E\u3002\u9ED1\u6C89\u6C89\u7684\u795E\u6BBF\u6084\u65E0\u58F0\u606F\uFF0C\u90A3\u4E2A\u767D\u8863\u715E\u661F\u76F4\u5982\u84B8\u53D1\u822C\u4E86\u65E0\u75D5\u8FF9\u3002</p><p>\u6B63\u72B9\u7591\u95F4\uFF0C\u9ED1\u6697\u4E2D\u4F20\u6765\u6155\u5BB9\u9F99\u7684\u58F0\u97F3\uFF1A\u300C\u6881\u4E0A\uFF01\u300D</p><p>\u51E0\u540D\u53CD\u5E94\u5FEB\u7684\u5E2E\u4F17\u7ACB\u65F6\u9192\u609F\u8FC7\u6765\uFF0C\u8FDE\u5FD9\u6263\u4F4F\u6697\u5668\uFF0C\u98DE\u8EAB\u8DC3\u8D77\u3002\u4F46\u6BD4\u4ED6\u4EEC\u53CD\u5E94\u66F4\u5FEB\u7684\u662F\u96EA\u5CF0\u795E\u5C3C\uFF0C\u5979\u542C\u51FA\u6155\u5BB9\u9F99\u7684\u6240\u5728\uFF0C\u7ACB\u5373\u51FA\u624B\uFF0C\u53EA\u89C1\u4E00\u6761\u767D\u5F71\u95EA\u7535\u822C\u4ECE\u6BBF\u9876\u63A0\u4E0B\uFF0C\u76F4\u6251\u6BBF\u89D2\u3002</p><p>\u767D\u5F71\u5904\u7206\u51FA\u4E00\u9635\u52B2\u6C14\u4EA4\u51FB\u7684\u95F7\u54CD\uFF0C\u7247\u523B\u540E\u7A81\u7136\u505C\u6B62\u3002\u53EA\u5269\u4E0B\u7C97\u91CD\u7684\u5598\u606F\u58F0\u3002</p><p>\u6155\u5BB9\u9F99\u541E\u4E0B\u5589\u4E2D\u7684\u8840\u8165\u6C14\uFF0C\u4F4E\u58F0\u9053\uFF1A\u300C\u4E3E\u706B\uFF01\u300D</p>',48);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
