import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_38" tabindex="-1"><a class="header-anchor" href="#_38" aria-hidden="true">#</a> 38</h1><p>\u706B\u628A\u6B21\u7B2C\u4EAE\u8D77\uFF0C\u6620\u51FA\u96EA\u5CF0\u795E\u5C3C\u7684\u767D\u8863\u548C\u6C90\u58F0\u4F20\u7684\u7EFF\u888D\uFF0C\u4E24\u4EBA\u56DB\u624B\u76F8\u4EA4\uFF0C\u6B63\u5728\u6BD4\u62FC\u5185\u529F\u3002\u6155\u5BB9\u9F99\u7D27\u7D27\u62B1\u7740\u7EAA\u7709\u59A9\uFF0C\u8138\u8272\u82CD\u767D\uFF0C\u521A\u624D\u5168\u9760\u5979\u7684\u63A9\u62A4\uFF0C\u624D\u6CA1\u8BA9\u96EA\u5CF0\u795E\u5C3C\u4E00\u638C\u51FB\u4E2D\u8981\u5BB3\u3002</p><p>\u6BBF\u4E2D\u6240\u6709\u5E2E\u4F17\uFF0C\u9664\u4E86\u6C90\u58F0\u4F20\u90FD\u6362\u6210\u4E86\u9ED1\u8863\uFF0C\u8FDE\u970D\u72C2\u7130\u4E5F\u4E0D\u4F8B\u5916\u3002\u4ED6\u4E2D\u5348\u88AB\u6C34\u67D4\u4ED9\u4E00\u62DB\u5236\u4F4F\uFF0C\u5927\u4E22\u9762\u5B50\uFF0C\u6B64\u65F6\u6025\u4E8E\u7ACB\u529F\uFF0C\u53CC\u624B\u4E00\u9519\uFF0C\u706B\u7130\u4EE4\u76F4\u523A\u96EA\u5CF0\u795E\u5C3C\u9888\u4E2D\u3002</p><p>\u4ED6\u4EEC\u53EF\u4ECE\u6765\u4E0D\u8BB2\u4EC0\u4E48\u6C5F\u6E56\u9053\u4E49\uFF0C\u83AB\u8BF4\u96EA\u5CF0\u795E\u5C3C\u8FD9\u4F1A\u513F\u6B63\u5728\u5BF9\u654C\uFF0C\u5C31\u662F\u6B63\u5728\u751F\u5B69\u5B50\u4ED6\u4E5F\u8BE5\u51FA\u624B\u65F6\u5C31\u51FA\u624B\u3002</p><p>\u96EA\u5CF0\u795E\u5C3C\u773C\u4E2D\u5BD2\u8292\u5927\u76DB\uFF0C\u7389\u638C\u4E00\u63A8\uFF0C\u63A5\u7740\u56DE\u624B\u62CD\u5728\u970D\u72C2\u7130\u8155\u4E0A\u3002\u300C\u683C\u300D\u7684\u4E00\u58F0\uFF0C\u970D\u72C2\u7130\u8155\u9AA8\u5C3D\u788E\uFF0C\u540C\u65F6\u96EA\u5CF0\u795E\u5C3C\u4E5F\u55B7\u51FA\u4E00\u53E3\u9C9C\u8840\uFF0C\u98DE\u8EAB\u63A0\u5411\u6BBF\u95E8\u3002\u6C90\u58F0\u4F20\u8138\u4E0A\u8499\u7740\u4E00\u5C42\u68EE\u51B7\u7684\u7EFF\u6C14\uFF0C\u7F13\u7F13\u76D8\u819D\u5750\u4E0B\u3002</p><p>\u6BBF\u5916\u5200\u67AA\u6797\u7ACB\uFF0C\u5C3D\u662F\u957F\u67AA\u91CD\u621F\u5DE8\u65A7\u5927\u9524\u7B49\u7528\u6765\u653B\u575A\u7684\u91CD\u578B\u5175\u5668\uFF0C\u4E00\u65E6\u843D\u5165\u9635\u4E2D\uFF0C\u53EA\u6709\u529B\u6218\u800C\u4EA1\u7684\u7ED3\u5C40\u3002\u96EA\u5CF0\u795E\u5C3C\u786C\u751F\u751F\u683C\u5F00\u4E24\u67C4\u5DE8\u65A7\uFF0C\u4ECE\u6BBF\u95E8\u4E0A\u65B9\u63A0\u51FA\uFF0C\u63A5\u7740\u7FFB\u8EAB\u843D\u5728\u795E\u6BBF\u4E4B\u4E0A\u3002</p><p>\u6155\u5BB9\u9F99\u62A2\u8EAB\u800C\u51FA\uFF0C\u4E00\u628A\u4E3E\u8D77\u7EAA\u7709\u59A9\uFF0C\u9AD8\u58F0\u53EB\u9053\uFF1A\u300C\u8D3C\u5C3C\u770B\u7740\uFF01\u300D\u8BF4\u7740\u63B0\u5F00\u7EAA\u7709\u59A9\u7684\u53CC\u817F\uFF0C\u51C6\u5907\u5F53\u7740\u795E\u5C3C\u7684\u9762\u72E0\u72E0\u73A9\u5F04\u5979\u7684\u7231\u5F92\u3002</p><p>\u96EA\u5CF0\u795E\u5C3C\u604D\u82E5\u672A\u95FB\uFF0C\u63A0\u4E0A\u6BBF\u540E\u5149\u6E9C\u6E9C\u7684\u77F3\u5CF0\uFF0C\u8FF3\u76F4\u98DE\u5954\uFF0C\u4E0D\u591A\u65F6\u8EAB\u5F62\u4E00\u6643\uFF0C\u767D\u8863\u6D88\u5931\u5728\u4E71\u77F3\u4E4B\u4E2D\u3002</p><p>\u6155\u5BB9\u9F99\u9762\u8272\u51B0\u5BD2\uFF0C\u4E00\u62F3\u91CD\u91CD\u51FB\u5728\u7EAA\u7709\u59A9\u7684\u8179\u4E0B\u3002\u7EAA\u7709\u59A9\u95F7\u54FC\u4E00\u58F0\uFF0C\u987F\u65F6\u6655\u4E86\u8FC7\u53BB\u3002\u80A1\u95F4\u7FFB\u5377\u7684\u5AE9\u8089\u8840\u8272\u7686\u65E0\uFF0C\u7247\u523B\u540E\u624D\u6025\u901F\u5145\u8840\u80BF\u80C0\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u7EFF\u888D\u8001\u8005\u529F\u529B\u679C\u7136\u4E0D\u4FD7\uFF0C\u96EA\u5CF0\u795E\u5C3C\u4F24\u4E0A\u52A0\u4F24\uFF0C\u5168\u9760\u7EC3\u81F3\u7B2C\u4E03\u5C42\u7684\u51E4\u51F0\u5B9D\u5178\u52C9\u529B\u652F\u6491\u3002\u5979\u4ECE\u5CF0\u540E\u8DC3\u5165\u6E56\u4E2D\uFF0C\u4E0D\u987E\u4F24\u52BF\u52A0\u5267\uFF0C\u7AED\u529B\u50AC\u53D1\u771F\u6C14\uFF0C\u4ECD\u4EE5\u4E00\u82C7\u6E21\u6C5F\u7684\u7EDD\u9876\u8F7B\u529F\uFF0C\u79BB\u5F00\u661F\u6708\u6E56\u3002</p><p>\u582A\u582A\u4ECE\u6C34\u9762\u63A0\u51FA\u4E24\u91CC\uFF0C\u96EA\u5CF0\u795E\u5C3C\u6C14\u606F\u4E00\u7A92\uFF0C\u5C0F\u817F\u5DF2\u6CA1\u5165\u6E56\u4E2D\u3002\u5979\u4E0D\u6562\u6020\u6162\uFF0C\u7ACB\u5373\u62B1\u5143\u5B88\u4E00\uFF0C\u534A\u8EAB\u6D78\u5728\u6C34\u4E2D\uFF0C\u8C03\u606F\u8D77\u6765\u3002\u706B\u70ED\u7684\u771F\u6C14\u4ECE\u4E39\u7530\u7F15\u7F15\u6563\u51FA\uFF0C\u901A\u8FDE\u6708\u534E\u5730\u6C14\uFF0C\u7F13\u7F13\u4FEE\u590D\u91CD\u521B\u7684\u7ECF\u8109\u3002</p><p>\u6708\u8272\u5982\u6D17\uFF0C\u6E56\u9762\u4E0A\u4EFF\u4F5B\u6F02\u8361\u7740\u4E00\u6735\u6D12\u6EE1\u94F6\u8F89\u7684\u767D\u82B1\uFF0C\u9759\u9759\u5410\u9732\u82AC\u82B3\u3002</p><p>\u4E00\u523B\u949F\u540E\uFF0C\u96EA\u5CF0\u795E\u500F\u7136\u7741\u773C\uFF0C\u4F9D\u5979\u73B0\u5728\u7684\u4F24\u52BF\uFF0C\u5373\u4F7F\u78B0\u4E0A\u5341\u4F59\u4E2A\u666E\u901A\u5E2E\u4F17\uFF0C\u53EA\u6015\u4E5F\u96BE\u4EE5\u8131\u8EAB\u3002\u56E0\u6B64\u53CC\u81C2\u4E00\u5C55\uFF0C\u6084\u65E0\u58F0\u606F\u5730\u671D\u6765\u8DEF\u6E38\u53BB\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u738B\u540D\u6CFD\u5FC3\u91CC\u53EB\u5A18\uFF0C\u8FDE\u5FD9\u53C8\u6F5C\u5230\u6E56\u5E95\uFF0C\u6068\u4E0D\u5F97\u53D8\u6210\u4E00\u53EA\u4E4C\u9F9F\u624D\u597D\u3002\u8FD9\u6B21\u6076\u5C3C\u715E\u661F\u7684\u901F\u5EA6\u6162\u4E86\u8BB8\u591A\uFF0C\u738B\u540D\u6CFD\u4E00\u53E3\u6C14\u65E9\u5DF2\u7528\u5C3D\uFF0C\u5979\u624D\u6E38\u5230\u5CB8\u8FB9\u3002</p><p>\u96EA\u5CF0\u795E\u5C3C\u6E7F\u6DCB\u6DCB\u8D70\u4E0A\u6E56\u5CB8\uFF0C\u7EA2\u5507\u5FAE\u5F20\uFF0C\u53C8\u5410\u51FA\u4E00\u53E3\u9C9C\u8840\u3002\u5979\u8FDE\u5FD9\u7528\u8896\u5B50\u63A5\u4F4F\uFF0C\u514D\u5F97\u7559\u4E0B\u75D5\u8FF9\u3002</p><p>\u738B\u540D\u6CFD\u7B49\u5979\u8D70\u5165\u6811\u6797\uFF0C\u8D76\u7D27\u4F38\u5934\u91CD\u91CD\u5410\u4E86\u53E3\u6C14\uFF0C\u8111\u5B50\u98DE\u5FEB\u7684\u65CB\u8F6C\u8D77\u6765\uFF0C\u300C\u8D3C\u5C3C\u5C45\u7136\u53D7\u4E86\u91CD\u4F24\uFF0C\u771F\u662F\u5929\u8D50\u826F\u673A\uFF01\u5982\u679C\u80FD\u64D2\u4F4F\u5979\u2026\u2026\u9760\uFF0C\u5C31\u7B97\u4EBA\u5BB6\u53D7\u4E86\u91CD\u4F24\uFF0C\u5269\u4E0B\u90A3\u70B9\u5DE5\u592B\u60F3\u6740\u81EA\u5DF1\u4E5F\u8DDF\u634F\u6B7B\u4E00\u53EA\u8682\u8681\u5DEE\u4E0D\u591A\u3002\u8FD8\u662F\u6743\u5F53\u6CA1\u770B\u89C1\uFF0C\u5B89\u5B89\u5206\u5206\u5F53\u6211\u7684\u5C0F\u55BD\u7F57\u597D\u4E86\u3002\u300D</p><p>\u300C\u5982\u679C\u80FD\u64D2\u4F4F\u5979\u2026\u2026\u300D\u738B\u540D\u6CFD\u7728\u7728\u773C\uFF0C\u5FCD\u4E0D\u4F4F\u53C8\u5E7B\u60F3\u8D77\u6765\u3002\u300C\u80FD\u64D2\u4F4F\u96EA\u5CF0\u795E\u5C3C\uFF0C\u8D77\u7801\u80FD\u6DF7\u4E2A\u9999\u4E3B\uFF0C\u8BF4\u4E0D\u5B9A\u8FD8\u80FD\u5F53\u4E0A\u957F\u8001\u5462\u2014\u2014\u5C31\u7B97\u53EA\u662F\u9999\u4E3B\uFF0C\u5230\u65F6\u5C5E\u4E0B\u7684\u5341\u4E8C\u5E2E\u4F1A\u5C31\u53EF\u4EE5\u4E3A\u6240\u6B32\u4E3A\u4E86\u2026\u2026\u300D\u738B\u540D\u6CFD\u8D8A\u60F3\u8D8A\u662F\u5174\u594B\uFF0C\u300C\u4E0D\u884C\u5C31\u8DDF\u5728\u540E\u9762\uFF01\u80FD\u627E\u5230\u6076\u5C3C\u85CF\u8EAB\u7684\u5730\u65B9\uFF0C\u4E5F\u662F\u5927\u529F\u4E00\u4EF6\u554A\uFF01\u300D</p><p>\u738B\u540D\u6CFD\u5FC3\u4E00\u6A2A\uFF0C\u4ECE\u6C34\u91CC\u94BB\u4E86\u51FA\u6765\uFF0C\u6CBF\u7740\u6C34\u8FF9\u4E00\u8DEF\u8FFD\u5165\u6797\u4E2D\u3002</p><p>\u96EA\u5CF0\u795E\u5C3C\u81EA\u77E5\u8EAB\u5728\u9669\u5730\uFF0C\u65E0\u5948\u4F24\u52BF\u592A\u91CD\uFF0C\u60F3\u5FEB\u4E5F\u5FEB\u4E0D\u8D77\u6765\u3002\u5979\u5728\u6797\u4E2D\u7A7F\u884C\u5341\u4F59\u91CC\uFF0C\u6700\u540E\u5728\u4E00\u6761\u5C71\u6DA7\u524D\u505C\u4E0B\u811A\u6B65\uFF0C\u770B\u770B\u56DB\u5468\uFF0C\u7136\u540E\u98DE\u8EAB\u800C\u4E0B\u3002</p><p>\u300C\u5E08\u7236\uFF0C\u4F60\u53D7\u4F24\u4E86\uFF1F\u300D\u6797\u9999\u8FDC\u542C\u51FA\u811A\u6B65\u58F0\u6709\u5F02\uFF0C\u8FDE\u5FD9\u6478\u7D22\u7740\u7AD9\u8D77\u6765\u3002</p><p>\u300C\u4E0D\u59A8\u3002\u300D\u96EA\u5CF0\u795E\u5C3C\u94BB\u8FDB\u9690\u5BC6\u7684\u77F3\u6D1E\uFF0C\u76D8\u819D\u5750\u4E0B\u3002</p><p>\u6797\u9999\u8FDC\u4E0D\u6562\u51FA\u58F0\u60CA\u6270\uFF0C\u53EA\u597D\u6EE1\u5FC3\u7126\u707C\u5730\u5B88\u5728\u4E00\u8FB9\u3002</p><p>\u4E00\u4E2A\u65F6\u8FB0\u540E\uFF0C\u96EA\u5CF0\u795E\u5C3C\u54B3\u51FA\u4E00\u53E3\u7D2B\u9ED1\u7684\u8840\u8FF9\uFF0C\u8212\u4E86\u53E3\u6C14\uFF0C\u7F13\u7F13\u9053\uFF1A\u300C\u90A3\u4E2A\u7EFF\u888D\u8001\u8005\u6B66\u529F\u9AD8\u5F3A\uFF0C\u4F60\u77E5\u9053\u4ED6\u662F\u4EC0\u4E48\u4EBA\u5417\uFF1F\u300D</p><p>\u6797\u9999\u8FDC\u601D\u7D22\u9053\uFF1A\u300C\u5E94\u8BE5\u662F\u6728\u5802\u957F\u8001\u6C90\u58F0\u4F20\uFF0C\u7EAA\u5E08\u59B9\u548C\u5C0F\u5E08\u59B9\u8DDF\u4ED6\u4EA4\u8FC7\u624B\u3002\u7EAA\u5E08\u59B9\u66FE\u8BF4\u5355\u6253\u72EC\u6597\u96BE\u6321\u767E\u62DB\u3002\u300D</p><p>\u96EA\u5CF0\u795E\u5C3C\u60F3\u8D77\u7EAA\u7709\u59A9\u53D7\u8FB1\u7684\u6A21\u6837\u4E0D\u7531\u5FC3\u5934\u523A\u75BC\uFF0C\u4E00\u638C\u51FB\u5728\u77F3\u58C1\u4E0A\uFF0C\u6068\u6068\u9053\uFF1A\u300C\u8FD9\u4E9B\u5996\u5B7D\u5982\u6B64\u6076\u6BD2\uFF01\u300D</p><p>\u6797\u9999\u8FDC\u51C4\u7136\u6CEA\u4E0B\uFF0C\u8DEA\u5728\u795E\u5C3C\u8EAB\u524D\uFF0C\u98A4\u58F0\u9053\uFF1A\u300C\u5F92\u513F\u53D7\u6B64\u5947\u8FB1\uFF0C\u518D\u65E0\u8138\u6D3B\u5728\u4E16\u4E0A\u2026\u2026\u300D</p><p>\u96EA\u5CF0\u795E\u5C3C\u5389\u58F0\u9053\uFF1A\u300C\u592B\u4EC7\u672A\u62A5\uFF0C\u5DF1\u803B\u672A\u96EA\uFF0C\u4F60\u5C31\u8981\u5BFB\u6B7B\u5417\uFF01\u300D</p><p>\u6797\u9999\u8FDC\u54FD\u54BD\u7740\u8BF4\uFF1A\u300C\u80DC\u54E5\u2026\u2026\u5F92\u513F\u4E5F\u4E0D\u60F3\u6D3B\u4E86\uFF0C\u53EA\u662F\u2026\u2026\u53EA\u662F\u2026\u2026\u6211\u6709\u4EC0\u4E48\u9762\u76EE\u53BB\u89C1\u80DC\u54E5\u2026\u2026\u6C42\u5E08\u7236\u5C06\u6211\u4FE9\u9694\u5C71\u800C\u846C\u2026\u2026\u300D</p><p>\u795E\u5C3C\u7709\u5934\u6311\u8D77\uFF0C\u5389\u559D\u9053\uFF1A\u300C\u6B66\u529F\u88AB\u5E9F\u8FD8\u53EF\u518D\u7EC3\uFF01\u8EAB\u8D1F\u5927\u4EC7\u81EA\u5F53\u8840\u503A\u8840\u507F\uFF0C\u624B\u5203\u4EC7\u654C\uFF01\u8F7B\u751F\u4EE5\u6C42\u89E3\u8131\uFF0C\u53EA\u80FD\u5815\u5165\u8F6E\u56DE\uFF01\u5982\u6B64\u54ED\u54ED\u557C\u557C\uFF0C\u600E\u7B97\u5F97\u6211\u98D8\u6885\u5CF0\u5F1F\u5B50\uFF01\u300D</p><p>\u6797\u9999\u8FDC\u653E\u58F0\u75DB\u54ED\u3002</p><p>\u7231\u5F92\u54C0\u60CB\u6B32\u7EDD\u7684\u51C4\u695A\uFF0C\u4F7F\u795E\u5C3C\u9F3B\u4E2D\u4E5F\u5FAE\u5FAE\u53D1\u9178\uFF0C\u5979\u6E29\u8A00\u9053\uFF1A\u300C\u4E16\u95F4\u8BF8\u822C\u82E6\u695A\uFF0C\u65E0\u975E\u68A6\u5E7B\u6CE1\u5F71\u3002\u4E0D\u8981\u591A\u60F3\u4E86\u3002\u300D</p><p>\u5E08\u5F92\u4FE9\u76F8\u62E5\u65E0\u8A00\u3002\u5929\u8272\u6E10\u6E10\u53D1\u767D\uFF0C\u96EA\u5CF0\u795E\u5C3C\u64E6\u53BB\u7231\u5F92\u8138\u4E0A\u7684\u6CEA\u6C34\uFF0C\u957F\u8EAB\u800C\u8D77\u3002</p><p>\u6797\u9999\u8FDC\u60CA\u9053\uFF1A\u300C\u5E08\u7236\uFF0C\u4F60\u53BB\u54EA\u91CC\uFF1F\u300D</p><p>\u300C\u6C90\u58F0\u4F20\u5185\u529F\u6DF1\u539A\uFF0C\u4E09\u65E5\u5185\u5FC5\u53EF\u590D\u5143\u3002\u6211\u8981\u518D\u53BB\u4E00\u8D9F\u661F\u6708\u6E56\u3002\u300D</p><p>\u300C\u5E08\u7236\uFF0C\u4F60\u7684\u4F24\u52BF\u2026\u2026\u300D</p><p>\u96EA\u5CF0\u795E\u5C3C\u62CD\u62CD\u5979\u7684\u624B\uFF0C\u300C\u5E08\u7236\u53EA\u662F\u53BB\u6E56\u8FB9\u5C06\u5916\u56F4\u7684\u5996\u5B7D\u9664\u5C3D\uFF0C\u5348\u65F6\u4FBF\u53EF\u8D76\u56DE\u3002\u300D</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u5F85\u96EA\u5CF0\u795E\u5C3C\u53BB\u8FDC\uFF0C\u738B\u540D\u6CFD\u4ECE\u6D1E\u540E\u817E\u8EAB\u8DC3\u4E0B\uFF0C\u4E0D\u6210\u60F3\u5D16\u4E0A\u4E00\u5757\u77F3\u5934\u4F38\u5F97\u592A\u957F\uFF0C\u5728\u80CC\u4E0A\u4E00\u649E\uFF0C\u8EAB\u4F53\u7ACB\u523B\u6A2A\u4E86\u8FC7\u6765\uFF0C\u300C\u84EC\u300D\u7684\u4E00\u58F0\u8DB4\u5728\u5730\u4E0A\uFF0C\u80F8\u8179\u7740\u5730\uFF0C\u6454\u5F97\u72FC\u72C8\u4E0D\u582A\u3002</p><p>\u773C\u524D\u5BD2\u5149\u4E00\u95EA\uFF0C\u4E00\u67C4\u957F\u5251\u62B5\u5728\u8EAB\u540E\uFF0C\u738B\u540D\u6CFD\u6697\u53EB\u300C\u6211\u547D\u4F11\u77E3\u300D\uFF0C\u540E\u6094\u4E0D\u8FED\u3002</p><p>\u53EA\u542C\u6797\u9999\u8FDC\u51B7\u559D\u9053\uFF1A\u300C\u4EC0\u4E48\u4EBA\uFF01\u300D</p><p>\u738B\u540D\u6CFD\u60F3\u8D77\u5979\u53CC\u76EE\u5931\u660E\uFF0C\u7ACB\u523B\u53C8\u5347\u8D77\u5E0C\u671B\uFF0C\u8FDE\u5FD9\u53EB\u9053\uFF1A\u300C\u6211\u2026\u2026\u6211\u662F\u5C71\u4E2D\u6A35\u592B\u2026\u2026\u6253\u67F4\u4E0D\u5C0F\u5FC3\u6454\u4E86\u4E0B\u6765\uFF0C\u6C42\u59D1\u5A18\u6551\u547D\u2026\u2026\u300D</p><p>\u6797\u9999\u8FDC\u534A\u4FE1\u534A\u7591\uFF0C\u4F46\u542C\u4ED6\u6454\u5F97\u5982\u6B64\u72FC\u72C8\uFF0C\u5012\u4E0D\u50CF\u661F\u6708\u6E56\u9AD8\u624B\uFF0C\u4E8E\u662F\u7F13\u7F13\u6536\u8D77\u957F\u5251\u3002</p><p>\u738B\u540D\u6CFD\u5FC3\u4E0B\u5927\u559C\uFF0C\u8FDE\u5FD9\u6323\u624E\u7740\u60F3\u722C\u8D77\u6765\u3002</p><p>\u6797\u9999\u8FDC\u957F\u5251\u4E00\u6325\uFF0C\u300C\u522B\u52A8\uFF01\u300D\u5979\u7EC8\u7A76\u662F\u4E0D\u653E\u5FC3\uFF0C\u8FD8\u662F\u5148\u6263\u4E0B\u4ED6\uFF0C\u7B49\u5E08\u7236\u56DE\u6765\u518D\u8BF4\u3002</p><p>\u738B\u540D\u6CFD\u8FDE\u8FDE\u53EB\u82E6\uFF0C\u8BD5\u7740\u8FD0\u4E86\u8FD0\u6C14\uFF0C\u80CC\u4E0A\u867D\u7136\u5267\u75DB\uFF0C\u597D\u5728\u7ECF\u8109\u5374\u662F\u65E0\u6059\u3002\u4ED6\u6162\u6162\u51DD\u805A\u529F\u529B\uFF0C\u51C6\u5907\u66B4\u8D77\u53D1\u96BE\u3002</p><p>\u6797\u9999\u8FDC\u7693\u8155\u4E00\u6296\uFF0C\u957F\u5251\u6311\u51FA\u4E24\u4E2A\u5251\u82B1\uFF0C\u62DB\u5F0F\u5DE7\u5999\u7F8E\u89C2\u3002\u738B\u540D\u6CFD\u5FC3\u91CC\u4E00\u51C9\uFF0C\u6CA1\u60F3\u5230\u8FD9\u5A4A\u5B50\u6B66\u529F\u53C8\u6062\u590D\u4E86\u2026\u2026</p><p>\u5176\u5B9E\u6797\u9999\u8FDC\u7684\u5251\u6CD5\u53EA\u662F\u5F92\u5177\u5176\u8868\uFF0C\u4F53\u5185\u7684\u771F\u6C14\u4EC5\u5269\u4E0B\u8584\u8584\u4E00\u5C42\u3002\u4F46\u5979\u4E45\u7ECF\u6218\u9635\uFF0C\u81EA\u7136\u800C\u7136\u4FBF\u6709\u9AD8\u624B\u6C14\u6D3E\u3002</p><p>\u738B\u540D\u6CFD\u624B\u811A\u4E0D\u6562\u518D\u52A8\uFF0C\u773C\u73E0\u5374\u6EF4\u6E9C\u6E9C\u5728\u5979\u8EAB\u4E0A\u56DB\u4E0B\u6253\u91CF\u3002\u8FD8\u771F\u770B\u4E0D\u51FA\u6765\uFF0C\u5F53\u65E5\u5265\u5149\u4E86\u8FDE\u6761\u72D7\u90FD\u4E0D\u5982\uFF0C\u53EA\u987E\u7740\u64CD\u5979\u7684\u5C44\u4E86\uFF0C\u957F\u4EC0\u4E48\u6837\u90FD\u6CA1\u5728\u610F\u3002\u8FD9\u4F1A\u513F\u7A7F\u4E0A\u4EF6\u5355\u8863\uFF0C\u770B\u7740\u8FD8\u771F\u662F\u4E2A\u5927\u7F8E\u4EBA\u513F\u5462\u3002\u9F3B\u5B50\u662F\u9F3B\u5B50\u773C\u662F\u773C\u7684\uFF0C\u90A3\u5F20\u5C0F\u5634\u7EA2\u5AE9\u5AE9\u6C34\u7075\u7075\u2014\u2014\u662F\u4E0D\u662F\u559D\u8001\u5B50\u7684\u9633\u7CBE\u559D\u51FA\u6765\u7684\uFF1F</p><p>\u6B63\u80E1\u601D\u4E71\u60F3\u95F4\uFF0C\u5FFD\u7136\u8FDC\u5904\u4F20\u6765\u9635\u9635\u547C\u558A\uFF0C\u8D8A\u6765\u8D8A\u8FD1\u3002</p><p>\u6797\u9999\u8FDC\u5FC3\u91CC\u4E00\u7D27\uFF0C\u624B\u91CC\u7684\u957F\u5251\u8BD5\u7740\u5411\u524D\u4F38\u51FA\uFF0C\u90A3\u6A35\u592B\u4E00\u58F0\u60E8\u53EB\uFF0C\u5979\u8FDE\u5FD9\u505C\u624B\uFF0C\u5389\u58F0\u9053\uFF1A\u300C\u6765\u7684\u662F\u4EC0\u4E48\u4EBA\uFF01\u300D</p><p>\u738B\u540D\u6CFD\u62AC\u773C\u4E00\u770B\uFF0C\u539F\u6765\u662F\u5C71\u4E2D\u730E\u6237\u7ED3\u961F\u8D76\u5C71\uFF0C\u4ECE\u5C71\u6DA7\u8DEF\u8FC7\u3002\u4ED6\u773C\u73E0\u4E00\u8F6C\uFF0C\u8BF4\u9053\uFF1A\u300C\u662F\u4E00\u7FA4\u5F6A\u5F62\u5927\u6C49\uFF0C\u62FF\u7740\u5200\u67AA\u671D\u8FD9\u8FB9\u6765\u4E86\u2026\u2026\u597D\u50CF\u662F\u4E00\u7FA4\u571F\u532A\u2026\u2026\u300D</p><p>\u6797\u9999\u8FDC\u795E\u60C5\u5927\u53D8\uFF0C\u7D27\u5F20\u5730\u5598\u4E86\u53E3\u6C14\uFF0C\u300C\u8FDB\u6765\uFF01\u300D</p><p>\u738B\u540D\u6CFD\u54ED\u4E27\u7740\u8138\u8BF4\uFF1A\u300C\u6709\u51E0\u4E2A\u4EBA\u8DF3\u4E0B\u6765\u4E86\uFF0C\u6CBF\u7740\u5C71\u6C9F\u641C\u5462\u2026\u2026\u300D</p><p>\u770B\u6765\u5C71\u6D1E\u4E5F\u65E0\u5BC6\u53EF\u5B88\uFF0C\u6797\u9999\u8FDC\u601D\u7D22\u7247\u523B\uFF0C\u95EE\u9053\uFF1A\u300C\u4F60\u77E5\u9053\u5C71\u91CC\u7684\u8DEF\u5417\uFF1F\u300D</p><p>\u300C\u77E5\u9053\u77E5\u9053\u2026\u2026\u300D\u738B\u540D\u6CFD\u4E00\u8FED\u58F0\u5730\u8BF4\u3002</p><p>\u6797\u9999\u8FDC\u4E00\u54AC\u7259\uFF0C\u300C\u5E26\u6211\u79BB\u5F00\u8FD9\u91CC\uFF01\u300D</p><p>\u738B\u540D\u6CFD\u5FC3\u82B1\u6012\u653E\uFF0C\u8FDE\u5FD9\u722C\u8D77\u6765\u8BF4\u9053\uFF1A\u300C\u8FD9\u8FB9\u8D70\u3002\u300D</p><p>\u6797\u9999\u8FDC\u89C1\u8FD9\u4EBA\u5728\u81EA\u5DF1\u5251\u4E0B\u8EBA\u4E86\u8FD1\u4E00\u65F6\u8FB0\u4E5F\u6CA1\u6562\u52A8\u4F5C\uFF0C\u5012\u662F\u6709\u4E5D\u5206\u76F8\u4FE1\u4ED6\u662F\u5C71\u4E2D\u6A35\u592B\u3002\u5979\u610F\u6B32\u9707\u6151\u6B64\u4EBA\uFF0C\u300C\u53ED\u300D\u7684\u4E00\u58F0\u957F\u5251\u5165\u9798\uFF0C\u6BD4\u660E\u773C\u4EBA\u8FD8\u5229\u7D22\u5F97\u591A\u3002\u7136\u540E\u63E1\u4F4F\u5251\u67C4\uFF0C\u5C06\u9798\u8EAB\u9012\u5230\u6A35\u592B\u624B\u4E2D\u3002</p><p>\u9AD8\u9AD8\u4F4E\u4F4E\u8D70\u4E86\u534A\u4E2A\u65F6\u8FB0\uFF0C\u547C\u558A\u58F0\u6E10\u6E10\u8FDC\u53BB\u3002\u6797\u9999\u8FDC\u505C\u4E0B\u811A\u6B65\uFF0C\u4FA7\u8033\u503E\u542C\u7247\u523B\uFF0C\u4E0D\u7531\u677E\u4E86\u53E3\u6C14\uFF0C\u300C\u56DE\u53BB\u5427\u3002\u300D</p><p>\u300C\u54CE\u300D\u6A35\u592B\u8001\u8001\u5B9E\u5B9E\u5730\u7B54\u5E94\u4E00\u58F0\uFF0C\u8F6C\u8EAB\u671D\u6765\u8DEF\u8D70\u53BB\u3002</p><p>\u4F46\u8FD9\u4E00\u8D70\uFF0C\u76F4\u8D70\u4E86\u6574\u4E2A\u65F6\u8FB0\u4E5F\u6CA1\u56DE\u5230\u5C71\u6D1E\u3002\u6797\u9999\u8FDC\u5FC3\u4E0B\u8D77\u7591\uFF0C\u4E00\u628A\u63B0\u5F00\u9798\u4E0A\u7684\u5361\u7C27\uFF0C\u6296\u624B\u62D4\u51FA\u957F\u5251\uFF0C\u5389\u559D\u9053\uFF1A\u300C\u4F60\u671D\u54EA\u513F\u8D70\uFF01\u300D</p><p>\u6A35\u592B\u98A4\u58F0\u9053\uFF1A\u300C\u671D\u521A\u624D\u6765\u7684\u5730\u65B9\u2026\u2026\u300D</p><p>\u300C\u600E\u4E48\u8D70\u4E86\u8FD9\u4E48\u957F\u65F6\u95F4\uFF1F\u300D</p><p>\u90A3\u6A35\u592B\u55EB\u5685\u534A\u5929\uFF0C\u5E26\u7740\u54ED\u8154\u8BF4\uFF1A\u300C\u59D1\u5A18\u9976\u547D\u2026\u2026\u5C0F\u7684\u2026\u2026\u5C0F\u7684\u4E0D\u8BA4\u8BC6\u8DEF\u2026\u2026\u300D</p><p>\u6797\u9999\u8FDC\u6C14\u5F97\u5410\u8840\uFF0C\u300C\u4F60\u521A\u624D\u600E\u4E48\u8BF4\u8BA4\u8BC6\uFF1F\u300D</p><p>\u300C\u6211\u4EE5\u4E3A\u59D1\u5A18\u662F\u95EE\u4E0B\u5C71\u7684\u8DEF\u2026\u2026\u300D</p><p>\u6797\u9999\u8FDC\u6C89\u9ED8\u534A\u5929\uFF0C\u8FD9\u4F1A\u513F\u5728\u5C71\u91CC\u7ED5\u4E86\u5C06\u8FD1\u4E24\u4E2A\u65F6\u8FB0\uFF0C\u83AB\u8BF4\u81EA\u5DF1\u53CC\u76EE\u5931\u660E\uFF0C\u5C31\u662F\u5E73\u5E38\u4E5F\u96BE\u4EE5\u5728\u7FA4\u5CF0\u4E2D\u627E\u5230\u90A3\u4E2A\u9690\u5BC6\u7684\u5C71\u6D1E\u3002\u5982\u53BB\u5BFB\u627E\u5C71\u4E2D\u4F4F\u6237\uFF0C\u81EA\u5DF1\u53C8\u6CA1\u6CD5\u8BF4\u6E05\u5C71\u6D1E\u7684\u6A21\u6837\u2026\u2026</p><p>\u5979\u60F3\u6765\u60F3\u53BB\u4E5F\u6CA1\u4E2A\u4E3B\u610F\uFF0C\u5FC3\u4E00\u6A2A\u9053\uFF1A\u300C\u4F60\u65E2\u7136\u77E5\u9053\u4E0B\u5C71\u7684\u9053\u8DEF\uFF0C\u90A3\u5C31\u5E26\u6211\u4E0B\u5C71\u3002\u5982\u679C\u80FD\u9001\u6211\u5230\u5DDD\u5357\u4E34\u909B\uFF0C\u6211\u5FC5\u6709\u91CD\u8C22\u3002\u300D</p><p>\u738B\u540D\u6CFD\u5FC3\u91CC\u72DE\u7B11\u7740\u8FDE\u58F0\u7B54\u5E94\u3002</p>',71);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
