import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_40" tabindex="-1"><a class="header-anchor" href="#_40" aria-hidden="true">#</a> 40</h1><p>\u6797\u9999\u8FDC\u76EE\u4E0D\u89C1\u7269\uFF0C\u4E00\u8DEF\u8DCC\u8DCC\u649E\u649E\uFF0C\u8D70\u5F97\u4E24\u817F\u9178\u75DB\uFF0C\u4ECD\u54AC\u7259\u575A\u6301\u3002\u5979\u6B66\u529F\u88AB\u5E9F\uFF0C\u4F53\u529B\u53EA\u5982\u5BFB\u5E38\u5973\u5B50\u3002\u90A3\u4E2A\u6A35\u592B\u5012\u662F\u5728\u6B64\u751F\u6D3B\u591A\u5E74\uFF0C\u8D70\u8D77\u5C71\u8DEF\u6BEB\u4E0D\u5403\u529B\u3002</p><p>\u8033\u8FB9\u54CD\u8D77\u4E00\u9635\u6F7A\u6F7A\u6C34\u58F0\uFF0C\u6A35\u592B\u505C\u4E0B\u811A\u6B65\uFF0C\u300C\u524D\u5934\u662F\u6761\u5C71\u6EAA\uFF0C\u6211\u80CC\u59D1\u5A18\u8FC7\u53BB\u5427\u3002\u300D</p><p>\u6797\u9999\u8FDC\u60F3\u90FD\u4E0D\u60F3\uFF0C\u7ACB\u5373\u6447\u5934\u62D2\u7EDD\u3002</p><p>\u6EAA\u6C34\u4E0D\u8FC7\u4E24\u4E08\u6765\u5BBD\uFF0C\u6DF1\u7EA6\u4E24\u5C3A\uFF0C\u6E05\u6F88\u89C1\u5E95\uFF0C\u4E0A\u9762\u8FD8\u67B6\u7740\u4E00\u6839\u4E0A\u90E8\u524A\u5E73\u7684\u5DE8\u6728\u3002</p><p>\u738B\u540D\u6CFD\u5374\u5728\u79BB\u6728\u6865\u56DB\u4E94\u4E08\u7684\u5730\u65B9\u4E0B\u4E86\u6C34\uFF0C\u4E13\u95E8\u6311\u4E71\u77F3\u6700\u591A\u7684\u5730\u65B9\u62C9\u7740\u6797\u9999\u8FDC\u8FC7\u6EAA\u3002</p><p>\u6CA1\u8D70\u4E0A\u4E24\u6B65\uFF0C\u6797\u9999\u8FDC\u811A\u4E0B\u4E00\u6ED1\uFF0C\u6454\u5012\u6C34\u4E2D\u3002\u5979\u6454\u8131\u6A35\u592B\u4F38\u6765\u7684\u624B\u638C\uFF0C\u7528\u5251\u9798\u6491\u7740\u652F\u8D77\u8EAB\u5B50\u3002\u5979\u8EAB\u4E0A\u7A7F\u7740\u795E\u5C3C\u7684\u7F01\u8863\uFF0C\u6CBE\u6C34\u4E4B\u540E\u8863\u895F\u4F4E\u5782\uFF0C\u9732\u51FA\u4E00\u5927\u534A\u767D\u5AE9\u7684\u80F8\u812F\uFF0C\u4E00\u53EA\u978B\u5B50\u4E5F\u987A\u6C34\u6F02\u8D70\u3002</p><p>\u4E00\u8DEF\u8D70\u6765\uFF0C\u738B\u540D\u6CFD\u5DF2\u770B\u51FA\u5979\u5185\u529B\u7686\u65E0\uFF0C\u82E5\u8981\u64D2\u4E0B\u5979\u6613\u5982\u53CD\u638C\uFF0C\u4F46\u5802\u5802\u5BD2\u6708\u5200\u88AB\u81EA\u5DF1\u800D\u7334\u4F3C\u7684\u9A97\u5F97\u56E2\u56E2\u8F6C\uFF0C\u60F3\u60F3\u5C31\u4ED6\u5988\u7684\u723D\uFF01</p><p>\u6797\u9999\u8FDC\u4ECE\u6C34\u6D41\u7684\u65B9\u5411\u8FA8\u660E\u8DEF\u5F84\uFF0C\u6323\u624E\u7740\u722C\u5230\u5CB8\u4E0A\uFF0C\u62AC\u624B\u6495\u4E0B\u4E00\u5757\u8863\u895F\u5305\u4F4F\u8D64\u88F8\u7684\u79C0\u8DB3\uFF0C\u6C89\u58F0\u9053\uFF1A\u300C\u8D70\u5427\u3002\u300D</p><p>\u738B\u540D\u6CFD\u5FC3\u4E0B\u51B7\u7B11\uFF0C\u770B\u4E86\u770B\u5730\u5F62\uFF0C\u626C\u58F0\u9053\uFF1A\u300C\u524D\u9762\u6709\u4E00\u6761\u8FD1\u8DEF\uFF0C\u6BD4\u5927\u8DEF\u7701\u4E86\u4E00\u4E2A\u65F6\u8FB0\u7684\u8DEF\u7A0B\uFF0C\u5C31\u662F\u4E0D\u592A\u597D\u8D70\u2026\u2026\u59D1\u5A18\uFF0C\u4F60\u770B\u600E\u4E48\u529E\uFF1F\u300D</p><p>\u6797\u9999\u8FDC\u6697\u60F3\uFF0C\u4F60\u80FD\u8D70\u6211\u4E5F\u80FD\u8D70\uFF0C\u4F55\u51B5\u8FD8\u80FD\u7701\u4E0B\u4E00\u4E2A\u65F6\u8FB0\u7684\u8DEF\u7A0B\uFF0C\u6B64\u523B\u65F6\u95F4\u5DF2\u665A\uFF0C\u82E5\u5728\u5C71\u4E2D\u4F4F\u5BBF\uFF0C\u4E0D\u4FBF\u4E4B\u5904\u751A\u591A\uFF0C\u4E8E\u662F\u9053\uFF1A\u300C\u8D70\u8FD1\u8DEF\u597D\u4E86\u3002\u300D</p><p>\u8FD1\u8DEF\u786E\u5B9E\u5D0E\u5C96\u96BE\u884C\uFF0C\u56E0\u4E3A\u6839\u672C\u5C31\u6CA1\u6709\u8DEF\u3002\u6E7F\u900F\u7684\u8863\u886B\u4E0D\u65F6\u88AB\u4E1B\u751F\u7684\u8346\u68D8\u52FE\u4F4F\uFF0C\u7565\u6709\u4E0D\u614E\u4FBF\u4F1A\u6495\u4E0B\u4E00\u5E45\u3002\u738B\u540D\u6CFD\u770B\u51C6\u4F4D\u7F6E\uFF0C\u628A\u8346\u6761\u9001\u5230\u5979\u8170\u4FA7\u817F\u95F4\uFF0C\u4E0D\u591A\u65F6\uFF0C\u6797\u9999\u8FDC\u4FBF\u8863\u886B\u8934\u891B\uFF0C\u4E0B\u88F3\u88AB\u6495\u5F00\u4E00\u6761\u5927\u7F1D\uFF0C\u767D\u5AE9\u7684\u5927\u817F\u65F6\u9690\u65F6\u73B0\uFF0C\u5C0F\u817F\u548C\u624B\u81C2\u66F4\u662F\u5212\u51FA\u9053\u9053\u8840\u8FF9\u3002\u5979\u6697\u81EA\u540E\u6094\uFF0C\u4F46\u4E8B\u5DF2\u81F3\u6B64\uFF0C\u4E0D\u5982\u6A2A\u4E0B\u5FC3\u8D70\u5B8C\u518D\u8BF4\u3002</p><p>\u738B\u540D\u6CFD\u76EF\u7740\u7834\u8863\u95F4\u767D\u817B\u7684\u808C\u80A4\u6B63\u770B\u5F97\u9AD8\u5174\uFF0C\u4E0D\u6599\u4E50\u6781\u751F\u60B2\uFF0C\u4E00\u5934\u649E\u5728\u6A2A\u751F\u6811\u679D\u4E0A\uFF0C\u987F\u65F6\u987A\u7740\u5C71\u5761\u6EDA\u4E86\u4E0B\u53BB\u3002\u597D\u5728\u4ED6\u6B66\u529F\u4E0D\u5F31\uFF0C\u614C\u4E71\u4E2D\u8FD0\u8DB3\u771F\u6C14\u5012\u4E5F\u672A\u66FE\u53D7\u4F24\u3002</p><p>\u6797\u9999\u8FDC\u88AB\u4ED6\u7684\u51B2\u529B\u4E00\u5E26\uFF0C\u5251\u9798\u51E0\u4E4E\u8131\u624B\uFF0C\u5F85\u542C\u5230\u4ED6\u5728\u4E0B\u9762\u53C8\u662F\u53EB\u75DB\u53C8\u662F\u5927\u9A82\uFF0C\u4E0D\u7531\u7126\u6025\u8D77\u6765\uFF0C\u53EB\u9053\uFF1A\u300C\u4F60\u600E\u4E48\u6837\uFF1F\u53D7\u4F24\u4E86\u5417\uFF1F\u300D</p><p>\u738B\u540D\u6CFD\u54FC\u5527\u534A\u5929\u4E5F\u6CA1\u6709\u56DE\u7B54\u3002</p><p>\u6797\u9999\u8FDC\u4E00\u54AC\u7259\uFF0C\u8BD5\u63A2\u7740\u5750\u5728\u659C\u5761\u4E0A\uFF0C\u6162\u6162\u6ED1\u4E0B\u3002</p><p>\u5F85\u6ED1\u5230\u4E00\u534A\uFF0C\u738B\u540D\u6CFD\u53EB\u9053\uFF1A\u300C\u59D1\u5A18\u5C0F\u5FC3\uFF01\u300D\u8BF4\u7740\u62E3\u8D77\u4E00\u6839\u67AF\u679D\u501A\u5728\u5761\u4E0A\u3002</p><p>\u5A07\u8EAF\u52A0\u901F\u6ED1\u4E0B\uFF0C\u6797\u9999\u8FDC\u5FFD\u7136\u95F7\u54FC\u4E00\u58F0\uFF0C\u8138\u8272\u5927\u53D8\u3002\u90A3\u6839\u67AF\u679D\u4E0D\u504F\u4E0D\u501A\u6B63\u9876\u5728\u5979\u4E24\u817F\u4E4B\u95F4\uFF0C\u5DE8\u5927\u7684\u51B2\u529B\u4F7F\u6811\u679D\u9876\u7AEF\u91CD\u91CD\u649E\u5165\u79D8\u5904\u3002</p><p>\u6797\u9999\u8FDC\u8138\u8272\u60E8\u767D\uFF0C\u98A4\u7740\u624B\u6307\u62D4\u51FA\u67AF\u679D\u3002\u6811\u679D\u523A\u5165\u8DB3\u6709\u4E00\u62F3\u6DF1\u6D45\uFF0C\u6811\u76AE\u4E0A\u9690\u9690\u5E26\u7740\u8840\u8FF9\uFF0C\u80A1\u95F4\u7684\u8863\u88E4\u6495\u5F00\u4E00\u4E2A\u5927\u6D1E\uFF0C\u9732\u51FA\u4E4C\u4EAE\u7684\u9634\u6BDB\u548C\u67D4\u7F8E\u7684\u82B1\u74E3\u3002\u9634\u961C\u4E0B\u8FD8\u6709\u4E00\u622A\u7EC6\u7EC6\u7684\u94C1\u94FE\u3002</p><p>\u8C8C\u7F8E\u5982\u82B1\u7684\u5C11\u5987\u67F3\u7709\u98A6\u7D27\uFF0C\u8138\u4E0A\u98DE\u8D77\u4E00\u7247\u7EA2\u971E\uFF0C\u5145\u6EE1\u53C8\u662F\u75BC\u75DB\u53C8\u662F\u7F9E\u8D67\u7684\u52A8\u4EBA\u795E\u60C5\u3002\u738B\u540D\u6CFD\u6697\u6697\u72DE\u7B11\u7740\u628A\u5251\u67C4\u9012\u5230\u6797\u9999\u8FDC\u624B\u4E2D\uFF0C\u88C5\u51FA\u61A8\u539A\u7684\u58F0\u97F3\u9053\uFF1A\u300C\u59D1\u5A18\u62FF\u597D\u3002\u300D</p><p>\u6B64\u4E3E\u53C8\u640F\u5F97\u6797\u9999\u8FDC\u7684\u4FE1\u4EFB\uFF0C\u5979\u614C\u4E71\u5730\u626F\u8D77\u886B\u89D2\uFF0C\u63A9\u4F4F\u88F8\u9732\u7684\u4E0B\u4F53\uFF0C\u54AC\u7259\u7AD9\u4E86\u8D77\u6765\u3002</p><p>\u65E5\u8272\u504F\u897F\uFF0C\u665A\u98CE\u8F7B\u62C2\uFF0C\u5E26\u6765\u4E00\u9635\u51C4\u51C9\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u6CBF\u6E56\u63A5\u8FDE\u53D1\u73B0\u4E8C\u5341\u4F59\u540D\u5E2E\u4F17\u5C38\u4F53\uFF0C\u6BCF\u5177\u5C38\u4F53\u53D7\u4F24\u90E8\u4F4D\u5404\u4E0D\u76F8\u540C\uFF0C\u4F46\u90FD\u662F\u4E00\u5251\u6BD9\u547D\uFF0C\u62DB\u672F\u72E0\u8FA3\u5F02\u5E38\u3002</p><p>\u53F6\u884C\u5357\u7FFB\u770B\u826F\u4E45\uFF0C\u6C89\u58F0\u9053\uFF1A\u300C\u5979\u7684\u529F\u529B\u6B63\u5728\u6062\u590D\uFF0C\u6700\u8FDF\u660E\u65E5\u4FBF\u53EF\u590D\u5143\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u5FC3\u5934\u6536\u7D27\uFF0C\u9762\u4E0A\u5374\u6BEB\u65E0\u8868\u60C5\u3002\u6C90\u58F0\u4F20\u5185\u4F24\u9887\u91CD\uFF0C\u4E24\u5929\u5185\u7EDD\u5BF9\u65E0\u6CD5\u4E0E\u4EBA\u52A8\u624B\uFF1B\u91D1\u5F00\u7532\u53D7\u4F24\u66F4\u91CD\uFF0C\u970D\u72C2\u7130\u548C\u5C60\u6000\u6C89\u6B66\u529F\u5DEE\u4E86\u4E00\u622A\uFF1B\u81EA\u5DF1\u7684\u592A\u4E00\u7ECF\u53C8\u662F\u521A\u521A\u5F00\u59CB\u4FEE\u4E60\u2026\u2026\u661F\u6708\u6E56\u4ECE\u6765\u6CA1\u6709\u50CF\u73B0\u5728\u8FD9\u6837\u4E0D\u582A\u4E00\u51FB\u2014\u2014\u62DB\u63FD\u4EBA\u9A6C\uFF0C\u57F9\u690D\u52BF\u529B\u523B\u4E0D\u5BB9\u7F13\uFF01</p><p>\u6155\u5BB9\u9F99\u9ED8\u7B97\u826F\u4E45\uFF0C\u5F90\u5F90\u9053\uFF1A\u300C\u64A4\u56DE\u6240\u6709\u6697\u54E8\uFF0C\u79BB\u5CB8\u4E00\u91CC\u5E03\u7F6E\u8239\u53EA\uFF0C\u4EE5\u5F7C\u6B64\u80FD\u89C1\u4E3A\u5EA6\u3002\u300D</p><p>\u7D2B\u8863\u4F8D\u8005\u9886\u547D\u800C\u53BB\u3002</p><p>\u6155\u5BB9\u9F99\u6DF1\u5438\u4E86\u53E3\u6C14\uFF0C\u6C89\u58F0\u9053\uFF1A\u300C\u970D\u957F\u8001\uFF0C\u5C06\u7834\u7A7A\u96F7\u5C3D\u6570\u53D6\u6765\u2014\u2014\u80FD\u9664\u6389\u96EA\u5CF0\u795E\u5C3C\uFF0C\u6211\u70B8\u5E73\u795E\u6BBF\u5728\u6240\u4E0D\u60DC\uFF01\u300D</p><p>\u970D\u72C2\u7130\u773C\u4E2D\u51F6\u5149\u95EA\u52A8\uFF0C\u8D77\u8EAB\u5927\u58F0\u5E94\u8BFA\u3002</p><p>\u5C4F\u98CE\u540E\u4F20\u6765\u4E00\u9635\u6025\u4FC3\u7684\u91D1\u77F3\u6572\u51FB\u58F0\u3002\u5F85\u4F8D\u8005\u6253\u5F00\u77F3\u95E8\uFF0C\u6155\u5BB9\u7D2B\u73AB\u5954\u51FA\u6765\u53EB\u9053\uFF1A\u300C\u53F6\u62A4\u6CD5\uFF0C\u6211\u5A18\u2014\u2014\u300D\u62AC\u773C\u770B\u5230\u970D\u72C2\u7130\u7684\u7EA2\u888D\uFF0C\u5389\u58F0\u9053\uFF1A\u300C\u59D3\u970D\u7684\uFF01\u4F60\u7ED9\u6211\u7AD9\u4F4F\uFF01\u300D</p><p>\u970D\u72C2\u7130\u6115\u7136\u56DE\u9996\uFF0C\u770B\u7740\u4FCF\u76EE\u51E0\u6B32\u55B7\u706B\u7684\u5C11\u592B\u4EBA\u3002</p><p>\u7D2B\u73AB\u8D70\u5230\u4ED6\u9762\u524D\uFF0C\u5BD2\u58F0\u9053\uFF1A\u300C\u4F60\u53BB\u6B66\u9675\u5E72\u4EC0\u4E48\u4E86\uFF1F\u300D</p><p>\u970D\u72C2\u7130\u602A\u7B11\u9053\uFF1A\u300C\u5C5E\u4E0B\u5949\u5BAB\u4E3B\u4E4B\u547D\u4E00\u8DEF\u62A4\u9001\u5C11\u592B\u4EBA\uFF0C\u53EA\u662F\u8DEF\u8FC7\u6B66\u9675\u7F62\u4E86\u3002\u300D</p><p>\u7D2B\u73AB\u538B\u4F4E\u58F0\u97F3\uFF0C\u54AC\u7259\u9053\uFF1A\u300C\u6CAE\u6E20\u5BB6\u6709\u8C01\u6D3B\u4E86\u4E0B\u6765\uFF1F\u300D</p><p>\u970D\u72C2\u7130\u773C\u73E0\u4E00\u8F6C\uFF0C\u7B11\u9053\uFF1A\u300C\u6CAE\u6E20\u5BB6\u7684\u5C0F\u5154\u5D3D\u5B50\u771F\u662F\u4E2A\u8113\u5305\uFF0C\u5C5E\u4E0B\u53EA\u6253\u65AD\u4ED6\u4E00\u53EA\u80F3\u818A\uFF0C\u4ED6\u5C31\u6D95\u6CEA\u4EA4\u6D41\uFF0C\u628A\u77E5\u9053\u7684\u90FD\u8BF4\u4E86\u51FA\u6765\u2014\u2014\u5F53\u7136\u4E5F\u5305\u62EC\u5C11\u592B\u4EBA\u7684\u884C\u8E2A\u3002\u300D</p><p>\u5C55\u626C\u54E5\u54E5\u65AD\u4E86\u4E00\u6761\u624B\u81C2\uFF01\u7D2B\u73AB\u5FC3\u5934\u4E00\u9178\uFF0C\u51E0\u4E4E\u843D\u4E0B\u6CEA\u6765\uFF0C\u8F7B\u58F0\u9053\uFF1A\u300C\u4ED6\u8FD8\u6D3B\u7740\u5417\uFF1F\u300D</p><p>\u300C\u6D3B\u7740\u2014\u2014\u53BB\u6E05\u51C9\u5C71\u5F53\u548C\u5C1A\u4E86\u3002\u5C11\u592B\u4EBA\u60F3\u89C1\uFF0C\u5C5E\u4E0B\u5373\u53EF\u6D3E\u4EBA\u628A\u4ED6\u62BC\u6765\u3002\u300D</p><p>\u300C\u53EA\u5269\u4ED6\u7684\u4E00\u4E2A\u4EBA\u5417\uFF1F\u300D</p><p>\u300C\u8FD8\u6709\u4E2A\u5C0F\u5A4A\u5B50\uFF0C\u5929\u751F\u7684\u8D31\u9AA8\u5934\uFF0C\u542C\u8BF4\u5F53\u5A4A\u5B50\u4E86\u3002\u300D</p><p>\u660E\u5170\u624D\u5341\u56DB\u5C81\u2026\u2026\u7D2B\u73AB\u6EE1\u8138\u6CEA\u5149\uFF0C\u6B7B\u6B7B\u76EF\u7740\u970D\u72C2\u7130\uFF0C\u6068\u4E0D\u5F97\u628A\u4ED6\u788E\u5C38\u4E07\u6BB5\u3002</p><p>\u970D\u72C2\u7130\u6EE1\u4E0D\u5728\u4E4E\u5730\u6311\u8D77\u8D64\u7709\uFF0C\u300C\u5C11\u592B\u4EBA\u82E5\u662F\u65E0\u4E8B\uFF0C\u5C5E\u4E0B\u4FBF\u544A\u9000\u4E86\u3002\u300D</p><p>\u7D2B\u73AB\u54BD\u4E0B\u773C\u6CEA\uFF0C\u8F6C\u8EAB\u8D70\u5230\u53F6\u884C\u5357\u8EAB\u524D\uFF0C\u58F0\u97F3\u5FAE\u98A4\u5730\u8BF4\uFF1A\u300C\u8BF7\u53F6\u62A4\u6CD5\u770B\u770B\u6211\u5A18\u2026\u2026\u300D</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u4E30\u6EE1\u7684\u96EA\u81C0\u65E0\u6CD5\u5408\u62E2\uFF0C\u6D45\u9EC4\u8272\u7684\u6C61\u7269\u6DF7\u7740\u9C9C\u8840\uFF0C\u4ECE\u6495\u88C2\u7684\u4F24\u53E3\u4E0D\u4F4F\u6D8C\u51FA\u3002\u53F6\u884C\u5357\u7528\u6E7F\u5DFE\u5C06\u6C61\u7269\u64E6\u51C0\uFF0C\u7136\u540E\u5C0F\u5FC3\u5730\u62E8\u5F00\u83CA\u7EB9\u7EC6\u770B\u4F24\u52BF\u3002</p><p>\u6155\u5BB9\u9F99\u6709\u610F\u6253\u6389\u767E\u82B1\u89C2\u97F3\u7684\u77DC\u6301\uFF0C\u8BA9\u5979\u5FC3\u7518\u60C5\u613F\u505A\u81EA\u5DF1\u7684\u73A9\u7269\uFF0C\u56E0\u6B64\u52A8\u4F5C\u6781\u662F\u51F6\u6B8B\u3002\u809B\u95E8\u5468\u56F4\u7684\u62EC\u7EA6\u808C\u5C3D\u6570\u6495\u788E\uFF0C\u4EE5\u53F6\u884C\u5357\u7684\u533B\u672F\uFF0C\u53EA\u6015\u4E5F\u65E0\u6CD5\u4F7F\u5979\u75CA\u7652.\u53F6\u884C\u5357\u62FF\u51FA\u4E00\u6839\u624B\u6307\u7C97\u7EC6\u7684\u8F6F\u68CD\uFF0C\u6D82\u4E0A\u836F\u7269\uFF0C\u6162\u6162\u7EB3\u5165\u7F8E\u5987\u809B\u4E2D\uFF0C\u5C06\u5410\u9732\u7684\u809B\u7AA6\u585E\u56DE\u539F\u5904\uFF0C\u7F13\u58F0\u9053\uFF1A\u300C\u4E09\u65E5\u5185\u4E0D\u53EF\u79FB\u52A8\uFF0C\u5C3D\u91CF\u4E0D\u8981\u996E\u98DF\uFF0C\u5207\u5FCC\u8F9B\u8FA3\u98DF\u7269\u3002\u300D</p><p>\u660F\u7761\u4E2D\uFF0C\u8427\u4F5B\u5974\u8138\u4E0A\u8FD8\u5E26\u7740\u51C4\u5A49\u6B32\u7EDD\u7684\u4F24\u75DB\u3002\u7D2B\u73AB\u62C9\u8D77\u8584\u6BEF\uFF0C\u8F7B\u8F7B\u642D\u5728\u6BCD\u4EB2\u80CC\u4E0A\uFF0C\u4F4E\u58F0\u9053\uFF1A\u300C\u8FD8\u6709\u7EAA\u5E08\u59D0\u3002\u300D</p><p>\u4E00\u591C\u4E4B\u95F4\uFF0C\u7EAA\u7709\u59A9\u4E0B\u4F53\u5DF2\u7ECF\u5F00\u59CB\u53D8\u5F97\u7D2B\u9ED1\u3002\u539F\u672C\u7EC6\u5AE9\u7684\u82B1\u74E3\u5145\u6EE1\u6DE4\u8840\uFF0C\u50CF\u4E00\u6735\u7D2B\u9ED1\u8272\u7684\u7261\u4E39\u5728\u80A1\u95F4\u76DB\u5F00\u3002</p><p>\u53EA\u770B\u4E86\u4E00\u773C\uFF0C\u53F6\u884C\u5357\u4FBF\u77E5\u53EA\u662F\u79EF\u8840\u6DE4\u80BF\uFF0C\u5E76\u65E0\u5927\u788D\u3002\u4E8E\u662F\u4ECE\u6000\u4E2D\u638F\u51FA\u521A\u521A\u914D\u5236\u7684\u836F\u818F\u3002</p><p>\u7EB5\u7136\u662F\u5927\u592B\uFF0C\u7D2B\u73AB\u4E5F\u4E0D\u613F\u770B\u7740\u4ED6\u6446\u5F04\u5E08\u59D0\u7684\u6027\u5668\uFF0C\u4E8E\u662F\u4F38\u624B\u63A5\u4E86\u8FC7\u6765\u3002\u6241\u5E73\u7684\u5706\u76D2\u5185\u76DB\u6EE1\u78A7\u7EFF\u7684\u818F\u836F\uFF0C\u6563\u53D1\u51FA\u4E00\u80A1\u6DE1\u6DE1\u7684\u6E05\u9999\u3002\u7D2B\u73AB\u6311\u8D77\u4E00\u56E2\uFF0C\u7EC6\u7EC6\u6D82\u5728\u7EAA\u5E08\u59D0\u79D8\u5904\u3002\u80BF\u80C0\u7684\u8089\u7247\u8DB3\u6709\u534A\u5BF8\u5BBD\u7A84\uFF0C\u91CC\u9762\u6EE1\u662F\u51DD\u7ED3\u7684\u8840\u5757\uFF0C\u5927\u5927\u5C0F\u5C0F\u4ECE\u6307\u5C16\u6ED1\u52A8\uFF0C\u8BA9\u4EBA\u5FC3\u5934\u53D1\u98A4\u3002</p><p>\u53F6\u884C\u5357\u6DE1\u6DE1\u9053\uFF1A\u300C\u6B64\u836F\u6CBB\u4F24\u6781\u4F73\uFF0C\u4E09\u4E2A\u65F6\u8FB0\u6D82\u62B9\u4E00\u6B21\uFF0C\u660E\u65E5\u6B64\u65F6\u4FBF\u53EF\u6062\u590D\u3002</p><p>\u4E0D\u8FC7\u836F\u6548\u53D1\u6325\u540E\uFF0C\u4F24\u5904\u9EBB\u75D2\u96BE\u5F53\uFF0C\u9700\u7528\u7EF3\u7D22\u7F1A\u5176\u56DB\u80A2\uFF0C\u514D\u5F97\u6323\u52A8\u95F4\u78B0\u5230\u4F24\u5904\u3002\u300D</p><p>\u7D2B\u73AB\u4E00\u4E00\u8BB0\u5728\u5FC3\u91CC\uFF0C\u4E00\u8FB9\u62FF\u7EF3\u5B50\u5C06\u7EAA\u7709\u59A9\u624B\u811A\u6346\u4F4F\uFF0C\u4E00\u8FB9\u5C0F\u58F0\u8BF4\uFF1A\u300C\u5E08\u59D0\uFF0C\u4F60\u5FCD\u4E00\u5FCD\uFF0C\u4E0D\u884C\u5C31\u7761\u4E00\u4F1A\u513F\uFF0C\u660E\u5929\u5C31\u4E0D\u75DB\u4E86\u2026\u2026\u300D</p><p>\u7EAA\u7709\u59A9\u51C4\u7136\u5408\u4E0A\u7F8E\u76EE\uFF0C\u6697\u60F3\uFF1A\u300C\u6700\u597D\u836F\u7269\u4E0D\u8D77\u4F5C\u7528\uFF0C\u8BA9\u8EAB\u5B50\u70C2\u4E86\u6700\u597D\u3002\u8FD9\u6837\u4EFB\u4EBA\u8E42\u8E8F\uFF0C\u8FD8\u4E0D\u5982\u6B7B\u4E86\u5E72\u51C0\u2026\u2026\u300D</p><p>\u4F46\u4E8B\u4E0E\u613F\u8FDD\uFF0C\u4E0D\u591A\u65F6\uFF0C\u80C0\u75BC\u7684\u79D8\u5904\u4FBF\u4F20\u6765\u4E00\u9635\u6E05\u51C9\uFF0C\u6DE4\u8840\u6E10\u6E10\u5316\u5F00\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u6155\u5BB9\u7D2B\u73AB\u8F7B\u624B\u8F7B\u811A\u56DE\u5230\u4E3B\u5BA4\u3002\u767D\u6C0F\u59D0\u59B9\u6B63\u628A\u4E00\u5E45\u5DE8\u6BEF\u6302\u5728\u77F3\u58C1\u4E0A\u3002\u5DE8\u6BEF\u957F\u7EA6\u4E09\u4E08\u9AD8\u8FD1\u4E24\u4E08\uFF0C\u8DB3\u6709\u6570\u767E\u65A4\u91CD\u3002\u4F46\u4E24\u5973\u4E00\u4EBA\u626F\u7740\u4E00\u89D2\uFF0C\u6BEB\u4E0D\u8D39\u529B\u7684\u4FBF\u6500\u5230\u58C1\u4E0A\u3002</p><p>\u7D2B\u73AB\u5FC3\u4E0B\u4E00\u52A8\uFF0C\u626C\u58F0\u9053\uFF1A\u300C\u5C0F\u83BA\u5C0F\u9E42\uFF0C\u4E0B\u6765\u6B47\u6B47\u5427\u3002\u300D</p><p>\u94C3\u58F0\u5FAE\u54CD\uFF0C\u5A07\u4FCF\u7684\u59D0\u59B9\u82B1\u5B9B\u5982\u4E00\u5BF9\u6676\u83B9\u5254\u900F\u7684\u74A7\u4EBA\uFF0C\u5E26\u7740\u6DE1\u6DE1\u7684\u9999\u6C14\u843D\u5728\u7D2B\u73AB\u9762\u524D\uFF0C\u5E76\u80A9\u8DEA\u4E0B\u3002</p><p>\u7D2B\u73AB\u8FDE\u5FD9\u62C9\u4F4F\uFF0C\u300C\u54CE\u5440\uFF0C\u90A3\u4E2A\u6DF7\u86CB\u4E0D\u5728\uFF0C\u4F60\u4EEC\u5C31\u522B\u8FD9\u6837\u5566\u2014\u2014\u8FD8\u6709\uFF0C\u522B\u53EB\u6211\u5C11\u592B\u4EBA\uFF0C\u60F3\u60F3\u5C31\u6076\u5FC3\uFF01\u300D</p><p>\u767D\u7389\u83BA\u4F4E\u58F0\u9053\uFF1A\u300C\u4ED9\u5B50\u6709\u4EC0\u4E48\u5429\u5490\u2026\u2026\u300D</p><p>\u7D2B\u73AB\u8F7B\u53F9\u4E00\u58F0\uFF0C\u5546\u91CF\u9053\uFF1A\u300C\u8FD8\u548C\u4EE5\u524D\u4E00\u6837\uFF0C\u4F60\u4EEC\u53EB\u6211\u59D0\u59D0\uFF0C\u6211\u53EB\u4F60\u4EEC\u59B9\u59B9\u597D\u4E0D\u597D\uFF1F\u300D</p><p>\u59D0\u59B9\u4FE9\u5C55\u989C\u4E00\u7B11\uFF0C\u8138\u86CB\u4E0A\u5404\u81EA\u51FA\u73B0\u4E00\u4E2A\u5C0F\u5C0F\u7684\u9152\u7A9D\u3002</p><p>\u7D2B\u73AB\u628A\u5979\u4EEC\u62C9\u5230\u5E8A\u4E0A\uFF0C\u6084\u58F0\u95EE\u9053\uFF1A\u300C\u4F60\u4EEC\u7684\u6B66\u529F\u600E\u4E48\u8FD8\u5728\uFF1F\u300D</p><p>\u300C\u2026\u2026\u53EF\u80FD\u662F\u5BAB\u4E3B\u89C1\u6211\u4EEC\u6B66\u529F\u592A\u4F4E\u3002\u300D</p><p>\u7D2B\u73AB\u56DE\u5FC6\u7740\u9053\uFF1A\u300C\u4F60\u4EEC\u4FE9\u5F53\u65F6\u80FD\u6321\u4F4F\u4E09\u540D\u9999\u4E3B\uFF0C\u6B66\u529F\u5F88\u597D\u4E86\u3002\u300D</p><p>\u767D\u7389\u9E42\u9053\uFF1A\u300C\u90A3\u662F\u6211\u8DDF\u59D0\u59D0\u8054\u624B\uFF0C\u5982\u679C\u5355\u6253\u72EC\u6597\uFF0C\u6BD4\u4ED6\u4EEC\u8FD8\u5DEE\u4E00\u4E9B\u3002\u300D</p><p>\u7D2B\u73AB\u63E1\u4F4F\u5C0F\u62F3\u5934\uFF0C\u5174\u594B\u5730\u8BF4\uFF1A\u300C\u90A3\u4E5F\u5F88\u597D\u4E86\u3002\u4ECA\u5929\u665A\u4E0A\u6211\u5E08\u7236\u80AF\u5B9A\u4F1A\u6765\uFF0C\u5230\u65F6\u4ED6\u4EEC\u90FD\u5728\u524D\u9762\uFF0C\u54B1\u4EEC\u4E58\u673A\u628A\u752C\u9053\u5835\u4F4F\uFF0C\u7136\u540E\u4ECE\u540E\u95E8\u9003\u8D70\u600E\u4E48\u6837\uFF1F\u300D</p><p>\u5979\u8BF4\u7684\u662F\u5173\u62BC\u98CE\u665A\u534E\u7684\u5730\u5B57\u752C\u9053\u3002\u8FD9\u6761\u752C\u9053\u5E73\u65F6\u88AB\u9694\u5728\u77F3\u5BAB\u4E4B\u5916\uFF0C\u63B3\u6765\u7684\u5973\u5B50\u90FD\u56DA\u5728\u5176\u4E2D\uFF0C\u4E13\u4F9B\u5E2E\u4F17\u5978\u6DEB\u3002\u767D\u7389\u83BA\u72B9\u8C6B\u9053\uFF1A\u300C\u90A3\u6761\u5730\u9053\u6709\u94C1\u95E8\uFF0C\u600E\u4E48\u6253\u5F00\u5462\uFF1F\u300D</p><p>\u7D2B\u73AB\u661F\u7738\u5149\u8292\u95EA\u52A8\uFF0C\u300C\u6211\u7684\u5B9D\u5200\u5728\u90A3\u4E2A\u6DF7\u86CB\u624B\u91CC\uFF0C\u8BA9\u6211\u60F3\u529E\u6CD5\u628A\u5B83\u5077\u8FC7\u6765\uFF0C\u5288\u5F00\u94C1\u95E8\u6613\u5982\u53CD\u638C\u3002\u300D</p><p>\u300C\u7D2B\u73AB\u59D0\u59D0\uFF0C\u4F60\u8BA9\u6211\u4EEC\u505A\u4EC0\u4E48\uFF01\u300D</p><p>\u300C\u6211\u5185\u529F\u88AB\u6563\uFF0C\u5982\u679C\u8BA9\u4ED6\u4EEC\u53D1\u89C9\uFF0C\u8FD8\u5F97\u9760\u4F60\u4EEC\u4FE9\u5462\u3002\u752C\u9053\u8FD9\u4E48\u7A84\uFF0C\u4F60\u4EEC\u4FE9\u8054\u624B\uFF0C\u5C31\u662F\u90A3\u4E2A\u6DF7\u86CB\u4E0A\u6765\u4E5F\u80FD\u62B5\u6321\u4E00\u9635\uFF0C\u53EA\u8981\u80FD\u62A4\u4F4F\u6211\u5A18\u3001\u7EAA\u5E08\u59D0\u3001\u98CE\u5E08\u59D0\uFF0C\u7B49\u6211\u5E08\u7236\u6740\u8FDB\u6765\u54B1\u4EEC\u5C31\u4EC0\u4E48\u90FD\u4E0D\u6015\u4E86\uFF01\u300D</p><p>\u8FDE\u65E5\u6765\u91D1\u5F00\u7532\u3001\u6C90\u58F0\u4F20\u7EB7\u7EB7\u53D7\u4F24\uFF0C\u767D\u6C0F\u59D0\u59B9\u4E5F\u77E5\u9053\u795E\u5C3C\u7684\u5389\u5BB3\u3002\u60F3\u5230\u80FD\u9003\u79BB\u9B54\u638C\uFF0C\u4E24\u5973\u90FD\u4E0D\u7531\u7B11\u9010\u989C\u5F00\uFF0C\u300C\u5982\u679C\u4E00\u4F1A\u513F\u5BAB\u4E3B\u8BA9\u6211\u4EEC\u4FE9\u4F3A\u5019\uFF0C\u6211\u4EEC\u5C31\u60F3\u529E\u6CD5\u628A\u4E1C\u897F\u7ED9\u59D0\u59D0\u62FF\u6765\u2026\u2026\u300D</p><p>\u4E09\u5973\u6B63\u8BF4\u5F97\u9AD8\u5174\uFF0C\u7389\u95E8\u7A81\u7136\u63A8\u5F00\uFF0C\u9732\u51FA\u4E00\u5F20\u4FCA\u96C5\u975E\u51E1\u7684\u9762\u5B54\u3002\u4F46\u8FD9\u5F20\u9762\u5B54\u5374\u662F\u5BAB\u4E2D\u6240\u6709\u5973\u4EBA\u7684\u6076\u68A6\u3002</p>',74);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
