import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_41" tabindex="-1"><a class="header-anchor" href="#_41" aria-hidden="true">#</a> 41</h1><p>\u7D2B\u73AB\u8BE1\u8BA1\u591A\u7AEF\uFF0C\u5982\u679C\u4E0E\u767D\u6C0F\u59D0\u59B9\u592A\u8FC7\u4EB2\u8FD1\u591A\u6709\u4E0D\u6D4B\uFF0C\u6155\u5BB9\u9F99\u5BD2\u58F0\u9053\uFF1A\u300C\u59D0\u59D0\u53EF\u662F\u4F60\u4EEC\u4E24\u4E2A\u8D31\u5974\u53EB\u7684\uFF1F\u8FC7\u6765\uFF01\u300D</p><p>\u7389\u83BA\u7389\u9E42\u8FDE\u5FD9\u8DEA\u5728\u4E3B\u5B50\u9762\u524D\uFF0C\u5A07\u8EAF\u6218\u6817\u3002\u7D2B\u73AB\u77E5\u9053\u81EA\u5DF1\u7684\u6073\u6C42\u53EA\u4F1A\u4F7F\u59D0\u59B9\u4FE9\u53D7\u5230\u66F4\u5927\u7684\u75DB\u82E6\uFF0C\u53EA\u597D\u4E00\u8A00\u4E0D\u53D1\u3002</p><p>\u4E59\u5BA4\u6446\u6EE1\u5404\u79CD\u5175\u5203\uFF0C\u6B63\u4E2D\u7684\u51E0\u4E0A\u653E\u7740\u4E00\u4E2A\u7A7A\u843D\u843D\u7684\u5251\u67B6\uFF0C\u5DE6\u53F3\u5206\u522B\u662F\u4E00\u679D\u957F\u97AD\u548C\u4E00\u5BF9\u6708\u7259\u72B6\u5F2F\u94A9\uFF0C\u6B63\u662F\u661F\u6708\u6E56\u4E09\u5927\u795E\u5175\u4E4B\u4E8C\uFF1A\u8361\u661F\u97AD\u3001\u65E5\u6708\u94A9\u3002\u661F\u6708\u6E56\u9547\u6559\u4E4B\u5B9D\u7384\u5929\u5251\u6570\u5341\u5E74\u524D\u4FBF\u4E0B\u843D\u4E0D\u660E\uFF0C\u4E3A\u6B64\u8FD8\u642D\u4E0A\u4E24\u4F4D\u4F7F\u8005\u7684\u6027\u547D\u3002</p><p>\u6155\u5BB9\u9F99\u62FF\u8D77\u65E5\u6708\u94A9\u4ED4\u7EC6\u7AEF\u8BE6\u3002\u65E5\u6708\u94A9\u5F84\u7EA6\u534A\u5C3A\uFF0C\u72B6\u5982\u5F2F\u6708\uFF0C\u4E24\u7AEF\u950B\u8292\u6BD5\u9732\uFF0C\u5706\u5F27\u5185\u5E03\u6EE1\u4E0D\u89C4\u5219\u7684\u7A81\u8D77\uFF0C\u6D51\u7136\u5929\u6210\u3002\u5B83\u7684\u4EFD\u91CF\u5E76\u4E0D\u751A\u91CD\uFF0C\u8D28\u5730\u975E\u91D1\u975E\u77F3\uFF0C\u8272\u6CFD\u5982\u7389\uFF0C\u53E9\u4E4B\u5374\u6709\u91D1\u94C1\u4E4B\u58F0\u3002\u4E24\u94A9\u88AB\u4E00\u6839\u4E08\u8BB8\u94A2\u94FE\u7CFB\u5728\u4E00\u8D77\uFF0C\u5F62\u72B6\u76F8\u4F3C\uFF0C\u63E1\u5728\u624B\u4E2D\u5374\u4E00\u5BD2\u4E00\u70ED\uFF0C\u5927\u5F02\u5176\u8DA3\u3002</p><p>\u6155\u5BB9\u9F99\u5C06\u65E5\u94A9\u63D2\u5728\u8170\u540E\uFF0C\u63A5\u7740\u624B\u4E00\u6296\uFF0C\u6708\u94A9\u65E0\u58F0\u65E0\u606F\u5730\u5212\u51FA\u4E00\u4E2A\u534A\u5706\uFF0C\u7A33\u7A33\u7F20\u5728\u8170\u95F4\u3002</p><p>\u8361\u661F\u97AD\u67C4\u957F\u5C3A\u4F59\uFF0C\u4E0A\u9762\u9576\u7740\u4E03\u661F\u5B9D\u77F3\u3002\u97AD\u4F53\u8272\u6CFD\u4E73\u767D\uFF0C\u9690\u9690\u6CDB\u51FA\u4E00\u5C42\u8840\u8272\u3002</p><p>\u8FD9\u67C4\u8361\u661F\u97AD\u662F\u524D\u4EE3\u592A\u51B2\u5BAB\u4E3B\u7684\u968F\u8EAB\u5175\u5203\uFF0C\u4ED6\u4E0E\u661F\u6708\u6E56\u5343\u5E74\u6765\u6700\u5371\u9669\u7684\u5927\u654C\u540C\u5F52\u4E8E\u5C3D\uFF0C\u5C38\u9AA8\u65E0\u5B58\uFF0C\u53EA\u7559\u4E0B\u8FD9\u67C4\u8361\u661F\u97AD\uFF0C\u88AB\u540E\u4EBA\u4F9B\u5949\u5728\u5723\u5BAB\u5185\u3002</p><p>\u6155\u5BB9\u9F99\u633D\u8D77\u8361\u661F\u97AD\uFF0C\u653E\u5165\u8863\u8896\uFF0C\u5FC3\u4E2D\u4E0D\u7531\u60F3\u5230\uFF1A\u96EA\u5CF0\u795E\u5C3C\u4E0E\u5F53\u65E5\u7684\u7075\u7280\u5F69\u51E4\u76F8\u6BD4\uFF0C\u7A76\u7ADF\u8C01\u66F4\u53EF\u6015\uFF1F</p><p>\u767D\u6C0F\u59D0\u59B9\u6218\u6218\u5162\u5162\u8DEA\u5728\u95E8\u5916\uFF0C\u53EA\u542C\u4E3B\u5B50\u4E00\u58F0\u51B7\u559D\uFF0C\u300C\u633A\u8D77\u80F8\u6765\u3002\u300D\u4E24\u5973\u614C\u5FD9\u64A9\u8D77\u8F7B\u7EB1\uFF0C\u633A\u8D77\u9165\u4E73\u3002</p><p>\u6155\u5BB9\u9F99\u62FD\u4F4F\u767D\u7389\u83BA\u5DE6\u4E73\u548C\u767D\u7389\u9E42\u7684\u53F3\u4E73\uFF0C\u5C06\u4E73\u5934\u4E0A\u4E24\u53EA\u91D1\u73AF\u653E\u5230\u4E00\u8D77\uFF0C\u7136\u540E\u62FF\u51FA\u4E00\u53EA\u7CBE\u81F4\u7684\u5C0F\u9501\u300C\u556A\u300D\u7684\u9501\u4E0A\u3002\u9501\u5B8C\u4E24\u4E73\u548C\u9634\u8482\u4E0A\u7684\u4E09\u5BF9\u91D1\u73AF\uFF0C\u6155\u5BB9\u9F99\u5408\u638C\u5C06\u4E09\u679A\u94A5\u5319\u634F\u6210\u4E00\u56E2\uFF0C\u968F\u624B\u4E00\u6254\uFF0C\u7136\u540E\u626C\u957F\u800C\u53BB\u3002</p><p>\u767D\u6C0F\u59D0\u59B9\u9762\u9762\u76F8\u89D1\uFF0C\u7A81\u7136\u610F\u8BC6\u5230\u4E24\u4EBA\u65E0\u8BBA\u884C\u52A8\u8D77\u5C45\uFF0C\u90FD\u53EA\u80FD\u8FD9\u6837\u9762\u5BF9\u9762\u8FDE\u5728\u4E00\u8D77\u2026\u2026</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u521A\u8FC7\u672A\u65F6\uFF0C\u6E56\u9762\u7A81\u7136\u4F20\u6765\u4E00\u58F0\u5C16\u5229\u7684\u54E8\u54CD\u3002</p><p>\u6B63\u5728\u5E03\u7F6E\u7684\u706B\u5802\u5E2E\u4F17\u95FB\u58F0\u7EB7\u7EB7\u505C\u624B\u62AC\u5934\uFF0C\u795E\u6BBF\u5185\u987F\u65F6\u5145\u6EE1\u6D53\u91CD\u7684\u6740\u673A\u3002</p><p>\u4E09\u5929\u4E4B\u5185\uFF0C\u96EA\u5CF0\u795E\u5C3C\u56DB\u5EA6\u6765\u88AD\uFF0C\u63A5\u8FDE\u51FB\u6BD9\u62A4\u6CD5\u6731\u90AA\u9752\u6811\u3001\u5C48\u82E6\u85E4\uFF1B\u51FB\u4F24\u91D1\u5F00\u7532\u3001\u6C90\u58F0\u4F20\uFF0C\u6559\u4E2D\u9876\u5C16\u9AD8\u624B\u51E0\u4E4E\u88AB\u5979\u5B64\u8EAB\u4E00\u4EBA\u65A9\u6740\u6B86\u5C3D\u3002\u82E5\u662F\u5176\u4ED6\u95E8\u6D3E\uFF0C\u9047\u4E0A\u8FD9\u7B49\u5F3A\u654C\u591A\u534A\u65E9\u5DF2\u95FB\u98CE\u4E27\u80C6\uFF0C\u4F46\u661F\u6708\u6E56\u5E2E\u4F17\u591A\u662F\u51F6\u5F3A\u4E4B\u5F92\uFF0C\u608D\u4E0D\u754F\u6B7B\u3002</p><p>\u6155\u5BB9\u9F99\u4EE5\u4E3A\u96EA\u5CF0\u795E\u5C3C\u8FD8\u4F1A\u8D81\u591C\u8272\u6765\u88AD\uFF0C\u6CA1\u60F3\u5230\u4ECA\u65E5\u8FD9\u4E48\u65E9\u4FBF\u6562\u786C\u95EF\u3002\u4ED6\u98DE\u8EAB\u8D8A\u8FC7\u5C4F\u98CE\uFF0C\u987E\u4E0D\u5F97\u5E03\u7F6E\u672A\u5F53\uFF0C\u6025\u5FD9\u547D\u4F17\u4EBA\u8FC5\u901F\u9000\u51FA\u795E\u6BBF\uFF0C\u53EA\u7559\u4E0B\u970D\u72C2\u7130\u4E25\u9635\u4EE5\u5F85\u3002</p><p>\u96EA\u5CF0\u795E\u5C3C\u534A\u4E2A\u65F6\u8FB0\u524D\u8D76\u56DE\u5C71\u6D1E\uFF0C\u624D\u53D1\u73B0\u6797\u9999\u8FDC\u5DF2\u82B3\u8E2A\u6773\u7136\u3002\u5979\u56DB\u5904\u641C\u7D22\uFF0C\u53EA\u5728\u6D1E\u5916\u53D1\u73B0\u4E00\u5757\u9ED1\u8272\u788E\u5E03\uFF0C\u770B\u8D28\u5730\u4E0E\u661F\u6708\u6E56\u6C34\u5802\u670D\u8272\u4E00\u822C\u65E0\u4E8C\u3002\u5979\u52C3\u7136\u5927\u6012\uFF0C\u7ACB\u523B\u91CD\u8FD4\u661F\u6708\u6E56\u3002\u8FD9\u4E00\u8DEF\u5979\u6CA1\u6709\u9047\u5230\u4EFB\u4F55\u62B5\u6297\u3002\u82E5\u975E\u5979\u6E05\u695A\u5730\u611F\u5E94\u5230\u56DB\u5904\u6697\u85CF\u7740\u65E0\u6570\u51F6\u6076\u7684\u773C\u795E\uFF0C\u4F1A\u4EE5\u4E3A\u9B54\u6559\u8FD9\u8FD1\u5343\u5E2E\u4F17\uFF0C\u4E0D\u5230\u534A\u65E5\u5DE5\u592B\u4FBF\u5C3D\u6570\u6D88\u5931\u3002</p><p>\u795E\u6BBF\u524D\u7A7A\u65E0\u4E00\u4EBA\uFF0C\u4E0E\u6628\u65E5\u7684\u5200\u67AA\u6797\u7ACB\u5982\u4E34\u5927\u654C\uFF0C\u5224\u82E5\u4E91\u6CE5\u3002\u6155\u5BB9\u9F99\u7AD9\u5728\u95E8\u5185\uFF0C\u6D12\u7136\u7B11\u9053\uFF1A\u300C\u795E\u5C3C\u8FD9\u4E48\u7740\u6025\uFF0C\u83AB\u975E\u662F\u6025\u4E8E\u732E\u8EAB\u672C\u6559\uFF1F\u300D</p><p>\u9759\u9ED8\u7684\u5927\u6BBF\u867D\u7136\u4E00\u5982\u5F80\u65E5\uFF0C\u5374\u5904\u5904\u6697\u85CF\u6740\u673A\u3002\u96EA\u5CF0\u795E\u5C3C\u6027\u70C8\u5982\u706B\uFF0C\u4F46\u5E76\u975E\u9C81\u83BD\u4E4B\u4EBA\u3002\u5979\u51DD\u8EAB\u800C\u7ACB\uFF0C\u5BD2\u58F0\u9053\uFF1A\u300C\u6797\u9999\u8FDC\u73B0\u5728\u4F55\u5904\uFF1F\u300D</p><p>\u6155\u5BB9\u9F99\u5FAE\u5FAE\u4E00\u6014\uFF0C\u65CB\u5373\u7B11\u9053\uFF1A\u300C\u5927\u5E08\u7684\u8BF8\u4F4D\u7231\u5F92\u5728\u6BBF\u5185\u63A5\u5BA2\uFF0C\u5927\u5E08\u4E0D\u59A8\u5165\u5185\u4E00\u89C2\u3002\u300D\u8BF4\u7F62\u60A0\u7136\u8E31\u56DE\u6BBF\u5185\u3002</p><p>\u4ED6\u7684\u72B9\u7591\u867D\u662F\u4E00\u95EA\u800C\u8FC7\uFF0C\u96EA\u5CF0\u795E\u5C3C\u8FD8\u662F\u770B\u51FA\u7AEF\u502A\uFF0C\u4F46\u5373\u4F7F\u6797\u9999\u8FDC\u5E76\u672A\u843D\u5165\u4ED6\u4EEC\u624B\u4E2D\uFF0C\u5176\u4ED6\u4E09\u4F4D\u7231\u5F92\u4E5F\u662F\u4EB2\u540C\u9AA8\u8089\u3002\u795E\u5C3C\u6DF1\u5438\u4E00\u53E3\u6C14\uFF0C\u817E\u8EAB\u800C\u8D77\u3002</p><p>\u7B49\u795E\u5C3C\u8FDB\u5165\u6BBF\u5185\uFF0C\u6155\u5BB9\u9F99\u7ACB\u5373\u7075\u5DE7\u5730\u7FFB\u4E86\u4E2A\u65A4\u6597\uFF0C\u843D\u5728\u5C4F\u98CE\u4E4B\u540E\u3002\u795E\u5C3C\u8EAB\u5728\u534A\u7A7A\u4E00\u638C\u63A8\u51FA\uFF0C\u4E09\u4E08\u5916\u7684\u5C4F\u98CE\u7ACB\u5373\u5E94\u624B\u800C\u5012\u3002</p><p>\u6155\u5BB9\u9F99\u5E76\u672A\u9003\u5165\u752C\u9053\uFF0C\u800C\u662F\u4F9D\u5899\u800C\u7ACB\uFF0C\u4E00\u624B\u6309\u7D27\u8170\u540E\u7684\u65E5\u6708\u94A9\uFF0C\u4E00\u624B\u6325\u51FA\u8361\u661F\u97AD\u3002</p><p>\u96EA\u5CF0\u795E\u5C3C\u8EAB\u5728\u534A\u7A7A\uFF0C\u53F3\u624B\u957F\u4F38\uFF0C\u76F4\u62CD\u97AD\u68A2\u3002</p><p>\u300C\u556A\u300D\u7684\u4E00\u58F0\uFF0C\u8F6F\u4E0D\u7740\u529B\u7684\u97AD\u8EAB\u88AB\u5979\u4E00\u638C\u51FB\u5F97\u7C89\u788E\uFF0C\u5A01\u529B\u6240\u53CA\uFF0C\u6155\u5BB9\u9F99\u80CC\u90E8\u91CD\u91CD\u649E\u5728\u77F3\u58C1\u4E0A\uFF0C\u51E0\u6B32\u5410\u8840\u3002\u4ED6\u9762\u8272\u60E8\u767D\uFF0C\u5FC3\u4E0B\u9707\u60CA\u65E0\u6BD4\u3002\u9547\u6559\u795E\u5175\u7ADF\u8FD9\u6837\u88AB\u4EBA\u4E00\u638C\u51FB\u788E\uFF0C\u96EA\u5CF0\u795E\u5C3C\u529F\u529B\u4E4B\u5F3A\u7740\u5B9E\u9A87\u4EBA\u542C\u95FB\u2026\u2026</p><p>\u65C1\u8FB9\u7EA2\u987B\u7EA2\u888D\u7684\u970D\u72C2\u7130\u5927\u8896\u4E00\u626C\uFF0C\u4E24\u679A\u9ED1\u8272\u7684\u5706\u7403\u65E0\u58F0\u65E0\u606F\u5730\u98DE\u4E86\u8FC7\u6765\u3002</p><p>\u5706\u7403\u65E0\u950B\u65E0\u8292\uFF0C\u58F0\u52BF\u4E5F\u4E0D\u51CC\u5389\uFF0C\u795E\u5C3C\u6697\u6043\u53EF\u80FD\u662F\u9644\u6709\u5267\u6BD2\uFF0C\u56E0\u6B64\u52B2\u805A\u53F3\u624B\uFF0C\u5F39\u6307\u51FB\u51FA\u3002</p><p>\u624B\u6307\u521A\u521A\u4F38\u51FA\uFF0C\u5706\u7403\u7A81\u7136\u7206\u51FA\u4E00\u56E2\u5267\u70C8\u7684\u706B\u5149\u3002\u96EA\u5CF0\u795E\u5C3C\u53F3\u624B\u5982\u53D7\u96F7\u51FB\uFF0C\u5DE8\u54CD\u8FC7\u540E\uFF0C\u6574\u53EA\u53F3\u8896\u987F\u65F6\u5316\u4E3A\u98DE\u8776\uFF0C\u53EA\u5269\u4E0B\u4E00\u6761\u6676\u83B9\u7684\u7389\u81C2\u3002\u62C7\u6307\u3001\u98DF\u6307\u3001\u4E2D\u6307\u5982\u88AB\u70C8\u706B\u70E7\u70BD\uFF0C\u82CD\u9ED1\u4E00\u7247\u3002</p><p>\u8FD1\u767E\u5E74\u524D\uFF0C\u661F\u6708\u6E56\u4E00\u4F4D\u5BAB\u4E3B\u70BC\u5236\u4E39\u836F\u65F6\uFF0C\u65E0\u610F\u4E2D\u53D1\u73B0\u6728\u70AD\u3001\u785D\u78FA\u7B49\u7269\u5408\u70BC\u4F1A\u4EA7\u751F\u6781\u5927\u7684\u5A01\u529B\u3002\u4ED6\u6F5C\u5FC3\u94BB\u7814\uFF0C\u7A77\u5341\u4F59\u5E74\u5BD2\u6691\u4E4B\u5DE5\uFF0C\u7EC8\u4E8E\u5236\u51FA\u53EF\u968F\u8EAB\u643A\u5E26\uFF0C\u9760\u5185\u529B\u6FC0\u53D1\u7684\u7834\u7A7A\u96F7\u3002\u6B64\u7269\u662F\u661F\u6708\u6E56\u6559\u4E2D\u79D8\u4F20\uFF0C\u51E1\u662F\u4EE5\u5176\u5BF9\u654C\uFF0C\u7EDD\u65E0\u6D3B\u53E3\uFF0C\u56E0\u6B64\u867D\u7136\u7D2F\u7ACB\u5947\u529F\uFF0C\u6559\u5916\u5374\u7EDD\u5C11\u6709\u4EBA\u77E5\u95FB\u3002</p><p>\u970D\u72C2\u7130\u89C1\u96EA\u5CF0\u795E\u5C3C\u786C\u6321\u7834\u7A7A\u96F7\u7ADF\u7136\u624B\u6307\u5B8C\u597D\u65E0\u635F\uFF0C\u4E0D\u7981\u5927\u5403\u4E00\u60CA\uFF0C\u6123\u4E86\u4E00\u4E0B\u624D\u65BD\u51FA\u6F2B\u5929\u82B1\u96E8\u7684\u624B\u6CD5\uFF0C\u4E00\u4E3E\u6253\u51FA\u5341\u4E8C\u679A\u7834\u7A7A\u96F7\u3002</p><p>\u8FD9\u7247\u523B\u8FDF\u7591\uFF0C\u4F7F\u96EA\u5CF0\u795E\u5C3C\u62E3\u4E86\u6761\u6027\u547D\u3002\u5979\u53F3\u624B\u9707\u5F97\u9EBB\u75F9\uFF0C\u4E09\u6839\u624B\u6307\u7ECF\u8109\u53D7\u521B\uFF0C\u65E0\u6CD5\u52A8\u4F5C\uFF0C\u4F53\u5185\u66F4\u662F\u6C14\u8840\u7FFB\u817E\u3002\u5979\u52C9\u5F3A\u7528\u5DE6\u624B\u62D4\u51FA\u957F\u5251\uFF0C\u6325\u624B\u523A\u5728\u67F1\u4E0A\u3002</p><p>\u5341\u4E8C\u679A\u7834\u7A7A\u96F7\u540C\u65F6\u70B8\u5F00\uFF0C\u5DE8\u67F1\u8F70\u7136\u800C\u65AD\u3002\u5E9E\u5927\u7684\u795E\u6BBF\u5185\u4EAE\u8D77\u4E00\u56E2\u523A\u76EE\u7684\u706B\u5149\uFF0C\u7206\u70B8\u58F0\u9707\u8033\u6B32\u804B\uFF0C\u5DE8\u5927\u7684\u6C14\u6D41\u65CB\u98CE\u822C\u626B\u8FC7\uFF0C\u6574\u5EA7\u795E\u6BBF\u90FD\u4E3A\u4E4B\u6447\u64BC\u3002</p><p>\u6309\u7167\u539F\u8BA2\u8BA1\u5212\uFF0C\u795E\u6BBF\u5404\u5904\u6240\u57CB\u7684\u706B\u836F\u4E5F\u5C06\u540C\u65F6\u5F15\u7206\uFF0C\u6155\u5BB9\u9F99\u548C\u970D\u72C2\u7130\u6F5C\u5165\u5723\u5BAB\u8EB2\u907F\uFF0C\u628A\u795E\u5C3C\u70B8\u4E2A\u5C38\u9AA8\u65E0\u5B58\uFF0C\u4E0E\u5927\u6BBF\u4E00\u540C\u7070\u98DE\u70DF\u706D\u3002\u4F46\u795E\u5C3C\u6765\u5F97\u592A\u5FEB\uFF0C\u6BBF\u5185\u5E03\u7F6E\u672A\u5F53\uFF0C\u56E0\u6B64\u9664\u4E86\u4E2D\u95F4\u4E00\u6839\u5DE8\u67F1\u88AB\u70B8\u65AD\u4E4B\u5916\uFF0C\u795E\u6BBF\u7684\u7ED3\u6784\u5B89\u7136\u65E0\u6059\u3002</p><p>\u6155\u5BB9\u9F99\u4E3E\u8896\u62B5\u6321\u7EB7\u98DE\u7684\u77F3\u5C51\uFF0C\u5927\u6BBF\u4F59\u6CE2\u672A\u6B62\uFF0C\u4ED6\u4FBF\u63E1\u4F4F\u65E5\u6708\u94A9\u98DE\u8EAB\u63A0\u5F80\u7206\u70B8\u4E2D\u5FC3\u3002\u6D53\u70DF\u6563\u5F00\uFF0C\u51E0\u7247\u6D01\u767D\u7684\u788E\u8863\u88AB\u70E7\u6210\u98DE\u7070\uFF0C\u8F7B\u98D8\u98D8\u5728\u7A7A\u4E2D\u98DE\u821E\uFF1B\u90A3\u67C4\u957F\u5251\u8FD8\u5728\u65AD\u67F1\u4E0A\u8F7B\u8F7B\u98A4\u6296\uFF0C\u96EA\u5CF0\u795E\u5C3C\u5374\u4EFF\u4F5B\u5316\u4E3A\u7070\u70EC\u822C\u8E2A\u5F71\u7686\u65E0\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u738B\u540D\u6CFD\u5728\u5C71\u91CC\u8F6C\u4E86\u534A\u65E5\uFF0C\u53C8\u56DE\u5230\u5F53\u521D\u8D70\u8FC7\u7684\u5C71\u6EAA\u65C1\u3002\u6B64\u65F6\u65E5\u8584\u897F\u5C71\uFF0C\u4F46\u56DB\u6708\u5929\u957F\uFF0C\u79BB\u5929\u9ED1\u8FD8\u8FDC\uFF0C\u738B\u540D\u6CFD\u5374\u9053\uFF1A\u300C\u59D1\u5A18\uFF0C\u5929\u9ED1\u7684\u770B\u4E0D\u6E05\uFF0C\u4E0D\u5982\u5BBF\u4E00\u665A\uFF0C\u660E\u5929\u63A5\u7740\u8D76\u8DEF\uFF1F\u300D</p><p>\u6797\u9999\u8FDC\u6D51\u8EAB\u9999\u6C57\u6DCB\u6F13\uFF0C\u7528\u6765\u88F9\u811A\u7684\u8863\u895F\u65E9\u5DF2\u7834\u788E\u4E0D\u582A\uFF0C\u7EA4\u8DB3\u4F24\u75D5\u7D2F\u7D2F\uFF0C\u5C24\u5176\u662F\u80A1\u95F4\u7684\u4F24\u5904\uFF0C\u4E00\u8FC8\u6B65\u4FBF\u970D\u970D\u4F5C\u75DB\u3002\u5979\u7CBE\u75B2\u529B\u5C3D\u7684\u70B9\u4E86\u70B9\u5934\uFF0C\u6478\u7D22\u7740\u5750\u5230\u4E00\u68F5\u6811\u4E0B\uFF0C\u626F\u597D\u8863\u670D\uFF0C\u76D8\u819D\u8C03\u606F\u3002\u5F00\u59CB\u91CD\u65B0\u4FEE\u884C\u7684\u8270\u96BE\u5386\u7A0B\u3002</p><p>\u738B\u540D\u6CFD\u5C4F\u4F4F\u547C\u5438\uFF0C\u7528\u4E00\u6839\u5C0F\u6811\u679D\u8F7B\u8F7B\u6311\u5F00\u8863\u895F\uFF0C\u671D\u5C11\u5987\u8179\u4E0B\u770B\u53BB\u3002\u80A1\u95F4\u6D01\u767D\u7684\u50E7\u888D\u7834\u4E86\u4E00\u4E2A\u5927\u6D1E\uFF0C\u788E\u8863\u8FB9\u7F18\u6CBE\u6EE1\u6CE5\u571F\u8840\u8FF9\uFF0C\u810F\u4E71\u4E0D\u582A\u3002\u8863\u4E0B\u9690\u9690\u9732\u51FA\u96EA\u767D\u7684\u817F\u6839\u548C\u4E00\u62B9\u7EA2\u80BF\u7684\u5AE9\u8089\u3002</p><p>\u4ED6\u58EE\u8D77\u80C6\u5B50\u62E8\u5F00\u7834\u6D1E\uFF0C\u67D4\u7F8E\u7684\u82B1\u74E3\u6E10\u6E10\u7EFD\u73B0\uFF0C\u4EFF\u4F5B\u4E00\u7247\u7EA2\u83B2\u4ECE\u8179\u4E0B\u4F38\u4F38\u5C16\u5C16\u4E00\u89D2\u3002\u9876\u7AEF\u7684\u82B1\u8482\u65C1\u8FB9\u88AB\u523A\u51FA\u4E24\u4E2A\u5C0F\u5B54\uFF0C\u4E00\u6839\u7EA4\u7EC6\u800C\u575A\u56FA\u7684\u94C1\u94FE\u4ECE\u4E2D\u7A7F\u51FA\uFF0C\u968F\u7740\u5C11\u5987\u7684\u5410\u7EB3\u5FAE\u5FAE\u98A4\u52A8\u3002\u738B\u540D\u6CFD\u556E\u7740\u7259\u770B\u4E86\u534A\u5929\uFF0C\u59CB\u7EC8\u53EA\u80FD\u770B\u5230\u79D8\u5904\u4E00\u89D2\uFF0C\u4E8E\u662F\u4ECE\u8349\u4E1B\u4E2D\u6478\u4E86\u53EA\u7532\u866B\uFF0C\u5F39\u5230\u5979\u8179\u4E0B\u3002</p><p>\u6797\u9999\u8FDC\u4E00\u60CA\uFF0C\u8FDE\u5FD9\u4E00\u624B\u6293\u7D27\u957F\u5251\uFF0C\u4E00\u624B\u63A2\u5230\u79D8\u5904\u3002\u5F85\u6478\u51FA\u662F\u53EA\u7532\u866B\uFF0C\u5979\u7389\u6307\u4E00\u637B\uFF0C\u6068\u6068\u5C06\u7532\u866B\u634F\u788E\u3002\u7A81\u7136\u4E4B\u95F4\u5FC3\u5E95\u6CDB\u8D77\u4E00\u80A1\u53C8\u9178\u53C8\u6DA9\u7684\u75DB\u695A\uFF0C\u5979\u62BD\u6CE3\u7740\u64E6\u51C0\u6307\u5C16\uFF0C\u4ED4\u7EC6\u62C9\u597D\u8863\u895F\u3002</p><p>\u738B\u540D\u6CFD\u7709\u98DE\u8272\u821E\uFF0C\u5FC3\u91CC\u72C2\u7B11\u4E0D\u5DF2\uFF0C\u4EC0\u4E48\u6B66\u6797\u5973\u4FA0\u5BD2\u6708\u5200\uFF0C\u8FD8\u4E0D\u662F\u8BA9\u8001\u5B50\u968F\u4FBF\u620F\u5F04\u7684\u778E\u5A4A\u5B50\u3002\u5FFD\u7136\u4E00\u53EA\u8001\u9F20\u4ECE\u8EAB\u8FB9\u7A9C\u8FC7\uFF0C\u4ED6\u4E5F\u771F\u6709\u51E0\u5206\u529F\u592B\uFF0C\u51FA\u624B\u5982\u7535\uFF0C\u4E00\u628A\u5C06\u8001\u9F20\u6293\u5728\u624B\u4E2D\uFF0C\u7136\u540E\u6162\u6162\u671D\u6797\u9999\u8FDC\u79D8\u5904\u585E\u53BB\u3002</p><p>\u4E00\u56E2\u6E29\u70ED\u7684\u6D3B\u7269\u7A81\u7136\u5728\u8179\u4E0B\u6323\u624E\u8D77\u6765\uFF0C\u6797\u9999\u8FDC\u5927\u60CA\u5931\u8272\u3002\u5979\u4F38\u624B\u4E00\u6293\uFF0C\u5C45\u7136\u662F\u53EA\u6BDB\u8338\u8338\u7684\u8001\u9F20\u94BB\u5230\u80EF\u95F4\uFF0C\u987F\u65F6\u53C8\u662F\u6076\u5FC3\u53C8\u662F\u6050\u60E7\uFF0C\u614C\u5FD9\u6495\u624B\u6254\u5F00\uFF0C\u7AD9\u8D77\u8EAB\u4E0D\u4F4F\u5598\u606F\u3002</p><p>\u5598\u606F\u7247\u523B\uFF0C\u6797\u9999\u8FDC\u6162\u6162\u5E73\u9759\u4E0B\u6765\uFF0C\u5979\u62FF\u8D77\u957F\u5251\uFF0C\u6478\u7D22\u7740\u671D\u6D41\u6C34\u5904\u8D70\u53BB\u3002</p><p>\u738B\u540D\u6CFD\u5DEE\u70B9\u513F\u7B11\u7834\u809A\u76AE\uFF0C\u773C\u89C1\u6797\u9999\u8FDC\u8981\u300C\u8D81\u591C\u8272\u300D\u53BB\u6EAA\u91CC\u6D17\u6FA1\uFF0C\u8FDE\u5FD9\u8E51\u624B\u8E51\u811A\u5730\u8DDF\u5728\u540E\u9762\u3002</p><p>\u6EAA\u6C34\u4EFF\u4F5B\u6E29\u6696\u7684\u624B\u638C\uFF0C\u8FD8\u5E26\u7740\u9633\u5149\u7684\u70ED\u5EA6\u3002\u6797\u9999\u8FDC\u8BD5\u63A2\u7740\u8D70\u5230\u6C34\u4E2D\uFF0C\u9178\u75DB\u6C61\u6D4A\u7684\u8EAB\u4F53\u88AB\u6E05\u6F88\u7684\u6D41\u6C34\u6162\u6162\u5305\u56F4\uFF0C\u4EFF\u4F5B\u6709\u79CD\u8680\u9AA8\u7684\u6E29\u67D4\uFF0C\u5979\u4E0D\u7531\u957F\u957F\u51FA\u4E86\u53E3\u6C14\uFF0C\u653E\u677E\u4E0B\u6765\u3002</p><p>\u6DE1\u9EC4\u8272\u7684\u9633\u5149\u91D1\u5C51\u822C\u6D6E\u5728\u6C34\u9762\u4E0A\uFF0C\u987A\u6D41\u800C\u4E0B\uFF0C\u5C11\u5987\u4FA7\u8EAB\u5750\u5728\u6EAA\u4E2D\uFF0C\u6D51\u8EAB\u6C90\u6D74\u7740\u91D1\u707F\u707F\u7684\u5149\u8F89\u3002\u5979\u5F13\u4E0B\u8170\u80A2\uFF0C\u7389\u624B\u63A2\u5165\u8702\u871C\u822C\u9ECF\u7A20\u7684\u6D41\u6C34\uFF0C\u63A5\u5728\u9999\u8F6F\u767D\u5AE9\u7684\u7EA4\u8DB3\u4E0A\u3002\u52A8\u4F5C\u4F18\u96C5\u800C\u53C8\u7EC6\u817B\uFF0C\u5145\u6EE1\u5973\u6027\u9B45\u529B\u3002</p><p>\u738B\u540D\u6CFD\u8272\u5FC3\u5927\u52A8\uFF0C\u4E5F\u8DDF\u7740\u6F5C\u5230\u6C34\u4E2D\uFF0C\u4F38\u51FA\u9F3B\u5B50\u5728\u7C89\u5AE9\u7684\u67D4\u9888\u540E\u6DF1\u6DF1\u4E00\u5438\uFF0C\u7422\u78E8\u7740\u600E\u4E48\u73A9\u5F04\u8FD9\u4E2A\u5931\u660E\u7684\u7F8E\u5987\u3002</p><p>\u6B63\u601D\u7D22\u95F4\uFF0C\u5FFD\u7136\u8179\u4E0B\u4E00\u51C9\uFF0C\u738B\u540D\u6CFD\u6115\u7136\u4F4E\u5934\uFF0C\u53D1\u73B0\u8170\u8179\u4E2D\u8D6B\u7136\u591A\u51FA\u4E00\u622A\u96EA\u4EAE\u7684\u5251\u950B\u3002</p><p>\u82E5\u4E0D\u662F\u90A3\u53EA\u7A81\u7136\u51FA\u73B0\u7684\u8001\u9F20\uFF0C\u6797\u9999\u8FDC\u4E5F\u4E0D\u4F1A\u6000\u7591\u4ED6\u7684\u8EAB\u4EFD\u3002\u5F53\u65F6\u60CA\u9B42\u672A\u5B9A\uFF0C\u65E5\u95F4\u8BF8\u822C\u8E4A\u8DF7\u4E4B\u4E8B\u4E00\u4E00\u6D8C\u4E0A\u5FC3\u5934\uFF0C\u5979\u604D\u7136\u660E\u767D\u81EA\u5DF1\u662F\u88AB\u4EBA\u620F\u5F04\u4E86\u3002\u4F46\u5979\u6B66\u529F\u88AB\u5E9F\uFF0C(\u8D3C\u5427\uFF3Aei\uFF18.\uFF23\uFF2F\uFF2D\u7535\u5B50\u4E66)\u82E5\u662F\u786C\u62FC\u6839\u672C\u4E0D\u662F\u4ED6\u7684\u5BF9\u624B\uFF0C\u56E0\u6B64\u82E5\u65E0\u5176\u4E8B\u5730\u8D70\u5230\u6EAA\u4E2D\u8BC8\u505A\u6D17\u6D74\uFF0C\u8D81\u4ED6\u6328\u5230\u8EAB\u540E\u65F6\u9661\u7136\u4E00\u5251\u523A\u51FA\u3002</p><p>\u4E00\u51FB\u5F97\u624B\uFF0C\u6797\u9999\u8FDC\u7ACB\u523B\u95EA\u8EAB\u8EB2\u907F\u3002\u4F46\u5979\u4E8F\u5728\u76EE\u4E0D\u89C1\u7269\uFF0C\u738B\u540D\u6CFD\u5927\u53EB\u4E00\u58F0\uFF0C\u62AC\u638C\u6253\u5728\u6797\u9999\u8FDC\u80A9\u540E\u3002\u6797\u9999\u8FDC\u5E94\u624B\u629B\u8DCC\uFF0C\u5A07\u8EAF\u91CD\u91CD\u6454\u5728\u5CB8\u8FB9\uFF0C\u6E85\u8D77\u4E00\u7247\u6C34\u82B1\u3002</p><p>\u660F\u8FF7\u524D\uFF0C\u5979\u9690\u7EA6\u542C\u5230\u7A7A\u4E2D\u8FDC\u8FDC\u4F20\u6765\u4E00\u58F0\u7206\u54CD\u3002</p>',52);function t(i,_){return a}var d=e(r,[["render",t]]);export{d as default};
