import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_42" tabindex="-1"><a class="header-anchor" href="#_42" aria-hidden="true">#</a> 42</h1><p>\u6155\u5BB9\u7D2B\u73AB\u88AB\u795E\u6BBF\u4F20\u6765\u7684\u5267\u70C8\u7206\u70B8\u58F0\u60CA\u9192\uFF0C\u5979\u8FDE\u5FD9\u8DD1\u5230\u6D1E\u53E3\u542C\u4E86\u534A\u664C\uFF0C\u53EF\u4EC0\u4E48\u90FD\u6CA1\u542C\u5230\uFF0C\u53EA\u597D\u60BB\u60BB\u7136\u56DE\u5230\u77F3\u5BAB\u3002</p><p>\u5927\u5385\u6B63\u4E2D\u7684\u592A\u6781\u56FE\u4E0A\uFF0C\u6446\u653E\u7740\u9634\u5BAB\u4E3B\u4E0E\u516C\u725B\u4EA4\u5408\u7684\u8273\u5C38\uFF0C\u6BCF\u6B21\u4ECE\u5979\u8EAB\u8FB9\u8D70\u8FC7\uFF0C\u4EFF\u4F5B\u90FD\u80FD\u542C\u5230\u5C38\u4F53\u6DE1\u6DE1\u7684\u547C\u5438\u3002\u7D2B\u73AB\u628A\u8138\u626D\u5230\u4E00\u65C1\uFF0C\u5306\u5306\u8D70\u8FC7\uFF0C\u7EAA\u7709\u59A9\u7684\u4F24\u52BF\u597D\u4E86\u8BB8\u591A\u3002\u78A7\u7EFF\u8272\u7684\u836F\u818F\u50CF\u662F\u88AB\u5AE9\u8089\u5B8C\u5168\u5438\u6536\u822C\uFF0C\u6CA1\u6709\u7559\u4E0B\u4E00\u4E1D\u75D5\u8FF9\uFF0C\u4E0B\u4F53\u7D2B\u9ED1\u8272\u7684\u6DE4\u8840\u5DF2\u7ECF\u6563\u5F00\uFF0C\u4F46\u82B1\u74E3\u4F9D\u7136\u80BF\u80C0\u5982\u6545\uFF0C\u7EA2\u8273\u8273\u5145\u6EE1\u8840\u8272\uFF0C\u50CF\u662F\u4E00\u5F20\u4E30\u6EE1\u7684\u5634\u5507\u3002</p><p>\u300C\u7EAA\u5E08\u59D0\uFF0C\u611F\u89C9\u600E\u4E48\u6837\uFF1F\u6709\u6CA1\u6709\u4EC0\u4E48\u4E0D\u8212\u670D\uFF1F\u300D</p><p>\u7EAA\u7709\u59A9\u5446\u5446\u770B\u7740\u5BA4\u9876\uFF0C\u534A\u664C\u8F7B\u8F7B\u6447\u4E86\u6447\u5934\uFF0C\u773C\u89D2\u95EA\u4EAE\u7684\u6CEA\u73E0\u968F\u4E4B\u6ED1\u843D\uFF0C\u6CA1\u6709\u8BF4\u8BDD\u3002</p><p>\u7D2B\u73AB\u7B97\u7B97\u65F6\u95F4\uFF0C\u867D\u7136\u4E0D\u5230\u4E09\u4E2A\u65F6\u8FB0\uFF0C\u4F46\u73B0\u5728\u836F\u818F\u5DF2\u7ECF\u5316\u5C3D\uFF0C\u591A\u62B9\u4E9B\u4E5F\u65E0\u59A8\u3002</p><p>\u4E8E\u662F\u62FF\u51FA\u53F6\u884C\u5357\u6240\u5236\u7684\u836F\u7269\uFF0C\u7EC6\u7EC6\u7ED9\u5E08\u59D0\u6D82\u4E0A\uFF0C\u53E3\u91CC\u5B89\u6170\u9053\uFF1A\u300C\u5E08\u59D0\uFF0C\u522B\u7740\u6025\u3002</p><p>\u5E08\u7236\u5DF2\u7ECF\u6765\u6551\u6211\u4EEC\u4E86\u3002\u7B49\u4F24\u6CBB\u597D\uFF0C\u54B1\u4EEC\u4E5F\u8BE5\u79BB\u5F00\u8FD9\u4E2A\u9B3C\u5730\u65B9\u4E86\u3002\u300D</p><p>\u7EAA\u7709\u59A9\u51C4\u7136\u4E00\u7B11\uFF0C\u59E3\u4E3D\u7684\u8138\u4E0A\u9732\u51FA\u865A\u8131\u822C\u7684\u60B2\u51C9\uFF0C\u7F13\u7F13\u95ED\u4E0A\u773C\u775B\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u96EA\u5CF0\u795E\u5C3C\u8D81\u970D\u72C2\u7130\u7565\u4E00\u53D1\u6014\u7684\u65F6\u673A\uFF0C\u771F\u6C14\u4EE5\u95EA\u7535\u822C\u901F\u5EA6\u7684\u6D41\u8F6C\uFF0C\u786C\u751F\u751F\u5C06\u524D\u63A0\u4E4B\u52BF\u6539\u4E3A\u540E\u8DC3\uFF0C\u63A0\u5F80\u795E\u6BBF\u5927\u95E8\u3002\u5728\u7834\u7A7A\u96F7\u7206\u70B8\u524D\u7684\u4E00\u5239\u90A3\uFF0C\u501A\u4ED7\u81EA\u5DF1\u76D6\u4E16\u795E\u529F\u907F\u8FC7\u4E86\u6740\u8EAB\u4E4B\u7978\uFF0C\u4F46\u80CC\u90E8\u7ECF\u8109\u4E5F\u88AB\u5DE8\u5927\u7684\u51B2\u51FB\u529B\u9707\u4F24\u3002</p><p>\u89C5\u5730\u4F11\u517B\u4E00\u4E2A\u65F6\u8FB0\u4E4B\u540E\uFF0C\u4FEE\u70BC\u81F3\u7B2C\u4E03\u5C42\u7684\u51E4\u51F0\u5B9D\u5178\u5A01\u529B\u5C3D\u663E\uFF0C\u4F24\u52BF\u5DF2\u597D\u4E86\u5927\u534A\u3002</p><p>\u771F\u6C14\u7F13\u7F13\u6563\u81F3\u4E09\u8F6E\u4E03\u8109\uFF0C\u96EA\u5CF0\u795E\u5C3C\u5410\u6C14\u6536\u529F\uFF0C\u51DD\u76EE\u6C89\u601D\u3002\u6570\u5341\u5E74\u6765\uFF0C\u5979\u5468\u6E38\u5929\u4E0B\u65E0\u5F80\u4E0D\u5229\uFF0C\u6B64\u756A\u56DB\u95EF\u9B54\u5BAB\u7ADF\u7136\u4E09\u6B21\u8D1F\u4F24\uFF0C\u661F\u6708\u6E56\u7684\u5F3A\u52B2\u5B9E\u5728\u51FA\u4E4E\u5979\u7684\u610F\u6599\u3002</p><p>\u4EE5\u4E00\u5DF1\u4E4B\u529B\u5BF9\u4ED8\u8FD9\u6837\u4E00\u4E2A\u5E9E\u5927\u7684\u7EC4\u7EC7\uFF0C\u53EA\u80FD\u4E00\u70B9\u70B9\u6D88\u8017\u5BF9\u65B9\u5B9E\u529B\uFF0C\u6700\u540E\u518D\u7ED9\u4E88\u5176\u81F4\u547D\u4E00\u51FB\u3002</p><p>\u4E8B\u4E0D\u5B9C\u8FDF\uFF0C\u96EA\u5CF0\u795E\u5C3C\u500F\u7136\u8D77\u8EAB\uFF0C\u4ECE\u6811\u4E0A\u8DC3\u4E0B\u3002\u5C31\u5728\u8FD9\u65F6\uFF0C\u8FDC\u5904\u7A81\u7136\u5347\u8D77\u4E00\u9897\u6D41\u661F\uFF0C\u5728\u660F\u9EC4\u7684\u5929\u9645\u7206\u5F00\u3002\u96EA\u5CF0\u795E\u5C3C\u5FC3\u4E0B\u4E00\u52A8\uFF0C\u7ACB\u523B\u671D\u6D41\u661F\u5347\u8D77\u5904\u63A0\u53BB\u3002</p><p>\u5728\u5C71\u4E2D\u6025\u884C\u4E8C\u5341\u4F59\u91CC\u4E4B\u540E\uFF0C\u773C\u524D\u51FA\u73B0\u4E00\u6761\u4E24\u4E08\u5BBD\u7A84\u7684\u5C0F\u6EAA\u3002\u6E05\u6F88\u7684\u6EAA\u6C34\u873F\u8712\u6D41\u8FC7\uFF0C\u6C47\u5165\u91CC\u8BB8\u5916\u7684\u661F\u6708\u6E56\u3002\u5468\u56F4\u5DE8\u6728\u53C2\u5929\uFF0C\u85E4\u8513\u8499\u7F57\uFF0C\u5982\u8BD7\u5982\u753B\u3002</p><p>\u4E00\u4E2A\u7537\u5B50\u4EF0\u9762\u5012\u5728\u6EAA\u4E2D\uFF0C\u4E00\u67C4\u5229\u5251\u659C\u659C\u4ECE\u8179\u4E0B\u523A\u5165\uFF0C\u5251\u9537\u4E4B\u4E0B\u4EC5\u9732\u51FA\u5BF8\u8BB8\u9752\u950B\u3002\u4ED6\u4E24\u773C\u7A81\u8D77\uFF0C\u5145\u6EE1\u6115\u7136\u4E4B\u8272\uFF0C\u663E\u7136\u5DF2\u6B7B\u591A\u65F6\u3002\u6EAA\u7554\u4F0F\u7740\u4E00\u4E2A\u4E0D\u7701\u4EBA\u4E8B\u7684\u5973\u5B50\uFF0C\u79C0\u53D1\u6563\u5728\u77F3\u4E0A\uFF0C\u8170\u8EAB\u4EE5\u4E0B\u6D78\u5728\u6C34\u4E2D\u3002\u7834\u788E\u7684\u767D\u8863\u6D6E\u5728\u6C34\u9762\u4E0A\u4E0D\u4F4F\u6F02\u8361\uFF0C\u6B63\u662F\u795E\u5C3C\u81EA\u5DF1\u7684\u50E7\u888D\u3002</p><p>\u96EA\u5CF0\u795E\u5C3C\u8FDE\u5FD9\u6276\u8D77\u6797\u9999\u8FDC\u3002\u624B\u6307\u642D\u4E0A\u5979\u7684\u8109\u95E8\uFF0C\u4FBF\u77E5\u7231\u5F92\u8EAB\u53D7\u91CD\u51FB\uFF0C\u751F\u673A\u5C06\u7EDD\u3002\u795E\u5C3C\u7ACB\u5373\u5C06\u5979\u62B1\u5728\u6000\u4E2D\uFF0C\u4E00\u624B\u629A\u7740\u540E\u5FC3\uFF0C\u4E00\u624B\u6309\u4F4F\u4E39\u7530\uFF0C\u7F13\u7F13\u6E21\u5165\u771F\u6C14\u3002\u9996\u5148\u62A4\u4F4F\u5979\u7684\u5FC3\u8109\uFF0C\u7136\u540E\u518D\u8C03\u517B\u91CD\u521B\u7684\u7ECF\u7EDC\u3002</p><p>\u591C\u98CE\u62C2\u8FC7\uFF0C\u7EFF\u53F6\u67D4\u67D4\u8212\u5C55\uFF0C\u54CD\u8D77\u4E00\u7247\u60A6\u8033\u7684\u6F6E\u58F0\u3002\u96EA\u5CF0\u795E\u5C3C\u7075\u53F0\u7A7A\u660E\uFF0C\u4E00\u8FB9\u8FD0\u529F\u6551\u6CBB\uFF0C\u4E00\u8FB9\u5C06\u5468\u56F4\u7684\u52A8\u9759\u949C\u7EC6\u65E0\u9057\uFF0C\u4E00\u4E00\u6536\u5165\u8033\u4E2D\u3002\u82E5\u975E\u9999\u8FDC\u53D7\u4F24\u592A\u91CD\uFF0C\u5979\u7EDD\u4E0D\u4F1A\u5728\u79BB\u661F\u6708\u6E56\u5982\u6B64\u8FD1\u7684\u5730\u65B9\u5927\u8017\u771F\u5143\u3002</p><p>\u534A\u4E2A\u65F6\u8FB0\u4E4B\u540E\uFF0C\u6797\u9999\u8FDC\u5FC3\u8109\u4E00\u9707\uFF0C\u7F13\u7F13\u8DF3\u52A8\u8D77\u6765\u3002\u96EA\u5CF0\u795E\u5C3C\u677E\u4E86\u53E3\u6C14\uFF0C\u77E5\u9053\u5DF2\u4FDD\u4F4F\u5F92\u513F\u7684\u6027\u547D\u3002\u4F46\u82E5\u662F\u5C31\u6B64\u7F62\u624B\uFF0C\u6797\u9999\u8FDC\u7EB5\u7136\u4FDD\u4F4F\u6027\u547D\uFF0C\u4E5F\u53EA\u662F\u5E9F\u4EBA\uFF0C\u7EC8\u8EAB\u65E0\u6CD5\u4E60\u6B66\u3002\u56E0\u6B64\u795E\u5C3C\u6BEB\u4E0D\u505C\u987F\u7684\u7EE7\u7EED\u50AC\u53D1\u771F\u6C14\uFF0C\u5C06\u6797\u9999\u8FDC\u4F53\u5185\u6B8B\u4F59\u7684\u771F\u6C14\u6536\u62E2\u8D77\u6765\uFF0C\u9001\u5165\u4E39\u7530\u3002</p><p>\u4E24\u4EBA\u771F\u6C14\u4EA4\u878D\uFF0C\u521A\u521A\u6D41\u8F6C\u4E00\u5C0F\u5468\u5929\uFF0C\u96EA\u5CF0\u795E\u5C3C\u4E39\u7530\u7A81\u7136\u4E00\u987F\uFF0C\u4E00\u53E3\u771F\u6C14\u505C\u5728\u534A\u9014\uFF0C\u505C\u6EDE\u7247\u523B\u540E\u8F70\u7136\u6563\u5F00\uFF0C\u5C31\u6B64\u6D88\u5931\u5F97\u65E0\u5F71\u65E0\u8E2A\u3002\u96EA\u5CF0\u795E\u5C3C\u5FC3\u4E0B\u5927\u60CA\uFF0C\u8FDE\u5FD9\u51DD\u795E\u805A\u6C14\u3002\u53EF\u4E39\u7530\u4F3C\u4E4E\u53D8\u6210\u4E86\u4E00\u4E2A\u65E0\u5E95\u7684\u6DF1\u6D1E\uFF0C\u6C79\u6D8C\u7684\u771F\u6C14\u6D41\u5165\u5176\u4E2D\uFF0C\u7ACB\u5373\u5316\u4F5C\u4E1D\u4E1D\u7F15\u7F15\u7684\u6E38\u6C14\uFF0C\u6563\u81F3\u56DB\u80A2\u767E\u9AB8\u3002</p><p>\u8FDC\u5904\u4F20\u6765\u4E00\u58F0\u6717\u7B11\uFF0C\u6155\u5BB9\u9F99\u4E24\u624B\u8D1F\u5728\u8EAB\u540E\uFF0C\u4ECE\u6811\u540E\u60A0\u7136\u8E31\u51FA\uFF0C\u53F9\u9053\uFF1A\u300C\u5E08\u592A\u679C\u7136\u662F\u795E\u529F\u8D85\u7FA4\uFF0C\u9119\u6559\u5316\u771F\u6563\u795E\u5999\u83AB\u6D4B\uFF0C\u53EA\u9700\u4E1D\u6BEB\u4FBF\u53EF\u6563\u53BB\u5168\u8EAB\u529F\u529B\uFF0C\u795E\u5C3C\u7ADF\u80FD\u6491\u4E0A\u8FD9\u4E48\u4E45\u2026\u2026\u597D\u529F\u529B\uFF0C\u597D\u529F\u529B\uFF01\u300D</p><p>\u96EA\u5CF0\u795E\u5C3C\u8138\u8272\u82CD\u767D\uFF0C\u9762\u4E0A\u5374\u6CA1\u6709\u4EFB\u4F55\u6CE2\u52A8\u3002\u5979\u653E\u4E0B\u6797\u9999\u8FDC\uFF0C\u7F13\u7F13\u8D77\u8EAB\u3002</p><p>\u6155\u5BB9\u9F99\u773C\u4E2D\u63A0\u8FC7\u4E00\u4E1D\u8BB6\u8272\uFF0C\u5C0F\u5FC3\u7FFC\u7FFC\u5730\u63D0\u805A\u771F\u6C14\uFF0C\u9632\u5907\u8FD9\u4E2A\u529F\u76D6\u5F53\u4E16\u7684\u7EDD\u9876\u9AD8\u624B\u8FD8\u6709\u4F59\u529B\u66B4\u8D77\u53D1\u96BE\u3002</p><p>\u738B\u540D\u6CFD\u4E34\u6B7B\u524D\u7EC8\u4E8E\u653E\u51FA\u62A5\u8B66\u7684\u6D41\u661F\u3002\u4ED6\u5E26\u7740\u6797\u9999\u8FDC\u5728\u5C71\u4E2D\u7ED5\u4E86\u4E00\u5929\uFF0C\u51C6\u5907\u597D\u597D\u73A9\u5F04\u5979\u4E00\u756A\u518D\u9001\u5230\u5BAB\u4E2D\uFF0C\u6700\u540E\u505C\u5728\u6E56\u8FB9\u3002\u8FD9\u6837\u5C31\u4F7F\u6155\u5BB9\u9F99\u80FD\u5728\u7B2C\u4E00\u65F6\u95F4\u8D76\u81F3\u3002</p><p>\u6155\u5BB9\u9F99\u89C1\u5230\u6D41\u661F\uFF0C\u4EE5\u4E3A\u6709\u4EBA\u53D1\u73B0\u4E86\u795E\u5C3C\u7684\u8E2A\u8FF9\uFF0C\u7ACB\u5373\u7387\u9886\u6559\u4E2D\u6B8B\u4F59\u7684\u9AD8\u624B\u503E\u5DE2\u800C\u51FA\uFF0C\u6CA1\u60F3\u5230\u5374\u662F\u5931\u8E2A\u7684\u6797\u9999\u8FDC\u3002\u4ED6\u8D81\u6797\u9999\u8FDC\u660F\u8FF7\uFF0C\u7ED9\u5979\u670D\u4E0B\u53EF\u901A\u8FC7\u771F\u6C14\u4EA4\u6362\u4F20\u64AD\u7684\u5316\u771F\u6563\uFF0C\u968F\u540E\u53C8\u8865\u4E86\u4E00\u638C\uFF0C\u4F7F\u5979\u91CD\u4F24\u6FD2\u6B7B\u3002\u7136\u540E\u4F0F\u5728\u6697\u4E2D\u89C2\u5BDF\u52A8\u9759\u3002\u5F85\u96EA\u5CF0\u795E\u5C3C\u5927\u8017\u529F\u529B\uFF0C\u65E0\u6CD5\u9A71\u9664\u5316\u771F\u6563\u7684\u836F\u6027\uFF0C\u6155\u5BB9\u9F99\u624D\u4ECE\u5BB9\u73B0\u8EAB\u3002</p><p>\u4E24\u4EBA\u50F5\u6301\u534A\u664C\uFF0C\u6155\u5BB9\u9F99\u51B7\u7136\u4E00\u7B11\uFF0C\u7FFB\u638C\u5E73\u5E73\u63A8\u51FA\u3002\u9694\u4E86\u4E24\u4E08\u8DDD\u79BB\uFF0C\u4ED6\u7684\u638C\u529B\u5E76\u4E0D\u51CC\u5389\uFF0C\u4F46\u5185\u529F\u5C3D\u6563\u7684\u96EA\u5CF0\u795E\u5C3C\u6839\u672C\u65E0\u529B\u76F8\u6297\uFF0C\u8EAB\u5B50\u4E00\u6643\uFF0C\u5012\u5728\u6797\u9999\u8FDC\u8EAB\u65C1\uFF0C\u51B7\u82E5\u51B0\u971C\u7684\u7389\u5BB9\u5145\u6EE1\u6068\u610F\u3002\u6155\u5BB9\u9F99\u773C\u4E2D\u5F02\u5F69\u8FDE\u95EA\uFF0C\u76EF\u7740\u795E\u5C3C\u5C11\u5973\u822C\u7EC6\u81F4\u7684\u7EA4\u8170\uFF0C\u5634\u89D2\u6162\u6162\u9732\u51FA\u4E00\u4E1D\u5145\u6EE1\u6DEB\u90AA\u610F\u5473\u7684\u72DE\u7B11\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u300C\u53F6\u62A4\u6CD5\u2026\u2026\u5974\u5A62\u91CC\u9762\u2026\u2026\u6709\u4E9B\u7D27\u4E86\u2026\u2026\u300D\u767D\u7389\u83BA\u5A07\u5598\u606F\u606F\uFF0C\u6EE1\u8138\u7EA2\u6655\u7684\u4F4E\u58F0\u8BF4\u3002\u5979\u4E0E\u59B9\u59B9\u4E00\u4E0A\u4E00\u4E0B\u9762\u5BF9\u9762\u4F0F\u5728\u6848\u4E0A\u3002\u4E24\u5177\u767D\u5AE9\u7684\u8089\u4F53\u7D27\u7D27\u8D34\u5728\u4E00\u8D77\uFF0C\u4EA4\u9888\u800C\u5367\uFF0C\u5B9B\u5982\u6C34\u9762\u4E0A\u7684\u5012\u5F71\uFF0C\u7EA4\u6BEB\u4E0D\u5DEE\u3002</p><p>\u300C\u55EF\u3002\u300D\u53F6\u884C\u5357\u4E0D\u7F6E\u53EF\u5426\u5730\u7B54\u5E94\u4E00\u58F0\uFF0C\u5C06\u4E39\u7802\u70BC\u51FA\u7684\u9EC4\u82BD\u6295\u5165\u65E2\u6D4E\u7089\u4E2D\uFF0C\u7136\u540E\u64E6\u4E86\u64E6\u624B\uFF0C\u76F4\u8D77\u8170\u6765\u3002</p><p>\u767D\u6C0F\u59D0\u59B9\u65E2\u5A07\u4FCF\u53EF\u7231\uFF0C\u53C8\u4E56\u5DE7\u67D4\u987A\uFF0C\u4F46\u5BF9\u4E8E\u53F6\u884C\u5357\u6765\u8BF4\uFF0C\u8FD9\u5BF9\u59D0\u59B9\u82B1\u8FD8\u6709\u7279\u6B8A\u610F\u4E49\u3002\u5404\u4EBA\u4F53\u8D28\u5343\u5DEE\u4E07\u522B\uFF0C\u884C\u533B\u7528\u836F\u6240\u7528\u7684\u5206\u5BF8\u3001\u5242\u91CF\u4E5F\u5404\u4E0D\u76F8\u540C\uFF0C\u4EE5\u5F80\u6BCF\u70BC\u5236\u4E00\u79CD\u65B0\u836F\uFF0C\u8BD5\u9A8C\u65F6\u90FD\u9700\u8981\u8003\u8651\u4F53\u8D28\u56E0\u7D20\uFF0C\u767E\u822C\u6BD4\u8F83\u659F\u914C\uFF0C\u8D39\u65F6\u8D39\u529B\u3002</p><p>\u800C\u767D\u7389\u83BA\u3001\u767D\u7389\u9E42\u8FD9\u5BF9\u5B6A\u751F\u59D0\u59B9\u5B9B\u5982\u4E00\u4EBA\uFF0C\u5242\u91CF\u591A\u5BE1\u5728\u4E24\u5973\u8EAB\u4E0A\u7684\u5DEE\u522B\u4E00\u76EE\u4E86\u7136\u3002\u56E0\u6B64\u70BC\u6210\u79CD\u5B50\u7075\u4E39\u4E4B\u540E\uFF0C\u53F6\u884C\u5357\u5148\u5524\u6765\u4E24\u5973\uFF0C\u5728\u5979\u4EEC\u8EAB\u4E0A\u8BD5\u9A8C\u4E00\u756A\u3002</p><p>\u767D\u6C0F\u59D0\u59B9\u80F8\u8179\u76F8\u8FDE\uFF0C\u56DB\u817F\u7EA0\u7F20\u7740\u642D\u5728\u6848\u4FA7\uFF0C\u7389\u6237\u5766\u9732\uFF0C\u6BB7\u7EA2\u7684\u82B1\u74E3\u5185\u5404\u9732\u51FA\u4E00\u6839\u767D\u8272\u68C9\u7EBF\u3002\u53F6\u884C\u5357\u62FF\u51FA\u9488\u7078\u6240\u7528\u7684\u6241\u5934\u94F6\u9488\uFF0C\u523A\u5165\u767D\u7389\u9E42\u4F1A\u9634\u3002\u95EA\u4EAE\u7684\u9488\u5C16\u7A7F\u7834\u808C\u80A4\uFF0C\u8F7B\u8F7B\u4E00\u62E8\uFF0C\u767D\u7389\u9E42\u4E0B\u8179\u4E00\u9635\u6536\u7F29\u3002\u82B1\u74E3\u5F00\u5408\u95F4\uFF0C\u68C9\u7EBF\u8F7B\u98A4\u7740\u5782\u843D\uFF0C\u4E00\u6761\u67D4\u8F6F\u7EC6\u957F\u7684\u80F6\u72B6\u7269\u4F53\uFF0C\u4ECE\u6ED1\u6DA6\u7684\u8089\u7A74\u5185\u7F13\u7F13\u6392\u51FA\u3002</p><p>\u94F6\u9488\u523A\u5165\u767D\u7389\u83BA\u4E0B\u4F53\u65F6\uFF0C\u68C9\u7EBF\u540C\u6837\u98A4\u6296\u8D77\u6765\uFF0C\u4F46\u59CB\u7EC8\u6CA1\u6709\u6389\u843D\u3002\u53F6\u884C\u5357\u634F\u4F4F\u7EBF\u5934\u4E00\u626F\uFF0C\u53D1\u73B0\u8089\u7A74\u4F9D\u7136\u5F39\u6027\u5341\u8DB3\uFF0C\u800C\u5B50\u5BAB\u9888\u5374\u7D27\u7D27\u95ED\u5408\uFF0C\u4E0D\u7531\u7709\u5934\u8212\u5C55\uFF0C\u79CD\u5B50\u7075\u4E39\u5DF2\u662F\u5927\u529F\u544A\u6210\u3002</p><p>\u767D\u7389\u83BA\u5374\u662F\u82E6\u4E0D\u582A\u8A00\uFF0C\u5979\u548C\u59B9\u59B9\u88AB\u6155\u5BB9\u9F99\u9501\u4F4F\u4E73\u5934\u9634\u8482\u8FDE\u5728\u4E00\u8D77\uFF0C\u5F7C\u6B64\u8FDE\u8170\u90FD\u65E0\u6CD5\u5F2F\u66F2\u3002\u8FD8\u662F\u5C11\u5973\u7684\u5B50\u5BAB\u88AB\u786C\u751F\u751F\u6345\u5165\u5F02\u7269\uFF0C\u6536\u7D27\u7684\u5BAB\u9888\u80C0\u75DB\u4E0D\u5DF2\u3002\u800C\u4E14\u836F\u7269\u5185\u8FD8\u542B\u6709\u50AC\u60C5\u6210\u5206\uFF0C\u6B64\u65F6\u5C0F\u7A74\u5185\u5DF2\u662F\u6DEB\u6C34\u8FDE\u8FDE\u3002</p><p>\u7D2B\u73AB\u63A8\u95E8\u800C\u5165\uFF0C\u7ACB\u523B\u53C8\u546F\u7684\u6454\u4E0A\u95E8\u3002</p><p>\u53F6\u884C\u5357\u6162\u6761\u65AF\u7406\u5730\u5728\u767D\u6C0F\u59D0\u59B9\u56DB\u4E2A\u8089\u7A74\u5185\u8F6E\u756A\u62BD\u9001\uFF0C\u5C04\u7CBE\u4E4B\u540E\u624D\u5F00\u8A00\u9053\uFF1A\u300C\u5C11\u592B\u4EBA\u6240\u6765\u4F55\u4E8B\uFF1F\u300D</p><p>\u7D2B\u73AB\u5728\u95E8\u5916\u9053\uFF1A\u300C\u6211\u5E08\u2026\u2026\u7EAA\u5974\u6709\u4E9B\u4E0D\u9002\uFF0C\u8BF7\u53F6\u62A4\u6CD5\u53BB\u770B\u4E00\u4E0B\u3002\u300D</p><p>\u767D\u6C0F\u59D0\u59B9\u76F8\u62E5\u7740\u51FA\u623F\u95E8\uFF0C\u5979\u4EEC\u4FE9\u53EA\u80FD\u5E76\u4F53\u6A2A\u884C\u6216\u662F\u4E00\u524D\u4E00\u9000\uFF0C\u884C\u8D70\u65F6\u4E73\u5934\u9634\u8482\u4E92\u76F8\u62C9\u626F\uFF0C\u4E0D\u65F6\u53D1\u51FA\u4F4E\u4F4E\u7684\u75DB\u53EB\uFF0C\u770B\u4E0A\u53BB\u8BA9\u4EBA\u53C8\u662F\u597D\u7B11\u53C8\u662F\u96BE\u8FC7\u3002</p><p>\u7D2B\u73AB\u5F85\u53F6\u884C\u5357\u8D70\u8FC7\uFF0C\u4F4E\u58F0\u95EE\u9053\uFF1A\u300C\u75DB\u5417\uFF1F\u300D</p><p>\u4E24\u5973\u5782\u9996\u65E0\u8A00\u3002</p><p>\u7D2B\u73AB\u53F9\u4E86\u53E3\u6C14\uFF0C\u65CB\u5373\u5C0F\u58F0\u9053\uFF1A\u300C\u5C0F\u83BA\u5C0F\u9E42\uFF0C\u665A\u4E0A\u90A3\u5BB6\u4F19\u5982\u679C\u53EB\u4F60\u4EEC\uFF0C\u4F60\u4EEC\u60F3\u529E\u6CD5\u628A\u7247\u7389\u62FF\u5230\u624B\uFF01\u5230\u65F6\u6211\u60F3\u529E\u6CD5\u628A\u9501\u524A\u6389\u3002\u300D</p><p>\u767D\u7389\u83BA\u4F4E\u58F0\u9053\uFF1A\u300C\u5974\u5A62\u77E5\u9053\u4E86\u3002\u300D</p><p>\u7D2B\u73AB\u4E00\u6014\uFF0C\u7A81\u7136\u7EAA\u7709\u59A9\u7684\u53EB\u58F0\u4F20\u6765\uFF0C\u5979\u8FDE\u5FD9\u53BB\u4E86\u3002</p><p>\u521A\u624D\u6D82\u4E0A\u836F\u540E\uFF0C\u7EAA\u7709\u59A9\u4E0B\u4F53\u50CF\u5931\u8840\u822C\u53D8\u6210\u8FD1\u4E4E\u900F\u660E\u7684\u767D\u8272\uFF0C\u63A5\u7740\u4FBF\u62BD\u6410\u8D77\u6765\u3002</p><p>\u7D2B\u73AB\u53C8\u60CA\u53C8\u6025\uFF0C\u8FDE\u5FD9\u6765\u627E\u53F6\u884C\u5357\u8BCA\u6CBB\u3002</p><p>\u53F6\u884C\u5357\u62E8\u5F04\u7740\u7EAA\u7709\u59A9\u80BF\u80C0\u7684\u79D8\u5904\uFF0C\u8BF4\u9053\uFF1A\u300C\u5C11\u592B\u4EBA\u6D82\u836F\u592A\u65E9\u4E86\u4E9B\u2026\u2026\u4E0D\u8FC7\u4E5F\u597D\uFF0C\u8840\u884C\u52A0\u901F\uFF0C\u836F\u6548\u66F4\u5FEB\u3002\u300D</p><p>\u7EAA\u7709\u59A9\u75DB\u82E6\u7684\u54AC\u4F4F\u7ECF\u5507\uFF0C\u5A07\u8EAF\u98A4\u6296\uFF0C\u56E0\u80BF\u80C0\u800C\u80A5\u539A\u7684\u82B1\u74E3\u50CF\u51B0\u822C\u5254\u900F\uFF0C\u91CC\u9762\u5BC6\u5E03\u7684\u8840\u7BA1\u6E05\u6670\u53EF\u8FA8\u3002</p><p>\u7D2B\u73AB\u7126\u6025\u5730\u8BF4\uFF1A\u300C\u8FD9\uFF0C\u600E\u4E48\u4F1A\u53D8\u6210\u8FD9\u6837\u2026\u2026\u53F6\u62A4\u6CD5\uFF0C\u662F\u4E0D\u662F\u7528\u9519\u836F\u4E86\uFF1F\u300D</p><p>\u53F6\u884C\u5357\u51B7\u54FC\u4E00\u58F0\uFF0C\u300C\u6B64\u836F\u4E43\u8001\u592B\u7A77\u5341\u5E74\u4E4B\u529F\u70BC\u5236\u800C\u5F97\uFF0C\u5C82\u4F1A\u7528\u9519\uFF1F\u5982\u679C\u7B49\u591F\u4E09\u4E2A\u65F6\u8FB0\uFF0C\u5F85\u4F24\u5904\u590D\u539F\u518D\u884C\u6D82\u62B9\u5FC5\u53EF\u6062\u590D\u5982\u521D\u3002\u5C11\u592B\u4EBA\u6D82\u5F97\u592A\u65E9\uFF0C\u4F24\u5904\u867D\u53EF\u6CBB\u7652\uFF0C\u4F46\u7EAA\u5974\u4E0B\u4F53\u7EC8\u751F\u5982\u6B64\uFF01\u300D</p><p>\u7D2B\u73AB\u5931\u58F0\u60CA\u53EB\uFF0C\u6123\u6123\u770B\u7740\u80BF\u8D77\u8DB3\u6709\u81EA\u5DF1\u624B\u638C\u5927\u5C0F\u7684\u79D8\u5904\uFF0C\u8BF4\u4E0D\u51FA\u8BDD\u6765\u3002</p><p>\u300C\u8FD8\u6709\u4E24\u6B21\uFF0C\u8BF7\u5C11\u592B\u4EBA\u8BB0\u6E05\uFF1A\u4E09\u4E2A\u65F6\u8FB0\u4E00\u62B9\u3002\u300D\u53F6\u884C\u5357\u51B7\u7B11\u7740\u51FA\u95E8\u800C\u53BB\u3002</p><p>\u5176\u5B9E\u7EAA\u7709\u59A9\u7684\u4F24\u52BF\u4E00\u6B21\u4FBF\u53EF\u6CBB\u7652\uFF0C\u4E4B\u6240\u4EE5\u8FDE\u7528\u56DB\u6B21\uFF0C\u662F\u56E0\u4E3A\u53F6\u884C\u5357\u5728\u836F\u91CC\u52A0\u4E86\u711A\u60C5\u818F\u3002\u8FD9\u711A\u60C5\u818F\u624D\u662F\u4ED6\u7A77\u5341\u5E74\u529F\u70BC\u5236\u7684\u79D8\u836F\uFF0C\u836F\u6548\u6DF1\u5165\u9AA8\u9AD3\uFF0C\u8DB3\u4EE5\u4F7F\u77F3\u5973\u53D8\u6210\u8361\u5987\u3002</p><p>\u8FDE\u65BD\u56DB\u6B21\u4E4B\u540E\uFF0C\u5C06\u5B8C\u5168\u6539\u53D8\u5973\u6027\u7684\u4F53\u8D28\uFF0C\u8EAB\u4F53\u7684\u654F\u611F\u5C06\u4EE5\u767E\u500D\u589E\u52A0\uFF0C\u751A\u81F3\u5FAE\u98CE\u8F7B\u62C2\u4E5F\u4F1A\u4F7F\u5973\u5B50\u5FEB\u611F\u8FDE\u8FDE\u3002\u4E0E\u4E4B\u540C\u65F6\uFF0C\u5973\u6027\u4E5F\u4F1A\u56E0\u6B64\u6B32\u706B\u711A\u8EAB\uFF0C\u65F6\u65F6\u5904\u4E8E\u9965\u6E34\u4E4B\u4E2D\u3002\u711A\u60C5\u818F\u914D\u5236\u4E0D\u6613\uFF0C\u82E5\u975E\u6155\u5BB9\u9F99\u62C5\u5FC3\u795E\u667A\u6E05\u9192\u7684\u7EAA\u7709\u59A9\u6210\u4E3A\u7D2B\u73AB\u7684\u5E2E\u624B\uFF0C\u4E5F\u4E0D\u4F1A\u65BD\u836F\u5C06\u5979\u53D8\u4E3A\u5E9F\u4EBA\u3002</p><p>\u6B64\u523B\u836F\u6548\u8FD8\u672A\u66FE\u53D1\u4F5C\uFF0C\u7D2B\u73AB\u53EA\u77E5\u9053\u5E08\u59D0\u4E0B\u4F53\u7684\u80BF\u80C0\u518D\u65E0\u6CD5\u6D88\u9664\uFF0C\u5374\u4E0D\u77E5\u9053\u8FD9\u4E2A\u7F9E\u6DA9\u6E29\u5A49\u7684\u5973\u5B50\u5C06\u4ECE\u6B64\u6C89\u6CA6\u6B32\u6D77\uFF0C\u53D8\u6210\u4E3A\u6027\u6B32\u800C\u6D3B\u7684\u6DEB\u517D\u2026\u2026</p>',55);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
