import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_43" tabindex="-1"><a class="header-anchor" href="#_43" aria-hidden="true">#</a> 43</h1><p>\u8D70\u51FA\u51C9\u723D\u7684\u77F3\u5BAB\uFF0C\u95F7\u70ED\u7684\u7A7A\u6C14\u7ACB\u523B\u4ECE\u56DB\u9762\u6D8C\u6765\u3002\u6BBF\u4E0B\u706F\u706B\u5BC6\u5E03\uFF0C\u4EAE\u5982\u767D\u663C\uFF0C\u6570\u5343\u540D\u6559\u4F17\u6309\u670D\u8272\u5206\u4E3A\u4E94\u7EC4\uFF0C\u6247\u5F62\u56F4\u5728\u9636\u524D\uFF0C\u9759\u6084\u6084\u6CA1\u6709\u4E00\u4E1D\u58F0\u606F\u3002</p><p>\u56DB\u540D\u7D2B\u8863\u4F8D\u8005\u642C\u6765\u5B9D\u5EA7\uFF0C\u6155\u5BB9\u7D2B\u73AB\u4E0D\u5BA2\u6C14\u5730\u4E00\u5C41\u80A1\u5750\u4E0B\uFF0C\u5FC3\u91CC\u76F4\u72AF\u5600\u5495\u3002\u8FD9\u4E9B\u5BB6\u4F19\u6446\u51FA\u8FD9\u9635\u52BF\u96BE\u9053\u662F\u60F3\u628A\u5E08\u7236\u5413\u8D70\uFF1F\u91D1\u5F00\u7532\u4E0D\u5FC5\u8BF4\u4E86\uFF0C\u5C31\u5269\u4E00\u53EA\u773C\u775B\u8FD8\u51FA\u6765\u778E\u6DF7\u4EC0\u4E48\u5462\uFF1B\u8001\u6728\u5934\u8FDE\u773C\u90FD\u7741\u4E0D\u5F00\uFF0C\u4F30\u8BA1\u4E5F\u6D3B\u4E0D\u957F\u4E86\uFF1B\u522B\u770B\u5C60\u80D6\u5B50\u7AD9\u5F97\u633A\u7A33\uFF0C\u7B49\u5E08\u7236\u6765\u4E86\uFF0C\u4E00\u811A\u5C31\u80FD\u628A\u4F60\u7684\u5C4E\u8E29\u51FA\u6765\uFF01</p><p>\u7D2B\u73AB\u8D8A\u60F3\u8D8A\u662F\u5F00\u5FC3\uFF0C\u683C\u683C\u8F7B\u58F0\u7B11\u4E86\u8D77\u6765\u3002\u53EF\u4E00\u770B\u5230\u970D\u72C2\u7130\u706B\u7EA2\u7684\u957F\u888D\uFF0C\u5979\u7684\u7B11\u5BB9\u7ACB\u5373\u6D88\u5931\u3002\u65E0\u8BBA\u5982\u4F55\uFF0C\u6211\u90FD\u8981\u9664\u6389\u8FD9\u5BB6\u4F19\uFF0C\u4E3A\u5C55\u626C\u54E5\u54E5\u4E00\u5BB6\u62A5\u4EC7\uFF01</p><p>\u5341\u4F59\u540D\u5973\u5B50\u9C7C\u8D2F\u800C\u51FA\uFF0C\u8DEA\u5728\u9636\u4E0B\u3002\u7D2B\u73AB\u4E00\u773C\u4FBF\u770B\u5230\u5927\u5E08\u59D0\u3002\u98CE\u665A\u534E\u8138\u8272\u6194\u60B4\uFF0C\u88AB\u6155\u5BB9\u9F99\u5438\u5C3D\u529F\u529B\u4E4B\u540E\uFF0C\u7435\u7436\u9AA8\u4E0A\u94C1\u94FE\u5DF2\u7ECF\u53BB\u9664\uFF0C\u53EA\u5269\u4E0B\u4E24\u4E2A\u672A\u66FE\u7652\u5408\u7684\u8840\u6D1E\u3002</p><p>\u5DE6\u4E73\u575A\u633A\u5982\u6614\uFF0C\u53F3\u4E73\u5374\u8F6F\u8F6F\u5782\u5728\u80F8\u524D\uFF0C\u4E73\u5934\u5FAE\u5FAE\u6447\u6643\uFF0C\u5C61\u53D7\u6467\u6B8B\u7684\u4F24\u53E3\u65F6\u9690\u65F6\u73B0\u3002</p><p>\u4E24\u4EBA\u76EE\u5149\u4E00\u89E6\uFF0C\u98CE\u665A\u534E\u773C\u4E2D\u5C04\u51FA\u70BD\u70ED\u7684\u611F\u60C5\uFF0C\u534E\u7F8E\u7684\u7389\u5BB9\u5145\u6EE1\u575A\u6BC5\u4E4B\u8272\u3002</p><p>\u7D2B\u73AB\u542B\u6CEA\u5FAE\u5FAE\u70B9\u5934\uFF0C\u4ECA\u591C\u662F\u6700\u540E\u4E00\u591C\uFF0C\u660E\u5929\u5E08\u59D0\u4FBF\u53EF\u79BB\u5F00\u77F3\u5BA4\u3002</p><p>\u9664\u98CE\u665A\u534E\u4E4B\u5916\uFF0C\u767D\u6C0F\u59D0\u59B9\u3001\u536B\u79C0\u7EB9\u4E5F\u5728\u5176\u4E2D\uFF0C\u5269\u4E0B\u8FD8\u6709\u51E0\u540D\u4E0D\u8BA4\u8BC6\u7684\u5973\u5B50\uFF0C\u5927\u6982\u662F\u88AB\u8D2C\u4E3A\u5974\u96B6\u7684\u6559\u4F17\u3002</p><p>\u6155\u5BB9\u9F99\u7F13\u6B65\u800C\u51FA\uFF0C\u82F1\u633A\u7684\u8EAB\u6750\u72B9\u5982\u7389\u6811\u4E34\u98CE\uFF0C\u8138\u4E0A\u6302\u7740\u6DE1\u6DE1\u7684\u7B11\u5BB9\uFF0C\u4F18\u96C5\u800C\u53C8\u65AF\u6587\uFF0C\u4F46\u7D2B\u73AB\u5374\u611F\u89C9\u5230\u4E00\u79CD\u65E0\u5F62\u7684\u538B\u529B\uFF0C\u60C5\u4E0D\u81EA\u7981\u5730\u5411\u65C1\u8BA9\u4E86\u8BA9\uFF0C\u8EB2\u907F\u90A3\u80A1\u9690\u7EA6\u7684\u5BD2\u610F\u3002</p><p>\u6155\u5BB9\u9F99\u7ACB\u5728\u6BBF\u524D\uFF0C\u6DE1\u6DE1\u9053\uFF1A\u300C\u5C06\u9003\u5974\u5E26\u8FC7\u6765\u3002\u300D</p><p>\u5782\u6B7B\u7684\u5973\u5B50\u88AB\u4E24\u540D\u7D2B\u8863\u4F8D\u8005\u67B6\u7740\u62D6\u5230\u573A\u4E2D\uFF0C\u7D2B\u73AB\u7ACB\u523B\u8BA4\u51FA\u5AC2\u5AC2\u66FC\u5999\u7684\u4F53\u5F62\uFF0C\u987F\u65F6\u82B3\u5FC3\u63EA\u7D27\u3002\u5AC2\u5AC2\u4E09\u65E5\u524D\u5DF2\u88AB\u6551\u51FA\uFF0C\u600E\u4E48\u53C8\u843D\u5165\u9B54\u638C\uFF1F\u83AB\u975E\u2026\u2026</p><p>\u6155\u5BB9\u9F99\u5F90\u5F90\u9053\uFF1A\u300C\u4EFB\u4F55\u5973\u5B50\uFF0C\u4E00\u5165\u795E\u6559\u4FBF\u7EC8\u8EAB\u4E3A\u5974\uFF0C\u80C6\u6562\u9003\u8D70\u8005\u4E00\u5F8B\u5904\u4EE5\u5E7D\u95ED\u4E4B\u5211\u3002\u53F6\u62A4\u6CD5\uFF0C\u8BF7\u3002\u300D</p><p>\u53F6\u884C\u5357\u6162\u6162\u8D77\u8EAB\uFF0C\u628A\u4E00\u4E2A\u6728\u5323\u653E\u5728\u6848\u4E0A\uFF0C\u7136\u540E\u62FF\u51FA\u4E00\u679D\u9752\u8272\u7684\u5C0F\u89D2\u5728\u6797\u9999\u8FDC\u9F3B\u524D\u4E00\u6643\u3002\u8FD9\u86C7\u89D2\u51FA\u81EA\u5D11\u4ED1\uFF0C\u5176\u6027\u81F3\u5BD2\uFF0C\u6709\u8FD8\u9B42\u51DD\u795E\u4E4B\u6548\u3002</p><p>\u6797\u9999\u8FDC\u60A0\u60A0\u9192\u8F6C\uFF0C\u53D1\u73B0\u81EA\u5DF1\u8EBA\u5728\u4E00\u5F20\u575A\u77F3\u7684\u6728\u684C\u4E0A\uFF0C\u8033\u7554\u4E0D\u65F6\u4F20\u6765\u706B\u628A\u8F7B\u5FAE\u7684\u7206\u54CD\u3002\u4E00\u53CC\u5E72\u67AF\u7684\u624B\u638C\u4F38\u8FDB\u5927\u817F\u5185\u4FA7\uFF0C\u5C06\u53CC\u817F\u5DE6\u53F3\u5206\u5F00\uFF0C\u63A5\u7740\u4E00\u4E2A\u67D4\u8F6F\u7684\u56CA\u72B6\u7269\u4F53\u585E\u5165\u4F53\u5185\u3002\u624B\u6307\u62E8\u5F04\u4E0B\uFF0C\u79D8\u5904\u9635\u9635\u9165\u75D2\u3002\u4E0D\u591A\u65F6\u8179\u5185\u4E00\u52A8\uFF0C\u76AE\u56CA\u7F13\u7F13\u81A8\u80C0\u8D77\u6765\u3002</p><p>\u867D\u7136\u4E0D\u77E5\u9053\u4EC0\u4E48\u53EB\u300C\u5E7D\u95ED\u300D\uFF0C\u4F46\u770B\u5230\u5AC2\u5AC2\u7389\u4F53\u6A2A\u9648\u7684\u6837\u5B50\u7D2B\u73AB\u4FBF\u77E5\u4E8B\u60C5\u4E0D\u5999\u3002</p><p>\u5979\u770B\u770B\u9762\u65E0\u8868\u60C5\u7684\u6155\u5BB9\u9F99\uFF0C\u538B\u4E0B\u4E5E\u6C42\u7684\u5FF5\u5934\uFF0C\u6525\u4F4F\u8863\u89D2\u7D27\u5F20\u5730\u76EF\u7740\u573A\u4E2D\u3002</p><p>\u8089\u7A74\u5185\u7684\u76AE\u56CA\u5145\u6EE1\u6C14\u4F53\uFF0C\u6491\u8D77\u7389\u80A1\u95F4\u6BB7\u7EA2\u593A\u76EE\u7684\u5AE9\u8089\u3002\u6797\u9999\u8FDC\u4E0B\u8179\u5FAE\u9F13\uFF0C\u79D8\u5904\u7684\u82B1\u74E3\u5C3D\u6570\u7EFD\u653E\uFF0C\u5927\u5C0F\u9634\u5507\u5C42\u6B21\u5206\u660E\uFF0C\u7EC6\u5AE9\u6ED1\u817B\uFF0C\u67D4\u7F8E\u8FF7\u4EBA\u3002\u5185\u4FA7\u82B1\u74E3\u4E0A\u7F18\u7ED3\u5408\u5904\uFF0C\u4E00\u7C92\u7EA2\u8273\u8273\u7684\u5C0F\u5DE7\u8089\u7C92\u88AB\u538B\u5728\u94C1\u94FE\u4E0B\uFF0C\u5FAE\u5FAE\u98A4\u52A8\u3002\u706B\u5149\u63A9\u6620\u4E2D\uFF0C\u80FD\u6E05\u695A\u5730\u770B\u5230\u82B1\u74E3\u95F4\u7FD5\u5F20\u7684\u8089\u7A74\u3002</p><p>\u80EF\u9AA8\u7684\u94C1\u94FE\u662F\u4E2A\u6B7B\u7ED3\uFF0C\u53F6\u884C\u5357\u4E5F\u4E0D\u8D39\u5FC3\u53BB\u89E3\uFF0C\u4F38\u624B\u7FFB\u5F00\u6728\u5323\uFF0C\u62FF\u51FA\u4E00\u679D\u5947\u602A\u7684\u7269\u54C1\u3002\u7269\u54C1\u50CF\u662F\u4E00\u679D\u88AB\u5256\u5F00\u7684\u82A6\u7BA1\uFF0C\u9EC4\u94DC\u6253\u5236\uFF0C\u53C8\u7EC6\u53C8\u957F\uFF0C\u4E0B\u90E8\u6253\u78E8\u950B\u5229\u3002\u53F6\u884C\u5357\u637B\u8D77\u5916\u4FA7\u80A5\u539A\u7684\u82B1\u74E3\uFF0C\u627E\u51C6\u4F1A\u9634\u5904\u5AE9\u8089\u9686\u8D77\u7684\u90E8\u4F4D\uFF0C\u534A\u5706\u5F62\u7684\u5200\u950B\u4F38\u81F3\u8089\u7247\u6839\u90E8\uFF0C\u5FAE\u5FAE\u4E00\u9001\uFF0C\u5AE9\u8089\u7ACB\u5373\u5E94\u624B\u7EFD\u88C2\u3002</p><p>\u300C\u554A\u2026\u2026\u300D\u5973\u4EBA\u6700\u5A07\u5AE9\u9690\u79D8\u7684\u90E8\u4F4D\u7A81\u7136\u88AB\u751F\u751F\u5272\u5F00\uFF0C\u6797\u9999\u8FDC\u5A07\u8EAF\u4E00\u7D27\uFF0C\u5931\u58F0\u75DB\u53EB\uFF0C\u4E24\u6761\u7389\u817F\u7AED\u529B\u5408\u62E2\u3002</p><p>\u56DB\u540D\u7D2B\u8863\u4F8D\u8005\u5206\u522B\u6309\u7740\u5C11\u5987\u7684\u56DB\u80A2\u80EF\u9AA8\uFF0C\u4F7F\u5979\u52A8\u5F39\u4E0D\u5F97\u3002\u65C1\u8FB9\u8BF8\u5973\u90FD\u662F\u9762\u65E0\u4EBA\u8272\uFF0C\u53EA\u6709\u98CE\u665A\u534E\u7F8E\u76EE\u55B7\u706B\uFF0C\u54AC\u7259\u76EF\u7740\u53F6\u884C\u5357\u3002</p><p>\u5200\u950B\u5411\u4E0A\u6311\u8D77\uFF0C\u6BEB\u65E0\u963B\u62E6\u5730\u5207\u5F00\u8089\u7247\uFF0C\u67D4\u7F8E\u7684\u82B1\u74E3\u4EFF\u4F5B\u6ED1\u817B\u7684\u51DD\u8102\uFF0C\u4E00\u70B9\u70B9\u6DCC\u5165\u534A\u5706\u5F62\u7684\u5200\u8EAB\uFF0C\u8D8A\u4F38\u8D8A\u957F\u3002\u5200\u950B\u8FC7\u5904\uFF0C\u53EA\u5269\u4E0B\u4E00\u6761\u5E73\u6574\u7684\u5F2F\u6708\u72B6\u4F24\u53E3\u3002\u80A1\u95F4\u96EA\u767D\u7684\u808C\u80A4\u4E0E\u79D8\u5904\u8273\u7EA2\u7684\u5AE9\u8089\u8FDE\u6210\u4E00\u4F53\uFF0C\u518D\u65E0\u963B\u788D\u3002</p><p>\u53F6\u884C\u5357\u624B\u6307\u4F3C\u7F13\u5B9E\u5FEB\uFF0C\u7EDD\u65E0\u534A\u5206\u591A\u4F59\u52A8\u4F5C\uFF0C\u4E00\u7728\u773C\u7684\u5DE5\u592B\uFF0C\u5BD2\u6708\u5200\u5DE6\u4FA7\u5927\u9634\u5507\u5DF2\u88AB\u5B8C\u6574\u7684\u5207\u4E86\u4E0B\u6765\u3002\u9C9C\u8840\u4E5F\u4F3C\u4E4E\u88AB\u6B8B\u5FCD\u5413\u4F4F\uFF0C\u6014\u4E86\u4E00\u4E0B\u624D\u5954\u6D8C\u800C\u6765\u3002</p><p>\u53F6\u884C\u5357\u62FF\u8D77\u836F\u68C9\u4E00\u6309\uFF0C\u63A5\u7740\u6D12\u4E0A\u4E00\u5C42\u7684\u836F\u672B\uFF0C\u6B62\u4F4F\u9C9C\u8840\uFF0C\u7136\u540E\u634F\u8D77\u94DC\u7BA1\u5185\u90A3\u6761\u5A07\u5AE9\u7684\u82B1\u74E3\uFF0C\u653E\u5728\u4E00\u53EA\u74F7\u76D8\u5185\u3002</p><p>\u5200\u950B\u89E6\u5230\u5AE9\u8089\u540C\u65F6\uFF0C\u66B4\u8DF3\u7684\u7D2B\u73AB\u4FBF\u88AB\u6155\u5BB9\u9F99\u5236\u4F4F\u7A74\u9053\uFF0C\u5979\u5446\u5446\u770B\u7740\u5AC2\u5AC2\uFF0C\u773C\u6CEA\u65E0\u58F0\u65E0\u606F\u5730\u6DCC\u843D\u51FA\u6765\u3002\u7EC6\u957F\u7684\u5AE9\u8089\u9759\u9759\u8EBA\u5728\u5149\u6D01\u7684\u74F7\u76D8\u5185\uFF0C\u4ECD\u4FDD\u6301\u7740\u539F\u672C\u67D4\u7F8E\u7684\u6A21\u6837\u3002\u9C9C\u8840\u4E0D\u4F4F\u4ECE\u5E73\u6574\u7684\u65AD\u53E3\u6D41\u51FA\uFF0C\u8FD8\u5E26\u7740\u4E3B\u4EBA\u6E29\u6696\u7684\u4F53\u6E29\u3002</p><p>\u53F6\u884C\u5357\u98DE\u5FEB\u5730\u5272\u4E0B\u53E6\u4E00\u4FA7\u9634\u5507\uFF0C\u7136\u540E\u6362\u4E86\u4E00\u652F\u66F4\u7EC6\u7684\u534A\u5706\u72B6\u7B52\u5200\u3002\u8FD9\u67C4\u7B52\u5200\u5200\u950B\u6210\u5C16\u9F7F\u72B6\uFF0C\u53F6\u884C\u5357\u4E5F\u4E0D\u518D\u662F\u76F4\u63A5\u5207\u9664\uFF0C\u800C\u662F\u987A\u7740\u8840\u8109\uFF0C\u9010\u6B65\u5254\u53BB\u5C0F\u9634\u5507\u4E0A\u7684\u5AE9\u8089\u3002</p><p>\u968F\u7740\u5200\u950B\u8D77\u843D\uFF0C\u7CBE\u81F4\u82B1\u74E3\u53D8\u6210\u4E00\u7F15\u7F15\u7A00\u788E\u7684\u8089\u4E1D\uFF0C\u6E10\u6E10\u6D88\u5931\u65E0\u8E2A\u3002\u6797\u9999\u8FDC\u7684\u53EB\u58F0\u8D8A\u6765\u8D8A\u51C4\u5389\uFF0C\u5979\u5C0F\u8179\u7EF7\u7D27\uFF0C\u88AB\u4EBA\u7D27\u6309\u7740\u7684\u80EF\u9AA8\u62FC\u547D\u633A\u52A8\uFF0C\u7389\u6237\u95F4\u9C9C\u8840\u6DCB\u6F13\u3002</p><p>\u53F6\u884C\u5357\u4E0D\u52A8\u58F0\u8272\uFF0C\u4E00\u4E1D\u4E0D\u82DF\u5730\u5254\u5C3D\u82B1\u74E3\uFF0C\u53EA\u7559\u4E0B\u51E0\u6839\u7EC6\u82E5\u53D1\u4E1D\u7684\u8840\u7BA1\u5728\u4F24\u53E3\u4E0A\u6643\u52A8\u3002\u4ED6\u5341\u6307\u7075\u52A8\u5982\u98DE\uFF0C\u8F7B\u5DE7\u5730\u5C06\u8840\u7BA1\u4E00\u4E00\u6253\u7ED3\uFF0C\u7136\u540E\u5C06\u4F24\u53E3\u4E24\u4FA7\u7684\u5AE9\u8089\u62C9\u7D27\uFF0C\u7528\u725B\u6BDB\u7EC6\u9488\u7F1D\u5408\u5728\u4E00\u8D77\u3002</p><p>\u7B49\u53F6\u884C\u5357\u653E\u5F00\u624B\uFF0C\u5C11\u5987\u4E0B\u4F53\u5C42\u5C42\u53E0\u53E0\u7684\u82B1\u74E3\u5DF2\u7ECF\u6D88\u5931\u5F97\u65E0\u5F71\u65E0\u8E2A\uFF0C\u7C89\u5AE9\u7684\u96EA\u80A1\u95F4\uFF0C\u9732\u51FA\u4E00\u7247\u72ED\u957F\u7684\u6843\u53F6\u72B6\u8273\u7EA2\uFF0C\u5E73\u6574\u5982\u65B0\u3002\u67D4\u7F8E\u7684\u5927\u9634\u5507\u53EA\u5269\u4E0B\u4E24\u9053\u51C4\u60E8\u7684\u4F24\u53E3\uFF0C\u4F24\u53E3\u5185\u6DA6\u5982\u7EA2\u7389\u7684\u5AE9\u8089\u4E00\u89C8\u65E0\u4F59\uFF0C\u539F\u672C\u88AB\u82B1\u74E3\u906E\u63A9\u7684\u8089\u7A74\u8D64\u88F8\u88F8\u66B4\u9732\u5728\u5916\u3002</p><p>\u8FD9\u7247\u5149\u6DA6\u5AE9\u7EA2\u4E4B\u95F4\uFF0C\u9ED1\u8272\u7684\u94C1\u94FE\u663E\u5F97\u5206\u5916\u9192\u76EE\u3002</p><p>\u6797\u9999\u8FDC\u6D51\u8EAB\u9999\u6C57\u6DCB\u6F13\uFF0C\u5C0F\u817F\u7528\u529B\u4F38\u76F4\uFF0C\u7EA4\u8DB3\u7EF7\u7D27\uFF0C\u300C\u554A\u2026\u2026\u554A\u2026\u2026\u554A\u5440\u2026\u300D</p><p>\u65AD\u65AD\u7EED\u7EED\u5730\u54C0\u53EB\u7740\u3002\u867D\u7136\u75DB\u5F97\u6B7B\u53BB\u6D3B\u6765\uFF0C\u4F46\u5F53\u53F6\u884C\u5357\u634F\u7D27\u7EC6\u5C0F\u7684\u82B1\u8482\u65F6\uFF0C\u5979\u8FD8\u662F\u611F\u5230\u4E00\u80A1\u6DF1\u5165\u9AA8\u9AD3\u7684\u5FEB\u611F\u3002</p><p>\u53F6\u884C\u5357\u624B\u6307\u8F7B\u637B\uFF0C\u6307\u95F4\u7684\u82B1\u8482\u5728\u4ED6\u5185\u529B\u6FC0\u53D1\u4E0B\u8FC5\u901F\u5145\u8840\u81A8\u80C0\uFF0C\u8D64\u88F8\u88F8\u7684\u8089\u7A74\u5185\u4E5F\u968F\u4E4B\u6625\u6F6E\u6CDB\u6EE5\uFF0C\u88AB\u76AE\u56CA\u6491\u6EE1\u7684\u8089\u58C1\u6E17\u51FA\u6CBE\u8840\u7684\u6DEB\u6DB2\u3002</p><p>\u5F53\u6797\u9999\u8FDC\u51E0\u8FD1\u5931\u795E\u4E4B\u65F6\uFF0C\u7A81\u7136\u4E0B\u4F53\u4E00\u75BC\uFF0C\u5347\u817E\u7684\u5FEB\u611F\u987F\u65F6\u88AB\u8FDE\u6839\u62D4\u8D77\uFF0C\u7A7A\u843D\u843D\u518D\u65E0\u4EFB\u4F55\u4F9D\u6258\u3002\u5979\u731B\u7136\u7741\u5F00\u5931\u660E\u7684\u53CC\u76EE\uFF0C\u75DB\u547C\u54FD\u5728\u5589\u4E2D\uFF0C\u63A5\u7740\u67D4\u9888\u4E00\u4FA7\uFF0C\u660F\u6B7B\u8FC7\u53BB\u3002</p><p>\u5145\u8840\u7684\u8089\u7C92\u5FAE\u5FAE\u4E00\u8DF3\uFF0C\u843D\u5728\u74F7\u76D8\u4E2D\uFF0C\u9C9C\u8840\u7BAD\u77E2\u822C\u6FC0\u5C04\u800C\u51FA\u3002\u53F6\u884C\u5357\u624B\u6307\u4E00\u637A\uFF0C\u786C\u751F\u751F\u903C\u56DE\u8840\u6CC9\uFF0C\u7136\u540E\u5C0F\u5FC3\u5730\u5250\u51C0\u82B1\u8482\uFF0C\u5C06\u91CC\u9762\u7EC6\u5BC6\u7684\u7ECF\u7EDC\u4E00\u4E00\u5254\u51FA\uFF0C\u628A\u5973\u6027\u5FEB\u611F\u4E4B\u6E90\u5C3D\u6570\u6467\u6BC1\uFF0C\u6700\u540E\u624D\u53D6\u51FA\u76AE\u56CA\uFF0C\u6577\u4E0A\u836F\u7269\u3002</p><p>\u505A\u5B8C\u8FD9\u4E00\u5207\uFF0C\u53F6\u884C\u5357\u76F4\u8D77\u8170\uFF0C\u6311\u51FA\u4E00\u540D\u5973\u5974\uFF0C\u6325\u5200\u628A\u5979\u4E30\u6EE1\u7684\u4E73\u623F\u4E00\u5206\u4E3A\u4E8C\uFF0C\u5207\u4E0B\u534A\u53EA\u3002\u63A5\u7740\u5C06\u4E73\u8089\u5254\u5C3D\uFF0C\u53EA\u7559\u4E0B\u4E00\u5757\u692D\u5706\u7684\u76AE\u80A4\uFF0C\u7EC6\u7EC6\u6D82\u4E0A\u836F\u7269\uFF0C\u7136\u540E\u8499\u5728\u6797\u9999\u8FDC\u8179\u4E0B\u3002\u7B49\u4E09\u5929\u540E\u4F24\u52BF\u7652\u5408\uFF0C\u63ED\u5F00\u76AE\u80A4\uFF0C\u5BD2\u6708\u5200\u4E0B\u4F53\u5C31\u50CF\u4ECE\u672A\u751F\u8FC7\u9634\u5507\u822C\u5149\u6ED1\u81EA\u7136\u3002</p><p>\u773C\u7741\u7741\u770B\u7740\u6797\u5973\u4FA0\u4E0B\u4F53\u88AB\u6467\u6B8B\u6B86\u5C3D\uFF0C\u767D\u7389\u83BA\u767D\u7389\u9E42\u5413\u5F97\u7D27\u7D27\u62E5\u5728\u4E00\u8D77\uFF0C\u4E0D\u4F4F\u98A4\u6296\u3002</p><p>\u90A3\u4E2A\u5931\u53BB\u4E73\u623F\u7684\u5973\u5B50\u4E00\u8FB9\u60E8\u53EB\u4E00\u8FB9\u5728\u5730\u4E0A\u7FFB\u6EDA\uFF0C\u53F6\u884C\u5357\u6BEB\u4E0D\u7406\u4F1A\uFF0C\u8FF3\u76F4\u8D70\u5230\u6797\u9999\u8FDC\u8EAB\u524D\uFF0C\u5148\u5207\u53BB\u5979\u7684\u4E24\u53EA\u4E73\u5934\uFF0C\u7136\u540E\u5C06\u4E73\u6655\u7EC6\u7EC6\u5265\u5C3D\u3002\u7F8E\u5987\u4E00\u5BF9\u96EA\u4E73\u6DCC\u6EE1\u9C9C\u8840\uFF0C\u4E73\u5C16\u4ECD\u4FDD\u6301\u7740\u4F18\u7F8E\u7684\u5F62\u72B6\uFF0C\u5931\u53BB\u76AE\u80A4\u7684\u5AE9\u8089\u88F8\u9732\u5728\u7A7A\u6C14\u4E2D\uFF0C\u5FAE\u5FAE\u98A4\u6296\u3002</p><p>\u53F6\u884C\u5357\u5401\u4E86\u53E3\u6C14\uFF0C\u62C9\u8D77\u5730\u4E0A\u90A3\u4E2A\u6323\u624E\u7684\u5973\u5B50\uFF0C\u4ED4\u7EC6\u7AEF\u8BE6\u7740\u5979\u7684\u808C\u80A4\uFF0C\u6700\u540E\u4ECE\u5979\u4E73\u4E0B\u6700\u67D4\u5AE9\u7684\u90E8\u4F4D\u5265\u4E0B\u4E24\u5757\u86CB\u5F62\u76AE\u80A4\uFF0C\u8499\u5728\u6797\u9999\u8FDC\u8840\u6DCB\u6DCB\u7684\u4E73\u5C16\u4E0A\u3002</p><p>\u5BD2\u6708\u65E0\u58F0\uFF0C\u706B\u5149\u4E2D\u6620\u51FA\u4E00\u7FA4\u72F0\u72DE\u7684\u5634\u8138\uFF0C\u4EA2\u594B\u5730\u76EF\u7740\u573A\u4E2D\u5931\u53BB\u77E5\u89C9\u4FA0\u5973\u3002</p><p>\u6797\u9999\u8FDC\u76F4\u76F4\u8EBA\u5728\u6848\u4E0A\uFF0C\u56DB\u80A2\u4E0D\u4F4F\u75C9\u631B\u3002\u7F8E\u8273\u7684\u8138\u5E9E\u4E0A\u75DB\u82E6\u4E07\u72B6\uFF0C\u5979\u4E73\u9634\u5904\u8840\u8FF9\u6591\u6591\uFF0C\u867D\u7136\u67D4\u8EAF\u5A07\u7F8E\u5982\u6614\uFF0C\u4F46\u4ECE\u4ECA\u540E\u5979\u518D\u4E5F\u65E0\u6CD5\u4EAB\u53D7\u5973\u6027\u7684\u6B22\u4E50\uFF0C\u518D\u6CA1\u6709\u9634\u8482\u9634\u5507\u4F9B\u4EBA\u7231\u629A\uFF0C\u518D\u6CA1\u6709\u4E73\u5934\u6765\u54FA\u80B2\u5B69\u5B50\uFF0C\u53EA\u80FD\u6210\u4E3A\u4E00\u4E2A\u4E0D\u77E5\u5FEB\u611F\u4E3A\u4F55\u7269\u7684\u5668\u5177\uFF0C\u7528\u81EA\u5DF1\u6B8B\u7F3A\u7684\u8089\u4F53\u4F9B\u4EBA\u53D1\u6CC4\u2026\u2026</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u6155\u5BB9\u9F99\u89E3\u5F00\u7D2B\u73AB\u7684\u7A74\u9053\uFF0C\u5FAE\u7B11\u9053\uFF1A\u300C\u6797\u5A4A\u5B50\u53EA\u5269\u4E2A\u5149\u79C3\u79C3\u7684\u9A9A\u6D1E\uFF0C\u4F60\u770B\u597D\u4E0D\u597D\u73A9\uFF1F\u300D</p><p>\u7D2B\u73AB\u51FA\u5947\u5730\u6CA1\u6709\u54ED\u6CE3\uFF0C\u5979\u7F8E\u76EE\u901A\u7EA2\u5730\u76EF\u7740\u6155\u5BB9\u9F99\uFF0C\u5145\u6EE1\u6068\u610F\u5730\u8BF4\uFF1A\u300C\u4F60\u8FD9\u4E2A\u755C\u7272\u4E0D\u5F97\u597D\u6B7B\uFF01\u7B49\u6211\u5E08\u7236\u6740\u6765\uFF0C\u975E\u628A\u4F60\u4EEC\u90FD\u788E\u5C38\u4E07\u6BB5\uFF01\u300D</p><p>\u6155\u5BB9\u9F99\u8F7B\u7B11\u9053\uFF1A\u300C\u542C\u8BF4\u4F60\u5E08\u7236\u8FD8\u662F\u5904\u5B50\u4E4B\u8EAB\uFF0C\u95EF\u8361\u6C5F\u6E56\u8FD9\u4E48\u591A\u5E74\u5C45\u7136\u6CA1\u88AB\u4EBA\u64CD\u8FC7\uFF0C\u4E5F\u662F\u4E00\u5947\u2026\u2026\u300D</p><p>\u7D2B\u73AB\u72E0\u72E0\u5550\u4E86\u4ED6\u4E00\u53E3\uFF0C\u54AC\u7259\u9053\uFF1A\u300C\u4F60\u7B97\u4EC0\u4E48\u4E1C\u897F\uFF01\u6562\u8FD9\u6837\u8BF4\u6211\u5E08\u7236\uFF01\u300D</p><p>\u6155\u5BB9\u9F99\u591A\u5E74\u6765\u4E00\u76F4\u88AB\u5F53\u6210\u5A08\u7AE5\u73A9\u5F04\uFF0C\u81EA\u5C0A\u5FC3\u7279\u522B\u5F3A\u70C8\uFF0C\u95FB\u8A00\u8138\u8272\u4E00\u6C89\uFF0C\u5BD2\u58F0\u9053\uFF1A\u300C\u6211\u7B97\u4EC0\u4E48\u4E1C\u897F\uFF1F\u6211\u662F\u4F60\u54E5\u54E5\uFF0C\u4E5F\u662F\u4F60\u4E08\u592B\uFF0C\u66F4\u662F\u8FD9\u4E9B\u8D31\u5974\u7684\u751F\u6740\u4E3B\u5BB0\uFF01\u300D</p><p>\u300C\u505A\u68A6\u53BB\u5427\uFF01\u6211\u5B81\u613F\u53BB\u6B7B\u4E5F\u4E0D\u4F1A\u4F60\u78B0\u4E00\u4E0B\uFF01\u300D\u7D2B\u73AB\u7C89\u8138\u6DA8\u7EA2\u5389\u58F0\u6012\u9A82\uFF0C\u5C0F\u5DE7\u7684\u9165\u4E73\u5728\u8863\u4E0A\u5267\u70C8\u8D77\u4F0F\u3002</p><p>\u6155\u5BB9\u9F99\u773C\u795E\u51B7\u5982\u7384\u51B0\uFF0C\u76EF\u7740\u7D2B\u73AB\u7684\u4FCF\u8138\u626C\u58F0\u9053\uFF1A\u300C\u628A\u8001\u5A4A\u5B50\u5E26\u8FC7\u6765\uFF01\u300D\u4ED6\u7528\u9F3B\u5C16\u9876\u7740\u7D2B\u73AB\u5149\u6DA6\u7684\u5C0F\u9F3B\u5C16\uFF0C\u72DE\u58F0\u9053\uFF1A\u300C\u6211\u4ECA\u5929\u5C31\u8BA9\u4F60\u6B7B\u4E86\u8FD9\u6761\u5FC3\uFF01\u770B\u6E05\u695A\u2014\u2014\u6211\u600E\u4E48\u7ED9\u4F60\u5E08\u7236\u5F00\u82DE\uFF01\u300D</p><p>\u6155\u5BB9\u7D2B\u73AB\u5982\u4E94\u96F7\u8F70\u9876\uFF0C\u7F8E\u76EE\u5706\u77AA\uFF0C\u50BB\u50BB\u770B\u7740\u81EA\u5DF1\u656C\u7231\u5D07\u6155\u7684\u5E08\u7236\u88AB\u4EBA\u62C9\u626F\u7740\u6254\u5230\u6BBF\u524D\u3002</p><p>\u96EA\u5CF0\u795E\u5C3C\u767D\u8863\u4E0A\u6CBE\u6EE1\u6CE5\u571F\u7834\u788E\u4E0D\u582A\uFF0C\u663E\u7136\u662F\u88AB\u4E00\u8DEF\u62D6\u6765\u3002\u90A3\u9876\u5C3C\u5E3D\u65E9\u5DF2\u4E0D\u77E5\u53BB\u5411\uFF0C\u9732\u51FA\u767D\u51C0\u6D51\u5706\u7684\u5934\u5F62\u3002\u5979\u5F53\u65F6\u4E00\u76F4\u5C0F\u5FC3\u6212\u5907\uFF0C\u4F46\u4ECE\u6765\u6CA1\u60F3\u8FC7\u4F1A\u6709\u4E00\u79CD\u836F\u7269\u53EF\u901A\u8FC7\u771F\u6C14\u4EA4\u6362\u4F20\u64AD\u3002\u5316\u771F\u6563\u836F\u6548\u7279\u5F02\uFF0C\u4E24\u4E09\u65E5\u5185\u771F\u6C14\u7EDD\u65E0\u6CD5\u51DD\u805A\uFF0C\u4EFB\u5979\u6B66\u529F\u518D\u9AD8\uFF0C\u4E5F\u5F62\u540C\u5E9F\u4EBA\u3002</p><p>\u96EA\u5CF0\u795E\u5C3C\u76EE\u5149\u7F13\u7F13\u626B\u8FC7\u53D7\u5211\u7684\u6797\u9999\u8FDC\uFF1B\u65AD\u81C2\u7684\u98CE\u665A\u534E\u4E0D\u7531\u5FC3\u5934\u9707\u75DB\uFF0C\u665A\u534E\u662F\u5979\u6536\u517B\u7684\u5B64\u513F\uFF0C\u81EA\u5C0F\u5C31\u8DDF\u5728\u5979\u8EAB\u8FB9\uFF0C\u60C5\u540C\u6BCD\u5973\uFF1B\u73AB\u513F\u867D\u7136\u6CEA\u6D41\u6EE1\u9762\uFF0C\u8EAB\u4E0A\u5374\u8FD8\u597D\u7AEF\u7AEF\u7A7F\u7740\u8863\u670D\u2026\u2026\u6700\u540E\u76EE\u5149\u505C\u5728\u6155\u5BB9\u9F99\u8EAB\u4E0A\uFF0C\u51B7\u51B7\u76EF\u7740\u8FD9\u4E2A\u706D\u7EDD\u4EBA\u6027\u7684\u79BD\u517D\u3002</p><p>\u6155\u5BB9\u9F99\u5BF8\u6B65\u4E0D\u8BA9\u5730\u4E0E\u5979\u5BF9\u89C6\uFF0C\u5BD2\u58F0\u9053\uFF1A\u300C\u4ECA\u65E5\u98D8\u6885\u5CF0\u6EE1\u95E8\u5E08\u5F92\u9F50\u805A\u661F\u6708\u6E56\uFF0C\u9119\u6559\u84EC\u58C1\u751F\u8F89\u2014\u2014\u300D\u4ED6\u51B7\u51B7\u4E00\u7B11\uFF0C\u300C\u66F4\u662F\u8273\u798F\u65E0\u8FB9\u2026\u2026\u300D</p><p>\u300C\u7709\u59A9\u5462\uFF1F\u300D\u96EA\u5CF0\u795E\u5C3C\u5FC3\u6302\u5F92\u513F\uFF0C\u5389\u58F0\u95EE\u9053\u3002</p><p>\u6155\u5BB9\u9F99\u542C\u5230\u5979\u8D28\u95EE\u7684\u53E3\u6C14\uFF0C\u4E0D\u7981\u7B11\u9053\uFF0C\u300C\u5E08\u592A\u597D\u751F\u5389\u5BB3\uFF0C\u771F\u662F\u5413\u574F\u5728\u4E0B\u4E86\u2026\u2026\u5E08\u592A\u6559\u5BFC\u6709\u65B9\uFF0C\u7EAA\u5A4A\u5B50\u4F3A\u5019\u5728\u4E0B\uFF0C\u4F3A\u5019\u5F97\u592A\u5356\u529B\u4E86\u4E9B\uFF0C\u5C44\u80BF\u5F97\u817F\u90FD\u5408\u4E0D\u62E2\uFF0C\u672C\u5BAB\u601C\u9999\u60DC\u7389\uFF0C\u672A\u8BA9\u5979\u51FA\u6765\u8FCE\u63A5\u5E08\u592A\uFF0C\u8FD8\u671B\u6055\u7F6A\u3002\u300D</p><p>\u300C\u653E\u4E86\u5979\u4EEC\uFF0C\u6211\u96EA\u5CF0\u4EFB\u6740\u4EFB\u5250\uFF0C\u7EDD\u4E0D\u76B1\u4E00\u4E0B\u7709\u5934\uFF01\u300D</p><p>\u6155\u5BB9\u9F99\u5931\u7B11\u9053\uFF1A\u300C\u5E08\u592A\u4E0D\u662F\u6CA1\u7761\u9192\u5427\uFF1F\u4F60\u73B0\u5728\u8FD8\u6709\u4EC0\u4E48\u8D44\u683C\u8DDF\u6211\u8BB2\u6761\u4EF6\uFF1F\u4EFB\u6740\u4EFB\u5250\uFF0C\u5475\u5475\u2014\u2014\u5E08\u592A\u5E74\u7EAA\u867D\u7136\u5927\u4E86\u4E9B\uFF0C\u770B\u7740\u5012\u8FD8\u6C34\u7075\uFF0C\u5976\u5B50\u867D\u7136\u5E73\u4E86\u4E9B\uFF0C\u5C41\u80A1\u5012\u8FD8\u633A\u5927\u2026\u2026\u7B49\u5728\u4E0B\u7ED9\u4F60\u5F00\u4E86\u82DE\uFF0C\u9119\u6559\u8FD1\u5343\u540D\u5E2E\u4F17\u8FD8\u60F3\u5C1D\u5C1D\u795E\u529F\u76D6\u4E16\u7684\u96EA\u5CF0\u795E\u5C3C\uFF0C\u7A76\u7ADF\u662F\u4EC0\u4E48\u6ECB\u5473\u2026\u2026\u300D\u8BF4\u7740\u4E3E\u6B65\u8D70\u4E0B\u77F3\u9636\u3002</p><p>\u5FFD\u7136\u8098\u540E\u4E00\u7D27\uFF0C\u7D2B\u73AB\u626F\u4F4F\u6155\u5BB9\u9F99\u7684\u8863\u8896\uFF0C\u7B2C\u4E00\u6B21\u5C48\u4E0B\u53CC\u819D\u8DEA\u5728\u4ED6\u9762\u524D\uFF0C\u51C4\u58F0\u9053\uFF1A\u300C\u522B\u78B0\u6211\u5E08\u7236\uFF0C\u6211\u2026\u2026\u59B9\u59B9\u4E00\u5B9A\u4E56\u4E56\u542C\u8BDD\uFF0C\u5AC1\u7ED9\u2026\u2026\u54E5\u54E5\u2026\u2026\u300D</p>',58);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
