import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_44" tabindex="-1"><a class="header-anchor" href="#_44" aria-hidden="true">#</a> 44</h1><p>\u95F7\u70ED\u7684\u7A7A\u6C14\u7EC8\u4E8E\u900F\u6765\u4E00\u4E1D\u6E05\u51C9\u7684\u591C\u98CE\uFF0C\u6570\u767E\u679D\u706B\u628A\u540C\u65F6\u6447\u66F3\u8D77\u6765\uFF0C\u6155\u5BB9\u9F99\u6F06\u9ED1\u7684\u77B3\u5B54\u5E7D\u5E7D\u95EA\u7740\u5149\uFF0C\u534A\u664C\u9053\uFF1A\u300C\u4F60\u7B54\u5E94\u4E86\uFF1F\u300D</p><p>\u6155\u5BB9\u7D2B\u73AB\u6EE1\u9762\u6CEA\u5149\uFF0C\u62FC\u547D\u70B9\u7740\u5934\u3002</p><p>\u300C\u7ED9\u54E5\u54E5\u751F\u5B69\u5B50\uFF1F\u300D</p><p>\u7D2B\u73AB\u6BEB\u4E0D\u72B9\u8C6B\u5730\u70B9\u5934\u540C\u610F\u3002\u6155\u5BB9\u9F99\u500F\u7136\u4FEF\u8EAB\uFF0C\u543B\u4F4F\u5979\u7684\u7EA2\u5507\u3002\u7D2B\u73AB\u4E0D\u518D\u6297\u62D2\uFF0C\u4E56\u4E56\u5F20\u5F00\u5634\uFF0C\u4EFB\u4ED6\u7684\u820C\u5934\u5728\u81EA\u5DF1\u82B3\u9999\u6E29\u6DA6\u7684\u53E3\u8154\u5185\u957F\u9A71\u76F4\u5165\u3002</p><p>\u6155\u5BB9\u9F99\u75DB\u543B\u4E00\u756A\uFF0C\u604B\u604B\u4E0D\u820D\u5730\u62AC\u8D77\u5934\uFF0C\u4F4E\u58F0\u9053\uFF1A\u300C\u516D\u65E5\u540E\u4FBF\u662F\u5927\u5A5A\u4E4B\u65E5\uFF0C\u53EA\u8981\u4F60\u4E56\u4E56\u542C\u8BDD\uFF0C\u6211\u7EDD\u4E0D\u78B0\u5979\uFF01\u300D\u89C1\u7D2B\u73AB\u542B\u6CEA\u70B9\u5934\uFF0C\u4ED6\u5FAE\u5FAE\u4E00\u7B11\uFF0C\u626C\u58F0\u9053\uFF1A\u300C\u6709\u52B3\u6C90\u62A4\u6CD5\u628A\u5E08\u592A\u9001\u5165\u795E\u5BAB\u3002\u300D</p><p>\u6C90\u58F0\u4F20\u5BF9\u5BAB\u4E3B\u8D2A\u604B\u7D2B\u73AB\u5927\u4E0D\u4EE5\u4E3A\u7136\uFF0C\u4F46\u7D2B\u73AB\u662F\u6155\u5BB9\u9F99\u552F\u4E00\u7684\u5AE1\u4EB2\u59B9\u59B9\uFF0C\u53C8\u662F\u4E3A\u6C42\u55E3\u4E4B\u4E8B\uFF0C\u4FBF\u4E0D\u518D\u591A\u53E3\uFF0C\u62CE\u8D77\u96EA\u5CF0\u795E\u5C3C\u8D70\u5165\u795E\u6BBF\u3002</p><p>\u6155\u5BB9\u9F99\u73AF\u89C6\u5168\u573A\uFF0C\u7B11\u9053\uFF1A\u300C\u4ECA\u65E5\u98D8\u6885\u5CF0\u5408\u95E8\u5E08\u5F92\u5C3D\u6570\u6210\u64D2\uFF0C\u6B66\u6797\u540D\u6D3E\u4E00\u671D\u9664\u540D\uFF0C\u6211\u795E\u6559\u5A01\u9707\u5929\u4E0B\uFF0C\u8FD9\u90FD\u662F\u5927\u4F19\u7684\u529F\u52B3\uFF01\u4F17\u5F1F\u5144\u591A\u65E5\u8F9B\u82E6\uFF0C\u4ECA\u591C\u597D\u597D\u4E50\u4E0A\u4E00\u4E50\uFF01\u300D</p><p>\u4F17\u4EBA\u9F50\u58F0\u6B22\u547C\uFF0C\u970D\u72C2\u7130\u4E00\u9A6C\u5F53\u5148\uFF0C\u4E00\u628A\u6309\u4F4F\u98CE\u665A\u534E\uFF0C\u628A\u5927\u624B\u4F38\u8FDB\u5979\u4E24\u817F\u4E4B\u95F4\u3002</p><p>\u7D2B\u73AB\u731B\u7136\u7701\u8D77\uFF0C\u8FDE\u5FD9\u9053\uFF1A\u300C\u4F60\u7B54\u5E94\u8FC7\u6211\uFF0C\u5341\u65E5\u4FBF\u8BA9\u5927\u5E08\u59D0\u5165\u5BAB\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u773C\u4E2D\u51F6\u5149\u4E00\u95EA\uFF0C\u6076\u72E0\u72E0\u9053\uFF1A\u300C\u6211\u6CA1\u7B54\u5E94\u8FC7\uFF01\u300D</p><p>\u7D2B\u73AB\u5927\u6025\uFF0C\u987F\u8DB3\u9053\uFF1A\u300C\u4F60\u90A3\u65E5\u8BF4\u8FC7\u7684\u2014\u2014\u56DB\u6708\u5341\u65E5\u8BA9\u2026\u2026\u8BA9\u2026\u2026\u98CE\u5974\u5165\u5BAB\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u89C1\u5979\u77E5\u8DA3\uFF0C\u7B11\u9053\uFF1A\u300C\u8FD9\u4E2A\u6211\u5012\u662F\u8BF4\u8FC7\uFF0C\u5F85\u65E5\u51FA\u540E\u5C31\u8BA9\u5979\u8FDB\u6765\u597D\u4E86\u3002\u300D</p><p>\u7D2B\u73AB\u677E\u4E86\u53E3\u6C14\u3002\u4F46\u5982\u679C\u5979\u77E5\u9053\u98CE\u665A\u534E\u5165\u5BAB\u4E4B\u540E\uFF0C\u4F1A\u53D7\u5230\u4EC0\u4E48\u6837\u6298\u78E8\uFF0C\u4E5F\u8BB8\u5B81\u613F\u8BA9\u5E08\u59D0\u6C38\u8FDC\u7559\u5728\u77F3\u5BA4\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u8FD9\u4E00\u591C\u5206\u5916\u6F2B\u957F\uFF0C\u7D2B\u73AB\u5954\u8D70\u5728\u77F3\u5BAB\u3001\u795E\u6BBF\u4E4B\u95F4\uFF0C\u6CA1\u6709\u7247\u523B\u5B89\u5B81\u3002</p><p>\u7EAA\u5E08\u59D0\u7684\u6323\u624E\u8D8A\u6765\u8D8A\u5267\u70C8\uFF0C\u7B2C\u4E09\u6B21\u6D82\u4E0A\u540C\u6837\u836F\u818F\uFF0C\u80BF\u80C0\u7684\u82B1\u74E3\u7ACB\u523B\u5145\u6EE1\u4E86\u8840\u6DB2\uFF0C\u50CF\u4E00\u6735\u5DE8\u5927\u7684\u8089\u82B1\u5728\u8179\u4E0B\u6447\u6447\u6B32\u5815\uFF0C\u8089\u7A74\u5185\u900F\u660E\u7684\u6DB2\u4F53\u72C2\u6D8C\u4E0D\u6B62\u3002</p><p>\u7D2B\u73AB\u6839\u672C\u60F3\u4E0D\u5230\u5973\u5B50\u7684\u6DEB\u6DB2\u4F1A\u5982\u6B64\u5954\u6D8C\uFF0C\u8FD8\u4EE5\u4E3A\u5E08\u59D0\u662F\u5C0F\u4FBF\u5931\u7981\uFF0C\u8FDE\u5FD9\u7528\u6BDB\u5DFE\u53BB\u64E6\u3002\u53EF\u6DEB\u6DB2\u8D8A\u64E6\u8D8A\u591A\uFF0C\u800C\u4E14\u6BCF\u6B21\u6BDB\u5DFE\u78B0\u5230\u5AE9\u8089\u7EAA\u7709\u59A9\u90FD\u4F1A\u9AD8\u58F0\u547B\u541F\uFF0C\u4F46\u58F0\u97F3\u91CC\u5E76\u6CA1\u6709\u75DB\u82E6\uFF0C\u53CD\u800C\u50CF\u5145\u6EE1\u4E86\u6109\u60A6\u3002\u7D2B\u73AB\u614C\u4E86\u624B\u811A\uFF0C\u53C8\u53BB\u627E\u53F6\u884C\u5357\u8BCA\u6CBB\u3002</p><p>\u53F6\u884C\u5357\u9694\u7740\u95E8\u628A\u7EAA\u7709\u59A9\u75C7\u72B6\u8BF4\u5F97\u5206\u6BEB\u4E0D\u5DEE\uFF0C\u5B9B\u5982\u76EE\u89C1\uFF0C\u6700\u540E\u8BF4\u8FD9\u662F\u5FC5\u7136\u4E4B\u72B6\uFF0C\u8BA9\u5979\u4E0D\u5FC5\u591A\u60F3\u3002\u7D2B\u73AB\u542C\u4ED6\u8BF4\u5F97\u7B03\u5B9A\uFF0C\u53EA\u597D\u534A\u4FE1\u534A\u7591\u7684\u53BB\u4E86\u3002\u5B89\u6170\u7EAA\u7709\u59A9\u7247\u523B\uFF0C\u89C1\u5E08\u59D0\u4E0B\u4F53\u8FD8\u662F\u6C34\u6D41\u4E0D\u6B62\uFF0C\u5E72\u8106\u62FF\u6765\u4E00\u5E8A\u88AB\u8925\u653E\u5728\u7EAA\u7709\u59A9\u81C0\u4E0B\u3002</p><p>\u5B89\u987F\u4E86\u7EAA\u5E08\u59D0\uFF0C\u7D2B\u73AB\u53C8\u8D76\u5230\u6C90\u58F0\u4F20\u9694\u58C1\u7684\u77F3\u5BA4\uFF0C\u4FEF\u8033\u503E\u542C\u5BA4\u5185\u7684\u52A8\u9759\u3002\u4E0D\u77E5\u9053\u4ED6\u4EEC\u7528\u4EC0\u4E48\u4F0E\u4FE9\u5236\u4F4F\u4E86\u5E08\u7236\uFF0C\u597D\u5728\u90A3\u4E2A\u79BD\u517D\u8BF4\u8BDD\u7B97\u8BDD\uFF0C\u628A\u5E08\u7236\u4E00\u4E2A\u4EBA\u5173\u5728\u77F3\u5BA4\uFF0C\u5E76\u6CA1\u6709\u52A0\u4EE5\u51CC\u8FB1\u3002\u77F3\u5BA4\u9694\u97F3\u6781\u597D\uFF0C\u7D2B\u73AB\u542C\u4E86\u534A\u664C\u4E5F\u6CA1\u542C\u51FA\u4E2A\u6240\u4EE5\u7136\u6765\uFF0C\u4FBF\u8D77\u8EAB\u8D70\u5230\u752C\u9053\u901A\u5411\u795E\u6BBF\u7684\u5C0F\u95E8\uFF0C\u4F7F\u52B2\u6572\u4E86\u8D77\u6765\u3002</p><p>\u5728\u6BBF\u5185\u503C\u5B88\u7684\u7D2B\u8863\u5E2E\u4F17\u8BF4\u5929\u8FD8\u6CA1\u4EAE\uFF0C\u7D2B\u73AB\u53EA\u597D\u56DE\u5230\u767E\u82B1\u89C2\u97F3\u5BA4\u5185\uFF0C\u7126\u6025\u5730\u7B49\u5F85\u5929\u4EAE\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u4E0D\u77E5\u7A7F\u68AD\u4E86\u51E0\u4E2A\u6765\u56DE\uFF0C\u4E3B\u5BA4\u623F\u95E8\u4E00\u5F00\uFF0C\u8C03\u606F\u4E00\u591C\u7684\u6155\u5BB9\u9F99\u795E\u91C7\u5955\u5955\u5730\u8D70\u4E86\u51FA\u6765\u3002</p><p>\u300C\u8FC7\u6765\uFF0C\u8BA9\u54E5\u54E5\u4EB2\u4EB2\u4F60\u7684\u5C0F\u5634\u3002\u300D</p><p>\u7D2B\u73AB\u54AC\u7D27\u7259\u5173\uFF0C\u632A\u7740\u6B65\u5B50\u8D70\u4E86\u8FC7\u53BB\uFF0C\u4EF0\u8D77\u4FCF\u8138\u3002</p><p>\u6155\u5BB9\u9F99\u5C55\u81C2\u628A\u5A07\u4FCF\u7684\u59B9\u59B9\u5708\u5728\u6000\u4E2D\uFF0C\u4E00\u53E3\u628A\u5A07\u8273\u6B32\u6EF4\u7684\u7EA2\u5507\u542B\u5230\u5634\u4E2D\u3002</p><p>\u7C97\u7CD9\u800C\u53C8\u6ED1\u817B\u7684\u820C\u5934\u8214\u8210\u7740\u5507\u74E3\uFF0C\u90A3\u79CD\u89E6\u7535\u822C\u7684\u9165\u9EBB\u76F4\u5165\u5FC3\u5E95\u3002\u7D2B\u73AB\u7D27\u95ED\u53CC\u773C\uFF0C\u6297\u62D2\u7740\u6155\u5BB9\u9F99\u8EAB\u4E0A\u6D53\u91CD\u7684\u7537\u6027\u6C14\u606F\uFF0C\u8111\u6D77\u91CC\u4E0D\u7981\u6D6E\u73B0\u51FA\u6CAE\u6E20\u5C55\u626C\u5FAE\u7B11\u7684\u795E\u60C5\u3002</p><p>\u8981\u662F\u5C55\u626C\u54E5\u54E5\u8BE5\u591A\u597D\u554A\u2026\u2026\u53EF\u90A3\u65F6\u5019\u81EA\u5DF1\u6700\u591A\u53EA\u8BA9\u4ED6\u4EB2\u4EB2\u8138\u86CB\u3002</p><p>\u90A3\u8FD8\u662F\u4E94\u5E74\u524D\u7684\u4E8B\u4E86\u5427\uFF0C\u5C55\u626C\u54E5\u54E5\u5E26\u7740\u660E\u5170\uFF0C\u7ED9\u81EA\u5DF1\u8FC7\u5341\u4E00\u5C81\u751F\u65E5\uFF0C\u4E0D\u77E5\u600E\u4E48\u7740\u628A\u660E\u5170\u60F9\u54ED\u4E86\uFF0C\u5C55\u626C\u54E5\u54E5\u5F88\u4E0D\u9AD8\u5174\uFF0C\u81EA\u5DF1\u8BF4\u5C3D\u597D\u8BDD\uFF0C\u53C8\u8BA9\u4ED6\u4EB2\u4EB2\u8138\u86CB\uFF0C\u624D\u4F7F\u5C55\u626C\u54E5\u54E5\u8F6C\u6012\u4E3A\u559C\u2026\u2026</p><p>\u9999\u8F6F\u7684\u5C0F\u820C\u4E00\u76F4\u8EB2\u907F\u7740\uFF0C\u4E0D\u80AF\u8BA9\u4ED6\u5659\u4F4F\u3002\u6155\u5BB9\u9F99\u677E\u5F00\u5634\uFF0C\u7B11\u9053\uFF1A\u300C\u628A\u820C\u5934\u4F38\u51FA\u6765\u3002\u300D</p><p>\u7D2B\u73AB\u72B9\u8C6B\u7247\u523B\uFF0C\u4E00\u72E0\u5FC3\u5410\u51FA\u4E01\u9999\u5C0F\u820C\u3002\u5AE9\u7EA2\u7684\u820C\u5C16\u6ED1\u817B\u52A8\u4EBA\uFF0C\u5E26\u7740\u4E00\u80A1\u9999\u751C\u7684\u82AC\u82B3\u3002\u6155\u5BB9\u9F99\u6B23\u8D4F\u79FB\u65F6\uFF0C\u7B49\u7D2B\u73AB\u820C\u6839\u53D1\u50F5\uFF0C\u624D\u7528\u820C\u5C16\u8F7B\u8F7B\u4E00\u8214\u3002\u7D2B\u73AB\u5A07\u8EAF\u5FAE\u98A4\uFF0C\u4F46\u8FD8\u662F\u5F3A\u5FCD\u7740\u4ED6\u7684\u620F\u5F04\u3002\u6155\u5BB9\u9F99\u542B\u4F4F\u5C0F\u820C\u62FC\u547D\u5438\u542E\uFF0C\u4EFF\u4F5B\u8981\u628A\u6ED1\u5AE9\u7684\u9999\u820C\u541E\u5165\u809A\u5185\u3002</p><p>\u826F\u4E45\uFF0C\u5507\u5206\u3002\u7D2B\u73AB\u820C\u5934\u88AB\u4ED6\u5438\u5F97\u53C8\u75DB\u53C8\u9EBB\uFF0C\u5598\u4E86\u534A\u5929\u6C14\u624D\u8BF4\u9053\uFF1A\u300C\u5929\u5DF2\u7ECF\u4EAE\u4E86\uFF0C\u5FEB\u628A\u98CE\u5E08\u2026\u2026\u98CE\u5974\u53EB\u8FDB\u6765\uFF01\u300D</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u300C\u516D\u65E5\u4E4B\u540E\uFF0C\u4FBF\u662F\u672C\u5BAB\u4E0E\u73AB\u7470\u4ED9\u5B50\u6210\u4EB2\u5409\u65E5\u3002\u5C4A\u65F6\u5929\u4E0B\u540C\u9053\u9F50\u805A\u5BAB\u4E2D\u3002\u4E3A\u626C\u795E\u6559\u5A01\u540D\uFF0C\u5927\u5BB6\u591A\u591A\u8F9B\u82E6\u3002\u300D</p><p>\u8FD1\u5343\u5E2E\u4F17\u9F50\u58F0\u5E94\u8BFA\uFF0C\u58F0\u632F\u7FA4\u5CF0\u3002</p><p>\u6155\u5BB9\u9F99\u4EF0\u9996\u671B\u7740\u78A7\u84DD\u7684\u82CD\u7A79\uFF0C\u5FC3\u795E\u4EFF\u4F5B\u98DE\u626C\u7684\u767D\u4E91\uFF0C\u8D8A\u8FC7\u7EC8\u5357\u7FA4\u5CF0\uFF0C\u7FF1\u6E38\u6D12\u6EE1\u9633\u5149\u7684\u5E73\u539F\u4E0A\u3002</p><p>\u5B9D\u85CF\u3002\u5175\u9A6C\u3002\u65E0\u8FB9\u65E0\u9645\u7684\u539F\u91CE\u3002\u9C9C\u660E\u7684\u8863\u7532\u3001\u6797\u7ACB\u7684\u65D7\u5E1C\u548C\u5C18\u571F\u4E2D\u8DEA\u4F0F\u819C\u62DC\u7684\u5B50\u6C11\u2026\u2026</p><p>\u88AB\u6298\u78E8\u6574\u591C\u7684\u5973\u4EBA\u8737\u7F29\u7740\u8EAB\u4F53\uFF0C\u6709\u6C14\u65E0\u529B\u7684\u547B\u541F\u7740\u3002\u767D\u6C0F\u59D0\u59B9\u8EAB\u6000\u6B66\u529F\uFF0C\u4F46\u5979\u4EEC\u4FE9\u88AB\u9501\u5728\u4E00\u8D77\uFF0C\u6628\u591C\u8BB8\u591A\u4EBA\u90FD\u60F3\u628A\u5979\u4EEC\u5206\u5F00\uFF0C\u7ED3\u679C\u4E24\u5973\u4E73\u5934\u51E0\u4E4E\u88AB\u626F\u6389\u3002\u88AB\u65E0\u6570\u8089\u68D2\u6345\u5F04\u7684\u79D8\u5904\u7EA2\u80BF\u4E0D\u582A\uFF0C\u767D\u7389\u83BA\u7684\u82B1\u8482\u66F4\u662F\u88AB\u751F\u751F\u626F\u788E\uFF0C\u4E24\u53EA\u91D1\u94C3\u90FD\u60AC\u5728\u59B9\u59B9\u4F53\u4E0B\u3002\u5F85\u4EBA\u7FA4\u6563\u5F00\uFF0C\u59D0\u59B9\u4FE9\u6402\u62B1\u7740\u4E00\u6B65\u6B65\u632A\u56DE\u795E\u6BBF\u3002</p><p>\u5176\u4ED6\u5973\u5B50\u5374\u6CA1\u6709\u8FD9\u4E48\u597D\u8FD0\uFF0C\u5979\u4EEC\u4ECD\u88AB\u5E26\u56DE\u4E0D\u89C1\u5929\u65E5\u7684\u77F3\u5BA4\uFF0C\u7EE7\u7EED\u63A5\u53D7\u6467\u6B8B\u548C\u8E42\u8E8F\u3002</p><p>\u6797\u9999\u8FDC\u7389\u6237\u4F24\u52BF\u672A\u7652\uFF0C\u4F46\u90A3\u4E9B\u4EBA\u4E5F\u6CA1\u6709\u653E\u8FC7\u5979\u7684\u53E6\u4E00\u4E2A\u8089\u7A74\u3002\u83CA\u809B\u88AB\u6363\u6210\u8840\u7EA2\u7684\u6D1E\u7A74\uFF0C\u91CC\u9762\u704C\u6EE1\u7CBE\u6DB2\u3002\u7EED\u597D\u7684\u94C1\u94FE\u4ECE\u90A3\u5757\u8D34\u4E0A\u53BB\u7684\u76AE\u80A4\u4E0B\u4F38\u51FA\uFF0C\u4ECD\u7CFB\u5728\u77F3\u680F\u95F4\u3002</p><p>\u98CE\u665A\u534E\u65E9\u5DF2\u660F\u8FF7\u591A\u65F6\uFF0C\u5979\u659C\u8EAB\u5012\u5728\u9ED1\u8272\u7684\u5927\u7406\u77F3\u4E0A\uFF0C\u4E00\u6761\u7389\u817F\u5782\u5728\u9636\u4E0B\uFF0C\u655E\u9732\u7684\u80A1\u95F4\u5AE9\u8089\u7FFB\u5377\uFF0C\u7EA2\u80BF\u96F6\u4E71\u3002</p><p>\u7D2B\u73AB\u5C0F\u5FC3\u5730\u6258\u8D77\u5927\u5E08\u59D0\uFF0C\u53EA\u89C1\u5979\u7389\u4E73\u4E00\u9635\u6643\u52A8\uFF0C\u88AB\u6155\u5BB9\u9F99\u300C\u5F00\u82DE\u300D\u7684\u53F3\u4E73\u4E73\u5C16\u671D\u4E0A\u7FFB\u8D77\uFF0C\u4F24\u6D1E\u4E2D\u6D41\u51FA\u5927\u91CF\u6D53\u6D4A\u7684\u9633\u7CBE\u3002\u6D41\u971C\u5251\u5728\u6B66\u6797\u540D\u58F0\u6781\u54CD\uFF0C\u5979\u6240\u53D7\u7684\u5978\u6DEB\u4E5F\u6700\u591A\uFF0C\u6240\u6709\u7684\u8089\u7A74\u4F3C\u4E4E\u90FD\u76DB\u6EE1\u7537\u5B50\u7684\u6392\u6CC4\u7269\uFF0C\u5A07\u8EAF\u4E00\u52A8\uFF0C\u4FBF\u4E00\u80A1\u4E00\u80A1\u6D41\u4E2A\u4E0D\u505C\u3002</p><p>\u7D2B\u73AB\u53C8\u6068\u53C8\u75BC\uFF0C\u5403\u529B\u5730\u62B1\u8D77\u98CE\u5E08\u59D0\u8D70\u5165\u6BBF\u5185\u3002</p><p>\u98CE\u665A\u534E\u82CD\u767D\u7684\u9762\u5B54\u4ECE\u7D2B\u73AB\u80A9\u4FA7\u9732\u51FA\uFF0C\u6155\u5BB9\u9F99\u9759\u9759\u671B\u7740\u5979\u9ECF\u6E7F\u7684\u79C0\u53D1\uFF0C\u5634\u89D2\u6162\u6162\u6311\u8D77\u4E00\u4E1D\u5FAE\u7B11\u3002</p><p>\u5E26\u7740\u6E56\u6C34\u5473\u9053\u7684\u6668\u98CE\u5439\u8FC7\uFF0C\u8FDC\u5904\u9AD8\u8038\u7684\u65D7\u6746\u4E0A\uFF0C\u521A\u521A\u5347\u8D77\u7684\u5927\u65D7\u8FCE\u98CE\u62DB\u5C55\uFF0C\u8C61\u5F81\u7740\u4E00\u4E2A\u53E4\u8001\u52BF\u529B\u7684\u65B0\u751F\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u7EAA\u7709\u59A9\u7684\u623F\u95F4\u88AB\u4EBA\u7D27\u9501\uFF0C\u7D2B\u73AB\u53EA\u597D\u628A\u98CE\u665A\u534E\u5E26\u5230\u4E3B\u5BA4\u3002\u5979\u7EC6\u7EC6\u64E6\u53BB\u5E08\u59D0\u8EAB\u4E0A\u7684\u5404\u79CD\u6C61\u6E0D\uFF0C\u4E00\u8FB9\u64E6\u4E00\u8FB9\u6389\u6CEA\u3002</p><p>\u8EAB\u540E\u811A\u6B65\u58F0\u54CD\uFF0C\u7D2B\u73AB\u5934\u4E5F\u4E0D\u62AC\u5730\u4F4E\u58F0\u9053\uFF1A\u300C\u6211\u8981\u89C1\u5E08\u7236\u3002\u300D</p><p>\u300C\u53EF\u4EE5\u3002\u300D\u6155\u5BB9\u9F99\u8BF4\u7740\u62E7\u8D77\u98CE\u665A\u534E\u7684\u65AD\u81C2\u3002</p><p>\u300C\u4F60\u8981\u5E72\u4EC0\u4E48\uFF1F\u300D\u7D2B\u73AB\u4E00\u60CA\uFF0C\u60F6\u7136\u633A\u8EAB\u6321\u4F4F\u3002</p><p>\u300C\u8FD9\u662F\u4F60\u6211\u7684\u6D1E\u623F\uFF0C\u600E\u4E48\u80FD\u8BA9\u8FD9\u4E2A\u8D31\u5974\u8FDB\u6765\uFF1F\u54E5\u54E5\u7ED9\u5979\u6362\u95F4\u5C4B\u5B50\u3002\u300D</p><p>\u300C\u8BA9\u5979\u548C\u7EAA\u5974\u4F4F\u5728\u4E00\u8D77\u5427\uFF1F\u300D\u7D2B\u73AB\u5C0F\u58F0\u8BF4\u3002</p><p>\u6155\u5BB9\u9F99\u624B\u4E00\u7D27\uFF0C\u98CE\u665A\u534E\u4ECE\u5E8A\u4E0A\u6389\u843D\uFF0C\u8170\u817F\u8F6F\u7EF5\u7EF5\u62D6\u5728\u5730\u4E0A\u6CA1\u6709\u4E00\u4E1D\u529B\u6C14\u3002</p><p>\u7D2B\u73AB\u8FDE\u5FD9\u62B1\u8D77\u5E08\u59D0\u4E24\u817F\uFF0C\u8DDF\u5728\u6155\u5BB9\u9F99\u8EAB\u540E\u8D70\u51FA\u752C\u9053\u3002</p><p>\u300C\u8FD9\u600E\u4E48\u53EF\u4EE5\uFF01\u300D\u7D2B\u73AB\u5389\u58F0\u5A07\u559D\uFF0C\u6B7B\u6B7B\u62B1\u7740\u5E08\u59D0\u7684\u8170\u817F\u4E0D\u613F\u8FC8\u6B65\u3002</p><p>\u6155\u5BB9\u9F99\u4E00\u626F\uFF0C\u7D2B\u73AB\u8E09\u8DC4\u7740\u88AB\u5E26\u5165\u5730\u5B57\u752C\u9053\u3002\u5979\u4E00\u5C41\u80A1\u5750\u5728\u5730\u4E0A\u5927\u54ED\u8D77\u6765\uFF0C\u300C\u4F60\u8BF4\u8FC7\u4E0D\u6740\u5979\u7684\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u7B11\u563B\u563B\u770B\u7740\u5979\u7684\u6CEA\u73E0\uFF0C\u300C\u8C01\u8BF4\u54E5\u54E5\u8981\u6740\u5979\uFF1F\u300D</p><p>\u300C\u90A3\u4E3A\u4EC0\u4E48\u8981\u6765\u8FD9\u91CC\uFF1F\u300D</p><p>\u300C\u56E0\u4E3A\u8FD9\u513F\u6709\u95F4\u623F\u6B63\u9002\u5408\u5979\u4F4F\u2026\u2026\u300D\u6155\u5BB9\u9F99\u6323\u8131\u5979\u7684\u624B\u638C\uFF0C\u8D70\u5230\u752C\u9053\u5C3D\u5934\u3002</p><p>\u7D2B\u73AB\u6323\u624E\u7740\u722C\u8D77\u6765\uFF0C\u60F3\u8D77\u5F53\u65E5\u864E\u53E3\u6C34\u67D4\u4ED9\u5B64\u96F6\u96F6\u7684\u5934\u9885\u3002\u5FC3\u91CC\u53C8\u662F\u7D27\u5F20\u53C8\u5BB3\u6015\uFF0C\u546F\u546F\u76F4\u8DF3\u3002</p><p>\u6155\u5BB9\u9F99\u5728\u300C\u620C\u300D\u5BA4\u524D\u505C\u4E0B\u811A\u6B65\uFF0C\u6273\u52A8\u673A\u62EC\uFF0C\u8F67\u8F67\u58F0\u54CD\u4E2D\uFF0C\u4E45\u672A\u5F00\u542F\u7684\u77F3\u95E8\u7F13\u7F13\u5206\u5F00\u3002</p>',61);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
