import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_45" tabindex="-1"><a class="header-anchor" href="#_45" aria-hidden="true">#</a> 45</h1><p>\u4E00\u6761\u9ED1\u5F71\u95EA\u7535\u822C\u63A0\u51FA\uFF0C\u6155\u5BB9\u9F99\u5C48\u6307\u4E00\u5F39\uFF0C\u90A3\u6761\u9ED1\u5F71\u84EC\u7136\u843D\u5730\uFF0C\u6E85\u8D77\u4E00\u7247\u7070\u5C18\u3002</p><p>\u7D2B\u73AB\u8FD9\u624D\u770B\u51FA\u9ED1\u5F71\u662F\u4E00\u6761\u5DE8\u5927\u7684\u9ED1\u7352\uFF0C\u6B63\u5F13\u8170\u6C89\u80CC\uFF0C\u4F5C\u52BF\u6B32\u6251\u3002\u63A5\u7740\u5BA4\u5185\u54CD\u8D77\u4E00\u7247\u5145\u6EE1\u6740\u673A\u7684\u4F4E\u54CD\uFF0C\u7D2B\u73AB\u7A33\u4F4F\u5FC3\u795E\uFF0C\u5C0F\u5FC3\u770B\u53BB\uFF0C\u624D\u53D1\u73B0\u5BA4\u4E2D\u5171\u6709\u56DB\u6761\u5DE8\u72AC\uFF0C\u5404\u6709\u725B\u728A\u5927\u5C0F\uFF0C\u9ED1\u3001\u9EC4\u3001\u82B1\u3001\u767D\u5404\u8272\u4FF1\u5168\uFF0C\u4F46\u90FD\u662F\u76EE\u9732\u51F6\u5149\u3002</p><p>\u5DE8\u72AC\u957F\u957F\u7684\u7EA2\u820C\u62D6\u5728\u53E3\u5916\uFF0C\u6D8E\u6CAB\u4E0D\u4F4F\u6EF4\u843D\u3002\u767D\u68EE\u68EE\u7684\u7259\u9F7F\u65F6\u5F00\u65F6\u5408\uFF0C\u5589\u5499\u91CC\u4F20\u6765\u9635\u9635\u4F4E\u6C89\u7684\u5486\u54EE\uFF0C\u5BFB\u627E\u673A\u4F1A\u628A\u4E09\u4EBA\u6495\u6210\u788E\u7247\u3002</p><p>\u6155\u5BB9\u9F99\u62FF\u51FA\u4E00\u53EA\u74F6\u5B50\uFF0C\u5C06\u91CC\u9762\u6DE1\u9EC4\u8272\u7684\u6DB2\u4F53\u671D\u7D2B\u73AB\u8EAB\u4E0A\u6D12\u4E86\u4E00\u6EF4\uFF0C\u7136\u540E\u585E\u5230\u5979\u624B\u4E2D\uFF0C\u300C\u7ED9\u98CE\u5A4A\u5B50\u62B9\u4E0A\u3002\u300D</p><p>\u300C\u8FD9\u662F\u4EC0\u4E48\uFF1F\u300D</p><p>\u300C\u4E0D\u60F3\u8BA9\u5979\u6B7B\uFF0C\u5C31\u8D76\u7D27\u62B9\u3002\u300D\u6155\u5BB9\u9F99\u51B7\u7136\u9053\u3002</p><p>\u7D2B\u73AB\u53EA\u597D\u4FEF\u8EAB\u628A\u836F\u6DB2\u5012\u5728\u638C\u5FC3\uFF0C\u5728\u98CE\u665A\u534E\u8D64\u88F8\u7684\u80A9\u5934\u62B9\u4E86\u4E00\u70B9\u3002</p><p>\u300C\u591A\u62B9\u70B9\u3002\u8FD9\u4E9B\u72D7\u53EA\u8BA4\u6C14\u5473\uFF0C\u6709\u4E00\u4E2A\u5730\u65B9\u6CA1\u62B9\u5230\uFF0C\u5B83\u4EEC\u53EF\u4E0D\u4F1A\u5BA2\u6C14\u3002\u300D</p><p>\u7D2B\u73AB\u626C\u8D77\u8138\uFF0C\u8F7B\u58F0\u9053\uFF1A\u300C\u4F60\u8981\u628A\u5979\u653E\u5728\u8FD9\u91CC\uFF1F\u300D</p><p>\u6155\u5BB9\u9F99\u70B9\u70B9\u5934\uFF0C\u300C\u6CA1\u9519\u3002\u5979\u53EA\u914D\u8DDF\u72D7\u4F4F\u5728\u4E00\u8D77\u3002\u300D</p><p>\u7D2B\u73AB\u62B9\u4E86\u628A\u773C\u6CEA\uFF0C\u4F4E\u58F0\u9053\uFF1A\u300C\u4E0D\u80FD\u6362\u4E2A\u5730\u65B9\u5417\uFF1F\u6211\u53EF\u4EE5\u4E56\u4E56\u542C\u4F60\u7684\u8BDD\uFF0C\u4F60\u8981\u600E\u4E48\u6837\u5C31\u600E\u4E48\u6837\u2014\u2014\u4F60\u4E0D\u662F\u60F3\u8BA9\u6211\u4EB2\u2026\u2026\u5B83\u5417\uFF1F\u6211\u613F\u610F\u2026\u2026\u300D\u5979\u54ED\u5F97\u8BF4\u4E0D\u51FA\u8BDD\u6765\u3002</p><p>\u6155\u5BB9\u9F99\u6DE1\u6DE1\u9053\uFF1A\u300C\u6709\u8D3C\u5C3C\u4E00\u4E2A\u5C31\u53EF\u4EE5\u8BA9\u4F60\u542C\u8BDD\uFF0C\u6211\u4F55\u5FC5\u518D\u505A\u8BA9\u6B65\uFF1F\u4E00\u7B14\u672C\u94B1\u5C31\u53EF\u4EE5\u505A\u7684\u751F\u610F\uFF0C\u54E5\u54E5\u7EDD\u4E0D\u518D\u8D34\u4E0A\u4E00\u7B14\u3002\u300D</p><p>\u7D2B\u73AB\u5782\u4E0B\u5934\uFF0C\u4E00\u8FB9\u6D12\u6CEA\uFF0C\u4E00\u8FB9\u628A\u836F\u6DB2\u62B9\u904D\u5E08\u59D0\u5168\u8EAB\uFF0C\u534A\u664C\u624D\u9053\uFF1A\u300C\u5B83\u4EEC\u771F\u4E0D\u4F1A\u54AC\u5979\u5417\uFF1F\u300D</p><p>\u300C\u4E0D\u4F1A\u3002\u300D\u6155\u5BB9\u9F99\u8BF4\u7740\uFF0C\u5FC3\u91CC\u4F4E\u7B11\u9053\uFF1A\u300C\u4F55\u6B62\u4E0D\u4F1A\u54AC\u5979\uFF0C\u8FD8\u4F1A\u628A\u5979\u5F53\u6210\u4E00\u6761\u9A9A\u54C4\u54C4\u7684\u6BCD\u72D7\u75BC\u5979\u5462\uFF01\u300D</p><p>\u56DB\u6761\u5DE8\u72AC\u55C5\u5230\u6C14\u5473\uFF0C\u4E0D\u518D\u8DF3\u8DC3\u4F5C\u52BF\uFF0C\u53EA\u7AD6\u7740\u5C3E\u5DF4\u7F13\u7F13\u8FEB\u8FD1\uFF0C\u9C9C\u7EA2\u7684\u820C\u5934\u4E0D\u4F4F\u4F38\u7F29\u3002</p><p>\u6155\u5BB9\u9F99\u5F80\u98CE\u665A\u534E\u5634\u91CC\u585E\u4E86\u4E00\u7C92\u836F\u4E38\uFF0C\u7136\u540E\u62FF\u51FA\u4E00\u4E2A\u9879\u5708\u5957\u5728\u6D41\u971C\u5251\u67D4\u9888\u4E2D\u3002</p><p>\u5F85\u94C1\u94FE\u9501\u7D27\uFF0C\u98CE\u91C7\u7167\u4EBA\u7684\u98CE\u665A\u534E\u4FBF\u8D64\u8EAB\u88F8\u4F53\u88AB\u6254\u5728\u7FA4\u72AC\u4E4B\u4E2D\u3002</p><p>\u5979\u832B\u7136\u7741\u5F00\u773C\u775B\uFF0C\u53EA\u89C9\u8179\u5185\u4EFF\u4F5B\u88AB\u70C8\u706B\u70E7\u7099\u822C\u707C\u75DB\uFF0C\u6D51\u8EAB\u7684\u8840\u6DB2\u968F\u4E4B\u84B8\u817E\uFF0C\u5934\u8111\u4E5F\u88AB\u70E7\u5F97\u660F\u660F\u6C89\u6C89\u3002\u5979\u9690\u7EA6\u542C\u5230\u7D2B\u73AB\u7684\u58F0\u97F3\uFF0C\u300C\u2026\u2026\u660E\u5929\u6765\u770B\u4F60\u2026\u2026\u300D\u63A5\u7740\u623F\u95E8\u5408\u7D27\uFF0C\u5BA4\u5185\u518D\u6CA1\u6709\u4E00\u4E1D\u5149\u4EAE\u3002</p><p>\u8FD8\u6709\u5149\u4EAE\u3002\u5468\u56F4\u51E0\u53EA\u7855\u5927\u7684\u660E\u73E0\u60AC\u6D6E\u5728\u7A7A\u4E2D\uFF0C\u95EA\u7740\u84DD\u8367\u8367\u7684\u5E7D\u5149\u6162\u6162\u9760\u8FD1\u3002</p><p>\u98CE\u665A\u534E\u5403\u529B\u5730\u7528\u4EC5\u5269\u7684\u624B\u81C2\u6491\u8D77\u8EAB\u4F53\uFF0C\u60F3\u7AD9\u8D77\u6765\u3002\u521A\u626C\u8D77\u5934\uFF0C\u5FFD\u7136\u9888\u4E2D\u4E00\u7D27\uFF0C\u53C8\u6454\u5728\u5730\u4E0A\u3002</p><p>\u4E00\u80A1\u70ED\u547C\u547C\u7684\u8165\u81ED\u6C14\u606F\u5439\u5230\u8138\u4E0A\uFF0C\u98CE\u665A\u534E\u8D6B\u7136\u53D1\u73B0\uFF0C\u90A3\u4E9B\u95EA\u7740\u5E7D\u5149\u7684\u660E\u73E0\u5C45\u7136\u662F\u4E00\u4E9B\u773C\u775B\uFF0C\u91CE\u517D\u7684\u773C\u775B\uFF01\u9ED1\u6697\u50CF\u6C89\u7538\u7538\u7684\u91CD\u7269\u538B\u5728\u865A\u5F31\u7684\u8EAB\u4F53\u4E0A\uFF0C\u98CE\u665A\u534E\u5FC3\u91CC\u5145\u6EE1\u6050\u60E7\uFF0C\u5F53\u4E00\u4E2A\u70ED\u70ED\u7684\u820C\u5934\u8214\u5230\u67D4\u5AE9\u7684\u808C\u80A4\u4E0A\u65F6\uFF0C\u5979\u5FC3\u5934\u731B\u7136\u4E00\u7D27\uFF0C\u7EB7\u4E71\u7684\u8111\u6D77\u548C\u70BD\u70ED\u7684\u8089\u4F53\u6FC0\u8361\u7740\uFF0C\u987F\u65F6\u6655\u4E86\u8FC7\u53BB\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u96EA\u5CF0\u795E\u5C3C\u76D8\u819D\u800C\u5750\uFF0C\u624B\u634F\u6CD5\u5370\uFF0C\u4ECE\u5947\u7ECF\u516B\u8109\u51DD\u805A\u6563\u4E71\u7684\u771F\u6C14\u3002\u5316\u771F\u6563\u836F\u6548\u679C\u7136\u795E\u5999\uFF0C\u5979\u8D39\u5C3D\u4E5D\u725B\u4E8C\u864E\u4E4B\u529B\uFF0C\u624D\u52C9\u5F3A\u4ECE\u5E26\u8109\u628A\u63E1\u5230\u4E00\u7F15\u6E38\u4E1D\u822C\u7684\u771F\u6C14\u3002\u795E\u5C3C\u5C0F\u5FC3\u7FFC\u7FFC\u5730\u907F\u5F00\u4E39\u7530\u6C14\u6D77\uFF0C\u5C06\u771F\u6C14\u4ECE\u522B\u8109\u6C47\u5165\u8C37\u5E9C\u3002\u771F\u6C14\u521A\u521A\u7A33\u4F4F\uFF0C\u5FFD\u7136\u9F3B\u524D\u4E00\u52A8\uFF0C\u4FBF\u6D88\u6563\u65E0\u8FF9\u3002</p><p>\u84DD\u8272\u7684\u836F\u74F6\u5728\u795E\u5C3C\u9F3B\u524D\u4E00\u6643\uFF0C\u6155\u5BB9\u9F99\u628A\u5316\u771F\u6563\u7EB3\u5165\u6000\u4E2D\uFF0C\u5FAE\u7B11\u9053\uFF1A\u300C\u5E08\u592A\u5DF2\u7ECF\u5165\u6211\u795E\u6559\uFF0C\u4F55\u5FC5\u5982\u6B64\u7528\u529F\uFF1F\u300D</p><p>\u8DEF\u4E0A\u7D2B\u73AB\u65E9\u5DF2\u64E6\u5E72\u6CEA\u75D5\uFF0C\u5979\u88C5\u4F5C\u82E5\u65E0\u5176\u4E8B\u5730\u8BF4\uFF1A\u300C\u4F60\u5148\u51FA\u53BB\uFF0C\u6211\u8DDF\u5E08\u7236\u8BF4\u51E0\u53E5\u8BDD\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u6599\u60F3\u8FD9\u4E24\u4E2A\u5185\u529F\u5C3D\u5931\u7684\u5F31\u5973\u5B50\u4E5F\u73A9\u4E0D\u51FA\u4EC0\u4E48\u82B1\u6837\uFF0C\u4FBF\u5927\u5EA6\u5730\u8F6C\u8EAB\u79BB\u53BB\u3002</p><p>\u6155\u5BB9\u7D2B\u73AB\u5C06\u4E8B\u60C5\u539F\u539F\u672C\u672C\u544A\u8BC9\u96EA\u5CF0\u795E\u5C3C\uFF0C\u53EA\u662F\u5E08\u59D0\u53D7\u8FB1\u7684\u7ECF\u8FC7\u96BE\u4EE5\u542F\u9F7F\uFF0C\u5979\u6CA1\u6709\u591A\u8BF4\uFF0C\u6700\u540E\u4F4E\u58F0\u9053\uFF1A\u300C\u5E08\u7236\uFF0C\u90FD\u662F\u6211\u4E0D\u597D\uFF0C\u5BF9\u4E0D\u8D77\u4F60\u548C\u4E09\u4F4D\u5E08\u59D0\u3002\u300D</p><p>\u300C\u522B\u4EBA\u60F3\u5077\u4F60\u7684\u4E1C\u897F\uFF0C\u96BE\u9053\u662F\u4F60\u7684\u4E0D\u597D\u5417\uFF1F\u73AB\u513F\uFF0C\u4E0D\u8981\u81EA\u8D23\u4E86\u3002\u300D</p><p>\u7D2B\u73AB\u542B\u7740\u773C\u6CEA\uFF0C\u98A4\u58F0\u9053\uFF1A\u300C\u5E08\u7236\uFF0C\u5F92\u513F\u8BE5\u600E\u4E48\u529E\uFF1F\u300D</p><p>\u96EA\u5CF0\u795E\u5C3C\u6C89\u9ED8\u7247\u523B\uFF0C\u53F9\u9053\uFF1A\u300C\u73B0\u5728\u53EA\u6709\u5148\u4E0E\u4ED6\u865A\u4E0E\u59D4\u86C7\u2026\u2026\u300D\u5979\u65CB\u5373\u60F3\u8D77\u4E00\u4EF6\u5927\u4E8B\uFF0C\u4E00\u628A\u63E1\u4F4F\u7D2B\u73AB\u7684\u67D4\u8351\uFF0C\u6025\u5207\u5730\u8BF4\uFF1A\u300C\u4E14\u8BB0\u4E0D\u53EF\u4E0E\u4ED6\u540C\u623F\uFF0C\u51E4\u51F0\u5B9D\u5178\u672A\u7EC3\u81F3\u7B2C\u516B\u5C42\u7EDD\u4E0D\u53EF\u5931\u8EAB\uFF0C\u5426\u4E14\u6027\u547D\u96BE\u4FDD\uFF01\u300D</p><p>\u7D2B\u73AB\u604D\u7136\u8BB0\u8D77\uFF0C\u5E08\u7236\u66FE\u8BF4\u7B49\u5979\u7EC3\u81F3\u7B2C\u4E03\u5C42\u65F6\uFF0C\u5E08\u5F92\u4FE9\u4E00\u540C\u53C2\u8BE6\u51E4\u51F0\u5B9D\u5178\u7684\u5965\u4E49\uFF0C\u5728\u6B64\u4E4B\u524D\u7EDD\u4E0D\u53EF\u5931\u8EAB\u4E8E\u4EBA\u3002\u5979\u5F53\u65F6\u89C9\u5F97\u90A3\u662F\u975E\u5E38\u9065\u8FDC\u7684\u4E8B\u60C5\uFF0C\u5E76\u672A\u653E\u5728\u5FC3\u91CC\uFF0C\u53EF\u73B0\u5728\u79BB\u5A5A\u793C\u53EA\u5269\u4E0B\u516D\u5929\u65F6\u95F4\u2026\u2026</p><p>\u534A\u664C\u7D2B\u73AB\u5AE3\u7136\u4E00\u7B11\uFF0C\u8F7B\u677E\u5730\u8BF4\uFF1A\u300C\u6B7B\u4E86\u4E5F\u597D\uFF0C\u90A3\u6DF7\u86CB\u5C31\u6211\u4E00\u4E2A\u4EB2\u59B9\u59B9\uFF0C\u6B7B\u4E86\u4ED6\u5C31\u4E0D\u7528\u505A\u68A6\u4E86\u3002\u300D</p><p>\u96EA\u5CF0\u795E\u5C3C\u76EE\u5149\u95EA\u95EA\u5730\u671B\u7740\u5979\uFF0C\u4F4E\u58F0\u9053\uFF1A\u300C\u4E0D\u5230\u6700\u540E\u5173\u5934\u7EDD\u4E0D\u8981\u8F7B\u6613\u653E\u5F03\u3002\u5230\u65F6\u4E0D\u59A8\u544A\u8BC9\u4ED6\uFF0C\u8BA9\u4ED6\u81EA\u5DF1\u9009\u62E9\u3002\u300D</p><p>\u7D2B\u73AB\u7B11\u9053\uFF1A\u300C\u80FD\u4E0D\u6B7B\u5F92\u513F\u5F53\u7136\u4E0D\u613F\u610F\u6B7B\u4E86\u2026\u2026\u5BF9\u4E86\uFF0C\u5F92\u513F\u4E00\u4E2A\u6708\u524D\u884C\u529F\u65F6\u7A81\u7136\u89C9\u5F97\u4E0D\u540C\u2026\u2026\u300D\u63A5\u7740\u5979\u628A\u5F53\u65E5\u4E0E\u7EAA\u7709\u59A9\u540C\u8D74\u8700\u4E2D\u65F6\u7EC3\u529F\u7684\u611F\u53D7\u4E00\u4E00\u544A\u8BC9\u5E08\u7236\u3002</p><p>\u96EA\u5CF0\u795E\u5C3C\u6C89\u541F\u9053\uFF1A\u300C\u4F60\u65E2\u7136\u89C9\u5F97\u6C14\u6D77\u9707\u6C64\uFF0C\u771F\u6C14\u7F15\u7F15\u4E0D\u7EDD\uFF0C\u90A3\u4FBF\u662F\u7EC3\u81F3\u51E4\u51F0\u5B9D\u5178\u7B2C\u4E94\u5C42\u51E4\u7BAB\u58F0\u52A8\u7684\u8FF9\u8C61\u3002\u5176\u540E\u4F9D\u6B21\u662F\u51E4\u5C55\u5F69\u7FFC\u3001\u51E4\u9E23\u671D\u9633\u3001\u51E4\u51F0\u4E8E\u98DE\uFF0C\u6700\u540E\u662F\u7B2C\u4E5D\u5C42\u51E4\u6E05\u7D2B\u9E3E\u3002\u516D\u5E74\u524D\u4F60\u5165\u95E8\u65F6\u5E08\u7236\u4FBF\u7EC3\u5230\u7B2C\u4E03\u5C42\u51E4\u9E23\u671D\u9633\uFF0C\u4F46\u6B64\u540E\u518D\u65E0\u5BF8\u8FDB\uFF0C\u6211\u98D8\u6885\u5CF0\u9664\u4E86\u5F00\u5C71\u5E08\u7956\uFF0C\u5386\u4EE3\u5F1F\u5B50\u4E5F\u90FD\u672A\u80FD\u7EC3\u81F3\u7B2C\u516B\u5C42\u51E4\u51F0\u4E8E\u98DE\u2026\u2026\u300D</p><p>\u795E\u5C3C\u5F53\u65F6\u50AC\u4FC3\u7D2B\u73AB\u7EC3\u529F\u751A\u6025\uFF0C\u5176\u5B9E\u662F\u56E0\u4E3A\u6015\u5979\u50CF\u81EA\u5DF1\u4E00\u6837\u8FDF\u8FDF\u8FC7\u4E0D\u4E86\u7B2C\u4E03\u5C42\u7684\u754C\u9650\uFF0C\u65E0\u6CD5\u5AC1\u4EBA\u3002\u5979\u4F30\u8BA1\u4EE5\u7D2B\u73AB\u7684\u8D44\u8D28\uFF0C\u5341\u5E74\u4FBF\u53EF\u4E0E\u5979\u540C\u6837\u7EC3\u81F3\u7B2C\u4E03\u5C42\uFF0C\u5230\u65F6\u5E08\u5F92\u4FE9\u5171\u540C\u53C2\u8BE6\uFF0C\u82E5\u80FD\u4FEE\u884C\u81F3\u7B2C\u516B\u5C42\u6700\u597D\uFF1B\u5982\u679C\u4E0D\u80FD\uFF0C\u795E\u5C3C\u4FBF\u6253\u7B97\u5C06\u529F\u529B\u5C3D\u6570\u8F93\u4E8E\u7D2B\u73AB\uFF0C\u6599\u60F3\u8DB3\u4EE5\u7A81\u7834\u51E4\u9E23\u671D\u9633\u4E00\u5173\u3002</p><p>\u7B2C\u516B\u5C42\u51E4\u51F0\u4E8E\u98DE\uFF0C\u5FC3\u6CD5\u4E0A\u6CE8\u660E\u59CB\u53EF\u7834\u4F53\uFF0C\u9634\u9633\u5408\u6D4E\uFF0C\u5230\u65F6\u4FBF\u80FD\u987A\u987A\u5229\u5229\u4E0E\u6CAE\u6E20\u5C55\u626C\u6210\u4EB2\u3002\u81F3\u4E8E\u6700\u540E\u51E4\u6E05\u7D2B\u9E3E\u5FC3\u6CD5\u4E0A\u8BF4\u7684\u9634\u4E0A\u52A0\u9634\uFF0C\u767E\u5E74\u6765\u4ECE\u65E0\u4EBA\u80FD\u4E00\u63A2\u7A76\u7ADF\uFF0C\u73B0\u5728\u4E5F\u4E0D\u5FC5\u591A\u60F3\u4E86\u3002</p><p>\u7D2B\u73AB\u9053\uFF1A\u300C\u4ED6\u8BF4\u8FC7\u5A5A\u793C\u4E4B\u540E\u5C31\u7ED9\u6211\u5316\u771F\u6563\u7684\u89E3\u836F\u3002\u5C31\u7B97\u4ED6\u4E0D\u7ED9\uFF0C\u4E24\u4E09\u65E5\u540E\u5316\u771F\u6563\u4E5F\u4F1A\u5931\u6548\u3002\u524D\u4E9B\u65E5\u5B50\u6211\u95EE\u8FC7\u53F6\u8001\u5934\uFF0C\u5316\u771F\u6563\u672C\u6765\u5C31\u4E0D\u591A\uFF0C\u80AF\u5B9A\u4E0D\u591F\u4E24\u4E2A\u4EBA\u7528\u3002</p><p>\u5230\u65F6\u5982\u679C\u5A5A\u793C\u5EF6\u671F\uFF0C\u5F92\u513F\u4E00\u5B9A\u52E4\u4FEE\u5B9D\u5178\uFF0C\u65E9\u65E5\u7EC3\u5230\u7B2C\u4E03\u5C42\uFF0C\u628A\u8FD9\u4E9B\u6DF7\u86CB\u7EDF\u7EDF\u6740\u6389\uFF01\u300D</p><p>\u5979\u8D8A\u8BF4\u8D8A\u6068\uFF0C\u7D27\u7D27\u6525\u7740\u5C0F\u62F3\u5934\uFF0C\u6068\u4E0D\u80FD\u5373\u523B\u4FBF\u50CF\u5E08\u7236\u4E00\u6837\u795E\u529F\u5728\u8EAB\uFF0C\u5148\u6495\u788E\u6155\u5BB9\u9F99\u8FD9\u5BB6\u4F19\uFF01</p><p>\u96EA\u5CF0\u795E\u5C3C\u5374\u6CA1\u8FD9\u4E48\u4E50\u89C2\uFF0C\u5373\u4F7F\u5316\u771F\u6563\u4E0D\u6577\u4F7F\u7528\uFF0C\u800C\u4E14\u96BE\u4EE5\u914D\u5236\uFF0C\u4ED6\u4EEC\u80AF\u5B9A\u8FD8\u80FD\u60F3\u51FA\u5176\u5B83\u65B9\u6CD5\u514B\u5236\u7D2B\u73AB\u7684\u771F\u6C14\uFF0C\u751A\u6216\u662F\u50CF\u5BF9\u4ED8\u5176\u4ED6\u51E0\u4F4D\u5F92\u5F1F\u4E00\u6837\uFF0C\u76F4\u63A5\u5438\u5C3D\u5979\u7684\u529F\u529B\u3002\u4F46\u770B\u5230\u7231\u5F92\u6FC0\u6602\u7684\u795E\u60C5\uFF0C\u96EA\u5CF0\u795E\u5C3C\u4E5F\u4E0D\u613F\u6CFC\u5979\u51B7\u6C34\u3002</p><p>\u4E8B\u5230\u5982\u4ECA\uFF0C\u53EA\u6709\u8D70\u4E00\u6B65\u662F\u4E00\u6B65\uFF0C\u6697\u4E2D\u5BFB\u627E\u65F6\u673A\u4E86\u3002\u5979\u5FAE\u53F9\u4E00\u58F0\uFF0C\u8D34\u5728\u7D2B\u73AB\u8033\u8FB9\uFF0C\u5C06\u51E4\u7BAB\u58F0\u52A8\u3001\u51E4\u5C55\u5F69\u7FFC\u3001\u51E4\u9E23\u671D\u9633\u3001\u51E4\u51F0\u4E8E\u98DE\u3001\u51E4\u6E05\u7D2B\u9E3E\u8FD9\u4E94\u5C42\u7684\u4FEE\u7EC3\u5FC3\u6CD5\u4ED4\u7EC6\u8BF4\u660E\u3002</p><p>\u7D2B\u73AB\u4E00\u4E00\u8BB0\u4E0B\uFF0C\u76F4\u5750\u4E86\u4E00\u4E2A\u65F6\u8FB0\u624D\u79BB\u5F00\u77F3\u5BA4\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u7D2B\u73AB\u60F3\u8D77\u8981\u7ED9\u7EAA\u7709\u59A9\u6D82\u836F\uFF0C\u8FD9\u65F6\u5DF2\u7ECF\u8FC7\u4E86\u4E00\u4E2A\u65F6\u8FB0\uFF0C\u4E0D\u77E5\u9053\u5E08\u59D0\u600E\u4E48\u6837\u4E86\u3002</p><p>\u82E5\u518D\u51FA\u5DEE\u9519\u2026\u2026\u5979\u6025\u6025\u8DD1\u5230\u5929\u5B57\u7678\u5BA4\uFF0C\u5374\u53D1\u73B0\u95E8\u8FD8\u5728\u9501\u7740\u3002</p><p>\u6155\u5BB9\u9F99\u6162\u60A0\u60A0\u8D70\u8FC7\u6765\uFF0C\u638F\u51FA\u94A5\u5319\u6253\u5F00\u77F3\u95E8\u3002</p><p>\u4E00\u63A8\u95E8\uFF0C\u4FBF\u542C\u5230\u7EAA\u7709\u59A9\u7C97\u91CD\u7684\u5598\u606F\uFF0C\u5979\u5BF9\u4E24\u4EBA\u5165\u5185\u604D\u82E5\u672A\u89C9\uFF0C\u53EA\u662F\u53CC\u76EE\u8D64\u7EA2\u5730\u62FC\u547D\u633A\u4E0B\u8179\uFF0C\u5728\u817F\u95F4\u7684\u88AB\u8925\u4E0A\u7AED\u529B\u78E8\u64E6\u3002\u8584\u88AB\u6D78\u6EE1\u6DEB\u6DB2\uFF0C\u6563\u53D1\u51FA\u6DE1\u6DE1\u7684\u5F02\u5473\u3002</p><p>\u7EAA\u7709\u59A9\u5A07\u8EAF\u96EA\u767D\uFF0C\u552F\u6709\u4E73\u5934\u548C\u4E0B\u9634\u7EA2\u5F97\u60CA\u4EBA\u3002\u5C0F\u5DE7\u7684\u4E73\u5934\u786C\u786C\u6311\u5728\u9F13\u80C0\u7684\u4E73\u623F\u4E0A\uFF0C\u4F38\u51FA\u6307\u5C16\u957F\u77ED\uFF0C\u968F\u7740\u5979\u7684\u633A\u52A8\u6C89\u7538\u7538\u7684\u4E73\u7403\u524D\u540E\u4E71\u6643\uFF0C\u4E73\u8089\u76F8\u51FB\u58F0\u4E0D\u65AD\u4F20\u6765\uFF1B\u5206\u5F00\u7684\u5927\u817F\u95F4\uFF0C\u80A5\u539A\u7684\u82B1\u74E3\u4EFF\u4F5B\u4E00\u56E2\u6D41\u52A8\u7684\u9C9C\u8840\uFF0C\u5728\u80A1\u95F4\u6EDA\u6765\u6EDA\u53BB\u3002</p><p>\u7D2B\u73AB\u6251\u8FC7\u53BB\u53EB\u9053\uFF1A\u300C\u5E08\u59D0\uFF01\u4F60\u600E\u4E48\u4E86\uFF01\u300D</p><p>\u7EAA\u7709\u59A9\u4EFF\u4F5B\u4E0D\u8BA4\u8BC6\u5979\u4E00\u822C\uFF0C\u8FF7\u4E71\u5730\u7741\u7740\u7F8E\u76EE\uFF0C\u7247\u523B\u540E\u7A81\u7136\u53EB\u9053\uFF1A\u300C\u5FEB\u6765\u2026\u2026\u5FEB\u6765\u64CD\u6211\u2026\u2026\u64CD\u6211\u2026\u2026\u300D</p><p>\u7D2B\u73AB\u6115\u7136\u770B\u7740\u6E29\u67D4\u6587\u96C5\u7684\u5E08\u59D0\uFF0C\u4F38\u624B\u6478\u6478\u5979\u7684\u989D\u5934\uFF0C\u53EA\u89C9\u5165\u624B\u51B0\u51C9\u3002\u5979\u626D\u5934\u6CE3\u58F0\u9053\uFF1A\u300C\u6211\u5E08\u59D0\u600E\u4E48\u4F1A\u8FD9\u4E2A\u6837\u5B50\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u7B11\u9053\uFF1A\u300C\u8FD8\u4E0D\u8D76\u7D27\u6577\u836F\uFF1F\u300D</p><p>\u7D2B\u73AB\u9192\u609F\u8FC7\u6765\uFF0C\u4EE5\u4E3A\u662F\u81EA\u5DF1\u8BEF\u4E86\u6D82\u836F\u7684\u7F18\u6545\uFF0C\u8FDE\u5FD9\u624B\u5FD9\u811A\u4E71\u5730\u62FF\u6765\u836F\u74F6\uFF0C\u5C06\u8CF8\u4F59\u7684\u836F\u818F\u5168\u90E8\u62B9\u5728\u5E08\u59D0\u4E0B\u4F53\u3002</p><p>\u7EAA\u7709\u59A9\u6D51\u8EAB\u51B0\u51C9\uFF0C\u79D8\u5904\u5374\u70ED\u5F97\u70EB\u624B\uFF0C\u6E05\u51C9\u7684\u836F\u818F\u62B9\u5728\u5AE9\u8089\u4E0A\uFF0C\u5979\u987F\u65F6\u5A07\u8EAF\u8FDE\u98A4\uFF0C\u53E3\u9F3B\u4E2D\u53D1\u51FA\u65AD\u65AD\u7EED\u7EED\u5730\u547B\u541F\u3002\u7D27\u7F1A\u7684\u56DB\u80A2\u626D\u6765\u626D\u53BB\u4E0D\u4F4F\u62E7\u52A8\u3002</p><p>\u5145\u8840\u7684\u82B1\u74E3\u6324\u6210\u4E00\u56E2\uFF0C\u7EB5\u7136\u662F\u4E24\u817F\u5927\u5F20\uFF0C\u4E5F\u770B\u4E0D\u5230\u91CC\u9762\u7684\u60C5\u666F\u3002\u4F46\u90A3\u7C92\u539F\u672C\u7EC6\u5C0F\u5982\u8C46\u7684\u82B1\u8482\u5374\u4ECE\u8089\u7F1D\u4E2D\u52C3\u7136\u4F38\u51FA\uFF0C\u786C\u786C\u633A\u7ACB\u5728\u6E7F\u6DCB\u6DCB\u5AE9\u8089\u95F4\u3002</p><p>\u78A7\u7EFF\u7684\u836F\u818F\u6D82\u4E0A\u706B\u70ED\u7684\u5AE9\u8089\u4E0A\uFF0C\u7ACB\u523B\u5316\u4E3A\u4E4C\u6709\uFF0C\u7EA2\u80BF\u82B1\u74E3\u53CD\u800C\u66F4\u6108\u52A0\u9C9C\u4EAE\u3002</p><p>\u5F53\u7D2B\u73AB\u629A\u5230\u82B1\u8482\u65F6\uFF0C\u7EAA\u7709\u59A9\u7A81\u7136\u53D1\u51FA\u4E00\u58F0\u6FC0\u70C8\u7684\u53EB\u558A\uFF0C\u7389\u817F\u731B\u7136\u633A\u76F4\uFF0C\u82B1\u74E3\u4E00\u9635\u4E71\u98A4\uFF0C\u4ECE\u80BF\u6210\u4E00\u6761\u7F1D\u7684\u7EA2\u8089\u95F4\u55B7\u51FA\u4E00\u80A1\u767D\u8272\u7684\u9634\u7CBE\u3002\u9634\u7CBE\u6DCC\u5C3D\u4E4B\u540E\uFF0C\u5979\u50CF\u662F\u8017\u5149\u4E86\u5168\u8EAB\u7684\u529B\u6C14\uFF0C\u4E24\u817F\u8F6F\u8F6F\u6389\u5728\u5E8A\u4E0A\uFF0C\u811A\u8E1D\u5904\u7684\u76AE\u80A4\u88AB\u7EF3\u7D22\u78E8\u7834\uFF0C\u9C9C\u8840\u6DCB\u6F13\u3002</p>',59);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
