import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_46" tabindex="-1"><a class="header-anchor" href="#_46" aria-hidden="true">#</a> 46</h1><p>\u7D2B\u73AB\u7D27\u5F20\u5730\u76EF\u7740\u5E08\u59D0\uFF0C\u53EA\u89C1\u7EAA\u7709\u59A9\u7C89\u5AE9\u7684\u7389\u4F53\u6E10\u6E10\u53D1\u7EA2\uFF0C\u5927\u9897\u5927\u9897\u7684\u6C57\u73E0\u4ECE\u808C\u80A4\u4E2D\u6E17\u51FA\uFF0C\u6C47\u6210\u4E00\u7247\u3002</p><p>\u968F\u7740\u4F53\u6E29\u5347\u9AD8\uFF0C\u521A\u521A\u5E73\u9759\u4E0B\u6765\u7684\u7EAA\u7709\u59A9\u53C8\u6323\u624E\u8D77\u6765\uFF0C\u5979\u5A07\u8EAF\u7D27\u7EF7\uFF0C\u624B\u811A\u4F3C\u4E4E\u50CF\u8981\u626F\u65AD\u7EF3\u7D22\u822C\u6B7B\u6B7B\u62C9\u7D27\uFF0C\u7C89\u9888\u524D\u4EF0\uFF0C\u5C0F\u5634\u98A4\u6296\uFF0C\u9ED1\u767D\u5206\u660E\u7684\u7F8E\u76EE\u5E03\u6EE1\u8840\u4E1D\uFF0C\u76F4\u76F4\u76EF\u7740\u80A1\u95F4\u9F13\u80C0\u7684\u8089\u82B1\uFF0C\u628A\u5168\u8EAB\u7684\u529B\u6C14\u90FD\u96C6\u4E2D\u5728\u6414\u75D2\u96BE\u8010\u7684\u4E0B\u8179\u3002</p><p>\u6155\u5BB9\u9F99\u62B1\u80A9\u7ACB\u5728\u5E8A\u4FA7\uFF0C\u4E00\u5B57\u4E00\u987F\u5730\u8BF4\uFF1A\u300C\u4F60\u3001\u662F\u3001\u4E2A\u3001\u4E0B\u3001\u8D31\u3001\u7684\u3001\u6DEB\u3001\u5974\uFF01\u300D</p><p>\u7EAA\u7709\u59A9\u542C\u5230\u7537\u4EBA\u7684\u58F0\u97F3\uFF0C\u7ACB\u523B\u6602\u8D77\u5934\uFF0C\u5931\u795E\u5730\u5598\u606F\u7740\u8BF4\u9053\uFF1A\u300C\u5974\u5A62\u662F\u8D31\u5974\uFF0C\u662F\u8D31\u5974\u2026\u2026\u6C42\u4E3B\u5B50\u64CD\u5974\u5A62\u2026\u2026\u72E0\u72E0\u5730\u2026\u2026\u64CD\u5974\u5A62\u2026\u2026\u300D</p><p>\u7D2B\u73AB\u5C16\u53EB\u4E00\u58F0\uFF0C\u8DCC\u8DCC\u649E\u649E\u9000\u5230\u95E8\u8FB9\uFF0C\u80F8\u53E3\u4E0D\u4F4F\u8D77\u4F0F\u3002\u7247\u523B\u540E\u5979\u62D4\u817F\u8DD1\u5230\u6C34\u623F\uFF0C\u7AEF\u6765\u4E00\u76C6\u6E05\u6C34\uFF0C\u62FF\u8D77\u6BDB\u5DFE\u53D1\u75AF\u4F3C\u7684\u62FC\u547D\u64E6\u6D17\u7EAA\u7709\u59A9\u7684\u4E0B\u4F53\u3002\u5979\u7EC8\u4E8E\u660E\u767D\u8FC7\u6765\uFF0C\u836F\u91CC\u542B\u6709\u4EC0\u4E48\u6837\u7684\u6210\u4EFD\uFF0C\u800C\u8FD9\u4E9B\u836F\uFF0C\u90FD\u662F\u81EA\u5DF1\u4E00\u70B9\u70B9\u7ED9\u5E08\u59D0\u6D82\u4E0A\u7684\u3002</p><p>\u6E7F\u70ED\u7684\u6BDB\u5DFE\u5212\u8FC7\u79D8\u5904\uFF0C\u7EAA\u7709\u59A9\u7ACB\u523B\u53D1\u51FA\u67D4\u5A9A\u5165\u9AA8\u7684\u547B\u541F\u58F0\uFF0C\u5C0F\u8179\u633A\u52A8\u7740\u8FFD\u9010\u7D2B\u73AB\u7684\u624B\u6307\u3002</p><p>\u6D01\u767D\u7684\u6BDB\u5DFE\u4E0D\u591A\u65F6\u4FBF\u6CBE\u6EE1\u9ECF\u7A20\u7684\u6DEB\u6DB2\uFF0C\u8D8A\u64E6\u8D8A\u591A\u3002\u7D2B\u73AB\u6E10\u6E10\u6162\u4E86\u4E0B\u6765\uFF0C\u6123\u6123\u95EE\u9053\uFF1A\u300C\u836F\u6548\u4EC0\u4E48\u65F6\u5019\u8FC7\u53BB\uFF1F\u300D</p><p>\u300C\u8FDE\u62B9\u56DB\u6B21\uFF0C\u836F\u6548\u6DF1\u5165\u9AA8\u9AD3\uFF0C\u563F\u563F\u2026\u2026\u7EAA\u5974\u795E\u667A\u8FD8\u662F\u6E05\u695A\u7684\uFF0C\u53EA\u4E0D\u8FC7\u6B20\u64CD\u7F62\u4E86\u3002\u300D</p><p>\u7D2B\u73AB\u5634\u5507\u54AC\u5F97\u51FA\u8840\uFF0C\u5979\u6162\u6162\u89E3\u5F00\u5E08\u59D0\u624B\u811A\u6346\u7F1A\u7684\u7EF3\u7D22\uFF0C\u5FC3\u91CC\u6068\u6B7B\u4E86\u81EA\u5DF1\u7684\u5E7C\u7A1A\u3001\u65E0\u77E5\u3001\u611A\u8822\uFF01</p><p>\u7EAA\u7709\u59A9\u624B\u4E0A\u4E00\u677E\uFF0C\u7ACB\u523B\u628A\u624B\u6307\u4F38\u5230\u79D8\u5904\uFF0C\u7528\u529B\u63C9\u6413\u3002\u7B49\u7D2B\u73AB\u89E3\u5F00\u811A\u4E0A\u7684\u7EF3\u7D22\uFF0C\u5979\u4FBF\u6323\u624E\u7740\u8DEA\u5230\u6155\u5BB9\u9F99\u8EAB\u524D\uFF0C\u6025\u5207\u5730\u9694\u7740\u8863\u7269\u53BB\u4EB2\u543B\u90A3\u6839\u8089\u68D2\u3002</p><p>\u7D2B\u73AB\u5FC3\u5934\u6EF4\u8840\uFF0C\u4E0D\u5FCD\u770B\u5E08\u59D0\u6DEB\u8D31\u7684\u6A21\u6837\uFF0C\u5E26\u7740\u6EE1\u8154\u7684\u6068\u610F\u548C\u81EA\u8D23\uFF0C\u79BB\u5F00\u77F3\u5BA4\u3002</p><p>\u811A\u6B65\u58F0\u54CD\uFF0C\u6155\u5BB9\u9F99\u4E5F\u8DDF\u4E86\u51FA\u6765\u3002\u7D2B\u73AB\u731B\u7136\u65CB\u8EAB\uFF0C\u54AC\u7259\u5207\u9F7F\u5730\u8BF4\uFF1A\u300C\u4E3A\u4EC0\u4E48\u9A97\u6211\uFF1F\u300D</p><p>\u6155\u5BB9\u9F99\u6DE1\u6DE1\u9053\uFF1A\u300C\u8C01\u9A97\u4F60\u4E86\uFF1F\u4F60\u8981\u7ED9\u7EAA\u5A4A\u5B50\u6CBB\u4F24\uFF0C\u73B0\u5728\u4E0D\u662F\u6CBB\u597D\u4E86\u5417\uFF1F\u5982\u679C\u4E0D\u662F\u7B2C\u4E8C\u6B21\u62B9\u836F\u7684\u65F6\u95F4\u4E0D\u5BF9\uFF0C\u5979\u7684\u8D31\u5C44\u4E5F\u4E0D\u4F1A\u80BF\u8FD9\u4E48\u5927\u3002\u81F3\u4E8E\u5979\u53D8\u6210\u8FD9\u6837\u5B50\uFF0C\u4E00\u534A\u662F\u56E0\u4E3A\u8FD9\u836F\u7684\u526F\u4F5C\u7528\uFF0C\u53E6\u4E00\u534A\u662F\u56E0\u4E3A\u5979\u5929\u751F\u5C31\u662F\u4E2A\u6DEB\u8361\u7684\u8D31\u4EBA\uFF01\u300D</p><p>\u7D2B\u73AB\u5F3A\u5FCD\u7740\u6CA1\u6709\u6D41\u4E0B\u6CEA\u6765\u3002</p><p>\u5931\u795E\u7684\u5C11\u5973\u722C\u5230\u6155\u5BB9\u9F99\u8EAB\u540E\uFF0C\u4E5E\u6C42\u9053\uFF1A\u300C\u64CD\u5974\u5A62\uFF0C\u4E3B\u5B50\u6765\u64CD\u5974\u5A62\u7684\u8D31\u5C44\u2026\u300D</p><p>\u6700\u7F9E\u6DA9\u6E29\u5A49\u7684\u7EAA\u5E08\u59D0\u7ADF\u4F1A\u8BF4\u51FA\u8FD9\u79CD\u65E0\u803B\u4E0B\u8D31\u7684\u8BDD\u8BED\uFF0C\u7D2B\u73AB\u73E0\u6CEA\u76C8\u7136\uFF0C\u5C0F\u5634\u6241\u4E86\u8D77\u6765\u3002</p><p>\u6155\u5BB9\u9F99\u5BF9\u7EAA\u7709\u59A9\u7684\u54C0\u6C42\u65E0\u52A8\u4E8E\u8877\uFF0C\u53EA\u51B7\u51B7\u770B\u7740\u7D2B\u73AB\u3002</p><p>\u7D2B\u73AB\u54BD\u4E0B\u773C\u6CEA\uFF0C\u987F\u8DB3\u9053\uFF1A\u300C\u4F60\u8FD8\u4E0D\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u7231\u715E\u4E86\u59B9\u59B9\u8FD9\u79CD\u542B\u7F9E\u5E26\u6012\u7684\u5A07\u7F8E\u795E\u60C5\uFF0C\u95FB\u8A00\u52B2\u7709\u4E00\u626C\uFF0C\u300C\u600E\u4E48\uFF1F\u300D</p><p>\u7D2B\u73AB\u7EA2\u5507\u98A4\u6296\uFF0C\u65E0\u8BBA\u5982\u4F55\u4E5F\u5F20\u4E0D\u5F00\u53E3\u3002\u542C\u7740\u5E08\u59D0\u7684\u547B\u541F\u8D8A\u6765\u8D8A\u51C4\u5389\uFF0C\u5979\u4E00\u54AC\u7259\uFF0C\u5927\u58F0\u9053\uFF1A\u300C\u53BB\u64CD\u5979\uFF01\u300D\u8BF4\u7F62\u626D\u5934\u5C31\u8D70\u3002</p><p>\u6155\u5BB9\u9F99\u7684\u8C03\u7B11\u58F0\u4ECE\u8EAB\u540E\u4F20\u6765\uFF0C\u300C\u4F60\u5F97\u6C42\u6211\u2014\u2014\u300D\u7D2B\u73AB\u67D4\u8EAF\u4E00\u50F5\uFF0C\u59D4\u5C48\u8F9B\u9178\u96BE\u53D7\u6816\u60F6\uFF0C\u5404\u79CD\u6ECB\u5473\u4E00\u53E4\u8111\u6D8C\u4E0A\u5FC3\u5934\u3002\u5979\u76EF\u7740\u6155\u5BB9\u9F99\u7684\u773C\u775B\uFF0C\u7528\u6E05\u6670\u7684\u58F0\u97F3\u6162\u6162\u8BF4\uFF1A\u300C\u6211\u6C42\u4F60\u53BB\u64CD\u5979\u3002\u7EAA\u5974\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u54C8\u54C8\u4E00\u7B11\uFF0C\u5C31\u5728\u752C\u9053\u4E2D\u6258\u8D77\u7EAA\u7709\u59A9\u7684\u5706\u81C0\uFF0C\u633A\u8EAB\u523A\u5165\u80A5\u5AE9\u7684\u79D8\u5904\uFF0C\u4E00\u8FB9\u62BD\u9001\u4E00\u8FB9\u8D5E\u9053\uFF1A\u300C\u7EAA\u5A4A\u5B50\u7684\u5C44\u80BF\u6210\u8FD9\u6837\uFF0C\u5706\u9F13\u9F13\u3001\u80A5\u561F\u561F\uFF0C\u64CD\u8D77\u6765\u5B9E\u5728\u662F\u8212\u670D\uFF01\u300D</p><p>\u9965\u6E34\u96BE\u8010\u7684\u7EAA\u7709\u59A9\u6D51\u8EAB\u6218\u6817\uFF0C\u6D6A\u53EB\u4E0D\u7EDD\u3002</p><p>\u7D2B\u73AB\u5E73\u9759\u5730\u770B\u4E86\u7247\u523B\uFF0C\u6162\u6162\u56DE\u5230\u8427\u4F5B\u5974\u7684\u5367\u5BA4\u3002\u4E00\u5173\u4E0A\u95E8\uFF0C\u5979\u7ACB\u523B\u6251\u5230\u6BCD\u4EB2\u6000\u4E2D\u653E\u58F0\u75DB\u54ED\u8D77\u6765\u3002</p><p>\u8427\u4F5B\u5974\u4E0D\u77E5\u539F\u59D4\uFF0C\u67D4\u58F0\u5B89\u6170\u534A\u5929\uFF0C\u8BAF\u95EE\u5973\u513F\u4E3A\u4EC0\u4E48\u54ED\u5F97\u8FD9\u4E48\u4F24\u5FC3\u3002\u7D2B\u73AB\u53EA\u662F\u4E00\u4E2A\u52B2\u513F\u7684\u557C\u54ED\uFF0C\u600E\u4E48\u4E5F\u4E0D\u613F\u8BF4\u51FA\u7EAA\u5E08\u59D0\u7684\u906D\u9047\u3002\u54ED\u4E86\u534A\u664C\uFF0C\u4E00\u591C\u672A\u7720\u7684\u5C11\u5973\u75B2\u60EB\u4E0D\u582A\uFF0C\u542B\u7740\u773C\u6CEA\u6C89\u6C89\u5165\u7761\u3002</p><p>\u767E\u82B1\u89C2\u97F3\u770B\u7740\u7D2B\u73AB\u8138\u4E0A\u7684\u6EDA\u843D\u6CEA\u73E0\uFF0C\u5FC3\u91CC\u53C8\u9178\u53C8\u6DA9\uFF0C\u4F38\u624B\u60F3\u66FF\u5973\u513F\u8F7B\u8F7B\u64E6\u53BB\u3002</p><p>\u8EAB\u5B50\u4E00\u52A8\uFF0C\u624D\u60F3\u8D77\u81EA\u5DF1\u624B\u811A\u7684\u7B4B\u8171\u5DF2\u88AB\u4EB2\u751F\u513F\u5B50\u6B8B\u5FCD\u5730\u62BD\u53BB\u3002\u67D4\u80A0\u767E\u8F6C\u95F4\uFF0C\u7F8E\u8273\u7684\u8138\u5E9E\u4E5F\u662F\u4E00\u7247\u6CEA\u5149\u3002\u8FD9\u6837\u7684\u65E5\u5B50\uFF0C\u7A76\u7ADF\u4F55\u65F6\u624D\u662F\u5C3D\u5934\u2026\u2026</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u5750\u7A33\u5BAB\u4E3B\u4E4B\u4F4D\u540E\uFF0C\u6155\u5BB9\u9F99\u7740\u624B\u6539\u7EC4\u4E94\u884C\u95E8\uFF0C\u4EE5\u5F80\u661F\u6708\u6E56\u6700\u6709\u52BF\u529B\u7684\u5F53\u5C5E\u56DB\u9547\u795E\u5C06\uFF0C\u9E9F\u3001\u51E4\u3001\u9F9F\u3001\u9F99\u5404\u636E\u4E00\u65B9\uFF0C\u62F1\u536B\u795E\u5BAB\u3002\u4F46\u767E\u4F59\u5E74\u524D\u4E00\u573A\u5185\u4E71\uFF0C\u56DB\u9547\u51CB\u96F6\uFF0C\u53EA\u5269\u4E0B\u7A7A\u540D\uFF0C\u9634\u5BAB\u4E3B\u638C\u653F\u4E0D\u4E45\u4FBF\u5DF2\u6D88\u4EA1\u3002\u5982\u4ECA\u6559\u4E2D\u5B9E\u529B\u90FD\u96C6\u4E2D\u5728\u4E94\u884C\u95E8\u5185\uFF0C\u5C24\u5176\u662F\u5404\u5802\u4E0B\u5C5E\u7684\u5E2E\u4F1A\uFF0C\u5BF9\u4ED6\u8D77\u4E8B\u5927\u6709\u52A9\u76CA\u3002</p><p>\u6155\u5BB9\u9F99\u4E0E\u91D1\u5F00\u7532\u3001\u6C90\u58F0\u4F20\u5546\u8BAE\u8BB8\u4E45\uFF0C\u90FD\u75DB\u611F\u6559\u5185\u7F3A\u4E4F\u4EBA\u624D\u3002\u4E8E\u662F\u53CC\u7BA1\u9F50\u4E0B\uFF0C\u7531\u6C90\u58F0\u4F20\u4ECE\u6559\u5185\u9009\u62E9\u53EF\u9020\u4E4B\u6750\uFF0C\u7528\u5FC3\u8C03\u6559\uFF1B\u970D\u72C2\u7130\u548C\u5C60\u6000\u6C89\u62DB\u63FD\u6559\u5916\u9AD8\u624B\uFF0C\u5171\u6518\u5927\u4E1A\uFF1B\u91D1\u5F00\u7532\u5219\u8D1F\u8D23\u8BAD\u7EC3\u5E2E\u4F17\uFF0C\u5C06\u8FD9\u4E9B\u6C5F\u6E56\u8C6A\u58EB\u6539\u7EC4\u4E3A\u7CBE\u5175\u5F3A\u5C06\u3002\u81F3\u4E8E\u5A5A\u5E86\u5927\u5178\u7684\u5E03\u7F6E\uFF0C\u53EA\u7B97\u5C0F\u4E8B\u4E00\u6869\uFF0C\u9189\u7FC1\u4E4B\u610F\u4E0D\u5728\u4E8E\u9152\u3002</p><p>\u6155\u5BB9\u9F99\u7BE1\u4F4D\u800C\u7ACB\uFF0C\u5BF9\u4E94\u5802\u957F\u8001\u4E00\u76F4\u5FC3\u6000\u6212\u5907\uFF0C\u60F3\u65B9\u8BBE\u6CD5\u8981\u9664\u53BB\u8FD9\u4E9B\u5F02\u5DF1\u3002\u4ECA\u65E5\u5171\u5546\u5927\u4E8B\uFF0C\u5F7C\u6B64\u63A8\u5FC3\u7F6E\u8179\uFF0C\u4ED6\u5BF9\u91D1\u5F00\u7532\u7684\u96C4\u5FC3\u52C3\u52C3\u548C\u6C90\u58F0\u4F20\u6C89\u7A33\u591A\u8BC6\u5927\u611F\u75DB\u5FEB\uFF0C\u8D77\u8EAB\u8BDA\u6073\u5730\u62F1\u624B\u9053\uFF1A\u300C\u76F8\u8BC6\u591A\u5E74\uFF0C\u76F8\u4EA4\u6068\u665A\uFF0C\u4EE5\u5F80\u591A\u6709\u5931\u793C\uFF0C\u8BF7\u4E24\u4F4D\u52FF\u602A\u3002\u300D</p><p>\u91D1\u5F00\u7532\u5F00\u6000\u7B11\u9053\uFF1A\u300C\u6211\u4EE5\u4E3A\u81EA\u5DF1\u4F1A\u7EC8\u8EAB\u57CB\u6CA1\u8349\u83BD\uFF0C\u53EA\u80FD\u505A\u4E2A\u608D\u532A\u3002\u5982\u4ECA\u80FD\u8F85\u4F50\u5BAB\u4E3B\uFF0C\u56FE\u8C0B\u5929\u4E0B\uFF0C\u7740\u5B9E\u75DB\u5FEB\uFF01\u300D</p><p>\u6C90\u58F0\u4F20\u5374\u9053\uFF1A\u300C\u5BAB\u4E3B\u6B32\u56FE\u5927\u4E8B\uFF0C\u4EE5\u5BAB\u4E2D\u8D22\u529B\uFF0C\u6050\u6015\u96BE\u4EE5\u652F\u5E94\u3002\u300D\u661F\u6708\u6E56\u4EE5\u5F80\u53EA\u56FE\u4FEE\u9053\u4FBF\u5229\uFF0C\u6240\u5C5E\u5E2E\u4F1A\u5927\u591A\u4F4D\u4E8E\u9053\u6559\u540D\u5C71\uFF0C\u6216\u662F\u51FA\u4EA7\u4E39\u7802\u3001\u94C5\u6C5E\u7B49\u70BC\u4E39\u836F\u6750\u4E4B\u5730\uFF0C\u4F9B\u5E94\u5BAB\u4E2D\u5F00\u652F\u81EA\u662F\u65E0\u5FE7\uFF0C\u4F46\u8981\u4F9B\u517B\u4E00\u652F\u519B\u961F\uFF0C\u5374\u662F\u4E0D\u6613\u3002</p><p>\u6155\u5BB9\u9F99\u659F\u914C\u7247\u523B\uFF0C\u5C06\u5B9D\u85CF\u5408\u76D8\u6258\u51FA\uFF0C\u300C\u5A5A\u793C\u4E4B\u540E\uFF0C\u8BF7\u6C90\u62A4\u6CD5\u5750\u9547\u5BAB\u4E2D\uFF0C\u6211\u4E0E\u91D1\u957F\u8001\u540C\u8D74\u9F99\u57CE\uFF0C\u8D77\u51FA\u5B9D\u85CF\u3002\u300D</p><p>\u6C90\u58F0\u4F20\u70B9\u70B9\u5934\uFF0C\u6DE1\u6DE1\u9053\uFF1A\u300C\u5B9D\u85CF\u53EA\u53EF\u4F9B\u4E00\u65F6\u4E4B\u9700\uFF0C\u8BF7\u5BAB\u4E3B\u4E09\u601D\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u62CD\u6848\u7B11\u9053\uFF1A\u300C\u6211\u660E\u767D\u4E86\u3002\u90A3\u4FBF\u8BA9\u970D\u957F\u8001\u6269\u5F20\u52BF\u529B\uFF0C\u628A\u901A\u5546\u5927\u9091\u7684\u5E2E\u4F1A\u4E00\u5E76\u7EB3\u5165\u6559\u4E2D\uFF01\u300D</p><p>\u6C90\u58F0\u4F20\u7834\u5929\u8352\u5730\u9732\u51FA\u4E00\u4E1D\u7B11\u610F\u3002</p><p>\u6155\u5BB9\u9F99\u795E\u91C7\u98DE\u626C\uFF0C\u957F\u7B11\u9053\uFF1A\u300C\u5F97\u4E24\u4F4D\u4E4B\u52A9\uFF0C\u6211\u6155\u5BB9\u9F99\u4F55\u6101\u5927\u4E8B\u4E0D\u6210\uFF01\u300D</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u6155\u5BB9\u7D2B\u73AB\u5374\u4E0D\u5173\u5FC3\u4ED6\u4EEC\u7684\u300C\u5927\u4E8B\u300D\uFF0C\u5BF9\u5979\u6765\u8BF4\uFF0C\u6BCD\u4EB2\u3001\u5E08\u7236\u3001\u5E08\u59D0\u624D\u662F\u5927\u4E8B\u3002</p><p>\u9192\u6765\u65F6\u5DF2\u662F\u508D\u665A\uFF0C\u7D2B\u73AB\u966A\u6BCD\u4EB2\u8BF4\u4E86\u51E0\u53E5\u8BDD\uFF0C\u5E2E\u5979\u6309\u6469\u4E86\u56DB\u80A2\uFF0C\u5306\u5306\u8D76\u5230\u7EAA\u5E08\u59D0\u7684\u623F\u95F4\u3002</p><p>\u7EAA\u7709\u59A9\u6D51\u8EAB\u71E5\u70ED\uFF0C\u4E00\u4E1D\u4E0D\u6302\u5730\u8EBA\u5728\u69BB\u4E0A\u62E8\u5F04\u6E7F\u6DCB\u6DCB\u7684\u79D8\u5904\u3002\u5F85\u53C8\u4E00\u6B21\u9AD8\u6F6E\u6765\u4E34\u540E\uFF0C\u5979\u624D\u865A\u5F31\u5730\u7741\u5F00\u773C\u775B\u3002\u79C0\u7F8E\u7684\u8138\u4E0A\u90A3\u79CD\u77DC\u6301\u4E4B\u8272\u5DF2\u7ECF\u8361\u7136\u65E0\u5B58\uFF0C\u7709\u68A2\u773C\u89D2\u6625\u60C5\u6D41\u9732\uFF0C\u6709\u4E00\u79CD\u51FA\u5947\u7684\u5996\u8273\u3002</p><p>\u7D2B\u73AB\u5E2E\u5E08\u59D0\u64E6\u51C0\u8EAB\u4E0A\u7684\u6C57\u6C34\u6DEB\u6DB2\uFF0C\u62AB\u4E0A\u8F7B\u7EB1\uFF0C\u52C9\u5F3A\u6324\u51FA\u4E00\u4E1D\u7B11\u5BB9\uFF0C\u300C\u597D\u4E9B\u4E86\u5417\uFF1F\u300D</p><p>\u8F6F\u5F31\u7684\u7EAA\u7709\u59A9\u6839\u672C\u65E0\u529B\u6297\u62D2\u8089\u4F53\u7684\u6B32\u671B\uFF0C\u5979\u8F6F\u8F6F\u4F9D\u5728\u6795\u4E0A\uFF0C\u7EC6\u767D\u7684\u624B\u6307\u7EDE\u5F04\u7740\u79C0\u53D1\uFF0C\u5446\u5446\u770B\u7740\u5BA4\u9876\uFF0C\u534A\u664C\u624D\u9053\uFF1A\u300C\u5C31\u8FD9\u6837\u5427\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u7684\u58F0\u97F3\u4ECE\u8EAB\u540E\u54CD\u8D77\uFF0C\u300C\u8FD9\u5C31\u5BF9\u4E86\u3002\u300D\u4ED6\u6258\u8D77\u7D2B\u73AB\u7684\u4E0B\u5DF4\uFF0C\u5728\u5979\u5507\u89D2\u6D45\u6D45\u4E00\u543B\uFF0C\u300C\u50CF\u5979\u4E00\u6837\u65E9\u4E9B\u8BA4\u547D\uFF0C\u80FD\u5C11\u5403\u5F88\u591A\u82E6\u5934\u2026\u2026\u300D</p><p>\u7D2B\u73AB\u5782\u4E0B\u773C\u775B\uFF0C\u67D4\u987A\u5730\u8F7B\u58F0\u9053\uFF1A\u300C\u77E5\u9053\u4E86\u3002\u54E5\u54E5\uFF0C\u6211\u60F3\u53BB\u7ED9\u98CE\u5974\u9001\u996D\u2026\u2026\u300D</p><p>\u300C\u8BA9\u83BA\u5974\u3001\u9E42\u5974\uFF0C\u6216\u8005\u7EAA\u5974\u53BB\u90FD\u884C\u3002\u4F60\u662F\u4E3B\u5B50\uFF0C\u4F55\u5FC5\u4EB2\u81EA\u52A8\u624B\uFF1F\u300D</p><p>\u7D2B\u73AB\u561F\u8D77\u5C0F\u5634\uFF0C\u4E00\u8138\u5730\u4E0D\u60C5\u613F\u3002</p><p>\u6155\u5BB9\u9F99\u53EA\u597D\u8BA9\u6B65\uFF0C\u300C\u597D\uFF0C\u597D\uFF0C\u54B1\u4EEC\u4E00\u8D77\u53BB\u3002\u300D</p><p>\u7FFB\u5F00\u77F3\u95E8\u4E0B\u7684\u6321\u677F\uFF0C\u7D2B\u73AB\u5C0F\u5FC3\u5730\u628A\u98DF\u76D2\u585E\u5230\u5BA4\u5185\uFF0C\u5C0F\u58F0\u5524\u9053\uFF1A\u300C\u98CE\u2026\u2026\u665A\u534E\u2026\u2026\u300D</p><p>\u5BA4\u5185\u6CA1\u6709\u56DE\u7B54\uFF0C\u53EA\u542C\u89C1\u4E00\u9635\u8F7B\u5FAE\u5F02\u54CD\uFF0C\u50CF\u7AF9\u7AFF\u5728\u6CE5\u6CDE\u4E2D\u62BD\u63D2\u822C\uFF0C\u5E26\u7740\u6E7F\u6DCB\u6DCB\u7684\u6C34\u58F0\u3002</p><p>\u7D2B\u73AB\u7740\u6025\u8D77\u6765\uFF0C\u5927\u58F0\u53EB\u9053\uFF1A\u300C\u98CE\u5E08\u59D0\u3001\u98CE\u5E08\u59D0\uFF01\u300D</p><p>\u558A\u4E86\u4E00\u9635\uFF0C\u9ED1\u6697\u4E2D\u4F38\u51FA\u4E00\u53EA\u96EA\u767D\u7684\u7389\u624B\uFF0C\u5C06\u98DF\u76D2\u62D6\u5230\u9634\u5F71\u4E2D\uFF0C\u63A5\u7740\u54CD\u8D77\u541E\u54BD\u7684\u58F0\u97F3\u3002</p><p>\u7D2B\u73AB\u677E\u4E86\u53E3\u6C14\uFF0C\u4F46\u5FC3\u4E0B\u7591\u56E2\u672A\u89E3\uFF0C\u4E8E\u662F\u8BF4\u9053\uFF1A\u300C\u98CE\u5E08\u59D0\uFF0C\u662F\u6211\uFF0C\u7D2B\u73AB\u3002\u4F60\u8BF4\u8BDD\u554A\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u5FC3\u91CC\u51B7\u7B11\u9053\uFF1A\u300C\u4F60\u5373\u4F7F\u558A\u7834\u5589\u5499\uFF0C\u670D\u4E86\u54D1\u836F\u7684\u98CE\u5A4A\u5B50\u4E5F\u4E0D\u4F1A\u518D\u8BF4\u8BDD\u4E86\u3002\u300D</p><p>\u7D2B\u73AB\u8D8A\u558A\u8D8A\u6025\uFF0C\u76F4\u8170\u62C9\u4F4F\u6155\u5BB9\u9F99\u7684\u624B\u81C2\u8BF4\u9053\uFF1A\u300C\u8BA9\u6211\u8FDB\u53BB\u770B\u4E00\u773C\uFF0C\u597D\u4E0D\u597D\uFF1F\u300D</p><p>\u6155\u5BB9\u9F99\u5FAE\u7B11\u7740\u6447\u4E86\u6447\u5934\uFF0C\u300C\u660E\u65E5\u665A\u95F4\u518D\u8BF4\u3002\u5230\u65F6\u4E5F\u8BE5\u7ED9\u98CE\u5A4A\u5B50\u62B9\u836F\u4E86\u3002\u300D</p><p>\u7D2B\u73AB\u79C0\u7738\u4E00\u95EA\uFF0C\u5BD2\u58F0\u9053\uFF1A\u300C\u90A3\u836F\u91CC\u6709\u4EC0\u4E48\uFF1F\u300D</p><p>\u300C\u6CA1\u4EC0\u4E48\u3002\u53EA\u662F\u72D7\u95FB\u5230\u4E86\u4E0D\u4F1A\u54AC\u5979\u800C\u5DF2\u3002\u300D</p><p>\u7D2B\u73AB\u4E0D\u4F1A\u518D\u76F8\u4FE1\u4ED6\u7684\u8BDD\uFF0C\u54AC\u7259\u9053\uFF1A\u300C\u5982\u679C\u5979\u6709\u4EC0\u4E48\u610F\u5916\uFF0C\u6211\u2026\u2026\u6211\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u89C1\u5979\u6C14\u6025\u8D25\u574F\uFF0C\u534A\u5929\u4E5F\u6CA1\u60F3\u51FA\u5A01\u80C1\u7684\u8BDD\uFF0C\u4E0D\u7531\u5931\u7B11\u9053\uFF1A\u300C\u522B\u62C5\u5FC3\uFF0C\u5979\u80AF\u5B9A\u6B7B\u4E0D\u4E86\u3002\u300D</p><p>\u51FA\u4E8E\u9965\u997F\u7684\u672C\u80FD\uFF0C\u98CE\u665A\u534E\u4F0F\u5728\u5730\u4E0A\uFF0C\u660F\u660F\u6C89\u6C89\u5730\u5403\u4E0B\u98DF\u7269\u3002\u795E\u5FD7\u7565\u5FAE\u6E05\u9192\u4E4B\u540E\uFF0C\u5979\u7ACB\u523B\u6323\u52A8\u8D77\u6765\u3002\u6B63\u5728\u62BD\u9001\u7684\u5DE8\u72AC\u4F4E\u543C\u4E00\u58F0\uFF0C\u8165\u81ED\u7684\u553E\u6DB2\u55B7\u5728\u8138\u4E0A\uFF0C\u98CE\u665A\u534E\u8179\u5185\u4E00\u9635\u7FFB\u817E\u3002\u5979\u5FCD\u4F4F\u6076\u5FC3\u628A\u552F\u4E00\u4E00\u53EA\u624B\u52C9\u5F3A\u4F38\u51FA\u81C0\u540E\uFF0C\u6293\u4F4F\u72F0\u72DE\u7684\u517D\u6839\u5411\u5916\u7528\u529B\u4E00\u626F\u3002\u81A8\u80C0\u7684\u8089\u7624\u5361\u7D27\u8089\u58C1\uFF0C\u8089\u7A74\u987F\u65F6\u5267\u75DB\u8FDE\u8FDE\u3002</p><p>\u98CE\u665A\u534E\u54AC\u7D27\u7259\u5173\uFF0C\u5B81\u80AF\u628A\u4E0B\u4F53\u6495\u788E\u4E5F\u4E0D\u613F\u8FD9\u6837\u4EFB\u91CE\u517D\u5978\u6DEB\u3002\u633A\u52A8\u7684\u5DE8\u72AC\u5403\u75DB\uFF0C\u53D1\u8D77\u6012\u6765\uFF0C\u62AC\u722A\u4E00\u6251\uFF0C\u98CE\u665A\u534E\u9999\u80A9\u7ACB\u523B\u9C9C\u8840\u6DCB\u6F13\uFF0C\u5979\u95F7\u54FC\u4E00\u58F0\uFF0C\u660F\u4E86\u8FC7\u53BB\u3002</p><p>\u65E0\u8FB9\u7684\u9ED1\u6697\u4E2D\uFF0C\u518D\u6CA1\u6709\u65F6\u95F4\u548C\u7A7A\u95F4\u7684\u6982\u5FF5\u3002\u98CE\u665A\u534E\u65F6\u660F\u65F6\u9192\uFF0C\u5728\u5978\u6DEB\u4E2D\u660F\u8FF7\uFF0C\u53C8\u5728\u5978\u6DEB\u4E2D\u6E05\u9192\u3002\u6D41\u971C\u5251\u575A\u6BC5\u7684\u795E\u5FD7\uFF0C\u5728\u91CE\u517D\u65E0\u4F11\u6B62\u5730\u5978\u6DEB\u548C\u836F\u529B\u5939\u653B\u4E0B\uFF0C\u4E00\u70B9\u70B9\u5D29\u6E83\u3002</p>',65);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
