import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_47" tabindex="-1"><a class="header-anchor" href="#_47" aria-hidden="true">#</a> 47</h1><p>\u6155\u5BB9\u9F99\u5BF9\u96EA\u5CF0\u795E\u5C3C\u4E0D\u6562\u6389\u4EE5\u8F7B\u5FC3\uFF0C\u5316\u771F\u6563\u7684\u836F\u6548\u867D\u7136\u53EF\u4EE5\u652F\u6491\u4E09\u5929\uFF0C\u4F46\u4ED6\u6BCF\u5929\u90FD\u6765\u5DE1\u89C6\u4E00\u756A\uFF0C\u7ED9\u5979\u670D\u836F\u3002\u96EA\u5CF0\u795E\u5C3C\u6BEB\u4E0D\u6C14\u9981\uFF0C\u65F6\u523B\u6253\u5750\u7EC3\u529F\uFF0C\u5BF9\u4ED6\u8BF8\u822C\u8C03\u620F\u65E0\u52A8\u4E8E\u8877\u3002</p><p>\u6155\u5BB9\u9F99\u5567\u5567\u79F0\u5947\uFF0C\u8FD9\u79CD\u767D\u8D39\u5DE5\u592B\u5730\u52E4\u4FEE\u5B9E\u5728\u4E0D\u53EF\u601D\u8BAE\u3002\u4ED6\u629A\u6478\u7740\u795E\u5C3C\u7684\u5149\u5934\uFF0C\u7B11\u9053\uFF1A\u300C\u5E08\u592A\u8FD9\u5927\u5934\u53C8\u5706\u53C8\u4EAE\uFF0C\u8DDF\u5728\u4E0B\u7684\u5C0F\u5934\u5B9E\u6709\u4E00\u6BD4\u2026\u2026\u300D</p><p>\u96EA\u5CF0\u795E\u5C3C\u4E0D\u52A8\u58F0\u8272\uFF0C\u95ED\u76EE\u51DD\u795E\u3002</p><p>\u6155\u5BB9\u9F99\u5E72\u8106\u638F\u51FA\u8089\u68D2\uFF0C\u7D2B\u9ED1\u7684\u9F9F\u5934\u5728\u96EA\u5CF0\u795E\u5C3C\u7684\u7389\u8138\u4E0A\u786C\u6886\u6886\u6233\u5F04\u7740\uFF0C\u6DEB\u7B11\u9053\uFF1A\u300C\u795E\u5C3C\u4FEE\u884C\u591A\u5E74\uFF0C\u53EF\u66FE\u89C1\u8FC7\u8FD9\u7B49\u5947\u7269\uFF1F\u300D</p><p>\u72F0\u72DE\u7684\u9F9F\u5934\u8DB3\u6709\u5A74\u513F\u62F3\u5934\u5927\u5C0F\uFF0C\u5149\u4EAE\u575A\u786C\uFF0C\u72B9\u5982\u7CBE\u94A2\u6253\u94F8\u3002\u5F53\u9F9F\u5934\u4F38\u5230\u9F3B\u4E0B\uFF0C\u6311\u5F04\u7EA2\u5507\u65F6\uFF0C\u795E\u5C3C\u7EC8\u4E8E\u5FCD\u4E0D\u4F4F\u4FA7\u8138\u907F\u5F00\uFF0C\u55D4\u76EE\u5389\u559D\u9053\uFF1A\u300C\u4E0D\u8FC7\u4E00\u526F\u81ED\u76AE\u56CA\uFF01\u4F60\u5982\u6B64\u4F5C\u4E3A\uFF0C\u5FC5\u7136\u6CA6\u4E3A\u755C\u7272\u9053\uFF0C\u6C38\u4E16\u4E0D\u5F97\u8D85\u751F\uFF01\u300D</p><p>\u6155\u5BB9\u9F99\u8BA5\u7B11\u9053\uFF1A\u300C\u4F5B\u95E8\u8F6E\u56DE\u4E4B\u8BF4\uFF0C\u53EA\u80FD\u9A97\u9A97\u4E09\u5C81\u5C0F\u513F\u2014\u2014\u5C31\u7B97\u6CA6\u4E3A\u755C\u7272\u53C8\u6709\u4F55\u59A8\uFF1F\u4F60\u90A3\u5927\u5F92\u5F1F\uFF0C\u8FD8\u4E0D\u662F\u8BA9\u4E00\u5934\u91CE\u732A\u7834\u4E86\u8EAB\u5B50\uFF0C\u8FD9\u4F1A\u513F\u8FD8\u2026\u2026\u300D\u4ED6\u987F\u4E86\u4E00\u4E0B\uFF0C\u6539\u53E3\u9053\uFF1A\u300C\u8BA9\u6211\u6765\u770B\u770B\u5E08\u592A\u7684\u81ED\u76AE\u56CA\u2026\u2026\u300D\u8BF4\u7740\u89E3\u5F00\u795E\u5C3C\u7684\u8863\u5E26\u3002</p><p>\u96EA\u5CF0\u795E\u5C3C\u7389\u5BB9\u65E0\u6CE2\uFF0C\u51B7\u51B0\u51B0\u4EFB\u4ED6\u65BD\u4E3A\u3002</p><p>\u8863\u5E26\u4E2D\u5206\uFF0C\u50E7\u888D\u655E\u5F00\u5904\u9732\u51FA\u96EA\u767D\u7684\u4E2D\u8863\u3002\u5E03\u6599\u867D\u7136\u7C97\u7CD9\uFF0C\u4F46\u4E00\u5C18\u4E0D\u67D3\uFF0C\u81EA\u6709\u4E00\u79CD\u6D17\u51C0\u7E41\u534E\u7684\u98D8\u9038\u4E4B\u6C14\u3002\u6155\u5BB9\u9F99\u89C1\u795E\u5C3C\u6BEB\u4E0D\u6323\u624E\uFF0C\u4E0D\u5BA2\u6C14\u5730\u628A\u5979\u63A8\u5012\u5728\u69BB\u4E0A\uFF0C\u5148\u6258\u8D77\u811A\u8E1D\uFF0C\u4E00\u628A\u62FD\u6389\u5E03\u5C65\uFF0C\u7136\u540E\u626F\u5F00\u5305\u88F9\u7EA4\u8DB3\u7684\u767D\u5E03\uFF0C\u6414\u5F04\u7740\u795E\u5C3C\u7684\u811A\u5E95\u7B11\u9053\uFF1A\u300C\u5012\u4E5F\u4E0D\u81ED\u561B\u3002\u300D</p><p>\u867D\u7136\u5947\u75D2\u653B\u5FC3\uFF0C\u4F46\u795E\u5C3C\u6C14\u606F\u60A0\u957F\uFF0C\u6CA1\u6709\u4E1D\u6BEB\u6563\u4E71\u3002</p><p>\u6155\u5BB9\u9F99\u64A9\u8D77\u4E2D\u8863\uFF0C\u62C9\u8D77\u4EB5\u88E4\u6D45\u9EC4\u7684\u4E1D\u7EE6\uFF0C\u7B11\u9053\uFF1A\u300C\u5E08\u592A\u7684\u8170\u771F\u7EC6\u2026\u2026\u5475\uFF0C\u7ADF\u7136\u6253\u4E86\u4E2A\u76F8\u601D\u7ED3\u2026\u2026\u4F5B\u7956\u4FDD\u4F51\uFF0C\u54B1\u4EEC\u5E08\u592A\u770B\u4E0A\u53BB\u4E00\u8138\u6B63\u5BB9\uFF0C\u5343\u4E07\u522B\u662F\u4E2A\u88AB\u4EBA\u73A9\u70C2\u7684\u8D31\u8D27\u3002\u300D</p><p>\u6B66\u6797\u7B2C\u4E00\u9AD8\u624B\u6A2A\u9648\u69BB\u4E0A\u4EFB\u5DF1\u4E3A\u6240\u6B32\u4E3A\uFF0C\u6155\u5BB9\u9F99\u4E0D\u7981\u7709\u98DE\u8272\u821E\u3002\u4ED6\u633D\u4F4F\u795E\u5C3C\u8170\u4FA7\u7684\u4EB5\u88E4\uFF0C\u6162\u6162\u892A\u4E0B\u3002</p><p>\u7C97\u5E03\u4E0B\u7F13\u7F13\u9732\u51FA\u4E00\u7247\u817B\u5982\u7F8A\u8102\u7684\u808C\u80A4\uFF0C\u6ED1\u5AE9\u52A8\u4EBA\u3002\u6155\u5BB9\u9F99\u624B\u6307\u4E00\u50F5\uFF0C\u534A\u664C\u540E\u7A81\u7136\u7206\u53D1\u51FA\u4E00\u9635\u5927\u7B11\u3002\u5149\u6DA6\u7684\u7389\u817F\u7B14\u76F4\u4FEE\u957F\uFF0C\u8170\u8EAB\u7EC6\u81F4\uFF0C\u5C0F\u8179\u5E73\u5766\uFF0C\u4E00\u7247\u4E1D\u7EF8\u822C\u5149\u6ED1\u7684\u808C\u80A4\u4E2D\u5374\u7F20\u7740\u4E00\u622A\u7C97\u5E03\uFF0C\u539A\u539A\u88F9\u5728\u80A1\u95F4\u3002</p><p>\u5F53\u65F6\u5973\u5B50\u5185\u8863\u591A\u662F\u62B9\u80F8\u3001\u8BC3\u5B50\u4E4B\u7C7B\u7684\u4E0A\u8863\uFF1B\u7565\u957F\u4E00\u4E9B\u7684\u6709\u62B9\u809A\u3001\u809A\u515C\uFF0C\u53EF\u4F38\u81F3\u8179\u4E0B\uFF1B\u8C6A\u95E8\u8D35\u5987\u53E6\u6709\u8D34\u8EAB\u5C0F\u8863\uFF0C\u906E\u63A9\u7389\u4F53\u3002\u4F46\u50CF\u795E\u5C3C\u8FD9\u6837\u7684\u7EDD\u65E0\u4EC5\u6709\uFF0C\u56E0\u4E3A\u8FD9\u662F\u4E00\u4E9B\u82E6\u529B\u6216\u8005\u58EB\u5175\u4E3A\u907F\u514D\u9633\u5177\u53D7\u4F24\u4F7F\u7528\u7684\u515C\u88C6\uFF0C\u53EA\u9650\u4E8E\u7537\u5B50\u4F7F\u7528\u3002</p><p>\u300C\u5E08\u592A\uFF0C\u8FD9\u79CD\u4E0B\u4E09\u6EE5\u7684\u8863\u7269\u4F60\u4E5F\u7A7F\u2026\u2026\u6342\u8FD9\u4E48\u7D27\uFF0C\u4E5F\u4E0D\u6015\u95F7\u574F\u4E86\u5B83\uFF1F\u300D\u6155\u5BB9\u9F99\u6478\u5F04\u7740\u8C03\u7B11\u9053\u3002\u96EA\u5CF0\u795E\u5C3C\u8138\u4E0A\u867D\u7136\u6CA1\u6709\u8868\u60C5\uFF0C\u8033\u6839\u5374\u9690\u9690\u53D1\u7EA2\u3002</p><p>\u767D\u8272\u7684\u7C97\u5E03\u4E00\u5C42\u5C42\u89E3\u5F00\uFF0C\u9732\u51FA\u9634\u961C\u4E0A\u6D53\u5BC6\u7684\u6BDB\u53D1\u3002\u5F2F\u66F2\u7684\u9634\u6BDB\u53C8\u957F\u53C8\u539A\uFF0C\u906E\u4F4F\u5C0F\u8179\u534A\u6570\u6709\u4F59\uFF0C\u6155\u5BB9\u9F99\u54C8\u54C8\u5927\u7B11\u9053\uFF1A\u300C\u5E08\u592A\u8FD9\u662F\u5934\u4E0A\u6CA1\u6709\u5C44\u4E0A\u8865\uFF0C\u7ADF\u7136\u957F\u8FD9\u4E48\u591A\u2026\u2026\u300D\u4ED6\u634B\u4F4F\u4E00\u64AE\u9634\u6BDB\u7528\u529B\u62C9\u957F\uFF0C\u96EA\u767D\u7684\u9634\u961C\u5728\u6BDB\u53D1\u4E0B\u65F6\u9690\u65F6\u73B0\uFF0C\u6563\u53D1\u51FA\u4E00\u79CD\u6D53\u91CD\u7684\u5973\u6027\u4F53\u5473\u3002</p><p>\u5F85\u89E3\u4E0B\u6700\u540E\u4E00\u5C42\u767D\u5E03\uFF0C\u53EA\u89C1\u767D\u751F\u751F\u7684\u817F\u7F1D\u4E2D\u9732\u51FA\u4E00\u56E2\u5AE9\u8089\uFF0C\u7EA2\u6DA6\u52A8\u4EBA\u3002\u6155\u5BB9\u9F99\u5FC3\u4E0B\u5927\u5947\uFF0C\u8FDE\u5FD9\u63B0\u5F00\u795E\u5C3C\u7684\u53CC\u817F\u3002\u7EA2\u5149\u4E00\u95EA\uFF0C\u4E00\u56E2\u80A5\u5AE9\u7684\u8089\u82B1\u5728\u96EA\u80A4\u4E2D\u4E4D\u7136\u7EFD\u5F00\uFF0C\u4EFF\u4F5B\u542B\u9732\u7261\u4E39\uFF0C\u5E26\u7740\u4E00\u7247\u6C34\u5149\uFF0C\u8272\u6CFD\u9C9C\u7EA2\u3002\u6574\u4E2A\u4E0B\u8179\u90FD\u88AB\u5AE9\u8089\u6324\u6EE1\uFF0C\u51E0\u4E4E\u6BD4\u5F97\u4E0A\u4E0E\u7EAA\u7709\u59A9\u80BF\u80C0\u7684\u79D8\u5904\u3002\u82B1\u8482\u50CF\u8981\u6323\u8131\u8986\u76D6\u7684\u5305\u76AE\u822C\u9AD8\u9AD8\u9F13\u8D77\u3002</p><p>\u6155\u5BB9\u9F99\u6EE1\u5FC3\u8BE7\u5F02\uFF0C\u795E\u5C3C\u4E0B\u4F53\u5982\u4F55\u4F1A\u751F\u6210\u8FD9\u822C\u6A21\u6837\uFF0C\u5373\u4F7F\u4EA4\u5408\u591A\u5E74\uFF0C\u88AB\u65E0\u6570\u8089\u68D2\u6345\u5F04\u7684\u8361\u5987\u4E5F\u4E0D\u81F3\u4E8E\u5982\u6B64\u2026\u2026</p><p>\u4ED6\u62E8\u5F00\u6E7F\u6DCB\u6DCB\u7684\u82B1\u74E3\uFF0C\u4ED4\u7EC6\u68C0\u89C6\u8089\u7A74\u3002\u90A3\u5C42\u8584\u819C\u5B8C\u597D\u65E0\u635F\uFF0C\u4ECD\u662F\u5904\u5B50\u4E4B\u8EAB\u3002</p><p>\u6155\u5BB9\u9F99\u628A\u6307\u4E0A\u9ECF\u6E7F\u7684\u6DEB\u6C34\u6D82\u5230\u795E\u5C3C\u5507\u4E0A\uFF0C\u7B11\u9053\uFF1A\u300C\u5728\u4E0B\u53EA\u968F\u4FBF\u6478\u6478\uFF0C\u5E08\u592A\u5C31\u6D41\u4E86\u8FD9\u4E48\u591A\u6C34\u2026\u2026\u91CC\u9762\u662F\u4E0D\u662F\u5F88\u75D2\u554A\uFF1F\u9A9A\u8D27\uFF1F\u300D</p><p>\u96EA\u5CF0\u795E\u5C3C\u6EE1\u8138\u98DE\u7EA2\uFF0C\u53CC\u76EE\u7D27\u95ED\uFF0C\u5634\u5507\u5FAE\u52A8\uFF0C\u5583\u5583\u8BF5\u7ECF\u4E0D\u5DF2\u3002\u81EA\u4ECE\u516D\u5E74\u524D\u5979\u7EC3\u6210\u51E4\u9E23\u671D\u9633\u4E4B\u540E\uFF0C\u539F\u672C\u6B63\u5E38\u7684\u9634\u90E8\u5C31\u5F00\u59CB\u4E0D\u65AD\u589E\u5927\uFF0C\u800C\u4E14\u8D8A\u6765\u8D8A\u654F\u611F\uFF0C\u4EE5\u81F3\u4E8E\u4E0D\u5F97\u4E0D\u7528\u767D\u5E03\u5305\u88F9\u4E0B\u4F53\u3002\u5982\u4ECA\u6700\u9690\u79D8\u7684\u5730\u65B9\u88AB\u4EBA\u4EFB\u610F\u73A9\u5F04\u8C03\u7B11\uFF0C\u5373\u4F7F\u4FEE\u884C\u518D\u6DF1\uFF0C\u4E5F\u96BE\u4EE5\u6D88\u9664\u8FD9\u79CD\u7F9E\u803B\u548C\u7A98\u8FEB\u3002</p><p>\u6155\u5BB9\u9F99\u57CB\u5934\u6DF1\u6DF1\u5438\u4E86\u53E3\u6C14\uFF0C\u6447\u5934\u9053\uFF1A\u300C\u5E08\u592A\u7684\u76AE\u56CA\u786E\u5B9E\u4E0D\u81ED\uFF0C\u4E0D\u8FC7\u771F\u591F\u9A9A\u7684\u3002\u300D</p><p>\u4ED6\u4E0D\u6562\u5192\u9669\u7528\u771F\u6C14\u53BB\u64A9\u62E8\u795E\u5C3C\u7684\u8089\u4F53\uFF0C\u4FBF\u64AE\u5507\u5410\u51FA\u4E00\u53E3\u52B2\u6C14\u3002\u52B2\u98CE\u5230\u5904\uFF0C\u5AE9\u8089\u4E00\u9635\u98A4\u6296\uFF0C\u96EA\u5CF0\u795E\u5C3C\u4E0B\u4F53\u53C8\u6D8C\u51FA\u4E00\u80A1\u6DEB\u6DB2\u3002</p><p>\u6155\u5BB9\u9F99\u7B11\u9053\uFF1A\u300C\u5E08\u592A\u4E0B\u9762\u957F\u5F97\u597D\u751F\u6DEB\u8361\uFF0C\u5C31\u662F\u8FD9\u80F8\u5E73\u4E86\u4E9B\u2026\u2026\u300D\u4ED6\u4E00\u8DEF\u55C5\u7740\u7528\u9F3B\u5C16\u8E6D\u8D77\u4E2D\u8863\uFF0C\u6B63\u5F85\u4F38\u624B\u53BB\u89E3\u3002\u4E00\u76F4\u6C89\u9ED8\u7684\u96EA\u5CF0\u795E\u5C3C\u7A81\u7136\u7741\u5F00\u53CC\u76EE\uFF0C\u5FCD\u65E0\u53EF\u5FCD\u5730\u5E76\u6307\u671D\u6155\u5BB9\u9F99\u773C\u4E2D\u523A\u53BB\u3002</p><p>\u6155\u5BB9\u9F99\u4E0D\u95EA\u4E0D\u907F\uFF0C\u7B49\u624B\u6307\u4F38\u5230\u773C\u524D\u624D\u5C48\u6307\u4E00\u5F39\u3002\u96EA\u5CF0\u795E\u5C3C\u7684\u624B\u81C2\u5E94\u6307\u800C\u843D\uFF0C\u91CD\u91CD\u6389\u5728\u8EAB\u4FA7\u3002\u6155\u5BB9\u9F99\u5341\u4E8C\u5206\u5FEB\u610F\u5730\u7768\u89C6\u7740\u795E\u5C3C\u55B7\u706B\u7684\u53CC\u76EE\uFF0C\u5632\u7B11\u9053\uFF1A\u300C\u5E08\u592A\u52A8\u4E86\u55D4\u5FF5\uFF0C\u4E8E\u4FEE\u4E3A\u5927\u6709\u6240\u788D\u3002\u300D</p><p>\u89E3\u5F00\u96EA\u767D\u7684\u4E2D\u8863\uFF0C\u91CC\u9762\u4ECD\u662F\u5BC6\u5BC6\u7F20\u7D27\u7684\u767D\u5E03\uFF0C\u6155\u5BB9\u9F99\u4E0D\u8010\u70E6\u4E00\u4E00\u89E3\u5F00\uFF0C\u5E72\u8106\u4F38\u6307\u4E00\u5212\uFF0C\u6570\u5C42\u767D\u5E03\u5200\u5272\u822C\u4E4D\u7136\u7834\u88C2\uFF0C\u4E00\u5BF9\u80A5\u7855\u7684\u5DE8\u4E73\u5E94\u624B\u5F39\u51FA\uFF0C\u5728\u80F8\u524D\u98A4\u5FAE\u5FAE\u4E0D\u4F4F\u8DF3\u52A8\u3002</p><p>\u6155\u5BB9\u9F99\u6123\u4E86\u7247\u523B\u624D\u7B11\u9053\uFF1A\u300C\u5E08\u592A\u597D\u5927\u7684\u5976\u5B50\u2026\u2026\u300D</p><p>\u6D51\u5706\u7684\u4E73\u7403\u5E76\u5728\u4E00\u8D77\uFF0C\u51E0\u4E4E\u6EA2\u51FA\u795E\u5C3C\u8EAB\u4F53\uFF0C\u80A5\u5AE9\u7684\u4E73\u8089\u5145\u6EE1\u6CB9\u8102\u822C\u767D\u4EAE\u5149\u6DA6\uFF0C\u6ED1\u817B\u52A8\u4EBA\u3002\u53C8\u5927\u53C8\u5706\u7684\u4E73\u6655\u9F13\u51FA\u4E00\u5708\u7C89\u7EA2\uFF0C\u4E73\u5934\u9AD8\u9AD8\u7FD8\u8D77\uFF0C\u50CF\u4E00\u4E2A\u5AE9\u7EA2\u6307\u5C16\u3002</p><p>\u6155\u5BB9\u9F99\u8F7B\u8F7B\u4E00\u637B\uFF0C\u4E73\u5934\u7ACB\u5373\u53D8\u5F97\u575A\u786C\u3002\u4ED6\u634F\u4F4F\u4E73\u5C16\u7528\u529B\u5C06\u4E73\u7403\u62C9\u957F\u3002\u5145\u6EE1\u5F39\u6027\u7684\u4E73\u8089\u7F13\u7F13\u4F38\u5C55\uFF0C\u4E73\u5934\u79BB\u5F00\u80F8\u90E8\u8D85\u8FC7\u534A\u5C3A\u957F\u77ED\u3002</p><p>\u6155\u5BB9\u9F99\u4F38\u624B\u4E00\u6BD4\uFF0C\u5927\u7B11\u9053\uFF1A\u300C\u5E08\u592A\u8FD9\u5BF9\u5927\u5976\u771F\u662F\u8C6A\u6C14\u8FEB\u4EBA\uFF01\u633A\u7740\u5B83\u62DB\u6447\u8FC7\u5E02\uFF0C\u795E\u5C3C\u7684\u540D\u58F0\u80AF\u5B9A\u8DB3\u5C3A\u52A0\u4E09\uFF0C\u5E72\u561B\u906E\u906E\u63A9\u63A9\uFF1F\u300D</p><p>\u624B\u6307\u4E00\u677E\uFF0C\u4E73\u5934\u500F\u5FFD\u5F39\u843D\uFF0C\u4EAE\u6676\u6676\u7684\u77F3\u5B50\u822C\u546F\u7136\u8DF3\u52A8\uFF0C\u80A5\u5AE9\u7684\u4E73\u8089\u767D\u5149\u95EA\u52A8\uFF0C\u7FFB\u6EDA\u4E0D\u5DF2\u3002</p><p>\u4E0B\u9634\u7684\u5F02\u72B6\u8FD8\u597D\u9690\u85CF\uFF0C\u4F46\u4E73\u623F\u7684\u589E\u5927\u5374\u4F7F\u96EA\u5CF0\u795E\u5C3C\u6781\u5176\u96BE\u582A\u3002\u884C\u8D70\u6C5F\u6E56\uFF0C\u633A\u7740\u8FD9\u6837\u4E00\u5BF9\u5DE8\u4E73\u5FC5\u7136\u60F9\u4EBA\u975E\u8BAE\uFF0C\u56E0\u6B64\u5979\u624D\u675F\u4F4F\u80F8\u90E8\uFF0C\u907F\u514D\u90A3\u4E9B\u5632\u8BBD\u7684\u76EE\u5149\u3002\u4F46\u6B64\u65F6\u8FD9\u5BF9\u7F9E\u4E8E\u89C1\u4EBA\u7684\u4E73\u623F\u4E0D\u4F46\u88AB\u4EBA\u770B\u4E2A\u6E05\u695A\uFF0C\u800C\u4E14\u8FD8\u628A\u73A9\u8C03\u5F04\uFF0C\u96EA\u5CF0\u795E\u5C3C\u7F9E\u5F97\u65E0\u5730\u81EA\u5BB9\u3002</p><p>\u6155\u5BB9\u9F99\u7231\u4E0D\u91CA\u624B\u7684\u628A\u73A9\u534A\u664C\uFF0C\u7136\u540E\u628A\u8138\u57CB\u5728\u4E73\u6C9F\u4E2D\uFF0C\u6367\u7740\u6ED1\u817B\u7684\u4E73\u8089\u53C8\u8214\u53C8\u54AC\u3002\u4ED6\u5FC3\u5934\u6B32\u706B\u5347\u817E\uFF0C\u6068\u4E0D\u5F97\u7ACB\u523B\u4FBF\u5360\u6709\u795E\u5C3C\u7684\u5904\u5B50\u4E4B\u8EAF\u3002</p><p>\u96EA\u5CF0\u795E\u5C3C\u6DF1\u5438\u7F13\u5410\uFF0C\u514B\u5236\u4F4F\u7F9E\u803B\u548C\u72C2\u6D8C\u7684\u6012\u706B\u3002\u7F9E\u803B\u548C\u53D1\u6012\u90FD\u6CA1\u6709\u4E00\u70B9\u7528\u5904\u2026\u2026\u7834\u4F53\u4E4B\u65F6\uFF0C\u4FBF\u662F\u6B92\u547D\u4E4B\u523B\uFF0C\u629B\u5374\u65E7\u76AE\u56CA\uFF0C\u8FCE\u5F97\u5927\u89E3\u8131\u2026\u2026\u53EA\u662F\u8FD8\u6709\u51E0\u4F4D\u5F92\u5F1F\u2026\u2026\u8BE5\u6740\u7684\u5996\u9B54\uFF01</p><p>\u6B63\u6124\u6068\u95F4\uFF0C\u6155\u5BB9\u9F99\u7A81\u7136\u62AC\u8D77\u5934\uFF0C\u89E3\u5F00\u795E\u5C3C\u7684\u7A74\u9053\uFF0C\u8F6C\u8EAB\u79BB\u5F00\u77F3\u5BA4\u3002\u81EA\u53BB\u627E\u7EAA\u7709\u59A9\u6216\u662F\u767D\u6C0F\u59D0\u59B9\u53D1\u6CC4\u6B32\u706B\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u795E\u6BBF\u5185\u300C\u546F\u546F\u6886\u6886\u300D\u54CD\u4E2A\u4E0D\u505C\uFF0C\u6728\u5802\u5E2E\u4F17\u6B63\u5728\u4FEE\u8865\u88AB\u70B8\u574F\u7684\u5DE8\u67F1\u548C\u95E8\u7A97\u3002\u795E\u6BBF\u5185\u5916\u4EBA\u6765\u4EBA\u5F80\uFF0C\u6216\u662F\u56DB\u5904\u6E05\u626B\uFF0C\u6216\u662F\u626F\u8D77\u5E03\u5E54\uFF0C\u79FB\u6765\u82B1\u8349\uFF0C\u5E03\u7F6E\u5BAB\u4E3B\u7684\u5A5A\u793C\u3002</p><p>\u505A\u4E3A\u5A5A\u793C\u7684\u5973\u4E3B\u89D2\uFF0C\u7D2B\u73AB\u4FCF\u8138\u5982\u51B0\uFF0C\u6068\u4E0D\u5F97\u4E00\u628A\u70E7\u4E86\u8FD9\u4E9B\u7834\u70C2\u3002\u64D2\u5230\u96EA\u5CF0\u795E\u5C3C\u4E4B\u540E\uFF0C\u6155\u5BB9\u9F99\u4E0D\u518D\u7981\u6B62\u5979\u79BB\u5F00\u752C\u9053\uFF0C\u4F46\u65E0\u8BBA\u5230\u4EC0\u4E48\u5730\u65B9\uFF0C\u90FD\u6709\u4E24\u540D\u7D2B\u8863\u4F8D\u8005\u8DDF\u5728\u540E\u9762\u3002</p><p>\u300C\u6EDA\u5F00\uFF01\u300D\u7D2B\u73AB\u4E00\u58F0\u5389\u559D\u3002</p><p>\u4E00\u540D\u5E2E\u4F17\u8FDE\u5FD9\u653E\u624B\uFF0C\u614C\u614C\u5F20\u5F20\u94BB\u5230\u4EBA\u7FA4\u4E2D\u3002\u7D2B\u73AB\u538B\u4F4F\u5FC3\u91CC\u7206\u53D1\u7684\u6068\u610F\uFF0C\u8F7B\u8F7B\u62D4\u51FA\u5AC2\u5AC2\u809B\u5185\u7684\u6728\u68CD\uFF0C\u628A\u624B\u91CC\u7684\u5DFE\u88AB\u62AB\u5728\u6797\u9999\u8FDC\u8EAB\u4E0A\u3002\u4E00\u4E2A\u7D2B\u8863\u4F8D\u8005\u9634\u9633\u602A\u6C14\u5730\u8BF4\uFF1A\u300C\u5BAB\u4E3B\u6709\u4EE4\uFF0C\u6797\u5A4A\u5B50\u4E0D\u8BB8\u7A7F\u2026\u2026\u300D</p><p>\u8BDD\u8FD8\u6CA1\u8BF4\u5B8C\uFF0C\u7D2B\u73AB\u626D\u5934\u9A82\u9053\uFF1A\u300C\u53BB\u4F60\u5988\u7684\uFF01\u300D</p><p>\u7D2B\u8863\u4F8D\u8005\u6CA1\u60F3\u5230\u7F8E\u82E5\u5929\u4ED9\u7684\u73AB\u7470\u4ED9\u5B50\u5C45\u7136\u4F1A\u9A82\u51FA\u8FD9\u79CD\u7C97\u8BDD\uFF0C\u987F\u65F6\u54D1\u53E3\u65E0\u8A00\u3002</p><p>\u56DB\u6708\u7684\u9633\u5149\u70BD\u70ED\u5982\u706B\uFF0C\u9ED1\u8272\u7684\u5927\u7406\u77F3\u6652\u5F97\u6EDA\u70EB\u3002</p><p>\u6797\u9999\u8FDC\u9759\u9759\u4F0F\u5728\u84B8\u817E\u7684\u7A7A\u6C14\u4E2D\uFF0C\u767D\u5AE9\u5982\u8102\u7684\u8089\u4F53\u4EFF\u4F5B\u968F\u65F6\u90FD\u4F1A\u878D\u5316\u3002\u7389\u4F53\u4E0A\u5C3D\u662F\u6591\u6591\u8840\u8FF9\uFF0C\u904D\u5E03\u9752\u80BF\u3002\u9999\u8F6F\u7684\u4E73\u623F\u60AC\u5728\u8EAB\u4E0B\uFF0C\u770B\u4E0D\u5230\u5979\u8179\u4E0B\u7684\u4F24\u52BF\u3002\u4E73\u5C16\u7684\u4F24\u53E3\u4E0E\u79FB\u690D\u7684\u76AE\u80A4\u7D27\u7D27\u751F\u5728\u4E00\u8D77\uFF0C\u5265\u58F3\u7684\u9E21\u86CB\u822C\u6D51\u5706\u5149\u6DA6\uFF0C\u63A9\u76D6\u4E86\u4E73\u6655\u4E0B\u4E1D\u4E1D\u7F15\u7F15\u6D45\u767D\u8272\u7684\u7B4B\u7EDC\uFF0C\u4EFF\u4F5B\u4ECE\u672A\u751F\u8FC7\u4E73\u5934\u822C\uFF0C\u6CA1\u6709\u4E00\u4E1D\u79FB\u690D\u7684\u75D5\u8FF9\u3002</p><p>\u7D2B\u73AB\u6068\u5F97\u54AC\u7259\u5207\u9F7F\uFF0C\u5728\u6155\u5BB9\u9F99\u9762\u524D\u8FD8\u8981\u88C5\u51FA\u4E56\u5DE7\u67D4\u987A\u7684\u6A21\u6837\uFF0C\u65E9\u5C31\u618B\u4E86\u4E00\u809A\u5B50\u7684\u706B\u3002\u522B\u8BF4\u9A82\u4EBA\uFF0C\u82E5\u4E0D\u662F\u5185\u529F\u5C3D\u5931\uFF0C\u8FD8\u8981\u52A8\u624B\u884C\u51F6\u3002\u9A82\u4E86\u4E00\u53E5\uFF0C\u5FC3\u91CC\u7565\u5FAE\u75DB\u5FEB\u4E00\u70B9\u3002\u5979\u6276\u8D77\u5AC2\u5AC2\uFF0C\u51D1\u5728\u5979\u8033\u8FB9\u4F4E\u58F0\u9053\uFF1A\u300C\u5AC2\u5AC2\uFF0C\u4F60\u518D\u5FCD\u51E0\u5929\uFF0C\u6211\u60F3\u529E\u6CD5\u8BA9\u4F60\u79BB\u5F00\u8FD9\u91CC\u3002\u300D</p><p>\u65E0\u5C3D\u7684\u75DB\u82E6\u5DF2\u7ECF\u9EBB\u6728\u4E86\u5979\u7684\u611F\u89C9\uFF0C\u6797\u9999\u8FDC\u865A\u5F31\u7684\u5598\u606F\u7740\uFF0C\u826F\u4E45\u53D1\u51FA\u4E00\u58F0\u53F9\u606F\u822C\u7684\u547B\u541F\uFF0C\u5145\u6EE1\u54C0\u5A49\u548C\u75DB\u695A\u3002</p><p>\u8C8C\u7F8E\u5982\u82B1\u7684\u5AC2\u5AC2\u3001\u82F1\u59FF\u52C3\u53D1\u7684\u4E8C\u5E08\u59D0\uFF0C\u540D\u626C\u6C5F\u6E56\u7684\u5BD2\u6708\u5200\uFF0C\u5982\u4ECA\u5374\u843D\u5F97\u53CC\u76EE\u5931\u660E\uFF0C\u6027\u5668\u88AB\u5C3D\u6570\u5207\u9664\uFF0C\u72D7\u4E00\u822C\u6813\u5728\u5BA4\u5916\u4EFB\u4EBA\u6DEB\u8FB1\u2026\u2026\u7D2B\u73AB\u5FC3\u75DB\u5F97\u50CF\u88AB\u65E0\u6570\u624B\u638C\u751F\u751F\u6495\u88C2\uFF0C\u788E\u6210\u4E00\u7247\u4E00\u7247\u3002\u5979\u6402\u7740\u5AC2\u5AC2\uFF0C\u4E45\u4E45\u6CA1\u6709\u8BF4\u8BDD\u3002</p><p>\u4E91\u6735\u5DE8\u5927\u7684\u9634\u5F71\u5728\u7FA4\u5CF0\u95F4\u60A0\u7136\u98DE\u6E21\uFF0C\u8F6C\u773C\u6D88\u5931\u5F97\u65E0\u5F71\u65E0\u8E2A\u3002\u5C71\u98CE\u5439\u8FC7\uFF0C\u5E26\u6765\u4E1D\u4E1D\u51C9\u610F\u3002</p><p>\u7D2B\u73AB\u50BB\u50BB\u671B\u7740\u8FDC\u5904\u98D8\u626C\u7684\u5927\u65D7\uFF0C\u4E00\u65F6\u95F4\u604D\u60DA\u8D77\u6765\u3002\u96BE\u9053\u8FD9\u4E00\u5207\u90FD\u662F\u771F\u7684\uFF1F\u5979\u77E5\u9053\u81EA\u5DF1\u4E00\u79BB\u5F00\uFF0C\u9A6C\u4E0A\u5C31\u6709\u4EBA\u6765\u6298\u78E8\u5AC2\u5AC2\uFF0C\u4F46\u8FD8\u6709\u6BCD\u4EB2\u3001\u5927\u5E08\u59D0\u3001\u4E09\u5E08\u59D0\u90FD\u9700\u8981\u5979\u6765\u7167\u987E\u3002\u6C89\u7538\u7538\u7684\u8D23\u4EFB\u538B\u5728\u67D4\u5F31\u7684\u80A9\u8180\u4E0A\uFF0C\u4E00\u79CD\u4FB5\u5165\u9AA8\u9AD3\u7684\u75B2\u60EB\u7A81\u7136\u4FB5\u5165\u5FC3\u5E95\uFF0C\u8FD8\u4E0D\u6EE1\u5341\u516D\u5C81\u7684\u5C0F\u5973\u5B69\u987F\u65F6\u89C9\u5F97\u6D51\u8EAB\u53D1\u8F6F\uFF0C\u53EA\u60F3\u8EBA\u4E0B\u597D\u597D\u7761\u4E0A\u4E00\u89C9\u3002</p><p>\u4E5F\u8BB8\u660E\u5929\u9192\u6765\uFF0C\u81EA\u5DF1\u8FD8\u5728\u98D8\u6885\u5CF0\u7684\u767D\u96EA\u4E2D\uFF0C\u6B63\u548C\u5E08\u59D0\u6311\u9009\u4E00\u679D\u6700\u6F02\u4EAE\u7684\u6885\u82B1\u6765\u88C5\u70B9\u5C0F\u5C0F\u7684\u5EB5\u5802\u2026\u2026</p>',50);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
