import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_48" tabindex="-1"><a class="header-anchor" href="#_48" aria-hidden="true">#</a> 48</h1><p>\u521A\u5165\u591C\uFF0C\u7D2B\u73AB\u4FBF\u62C9\u4F4F\u6155\u5BB9\u9F99\uFF0C\u7B11\u76C8\u76C8\u9053\uFF1A\u300C\u54E5\u54E5\uFF0C\u4F60\u522B\u5FD8\u4E86\uFF0C\u6628\u5929\u7B54\u5E94\u6211\u53BB\u770B\u98CE\u5974\u7684\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u6B63\u51C6\u5907\u8BE2\u95EE\u5C60\u6000\u6C89\u9080\u8BF7\u5BBE\u5BA2\u4E4B\u4E8B\uFF0C\u4F46\u89C1\u59B9\u59B9\u8F6F\u8BED\u76F8\u6C42\uFF0C\u4FBF\u628A\u6B63\u4E8B\u5F80\u540E\u653E\u653E\u3002\u4F55\u51B5\u4ED6\u4E5F\u60F3\u89C1\u89C1\u98CE\u665A\u534E\u4E0E\u5DE8\u72AC\u540C\u5C45\u4E24\u5929\u7A76\u7ADF\u53D8\u6210\u4E86\u4EC0\u4E48\u6A21\u6837\u3002</p><p>\u8D64\u88F8\u7684\u7389\u4F53\u4EFF\u4F5B\u51CB\u8C22\u7684\u767D\u82B1\u74E3\uFF0C\u9759\u9759\u4F0F\u5728\u5BA4\u89D2\u3002\u98CE\u665A\u534E\u65AD\u81C2\u4F38\u5728\u4F53\u524D\uFF0C\u7389\u624B\u4ECE\u8179\u4E0B\u4F38\u5230\u4E24\u817F\u95F4\uFF0C\u7D27\u7D27\u6342\u7740\u79D8\u5904\u3002\u7C89\u5AE9\u7684\u7389\u80CC\u5E03\u6EE1\u722A\u75D5\uFF0C\u5C24\u5176\u662F\u80A9\u4E0A\u51E0\u9053\u6DF1\u6DF1\u7684\u4F24\u53E3\uFF0C\u9999\u808C\u7FFB\u5377\uFF0C\u9C9C\u8840\u6DCB\u6F13\u3002</p><p>\u300C\u4F60\u9A97\u4EBA\uFF01\u300D\u7D2B\u73AB\u5C16\u53EB\u4E00\u58F0\uFF0C\u671D\u6155\u5BB9\u9F99\u624B\u80CC\u4E0A\u54AC\u53BB\u3002</p><p>\u300C\u8C01\u9A97\u4F60\u4E86\uFF01\u300D\u6155\u5BB9\u9F99\u5389\u559D\u9053\uFF1A\u300C\u6211\u8BF4\u8FC7\u4E0D\u4F1A\u54AC\uFF0C\u80AF\u5B9A\u5C31\u4E0D\u4F1A\u54AC\u3002\u6293\u4F24\u662F\u5979\u81EA\u5DF1\u4E0D\u8001\u5B9E\uFF01\u4E56\u4E56\u8FD9\u836F\u62B9\u4E0A\uFF01\u8FD8\u6709\u8FD9\u4E2A\uFF0C\u7ED9\u98CE\u5A4A\u5B50\u5582\u4E0B\u3002\u300D</p><p>\u7D2B\u73AB\u6293\u8D77\u836F\u74F6\u836F\u4E38\u72E0\u72E0\u6254\u5230\u752C\u9053\u5C3D\u5934\u3002\u6155\u5BB9\u9F99\u8138\u4E00\u6C89\uFF0C\u4F38\u624B\u5173\u4E0A\u77F3\u95E8\u3002\u7D2B\u73AB\u4E00\u58F0\u4E0D\u54CD\u5730\u95EA\u8EAB\u94BB\u5230\u5BA4\u5185\uFF0C\u8138\u4E0A\u5E26\u7740\u4E0E\u5E08\u59D0\u540C\u751F\u5171\u6B7B\u7684\u51B3\u7136\u3002</p><p>\u300C\u546F\u300D\u7684\u4E00\u58F0\uFF0C\u6155\u5BB9\u9F99\u628A\u77F3\u95E8\u5173\u4E0A\uFF0C\u5FC3\u9053\u5413\u5413\u8FD9\u4E2B\u5934\u4E5F\u597D\u3002\u4F46\u4ED6\u7EC8\u7A76\u4E0D\u653E\u5FC3\uFF0C\u6084\u6084\u8DB4\u5728\u95E8\u4E0A\u503E\u542C\u5BA4\u5185\u7684\u52A8\u9759\u3002</p><p>\u8FC7\u4E86\u7247\u523B\uFF0C\u6155\u5BB9\u9F99\u4F30\u8BA1\u5DEE\u4E0D\u591A\u4E86\uFF0C\u4FBF\u62C9\u5F00\u623F\u95E8\u3002\u53EA\u89C1\u7D2B\u73AB\u6602\u9996\u5750\u5728\u5730\u4E0A\uFF0C\u4FCF\u8138\u4E0A\u4E00\u80A1\u5927\u4E49\u51DB\u7136\u7684\u795E\u8272\u3002\u6155\u5BB9\u9F99\u6C14\u5F97\u7B11\u4E86\u8D77\u6765\uFF0C\u8FD9\u6B21\u4E0D\u5149\u628A\u95E8\u5173\u7D27\uFF0C\u8FD8\u628A\u673A\u62EC\u4E5F\u7EDF\u7EDF\u6273\u4E0A\u518D\u6273\u4E0B\u6765\uFF0C\u53C8\u638F\u51FA\u94A5\u5319\u5149\u5149\u5577\u5577\u5F04\u51FA\u4E00\u7247\u58F0\u54CD\uFF0C\u88C5\u505A\u628A\u95E8\u9501\u7D27\u7684\u6837\u5B50\u3002</p><p>\u4EE5\u6155\u5BB9\u9F99\u7684\u529F\u529B\uFF0C\u9694\u7740\u539A\u539A\u7684\u77F3\u95E8\uFF0C\u5BA4\u5185\u7684\u52A8\u9759\u4E5F\u80FD\u949C\u7EC6\u65E0\u9057\u7684\u5C3D\u6536\u8033\u4E2D\u3002</p><p>\u4ED6\u542C\u51FA\u98CE\u665A\u534E\u8FD8\u5728\u660F\u8FF7\uFF0C\u90A3\u51E0\u53EA\u5DE8\u72AC\u5927\u6982\u662F\u521A\u5C04\u8FC7\u7CBE\uFF0C\u6B63\u61D2\u6D0B\u6D0B\u515C\u7740\u5708\u5B50\u3002</p><p>\u7D2B\u73AB\u5012\u4E5F\u6C89\u5F97\u4F4F\u6C14\uFF0C\u534A\u5929\u4E5F\u4E0D\u542D\u4E00\u58F0\u3002\u6155\u5BB9\u9F99\u542C\u7740\u4E00\u5934\u5DE8\u72AC\u671D\u7D2B\u73AB\u8D70\u53BB\uFF0C\u5FC3\u5934\u6162\u6162\u63EA\u8D77\uFF0C\u4E0D\u77E5\u9053\u83BA\u5974\u521A\u624D\u5582\u8FC7\u5B83\u4EEC\u6CA1\u6709\u2026\u2026</p><p>\u7A81\u7136\u5BA4\u5185\u54CD\u8D77\u4E00\u9635\u7EC6\u5FAE\u7684\u545C\u54BD\uFF0C\u6155\u5BB9\u9F99\u8FDE\u5FD9\u63A8\u95E8\u800C\u5165\u3002\u53EA\u89C1\u7D2B\u73AB\u6241\u7740\u5C0F\u5634\uFF0C\u300C\u545C\u545C\u300D\u54ED\u6CE3\uFF0C\u4E00\u5934\u76AE\u6BDB\u6CB9\u4EAE\u7684\u9ED1\u72AC\u6B63\u4F38\u51FA\u9C9C\u7EA2\u7684\u957F\u820C\uFF0C\u5728\u5979\u5A07\u5AE9\u7684\u7389\u8138\u4E0A\u6765\u56DE\u8214\u5F04\u3002\u5C0F\u59D1\u5A18\u76F4\u76F4\u5750\u5728\u5730\u4E0A\uFF0C\u4E24\u624B\u80CC\u5728\u8EAB\u540E\uFF0C\u5413\u5F97\u4E00\u52A8\u4E5F\u4E0D\u6562\u52A8\u3002</p><p>\u6155\u5BB9\u9F99\u54C8\u54C8\u7B11\u9053\uFF1A\u300C\u4E56\u4E56\u8DDF\u6211\u51FA\u6765\u5427\u3002\u300D</p><p>\u7D2B\u73AB\u8138\u4E0A\u6302\u6EE1\u5706\u5706\u7684\u6CEA\u73E0\uFF0C\u54ED\u9053\uFF1A\u300C\u54E5\u54E5\uFF0C\u6211\u6C42\u6C42\u4F60\u4E86\uFF0C\u522B\u8BA9\u5979\u4E00\u4E2A\u4EBA\u5728\u8FD9\u91CC\u2026\u2026\u91CC\u9762\u597D\u9ED1\u2026\u2026\u300D</p><p>\u300C\u4E00\u4E2A\u4EBA\uFF1F\u90A3\u8BA9\u7EAA\u5A4A\u5B50\u6765\u966A\u5979\u5427\u3002\u300D</p><p>\u7D2B\u73AB\u8FDE\u5FD9\u6447\u5934\uFF0C\u300C\u4E0D\u662F\uFF0C\u4E0D\u662F\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u51B7\u54FC\u4E00\u58F0\uFF0C\u300C\u98CE\u5A4A\u5B50\u4E0D\u4F4F\u8FD9\u513F\uFF0C\u5C31\u53EA\u80FD\u56DE\u53BB\u8BA9\u4EBA\u968F\u4FBF\u64CD\u4E86\u2014\u2014\u4F60\u770B\u600E\u4E48\u529E\u3002\u300D</p><p>\u7D2B\u73AB\u770B\u7740\u5E08\u59D0\u80A9\u4E0A\u7684\u4F24\u75D5\uFF0C\u53C8\u60F3\u8D77\u77F3\u5BA4\u6392\u6210\u957F\u961F\u7684\u7537\u4EBA\uFF0C\u59D4\u5B9E\u96BE\u4EE5\u9009\u62E9\u3002</p><p>\u6155\u5BB9\u9F99\u5F15\u8BF1\u9053\uFF1A\u300C\u53EA\u8981\u4F60\u7ED9\u5979\u64E6\u4E0A\u836F\uFF0C\u518D\u5582\u4E0A\u51E0\u7C92\u836F\u3002\u6211\u4FDD\u8BC1\u8FD9\u4E9B\u72D7\u4E0D\u4F1A\u54AC\u5979\uFF0C\u4E5F\u4E0D\u4F1A\u518D\u6293\u5979\u3002\u300D</p><p>\u7D2B\u73AB\u626C\u8D77\u8138\uFF0C\u8BA4\u771F\u5730\u8BF4\uFF1A\u300C\u4F60\u4FDD\u8BC1\u5417\uFF1F\u300D</p><p>\u6155\u5BB9\u9F99\u70B9\u70B9\u5934\u3002</p><p>\u7D2B\u73AB\u4E00\u54AC\u7259\uFF0C\u7AD9\u4E86\u8D77\u6765\u3002\u53EA\u8981\u8FD9\u4E9B\u51F6\u6076\u7684\u5927\u72D7\u4E0D\u78B0\u5979\uFF0C\u5E08\u59D0\u80AF\u5B9A\u9009\u62E9\u8FD9\u95F4\u9ED1\u5C4B\uFF0C\u4E5F\u4E0D\u613F\u56DE\u53BB\u4EFB\u4EBA\u8E42\u8E8F\u3002</p><p>\u91CD\u65B0\u64E6\u5B8C\u836F\uFF0C\u7D2B\u73AB\u628A\u836F\u4E38\u653E\u5230\u5E08\u59D0\u5634\u91CC\uFF0C\u5582\u4E86\u4E9B\u6C34\u8BA9\u5979\u559D\u4E0B\u53BB\uFF0C\u5C0F\u58F0\u9053\uFF1A\u300C\u5927\u5E08\u59D0\uFF0C\u522B\u6015\uFF0C\u5403\u4E86\u836F\u5B83\u4EEC\u5C31\u4E0D\u4F1A\u78B0\u4F60\u4E86\u2026\u2026\u300D</p><p>\u98CE\u665A\u534E\u4ECD\u7136\u660F\u8FF7\u4E0D\u9192\uFF0C\u5BF9\u5E08\u59B9\u7684\u8BDD\u5145\u8033\u4E0D\u95FB\u3002\u7D2B\u73AB\u6CA1\u6709\u770B\u5230\uFF0C\u5979\u7D27\u7D27\u6342\u4F4F\u4E0B\u4F53\u7684\u624B\u6307\u95F4\uFF0C\u6CBE\u6EE1\u4E86\u767D\u8272\u7684\u9ECF\u6DB2\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u300C\u8FD9\u662F\u4EC0\u4E48\uFF1F\u300D\u6155\u5BB9\u7D2B\u73AB\u73B0\u5728\u5BF9\u836F\u7269\u7279\u522B\u654F\u611F\uFF0C\u770B\u5230\u767D\u6C0F\u59D0\u59B9\u5728\u6BCD\u4EB2\u8EAB\u4E0A\u6D82\u62B9\u4E1C\u897F\uFF0C\u7ACB\u5373\u51B2\u8FDB\u6765\u5389\u58F0\u95EE\u9053\u3002</p><p>\u59D0\u59B9\u4FE9\u8FDE\u5FD9\u505C\u624B\uFF0C\u767D\u7389\u9E42\u8F7B\u58F0\u8BF4\uFF1A\u300C\u8FD9\u662F\u5BAB\u4E3B\u7684\u5429\u5490\u3002\u6BCF\u5929\u7528\u8309\u8389\u82B1\u6CB9\u63BA\u9999\u7C89\u7ED9\u592B\u4EBA\u6309\u6469\u2026\u2026\u300D</p><p>\u7D2B\u73AB\u5C06\u4FE1\u5C06\u7591\uFF0C\u62FF\u8D77\u7389\u74F6\u95FB\u4E86\u95FB\u3002\u5165\u9F3B\u82AC\u82B3\u9999\u751C\uFF0C\u786E\u5B9E\u662F\u82B1\u4E2D\u63D0\u70BC\u7684\u7CBE\u6CB9\u3002</p><p>\u8FD9\u4E9B\u65E5\u5B50\u5979\u6015\u6BCD\u4EB2\u957F\u4E45\u9759\u5367\u4E0D\u52A8\uFF0C\u808C\u8089\u840E\u7F29\uFF0C\u6BCF\u5929\u90FD\u4E00\u8FB9\u4E0E\u6BCD\u4EB2\u804A\u5929\uFF0C\u4E00\u8FB9\u5E2E\u5979\u6309\u6469\uFF0C\u73B0\u5728\u6709\u7389\u83BA\u7389\u9E42\u5E2E\u5FD9\uFF0C\u5012\u66FF\u81EA\u5DF1\u5206\u62C5\u4E86\u4E49\u52A1\u3002\u7D2B\u73AB\u6B49\u610F\u5730\u8BF4\uFF1A\u300C\u54B1\u4EEC\u4E00\u8D77\u6765\u5427\u3002\u300D</p><p>\u64E6\u8FC7\u8309\u8389\u82B1\u6CB9\uFF0C\u8427\u4F5B\u5974\u808C\u80A4\u6108\u52A0\u6676\u83B9\u593A\u76EE\uFF0C\u7389\u5170\u822C\u82B3\u9999\u99A5\u90C1\u3002\u6574\u4E2A\u8EAB\u4F53\u4EFF\u4F5B\u5DE7\u593A\u5929\u5DE5\u7684\u60CA\u4E16\u4E4B\u4F5C\uFF0C\u534E\u7F8E\u52A8\u4EBA\u3002</p><p>\u81EA\u4ECE\u77E5\u9053\u6BCD\u4EB2\u6000\u5B55\u4E4B\u540E\uFF0C\u7D2B\u73AB\u5BF9\u5979\u7684\u8179\u90E8\u65F6\u65F6\u7559\u610F\uFF0C\u4F46\u53F6\u884C\u5357\u8BF4\u73B0\u5728\u8FD8\u4E0D\u8DB3\u6708\u6570\uFF0C\u5916\u8868\u770B\u4E0D\u51FA\u6765\u3002</p><p>\u5979\u5C0F\u5FC3\u5730\u8F7B\u8F7B\u6309\u6469\u5149\u6D01\u5982\u7389\u7684\u5C0F\u8179\uFF0C\u72B9\u8C6B\u7740\u8981\u4E0D\u8981\u628A\u8FD9\u4E8B\u544A\u8BC9\u6BCD\u4EB2\u3002\u4F46\u600E\u4E48\u5F00\u53E3\u5462\uFF1F\u96BE\u9053\u8BF4\uFF1A\u300C\u5A18\uFF0C\u54E5\u54E5\u628A\u4F60\u7684\u809A\u5B50\u5F04\u5927\u4E86\uFF1F\u300D\u5929\u554A\uFF0C\u8FD9\u4E2A\u5B7D\u79CD\u7B97\u4EC0\u4E48\u8EAB\u4EFD\u5462\uFF1F\u7B97\u662F\u5F1F\u5F1F\u8FD8\u662F\u4F84\u5B50\uFF1F\u7B97\u6765\u7B97\u53BB\uFF0C\u90FD\u662F\u591A\u4F59\u7684\u4E00\u4E2A\u2026\u2026\u4E00\u4E2A\u2026\u2026\u4E00\u4E2A\u4EC0\u4E48\u4E1C\u897F\u5462\uFF1F</p><p>\u7D2B\u73AB\u600E\u4E48\u7B97\u4E5F\u7B97\u4E0D\u51FA\u6765\uFF0C\u53EA\u597D\u5148\u653E\u5230\u4E00\u8FB9\u3002\u5979\u6316\u7A7A\u5FC3\u601D\u5730\u627E\u4E9B\u8BDD\u9898\u6765\u8BF4\uFF0C\u6BD4\u5982\u4ECA\u5929\u5929\u6C14\u597D\u70ED\uFF1B\u53F6\u8001\u5934\u767D\u80E1\u5B50\u53C8\u591A\u4E00\u6839\uFF0C\u773C\u770B\u5C31\u6D3B\u4E0D\u957F\u4E86\u2026\u2026\u80E1\u626F\u516B\u9053\u9017\u6BCD\u4EB2\u5F00\u5FC3\u3002</p><p>\u8427\u4F5B\u5974\u5AE3\u7136\u4E00\u7B11\uFF0C\u7F8E\u8273\u7684\u8138\u5E9E\u5B9B\u5982\u5947\u82B1\u521D\u7EFD\uFF0C\u6D41\u5149\u6EA2\u5F69\u4EE4\u4EBA\u9B42\u9500\u9B44\u6563\u3002</p><p>\u7D2B\u73AB\u4E00\u4E0B\u770B\u5446\u4E86\uFF0C\u534A\u664C\u624D\u9053\uFF1A\u300C\u5A18\uFF0C\u4F60\u597D\u6F02\u4EAE\u2026\u2026\u300D</p><p>\u300C\u5A18\u5DF2\u7ECF\u8001\u4E86\u3002\u73AB\u513F\uFF0C\u4F60\u957F\u5F97\u8DDF\u5A18\u5E74\u8F7B\u65F6\u5019\u4E00\u6A21\u4E00\u6837\u2026\u2026\u4F46\u4F60\u6BD4\u5A18\u80FD\u5E72\u5F97\u591A\u2026\u2026\u300D\u8427\u4F5B\u5974\u8BF4\u7740\u5634\u5507\u98A4\u6296\u8D77\u6765\u3002</p><p>\u7D2B\u73AB\u8FDE\u5FD9\u5C94\u5F00\u8BDD\u9898\uFF0C\u54C4\u6BCD\u4EB2\u7761\u89C9\u3002</p><p>\u5E2E\u767E\u82B1\u89C2\u97F3\u64E6\u5B8C\u8EAB\u5B50\uFF0C\u7D2B\u73AB\u53EB\u4F4F\u6B63\u8981\u79BB\u5F00\u7684\u767D\u6C0F\u59D0\u59B9\uFF0C\u300C\u5C0F\u83BA\u5C0F\u9E42\uFF0C\u90A3\u6DF7\u86CB\u7684\u5200\u662F\u4E0D\u662F\u8FD8\u5E26\u5728\u8EAB\u4E0A\uFF1F\u300D</p><p>\u767D\u7389\u83BA\u5782\u4E0B\u5934\uFF0C\u6CA1\u6709\u4F5C\u58F0\u3002</p><p>\u767D\u7389\u9E42\u5C0F\u58F0\u9053\uFF1A\u300C\u5C11\u592B\u4EBA\u2026\u2026\u5974\u5A62\u4E0D\u6562\u2026\u2026\u300D</p><p>\u7D2B\u73AB\u4E00\u6115\uFF0C\u4F46\u770B\u5230\u59D0\u59B9\u4FE9\u4E73\u623F\u76F8\u8FDE\u7684\u75DB\u82E6\u6A21\u6837\uFF0C\u5979\u53EA\u53F9\u4E86\u53E3\u6C14\uFF0C\u633D\u4F4F\u4E24\u4EBA\u7684\u5C0F\u624B\u634F\u4E86\u4E00\u628A\uFF0C\u575A\u5B9A\u5730\u8BF4\uFF1A\u300C\u4E0D\u7528\u6015\u3002\u603B\u6709\u4E00\u5929\uFF0C\u6211\u4F1A\u628A\u5927\u5BB6\u90FD\u6551\u51FA\u53BB\uFF01\u300D</p><p>\u7531\u4E8E\u7D2B\u73AB\u7684\u575A\u6301\uFF0C\u6155\u5BB9\u9F99\u53EA\u597D\u8BA9\u5979\u4E00\u4EBA\u72EC\u5C45\u4E3B\u5BA4\uFF0C\u81EA\u5DF1\u5148\u4F4F\u5728\u7EAA\u7709\u59A9\u548C\u767D\u6C0F\u59D0\u59B9\u4E4B\u95F4\u7684\u5929\u5B57\u58EC\u5BA4\u3002</p><p>\u7D2B\u73AB\u53C8\u4E00\u6B21\u4ECE\u4ED6\u95E8\u4E0A\u9893\u7136\u62D4\u4E0B\u94A5\u5319\uFF0C\u6697\u9A82\u5F53\u521D\u7684\u8BBE\u8BA1\u8005\u592A\u4E0D\u50CF\u8BDD\uFF0C\u7ADF\u7136\u628A\u6BCF\u95F4\u623F\u5B50\u7684\u9501\u90FD\u5F04\u5F97\u4E0D\u4E00\u6837\uFF0C\u8FD9\u7B97\u4EC0\u4E48\u4E8B\u561B\u3002\u5979\u628A\u4E3B\u5BA4\u7684\u94A5\u5319\u63E3\u5230\u6000\u91CC\uFF0C\u633A\u80F8\u671D\u53F6\u884C\u5357\u623F\u95F4\u8D70\u53BB\u3002</p><p>\u53F6\u884C\u5357\u62C9\u5F00\u95E8\uFF0C\u6709\u6C14\u65E0\u529B\u5730\u8BF4\uFF1A\u300C\u5C11\u592B\u4EBA\uFF0C\u5DF2\u7ECF\u4EA5\u672B\u65F6\u5206\uFF0C\u6709\u4E8B\u660E\u5929\u518D\u8BF4\u597D\u4E0D\u597D\uFF1F\u300D</p><p>\u300C\u4E0D\u597D\uFF01\u300D\u6155\u5BB9\u7D2B\u73AB\u5927\u6A21\u5927\u6837\u5730\u5750\u5230\u53F6\u884C\u5357\u7684\u6905\u4E2D\uFF0C\u62C9\u5F00\u4E39\u7089\u77A7\u4E86\u77A7\uFF0C\u300C\u546F\u300D</p><p>\u7684\u5173\u4E0A\uFF1B\u53C8\u62FF\u8D77\u6848\u4E0A\u7684\u836F\u74F6\uFF0C\u628A\u91CC\u9762\u7684\u836F\u4E38\u5012\u51FA\u6765\uFF0C\u4E00\u4E94\u4E00\u5341\u7684\u6570\u4E86\u4E00\u904D\uFF0C\u8FD9\u624D\u6162\u60A0\u60A0\u8BF4\u9053\uFF1A\u300C\u59D3\u53F6\u7684\uFF0C\u6211\u7EAA\u5E08\u59D0\u7528\u7684\u836F\u662F\u4F60\u5236\u7684\u5417\uFF1F\u300D</p><p>\u53F6\u884C\u5357\u5E72\u54B3\u4E00\u58F0\uFF0C\u9762\u4E0D\u6539\u5BB9\u5730\u8BF4\u9053\uFF1A\u300C\u90A3\u662F\u6559\u4E2D\u6240\u4F20\u836F\u65B9\uFF0C\u8001\u592B\u53EA\u662F\u4F9D\u6CD5\u914D\u5236\uFF0C\u5949\u5BAB\u4E3B\u4E4B\u547D\u7ED9\u7EAA\u59D1\u5A18\u4F7F\u7528\u3002\u300D</p><p>\u8001\u5BB6\u4F19\u5F20\u5F00\u53E3\u4FBF\u63A8\u7684\u4E00\u5E72\u4E8C\u51C0\uFF0C\u7D2B\u73AB\u51B7\u7B11\u9053\uFF1A\u300C\u662F\u4F60\u914D\u7684\u5C31\u597D\u2014\u2014\u4E45\u95FB\u53F6\u62A4\u6CD5\u533B\u672F\u901A\u795E\uFF0C\u90A3\u5C31\u9EBB\u70E6\u4F60\u518D\u914D\u4E00\u526F\u89E3\u836F\u3002\u8FC7\u4E24\u5929\u7ED9\u6211\u9001\u6765\u3002\u300D</p><p>\u53F6\u884C\u5357\u77A0\u76EE\u7ED3\u820C\uFF0C\u711A\u60C5\u818F\u7A77\u4ED6\u5341\u5E74\u4E4B\u529B\u65B9\u624D\u5236\u51FA\uFF0C\u5BF9\u5B83\u7684\u836F\u6027\u81EA\u5DF1\u4E86\u5982\u6307\u638C\uFF0C\u4E00\u65E6\u751F\u6548\uFF0C\u7EDD\u5BF9\u65E0\u6CD5\u89E3\u9664\u3002\u4F46\u8FD9\u8BDD\u5343\u4E07\u53EF\u4E0D\u80FD\u8BF4\uFF0C\u8981\u8BA9\u8FD9\u4E2B\u5934\u77E5\u9053\u711A\u60C5\u818F\u771F\u662F\u81EA\u5DF1\u4E00\u624B\u70AE\u5236\u7684\uFF0C\u6050\u6015\u5979\u7ACB\u523B\u5C31\u8981\u52A8\u624B\u70E7\u623F\u5B50\u3002</p><p>\u8E0C\u8E87\u534A\u664C\uFF0C\u53F6\u884C\u5357\u6B63\u5BB9\u9053\uFF1A\u300C\u5C11\u592B\u4EBA\u6709\u4EE4\uFF0C\u5728\u4E0B\u81EA\u7136\u9075\u4ECE\u3002\u4F46\u6B64\u836F\u662F\u4E0A\u53E4\u79D8\u65B9\uFF0C\u5728\u4E0B\u6CA1\u6709\u628A\u63E1\u80FD\u914D\u51FA\u89E3\u836F\u2026\u2026\u300D</p><p>\u7D2B\u73AB\u6643\u7740\u8111\u888B\uFF0C\u6DE1\u6DE1\u9053\uFF1A\u300C\u5C31\u7B97\u914D\u4E0D\u51FA\uFF0C\u6211\u4E5F\u4E0D\u80FD\u628A\u4F60\u5403\u4E86\u2014\u2014\u662F\u5417\uFF1F\u300D</p><p>\u53F6\u884C\u5357\u6B63\u88AB\u5979\u8BF4\u4E2D\u5FC3\u4E8B\uFF0C\u4E0D\u7981\u8001\u8138\u4E00\u7EA2\uFF0C\u8FDE\u5FD9\u9053\uFF1A\u300C\u5728\u4E0B\u4E00\u5B9A\u5C3D\u529B\u800C\u4E3A\uFF0C\u8BF7\u5C11\u592B\u4EBA\u653E\u5FC3\u3002\u300D</p><p>\u7D2B\u73AB\u65E0\u53EF\u65E0\u4E0D\u53EF\u7684\u51B7\u54FC\u4E00\u58F0\uFF0C\u300C\u6211\u5927\u5E08\u59D0\u7528\u7684\u836F\u4E5F\u662F\u4F60\u914D\u7684\u5417\uFF1F\u300D</p><p>\u53F6\u884C\u5357\u8FDE\u8FDE\u70B9\u5934\uFF0C\u89E3\u91CA\u9053\uFF1A\u300C\u90A3\u836F\u53EA\u662F\u72AC\u5C3F\u91CC\u63BA\u4E86\u4E00\u4E9B\u767D\u6C0F\uFF0C\u8BA9\u6C14\u5473\u80FD\u4FDD\u5B58\u4E09\u5929\u4EE5\u4E0A\uFF0C\u907F\u514D\u72AC\u53EA\u4F24\u4EBA\uFF0C\u7EDD\u65E0\u5371\u5BB3\u3002\u300D\u4ED6\u6CA1\u8BE6\u7EC6\u8BF4\u660E\uFF0C\u90A3\u5C3F\u6DB2\u662F\u53D1\u60C5\u6BCD\u72D7\u7684\u5C3F\u6DB2\u3002</p><p>\u300C\u6211\u5E08\u59D0\u5403\u7684\u836F\u662F\u4EC0\u4E48\uFF1F\u300D</p><p>\u300C\u53EA\u662F\u5B89\u795E\u9759\u5FC3\u7684\u4E38\u5242\uFF0C\u6015\u98CE\u59D1\u5A18\u5728\u9ED1\u6697\u91CC\u5F85\u7684\u65F6\u95F4\u957F\u4E86\uFF0C\u5FC3\u795E\u4E0D\u5B81\uFF0C\u60CA\u52A8\u90A3\u4E9B\u755C\u7272\uFF0C\u9020\u6210\u8BEF\u4F24\u3002\u300D\u8FD9\u4E2A\u4ED6\u5012\u6CA1\u5938\u5927\u836F\u6548\uFF0C\u53EA\u662F\u628A\u5931\u795E\u4E39\u7684\u529F\u6548\u8D2C\u4F4E\u4E86\u3002\u4F55\u6B62\u5B89\u795E\u9759\u5FC3\uFF0C\u957F\u4E45\u670D\u7528\uFF0C\u4F1A\u628A\u4EBA\u53D8\u6210\u4E27\u795E\u5931\u5FC3\u7684\u884C\u5C38\u8D70\u8089\u3002</p><p>\u7D2B\u73AB\u4E5F\u96BE\u8FA8\u771F\u5047\uFF0C\u4E8E\u662F\u8F6C\u8FC7\u8BDD\u9898\uFF0C\u53C8\u95EE\u9053\uFF1A\u300C\u6211\u5A18\u7528\u7684\u836F\u4E5F\u662F\u4F60\u914D\u7684\u5427\uFF1F\u300D</p><p>\u53F6\u884C\u5357\u8FD9\u4F1A\u513F\u662F\u6EE1\u5FC3\u540E\u6094\uFF0C\u5F53\u5E74\u5B66\u4EC0\u4E48\u4E0D\u597D\uFF0C\u975E\u8981\u5B66\u533B\uFF1F\u8981\u8DDF\u8001\u6C90\u90A3\u6837\u50BB\u547C\u547C\u7684\u4E0B\u6B7B\u52B2\u7EC3\u4ED6\u4E00\u8EAB\u672C\u9886\uFF0C\u4F55\u5FC5\u53D7\u8FD9\u4EFD\u7F6A\uFF1F\u8D70\u4E86\u4E94\u5341\u591A\u5E74\u7684\u5F2F\u8DEF\u554A\u2026\u2026</p><p>\u300C\u592B\u4EBA\u7528\u7684\u662F\u8309\u8389\u82B1\u6CB9\u52A0\u82CF\u5408\u9999\uFF0C\u6709\u52A9\u4E8E\u8840\u8109\u901A\u7545\uFF0C\u62A4\u80A4\u751F\u808C\uFF0C\u6D88\u9664\u6591\u7EB9\uFF0C\u4FDD\u517B\u8EAB\u4F53\uFF0C\u5EF6\u7F13\u8870\u8001\uFF0C\u8FD8\u80FD\u8C03\u6C14\u517B\u989C\uFF0C\u6ECB\u9634\u58EE\u9633\u2026\u2026\u300D\u4ED6\u7D6E\u7D6E\u53FC\u53FC\u8BF4\u4E86\u534A\u5929\uFF0C\u7B49\u7D2B\u73AB\u4E0D\u8010\u70E6\u7684\u76B1\u8D77\u7709\u5934\u624D\u4F4F\u53E3\uFF0C\u6700\u540E\u53C8\u52A0\u4E00\u53E5\uFF1A\u300C\u767E\u76CA\u800C\u65E0\u4E00\u5BB3\u3002\u300D\u4ED6\u6CA1\u6709\u628A\u597D\u5904\u8BF4\u5168\uFF0C\u9664\u4E86\u4E0A\u9762\u8FD9\u4E9B\uFF0C\u8FD9\u836F\u8FD8\u80FD\u5B89\u80CE\u5B81\u795E\uFF0C\u6700\u91CD\u8981\u7684\u662F\u80FD\u4E30\u4E73\u50AC\u5976\u2026\u2026</p><p>\u7D2B\u73AB\u4ECE\u9F3B\u5B54\u91CC\u53D1\u51FA\u4E00\u58F0\u8F7B\u8511\u7684\u51B7\u7B11\uFF0C\u300C\u8FD9\u4E48\u597D\u2014\u2014\u4F60\u90A3\u5F20\u8001\u8138\u600E\u4E48\u5C31\u4E0D\u77E5\u9053\u7528\u4E9B\u5462\uFF1F\u300D</p><p>\u53F6\u884C\u5357\u6C14\u5F97\u5410\u8840\uFF0C\u534A\u664C\u624D\u6324\u51FA\u4E00\u4E1D\u52C9\u5F3A\u7684\u7B11\u5BB9\uFF0C\u300C\u5C11\u592B\u4EBA\u8BF4\u7B11\u4E86\u3002\u300D</p><p>\u300C\u54CE\u2014\u2014\u300D\u7D2B\u73AB\u5927\u5EA6\u5730\u6446\u6446\u624B\uFF0C\u539F\u8C05\u4E86\u4ED6\u7684\u65E0\u77E5\uFF0C\u300C\u6211\u53EF\u6CA1\u6709\u8BF4\u7B11\u3002\u6211\u662F\u5173\u5FC3\u53F6\u62A4\u6CD5\u2014\u2014\u65E2\u7136\u836F\u8FD9\u4E48\u597D\uFF0C\u6BCF\u6B21\u4F60\u5148\u7528\u4E00\u4E9B\uFF0C\u4E5F\u6ECB\u8865\u6ECB\u8865\u3002\u5269\u4E0B\u7684\u6211\u518D\u7ED9\u5A18\u62FF\u53BB\u3002\u300D</p><p>\u53F6\u884C\u5357\u5982\u4E94\u96F7\u8F70\u9876\uFF0C\u4ED6\u7D27\u5F20\u5728\u8111\u6D77\u91CC\u5206\u6790\u836F\u7269\u7684\u5404\u79CD\u6210\u5206\u2026\u2026\u5E94\u8BE5\u8BF4\u5BF9\u81EA\u5DF1\u65E0\u5BB3\u5427\uFF1F</p><p>\u7D2B\u73AB\u8DF3\u8D77\u6765\uFF0C\u62CD\u62CD\u624B\u4E0A\u7684\u836F\u6E23\uFF0C\u5BA3\u5E03\u9053\uFF1A\u300C\u5C31\u8FD9\u4E48\u5B9A\u4E86\uFF01\u591C\u6DF1\u4E86\uFF0C\u53F6\u62A4\u6CD5\u65E9\u4E9B\u4F11\u606F\uFF0C\u4E0D\u8981\u8D2A\u73A9\u3002\u300D\u8BF4\u7F62\u98CE\u59FF\u7EF0\u7EA6\u5730\u51FA\u95E8\u800C\u53BB\u3002</p><p>\u5269\u4E0B\u53F6\u884C\u5357\u6123\u6123\u770B\u7740\u6848\u4E0A\u3002\u4E00\u76F4\u5149\u987E\u7740\u8BF4\u8BDD\uFF0C\u90A3\u4E9B\u521A\u914D\u597D\u7684\u836F\u4E38\u4E0D\u77E5\u4E0D\u89C9\u90FD\u88AB\u5C11\u592B\u4EBA\u637B\u6210\u4E86\u788E\u672B\u3002</p>',66);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
