import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_49" tabindex="-1"><a class="header-anchor" href="#_49" aria-hidden="true">#</a> 49</h1><p>\u53F7\u89D2\u58F0\u8D77\uFF0C\u4E00\u8258\u521A\u9020\u6210\u4E0D\u4E45\u7684\u8C6A\u534E\u5927\u8239\u4ECE\u8FDC\u5904\u9A76\u6765\u3002</p><p>\u6155\u5BB9\u9F99\u7ACB\u5728\u88C5\u9970\u4E00\u65B0\u7684\u795E\u6BBF\u524D\uFF0C\u51DD\u89C6\u7247\u523B\uFF0C\u7136\u540E\u98D8\u8EAB\u56DE\u5230\u6BBF\u5185\u3002</p><p>\u5341\u4F59\u540D\u4EBA\u8DC3\u4E0B\u5927\u8239\uFF0C\u7531\u5C60\u6000\u6C89\u966A\u540C\u4E00\u8DEF\u671D\u6000\u6708\u5CF0\u4E0B\u7684\u795E\u6BBF\u8D70\u6765\u3002\u8FD9\u4E9B\u4EBA\u9AD8\u9AD8\u77EE\u77EE\uFF0C\u670D\u8272\u5404\u5F02\uFF0C\u4F46\u5747\u662F\u9762\u5E26\u90AA\u6C14\uFF0C\u76EE\u9732\u51F6\u5149\u3002</p><p>\u8840\u65A9\u53CC\u715E\u95EF\u8361\u6C5F\u6E56\u591A\u5E74\uFF0C\u5BF9\u661F\u6708\u6E56\u9690\u7EA6\u6709\u6240\u8033\u95FB\u3002\u63A5\u5230\u9080\u8BF7\u540E\uFF0C\u4FBF\u4E0E\u5341\u4F59\u540D\u9ED1\u9053\u9AD8\u624B\u7B2C\u4E00\u6279\u8D76\u6765\u3002\u4E24\u4EBA\u7EB5\u6A2A\u6DEE\u6CB3\u4E00\u5E26\uFF0C\u52AB\u8D22\u8D8A\u8D27\uFF0C\u6740\u4EBA\u5982\u9EBB\uFF0C\u4E00\u5411\u76EE\u4E2D\u65E0\u4EBA\uFF0C\u773C\u89C1\u661F\u6708\u6E56\u504C\u5927\u7684\u57FA\u4E1A\uFF0C\u4ECD\u662F\u4E00\u5E45\u4E0D\u4EE5\u4E3A\u7136\u7684\u795E\u6001\u3002</p><p>\u5C60\u6000\u6C89\u5FC3\u4E0B\u96EA\u4EAE\uFF0C\u4F46\u8138\u4E0A\u8FD8\u662F\u5806\u6EE1\u7B11\u5BB9\uFF0C\u70ED\u60C5\u5730\u8DDF\u4F17\u4EBA\u4E00\u4E00\u5BD2\u55A7\u95EE\u597D\u3002</p><p>\u4EC7\u767E\u718A\u8146\u7740\u809A\u5B50\uFF0C\u5927\u5587\u5587\u9053\uFF1A\u300C\u542C\u8BF4\u4F60\u4EEC\u661F\u6708\u6E56\u64D2\u4F4F\u4E86\u6D41\u971C\u5251\u2014\u2014\u53EF\u662F\u771F\u7684\uFF1F\u300D</p><p>\u5C60\u6000\u6C89\u7B11\u5475\u5475\u9053\uFF1A\u300C\u662F\u771F\u662F\u5047\u7A0D\u540E\u4FBF\u77E5\uFF0C\u8BF8\u4F4D\u8BF7\u3002\u300D</p><p>\u4EC7\u767E\u9CCC\u51B7\u54FC\u4E00\u58F0\uFF0C\u5FC3\u9053\u6D41\u971C\u5251\u8FD8\u6709\u4E2A\u5E08\u59B9\u5BD2\u6708\u5200\u6797\u9999\u8FDC\uFF0C\u4E0A\u9762\u8FD8\u6709\u96EA\u5CF0\u795E\u5C3C\uFF0C\u4F60\u661F\u6708\u6E56\u5C0F\u5FC3\u597D\u5403\u96BE\u6D88\u5316\u3002</p><p>\u4E0A\u5CB8\u8D70\u4E86\u91CC\u8BB8\uFF0C\u6797\u4E2D\u51FA\u73B0\u4E00\u5EA7\u9AD8\u5927\u7684\u6C49\u767D\u7389\u7891\u574A\uFF0C\u574A\u4E0A\u523B\u7740\u300C\u6F5C\u5E7D\u300D\u4E8C\u5B57\u3002</p><p>\u4E00\u884C\u4EBA\u6765\u5230\u574A\u524D\uFF0C\u5747\u662F\u53CC\u76EE\u4E00\u4EAE\u3002</p><p>\u4E00\u4E2A\u7EA2\u8863\u5C11\u5973\u4FCF\u751F\u751F\u7ACB\u5728\u574A\u4E0B\uFF0C\u7CBE\u7F8E\u7684\u4E94\u5B98\u5B9B\u5982\u671D\u9633\u4E0B\u7684\u6C34\u6676\uFF0C\u5149\u8292\u56DB\u5C04\u3002</p><p>\u5979\u5F88\u4E0D\u6DD1\u5973\u7684\u4E24\u624B\u62B1\u81C2\uFF0C\u4F46\u914D\u5408\u7740\u5A40\u5A1C\u6709\u81F4\u7684\u5A07\u8EAF\uFF0C\u522B\u6709\u4E00\u756A\u98CE\u6D41\u5A49\u8F6C\u3002\u5982\u6C34\u7684\u79CB\u6CE2\u4E00\u8F6C\uFF0C\u5C11\u5973\u4F38\u51FA\u4E00\u53EA\u7693\u5982\u660E\u6708\u7684\u7389\u8155\uFF0C\u7EA4\u7EA4\u7389\u6307\u70B9\u5411\u4EC7\u6C0F\u5144\u5F1F\uFF0C\u300C\u4F60\uFF0C\u65C1\u8FB9\u6751\u91CC\u7684\uFF1F\u300D</p><p>\u8840\u65A9\u53CC\u715E\u5446\u5446\u70B9\u4E86\u70B9\u5934\uFF0C\u65CB\u5373\u5927\u6447\u5176\u5934\uFF0C\u671F\u671F\u827E\u827E\u5730\u8BF4\uFF1A\u300C\u4E0D\u2026\u2026\u4E0D\u662F\u2026\u2026\u6211\u2026\u2026\u5728\u4E0B\u2026\u2026\u300D</p><p>\u5C11\u5973\u4E0D\u8010\u70E6\u5730\u622A\u65AD\u4E24\u4EBA\u7684\u8BDD\u5934\uFF0C\u300C\u4E0D\u662F\u6751\u91CC\u7684\uFF0C\u80CC\u7740\u9570\u5200\u5E72\u561B\uFF1F\u300D</p><p>\u4E24\u4EBA\u6123\u4E86\u7247\u523B\uFF0C\u987F\u65F6\u52C3\u7136\u5927\u6012\u3002\u4E24\u4EBA\u7684\u8840\u65A9\u4E5F\u662F\u6B66\u6797\u4E2D\u7684\u6210\u540D\u5175\u5668\uFF0C\u4E0D\u77E5\u996E\u8FC7\u591A\u5C11\u82F1\u96C4\u8C6A\u6770\u7684\u9C9C\u8840\uFF0C\u6CA1\u60F3\u5230\u7ADF\u88AB\u4E00\u4E2A\u4E73\u81ED\u672A\u5E72\u7684\u5C0F\u4E2B\u5934\u5F53\u6210\u4E86\u9570\u5200\uFF01</p><p>\u300C\u5C0F\u8D31\u4EBA\uFF01\u300D\u4EC7\u767E\u718A\u6012\u543C\u4E00\u58F0\uFF0C\u957F\u6EE1\u9ED1\u6BDB\u7684\u5927\u624B\u5E26\u7740\u51CC\u5389\u7684\u52B2\u98CE\uFF0C\u671D\u5C11\u5973\u767D\u767D\u5AE9\u5AE9\u7684\u67D4\u9888\u4E2D\u6293\u53BB\u3002</p><p>\u624B\u81C2\u521A\u521A\u4F38\u624B\uFF0C\u5FFD\u7136\u8155\u4E0A\u4E00\u7D27\uFF0C\u5C60\u6000\u6C89\u7B11\u563B\u563B\u9053\uFF1A\u300C\u4EC7\u5927\u4FA0\u4E14\u83AB\u52A8\u6012\u2026\u2026\u300D</p><p>\u4EC7\u767E\u718A\u6839\u672C\u4E0D\u628A\u8FD9\u4E2A\u77EE\u80D6\u5B50\u653E\u5728\u773C\u91CC\uFF0C\u4F46\u8FDE\u8FD0\u4E09\u6B21\u529B\u9053\u90FD\u5982\u77F3\u6C89\u5927\u6D77\uFF0C\u624B\u6307\u786C\u662F\u9012\u4E0D\u51FA\u534A\u5BF8\u3002</p><p>\u4EC7\u767E\u9CCC\u817E\u8EAB\u800C\u8D77\uFF0C\u5341\u6307\u7B95\u5F20\uFF0C\u6076\u72E0\u72E0\u5730\u6251\u4E86\u8FC7\u53BB\uFF0C\u51C6\u5907\u5148\u62E7\u65AD\u5C0F\u8D31\u4EBA\u4E00\u6761\u8180\u5B50\u518D\u8BF4\u3002\u5C11\u5973\u5BF9\u4ED6\u51F6\u731B\u7684\u6765\u52BF\u7406\u90FD\u4E0D\u7406\uFF0C\u53CD\u800C\u4FA7\u8FC7\u8138\u4E0D\u5C51\u5730\u51B7\u7B11\u4E00\u58F0\u3002\u4EC7\u767E\u9CCC\u6C14\u5F97\u4E24\u773C\u5192\u706B\uFF0C\u624B\u4E0A\u53C8\u52A0\u4E86\u4E09\u5206\u529B\u9053\u3002</p><p>\u4E24\u540D\u7D2B\u8863\u4F8D\u8005\u4ECE\u5C11\u5973\u80CC\u540E\u500F\u5FFD\u95EA\u51FA\uFF0C\u5404\u51FA\u4E00\u624B\u786C\u751F\u751F\u6321\u4F4F\u4EC7\u767E\u9CCC\u7684\u94C1\u638C\u3002\u300C\u817E\u300D\u7684\u4E00\u58F0\u95F7\u54CD\uFF0C\u4EC7\u767E\u9CCC\u8E09\u8DC4\u7740\u843D\u5728\u5730\u4E0A\uFF0C\u90A3\u4E24\u540D\u7D2B\u8863\u4F8D\u8005\u5219\u8FDE\u9000\u6570\u6B65\u624D\u7A33\u4F4F\u8EAB\u5F62\u3002\u770B\u4E0A\u53BB\u4EC7\u767E\u9CCC\u5360\u4E86\u4E0A\u98CE\uFF0C\u4F46\u5BF9\u65B9\u53EA\u662F\u661F\u6708\u6E56\u5C0F\u5352\uFF0C\u8FD9\u8138\u9762\u53EF\u4E22\u5927\u4E86\u3002\u4ED6\u51F6\u6027\u5927\u53D1\uFF0C\u53CD\u624B\u62D4\u51FA\u8840\u65A9\u3002</p><p>\u767D\u8863\u4E00\u95EA\uFF0C\u4E00\u4E2A\u7537\u5B50\u98DE\u53F6\u822C\u8F7B\u98D8\u98D8\u843D\u5728\u5C11\u5973\u8EAB\u524D\uFF0C\u4E00\u63D6\u5230\u5E95\u606D\u656C\u5730\u8BF4\u9053\uFF1A\u300C\u9601\u4E0B\u606F\u6012\u3002\u300D</p><p>\u4EC7\u767E\u9CCC\u773C\u4E2D\u51F6\u5149\u95EA\u52A8\uFF0C\u8FD9\u5BB6\u4F19\u80CC\u540E\u7A7A\u95E8\u5927\u9732\uFF0C\u81EA\u5DF1\u4E00\u65A9\u51FB\u51FA\uFF0C\u6709\u5341\u4E8C\u6210\u628A\u63E1\u5728\u4ED6\u80CC\u4E0A\u5F00\u4E2A\u900F\u660E\u7A9F\u7ABF\u3002\u53EF\u662F\u767D\u8863\u4EBA\u6709\u6301\u65E0\u6050\u7684\u6837\u5B50\uFF0C\u5374\u8BA9\u4ED6\u72B9\u8C6B\u8D77\u6765\u3002</p><p>\u5E2E\u5FD9\u8FCE\u5BBE\u7684\u767D\u94F6\u9999\u4E3B\u633A\u8170\u7B11\u9053\uFF1A\u300C\u6211\u7ED9\u5927\u5BB6\u4ECB\u7ECD\u4E00\u4E0B\uFF0C\u8FD9\u4E24\u4F4D\u662F\u540D\u9707\u6C5F\u6DEE\u7684\u8840\u65A9\u53CC\u715E\u4EC7\u2026\u2026\u300D</p><p>\u300C\u5601\uFF01\u300D\u5C11\u5973\u4E00\u8138\u8BA5\u7B11\u5730\u8BF4\u9053\uFF1A\u300C\u8FDE\u4F60\u4EEC\u8FD9\u4E9B\u7B28\u86CB\u90FD\u6253\u4E0D\u8FC7\uFF0C\u7B97\u4EC0\u4E48\u4E1C\u897F\uFF01\u8FD8\u540D\u9707\u6C5F\u6DEE\uFF0C\u5478\uFF01\u300D</p><p>\u5C60\u6000\u6C89\u677E\u5F00\u4EC7\u767E\u718A\u7684\u624B\u8155\uFF0C\u5148\u957F\u7B11\u7740\u5316\u89E3\u573A\u4E2D\u7684\u5C34\u5C2C\uFF0C\u7136\u540E\u8BF4\uFF1A\u300C\u4E24\u4F4D\u83AB\u602A\uFF0C\u8FD9\u662F\u9119\u6559\u5C11\u592B\u4EBA\u73AB\u7470\u2026\u2026\u300D</p><p>\u6155\u5BB9\u7D2B\u73AB\u5C0F\u86EE\u8170\u4E00\u626D\uFF0C\u4EF0\u7740\u8138\u626C\u957F\u800C\u53BB\u3002\u4E24\u540D\u7D2B\u8863\u4F8D\u8005\u8FDE\u5FD9\u8DDF\u5728\u540E\u9762\u3002</p><p>\u4E00\u884C\u4EBA\u76F4\u52FE\u52FE\u770B\u7740\u7EA2\u88D9\u4E2D\u65F6\u9690\u65F6\u73B0\u7684\u7389\u817F\uFF0C\u90FD\u6123\u4F4F\u4E86\u3002\u534A\u664C\u624D\u56DE\u8FC7\u795E\u6765\uFF0C\u300C\u2026\u2026\u5979\u5C31\u662F\u98D8\u6885\u5CF0\u7684\u5173\u95E8\u5F1F\u5B50\uFF0C\u73AB\u7470\u4ED9\u5B50\u6155\u5BB9\u7D2B\u73AB\u2026\u2026\u771F\u662F\u82B1\u5BB9\u6708\u8C8C\uFF0C\u56FD\u8272\u5929\u9999\u2026\u2026\u5C31\u662F\u8FD9\u813E\u6C14\u2014\u2014\u600E\u4E48\u4E5F\u4E0D\u50CF\u662F\u5A5A\u793C\u7684\u65B0\u5A18\u554A\uFF1F\u300D</p><p>\u5C60\u6000\u6C89\u6253\u7740\u54C8\u54C8\u89E3\u91CA\u9053\uFF1A\u300C\u5C11\u592B\u4EBA\u5E74\u5E7C\u597D\u987D\uFF0C\u5404\u4F4D\u83AB\u602A\u83AB\u602A\uFF0C\u5305\u6DB5\u5305\u6DB5\uFF0C\u8BF7\u8BF7\u8BF7\u2026\u2026\u300D</p><p>\u7D2B\u73AB\u8D8A\u8D70\u8D8A\u5FEB\uFF0C\u6C14\u51B2\u51B2\u5954\u5165\u795E\u6BBF\u3002</p><p>\u6155\u5BB9\u9F99\u51FA\u795E\u5730\u5BA1\u89C6\u7740\u5DE8\u67F1\u4E0A\u866C\u5C48\u7684\u87E0\u9F99\uFF0C\u95FB\u58F0\u6DE1\u6DE1\u9053\uFF1A\u300C\u600E\u4E48\u4E86\uFF1F\u751F\u8C01\u7684\u6C14\u5462\uFF1F\u300D</p><p>\u7D2B\u73AB\u59D4\u5C48\u5730\u8BF4\uFF1A\u300C\u4ED6\u4EEC\u9A82\u6211\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u5947\u9053\uFF1A\u300C\u8C01\u6562\u9A82\u4F60\uFF1F\u300D</p><p>\u300C\u90A3\u4E24\u4E2A\u62FF\u9570\u5200\u7684\uFF01\u300D\u7D2B\u73AB\u54C7\u7684\u54ED\u4E86\u8D77\u6765\uFF0C\u300C\u4ED6\u4EEC\u9A82\u6211\u5C0F\u8D31\u4EBA\uFF0C\u8FD8\u8981\u6253\u6211\u2026\u2026\u8FD8\u6709\u5C60\u80D6\u5B50\uFF01\u4ED6\u8FD8\u5728\u4E00\u8FB9\u7B11\uFF01\u4ED6\u4EEC\u90FD\u6B3A\u8D1F\u6211\u2026\u2026\u4F60\u53BB\u628A\u4ED6\u4EEC\u90FD\u6740\u4E86\uFF01\u300D</p><p>\u8FD9\u8BDD\u6155\u5BB9\u9F99\u5012\u6709\u4E5D\u5206\u4E0D\u4FE1\uFF0C\u4F46\u59B9\u59B9\u54ED\u8FD9\u4E48\u54CD\uFF0C\u53EA\u597D\u54C4\u7740\u8BF4\uFF1A\u300C\u5148\u522B\u54ED\uFF0C\u56DE\u5934\u54E5\u54E5\u7ED9\u4F60\u51FA\u6C14\u3002\u300D</p><p>\u7D2B\u73AB\u62BD\u564E\u7740\u6536\u4F4F\u6CEA\uFF0C\u300C\u54E5\u54E5\uFF0C\u4F60\u4E00\u5B9A\u8981\u7ED9\u59B9\u59B9\u51FA\u6C14\u554A\u2026\u2026\u300D\u542C\u8BF4\u4E0A\u5348\u6709\u5BBE\u5BA2\u8981\u6765\uFF0C\u5979\u5C31\u65E9\u65E9\u5B88\u5728\u6F5C\u5E7D\u7891\u574A\u8FD9\u4E2A\u5C9B\u4E0A\u7684\u5FC5\u7ECF\u4E4B\u8DEF\uFF0C\u6210\u5FC3\u64A9\u62E8\u662F\u975E\uFF0C\u80FD\u8BA9\u8FD9\u5E2E\u6DF7\u86CB\u6597\u4E2A\u4F60\u6B7B\u6211\u6D3B\u6700\u597D\uFF0C\u5C31\u7B97\u6CA1\u51FA\u4EBA\u547D\uFF0C\u4E5F\u5F04\u4ED6\u4EEC\u4E2A\u4E0D\u75DB\u5FEB\u3002\u8BA9\u4F60\u4EEC\u5403\u9971\u6491\u7684\uFF0C\u6765\u661F\u6708\u6E56\u778E\u51D1\u4EC0\u4E48\u70ED\u95F9\uFF01\u8FD9\u4F1A\u513F\u987A\u624B\u628A\u5C60\u80D6\u5B50\u4E5F\u62C9\u4E0B\u6C34\u3002</p><p>\u6155\u5BB9\u9F99\u6577\u884D\u7740\u8BF4\uFF1A\u300C\u4E00\u5B9A\u4E00\u5B9A\u3002\u9A6C\u4E0A\u5C31\u8981\u5F53\u65B0\u5A18\u4E86\uFF0C\u54ED\u6210\u8FD9\u4E2A\u6837\u5B50\u2026\u2026\u53BB\u6D17\u6D17\u8138\u554A\u3002\u300D</p><p>\u7D2B\u73AB\u4E56\u4E56\u70B9\u4E86\u70B9\u5934\uFF0C\u9999\u80A9\u62BD\u52A8\u7740\u53BB\u4E86\u3002</p><p>\u6155\u5BB9\u9F99\u770B\u7740\u5979\u7684\u80CC\u5F71\uFF0C\u82E6\u7B11\u7740\u6447\u4E86\u6447\u5934\uFF0C\u5524\u6765\u4E24\u540D\u7D2B\u8863\u4F8D\u8005\uFF0C\u8BAF\u95EE\u5F53\u65F6\u7684\u60C5\u666F\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u4E00\u8FDB\u752C\u9053\uFF0C\u73AB\u7470\u4ED9\u5B50\u8138\u4E0A\u7684\u59D4\u5C48\u7ACB\u523B\u70DF\u9500\u4E91\u6563\u3002\u5979\u6E9C\u8FDB\u767D\u6C0F\u59D0\u59B9\u7684\u623F\u95F4\uFF0C\u4E0D\u591A\u65F6\u53C8\u94BB\u4E86\u51FA\u6765\u3002\u773C\u73E0\u56DB\u4E0B\u4E00\u8F6C\uFF0C\u6084\u6084\u8D70\u8FDB\u65C1\u8FB9\u7684\u5730\u5B57\u752C\u9053\u3002</p><p>\u7D2B\u73AB\u4F0F\u5728\u5730\u4E0A\uFF0C\u63A8\u5F00\u620C\u5BA4\u95E8\u4E0B\u7684\u6321\u677F\uFF0C\u5C0F\u5FC3\u5730\u5E03\u4E0B\u7EF3\u5708\uFF0C\u7136\u540E\u638F\u51FA\u4E00\u5757\u8840\u6DCB\u6DCB\u7684\u725B\u8089\u653E\u5728\u5730\u4E0A\u3002</p><p>\u623F\u95F4\u91CC\u4ECD\u7136\u54CD\u7740\u90A3\u79CD\u5947\u602A\u5730\u53FD\u53FD\u58F0\u3002\u7D2B\u73AB\u4E0D\u77E5\u9053\u88AB\u704C\u54D1\u7684\u5E08\u59D0\u6B63\u5728\u627F\u53D7\u5DE8\u72AC\u5978\u6DEB\uFF0C\u65E0\u8BBA\u662F\u5F53\u521D\u98CE\u5E08\u59D0\u88AB\u91CE\u732A\u7834\u8EAB\uFF0C\u8FD8\u662F\u6C34\u67D4\u4ED9\u88AB\u8001\u864E\u5F3A\u66B4\uFF0C\u90FD\u662F\u6709\u4EBA\u5728\u65C1\u5E2E\u5FD9\u3002\u5979\u4E0D\u76F8\u4FE1\u4F1A\u6709\u52A8\u7269\u4E3B\u52A8\u5F3A\u884C\u4E0E\u4EBA\u7C7B\u4EA4\u5408\uFF0C\u51B5\u4E14\u662F\u81EA\u5DF1\u6700\u656C\u4F69\u7684\u5927\u5E08\u59D0\u2026\u2026</p><p>\u7247\u523B\u540E\uFF0C\u4E24\u53EA\u84DD\u5E7D\u5E7D\u7684\u773C\u775B\u4ECE\u9ED1\u6697\u4E2D\u6162\u6162\u8FEB\u8FD1\u3002\u5DE8\u72AC\u8840\u53E3\u4E00\u5F20\uFF0C\u628A\u725B\u8089\u541E\u5230\u53E3\u4E2D\u3002</p><p>\u7D2B\u73AB\u94F6\u7259\u7D27\u54AC\uFF0C\u4F7F\u51FA\u5403\u5976\u7684\u52B2\u513F\u62FC\u547D\u4E00\u62FD\uFF0C\u7EF3\u5708\u7D27\u7D27\u5957\u5728\u5DE8\u72AC\u9888\u4E2D\u3002\u5979\u524D\u601D\u540E\u60F3\uFF0C\u90A3\u6DF7\u86CB\u53C8\u4E0D\u662F\u8FD9\u4E9B\u755C\u7272\u7684\u4EB2\u7239\uFF0C\u5149\u51ED\u5634\u5DF4\u8BF4\u8BF4\uFF0C\u4E0D\u53EF\u80FD\u4FDD\u8BC1\u5E08\u59D0\u7684\u5B89\u5168\uFF0C\u65E2\u7136\u4E0D\u80FD\u8BA9\u5E08\u59D0\u4F4F\u5728\u522B\u5BA4\uFF0C\u90A3\u6700\u597D\u7684\u529E\u6CD5\u8FD8\u662F\u628A\u5B83\u4EEC\u90FD\u5F04\u6B7B\u3002</p><p>\u4E0D\u5C31\u56DB\u5934\u561B\uFF0C\u9876\u591A\u4E24\u5929\u5DE5\u592B\u5C31\u80FD\u628A\u8FD9\u623F\u95F4\u817E\u51FA\u6765\uFF0C\u8BA9\u5E08\u59D0\u4E00\u4E2A\u4EBA\u4F4F\u3002\u56E0\u6B64\u624D\u81EA\u544A\u594B\u52C7\u66FF\u767D\u6C0F\u59D0\u59B9\u5582\u72D7\u3002</p><p>\u7D2B\u73AB\u4E24\u811A\u8E6C\u4F4F\u77F3\u95E8\uFF0C\u62FC\u547D\u4F7F\u529B\u3002\u90A3\u5934\u5DE8\u72AC\u4ECE\u95E8\u6D1E\u91CC\u9732\u51FA\u4E24\u53EA\u773C\u775B\uFF0C\u5947\u602A\u5730\u770B\u7740\u5979\u3002\u8FC7\u4E86\u4E00\u4F1A\u513F\uFF0C\u53EF\u80FD\u89C9\u5F97\u8116\u5B50\u6709\u4E9B\u75D2\uFF0C\u4E8E\u662F\u6643\u4E86\u6643\u8111\u888B\uFF0C\u671D\u540E\u9000\u53BB\u3002</p><p>\u4E00\u80A1\u5927\u529B\u6D8C\u6765\uFF0C\u7D2B\u73AB\u8EAB\u4E0D\u7531\u5DF1\u5730\u88AB\u5E26\u5230\u95E8\u4E0A\u3002\u5979\u6491\u4E86\u7247\u523B\uFF0C\u4F7F\u4E0D\u51FA\u771F\u6C14\u7684\u624B\u81C2\u53C8\u9178\u53C8\u75DB\uFF0C\u5B9E\u5728\u662F\u62C9\u4E0D\u8FC7\u8FD9\u5934\u755C\u7272\uFF0C\u53EA\u597D\u6492\u5F00\u624B\u8BA4\u8F93\u4E86\u4E8B\u3002\u4F46\u5979\u5FD8\u81EA\u5DF1\u5F00\u59CB\u51C6\u5907\u6709\u591A\u5145\u5206\uFF0C\u7EF3\u7D22\u4E00\u5934\u8FD8\u7F20\u5728\u81C2\u95F4\u3002\u8FD9\u4F1A\u513F\u624B\u81C2\u5361\u5728\u95E8\u6D1E\u4E0A\uFF0C\u5267\u75DB\u653B\u5FC3\u3002\u5979\u624B\u5FD9\u811A\u4E71\u5730\u89E3\u5F00\u7EF3\u5B50\uFF0C\u5A07\u5AE9\u7684\u808C\u80A4\u5DF2\u7ECF\u88AB\u78E8\u51FA\u4E00\u9053\u8840\u75D5\u3002</p><p>\u7D2B\u73AB\u75DB\u5F97\u76F4\u6389\u773C\u6CEA\uFF0C\u9760\u5728\u95E8\u4E0A\u628A\u8FD9\u4E9B\u755C\u7272\u7684\u7956\u5B97\u516B\u4EE3\u90FD\u9A82\u4E86\u4E2A\u904D\u3002\u9A82\u7740\u9A82\u7740\u53C8\u9A82\u5230\u53F6\u884C\u5357\u8EAB\u4E0A\uFF0C\u8FD9\u8001\u5BB6\u4F19\u6574\u5929\u9632\u8D3C\u4F3C\u7684\u9632\u7740\u5979\uFF0C\u5982\u679C\u4ED6\u8BA9\u81EA\u5DF1\u5077\u70B9\u6BD2\u836F\u51FA\u6765\uFF0C\u8FD8\u7528\u53D7\u8FD9\u4EFD\u82E6\u5417\uFF1F</p><p>\u60F3\u6765\u60F3\u53BB\u90FD\u53F6\u8001\u5934\u4E0D\u597D\uFF01\u7D2B\u73AB\u6068\u6068\u7AD9\u8D77\u6765\u3002</p><p>\u53F6\u884C\u5357\u6728\u7740\u8138\u5750\u5728\u6848\u540E\uFF0C\u684C\u9762\u4E0A\u5E72\u5E72\u51C0\u51C0\uFF0C\u53EA\u653E\u7740\u4E00\u74F6\u914D\u5236\u597D\u7684\u8309\u8389\u82B1\u6CB9\u548C\u4E00\u76D2\u9ED1\u8272\u4E39\u836F\u3002</p><p>\u7D2B\u73AB\u62FF\u8D77\u8309\u8389\u82B1\u6CB9\u95FB\u4E86\u95FB\uFF0C\u559C\u5B5C\u5B5C\u5730\u8BF4\uFF1A\u300C\u597D\u9999\u3002\u300D\u5979\u4ECE\u76D2\u91CC\u968F\u4FBF\u62E3\u4E86\u9897\u4E39\u836F\uFF0C\u5F80\u53F6\u884C\u5357\u9762\u524D\u4E00\u653E\uFF0C\u300C\u5FEB\u70B9\u5403\u4E86\uFF0C\u6211\u8981\u53BB\u7ED9\u5A18\u64E6\u8EAB\u5B50\u3002\u300D</p><p>\u5979\u6628\u5929\u665A\u4E0A\u53EF\u6CA1\u8BF4\u8FD9\u5931\u795E\u4E39\u4E5F\u5F97\u5148\u8BD5\u2026\u2026\u4F46\u8FD9\u4F1A\u513F\u8BF4\u4EC0\u4E48\u4E5F\u662F\u767D\u642D\u3002\u53F6\u884C\u5357\u5589\u7ED3\u4E00\u52A8\uFF0C\u5E72\u6DA9\u5730\u54BD\u4E86\u53E3\u5410\u6CAB\uFF0C\u628A\u5931\u795E\u4E39\u653E\u5230\u5634\u91CC\u3002</p><p>\u300C\u53EF\u8981\u54BD\u4E0B\u53BB\u54E6\u3002\u300D\u7D2B\u73AB\u7B11\u772F\u772F\u8BF4\u3002\u5979\u76EF\u7740\u53F6\u884C\u5357\u541E\u4E0B\u4E39\u836F\uFF0C\u628A\u8309\u8389\u82B1\u6CB9\u5012\u5728\u624B\u5FC3\u91CC\u3002\u4F38\u51FA\u67D4\u82E5\u65E0\u9AA8\u7684\u5C0F\u624B\uFF0C\u5E26\u7740\u6D53\u90C1\u7684\u82B3\u9999\u6309\u5728\u6EE1\u662F\u76B1\u7EB9\u7684\u8138\u4E0A\u3002\u76F4\u64E6\u4E86\u4E00\u523B\u949F\u5DE5\u592B\uFF0C\u7D2B\u73AB\u4F30\u8BA1\u4E39\u836F\u5DF2\u7ECF\u5316\u5F00\uFF0C\u624D\u505C\u4F4F\u624B\uFF0C\u5DE6\u53F3\u7AEF\u8BE6\u4E00\u4E0B\uFF0C\u6EE1\u610F\u5730\u70B9\u70B9\u5934\uFF0C\u300C\u771F\u662F\u6F02\u4EAE\u591A\u4E86\u3002\u300D</p><p>\u7B49\u8FD9\u514B\u661F\u4E00\u8D70\uFF0C\u53F6\u884C\u5357\u7ACB\u523B\u4ECE\u836F\u67B6\u5E95\u5904\u62FF\u51FA\u4E00\u74F6\u836F\u6C41\uFF0C\u300C\u561F\u561F\u561F\u561F\u300D\u559D\u4E86\u4E2A\u5E72\u51C0\u3002\u4ED6\u5598\u7740\u6C14\u4E3E\u8D77\u836F\u74F6\uFF0C\u563F\u563F\u4E50\u4E86\u8D77\u6765\uFF0C\u300C\u4ED6\u5988\u7684\uFF0C\u8001\u5B50\u771F\u662F\u806A\u660E\uFF0C\u5148\u914D\u4E0B\u4E86\u5931\u795E\u4E39\u7684\u89E3\u836F\u3002\u60F3\u8BA9\u6211\u4E0A\u5F53\uFF0C\u6CA1\u6709\u53EF\u80FD\uFF01\u300D</p>',55);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
