import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_50" tabindex="-1"><a class="header-anchor" href="#_50" aria-hidden="true">#</a> 50</h1><p>\u8840\u65A9\u53CC\u715E\u6C14\u7130\u5927\u51CF\u3002\u5C60\u6000\u6C89\u8EAB\u4E3A\u661F\u6708\u6E56\u957F\u8001\u5012\u4E5F\u7F62\u4E86\uFF0C\u90A3\u4E24\u4E2A\u7D2B\u8863\u4EBA\u53EA\u662F\u5BAB\u5185\u4F8D\u8005\uFF0C\u7ADF\u80FD\u6321\u4F4F\u4EC7\u767E\u9CCC\u5168\u529B\u4E00\u51FB\uFF0C\u661F\u6708\u6E56\u786E\u662F\u85CF\u9F99\u5367\u864E\u3002</p><p>\u4F17\u4EBA\u6765\u5230\u6BBF\u524D\uFF0C\u6CA1\u770B\u6E05\u795E\u6BBF\u5982\u4F55\u5DCD\u5CE8\uFF0C\u5E03\u7F6E\u5982\u4F55\u534E\u4E3D\uFF0C\u773C\u5149\u90FD\u843D\u5728\u77F3\u9636\u4E0A\u3002</p><p>\u4E09\u5341\u516D\u7EA7\u77F3\u9636\u5206\u4E3A\u4E09\u5C42\uFF0C\u5C3D\u662F\u9ED1\u8272\u5927\u7406\u77F3\u94FA\u5C31\uFF0C\u6253\u78E8\u5F97\u660E\u955C\u4E00\u822C\u3002\u5728\u7B2C\u4E8C\u5C42\u77F3\u9636\u4E0A\uFF0C\u4E00\u4E2A\u8EAB\u6750\u66FC\u5999\u7684\u5C11\u5987\u8D64\u88F8\u88F8\u8DEA\u4F0F\u5728\u5730\uFF0C\u767D\u5AE9\u7684\u808C\u80A4\u7EC6\u817B\u52A8\u4EBA\u3002\u4E00\u6839\u94C1\u94FE\u4ECE\u8179\u4E0B\u4F38\u51FA\uFF0C\u7CFB\u5728\u680F\u6746\u6276\u624B\u4E0A\uFF0C\u62C9\u5F97\u7B14\u76F4\u3002\u6D51\u5706\u7684\u96EA\u81C0\u88AB\u60AC\u7A7A\u62C9\u8D77\uFF0C\u9AD8\u9AD8\u4E3E\u5728\u4F17\u4EBA\u9762\u524D\u3002</p><p>\u5C11\u5987\u67D4\u97E7\u7684\u8170\u80A2\uFF0C\u5149\u6DA6\u7684\u5927\u817F\uFF0C\u65E0\u4E0D\u5145\u6EE1\u5973\u6027\u7684\u9B45\u529B\uFF0C\u4F46\u5979\u80A1\u95F4\u5374\u6CA1\u6709\u5973\u4EBA\u67D4\u7F8E\u7684\u82B1\u74E3\uFF0C\u96EA\u767D\u7684\u4E24\u817F\u95F4\uFF0C\u53EA\u6709\u4E00\u7247\u5149\u6ED1\u65E0\u6BD4\u7684\u5AE9\u8089\uFF0C\u5B9B\u5982\u4E00\u5757\u72ED\u957F\u7684\u7EA2\u7389\uFF0C\u5D4C\u5728\u4E30\u6EE1\u7684\u96EA\u80A1\u4E4B\u4E2D\u3002\u8089\u7A74\u6BEB\u65E0\u906E\u63A9\u5730\u66B4\u9732\u5728\u5916\uFF0C\u6BCF\u5904\u7EC6\u5C0F\u7684\u8936\u76B1\u90FD\u5C3D\u6536\u773C\u5E95\u3002</p><p>\u300C\u5C60\u957F\u8001\uFF0C\u8FD9\u662F\u2026\u2026\u300D\u6123\u4E86\u534A\u5929\uFF0C\u6709\u4EBA\u95EE\u9053\u3002</p><p>\u5C60\u6000\u6C89\u7B11\u800C\u4E0D\u7B54\u3002</p><p>\u4EC7\u767E\u9CCC\u51D1\u8FC7\u53BB\u5C0F\u5FC3\u7FFC\u7FFC\u5730\u64A9\u8D77\u5C11\u5987\u7684\u79C0\u53D1\uFF0C\u5634\u5DF4\u4E00\u4E0B\u5B50\u5F20\u5F97\u6D51\u5706\uFF0C\u50CF\u88AB\u4EBA\u70B9\u4E2D\u4E86\u7A74\u9053\u822C\u52A8\u5F39\u4E0D\u5F97\u3002</p><p>\u4EC7\u767E\u718A\u6324\u8FC7\u53BB\u4E00\u770B\uFF0C\u7A81\u7136\u53D1\u51FA\u4E00\u58F0\u602A\u53EB\uFF1A\u300C\u5BD2\u3001\u5BD2\u3001\u5BD2\u6708\u5200\uFF01\u6797\u9999\u8FDC\uFF01\uFF1F\u300D</p><p>\u300C\u5475\u5475\u2026\u2026\u300D\u5C60\u6000\u6C89\u6478\u7740\u6EE1\u662F\u80A5\u8198\u7684\u5927\u809A\u7B11\u9053\uFF1A\u300C\u5404\u4F4D\u89C1\u7B11\u5566\u3002\u6797\u5A4A\u5B50\u5DF2\u88AB\u6211\u795E\u6559\u6536\u4E3A\u5974\u96B6\uFF0C\u5728\u6B64\u5F53\u53EA\u8FCE\u5BBE\u7684\u6BCD\u72D7\uFF0C\u53EA\u662F\u8BA9\u5927\u5BB6\u5F00\u5FC3\u5427\uFF0C\u54C8\u54C8\uFF0C\u968F\u4FBF\u73A9\uFF01\u300D</p><p>\u4EBA\u7FA4\u987F\u65F6\u70B8\u5F00\u4E86\uFF0C\u5341\u4F59\u4EBA\u540C\u65F6\u56F4\u8FC7\u53BB\uFF0C\u4F38\u624B\u5F80\u6797\u9999\u8FDC\u5468\u8EAB\u4E0A\u4E0B\u4E71\u6478\u3002</p><p>\u300C\u8FD9\u5C44\u662F\u600E\u4E48\u957F\u7684\uFF1F\u5149\u6E9C\u6E9C\u4EC0\u4E48\u90FD\u6CA1\u6709\uFF1F\u300D</p><p>\u300C\u64CD\uFF0C\u6709\u6D1E\u5C31\u884C\u4E86\uFF0C\u7BA1\u90A3\u4E48\u591A\uFF01\u563F\uFF0C\u91CC\u9762\u8FD8\u771F\u7D27\u3002\u300D</p><p>\u5C60\u6000\u6C89\u89E3\u8BF4\u9053\uFF1A\u300C\u672C\u6765\u6559\u91CC\u51E0\u5343\u540D\u5144\u5F1F\u64CD\u8FC7\uFF0C\u677E\u5F97\u80FD\u4F38\u8FDB\u53BB\u62F3\u5934\uFF0C\u8FD9\u4E3A\u4E86\u5404\u4F4D\u8FDC\u9053\u800C\u6765\u7684\u8D35\u5BA2\uFF0C\u521A\u6CBB\u597D\u2026\u2026\u300D</p><p>\u300C\u5BD2\u6708\u5200\u88AB\u51E0\u5343\u4EBA\u64CD\u8FC7\u4E86\uFF1F\u300D</p><p>\u300C\u4E0D\u9519\uFF01\u6797\u5A4A\u5B50\u4E0D\u8FC7\u662F\u53EA\u770B\u95E8\u72D7\u3002\u795E\u6559\u5404\u8272\u5973\u5974\u65E0\u6570\uFF0C\u53EA\u8981\u662F\u6211\u6559\u5144\u5F1F\uFF0C\u8C01\u60F3\u64CD\u8C01\u64CD\uFF01\u300D</p><p>\u4F17\u4EBA\u77ED\u6682\u7684\u6C89\u9ED8\u4E00\u4E0B\uFF0C\u65CB\u5373\u53C8\u55A7\u95F9\u8D77\u6765\uFF0C\u300C\u6211\u64CD\uFF0C\u5976\u5934\u4E5F\u6CA1\u6709\uFF01\u300D</p><p>\u300C\u5149\u6E9C\u6E9C\u7684\uFF0C\u96BE\u9053\u6CA1\u957F\uFF1F\u300D</p><p>\u300C\u5272\u7684\uFF01\u2014\u2014\u4E0D\u4F1A\u5427\uFF1F\u8FDE\u5C44\u5E26\u5976\u5934\u90FD\u5207\u4E86\uFF1F\u300D</p><p>\u300C\u773C\u4E5F\u778E\u4E86\uFF01\u5BD2\u6708\u5200\u6797\u9999\u8FDC\u5728\u6C5F\u6E56\u4E5F\u662F\u9F0E\u9F0E\u5927\u540D\uFF0C\u5728\u8FD9\u8FDE\u6761\u72D7\u90FD\u4E0D\u5982\u554A\uFF01\u300D</p><p>\u300C\u4E09\u4E2A\u6708\u524D\u5BD2\u6708\u5200\u8DDF\u4F0F\u9F99\u6DA7\u7684\u6155\u5BB9\u80DC\u6210\u4EB2\uFF0C\u4ED6\u5A18\u7684\u8F70\u4F20\u6B66\u6797\uFF0C\u90FD\u8BF4\u662F\u795E\u4ED9\u4FA0\u4FA3\u3002\u6CA1\u6210\u60F3\u8FD9\u624D\u51E0\u5929\u5DE5\u592B\uFF0C\u5C31\u843D\u5230\u795E\u6559\u624B\u91CC\uFF0C\u771F\u662F\u73A9\u70C2\u4E86\u2026\u2026\u300D</p><p>\u5C60\u6000\u6C89\u6EE1\u9762\u5806\u7B11\uFF0C\u5FC3\u77E5\u8FD9\u4E00\u4E0B\u81F3\u5C11\u6253\u52A8\u4E86\u4E00\u534A\u4EBA\u3002</p><p>\u6797\u9999\u8FDC\u7684\u8138\u8272\u82CD\u767D\uFF0C\u5979\u5634\u91CC\u88AB\u585E\u4E0A\u9EBB\u6838\uFF0C\u53C8\u5236\u4F4F\u54D1\u7A74\uFF0C\u53EA\u80FD\u8D64\u88F8\u7740\u6B8B\u7F3A\u7684\u8EAB\u4F53\uFF0C\u9ED8\u9ED8\u65E0\u8A00\u5730\u5FCD\u53D7\u65E0\u5C3D\u7684\u51CC\u8FB1\u3002</p><p>\u6155\u5BB9\u9F99\u7AD9\u5728\u5E7D\u6697\u7684\u795E\u6BBF\u5185\uFF0C\u8FDC\u8FDC\u7684\u6253\u91CF\u7740\u4F17\u4EBA\uFF0C\u8138\u4E0A\u6DE1\u6DE1\u7684\uFF0C\u770B\u4E0D\u51FA\u4E00\u4E1D\u8868\u60C5\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u4E00\u5927\u65E9\u7D2B\u73AB\u53C8\u6765\u5230\u6F5C\u5E7D\u7891\u574A\uFF0C\u7422\u78E8\u7740\u600E\u4E48\u6311\u52A8\u661F\u6708\u6E56\u4EBA\u9A6C\u8DDF\u8D3A\u559C\u7684\u5BBE\u5BA2\u8840\u6597\u4E00\u573A\uFF0C\u8981\u80FD\u4E24\u8D25\u4FF1\u4F24\uFF0C\u6574\u4E2A\u5C9B\u4E0A\u6B7B\u7684\u5C31\u5269\u81EA\u5DF1\u4E00\u4E2A\u4EBA\uFF0C\u90A3\u662F\u6700\u597D\u4E0D\u8FC7\u4E86\u3002\u53EF\u6068\u8FCE\u5BBE\u7684\u662F\u6574\u5929\u53EA\u4F1A\u50BB\u7B11\u7684\u5C60\u80D6\u5B50\uFF0C\u59D3\u970D\u7684\u6B7B\u54EA\u513F\u4E86\uFF1F\u90A3\u738B\u516B\u86CB\u8981\u5728\uFF0C\u7528\u4E0D\u4E86\u4E24\u53E5\u8BDD\u80AF\u5B9A\u5C31\u4F1A\u8840\u6E85\u5F53\u573A\u3002</p><p>\u8FD9\u6B21\u6765\u4E86\u4E8C\u5341\u4F59\u4EBA\uFF0C\u7D2B\u73AB\u8E2E\u7740\u811A\u5C16\uFF0C\u7EC6\u77A7\u91CC\u9762\u54EA\u4E2A\u50CF\u662F\u5934\u8111\u7B80\u5355\u7684\u50BB\u74DC\u3002</p><p>\u8EAB\u8FB9\u4EBA\u5F71\u4E00\u95EA\uFF0C\u7D2B\u73AB\u8138\u4E0A\u7684\u731C\u6D4B\u7ACB\u523B\u6362\u6210\u597D\u5947\uFF0C\u300C\u54A6\uFF1F\u90A3\u4EBA\u4E2A\u5B50\u597D\u9AD8\u554A\uFF0C\u4ED6\u624B\u91CC\u62FF\u7684\u4EC0\u4E48\uFF1F\u300D</p><p>\u300C\u516B\u89D2\u69CC\u3002\u300D</p><p>\u7D2B\u73AB\u50CF\u662F\u521A\u521A\u77E5\u9053\u8EAB\u8FB9\u6709\u4EBA\u7684\u6837\u5B50\uFF0C\u5413\u5F97\u4E00\u98A4\uFF0C\u5C0F\u624B\u62CD\u7740\u80F8\u53E3\u5A07\u5598\u9053\uFF1A\u300C\u54E5\u54E5\uFF0C\u4F60\u600E\u4E48\u6765\u4E86\uFF1F\u300D</p><p>\u300C\u54E5\u54E5\u6015\u4F60\u518D\u770B\u8C01\u7684\u9570\u5200\u4E0D\u987A\u773C\u2014\u2014\u88AB\u4EBA\u5BB6\u6B3A\u8D1F\u3002\u300D</p><p>\u7D2B\u73AB\u88C5\u505A\u6CA1\u542C\u51FA\u4ED6\u7684\u63F6\u63C4\uFF0C\u5DE7\u7B11\u5AE3\u7136\u5730\u8BF4\uFF1A\u300C\u6709\u54E5\u54E5\u5728\u8FD9\u513F\uFF0C\u600E\u4E48\u4F1A\u6709\u6562\u6B3A\u8D1F\u6211\uFF1F\u300D\u5FC3\u91CC\u6697\u9A82\uFF0C\u80AF\u5B9A\u662F\u90A3\u4E24\u4E2A\u7D2B\u8304\u5B50\u591A\u5634\u591A\u820C\uFF0C\u8BA9\u8FD9\u6DF7\u86CB\u6765\u76D1\u89C6\u81EA\u5DF1\u3002</p><p>\u9ED1\u98CE\u8C79\u8521\u4E91\u5CF0\u6C14\u5B87\u8F69\u6602\u5730\u8D70\u5230\u7891\u574A\u524D\uFF0C\u7ACB\u523B\u4E5F\u8DDF\u4F17\u4EBA\u4E00\u822C\uFF0C\u6EE1\u8138\u60CA\u8273\u5730\u76EF\u7740\u6155\u5BB9\u7D2B\u73AB\u3002</p><p>\u7D2B\u73AB\u5F84\u76F4\u8D70\u5230\u4ED6\u9762\u524D\uFF0C\u4EF0\u7740\u4FCF\u8138\u4E0A\u4E0B\u6253\u91CF\u3002\u6155\u5BB9\u9F99\u6697\u6697\u5438\u4E86\u53E3\u6C14\uFF0C\u968F\u65F6\u51C6\u5907\u51FA\u624B\uFF0C\u8D76\u5728\u9ED1\u98CE\u8C79\u72C2\u6012\u4E4B\u524D\u6551\u4E0B\u8FD9\u4E2A\u4E0D\u77E5\u5929\u9AD8\u5730\u539A\u7684\u5C0F\u4E2B\u5934\u3002</p><p>\u300C\u5594\u2026\u2026\u300D\u7D2B\u73AB\u7741\u7740\u6C34\u7075\u7075\u7684\u7F8E\u76EE\uFF0C\u957F\u957F\u5410\u4E86\u53E3\u6C14\u3002</p><p>\u95FB\u5230\u90A3\u80A1\u9999\u751C\u7684\u5C11\u5973\u6C14\u606F\uFF0C\u8521\u4E91\u5CF0\u51E0\u4E4E\u9189\u5012\u3002\u6CA1\u60F3\u5230\u95F2\u8F6C\u8FD9\u4E00\u8D9F\uFF0C\u7ADF\u80FD\u9047\u4E0A\u8FD9\u4E48\u7F8E\u4E3D\u7684\u5973\u5B69\u513F\uFF0C\u770B\u6765\u5979\u5BF9\u81EA\u5DF1\u597D\u50CF\u6709\u70B9\u610F\u601D\u2026\u2026</p><p>\u7D2B\u73AB\u4E24\u624B\u6367\u5FC3\uFF0C\u773C\u4E2D\u5149\u8292\u95EA\u52A8\uFF0C\u7A81\u7136\u5927\u58F0\u8BF4\u9053\uFF1A\u300C\u4F60\u957F\u5F97\u597D\u5E05\u554A\uFF01\u6211\u5AC1\u7ED9\u4F60\u5F53\u8001\u5A46\u597D\u5417\uFF1F\u300D</p><p>\u8521\u4E91\u5CF0\u8EAB\u5B50\u4E00\u8F6F\uFF0C\u534A\u8DEA\u5728\u5730\u4E0A\uFF0C\u53EA\u89C9\u8EAB\u5B50\u8F7B\u98D8\u98D8\u7684\uFF0C\u4E00\u65F6\u95F4\u4E0D\u77E5\u9053\u81EA\u5DF1\u8EAB\u5728\u4F55\u5904\u3002\u8033\u4E2D\u6EDA\u6765\u6EDA\u53BB\u90FD\u662F\u90A3\u53E5\uFF1A\u300C\u957F\u5F97\u597D\u5E05\u554A\uFF0C\u5AC1\u7ED9\u4F60\u597D\u5417\u2026\u2026\u300D</p><p>\u4E0D\u77E5\u8FC7\u4E86\u591A\u4E45\uFF0C\u4ED6\u5446\u5446\u8BF4\u4E86\u53E5\uFF0C\u300C\u597D\u2026\u2026\u300D</p><p>\u62AC\u773C\u4E00\u770B\uFF0C\u8EAB\u8FB9\u4E00\u4E2A\u4EBA\u90FD\u6CA1\u6709\u3002</p><p>\u53C8\u8FC7\u4E86\u534A\u664C\uFF0C\u9ED1\u98CE\u8C79\u6478\u6478\u8111\u888B\u7AD9\u8D77\u8EAB\u6765\uFF0C\u6DF1\u4E00\u811A\u6D45\u4E00\u811A\u671D\u5C9B\u5185\u8D70\u53BB\uFF0C\u8FDE\u516B\u89D2\u69CC\u4E5F\u5FD8\u4E86\u62FF\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u6155\u5BB9\u9F99\u631F\u7740\u7D2B\u73AB\u98DE\u4E5F\u4F3C\u7684\u63A0\u56DE\u5BAB\u4E2D\uFF0C\u628A\u5979\u5F80\u5E8A\u4E0A\u4E00\u6254\uFF0C\u6012\u559D\u9053\uFF1A\u300C\u4F60\u4EC0\u4E48\u610F\u601D\uFF01\u300D</p><p>\u7D2B\u73AB\u7528\u6795\u5934\u906E\u4F4F\u8138\uFF0C\u5C0F\u58F0\u8BF4\uFF1A\u300C\u4ED6\u957F\u5F97\u6BD4\u4F60\u9AD8\u3002\u300D</p><p>\u8FD9\u4E2B\u5934\u8FD8\u6562\u6C14\u4ED6\uFF0C\u6155\u5BB9\u9F99\u6012\u6781\u800C\u7B11\uFF0C\u54AC\u7259\u5207\u9F7F\u5730\u8BF4\u9053\uFF1A\u300C\u518D\u4ED6\u5988\u6562\u8DDF\u6211\u73A9\u82B1\u6837\uFF0C\u522B\u8BF4\u4F60\u5E08\u59D0\u3001\u5E08\u7236\uFF0C\u6211\u8FDE\u5A18\u90FD\u6562\u6254\u51FA\u53BB\u8BA9\u4ED6\u4EEC\u968F\u3001\u4FBF\u3001\u64CD\uFF01\u300D</p><p>\u7D2B\u73AB\u817E\u5730\u6254\u6389\u6795\u5934\uFF0C\u6B7B\u6B7B\u76EF\u7740\u4ED6\u3002\u8FD9\u79BD\u517D\u5C45\u7136\u8FDE\u4EB2\u5A18\u4E5F\u4E0D\u653E\u5728\u5FC3\u4E0A\u2026\u2026</p><p>\u6155\u5BB9\u9F99\u5BF8\u6B65\u4E0D\u8BA9\u5730\u4E0E\u5979\u5BF9\u89C6\uFF0C\u5634\u89D2\u6162\u6162\u6D6E\u8D77\u72DE\u7B11\u3002\u7D2B\u73AB\u5FC3\u5934\u4E00\u51C9\uFF0C\u77E5\u9053\u4ED6\u7ACB\u523B\u5C31\u4F1A\u4E0B\u4EE4\u628A\u5E08\u59D0\u6254\u7ED9\u90A3\u4E9B\u51F6\u6076\u7684\u964C\u751F\u4EBA\uFF0C\u8FDE\u5FD9\u6251\u5230\u5E8A\u4E0A\uFF0C\u57CB\u5934\u5927\u54ED\u8D77\u6765\u3002</p><p>\u8FD9\u4E00\u54ED\u6D88\u89E3\u4E86\u6155\u5BB9\u9F99\u7684\u6012\u6C14\uFF0C\u4ED6\u9893\u7136\u5750\u5728\u5E8A\u4FA7\uFF0C\u75DB\u5FC3\u75BE\u9996\u5730\u8BF4\uFF1A\u300C\u4F60\u628A\u54E5\u54E5\u7684\u8138\u90FD\u4E22\u5C3D\u4E86\uFF01\u300D</p><p>\u4ED6\u5988\u7684\uFF0C\u81EA\u5DF1\u7684\u65B0\u5A18\u5C45\u7136\u5F53\u7740\u56DB\u65B9\u7FA4\u96C4\u7684\u9762\u558A\u7740\u8981\u5AC1\u7ED9\u522B\u4EBA\uFF0C\u4E0D\u4F46\u81EA\u5DF1\u989C\u9762\u65E0\u5B58\uFF0C\u661F\u6708\u6E56\u7684\u8138\u4E5F\u4E22\u5C3D\u4E86\u3002\u4E0D\u77E5\u9053\u5C60\u6000\u6C89\u600E\u4E48\u8DDF\u90A3\u5E2E\u4EBA\u89E3\u91CA\u7684\u2026\u2026\u4ED6\u5988\u7684\uFF0C\u81EA\u5DF1\u4E5F\u591F\u8822\uFF0C\u90FD\u8FD9\u65F6\u5019\u4E86\u8FD8\u8BA9\u5979\u5728\u5916\u9762\u4E71\u901B\u3002</p><p>\u300C\u6211\u53EA\u662F\u770B\u4ED6\u50BB\u4E4E\u4E4E\u7684\u2026\u2026\u9017\u4ED6\u73A9\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u91CD\u91CD\u5598\u4E86\u4E00\u53E3\u7C97\u6C14\uFF0C\u6C89\u58F0\u9053\uFF1A\u300C\u4ECE\u73B0\u5728\u8D77\uFF0C\u4E0D\u8BB8\u4F60\u518D\u51FA\u5723\u5BAB\uFF01\u77E5\u9053\u4E86\u5417\uFF1F\u300D</p><p>\u7D2B\u73AB\u62BD\u564E\u7740\u70B9\u70B9\u5934\uFF0C\u53C8\u9053\uFF1A\u300C\u6211\u6BCF\u5929\u53EA\u51FA\u53BB\u4E00\u6B21\u597D\u4E0D\u597D\uFF1F\u6211\u4FDD\u8BC1\u4E0D\u8BF4\u8BDD\u3002\u300D</p><p>\u300C\u4E0D\u884C\uFF01\u300D\u6155\u5BB9\u9F99\u4E00\u53E3\u56DE\u7EDD\uFF0C\u300C\u7ED9\u6211\u5B89\u5B89\u5206\u5206\u5F85\u5728\u5BAB\u91CC\uFF0C\u51C6\u5907\u5F53\u65B0\u5A18\uFF01\u6CA1\u4E00\u70B9\u5BB6\u6559\uFF01\u300D</p><p>\u90A3\u65E5\u88AB\u6155\u5BB9\u9F99\u7F9E\u8FB1\u4E4B\u540E\uFF0C\u96EA\u5CF0\u795E\u5C3C\u7EC3\u529F\u6108\u53D1\u6025\u5207\u3002\u5979\u51ED\u501F\u51E4\u51F0\u5B9D\u5178\u7684\u795E\u5F02\uFF0C\u4ECE\u65C1\u8109\u5165\u624B\uFF0C\u907F\u5F00\u6C14\u6D77\u4E39\u7530\u53CA\u5468\u8EAB\u8BF8\u5904\u5927\u7A74\uFF0C\u7EC8\u4E8E\u53EF\u4EE5\u628A\u63E1\u5230\u4E00\u7F15\u6781\u5F31\u7684\u771F\u6C14\u3002</p><p>\u795E\u5C3C\u5F3A\u5FCD\u4F4F\u8840\u8109\u9006\u884C\u7684\u75DB\u695A\uFF0C\u771F\u6C14\u5728\u4EFB\u7763\u4E8C\u8109\u4E4B\u95F4\u5F39\u4E38\u822C\u6CBF\u5E26\u8109\u6A2A\u5411\u6E38\u52A8\uFF0C\u6700\u540E\u8BD5\u63A2\u7740\u98DE\u901F\u63A0\u8FC7\u4E39\u7530\u3002\u5FAE\u5F31\u7684\u6C14\u6D41\u4ECE\u8110\u4E0B\u4E09\u5206\u5904\u4E00\u95EA\u800C\u8FC7\uFF0C\u7EC8\u4E8E\u6210\u529F\u7684\u8EB2\u8FC7\u4E86\u5316\u771F\u6563\u7684\u8086\u8650\u3002\u4F46\u8FD9\u4E1D\u771F\u6C14\u592A\u8FC7\u5FAE\u5F31\uFF0C\u65E0\u8BBA\u662F\u60F3\u903C\u51FA\u836F\u529B\u8FD8\u662F\u5236\u654C\u4F24\u4EBA\uFF0C\u90FD\u96BE\u6709\u4F5C\u4E3A\u3002</p><p>\u623F\u95E8\u8F67\u8F67\u6D1E\u5F00\uFF0C\u7D2B\u73AB\u50CF\u4E2A\u59D4\u5C48\u7684\u5C0F\u5AB3\u5987\uFF0C\u695A\u695A\u53EF\u601C\u5730\u8DDF\u5728\u6155\u5BB9\u9F99\u8EAB\u540E\u8FDB\u6765\u3002</p><p>\u6155\u5BB9\u9F99\u773C\u5149\u5728\u795E\u5C3C\u80F8\u8179\u4E0A\u626B\u4E86\u51E0\u4E2A\u6765\u56DE\uFF0C\u9732\u51FA\u5632\u7B11\u7684\u795E\u8272\u3002\u96EA\u5CF0\u795E\u5C3C\u8138\u4E0A\u5FAE\u5FAE\u4E00\u7EA2\uFF0C\u65CB\u5373\u5927\u6012\u3002</p><p>\u6155\u5BB9\u9F99\u628A\u7D2B\u73AB\u63A8\u4E0A\u6765\uFF0C\u7B11\u9053\uFF1A\u300C\u518D\u6709\u4E09\u65E5\uFF0C\u5728\u4E0B\u4FBF\u8981\u4E0E\u4EE4\u5F92\u6210\u4EB2\uFF0C\u8BF7\u5E08\u592A\u6559\u6559\u5979\u4E3A\u5987\u4E4B\u9053\u3002\u300D</p><p>\u96EA\u5CF0\u795E\u5C3C\u4E43\u662F\u4F5B\u95E8\u4E2D\u4EBA\uFF0C\u8FD9\u8BDD\u5206\u660E\u662F\u8C03\u620F\u7684\u8A00\u8BED\u3002\u5979\u6309\u4E0B\u5FC3\u4E2D\u7684\u6012\u706B\uFF0C\u95ED\u76EE\u4E0D\u7406\u3002</p><p>\u6155\u5BB9\u9F99\u62D7\u4E0D\u8FC7\u7D2B\u73AB\u7684\u54C0\u6C42\uFF0C\u8BA9\u5979\u6765\u63A2\u671B\u795E\u5C3C\uFF0C\u5173\u95E8\u79BB\u5F00\u65F6\u53C8\u52A0\u4E0A\u4E00\u53E5\uFF0C\u300C\u5341\u516D\u65E5\u7684\u5A5A\u793C\uFF0C\u8FD8\u8BF7\u5E08\u592A\u51FA\u5E2D\u3002\u300D</p><p>\u5BA4\u5185\u9759\u4E86\u7247\u523B\uFF0C\u7D2B\u73AB\u89E3\u91CA\u8BF4\uFF1A\u300C\u4ED6\u8BF4\u8981\u660E\u5A92\u6B63\u5A36\uFF0C\u7B97\u661F\u6708\u6E56\u4E0E\u98D8\u6885\u5CF0\u8054\u59FB\u2026\u2026\u300D</p><p>\u60F3\u8BA9\u81EA\u5DF1\u5728\u4F17\u4EBA\u9762\u524D\u628A\u7231\u5F92\u8BB8\u914D\u7ED9\u90A3\u4E2A\u755C\u7272\uFF01\u96EA\u5CF0\u795E\u5C3C\u51B7\u54FC\u4E00\u58F0\uFF0C\u62E7\u7709\u6C89\u601D\u534A\u664C\uFF0C\u659F\u914C\u7740\u9053\uFF1A\u300C\u5C4A\u65F6\u4E0D\u5FC5\u6FC0\u6012\u4ED6\uFF0C\u5230\u665A\u95F4\uFF0C\u4F60\u60F3\u529E\u6CD5\u4ECE\u4ED6\u8EAB\u4E0A\u53D6\u6765\u5175\u5203\u3002\u300D</p><p>\u5979\u987F\u4E86\u4E00\u4E0B\uFF0C\u95EE\u9053\uFF1A\u300C\u8FD9\u91CC\u6709\u591A\u5C11\u4EBA\uFF1F\u300D\u300C\u77F3\u5BAB\u53EA\u6709\u53F6\u884C\u5357\u3001\u6C90\u58F0\u4F20\u3001\u6211\u5A18\u3001\u98CE\u5E08\u59D0\u3001\u7EAA\u5E08\u59D0\u3001\u5C0F\u83BA\u5C0F\u9E42\u2014\u2014\u53F6\u8001\u5934\u3001</p><p>\u6C90\u8001\u5934\u5230\u65F6\u90FD\u4F1A\u56DE\u907F\uFF0C\u90A3\u5C31\u53EA\u5269\u4ED6\u4E00\u4E2A\u574F\u4EBA\u4E86\u3002\u300D</p><p>\u96EA\u5CF0\u795E\u5C3C\u7CBE\u795E\u4E00\u632F\uFF0C\u628A\u81EA\u5DF1\u521A\u624D\u4FEE\u70BC\u6240\u5F97\u4E00\u4E00\u4F20\u6388\u7ED9\u7D2B\u73AB\u3002\u7D2B\u73AB\u4F9D\u6CD5\u8FD0\u4E86\u534A\u5929\u6C14\uFF0C\u7ED3\u679C\u4ECD\u662F\u4E00\u65E0\u6240\u5F97\u3002\u795E\u5C3C\u77E5\u9053\u5979\u4FEE\u4E3A\u592A\u6D45\uFF0C\u53EA\u597D\u5148\u653E\u5728\u4E00\u8FB9\uFF0C\u300C\u5230\u65F6\u80FD\u85C9\u673A\u523A\u6B7B\u4ED6\u6700\u597D\uFF1B\u5982\u679C\u4E0D\u80FD\uFF0C\u5C31\u8D81\u4ED6\u5F97\u610F\u5FD8\u5F62\u7684\u65F6\u5019\u62FF\u5230\u94A5\u5319\u2026\u2026\u665A\u534E\u548C\u7709\u59A9\u600E\u4E48\u6837\u4E86\uFF1F\u300D</p><p>\u7D2B\u73AB\u5782\u4E0B\u5934\uFF0C\u4F4E\u58F0\u9053\uFF1A\u300C\u5979\u4EEC\u7684\u6B66\u529F\u88AB\u5E9F\u4E86\u2026\u2026\u300D</p><p>\u96EA\u795E\u795E\u5C3C\u8F7B\u53F9\u4E00\u58F0\uFF0C\u300C\u665A\u534E\u6027\u60C5\u575A\u6BC5\uFF0C\u4EE5\u540E\u8FD8\u53EF\u91CD\u65B0\u4FEE\u4E60\uFF0C\u7709\u59A9\u2026\u2026\u300D</p><p>\u7D2B\u73AB\u4E0D\u6562\u63A5\u53E3\uFF0C\u6C89\u9ED8\u4E00\u4F1A\u513F\u624D\u8BF4\uFF1A\u300C\u5316\u771F\u6563\u7684\u89E3\u836F\u4E0D\u77E5\u85CF\u5728\u4F55\u5904\u3002\u5F92\u513F\u5728\u53F6\u8001\u5934\u623F\u91CC\u627E\u4E86\u591A\u6B21\uFF0C\u4E5F\u6CA1\u627E\u5230\u3002\u300D</p><p>\u795E\u5C3C\u629A\u6478\u7740\u7D2B\u73AB\u5149\u4EAE\u7684\u957F\u53D1\uFF0C\u5B89\u6170\u9053\uFF1A\u300C\u89E3\u836F\u80AF\u5B9A\u4E0D\u597D\u627E\u2026\u2026\u5BF9\u4E86\uFF0C\u4F60\u5F53\u521D\u8BF4\u7684\u5B9D\u85CF\u600E\u4E48\u6837\uFF1F\u300D</p><p>\u300C\u6211\u627E\u5230\u4E86\u4E09\u5904\uFF0C\u541B\u5B57\u752C\u9053\u4E00\u76F4\u9501\u7740\uFF0C\u53E6\u4E00\u4E2A\u662F\u539F\u6765\u98CE\u5E08\u59D0\u4F4F\u7684\u4EB2\u5B57\u4E01\u5BA4\uFF0C\u5F92\u513F\u6CA1\u529E\u6CD5\u53BB\u770B\u3002\u300D</p><p>\u795E\u5C3C\u70B9\u70B9\u5934\uFF0C\u53EE\u5631\u9053\uFF1A\u300C\u8BB0\u4F4F\uFF0C\u5343\u4E07\u4E0D\u8981\u7834\u8EAB\u3002\u8FD9\u4E8B\u6027\u547D\u6538\u5173\uFF0C\u5230\u65F6\u4E0D\u59A8\u7ED9\u4ED6\u8BB2\u660E\u3002\u300D</p><p>\u7D2B\u73AB\u95EE\u9053\uFF1A\u300C\u771F\u6C14\u5DF2\u7ECF\u88AB\u5316\u771F\u6563\u5316\u89E3\u8FD8\u6709\u5371\u9669\u5417\uFF1F\u300D</p><p>\u300C\u771F\u6C14\u5E76\u975E\u6D88\u89E3\uFF0C\u800C\u662F\u6563\u4E71\u96BE\u805A\u3002\u771F\u6C14\u4E0E\u7CBE\u5143\u76F8\u8FDE\uFF0C\u9664\u975E\u5185\u529F\u5C3D\u5E9F\uFF0C\u5426\u5219\u5FC5\u4F1A\u5371\u53CA\u6027\u547D\u3002\u5343\u4E07\u5C0F\u5FC3\u2026\u2026\u300D</p>',73);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
