import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_51" tabindex="-1"><a class="header-anchor" href="#_51" aria-hidden="true">#</a> 51</h1><p>\u98D8\u6885\u5CF0\u7ACB\u6D3E\u767E\u4F59\u5E74\uFF0C\u867D\u7136\u540D\u5C5E\u4F5B\u95E8\uFF0C\u4F46\u5386\u6765\u4EE5\u4FA0\u4E49\u9053\u81EA\u5C45\u3002\u53CA\u81F3\u96EA\u5CF0\u795E\u5C3C\uFF0C\u4ED7\u5251\u65A9\u5996\u8BDB\u90AA\u58F0\u540D\u8D6B\u8D6B\u3002\u95E8\u4E0B\u9AD8\u5F92\u6D41\u971C\u5251\u4E0E\u5BD2\u6708\u5200\u4E0D\u4F46\u662F\u540D\u9707\u6C5F\u6E56\u7684\u4FA0\u5973\uFF0C\u800C\u4E14\u90FD\u662F\u56FD\u8272\u5929\u9999\u7684\u7EDD\u8272\u7F8E\u5973\u3002</p><p>\u5982\u4ECA\u90AA\u9053\u81F3\u5C0A\u661F\u6708\u6E56\u4E0E\u4E16\u5916\u540D\u95E8\u98D8\u6885\u5CF0\u8054\u59FB\uFF0C\u65B0\u4EFB\u5BAB\u4E3B\u5A36\u7684\u6B63\u662F\u73AB\u7470\u4ED9\u5B50\uFF0C\u7ACB\u65F6\u5F15\u6765\u65E0\u6570\u4EBA\u7684\u597D\u5947\u4E0E\u731C\u6D4B\u3002\u661F\u6708\u6E56\u6B64\u756A\u4E00\u53CD\u5E38\u6001\uFF0C\u5927\u5F20\u65D7\u9F13\u5730\u5E7F\u9080\u540C\u9053\uFF0C\u56E0\u6B64\u5E94\u8005\u5982\u4E91\u3002</p><p>\u91D1\u74A7\u8F89\u714C\u7684\u87E0\u9F99\u5DE8\u67F1\u4E0B\uFF0C\u7ACB\u7740\u4E00\u6761\u8EAB\u9AD8\u8FD1\u4E08\u7684\u58EE\u6C49\uFF0C\u8D64\u88F8\u7684\u4E0A\u8EAB\u808C\u8089\u866C\u7ED3\uFF0C\u6CB9\u5149\u53D1\u4EAE\u3002\u4ED6\u624B\u81C2\u4E00\u6325\uFF0C\u957F\u903E\u4E09\u4E08\u7684\u5DE8\u97AD\u7075\u86C7\u822C\u5728\u7A7A\u4E2D\u4E00\u51FB\uFF0C\u54CD\u5F7B\u5927\u6BBF\u3002</p><p>\u55A7\u95F9\u7684\u795E\u6BBF\u7ACB\u523B\u5B89\u9759\u4E0B\u6765\uFF0C\u6765\u81EA\u4E09\u5C71\u4E94\u5CB3\u7684\u6570\u767E\u540D\u90AA\u6D3E\u9AD8\u624B\uFF0C\u9F50\u9F50\u628A\u76EE\u5149\u6295\u5411\u6BBF\u4E0A\u3002</p><p>\u5E7F\u9614\u7684\u6BBF\u4E0A\u7A7A\u843D\u843D\u6446\u7740\u4E00\u9876\u7EB1\u5E10\u3002\u8584\u5982\u8749\u7FFC\u7684\u7EA2\u7EB1\u7F13\u7F13\u5377\u8D77\uFF0C\u9732\u51FA\u7389\u5C4F\u524D\u7AEF\u5750\u7684\u4E24\u540D\u6C14\u8D28\u8FE5\u5F02\u7684\u5973\u5B50\u3002</p><p>\u5DE6\u9996\u7684\u7F8E\u5987\u8863\u9970\u9C9C\u660E\uFF0C\u9AD8\u76D8\u7684\u4E91\u9AFB\u659C\u63D2\u7740\u4E00\u6839\u73E0\u9497\uFF0C\u81EA\u7136\u800C\u7136\u6D41\u9732\u51FA\u4E00\u80A1\u534E\u8D35\u4E4B\u6C14\u3002\u867D\u7136\u662F\u7AEF\u5750\u6905\u4E2D\uFF0C\u4ECD\u663E\u5F97\u4EEA\u6001\u4E07\u65B9\u3002\u7EA4\u67D4\u7684\u4E24\u624B\u9759\u9759\u4EA4\u653E\u8EAB\u524D\uFF0C\u91D1\u7EA2\u4EA4\u9519\uFF0C\u7EB9\u7EE3\u7CBE\u7F8E\u7684\u8863\u8896\u95F4\u770B\u4E0D\u6E05\u662F\u6307\u662F\u8155\uFF0C\u53EA\u6709\u4E00\u62B9\u8000\u76EE\u7684\u96EA\u767D\u3002\u5979\u7F8E\u8273\u7684\u9762\u5B54\u5982\u89C2\u97F3\u5927\u58EB\u822C\u7AEF\u5E84\u5723\u6D01\uFF0C\u6CE2\u5149\u6D41\u8F6C\u7684\u660E\u7738\u4E2D\uFF0C\u9971\u542B\u7740\u60B2\u60AF\u7684\u795E\u60C5\uFF0C\u4EE4\u4EBA\u4E0D\u6562\u903C\u89C6\u3002</p><p>\u5728\u5343\u4F59\u9053\u76EE\u5149\u6CE8\u89C6\u4E0B\uFF0C\u7F8E\u5987\u7F13\u7F13\u5408\u4E0A\u5999\u76EE\uFF0C\u8138\u4E0A\u9732\u51FA\u4EE4\u4EBA\u546F\u7136\u5FC3\u52A8\u7684\u51C4\u5A49\u548C\u54C0\u75DB\u3002</p><p>\u4E0E\u8D35\u5987\u7684\u534E\u4E3D\u76F8\u6BD4\uFF0C\u53F3\u9996\u90A3\u4E2A\u4E09\u5341\u5C81\u8BB8\u7684\u5973\u5B50\u663E\u5F97\u5341\u5206\u7D20\u96C5\u3002\u5979\u8EAB\u4E0A\u53EA\u6709\u4E00\u88AD\u96EA\u767D\u7684\u8863\u888D\uFF0C\u7389\u5BB9\u768E\u768E\u751F\u8F89\uFF0C\u72B9\u5982\u51B0\u96D5\u96EA\u780C\u3002\u67F3\u7709\u5FAE\u5FAE\u6311\u8D77\uFF0C\u9690\u9690\u900F\u51FA\u4E00\u756A\u51CC\u5389\u8083\u6740\u4E4B\u8272\u3002\u5979\u808C\u80A4\u5149\u6DA6\u5982\u7389\uFF0C\u5E26\u7740\u4E00\u5C42\u6DE1\u6DE1\u7684\u80A4\u5149\u3002\u5C24\u5176\u662F\u90A3\u5BF9\u51E4\u76EE\uFF0C\u987E\u76FC\u95F4\u5BD2\u5149\u56DB\u5C04\uFF0C\u5982\u6709\u5B9E\u8D28\uFF0C\u4E00\u770B\u4FBF\u662F\u5E38\u5E74\u4FEE\u4E60\u5185\u5BB6\u771F\u6C14\u7684\u7EDD\u9876\u9AD8\u624B\u3002</p><p>\u5F53\u90A3\u5973\u5B50\u76EE\u5149\u51B7\u51B7\u626B\u8FC7\u5168\u573A\uFF0C\u8FD9\u4E9B\u80C6\u5927\u5305\u5929\u7684\u51F6\u5F92\u7ACB\u65F6\u5664\u82E5\u5BD2\u8749\uFF0C\u534A\u6570\u90FD\u5782\u4E0B\u773C\uFF0C\u56DE\u907F\u5979\u7684\u76EE\u5149\u3002</p><p>\u96EA\u5CF0\u795E\u5C3C\u6210\u540D\u903E\u4E8C\u5341\u5E74\uFF0C\u4E0B\u624B\u4ECE\u6765\u4E0D\u5BB9\u60C5\uFF0C\u4E3A\u975E\u4F5C\u6B79\u4E4B\u5F92\u8981\u8BA9\u5979\u78B0\u4E0A\u975E\u6B7B\u5373\u4F24\uFF0C\u8C01\u90FD\u6CA1\u60F3\u5230\u8FD9\u4E2A\u715E\u661F\u5C45\u7136\u4E5F\u4F1A\u51FA\u73B0\uFF0C\u800C\u4E14\u8D35\u5BA2\u822C\u9AD8\u9AD8\u5728\u4E0A\u3002\u5728\u573A\u7684\u90FD\u662F\u6B66\u6797\u884C\u5BB6\uFF0C\u867D\u7136\u795E\u5C3C\u4E0D\u8A00\u4E0D\u7B11\uFF0C\u4F46\u90FD\u770B\u51FA\u5979\u6B66\u529F\u5C3D\u5728\uFF0C\u6D51\u4E0D\u4F3C\u88AB\u4EBA\u5236\u4F4F\u7684\u6A21\u6837\u3002</p><p>\u60F3\u5230\u81EA\u5DF1\u521A\u521A\u8FD8\u5978\u6DEB\u8FC7\u5979\u8001\u4EBA\u5BB6\u7684\u95E8\u4E0B\u9AD8\u5F92\u6797\u9999\u8FDC\uFF0C\u5411\u6765\u6A2A\u884C\u65E0\u5FCC\u7684\u8840\u65A9\u53CC\u715E\u4E0D\u7531\u5FC3\u91CC\u9635\u9635\u53D1\u51C9\u3002</p><p>\u6B63\u72D0\u7591\u95F4\uFF0C\u6BBF\u5916\u5FFD\u7136\u4F20\u6765\u4E00\u58F0\u6E05\u8D8A\u7684\u78EC\u54CD\uFF0C\u4E00\u540D\u7D2B\u8863\u5C11\u5E74\u6717\u58F0\u9053\uFF1A\u300C\u5409\u65F6\u5DF2\u5230\u3002\u300D</p><p>\u9F13\u4E50\u4E4B\u58F0\u5927\u4F5C\uFF0C\u7389\u5C4F\u540E\u9E23\u4F69\u58F0\u52A8\uFF0C\u4E00\u5BF9\u5A07\u5A9A\u53EF\u7231\u7684\u5C11\u5973\u633D\u7740\u5BAB\u706F\u5E76\u80A9\u800C\u51FA\u3002</p><p>\u4E24\u5973\u808C\u80A4\u80DC\u96EA\uFF0C\u8C8C\u7F8E\u5982\u82B1\uFF0C\u96BE\u5F97\u662F\u5979\u4EEC\u5BB9\u8C8C\u4F53\u5F62\u4E00\u65E0\u4E8C\u81F4\uFF0C\u8FDE\u8138\u4E0A\u7684\u9152\u7A9D\u90FD\u5206\u6BEB\u4E0D\u5DEE\uFF0C\u5B9B\u5982\u767D\u74A7\u96D5\u5C31\u7684\u4E00\u5BF9\u7389\u4EBA\u3002</p><p>\u63A5\u7740\u4E00\u540D\u7537\u5B50\u7F13\u6B65\u8D70\u51FA\uFF0C\u4ED6\u5E74\u7EAA\u4E0D\u8FC7\u4E8C\u5341\u4F59\u5C81\uFF0C\uFF0C\u9F3B\u6881\u633A\u76F4\uFF0C\u76EE\u5982\u5BD2\u661F\uFF0C\u633A\u62D4\u7684\u8EAB\u5F62\u6F47\u6D12\u4FCA\u6717\uFF0C\u82F1\u59FF\u52C3\u53D1\u3002</p><p>\u6BBF\u4E2D\u8D3A\u5BA2\u591A\u534A\u90FD\u4EE5\u4E3A\u661F\u6708\u6E56\u5BAB\u4E3B\u4F1A\u662F\u4E2A\u6D51\u8EAB\u5996\u6C14\u7684\u8001\u9053\uFF0C\u6CA1\u60F3\u5230\u4ED6\u7ADF\u5982\u6B64\u5E74\u5C11\u82F1\u633A\uFF0C\u90FD\u6697\u6697\u559D\u4E86\u58F0\u5F69\u3002</p><p>\u6155\u5BB9\u9F99\u6EE1\u9762\u6625\u98CE\uFF0C\u4E00\u624B\u80CC\u5728\u8EAB\u540E\uFF0C\u4E00\u624B\u5E73\u6258\uFF0C\u7F13\u7F13\u8FC8\u51FA\u7389\u5C4F\u3002</p><p>\u5F53\u65E5\u4F0F\u9F99\u6DA7\u6155\u5BB9\u80DC\u4E0E\u6797\u9999\u8FDC\u5A5A\u793C\u4E0A\uFF0C\u6155\u5BB9\u7D2B\u73AB\u752B\u4E00\u9732\u9762\u7ACB\u5373\u8273\u60CA\u56DB\u5EA7\uFF0C\u73AB\u7470\u4ED9\u5B50\u7684\u82B3\u540D\u6570\u65E5\u95F4\u4FBF\u8F70\u4F20\u5927\u6C5F\u5357\u5317\u3002\u4F17\u53E3\u76F8\u4F20\u5176\u7F8E\u8C8C\u5C24\u5728\u8DB3\u4EE5\u79F0\u4E4B\u6C5F\u6E56\u7EDD\u8272\u7684\u98CE\u665A\u534E\u548C\u6797\u9999\u8FDC\u4E4B\u4E0A\uFF0C\u4F46\u89C1\u8FC7\u6155\u5BB9\u7D2B\u73AB\u7684\u5374\u5BE5\u5BE5\u65E0\u51E0\u3002\u4F17\u4EBA\u90FD\u4E0D\u7981\u77AA\u5927\u773C\u775B\uFF0C\u76EF\u7740\u73AB\u7470\u4ED9\u5B50\u73B0\u8EAB\u5904\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u5C4F\u98CE\u540E\u7F13\u7F13\u4F38\u51FA\u4E00\u53EA\u6B3A\u971C\u5BD2\u96EA\u7684\u7EA4\u7EA4\u7389\u624B\u3002\u4EFF\u4F5B\u4ECE\u60A0\u8FDC\u7684\u68A6\u4E2D\u63A2\u51FA\uFF0C\u4EE5\u6162\u5F97\u4EE4\u4EBA\u505C\u6B62\u5FC3\u8DF3\u7684\u901F\u5EA6\u7F13\u7F13\u51FA\u73B0\u3002\u6BBF\u4E2D\u9759\u6084\u6084\u518D\u65E0\u4E1D\u6BEB\u58F0\u97F3\uFF0C\u6BCF\u4E2A\u4EBA\u90FD\u5C4F\u4F4F\u547C\u5438\uFF0C\u76F4\u52FE\u52FE\u671B\u7740\u90A3\u53EA\u67D4\u82E5\u65E0\u9AA8\u7684\u5C0F\u624B\u3002\u4E0D\u77E5\u8FC7\u4E86\u591A\u4E45\uFF0C\u5C4F\u540E\u7EC8\u4E8E\u9732\u51FA\u4E00\u53EA\u67D4\u7F8E\u751F\u59FF\u7684\u7693\u8155\u3002</p><p>\u6BBF\u4E2D\u7684\u706F\u70DB\u4F3C\u4E4E\u7A81\u7136\u4E4B\u95F4\u5927\u653E\u5149\u660E\uFF0C\u7F8A\u8102\u7EC6\u7389\u6253\u5236\u7684\u5C4F\u98CE\u5149\u6655\u6D41\u52A8\uFF0C\u4E00\u5F20\u5A07\u7F8E\u65E0\u5339\u7684\u4FCF\u8138\u4EFF\u4F5B\u521D\u5347\u7684\u660E\u6708\uFF0C\u5E26\u7740\u8000\u76EE\u7684\u98CE\u534E\uFF0C\u5448\u73B0\u5728\u4F17\u4EBA\u9762\u524D\u3002</p><p>\u4EFF\u4F5B\u65E0\u6570\u7EC6\u5C0F\u7684\u9C9C\u82B1\u5728\u7A7A\u6C14\u4E2D\u540C\u65F6\u7EFD\u653E\uFF0C\u4E1D\u4E1D\u7F15\u7F15\u6C81\u4EBA\u5FC3\u813E\u7684\u9999\u751C\u99A5\u90C1\uFF0C\u4ECE\u6BBF\u4E0A\u6D41\u6C34\u822C\u503E\u6CC4\u800C\u51FA\uFF0C\u5145\u585E\u5929\u5730\u3002</p><p>\u4E00\u7247\u6C89\u5BC2\u4E2D\uFF0C\u7A81\u7136\u4F20\u6765\u300C\u549A\u300D\u7684\u4E00\u58F0\u95F7\u54CD\uFF0C\u6BBF\u95E8\u65C1\u4E00\u540D\u9AD8\u5927\u7684\u6C49\u5B50\u4EF0\u5929\u5012\u5728\u5730\u4E0A\u3002</p><p>\u90A3\u6B21\u60CA\u8273\u4F7F\u9ED1\u98CE\u8C79\u8521\u4E91\u5CF0\u8FDE\u65E5\u6765\u98DF\u4E0D\u77E5\u5473\uFF0C\u5BDD\u4E0D\u5B89\u5E2D\uFF0C\u660F\u660F\u5669\u5669\u5728\u5C9B\u4E0A\u8F6C\u4E86\u4E09\u5929\u3002\u5C60\u6000\u6C89\u5FF5\u4ED6\u4E5F\u662F\u6B66\u6797\u4E2D\u6210\u540D\u8C6A\u6770\uFF0C\u65AD\u4E0D\u81F3\u505A\u51FA\u4EC0\u4E48\u6709\u5931\u4F53\u7EDF\u7684\u4E8B\u6765\uFF0C\u60F3\u6765\u60F3\u53BB\u628A\u4ED6\u5B89\u6392\u5728\u6700\u540E\u9762\u7684\u5E2D\u4F4D\u4E0A\uFF0C\u4E0D\u9632\u8FD8\u662F\u51FA\u4E86\u610F\u5916\u3002</p><p>\u5E78\u597D\u4F17\u4EBA\u773C\u5149\u90FD\u76EF\u5728\u5C11\u592B\u4EBA\u8EAB\u4E0A\uFF0C\u8FDE\u4E0E\u8521\u4E91\u5CF0\u540C\u5E2D\u7684\u90FD\u6CA1\u53D1\u89C9\u5F02\u6837\u3002\u4ED6\u8FDE\u5FD9\u547D\u4EBA\u6084\u6084\u628A\u8FD9\u5934\u6A2A\u884C\u6F20\u5317\u7684\u8C79\u5B50\u62D6\u5230\u6BBF\u5916\u3002</p><p>\u4E4C\u4EAE\u7684\u79C0\u53D1\u67D4\u67D4\u76D8\u8D77\uFF0C\u6B63\u4E2D\u662F\u4E00\u53EA\u4E24\u7FFC\u98DE\u626C\u7684\u7389\u51E4\u3002\u51E4\u53E3\u5782\u4E0B\u7684\u73E0\u5EC9\u5448\u6247\u5F62\u6321\u5728\u989D\u524D\uFF0C\u906E\u4F4F\u9ED1\u767D\u5206\u660E\u7684\u7F8E\u76EE\u3002\u6155\u5BB9\u7D2B\u73AB\u9759\u9759\u7684\u671B\u7740\u811A\u5C16\uFF0C\u7EC6\u767D\u7684\u7389\u9888\u67D4\u7F8E\u751F\u59FF\u3002</p><p>\u5979\u8EAB\u4E0A\u7684\u5AC1\u8863\u707F\u5982\u671D\u971E\uFF0C\u7EC6\u8170\u5E7F\u8896\uFF0C\u7EA4\u519C\u5408\u5EA6\uFF0C\u80A9\u4E0A\u7EE3\u7740\u6781\u7EC6\u7684\u76D8\u91D1\u4E91\u9970\uFF0C\u7EE6\u7EA2\u8272\u7684\u8F7B\u7EF8\u62D6\u5728\u5730\u4E0A\uFF0C\u968F\u7740\u8F7B\u67D4\u811A\u6B65\u4E0D\u4F4F\u8212\u5377\u3002\u4EFF\u4F5B\u4E00\u6735\u542B\u82DE\u7684\u73AB\u7470\uFF0C\u5728\u6EE1\u6BBF\u6D41\u5149\u6EA2\u5F69\u7684\u8F89\u714C\u4E2D\u5189\u5189\u5F00\u653E\u3002</p><p>\u5979\u8138\u4E0A\u65E2\u6CA1\u6709\u65B0\u5A5A\u7684\u559C\u60A6\uFF0C\u4E5F\u6CA1\u6709\u88AB\u903C\u8FEB\u7684\u65E0\u5948\uFF0C\u53EA\u662F\u5E73\u9759\u5730\u7531\u6155\u5BB9\u9F99\u6258\u7740\u624B\uFF0C\u8F7B\u8F7B\u8D70\u5230\u6BCD\u4EB2\u548C\u5E08\u7236\u9762\u524D\uFF0C\u7136\u540E\u76C8\u76C8\u8DEA\u5012\u3002</p><p>\u300C\u4E00\u62DC\u5929\u5730\u2014\u2014\u300D\u300C\u4E8C\u62DC\u9AD8\u5802\u2014\u2014\u300D\u8427\u4F5B\u5974\u62FC\u547D\u5FCD\u4F4F\u773C\u6CEA\uFF0C\u7EA2\u5507\u5FAE\u98A4\u3002\u671B\u7740\u81EA\u5DF1\u7684\u4E00\u5BF9\u4EB2\u751F\u9AA8\u8089\u5E76\u80A9\u8DEA\u5728\u9762\u524D\uFF0C\u4E0D\u7531\u67D4\u80A0\u5BF8\u65AD\u3002\u82E5\u975E\u7A74\u9053\u88AB\u5236\uFF0C\u5979\u65E9\u5C31\u653E\u58F0\u75DB\u54ED\u8D77\u6765\u3002\u771F\u4E0D\u77E5\u81EA\u5DF1\u524D\u751F\u505A\u4E86\u4EC0\u4E48\u5B7D\uFF0C\u7ADF\u7136\u88AB\u4EB2\u751F\u513F\u5B50\u62BD\u53BB\u56DB\u80A2\u7B4B\u8171\uFF0C\u5F04\u6210\u4E13\u4F9B\u6DEB\u4E50\u7684\u5E9F\u4EBA\u3002\u5982\u4ECA\u53C8\u5F53\u7740\u81EA\u5DF1\u7684\u9762\u5F3A\u5A36\u5AE1\u4EB2\u59B9\u59B9\u4E3A\u59BB\u2026\u2026</p><p>\u8FD9\u7B49\u4E71\u4F26\u80CC\u5FB7\u7684\u60E8\u5267\uFF0C\u628A\u4E00\u5411\u5D07\u4F5B\u4FE1\u9053\u7684\u767E\u82B1\u89C2\u97F3\u538B\u5F97\u900F\u4E0D\u8FC7\u6C14\u6765\u3002\u8427\u4F5B\u5974\u773C\u7736\u4E00\u70ED\uFF0C\u900F\u660E\u7684\u6CEA\u73E0\u4E32\u4E32\u6EDA\u843D\u3002</p><p>\u7D2B\u73AB\u9759\u9759\u78D5\u4E0B\u5934\u53BB\uFF0C\u51E4\u9497\u4E0A\u7684\u73E0\u4E32\u78B0\u5728\u77F3\u4E0A\uFF0C\u53D1\u51FA\u4E00\u7247\u60A6\u8033\u7684\u8F7B\u54CD\u3002</p><p>\u6155\u5BB9\u9F99\u8138\u4E0A\u5E26\u7740\u83AB\u6D4B\u7684\u7B11\u610F\uFF0C\u773C\u5149\u4ECE\u8427\u4F5B\u5974\u6676\u83B9\u7684\u8138\u4E0A\u4E00\u8DEF\u5411\u4E0B\uFF0C\u843D\u5728\u4E00\u65E0\u5F02\u72B6\u7684\u5C0F\u8179\u4E0A\u3002\u90A3\u4E2A\u66FE\u7ECF\u517B\u80B2\u8FC7\u81EA\u5DF1\u7684\u5B50\u5BAB\u5185\uFF0C\u81EA\u5DF1\u64AD\u4E0B\u7684\u79CD\u5B50\u6B63\u5728\u98DE\u901F\u6210\u957F\u2026\u2026</p><p>\u7D2B\u73AB\u65E0\u559C\u65E0\u5FE7\uFF0C\u5979\u907F\u5F00\u5E08\u7236\u7684\u76EE\u5149\uFF0C\u606D\u606D\u656C\u656C\u5730\u78D5\u4E0B\u5934\u53BB\u3002</p><p>\u96EA\u5CF0\u795E\u5C3C\u76EE\u4E2D\u7CBE\u5149\u95EA\u52A8\uFF0C\u4E00\u8A00\u4E0D\u53D1\u5730\u4EFB\u4E24\u4EBA\u884C\u8FC7\u5927\u793C\u3002\u5F85\u6155\u5BB9\u9F99\u62AC\u8D77\u5934\uFF0C\u5979\u5BD2\u76EE\u4E00\u95EA\uFF0C\u950B\u5229\u7684\u773C\u795E\u76F4\u523A\u6155\u5BB9\u9F99\u5FC3\u5E95\u3002</p><p>\u6155\u5BB9\u9F99\u5FC3\u91CC\u300C\u54AF\u767B\u300D\u4E00\u58F0\uFF0C\u50CF\u88AB\u5B9E\u7269\u91CD\u51FB\u4E86\u4E00\u822C\uFF0C\u6C14\u8840\u7FFB\u6D8C\u3002\u4ED6\u5FAE\u5FAE\u5438\u4E86\u53E3\u6C14\uFF0C\u538B\u4E0B\u5FC3\u4E2D\u4E0D\u5B89\uFF0C\u5FC3\u9053\uFF1A\u300C\u8FD9\u8D3C\u5C3C\u679C\u7136\u4E86\u5F97\uFF0C\u529F\u529B\u4E00\u81F3\u4E8E\u65AF\u2026\u2026\u300D</p><p>\u300C\u4E8B\u5230\u5982\u4ECA\u8FD8\u8FD9\u822C\u56A3\u5F20\u2026\u2026\u8D31\u5A4A\u5B50\uFF0C\u6709\u4F60\u4E50\u7684\u65F6\u5019\uFF01\u300D\u6155\u5BB9\u9F99\u5507\u89D2\u5FAE\u6311\uFF0C\u51B7\u51B7\u56DE\u671B\u8FC7\u53BB\u3002</p><p>\u300C\u592B\u59BB\u5BF9\u62DC\uFF01\u300D</p><p>\u6155\u5BB9\u7D2B\u73AB\u8F7B\u8F7B\u8F6C\u8FC7\u8EAB\u5B50\uFF0C\u4E0E\u6155\u5BB9\u9F99\u76F4\u9762\u76F8\u5BF9\u3002\u65E0\u8BBA\u662F\u6BCD\u4EB2\u7684\u51C4\u5A49\u6B32\u7EDD\u8FD8\u662F\u5E08\u7236\u7684\u6EE1\u5FC3\u6068\u610F\uFF0C\u5979\u7684\u795E\u60C5\u90FD\u6CA1\u6709\u4E00\u4E1D\u6CE2\u52A8\uFF0C\u53EA\u662F\u9759\u9759\u5782\u76EE\u770B\u7740\u5730\u9762\u3002</p><p>\u6155\u5BB9\u9F99\u7565\u8FDF\u7247\u523B\uFF0C\u7B49\u7D2B\u73AB\u5148\u4FEF\u4E0B\u5A07\u8EAF\u8EAB\u624D\u5F90\u5F90\u65BD\u793C\u3002\u5BF9\u8FD9\u4E2A\u7CBE\u7075\u53E4\u602A\u7684\u59B9\u59B9\u4ED6\u4E00\u70B9\u4E5F\u4E0D\u6562\u6389\u4EE5\u8F7B\u5FC3\uFF0C\u82E5\u975E\u624B\u91CC\u634F\u7740\u6709\u6BCD\u4EB2\u548C\u795E\u5C3C\uFF0C\u5C0F\u4E2B\u5934\u65E0\u8BBA\u5982\u4F55\u4E5F\u4E0D\u4F1A\u8FD9\u4E48\u7ED9\u81EA\u5DF1\u9762\u5B50\uFF0C\u5B89\u5B89\u4EFD\u4EFD\u5730\u4E3E\u884C\u5A5A\u793C\u3002\u5982\u679C\u5A5A\u793C\u4E0A\u518D\u51FA\u4E86\u4EC0\u4E48\u610F\u5916\uFF0C\u8FD9\u8138\u53EF\u5C31\u4E22\u5927\u4E86\u3002</p><p>\u300C\u793C\u6BD5\uFF0C\u65B0\u90CE\u65B0\u5A18\u5165\u6D1E\u623F\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u677E\u4E86\u53E3\u6C14\uFF0C\u8FD9\u8FB9\u7389\u83BA\u7389\u9E42\u8FDE\u5FD9\u4E0A\u524D\u6276\u8D77\u5C11\u592B\u4EBA\uFF0C\u8D70\u5165\u5723\u5BAB\u3002\u6BBF\u4E0A\u7EA2\u7EB1\u843D\u4E0B\uFF0C\u906E\u4F4F\u4E86\u8427\u4F5B\u5974\u7684\u6CEA\u6C34\uFF0C\u4E5F\u906E\u4F4F\u4E86\u96EA\u5CF0\u795E\u5C3C\u7684\u6740\u673A\u3002</p><p>\u6BBF\u5185\u4E00\u7247\u6C89\u5BC2\uFF0C\u7247\u523B\u540E\u8F70\u7136\u58F0\u8D77\uFF0C\u4F17\u4EBA\u5FC3\u795E\u4FF1\u9189\uFF0C\u53C8\u662F\u5927\u60D1\u4E0D\u89E3\u3002\u90A3\u8D35\u5987\u60F3\u6765\u5C31\u662F\u73AB\u7470\u4ED9\u5B50\u7684\u6BCD\u4EB2\uFF0C\u4F0F\u9F99\u6DA7\u7684\u5BE8\u4E3B\u592B\u4EBA\u767E\u82B1\u89C2\u97F3\u4E86\u3002\u4F0F\u9F99\u6DA7\u88AB\u661F\u6708\u6E56\u5C60\u706D\uFF0C\u6155\u5BB9\u536B\u6155\u5BB9\u80DC\u7236\u5B50\u8EAB\u6B7B\u4E4B\u4E8B\u65E9\u5DF1\u4F20\u626C\u6C5F\u6E56\u3002\u6CA1\u60F3\u5230\u73AB\u7470\u4ED9\u5B50\u7ADF\u7136\u4F1A\u5B89\u7136\u4E0E\u6740\u7236\u5C60\u5144\u7684\u4EC7\u4EBA\u6210\u4EB2\u2026\u2026\u602A\u4E0D\u5F97\u5979\u5A18\u6CEA\u6D41\u6EE1\u9762\u3002</p><p>\u8FD8\u6709\u96EA\u5CF0\u795E\u5C3C\uFF0C\u770B\u7740\u4E00\u8138\u715E\u6C14\uFF0C\u600E\u4E48\u4E5F\u80FD\u5B89\u5B89\u7A33\u7A33\u5750\u5728\u6905\u4E2D\uFF0C\u4E00\u8A00\u4E0D\u53D1\uFF1F\u77A7\u5979\u773C\u795E\u4E2D\u6D41\u9732\u51FA\u6765\u7684\u529F\u529B\uFF0C\u4E0D\u50CF\u662F\u88AB\u5236\u4F4F\u4E86\u554A\uFF1F\u4F17\u4EBA\u8BAE\u8BBA\u7EB7\u7EB7\uFF0C\u4F46\u8C08\u5F97\u6700\u591A\u7684\uFF0C\u8FD8\u662F\u73AB\u7470\u4ED9\u5B50\u7684\u8273\u8272\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u6155\u5BB9\u9F99\u5728\u7D2B\u73AB\u5507\u89D2\u6D45\u6D45\u4E86\u543B\u4E86\u4E00\u53E3\uFF0C\u300C\u8FD9\u6837\u4E56\u5C31\u5BF9\u4E86\u3002\u300D</p><p>\u7D2B\u73AB\u6CA1\u6709\u4F5C\u7B54\uFF0C\u8138\u4E0A\u6302\u6EE1\u8BA4\u547D\u7684\u5E73\u9759\uFF0C\u5FC3\u91CC\u5374\u4E0D\u4F4F\u8F6C\u7740\u5FF5\u5934\uFF0C\u600E\u4E48\u529E\uFF1F\u600E\u4E48\u529E\uFF1F</p><p>\u7247\u523B\u540E\uFF0C\u767D\u6C0F\u59D0\u59B9\u6258\u7740\u8427\u4F5B\u5974\u8D70\u5165\u6D1E\u623F\uFF0C\u7136\u540E\u5782\u624B\u7ACB\u5728\u65C1\u8FB9\u3002</p><p>\u300C\u5E08\u7236\u5462\uFF1F\u300D</p><p>\u6155\u5BB9\u9F99\u4EB2\u624B\u628A\u8427\u4F5B\u5974\u6276\u5230\u5A5A\u5E8A\u4E0A\u5B89\u7F6E\u597D\uFF0C\u6DE1\u7B11\u9053\uFF1A\u300C\u4E0D\u7528\u6025\uFF0C\u54E5\u54E5\u53BB\u7167\u6599\u795E\u5C3C\u5979\u8001\u4EBA\u5BB6\u3002\u300D\u8BF4\u7F62\u6D12\u7136\u800C\u53BB\u3002</p><p>\u767E\u82B1\u89C2\u97F3\u4FA7\u8FC7\u8138\uFF0C\u4E00\u4E2A\u52B2\u513F\u7684\u6D41\u7740\u773C\u6CEA\u3002\u4E8B\u5230\u5982\u4ECA\uFF0C\u5979\u5DF2\u7ECF\u4E07\u5FF5\u4FF1\u7070\u3002\u7D2B\u73AB\u52C9\u5F3A\u4E00\u7B11\uFF0C\u5374\u4E5F\u65E0\u8BDD\u53EF\u8BF4\uFF0C\u53EA\u597D\u626F\u8D77\u67D4\u6BEF\u76D6\u5728\u6BCD\u4EB2\u8EAB\u4E0A\u3002</p><p>\u7B49\u4E86\u4E00\u67F1\u9999\u5DE5\u592B\u8FD8\u4E0D\u89C1\u6155\u5BB9\u9F99\u5E26\u5E08\u7236\u56DE\u6765\uFF0C\u7D2B\u73AB\u4E0D\u7531\u5FC3\u4E0B\u7126\u6025\uFF0C\u8D77\u8EAB\u671D\u95E8\u53E3\u8D70\u53BB\u3002\u767D\u6C0F\u59D0\u59B9\u8EAB\u5F62\u5FAE\u52A8\uFF0C\u5E76\u80A9\u6321\u4F4F\u53BB\u8DEF\u3002</p><p>\u7D2B\u73AB\u6068\u6068\u76EF\u7740\u59D0\u59B9\u4FE9\uFF0C\u7A81\u7136\u62AC\u624B\u4E00\u638C\u6253\u5728\u767D\u7389\u83BA\u8138\u4E0A\uFF0C\u9A82\u9053\uFF1A\u300C\u8D31\u4EBA\uFF01\u300D\u5979\u5185\u529F\u88AB\u5236\uFF0C\u8FD9\u4E00\u638C\u5E76\u4E0D\u5FEB\uFF0C\u4F46\u767D\u7389\u83BA\u4E0D\u6562\u95EA\u907F\uFF0C\u4FCF\u8138\u987F\u65F6\u7EA2\u4E86\u4E00\u7247\u3002</p><p>\u6848\u4E0A\u4E24\u679D\u901A\u5BB5\u5DE8\u70DB\u65E0\u98CE\u800C\u52A8\uFF0C\u6620\u51FA\u73AB\u7470\u4ED9\u5B50\u773C\u4E2D\u65E0\u6BD4\u7684\u6068\u610F\u3002</p>',54);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
