import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_52" tabindex="-1"><a class="header-anchor" href="#_52" aria-hidden="true">#</a> 52</h1><p>\u6155\u5BB9\u9F99\u795E\u91C7\u98DE\u626C\u5730\u6717\u58F0\u9053\uFF1A\u300C\u8BF8\u4F4D\u8C6A\u6770\uFF01\u300D\u5F85\u6BBF\u4E2D\u55A7\u95F9\u6E10\u6B62\uFF0C\u4ED6\u62F1\u624B\u81F4\u610F\u9053\uFF1A\u300C\u8BF8\u4F4D\u8FDC\u9053\u800C\u6765\uFF0C\u672C\u5BAB\u65E0\u4EE5\u4E3A\u62A5\u2026\u2026\u300D</p><p>\u6BBF\u4E0A\u7684\u7EA2\u7EB1\u518D\u5EA6\u5377\u8D77\uFF0C\u9732\u51FA\u9AD8\u5C45\u5176\u4E0A\u7684\u96EA\u5CF0\u795E\u5C3C\u3002</p><p>\u300C\u2026\u2026\u98D8\u6885\u5CF0\u4E0E\u6211\u7B49\u591A\u5E74\u4E3A\u4EC7\uFF0C\u591A\u5C11\u82F1\u96C4\u8C6A\u6770\u6298\u5728\u8FD9\u4E9B\u8D31\u4EBA\u624B\u4E2D\u3002\u627F\u8BF8\u4F4D\u4E0D\u5F03\uFF0C\u5949\u6211\u661F\u6708\u6E56\u4E3A\u6B66\u6797\u4E4B\u9996\uFF0C\u6597\u6076\u9664\u654C\u4E43\u672C\u5BAB\u4EFD\u5185\u4E4B\u4E8B\u2026\u2026\u300D\u6155\u5BB9\u9F99\u50B2\u7136\u4E00\u7B11\uFF0C\u300C\u5982\u4ECA\u98D8\u6885\u5CF0\u96EA\u5CF0\u795E\u5C3C\u4EE5\u4E0B\u8BF8\u5973\uFF0C\u5DF2\u5C3D\u6210\u6211\u661F\u6708\u6E56\u9636\u4E0B\u4E4B\u56DA\uFF01\u300D</p><p>\u6BBF\u5185\u987F\u65F6\u9F13\u566A\u8D77\u6765\uFF0C\u8840\u65A9\u53CC\u715E\u653E\u4E0B\u5FC3\u4E8B\uFF0C\u53C8\u662F\u9F13\u638C\u53C8\u662F\u632F\u81C2\u54C8\u54C8\u602A\u7B11\u3002\u98D8\u6885\u5CF0\u4E00\u76F4\u662F\u90AA\u9053\u7684\u514B\u661F\uFF0C\u4E0D\u8BF4\u96EA\u5CF0\u795E\u5C3C\u6B66\u529F\u76D6\u4E16\uFF0C\u5355\u662F\u6D41\u971C\u5251\u548C\u5BD2\u6708\u5200\u624B\u4E0B\u5C31\u6709\u4E0D\u5C11\u4EA1\u9B42\uFF0C\u5176\u4E2D\u7684\u8F9B\u9178\u82E6\u695A\u4E00\u8A00\u96BE\u5C3D\u3002\u73B0\u5728\u661F\u6708\u6E56\u4E00\u51FA\u624B\u4FBF\u9664\u53BB\u8FD9\u4E2A\u5FC3\u8179\u5927\u60A3\uFF0C\u4F17\u4EBA\u65E0\u4E0D\u611F\u6FC0\u3002</p><p>\u6155\u5BB9\u9F99\u7B11\u9053\uFF1A\u300C\u98D8\u6885\u5CF0\u540D\u52A8\u5929\u4E0B\uFF0C\u98CE\u5A4A\u5B50\u3001\u6797\u5A4A\u5B50\u3001\u7EAA\u5A4A\u5B50\u6B66\u529F\u5E73\u5E73\uFF0C\u59FF\u8272\u8FD8\u90FD\u770B\u5F97\u8FC7\u53BB\u2026\u2026\u300D</p><p>\u4ED6\u66A7\u6627\u7684\u53E3\u543B\u4F7F\u4F17\u4EBA\u546F\u7136\u5FC3\u52A8\uFF0C\u6570\u767E\u4EBA\u90FD\u5C4F\u606F\u51DD\u89C6\uFF0C\u9759\u7B49\u5BAB\u4E3B\u7684\u4E0B\u6587\u3002</p><p>\u6155\u5BB9\u9F99\u8F7B\u8F7B\u4E00\u51FB\u638C\uFF0C\u300C\u503C\u6B64\u826F\u65E5\uFF0C\u672C\u5BAB\u4E0E\u8BF8\u4F4D\u540C\u4E50\uFF01\u5C31\u8BA9\u5979\u4EEC\u4E00\u8D77\u6765\u4F3A\u5019\u5404\u4F4D\u3002\u300D</p><p>\u6BBF\u5185\u77ED\u6682\u7684\u9759\u9ED8\u7247\u523B\uFF0C\u7136\u540E\u66B4\u8D77\u4E00\u9635\u5DE8\u96F7\u822C\u7684\u559D\u5F69\u4E4B\u58F0\uFF0C\u4F17\u4EBA\u4E0D\u7EA6\u800C\u540C\u7684\u7AD9\u8D77\u8EAB\u6765\uFF0C\u5927\u53EB\u6B64\u884C\u4E0D\u865A\uFF0C\u661F\u6708\u6E56\u8FD9\u7B49\u8C6A\u723D\uFF0C\u5B9E\u5728\u591F\u610F\u601D\uFF01</p><p>\u6B22\u547C\u58F0\u4E2D\uFF0C\u4E00\u4E2A\u5C11\u5973\u602F\u751F\u751F\u8D70\u5230\u6BBF\u4E0A\uFF0C\u8DEA\u5728\u6155\u5BB9\u9F99\u8EAB\u4FA7\u3002\u5979\u8EAB\u4E0A\u53EA\u7F20\u7740\u4E24\u6839\u4E1D\u5E26\uFF0C\u96EA\u80A4\u9999\u808C\u5C3D\u6570\u66B4\u9732\u5728\u5916\u3002\u96EA\u767D\u7684\u4E1D\u5E26\u4ECE\u80A9\u5934\u7ED5\u8FC7\uFF0C\u52C9\u5F3A\u906E\u4F4F\u4E73\u5C16\uFF0C\u767D\u99A5\u99A5\u7684\u5706\u4E73\u5FAE\u5FAE\u6447\u6643\uFF0C\u80A4\u5149\u95EA\u52A8\u3002\u7136\u540E\u4E1D\u5E26\u5728\u8179\u4E0B\u4EA4\u7EC7\u7740\uFF0C\u628A\u90A3\u7247\u6ED1\u5AE9\u7684\u8F6F\u8089\u88F9\u5728\u5176\u4E2D\u3002</p><p>\u4F17\u4EBA\u770B\u5F97\u8840\u8109\u8D32\u5F20\uFF0C\u53EA\u662F\u8FD9\u5C11\u5973\u7684\u82B1\u5BB9\u6708\u8C8C\u6709\u4E9B\u9762\u751F\u3002\u7565\u6709\u4E00\u4E24\u4E2A\u8BC6\u5F97\u7684\uFF0C\u5DF2\u4ECE\u5979\u8EAB\u4E0A\u7684\u4E1D\u5E26\u770B\u51FA\u8FD9\u662F\u98D8\u6885\u5CF0\u4E09\u5F92\u7275\u4E1D\u624B\u7EAA\u7709\u59A9\u3002</p><p>\u7EAA\u7709\u59A9\u9762\u671D\u4F17\u4EBA\u5782\u4E0B\u5934\uFF0C\u5446\u5446\u770B\u7740\u5730\u9762\uFF0C\u4E0D\u6562\u56DE\u5934\u770B\u5E08\u7236\u4E00\u773C\u3002</p><p>\u521A\u624D\u90A3\u756A\u8A00\u8BED\u96EA\u5CF0\u795E\u5C3C\u5C3D\u6570\u542C\u5728\u8033\u5185\uFF0C\u65E9\u5DF2\u662F\u5FC3\u4E0B\u72C2\u6012\u3002\u6B64\u65F6\u89C1\u5230\u7231\u5F92\u9006\u6765\u987A\u53D7\u51C4\u695A\u7684\u6A21\u6837\uFF0C\u5979\u76EE\u5149\u970D\u7136\u4E00\u8DF3\uFF0C\u5FC3\u4E0B\u53C8\u662F\u6C14\u6068\u53C8\u662F\u601C\u60DC\u3002</p><p>\u300C\u8131\u4E86\u5427\u3002\u300D\u6155\u5BB9\u9F99\u6DE1\u6DE1\u9053\u3002</p><p>\u7EAA\u7709\u59A9\u53D1\u68A2\u5FAE\u98A4\uFF0C\u5979\u5403\u529B\u5730\u6162\u6162\u62AC\u624B\u633D\u4F4F\u4E1D\u5E26\uFF0C\u5411\u4E24\u65C1\u62C9\u5F00\u3002\u6BB7\u7EA2\u7684\u4E73\u5934\u5E94\u624B\u8DC3\u51FA\uFF0C\u5728\u7389\u4E73\u4E0A\u4E0D\u4F4F\u8DF3\u52A8\u3002\u4E1D\u5E26\u67D4\u67D4\u6ED1\u843D\uFF0C\u80A1\u95F4\u5931\u53BB\u675F\u7F1A\u7684\u5AE9\u8089\u7ACB\u523B\u5F39\u51FA\uFF0C\u4E00\u6735\u7855\u5927\u7684\u8089\u82B1\u822C\u7EFD\u653E\u5F00\u6765\u3002</p><p>\u6BBF\u4E0B\u54CD\u8D77\u4E00\u7247\u541E\u54BD\u58F0\uFF0C\u770B\u4E0D\u51FA\u8FD9\u6837\u4E00\u4E2A\u6E29\u96C5\u79C0\u7F8E\u7684\u5C11\u5973\uFF0C\u4E0B\u4F53\u7ADF\u751F\u5F97\u5982\u6B64\u6DEB\u8361\u3002</p><p>\u300C\u8BA9\u5927\u4F19\u4ED4\u7EC6\u770B\u770B\u3002\u300D</p><p>\u4E24\u884C\u6E05\u6CEA\u4ECE\u7EAA\u7709\u59A9\u8138\u988A\u6EDA\u843D\uFF0C\u5979\u7D27\u7D27\u95ED\u7740\u773C\u775B\uFF0C\u54AC\u4F4F\u7EA2\u5507\uFF0C\u4F9D\u8A00\u5206\u5F00\u53CC\u819D\uFF0C\u628A\u624B\u4F38\u5230\u8179\u4E0B\uFF0C\u63B0\u5F00\u80A5\u5AE9\u7684\u82B1\u74E3\u3002\u5979\u4E0B\u4F53\u8DB3\u6709\u5E73\u5E38\u5973\u5B50\u7684\u4E09\u500D\u5927\u5C0F\uFF0C\u9F13\u9F13\u62E5\u5728\u817F\u95F4\uFF0C\u6C34\u5149\u95EA\u52A8\uFF0C\u8273\u7EA2\u7684\u5AE9\u8089\u95F4\u6DEB\u6C34\u6DCB\u6F13\uFF0C\u663E\u5F97\u6ED1\u817B\u65E0\u6BD4\u3002</p><p>\u53EA\u8F7B\u8F7B\u4E00\u78B0\uFF0C\u79D8\u5904\u7684\u9165\u9EBB\u5C31\u4F7F\u7EAA\u7709\u59A9\u96BE\u4EE5\u81EA\u5DF2\u3002\u5979\u5F7B\u5E95\u653E\u5F03\u77DC\u6301\u548C\u5C0A\u4E25\uFF0C\u5728\u6570\u767E\u540D\u964C\u751F\u7537\u4EBA\u9762\u524D\u5B8C\u5168\u66B4\u9732\u81EA\u5DF1\u3002\u4E0E\u6B64\u540C\u65F6\uFF0C\u7EAA\u7709\u59A9\u4E5F\u653E\u5F03\u4E86\u6240\u6709\u7684\u5E0C\u671B\u3002</p><p>\u300C\u5E08\u592A\u7684\u9AD8\u5F92\uFF0C\u59FF\u8D28\u679C\u7136\u4E0D\u4FD7\uFF0C\u72EC\u6597\u96C1\u95E8\u4E09\u5947\u8FD8\u4E0D\u843D\u4E0B\u98CE\u3002\u300D\u6155\u5BB9\u9F99\u8BA5\u9053\u3002</p><p>\u7EAA\u7709\u59A9\u767D\u5AE9\u7684\u8EAB\u4F53\u50CF\u4E00\u9053\u4E30\u76DB\u5927\u9910\uFF0C\u5E73\u5E73\u6A2A\u5728\u5DE6\u9996\u7B2C\u4E00\u5F20\u6848\u4E0A\uFF0C\u4E09\u4E2A\u62AB\u53D1\u8C6A\u5BA2\u6B63\u5728\u5979\u4F53\u5185\u62FC\u547D\u51B2\u6740\u3002\u4EAB\u53D7\u7EAA\u7709\u59A9\u5C0F\u5634\u7684\u79C3\u53D1\u4EC0\u5065\u72C2\u7B11\u7740\u62AC\u8D77\u5934\uFF0C\u4E0E\u96EA\u5CF0\u795E\u5C3C\u51CC\u5389\u7684\u76EE\u5149\u4E00\u89E6\uFF0C\u7B11\u5BB9\u4E00\u4E0B\u5B50\u50F5\u4F4F\uFF0C\u4ED6\u6253\u4E86\u4E2A\u54C6\u55E6\uFF0C\u4FBF\u5043\u65D7\u606F\u9F13\u3002</p><p>\u6B64\u523B\uFF0C\u795E\u5C3C\u4F53\u5185\u771F\u6C14\u84EC\u52C3\u6FC0\u8361\uFF0C\u6500\u81F3\u6BD5\u751F\u6765\u7684\u5DC5\u5CF0\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u300C\u8DEA\u4E0B\uFF01\u300D\u6155\u5BB9\u7D2B\u73AB\u5BD2\u58F0\u9053\u3002</p><p>\u767D\u6C0F\u59D0\u59B9\u7565\u4E00\u72B9\u8C6B\uFF0C\u8DEA\u5728\u5C11\u592B\u4EBA\u9762\u524D\u3002</p><p>\u7D2B\u73AB\u6068\u610F\u6D8C\u8D77\uFF0C\u6325\u624B\u7ED9\u4E86\u4E24\u4EBA\u51E0\u8BB0\u8033\u5149\uFF0C\u5598\u606F\u7740\u6012\u89C6\u8FD9\u5BF9\u66FE\u7ECF\u7EAF\u6D01\u5254\u900F\u7684\u59D0\u59B9\u82B1\u3002</p><p>\u4E00\u523B\uFF0C\u53EA\u5DEE\u4E00\u523B\u81EA\u5DF1\u5C31\u80FD\u6551\u51FA\u6BCD\u4EB2\u3001\u5E08\u7236\u3001\u5E08\u59D0\uFF0C\u8FD8\u6709\u8FD9\u4E24\u4E2A\u8D31\u5A62\u3002\u4F46\u7D2B\u73AB\u600E\u4E48\u4E5F\u6CA1\u60F3\u5230\uFF0C\u5979\u4EEC\u7ADF\u7136\u4F1A\u80CC\u53DB\u81EA\u5DF1\uFF0C\u7518\u5FC3\u505A\u90A3\u4E2A\u79BD\u517D\u7684\u5E2E\u51F6\u3002</p><p>\u6628\u65E5\uFF0C\u56DB\u6708\u5341\u4E94\uFF0C\u53D1\u751F\u4E86\u4E24\u4EF6\u7D2B\u73AB\u6C38\u8FDC\u5FD8\u4E0D\u6389\u7684\u4E8B\u3002</p><p>\u7B2C\u4E00\u4EF6\u53D1\u751F\u5728\u4E2D\u5348\u65F6\u5206\u3002</p><p>\u505A\u4E3A\u5211\u5BA4\u7684\u5730\u5B57\u752C\u9053\u50CF\u5F80\u5E38\u90A3\u822C\u9634\u6C89\u6C89\uFF0C\u51B7\u51B0\u51B0\u3002\u56DA\u7981\u5E08\u59D0\u7684\u620C\u5BA4\u4ECD\u50CF\u5F80\u5E38\u90A3\u822C\u6C89\u9ED8\u3002\u4F46\u8FD9\u6B21\u5F53\u7D2B\u73AB\u62FF\u51FA\u98DF\u7269\u65F6\uFF0C\u5979\u770B\u5230\u4E86\u90A3\u5F20\u719F\u6089\u7684\u9762\u5BB9\u3002</p><p>\u56E0\u4E3A\u4E0D\u89C1\u5929\u65E5\uFF0C\u90A3\u5F20\u8138\u663E\u5F97\u6709\u4E9B\u82CD\u767D\uFF0C\u867D\u7136\u7F8E\u8C8C\u5982\u6614\uFF0C\u4F46\u4EE5\u5F80\u90A3\u79CD\u593A\u76EE\u7684\u5149\u5F69\u98CE\u534E\u5374\u6D88\u6563\u65E0\u8FF9\uFF0C\u5C31\u50CF\u8499\u4E0A\u4E86\u6C61\u57A2\u7684\u73CD\u73E0\u822C\uFF0C\u4E27\u5931\u4E86\u66FE\u6709\u7684\u5149\u8F89\u3002\u5979\u751A\u81F3\u6CA1\u6709\u770B\u7D2B\u73AB\u4E00\u773C\uFF0C\u4FBF\u56DB\u80A2\u7740\u5730\u76F4\u63A5\u4F0F\u5728\u6728\u76D8\u4E0A\u5927\u53E3\u5927\u53E3\u541E\u54BD\u8D77\u6765\u3002</p><p>\u7D2B\u73AB\u538B\u4E0B\u5FC3\u4E2D\u7684\u6050\u614C\uFF0C\u5C0F\u58F0\u53EB\u9053\uFF1A\u300C\u5E08\u59D0\u3001\u5E08\u59D0\u3002\u300D</p><p>\u98CE\u665A\u534E\u5BF9\u5979\u8FD1\u5728\u54AB\u5C3A\u7684\u58F0\u97F3\u7F6E\u82E5\u60D8\u95FB\uFF0C\u4E13\u5FC3\u81F4\u5FD7\u5730\u4F0F\u5728\u76D8\u4E0A\u8214\u98DF\u3002\u5FFD\u7136\u8EAB\u4F53\u4E00\u52A8\uFF0C\u50CF\u88AB\u4EBA\u4ECE\u540E\u63A8\u4E86\u4E00\u628A\u822C\u5411\u524D\u503E\u659C\uFF0C\u81FB\u9996\u91CD\u91CD\u78B0\u5728\u77F3\u95E8\u4E0A\u3002\u5979\u5C0F\u5DE7\u7684\u9F3B\u5B50\u91CC\u53D1\u51FA\u4E00\u58F0\u95F7\u54FC\uFF0C\u7389\u8138\u6D6E\u51FA\u4E00\u62B9\u5996\u5F02\u7684\u8273\u7EA2\u3002</p><p>\u7D2B\u73AB\u6014\u6014\u54BD\u4E86\u53E3\u9999\u553E\uFF0C\u50BB\u50BB\u770B\u7740\u5E08\u59D0\u4E00\u8FB9\u6709\u8282\u594F\u7684\u524D\u540E\u6447\u52A8\uFF0C\u4E00\u8FB9\u9999\u751C\u5730\u5403\u7740\u3002\u996D\u7C92\u56DB\u5904\u6563\u843D\uFF0C\u6CBE\u5728\u5507\u4E0A\u8138\u4E0A\u9F3B\u4E0A\u53D1\u4E0A\u2026\u2026</p><p>\u7D2B\u73AB\u80F8\u53E3\u50CF\u88AB\u5DE8\u77F3\u538B\u4F4F\u822C\u900F\u4E0D\u8FC7\u6C14\u6765\u3002\u4E0D\u77E5\u9053\u4EC0\u4E48\u65F6\u5019\u5979\u8DCC\u5750\u5728\u5730\u4E0A\uFF0C\u9694\u7740\u5C0F\u5C0F\u7684\u95E8\u6D1E\u671B\u7740\u90A3\u5F20\u4EB2\u5207\u7684\u7389\u8138\uFF0C\u8111\u4E2D\u4E00\u7247\u6DF7\u4E71\u3002</p><p>\u7247\u523B\u540E\uFF0C\u98CE\u665A\u534E\u5A07\u5598\u7740\u62AC\u8D77\u8138\uFF0C\u4E24\u81C2\u632A\u52A8\u7740\u627E\u4E86\u6700\u8212\u670D\u7684\u59FF\u52BF\u3002\u90A3\u53CC\u6CA1\u6709\u7126\u70B9\u7684\u79C0\u7738\u4ECE\u4EB2\u5982\u624B\u8DB3\u7684\u5E08\u59B9\u9762\u4E0A\u5212\u8FC7\uFF0C\u6CA1\u6709\u6380\u8D77\u4E00\u4E1D\u6CE2\u6F9C\u3002</p><p>\u4E00\u53EA\u7855\u5927\u7684\u5934\u9885\u7A81\u7136\u5728\u95E8\u6D1E\u5185\u51FA\u73B0\uFF0C\u88AB\u98DF\u7269\u7684\u9999\u6C14\u5F15\u6765\u7684\u5DE8\u72AC\u6324\u5F00\u98CE\u665A\u534E\uFF0C\u4F38\u51FA\u957F\u820C\u5C06\u98DF\u7269\u5C3D\u6570\u541E\u4E0B\u3002\u98CE\u665A\u534E\u987A\u4ECE\u5730\u9000\u5230\u4E00\u65C1\uFF0C\u5C0F\u5FC3\u7FFC\u7FFC\u5730\u671B\u7740\u5DE8\u72AC\uFF0C\u773C\u91CC\u5E26\u7740\u6050\u60E7\u3001\u670D\u4ECE\uFF0C\u8FD8\u6709\u4E00\u4E1D\u606D\u656C\u2026\u2026</p><p>\u7D2B\u73AB\u628A\u62F3\u5934\u653E\u5728\u5507\u8FB9\uFF0C\u7F8E\u76EE\u5706\u7741\uFF0C\u4E00\u80A1\u68EE\u51B7\u523A\u9AA8\u7684\u5BD2\u610F\u4ECE\u811A\u5E95\u5347\u8D77\uFF0C\u4F7F\u5979\u7684\u5FC3\u795E\u4E5F\u4E4B\u98A4\u6296\u3002</p><p>\u5F85\u5DE8\u72AC\u5403\u5B8C\uFF0C\u5728\u65C1\u7B49\u4E86\u8BB8\u4E45\u7684\u98CE\u665A\u534E\u7A81\u7136\u51D1\u5411\u524D\u53BB\uFF0C\u4F38\u51FA\u9C9C\u7EA2\u7684\u9999\u820C\uFF0C\u5C06\u5DE8\u72AC\u5634\u4E0A\u7684\u996D\u7C92\u4E00\u4E00\u8214\u5C3D\uFF0C\u7136\u540E\u610F\u72B9\u672A\u5C3D\u5730\u5728\u5507\u74E3\u4E0A\u541E\u5410\u7FFB\u5377\u3002</p><p>\u6155\u5BB9\u7D2B\u73AB\u8111\u4E2D\u8F70\u7136\u4E00\u54CD\uFF0C\u7136\u540E\u66B4\u53D1\u51FA\u4E00\u58F0\u60CA\u5FC3\u52A8\u9B44\u5730\u5C16\u53EB\u3002\u90A3\u4E2A\u719F\u6089\u7684\u8EAB\u4F53\u5728\u5979\u773C\u4E2D\u6E10\u6E10\u53D8\u5316\uFF0C\u4E00\u5411\u521A\u6BC5\u51B3\u65AD\u7684\u5927\u5E08\u59D0\u4E0E\u8DEF\u8FB9\u968F\u5904\u53EF\u89C1\u7684\u91CE\u72D7\u878D\u5408\u5728\u4E00\u8D77\uFF0C\u5206\u4E0D\u51FA\u5F7C\u6B64\u3002\u98CE\u665A\u534E\u88AB\u5979\u7684\u53EB\u58F0\u60CA\u52A8\uFF0C\u626C\u9996\u770B\u4E86\u5979\u4E00\u773C\uFF0C\u53C8\u82E5\u65E0\u5176\u4E8B\u5730\u5782\u4E0B\u5934\uFF0C\u5A07\u8EAF\u5174\u594B\u5730\u524D\u540E\u6446\u52A8\uFF0C\u4E0D\u65F6\u5C06\u4EC5\u5B58\u7684\u5DE6\u624B\u671D\u8EAB\u4E0B\u770B\u4E0D\u5230\u7684\u9ED1\u6697\u5904\u4F38\u53BB\u3002</p><p>\u300C\u5947\u602A\u5417\uFF1F\u300D\u88AB\u7D2B\u73AB\u5C16\u53EB\u5F15\u6765\u7684\u6155\u5BB9\u9F99\u7B11\u9053\u3002</p><p>\u300C\u2026\u2026\u300D</p><p>\u300C\u60F3\u8FDB\u53BB\u770B\u770B\u5417\uFF1F\u300D</p><p>\u300C\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u5F97\u610F\u5730\u4E00\u637B\u6307\uFF0C\u6253\u5F00\u77F3\u95E8\u3002</p><p>\u7D2B\u73AB\u6CA1\u6709\u52A8\u4F5C\uFF0C\u53EA\u662F\u6728\u5076\u822C\u5750\u5728\u51B0\u51B7\u7684\u77F3\u677F\u4E0A\uFF0C\u770B\u7740\u5BA4\u5185\u6E10\u6E10\u4EAE\u8D77\u3002</p><p>\u98CE\u665A\u534E\u50CF\u72D7\u4E00\u822C\u8DB4\u5728\u5BA4\u5185\uFF0C\u7FD8\u8D77\u767D\u5AE9\u7684\u5706\u81C0\u3002\u4E00\u6761\u6BDB\u53D1\u8038\u7136\u7684\u9ED1\u72AC\u6B63\u4F0F\u5728\u5979\u8EAB\u4E0A\u4E0D\u4F4F\u633A\u52A8\u3002\u6155\u5BB9\u9F99\u62AC\u811A\u6311\u8D77\u6D41\u971C\u5251\u7684\u4E0B\u5DF4\uFF0C\u77A7\u7740\u5979\u6655\u7EA2\u7684\u9762\u988A\uFF0C\u53D1\u51FA\u4E00\u58F0\u957F\u7B11\u3002</p><p>\u98CE\u665A\u534E\u5A9A\u773C\u5982\u4E1D\uFF0C\u5598\u606F\u7740\u52AA\u529B\u8FCE\u5408\u5DE8\u72AC\u7684\u62BD\u63D2\uFF0C\u8BA9\u517D\u6839\u6DF1\u6DF1\u8FDB\u5165\u81EA\u5DF1\u4F53\u5185\u6700\u7F8E\u5999\u7684\u79D8\u5883\u3002\u5979\u7684\u547C\u5438\u8D8A\u6765\u8D8A\u6025\u4FC3\uFF0C\u7A81\u7136\u7EA2\u5507\u5FAE\u5F20\uFF0C\u300C\u5440\u300D\u7684\u98A4\u58F0\u5A07\u547C\uFF0C\u63A5\u7740\u6218\u6817\u8D77\u6765\u3002</p><p>\u81EA\u59CB\u81F3\u7EC8\uFF0C\u5979\u53EA\u53D1\u51FA\u8FD9\u4E00\u4E2A\u97F3\u8282\u3002</p><p>\u300C\u549A\u300D\u7684\u4E00\u58F0\uFF0C\u7D2B\u73AB\u5411\u540E\u4FBF\u5012\uFF0C\u540E\u8111\u91CD\u91CD\u78D5\u5728\u5730\u4E0A\u3002</p><p>\u5979\u5E76\u6CA1\u6709\u89C9\u5F97\u75BC\u75DB\uFF0C\u53EA\u89C9\u5F97\u81EA\u5DF1\u5F88\u8F7B\u3002\u8F7B\u529F\u6700\u597D\u7684\u65F6\u5019\uFF0C\u5979\u7684\u8EAB\u4F53\u4E5F\u6CA1\u6709\u8FD9\u4E48\u8F7B\u8FC7\uFF0C\u4EFF\u4F5B\u98D8\u5728\u4E91\u7AEF\uFF0C\u4E00\u4E1D\u98CE\u5C31\u80FD\u628A\u81EA\u5DF1\u5439\u6563\u3002\u5728\u65E0\u8FB9\u7684\u9ED1\u6697\u4E2D\uFF0C\u5979\u542C\u5230\u4E00\u4E2A\u6E05\u6670\u7684\u58F0\u97F3\uFF0C\u300C\u662F\u4F60\uFF0C\u662F\u4F60\u5BB3\u4E86\u5979\u3002\u5C31\u50CF\u4F60\u5BB3\u4E86\u7EAA\u7709\u59A9\u4E00\u6837\uFF0C\u5979\u62B9\u7684\u836F\u3001\u5403\u7684\u836F\u90FD\u662F\u4F60\u4EB2\u624B\u5582\u4E0B\u7684\u2026\u2026\u300D</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u505A\u4E3A\u8FCE\u5BBE\u72AC\u7684\u6797\u9999\u8FDC\u4E5F\u88AB\u5E26\u5230\u6BBF\u4E2D\uFF0C\u5931\u53BB\u4E86\u9634\u5507\u548C\u4E73\u5934\u7684\u8EAB\u4F53\u5F15\u8D77\u4F17\u4EBA\u83AB\u5927\u7684\u5174\u8DA3\u3002\u6CA1\u6709\u6311\u9017\u4E5F\u6CA1\u6709\u620F\u5F04\uFF0C\u5BD2\u6708\u5200\u50CF\u5783\u573E\u822C\u88AB\u6155\u5BB9\u9F99\u62AC\u811A\u8E22\u5230\u4EBA\u7FA4\u4E2D\u3002</p><p>\u96EA\u767D\u7684\u8EAB\u4F53\u5212\u5230\u4E00\u6761\u5F27\u7EBF\uFF0C\u8FD8\u5728\u534A\u7A7A\u4E2D\uFF0C\u5C31\u6709\u4E94\u4EBA\u9AD8\u9AD8\u8DC3\u8D77\u3002\u6765\u81EA\u5317\u51C9\u7684\u8D6B\u8FDE\u96C4\uFF0C\u5DF4\u9675\u7684\u5B89\u5B50\u5B8F\u5404\u6293\u4F4F\u6797\u9999\u8FDC\u7684\u4E00\u53EA\u811A\u8E1D\uFF0C\u9AD8\u660C\u7684\u4E5E\u4F0F\u7A77\u9686\u5219\u63E1\u4F4F\u6797\u9999\u8FDC\u7684\u7693\u8155\u3002\u4E09\u4EBA\u5404\u81EA\u51FA\u624B\uFF0C\u5C06\u5176\u4ED6\u4E24\u4EBA\u8FEB\u5F00\u3002</p><p>\u4E00\u51FA\u624B\u9AD8\u4E0B\u7ACB\u5206\uFF0C\u8D6B\u8FDE\u96C4\u4E0E\u5B89\u5B50\u5B8F\u6BEB\u4E0D\u505C\u7559\u5730\u626C\u8D77\u4E00\u811A\uFF0C\u671D\u4E09\u4EBA\u4E2D\u6700\u5F31\u7684\u4E5E\u4F0F\u7A77\u9686\u5589\u5934\u4E0B\u9634\u8E22\u53BB\u3002\u4E5E\u4F0F\u7A77\u9686\u602A\u53EB\u4E00\u58F0\uFF0C\u7FFB\u8EAB\u9000\u5F00\u3002</p><p>\u8FD9\u51E0\u62DB\u5154\u8D77\u9E5C\u843D\uFF0C\u5F85\u4F17\u4EBA\u770B\u6E05\uFF0C\u8D6B\u8FDE\u96C4\u4E0E\u5B89\u5B50\u5B8F\u5DF2\u5404\u81EA\u843D\u5728\u6848\u4E0A\uFF0C\u8F7B\u98D8\u98D8\u6CA1\u6709\u53D1\u51FA\u4E00\u4E1D\u58F0\u97F3\u3002\u6797\u9999\u8FDC\u53CC\u817F\u88AB\u4ED6\u4EEC\u63D0\u5728\u624B\u4E2D\uFF0C\u62C9\u6210\u4E00\u6761\u76F4\u7EBF\uFF0C\u5782\u843D\u7684\u79C0\u53D1\u5728\u4E24\u5F20\u957F\u51E0\u95F4\u6765\u56DE\u98D8\u8361\u3002\u4E24\u4EBA\u5404\u8FD0\u529F\u529B\uFF0C\u6BEB\u4E0D\u76F8\u8BA9\u3002\u53EA\u89C1\u6797\u9999\u8FDC\u5149\u79C3\u79C3\u7684\u8089\u7A74\u8D8A\u626F\u8D8A\u5BBD\uFF0C\u7ADF\u662F\u52BF\u5747\u529B\u654C\u3002</p><p>\u5B89\u5B50\u5B8F\u773C\u89C1\u96BE\u4EE5\u53D6\u80DC\uFF0C\u4E0D\u7531\u51F6\u6027\u5927\u53D1\uFF0C\u56DE\u624B\u4E00\u626F\u3002\u9C9C\u7EA2\u7684\u8089\u7A74\u5E94\u624B\u62C9\u5F00\uFF0C\u4F1A\u9634\u5904\u6ED1\u817B\u7684\u808C\u80A4\u7ACB\u523B\u7EF7\u7D27\u3002</p><p>\u6A21\u6A21\u7CCA\u7CCA\u4E2D\u6797\u9999\u8FDC\u611F\u89C9\u5230\u4E24\u80A1\u771F\u6C14\u5728\u4F53\u5185\u51B2\u7A81\uFF0C\u63A5\u7740\u80EF\u95F4\u4E00\u9635\u5267\u75DB\uFF0C\u51E0\u4E4E\u88AB\u4EBA\u751F\u751F\u6495\u88C2\uFF0C\u4E0D\u7531\u75DB\u53EB\u5931\u58F0\uFF0C\u4E24\u624B\u5403\u529B\u5730\u62FC\u547D\u6309\u4F4F\u817F\u6839\u3002</p><p>\u79C3\u5934\u9E20\u76EE\u7684\u5B89\u5B50\u5B8F\u8138\u4E0A\u9732\u51FA\u4E00\u4E1D\u6B8B\u5FCD\u7684\u7B11\u610F\uFF0C\u4E94\u6307\u5982\u94A9\uFF0C\u7D27\u7D27\u6263\u7740\u6797\u9999\u8FDC\u5149\u6DA6\u7684\u811A\u8E1D\u3002\u8D64\u88F8\u7684\u7F8E\u5987\u767D\u9C7C\u4E00\u822C\u626D\u52A8\u5A07\u8EAF\uFF0C\u7B14\u76F4\u5F20\u7740\u53CC\u817F\uFF0C\u6323\u624E\u54C0\u6CE3\u3002\u5468\u56F4\u6570\u5341\u540D\u8D3A\u5BA2\u4EFF\u4F5B\u82CD\u8747\u89C1\u5230\u8840\u822C\u56F4\u7740\u4E09\u4EBA\uFF0C\u5BF9\u6797\u9999\u8FDC\u5149\u6E9C\u6E9C\u7684\u4E0B\u4F53\u548C\u5706\u6ED1\u7684\u4E73\u5C16\u6307\u6307\u70B9\u70B9\uFF0C\u7B11\u563B\u563B\u770B\u70ED\u95F9\u3002\u82E5\u662F\u5BD2\u6708\u5200\u5728\u4F17\u76EE\u777D\u777D\u4E4B\u4E0B\u88AB\u6495\u6210\u4E24\u534A\uFF0C\u7740\u5B9E\u523A\u6FC0\u3002</p>',59);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
