import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_53" tabindex="-1"><a class="header-anchor" href="#_53" aria-hidden="true">#</a> 53</h1><p>\u8D6B\u8FDE\u96C4\u51B7\u7B11\u4E00\u58F0\uFF0C\u53CD\u624B\u63E1\u4F4F\u80CC\u4E0A\u7684\u77ED\u621F\uFF0C\u5B89\u5B50\u5B8F\u4E5F\u6293\u7D27\u8170\u95F4\u7684\u5F2F\u94A9\uFF0C\u773C\u89C1\u5C31\u8981\u8840\u6E85\u5F53\u573A\u3002</p><p>\u6155\u5BB9\u9F99\u5475\u5475\u4E00\u7B11\uFF0C\u8EAB\u5F62\u5FAE\u6643\uFF0C\u63A0\u5165\u4EBA\u7FA4\u3002\u62AC\u624B\u5728\u4E5E\u4F0F\u7A77\u9686\u80A9\u4E0A\u8F7B\u8F7B\u4E00\u62CD\u3002\u4E5E\u4F0F\u7A77\u9686\u4E94\u6307\u4E00\u677E\uFF0C\u7D27\u63E1\u7684\u4E09\u679A\u94C1\u4E38\u843D\u5728\u6000\u91CC\u7684\u76AE\u56CA\u4E2D\u3002\u4ED6\u8FDE\u5FD9\u9000\u5F00\u4E00\u6B65\uFF0C\u5168\u795E\u6212\u5907\u3002\u4F46\u6155\u5BB9\u9F99\u53EA\u662F\u4E0E\u4ED6\u64E6\u80A9\u800C\u8FC7\uFF0C\u5206\u624B\u63E1\u4F4F\u5C11\u5987\u7684\u819D\u5F2F\u3002</p><p>\u8D6B\u8FDE\u96C4\u548C\u5B89\u5B50\u5B8F\u987F\u89C9\u5BF9\u65B9\u7684\u52B2\u6C14\u5267\u6DA8\uFF0C\u8FDE\u5FD9\u5C55\u81C2\u5411\u524D\u9001\u53BB\u3002\u300C\u556A\u300D\u7684\u4E00\u58F0\u8106\u54CD\uFF0C\u6797\u9999\u8FDC\u53CC\u819D\u5408\u62E2\u3002\u63A5\u7740\u592A\u4E00\u771F\u6C14\u5FFD\u6536\u5FFD\u653E\uFF0C\u6155\u5BB9\u9F99\u624B\u8155\u4E00\u8F6C\uFF0C\u6797\u9999\u8FDC\u7684\u811A\u8E1D\u8F7B\u8F7B\u5DE7\u5DE7\u4ECE\u4E24\u4EBA\u624B\u4E2D\u8131\u51FA\u3002</p><p>\u624B\u4E0A\u52B2\u529B\u4E00\u6CC4\uFF0C\u8D6B\u8FDE\u96C4\u548C\u5B89\u5B50\u5B8F\u7ACB\u8DB3\u4E0D\u7A33\uFF0C\u6ED1\u6B65\u4E0B\u6848\u3002\u4E24\u4EBA\u76F8\u987E\u60CA\u7591\u4E0D\u5B9A\uFF0C\u8FD9\u65F6\u4ED6\u4EEC\u5DF2\u77E5\u6155\u5BB9\u9F99\u662F\u501F\u529B\u6253\u529B\uFF0C\u5185\u529F\u5E76\u975E\u6781\u5F3A\uFF0C\u4F46\u4ED6\u5E74\u7EAA\u8F7B\u8F7B\u5C31\u6709\u8FD9\u7B49\u773C\u529B\u529F\u529B\u2026\u2026</p><p>\u661F\u6708\u6E56\u5BAB\u4E3B\u4E00\u51FA\u624B\u4FBF\u4ECE\u4E24\u53EA\u8001\u864E\u53E3\u4E2D\u593A\u4E0B\u7F8E\u98DF\uFF0C\u6BBF\u4E2D\u987F\u65F6\u54CD\u8D77\u4E00\u7247\u5F69\u58F0\u3002</p><p>\u6155\u5BB9\u9F99\u6D12\u7136\u7B11\u9053\uFF1A\u300C\u4E24\u4F4D\u6B66\u529F\u9AD8\u5F3A\uFF0C\u96BE\u5206\u9AD8\u4E0B\u2026\u2026\u300D\u4ED6\u8BED\u6C14\u4E00\u8F6C\uFF0C\u53D8\u5F97\u6177\u6168\u6FC0\u6602\uFF0C\u300C\u5404\u4F4D\u90FD\u662F\u6B66\u6797\u6210\u540D\u8C6A\u6770\uFF0C\u5F7C\u6B64\u5FD7\u540C\u9053\u5408\uFF0C\u53EA\u662F\u6563\u843D\u56DB\u5904\uFF0C\u4E0D\u901A\u97F3\u8BAF\uFF0C\u4E3A\u4E9B\u8BB8\u5C0F\u4E8B\u4FBF\u6027\u547D\u76F8\u640F\uFF0C\u53EF\u60DC\u53EF\u60DC\u2026\u2026\u300D</p><p>\u5B89\u5B50\u5B8F\u602A\u773C\u4E00\u7FFB\uFF0C\u53D1\u51FA\u4E00\u58F0\u51B7\u54FC\uFF0C\u6446\u660E\u4E0D\u5C3F\u4ED6\u8FD9\u4E00\u58F6\u3002\u6155\u5BB9\u9F99\u5FAE\u5FAE\u4E00\u7B11\uFF0C\u628A\u624B\u4E2D\u9999\u8F6F\u7684\u5A07\u8EAF\u9012\u5230\u4ED6\u6000\u4E2D\uFF0C\u300C\u5728\u4E0B\u9C81\u83BD\uFF0C\u5B89\u5144\u5207\u52FF\u89C1\u602A\u3002\u5475\u5475\uFF0C\u8FD9\u8D31\u4EBA\u80FD\u64CD\u7684\u4F55\u6B62\u4E00\u5904\uFF0C\u5B89\u5144\u548C\u8D6B\u8FDE\u5144\u4E0D\u59A8\u8054\u624B\uFF0C\u4E0E\u5BD2\u6708\u5200\u540C\u6597\u4E00\u573A\u3002\u300D\u8BF4\u7F62\u7B11\u563B\u563B\u5BF9\u8D6B\u8FDE\u96C4\u4F4E\u58F0\u9053\uFF1A\u300C\u8FD9\u8D31\u4EBA\u5C41\u773C\u522B\u5177\u98CE\u5473\uFF0C\u8D6B\u8FDE\u5144\u4E0D\u59A8\u4E00\u8BD5\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u4EE5\u5BAB\u4E3B\u4E4B\u5C0A\uFF0C\u5982\u6B64\u793C\u8D24\u4E0B\u58EB\uFF0C\u4E5F\u7B97\u7ED9\u8DB3\u4E86\u9762\u5B50\u3002\u8D6B\u8FDE\u96C4\u6717\u7B11\u4E00\u58F0\uFF0C\u4E0E\u5B89\u5B50\u5B8F\u4E00\u524D\u4E00\u540E\u628A\u6797\u9999\u8FDC\u5939\u5728\u4E2D\u95F4\uFF0C\u4E24\u6839\u8089\u68D2\u540C\u65F6\u6324\u5165\u3002\u6797\u9999\u8FDC\u5589\u5934\u53D1\u51FA\u4E00\u58F0\u53F9\u606F\u822C\u7684\u547B\u541F\uFF0C\u7A7A\u6D1E\u7684\u53CC\u773C\u5374\u6CA1\u6709\u4E00\u6EF4\u6CEA\u6C34\u3002</p><p>\u6155\u5BB9\u9F99\u5728\u4EBA\u7FA4\u4E2D\u7F13\u7F13\u8E31\u6B65\uFF0C\u4E00\u8FB9\u7528\u4F59\u5149\u89C2\u5BDF\u4F17\u4EBA\u7684\u795E\u8272\uFF0C\u4E00\u8FB9\u9053\uFF1A\u300C\u5982\u4ECA\u5929\u4E0B\u5206\u5D29\uFF0C\u4E2D\u539F\u677F\u8361\uFF0C\u6211\u661F\u6708\u6E56\u6709\u5FD7\u8054\u7EDC\u56DB\u65B9\u8C6A\u6770\uFF0C\u5171\u6518\u5927\u4E1A\u3002\u5C4A\u65F6\u8D22\u5BCC\u3001\u7F8E\u5973\u4EFB\u4E88\u4EFB\u53D6\uFF0C\u4F55\u51B5\u533A\u533A\u4E00\u4E2A\u5BD2\u6708\u5200\u3002\u300D\u4ED6\u8D70\u56DE\u6BBF\u4E0A\uFF0C\u7B11\u541F\u541F\u770B\u7740\u795E\u5C3C\u3002</p><p>\u96EA\u5CF0\u795E\u5C3C\u5782\u76EE\u5165\u5B9A\uFF0C\u4E0D\u518D\u770B\u7231\u5F92\u53D7\u8FB1\u7684\u573A\u666F\u3002</p><p>\u4E00\u756A\u8BDD\u4F17\u4EBA\u542C\u5F97\u660E\u660E\u767D\u767D\uFF0C\u6155\u5BB9\u9F99\u6BEB\u4E0D\u63A9\u9970\u81EA\u5DF1\u7684\u91CE\u5FC3\uFF0C\u6311\u660E\u8981\u9010\u9E7F\u5929\u4E0B\uFF0C\u4E0D\u5C11\u4EBA\u90FD\u4E3A\u4E4B\u5FC3\u52A8\u3002</p><p>\u91D1\u5F00\u7532\u662F\u6EE1\u8154\u70ED\u8840\uFF1B\u970D\u72C2\u7130\u548C\u5C60\u6000\u6C89\u5BF9\u5929\u4E0B\u5E76\u4E0D\u653E\u5728\u5FC3\u4E0A\uFF0C\u4F46\u8D22\u5BCC\u3001\u7F8E\u5973\u5F53\u7136\u8D8A\u591A\u8D8A\u597D\uFF1B\u4E00\u89D2\u7684\u6C90\u58F0\u4F20\u5374\u7709\u5934\u5FAE\u76B1\uFF0C\u89C9\u5F97\u5BAB\u4E3B\u6025\u4E8E\u6C42\u6210\uFF0C\u8BF4\u5F97\u592A\u8FC7\u9732\u9AA8\u3002\u4F46\u8FD9\u6837\u4E5F\u597D\uFF0C\u5BF9\u8FD9\u822C\u4EBA\u8FD8\u662F\u6311\u660E\u4E86\u7684\u597D\u3002\u987A\u6211\u8005\u660C\u9006\u6211\u8005\u4EA1\uFF0C\u8BF4\u4E0D\u5F97\u4E00\u4F1A\u513F\u8981\u6740\u4EBA\u7ACB\u5A01\u3002</p><p>\u6BBF\u5185\u620F\u8C11\u58F0\u54CD\u6210\u4E00\u7247\uFF0C\u7EAA\u7709\u59A9\u548C\u6797\u9999\u8FDC\u7684\u547B\u541F\u75DB\u547C\u4E0D\u4F4F\u4F20\u6765\uFF0C\u96EA\u5CF0\u795E\u5C3C\u5374\u8138\u4E0A\u4E00\u65E0\u6240\u52A8\u3002\u6155\u5BB9\u9F99\u5BA1\u89C6\u826F\u4E45\uFF0C\u7A81\u7136\u820C\u7EFD\u6625\u96F7\uFF0C\u5927\u559D\u9053\uFF1A\u300C\u5E26\u98CE\u5974\u3002\u300D</p><p>\u98CE\u665A\u534E\u662F\u96EA\u5CF0\u795E\u5C3C\u4E00\u624B\u5E26\u5927\u7684\u5B64\u513F\uFF0C\u60C5\u540C\u6BCD\u5973\uFF0C\u4EFB\u5979\u4FEE\u4E3A\u518D\u9AD8\uFF0C\u95FB\u58F0\u4E5F\u4E0D\u7531\u5C55\u773C\u770B\u53BB\u3002</p><p>\u91D1\u94C3\u58F0\u54CD\uFF0C\u4E00\u4E2A\u66FC\u5999\u7684\u5973\u4F53\u56DB\u80A2\u7740\u5730\uFF0C\u6447\u6447\u6643\u6643\u5730\u722C\u4E86\u8FC7\u6765\u3002\u5979\u53F3\u81C2\u9F50\u8098\u800C\u65AD\uFF0C\u722C\u52A8\u65F6\u50CF\u65AD\u4E86\u524D\u817F\u7684\u6BCD\u72D7\u822C\u4E00\u8DDB\u4E00\u8DDB\uFF0C\u4E24\u53EA\u96EA\u4E73\u4E0D\u4F4F\u6447\u6446\u3002\u5176\u4E2D\u4E00\u53EA\u4E73\u623F\u4E73\u5C16\u88AB\u5207\u5F00\u4E00\u591A\u534A\uFF0C\u4E73\u5934\u76D6\u5B50\u822C\u7FFB\u5377\u8FC7\u6765\u3002\u8170\u80A2\u7EC6\u8F6F\uFF0C\u7C89\u81C0\u9AD8\u4E3E\uFF0C\u7EC6\u5AE9\u7684\u9999\u80A9\u8FD8\u6709\u672A\u6108\u7684\u4F24\u75D5\uFF0C\u67D4\u9888\u4E0A\u5957\u7740\u4E00\u4E2A\u94C1\u5236\u9879\u5708\u3002</p><p>\u98CE\u665A\u534E\u540D\u58F0\u72B9\u5728\u6797\u9999\u8FDC\u4E4B\u4E0A\uFF0C\u63D0\u8D77\u6D41\u971C\u5251\u7684\u5927\u540D\uFF0C\u6C5F\u6E56\u4E2D\u53EF\u8C13\u662F\u5C3D\u4EBA\u7686\u77E5\u3002</p><p>\u4ECA\u65E5\u6240\u6765\u8005\uFF0C\u6709\u534A\u6570\u90FD\u662F\u542C\u8BF4\u98CE\u665A\u534E\u5728\u661F\u6708\u6E56\u88AB\u91CE\u732A\u7834\u8EAB\uFF0C\u624D\u8D76\u8FC7\u6765\u770B\u70ED\u95F9\u7684\u3002</p><p>\u6B64\u65F6\u89C1\u5802\u5802\u4E00\u4E2A\u98CE\u91C7\u7167\u4EBA\u7684\u5973\u4FA0\u88AB\u5F04\u6210\u6BCD\u72D7\u822C\u6A21\u6837\uFF0C\u4EBA\u7FA4\u7ACB\u65F6\u5174\u594B\u8D77\u6765\u3002\u51E0\u4E2A\u5728\u5979\u624B\u4E0B\u5403\u4E8F\u7684\u5996\u4EBA\u66F4\u662F\u9AD8\u547C\u72C2\u7B11\uFF0C\u5B9B\u5982\u7FA4\u9B54\u4E71\u821E\u3002</p><p>\u98CE\u665A\u534E\u8138\u4E0A\u5E26\u7740\u5446\u6EDE\u7684\u7B11\u5BB9\uFF0C\u5BF9\u4F17\u4EBA\u7684\u6B22\u547C\u6BEB\u65E0\u53CD\u5E94\uFF0C\u5979\u624B\u81C2\u5DF2\u65AD\uFF0C\u53EA\u80FD\u4EE5\u8098\u652F\u5730\uFF0C\u4E0A\u8EAB\u524D\u503E\uFF0C\u56E0\u6B64\u5706\u81C0\u7FD8\u5F97\u5206\u5916\u9AD8\u633A\uFF0C\u6DEB\u6001\u5341\u8DB3\u3002</p><p>\u6155\u5BB9\u9F99\u5728\u5979\u81C0\u540E\u8E22\u4E86\u4E00\u811A\uFF0C\u559D\u9053\uFF1A\u300C\u722C\u4E0B\u53BB\u6328\u64CD\u3002\u300D</p><p>\u98CE\u665A\u534E\u4F3C\u4E4E\u542C\u61C2\u547D\u4EE4\uFF0C\u6447\u7740\u96EA\u81C0\u671D\u72C2\u70ED\u7684\u4EBA\u7FA4\u722C\u53BB\u3002</p><p>\u96EA\u5CF0\u795E\u5C3C\u5589\u5934\u4E00\u751C\uFF0C\u4E00\u80A1\u6BB7\u7EA2\u7684\u9C9C\u8840\u4ECE\u5507\u89D2\u6D8C\u51FA\u3002\u8FD9\u5C31\u662F\u81EA\u5DF1\u95E8\u4E0B\u9996\u5F92\uFF0C\u6C14\u8D28\u99A5\u534E\u7684\u98CE\u665A\u534E\u2026\u2026\u6298\u78E8\u6210\u6DEB\u517D\u7684\u6B66\u6797\u5973\u4FA0\u2026\u2026</p><p>\u6155\u5BB9\u9F99\u4F4E\u7B11\u9053\uFF1A\u300C\u5E08\u592A\u600E\u4E48\u4E0D\u54BD\u4E0B\u53BB\u5462\uFF1F\u662F\u4E0D\u662F\u5589\u5499\u52A8\u5F39\u4E0D\u5F97\u554A\uFF1F\u300D</p><p>\u96EA\u5CF0\u795E\u5C3C\u6012\u76EE\u800C\u89C6\uFF0C\u9C9C\u7EA2\u7684\u8840\u8FF9\u4E00\u4E32\u4E32\u843D\u5728\u96EA\u767D\u7684\u50E7\u8863\u4E0A\uFF0C\u4EFF\u4F5B\u98D8\u6885\u5CF0\u96EA\u5730\u4E0A\u5E38\u5E74\u6012\u653E\u7684\u6885\u82B1\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u706F\u82B1\u7206\u54CD\uFF0C\u6155\u5BB9\u7D2B\u73AB\u773C\u795E\u8D8A\u6765\u8D8A\u51B7\u9177\uFF0C\u767D\u6C0F\u59D0\u59B9\u65E2\u7136\u5949\u4EE4\u4E0D\u8BB8\u81EA\u5DF1\u51FA\u95E8\uFF0C\u90A3\u5C31\u53EA\u80FD\u5F85\u5728\u8FD9\u91CC\uFF0C\u9759\u7B49\u5408\u537A\u4E4B\u65F6\u3002\u60F3\u5230\u90A3\u6839\u5947\u5F62\u602A\u72B6\u7684\u68D2\u5B50\u8981\u8FDB\u5165\u81EA\u5DF1\u4F53\u5185\uFF0C\u7D2B\u73AB\u7981\u4E0D\u4F4F\u5FC3\u4E0B\u6218\u6817\u3002</p><p>\u90FD\u662F\u8FD9\u4E24\u4E2A\u8D31\u4EBA\uFF01</p><p>\u5F53\u65E5\u7D2B\u73AB\u4ECE\u660F\u8FF7\u4E2D\u9192\u6765\uFF0C\u6155\u5BB9\u9F99\u5DF2\u7ECF\u5230\u5916\u9762\u4E0E\u4F17\u4EBA\u5546\u8BAE\u5A5A\u793C\u7684\u5B89\u6392\u3002\u5927\u5E08\u59D0\u88AB\u91CE\u517D\u5978\u6DEB\u7684\u753B\u9762\u59CB\u7EC8\u5728\u773C\u524D\u6643\u52A8\u2026\u2026\u7D2B\u73AB\u7FFB\u8EAB\u800C\u8D77\uFF0C\u5F3A\u8FEB\u81EA\u5DF1\u4E0D\u518D\u53BB\u60F3\u6CA6\u843D\u6210\u6BCD\u72AC\u7684\u98CE\u665A\u534E\u3002</p><p>\u9B54\u5BAB\u9759\u6084\u6084\u6CA1\u6709\u4E00\u70B9\u58F0\u54CD\uFF0C\u4EFF\u4F5B\u53EA\u5269\u4E0B\u81EA\u5DF1\u4E00\u4E2A\u4EBA\uFF0C\u5B64\u5B64\u96F6\u96F6\u65E0\u4F9D\u65E0\u9760\u3002\u7D2B\u73AB\u4E00\u628A\u62B9\u53BB\u6CEA\u6C34\uFF0C\u6DF1\u6DF1\u5438\u4E86\u53E3\u6C14\uFF0C\u7A33\u4F4F\u5FC3\u795E\uFF0C\u628A\u94F6\u9497\u63A2\u5165\u94A5\u6D1E\u5185\u3002</p><p>\u8FD9\u4E9B\u65E5\u5B50\u5979\u4E00\u76F4\u6CA1\u6709\u653E\u5F03\uFF0C\u4F9D\u9760\u81EA\u5DF1\u548C\u6BCD\u4EB2\u623F\u95F4\u7684\u4E24\u679A\u94A5\u5319\u4E0D\u4F4F\u8BD5\u9A8C\uFF0C\u5BFB\u627E\u5F00\u9501\u7684\u6280\u5DE7\u3002\u6628\u5929\u5979\u5DF2\u7ECF\u80FD\u7528\u94F6\u9497\u6253\u5F00\u6BCD\u4EB2\u7684\u623F\u95F4\u4E86\u3002</p><p>\u65F6\u95F4\u7F13\u7F13\u6D41\u901D\uFF0C\u6C57\u6C34\u4ECE\u9B13\u89D2\u4E00\u6EF4\u6EF4\u6ED1\u843D\u3002\u4E0D\u77E5\u8FC7\u4E86\u591A\u4E45\uFF0C\u624B\u4E0A\u5FAE\u5FAE\u4E00\u52A8\uFF0C\u4F20\u6765\u7C27\u7247\u300C\u5361\u300D\u7684\u4E00\u58F0\u8F7B\u54CD\u3002\u7D2B\u73AB\u4E00\u6123\uFF0C\u7136\u540E\u5FC3\u5934\u6D8C\u4E0A\u4E00\u9635\u72C2\u559C\u3002</p><p>\u5979\u6025\u5FD9\u63A8\u95E8\u800C\u5165\uFF0C\u4E09\u4E0B\u4E94\u53BB\u4E8C\uFF0C\u5C06\u6155\u5BB9\u9F99\u7684\u58EC\u5BA4\u7FFB\u4E86\u4E2A\u5E95\u671D\u5929\u3002\u6155\u5BB9\u9F99\u53EA\u662F\u6682\u5C45\uFF0C\u9648\u8BBE\u5E76\u4E0D\u590D\u6742\uFF0C\u4E0D\u591A\u65F6\u7D2B\u73AB\u4FBF\u4ECE\u5E8A\u5934\u6478\u51FA\u4E00\u53EA\u74F7\u74F6\u3002</p><p>\u74F6\u91CC\u76DB\u7740\u9ED1\u8910\u8272\u7684\u836F\u672B\uFF0C\u5FAE\u5FAE\u4E00\u55C5\uFF0C\u4E00\u80A1\u8F9B\u8FA3\u7684\u6C14\u5473\u6251\u9F3B\u800C\u6765\u3002\u4F53\u5185\u6DA3\u6563\u7684\u771F\u6C14\u95FB\u98CE\u800C\u52A8\uFF0C\u767E\u5DDD\u5F52\u6D77\u822C\u4E1D\u4E1D\u7F15\u7F15\u6D8C\u5165\u4E39\u7530\u3002\u7D2B\u73AB\u5927\u559C\u8FC7\u671B\uFF0C\u987E\u4E0D\u5F97\u518D\u53BB\u627E\u7247\u7389\u9632\u8EAB\uFF0C\u8FDE\u5FD9\u95EA\u8EAB\u51FA\u95E8\u3002</p><p>\u5F00\u4E86\u4E00\u628A\u9501\uFF0C\u7D2B\u73AB\u987F\u65F6\u4FE1\u5FC3\u5927\u589E\uFF0C\u53EF\u5728\u5E08\u7236\u95E8\u4E0A\u62E8\u5F04\u826F\u4E45\uFF0C\u95E8\u9501\u4E00\u65E0\u6240\u52A8\u3002</p><p>\u6108\u6025\u6108\u6253\u4E0D\u5F00\uFF0C\u7D2B\u73AB\u505C\u4E0B\u624B\uFF0C\u8C03\u606F\u51DD\u795E\u3002\u4E0D\u8FC7\u534A\u523B\u65F6\u95F4\uFF0C\u5185\u529F\u5DF2\u7ECF\u6062\u590D\u4E86\u56DB\u4E94\u6210\u3002\u6DA3\u6563\u591A\u65E5\u7684\u771F\u6C14\u91CD\u65B0\u5728\u4F53\u5185\u6E38\u52A8\uFF0C\u90A3\u79CD\u559C\u60A6\u4F7F\u7D2B\u73AB\u51E0\u4E4E\u6B22\u547C\u8D77\u6765\u3002</p><p>\u300C\u8C01\uFF1F\u300D\u795E\u5C3C\u65E9\u5DF2\u542C\u51FA\u95E8\u5916\u7684\u52A8\u9759\uFF0C\u7247\u523B\u540E\u4F4E\u58F0\u95EE\u3002</p><p>\u7D2B\u73AB\u4E00\u8FB9\u671D\u752C\u9053\u5916\u5F20\u671B\uFF0C\u4E00\u8FB9\u8D34\u5728\u95E8\u4E0A\u538B\u4F4E\u58F0\u97F3\u5174\u594B\u5730\u8BF4\uFF1A\u300C\u5E08\u7236\uFF0C\u6211\u627E\u5230\u89E3\u836F\u4E86\uFF01\u300D</p><p>\u795E\u5C3C\u53C8\u60CA\u53C8\u559C\uFF0C\u300C\u5FEB\u9012\u8FDB\u6765\uFF01\u300D</p><p>\u300C\u95E8\u6253\u4E0D\u5F00\u2014\u2014\u300D\u7D2B\u73AB\u56DB\u5904\u641C\u7D22\uFF0C\u7126\u6025\u5730\u8BF4\u3002\u7A81\u7136\u7075\u673A\u4E00\u52A8\uFF0C\u300C\u6211\u628A\u836F\u4ECE\u95E8\u4E0B\u5439\u8FDB\u53BB\uFF01\u300D</p><p>\u5979\u624B\u5FD9\u811A\u4E71\u5730\u628A\u836F\u672B\u5C3D\u6570\u5012\u5728\u95E8\u4E0B\u7684\u7F1D\u9699\u4E0A\uFF0C\u7136\u540E\u562C\u5507\u4E00\u5439\uFF0C\u7EC6\u5C18\u822C\u7684\u836F\u672B\u8F7B\u8F7B\u677E\u677E\u6D8C\u5165\u5BA4\u5185\u3002</p><p>\u53EA\u542C\u795E\u5C3C\u91CD\u91CD\u6253\u4E86\u4E2A\u55B7\u568F\uFF0C\u7D2B\u73AB\u4E00\u5B50\u4E0B\u8DF3\u4E86\u8D77\u6765\uFF0C\u6025\u6025\u7528\u94F6\u9497\u62E8\u5F04\u9501\u94A5\u3002\u5FC3\u91CC\u6068\u6068\u9053\uFF1A\u300C\u6155\u5BB9\u9F99\uFF0C\u4F60\u4F1A\u6B7B\u7684\u5F88\u96BE\u770B\uFF01\u300D</p><p>\u6B63\u7528\u5FC3\u62E8\u5F04\uFF0C\u7A81\u7136\u80A9\u4E0A\u4E00\u9EBB\uFF0C\u94F6\u9497\u53EE\u7684\u6389\u5728\u5730\u4E0A\u3002\u7D2B\u73AB\u8F6C\u8FC7\u5934\uFF0C\u96BE\u4EE5\u7F6E\u4FE1\u5730\u770B\u7740\u80F8\u4E73\u76F8\u8FDE\u7684\u767D\u6C0F\u59D0\u59B9\u3002</p><p>\u300C\u4F60\u2026\u2026\u4F60\u4EEC\u2026\u2026\u4F60\u4EEC\u2026\u2026\u300D</p><p>\u767D\u7389\u83BA\u88AB\u5979\u7684\u76EE\u5149\u5413\u5F97\u98A4\u6296\u8D77\u6765\uFF0C\u300C\u5C11\u2026\u2026\u592B\u4EBA\uFF0C\u9003\u4E0D\u6389\u7684\u2026\u2026\u6211\u4E0D\u60F3\u6B7B\u2026\u2026\u300D</p><p>\u7D2B\u73AB\u50CF\u662F\u5931\u53BB\u4E86\u6240\u6709\u529B\u6C14\uFF0C\u8F6F\u7EF5\u7EF5\u4F9D\u5728\u95E8\u4E0A\uFF0C\u4E0D\u4F4F\u5598\u606F\u3002</p><p>\u300C\u5C11\u592B\u4EBA\uFF0C\u53EA\u8981\u542C\u5BAB\u4E3B\u5429\u5490\uFF0C\u4ED6\u2026\u2026\u300D</p><p>\u767D\u7389\u9E42\u8BDD\u672A\u8BF4\u5B8C\uFF0C\u7D2B\u73AB\u51FA\u624B\u5982\u7535\uFF0C\u4E00\u6307\u5C01\u4E86\u5979\u8170\u4E0B\u7684\u7A74\u9053\uFF0C\u63A5\u7740\u66F2\u8098\u51FB\u5411\u767D\u7389\u83BA\u808B\u4E0B\u3002</p><p>\u7D2B\u73AB\u5185\u529F\u5C1A\u672A\u5C3D\u590D\uFF0C\u539F\u62B5\u4E0D\u8FC7\u767D\u6C0F\u59D0\u59B9\u7684\u8054\u624B\uFF0C\u4F46\u5979\u8D81\u4E24\u5973\u4E0D\u5907\u5148\u51FB\u5012\u767D\u7389\u9E42\uFF0C\u4E24\u5973\u53C8\u88AB\u91D1\u73AF\u9501\u4F4F\u4E73\u5934\uFF0C\u8F6C\u52A8\u4E0D\u4FBF\uFF0C\u6570\u62DB\u95F4\u767D\u7389\u83BA\u5C31\u843D\u5728\u4E86\u4E0B\u98CE\u3002</p><p>\u7D2B\u73AB\u7D27\u7D27\u54AC\u4F4F\u7259\u5173\uFF0C\u4E24\u624B\u7FFB\u98DE\uFF0C\u51E4\u51F0\u5B9D\u5178\u7684\u771F\u6C14\u6D41\u52A8\u8D8A\u6765\u8D8A\u5FEB\u3002</p><p>\u767D\u7389\u83BA\u5DE6\u652F\u53F3\u7ECC\uFF0C\u8FED\u9022\u9669\u62DB\uFF0C\u582A\u582A\u67B6\u4F4F\u7D2B\u73AB\u5145\u6EE1\u6068\u610F\u7684\u4E00\u811A\uFF0C\u5979\u7A81\u7136\u653E\u58F0\u53EB\u9053\uFF1A\u300C\u5BAB\u4E3B\u2026\u2026\u5BAB\u4E3B\u2026\u2026\u300D</p><p>\u53EA\u53EB\u4E86\u4E24\u58F0\uFF0C\u752C\u9053\u5C3D\u5934\u7684\u77F3\u95E8\u4E4D\u7136\u6D1E\u5F00\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u300C\u597D\u4E86\u3002\u4F60\u53BB\u8BF7\u53F6\u62A4\u6CD5\u8FC7\u6765\u3002\u300D\u6155\u5BB9\u9F99\u653E\u4E0B\u7247\u7389\uFF0C\u628A\u73A9\u7740\u4E24\u679A\u5207\u65AD\u7684\u91D1\u73AF\u3002</p><p>\u7D2B\u73AB\u9759\u9759\u8EBA\u5728\u69BB\u4E0A\uFF0C\u5185\u529F\u672A\u590D\u5C31\u88AB\u5236\u4F4F\u7A74\u9053\u7684\u96EA\u5CF0\u795E\u5C3C\u659C\u659C\u5012\u5728\u5730\u4E0A\u3002\u7ACB\u4E0B\u5927\u529F\u7684\u767D\u7389\u83BA\u5982\u613F\u89E3\u53BB\u8054\u4E73\u4E4B\u5211\uFF0C\u745F\u7F29\u7740\u7ACB\u5728\u5BA4\u89D2\uFF0C\u9003\u907F\u7D2B\u73AB\u8FEB\u4EBA\u7684\u76EE\u5149\u3002</p><p>\u6155\u5BB9\u9F99\u53F9\u4E86\u53E3\u6C14\uFF0C\u300C\u8BE5\u8BF4\u7684\u90FD\u8BF4\u8FC7\u4E86\u3002\u4F60\u7ADF\u7136\u8FD8\u8981\u8FD9\u4E48\u505A\u2026\u2026\u4F60\u5E08\u7236\u3001\u5E08\u59D0\u5012\u8FD8\u6CA1\u4EC0\u4E48\u3002\u53EF\u5A18\u7684\u8EAB\u4F53\u2026\u2026\u5509\uFF0C\u4F60\u77E5\u9053\u5A18\u7684\u8EAB\u4F53\u4E0D\u597D\uFF0C\u600E\u4E48\u8FD8\u5FCD\u5FC3\u8BA9\u5979\u88AB\u4EBA\u7CDF\u8E4B\u5462\uFF1F\u300D</p><p>\u7D2B\u73AB\u5FC3\u5934\u53D1\u51B7\uFF0C\u6CA1\u60F3\u5230\u8FD9\u4E2A\u79BD\u517D\u7ADF\u7136\u771F\u8981\u8BA9\u4EBA\u8F6E\u5978\u81EA\u5DF1\u7684\u4EB2\u4EBA\uFF0C\u751A\u81F3\u8FB1\u53CA\u4EB2\u5A18\u2026\u2026</p><p>\u6155\u5BB9\u9F99\u61CA\u607C\u5730\u6447\u6447\u5934\uFF0C\u300C\u6211\u4E5F\u592A\u5927\u610F\u4E86\uFF0C\u7ADF\u7136\u5FD8\u4E86\u9501\u95E8\u2026\u2026\u300D\u4ED6\u4EB2\u5BC6\u5730\u5750\u5728\u7D2B\u73AB\u5E8A\u5934\uFF0C\u629A\u6478\u7740\u5979\u5149\u6ED1\u7684\u4FCF\u8138\uFF0C\u8F7B\u58F0\u9053\uFF1A\u300C\u59B9\u59B9\uFF0C\u5982\u679C\u4F60\u88AB\u51E0\u5343\u4E2A\u7537\u4EBA\u8F6E\u5978\u4E00\u904D\uFF0C\u4E5F\u8BB8\u5C31\u4E0D\u4F1A\u8FD9\u4E48\u4E0D\u4E56\u4E86\u3002\u300D</p><p>\u7D2B\u73AB\u4E00\u53E3\u6C14\u987F\u65F6\u564E\u5728\u5589\u5934\uFF0C\u5507\u74E3\u7981\u4E0D\u4F4F\u98A4\u6296\u8D77\u6765\u3002</p><p>\u6155\u5BB9\u9F99\u601C\u7231\u5730\u5728\u5979\u5507\u89D2\u8F7B\u8F7B\u4E00\u543B\uFF0C\u53F9\u9053\uFF1A\u300C\u53EF\u60DC\u4F60\u8981\u7ED9\u54E5\u54E5\u751F\u5B69\u5B50\uFF0C\u4E0D\u80FD\u8BA9\u90A3\u4E9B\u91CE\u79CD\u810F\u4E86\u8EAB\u5B50\u2026\u2026\u4F60\u8BF4\uFF0C\u6211\u8BE5\u600E\u4E48\u529E\u5462\uFF1F\u300D</p><p>\u7D2B\u73AB\u660E\u5A9A\u7684\u5927\u773C\u4E2D\u5145\u6EE1\u6CEA\u6C34\uFF0C\u5C0F\u58F0\u54C0\u6C42\u9053\uFF1A\u300C\u54E5\u54E5\uFF0C\u59B9\u59B9\u613F\u610F\u5AC1\u7ED9\u54E5\u54E5\uFF0C\u613F\u610F\u7ED9\u54E5\u54E5\u751F\u5B69\u5B50\u2026\u2026\u54E5\uFF0C\u653E\u8FC7\u5A18\u5979\u4EEC\u5427\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u6CE8\u89C6\u7740\u5979\u7684\u773C\u775B\uFF0C\u5546\u91CF\u9053\uFF1A\u300C\u54B1\u4EEC\u7684\u65B0\u5A5A\u4E4B\u591C\uFF0C\u8BA9\u5A18\u5728\u65C1\u8FB9\u770B\u7740\u597D\u4E0D\u597D\uFF1F\u300D</p><p>\u300C\u2026\u2026\u597D\u2026\u2026\u300D\u7D2B\u73AB\u77E5\u9053\u4ED6\u6BCF\u6B21\u90FD\u9700\u8981\u51E0\u4E2A\u5973\u4EBA\uFF0C\u8BA9\u5A18\u5728\u65C1\u8FB9\u770B\u7740\u5176\u5B9E\u5C31\u662F\u6BCD\u5973\u4FE9\u4E00\u8D77\u4F3A\u5019\u4ED6\u3002</p><p>\u300C\u8BA9\u4F60\u5E08\u7236\u4E5F\u5728\u65C1\u8FB9\u770B\u7740\u597D\u4E0D\u597D\uFF1F\u300D</p><p>\u300C\u2026\u2026\u6211\u5E08\u7236\u662F\u51FA\u5BB6\u4EBA\u2026\u2026\u300D\u7D2B\u73AB\u5FC3\u9053\uFF0C\u5A18\u5DF2\u7ECF\u88AB\u4ED6\u6C61\u4E86\u8EAB\u5B50\uFF0C\u751A\u81F3\u8FD8\u6000\u4E86\u4ED6\u7684\u5B69\u5B50\uFF0C\u65E0\u8BBA\u5982\u4F55\u4E5F\u8981\u4FDD\u4F4F\u5E08\u7236\u7684\u6E05\u767D\u2014\u2014\u8FD8\u6709\u6027\u547D\u3002</p><p>\u6155\u5BB9\u9F99\u70B9\u4E86\u70B9\u5934\uFF0C\u300C\u90A3\u5C31\u7B97\u4E86\u3002\u300D</p><p>\u300C\u54E5\uFF0C\u59B9\u59B9\u77E5\u9053\u9519\u4E86\uFF0C\u6211\u4EC0\u4E48\u90FD\u7B54\u5E94\u4F60\u2026\u2026\u4F60\u653E\u8FC7\u5979\u4EEC\u597D\u5417\uFF1F\u300D</p><p>\u300C\u653E\u5FC3\u5427\u2026\u2026\u300D\u8BF4\u7740\u6155\u5BB9\u9F99\u6258\u8D77\u7D2B\u73AB\u8F7B\u76C8\u7684\u9999\u8EAF\u3002</p><p>\u51FA\u95E8\u65F6\uFF0C\u7D2B\u73AB\u770B\u5230\u53F6\u884C\u5357\u62FF\u7740\u9488\u76D2\u8D70\u5165\u5E08\u7236\u7684\u77F3\u5BA4\u3002</p>',69);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
