import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_54" tabindex="-1"><a class="header-anchor" href="#_54" aria-hidden="true">#</a> 54</h1><p>\u98D8\u6885\u5CF0\u4E09\u540D\u5973\u5F92\u5E76\u80A9\u8DEA\u4F0F\uFF0C\u88AB\u6446\u6210\u72D7\u4EA4\u7684\u59FF\u52BF\u4EFB\u4EBA\u5978\u6DEB\u3002\u4E09\u5973\u7684\u79C0\u53D1\u90FD\u88AB\u633D\u7D27\uFF0C\u626C\u8D77\u5404\u5177\u7F8E\u6001\u7684\u4FCF\u8138\uFF0C\u7389\u5BB9\u4E0A\u6BCF\u4E2A\u7EC6\u5FAE\u7684\u8868\u60C5\u90FD\u5C3D\u6536\u773C\u5E95\u3002</p><p>\u5DE6\u9996\u7B2C\u4E00\u4E2A\u662F\u98CE\u665A\u534E\uFF0C\u5979\u8138\u4E0A\u5E26\u7740\u6728\u7136\u7684\u7B11\u610F\uFF0C\u5BF9\u4E8E\u8EAB\u540E\u62BD\u9001\u7684\u8089\u68D2\u6D51\u4E0D\u5728\u610F\u3002</p><p>\u76F8\u6BD4\u4E8E\u5DE8\u72AC\u7684\u9633\u5177\uFF0C\u8FD9\u4E9B\u90FD\u592A\u5C0F\u4E86\uFF0C\u6CA1\u6709\u4EC0\u4E48\u611F\u89C9\u3002</p><p>\u7D27\u6328\u7740\u5979\u7684\u662F\u6797\u9999\u8FDC\uFF0C\u5979\u7D27\u7D27\u54AC\u7740\u7EA2\u5507\uFF0C\u5982\u82B1\u7684\u8138\u5E9E\u4E0A\u6EE1\u662F\u75DB\u82E6\u4E4B\u8272\u3002\u65E0\u4F11\u6B62\u7684\u6298\u78E8\u4E2D\uFF0C\u4E08\u592B\u7684\u9762\u5BB9\u5DF2\u7ECF\u6A21\u7CCA\uFF0C\u4F46\u5979\u8FD8\u8BB0\u5F97\u90A3\u6BB5\u65F6\u95F4\u9C7C\u6C34\u4EA4\u6B22\u7684\u6B22\u6109\uFF0C\u800C\u5982\u4ECA\uFF0C\u4EFB\u4F55\u6311\u9017\u90FD\u96BE\u518D\u6709\u4E00\u4E1D\u5FEB\u611F\uFF0C\u8BB0\u5FC6\u4E2D\u9AD8\u6F6E\u8D8A\u6765\u523B\u9AA8\u94ED\u5FC3\u3002\u53EA\u6709\u75BC\u75DB\u7684\u62BD\u9001\uFF0C\u4F7F\u5979\u4E0D\u4F4F\u60F3\u8D77\u90A3\u4E9B\u7F8E\u5999\u7684\u611F\u89C9\u3002</p><p>\u65C1\u8FB9\u7684\u7EAA\u7709\u59A9\u5374\u662F\u9AD8\u6F6E\u8FED\u8D77\u3002\u534A\u4E2A\u65F6\u8FB0\u5185\uFF0C\u5979\u5DF2\u7ECF\u6CC4\u4E86\u4E09\u6B21\u8EAB\u5B50\uFF0C\u6B64\u65F6\u53C8\u662F\u6EE1\u8138\u6F6E\u7EA2\uFF0C\u5F20\u7740\u5C0F\u5634\u300C\u54A6\u54A6\u5440\u5440\u300D\u5A9A\u53EB\u8FDE\u58F0\u3002\u575A\u786C\u7684\u4E73\u5934\u4F38\u51FA\u5BF8\u8BB8\u957F\u77ED\uFF0C\u4E0E\u6797\u9999\u8FDC\u5149\u6E9C\u6E9C\u7684\u4E73\u5C16\u76F8\u6620\u6210\u8DA3\u3002</p><p>\u300C\u5475\u5475\uFF0C\u5E08\u592A\uFF0C\u4F60\u770B\u770B\u8D35\u5F92\u54EA\u4E2A\u6700\u6DEB\u8361\u5462\uFF1F\u300D\u6155\u5BB9\u9F99\u637B\u7740\u96EA\u5CF0\u795E\u5C3C\u7684\u8033\u5782\u7B11\u9053\u3002</p><p>\u96EA\u5CF0\u795E\u5C3C\u8138\u8272\u94C1\u9752\u3002\u5979\u5E73\u751F\u884C\u4FA0\u4ED7\u4E49\uFF0C\u51E0\u4F4D\u5F92\u513F\u867D\u7136\u79C9\u6027\u4E0D\u4E00\uFF0C\u51FA\u8EAB\u5404\u5F02\uFF0C\u4F46\u90FD\u662F\u79C0\u5916\u6167\u4E2D\u7684\u597D\u5973\u5B50\u3002\u4E0D\u6210\u60F3\u6BD5\u751F\u536B\u9053\u9664\u9B54\uFF0C\u5374\u5408\u95E8\u843D\u5165\u5996\u9B54\u624B\u4E2D\uFF0C\u773C\u7741\u7741\u770B\u7740\u5F92\u513F\u53D7\u5C3D\u79CD\u79CD\u975E\u4EBA\u7684\u51CC\u8FB1\u3002</p><p>\u90A3\u4E9B\u66FE\u7ECF\u98CE\u91C7\u593A\u76EE\u3001\u82F1\u6C14\u8FEB\u4EBA\u3001\u6E29\u5A49\u9AD8\u96C5\u7684\u8138\u5E9E\u4E00\u4E00\u53D8\u5F62\uFF0C\u6CA6\u4E3A\u7537\u4EBA\u6CC4\u6B32\u7684\u6DEB\u517D\u3001\u5668\u5177\u3001\u6027\u5974\u2026\u2026</p><p>\u795E\u5C3C\u773C\u524D\u4E00\u9ED1\uFF0C\u4E00\u53E3\u9C9C\u8840\u7BAD\u77E2\u822C\u55B7\u51FA\u3002</p><p>\u6155\u5BB9\u9F99\u653E\u58F0\u957F\u7B11\uFF0C\u300C\u4E45\u95FB\u5E08\u592A\u662F\u5929\u4E0B\u7B2C\u4E00\u9AD8\u624B\uFF0C\u795E\u529F\u76D6\u4E16\u3002\u5728\u4E0B\u4E0D\u624D\uFF0C\u613F\u4E0E\u5E08\u592A\u4E00\u8F83\u957F\u77ED\uFF0C\u63A2\u63A2\u795E\u5C3C\u6DF1\u6D45\uFF0C\u7EC6\u5FAE\u4E0D\u5230\u4E4B\u5904\u591A\u8BF7\u6307\u70B9\uFF0C\u7C97\u758F\u4E4B\u5904\u8FD8\u671B\u5305\u6DB5\u3002\u300D</p><p>\u8FD9\u756A\u8BDD\u8BF4\u5F97\u51A0\u5195\u5802\u7687\uFF0C\u4F46\u5176\u4E2D\u7684\u6DEB\u7325\u4E4B\u610F\u4E00\u95FB\u5373\u4E4B\uFF0C\u6BBF\u5185\u987F\u65F6\u4E00\u7247\u559D\u5F69\u58F0\u3002</p><p>\u95FB\u8BF4\u5BAB\u4E3B\u8981\u5F53\u573A\u7ED9\u96EA\u5CF0\u795E\u5C3C\u5F00\u82DE\uFF0C\u8FDE\u6B63\u5728\u5978\u6DEB\u8BF8\u5973\u7684\u51E0\u4EBA\u4E5F\u62AC\u8D77\u5934\u6765\uFF0C\u76F4\u52FE\u52FE\u770B\u7740\u6BBF\u4E0A\u91D1\u78A7\u8F89\u714C\u7684\u5B9D\u5EA7\u3002</p><p>\u6155\u5BB9\u9F99\u62AC\u8D77\u795E\u5C3C\u7684\u624B\u81C2\uFF0C\u5C06\u5979\u814B\u4E0B\u7684\u5E03\u7EBD\u4E00\u4E00\u89E3\u5F00\u3002\u8863\u895F\u5FAE\u5206\uFF0C\u9732\u51FA\u4E00\u7247\u6D01\u767D\u7684\u80F8\u812F\u3002\u6155\u5BB9\u9F99\u624B\u4E00\u62AC\uFF0C\u50E7\u8863\u626C\u8D77\uFF0C\u53EA\u89C1\u4E00\u6839\u62C7\u6307\u7C97\u7EC6\u7684\u9EBB\u7EF3\u4ECE\u795E\u5C3C\u4E73\u6655\u4E0A\u7F20\u8FC7\uFF0C\u4E24\u53EA\u4E73\u623F\u88AB\u538B\u6210\u5706\u997C\u5F62\u72B6\u3002\u80A5\u5AE9\u7684\u4E73\u8089\u4ECE\u7EF3\u4FA7\u6EA2\u51FA\u4E00\u56E2\u8000\u76EE\u7684\u767D\u4EAE\uFF0C\u6ED1\u817B\u8BF1\u4EBA\u3002</p><p>\u6155\u5BB9\u9F99\u4F38\u6307\u4E00\u52FE\uFF0C\u9EBB\u7EF3\u5E94\u624B\u800C\u65AD\uFF0C\u4E24\u56E2\u7855\u5927\u7684\u80A5\u4E73\u970D\u7136\u8DF3\u51FA\uFF0C\u98A4\u5FAE\u5FAE\u6643\u52A8\u4E0D\u5DF2\u3002</p><p>\u6155\u5BB9\u9F99\u6258\u7740\u4E73\u623F\u4E0A\u4E0B\u629B\u63B7\uFF0C\u663E\u793A\u5B83\u6C89\u7538\u7538\u7684\u4EFD\u91CF\uFF0C\u300C\u4E0D\u8BF4\u6B66\u529F\uFF0C\u5E08\u592A\u8FD9\u5BF9\u5976\u5B50\u53EF\u771F\u662F\u6B66\u6797\u5C11\u6709\u3002\u300D\u4ED6\u634F\u7740\u4E73\u6839\u628A\u4E73\u623F\u6324\u6210\u6D51\u5706\u7684\u8089\u7403\uFF0C\u4E3E\u5230\u4F17\u4EBA\u9762\u524D\u3002\u6BBF\u5185\u559D\u5F69\u58F0\u3001\u53EB\u597D\u58F0\u3001\u602A\u7B11\u58F0\u3001\u620F\u8C11\u58F0\u54CD\u6210\u4E00\u7247\u3002</p><p>\u300C\u770B\u4E0D\u51FA\u8D3C\u5C3C\u957F\u4E86\u8FD9\u4E48\u5BF9\u5927\u5976\uFF0C\u6211\u770B\u6709\u4E94\u65A4\uFF01\u300D</p><p>\u300C\u4E94\u65A4\uFF1F\u8D77\u7801\u4E03\u65A4\uFF01\u300D\u4EC7\u767E\u9CCC\u559D\u9189\u4E86\u822C\u8138\u6DA8\u5F97\u901A\u7EA2\uFF0C\u300C\u5434\u767B\u8001\u5A46\u90A3\u5BF9\u5976\u5B50\u8001\u5B50\u79F0\u8FC7\uFF0C\u6BD4\u8FD9\u8FD8\u5C0F\u5C31\u6709\u4E94\u65A4\uFF01\u300D</p><p>\u8BDD\u97F3\u521A\u843D\uFF0C\u5C31\u6709\u4EBA\u558A\u9053\uFF1A\u300C\u4EC7\u8001\u4E8C\uFF0C\u6DEE\u5B89\u90E1\u90A3\u8D77\u6848\u5B50\u662F\u4F60\u4EEC\u54E5\u513F\u4FE9\u505A\u7684\uFF1F\u300D</p><p>\u4EC7\u767E\u718A\u9AD8\u58F0\u9053\uFF1A\u300C\u6CA1\u9519\uFF01\u6211\u4EEC\u54E5\u513F\u4FE9\u4ECE\u73B0\u5728\u8D77\u62DC\u5728\u795E\u6559\u95E8\u4E0B\uFF0C\u83AB\u8BF4\u59D3\u5434\u7684\u53EA\u662F\u4E2A\u592A\u5B88\uFF0C\u5C31\u662F\u5978\u6740\u4E86\u523A\u53F2\u7684\u592B\u4EBA\u3001\u5BB0\u76F8\u7684\u5973\u513F\u53C8\u600E\u4E48\u7740\uFF01\u300D</p><p>\u5C60\u6000\u6C89\u95FB\u8A00\u7ACB\u5373\u62A2\u8EAB\u4E0A\u524D\uFF0C\u4E00\u8138\u7B11\u5BB9\u5730\u62C9\u4F4F\u4E24\u4EBA\u7684\u624B\u4EB2\u70ED\u5730\u8BF4\u9053\uFF1A\u300C\u8D24\u6606\u4EF2\u6709\u773C\u5149\uFF0C\u6709\u89C1\u8BC6\uFF01\u655D\u6559\u80FD\u5F97\u4E24\u4F4D\u76F8\u52A9\uFF0C\u771F\u662F\u7BF7\u58C1\u751F\u8F89\u2026\u2026\u300D</p><p>\u8840\u65A9\u53CC\u715E\u5F53\u573A\u6295\u8BDA\u4E3E\u52A8\u50CF\u4E00\u5757\u5DE8\u77F3\u6295\u5165\u6E56\u4E2D\uFF0C\u4F17\u4EBA\u7684\u55A7\u95F9\u58F0\u5982\u540C\u706B\u4E0A\u6D47\u6CB9\uFF0C\u53C8\u70ED\u95F9\u4E86\u4E09\u5206\u3002</p><p>\u6BBF\u89D2\u4E00\u4E2A\u9634\u6D4B\u6D4B\u7684\u58F0\u97F3\u9488\u4E00\u822C\u4F20\u6765\uFF0C\u300C\u4E03\u65A4\u90FD\u4E0D\u6B62\u3002\u767D\u8863\u5EB5\u90A3\u4E8C\u5341\u591A\u4E2A\u5C3C\u59D1\u90FD\u8BA9\u8D2B\u9053\u5403\u4E86\uFF0C\u9759\u8FDC\u8D3C\u5C3C\u90A3\u5BF9\u5976\u5B50\u4E00\u4E2A\u5C31\u6709\u516B\u65A4\uFF0C\u8FD8\u6CA1\u5979\u7684\u5927\u3002\u300D</p><p>\u795E\u6BBF\u987F\u65F6\u9759\u4E86\u4E0B\u6765\u3002\u5341\u5E74\u524D\u4EE5\u6697\u5668\u72EC\u6B65\u6C5F\u6E56\u7684\u767D\u8863\u5EB5\u88AB\u4EBA\u706D\u95E8\uFF0C\u5E08\u5F92\u4E8C\u5341\u4E03\u4EBA\u5168\u90E8\u5931\u8E2A\uFF0C\u6CA1\u60F3\u5230\u7ADF\u662F\u88AB\u8FD9\u4EBA\u4E00\u4E00\u5403\u6389\u3002</p><p>\u6155\u5BB9\u9F99\u62AC\u773C\u671B\u53BB\uFF0C\u53EA\u89C1\u90A3\u4EBA\u8EAB\u6750\u9AD8\u7626\uFF0C\u76D8\u7740\u53D1\u9AFB\uFF0C\u4E00\u8EAB\u9053\u88C5\u6253\u626E\uFF0C\u5374\u4E0D\u5728\u9080\u8BF7\u7684\u540D\u5355\u4E0A\u3002</p><p>\u6C90\u58F0\u4F20\u5E72\u54B3\u4E00\u58F0\uFF0C\u6DE1\u6DE1\u9053\uFF1A\u300C\u7075\u7389\u771F\u4EBA\u5927\u9A7E\u5149\u4E34\uFF0C\u672A\u514B\u8FDC\u8FCE\uFF0C\u8FD8\u671B\u6055\u7F6A\u3002\u300D</p><p>\u6B64\u8A00\u4E00\u51FA\uFF0C\u6BBF\u5185\u53C8\u662F\u5927\u54D7\u3002\u7075\u7389\u771F\u4EBA\u6614\u5E74\u6A2A\u884C\u6C5F\u6E56\uFF0C\u6700\u662F\u6B8B\u5FCD\u597D\u6740\uFF0C\u5C24\u559C\u751F\u98DF\u4EBA\u8089\u3002\u5341\u4E94\u5E74\u524D\u7A81\u7136\u6D88\u58F0\u533F\u8FF9\uFF0C\u4F17\u4EBA\u90FD\u4EE5\u4E3A\u4ED6\u662F\u6076\u8D2F\u6EE1\u76C8\uFF0C\u5374\u4E0D\u6599\u5728\u6B64\u51FA\u73B0\u3002</p><p>\u7075\u7389\u4E3E\u638C\u8EAC\u8EAB\u65BD\u793C\uFF0C\u300C\u6C90\u5144\u8FD8\u5728\u602A\u7F6A\u5C0F\u5F1F\u5417\uFF1F\u300D</p><p>\u661F\u6708\u6E56\u884C\u8E2A\u8BE1\u5BC6\uFF0C\u6C90\u58F0\u4F20\u5728\u6C5F\u6E56\u4E2D\u7684\u540D\u58F0\u5E76\u4E0D\u5F70\u663E\uFF0C\u6B64\u65F6\u89C1\u7075\u7389\u771F\u4EBA\u5982\u6B64\u5BA2\u6C14\uFF0C\u4F17\u4EBA\u5BF9\u4E2A\u6559\u4E66\u5320\u822C\u7684\u7CDF\u8001\u5934\u5B50\u90FD\u4E0D\u7981\u522E\u76EE\u76F8\u770B\u3002</p><p>\u6C90\u58F0\u4F20\u795E\u8272\u6728\u7136\uFF0C\u6CA1\u6709\u4F5C\u7B54\u3002</p><p>\u6155\u5BB9\u9F99\u89C1\u72B6\u7B11\u9053\uFF1A\u300C\u771F\u4EBA\u5148\u8BF7\u6682\u5750\uFF0C\u5F85\u5BB4\u540E\u518D\u884C\u7EC6\u8C08\uFF0C\u5982\u4F55\uFF1F\u300D</p><p>\u7075\u7389\u771F\u4EBA\u7A3D\u9996\u884C\u793C\uFF0C\u957F\u8896\u4E00\u632F\uFF0C\u76D8\u819D\u5750\u4E0B\u3002</p><p>\u6155\u5BB9\u9F99\u8F6C\u8FC7\u5934\u8F7B\u7B11\u9053\uFF1A\u300C\u7B49\u5927\u4F19\u90FD\u73A9\u817B\u4E86\uFF0C\u5C31\u628A\u8FD9\u5976\u5B50\u5272\u4E0B\u6765\u79F0\u79F0\u7A76\u7ADF\u6709\u591A\u91CD\u2014\u2014\u5E08\u592A\uFF0C\u4F60\u8BF4\u597D\u4E0D\u597D\uFF1F\u300D</p><p>\u96EA\u5CF0\u795E\u5C3C\u5FC3\u91CC\u9ED8\u5FF5\u4F5B\u53F7\uFF0C\u8BD5\u56FE\u538B\u4E0B\u5FC3\u5E95\u7FFB\u6EDA\u7684\u7F9E\u6124\u3002</p><p>\u6155\u5BB9\u9F99\u6325\u624B\u626F\u4E0B\u50E7\u888D\uFF0C\u767D\u8863\u98DE\u821E\u95F4\uFF0C\u96EA\u5CF0\u795E\u5C3C\u6574\u4E2A\u4E0A\u8EAB\u987F\u65F6\u88F8\u9732\u5728\u5916\u3002\u53EA\u89C1\u5149\u6DA6\u7684\u51B0\u808C\u96EA\u80A4\u4E0A\u91D1\u5149\u95EA\u52A8\uFF0C\u4E5D\u679A\u91D1\u9488\u6DF1\u6DF1\u63D2\u5728\u5979\u7684\u80A9\u5934\u9888\u4FA7\u8170\u808B\u7B49\u5904\uFF0C\u53EA\u9732\u51FA\u9488\u5C3E\u3002</p><p>\u6628\u65E5\u6155\u5BB9\u9F99\u62A2\u5728\u5316\u771F\u6563\u5931\u6548\u524D\u5236\u4F4F\u96EA\u5CF0\u795E\u5C3C\uFF0C\u60CA\u9B42\u752B\u5B9A\u4E0B\uFF0C\u60F3\u5230\u5BAB\u4E2D\u79D8\u6280\u300C\u51DD\u771F\u4E5D\u523A\u300D\u3002\u6B64\u6CD5\u4EE5\u4E13\u7834\u5185\u5BB6\u771F\u6C14\u7684\u51DD\u795E\u9488\u523A\u5165\u4EBA\u4F53\u7389\u6795\u3001\u51E4\u6C60\u7B49\u4E5D\u5904\u5927\u7A74\uFF0C\u4E00\u7ECF\u65BD\u5C55\uFF0C\u88AB\u5236\u8005\u6C14\u84C4\u4E39\u7530\uFF0C\u5374\u65E0\u6CD5\u8FD0\u7528\uFF0C\u800C\u4E14\u72B6\u540C\u6728\u5076\uFF0C\u8FDE\u5C0F\u6307\u4E5F\u65E0\u6CD5\u52A8\u4F5C\u3002</p><p>\u6155\u5BB9\u9F99\u5728\u795E\u5C3C\u8EAB\u4E0A\u4F7F\u7528\u8FD9\u7B49\u8017\u8D39\u5143\u6C14\u7684\u529F\u6CD5\uFF0C\u4E00\u6765\u662F\u8865\u5316\u771F\u6563\u7684\u4E0D\u8DB3\uFF0C\u53E6\u4E00\u65B9\u9762\u4E5F\u662F\u4E3A\u4E86\u6C72\u53D6\u795E\u5C3C\u7CBE\u7EAF\u6D51\u539A\u7684\u5185\u5BB6\u771F\u6C14\u3002</p><p>\u6905\u80CC\u4E00\u677E\uFF0C\u96EA\u5CF0\u795E\u5C3C\u968F\u4E4B\u5411\u540E\u4EF0\u5012\uFF0C\u80A5\u4E73\u9AD8\u8038\uFF0C\u66F4\u663E\u5F97\u7855\u5927\u65E0\u6BD4\u3002\u6155\u5BB9\u9F99\u6025\u4E8E\u501F\u5979\u7684\u529F\u529B\u4FEE\u70BC\u592A\u4E00\u7ECF\uFF0C\u5F53\u4E0B\u4E5F\u4E0D\u518D\u591A\u8BDD\uFF0C\u4E09\u628A\u4E24\u628A\u626F\u788E\u50E7\u8863\uFF0C\u7136\u540E\u5C06\u795E\u5C3C\u53CC\u817F\u67B6\u5728\u5B9D\u5EA7\u628A\u624B\u4E0A\u3002\u4E00\u8FB9\u629A\u6478\u4E00\u8FB9\u7B11\u9053\uFF1A\u300C\u5E08\u592A\u4FDD\u517B\u5F97\u771F\u597D\uFF0C\u8FD9\u8EAB\u7EC6\u8089\u8DDF\u8D35\u6D3E\u7684\u9AD8\u5F92\u4E0D\u76F8\u4E0A\u4E0B\uFF0C\u4E0D\u505A\u5A4A\u5B50\u672A\u514D\u6D6A\u8D39\u2026\u2026\u300D</p><p>\u4E00\u4E2A\u773C\u795E\u5C31\u80FD\u8BA9\u4EBA\u5C3F\u88E4\u5B50\u7684\u96EA\u5CF0\u795E\u5C3C\uFF0C\u5728\u5BBE\u5BA2\u9762\u524D\u5999\u5904\u6BD5\u9732\uFF0C\u4E00\u5E45\u6328\u64CD\u7684\u6A21\u6837\uFF0C\u4F17\u4EBA\u8840\u8109\u8D32\u5F20\u3002\u5F85\u6155\u5BB9\u9F99\u4EAE\u51FA\u90A3\u6839\u88AB\u6539\u9020\u5F97\u9762\u76EE\u5168\u975E\u7684\u5DE8\u68D2\uFF0C\u6BBF\u5185\u7684\u8F70\u95F9\u58F0\u7ACB\u5373\u54CD\u6210\u4E00\u7247\u3002</p><p>\u300C\u5BAB\u4E3B\u64CD\u5979\uFF01\u300D</p><p>\u300C\u64CD\u6B7B\u5979\uFF01\u64CD\u6B7B\u8FD9\u4E2A\u8D3C\u5C3C\uFF01\u300D</p><p>\u5B9D\u5EA7\u6781\u5BBD\uFF0C\u96EA\u5CF0\u795E\u5C3C\u4E24\u817F\u51E0\u4E4E\u5E73\u653E\u624D\u80FD\u642D\u4F4F\u6276\u624B\u3002\u5706\u6DA6\u7ED3\u5B9E\u7684\u5927\u817F\u4E4B\u95F4\uFF0C\u80A5\u539A\u7684\u82B1\u74E3\u6BB7\u7EA2\u593A\u76EE\u3002\u5979\u547C\u5438\u8D8A\u6765\u8D8A\u6025\u4FC3\uFF0C\u80F8\u4E73\u4E0D\u4F4F\u8D77\u4F0F\uFF0C\u7389\u6237\u655E\u9732\uFF0C\u8110\u4E0B\u4E09\u5206\u5904\u4E00\u6839\u7EC6\u7EC6\u7684\u91D1\u9488\u659C\u659C\u523A\u5165\u4E39\u7530\uFF0C\u6155\u5BB9\u9F99\u4E24\u6307\u5206\u5F00\u5FAE\u6E7F\u7684\u82B1\u74E3\uFF0C\u7528\u5A34\u719F\u7684\u6307\u6CD5\u6311\u9017\u7247\u523B\u3002</p><p>\u96EA\u5CF0\u795E\u5C3C\u591A\u5E74\u4FEE\u70BC\u51E4\u51F0\u5B9D\u5178\uFF0C\u672C\u5C31\u4F53\u8D28\u654F\u611F\uFF0C\u4E00\u7ECF\u6311\u9017\u79D8\u5904\u7ACB\u523B\u6DEB\u6C34\u6A2A\u6D41\u3002</p><p>\u6324\u5728\u524D\u6392\u7684\u96C1\u95E8\u4E09\u5947\u5662\u5662\u602A\u53EB\uFF0C\u4ED6\u4EEC\u7684\u9C9C\u5351\u8BDD\u4F17\u4EBA\u4E5F\u542C\u4E0D\u660E\u767D\uFF0C\u53EA\u542C\u7740\u5B89\u5B50\u5B8F\u7684\u58F0\u97F3\u5206\u5916\u523A\u8033\uFF0C\u300C\u6D6A\u51FA\u6C34\u513F\u4E86\uFF01\u54C8\u54C8\uFF0C\u4EC0\u4E48\u96EA\u5CF0\u795E\u5C3C\uFF0C\u88C5\u5F97\u51B0\u6E05\u7389\u6D01\uFF0C\u8FD8\u4E0D\u8DDF\u7A91\u5B50\u91CC\u7684\u59D0\u513F\u4E00\u6837\uFF1F\u4E00\u4E2A\u81ED\u5A4A\u5B50\uFF01\u300D</p><p>\u4EC7\u767E\u9CCC\u53EB\u9053\uFF1A\u300C\u8FD9\u6DEB\u5C3C\u5976\u5B50\u5927\uFF0C\u5C44\u4E5F\u591F\u80A5\u7684\uFF0C\u8DDF\u7EAA\u5A4A\u5B50\u53EF\u6709\u4E00\u6BD4\u3002\u300D</p><p>\u970D\u72C2\u7130\u72E0\u72E0\u5550\u4E86\u4E00\u53E3\uFF0C\u7EE7\u7EED\u628A\u624B\u4E2D\u7684\u4E00\u628A\u7B77\u5B50\u5C3D\u6570\u63D2\u5230\u98CE\u665A\u534E\u7684\u4E73\u6D1E\u5185\u3002\u6155\u5BB9\u9F99\u77E5\u9053\u4ED6\u5BF9\u98D8\u6885\u5CF0\u8BF8\u5973\u6068\u4E4B\u5165\u9AA8\uFF0C\u6015\u8FD9\u4E2A\u706B\u7206\u7684\u83BD\u6C49\u5F04\u51FA\u4EC0\u4E48\u4E8B\uFF0C\u5A5A\u5BB4\u524D\u53CD\u8986\u4EA4\u5F85\u8FC7\u3002\u56E0\u6B64\u970D\u72C2\u7130\u4E00\u76F4\u5F85\u5728\u65C1\u8FB9\u9ED8\u4E0D\u4F5C\u58F0\u3002\u4F46\u4E00\u770B\u5230\u98CE\u665A\u534E\uFF0C\u4ED6\u5FC3\u5934\u7684\u6012\u706B\u5C31\u564C\u564C\u5411\u5916\u5192\u3002</p><p>\u98CE\u665A\u534E\u4E73\u6D1E\u4E2D\u5DF2\u7ECF\u63D2\u4E86\u5341\u51E0\u679D\u5305\u91D1\u7389\u7BB8\uFF0C\u6491\u5F00\u6709\u5BF8\u8BB8\u5927\u5C0F\u3002\u75DB\u5F97\u6CEA\u6D41\u6EE1\u9762\uFF0C\u5440\u5440\u5730\u8FDE\u58F0\u4F4E\u53EB\u3002</p><p>\u6797\u9999\u8FDC\u548C\u7EAA\u7709\u59A9\u6B64\u65F6\u5747\u662F\u904D\u4F53\u9633\u7CBE\uFF0C\u81EA\u987E\u4E0D\u6687\u3002\u77ED\u77ED\u534A\u4E2A\u65F6\u8FB0\uFF0C\u4FBF\u6709\u5341\u51E0\u4EBA\u5149\u4E34\u8FC7\u5979\u4EEC\u7684\u8089\u7A74\u3001\u83CA\u809B\u548C\u5C0F\u5634\u3002</p><p>\u6155\u5BB9\u9F99\u628A\u7D2B\u7EA2\u7684\u9F9F\u5934\u62B5\u5728\u6ED1\u817B\u7684\u8089\u7A74\u4E0A\uFF0C\u7B11\u7740\u9053\uFF1A\u300C\u5E08\u592A\u5C0F\u5FC3\uFF0C\u5728\u4E0B\u8981\u8FDB\u53BB\u4E86\u3002\u300D</p><p>\u96EA\u5CF0\u795E\u5C3C\u7389\u4F53\u8F7B\u98A4\uFF0C\u8089\u7A74\u679C\u7136\u5E94\u58F0\u6536\u7D27\u3002</p><p>\u6155\u5BB9\u9F99\u5C31\u662F\u8981\u8BA9\u5979\u5BF9\u7834\u5904\u7684\u75DB\u82E6\u6C38\u4E16\u96BE\u5FD8\uFF0C\u5F85\u8089\u7A74\u6536\u7D27\uFF0C\u4ED6\u7ACB\u5373\u633A\u8EAB\u6324\u5165\u3002</p><p>\u8FD1\u5343\u9053\u76EE\u5149\u6CE8\u89C6\u4E0B\uFF0C\u5C0F\u513F\u62F3\u5934\u5927\u5C0F\u7684\u9F9F\u5934\u5C06\u5A07\u5AE9\u7684\u8089\u7247\u7F13\u7F13\u6324\u5F00\u3002</p><p>\u96EA\u5CF0\u795E\u5C3C\u901A\u4F53\u50F5\u786C\uFF0C\u5979\u5FD8\u8BB0\u4E86\u7F9E\u6124\uFF0C\u751A\u81F3\u8FDE\u72C2\u6D8C\u7684\u6012\u6C14\u4E5F\u88AB\u629B\u5728\u8111\u540E\uFF0C\u5168\u90E8\u5FC3\u795E\u90FD\u96C6\u4E2D\u5728\u4E0B\u4F53\u90A3\u6839\u706B\u70ED\u7684\u8089\u68D2\u4E0A\u3002\u6570\u5341\u5E74\u5B88\u8EAB\u5982\u7389\u7684\u8D1E\u6D01\u5373\u5C06\u6BC1\u4E8E\u4E00\u65E6\uFF0C\u800C\u4E14\u8FD8\u6709\u2026\u2026\u96EA\u5CF0\u795E\u5C3C\u5FC3\u5934\u7D27\u7D27\u63EA\u6210\u4E00\u56E2\uFF0C\u5FFD\u7136\u4E0B\u4F53\u4E00\u75DB\uFF0C\u5DE8\u5927\u7684\u9F9F\u5934\u5DF2\u7ECF\u6CA1\u5165\u8089\u7A74\u3002</p><p>\u6155\u5BB9\u9F99\u505C\u6B62\u524D\u8FDB\uFF0C\u8089\u68D2\u5FAE\u5FAE\u633A\u52A8\uFF0C\u611F\u53D7\u7740\u8584\u819C\u7684\u67D4\u97E7\u3002\u4E24\u624B\u5219\u987A\u7740\u7EC6\u8F6F\u7684\u8170\u80A2\u4E00\u8DEF\u5411\u4E0A\uFF0C\u4E00\u76F4\u6309\u5230\u9888\u4FA7\uFF0C\u7136\u540E\u6258\u7740\u5979\u7684\u540E\u8111\u5411\u8179\u4E0B\u6309\u53BB\uFF0C\u300C\u8FD9\u662F\u5E08\u592A\u5934\u4E00\u6B21\u5F53\u5A4A\u5B50\uFF0C\u4ED4\u7EC6\u770B\u7740\uFF0C\u4E3B\u5B50\u600E\u4E48\u64CD\u4F60\u7684\u8D31\u5C44\u2026\u2026\u300D</p><p>\u96EA\u5CF0\u795E\u5C3C\u7C89\u9762\u901A\u7EA2\uFF0C\u7D27\u5F20\u5F97\u900F\u4E0D\u8FC7\u6C14\u6765\u3002</p><p>\u56DB\u4E0B\u96C5\u96C0\u65E0\u58F0\uFF0C\u6240\u6709\u4EBA\u90FD\u5728\u7B49\u5F85\u795E\u5C3C\u7834\u5904\u7684\u4E00\u523B\u3002</p>',56);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
