import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_55" tabindex="-1"><a class="header-anchor" href="#_55" aria-hidden="true">#</a> 55</h1><p>\u6155\u5BB9\u9F99\u611F\u53D7\u7740\u6307\u5C16\u7684\u8109\u52A8\uFF0C\u5C31\u5728\u795E\u5C3C\u5FC3\u8DF3\u6700\u5267\u70C8\u7684\u4E00\u523B\uFF0C\u4ED6\u7A81\u7136\u5411\u524D\u4E00\u633A\u3002</p><p>\u8106\u5F31\u7684\u8584\u819C\u600E\u582A\u4ED6\u529B\u9053\u5341\u8DB3\u4E00\u51FB\uFF0C\u987F\u65F6\u4E4D\u7136\u7834\u788E\uFF0C\u8089\u68D2\u5DE8\u9F99\u822C\u76F4\u5165\u672A\u7ECF\u4EBA\u4E8B\u7684\u871C\u7A74\u3002</p><p>\u96EA\u5CF0\u795E\u5C3C\u53EA\u89C9\u4E0B\u4F53\u5267\u75DB\uFF0C\u300C\u554A\u2014\u2014\u5440\u2014\u2014\u300D\u53D1\u51FA\u4E00\u58F0\u5636\u5FC3\u88C2\u80BA\u7684\u60E8\u53EB\u3002</p><p>\u53EB\u58F0\u752B\u4E00\u51FA\u53E3\uFF0C\u795E\u5C3C\u7A81\u5730\u50F5\u4F4F\u4E86\u3002\u88AB\u53F6\u884C\u5357\u7684\u51DD\u771F\u4E5D\u523A\u5236\u4F4F\u540E\uFF0C\u522B\u8BF4\u51FA\u58F0\uFF0C\u5C31\u662F\u820C\u5934\u4E5F\u65E0\u6CD5\u52A8\u4F5C\u3002\u6CA1\u60F3\u5230\u6155\u5BB9\u9F99\u4F1A\u7A81\u7136\u62D4\u53BB\u91D1\u9488\uFF0C\u4F7F\u81EA\u5DF1\u5728\u4F17\u4EBA\u777D\u777D\u4E0B\u75DB\u53EB\u51FA\u58F0\u3002\u82E5\u975E\u5982\u6B64\uFF0C\u5C31\u662F\u88AB\u4EBA\u5343\u5200\u4E07\u5250\uFF0C\u5979\u4E5F\u4E0D\u4F1A\u53EB\u4E0A\u4E00\u58F0\u3002</p><p>\u6B64\u65F6\u7C97\u5927\u7684\u8089\u68D2\u5DF2\u7ECF\u8FDB\u5165\u5927\u534A\uFF0C\u6155\u5BB9\u9F99\u4E0D\u7B49\u795E\u5C3C\u6709\u6240\u51C6\u5907\uFF0C\u7ACB\u5373\u5411\u5916\u4E00\u62BD\u3002</p><p>\u8FD9\u4E00\u62BD\u4ED6\u662F\u6709\u610F\u65BD\u4E3A\uFF0C\u53EA\u89C1\u4E00\u80A1\u8840\u6CC9\u4ECE\u8089\u7A74\u5185\u7BAD\u77E2\u822C\u6FC0\u5C04\u800C\u51FA\u3002</p><p>\u5728\u573A\u7684\u90FD\u662F\u90AA\u9053\u4E2D\u4EBA\uFF0C\u5978\u6DEB\u63B3\u63A0\u65E0\u6076\u4E0D\u4F5C\uFF0C\u4F46\u8FD9\u6837\u8840\u5982\u6CC9\u6D8C\u7684\u7834\u5904\u8FD8\u662F\u7B2C\u4E00\u6B21\u770B\u5230\uFF0C\u4E2A\u4E2A\u770B\u5F97\u77AA\u76EE\u7ED3\u820C\u3002</p><p>\u770B\u5230\u81EA\u5DF1\u5904\u5B50\u7684\u9C9C\u8840\u98DE\u6E85\u800C\u51FA\uFF0C\u5267\u75DB\u653B\u5FC3\u7684\u96EA\u5CF0\u795E\u5C3C\u53C8\u662F\u5589\u5934\u53D1\u751C\uFF0C\u5598\u606F\u7740\u5410\u51FA\u4E00\u53E3\u9C9C\u8840\u3002</p><p>\u6155\u5BB9\u9F99\u5BF9\u96EA\u5CF0\u795E\u5C3C\u7684\u4FEE\u4E3A\u6781\u4E3A\u5FCC\u60EE\uFF0C\u5373\u4F7F\u5438\u53D6\u5979\u7684\u529F\u529B\u4E5F\u96BE\u4EE5\u5B89\u5FC3\uFF0C\u56E0\u6B64\u4E00\u9F13\u771F\u6C14\uFF0C\u8089\u68D2\u4E0A\u7684\u9897\u7C92\u3001\u5012\u523A\u7ACB\u65F6\u575A\u5982\u94C1\u77F3\uFF0C\u5728\u96EA\u5CF0\u795E\u5C3C\u65B0\u521B\u7684\u8089\u7A74\u5185\u72C2\u62BD\u731B\u9876\u3002</p><p>\u795E\u5C3C\u575A\u5FCD\u7247\u523B\uFF0C\u53EA\u89C9\u4E0B\u4F53\u5267\u75DB\u8FDE\u8FDE\uFF0C\u6574\u4E2A\u8089\u7A74\u4F3C\u4E4E\u90FD\u88AB\u8089\u68D2\u6495\u788E\u4E00\u822C\uFF0C\u6CA1\u6709\u4E00\u5BF8\u5B8C\u597D\uFF0C\u5230\u5904\u90FD\u662F\u76F4\u5165\u5FC3\u5E95\u7684\u75DB\u695A\u3002\u60F3\u5230\u5DF2\u7ECF\u88AB\u4F17\u4EBA\u542C\u5230\u4E86\u81EA\u5DF1\u7684\u75DB\u53EB\uFF0C\u5979\u4FBF\u4E0D\u518D\u52C9\u5F3A\u5FCD\u8010\u3002\u4E00\u8FB9\u54B3\u51FA\u5589\u4E2D\u7684\u9C9C\u8840\uFF0C\u4E00\u8FB9\u4F4E\u4F4E\u547B\u541F\u8D77\u6765\u3002</p><p>\u6155\u5BB9\u9F99\u5634\u89D2\u9732\u51FA\u4E00\u4E1D\u6B8B\u5FCD\u7684\u7B11\u610F\uFF0C\u624B\u8155\u4E00\u677E\uFF0C\u653E\u5F00\u795E\u5C3C\u7684\u67D4\u9888\uFF0C\u8089\u68D2\u6025\u8FDB\u6025\u51FA\uFF0C\u6BCF\u4E00\u6B21\u90FD\u91CD\u91CD\u6363\u5728\u82B1\u5FC3\u4E0A\u3002\u4ED6\u6709\u5FC3\u5728\u5927\u5EAD\u5E7F\u4F17\u4E0B\u628A\u8FD9\u4E2A\u5FC3\u8179\u4E4B\u60A3\u6D3B\u6D3B\u5978\u6B7B\u3002</p><p>\u4E0D\u8FC7\u7247\u523B\uFF0C\u795E\u5C3C\u4E0B\u4F53\u5DF2\u662F\u8840\u6D41\u5982\u6CE8\u3002</p><p>\u5996\u5F02\u8089\u68D2\u518D\u52A0\u4E0A\u6155\u5BB9\u9F99\u7684\u624B\u6BB5\uFF0C\u8FDE\u8361\u5987\u4E5F\u96BE\u4EE5\u62B5\u6297\uFF0C\u4F55\u51B5\u521A\u521A\u7834\u4F53\u7684\u96EA\u5CF0\u795E\u5C3C\u3002\u80A1\u95F4\u5A07\u67D4\u7684\u5AE9\u8089\u5C3D\u6570\u7EFD\u88C2\uFF0C\u80A5\u539A\u7684\u82B1\u74E3\u6CBE\u6EE1\u9C9C\u8840\u3002\u795E\u5C3C\u7389\u4F53\u7D27\u7EF7\uFF0C\u5927\u5F20\u7684\u53CC\u817F\u4E0D\u4F4F\u75C9\u631B\uFF0C\u4E24\u817F\u95F4\u7D27\u7A84\u7684\u8089\u7A74\u50CF\u88AB\u4E00\u679D\u5E03\u6EE1\u94A2\u523A\u7684\u707C\u70ED\u5DE8\u68D2\u6363\u5F97\u7C89\u788E\uFF0C\u75DB\u5F7B\u5FC3\u80BA\u3002\u5979\u75BC\u5F97\u904D\u4F53\u51B7\u6C57\uFF0C\u575A\u5FCD\u7247\u523B\u540E\uFF0C\u81EA\u5FD6\u7834\u4F53\u5FC5\u6B7B\uFF0C\u4E8E\u662F\u653E\u4E0B\u77DC\u6301\uFF0C\u75DB\u53EB\u8FDE\u58F0\u3002</p><p>\u5E2D\u95F4\u7684\u5978\u6DEB\u5DF2\u7ECF\u505C\u4E0B\u4E86\u6765\uFF0C\u6BCF\u4E2A\u4EBA\u90FD\u62AC\u7740\u5934\uFF0C\u770B\u7740\u96EA\u5CF0\u795E\u5C3C\u5728\u6155\u5BB9\u9F99\u8089\u68D2\u4E0B\u54C0\u547C\u75DB\u53EB\u7684\u60E8\u72B6\u3002\u4E09\u5973\u4E2D\u552F\u4E00\u8EAB\u4F53\u5B8C\u6574\u7684\u7EAA\u7709\u59A9\u5A07\u8EAF\u8DEA\u4F0F\uFF0C\u50BB\u50BB\u770B\u7740\u81EA\u5DF1\u5D07\u656C\u4FE1\u4EF0\u7684\u5E08\u7236\uFF0C\u5507\u89D2\u4E00\u7F15\u9633\u7CBE\u8D8A\u62C9\u8D8A\u957F\uFF0C\u4E00\u76F4\u62D6\u5230\u5730\u4E0A\u3002</p><p>\u867D\u7136\u75DB\u53EB\u58F0\u5DF2\u7ECF\u53D8\u5F62\uFF0C\u6797\u9999\u8FDC\u8FD8\u662F\u542C\u51FA\u662F\u5E08\u7236\u7684\u58F0\u97F3\u3002\u5979\u6728\u7136\u652F\u7740\u8EAB\u4F53\uFF0C\u82B3\u5FC3\u6E10\u6E10\u5316\u6210\u51B0\u51B7\u7684\u7070\u70EC\u3002</p><p>\u53EA\u6709\u98CE\u665A\u534E\u5BF9\u6BBF\u4E0A\u7684\u60E8\u53EB\u6BEB\u4E0D\u5728\u610F\uFF0C\u5979\u4E73\u4E0A\u7684\u4F24\u6D1E\u5DF2\u7ECF\u56E0\u4E3A\u970D\u72C2\u7130\u7C97\u66B4\u5730\u73A9\u5F04\u800C\u6495\u88C2\uFF0C\u4F46\u5979\u53EA\u662F\u4E0D\u77E5\u6240\u63AA\u5730\u6367\u7740\u4E73\u623F\uFF0C\u7709\u5934\u62E7\u7D27\uFF0C\u54C0\u54C0\u547B\u541F\u54ED\u6CE3\uFF0C\u751A\u81F3\u4E0D\u77E5\u9053\u62D4\u51FA\u90A3\u4E9B\u7ED9\u81EA\u5DF1\u5E26\u6765\u75DB\u82E6\u7684\u7B77\u5B50\u3002</p><p>\u591C\u8272\u6E10\u6D53\uFF0C\u661F\u6708\u6E56\u4EFF\u4F5B\u4E00\u4E2A\u88AB\u65F6\u95F4\u9057\u5FD8\u7684\u7A7A\u95F4\uFF0C\u6C89\u6D78\u5728\u9ED1\u6697\u4E0E\u5149\u660E\u4EA4\u6C47\u7684\u7F1D\u9699\u4E2D\u3002</p><p>\u6155\u5BB9\u9F99\u8138\u8272\u8D8A\u6765\u8D8A\u51DD\u91CD\uFF0C\u5F85\u96EA\u5CF0\u795E\u5C3C\u54ED\u53EB\u51FA\u58F0\uFF0C\u4ED6\u4FBF\u5F00\u59CB\u8FD0\u529F\u5438\u53D6\u795E\u5C3C\u7684\u771F\u5143\u3002</p><p>\u4EE5\u5F80\u53EA\u8981\u8089\u68D2\u62B5\u4F4F\u82B1\u5FC3\uFF0C\u771F\u6C14\u6D41\u8F6C\u95F4\uFF0C\u5973\u5B50\u4E39\u7530\u4E2D\u7684\u771F\u5143\u5C31\u4F1A\u50CF\u65CB\u8F6C\u7684\u6DA1\u6D41\uFF0C\u6CBF\u7740\u7CBE\u7BA1\u8FDB\u5165\u4F53\u5185\u3002\u53EF\u8FD9\u6B21\u8089\u68D2\u8FDE\u632F\u4E03\u6B21\uFF0C\u96EA\u5CF0\u795E\u5C3C\u4E39\u7530\u4E2D\u7684\u771F\u5143\u5374\u6BEB\u65E0\u53CD\u5E94\u3002</p><p>\u4ED6\u80FD\u611F\u89C9\u5230\u90A3\u80A1\u6D51\u539A\u65E0\u5339\u7684\u84EC\u52C3\u6C14\u65CB\u5728\u8089\u68D2\u9876\u7AEF\u4E0D\u4F4F\u8FD0\u8F6C\uFF0C\u5374\u5982\u540C\u6C34\u9762\u4E0A\u7684\u6CB9\u6EF4\uFF0C\u65E0\u6CD5\u878D\u5408\u3002</p><p>\u6155\u5BB9\u9F99\u4E00\u8FB9\u62BD\u9001\u4E00\u8FB9\u6C89\u601D\uFF0C\u6700\u540E\u6084\u6084\u62D4\u4E0B\u96EA\u5CF0\u795E\u5C3C\u4E39\u7530\u5904\u7684\u91D1\u9488\u2014\u2014\u4E5F\u8BB8\u662F\u56E0\u4E3A\u5B83\u7684\u7F18\u6545\uFF0C\u4F7F\u771F\u5143\u96BE\u4EE5\u5916\u6CC4\u3002</p><p>\u91D1\u9488\u521A\u521A\u8131\u79BB\u5C0F\u8179\uFF0C\u96EA\u5CF0\u795E\u5C3C\u4E39\u7530\u5185\u7ACB\u523B\u6FC0\u8361\u8D77\u6765\u3002\u6C79\u6D8C\u7684\u771F\u6C14\u6CE2\u6D9B\u822C\u6EDA\u6EDA\u4E0D\u606F\uFF0C\u4F46\u59CB\u7EC8\u81EA\u6210\u4F53\u7CFB\uFF0C\u6CA1\u6709\u4E00\u4E1D\u6D41\u5165\u6155\u5BB9\u9F99\u4F53\u5185\u3002</p><p>\u6155\u5BB9\u9F99\u637B\u7740\u91D1\u9488\u7684\u624B\u6307\u50F5\u5728\u534A\u7A7A\uFF0C\u4E24\u773C\u4E00\u7728\u4E0D\u7728\u5730\u76EF\u7740\u795E\u5C3C\uFF0C\u968F\u65F6\u51C6\u5907\u91CD\u65B0\u523A\u5165\u3002</p><p>\u4E39\u7530\u5185\u9F13\u8361\u7684\u771F\u6C14\u70BD\u70ED\u5982\u706B\uFF0C\u7535\u5149\u706B\u77F3\u822C\u98DE\u901F\u65CB\u8F6C\u3002\u6B63\u5F53\u6155\u5BB9\u9F99\u8D8A\u6765\u8D8A\u5FC3\u60CA\u7684\u65F6\u5019\uFF0C\u90A3\u80A1\u5E9E\u5927\u65E0\u5339\u7684\u771F\u6C14\u5374\u5728\u6BEB\u65E0\u5F81\u5146\u7684\u60C5\u51B5\u4E0B\u7A81\u7136\u505C\u6B62\u8FD0\u8F6C\uFF0C\u63A5\u7740\u6D88\u6563\u65E0\u8E2A\u3002</p><p>\u6155\u5BB9\u9F99\u5927\u60CA\u5931\u8272\uFF0C\u8FDE\u5FD9\u62D4\u51FA\u9633\u7269\u3002</p><p>\u96EA\u5CF0\u795E\u5C3C\u8138\u8272\u96EA\u767D\uFF0C\u6C14\u82E5\u6E38\u4E1D\uFF0C\u4EFF\u4F5B\u88AB\u4EBA\u62BD\u5E72\u4E86\u6240\u6709\u7CBE\u529B\u822C\u865A\u5F31\u3002\u4E0E\u6B64\u540C\u65F6\uFF0C\u773C\u4E2D\u8083\u6740\u7684\u5149\u8292\u6E10\u6E10\u9EEF\u6DE1\u3002\u5979\u7F13\u7F13\u5408\u4E0A\u773C\u775B\uFF0C\u80F8\u5185\u6B8B\u5B58\u7684\u6C14\u4F53\u4E00\u6D8C\u800C\u51FA\uFF0C\u4EFF\u4F5B\u4E00\u58F0\u957F\u5F97\u6CA1\u6709\u5C3D\u5934\u7684\u53F9\u606F\uFF0C\u53E3\u4E2D\u661F\u661F\u70B9\u70B9\u7684\u8840\u6CAB\u7EC6\u96E8\u822C\u56DB\u4E0B\u98DE\u6E85\u3002\u75C9\u631B\u7684\u7389\u4F53\u9010\u6E10\u5E73\u606F\uFF0C\u518D\u6CA1\u6709\u4E00\u4E1D\u52A8\u4F5C\u3002</p><p>\u6BBF\u4E2D\u4E00\u7247\u6B7B\u5BC2\uFF0C\u4F17\u4EBA\u65E2\u770B\u5F97\u60CA\u5FC3\u52A8\u9B44\uFF0C\u53C8\u6709\u4E9B\u610F\u72B9\u672A\u5C3D\u3002\u8C01\u80FD\u60F3\u5230\u540D\u9707\u5929\u4E0B\u7684\u96EA\u5CF0\u795E\u5C3C\u7ADF\u7136\u8FD9\u4E48\u4E0D\u8010\u64CD\uFF0C\u624D\u6345\u4E86\u51E0\u4E0B\u5C31\u6CA1\u6C14\u4E86\u3002</p><p>\u6797\u9999\u8FDC\u5FAE\u5FAE\u4FA7\u8FC7\u8138\uFF0C\u8138\u4E0A\u6EE1\u662F\u7591\u60D1\u3002\u7EAA\u7709\u59A9\u5462\u54DD\u822C\u8F7B\u58F0\u9053\uFF1A\u300C\u5E08\u7236\u6B7B\u4E86\u2026\u2026\u300D</p><p>\u6797\u9999\u8FDC\u5A07\u8EAF\u4E00\u8F6F\uFF0C\u4E00\u8A00\u4E0D\u53D1\u5730\u5012\u5728\u5730\u4E0A\u3002</p><p>\u6BBF\u4E0A\u67D4\u8F6F\u7684\u5973\u4F53\u6E10\u6E10\u53D8\u5F97\u575A\u786C\uFF0C\u5B9B\u5982\u51B0\u971C\uFF0C\u53EA\u6709\u80EF\u95F4\u6BB7\u7EA2\u7684\u9C9C\u8840\u8FD8\u4E0D\u4F4F\u6DCC\u843D\u3002</p><p>\u7EAA\u7709\u59A9\u6014\u6014\u843D\u4E0B\u6CEA\u6765\uFF0C\u5FFD\u7136\u81C0\u540E\u4E00\u75DB\uFF0C\u4E00\u53EA\u7C97\u7CD9\u7684\u624B\u6307\u786C\u751F\u751F\u6324\u5165\u83CA\u809B\u3002\u7EAA\u7709\u59A9\u626D\u8FC7\u5934\u6765\uFF0C\u542B\u7740\u773C\u6CEA\u9732\u51FA\u4E00\u4E2A\u59A9\u5A9A\u7684\u7B11\u5BB9\uFF0C\u300C\u5927\u7237\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u5BA1\u89C6\u7247\u523B\uFF0C\u5C55\u81C2\u53C9\u4F4F\u7389\u9888\u5C06\u96EA\u5CF0\u795E\u5C3C\u9AD8\u9AD8\u4E3E\u8D77\uFF0C\u671D\u4F17\u4EBA\u7B11\u9053\uFF1A\u300C\u8FD9\u5A4A\u5B50\u8FD8\u4E0D\u5982\u5979\u51E0\u4E2A\u5F92\u513F\u8010\u64CD\u2026\u2026\u8BF8\u4F4D\u82E5\u4E0D\u5ACC\u5F03\uFF0C\u4E0D\u59A8\u5C1D\u5C1D\u5979\u7684\u6ECB\u5473\u3002\u300D\u8BF4\u7740\u628A\u5979\u4E22\u5230\u5E2D\u95F4\u3002</p><p>\u6BBF\u5185\u53C8\u55A7\u95F9\u8D77\u6765\uFF0C\u6155\u5BB9\u9F99\u633A\u7740\u6EF4\u8840\u7684\u8089\u68D2\u6717\u58F0\u9053\uFF1A\u300C\u826F\u5BB5\u82E6\u77ED\uFF0C\u8BF8\u4F4D\u5C3D\u60C5\u4F5C\u4E50\uFF0C\u672C\u5BAB\u6682\u4E14\u5931\u966A\u3002\u300D\u8BF4\u7F62\u62F1\u624B\u79BB\u53BB\u3002</p><p>\u8D1F\u8D23\u62DB\u5F85\u5BBE\u5BA2\u7684\u5C60\u6000\u6C89\u7B11\u5475\u5475\u6765\u5230\u6BBF\u4E0A\uFF0C\u5C06\u6559\u5185\u4F3A\u4FAF\u7684\u5973\u5974\u5C3D\u6570\u62DB\u6765\uFF0C\u4F9B\u6765\u5BBE\u6DEB\u4E50\u3002\u4E00\u65F6\u95F4\u795E\u6BBF\u5185\u8102\u9999\u7C89\u6D53\uFF0C\u6625\u610F\u878D\u878D\u3002</p><p>\u6C90\u58F0\u4F20\u4E0E\u53F6\u884C\u5357\u5BF9\u6B64\u5174\u8DA3\u4E86\u4E86\uFF0C\u53C8\u8981\u56DE\u907F\u5BAB\u4E3B\u7684\u6D1E\u623F\u82B1\u70DB\u4E4B\u591C\uFF0C\u4FBF\u8054\u8882\u5230\u671B\u6708\u4EAD\u8D4F\u6708\u3002\u7075\u7389\u771F\u4EBA\u7565\u4E00\u8E0C\u8E87\uFF0C\u4E5F\u8DDF\u4E86\u51FA\u53BB\u3002</p><p>\u91D1\u5F00\u7532\u5219\u864E\u76EE\u751F\u5149\uFF0C\u4E00\u4E00\u6253\u91CF\u6765\u5BBE\uFF0C\u7740\u610F\u5BFB\u89C5\u4EBA\u624D\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u6155\u5BB9\u7D2B\u73AB\u5408\u8863\u9760\u5728\u5E8A\u5934\uFF0C\u4E24\u773C\u76EF\u7740\u58C1\u4E0A\u9690\u7EA6\u6D6E\u73B0\u7684\u82B1\u7EB9\u3002\u7EA2\u70DB\u8D8A\u70E7\u8D8A\u77ED\uFF0C\u5979\u5FC3\u91CC\u4E5F\u8D8A\u6765\u8D8A\u7740\u6025\u3002\u90A3\u6DF7\u86CB\u600E\u4E48\u53BB\u8FD9\u4E48\u4E45\uFF1F\u4ED6\u7B54\u5E94\u4E86\u4F1A\u653E\u8FC7\u5E08\u7236\u7684\u3002</p><p>\u6068\u6068\u770B\u4E86\u767D\u6C0F\u59D0\u59B9\u4E00\u773C\uFF0C\u7D2B\u73AB\u504F\u8FC7\u8138\u76D8\u7B97\u7740\u600E\u4E48\u5F00\u53E3\u8BF4\u660E\u81EA\u5DF1\u4E0D\u80FD\u7834\u4F53\u2014\u2014\u76F4\u63A5\u8BF4\u81EA\u5DF1\u4FEE\u70BC\u7684\u51E4\u51F0\u5B9D\u5178\u672A\u81F3\u5927\u6210\uFF1F\u4ED6\u4F1A\u4E0D\u4F1A\u4EE5\u4E3A\u81EA\u5DF1\u662F\u9A97\u4ED6\u5462\uFF1F</p><p>\u8111\u4E2D\u6D6E\u73B0\u51FA\u90A3\u6839\u72F0\u72DE\u7684\u9633\u5177\uFF0C\u7D2B\u73AB\u4E0D\u7531\u6253\u4E86\u4E2A\u5BD2\u5664\u2014\u2014\u5373\u4F7F\u6CA1\u7EC3\u8FC7\u51E4\u51F0\u5B9D\u5178\uFF0C\u90A3\u4E48\u5927\u7684\u4E1C\u897F\u4E5F\u4F1A\u8981\u81EA\u5DF1\u7684\u5C0F\u547D\u2026\u2026</p><p>\u4E00\u8FB9\u60F3\uFF0C\u4E00\u8FB9\u4E0D\u7531\u81EA\u4E3B\u7684\u4F38\u624B\u63A9\u5728\u8179\u4E0B\u3002\u5979\u6D17\u6FA1\u65F6\u89E6\u6478\u8FC7\u90A3\u4E2A\u5C0F\u7A74\uFF0C\u90A3\u91CC\u7D27\u5F97\u8FDE\u4E00\u6839\u624B\u6307\u90FD\u585E\u4E0D\u8FDB\u53BB\uFF0C\u4F55\u51B5\u662F\u513F\u81C2\u7C97\u7684\u5DE8\u7269\u5462\uFF1F</p><p>\u89C9\u5BDF\u5230\u81EA\u5DF1\u7F9E\u803B\u7684\u4E3E\u52A8\uFF0C\u7D2B\u73AB\u660E\u7389\u822C\u7684\u4FCF\u8138\u987F\u65F6\u98DE\u8D77\u4E00\u7247\u7EA2\u971E\u3002\u5979\u6084\u6084\u5550\u4E86\u4E00\u53E3\uFF0C\u538B\u4E0B\u5FC3\u5E95\u7684\u60E7\u610F\u3002</p><p>\u8EAB\u8FB9\u7684\u7F8E\u5987\u5FFD\u7136\u5FAE\u5FAE\u4E00\u52A8\uFF0C\u7D2B\u73AB\u8FDE\u5FD9\u4FEF\u8EAB\uFF0C\u8F7B\u58F0\u5524\u9053\uFF1A\u300C\u5A18\u3002\u300D</p><p>\u8427\u4F5B\u5974\u85CF\u5728\u5E8A\u89D2\u7684\u9634\u5F71\u91CC\uFF0C\u8138\u4E0A\u6CEA\u5149\u6D9F\u6D9F\u3002</p><p>\u7D2B\u73AB\u5FC3\u5934\u5FAE\u98A4\uFF0C\u5979\u8F7B\u8F7B\u8EBA\u5728\u6BCD\u4EB2\u6000\u4E2D\uFF0C\u62C9\u8D77\u8427\u4F5B\u5974\u8F6F\u7EF5\u7EF5\u7684\u624B\u81C2\u642D\u5728\u80CC\u4E0A\uFF0C\u8BA9\u6BCD\u4EB2\u62B1\u7740\u81EA\u5DF1\uFF0C\u67D4\u58F0\u9053\uFF1A\u300C\u5A18\uFF0C\u4E0D\u8981\u6015\u3002\u5973\u513F\u4E00\u5B9A\u4F1A\u6551\u4F60\u51FA\u53BB\u7684\u2026\u2026\u300D</p><p>\u96EA\u5CF0\u795E\u5C3C\u4E5F\u88AB\u751F\u64D2\u4E4B\u540E\uFF0C\u8427\u4F5B\u5974\u5BF9\u9003\u751F\u5DF2\u7ECF\u6CA1\u6709\u4E00\u4E1D\u5E0C\u671B\u4E86\u3002\u81EA\u5DF1\u65E0\u6240\u8C13\uFF0C\u53EA\u662F\u73AB\u513F\uFF0C\u51B0\u6E05\u7389\u6D01\u82B1\u679D\u822C\u7684\u5973\u513F\u88AB\u5AE1\u4EB2\u54E5\u54E5\u5F3A\u5A36\u2026\u2026</p><p>\u5973\u513F\u67D4\u987A\u5149\u4EAE\u7684\u957F\u53D1\u8F7B\u8F7B\u78E8\u64E6\u7740\u4E0B\u988C\uFF0C\u8427\u4F5B\u5974\u6EE1\u8179\u82E6\u6DA9\uFF0C\u5374\u65E0\u6CD5\u8A00\u8BF4\uFF0C\u53EA\u662F\u62BD\u54BD\u7740\u4E00\u53E0\u58F0\u5730\u4F4E\u5524\uFF1A\u300C\u73AB\u513F\u2026\u2026\u73AB\u513F\u2026\u2026\u73AB\u513F\u2026\u2026\u300D</p><p>\u6155\u5BB9\u7D2B\u73AB\u542C\u5F97\u9178\u695A\uFF0C\u6CEA\u6C34\u4E00\u6EF4\u6EF4\u843D\u5728\u6BCD\u4EB2\u80F8\u53E3\u7684\u8863\u895F\u4E0A\u3002\u534A\u664C\u540E\u5979\u6084\u6084\u64E6\u5E72\u6CEA\u6C34\uFF0C\u76F4\u8D77\u8170\u5C55\u989C\u4E00\u7B11\uFF0C\u300C\u5A18\uFF0C\u6CA1\u4E8B\u7684\u3002\u54E5\u54E5\u4E0D\u4F1A\u6B3A\u8D1F\u6211\u2026\u2026\u300D</p><p>\u7BA1\u4ED6\u4FE1\u8FD8\u662F\u4E0D\u4FE1\uFF0C\u53CD\u6B63\u81EA\u5DF1\u80AF\u5B9A\u4F1A\u6B7B\u3002\u8981\u6B7B\u8981\u6D3B\uFF0C\u8BA9\u4ED6\u770B\u7740\u529E\u597D\u4E86\u3002</p><p>\u5982\u679C\u60F3\u8981\u6D3B\u7684\uFF0C\u90A3\u5C31\u7B49\u5427\uFF0C\u4E5F\u8BB8\u5341\u5E74\uFF0C\u4E5F\u8BB8\u4E8C\u5341\u5E74\uFF0C\u4E5F\u8BB8\u4E09\u5341\u56DB\u5341\u5E74\uFF0C\u7B49\u81EA\u5DF1\u7EC3\u5230\u7B2C\u516B\u5C42\u5C31\u597D\u4E86\u2014\u2014\u54FC\uFF01\u80FD\u7EC3\u5230\u51E4\u51F0\u4E8E\u98DE\uFF0C\u6211\u5148\u628A\u4F60\u7684\u8111\u888B\u63EA\u4E0B\u6765\uFF01</p><p>\u623F\u95E8\u88AB\u6084\u65E0\u58F0\u606F\u5730\u63A8\u5F00\uFF0C\u8BA1\u8F83\u5DF2\u5B9A\u7684\u7D2B\u73AB\u6602\u8D77\u5934\uFF0C\u4E00\u8138\u8BA4\u771F\u5730\u8BF4\u9053\uFF1A\u300C\u6155\u5BB9\u9F99\uFF01\u6211\u2026\u2026\u300D</p><p>\u770B\u6E05\u6765\u4EBA\u7684\u60C5\u5F62\uFF0C\u7D2B\u73AB\u7684\u58F0\u660E\u987F\u65F6\u564E\u5728\u5589\u4E2D\u3002</p><p>\u6155\u5BB9\u9F99\u4E00\u8138\u5F97\u610F\u7684\u7B11\u5BB9\uFF0C\u4ED6\u4E0B\u4F53\u8D64\u88F8\uFF0C\u5DE8\u9633\u786C\u6886\u6886\u633A\u5728\u8EAB\u524D\uFF0C\u6DCB\u6F13\u7684\u9C9C\u8840\u968F\u7740\u4ED6\u7684\u6B65\u4F10\u4E00\u6EF4\u6EF4\u6D12\u5728\u7EAF\u767D\u7684\u957F\u7ED2\u5730\u6BEF\u4E0A\u3002</p><p>\u767D\u6C0F\u59D0\u59B9\u819D\u884C\u8FC7\u6765\uFF0C\u60F3\u5E2E\u4E3B\u5B50\u64E6\u51C0\u8EAB\u4F53\uFF0C\u5374\u88AB\u6155\u5BB9\u9F99\u4E00\u628A\u63A8\u5F00\u3002\u4ED6\u5BF9\u7D2B\u73AB\u5FAE\u7B11\u9053\uFF1A\u300C\u6709\u4EC0\u4E48\u8981\u544A\u8BC9\u54E5\u54E5\u7684\uFF1F\u300D</p><p>\u7D2B\u73AB\u82B3\u5FC3\u5927\u4E71\uFF0C\u534A\u664C\u624D\u98A4\u58F0\u9053\uFF1A\u300C\u4F60\u628A\u6211\u5E08\u7236\u600E\u4E48\u4E86\uFF1F\u300D</p><p>\u88AB\u8840\u8FF9\u67D3\u5F97\u901A\u7EA2\u7684\u5DE8\u68D2\u6602\u7136\u4E00\u633A\uFF0C\u6155\u5BB9\u9F99\u51B7\u7B11\u9053\uFF1A\u300C\u5978\u6B7B\u4E86\uFF01\u300D</p><p>\u7D2B\u73AB\u5446\u4E86\u7247\u523B\uFF0C\u7136\u540E\u795E\u8272\u5E73\u9759\u5730\u7B14\u76F4\u4F38\u5E73\u5A07\u8EAF\uFF0C\u4EF0\u9762\u8EBA\u5728\u534E\u4E3D\u82AC\u82B3\u7684\u9526\u887E\u4E4B\u4E2D\u3002</p><p>\u6848\u4E0A\u9AD8\u70E7\u7684\u7EA2\u70DB\u706B\u7130\u541E\u5410\uFF0C\u6591\u9A73\u7684\u70DB\u6CEA\u968F\u7740\u70DB\u8EAB\u7F13\u7F13\u6D41\u4E0B\u3002</p><p>\u706B\u5149\u6447\u66F3\u95F4\uFF0C\u6620\u51FA\u7D2B\u73AB\u5A07\u7F8E\u5982\u82B1\u7684\u8138\u5E9E\u3002\u5979\u9ED8\u4E0D\u4F5C\u58F0\u7684\u7D27\u95ED\u53CC\u773C\uFF0C\u957F\u957F\u7684\u776B\u6BDB\u4E00\u52A8\u4E0D\u52A8\u3002</p><p>\u6B7B\u4EA1\u662F\u4E0D\u662F\u5F88\u53EF\u6015\u5462\u2026\u2026</p>',61);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
