import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_56" tabindex="-1"><a class="header-anchor" href="#_56" aria-hidden="true">#</a> 56</h1><p>\u7EC6\u81F4\u7684\u4E94\u5B98\u7CBE\u7F8E\u7EDD\u4F26\uFF0C\u808C\u80A4\u6ED1\u817B\u5982\u8102\uFF0C\u6155\u5BB9\u9F99\u7528\u773C\u775B\u4ED4\u7EC6\u52FE\u5212\u7740\u7D2B\u73AB\u8138\u4E0A\u7684\u8F6E\u5ED3\uFF0C\u4E0D\u7531\u5FC3\u795E\u4FF1\u9189\u3002\u4E3A\u4E86\u8FD9\u4E00\u523B\uFF0C\u4ED6\u5DF2\u7ECF\u82E6\u82E6\u5FCD\u8010\u4E86\u5341\u516D\u5929\u2026\u2026\u4E0D\uFF0C\u662F\u5341\u516D\u5E74\u3002</p><p>\u4E00\u80A1\u6D53\u90C1\u7684\u7537\u6027\u6C14\u606F\u6251\u9762\u800C\u6765\uFF0C\u7D2B\u73AB\u5FC3\u810F\u731B\u7136\u6536\u7D27\u3002\u5979\u7D27\u7D27\u54AC\u4F4F\u7259\u5173\uFF0C\u5F3A\u5FCD\u7740\u8EAB\u4F53\u56E0\u6050\u60E7\u800C\u4EA7\u751F\u7684\u6218\u6817\u3002</p><p>\u8033\u8FB9\u7684\u547C\u5438\u6E10\u6E10\u7C97\u91CD\uFF0C\u70BD\u70ED\u7684\u5634\u5507\u4ECE\u989D\u5934\u5370\u4E0B\uFF0C\u6CBF\u7740\u4FCF\u4E3D\u7684\u9F3B\u6881\u91CD\u91CD\u543B\u5728\u7EA2\u5507\u4E0A\u3002</p><p>\u826F\u4E45\uFF0C\u6155\u5BB9\u9F99\u604B\u604B\u4E0D\u820D\u5730\u5410\u51FA\u6ED1\u5AE9\u7684\u5C0F\u820C\u3002\u4ED6\u5BF9\u7D2B\u73AB\u5F02\u6837\u7684\u5E73\u9759\u7565\u89C9\u5947\u602A\uFF0C\u4F46\u7F8E\u8272\u5F53\u524D\uFF0C\u4E5F\u65E0\u6687\u591A\u60F3\uFF0C\u9971\u543B\u4E00\u756A\u540E\u4FBF\u89E3\u5F00\u7D2B\u73AB\u7684\u7F57\u5E26\u3002</p><p>\u67D4\u8F6F\u7684\u8170\u8EAB\u76C8\u76C8\u4E00\u63E1\uFF0C\u9694\u7740\u4E24\u5C42\u5185\u8863\uFF0C\u8FD8\u80FD\u6E05\u695A\u611F\u53D7\u5230\u808C\u80A4\u7684\u5F39\u6027\u3002\u60F3\u5230\u8FD9\u4E2A\u5343\u5A07\u767E\u5A9A\u7684\u5C11\u5973\u662F\u81EA\u5DF1\u660E\u5A92\u6B63\u5A36\u7684\u5AE1\u4EB2\u59B9\u59B9\uFF0C\u6155\u5BB9\u9F99\u987F\u65F6\u6B32\u706B\u5347\u817E\u3002\u4ED6\u624B\u6307\u5FAE\u98A4\u5730\u6258\u8D77\u59B9\u59B9\u7EA4\u7EC6\u7684\u811A\u8E1D\uFF0C\u9664\u53BB\u7EE3\u978B\u3002</p><p>\u7F57\u889C\u7F13\u7F13\u892A\u4E0B\uFF0C\u9732\u51FA\u4E00\u53EA\u767D\u51C0\u9999\u8F6F\u7684\u7389\u8DB3\u3002\u5C0F\u5DE7\u7684\u811A\u8DBE\u6676\u83B9\u5254\u900F\uFF0C\u4EE4\u4EBA\u7231\u4E0D\u91CA\u624B\u3002\u6155\u5BB9\u9F99\u7D27\u7D27\u63E1\u4F4F\u8F6F\u7EF5\u7EF5\u7684\u811A\u638C\uFF0C\u8D34\u5728\u8138\u4E0A\uFF0C\u5FFD\u7136\u95F4\u4E00\u80A1\u8F9B\u8FA3\u7684\u611F\u89C9\u6D8C\u4E0A\u5FC3\u5934\uFF0C\u773C\u775B\u987F\u65F6\u6E7F\u4E86\u3002</p><p>\u53EA\u4E00\u77AC\u95F4\u4ED6\u4FBF\u6062\u590D\u4E86\u5E73\u9759\uFF0C\u91CD\u91CD\u5410\u4E86\u53E3\u6C14\uFF0C\u4ED6\u6536\u655B\u5FC3\u795E\uFF0C\u6545\u4F5C\u8F7B\u677E\u7684\u8F7B\u7B11\u4E00\u58F0\uFF0C\u5316\u89E3\u5FC3\u5934\u7684\u6FC0\u8361\u3002</p><p>\u7D2B\u73AB\u5589\u5934\u5FAE\u52A8\uFF0C\u5403\u529B\u7684\u54BD\u4E86\u53E3\u6D25\u6DB2\uFF0C\u5979\u9762\u4E0A\u867D\u7136\u9759\u82E5\u6B62\u6C34\uFF0C\u5C0F\u624B\u5374\u6697\u6697\u63E1\u7D27\u3002</p><p>\u5FFD\u7136\u80F8\u524D\u4E00\u51C9\uFF0C\u9C9C\u7EA2\u7684\u8863\u895F\u4E2D\u9732\u51FA\u4E00\u62B9\u96EA\u767D\u7684\u808C\u80A4\u3002\u5F53\u90A3\u53CC\u624B\u7ED5\u5230\u80CC\u540E\u89E3\u5F00\u62B9\u80F8\u7684\u7CFB\u5E26\u65F6\uFF0C\u7D2B\u73AB\u4E0D\u7531\u7D27\u5F20\u5F97\u5A07\u8EAF\u8F7B\u98A4\u3002</p><p>\u6155\u5BB9\u9F99\u67D4\u58F0\u9053\uFF1A\u300C\u522B\u6015\uFF0C\u54E5\u54E5\u4F1A\u5F88\u6E29\u5B58\u2014\u2014\u8BA9\u4F60\u5C1D\u5230\u4E16\u95F4\u6700\u7F8E\u5999\u7684\u6ECB\u5473\u2026\u2026\u300D</p><p>\u8BDD\u97F3\u4E00\u843D\uFF0C\u7D2B\u73AB\u7C89\u5AE9\u7684\u7389\u4F53\u5B9B\u5982\u5999\u624B\u8F7B\u62B9\u822C\uFF0C\u900F\u51FA\u4E00\u5C42\u9690\u7EA6\u7684\u6DE1\u7EA2\uFF0C\u63A5\u7740\u6108\u6765\u6108\u6DF1\u3002\u4E0E\u6B64\u540C\u65F6\uFF0C\u90A3\u80A1\u5C11\u5973\u7684\u9999\u751C\u6C14\u606F\uFF0C\u4E5F\u6108\u52A0\u6D53\u90C1\u3002</p><p>\u89C1\u5230\u7D2B\u73AB\u5982\u6B64\u52A8\u4EBA\u7684\u7F9E\u6001\uFF0C\u6155\u5BB9\u9F99\u80EF\u4E0B\u7684\u8089\u68D2\u80C0\u5F97\u51E0\u4E4E\u7206\u88C2\uFF0C\u4ED6\u4E00\u628A\u626F\u4E0B\u62B9\u80F8\uFF0C\u53EA\u89C1\u73AB\u7470\u4ED9\u5B50\u73B2\u73D1\u7684\u66F2\u7EBF\u72B9\u5982\u4E00\u6C6A\u6625\u6C34\uFF0C\u5E26\u7740\u52A8\u4EBA\u7684\u82AC\u82B3\uFF0C\u5728\u9526\u69BB\u4E0A\u67D4\u67D4\u6D41\u6DCC\u3002</p><p>\u5706\u6DA6\u9165\u4E73\u4E00\u624B\u4FBF\u53EF\u63E1\u4F4F\uFF0C\u9876\u7AEF\u4E24\u70B9\u5A07\u5AE9\u7684\u7C89\u7EA2\uFF0C\u56E0\u4E3A\u5267\u70C8\u7684\u5FC3\u8DF3\u800C\u9690\u9690\u98A4\u52A8\u3002</p><p>\u5149\u6D01\u7684\u53CC\u817F\u7D27\u7D27\u5E76\u62E2\uFF0C\u6CA1\u6709\u4E00\u4E1D\u7F1D\u9699\uFF0C\u5C0F\u8179\u5E95\u90E8\u76D6\u7740\u4E00\u5C42\u7A84\u7A84\u7684\u4E4C\u4EAE\u6BDB\u53D1\u3002</p><p>\u6155\u5BB9\u9F99\u8D8A\u770B\u8D8A\u7231\uFF0C\u4F38\u624B\u4ECE\u7D2B\u73AB\u4E24\u819D\u4E4B\u95F4\u63D2\u5165\uFF0C\u8BD5\u56FE\u770B\u6E05\u5904\u5B50\u7684\u5A07\u7F9E\u79D8\u5883\u3002\u89E6\u624B\u4E00\u7247\u4EE4\u4EBA\u9B42\u9500\u7684\u6ED1\u817B\uFF0C\u88AB\u7F9E\u6DA9\u67D3\u7EA2\u7684\u808C\u80A4\u6E29\u6DA6\u9999\u6696\uFF0C\u66F4\u663E\u5F97\u6625\u610F\u8361\u6F3E\u3002\u4ED6\u624B\u638C\u4E00\u8F6C\uFF0C\u5C11\u5973\u53CC\u817F\u67D4\u987A\u7684\u6084\u7136\u5206\u5F00\u3002</p><p>\u6B63\u5F85\u770B\u6E05\u59B9\u59B9\u80A1\u95F4\u7684\u7F8E\u5999\uFF0C\u8033\u8FB9\u7A81\u7136\u4F20\u6765\u4E00\u58F0\u5E26\u7740\u54ED\u8154\u7684\u75DB\u65A5\u3002</p><p>\u300C\u755C\u7272\uFF01\u300D\u8427\u4F5B\u5974\u6CE3\u58F0\u9A82\u9053\uFF0C\u300C\u6211\u600E\u4E48\u751F\u4E86\u4F60\u8FD9\u4E2A\u755C\u7272\u2026\u2026\u4F60\u6B3A\u8D1F\u4E86\u5A18\u8FD8\u4E0D\u591F\uFF0C\u8FDE\u4EB2\u59B9\u59B9\u4E5F\u4E0D\u653E\u8FC7\u2026\u2026\u4F5B\u7956\uFF0C\u4F60\u600E\u4E48\u4E0D\u5288\u4E86\u8FD9\u4E2A\u79BD\u517D\u554A\u2026\u2026\u300D</p><p>\u300C\u55E4\u5566\u300D\u4E00\u58F0\uFF0C\u8427\u4F5B\u5974\u7684\u54ED\u6CE3\u5E94\u58F0\u800C\u6B62\u3002</p><p>\u6155\u5BB9\u9F99\u793A\u5A01\u822C\u626C\u8D77\u624B\u4E2D\u7684\u788E\u8863\uFF0C\u63A5\u7740\u624B\u6307\u4E00\u5F39\uFF0C\u629B\u5728\u5730\u4E0A\uFF0C\u7136\u540E\u5C06\u767E\u82B1\u89C2\u97F3\u7684\u534E\u670D\u6495\u5F97\u7C89\u788E\u3002</p><p>\u8427\u4F5B\u5974\u9762\u4E0A\u70ED\u6CEA\u7EB5\u6A2A\uFF0C\u8D1D\u9F7F\u54AC\u7740\u7EA2\u5507\u98A4\u6296\u4E0D\u5DF1\uFF0C\u513F\u5B50\u6BD4\u79BD\u517D\u8FD8\u4E0B\u6D41\u7684\u4E3E\u52A8\uFF0C\u4F7F\u5979\u54C0\u75DB\u6B32\u7EDD\u3002</p><p>\u300C\u63A5\u7740\u9A82\u554A\uFF0C\u600E\u4E48\u4E0D\u9A82\u4E86\uFF1F\u300D\u6155\u5BB9\u9F99\u6258\u8D77\u6BCD\u4EB2\u7684\u4E0B\u5DF4\uFF0C\u5632\u8BBD\u9053\uFF0C\u300C\u662F\u4E0D\u662F\u60F3\u513F\u5B50\u7684\u9E21\u5DF4\u4E86\uFF1F\u300D</p><p>\u7D2B\u73AB\u7F8E\u76EE\u500F\u7136\u5F20\u5F00\uFF0C\u300C\u6DF7\u86CB\uFF01\u522B\u78B0\u6211\u5A18\uFF01\u300D</p><p>\u6155\u5BB9\u9F99\u77B3\u5B54\u4E00\u6536\uFF0C\u5BD2\u58F0\u9053\uFF1A\u300C\u4F60\u518D\u8BF4\u4E00\u904D\u3002\u300D</p><p>\u7D2B\u73AB\u5DF2\u7ECF\u4E0D\u6253\u7B97\u6D3B\u4E86\uFF0C\u773C\u90FD\u4E0D\u7728\u5730\u76EF\u7740\u4ED6\u8BF4\uFF1A\u300C\u4F60\u8FD9\u4E2A\u5343\u5200\u4E07\u5250\u7684\u6DF7\u86CB\uFF01\u5929\u6253\u96F7\u5288\u755C\u7272\uFF01\u6C38\u4E16\u4E0D\u5F97\u8D85\u751F\u7684\u65E0\u803B\u9F20\u8F88\uFF01\u4E0D\u8BB8\u4F60\u78B0\u6211\u5A18\uFF01\u300D</p><p>\u4ED6\u5988\u7684\uFF0C\u8FD9\u5C0F\u4E2B\u5934\u88C5\u5F97\u4E56\u5DE7\u542C\u8BDD\uFF0C\u539F\u6765\u4E00\u76F4\u90FD\u662F\u9A97\u8001\u5B50\u7684\uFF01\u4E00\u8154\u70ED\u60C5\u8981\u5408\u537A\u6210\u6B22\uFF0C\u5171\u6548\u4E8E\u98DE\u7684\u6155\u5BB9\u9F99\u4E0D\u7531\u6012\u6C14\u52C3\u53D1\uFF0C\u7A81\u7136\u66B4\u559D\u4E00\u58F0\uFF1A\u300C\u8FC7\u6765\uFF01\u300D</p><p>\u767D\u6C0F\u59D0\u59B9\u5728\u65C1\u8FB9\u770B\u5F97\u80C6\u6218\u5FC3\u60CA\uFF0C\u6123\u4E86\u4E00\u4E0B\u624D\u77E5\u9053\u4E3B\u5B50\u662F\u5728\u53EB\u81EA\u5DF1\uFF0C\u8FDE\u5FD9\u8DEA\u5230\u69BB\u524D\u3002</p><p>\u7D2B\u73AB\u5C16\u58F0\u9053\uFF1A\u300C\u6709\u79CD\u4F60\u5C31\u6740\u6211\u5427\uFF01\u300D</p><p>\u6155\u5BB9\u9F99\u54AC\u7259\u4E00\u7B11\uFF0C\u300C\u597D\u8BF4\u3002\u300D\u4ED6\u5934\u4E5F\u4E0D\u56DE\u7684\u5429\u5490\u9053\uFF1A\u300C\u628A\u706F\u70DB\u62FF\u8FC7\u6765\u3002\u300D</p><p>\u7D2B\u73AB\u5FC3\u4E00\u6A2A\uFF0C\u95ED\u76EE\u7B49\u6B7B\u3002</p><p>\u4E00\u4E08\u7EA2\u4E0A\u70DB\u5F71\u8F7B\u6447\uFF0C\u513F\u81C2\u7C97\u7EC6\u7684\u901A\u5BB5\u5DE8\u70DB\u4E0A\u76D8\u65CB\u7740\u6F06\u91D1\u9F99\u51E4\uFF0C\u6781\u5C3D\u96D5\u7422\u3002\u767D\u6C0F\u59D0\u59B9\u62D4\u4E0B\u8721\u70DB\uFF0C\u8DEA\u5728\u4E3B\u5B50\u8EAB\u540E\u3002</p><p>\u6155\u5BB9\u9F99\u62B1\u81C2\u633A\u8170\uFF0C\u9762\u8272\u9634\u6C89\u5730\u76EF\u7740\u7D2B\u73AB\uFF0C\u6DE1\u6DE1\u9053\uFF1A\u300C\u722C\u5230\u69BB\u4E0A\uFF0C\u628A\u8721\u70DB\u63D2\u5C44\u91CC\uFF0C\u7167\u4ED4\u7EC6\u4E86\u3002\u8BA9\u4E3B\u5B50\u770B\u6E05\u5148\u64CD\u6B7B\u54EA\u4E00\u4E2A\u3002\u300D</p><p>\u59D0\u59B9\u4FE9\u76F8\u987E\u5931\u8272\uFF0C\u8FD9\u5BF9\u7EA2\u70DB\u4E00\u624B\u96BE\u63E1\uFF0C\u8F83\u4E4B\u6155\u5BB9\u9F99\u7684\u5DE8\u9633\u8FD8\u7C97\u4E0A\u4E00\u4E9B\uFF0C\u6B8B\u70DB\u957F\u8FD1\u5C3A\u8BB8\uFF0C\u6C89\u7538\u7538\u4EFD\u91CF\u538B\u624B\uFF0C\u600E\u53EF\u7EB3\u5165\u9634\u4E2D\u3002\u53EF\u4E24\u5973\u7EB5\u7136\u5FC3\u4E0B\u6218\u6817\uFF0C\u5374\u8C01\u4E5F\u4E0D\u6562\u5F00\u53E3\u8BA8\u9976\u3002</p><p>\u59D0\u59B9\u4FE9\u5BF9\u671B\u4E00\u773C\uFF0C\u767D\u7389\u83BA\u65E0\u8A00\u7684\u8D77\u8EAB\u4E0A\u69BB\uFF0C\u8DEA\u4F0F\u5728\u89D2\u843D\u91CC\u3002\u5148\u6DF1\u6DF1\u5438\u4E86\u53E3\u6C14\uFF0C\u7136\u540E\u6C89\u8170\u4E3E\u81C0\uFF0C\u5C3D\u529B\u5C06\u79D8\u5904\u4EF0\u5929\u633A\u8D77\u3002\u767D\u7389\u9E42\u5F20\u5F00\u5C0F\u5634\uFF0C\u5728\u59D0\u59D0\u4E0B\u4F53\u4E0D\u4F4F\u8214\u8210\uFF0C\u7528\u9999\u553E\u6DA6\u6E7F\u8089\u7A74\u3002</p><p>\u7B49\u767D\u7389\u9E42\u4E3E\u8D77\u5DE8\u70DB\uFF0C\u53EA\u89C1\u7C97\u5927\u7684\u8721\u5E95\u51E0\u4E4E\u5C06\u59D0\u59D0\u7684\u82B1\u74E3\u5B8C\u5168\u906E\u76D6\u3002\u6B63\u8FDF\u7591\u95F4\uFF0C\u53EA\u542C\u6155\u5BB9\u9F99\u4E00\u58F0\u51B7\u54FC\uFF0C\u5979\u624B\u8155\u4E00\u98A4\uFF0C\u54AC\u7259\u628A\u8721\u5E95\u538B\u5728\u8089\u7F1D\u4E0A\u3002\u4E00\u8FB9\u63A8\u5165\uFF0C\u4E00\u8FB9\u4ECE\u7F1D\u9699\u91CC\u5C06\u67D4\u5AE9\u7684\u82B1\u74E3\u4E0D\u65AD\u5265\u51FA\u3002\u597D\u5728\u725B\u6CB9\u6240\u5236\u7684\u70DB\u4F53\u8FD8\u7B97\u5149\u6ED1\uFF0C\u88AB\u6155\u5BB9\u9F99\u5F00\u53D1\u8FC7\u7684\u8089\u7A74\u4E5F\u662F\u5F39\u6027\u5341\u8DB3\u3002\u7247\u523B\u540E\uFF0C\u767D\u7389\u83BA\u4E00\u58F0\u95F7\u54FC\uFF0C\u70DB\u8EAB\u7EC8\u4E8E\u6210\u529F\u5730\u6324\u5165\u8089\u7A74\u3002</p><p>\u767D\u7389\u9E42\u677E\u4E86\u53E3\u6C14\uFF0C\u6D45\u6D45\u9001\u5165\u5BF8\u8BB8\uFF0C\u4FBF\u51C6\u5907\u653E\u624B\u3002\u767D\u7389\u83BA\u5403\u529B\u5730\u8BF4\uFF1A\u300C\u653E\u677E\u2026\u2026\u518D\u6DF1\u4E00\u4E9B\u2026\u2026\u300D</p><p>\u767D\u7389\u9E42\u9192\u609F\u8FC7\u6765\uFF0C\u8FDE\u5FD9\u53C8\u63A8\u5165\u4E24\u5BF8\uFF0C\u628A\u5DE8\u70DB\u7262\u7262\u56FA\u5B9A\u5728\u59D0\u59D0\u4F53\u5185\u3002</p><p>\u767D\u7389\u83BA\u5C0F\u5FC3\u5730\u632A\u52A8\u53CC\u819D\uFF0C\u722C\u5230\u59B9\u59B9\u8EAB\u540E\u3002\u6A21\u4EFF\u7389\u9E42\u65B9\u624D\u7684\u52A8\u4F5C\uFF0C\u62AC\u5934\u6B32\u543B\u3002</p><p>\u67D4\u9888\u4E00\u52A8\uFF0C\u539F\u672C\u76F4\u7ACB\u7684\u7EA2\u70DB\u968F\u4E4B\u503E\u659C\uFF0C\u6EDA\u70EB\u7684\u70DB\u6CB9\u987F\u65F6\u6DCC\u5230\u7D27\u6491\u7684\u82B1\u74E3\u4E0A\uFF0C\u6E85\u8D77\u4E00\u7247\u707C\u75BC\u3002\u5979\u6015\u4E3B\u5B50\u7B49\u5F97\u4E0D\u8010\u70E6\uFF0C\u4E0D\u6562\u4F38\u624B\u63C9\u6413\uFF0C\u53EA\u597D\u5FCD\u7740\u75DB\u695A\uFF0C\u628A\u8721\u70DB\u585E\u8FDB\u59B9\u59B9\u5E72\u6DA9\u7684\u9634\u9053\u4E2D\u3002</p><p>\u767D\u751F\u751F\u7684\u80A5\u81C0\u817B\u5982\u7F8A\u8102\uFF0C\u6B63\u4E2D\u4E00\u6839\u7C97\u957F\u7684\u7EA2\u70DB\u7B14\u76F4\u633A\u7ACB\uFF0C\u70E7\u5F97\u6B63\u65FA\u3002\u83BA\u9E42\u59D0\u59B9\u5404\u636E\u4E00\u89D2\uFF0C\u52AA\u529B\u7FD8\u8D77\u5706\u81C0\uFF0C\u4E00\u52A8\u4E0D\u52A8\u5730\u7528\u8089\u4F53\u5145\u505A\u70DB\u53F0\u3002</p><p>\u6B64\u523B\u6155\u5BB9\u9F99\u7684\u6012\u6C14\u4E5F\u5DF2\u7ECF\u5E73\u606F\u4E86\u8BB8\u591A\uFF0C\u7BA1\u5979\u7684\uFF0C\u53CD\u6B63\u59B9\u59B9\u5DF2\u7ECF\u662F\u81EA\u5DF1\u6848\u4E0A\u7684\u9C7C\u8089\uFF0C\u72AF\u4E0D\u7740\u8DDF\u81EA\u5DF1\u7684\u98DF\u7269\u751F\u6C14\u3002\u4ED6\u5988\u7684\uFF0C\u5C0F\u4E2B\u5934\u771F\u662F\u7F8E\u5F97\u7D27\u5462\u3002</p><p>\u706B\u8FA3\u8FA3\u7684\u76EE\u5149\u767E\u770B\u4E0D\u538C\u5730\u5728\u5A07\u8273\u6B32\u6EF4\u7684\u7389\u4F53\u4E0A\u9021\u5DE1\u7740\uFF0C\u6155\u5BB9\u9F99\u5634\u89D2\u9732\u51FA\u4E00\u4E1D\u7B11\u5BB9\u3002\u4ED6\u7B11\u541F\u541F\u8DF3\u5230\u69BB\u4E0A\uFF0C\u5C06\u6BCD\u4EB2\u548C\u59B9\u59B9\u5E76\u80A9\u6446\u653E\u6574\u9F50\u3002</p><p>\u8427\u4F5B\u5974\u624B\u811A\u65E0\u529B\uFF0C\u53EA\u80FD\u4EFB\u4ED6\u4E3A\u6240\u6B32\u4E3A\uFF1B\u7D2B\u73AB\u4E00\u5FC3\u6C42\u6B7B\uFF0C\u7F8E\u76EE\u7D27\u95ED\uFF0C\u5BF9\u4ED6\u7684\u4E3E\u52A8\u4E0D\u95FB\u4E0D\u95EE\u3002\u6BCD\u5973\u4FE9\u4E00\u822C\u7684\u56FD\u8272\u5929\u9999\uFF0C\u7709\u679D\u5982\u753B\uFF0C\u5149\u6DA6\u7684\u7389\u4F53\u540C\u6837\u662F\u768E\u768E\u751F\u8F89\uFF0C\u7EC6\u770B\u6765\u5374\u53C8\u5404\u5177\u7F8E\u6001\u3002</p><p>\u7D2B\u73AB\u5E74\u7EAA\u5C1A\u5C0F\uFF0C\u8F83\u6BCD\u4EB2\u7565\u77EE\u4E00\u4E9B\uFF0C\u7C89\u5AE9\u7684\u8EAB\u4F53\u5F39\u6027\u5341\u8DB3\uFF0C\u808C\u80A4\u5439\u5F39\u53EF\u7834\uFF0C\u9971\u8574\u7740\u9752\u6625\u7684\u6D3B\u529B\u3002\u8427\u4F5B\u5974\u5219\u662F\u98CE\u97F5\u5341\u8DB3\uFF0C\u7389\u4F53\u53C8\u9999\u53C8\u8F6F\uFF0C\u522B\u6709\u4E00\u79CD\u6210\u719F\u7684\u8273\u6001\u3002</p><p>\u6155\u5BB9\u9F99\u5DE6\u987E\u53F3\u76FC\uFF0C\u6068\u4E0D\u80FD\u5206\u505A\u4E24\u4EBA\uFF0C\u597D\u6402\u7740\u4E24\u5177\u52A8\u4EBA\u7684\u7F8E\u4F53\u8086\u610F\u628A\u73A9\u3002\u4ED6\u63E1\u4F4F\u6BCD\u5973\u4FE9\u7684\u4E73\u623F\u4E00\u8FB9\u63C9\u634F\u4E00\u8FB9\u7B11\u9053\uFF1A\u300C\u59B9\u59B9\uFF0C\u4F60\u7684\u5976\u5B50\u6BD4\u5A18\u5C0F\u4E86\u4E9B\uFF0C\u53EF\u8981\u52AA\u529B\u5594\u3002</p><p>\u957F\u5F97\u53C8\u5706\u53C8\u5927\uFF0C\u54E5\u54E5\u624D\u559C\u6B22\u2026\u2026\u77A7\uFF0C\u5A18\u7684\u5976\u5B50\u591A\u597D\uFF0C\u80A5\u5AE9\u5AE9\u53C8\u7EC6\u53C8\u6ED1\uFF0C\u52A8\u8D77\u6765\u4E00\u8361\u4E00\u8361\u2026\u2026\u300D</p><p>\u300C\u5478\uFF01\u300D\u88AB\u513F\u5B50\u5982\u6B64\u73A9\u5F04\uFF0C\u8427\u4F5B\u5974\u7F9E\u5F97\u65E0\u5730\u81EA\u5BB9\uFF0C\u6068\u6068\u4E00\u53E3\u5550\u5230\u6155\u5BB9\u9F99\u8138\u4E0A\u3002</p><p>\u6155\u5BB9\u9F99\u4FCA\u8138\u4E0A\u7B11\u610F\u4E0D\u6539\uFF0C\u4ED6\u62FF\u8D77\u8427\u4F5B\u5974\u4E00\u53EA\u80A5\u4E73\u50CF\u62B9\u5E03\u822C\u4ED4\u7EC6\u64E6\u53BB\u8138\u4E0A\u7684\u9999\u553E\u3002\u6BCF\u65E5\u7528\u8309\u8389\u82B1\u6CB9\u6D82\u62B9\u8EAB\u4F53\uFF0C\u8427\u4F5B\u5974\u7684\u7389\u4E73\u767D\u5AE9\u82B3\u9999\uFF0C\u6478\u4E0A\u53BB\u6ED1\u4E0D\u6E9C\u624B\uFF0C\u300C\u5A18\u8FD9\u5BF9\u5976\u5B50\uFF0C\u54E5\u54E5\u600E\u4E48\u90FD\u73A9\u4E0D\u591F\u2026\u2026\u300D</p><p>\u300C\u556A\u300D\u4E00\u53EA\u5C0F\u624B\u7528\u529B\u6253\u5728\u624B\u81C2\u4E0A\uFF0C\u7D2B\u73AB\u7F8E\u76EE\u55B7\u706B\u5730\u76EF\u7740\u4ED6\uFF0C\u5FFD\u7136\u7389\u817F\u4E00\u5206\uFF0C\u6BC5\u7136\u9053\uFF1A\u300C\u6765\u5427\uFF01\u300D</p><p>\u6155\u5BB9\u9F99\u6EE1\u5FC3\u5947\u602A\u5730\u770B\u4E86\u5979\u4E00\u773C\uFF0C\u5F53\u773C\u5149\u843D\u5728\u7D2B\u73AB\u655E\u9732\u7684\u7389\u6237\u4E0A\uFF0C\u987F\u65F6\u51DD\u4F4F\u4E86\u3002</p><p>\u96EA\u767D\u7684\u7389\u80A1\u66F2\u7EBF\u67D4\u7F8E\uFF0C\u6676\u83B9\u5982\u7389\u3002\u6B63\u4E2D\u5D4C\u7740\u4E00\u9053\u7EC6\u7EC6\u7684\u5AE9\u7EA2\uFF0C\u82B1\u74E3\u5FAE\u5FAE\u5206\u5F00\uFF0C\u6697\u5410\u82AC\u82B3\u3002</p><p>\u6155\u5BB9\u9F99\u4E24\u773C\u9690\u9690\u53D1\u7EA2\uFF0C\u9633\u5177\u6012\u632F\uFF0C\u8089\u7C92\u4E00\u9897\u9897\u51F8\u8D77\uFF0C\u6DA8\u5F97\u7D2B\u9ED1\u53D1\u4EAE\u3002\u5E78\u597D\u4ED6\u8FD8\u8BB0\u5F97\u59B9\u59B9\u662F\u5904\u5B50\u4E4B\u8EAB\uFF0C\u91CD\u91CD\u5598\u4E86\u53E3\u6C14\uFF0C\u6155\u5BB9\u9F99\u4FEF\u8170\u5C06\u7D2B\u73AB\u62B1\u8D77\uFF0C\u653E\u5728\u6BCD\u4EB2\u8EAB\u4E0A\u3002</p><p>\u89E6\u624B\u987F\u89C9\u4E00\u7247\u6E29\u51C9\uFF0C\u51B0\u808C\u7389\u9AA8\u4EE4\u4EBA\u546F\u7136\u5FC3\u52A8\u3002\u4ED6\u89C9\u67E5\u5230\u5C11\u5973\u7684\u7D27\u5F20\uFF0C\u4E8E\u662F\u7B11\u9053\uFF1A\u300C\u8FD9\u53EF\u662F\u4F60\u81EA\u5DF1\u732E\u51FA\u8EAB\u5B50\u8981\u8BA9\u54E5\u54E5\u64CD\u7684\uFF0C\u6015\u4EC0\u4E48\u5462\uFF1F\u300D</p><p>\u7D2B\u73AB\u51B7\u54FC\u4E00\u58F0\uFF0C\u626D\u8FC7\u8138\u4E0D\u53BB\u7406\u4ED6\u3002</p><p>\u6155\u5BB9\u9F99\u54C8\u54C8\u4E00\u7B11\uFF0C\u4F38\u638C\u6258\u5728\u59B9\u59B9\u81C0\u4E0B\uFF0C\u4E3E\u5230\u9762\u524D\u3002</p><p>\u7389\u6237\u7A81\u8D77\uFF0C\u90A3\u9053\u72ED\u7D27\u7684\u8089\u7F1D\u4E4D\u7136\u7EFD\u653E\uFF0C\u9732\u51FA\u4E00\u7C92\u7EA2\u6DA6\u7684\u5C0F\u5C0F\u8089\u82BD\u3002\u82B1\u74E3\u5E95\u5904\uFF0C\u7EC6\u5C0F\u7684\u7A74\u53E3\u65F6\u9690\u65F6\u73B0\u3002</p><p>\u6155\u5BB9\u9F99\u8F7B\u8F7B\u5265\u5F00\u82B1\u74E3\uFF0C\u624B\u6307\u5F80\u5185\u4E00\u63A2\uFF0C\u53EA\u89C9\u67D4\u8F6F\u7684\u5AE9\u7A74\u7D27\u7EF7\u7EF7\u6536\u62E2\u5728\u4E00\u8D77\uFF0C\u91CC\u9762\u8F6C\u6765\u4E00\u80A1\u9690\u9690\u7684\u70BD\u70ED\u3002\u4ED6\u5927\u559C\u8FC7\u671B\uFF0C\u5FC3\u77E5\u59B9\u59B9\u79D8\u5904\u5FC5\u662F\u5176\u70ED\u5982\u706B\u7684\u5999\u7A74\u3002\u5F53\u4E0B\u6155\u5BB9\u9F99\u4FEF\u5728\u7D2B\u73AB\u7389\u817F\u95F4\uFF0C\u5148\u6DF1\u6DF1\u5438\u4E86\u53E3\u5904\u5B50\u7684\u5E7D\u9999\uFF0C\u7136\u540E\u4F38\u51FA\u820C\u5934\uFF0C\u5728\u73AB\u7470\u4ED9\u5B50\u7684\u82B1\u8482\u4E0A\u8F7B\u8F7B\u4E00\u8214\u3002</p><p>\u7C97\u7CD9\u7684\u820C\u857E\u5728\u5AE9\u8089\u4E0A\u5212\u8FC7\uFF0C\u4E00\u80A1\u524D\u6240\u672A\u6709\u7684\u5F3A\u70C8\u5FEB\u611F\u76F4\u5165\u8111\u9AD3\u3002\u7D2B\u73AB\u5A07\u8EAF\u4E00\u98A4\uFF0C\u9669\u4E9B\u53EB\u51FA\u58F0\u6765\u3002\u4E0D\u7B49\u5979\u7A33\u4F4F\u5FC3\u795E\uFF0C\u90A3\u6761\u820C\u5934\u5DF2\u7ECF\u5728\u82B1\u8482\u4E0A\u76D8\u65CB\u6311\u52A8\uFF0C\u4E0D\u65F6\u6CBF\u7740\u82B1\u74E3\u4E2D\u95F4\u7684\u7F1D\u9699\uFF0C\u4E00\u76F4\u4F38\u5230\u8089\u7A74\u5904\u3002\u53EA\u8214\u4E86\u6570\u4E0B\uFF0C\u7D2B\u73AB\u4FBF\u89C9\u5F97\u4E0B\u4F53\u5B9B\u5982\u4E00\u53EA\u719F\u900F\u7684\u6C34\u871C\u6843\uFF0C\u88AB\u4EBA\u8F7B\u8F7B\u54AC\u7834\u4E86\u4E00\u4E2A\u5C0F\u53E3\uFF0C\u9999\u751C\u7684\u871C\u6C41\u4ECE\u4F53\u5185\u6DF1\u5904\u503E\u6CC4\u800C\u51FA\u3002</p><p>\u5728\u6155\u5BB9\u9F99\u6781\u529B\u6311\u9017\u4E0B\uFF0C\u672A\u7ECF\u4EBA\u4E8B\u7684\u73AB\u7470\u4ED9\u5B50\u4E0D\u7531\u5A07\u5598\u8FDE\u8FDE\uFF0C\u7389\u4F53\u706B\u70E7\u822C\u70ED\u4E86\u8D77\u6765\u3002</p>',59);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
