import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_57" tabindex="-1"><a class="header-anchor" href="#_57" aria-hidden="true">#</a> 57</h1><p>\u5BBD\u9614\u7684\u77F3\u5BA4\u5185\uFF0C\u56DE\u6C64\u7740\u4E1D\u4E1D\u7F15\u7F15\u8361\u4EBA\u5FC3\u9B44\u7684\u7EC6\u5598\u3002\u767D\u7389\u6253\u5236\u7684\u69BB\u4E0A\uFF0C\u9526\u88AB\u8F7B\u6BEF\u4E94\u8272\u6742\u9648\uFF0C\u6D41\u5149\u6EA2\u5F69\uFF0C\u5B9B\u5982\u9C9C\u8273\u660E\u5A9A\u7684\u82B1\u4E1B\u3002\u56DB\u58C1\u62AB\u7EA2\u6302\u5F69\uFF0C\u5E8A\u5934\u7EA2\u70DB\u9AD8\u70E7\uFF0C\u900F\u51FA\u6D1E\u623F\u82B1\u70DB\u591C\u7684\u6D0B\u6D0B\u559C\u6C14\u3002</p><p>\u767E\u82B1\u82B1\u4E1B\u4E2D\u4E24\u5177\u53E0\u653E\u7684\u7389\u4F53\u8273\u5149\u56DB\u5C04\uFF0C\u5206\u5916\u593A\u76EE\u3002\u4E0A\u9762\u90A3\u4E2A\u5A07\u5C0F\u73B2\u73D1\u7684\u7389\u4F53\u900F\u51FA\u4E00\u62B9\u7EEF\u7EA2\uFF0C\u66F4\u662F\u5A07\u8273\u6B32\u6EF4\u3002</p><p>\u8427\u4F5B\u5974\u611F\u89C9\u5230\u7D2B\u73AB\u7684\u4F53\u6E29\uFF0C\u4E0D\u7531\u53C8\u7F9E\u53C8\u6025\uFF0C\u5979\u8D34\u5728\u5973\u513F\u53D1\u7EA2\u7684\u8033\u65C1\u5524\u9053\uFF1A\u300C\u73AB\u513F\uFF01\u73AB\u513F\uFF01\u300D</p><p>\u7D2B\u73AB\u610F\u8BC6\u5230\u81EA\u5DF1\u7684\u7F9E\u6001\uFF0C\u8FDE\u5FD9\u54AC\u7D27\u7EA2\u5507\u6B62\u4F4F\u5A07\u5598\uFF0C\u4F46\u6EDA\u70EB\u7684\u4F53\u6E29\u5374\u4E1D\u6BEB\u4E0D\u51CF\u3002</p><p>\u6155\u5BB9\u9F99\u6B63\u6311\u5F04\u5F97\u6709\u6ECB\u6709\u5473\uFF0C\u89C1\u6BCD\u4EB2\u7834\u574F\u4E86\u81EA\u5DF1\u7684\u597D\u4E8B\uFF0C\u4E0D\u7531\u653E\u4E0B\u7D2B\u73AB\uFF0C\u4E0A\u4E0B\u6253\u91CF\u7740\u4E24\u4E2A\u76F8\u8FDE\u7684\u7389\u6237\uFF0C\u7B11\u9053\uFF1A\u300C\u5A18\u7684\u5C44\u771F\u662F\u7F8E\uFF0C\u53C8\u6ED1\u53C8\u7D27\uFF0C\u70ED\u4E4E\u4E4E\u8212\u670D\u5F97\u5F88\u3002</p><p>\u54E5\u54E5\u6BCF\u6B21\u63D2\u8FDB\u53BB\u90FD\u4E0D\u60F3\u62D4\u51FA\u6765\uFF0C\u4E0D\u77E5\u9053\u59B9\u59B9\u7684\u600E\u4E48\u6837\u2026\u2026\u300D\u8BF4\u7740\u628A\u624B\u4F38\u5230\u8427\u4F5B\u5974\u4E24\u817F\u4E4B\u95F4\uFF0C\u8FD0\u529F\u4E00\u6311\u3002</p><p>\u8427\u4F5B\u5974\u8138\u4E0A\u7684\u7F9E\u6025\u987F\u65F6\u50F5\u786C\uFF0C\u90A3\u80A1\u719F\u6089\u7684\u6C14\u6D41\u4ECE\u82B1\u8482\u900F\u5165\uFF0C\u6CBF\u7740\u6700\u654F\u611F\u7684\u90E8\u4F4D\u4E00\u8DEF\u6E38\u8D70\uFF0C\u77AC\u65F6\u70B9\u71C3\u4E86\u5979\u7684\u60C5\u6B32. \u767E\u82B1\u89C2\u97F3\u67F3\u7709\u98A6\u7D27\uFF0C\u4F3C\u75DB\u4F3C\u75D2\u5730\u5A07\u547B\u4E00\u58F0\uFF0C\u63A5\u7740\u7389\u4F53\u5267\u98A4\uFF0C\u79D8\u5904\u5DF2\u662F\u9634\u7CBE\u6EDA\u6D8C\uFF0C\u6DEB\u6C34\u6DCB\u6F13\u3002</p><p>\u6155\u5BB9\u9F99\u5F97\u610F\u6D0B\u6D0B\u5730\u62AC\u8D77\u5934\uFF0C\u4E00\u53E3\u543B\u5728\u6BCD\u4EB2\u5507\u4E0A\uFF0C\u5C06\u5E26\u7740\u59B9\u59B9\u4F53\u6DB2\u7684\u820C\u5934\u4F38\u5230\u8427\u4F5B\u5974\u5634\u4E2D\uFF0C\u75DB\u543B\u4E00\u756A\u3002</p><p>\u8427\u4F5B\u5974\u6EE1\u8138\u6CEA\u5149\uFF0C\u88AB\u9732\u6C34\u6253\u6E7F\u7684\u7261\u4E39\u822C\u54ED\u6CE3\u7740\u4E0D\u505C\u98A4\u6296\uFF0C\u5FC3\u91CC\u7684\u54C0\u75DB\u4E0E\u8089\u4F53\u7684\u5FEB\u611F\u540C\u65F6\u6500\u5230\u6781\u70B9\u3002</p><p>\u6155\u5BB9\u9F99\u677E\u5F00\u5634\uFF0C\u770B\u7740\u96EA\u80A4\u82B1\u8C8C\u4EA4\u76F8\u8F89\u6620\u7684\u6BCD\u5973\u4FE9\uFF0C\u4E0D\u7531\u54C8\u54C8\u5927\u7B11\uFF0C\u4ED6\u63E1\u4F4F\u7D2B\u73AB\u819D\u5F2F\u5411\u4E24\u4FA7\u4E00\u5206\uFF0C\u610F\u6C14\u98CE\u53D1\u5730\u8BF4\uFF1A\u300C\u59B9\u59B9\uFF0C\u54E5\u54E5\u8981\u8FDB\u6765\u4E86\uFF01\u300D</p><p>\u4EE4\u4EBA\u6050\u60E7\u7684\u5DE8\u68D2\u5373\u5C06\u5316\u505A\u73B0\u5B9E\uFF0C\u6D3B\u751F\u751F\u8FDB\u5165\u4F53\u5185\u3002\u7D2B\u73AB\u5FC3\u5934\u62BD\u7D27\uFF0C\u7981\u4E0D\u4F4F\u4E0E\u8EAB\u4E0B\u7684\u6BCD\u4EB2\u56DB\u624B\u76F8\u63E1\uFF0C\u5341\u6307\u4EA4\u53C9\u62E7\u5728\u4E00\u8D77\u3002\u867D\u7136\u7ACB\u5FD7\u6C42\u6B7B\uFF0C\u4F46\u5979\u6BD5\u7ADF\u53EA\u662F\u4E2A\u5C0F\u5973\u5B69\uFF0C\u5F53\u6155\u5BB9\u9F99\u6311\u9017\u5730\u628A\u9633\u5177\u4E3E\u5230\u773C\u524D\uFF0C\u7D2B\u73AB\u5413\u5F97\u8FDE\u547C\u5438\u4E5F\u5FD8\u4E86\u3002</p><p>\u5DE8\u5927\u7684\u8089\u68D2\u8DB3\u6709\u5C3A\u8BB8\u957F\u77ED\uFF0C\u7C97\u5982\u513F\u81C2\u3002\u7D2B\u7EA2\u8272\u7684\u9F9F\u5934\u50CF\u4E00\u4E2A\u6253\u78E8\u5149\u6ED1\u7684\u94DC\u7403\uFF0C\u95EA\u52A8\u7740\u91D1\u5C5E\u7684\u5149\u6CFD\u3002\u9F9F\u51A0\u540E\u7684\u68D2\u8EAB\u87BA\u65CB\u72B6\u9576\u7740\u4E00\u5708\u4E00\u5708\u7684\u73E0\u72B6\u7A81\u8D77\uFF0C\u6BCF\u4E00\u7C92\u90FD\u9F13\u8D77\u6307\u5C16\u5927\u5C0F\u3002\u8089\u68D2\u4E2D\u90E8\u50CF\u5957\u7740\u4E00\u4E2A\u751F\u6EE1\u5012\u523A\u7684\u94DC\u73AF\uFF0C\u82E5\u975E\u4E0A\u9762\u8840\u7BA1\u5BC6\u5E03\uFF0C\u600E\u4E48\u90FD\u4E0D\u4F1A\u8BA9\u4EBA\u76F8\u4FE1\u5B83\u4F1A\u4E0E\u8840\u8089\u8FDE\u6210\u4E00\u4F53\u3002\u9633\u5177\u540E\u534A\u622A\u663E\u5F97\u6B63\u5E38\u4E86\u8BB8\u591A\uFF0C\u5149\u6E9C\u6E9C\u76F4\u633A\u633A\uFF0C\u5E76\u65E0\u5F02\u72B6\u3002</p><p>\u4F46\u5728\u8089\u68D2\u6839\u90E8\uFF0C\u5374\u5BC6\u5BC6\u9EBB\u9EBB\u751F\u7740\u4E00\u4E1B\u7EC6\u957F\u6709\u529B\u7684\u89E6\u624B\u3002\u6BD4\u7B77\u5B50\u7565\u7EC6\u4E00\u4E9B\uFF0C\u957F\u77ED\u4E0D\u4E00\uFF0C\u6700\u957F\u7684\u80FD\u78B0\u5230\u8089\u68D2\u4E2D\u90E8\u7684\u5012\u523A\uFF0C\u77ED\u8005\u4E5F\u6709\u5BF8\u8BB8\u3002\u5B83\u4EEC\u7262\u7262\u56F4\u7740\u8089\u68D2\u65F6\u5C48\u65F6\u4F38\uFF0C\u4E0D\u5B89\u5206\u7684\u52A8\u4F5C\u7740\u3002\u6574\u6839\u9633\u5177\uFF0C\u5B9B\u5982\u5669\u68A6\u4E2D\u51FA\u73B0\u7684\u602A\u7269\uFF0C\u5E26\u7740\u8840\u6DCB\u6DCB\u7684\u9C9C\u7EA2\uFF0C\u5996\u5F02\u800C\u53C8\u72F0\u72DE\u3002</p><p>\u7D2B\u73AB\u7528\u624B\u80CC\u6321\u4F4F\u5C0F\u5634\uFF0C\u7D27\u5F20\u5F97\u900F\u4E0D\u8FC7\u6C14\u6765\u3002\u8FD9\u602A\u7269\u8FDE\u5E08\u7236\u90FD\u5F04\u6B7B\u4E86\uFF0C\u8FD9\u4E48\u5927\u7684\u4E1C\u897F\u6345\u8FDB\u53BB\uFF0C\u81EA\u5DF1\u4E5F\u6D3B\u4E0D\u6210\u2026\u2026</p><p>\u4E00\u77AC\u95F4\uFF0C\u7D2B\u73AB\u6C42\u6B7B\u7684\u5FC3\u5FD7\u52A8\u6447\u4E86\uFF0C\u5979\u60F3\u628A\u51E4\u51F0\u5B9D\u5178\u7684\u4E8B\u544A\u8BC9\u6155\u5BB9\u9F99\uFF0C\u60F3\u8BF4\u81EA\u5DF1\u5E74\u7EAA\u8FD8\u5C0F\uFF0C\u518D\u7B49\u4E0A\u51E0\u5E74\u2026\u2026</p><p>\u4F46\u90A3\u4E2A\u5DE8\u5927\u7684\u9F9F\u5934\u5DF2\u7ECF\u9876\u5728\u8089\u7F1D\u4E0A\u3002\u7D2B\u73AB\u7259\u5173\u683C\u683C\u8F7B\u54CD\uFF0C\u6B7B\u6B7B\u634F\u4F4F\u6BCD\u4EB2\u7684\u624B\u6307\uFF0C\u5FC3\u9053\uFF1A\u300C\u5A18\uFF0C\u5973\u513F\u2026\u2026\u5973\u513F\u2026\u2026\u820D\u4E0D\u5F97\u4F60\u2026\u2026\u300D</p><p>\u513F\u81C2\u822C\u7684\u5DE8\u9633\u76F4\u76F4\u9876\u5728\u4E24\u817F\u4E4B\u95F4\uFF0C\u671D\u6B63\u4E2D\u90A3\u4E2A\u5C0F\u6307\u7C97\u7EC6\u7684\u7A84\u7A74\u5185\u786C\u751F\u751F\u6324\u53BB\u3002</p><p>\u4EFF\u4F5B\u96C4\u9E70\u6402\u4F4F\u8774\u8776\u822C\uFF0C\u4E24\u8005\u60AC\u6B8A\u7684\u6BD4\u4F8B\u4EE4\u4EBA\u96BE\u4EE5\u7F6E\u4FE1\u3002</p><p>\u5408\u62B1\u7684\u82B1\u74E3\u88AB\u5C3D\u6570\u906E\u6CA1\uFF0C\u6155\u5BB9\u9F99\u8F7B\u8F7B\u4E00\u9876\uFF0C\u53EA\u89C9\u9F9F\u5934\u9876\u5728\u4E00\u56E2\u67D4\u97E7\u7684\u8F6F\u8089\u4E0A\uFF0C\u6ED1\u817B\u9500\u9B42\uFF0C\u5374\u65E0\u5904\u53EF\u5165\u3002\u4ED6\u6015\u7D2B\u73AB\u75DB\u5F97\u592A\u5389\u5BB3\uFF0C\u7565\u987F\u4E86\u4E0B\uFF0C\u7B11\u9053\uFF1A\u300C\u5A18\uFF0C\u4F60\u79BB\u5F97\u8FD1\uFF0C\u53BB\u4EB2\u4EB2\u59B9\u59B9\u3002\u300D</p><p>\u8427\u4F5B\u5974\u9AD8\u6F6E\u752B\u8FC7\uFF0C\u5FC3\u4E27\u6B32\u6B7B\uFF0C\u5979\u865A\u5F31\u5730\u4FA7\u8FC7\u8138\uFF0C\u4E0D\u7406\u4F1A\u513F\u5B50\u4E0B\u6D41\u7684\u8981\u6C42\u3002</p><p>\u6155\u5BB9\u9F99\u67D4\u58F0\u9053\uFF1A\u300C\u5A18\uFF0C\u4F60\u5C31\u4F53\u8C05\u59B9\u59B9\u4E00\u4E9B\uFF0C\u5979\u8FD8\u5C0F\u5462\u3002\u5B69\u513F\u8FD9\u4E48\u5927\u7684\u9E21\u5DF4\uFF0C\u6015\u59B9\u59B9\u7684\u5C0F\u5AE9\u5C44\u627F\u4E0D\u4F4F\u2026\u2026\u300D</p><p>\u6B63\u8BF4\u7740\uFF0C\u5FFD\u7136\u8EAB\u4E0B\u7684\u7389\u4EBA\u4E00\u52A8\uFF0C\u7D2B\u73AB\u633A\u8D77\u4E0B\u8179\uFF0C\u5BF9\u7740\u5DE8\u68D2\u72E0\u72E0\u4E00\u9876\u3002\u5979\u542C\u5F97\u7F9E\u607C\u4EA4\u52A0\uFF0C\u8840\u6C14\u4E00\u6D8C\uFF0C\u987F\u65F6\u538B\u4E0B\u6050\u60E7\uFF0C\u4F46\u7389\u6237\u4E00\u4E3E\uFF0C\u7ACB\u5373\u82B1\u5BB9\u5931\u8272\u3002</p><p>\u6B64\u4E3E\u592A\u51FA\u4E4E\u6155\u5BB9\u9F99\u7684\u610F\u6599\uFF0C\u4ED6\u6123\u4E86\u4E00\u4E0B\u624D\u53CD\u5E94\u8FC7\u6765\u3002\u773C\u89C1\u59B9\u59B9\u5C0F\u5634\u4E00\u6241\uFF0C\u75DB\u5F97\u773C\u6CEA\u90FD\u5FEB\u4E0B\u6765\u4E86\uFF0C\u8FDE\u5FD9\u6293\u4F4F\u673A\u4F1A\uFF0C\u9633\u5177\u9F13\u52C7\u76F4\u5165\u3002</p><p>\u7D2B\u73AB\u4FCF\u76EE\u731B\u7136\u7741\u5927\uFF0C\u5C0F\u5634\u5F20\u5F97\u6D51\u5706\uFF0C\u53EA\u5438\u4E86\u534A\u53E3\u6C14\u5C31\u56E0\u4E3A\u5267\u75DB\u800C\u50F5\u4F4F\u4E86\u3002</p><p>\u9F9F\u5934\u91CD\u91CD\u6324\u5165\u7D27\u7A84\u65E0\u6BD4\u7684\u8089\u7A74\uFF0C\u88AB\u4E00\u5C42\u5C42\u6ED1\u817B\u800C\u53C8\u575A\u97E7\u7684\u8089\u58C1\u6B7B\u6B7B\u88F9\u4F4F\u3002\u90A3\u79CD\u611F\u89C9\u50CF\u662F\u5F53\u65E5\u5728\u98CE\u665A\u534E\u4E73\u4E2D\u786C\u751F\u751F\u6345\u51FA\u4E00\u4E2A\u8840\u6D1E\u822C\u523A\u6FC0\u3002\u6155\u5BB9\u9F99\u5FC3\u5934\u546F\u546F\u4E71\u8DF3\uFF0C\u751F\u6015\u771F\u628A\u59B9\u59B9\u7684\u5C0F\u7A74\u5F04\u5F97\u7C89\u788E\uFF0C\u4ED6\u4F38\u624B\u5728\u7D2B\u73AB\u4E0B\u4F53\u4E00\u63A2\u3002\u7A74\u53E3\u7684\u5AE9\u8089\u4E0E\u8089\u68D2\u7D27\u7D27\u54AC\u5728\u4E00\u8D77\uFF0C\u5FAE\u6E29\u7684\u6DB2\u4F53\u56DB\u4E0B\u6A2A\u6D41\u3002\u5F85\u770B\u6E05\u6307\u4E0A\u6CA1\u6709\u8840\u8FF9\uFF0C\u4ED6\u624D\u7565\u5FAE\u653E\u4E0B\u5FC3\u6765\u3002</p><p>\u50F5\u4E86\u7247\u523B\uFF0C\u7D2B\u73AB\u9ED1\u767D\u5206\u660E\u7684\u5927\u773C\u4E2D\u7A81\u7136\u8FF8\u51FA\u6CEA\u82B1\uFF0C\u5979\u54ED\u53EB\u9053\uFF1A\u300C\u62D4\u51FA\u6765\uFF01\u5FEB\u62D4\u51FA\u6765\uFF01\u6DF7\u86CB\uFF01\u4F60\u5FEB\u51FA\u6765\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u779F\u4E86\u6BCD\u4EB2\u4E00\u773C\uFF0C\u563F\u563F\u7B11\u9053\uFF1A\u300C\u522B\u6025\uFF0C\u54E5\u54E5\u8FD8\u6CA1\u6709\u6345\u7A7F\u59B9\u59B9\u7684\u5904\u5973\u819C\u5462\u2026\u2026\u300D\u8BF4\u7740\u4F5C\u52BF\u6B32\u5165\u3002</p><p>\u7D2B\u73AB\u4F38\u624B\u6309\u4F4F\u4ED6\u7684\u80A9\u5934\u6B7B\u547D\u5411\u5916\u63A8\u5F00\uFF0C\u7389\u817F\u6323\u624E\u7740\u8BD5\u56FE\u5408\u62E2\uFF0C\u54ED\u5F97\u68A8\u82B1\u5E26\u96E8\uFF0C\u300C\u4E0D\u8981\uFF01\u4E0D\u8981\u518D\u8FDB\u4E86\uFF0C\u6211\u4F1A\u6B7B\u7684\uFF01\u6211\u4F1A\u6B7B\u7684\u2026\u2026\u300D</p><p>\u8427\u4F5B\u5974\u5FC3\u75BC\u4E07\u5206\u5730\u770B\u7740\u5973\u513F\uFF0C\u5FFD\u7136\u7EA2\u5507\u5FAE\u5206\uFF0C\u628A\u7D2B\u73AB\u6676\u83B9\u7684\u8033\u5782\u542B\u5728\u53E3\u4E2D\uFF0C\u6E29\u67D4\u7684\u7EC6\u7EC6\u8214\u8210\uFF0C\u8BD5\u56FE\u51CF\u8F7B\u5973\u513F\u7684\u75DB\u695A\u3002</p><p>\u6B64\u523B\u5F13\u5728\u5F26\u4E0A\uFF0C\u4E0D\u5BB9\u4E0D\u53D1\uFF0C\u6155\u5BB9\u9F99\u5FC3\u9053\u957F\u75DB\u4E0D\u5982\u77ED\u75DB\uFF0C\u8FD9\u4E00\u5173\u8981\u662F\u5FC3\u8F6F\uFF0C\u90A3\u8FD8\u4E0D\u5982\u8D81\u65E9\u653E\u4EBA\u7684\u5E72\u51C0\u3002\u60F3\u7740\u864E\u8EAF\u4E00\u633A\uFF0C\u9F9F\u5934\u94C1\u9A91\u53E9\u5173\uFF0C\u786C\u751F\u751F\u6495\u5F00\u524D\u9762\u90A3\u5C42\u7CBE\u81F4\u7684\u8584\u819C\uFF0C\u5728\u5AE9\u8089\u5185\u6740\u5F00\u4E00\u6761\u8840\u8DEF\u3002</p><p>\u7D2B\u73AB\u53D1\u51FA\u4E00\u58F0\u51C4\u5207\u5730\u60E8\u53EB\uFF0C\u56DB\u80A2\u731B\u7136\u6536\u7D27\u3002\u7EC6\u4E0D\u5BB9\u6307\u7684\u5C0F\u7A74\u88AB\u7C97\u903E\u6570\u500D\u7684\u5DE8\u7269\u6345\u5165\uFF0C\u90A3\u79CD\u5267\u70C8\u75DB\u82E6\u4F7F\u5A07\u4FCF\u7684\u5C11\u5973\u82B1\u5BB9\u626D\u66F2\uFF0C\u6D95\u6CEA\u4EA4\u6D41\u3002</p><p>\u767D\u6C0F\u59D0\u59B9\u542C\u5F97\u771F\u5207\uFF0C\u4E24\u4EBA\u4E00\u8FB9\u4E3A\u7D2B\u73AB\u51C4\u5389\u7684\u75DB\u53EB\u800C\u52A8\u5BB9\uFF0C\u53E6\u4E00\u8FB9\u5374\u5FC3\u5934\u6697\u559C\u3002</p><p>\u540C\u65F6\u662F\u88AB\u661F\u6708\u6E56\u63B3\u6765\u7684\u5973\u5B50\uFF0C\u59D0\u59B9\u4FE9\u53EA\u662F\u6700\u4F4E\u8D31\u7684\u6027\u5974\uFF0C\u4EFB\u4EBA\u8E42\u8E8F\uFF1B\u800C\u73AB\u7470\u4ED9\u5B50\u5374\u50CF\u516C\u4E3B\u822C\u88AB\u4EBA\u9A84\u7EB5\u3002\u5F53\u4E24\u5973\u88AB\u4EBA\u51CC\u8FB1\u65F6\uFF0C\u65C1\u8FB9\u4E0D\u5BB9\u4FB5\u72AF\u7684\u7D2B\u73AB\uFF0C\u5C31\u4EFF\u4F5B\u662F\u9AD8\u9AD8\u5728\u4E0A\u7684\u4ED9\u5B50\u3002\u7EB5\u7136\u4E00\u6837\u542B\u7740\u6CEA\u6C34\u4E00\u6837\u75DB\u82E6\uFF0C\u4F46\u5979\u7684\u7EAF\u6D01\u5374\u4F7F\u4E24\u4EBA\u81EA\u60ED\u5F62\u79FD\u3002</p><p>\u5982\u4ECA\u4ED9\u5B50\u822C\u7684\u7F8E\u5973\u4E5F\u50CF\u5979\u4EEC\u66FE\u7ECF\u90A3\u6837\uFF0C\u5728\u8EAB\u8FB9\u88AB\u5978\u6DEB\u7684\u75DB\u54ED\u6D41\u6D95\uFF0C\u4E24\u4EBA\u4E0D\u7981\u5FC3\u751F\u5FEB\u610F\u3002</p><p>\u8427\u4F5B\u5974\u5FC3\u5982\u5200\u7EDE\uFF0C\u671D\u6155\u5BB9\u9F99\u558A\u9053\uFF1A\u300C\u8F7B\u4E00\u4E9B\uFF0C\u4F60\u8F7B\u4E00\u4E9B\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u4E5F\u6709\u4E9B\u8FC7\u610F\u4E0D\u53BB\uFF0C\u4ED6\u505C\u4F4F\u52A8\u4F5C\uFF0C\u8DB4\u5728\u59B9\u59B9\u8138\u4E0A\u5C0F\u58F0\u5475\u62A4\u5730\u8BF4\uFF1A\u300C\u522B\u6015\u522B\u6015\uFF0C\u4E00\u4F1A\u513F\u5C31\u4E0D\u75DB\u4E86\u2026\u2026\u771F\u7684\uFF0C\u54E5\u54E5\u4ECE\u6765\u90FD\u4E0D\u9A97\u4F60\u2026\u2026\u300D</p><p>\u5FFD\u7136\u80EF\u4E0B\u4E00\u7D27\uFF0C\u5927\u534A\u622A\u8FD8\u9732\u5728\u5916\u9762\u7684\u8089\u68D2\u88AB\u4E24\u53EA\u5C0F\u624B\u6B7B\u547D\u6525\u4F4F\u3002\u7D2B\u73AB\u8138\u8272\u96EA\u767D\uFF0C\u4E00\u8FB9\u98A4\u6296\uFF0C\u4E00\u8FB9\u6025\u4FC3\u5730\u5410\u7740\u6C14\uFF0C\u8270\u96BE\u5730\u8BF4\uFF1A\u300C\u6155\u5BB9\u9F99\uFF0C\u6211\u6B7B\u4E5F\u4E0D\u653E\u8FC7\u2026\u2026\u300D</p><p>\u4E0E\u6B64\u540C\u65F6\uFF0C\u8089\u68D2\u5468\u56F4\u6E17\u51FA\u4E00\u4E1D\u89E6\u76EE\u60CA\u5FC3\u7684\u9C9C\u7EA2\u3002\u63A5\u7740\u8D8A\u6765\u8D8A\u591A\uFF0C\u7247\u523B\u4FBF\u67D3\u7EA2\u4E86\u7D2B\u73AB\u96EA\u767D\u7684\u5C0F\u624B\u3002\u8840\u8FF9\u7ED5\u7740\u9633\u5177\u873F\u8712\u6D41\u8FC7\uFF0C\u5728\u5C11\u5973\u5A07\u5AE9\u7684\u8089\u7F1D\u8FB9\u6D45\u6D45\u5212\u4E86\u4E2A\u534A\u5706\uFF0C\u7136\u540E\u4ECE\u7EF7\u7D27\u7684\u4F1A\u9634\u5904\u6ED1\u4E0B\u3002</p><p>\u4E0B\u9762\u662F\u4E00\u4E2A\u540C\u6837\u7F8E\u4E3D\u7684\u8089\u7A74\uFF0C\u4F46\u8273\u7EA2\u7684\u82B1\u74E3\u5374\u6BD4\u7D2B\u73AB\u6210\u719F\u4E86\u8BB8\u591A\u3002\u6E29\u70ED\u7684\u8840\u6DB2\u6EF4\u5728\u8EAB\u4E0A\uFF0C\u8427\u4F5B\u5974\u4E0D\u7531\u5A07\u8EAF\u4E00\u98A4\uFF0C\u82B1\u74E3\u53D7\u60CA\u822C\u4E00\u9635\u67D4\u67D4\u5F00\u5408\u3002\u90A3\u6EF4\u9C9C\u8273\u7684\u8840\u73E0\u98A4\u5FAE\u5FAE\u6CBE\u5728\u4E0A\u9762\uFF0C\u4EFF\u4F5B\u4E00\u6EF4\u6676\u83B9\u7684\u6CEA\u73E0\u3002</p><p>\u6155\u5BB9\u9F99\u6DF1\u6DF1\u770B\u7740\u65B0\u5A5A\u59B9\u59B9\u5145\u6EE1\u6068\u610F\u7684\u661F\u7738\uFF0C\u8089\u68D2\u7F13\u6162\u5374\u7EDD\u4E0D\u8FDF\u7591\u5730\u5411\u521A\u521A\u7834\u4F53\u7684\u5C0F\u7A74\u6DF1\u5904\u6345\u53BB\u3002</p><p>\u5904\u5B50\u7684\u9C9C\u8840\u4E00\u6EF4\u4E00\u6EF4\u4ECE\u6ED1\u817B\u7684\u808C\u80A4\u4E0A\u6ED1\u843D\uFF0C\u8D8A\u6765\u8D8A\u5FEB\uFF0C\u6E10\u6E10\u8FDE\u6210\u4E00\u6761\u76F4\u7EBF\uFF0C\u6700\u540E\u53D8\u6210\u6C79\u6D8C\u7684\u8840\u6CC9\u3002\u7D2B\u73AB\u624B\u4E0A\u3001\u80A1\u95F4\u5C3D\u662F\u6DCB\u6F13\u7684\u9C9C\u8840\uFF0C\u8FDE\u8427\u4F5B\u5974\u8EAB\u4E0B\u4E5F\u88AB\u67D3\u7EA2\u3002</p><p>\u7D2B\u73AB\u6B7B\u6B7B\u4E0E\u6155\u5BB9\u9F99\u5BF9\u89C6\u7740\uFF0C\u4F46\u5979\u7684\u624B\u5374\u63E1\u4E0D\u4F4F\u90A3\u6839\u8086\u8650\u7684\u5DE8\u68D2\u3002\u624B\u5FC3\u91CC\u90A3\u5708\u5012\u523A\u6D3B\u7269\u822C\uFF0C\u8815\u52A8\u7740\u4E00\u70B9\u70B9\u6ED1\u51FA\u3002\u5FFD\u7136\u7A74\u53E3\u4E00\u7D27\uFF0C\u5E03\u6EE1\u5012\u523A\u7684\u8089\u7624\u5DF2\u62B5\u4F4F\u7EF7\u7D27\u7684\u5AE9\u8089\u3002</p><p>\u6155\u5BB9\u9F99\u773C\u775B\u5149\u8292\u95EA\u52A8\uFF0C\u9488\u4E00\u822C\u51DD\u89C6\u8FD9\u4E2A\u4EE4\u4ED6\u53C8\u7231\u53C8\u75DB\u7684\u5AE1\u4EB2\u59B9\u59B9\u3002\u5FFD\u7136\u8089\u68D2\u4E00\u9707\uFF0C\u6839\u90E8\u90A3\u4E1B\u89E6\u624B\u731B\u7136\u633A\u76F4\uFF0C\u63A5\u7740\u866C\u66F2\u7740\u52FE\u4F4F\u7D2B\u73AB\u7684\u624B\u6307\uFF0C\u5C06\u5979\u7684\u624B\u638C\u5305\u5728\u5176\u4E2D\u3002</p><p>\u5C11\u5973\u7C89\u5AE9\u7684\u7389\u817F\u4E2D\u592E\uFF0C\u9732\u51FA\u4E00\u5708\u7EC6\u7EC6\u7684\u7EA2\u8089\uFF0C\u5A07\u67D4\u7EA2\u6DA6\uFF0C\u695A\u695A\u52A8\u4EBA\u3002\u5DE8\u7855\u7684\u68D2\u8EAB\u95EA\u52A8\u7740\u72F0\u72DE\u7684\u7D2B\u7EA2\uFF0C\u7262\u7262\u63D2\u5728\u6EA2\u8840\u7684\u5AE9\u7EA2\u4E2D\u3002\u9F9F\u5934\u5BF8\u5BF8\u8FDB\u903C\uFF0C\u4E00\u76F4\u6345\u5230\u8089\u7A74\u6DF1\u5904\u3002\u70BD\u70ED\u7684\u5AE9\u8089\u6CE2\u6D6A\u822C\u7FFB\u5377\u8815\u52A8\uFF0C\u522E\u5F97\u9F9F\u5934\u9635\u9635\u9165\u9EBB\u3002</p><p>\u4EFB\u6155\u5BB9\u9F99\u9605\u5973\u65E0\u6570\uFF0C\u5176\u4E2D\u4E0D\u4E4F\u5185\u8C19\u5A9A\u529F\u7684\u597C\u5973\uFF0C\u4F46\u5982\u6B64\u7F8E\u5999\u7684\u6ECB\u5473\u8FD8\u662F\u7B2C\u4E00\u6B21\u5C1D\u5230\u3002\u5FFD\u7136\u9F9F\u5934\u4E00\u987F\uFF0C\u505C\u5728\u4E00\u4E2A\u4E0D\u4F4F\u6536\u7F29\u7684\u5C0F\u5B54\u524D\u3002</p><p>\u6B64\u65F6\u8089\u68D2\u8FD8\u672A\u5B8C\u5168\u8FDB\u5165\uFF0C\u5C0F\u624B\u7D27\u63E1\u7684\u90E8\u5206\u4ECD\u7559\u5728\u4F53\u5916\u3002\u6155\u5BB9\u9F99\u4E0D\u52A8\u58F0\u8272\u5730\u8FD0\u8D77\u7384\u529F\uFF0C\u7CBE\u53E3\u62B5\u5728\u5438\u542E\u7684\u82B1\u5FC3\u6B63\u4E2D\uFF0C\u771F\u6C14\u6D8C\u5411\u7D2B\u73AB\u7684\u771F\u5143\u3002</p><p>\u6628\u65E5\u7D2B\u73AB\u5DF2\u7ECF\u5316\u89E3\u4E86\u5316\u771F\u6563\u7684\u836F\u529B\uFF0C\u836F\u6548\u6301\u7EED\u4E2D\uFF0C\u518D\u65BD\u5316\u771F\u6563\u6BEB\u65E0\u4F5C\u7528\u3002\u6155\u5BB9\u9F99\u53EA\u597D\u6539\u7528\u5176\u4ED6\u836F\u7269\u6765\u514B\u5236\u7D2B\u73AB\u7684\u771F\u6C14\u3002\u672C\u6765\u4ED6\u4E5F\u4E0D\u613F\u6C72\u53D6\u59B9\u59B9\u7684\u771F\u5143\uFF0C\u4F46\u73B0\u5728\u5FC3\u5934\u6C14\u6068\uFF0C\u6709\u610F\u7ED9\u5979\u4E00\u4E2A\u6559\u8BAD\u3002</p><p>\u771F\u6C14\u7565\u4E00\u6D41\u8F6C\uFF0C\u6155\u5BB9\u9F99\u987F\u89C9\u6709\u5F02\uFF0C\u7D2B\u73AB\u771F\u5143\u7684\u6D41\u52A8\u4E0E\u96EA\u5CF0\u795E\u5C3C\u4E00\u822C\u65E0\u4E8C\uFF0C\u88AB\u4ED6\u592A\u4E00\u7ECF\u7684\u771F\u6C14\u4E00\u50AC\uFF0C\u7ACB\u5373\u98DE\u901F\u65CB\u8F6C\u8D77\u6765\u3002</p><p>\u6155\u5BB9\u9F99\u5927\u60CA\u5931\u8272\uFF0C\u8FDE\u5FD9\u5F13\u8170\u62D4\u51FA\u8089\u68D2\u3002\u5DF2\u7ECF\u6DF1\u5165\u82B1\u5F84\u7684\u8089\u7624\u4E00\u52A8\uFF0C\u7D27\u7A84\u7684\u8089\u7A74\u7ACB\u5373\u5411\u5916\u9F13\u8D77\uFF0C\u5012\u523A\u52FE\u7D27\u8089\u58C1\uFF0C\u4F3C\u4E4E\u8981\u5C06\u6574\u4E2A\u9634\u9053\u5B8C\u5168\u7FFB\u51FA\u3002\u8089\u523A\u76F4\u7ACB\uFF0C\u672C\u6765\u5C31\u6D41\u6DCC\u4E0D\u6B62\u7684\u9C9C\u8840\u7ACB\u5373\u4ECE\u7F1D\u9699\u4E2D\u72C2\u6D8C\u800C\u51FA\u3002</p><p>\u8427\u4F5B\u5974\u4E0B\u4F53\u50CF\u88AB\u6E29\u6C34\u6D47\u6D12\u822C\uFF0C\u7389\u6237\u5B8C\u5168\u88AB\u9C9C\u8840\u6D78\u6CA1\uFF0C\u5973\u513F\u7684\u8EAB\u4F53\u5374\u6E10\u6E10\u53D1\u51C9\uFF0C\u5979\u4E91\u9AFB\u6563\u4E71\uFF0C\u62FC\u547D\u626D\u52A8\u65E0\u4EE5\u65BD\u529B\u7684\u5A07\u8EAF\uFF0C\u75AF\u72C2\u5730\u53EB\u7740\uFF1A\u300C\u73AB\u513F\uFF01\u73AB\u513F\uFF01\u73AB\u513F\uFF01\u300D</p><p>\u51C4\u5389\u7684\u547C\u53EB\u5728\u77F3\u5BA4\u56DE\u6C64\u7740\uFF0C\u8FDE\u70DB\u5F71\u4E5F\u968F\u4E4B\u98A4\u6296\u3002</p>',52);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
