import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_58" tabindex="-1"><a class="header-anchor" href="#_58" aria-hidden="true">#</a> 58</h1><p>\u82E5\u5728\u5E73\u65F6\u6155\u5BB9\u9F99\u8FD8\u53EF\u65BD\u5C55\u624B\u6BB5\uFF0C\u6162\u6162\u8C03\u5F04\u3002\u4F46\u6B64\u65F6\u6025\u5207\u4E2D\u552F\u6050\u59B9\u59B9\u8131\u9634\u800C\u6B7B\uFF0C\u4ED6\u53EA\u597D\u6B62\u4F4F\u62BD\u79BB\u7684\u52A8\u4F5C\uFF0C\u9F9F\u5934\u91CD\u65B0\u9876\u4F4F\u82B1\u5FC3\uFF0C\u4E00\u8FB9\u5C0F\u5FC3\u89C2\u5BDF\u7D2B\u73AB\u771F\u6C14\u8FD0\u8F6C\uFF0C\u4E00\u8FB9\u7D27\u5F20\u5730\u770B\u7740\u59B9\u59B9\u7684\u795E\u8272\u3002</p><p>\u7D2B\u73AB\u7684\u8138\u8272\u6108\u53D1\u96EA\u767D\uFF0C\u51E0\u4E4E\u50CF\u900F\u660E\u4E00\u822C\u3002\u6155\u5BB9\u9F99\u6E05\u695A\u5730\u611F\u53D7\u5230\u82B1\u5FC3\u5728\u9F9F\u5934\u5438\u542E\u7684\u9891\u5F8B\u6E10\u6E10\u52A0\u5FEB\uFF0C\u5FFD\u7136\u7CBE\u53E3\u4E00\u9707\uFF0C\u4E00\u80A1\u70BD\u70ED\u7684\u6C14\u606F\u65CB\u8F6C\u7740\u8FDB\u5165\u7CBE\u7BA1\uFF0C\u4E00\u76F4\u6D8C\u5230\u4E39\u7530\u3002\u4F46\u4E0E\u4EE5\u5F80\u91C7\u8865\u98D8\u6885\u5CF0\u8BF8\u5973\u90A3\u79CD\u771F\u5143\u6EDA\u6EDA\u6D8C\u52A8\u7684\u72B6\u51B5\u4E0D\u540C\uFF0C\u7D2B\u73AB\u7684\u771F\u5143\u50CF\u662F\u65E0\u610F\u4E2D\u6F0F\u51FA\u4E00\u70B9\uFF0C\u4ECD\u662F\u81EA\u884C\u8FD0\u8F6C\u3002</p><p>\u82B1\u5FC3\u7684\u5438\u542E\u8D8A\u6765\u8D8A\u7D27\uFF0C\u6DF1\u5165\u9AA8\u9AD3\u7684\u9165\u9EBB\u4F7F\u9B42\u4E0D\u5B88\u820D\u6155\u5BB9\u9F99\u7CBE\u5173\u4E00\u677E\uFF0C\u6EDA\u70EB\u7684\u9633\u7CBE\u4E00\u80A1\u80A1\u5C04\u5165\u82B1\u5FC3\u4E4B\u4E2D\u3002\u4E0E\u6B64\u540C\u65F6\uFF0C\u6C14\u65CB\u4E5F\u8D8A\u8F6C\u8D8A\u5FEB\uFF0C\u51E0\u4E4E\u8D85\u8FC7\u610F\u8BC6\u7684\u6781\u9650\uFF0C\u518D\u4E5F\u65E0\u6CD5\u589E\u52A0\u3002</p><p>\u90A3\u79CD\u611F\u89C9\u6155\u5BB9\u9F99\u521A\u521A\u624D\u7ECF\u5386\u8FC7\u2014\u2014\u4ED6\u5FC3\u795E\u5267\u9707\uFF0C\u7A81\u7136\u9AD8\u58F0\u9053\uFF1A\u300C\u5FEB\u8BF7\u53F6\u62A4\u6CD5\uFF01\u300D</p><p>\u8BDD\u97F3\u521A\u843D\uFF0C\u98DE\u901F\u65CB\u8F6C\u7684\u771F\u6C14\u4FBF\u8F70\u7136\u6D88\u6563\u3002\u7D2B\u73AB\u8F7B\u8F7B\u5401\u4E86\u53E3\u6C14\uFF0C\u7F13\u7F13\u5408\u4E0A\u53CC\u773C\u3002</p><p>\u5F53\u90A3\u80A1\u5170\u82B1\u822C\u7684\u9999\u6C14\u6D88\u6563\u65F6\uFF0C\u5979\u4E24\u624B\u4E00\u677E\uFF0C\u6CBE\u6EE1\u81EA\u5DF1\u5904\u5B50\u9C9C\u8840\u7684\u5C0F\u624B\u8F6F\u7EF5\u7EF5\u6389\u5728\u8EAB\u4FA7\uFF0C\u843D\u5728\u6BCD\u4EB2\u4E00\u52A8\u4E0D\u52A8\u7684\u7389\u81C2\u4E0A\u3002</p><p>\u8427\u4F5B\u5974\u7684\u53EB\u558A\u621B\u7136\u800C\u6B62\u3002\u826F\u4E45\uFF0C\u5979\u8F7B\u53EB\u4E86\u58F0\u300C\u73AB\u513F\u2026\u2026\u300D\u58F0\u97F3\u8F7B\u5F97\u4EFF\u4F5B\u5C0F\u65F6\u5019\u5524\u9192\u5973\u513F\u90A3\u822C\u6E29\u67D4\u3002</p><p>\u6155\u5BB9\u9F99\u5446\u5446\u770B\u7740\u9999\u6D88\u7389\u6B92\u7684\u59B9\u59B9\uFF0C\u600E\u4E48\u4E5F\u4E0D\u6562\u76F8\u4FE1\u8FD9\u662F\u771F\u7684\u3002\u81EA\u5DF1\u91C7\u8865\u65E0\u6570\uFF0C\u6CA1\u6709\u4E00\u4E2A\u50CF\u5979\u4EEC\u5E08\u5F92\u4FE9\u8FD9\u6837\uFF0C\u83AB\u540D\u5176\u5999\u5C31\u9999\u9B42\u6773\u7136\u7684\u3002</p><p>\u59B9\u59B9\u8138\u4E0A\u8FD8\u5E26\u7740\u75DB\u695A\u7684\u795E\u60C5\uFF0C\u5931\u53BB\u8840\u8272\u7684\u5507\u74E3\u5A07\u7F8E\u4F9D\u7136\u3002\u4F46\u518D\u4E5F\u542C\u4E0D\u5230\u5979\u94F6\u94C3\u4F3C\u7684\u58F0\u97F3\uFF0C\u4E5F\u542C\u4E0D\u5230\u5979\u90A3\u4E9B\u5C0F\u5C0F\u7684\u8C0E\u8BDD\uFF0C\u542C\u4E0D\u5230\u5979\u6492\u5A07\u65F6\u7684\u5A49\u8F6C\u5A07\u61A8\u2026\u2026</p><p>\u7A81\u51FA\u5176\u6765\u7684\u6CEA\u6C34\u6A21\u7CCA\u4E86\u6155\u5BB9\u9F99\u53CC\u773C\u3002\u8FD9\u662F\u4ED6\u552F\u4E00\u7684\u59B9\u59B9\uFF0C\u8840\u8109\u76F8\u8FDE\u7684\u59B9\u59B9\uFF0C\u4E5F\u662F\u4ED6\u631A\u7231\u7684\u59BB\u5B50\u3002</p><p>\u767D\u6C0F\u59D0\u59B9\u8DEA\u5F97\u56DB\u80A2\u50F5\u786C\uFF0C\u542C\u5230\u547D\u4EE4\uFF0C\u8FDE\u5FD9\u6323\u624E\u7740\u722C\u8D77\u6765\u3002\u4E0D\u8FC7\u4E00\u4E2A\u65F6\u8FB0\u5DE5\u592B\uFF0C\u4E24\u4EBA\u7684\u4E0B\u4F53\u5DF2\u7ECF\u88AB\u70DB\u6CEA\u5B8C\u5168\u8986\u76D6\uFF0C\u597D\u5728\u5DE8\u70DB\u7559\u5728\u4F53\u5916\u7684\u5C1A\u591A\uFF0C\u5979\u4EEC\u4FE9\u5F7C\u6B64\u53D6\u51FA\u6B8B\u70DB\uFF0C\u6400\u6276\u7740\u7AD9\u8D77\u8EAB\u6765\u3002</p><p>\u56DE\u5934\u4E00\u770B\uFF0C\u767D\u7389\u9E42\u4E0D\u7531\u5931\u58F0\u60CA\u53EB\u9053\uFF1A\u300C\u5BAB\u4E3B\u2026\u2026\u592B\u4EBA\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u4E00\u60CA\uFF0C\u8FDE\u5FD9\u626D\u5934\u3002\u6CEA\u773C\u6A21\u7CCA\u4E2D\uFF0C\u53EA\u89C1\u8427\u4F5B\u5974\u7EC6\u767D\u7684\u67D4\u9888\u4FA7\u5728\u4E00\u65C1\uFF0C\u9C9C\u7EA2\u7684\u8840\u8FF9\u6D41\u6EE1\u4E86\u6795\u5934\u3002</p><p>\u300C\u5A18\uFF01\u300D\u6155\u5BB9\u9F99\u5927\u53EB\u4E00\u58F0\uFF0C\u4E00\u628A\u6402\u4F4F\u6BCD\u4EB2\u7684\u81FB\u9996\u3002</p><p>\u51E0\u6839\u6563\u4E71\u7684\u53D1\u4E1D\u88AB\u9C9C\u8840\u6CBE\u5728\u7389\u8138\u4E0A\uFF0C\u8427\u4F5B\u5974\u53CC\u76EE\u7D27\u95ED\uFF0C\u9C9C\u8840\u4ECE\u7EA2\u5507\u4E2D\u4E0D\u4F4F\u6D8C\u51FA\u3002</p><p>\u6155\u5BB9\u9F99\u4FCA\u76EE\u8840\u7EA2\uFF0C\u4ED6\u614C\u5FD9\u628A\u6BCD\u4EB2\u4ECE\u59B9\u59B9\u8EAB\u4E0B\u62B1\u51FA\uFF0C\u4E00\u624B\u6258\u7740\u9999\u80A9\uFF0C\u4E00\u624B\u634F\u5F00\u5C0F\u5634\u3002\u8427\u4F5B\u5974\u4E00\u58F0\u5267\u54B3\uFF0C\u5634\u4E2D\u7684\u9C9C\u8840\u96FE\u4E00\u822C\u55B7\u5728\u6155\u5BB9\u9F99\u8138\u4E0A\u3002</p><p>\u5634\u4E00\u5F20\u5F00\uFF0C\u6155\u5BB9\u9F99\u7ACB\u523B\u4FBF\u770B\u51FA\u6BCD\u4EB2\u662F\u54AC\u820C\u81EA\u5C3D\u3002\u4ED6\u53F3\u624B\u95EA\u7535\u822C\u6325\u51FA\uFF0C\u516D\u5904\u5927\u7A74\u4E00\u6325\u800C\u5C31\uFF0C\u5148\u6B62\u4F4F\u5954\u6D8C\u7684\u9C9C\u8840\u3002</p><p>\u767D\u7389\u83BA\u767D\u7389\u9E42\u88AB\u63A5\u4E8C\u8FDE\u4E09\u7684\u60CA\u53D8\u5413\u5F97\u82B1\u5BB9\u5931\u8272\uFF0C\u4E24\u5973\u987E\u4E0D\u5F97\u62AB\u4E0A\u8F7B\u7EB1\uFF0C\u5F20\u7740\u6CBE\u6EE1\u70DB\u6CEA\u7684\u6D51\u5706\u8089\u7A74\u4FBF\u8FDE\u5FD9\u8DD1\u51FA\u6D1E\u623F\uFF0C\u53BB\u5BFB\u627E\u53F6\u884C\u5357\u3002</p><p>\u8F6F\u5316\u7684\u9633\u5177\u7F13\u7F13\u8131\u843D\uFF0C\u6DCC\u8840\u8089\u7A74\u4ECD\u7136\u655E\u9732\u7740\u6BB7\u7EA2\u7684\u5165\u53E3\u3002\u7D2B\u73AB\u4E24\u817F\u5FAE\u5206\uFF0C\u9759\u9759\u6A2A\u9648\u69BB\u4E0A\u3002\u66FC\u5999\u7684\u67D4\u8EAF\u5149\u6DA6\u5982\u7389\uFF0C\u6CA1\u6709\u534A\u4E1D\u7455\u75B5\u3002\u53EA\u662F\u4E0B\u4F53\u9C9C\u8840\u6DCB\u6F13\u3002\u6155\u5BB9\u9F99\u4F38\u81C2\u8F7B\u8F7B\u633D\u8D77\u59B9\u59B9\u7684\u8170\u80A2\uFF0C\u5C06\u5979\u4E0A\u8EAB\u659C\u62B1\u6000\u4E2D\u3002</p><p>\u6155\u5BB9\u9F99\u7B14\u76F4\u5750\u5728\u69BB\u4E0A\uFF0C\u5DE6\u53F3\u62E5\u7740\u5782\u6B7B\u7684\u6BCD\u4EB2\u548C\u751F\u673A\u65AD\u7EDD\u7684\u59B9\u59B9\u3002\u7F8E\u5987\u53E3\u4E2D\u7684\u9C9C\u8840\u4ECE\u80F8\u4E73\u6DCC\u843D\uFF0C\u4E0E\u5973\u513F\u4E0B\u4F53\u7684\u5904\u5B50\u5143\u7EA2\u6C47\u5728\u4E00\u8D77\u3002\u9C9C\u8840\u6D78\u900F\u4E86\u8584\u8584\u7684\u9526\u887E\uFF0C\u5728\u7389\u69BB\u4E0A\u6C6A\u6210\u4E00\u7247\uFF0C\u6700\u540E\u4ECE\u7389\u69BB\u811A\u4E0A\u7EC6\u7EC6\u6D41\u4E0B\u3002</p><p>\u6D1E\u623F\u534E\u4E3D\u4F9D\u7136\uFF0C\u4F46\u5931\u53BB\u7EA2\u70DB\u7684\u5149\u8292\u4E4B\u540E\uFF0C\u53EA\u5269\u4E0B\u6E05\u51B7\u7684\u73E0\u8F89\uFF0C\u6620\u7740\u904D\u5BA4\u9C9C\u8840\uFF0C\u4EFF\u4F5B\u4E00\u5730\u51C4\u7136\u7684\u6CEA\u5149\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u5927\u6BBF\u5185\u55A7\u56A3\u5982\u6545\uFF0C\u676F\u76D8\u72FC\u85C9\u7684\u5BB4\u5E2D\u95F4\uFF0C\u6570\u5341\u5177\u767D\u751F\u751F\u7684\u5973\u4F53\u6742\u9648\u5176\u4E2D\uFF0C\u88AB\u6570\u767E\u540D\u517D\u6027\u5927\u53D1\u7684\u90AA\u9053\u9AD8\u624B\u7C97\u66B4\u7684\u8E42\u8E8F\u7740\u3002\u8EAB\u65C1\u4EBA\u6570\u6700\u591A\u7684\u5219\u662F\u4E00\u5177\u51B7\u51B0\u51B0\u7684\u5973\u4F53\u3002</p><p>\u96EA\u5CF0\u795E\u5C3C\u540D\u52A8\u516B\u65B9\uFF0C\u7BA1\u5979\u662F\u6B7B\u662F\u6D3B\uFF0C\u80FD\u8FDB\u5230\u5979\u4F53\u5185\u6345\u4E0A\u4E24\u4E0B\uFF0C\u8BF4\u8D77\u6765\u4E5F\u662F\u5978\u8FC7\u5929\u4E0B\u7B2C\u4E00\u9AD8\u624B\u2014\u2014\u8FD9\u9762\u5B50\u53EF\u5927\u4E86\u3002\u8F70\u7B11\u4E2D\uFF0C\u96EA\u5CF0\u795E\u5C3C\u4E24\u817F\u88AB\u4EBA\u538B\u5230\u80A9\u5934\uFF0C\u655E\u9732\u7684\u7389\u6237\u9AD8\u9AD8\u9F13\u8D77\uFF0C\u6DF7\u7740\u8840\u4E1D\u7684\u9633\u7CBE\u4ECE\u80A5\u539A\u7684\u8089\u82B1\u4E2D\u6C69\u6C69\u6A2A\u6D41\u3002</p><p>\u63A5\u5230\u6D88\u606F\u7684\u53F6\u884C\u5357\u98DE\u63A0\u800C\u81F3\uFF0C\u4ECE\u72C2\u6B22\u7684\u4EBA\u7FA4\u7A7F\u8FC7\u65F6\uFF0C\u4ED6\u773C\u73E0\u8F6C\u90FD\u4E0D\u8F6C\u3002\u9752\u8863\u4E00\u95EA\uFF0C\u4FBF\u6D88\u5931\u5728\u7389\u5C4F\u4E4B\u540E\u3002</p><p>\u4E8B\u60C5\u7D27\u6025\uFF0C\u4ED6\u987E\u4E0D\u5F97\u793C\u6570\uFF0C\u4E00\u628A\u63A8\u5F00\u4E3B\u5BA4\u7684\u7389\u95E8\u3002\u900F\u76EE\u662F\u6EE1\u69BB\u7684\u8840\u8165\u3002\u5BAB\u4E3B\u6728\u7136\u5750\u5728\u69BB\u4E0A\uFF0C\u81C2\u4E2D\u7D27\u7D27\u62B1\u7740\u4E24\u4E2A\u4E0D\u65AD\u6D41\u8840\u7684\u5973\u5B50\u3002</p><p>\u5BAB\u4E3B\u7684\u6D1E\u623F\u82B1\u70DB\u591C\u5F04\u6210\u5982\u6B64\u6E85\u8840\u60CA\u9B42\u7684\u60E8\u72B6\uFF0C\u53F6\u884C\u5357\u8138\u4E0A\u4E00\u65E0\u6240\u52A8\uFF0C\u5FC3\u91CC\u5374\u9707\u98A4\u4E0D\u5DF2\u3002\u4ED6\u98D8\u8EAB\u843D\u5728\u4E09\u4EBA\u8EAB\u65C1\uFF0C\u4E24\u624B\u5206\u522B\u6263\u4F4F\u592B\u4EBA\u548C\u5C11\u592B\u4EBA\u7684\u8109\u95E8\u3002</p><p>\u624B\u6307\u4E00\u642D\uFF0C\u53F6\u884C\u5357\u4E00\u559C\u4E00\u5FE7\u3002\u592B\u4EBA\u53EA\u662F\u5916\u4F24\uFF0C\u5BAB\u4E3B\u53C8\u5904\u7F6E\u5F97\u5F53\uFF0C\u5DF2\u7ECF\u95ED\u7A74\u6B62\u8840\uFF0C\u6027\u547D\u662F\u65E0\u59A8\u4E86\u3002\u800C\u5C11\u592B\u4EBA\u5219\u4F53\u5982\u5BD2\u51B0\uFF0C\u5BF8\u5173\u6084\u65E0\u58F0\u606F\u3002</p><p>\u53F6\u884C\u5357\u6536\u655B\u5FC3\u795E\uFF0C\u4E3E\u624B\u793A\u610F\u5BAB\u4E3B\u653E\u4E0B\u8427\u4F5B\u5974\uFF0C\u624B\u638C\u4E00\u62B9\uFF0C\u63B0\u5F00\u5979\u7684\u5C0F\u5634\uFF0C\u62AC\u773C\u4E00\u770B\uFF0C\u4FBF\u653E\u4E0B\u5FC3\u6765\u3002\u592B\u4EBA\u53EA\u662F\u4E2A\u5F31\u8D28\u5973\u6D41\uFF0C\u5E76\u672A\u54AC\u65AD\u820C\u5934\u3002\u53EA\u8981\u7565\u65BD\u5C0F\u6280\uFF0C\u81EA\u53EF\u6062\u590D\u5982\u521D\u3002\u53F6\u884C\u5357\u62AC\u624B\u5C06\u4E00\u679A\u4F24\u836F\u7EB3\u5165\u8427\u4F5B\u5974\u53E3\u4E2D\u3002\u63A5\u7740\u624B\u8155\u4E00\u8F6C\uFF0C\u70B9\u4E86\u5979\u7684\u7761\u7A74\u3002</p><p>\u6155\u5BB9\u9F99\u6CE8\u89C6\u7740\u6559\u4E2D\u795E\u533B\u7684\u52A8\u4F5C\uFF0C\u5FC3\u795E\u6E10\u6E10\u4ECE\u9707\u60CA\u4E2D\u6323\u624E\u51FA\u6765\u3002\u4ED6\u7F13\u7F13\u653E\u4E0B\u59B9\u59B9\u7684\u5A07\u8EAF\uFF0C\u7FFB\u8EAB\u4E0B\u69BB\u3002\u521A\u76F4\u8D77\u8EAB\u5B50\uFF0C\u7A81\u7136\u811A\u4E0B\u4E00\u8F6F\uFF0C\u4ED6\u8E09\u8DC4\u7740\u7A33\u4F4F\u8EAB\u5F62\uFF0C\u6162\u6162\u5750\u5728\u80E1\u5E8A\u4E0A\u4F4E\u58F0\u8BF4\uFF1A\u300C\u65E0\u8BBA\u5982\u4F55\u2026\u2026\u4FDD\u4F4F\u5979\u7684\u6027\u547D\u2026\u2026\u300D\u58F0\u97F3\u53C8\u6C99\u53C8\u54D1\u3002</p><p>\u767D\u7389\u83BA\u4E56\u5DE7\u5730\u5012\u4E86\u676F\u8336\uFF0C\u9012\u5728\u4E3B\u5B50\u624B\u4E2D\u3002\u6155\u5BB9\u9F99\u4E00\u53E3\u996E\u5E72\uFF0C\u7D27\u634F\u7740\u74F7\u676F\uFF0C\u4E24\u773C\u4E00\u7728\u4E0D\u7728\u5730\u76EF\u7740\u7D2B\u73AB\u3002</p><p>\u53F6\u884C\u5357\u5B89\u7F6E\u597D\u767E\u82B1\u89C2\u97F3\uFF0C\u7ACB\u5373\u6263\u4F4F\u7D2B\u73AB\u7684\u8109\u95E8\uFF0C\u67AF\u7626\u7684\u624B\u6307\u4F3C\u4E4E\u51DD\u5728\u7693\u5982\u660E\u6708\u7684\u7EA4\u8155\u4E0A\uFF0C\u4E00\u52A8\u4E0D\u52A8\uFF0C\u5168\u795E\u8D2F\u6CE8\u7684\u6355\u6349\u5979\u7684\u8109\u8C61\u3002</p><p>\u826F\u4E45\uFF0C\u53F6\u884C\u5357\u8FDF\u7591\u7740\u677E\u5F00\u624B\uFF0C\u7FFB\u5F00\u7D2B\u73AB\u7684\u773C\u76AE\u3002\u539F\u672C\u660E\u5A9A\u52A8\u4EBA\u7684\u773C\u775B\u5982\u4ECA\u795E\u5F69\u5168\u65E0\uFF0C\u53F6\u884C\u5357\u624B\u6307\u4E00\u98A4\uFF0C\u988C\u4E0B\u7684\u767D\u987B\u54C6\u54C6\u55E6\u55E6\u6296\u52A8\u8D77\u6765\u3002</p><p>\u6155\u5BB9\u9F99\u7684\u5FC3\u810F\u987F\u65F6\u6C89\u4E86\u4E0B\u53BB\u3002</p><p>\u53F6\u884C\u5357\u9893\u7136\u6492\u624B\uFF0C\u773C\u89D2\u7A81\u7136\u6E7F\u4E86\u3002\u4ED6\u5728\u77F3\u5BAB\u4E00\u4F4F\u6570\u5341\u5E74\uFF0C\u5730\u4F4D\u5C0A\u5D07\uFF0C\u5185\u5FC3\u5374\u5341\u5206\u5B64\u72EC\u3002</p><p>\u7D2B\u73AB\u867D\u7136\u6DD8\u6C14\uFF0C\u4F46\u4F36\u4FD0\u6D3B\u6CFC\u53C8\u5FC3\u5730\u5584\u826F\uFF0C\u53F6\u884C\u5357\u8001\u6765\u5BC2\u5BDE\uFF0C\u65E9\u628A\u5979\u5F53\u6210\u5973\u513F\u822C\u770B\u5F85\u3002\u600E\u6599\u8F6C\u773C\u95F4\uFF0C\u8FD9\u4E2A\u673A\u7075\u53E4\u602A\u7684\u5C0F\u4E2B\u5934\u5C31\u53D8\u6210\u4E00\u5177\u51B7\u51B0\u51B0\u7684\u5C38\u4F53\u3002\u518D\u4E5F\u4E0D\u4F1A\u8BF4\uFF0C\u4E0D\u4F1A\u7B11\uFF0C\u4E0D\u4F1A\u5201\u86EE\u5730\u53D1\u813E\u6C14\uFF0C\u4E0D\u4F1A\u72E1\u9EE0\u5730\u7728\u773C\u775B\uFF0C\u4E0D\u4F1A\u5343\u65B9\u767E\u8BA1\u4E0E\u81EA\u5DF1\u505A\u5BF9\u4E86\u2026\u2026</p><p>\u53F6\u884C\u5357\u60B2\u4ECE\u4E2D\u6765\uFF0C\u4E0D\u7531\u8001\u6CEA\u7EB5\u6A2A\u3002</p><p>\u5BA4\u4E2D\u6CA1\u6709\u4E00\u4E1D\u58F0\u97F3\uFF0C\u575F\u5893\u822C\u5BC2\u9759\u3002</p><p>\u4E0D\u77E5\u8FC7\u4E86\u591A\u4E45\uFF0C\u6155\u5BB9\u9F99\u624B\u4E2D\u4E00\u58F0\u8106\u54CD\uFF0C\u8336\u676F\u88AB\u634F\u5F97\u7C89\u788E\u3002\u4ED6\u6162\u6162\u62B9\u4E86\u62B9\u624B\uFF0C\u5C06\u96EA\u767D\u7684\u7C89\u672B\u6D12\u5728\u5730\u4E0A\uFF0C\u6DE1\u6DE1\u9053\uFF1A\u300C\u6682\u65F6\u4E0D\u8981\u544A\u8BC9\u5176\u4ED6\u4EBA\uFF0C\u5F85\u5BBE\u5BA2\u6563\u540E\u518D\u884C\u5904\u7406\u3002</p><p>\u8BF7\u53F6\u62A4\u6CD5\u51C6\u5907\u7269\u54C1\uFF0C\u660E\u65E9\u4E3A\u5C11\u592B\u4EBA\u6574\u7406\u9057\u4F53\u3002\u300D</p><p>\u767D\u7389\u9E42\u8F7B\u58F0\u9053\uFF1A\u300C\u7980\u5BAB\u4E3B\uFF0C\u73B0\u5728\u5DF2\u7ECF\u662F\u8FB0\u65F6\u4E86\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u4E00\u60CA\uFF0C\u59B9\u59B9\u7684\u54ED\u53EB\u597D\u50CF\u8FD8\u662F\u7247\u523B\u4E4B\u524D\uFF0C\u4F3C\u4E4E\u8FD8\u80FD\u611F\u89C9\u5230\u59B9\u59B9\u5AE9\u7A74\u5185\u7684\u706B\u70ED\u548C\u7D27\u5BC6\u2026\u2026</p><p>\u534A\u664C\u6155\u5BB9\u9F99\u9053\uFF1A\u300C\u90A3\u4E48\u5C31\u662F\u4E00\u4E2A\u65F6\u8FB0\u4E4B\u540E\u3002\u300D</p><p>\u53F6\u884C\u5357\u8E52\u8DDA\u7740\u53BB\u4E86\u3002</p><p>\u6155\u5BB9\u9F99\u6015\u6BCD\u4EB2\u770B\u5230\u59B9\u59B9\u7684\u5C38\u4F53\u60B2\u4F24\u8FC7\u5EA6\uFF0C\u518D\u505A\u51FA\u4EC0\u4E48\u4E8B\u6765\uFF0C\u4FBF\u547D\u767D\u6C0F\u59D0\u59B9\u5C06\u8427\u4F5B\u5974\u9001\u56DE\u5E9A\u5BA4\u3002</p><p>\u811A\u6B65\u58F0\u6E10\u6E10\u8FDC\u53BB\uFF0C\u6D1E\u623F\u5185\u53EA\u5269\u4E0B\u6155\u5BB9\u9F99\u548C\u7D2B\u73AB\u3002\u4E24\u4EBA\u4E00\u5750\u4E00\u8EBA\uFF0C\u9065\u9065\u76F8\u5BF9\u3002</p><p>\u73AB\u7470\u4ED9\u5B50\u9759\u9759\u8EBA\u5728\u7389\u69BB\u4E0A\uFF0C\u5931\u53BB\u8840\u8272\u7684\u5A07\u8EAF\u6108\u53D1\u6676\u83B9\u5254\u900F\u3002\u7CBE\u81F4\u7684\u7389\u5BB9\u6829\u6829\u5982\u751F\uFF0C\u6155\u5BB9\u9F99\u4E0D\u7531\u60F3\u8D77\u7B2C\u4E00\u6B21\u89C1\u5230\u59B9\u59B9\u65F6\uFF0C\u6DE1\u9EC4\u8272\u7684\u9633\u5149\u5728\u5979\u8138\u4E0A\u6D41\u52A8\u7684\u9999\u751C\u6C14\u606F\u2026\u2026</p><p>\u80F8\u524D\u7C89\u7EA2\u7684\u84D3\u857E\u5FAE\u5FAE\u7FD8\u8D77\uFF0C\u5C0F\u5DE7\u8FF7\u4EBA\u3002\u628A\u73A9\u5B83\u4EEC\u7684\u65F6\u5019\uFF0C\u6155\u5BB9\u9F99\u8FD8\u8981\u8003\u8651\u600E\u4E48\u8BA9\u5B83\u4EEC\u518D\u5927\u4E00\u4E9B\uFF0C\u6478\u8D77\u6765\u66F4\u8212\u670D\u3002\u4F46\u73B0\u5728\uFF0C\u4E00\u5207\u90FD\u6210\u4E86\u6CE1\u5F71\u3002</p><p>\u7EA4\u5F31\u7684\u8170\u8EAB\u67D4\u7F8E\u7EC6\u81F4\uFF0C\u5C0F\u8179\u5E73\u5766\uFF0C\u7389\u817F\u5FAE\u5206\u3002\u7389\u6237\u95F4\u6DCB\u6F13\u7684\u9C9C\u8840\u5DF2\u7ECF\u7591\u7ED3\uFF0C\u88AB\u5DE8\u9633\u6345\u5F04\u8FC7\u7684\u8089\u7A74\u5DF2\u7ECF\u95ED\u62E2\uFF0C\u7A84\u7A84\u7684\u82B1\u74E3\u4F3C\u4E4E\u5927\u4E86\u4E00\u4E9B\uFF0C\u66F4\u52A0\u7EA2\u5AE9\u53EF\u4EBA\u3002</p><p>\u4ED6\u5988\u7684\uFF01\u6155\u5BB9\u9F99\u5728\u5FC3\u91CC\u6068\u6068\u9A82\u4E86\u4E00\u58F0\uFF0C\u300C\u817E\u300D\u7684\u7AD9\u4E86\u8D77\u6765\u3002\u4ED6\u5598\u7740\u7C97\u6C14\u6293\u4F4F\u7D2B\u73AB\u7684\u811A\u8E1D\uFF0C\u5F80\u4E24\u65C1\u4E00\u63A8\u3002\u7389\u6237\u95F4\u7EA2\u8089\u8F7B\u98A4\uFF0C\u67D4\u67D4\u5206\u5F00\uFF0C\u9732\u51FA\u843D\u7EA2\u65E0\u6570\u7684\u79D8\u5883\u3002</p><p>\u6155\u5BB9\u9F99\u8089\u68D2\u4E00\u633A\uFF0C\u72E0\u72E0\u6233\u5165\u7D27\u7A84\u7684\u8089\u7A74\u5185\u3002\u5AE9\u8089\u4F9D\u7136\u5145\u6EE1\u5F39\u6027\uFF0C\u5F53\u8089\u68D2\u901A\u8FC7\u65F6\uFF0C\u4EFF\u4F5B\u88AB\u4E00\u53EA\u51B0\u51C9\u7684\u5C0F\u624B\u7D27\u7D27\u6525\u4F4F\u3002\u4ED6\u72E0\u72E0\u6345\u5230\u5E95\u90E8\uFF0C\u6574\u53EA\u9633\u5177\u5168\u90E8\u6CA1\u5165\u5C0F\u5C0F\u7684\u5AE9\u7A74\u4E2D\u3002\u7C97\u5927\u7684\u8089\u68D2\u4EFF\u4F5B\u4E00\u53EA\u624B\u81C2\u63D2\u8FDB\u5C11\u5973\u7684\u4E24\u817F\u4E4B\u95F4\uFF0C\u91CD\u91CD\u6363\u5728\u5B50\u5BAB\u9888\u4E0A\uFF0C\u5C06\u82B1\u5FC3\u538B\u5F97\u53D8\u5F62\uFF0C\u63A5\u7740\u53C8\u72E0\u72E0\u62D4\u51FA\u3002</p><p>\u9633\u5177\u4F3C\u4E4E\u6491\u6EE1\u4E86\u5C11\u5973\u7684\u6574\u4E2A\u8179\u8154\uFF0C\u6155\u5BB9\u9F99\u8170\u8EAB\u4E00\u62AC\uFF0C\u628A\u7D2B\u73AB\u7684\u4E0B\u4F53\u4E5F\u5E26\u7684\u62AC\u8D77\u3002\u4ED6\u4E24\u624B\u6309\u4F4F\u59B9\u59B9\u7684\u8170\u8EAB\uFF0C\u9633\u5177\u5377\u7740\u8089\u7A74\u5185\u7684\u5AE9\u8089\u7FFB\u5377\u800C\u51FA\u3002</p><p>\u6155\u5BB9\u9F99\u82F1\u4FCA\u7684\u9762\u5B54\u9732\u51FA\u6076\u9B54\u822C\u7684\u7B11\u610F\uFF0C\u5927\u529B\u5728\u59B9\u59B9\u7684\u5C38\u4F53\u4E2D\u62BD\u9001\u7740\u3002\u6B64\u65F6\u4ED6\u518D\u4E0D\u987E\u59B9\u59B9\u662F\u5426\u75BC\u75DB\uFF0C\u5DE8\u9633\u8089\u73E0\u3001\u5012\u523A\u3001\u89E6\u624B\u5C3D\u6570\u65BD\u5C55\uFF0C\u5728\u7D27\u7A84\u67D4\u97E7\u7684\u8089\u6D1E\u4E2D\u6781\u529B\u633A\u5F04\u3002</p><p>\u73AB\u7470\u4ED9\u5B50\u4E0B\u4F53\u9AD8\u4E3E\uFF0C\u96EA\u767D\u7684\u80A1\u95F4\uFF0C\u5C42\u5C42\u53E0\u53E0\u7684\u67D4\u7F8E\u82B1\u74E3\u4E0D\u4F4F\u5F00\u95ED\uFF0C\u8089\u7A74\u5185\u8273\u7EA2\u7684\u5AE9\u8089\u88AB\u9633\u5177\u5E26\u5F97\u7FFB\u8FDB\u7FFB\u51FA\u3002</p><p>\u6B63\u5F53\u6155\u5BB9\u9F99\u62FC\u547D\u5978\u6DEB\u59B9\u59B9\u7684\u5C38\u8EAB\u65F6\uFF0C\u51B0\u51B7\u7684\u8089\u7A74\u7A81\u7136\u5347\u8D77\u4E00\u80A1\u706B\u70ED\u7684\u6C14\u606F\uFF0C\u8089\u7A74\u7ACB\u5373\u70BD\u70ED\u8D77\u6765\u3002</p><p>\u6155\u5BB9\u9F99\u8111\u4E2D\u8F70\u7136\u4E00\u54CD\uFF0C\u62B1\u7740\u7D2B\u73AB\u6E10\u6E10\u53D1\u7EA2\u7684\u5A07\u8EAF\uFF0C\u8EAB\u4F53\u77F3\u96D5\u822C\u51DD\u56FA\u4E86\u3002</p>',57);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
