import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_59" tabindex="-1"><a class="header-anchor" href="#_59" aria-hidden="true">#</a> 59</h1><p>\u300C\u53F6\u62A4\u6CD5\uFF01\u300D\u767D\u7389\u9E42\u98DE\u4E5F\u4F3C\u7684\u8DD1\u5165\u53F6\u884C\u5357\u7684\u623F\u95F4\uFF0C\u300C\u5C11\u592B\u4EBA\u2026\u2026\u5C11\u592B\u4EBA\u2026\u2026\u6D3B\u2026\u2026\u6D3B\u8FC7\u6765\u4E86\u2026\u2026\u300D</p><p>\u53F6\u884C\u5357\u624B\u4E2D\u7684\u836F\u6750\u987F\u65F6\u6563\u843D\u6EE1\u5730\u3002</p><p>\u6155\u5BB9\u9F99\u8FD8\u6DF1\u6DF1\u63D2\u5728\u59B9\u59B9\u4F53\u5185\uFF0C\u8EAB\u4F53\u4E0D\u6562\u7A0D\u52A8\u3002\u8089\u68D2\u5468\u56F4\u7684\u5AE9\u8089\u5FAE\u5FAE\u8815\u52A8\uFF0C\u4F20\u6765\u4E00\u6CE2\u6CE2\u7D27\u5BC6\u7684\u5FEB\u611F\u3002\u4ED6\u5C4F\u4F4F\u547C\u5438\uFF0C\u8111\u4E2D\u660F\u660F\u6C89\u6C89\uFF0C\u8FA8\u4E0D\u51FA\u662F\u60CA\u662F\u559C\u3002</p><p>\u53F6\u884C\u5357\u795E\u8272\u51DD\u91CD\uFF0C\u534A\u664C\u653E\u5F00\u7D2B\u73AB\u53D1\u7EA2\u7684\u7693\u8155\uFF0C\u659F\u914C\u7740\u5F00\u53E3\u9053\uFF1A\u300C\u606D\u559C\u5BAB\u4E3B\uFF0C\u5C11\u592B\u4EBA\u751F\u673A\u5DF2\u590D\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u7EB9\u4E1D\u4E0D\u52A8\uFF0C\u9759\u7B49\u4ED6\u7684\u4E0B\u6587\u3002</p><p>\u53F6\u884C\u5357\u58F0\u97F3\u6709\u4E9B\u7D27\u5F20\uFF0C\u300C\u4F9D\u5C5E\u4E0B\u770B\u6765\uFF0C\u5C11\u592B\u4EBA\u53EF\u80FD\u662F\u4FEE\u7EC3\u8FC7\u300A\u51E4\u51F0\u5B9D\u5178\u300B\uFF0C\u4F46\u5C11\u592B\u4EBA\u7684\u529F\u529B\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u773C\u4E2D\u5BD2\u8292\u5927\u76DB\uFF0C\u300C\u300A\u51E4\u51F0\u5B9D\u5178\u300B\u5931\u8E2A\u591A\u5E74\uFF0C\u8C01\u90FD\u672A\u66FE\u89C1\u8FC7\uFF0C\u53F6\u62A4\u6CD5\u600E\u77E5\u5C11\u592B\u4EBA\u7EC3\u7684\u5C31\u662F\u6211\u6559\u795E\u529F\uFF1F\u300D</p><p>\u53F6\u884C\u5357\u4E5F\u5728\u76B1\u7709\u601D\u7D22\uFF0C\u300C\u6B64\u4E2D\u539F\u59D4\uFF0C\u5C5E\u4E0B\u4E5F\u662F\u4E0D\u89E3\u3002\u4F46\u5C11\u592B\u4EBA\u6B7B\u800C\u590D\u82CF\u4E4B\u72B6\uFF0C\u4E0E\u6559\u4E2D\u79D8\u7C4D\u6240\u8F7D\u76F8\u4EFF\u2026\u2026\u83AB\u975E\u7075\u7280\u5F69\u51E4\u5F53\u65E5\u672A\u6B7B\uFF1F\u300D</p><p>\u65F6\u9694\u767E\u5E74\uFF0C\u5F80\u4E8B\u65E9\u5DF2\u70DF\u6D88\u4E91\u6563\uFF0C\u4F46\u6559\u4E2D\u5377\u7C4D\u8BB0\u8F7D\u7684\u8840\u8165\u60E8\u70C8\u8FD8\u662F\u4EE4\u4E24\u4EBA\u5FC3\u91CC\u6253\u4E86\u4E2A\u7A81\u3002\u56DB\u9547\u795E\u5C06\u7684\u8986\u706D\u548C\u592A\u51B2\u5BAB\u4E3B\u7684\u8EAB\u6B7B\uFF0C\u662F\u661F\u6708\u6E56\u4E2D\u8870\u7684\u5F00\u59CB\u2026\u2026</p><p>\u6B63\u72B9\u7591\u95F4\uFF0C\u8EAB\u4E0B\u706B\u70ED\u7684\u5A07\u8EAF\u5FFD\u7136\u5FAE\u5FAE\u4E00\u52A8\uFF0C\u6155\u5BB9\u9F99\u8FDE\u5FD9\u5782\u4E0B\u5934\uFF0C\u8F7B\u5524\u9053\uFF1A\u300C\u59B9\u59B9\u2026\u2026\u300D</p><p>\u7D2B\u73AB\u300C\u5624\u549B\u300D\u4E00\u58F0\uFF0C\u8FD8\u672A\u7741\u773C\uFF0C\u4FCF\u8138\u4FBF\u75DB\u82E6\u5730\u76B1\u4E86\u8D77\u6765\u3002\u5979\u4F38\u624B\u6309\u4F4F\u4E0B\u8179\uFF0C\u5F53\u67D4\u638C\u78B0\u5230\u90A3\u6839\u786C\u6886\u6886\u7684\u5DE8\u9633\u65F6\uFF0C\u300C\u5440\u300D\u7684\u60CA\u53EB\u8D77\u6765\u3002\u7F8E\u76EE\u4E00\u7741\uFF0C\u6D51\u5706\u5254\u900F\u7684\u6CEA\u73E0\u968F\u4E4B\u6D8C\u51FA\uFF0C\u300C\u597D\u75DB\u2026\u2026\u300D</p><p>\u7D2B\u73AB\u626C\u8D77\u5C0F\u624B\uFF0C\u7528\u529B\u6253\u5728\u6155\u5BB9\u9F99\u8138\u4E0A\u9888\u4E0A\uFF0C\u54ED\u53EB\u9053\uFF1A\u300C\u6DF7\u86CB\u2026\u2026\u4F60\u600E\u4E48\u8FD8\u5728\u5F04\u2026\u2026\u5FEB\u62D4\u51FA\u6765\uFF01\u300D</p><p>\u867D\u7136\u5C0F\u4E2B\u5934\u6B7B\u800C\u590D\u751F\u8FD8\u662F\u8FD9\u4E48\u4E0D\u9A6F\u670D\uFF0C\u6155\u5BB9\u9F99\u5FC3\u91CC\u5374\u751C\u4E1D\u4E1D\u7684\u3002\u6D3B\u4E0B\u6765\u5C31\u597D\uFF0C\u54E5\u54E5\u8FD8\u6307\u671B\u4F60\u7ED9\u6211\u751F\u5B69\u5B50\u5462\u2026\u2026</p><p>\u4ED6\u6349\u4F4F\u7D2B\u73AB\u7684\u53CC\u624B\uFF0C\u7B11\u563B\u563B\u8BF4\uFF1A\u300C\u597D\u597D\u597D\uFF0C\u54E5\u54E5\u8FD9\u5C31\u62D4\u51FA\u6765\u2026\u2026\u5C0F\u5FC3\u4E9B\uFF0C\u522B\u7528\u529B\u2026\u2026\u4F60\u770B\uFF0C\u4E0D\u75DB\u5427\u2026\u2026\u300D</p><p>\u8BF4\u7740\u4E0D\u75DB\uFF0C\u7D2B\u73AB\u5DF2\u7ECF\u75DB\u5F97\u53EB\u4E0D\u51FA\u6765\u4E86\u3002\u751F\u6EE1\u5404\u79CD\u7528\u6765\u6298\u78E8\u5973\u4EBA\u5668\u5B98\u7684\u8089\u68D2\u50CF\u4E00\u53EA\u5E26\u7740\u5012\u94A9\u7684\u94C1\u62F3\uFF0C\u5C06\u8089\u58C1\u4E00\u70B9\u70B9\u62C9\u5230\u4F53\u5916\u3002\u5F53\u90A3\u4E2A\u7855\u5927\u7684\u8089\u7624\u300C\u5575\u300D\u7684\u4E00\u58F0\u79BB\u5F00\u8089\u7A74\u65F6\uFF0C\u4F3C\u4E4E\u628A\u5185\u810F\u90FD\u5E26\u4E86\u51FA\u53BB\u3002</p><p>\u4E00\u80A1\u6E05\u51C9\u7684\u7A7A\u6C14\u4ECE\u5927\u5F20\u7684\u8089\u6D1E\u8FDB\u5165\u4F53\u5185\uFF0C\u5E73\u7184\u4E86\u8EAB\u4F53\u7684\u70BD\u70ED\u3002\u7D2B\u73AB\u8737\u8D77\u9999\u8EAF\uFF0C\u4F0F\u5728\u69BB\u4E0A\u98A4\u6296\u7740\u5598\u606F\u4E0D\u5DF2\u3002</p><p>\u73CD\u7231\u7684\u59B9\u59B9\u5931\u800C\u590D\u5F97\uFF0C\u6155\u5BB9\u9F99\u559C\u4E0D\u81EA\u7981\uFF0C\u4ED6\u7B11\u7740\u62B1\u4F4F\u7D2B\u73AB\uFF0C\u300C\u8FD8\u75DB\u5417\uFF1F\u54E5\u54E5\u5E2E\u4F60\u2026\u2026\u300D\u8BDD\u672A\u8BF4\u5B8C\uFF0C\u6155\u5BB9\u9F99\u8138\u8272\u7A81\u7136\u5927\u53D8\uFF0C\u5931\u58F0\u53EB\u9053\uFF1A\u300C\u4E0D\u597D\uFF01\u300D</p><p>\u53F6\u884C\u5357\u540C\u65F6\u60F3\u8D77\u6BBF\u4E2D\u7684\u96EA\u5CF0\u795E\u5C3C\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u534A\u4E2A\u65F6\u8FB0\u524D\u96EA\u5CF0\u795E\u5C3C\u5C31\u51FA\u73B0\u4E86\u5F02\u5E38\uFF0C\u4F46\u62FF\u5979\u53D6\u4E50\u7684\u4F17\u4EBA\u5E76\u6CA1\u6709\u5728\u610F\u3002</p><p>\u5931\u53BB\u70ED\u5EA6\u7684\u8EAB\u4F53\u6CA1\u6709\u5F15\u8D77\u4EBA\u4EEC\u592A\u5927\u7684\u5174\u8DA3\uFF0C\u4ED6\u4EEC\u53EA\u662F\u628A\u8089\u68D2\u63D2\u5230\u96EA\u5CF0\u795E\u5C3C\u4F53\u5185\u633A\u52A8\u51E0\u4E0B\uFF0C\u8BF4\u8D77\u6765\u4E5F\u662F\u5978\u8FC7\u6B66\u6797\u7B2C\u4E00\u9AD8\u624B\u3002\u56E0\u6B64\u867D\u7136\u6570\u767E\u4EBA\u90FD\u8FDB\u5165\u8FC7\u96EA\u5CF0\u7684\u8EAB\u4F53\uFF0C\u5374\u6CA1\u7528\u591A\u5C11\u65F6\u95F4\uFF0C\u5176\u4F59\u65F6\u5019\u90FD\u662F\u60F3\u7740\u6CD5\u5B50\u73A9\u5F04\u5979\u7684\u300C\u5C38\u4F53\u300D\u3002</p><p>\u96C1\u95E8\u4E09\u5947\u628A\u96EA\u5CF0\u795E\u5C3C\u6446\u6210\u72D7\u8DB4\u7684\u59FF\u52BF\uFF0C\u8001\u5927\u79C3\u53D1\u4EC0\u5065\u7ACB\u5728\u795E\u5C3C\u80A5\u767D\u7684\u5706\u81C0\u540E\uFF0C\u62FF\u7740\u81EA\u5DF1\u7684\u72EC\u95E8\u5175\u5668\u4E03\u6BD2\u6775\uFF0C\u5728\u6495\u88C2\u7684\u8089\u7A74\u5185\u72E0\u72E0\u6363\u5F04\uFF0C\u4E00\u8FB9\u6363\u4E00\u8FB9\u5927\u58F0\u5531\u7740\u9C9C\u5351\u8C23\u3002</p><p>\u7B49\u4ED6\u73A9\u591F\u4E86\uFF0C\u4E00\u540D\u8138\u4E0A\u5E26\u7740\u5200\u75A4\u7684\u6C49\u5B50\u6324\u8FC7\u6765\uFF0C\u62CE\u51FA\u4E00\u6839\u7F20\u91D1\u7EF3\u7D22\uFF0C\u5C06\u795E\u5C3C\u7684\u7855\u4E73\u9F50\u6839\u624E\u4F4F\u3002</p><p>\u4EBA\u7FA4\u91CC\u6709\u4EBA\u53EB\u9053\uFF1A\u300C\u5B8B\u5927\u75A4\uFF0C\u62FF\u4F60\u7684\u9B3C\u7D22\u5E72\u561B\u5462\uFF1F\u300D</p><p>\u5B8B\u5927\u75A4\u5229\u843D\u7684\u624E\u597D\u4E24\u53EA\u4E73\u623F\uFF0C\u62CE\u7740\u9B3C\u7D22\u63D0\u4E86\u63D0\uFF0C\u300C\u8001\u8D3C\u5C3C\u8FD9\u5976\u5B50\u771F\u591F\u5927\u7684\uFF0C\u4F60\u4EEC\u8BF4\u540A\u7740\u5976\u5B50\u80FD\u4E0D\u80FD\u628A\u5979\u7ED9\u540A\u8D77\u6765\uFF1F\u300D</p><p>\u300C\u80FD\uFF01\u600E\u4E48\u4E0D\u80FD\uFF01\u6211\u8D4C\u4E94\u5341\u4E24\u94F6\u5B50\uFF01\u300D</p><p>\u300C\u4E00\u767E\u4E24\uFF01\u540A\u4E0D\u8D77\u6765\uFF01\u300D</p><p>\u300C\u5478\uFF01\u300D\u5B89\u5B50\u5B8F\u5410\u4E86\u53E3\u6D53\u75F0\uFF0C\u300C\u8001\u5B50\u51FA\u4E00\u767E\u4E24\u91D1\u5B50\uFF01\u540A\u5F97\u8D77\u6765\uFF01\u300D</p><p>\u7075\u7389\u771F\u4EBA\u9053\u888D\u4E00\u632F\uFF0C\u8FEB\u5F00\u5C04\u5411\u81EA\u5DF1\u7684\u6D53\u75F0\uFF0C\u6A2A\u7709\u76EF\u7740\u5DF4\u9675\u4E00\u67AD\u5B89\u5B50\u5B8F\uFF0C\u51B7\u51B7\u9053\uFF1A\u300C\u6211\u8DDF\u4F60\u8D4C\u4E00\u53EA\u624B\u3002\u540A\u4E0D\u8D77\u6765\u3002\u300D</p><p>\u5B89\u5B50\u5B8F\u602A\u773C\u4E00\u7FFB\uFF0C\u4ED6\u5012\u4E0D\u662F\u6210\u5FC3\u62DB\u60F9\u7075\u7389\uFF0C\u53EA\u662F\u968F\u53E3\u4E00\u5410\uFF0C\u78B0\u5DE7\u800C\u5DF2\u3002\u4F46\u4ED6\u6A2A\u884C\u6C5F\u6E56\u591A\u5E74\uFF0C\u9762\u5BF9\u51F6\u540D\u8D6B\u8D6B\u7684\u7075\u7389\u4E5F\u81EA\u4E0D\u60E7\uFF0C\u91CD\u91CD\u54FC\u4E86\u4E00\u58F0\uFF0C\u4E00\u53E0\u58F0\u50AC\u5B8B\u5927\u75A4\u8D76\u7D27\u52A8\u624B\u3002</p><p>\u8F70\u95F9\u58F0\u4E2D\uFF0C\u5B8B\u5927\u75A4\u624B\u4E00\u626C\uFF0C\u9B3C\u7D22\u7B14\u76F4\u7A9C\u5230\u534A\u7A7A\uFF0C\u7ED5\u8FC7\u6A2A\u6881\u3002\u6839\u90E8\u624E\u7D27\u7684\u80A5\u4E73\u9F13\u6210\u4E00\u4E2A\u767D\u817B\u7684\u5706\u7403\uFF0C\u96EA\u5CF0\u795E\u5C3C\u56DB\u80A2\u8F6F\u5782\uFF0C\u7389\u4F53\u968F\u7740\u7EF3\u7D22\u7684\u626F\u52A8\u6162\u6162\u62AC\u8D77\u3002\u5148\u662F\u80F8\u90E8\uFF0C\u63A5\u7740\u8170\u8EAB\u4E5F\u88AB\u62C9\u76F4\u3002</p><p>\u7CFB\u6210\u6D3B\u6263\u7684\u7EF3\u7D22\u8D8A\u6765\u8D8A\u7D27\uFF0C\u6DF1\u6DF1\u52D2\u8FDB\u4E73\u8089\u91CC\uFF0C\u6BB7\u7EA2\u7684\u4E73\u5934\u76F4\u76F4\u7FD8\u8D77\uFF0C\u5728\u4F17\u4EBA\u6025\u5207\u7684\u76EE\u5149\u8D8A\u5347\u8D8A\u9AD8\u3002</p><p>\u5F85\u4E0A\u8EAB\u5B8C\u5168\u62C9\u76F4\uFF0C\u96EA\u5CF0\u795E\u5C3C\u5FFD\u7136\u67D4\u9888\u4E00\u626C\uFF0C\u8EAB\u4F53\u5FAE\u5FAE\u540E\u503E\uFF0C\u88AB\u62C9\u6210\u4EF0\u9762\u671D\u5929\u7684\u6A21\u6837\u3002\u4E00\u5BF9\u6D51\u5706\u7684\u4E73\u7403\u80C0\u80C0\u9F13\u8D77\uFF0C\u4EFF\u4F5B\u80F8\u524D\u653E\u4E86\u4E24\u56E2\u7855\u5927\u7684\u96EA\u7403\u3002</p><p>\u98D8\u6885\u5CF0\u51E0\u540D\u5F1F\u5B50\u7ECF\u8FC7\u4E00\u591C\u7684\u8E42\u8E8F\uFF0C\u65E9\u5DF2\u660F\u8FF7\u4E0D\u9192\uFF0C\u6CA1\u6709\u770B\u5230\u5E08\u7236\u88AB\u4EBA\u6346\u7740\u4E73\u623F\u62D6\u8D77\u7684\u666F\u8C61\u3002</p><p>\u4E0D\u591A\u65F6\u96EA\u767D\u7684\u4E73\u7403\u5F00\u59CB\u53D1\u7EA2\uFF0C\u4F17\u4EBA\u770B\u5F97\u5174\u594B\uFF0C\u8FD8\u4EE5\u4E3A\u662F\u5145\u8840\u7684\u7F18\u6545\uFF0C\u8C01\u90FD\u6CA1\u6709\u60F3\u5230\u300C\u5C38\u4F53\u300D\u8FD8\u5982\u4F55\u5145\u8840\u3002</p><p>\u9B3C\u7D22\u6536\u7D27\uFF0C\u8272\u6CFD\u901A\u7EA2\u7684\u4E73\u7403\u5FFD\u7136\u4E00\u8DF3\uFF0C\u795E\u5C3C\u7684\u53CC\u819D\u5DF2\u7ECF\u79BB\u5F00\u5730\u9762\u3002</p><p>\u8FC7\u4E86\u8FD9\u4E00\u5173\uFF0C\u5B89\u5B50\u5B8F\u4E0D\u7531\u5472\u7259\u4E00\u4E50\uFF0C\u659C\u773C\u770B\u770B\u7075\u7389\u771F\u4EBA\u7684\u5DE6\u624B\uFF0C\u53C8\u659C\u773C\u770B\u770B\u4ED6\u7684\u53F3\u624B\u3002</p><p>\u7075\u7389\u771F\u4EBA\u4E0D\u52A8\u58F0\u8272\uFF0C\u53F3\u624B\u5728\u5DE6\u8896\u4E0A\u8F7B\u8F7B\u4E00\u5F39\uFF0C\u50CF\u662F\u5F39\u5F00\u53EA\u8682\u8681\u822C\u4E0D\u653E\u5728\u5FC3\u4E0A\u3002</p><p>\u5B8B\u5927\u75A4\u4E00\u5BF8\u4E00\u5BF8\u6536\u56DE\u9B3C\u7D22\uFF0C\u6F06\u9ED1\u7684\u7EF3\u8EAB\u6DF1\u6DF1\u5D4C\u8FDB\u7EA2\u767D\u52A8\u4EBA\u7684\u4E73\u7403\u4E2D\uFF0C\u50CF\u8981\u5C06\u4E73\u623F\u5207\u65AD\u4E00\u822C\u3002</p><p>\u96EA\u5CF0\u795E\u5C3C\u5927\u534A\u4E2A\u8EAB\u4F53\u5DF2\u7ECF\u88AB\u540A\u4E86\u8D77\u6765\uFF0C\u5979\u53CC\u76EE\u7D27\u95ED\uFF0C\u4E0A\u8EAB\u540E\u4EF0\uFF0C\u819D\u76D6\u79BB\u5F00\u5730\u9762\uFF0C\u5C0F\u817F\u5F2F\u66F2\uFF0C\u811A\u5C16\u8D34\u5728\u5730\u4E0A\uFF0C\u5C0F\u8179\u633A\u8D77\uFF0C\u5FAE\u5206\u7684\u53CC\u817F\u95F4\u80A5\u539A\u7684\u82B1\u74E3\u9AD8\u9AD8\u9F13\u8D77\u3002</p><p>\u5F53\u5979\u4E24\u817F\u4F38\u76F4\u7684\u65F6\u5019\uFF0C\u4E73\u8089\u7684\u627F\u53D7\u4E5F\u5230\u4E86\u6781\u9650\u3002\u4E73\u6839\u88AB\u9B3C\u7D22\u7F20\u5F97\u53EA\u5269\u4E00\u63E1\u7C97\u7EC6\u3002</p><p>\u80A5\u7855\u7684\u4E73\u7403\u50CF\u662F\u8131\u4F53\u800C\u53BB\uFF0C\u5728\u9694\u7740\u80F8\u90E8\u4E09\u5BF8\u6709\u4F59\u7684\u8DDD\u79BB\uFF0C\u7206\u88C2\u822C\u9690\u9690\u98A4\u6296\u3002</p><p>\u5B89\u5B50\u5B8F\u6307\u7740\u51E0\u4E4E\u8981\u6EF4\u8840\u822C\u7684\u4E73\u7403\u56B7\u9053\uFF1A\u300C\u540A\u8D77\u6765\u4E86\uFF01\u540A\u8D77\u6765\u4E86\uFF01\u300D</p><p>\u8D6B\u8FDE\u96C4\u62B1\u81C2\u9053\uFF1A\u300C\u5B89\u5144\u522B\u6025\uFF0C\u811A\u5C16\u8FD8\u6CA1\u79BB\u5730\u5462\u3002\u300D</p><p>\u5B89\u5B50\u5B8F\u62C9\u957F\u58F0\u97F3\u602A\u58F0\u9053\uFF1A\u300C\u6211\u4E0D\u6025\u3002\u6709\u4EBA\u6025\u2026\u2026\u300D</p><p>\u96EA\u5CF0\u795E\u5C3C\u7684\u811A\u638C\u6162\u6162\u7AD6\u76F4\uFF0C\u53EA\u5269\u811A\u5C16\u70B9\u5728\u5730\u4E0A\u3002\u6ED1\u5AE9\u7684\u4E73\u6839\u88AB\u6B8B\u5FCD\u5730\u626F\u6210\u7EC6\u957F\u72B6\uFF0C\u8089\u7403\u50CF\u662F\u8981\u88AB\u751F\u751F\u63EA\u6389\u822C\u53D8\u5F97\u7D2B\u9ED1\uFF0C\u4E73\u7403\u4E0E\u80F8\u90E8\u4E4B\u95F4\u8DB3\u4EE5\u5BB9\u7EB3\u4E00\u53EA\u5E73\u653E\u7684\u624B\u638C\u3002\u773C\u770B\u53EA\u5DEE\u4E00\u70B9\u8EAB\u4F53\u5C31\u8981\u51CC\u7A7A\u800C\u8D77\u65F6\uFF0C\u4E73\u4E0A\u9B3C\u7D22\u5FFD\u7136\u4E00\u52A8\uFF0C\u50CF\u524D\u6ED1\u4E86\u4E00\u5206\u3002</p><p>\u5B89\u5B50\u5B8F\u51B2\u5B8B\u5927\u75A4\u53EB\u9053\uFF1A\u300C\u6162\u7740\u70B9\u513F\uFF01\u7A33\u4F4F\uFF01\u300D</p><p>\u5B8B\u5927\u75A4\u5FC3\u91CC\u6697\u6697\u53EB\u82E6\uFF0C\u8FD9\u540A\u8D77\u6765\u5C31\u5F97\u7F6A\u4E86\u7075\u7389\u771F\u4EBA\uFF1B\u540A\u4E0D\u8D77\u6765\u53C8\u5F97\u7F6A\u4E86\u5DF4\u9675\u4E00\u67AD\u2026\u2026\u8FD9\u4E24\u4E2A\u4ED6\u8C01\u90FD\u60F9\u4E0D\u8D77\uFF0C\u53EA\u597D\u602A\u81EA\u5DF1\u591A\u4E8B\u3002</p><p>\u5B8B\u5927\u75A4\u7A33\u4F4F\u9B3C\u7D22\uFF0C\u7CFB\u6210\u6D3B\u6263\u7684\u7EF3\u7AEF\u4F7F\u529B\uFF0C\u5C06\u4E73\u7403\u7CFB\u5F97\u66F4\u7D27\u3002\u96EA\u5CF0\u795E\u5C3C\u50F5\u5728\u534A\u7A7A\uFF0C\u4E00\u52A8\u4E0D\u52A8\u3002\u6CA1\u6709\u4EBA\u77E5\u9053\u5979\u4F53\u5185\u7684\u771F\u6C14\u5DF2\u7ECF\u5F00\u59CB\u8FD0\u8F6C\u3002</p><p>\u300C\u5B8B\u5927\u75A4\uFF0C\u4F60\u4ED6\u5988\u7684\u600E\u4E48\u4E0D\u62C9\u4E86\uFF1F\u300D</p><p>\u5B8B\u5927\u75A4\u966A\u7740\u7B11\u8138\u9053\uFF1A\u300C\u5B89\u7237\uFF0C\u5C0F\u7684\u4F7F\u4E0D\u4E0A\u52B2\u513F\u2026\u2026\u300D</p><p>\u300C\u64CD\u4F60\u5988\uFF01\u62FD\uFF01\u300D</p><p>\u5B8B\u5927\u75A4\u54BD\u4E86\u53E3\u5410\u6CAB\uFF0C\u4E24\u624B\u6162\u6162\u4F7F\u529B\u3002\u7EC6\u767D\u7684\u811A\u638C\u5347\u8D77\u4E1D\u6BEB\uFF0C\u53EA\u5269\u8DBE\u5C16\u6328\u7740\u5730\u9762\uFF0C\u53C8\u4E0D\u52A8\u4E86\u3002</p><p>\u5B89\u5B50\u5B8F\u602A\u773C\u4E00\u7FFB\uFF0C\u7A81\u7136\u62AC\u624B\u5728\u795E\u5C3C\u5DE6\u80A9\u4E00\u637B\u3002</p><p>\u7075\u7389\u771F\u4EBA\u888D\u8896\u4E00\u7FFB\uFF0C\u4E00\u53EA\u7EC6\u957F\u6709\u529B\u7684\u624B\u638C\u500F\u5FFD\u4F38\u51FA\uFF0C\u9694\u5F00\u5B89\u5B50\u5B8F\u63A0\u5411\u795E\u5C3C\u53F3\u80A9\u7684\u7C97\u638C\u3002</p><p>\u300C\u7BF7\u300D\u7684\u4E00\u58F0\u52B2\u6C14\u4F4E\u54CD\uFF0C\u5B89\u5B50\u5B8F\u8138\u4E0A\u7D2B\u6C14\u9690\u73B0\u3002</p><p>\u7075\u7389\u771F\u4EBA\u51B7\u7B11\u9053\uFF1A\u300C\u59D3\u5B89\u7684\uFF0C\u8D4C\u4E0D\u8D77\uFF1F\u300D</p><p>\u5B89\u5B50\u5B8F\u731D\u4E0D\u53CA\u9632\uFF0C\u8F93\u4E86\u4E00\u62DB\uFF0C\u5E78\u597D\u7075\u7389\u6CA1\u6709\u501F\u52BF\u8FDB\u903C\u3002\u4ED6\u6068\u6068\u5478\u4E86\u4E00\u53E3\uFF0C\u628A\u624B\u91CC\u7684\u91D1\u9488\u5F80\u5730\u4E0A\u4E00\u4E22\u3002</p><p>\u773C\u770B\u53EA\u5DEE\u4E00\u4E1D\u5C31\u80FD\u53D6\u80DC\uFF0C\u5B89\u5B50\u5B8F\u6025\u5F97\u6293\u8033\u76EE\u6320\u816E\uFF0C\u4ED6\u89C1\u795E\u5C3C\u8EAB\u4E0A\u8FD8\u63D2\u7740\u4E94\u679A\u91D1\u9488\uFF0C\u6C42\u80DC\u5FC3\u5207\u4E4B\u4E0B\uFF0C\u4FBF\u6253\u8D77\u4E86\u8FD9\u4E9B\u7EC6\u9488\u7684\u4E3B\u610F\uFF0C\u60F3\u62D4\u4E0B\u5B83\u51CF\u8F7B\u5C38\u4F53\u7684\u4EFD\u91CF\u3002</p><p>\u4E00\u4F17\u56F4\u89C2\u8005\u89C1\u4E24\u4EBA\u52A8\u624B\uFF0C\u90FD\u4E50\u5475\u5475\u5728\u65C1\u8FB9\u770B\u70ED\u95F9\u3002\u5C60\u6000\u6C89\u8FDE\u5FD9\u8FC7\u6765\u5706\u573A\uFF0C\u6EE1\u8138\u5806\u7B11\u5730\u8BF4\uFF1A\u300C\u4E24\u4F4D\u83AB\u6012\uFF0C\u83AB\u6012\u3002\u8BA9\u5B8B\u5927\u4FA0\u7EE7\u7EED\uFF0C\u7EE7\u7EED\u3002\u5475\u5475\uFF0C\u4F9D\u5C0F\u5F1F\u4E4B\u89C1\uFF0C\u80FD\u4E0D\u80FD\u540A\u8D77\u5E08\u592A\u2014\u2014\u8FD8\u5728\u4E24\u53EF\u4E4B\u95F4\u2026\u2026\u300D</p><p>\u7075\u7389\u771F\u4EBA\u548C\u5B89\u5B50\u5B8F\u8FDC\u6765\u662F\u5BA2\uFF0C\u4E5F\u4E0D\u80FD\u4E0D\u4E70\u4E1C\u9053\u4E3B\u7684\u8D26\uFF0C\u4E24\u4EBA\u540C\u65F6\u51B7\u54FC\u4E00\u58F0\uFF0C\u522B\u8FC7\u8138\u77A7\u5411\u573A\u4E2D\u3002</p><p>\u4E24\u4EBA\u4E0B\u4E86\u91CD\u6CE8\uFF0C\u8D4C\u4E0A\u5BF9\u65B9\u4E00\u53EA\u624B\uFF0C\u4E0D\u7BA1\u80FD\u4E0D\u80FD\u540A\u8D77\u6765\uFF0C\u8FD9\u573A\u597D\u620F\u5927\u5925\u513F\u662F\u770B\u5B9A\u4E86\u3002\u6570\u767E\u540D\u5BBE\u5BA2\u56F4\u6210\u51E0\u5C42\uFF0C\u4E07\u4F17\u77A9\u76EE\u5730\u77A7\u7740\u96EA\u5CF0\u795E\u5C3C\u3002</p><p>\u660E\u5A9A\u7684\u9633\u5149\u4ECE\u95E8\u53E3\u6CC4\u5165\uFF0C\u51E0\u70B9\u7EC6\u5C0F\u7684\u7070\u5C18\u5728\u660E\u4EAE\u7684\u5149\u7EBF\u4E2D\u98D8\u821E\u7740\uFF0C\u6251\u5728\u96EA\u5CF0\u795E\u5C3C\u7684\u7389\u8138\u4E0A\u3002</p><p>\u5C31\u5728\u4F17\u4EBA\u6EE1\u5FC3\u671F\u76FC\u7684\u65F6\u5019\uFF0C\u96EA\u5CF0\u795E\u5C3C\u5FFD\u7136\u53CC\u76EE\u4E00\u7741\uFF0C\u773C\u4E2D\u7CBE\u5149\u56DB\u5C04\u3002\u4E0D\u7B49\u4F17\u4EBA\u53CD\u5E94\u8FC7\u6765\uFF0C\u5979\u53F3\u81C2\u4E00\u62AC\uFF0C\u63E1\u4F4F\u9B3C\u7D22\u5411\u4E0B\u4E00\u626F\u3002\u5B8B\u5927\u75A4\u5728\u4F17\u76EE\u777D\u777D\u4E0B\u500F\u5FFD\u817E\u7A7A\u800C\u8D77\uFF0C\u76F4\u76F4\u7A9C\u4E0A\u534A\u7A7A\u3002\u300C\u7BF7\u300D\u7684\u4E00\u58F0\uFF0C\u5934\u9885\u5728\u6A2A\u6881\u4E0A\u649E\u5F97\u7C89\u788E\u3002</p><p>\u9C9C\u8840\u6DF7\u7740\u8111\u6D46\u96E8\u70B9\u6E85\u843D\u4E0B\u6765\uFF0C\u6CBE\u5728\u96EA\u5CF0\u795E\u5C3C\u8D64\u88F8\u7684\u7389\u4F53\u4E0A\u3002</p>',66);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
