import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_60" tabindex="-1"><a class="header-anchor" href="#_60" aria-hidden="true">#</a> 60</h1><p>\u5927\u6BBF\u6084\u65E0\u58F0\u606F\uFF0C\u6570\u767E\u4EBA\u6CE5\u5851\u6728\u5076\u822C\u56F4\u6210\u4E00\u5708\uFF0C\u5446\u5446\u770B\u7740\u7A81\u7136\u590D\u6D3B\uFF0C\u5927\u5C55\u795E\u5A01\u7684\u96EA\u5CF0\u795E\u5C3C\u3002</p><p>\u96EA\u5CF0\u795E\u5C3C\u4E0A\u8EAB\u6CBE\u6EE1\u8840\u8FF9\u8111\u6D46\uFF0C\u5979\u5DE6\u80A9\u4E00\u632F\uFF0C\u4E00\u9053\u91D1\u5149\u95EA\u7535\u822C\u4ECE\u51B0\u808C\u7389\u9AA8\u4E2D\u5C04\u51FA\u3002</p><p>\u5BF9\u9762\u4E00\u4E2A\u62AB\u53D1\u6C49\u5B50\u989D\u5934\u4E0A\u4E4D\u7136\u73B0\u51FA\u4E00\u4E2A\u5C0F\u5C0F\u7684\u7EA2\u70B9\uFF0C\u63A5\u7740\u8138\u4E0A\u9732\u51FA\u96BE\u4EE5\u7F6E\u4FE1\u7684\u795E\u8272\uFF0C\u5C31\u6B64\u6C14\u7EDD\u3002</p><p>\u96EA\u5CF0\u795E\u5C3C\u53CC\u81C2\u51E4\u7FFC\u822C\u5C55\u5F00\uFF0C\u7389\u4F53\u6CDB\u8D77\u4E00\u5C42\u4E0D\u540C\u4E8E\u8840\u8272\u7684\u9C9C\u7EA2\u4E4B\u6C14\u3002\u7728\u773C\u95F4\u90A3\u5C42\u7EA2\u8272\u5DF2\u7ECF\u6EA2\u51FA\u8089\u4F53\uFF0C\u70BD\u70ED\u7684\u6C14\u6D41\u7ED5\u7740\u767D\u5AE9\u7684\u7389\u4F53\u6EDA\u6EDA\u6D6E\u52A8\uFF0C\u96EA\u5CF0\u795E\u5C3C\u5B9B\u5982\u6D74\u706B\u51E4\u51F0\u822C\u7FD8\u9996\u800C\u7ACB\uFF0C\u53D1\u51FA\u4E00\u58F0\u6E05\u4EAE\u7684\u957F\u5578\u3002</p><p>\u957F\u5578\u58F0\u4E2D\uFF0C\u7F20\u5728\u4E73\u4E0A\u7684\u9B3C\u7D22\u5BF8\u5BF8\u65AD\u88C2\uFF0C\u4F59\u4E0B\u91D1\u9488\u6FC0\u5C04\u800C\u51FA\uFF0C\u5468\u56F4\u56DB\u540D\u90AA\u9053\u9AD8\u624B\u88AB\u91D1\u9488\u8D2F\u9885\u800C\u5165\uFF0C\u65E0\u58F0\u65E0\u606F\u7684\u6BD9\u547D\u5F53\u573A\u3002</p><p>\u5239\u90A3\u95F4\u516D\u4EBA\u6A2A\u5C38\u5C31\u5730\uFF0C\u4EBA\u4EEC\u624D\u53CD\u5E94\u8FC7\u6765\u3002\u5982\u540C\u6570\u5341\u679A\u7834\u7A7A\u96F7\u540C\u65F6\u5728\u573A\u4E2D\u70B8\u5F00\uFF0C\u4EBA\u7FA4\u8F70\u7136\u800C\u6563\u3002\u7728\u773C\u5DE5\u592B\uFF0C\u6570\u767E\u4EBA\u56F4\u6210\u7684\u5927\u5708\u5B50\uFF0C\u5C31\u5269\u4E0B\u5BE5\u5BE5\u51E0\u6761\u8EAB\u5F71\u3002</p><p>\u7075\u7389\u771F\u4EBA\u957F\u7709\u4E00\u6311\uFF0C\u4ECE\u8896\u4E2D\u638F\u51FA\u4E00\u67C4\u62C2\u5C18\u3002</p><p>\u5B89\u5B50\u5B8F\u62D4\u51FA\u5F2F\u94A9\uFF0C\u77EE\u8EAB\u4F5C\u52BF\uFF0C\u53E3\u4E2D\u5475\u5475\u6709\u58F0\u3002</p><p>\u8D6B\u8FDE\u96C4\u4E24\u624B\u7D27\u63E1\u77ED\u621F\uFF0C\u4E1D\u6BEB\u4E0D\u6562\u5927\u610F\u3002</p><p>\u65C1\u8FB9\u8FD8\u6709\u4E00\u4E2A\u7FAF\u4EBA\u88C5\u675F\u7684\u9EC4\u987B\u6C49\u5B50\uFF0C\u4E5F\u672A\u9000\u5F00\u3002</p><p>\u5C60\u6000\u6C89\u5FC3\u60CA\u8089\u8DF3\uFF0C\u5355\u770B\u96EA\u5CF0\u795E\u5C3C\u903C\u51FA\u300C\u51DD\u771F\u4E5D\u9488\u300D\u7684\u58F0\u52BF\uFF0C\u4FBF\u77E5\u5979\u4E0D\u4F46\u5185\u529F\u5168\u590D\uFF0C\u800C\u4E14\u8F83\u6614\u65E5\u95EF\u5BAB\u7684\u60CA\u4EBA\u529F\u529B\u5C24\u4E3A\u7CBE\u76DB\u3002\u6B64\u65F6\u6C90\u3001\u91D1\u3001\u970D\u7B49\u4EBA\u90FD\u4E0D\u5728\uFF0C\u53EA\u5269\u81EA\u5DF1\u4E00\u540D\u957F\u8001\u5F3A\u6491\u573A\u9762\u3002\u4ED6\u786C\u7740\u5934\u76AE\u626C\u8D77\u53CC\u638C\uFF0C\u5E38\u5E74\u6302\u5728\u8138\u4E0A\u7684\u7B11\u5BB9\u4E0D\u7FFC\u800C\u98DE\u3002</p><p>\u96EA\u5CF0\u795E\u5C3C\u4E2D\u8BA1\u88AB\u64D2\uFF0C\u8EAB\u53D7\u5947\u8FB1\uFF0C\u65E9\u5DF2\u662F\u6EE1\u5FC3\u4EC7\u6068\uFF0C\u6B64\u65F6\u529F\u529B\u5C3D\u590D\uFF0C\u5F53\u4E0B\u7D20\u624B\u4E00\u626C\uFF0C\u70BD\u70ED\u7684\u6C14\u6D41\u72C2\u6D8C\u800C\u51FA\u3002</p><p>\u9996\u5F53\u5176\u51B2\u7684\u5B89\u5B50\u5B8F\u72C2\u53EB\u7740\u6325\u51FA\u5F2F\u94A9\uFF0C\u5168\u529B\u5C01\u6321\u3002\u5BD2\u5149\u56DB\u5C04\u7684\u94A2\u94A9\u4E0E\u53D1\u7EA2\u7684\u6C14\u6D41\u4E00\u89E6\uFF0C\u7ACB\u5373\u8131\u624B\u98DE\u8D77\uFF0C\u63A5\u7740\u7EA2\u5149\u5927\u76DB\uFF0C\u541E\u6CA1\u4E86\u4ED6\u7684\u53F3\u81C2\u3002\u5B89\u5B50\u5B8F\u53F3\u624B\u864E\u53E3\u9707\u88C2\uFF0C\u6574\u6761\u624B\u81C2\u50CF\u88AB\u70C8\u706B\u70E7\u7099\u822C\u5267\u75DB\u65E0\u6BD4\u3002</p><p>\u6726\u80E7\u7684\u7EA2\u5149\u4E2D\uFF0C\u53EA\u89C1\u5B89\u5B50\u5B8F\u864E\u53E3\u9C9C\u8840\u8FF8\u6D8C\uFF0C\u8840\u73E0\u4E00\u6EF4\u4E00\u6EF4\u88AB\u9F13\u8361\u7684\u771F\u6C14\u7EDE\u6210\u96FE\u72B6\uFF0C\u63A5\u7740\u88AB\u771F\u6C14\u84B8\u53D1\uFF0C\u6CA1\u6709\u4E00\u6EF4\u843D\u5728\u5730\u4E0A\u3002\u4ED6\u52C9\u5F3A\u50AC\u53D1\u5185\u529F\u7AED\u529B\u76F8\u6297\uFF0C\u7247\u523B\u95F4\uFF0C\u989D\u4E0A\u4FBF\u5E03\u6EE1\u5149\u6676\u6676\u7684\u6C57\u73E0\u3002</p><p>\u7075\u7389\u771F\u4EBA\u6B63\u72B9\u8C6B\u95F4\uFF0C\u8D6B\u8FDE\u96C4\u5DF2\u7ECF\u62A2\u5148\u51FA\u624B\uFF0C\u77ED\u621F\u4E0A\u6311\u4E0B\u5C01\uFF0C\u5954\u96F7\u822C\u523A\u5165\u96EA\u5CF0\u795E\u5C3C\u5468\u8EAB\u8361\u6F3E\u7684\u7EA2\u5149\u4E2D\u3002</p><p>\u8D6B\u8FDE\u96C4\u864E\u8EAF\u4E00\u9707\uFF0C\u77ED\u621F\u5982\u4E2D\u94C1\u77F3\uFF0C\u9707\u5F97\u53CC\u81C2\u53D1\u9EBB\u3002\u4ED6\u4E0D\u77E5\u9053\u795E\u5C3C\u521A\u521A\u7A81\u7834\u4E86\u51E4\u51F0\u5B9D\u5178\u7B2C\u4E03\u5C42\u7684\u754C\u9650\uFF0C\u6B63\u5904\u4E8E\u771F\u6C14\u5347\u817E\u7684\u7D27\u8981\u5173\u5934\u3002\u4E0D\u9700\u50AC\u53D1\uFF0C\u62A4\u4F53\u771F\u6C14\u4FBF\u7B3C\u7F69\u5168\u8EAB\uFF0C\u4E0D\u60E7\u5175\u5203\u3002</p><p>\u8D6B\u8FDE\u96C4\u4E00\u51FB\u65E0\u529F\uFF0C\u5C60\u6000\u6C89\u4E24\u638C\u4E00\u9519\uFF0C\u7331\u8EAB\u4E0A\u524D\u3002\u96EA\u5CF0\u795E\u5C3C\u624B\u81C2\u4E00\u5377\u4E00\u63A8\uFF0C\u53EA\u542C\u683C\u683C\u58F0\u54CD\uFF0C\u5B89\u5B50\u5B8F\u65AD\u7EBF\u98CE\u7B5D\u822C\u76F4\u98DE\u51FA\u53BB\uFF0C\u53F3\u81C2\u88AB\u62E7\u5F97\u7C89\u788E\u3002</p><p>\u7075\u7389\u771F\u4EBA\u62C2\u5C18\u4E00\u626C\uFF0C\u671D\u96EA\u5CF0\u795E\u5C3C\u8138\u4E0A\u626B\u53BB\uFF0C\u540C\u65F6\u4E94\u6307\u5982\u94A9\uFF0C\u8981\u5728\u795E\u5C3C\u80A5\u7855\u7684\u7389\u4E73\u4E0A\u638F\u51FA\u4E94\u4E2A\u8840\u6D1E\u3002</p><p>\u8D2F\u6EE1\u771F\u6C14\u7684\u62C2\u4E1D\u521A\u89E6\u5230\u795E\u5C3C\u7684\u62A4\u4F53\u771F\u6C14\uFF0C\u7ACB\u523B\u50CF\u6295\u5165\u706B\u7130\u4E2D\u4E00\u822C\u5377\u4E86\u8D77\u6765\u3002</p><p>\u96EA\u5CF0\u795E\u5C3C\u4E3E\u638C\u5C06\u5C60\u6000\u6C89\u9707\u51FA\u6570\u6B65\uFF0C\u63A5\u7740\u7389\u817F\u4E00\u62AC\uFF0C\u811A\u5C16\u8E22\u5411\u7075\u7389\u638C\u5FC3\u3002</p><p>\u7389\u817F\u626C\u8D77\uFF0C\u5E26\u7740\u6495\u88C2\u4F24\u75D5\u7684\u79D8\u5904\u4E4D\u7136\u66B4\u9732\u3002\u88AB\u4F17\u4EBA\u73A9\u5F04\u591A\u65F6\u7684\u82B1\u74E3\u53C8\u7EA2\u53C8\u80BF\uFF0C\u6108\u52A0\u80A5\u539A\u3002\u4E00\u7247\u8273\u7EA2\u4E2D\uFF0C\u8FD8\u6DCC\u7740\u51E0\u7F15\u6D53\u6D4A\u7684\u9633\u7CBE\u3002\u7D2B\u6DA8\u7684\u4E73\u7403\u4E0A\u4E0B\u8DF3\u8DC3\uFF0C\u88AB\u9B3C\u7D22\u52D2\u51FA\u7684\u75D5\u8FF9\u6DF1\u5165\u4E73\u8089\uFF0C\u50CF\u70D9\u5728\u808C\u80A4\u4E0A\u822C\u6E05\u6670\u53EF\u89C1\u3002</p><p>\u96EA\u5CF0\u795E\u5C3C\u6D01\u8EAB\u81EA\u597D\u6570\u5341\u5E74\uFF0C\u4ECE\u672A\u88AB\u4EBA\u89C1\u8FC7\u81EA\u5DF1\u7684\u8EAB\u5B50\uFF0C\u5982\u4ECA\u5374\u88AB\u4EBA\u53C8\u5978\u53C8\u6346\uFF0C\u8086\u610F\u73A9\u5F04\uFF0C\u8FD9\u4EFD\u803B\u8FB1\u523B\u9AA8\u96BE\u5FD8\uFF01\u6B64\u65F6\u4E24\u53EA\u4E73\u623F\u6346\u5F97\u5931\u53BB\u77E5\u89C9\uFF0C\u4E00\u62AC\u817F\uFF0C\u4E0B\u4F53\u7684\u6495\u88C2\u75DB\u5F7B\u5FC3\u80BA\u3002\u795E\u5C3C\u53C8\u7F9E\u53C8\u6012\uFF0C\u53CD\u624B\u6253\u5728\u8D6B\u8FDE\u96C4\u80A9\u4E0A\uFF0C\u5C06\u5927\u6F20\u98DE\u9E70\u6253\u4E86\u4E00\u4E2A\u65A4\u6597\uFF0C\u811A\u5C16\u52A0\u901F\u8E22\u51FA\u3002</p><p>\u7075\u7389\u771F\u4EBA\u89C1\u5230\u795E\u5C3C\u9707\u5F00\u661F\u6708\u6E56\u957F\u8001\u7684\u6C14\u52BF\uFF0C\u77E5\u9053\u786C\u62FC\u81EA\u5DF1\u4E5F\u96BE\u4EE5\u8BA8\u597D\uFF0C\u5DE6\u8155\u4E00\u7FFB\uFF0C\u64AE\u6307\u6210\u5200\uFF0C\u5212\u5728\u795E\u5C3C\u811A\u8E1D\u4E0A\u3002\u4ED6\u81EA\u5FD6\u8FD9\u4E00\u8BB0\u624B\u5200\u8FDE\u77F3\u67F1\u4E5F\u53EF\u51FB\u65AD\uFF0C\u4F46\u96EA\u5CF0\u795E\u5C3C\u786C\u751F\u751F\u53D7\u4E86\u4E00\u8BB0\uFF0C\u8138\u4E0A\u5374\u4E00\u65E0\u5F02\u72B6\u3002\u7075\u7389\u5927\u9A87\u9000\u5F00\uFF0C\u53EA\u89C9\u624B\u4E0A\u4E00\u8F7B\uFF0C\u62C2\u5C18\u53EA\u5269\u4E0B\u4E00\u4E2A\u5149\u79C3\u79C3\u7684\u7389\u67C4\u3002</p><p>\u795E\u5C3C\u4E3E\u624B\u6295\u8DB3\u95F4\uFF0C\u8D64\u88F8\u7684\u8EAB\u4F53\u7EA4\u6BEB\u6BD5\u9732\uFF0C\u808C\u5149\u80A4\u8272\u4E73\u6D6A\u81C0\u6CE2\u8361\u4EBA\u9B42\u9B44\uFF0C\u8BA9\u4EBA\u540E\u6094\u521A\u624D\u6CA1\u6709\u591A\u73A9\u5F04\u5979\u4E00\u4F1A\u513F\u3002\u4F46\u573A\u4E2D\u8BF8\u4EBA\u4E4D\u5408\u4E4D\u5206\uFF0C\u56DB\u540D\u9AD8\u624B\u90FD\u662F\u88AB\u96EA\u5CF0\u795E\u5C3C\u4E00\u62DB\u51FB\u9000\uFF0C\u5DF4\u9675\u4E00\u67AD\u66F4\u662F\u635F\u4E86\u4E00\u6761\u8180\u5B50\uFF0C\u4F17\u4EBA\u6EE1\u8154\u6B32\u706B\u90FD\u5316\u4F5C\u51B7\u6C57\u3002</p><p>\u96EA\u5CF0\u795E\u5C3C\u521A\u521A\u590D\u82CF\uFF0C\u771F\u6C14\u6D41\u8F6C\u8FD8\u7565\u6709\u4E0D\u7545\uFF0C\u8FEB\u5F00\u4F17\u4EBA\u540E\u5979\u6E38\u76EE\u56DB\u987E\uFF0C\u53EA\u89C1\u6BBF\u4E2D\u7684\u5DE8\u67F1\u540E\u9732\u51FA\u4E00\u622A\u767D\u751F\u751F\u7684\u624B\u81C2\uFF0C\u808C\u80A4\u7EC6\u5AE9\uFF0C\u5374\u662F\u9F50\u8098\u800C\u65AD\u3002\u5979\u5389\u5578\u4E00\u58F0\uFF0C\u817E\u7A7A\u800C\u8D77\u3002</p><p>\u90A3\u4E2A\u9EC4\u987B\u4EBA\u540C\u65F6\u8DC3\u8D77\uFF0C\u62AC\u624B\u6325\u51FA\u4E00\u6839\u94A2\u97AD\u3002\u97AD\u8EAB\u5E03\u6EE1\u950B\u5229\u7684\u5012\u523A\uFF0C\u9876\u7AEF\u4E00\u4E2A\u62F3\u5934\u5927\u5C0F\u7684\u5F2F\u94A9\u5982\u540C\u880D\u5C3E\u822C\uFF0C\u76F4\u94A9\u795E\u5C3C\u4E24\u817F\u4E4B\u95F4\u3002</p><p>\u300C\u880D\u5C3E\u97AD\uFF01\u300D\u6709\u4EBA\u8BA4\u51FA\u8FD9\u662F\u7FAF\u65CF\u9AD8\u624B\u77F3\u880D\u7684\u72EC\u95E8\u5175\u5668\u3002</p><p>\u96EA\u5CF0\u795E\u5C3C\u604D\u82E5\u672A\u95FB\uFF0C\u5B9B\u5982\u7389\u51E4\u51CC\u7A7A\uFF0C\u59FF\u52BF\u4F18\u7F8E\u5730\u63A0\u5F80\u6BBF\u67F1\u3002\u5FFD\u7136\u817F\u4E0A\u4E00\u7D27\uFF0C\u5DF2\u88AB\u880D\u5C3E\u97AD\u7F20\u4F4F\u3002</p><p>\u77F3\u880D\u8138\u4E0A\u9732\u51FA\u4E00\u4E1D\u6B8B\u5FCD\u7684\u7B11\u610F\uFF0C\u5E03\u6EE1\u5012\u94A9\u7684\u97AD\u8EAB\u500F\u5FFD\u6536\u7D27\uFF0C\u8FD9\u4E00\u4E0B\u5B9A\u8981\u8BA9\u96EA\u5CF0\u795E\u5C3C\u6574\u6761\u7389\u817F\u76AE\u8089\u65E0\u5B58\u3002</p><p>\u773C\u770B\u97AD\u68A2\u7684\u880D\u5C3E\u5C31\u8981\u94A9\u4F4F\u795E\u5C3C\u80A5\u539A\u7684\u82B1\u74E3\uFF0C\u880D\u5C3E\u97AD\u5FFD\u7136\u4E00\u8361\uFF0C\u4ECE\u7389\u817F\u4E0A\u6025\u901F\u5F39\u5F00\uFF0C\u5212\u51FA\u4E09\u56DB\u4E2A\u5BD2\u5149\u51DB\u51BD\u7684\u5708\u5B50\uFF0C\u671D\u77F3\u880D\u8116\u5B50\u4E0A\u5957\u53BB\u3002</p><p>\u77F3\u880D\u614C\u5FD9\u6492\u624B\uFF0C\u98DE\u8EAB\u95EA\u907F\u3002\u53EA\u542C\u8EAB\u540E\u4E00\u58F0\u60E8\u568E\uFF0C\u880D\u5C3E\u97AD\u4ECE\u4E00\u540D\u6C49\u5B50\u8138\u4E0A\u4E00\u63A0\u800C\u8FC7\uFF0C\u751F\u751F\u626F\u4E0B\u4ED6\u534A\u8FB9\u8138\u3002</p><p>\u67F1\u540E\u7684\u8D3A\u5BA2\u4E00\u8F70\u800C\u6563\uFF0C\u4E24\u4E2A\u8EB2\u95EA\u4E0D\u53CA\u7684\u88AB\u96EA\u5CF0\u795E\u5C3C\u4E00\u628A\u62E7\u4F4F\u8116\u5B50\uFF0C\u4E24\u4EBA\u54FC\u90FD\u6CA1\u54FC\u4E00\u58F0\u4FBF\u4E00\u547D\u545C\u547C\u3002</p><p>\u96EA\u5CF0\u795E\u5C3C\u62B1\u8D77\u98CE\u665A\u534E\uFF0C\u53EA\u89C1\u7231\u5F92\u4E0B\u4F53\u53C8\u7EA2\u53C8\u80BF\uFF0C\u4E00\u53EA\u4E73\u623F\u88AB\u4EBA\u6345\u7A7F\u4E00\u4E2A\u8840\u6D1E\uFF0C\u6D51\u8EAB\u6EE1\u662F\u7CBE\u6DB2\u7684\u8165\u81ED\u6C14\u5473\u3002</p><p>\u4EBA\u7FA4\u4E71\u7EB7\u7EB7\u671D\u95E8\u53E3\u6D8C\u53BB\uFF0C\u4E89\u76F8\u9003\u547D\u3002</p><p>\u96EA\u5CF0\u795E\u5C3C\u631F\u8D77\u98CE\u665A\u534E\u7EB5\u8EAB\u4E00\u8DC3\uFF0C\u6251\u5165\u6BBF\u95E8\u62E5\u6324\u7684\u4EBA\u6D41\u4E2D\u3002\u60E8\u53EB\u58F0\u987F\u65F6\u54CD\u6210\u4E00\u7247\uFF0C\u63A5\u7740\u8840\u5149\u66B4\u6DA8\uFF0C\u88AB\u795E\u5C3C\u5F92\u624B\u6495\u88C2\u7684\u65AD\u80A2\u788E\u8089\u56DB\u4E0B\u98DE\u6E85\u3002</p><p>\u5927\u95E8\u524D\u62E5\u6324\u7684\u4EBA\u7FA4\u987F\u65F6\u9E1F\u60CA\u517D\u8D70\uFF0C\u9732\u51FA\u4E00\u7247\u7A7A\u573A\u3002\u707F\u70C2\u7684\u9633\u5149\u4E2D\uFF0C\u53EA\u5269\u4E0B\u96EA\u5CF0\u795E\u5C3C\u6602\u7136\u800C\u7ACB\uFF0C\u6BB7\u7EA2\u7684\u9C9C\u8840\u4ECE\u4E30\u4E73\u80A5\u81C0\u4E0A\u4E0D\u4F4F\u6EF4\u843D\uFF0C\u98CE\u665A\u534E\u7684\u8170\u80A2\u8F6F\u7EF5\u7EF5\u642D\u5728\u5E08\u7236\u81C2\u95F4\uFF0C\u81FB\u9996\u4F4E\u5782\uFF0C\u7389\u4E73\u9AD8\u8038\uFF0C\u6E7F\u6DCB\u6DCB\u7684\u9633\u7CBE\u4E00\u76F4\u6D41\u5230\u811A\u5C16\uFF0C\u4E0E\u795E\u5C3C\u8DB3\u4E0B\u7684\u8840\u6CCA\u6C47\u6210\u4E00\u7247\u3002</p><p>\u96EA\u5CF0\u795E\u5C3C\u795E\u5C3C\u51B7\u51B0\u51B0\u8FC4\u7ACB\u5728\u795E\u6BBF\u5927\u95E8\u524D\uFF0C\u7389\u8138\u867D\u7136\u63A9\u6CA1\u5728\u9634\u5F71\u4E2D\uFF0C\u4F46\u6BCF\u4E2A\u4EBA\u90FD\u80FD\u770B\u51FA\u5979\u773C\u91CC\u65E0\u6BD4\u7684\u6068\u610F\u3002</p><p>\u534A\u664C\uFF0C\u96EA\u5CF0\u795E\u5C3C\u5389\u58F0\u9053\uFF1A\u300C\u65E0\u803B\u9F20\u8F88\uFF0C\u4ECA\u65E5\u6211\u8981\u5927\u5F00\u6740\u6212\uFF01\u5C06\u4F60\u4EEC\u4E00\u4E00\u788E\u5C38\u4E07\u6BB5\uFF01\u300D</p><p>\u80CC\u540E\u4E00\u58F0\u8F7B\u54B3\uFF0C\u795E\u5C3C\u8FD8\u672A\u56DE\u5934\uFF0C\u52B2\u6C14\u5DF2\u7136\u53CA\u4F53\u3002</p><p>\u795E\u6BBF\u5916\u6C90\u58F0\u4F20\u548C\u91D1\u5F00\u7532\u5E76\u80A9\u800C\u7ACB\uFF0C\u94DC\u8F6E\u5DE8\u65A7\u547C\u5578\u7740\u653B\u5165\u95E8\u4E2D\u3002</p><p>\u9762\u5BF9\u661F\u6708\u6E56\u8FD9\u4E24\u540D\u9876\u5C16\u9AD8\u624B\uFF0C\u96EA\u5CF0\u795E\u5C3C\u4E5F\u4E0D\u6562\u6258\u5927\u3002\u5979\u677E\u5F00\u98CE\u665A\u534E\uFF0C\u65CB\u8EAB\u8DC3\u8D77\uFF0C\u4E00\u62DB\u5F04\u7389\u5F15\u51E4\uFF0C\u5C06\u91D1\u5F00\u7532\u7684\u5DE8\u65A7\u5E26\u5230\u4E00\u65C1\uFF0C\u63A5\u7740\u65BD\u51FA\u98D8\u6885\u5CF0\u7EDD\u6280\uFF0C\u6321\u4F4F\u6C90\u58F0\u4F20\u7684\u77ED\u68CD\u3002</p><p>\u5979\u4EE5\u4E00\u654C\u4E8C\u72B9\u81EA\u653B\u591A\u5B88\u5C11\uFF0C\u6C90\u58F0\u4F20\u8138\u8272\u51DD\u91CD\uFF0C\u77ED\u68CD\u5708\u5B50\u8D8A\u5212\u8D8A\u5C0F\uFF0C\u7EF5\u7EF5\u5BC6\u5BC6\u5B88\u4F4F\u8981\u5BB3\u3002\u91D1\u5F00\u7532\u4EE5\u786C\u78B0\u786C\uFF0C\u5341\u51E0\u62DB\u4E00\u8FC7\uFF0C\u5DE8\u65A7\u7684\u98CE\u96F7\u4E4B\u58F0\u4E5F\u6E10\u6E10\u4F4E\u4E86\u4E0B\u53BB\u3002</p><p>\u4E5E\u4F0F\u7A77\u9686\u4E5F\u88AB\u56F0\u5728\u6BBF\u4E2D\uFF0C\u773C\u89C1\u795E\u5C3C\u80CC\u540E\u7A7A\u95E8\u5927\u9732\uFF0C\u7ACB\u5373\u62AC\u624B\u6253\u51FA\u4E09\u9897\u94C1\u4E38\u3002</p><p>\u8FD9\u4E00\u4E0B\u4F17\u4EBA\u624D\u9192\u609F\u8FC7\u6765\uFF0C\u5E26\u7740\u6697\u5668\u7684\u7EB7\u7EB7\u51FA\u624B\uFF0C\u4E00\u65F6\u95F4\u94C1\u83B2\u5B50\u3001\u94C1\u84BA\u85DC\u3001\u98DE\u5200\u3001\u8896\u7BAD\u3001\u94F6\u9488\u3001\u98DE\u8757\u77F3\u2026\u2026\u4E00\u7A9D\u8702\u822C\u671D\u795E\u5C3C\u80CC\u4E0A\u6253\u53BB\u3002</p><p>\u96EA\u5CF0\u795E\u5C3C\u5389\u559D\u4E00\u58F0\uFF0C\u8EAB\u4E0A\u7EA2\u5149\u95EA\u52A8\uFF0C\u4EFF\u4F5B\u4E00\u5C42\u541E\u5410\u7684\u706B\u7130\u3002\u4F17\u4EBA\u770B\u5F97\u6E05\u6E05\u695A\u695A\uFF0C\u8BF8\u822C\u6697\u5668\u6253\u5728\u7C89\u5AE9\u7684\u7389\u80CC\u4E0A\uFF0C\u7ACB\u5373\u88AB\u771F\u6C14\u8FEB\u5F00\u5F00\uFF0C\u5374\u6CA1\u6709\u7559\u4E0B\u4E00\u4E1D\u4F24\u75D5\u3002</p><p>\u6C90\u3001\u91D1\u4E8C\u4EBA\u6B63\u5403\u529B\u95F4\uFF0C\u5FFD\u7136\u4E00\u4E2A\u9AD8\u5927\u7684\u4EBA\u5F71\u4ECE\u4E24\u4EBA\u4E2D\u95F4\u6324\u5165\uFF0C\u4E00\u56E2\u4E4C\u5149\u76F4\u51FB\u795E\u5C3C\u80F8\u53E3\u3002</p><p>\u6765\u4EBA\u9ED1\u8863\u9ED1\u8896\uFF0C\u6B63\u662F\u9ED1\u98CE\u8C79\u8521\u4E91\u5CF0\uFF0C\u4ED6\u4E00\u8A00\u4E0D\u53D1\uFF0C\u4E24\u67C4\u516B\u89D2\u69CC\u76F4\u4E0A\u76F4\u4E0B\uFF0C\u5B8C\u5168\u662F\u4E0D\u8981\u547D\u7684\u6253\u6CD5\u3002\u8521\u4E91\u5CF0\u867D\u7136\u4E3E\u6B62\u53EF\u7B11\uFF0C\u4F46\u6B66\u529F\u5B9E\u5728\u4E86\u5F97\uFF0C\u4ED6\u4E00\u63D2\u624B\uFF0C\u6C90\u3001\u91D1\u4E24\u4EBA\u538B\u529B\u987F\u51CF\uFF0C\u582A\u582A\u6321\u4F4F\u795E\u5C3C\u7684\u653B\u52BF\u3002</p><p>\u4E00\u756A\u5267\u6597\uFF0C\u6FC0\u53D1\u4E86\u96EA\u5CF0\u795E\u5C3C\u4F53\u5185\u7684\u771F\u5143\uFF0C\u5979\u521A\u521A\u7A81\u7834\u96BE\u5173\uFF0C\u771F\u6C14\u8FD8\u672A\u80FD\u8FD0\u8F6C\u81EA\u5982\uFF0C\u6B64\u65F6\u8D8A\u6597\u8D8A\u987A\u7545\u3002\u5FFD\u7136\u95F4\u4E24\u624B\u4E00\u62B1\uFF0C\u4E00\u80A1\u5E9E\u5927\u65E0\u5339\u7684\u6C14\u52B2\u5DE8\u6D6A\u822C\u63A8\u51FA\u3002</p><p>\u6C90\u58F0\u4F20\u8138\u4E0A\u9752\u6C14\u4E00\u95EA\u5373\u6CA1\uFF0C\u5DF2\u7136\u53D7\u4E86\u5185\u4F24\uFF1B\u91D1\u5F00\u7532\u8138\u5982\u91D1\u7EB8\uFF0C\u88AB\u6797\u9999\u8FDC\u523A\u778E\u7684\u773C\u7403\u4E2D\u5192\u51FA\u4E00\u6EF4\u8840\u73E0\uFF1B\u8521\u4E91\u5CF0\u53C8\u5DEE\u4E86\u4E00\u5206\uFF0C\u53E3\u5410\u9C9C\u8840\uFF0C\u516B\u89D2\u69CC\u300C\u94DB\u5577\u300D\u843D\u5728\u5927\u7406\u77F3\u677F\u4E0A\u3002</p><p>\u96EA\u5CF0\u795E\u5C3C\u501A\u4ED7\u771F\u6C14\u62A4\u4F53\uFF0C\u5BF9\u6BBF\u5185\u8BF8\u4EBA\u6BEB\u4E0D\u653E\u5728\u5FC3\u4E0A\uFF0C\u4EFB\u7531\u80CC\u540E\u7A7A\u95E8\u5927\u9732\uFF0C\u7389\u638C\u4E00\u5206\uFF0C\u671D\u91D1\u5F00\u7532\u548C\u8521\u4E91\u5CF0\u5168\u529B\u5370\u4E0B\uFF0C\u8981\u5C06\u4E24\u4EBA\u6BD9\u4E8E\u638C\u5E95\u3002</p><p>\u91D1\u5F00\u7532\u66B4\u559D\u4E00\u58F0\uFF0C\u4E22\u5F00\u5DE8\u65A7\uFF0C\u94C1\u62F3\u91CD\u91CD\u51FB\u5728\u795E\u5C3C\u638C\u5FC3\uFF1B\u8521\u4E91\u5CF0\u5DF2\u7ECF\u91CD\u4F24\u5728\u5148\uFF0C\u867D\u7136\u608D\u4E0D\u754F\u6B7B\uFF0C\u4F46\u624B\u811A\u5374\u4E0D\u542C\u8BDD\uFF0C\u53EA\u597D\u773C\u7741\u7741\u770B\u7740\u90A3\u53EA\u7389\u638C\u7531\u5C0F\u53D8\u5927\u671D\u9762\u95E8\u5370\u6765\u3002</p><p>\u6B63\u5728\u6B64\u65F6\uFF0C\u4E24\u9053\u83B9\u767D\u7684\u5149\u8292\u5982\u540C\u6708\u534E\u822C\u4ECE\u80CC\u540E\u5C04\u6765\uFF0C\u65E0\u58F0\u65E0\u606F\u7684\u7834\u5F00\u62A4\u4F53\u771F\u6C14\u3002\u96EA\u5CF0\u795E\u5C3C\u80A9\u4E0A\u8840\u5149\u4E4D\u73B0\uFF0C\u4E24\u53EA\u975E\u91D1\u975E\u77F3\u7684\u7389\u767D\u5F2F\u94A9\u5B9B\u5982\u4E00\u5BF9\u5C16\u5C16\u7684\u5F2F\u6708\uFF0C\u523A\u7834\u9999\u808C\uFF0C\u6DF1\u6DF1\u94A9\u5165\u80A9\u5934\uFF0C\u7A7F\u8FDB\u7435\u7436\u9AA8\u5185\u3002</p><p>\u6EE1\u84C4\u7684\u771F\u6C14\u8F70\u7136\u800C\u6563\uFF0C\u96EA\u5CF0\u795E\u5C3C\u4E24\u817F\u4E00\u8F6F\uFF0C\u65E0\u529B\u5730\u8DEA\u5728\u5730\u4E0A\u3002</p>',54);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
