import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_61" tabindex="-1"><a class="header-anchor" href="#_61" aria-hidden="true">#</a> 61</h1><p>\u6155\u5BB9\u9F99\u7B11\u541F\u541F\u8D70\u5230\u96EA\u5CF0\u795E\u5C3C\u8EAB\u8FB9\uFF0C\u62AC\u811A\u5C06\u5979\u7684\u8170\u80A2\u91CD\u91CD\u8E29\u5728\u5730\u4E0A\uFF0C\u5FAE\u7B11\u9053\uFF1A\u300C\u8FD9\u65E5\u6708\u94A9\u662F\u6211\u661F\u6708\u6E56\u9547\u6559\u795E\u5175\uFF0C\u4E13\u7834\u5185\u5BB6\u771F\u6C14\u3002\u5E08\u592A\uFF0C\u6ECB\u5473\u5982\u4F55\uFF1F\u300D</p><p>\u96EA\u5CF0\u795E\u5C3C\u80A9\u4E0A\u7684\u4F24\u53E3\u8840\u5982\u6CC9\u6D8C\uFF0C\u53CC\u94A9\u5B9B\u5982\u6D78\u5728\u8840\u6CC9\u4E2D\u7684\u4E24\u9053\u6708\u5149\uFF0C\u4F9D\u7136\u8272\u6CFD\u5982\u7389\uFF0C\u6CA1\u6709\u6CBE\u4E0A\u4E00\u6EF4\u8840\u8FF9\u3002\u65E5\u94A9\u7684\u70ED\u6C14\u548C\u6708\u94A9\u7684\u5BD2\u6C14\u4ECE\u7435\u7436\u9AA8\u5185\u6CBF\u7740\u7ECF\u8109\u76F4\u900F\u4E39\u7530\uFF0C\u51E4\u51F0\u5B9D\u5178\u7684\u771F\u6C14\u7ACB\u65F6\u56DB\u5206\u4E94\u88C2\uFF0C\u6E83\u4E0D\u6210\u519B\u3002</p><p>\u6155\u5BB9\u9F99\u4E24\u624B\u4E00\u7D27\uFF0C\u5C06\u96EA\u5CF0\u795E\u5C3C\u7684\u4E0A\u534A\u8EAB\u626F\u4E86\u8D77\u6765\u3002\u96EA\u5CF0\u795E\u5C3C\u53CC\u819D\u7740\u5730\uFF0C\u8170\u90E8\u5374\u6298\u65AD\u822C\u7D27\u8D34\u7740\u5730\u9762\uFF0C\u4E0A\u8EAB\u88AB\u62C9\u6210\u7AD6\u76F4\u3002\u94A9\u8EAB\u7684\u7A81\u8D77\u78E8\u64E6\u5728\u9AA8\u9ABC\u4E0A\uFF0C\u9178\u75DB\u65E0\u6BD4\u3002</p><p>\u5979\u98A4\u6296\u7740\u54AC\u7D27\u7259\u5173\uFF0C\u7389\u4F53\u6E17\u51FA\u4E00\u5C42\u7EC6\u7EC6\u7684\u51B7\u6C57\u3002\u80A5\u5AE9\u7684\u4E73\u623F\u98A4\u5FAE\u5FAE\u60AC\u5728\u80F8\u524D\uFF0C\u6296\u8D77\u4E00\u7247\u767D\u817B\u7684\u8089\u5149\uFF0C\u8EAB\u540E\u79D8\u5904\u655E\u9732\uFF0C\u80A1\u95F4\u90A3\u56E2\u6CBE\u7740\u9633\u7CBE\u7684\u8089\u82B1\u7EA2\u8273\u8273\u9F13\u6210\u4E00\u56E2\uFF0C\u5D4C\u5728\u80A5\u767D\u5706\u6DA6\u7684\u96EA\u81C0\u6B63\u4E2D\uFF0C\u76F4\u76F4\u5BF9\u7740\u4F17\u4EBA\u3002</p><p>\u6155\u5BB9\u9F99\u4FA7\u5934\u6253\u91CF\u795E\u5C3C\u4E00\u756A\uFF0C\u624B\u4E2D\u4E00\u63D0\uFF0C\u5C06\u5979\u4E0A\u8EAB\u63D0\u5F97\u66F4\u9AD8\uFF0C\u62AC\u811A\u8E0F\u4F4F\u5979\u7684\u80A5\u4E73\u6413\u5F04\u7740\uFF0C\u5567\u5567\u7B11\u9053\uFF1A\u300C\u5E08\u592A\u8FD8\u771F\u662F\u6B20\u64CD\u5462\uFF0C\u5DF4\u5DF4\u7684\u6D3B\u8FC7\u6765\u2026\u2026\u8FD8\u6446\u6210\u8FD9\u5E45\u6328\u64CD\u7684\u6A21\u6837\u2026\u2026\u5C60\u957F\u8001\uFF0C\u627E\u4E2A\u5730\u65B9\u5C31\u8FD9\u6837\u628A\u8FD9\u8D31\u4EBA\u653E\u597D\uFF0C\u8BA9\u5927\u4F19\u655E\u5F00\u4E86\u64CD\uFF0C\u64CD\u6B7B\u4E3A\u6B62\u3002\u300D</p><p>\u5C60\u6000\u6C89\u7B54\u5E94\u4E00\u58F0\uFF0C\u5148\u4EE5\u91CD\u624B\u6CD5\u70B9\u4E86\u795E\u5C3C\u7684\u5341\u51E0\u5904\u5927\u7A74\uFF0C\u7136\u540E\u50CF\u7275\u7740\u731B\u864E\u822C\u5C0F\u5FC3\u7FFC\u7FFC\u5730\u62FF\u4F4F\u65E5\u6708\u94A9\u7684\u94C1\u94FE\uFF0C\u5C06\u96EA\u5CF0\u795E\u5C3C\u62D6\u5230\u6BBF\u5916\u3002</p><p>\u5145\u6EE1\u6068\u610F\u7684\u8138\u5E9E\uFF0C\u6EF4\u8840\u7684\u9999\u80A9\uFF0C\u767D\u5AE9\u7684\u8170\u81C0\u3001\u7389\u817F\u3001\u811A\u5C16\u4ECE\u4F17\u4EBA\u773C\u524D\u6E10\u6E10\u6D88\u5931\uFF0C\u5149\u6ED1\u7684\u5927\u7406\u77F3\u4E0A\u53EA\u7559\u4E0B\u4E00\u9053\u9C9C\u8840\u6DCB\u6F13\u7684\u5370\u8FF9\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u6155\u5BB9\u9F99\u62C9\u4F4F\u8521\u4E91\u5CF0\u7684\u624B\uFF0C\u8FB9\u8D70\u8FB9\u7B11\u9053\uFF1A\u300C\u8521\u5144\u597D\u529F\u592B\uFF01\u82E5\u975E\u5144\u53F0\u4ED7\u4E49\u51FA\u624B\uFF0C\u672C\u5BAB\u4E5F\u6CA1\u8FD9\u4E48\u5BB9\u6613\u64D2\u4E0B\u96EA\u5CF0\u8FD9\u8D31\u4EBA\u3002\u6765\uFF0C\u8BF7\u5750\u4E0B\u6682\u4E14\u4F11\u606F\uFF0C\u5F85\u6211\u8BF7\u6559\u4E2D\u795E\u533B\u53F6\u62A4\u6CD5\u4E3A\u8521\u5144\u8BCA\u6CBB\u4F24\u52BF\u3002\u300D</p><p>\u8521\u4E91\u5CF0\u7A81\u7136\u96C4\u8EAF\u4E00\u77EE\uFF0C\u8DEA\u5728\u5730\u4E0A\u91CD\u91CD\u78D5\u4E86\u4E2A\u5934\uFF0C\u7C97\u58F0\u9053\uFF1A\u300C\u8521\u67D0\u613F\u52A0\u5165\u661F\u6708\u6E56\u95E8\u4E0B\uFF0C\u4E3A\u5BAB\u4E3B\u548C\u592B\u4EBA\u8D74\u6C64\u8E48\u706B\u3001\u7C89\u8EAB\u788E\u9AA8\u4E07\u6B7B\u4E0D\u8F9E\uFF01\u8BF7\u5BAB\u4E3B\u6536\u7559\uFF01\u300D</p><p>\u6155\u5BB9\u9F99\u4E00\u6115\uFF0C\u65CB\u5373\u6717\u7B11\u9053\uFF1A\u300C\u597D\uFF01\u8521\u5144\u5FEB\u4EBA\u5FEB\u8BED\uFF0C\u679C\u7136\u662F\u6761\u597D\u6C49\uFF01\u300D\u4ED6\u58F0\u97F3\u4E00\u987F\uFF0C\u300C\u5C31\u8BF7\u8521\u5144\u4E3A\u6211\u6559\u957F\u8001\uFF0C\u6267\u638C\u6C34\u5802\uFF01\u300D</p><p>\u8521\u4E91\u5CF0\u52A0\u5165\u661F\u6708\u6E56\u53EA\u6C42\u671D\u5915\u80FD\u89C1\u4E0A\u73AB\u7470\u4ED9\u5B50\u4E00\u9762\uFF0C\u6CA1\u60F3\u5230\u5BAB\u4E3B\u5C45\u7136\u4EE5\u957F\u8001\u4E4B\u4F4D\u76F8\u8D60\uFF0C\u8FD9\u4EFD\u610F\u5916\u4E4B\u559C\u8BA9\u4ED6\u6655\u4E4E\u4E4E\u8BF4\u4E0D\u51FA\u8BDD\u6765\u3002</p><p>\u6155\u5BB9\u9F99\u56DE\u5934\u7B11\u9053\uFF1A\u300C\u5404\u4F4D\u53D7\u60CA\u4E86\uFF0C\u8BF7\u56DE\u5E2D\u95F4\u5B89\u5750\u3002\u300D</p><p>\u7B49\u4F17\u4EBA\u60CA\u9B42\u672A\u5B9A\u7684\u5750\u56DE\u5E2D\u95F4\uFF0C\u6155\u5BB9\u9F99\u53CC\u638C\u4E00\u62CD\uFF0C\u5341\u4F59\u540D\u5E2E\u4F17\u9C7C\u8D2F\u6D8C\u5165\u795E\u6BBF\uFF0C\u6BCF\u4EBA\u624B\u4E2D\u90FD\u62B1\u7740\u4E00\u4E2A\u9526\u76D2\uFF0C\u9759\u6084\u6084\u7ACB\u5728\u67F1\u65C1\u3002</p><p>\u300C\u672C\u5BAB\u4E0E\u73AB\u7470\u4ED9\u5B50\u6210\u5A5A\uFF0C\u6709\u52B3\u8BF8\u4F4D\u540C\u9053\u8D4F\u5149\u3002\u672C\u5BAB\u65E0\u4EE5\u4E3A\u62A5\uFF0C\u4E00\u70B9\u8584\u793C\uFF0C\u4E3A\u5404\u4F4D\u538B\u60CA\uFF0C\u656C\u8BF7\u7B11\u7EB3\u3002\u300D</p><p>\u5341\u4F59\u540D\u5E2E\u4F17\u6253\u5F00\u9526\u76D2\uFF0C\u53EA\u89C1\u76D2\u4E2D\u5F02\u5149\u95EA\u52A8\uFF0C\u5374\u662F\u4E00\u76D2\u660E\u73E0\u3002\u5341\u4F59\u540D\u5E2E\u4F17\u7A7F\u82B1\u8774\u8776\u822C\u6E38\u8D70\u5E2D\u95F4\uFF0C\u4E0D\u591A\u65F6\u6BCF\u4EBA\u5E2D\u4E0A\u90FD\u653E\u4E86\u4E00\u679A\u3002\u660E\u73E0\u6307\u5C16\u5927\u5C0F\uFF0C\u5149\u6655\u6D41\u8F6C\uFF0C\u867D\u7136\u4EF7\u503C\u4E0D\u532A\uFF0C\u5374\u4E5F\u5E76\u975E\u7F55\u89C1\u3002</p><p>\u4F17\u4EBA\u6B63\u7591\u60D1\u95F4\uFF0C\u53EA\u542C\u5BAB\u4E3B\u6717\u58F0\u9053\uFF1A\u300C\u6B64\u73E0\u4E43\u662F\u6211\u6559\u79D8\u5236\u660E\u73E0\u3002\u627F\u8499\u5404\u4F4D\u4E0D\u5F03\uFF0C\u5149\u4E34\u655D\u6559\uFF0C\u5C31\u4EE5\u6B64\u73E0\u4E3A\u62A5\uFF0C\u51ED\u6B64\u660E\u73E0\uFF0C\u65E0\u8BBA\u8BF8\u4F4D\u4F55\u4E8B\u76F8\u6258\uFF0C\u53EA\u8981\u6211\u661F\u6708\u6E56\u529B\u6240\u80FD\u53CA\uFF0C\u5FC5\u5B9A\u7AED\u529B\u76F8\u52A9\u3002\u300D</p><p>\u6B64\u8A00\u4E00\u51FA\uFF0C\u5E2D\u95F4\u7ACB\u65F6\u5927\u54D7\uFF0C\u5728\u5C9B\u4E0A\u6570\u65E5\uFF0C\u4F17\u4EBA\u5DF2\u77E5\u661F\u6708\u6E56\u52BF\u529B\u5E9E\u5927\uFF0C\u80FD\u548C\u8FD9\u7B49\u5927\u5E2E\u62C9\u4E0A\u5173\u7CFB\u5DF2\u7ECF\u662F\u5929\u5927\u7684\u9762\u5B50\uFF0C\u6CA1\u60F3\u5230\u6155\u5BB9\u5BAB\u4E3B\u7ADF\u4F1A\u5982\u6B64\u6177\u6168\u3002</p><p>\u91D1\u5F00\u7532\u5BF9\u6B64\u4E3E\u5927\u60D1\u4E0D\u89E3\uFF0C\u5982\u6B64\u4E00\u6765\uFF0C\u5149\u66FF\u8FD9\u51E0\u767E\u4EBA\u529E\u4E8B\uFF0C\u5C31\u5FD9\u4E0D\u8FC7\u6765\uFF0C\u8D77\u5175\u5927\u4E1A\u5982\u4F55\u5904\u7F6E\uFF1F</p><p>\u6C90\u58F0\u4F20\u5374\u662F\u5FC3\u4E0B\u6697\u8D5E\uFF0C\u8FD9\u4E00\u8BB0\u6536\u4E70\u4EBA\u5FC3\u975E\u6210\u5927\u4E8B\u8005\u96BE\u4EE5\u4E3A\u4E4B\u3002\u65E0\u8BBA\u4E8B\u60C5\u5927\u5C0F\uFF0C\u53EA\u8981\u5F00\u53E3\u76F8\u6C42\uFF0C\u4ECA\u540E\u4FBF\u4E0E\u661F\u6708\u6E56\u518D\u96BE\u65AD\u7EDD\u3002\u4E00\u7C92\u660E\u73E0\u6536\u4E70\u4E00\u540D\u9AD8\u624B\uFF0C\u7B97\u6765\u5B9E\u662F\u5927\u5360\u4FBF\u5B9C\u3002</p><p>\u6155\u5BB9\u9F99\u542B\u7B11\u9053\uFF1A\u300C\u655D\u6559\u4E0E\u8BF8\u4F4D\u4EFD\u5C5E\u540C\u9053\uFF0C\u60C5\u540C\u624B\u8DB3\uFF0C\u533A\u533A\u8584\u793C\u4E0D\u6210\u656C\u610F\u3002\u4E0D\u8FC7\u662F\u7565\u8868\u5BF8\u5FC3\uFF0C\u4EE5\u793A\u6211\u6559\u4E0E\u8BF8\u4F4D\u5171\u7518\u5171\u82E6\u4E4B\u76CA\u3002\u300D</p><p>\u7075\u7389\u771F\u4EBA\u6162\u6162\u62FF\u8D77\u660E\u73E0\uFF0C\u53EA\u89C1\u660E\u73E0\u83B9\u5149\u95EA\u52A8\uFF0C\u4E00\u94A9\u5F2F\u6708\u548C\u4E00\u9897\u5BD2\u661F\u5728\u73E0\u4E2D\u65F6\u9690\u65F6\u73B0\u3002\u4ED6\u6717\u7136\u4E00\u7B11\uFF0C\u957F\u8EAB\u800C\u8D77\uFF0C\u628A\u660E\u73E0\u6258\u5728\u638C\u5FC3\u6B63\u5BB9\u9053\uFF1A\u300C\u8D2B\u9053\u7075\u7389\uFF0C\u613F\u52A0\u5165\u661F\u6708\u6E56\u4EE5\u4F9B\u9A71\u4F7F\uFF0C\u540C\u6518\u5927\u4E1A\u3002\u6B64\u73E0\u74A7\u8FD8\u5BAB\u4E3B\u3002\u300D\u8BF4\u7F62\u4E00\u64A9\u9053\u888D\uFF0C\u4E00\u819D\u5C48\u5730\uFF0C\u9AD8\u9AD8\u4E3E\u8D77\u660E\u73E0\u3002</p><p>\u6155\u5BB9\u9F99\u8BF8\u4E8B\u7EB7\u5FD9\uFF0C\u4E00\u76F4\u6CA1\u6709\u6765\u5F97\u53CA\u8BAF\u95EE\u6C90\u58F0\u4F20\u4E0E\u7075\u7389\u6709\u4F55\u8FC7\u8282\uFF0C\u89C1\u8FD9\u4E2A\u5A01\u540D\u663E\u8D6B\u7684\u9AD8\u624B\u5F53\u573A\u6295\u8BDA\uFF0C\u4E0D\u7531\u5FC3\u4E0B\u5927\u559C\u3002\u4ED6\u671D\u6C90\u58F0\u4F20\u770B\u4E86\u4E00\u773C\u3002\u89C1\u6C90\u58F0\u4F20\u795E\u8272\u6728\u7136\uFF0C\u987F\u65F6\u653E\u4E0B\u5FC3\u6765\u3002</p><p>\u6155\u5BB9\u9F99\u8D70\u4E0B\u5B9D\u5EA7\uFF0C\u633D\u8D77\u7075\u7389\uFF0C\u957F\u7B11\u9053\uFF1A\u300C\u7075\u7389\u771F\u4EBA\u540D\u52A8\u5929\u4E0B\uFF0C\u672C\u5BAB\u4EF0\u6155\u5DF2\u4E45\uFF0C\u80FD\u4E0E\u9601\u4E0B\u5171\u4E8B\uFF0C\u5FEB\u6170\u5E73\u751F\uFF01\u6C90\u62A4\u6CD5\uFF0C\u4EE5\u4F60\u4E4B\u89C1\uFF0C\u795E\u6559\u6709\u4F55\u804C\u53EF\u8D60\u771F\u4EBA\uFF1F\u300D</p><p>\u6C90\u58F0\u4F20\u6DE1\u6DE1\u9053\uFF1A\u300C\u6728\u5802\u957F\u8001\u4E4B\u4F4D\u7A7A\u7F3A\u3002\u65E5\u540E\u79EF\u529F\uFF0C\u53EF\u6388\u795E\u5C06\u3002\u300D</p><p>\u300C\u597D\uFF01\u5C31\u8BF7\u771F\u4EBA\u4E3A\u6728\u5802\u957F\u8001\u3002\u300D\uFF08\u6587_\u5187_\u4EBA\uFF0D\u5187-\u4E66\uFF0D\u5C4B\uFF0D\u8D3C\u5427\uFF3Aei\uFF18\u3002\uFF23\uFF2F\uFF2D\u7535\u5B50\u4E66\uFF09</p><p>\u7075\u7389\u771F\u4EBA\u671D\u6C90\u58F0\u4F20\u957F\u63D6\u4F5C\u8C22\uFF0C\u300C\u6614\u65E5\u975E\u662F\u5C0F\u5F1F\u6562\u8D1F\u6C90\u5144\u4E4B\u6258\uFF0C\u5B9E\u662F\u5996\u5987\u82E6\u903C\uFF0C\u65E0\u5948\u9690\u5C45\u3002\u8BF7\u6C90\u5144\u89C1\u8C05\u3002\u300D</p><p>\u6C90\u58F0\u4F20\u7F13\u7F13\u9053\uFF1A\u300C\u5F80\u4E8B\u4E0D\u5FC5\u518D\u63D0\u3002\u5982\u4ECA\u6155\u5BB9\u5BAB\u4E3B\u80F8\u6000\u5929\u4E0B\uFF0C\u4F60\u5728\u6B64\u5927\u6709\u53EF\u4E3A\u3002\u300D</p><p>\u867D\u7136\u53EA\u5B57\u7247\u8BED\uFF0C\u6155\u5BB9\u9F99\u5DF2\u662F\u5FC3\u4E0B\u4E86\u7136\uFF0C\u5FC5\u662F\u5F53\u65E5\u4E24\u4EBA\u4E3A\u5408\u8C0B\u5BF9\u4ED8\u9634\u5BAB\u4E3B\uFF0C\u800C\u5FC3\u751F\u8BEF\u4F1A\u3002</p><p>\u5E2D\u95F4\u4F17\u4EBA\u4EA4\u5934\u63A5\u8033\uFF0C\u4E0D\u591A\u65F6\u8D6B\u8FDE\u96C4\u3001\u77F3\u880D\u3001\u79C3\u53D1\u4EC0\u5065\u3001\u4E5E\u4F0F\u7A77\u9686\u7B49\u4EBA\u7EB7\u7EB7\u626C\u58F0\u52A0\u5165\u661F\u6708\u6E56\u3002</p><p>\u5C60\u6000\u6C89\u521A\u521A\u5B89\u7F6E\u597D\u96EA\u5CF0\u795E\u5C3C\uFF0C\u89C1\u6BBF\u5185\u7FA4\u60C5\u6D8C\u52A8\uFF0C\u63A5\u8FDE\u6548\u5FE0\u6295\u8BDA\uFF0C\u90A3\u7247\u70ED\u95F9\u8BA9\u4ED6\u77EB\u820C\u96BE\u4E0B\uFF0C\u4E0D\u77E5\u5BAB\u4E3B\u7528\u4E86\u4EC0\u4E48\u624B\u6BB5\uFF0C\u80FD\u5C06\u8FD9\u7B49\u6840\u9A9C\u4E0D\u8BAD\u7684\u51F6\u5F92\u6536\u5165\u5F40\u4E2D\u3002</p><p>\u503E\u523B\u4E4B\u95F4\uFF0C\u4E94\u767E\u4F59\u540D\u5BBE\u5BA2\u6709\u516D\u6210\u5F53\u573A\u52A0\u5165\u661F\u6708\u6E56\u3002\u6155\u5BB9\u9F99\u4E00\u5982\u524D\u8BAE\uFF0C\u5429\u5490\u5C60\u6000\u6C89\u5B89\u987F\u4F17\u4EBA\uFF0C\u5404\u81EA\u91CF\u624D\u4EE5\u7528\u3002\u5269\u4E0B\u4E8C\u767E\u4F59\u4EBA\u4E5F\u662F\u597D\u8A00\u76F8\u5F85\uFF0C\u606D\u9001\u8BF8\u4EBA\u79BB\u5F00\u3002\u6709\u51E0\u4E2A\u6CA1\u6709\u5C1D\u591F\u98D8\u6885\u5CF0\u8BF8\u5973\u6ECB\u5473\u7684\uFF0C\u770B\u7740\u7559\u5728\u6559\u4E2D\u7684\u4F17\u4EBA\u5174\u51B2\u51B2\u53BB\u5C9B\u540E\u73A9\u5F04\u96EA\u5CF0\u795E\u5C3C\uFF0C\u4E0D\u7531\u6697\u6697\u540E\u6094\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u56DE\u5230\u752C\u9053\uFF0C\u6155\u5BB9\u9F99\u7684\u559C\u6C14\u6E10\u6E10\u6DE1\u4E86\u4E0B\u53BB\u3002\u8981\u5C06\u8FD9\u4E9B\u4E4C\u5408\u4E4B\u4F17\u7EC3\u6210\u7EB5\u6A2A\u5929\u4E0B\u7684\u7CBE\u5175\uFF0C\u60F3\u60F3\u5C31\u591F\u5934\u75BC\u7684\u3002</p><p>\u6628\u65E5\u665A\u95F4\uFF0C\u970D\u72C2\u7130\u5DF2\u7ECF\u79BB\u5BAB\uFF0C\u5E26\u7740\u6559\u4E2D\u7CBE\u9510\u8D76\u8D74\u6D1B\u9633\uFF0C\u6536\u670D\u5F53\u5730\u5E2E\u4F1A\uFF0C\u4E0D\u77E5\u662F\u5426\u987A\u5229\u3002\u5982\u4ECA\u626C\u540D\u53EF\u4EE5\uFF0C\u82E5\u5F04\u5F97\u4E0E\u767D\u9053\u6B66\u6797\u6B63\u9762\u4E3A\u654C\uFF0C\u90A3\u5C31\u5F97\u4E0D\u507F\u5931\u4E86\u3002\u4ED6\u5988\u7684\uFF0C\u90A3\u5BB6\u4F19\u592A\u9C81\u83BD\uFF0C\u4E0D\u5982\u8BA9\u5C60\u6000\u6C89\u53BB\u66F4\u653E\u5FC3\u3002\u7075\u7389\u3001\u8521\u4E91\u5CF0\u3001\u8D6B\u8FDE\u96C4\u2026\u2026\u8FD9\u51E0\u4E2A\u5012\u53EF\u59D4\u4EE5\u91CD\u4EFB\u3002</p><p>\u6155\u5BB9\u9F99\u76EE\u4E0D\u659C\u89C6\u5730\u4ECE\u661F\u6708\u5BAB\u4E3B\u7684\u8273\u5C38\u65C1\u8D70\u8FC7\uFF0C\u8FF3\u76F4\u6765\u5230\u752C\u9053\u5C3D\u5934\uFF0C\u4ED6\u5634\u89D2\u9732\u51FA\u4E00\u4E1D\u7B11\u610F\uFF0C\u63A8\u5F00\u623F\u95E8\u3002</p><p>\u5143\u7EA2\u65B0\u7834\u7684\u6155\u5BB9\u7D2B\u73AB\u5C0F\u732B\u822C\u8737\u7F29\u5728\u69BB\u4E0A\uFF0C\u4E00\u624B\u6342\u4F4F\u80F8\u53E3\uFF0C\u4E00\u624B\u653E\u5728\u8179\u4E0B\uFF0C\u7D27\u95ED\u7684\u776B\u6BDB\u95F4\u6302\u7740\u51E0\u6EF4\u6E05\u4EAE\u7684\u6CEA\u6C34\u3002</p><p>\u6155\u5BB9\u9F99\u820C\u5C16\u8F7B\u8F7B\u4E00\u8214\uFF0C\u773C\u6CEA\u54B8\u54B8\u7684\u6DA9\u6DA9\u7684\uFF0C\u8DDF\u4ED6\u66FE\u7ECF\u6D41\u8FC7\u7684\u4E00\u6837\u2026\u2026</p><p>\u7D2B\u73AB\u60CA\u9192\u8FC7\u6765\uFF0C\u5979\u5A07\u8EAF\u4E00\u98A4\uFF0C\u65CB\u5373\u7D27\u7D27\u6342\u4F4F\u706B\u8FA3\u8FA3\u7684\u4E0B\u4F53\uFF0C\u542B\u6CEA\u770B\u7740\u6155\u5BB9\u9F99\u3002</p><p>\u300C\u6765\uFF0C\u8BA9\u54E5\u54E5\u770B\u770B\u3002\u300D\u6155\u5BB9\u9F99\u7B11\u7740\u6380\u5F00\u6BDB\u6BEF\u3002</p><p>\u300C\u522B\u78B0\u6211\uFF01\u300D\u7D2B\u73AB\u6321\u4F4F\u9165\u80F8\u7684\u5C0F\u624B\u626C\u8D77\uFF0C\u4E00\u628A\u593A\u8FC7\u6BDB\u6BEF\uFF0C\u88F9\u7D27\u9999\u8EAF\u3002</p><p>\u6155\u5BB9\u9F99\u4F4E\u7B11\u4E00\u58F0\uFF0C\u624B\u6307\u5FAE\u4E00\u7528\u529B\uFF0C\u6BDB\u6BEF\u5200\u5272\u822C\u88C2\u5F00\u4E00\u9053\u7F1D\u9699\uFF0C\u9732\u51FA\u7D2B\u73AB\u767D\u5AE9\u7684\u5706\u81C0\u3002\u6155\u5BB9\u9F99\u4F38\u624B\u63A2\u5165\u81C0\u7F1D\uFF0C\u4ECE\u59B9\u59B9\u7D27\u6309\u7684\u7389\u6307\u4E0B\u671D\u79D8\u5904\u6478\u53BB\u3002\u89E6\u624B\u53EA\u89C9\u6ED1\u817B\u5982\u8102\uFF0C\u9999\u8F6F\u8FF7\u4EBA\u3002\u5F53\u6307\u5C16\u89E6\u5230\u5C0F\u5C0F\u7684\u83CA\u82B1\u857E\u65F6\uFF0C\u6155\u5BB9\u9F99\u6027\u6B32\u52C3\u53D1\uFF0C\u8089\u68D2\u987F\u65F6\u633A\u5F97\u7B14\u76F4\u3002</p><p>\u6B63\u5F85\u4E00\u5C1D\u59B9\u59B9\u540E\u5EAD\u9C9C\u82B1\u6ECB\u5473\uFF0C\u8033\u8FB9\u7A81\u7136\u54CD\u8D77\u4E00\u9635\u4F4E\u4F4E\u7684\u62BD\u6CE3\u58F0\u3002</p><p>\u300C\u4F60\u53C8\u8981\u6B3A\u8D1F\u4EBA\u5BB6\u2026\u2026\u4EBA\u5BB6\u75DB\u6B7B\u4E86\u2026\u2026\u300D\u5C0F\u5C0F\u7684\u7389\u4EBA\u58F0\u6CEA\u4FF1\u4E0B\uFF0C\u51C4\u51C4\u5207\u5207\u7684\u8BF4\u3002</p><p>\u6155\u5BB9\u9F99\u5FC3\u91CC\u4E00\u8F6F\uFF0C\u6536\u56DE\u624B\u6307\uFF0C\u62B1\u4F4F\u59B9\u59B9\u4EB2\u4E86\u4E00\u53E3\uFF0C\u67D4\u58F0\u5475\u54C4\u9053\uFF1A\u300C\u597D\uFF0C\u597D\uFF0C\u54E5\u54E5\u4E0D\u78B0\u4F60\u4E86\u3002\u4F60\u518D\u7761\u4E00\u4F1A\u513F\u3002\u300D</p><p>\u7D2B\u73AB\u628A\u5934\u57CB\u5728\u6795\u5934\u4E0B\u9762\uFF0C\u5C0F\u58F0\u54ED\u6CE3\u7740\u3002</p><p>\u6155\u5BB9\u9F99\u6B32\u706B\u96BE\u5E73\uFF0C\u4FBF\u53BB\u627E\u6BCD\u4EB2\u6CC4\u706B\u3002</p><p>\u8427\u4F5B\u5974\u8EAB\u4E0A\u5507\u4E0A\u7684\u8840\u8FF9\u5DF2\u88AB\u62B9\u51C0\uFF0C\u82CD\u767D\u7684\u7389\u8138\u8840\u8272\u5168\u65E0\u3002\u4E3A\u4E86\u9632\u6B62\u78B0\u5230\u4F24\u5904\uFF0C\u53F6\u884C\u5357\u6577\u836F\u4E4B\u540E\u7528\u4E00\u4E2A\u94A2\u4E1D\u5F2F\u6210\u7684\u66F2\u5F62\u7269\u4F53\u6491\u5F00\u5979\u7684\u7259\u5173\u3002\u820C\u6839\u548C\u820C\u5C16\u4E5F\u88AB\u94A2\u4E1D\u56FA\u5B9A\uFF0C\u5979\u5C31\u8FD9\u6837\u5706\u5F20\u7740\u5C0F\u5634\uFF0C\u9759\u9759\u5367\u5728\u9526\u887E\u4E4B\u4E2D\uFF0C\u5A07\u5AE9\u7684\u6A31\u5507\u4E2D\u9732\u7740\u4E00\u7247\u67D4\u5A9A\u7684\u7C89\u7EA2\uFF0C\u8102\u53E3\u9999\u820C\uFF0C\u82AC\u82B3\u56DB\u6EA2\u3002</p><p>\u6155\u5BB9\u9F99\u8D8A\u770B\u8D8A\u7231\uFF0C\u4E3E\u624B\u4F38\u5165\u9526\u88AB\uFF0C\u968F\u7740\u67D4\u82E5\u65E0\u9AA8\u7684\u79C0\u8DB3\u671D\u4E0A\u6478\u53BB\u3002</p><p>\u300C\u4ED6\u5988\u7684\uFF01\u8C01\u8BA9\u4F60\u4EEC\u7ED9\u5979\u7A7F\u8863\u670D\u7684\uFF1F\u591A\u4E8B\uFF01\u300D</p><p>\u7389\u83BA\u7389\u9E42\u5FF5\u7740\u8427\u4F5B\u5974\u4E3B\u6BCD\u7684\u8EAB\u4EFD\uFF0C\u7ED9\u592B\u4EBA\u7A7F\u4E0A\u4E86\u8D34\u8EAB\u7684\u5C0F\u8863\u3002\u672C\u6765\u662F\u4E00\u7247\u597D\u5FC3\uFF0C\u6CA1\u60F3\u5230\u5374\u6328\u4E86\u4E00\u901A\u75DB\u65A5\uFF0C\u4E24\u4EBA\u5664\u82E5\u5BD2\u8749\uFF0C\u8FDE\u5FD9\u8FC7\u6765\u5E2E\u4E3B\u5B50\u62FF\u8D77\u9526\u88AB\u3002</p><p>\u8427\u4F5B\u5974\u60A0\u7136\u9192\u8F6C\uFF0C\u53EA\u89C9\u8EAB\u4E0A\u5FAE\u51C9\uFF0C\u6709\u4EBA\u6B63\u6089\u6089\u7D22\u7D22\u9664\u4E0B\u81EA\u5DF1\u7684\u5185\u8863\uFF0C\u5979\u77E5\u9053\u53C8\u8981\u88AB\u513F\u5B50\u5978\u6DEB\uFF0C\u4E0D\u7981\u75DB\u82E6\u5730\u547B\u541F\u4E00\u58F0\uFF0C\u5782\u4E0B\u6CEA\u6765\u3002</p><p>\u867D\u7136\u5C61\u7ECF\u6298\u78E8\uFF0C\u767D\u5AE9\u7684\u5A07\u8EAF\u4F9D\u7136\u7F8E\u8273\u5982\u6614\u3002\u8427\u4F5B\u5974\u8D64\u88F8\u7684\u56DB\u80A2\u8F6F\u8F6F\u644A\u5F00\uFF0C\u67D4\u7F8E\u7684\u8EAF\u4F53\u4E0A\u7A7F\u7740\u4E00\u4EF6\u6E56\u7EFF\u8272\u7684\u8D34\u8EAB\u5C0F\u8863\uFF0C\u4E30\u80F8\u7EC6\u8170\u66F2\u7EBF\u73B2\u73D1\uFF0C\u5B9D\u84DD\u8272\u7684\u5C0F\u9886\u62E5\u5728\u9888\u4E2D\uFF0C\u66F4\u663E\u5F97\u67D4\u9888\u5176\u767D\u5982\u96EA\u3002\u9886\u53E3\u7684\u94AE\u6263\u505A\u6210\u8774\u8776\u5F62\u72B6\uFF0C\u8776\u7FC5\u91D1\u9576\u94F6\u7ED5\uFF0C\u7CBE\u81F4\u7EC6\u5DE7\u3002</p><p>\u767D\u7389\u83BA\u5148\u89E3\u5F00\u895F\u53E3\uFF0C\u7136\u540E\u628A\u624B\u4F38\u5230\u814B\u4E0B\uFF0C\u89E3\u5F00\u53E6\u4E00\u53EA\u8863\u6263\u3002\u624B\u6307\u8FD8\u672A\u653E\u5F00\uFF0C\u5706\u6DA6\u7684\u4E73\u623F\u7ACB\u5373\u4E00\u8DF3\uFF0C\u6491\u5F00\u8863\u895F\u3002\u6E56\u7EFF\u8272\u7684\u4EB5\u8863\u4ECE\u4E73\u4E0A\u6D41\u6C34\u822C\u6ED1\u4E0B\uFF0C\u9732\u51FA\u8D35\u5987\u9999\u8273\u52A8\u4EBA\u7684\u808C\u80A4\u3002</p>',55);function t(_,i){return a}var d=e(r,[["render",t]]);export{d as default};
