import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_62" tabindex="-1"><a class="header-anchor" href="#_62" aria-hidden="true">#</a> 62</h1><p>\u6155\u5BB9\u9F99\u8D2A\u5A6A\u5730\u76EF\u7740\u9762\u524D\u5A07\u8273\u7684\u8EAB\u4F53\u3002\u4ED6\u6367\u8D77\u6BCD\u4EB2\u8F6F\u7EF5\u7EF5\u7684\u811A\u638C\uFF0C\u4F4E\u5934\u4E00\u543B\u3002</p><p>\u706B\u70ED\u7684\u5634\u5507\u968F\u7740\u811A\u8E1D\u3001\u819D\u5F2F\uFF0C\u4ECE\u5927\u817F\u5185\u4FA7\u4E00\u76F4\u78E8\u64E6\u5230\u6ED1\u817B\u7684\u82B1\u74E3\u4E0A\u3002\u4ED6\u5F20\u5F00\u5634\uFF0C\u628A\u90A3\u4E1B\u5AE9\u8089\u4E00\u53E3\u542B\u4F4F\u3002\u4E00\u8FB9\u8214\u8210\uFF0C\u4E00\u8FB9\u62E5\u7D27\u4E24\u6761\u5149\u6DA6\u7684\u5927\u817F\uFF0C\u628A\u8138\u57CB\u5728\u6BCD\u4EB2\u8EAB\u4F53\u6B63\u4E2D\uFF0C\u4EAB\u53D7\u7740\u90A3\u91CC\u7684\u82B3\u9999\u548C\u7518\u751C\u3002</p><p>\u8427\u4F5B\u5974\u867D\u7136\u4E07\u822C\u4E0D\u613F\uFF0C\u4F46\u5728\u513F\u5B50\u7684\u4EB2\u543B\u4E0B\uFF0C\u7981\u4E0D\u4F4F\u79D8\u5904\u6DEB\u6DB2\u6F6E\u6D8C\u3002\u5979\u4FCF\u8138\u98DE\u7EA2\uFF0C\u9F3B\u4E2D\u53D1\u51FA\u65F6\u65AD\u65F6\u7EED\u7684\u547B\u541F\u58F0\u3002</p><p>\u6155\u5BB9\u9F99\u542B\u4F4F\u82B1\u8482\u7528\u529B\u4E00\u5438\uFF0C\u5F85\u7F8E\u5987\u54C6\u55E6\u7740\u55B7\u51FA\u9634\u7CBE\uFF0C\u4ED6\u624D\u5410\u51FA\u82B1\u74E3\uFF0C\u6251\u8EAB\u5C06\u7C89\u5AE9\u7684\u8089\u4F53\u538B\u5728\u8EAB\u4E0B\uFF0C\u7B11\u9053\uFF1A\u300C\u5A18\uFF0C\u8212\u670D\u5417\uFF1F\u300D</p><p>\u8427\u4F5B\u5974\u7F9E\u5F97\u8033\u6839\u53D1\u7EA2\uFF0C\u5979\u62FC\u547D\u6447\u7740\u5934\uFF0C\u8BD5\u56FE\u75DB\u9A82\u513F\u5B50\u7684\u517D\u884C\uFF0C\u4F46\u5634\u91CC\u53EA\u53D1\u51FA\u300C\u54A6\u54A6\u5440\u5440\u300D\u7684\u58F0\u97F3\u3002</p><p>\u6155\u5BB9\u9F99\u62B1\u4F4F\u6BCD\u4EB2\u80A5\u5AE9\u7684\u9999\u4E73\uFF0C\u8089\u68D2\u7B14\u76F4\u9876\u5728\u6E7F\u6F09\u6F09\u7684\u5AE9\u8089\u4E2D\uFF0C\u76EF\u7740\u8427\u4F5B\u5974\u75DB\u82E6\u800C\u53C8\u65E0\u5948\u7684\u54ED\u8BC9\uFF0C\u773C\u91CC\u4E00\u80A1\u5145\u6EE1\u90AA\u6076\u7684\u7B11\u610F\u6E10\u6E10\u6C64\u5F00\u3002</p><p>\u300C\u5A18\uFF0C\u4F60\u505A\u513F\u5B50\u7684\u5C0F\u5B9D\u5B9D\u597D\u4E0D\u597D\uFF1F\u300D\u8BF4\u7740\u9633\u5177\u4E00\u633A\uFF0C\u6345\u8FDB\u4ECD\u5728\u6536\u7F29\u7684\u8089\u7A74\u4E2D\u3002</p><p>\u8427\u4F5B\u5974\u300C\u5440\u300D\u7684\u4E00\u58F0\u957F\u53EB\uFF0C\u7709\u5934\u987F\u65F6\u62E7\u6210\u4E00\u56E2\u3002</p><p>\u6155\u5BB9\u9F99\u5E26\u7740\u6DE1\u6DE1\u7684\u7B11\u610F\uFF0C\u7528\u529B\u4E00\u9876\uFF0C\u9F9F\u5934\u91CD\u91CD\u649E\u5728\u6BCD\u4EB2\u7684\u82B1\u5FC3\u4E0A\u3002\u8427\u4F5B\u5974\u67D4\u9888\u4E00\u626C\uFF0C\u4E00\u53E3\u6C14\u564E\u5728\u5589\u5934\u3002\u6155\u5BB9\u9F99\u4E0D\u7B49\u5979\u5598\u8FC7\u6C14\u6765\uFF0C\u8089\u68D2\u6839\u90E8\u7684\u89E6\u624B\u4E00\u6D8C\u800C\u4E0A\uFF0C\u5C06\u82B1\u74E3\u6491\u6210\u4E00\u7247\u8273\u7EA2\u7684\u6D51\u5706\u3002\u51E0\u6839\u7279\u522B\u7EC6\u957F\u6709\u529B\u7684\u89E6\u624B\uFF0C\u5728\u7389\u6237\u4E2D\u62FC\u547D\u821E\u52A8\u3002</p><p>\u4ED6\u7684\u633A\u9001\u6108\u53D1\u7528\u529B\uFF0C\u62D4\u51FA\u65F6\u89E6\u624B\u5939\u7D27\u82B1\u8482\uFF0C\u5C06\u7EC6\u5C0F\u7684\u8089\u7C92\u626F\u5F97\u7EC6\u957F\uFF0C\u63D2\u5165\u65F6\u4E0D\u4F46\u6574\u652F\u7C97\u58EE\u7684\u9633\u5177\u5C3D\u6570\u6363\u5165\u6E29\u6DA6\u7EF5\u8F6F\u7684\u8089\u7A74\uFF0C\u6709\u4E00\u6839\u89E6\u624B\u751A\u81F3\u6345\u8FDB\u5C3F\u9053\uFF0C\u5728\u91CC\u9762\u4E0D\u4F4F\u6405\u52A8\u3002</p><p>\u8427\u4F5B\u5974\u4E0D\u65F6\u53D1\u51FA\u542B\u7CCA\u7684\u5C16\u53EB\uFF0C\u661F\u7738\u50CF\u6D78\u5728\u6C34\u5E95\u822C\u88AB\u6CEA\u6C34\u8986\u76D6\uFF0C\u53D1\u7EA2\u7684\u7389\u8138\u66F4\u663E\u5F97\u5A07\u8273\u6B32\u6EF4\u3002\u8089\u7A74\u88AB\u5DE8\u7269\u585E\u6EE1\u7684\u5FEB\u611F\u4E0D\u65AD\u88AD\u6765\uFF0C\u6BCF\u4E00\u6B21\u6345\u5165\uFF0C\u82B1\u5FC3\u90FD\u88AB\u9876\u5F97\u53C8\u9178\u53C8\u9EBB\u3002\u4ECE\u672A\u88AB\u5F02\u7269\u8FDB\u5165\u8FC7\u7684\u5C3F\u9053\u5219\u50CF\u662F\u88AB\u6495\u88C2\u822C\uFF0C\u5267\u75DB\u8FDE\u8FDE\u3002\u4E0B\u4F53\u7684\u5FEB\u611F\u548C\u75BC\u75DB\u4EA4\u66FF\u88AD\u6765\uFF0C\u6E10\u6E10\u8FDE\u6210\u4E00\u4F53\uFF0C\u8BA9\u5A07\u5F31\u7684\u8D35\u5987\u5206\u4E0D\u6E05\u7A76\u7ADF\u662F\u75BC\u75DB\u8FD8\u662F\u5FEB\u611F\u3002</p><p>\u6155\u5BB9\u9F99\u89C1\u6BCD\u4EB2\u773C\u795E\u6E10\u6E10\u6563\u4E71\uFF0C\u5FFD\u7136\u4E24\u624B\u4E00\u4E3E\uFF0C\u5C06\u8427\u4F5B\u5974\u4E24\u817F\u5411\u538B\u5728\u80A9\u65C1\uFF0C\u4F7F\u80A5\u81C0\u9AD8\u9AD8\u633A\u8D77\u3002\u63A5\u7740\u62D4\u51FA\u9633\u5177\uFF0C\u671D\u8089\u7A74\u4E0B\u7684\u83CA\u82B1\u857E\u4E2D\u4E00\u6345\u800C\u5165\u3002</p><p>\u67D4\u8F6F\u7684\u9999\u8EAF\u731B\u7136\u7EF7\u7D27\uFF0C\u8427\u4F5B\u5974\u7F8E\u76EE\u5706\u7741\uFF0C\u88AB\u94A2\u5957\u6491\u5F00\u7684\u5C0F\u5634\u6B7B\u6B7B\u54AC\u7D27\uFF0C\u6CA1\u6709\u53D1\u51FA\u4E00\u70B9\u58F0\u606F\u3002</p><p>\u6155\u5BB9\u9F99\u5FAE\u7B11\u7740\u62D4\u51FA\u8089\u68D2\uFF0C\u632A\u5F00\u8EAB\u4F53\u3002</p><p>\u4ED6\u4E24\u624B\u4F9D\u7136\u4E3E\u7740\u6BCD\u4EB2\u7684\u53CC\u817F\uFF0C\u53EA\u89C1\u8427\u4F5B\u5974\u5706\u81C0\u671D\u5929\u4EF0\u8D77\uFF0C\u4E00\u7247\u6ED1\u5AE9\u7684\u767D\u817B\u4E2D\uFF0C\u655E\u9732\u7684\u7389\u6237\u7EA4\u6BEB\u6BD5\u73B0\uFF0C\u6BB7\u7EA2\u7684\u82B1\u74E3\u4E0D\u4F4F\u7F29\u52A8\u6536\u7D27\uFF0C\u5374\u600E\u4E48\u4E5F\u906E\u4E0D\u4F4F\u91CC\u9762\u90A3\u4E00\u5927\u4E00\u5C0F\u4E24\u4E2A\u7EA2\u7EA2\u7684\u7A74\u53E3\uFF0C\u88AB\u5DE8\u9633\u6495\u7834\u7684\u83CA\u809B\u5374\u655E\u7740\u6D51\u5706\u7684\u8089\u6D1E\uFF0C\u7C89\u7EA2\u7684\u8089\u58C1\u4E0A\u6495\u5F00\u51E0\u9053\u6DF1\u6DF1\u7684\u88C2\u75D5\uFF0C\u9C9C\u8840\u6B63\u4ECE\u4F24\u53E3\u5185\u7F13\u7F13\u6D8C\u51FA\u3002</p><p>\u50F5\u6301\u7247\u523B\u540E\uFF0C\u8089\u7A74\u4E0A\u65B9\u7684\u5C0F\u5B54\u7A81\u7136\u5411\u5916\u4E00\u9F13\uFF0C\u4E00\u9053\u6DE1\u9EC4\u8272\u7684\u6DB2\u4F53\u5212\u51FA\u4E00\u9053\u5F27\u7EBF\uFF0C\u8FDC\u8FDC\u843D\u5728\u5E8A\u5916\uFF0C\u6C34\u82B1\u56DB\u6E85\u3002</p><p>\u5C45\u7136\u88AB\u513F\u5B50\u5F3A\u66B4\u5F97\u5C0F\u4FBF\u5931\u7981\uFF0C\u8427\u4F5B\u5974\u7F9E\u6124\u6B32\u6B7B\uFF0C\u53EF\u5979\u6CA1\u529E\u6CD5\u4E3E\u624B\u6342\u4F4F\u4F4F\u6EDA\u70EB\u7684\u7389\u8138\uFF0C\u53EA\u80FD\u52C9\u5F3A\u628A\u5934\u4FA7\u5230\u4E00\u8FB9\uFF0C\u7528\u6563\u4E71\u7684\u79C0\u53D1\u906E\u4F4F\u81EA\u5DF1\u7684\u7F9E\u8D67\u3002</p><p>\u5C3F\u6DB2\u55B7\u6E85\u7684\u300C\u54D7\u54D7\u300D\u58F0\u5728\u5BA4\u56DE\u6C64\u826F\u4E45\uFF0C\u5C31\u5728\u8427\u4F5B\u5974\u96BE\u582A\u7684\u65E0\u5730\u81EA\u5BB9\u65F6\uFF0C\u624D\u6162\u6162\u6B62\u4F4F\u3002\u6CBE\u6EE1\u5C3F\u6DB2\u7684\u5C0F\u5B54\u6E10\u6E10\u95ED\u62E2\uFF0C\u5FFD\u7136\u53C8\u662F\u4E00\u9F13\uFF0C\u5192\u51FA\u4E00\u9053\u5C0F\u5C0F\u7684\u55B7\u6CC9\u3002\u8FD9\u6B21\u6B8B\u4F59\u7684\u5C3F\u6DB2\u5C3D\u6570\u843D\u5728\u8427\u4F5B\u5974\u7684\u80A1\u95F4\uFF0C\u6CBE\u5F97\u4E0B\u4F53\u5230\u5904\u90FD\u662F\u3002</p><p>\u88AB\u6309\u5F97\u671D\u5929\u4EF0\u8D77\u7684\u80A5\u81C0\u54C6\u55E6\u7740\u6062\u590D\u5E73\u9759\uFF0C\u521A\u7652\u5408\u4E0D\u4E45\u53C8\u88AB\u6DF1\u6DF1\u6495\u88C2\u7684\u83CA\u809B\u62EC\u7EA6\u808C\uFF0C\u8FD9\u4E00\u6B21\u5F7B\u5E95\u635F\u574F\uFF0C\u518D\u4E5F\u65E0\u6CD5\u5408\u62E2\u3002\u6D51\u5706\u7684\u809B\u6D1E\u4E2D\u9C9C\u8840\u6EE1\u6EA2\uFF0C\u6700\u540E\u987A\u7740\u80A1\u6C9F\u67D3\u7EA2\u4E86\u8EAB\u4E0B\u7684\u8F7B\u6BEF\u3002</p><p>\u6155\u5BB9\u9F99\u6276\u8D77\u96C4\u98CE\u72B9\u5728\u7684\u9633\u5177\uFF0C\u9F9F\u5934\u6CBF\u7740\u81C0\u7F1D\u4E00\u8DEF\u64E6\u7740\u8840\u8FF9\uFF0C\u6345\u5165\u809B\u6D1E\u7684\u8840\u6C60\u4E2D\u3002\u8089\u68D2\u4E0B\u8840\u6D41\u5982\u6CE8\uFF0C\u5728\u767D\u81C0\u95F4\u4EA4\u9519\u7EB5\u6A2A\u3002</p><p>\u67D4\u7F8E\u7684\u5A07\u8EAF\u4E0D\u4F4F\u6218\u6817\uFF0C\u8427\u4F5B\u5974\u75DB\u5F97\u795E\u667A\u604D\u60DA\uFF0C\u96EA\u767D\u7684\u5589\u5934\u62BD\u52A8\u7740\uFF0C\u53D1\u51FA\u8270\u96BE\u7684\u75DB\u547C\u3002</p><p>\u8089\u6839\u6D78\u6CA1\u5728\u6E29\u70ED\u7684\u8840\u6DB2\u4E2D\uFF0C\u88AB\u67D4\u8F6F\u7684\u80A0\u58C1\u5BC6\u5BC6\u88F9\u4F4F\u3002\u80A5\u7F8E\u7684\u96EA\u81C0\u5728\u51F6\u72E0\u7684\u649E\u51FB\u4E0B\u65F6\u5706\u65F6\u6241\uFF0C\u67D4\u5A9A\u8FF7\u4EBA\u3002\u6155\u5BB9\u9F99\u633A\u5F04\u591A\u65F6\uFF0C\u6700\u540E\u5927\u559D\u4E00\u58F0\uFF0C\u9633\u7CBE\u72C2\u6CC4\u3002</p><p>\u8427\u4F5B\u5974\u65E9\u5DF2\u660F\u8FF7\u591A\u65F6\uFF0C\u513F\u5B50\u7684\u624B\u81C2\u79BB\u5F00\u540E\uFF0C\u5931\u53BB\u7B4B\u8171\u7684\u7389\u817F\u4ECD\u642D\u5728\u80A9\u5934\u3002\u809B\u4E2D\u4E00\u4E32\u8840\u6CE1\u7FFB\u6EDA\u6D6E\u51FA\uFF0C\u6DF7\u7740\u80A1\u80A1\u767D\u6D53\u7684\u6D4A\u7CBE\u3002\u6155\u5BB9\u9F99\u770B\u7740\u6BCD\u4EB2\u4F9D\u7136\u5E73\u5766\u7684\u5C0F\u8179\uFF0C\u6162\u6162\u64E6\u51C0\u8089\u68D2\u4E0A\u7684\u8840\u8FF9\u3002</p><p>\u4E0D\u77E5\u9053\u91CC\u9762\u662F\u7537\u662F\u5973\uFF0C\u6700\u597D\u662F\u4E2A\u50CF\u5A18\u4E00\u6837\u7F8E\u8273\u7684\u5973\u513F\u3002\u82E5\u662F\u7537\u5B69\u2014\u2014\u600E\u4E48\u6BD4\u5F97\u4E0A\u4EB2\u59B9\u8840\u7EDF\u7EAF\u6B63\u5462\u2026\u2026</p><p>\u4E22\u5F00\u4E1D\u5DFE\uFF0C\u6155\u5BB9\u9F99\u6DE1\u6DE1\u9053\uFF1A\u300C\u7167\u6599\u592B\u4EBA\u3002\u9E42\u5974\uFF0C\u53BB\u53F6\u62A4\u6CD5\u5904\uFF0C\u628A\u79CD\u5B50\u7075\u4E39\u53D6\u6765\u3002\u300D</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u300C\u8BF8\u4F4D\u3002\u300D\u770B\u7740\u5E2D\u95F4\u6570\u5341\u4EBA\u6D4E\u6D4E\u4E00\u5802\uFF0C\u6155\u5BB9\u9F99\u6B62\u4E0D\u4F4F\u5174\u594B\u4E4B\u60C5\uFF0C\u524D\u4E24\u65E5\u4ED6\u8FD8\u5728\u4E3A\u6559\u4E2D\u65E0\u4EBA\u5934\u75BC\uFF0C\u5982\u4ECA\u5E73\u6DFB\u4F17\u591A\u9AD8\u624B\uFF0C\u5B9E\u529B\u5927\u589E\uFF0C\u518D\u975E\u5F80\u65E5\u6349\u895F\u89C1\u8098\u7684\u7A98\u6001\u3002</p><p>\u300C\u4ECE\u4ECA\u5F80\u540E\u5927\u4F19\u90FD\u662F\u4E00\u5BB6\u4EBA\uFF0C\u5BA2\u6C14\u8BDD\u4E5F\u4E0D\u518D\u591A\u8BF4\u3002\u300D\u4ED6\u4E3E\u676F\u4E00\u996E\u800C\u5C3D\uFF0C\u7136\u540E\u4E24\u6307\u4E00\u7D27\uFF0C\u52B2\u529B\u5230\u5904\uFF0C\u74F7\u676F\u7ACB\u65F6\u5316\u4E3A\u9F51\u7C89\uFF0C\u300C\u672C\u5BAB\u4E0E\u8BF8\u4F4D\u5144\u5F1F\u540C\u5FC3\u540C\u5FB7\uFF0C\u5171\u4E3E\u5927\u4E8B\u3002\u82E5\u6709\u8D1F\u5FC3\uFF0C\u6709\u5982\u6B64\u676F\u3002\u300D</p><p>\u7075\u7389\u771F\u4EBA\u4E3E\u676F\u5F80\u53E3\u4E00\u503E\uFF0C\u63A5\u7740\u7FFB\u638C\u62CD\u5728\u6848\u4E0A\u3002\u4ED6\u8FD9\u4E00\u638C\u8F7B\u98D8\u98D8\u6BEB\u65E0\u529B\u9053\uFF0C\u66F4\u6CA1\u6709\u4E00\u4E1D\u58F0\u97F3\u3002\u5F85\u62AC\u8D77\u624B\u638C\uFF0C\u9152\u676F\u5DF2\u6084\u7136\u7C89\u788E\u3002</p><p>\u8FD9\u6B21\u665A\u5BB4\u53C2\u4E0E\u8005\u90FD\u662F\u5C60\u6000\u6C89\u7CBE\u5FC3\u6311\u9009\u7684\u4E00\u7B49\u4E00\u9AD8\u624B\uFF0C\u5F53\u4E0B\u4F17\u4EBA\u5404\u65BD\u5947\u529F\uFF0C\u5728\u5BAB\u4E3B\u9762\u524D\u9732\u4E86\u4E00\u624B\u3002</p><p>\u6C90\u58F0\u4F20\u53CC\u773C\u4F3C\u7741\u4F3C\u95ED\uFF0C\u4F46\u6BCF\u4E2A\u4EBA\u7684\u624B\u6CD5\u3001\u529F\u529B\u3001\u53CD\u5E94\u3001\u795E\u60C5\u3001\u6C14\u5EA6\u65E0\u4E0D\u5C3D\u6536\u773C\u5E95\u3002</p><p>\u6155\u5BB9\u9F99\u559C\u4E0D\u81EA\u80DC\uFF0C\u5F53\u573A\u62DC\u8BF7\u6B66\u529F\u6700\u5F3A\u7684\u8D6B\u8FDE\u96C4\u3001\u77F3\u880D\u4E0E\u897F\u79E6\u72EC\u884C\u5927\u76D7\u5BAB\u767D\u7FBD\u4E3A\u6559\u4E2D\u4F9B\u5949\uFF0C\u5176\u4F59\u4E3A\u5404\u5802\u9999\u4E3B\u3002</p><p>\u5F85\u4F17\u4EBA\u4F9D\u4F4D\u6B21\u5750\u5B9A\uFF0C\u6155\u5BB9\u9F99\u7ACB\u5373\u8F6C\u5165\u6B63\u9898\uFF0C\u300C\u795E\u6559\u6C47\u96C6\u516B\u65B9\u82F1\u8C6A\uFF0C\u5FD7\u5728\u5929\u4E0B\u3002</p><p>\u4F9D\u5404\u4F4D\u4E4B\u89C1\uFF0C\u5F53\u4ECE\u4F55\u5904\u4E0B\u624B\uFF1F\u300D</p><p>\u300C\u94B1\u3001\u7CAE\u3001\u5175\u9A6C\u3002\u300D\u7075\u7389\u6BEB\u4E0D\u72B9\u8C6B\u5730\u7B54\u9053\uFF0C\u300C\u6211\u6559\u897F\u8FDE\u957F\u5B89\uFF0C\u4E1C\u8FD1\u6D1B\u9633\uFF0C\u82E5\u80FD\u5360\u636E\u4E24\u57CE\uFF0C\u5373\u53EF\u9010\u9E7F\u5929\u4E0B\u3002\u300D</p><p>\u300C\u4E09\u5E74\u524D\u957F\u5B89\u88AB\u5927\u5468\u653B\u7834\uFF0C\u5143\u6C14\u81F3\u4ECA\u672A\u590D\u3002\u6211\u770B\uFF0C\u8FD8\u662F\u5148\u56FE\u6D1B\u9633\u3002\u300D\u77F3\u880D\u63A5\u53E3\u9053\u3002</p><p>\u300C\u9647\u897F\u4E5F\u5BCC\u5F97\u5F88\u3002\u300D\u5BAB\u767D\u7FBD\u5728\u51C9\u5DDE\u591A\u5E74\uFF0C\u719F\u77E5\u5F53\u5730\u60C5\u51B5\uFF0C\u300C\u82E5\u8981\u94F6\u5B50\uFF0C\u6211\u5E26\u5144\u5F1F\u4EEC\u53BB\u3002\u300D</p><p>\u300C\u626C\u4E00\u76CA\u4E8C\uFF0C\u626C\u5DDE\u3001\u6210\u90FD\u90FD\u662F\u5BA2\u5546\u4E91\u96C6\u7684\u597D\u5730\u65B9\u3002\u300D</p><p>\u300C\u6D1B\u9633\uFF0C\u8FD8\u662F\u6D1B\u9633\u5927\u6237\u591A\uFF01\u5BAB\u4E3B\uFF0C\u6211\u4EEC\u54E5\u513F\u4FE9\u8D70\u4E00\u8D9F\uFF01\u4E0D\u5F04\u56DE\u5341\u4E07\u4E24\u94F6\u5B50\uFF0C\u4E0D\u7528\u5BAB\u4E3B\u5429\u5490\uFF0C\u6211\u4EC7\u767E\u718A\u81EA\u5DF1\u628A\u8111\u888B\u5272\u4E0B\u6765\uFF01\u300D</p><p>\u4E00\u8BF4\u6253\u5BB6\u52AB\u820D\uFF0C\u4F17\u4EBA\u7ACB\u523B\u5174\u81F4\u5927\u53D1\u3002</p><p>\u597D\u7AEF\u7AEF\u5546\u91CF\u7ACB\u56FD\u5927\u8BA1\uFF0C\u7ED3\u679C\u5F04\u6210\u660E\u5077\u6697\u62A2\u3002\u6155\u5BB9\u9F99\u5FC3\u91CC\u82E6\u7B11\uFF0C\u4E00\u65F6\u534A\u523B\u60F3\u6539\u6389\u4ED6\u4EEC\u7684\u532A\u5F92\u672C\u8272\u53EA\u662F\u7591\u4EBA\u8BF4\u68A6\u3002</p><p>\u5FFD\u7136\u4E00\u4E2A\u58F0\u97F3\u5F00\u53E3\u9053\uFF1A\u300C\u7075\u7389\u9053\u957F\u6240\u8A00\u4E0D\u5DEE\uFF0C\u94B1\u7CAE\u5175\u9A6C\uFF0C\u7F3A\u4E00\u4E0D\u53EF\u3002\u5728\u4E0B\u613F\u8D74\u96C1\u95E8\uFF0C\u641C\u8D2D\u6218\u9A6C\uFF0C\u4E3A\u5BAB\u4E3B\u8BAD\u7EC3\u4E00\u652F\u7CBE\u9A91\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u8D4F\u8BC6\u5730\u770B\u4E86\u8D6B\u8FDE\u96C4\u4E00\u773C\uFF0C\u70B9\u5934\u9053\uFF1A\u300C\u4F9B\u5949\u8BF4\u7684\u6781\u662F\u3002\u5C31\u8BF7\u8D6B\u8FDE\u5144\u5230\u96C1\u95E8\u5BDF\u770B\u5F62\u52BF\uFF0C\u82E5\u6709\u673A\u4F1A\u80FD\u63A7\u5236\u5F53\u573A\u9A6C\u5E02\u6700\u597D\uFF01\u300D</p><p>\u79C3\u53D1\u4EC0\u5065\u5144\u5F1F\u672C\u662F\u5F53\u5730\u4EBA\u6C0F\uFF0C\u95FB\u8A00\u7ACB\u5373\u9AD8\u58F0\u9644\u5408\uFF0C\u8981\u6C42\u540C\u53BB\u3002\u91D1\u5F00\u7532\u4E5F\u8DC3\u8DC3\u6B32\u8BD5\uFF0C\u5374\u88AB\u5BAB\u4E3B\u7528\u773C\u8272\u6B62\u4F4F\u3002</p><p>\u6155\u5BB9\u9F99\u542B\u7B11\u9053\uFF1A\u300C\u8521\u957F\u8001\u6709\u4F55\u89C1\u89E3\uFF1F\u300D</p><p>\u6CA1\u80FD\u89C1\u5230\u5C11\u592B\u4EBA\uFF0C\u8521\u4E91\u5CF0\u6709\u4E9B\u9B42\u4E0D\u5B88\u820D\uFF0C\u95FB\u8A00\u6014\u4E86\u4E00\u4E0B\uFF0C\u62B1\u62F3\u9053\uFF1A\u300C\u4F46\u51ED\u5BAB\u4E3B\u5429\u5490\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u5BF9\u4ED6\u7684\u5FC3\u4E0D\u5728\u7109\u4E00\u7B11\u7F6E\u4E4B\uFF0C\u6C89\u541F\u9053\uFF1A\u300C\u970D\u957F\u8001\u5DF2\u7ECF\u8D76\u8D74\u6D1B\u9633\uFF0C\u4F46\u6D1B\u9633\u662F\u5468\u56FD\u7687\u90FD\uFF0C\u5E2E\u4F1A\u6797\u7ACB\uFF0C\u53EA\u6015\u970D\u957F\u8001\u5B64\u6728\u96BE\u652F\u3002\u8521\u957F\u8001\u53EF\u5E26\u6C34\u5802\u5E2E\u4F17\u524D\u53BB\u76F8\u52A9\u3002\u300D</p><p>\u8FD9\u4E48\u5FEB\u5C31\u8981\u79BB\u5BAB\uFF0C\u8521\u4E91\u5CF0\u5FC3\u91CC\u6709\u70B9\u4E0D\u820D\uFF0C\u4F46\u8FD8\u662F\u70B9\u5934\u7B54\u5E94\u3002</p><p>\u53EA\u542C\u5BAB\u4E3B\u53C8\u9053\uFF1A\u300C\u6570\u65E5\u540E\u672C\u5BAB\u5C06\u4EB2\u8D74\u6D1B\u9633\uFF0C\u65E0\u8BBA\u5982\u4F55\u8981\u5C06\u6D1B\u9633\u7EB3\u5165\u6211\u6559\uFF01\u300D</p><p>\u8521\u4E91\u5CF0\u5FC3\u82B1\u6012\u653E\uFF0C\u4F3C\u4E4E\u770B\u5230\u73AB\u7470\u4ED9\u5B50\u7B11\u76C8\u76C8\u8BF4\uFF1A\u300C\u8FD9\u4E48\u5FEB\u5C31\u6536\u670D\u6D1B\u9633\u8BF8\u5E2E\uFF0C\u8521\u957F\u8001\u8F9B\u82E6\u4E86\u3002\u300D</p><p>\u300C\u9075\u547D\uFF01\u300D\u8521\u4E91\u5CF0\u9AD8\u58F0\u9053\u3002</p><p>\u6155\u5BB9\u9F99\u6DE1\u6DE1\u4E00\u7B11\uFF0C\u300C\u5C4A\u65F6\u8BF7\u6C90\u62A4\u6CD5\u5750\u9547\u5BAB\u4E2D\uFF0C\u91D1\u957F\u8001\u3001\u7075\u7389\u957F\u8001\u3001\u77F3\u4F9B\u5949\u4E0E\u672C\u5BAB\u540C\u884C\u3002\u300D\u4ED6\u671B\u7740\u8FDC\u5904\u8FDE\u7EF5\u7684\u7FA4\u5C71\uFF0C\u58F0\u97F3\u6E10\u6E10\u51DD\u91CD\uFF0C\u300C\u672C\u5BAB\u8981\u5230\u9F99\u57CE\u62DC\u796D\u6211\u6155\u5BB9\u6C0F\u7956\u5148\u3002\u300D</p><p>\u8FD8\u6709\u90A3\u4E00\u5927\u7B14\u5B9D\u85CF\uFF01</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u7D2B\u73AB\u8FD8\u662F\u90A3\u4E2A\u59FF\u52BF\u8737\u5728\u69BB\u4E0A\u3002\u5979\u771F\u662F\u75BC\u5F97\u7D27\u4E86\uFF0C\u8EBA\u4E86\u4E00\u6574\u5929\uFF0C\u4E0B\u4F53\u4F3C\u4E4E\u8FD8\u63D2\u7740\u90A3\u6839\u5E9E\u7136\u5DE8\u7269\uFF0C\u7565\u4E00\u52A8\u4F5C\u5C31\u970D\u970D\u4F5C\u75DB\u3002\u5979\u5C0F\u5FC3\u7FFC\u7FFC\u5730\u62AC\u8D77\u53F3\u624B\uFF0C\u53EA\u89C1\u6307\u5C16\u8FD8\u6CBE\u7740\u6BB7\u7EA2\u7684\u8840\u8FF9\u3002\u7D2B\u73AB\u5C0F\u5634\u4E00\u6241\uFF0C\u59D4\u5C48\u5730\u5624\u5624\u54ED\u6CE3\u8D77\u6765\u3002</p><p>\u4E00\u8FB9\u54ED\uFF0C\u4E00\u8FB9\u6697\u6697\u7591\u60D1\uFF0C\u600E\u4E48\u81EA\u5DF1\u7834\u4F53\u540E\u5E76\u672A\u6B92\u547D\uFF1F</p><p>\u6155\u5BB9\u9F99\u8E51\u624B\u8E51\u811A\u8D70\u8FDB\u623F\u95F4\uFF0C\u52FE\u5934\u89C2\u5BDF\u59B9\u59B9\u7684\u795E\u8272\u3002</p><p>\u7D2B\u73AB\u5BF9\u4ED6\u6068\u4E4B\u5165\u9AA8\uFF0C\u95ED\u7740\u773C\u5BF9\u4ED6\u6BEB\u4E0D\u7406\u776C\u3002</p><p>\u6155\u5BB9\u9F99\u54BD\u4E86\u53E3\u5410\u6CAB\uFF0C\u6309\u4E86\u6309\u6000\u91CC\u7684\u79CD\u5B50\u7075\u4E39\uFF0C\u6CA1\u6709\u638F\u51FA\u6765\u3002\u4ED6\u6328\u7740\u7D2B\u73AB\u8EBA\u5728\u5E8A\u4E0A\uFF0C\u6162\u6162\u4F38\u76F4\u8EAB\u4F53\uFF0C\u7136\u540E\u5C55\u81C2\u6402\u4F4F\u59B9\u59B9\u9999\u8F6F\u7684\u8EAB\u4F53\uFF0C\u957F\u957F\u8212\u4E86\u53E3\u6C14\u3002</p><p>\u7D2B\u73AB\u6B62\u4F4F\u54ED\u58F0\uFF0C\u4F46\u773C\u6CEA\u5374\u8D8A\u6D41\u8D8A\u591A\uFF0C\u5979\u6068\u6068\u62B9\u4E86\u628A\u6CEA\u6C34\uFF0C\u7EF7\u7740\u8138\u4E00\u8A00\u4E0D\u53D1\u3002</p><p>\u300C\u597D\u5566\uFF0C\u597D\u5566\uFF0C\u522B\u518D\u54ED\u4E86\u2026\u2026\u773C\u90FD\u80BF\u4E86\u2026\u2026\u300D</p><p>\u300C\u6211\u5C31\u54ED\uFF01\u4F60\u6B3A\u8D1F\u6211\uFF01\u300D</p><p>\u6155\u5BB9\u9F99\u5E2E\u5979\u64E6\u53BB\u8138\u4E0A\u7684\u6CEA\u82B1\uFF0C\u4F4E\u7B11\u9053\uFF1A\u300C\u5973\u4EBA\u7B2C\u4E00\u6B21\u90FD\u8FD9\u6837\uFF0C\u4EE5\u540E\u5C31\u4E0D\u4F1A\u75DB\u4E86\u2014\u2014\u90A3\u79CD\u6B32\u4ED9\u6B32\u6B7B\u7684\u611F\u89C9\uFF0C\u5C1D\u4E00\u6B21\u4E00\u8F88\u5B50\u90FD\u5FD8\u4E0D\u4E86\u5462\u3002\u300D</p><p>\u300C\u5478\uFF01\u300D\u7D2B\u73AB\u6C14\u51B2\u51B2\u7FFB\u8FC7\u8EAB\u5B50\uFF0C\u80CC\u5BF9\u7740\u6155\u5BB9\u9F99\u3002</p><p>\u8FD9\u6837\u7684\u5A07\u55D4\u8584\u6012\u4F7F\u6155\u5BB9\u9F99\u5FC3\u91CC\u4E00\u8361\uFF0C\u4ED6\u4F4E\u5934\u5728\u7D2B\u73AB\u9888\u4E2D\u4E00\u543B\uFF0C\u6B63\u5BB9\u9053\uFF1A\u300C\u4F60\u7EC3\u7684\u662F\u4EC0\u4E48\u5185\u529F\uFF1F\u300D</p>',66);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
