import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_63" tabindex="-1"><a class="header-anchor" href="#_63" aria-hidden="true">#</a> 63</h1><p>\u7D2B\u73AB\u50CF\u662F\u7761\u7740\u4E86\uFF0C\u5BF9\u6155\u5BB9\u9F99\u7684\u8BE2\u95EE\u7F6E\u82E5\u60D8\u95FB\u3002</p><p>\u6155\u5BB9\u9F99\u58F0\u97F3\u4E00\u51B7\uFF0C\u300C\u4F60\u600E\u4E48\u4F1A\u51E4\u51F0\u5B9D\u5178\uFF1F\u300D</p><p>\u7D2B\u73AB\u82B3\u5FC3\u6697\u98A4\uFF0C\u6CA1\u60F3\u5230\u4ED6\u7ADF\u7136\u4F1A\u77E5\u9053\u672C\u95E8\u79D8\u7C4D\u3002</p><p>\u300C\u662F\u96EA\u5CF0\u90A3\u4E2A\u8D3C\u5C3C\u4F20\u4F60\u7684\u5417\uFF1F\u300D</p><p>\u58F0\u97F3\u91CC\u5E26\u7740\u5E9E\u5927\u7684\u538B\u529B\uFF0C\u7D2B\u73AB\u4E0D\u80FD\u518D\u88C5\u804B\u4F5C\u54D1\u4E0B\u53BB\uFF0C\u4E8E\u662F\u5C0F\u58F0\u9053\uFF1A\u300C\u4EC0\u4E48\u51E4\u51F0\u5B9D\u5178\uFF1F\u6CA1\u542C\u8BF4\u8FC7\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u538B\u6839\u513F\u4E0D\u4FE1\uFF0C\u300C\u96EA\u5CF0\u4F20\u4F60\u7684\u662F\u4EC0\u4E48\u529F\u592B\uFF1F\u300D</p><p>\u300C\u4E5D\u7384\u771F\u6C14\u3002\u300D\u7D2B\u73AB\u773C\u4E5F\u4E0D\u7728\u5730\u80E1\u8BCC\u4E2A\u540D\u79F0\u3002</p><p>\u300C\u4E5D\u7384\u771F\u6C14\u7834\u4F53\u540E\u4F1A\u5047\u6B7B\u5417\uFF1F\u300D</p><p>\u300C\u5047\u6B7B\uFF1F\u300D\u7D2B\u73AB\u6CEA\u73E0\u6251\u6251\u7C0C\u7C0C\u6389\u4E86\u51FA\u6765\uFF0C\u59D4\u5C48\u4E07\u5206\u5730\u8BF4\uFF1A\u300C\u4F60\u7684\u574F\u4E1C\u897F\u90A3\u4E48\u5927\uFF0C\u6211\u5DEE\u4E00\u70B9\u5C31\u771F\u6B7B\u4E86\uFF0C\u545C\u2026\u2026\u4F60\u8FD9\u4E2A\u6DF7\u86CB\uFF0C\u4E00\u70B9\u90FD\u4E0D\u5FC3\u75BC\u6211\u2026\u2026\u300D</p><p>\u5979\u8D8A\u8BF4\u8D8A\u607C\uFF0C\u4E00\u811A\u8E22\u5728\u6155\u5BB9\u9F99\u817F\u4E0A\u3002\u817F\u4E00\u52A8\uFF0C\u5979\u300C\u54CE\u5440\u300D\u4E00\u58F0\u75DB\u53EB\uFF0C\u7EC6\u7709\u987F\u65F6\u62E7\u7D27\uFF0C\u8FD9\u4E0B\u5012\u4E0D\u662F\u88C5\u7684\u3002</p><p>\u6155\u5BB9\u9F99\u62FF\u5979\u4E5F\u6CA1\u529E\u6CD5\uFF0C\u7B49\u5979\u54ED\u5B8C\uFF0C\u53C8\u95EE\u9053\uFF1A\u300C\u4F60\u5F53\u65F6\u771F\u5143\u6709\u6CA1\u6709\u4EC0\u4E48\u5F02\u5E38\uFF1F\u300D</p><p>\u6709\uFF0C\u5F53\u7136\u6709\u5F02\u5E38\u3002\u7D2B\u73AB\u6447\u4E86\u6447\u5934\uFF0C\u53C8\u5FAE\u5FAE\u70B9\u5934\uFF0C\u8FDF\u7591\u7740\u8BF4\uFF1A\u300C\u597D\u50CF\u6709\u2026\u2026\u597D\u50CF\u88AB\u4F60\u5438\u8D70\u4E86\u2026\u2026\u6155\u5BB9\u9F99\uFF01\u300D\u5979\u7A81\u7136\u53EB\u4E86\u8D77\u6765\uFF0C\u300C\u4F60\u662F\u4E0D\u662F\u5077\u8D70\u4E86\u6211\u7684\u529F\u529B\uFF01\u300D</p><p>\u6155\u5BB9\u9F99\u7565\u5E26\u5C34\u5C2C\u5730\u7B11\u9053\uFF1A\u300C\u6CA1\u6709\u2026\u2026\u771F\u6CA1\u6709\u2026\u2026\u300D\u4E0D\u8FC7\u597D\u50CF\u771F\u6709\u4E00\u70B9\u3002</p><p>\u5176\u5B9E\u4E0D\u53EA\u662F\u4ED6\u5438\u53D6\u4E86\u7D2B\u73AB\u7684\u771F\u5143\uFF0C\u6155\u5BB9\u9F99\u6C47\u5165\u7D2B\u73AB\u4E39\u7530\u7684\u771F\u5143\u66F4\u591A\u3002</p><p>\u51E4\u51F0\u5B9D\u5178\u672C\u662F\u4E0A\u53E4\u7384\u7ECF\uFF0C\u76F8\u4F20\u4E3A\u4E5D\u5929\u7384\u5973\u6240\u6388\uFF0C\u4FEE\u4E60\u8005\u5FC5\u4E3A\u7EAF\u9634\u4E4B\u8D28\u3002\u6218\u56FD\u4E4B\u521D\uFF0C\u5B9D\u5178\u843D\u5165\u4E00\u4F4D\u65B9\u58EB\u7384\u5999\u5B50\u624B\u4E2D\u3002</p><p>\u7384\u5999\u5B50\u672C\u662F\u9053\u5BB6\u5AE1\u8109\uFF0C\u7CBE\u4FEE\u592A\u4E00\u7ECF\u591A\u5E74\uFF0C\u4F46\u59CB\u7EC8\u96BE\u81F3\u5927\u6210\u3002\u5F97\u5230\u51E4\u51F0\u5B9D\u5178\u4E4B\u540E\uFF0C\u624D\u667A\u9AD8\u7EDD\u7684\u7384\u5999\u5B50\u7ACB\u523B\u770B\u51FA\u5B9D\u5178\u4E0E\u592A\u4E00\u7ECF\u867D\u7136\u9634\u9633\u5404\u5F02\uFF0C\u5374\u662F\u76F8\u8F85\u76F8\u627F\u3002</p><p>\u4ED6\u60EE\u7CBE\u7AED\u667A\u7CBE\u7814\u5176\u4E2D\u5965\u5999\u3002\u5E76\u767E\u822C\u6311\u9009\uFF0C\u5A36\u4E86\u4E00\u540D\u8D28\u6167\u8C8C\u7F8E\u7684\u5C11\u5973\u4E3A\u59BB\uFF0C\u6388\u4EE5\u5B9D\u5178\u3002</p><p>\u592B\u59BB\u4E8C\u4EBA\u6F5C\u5FC3\u4FEE\u70BC\uFF0C\u6700\u7EC8\u4F7F\u592A\u4E00\u7ECF\u4E0E\u51E4\u51F0\u5B9D\u5178\u878D\u6C47\u8D2F\u901A\uFF0C\u6210\u529F\u7684\u7834\u53BB\u4E86\u4FEE\u4E60\u51E4\u51F0\u5B9D\u5178\u5FC5\u9700\u7EAF\u9634\u4E4B\u8D28\u7684\u9650\u5236\u3002</p><p>\u4F46\u4E50\u6781\u751F\u60B2\uFF0C\u6B63\u5F53\u7384\u5999\u5B50\u4E3A\u5927\u529F\u544A\u6210\u6B22\u6B23\u9F13\u821E\u4E4B\u65F6\uFF0C\u7231\u59BB\u5374\u7A81\u7136\u53CD\u76EE\u6210\u4EC7\uFF0C\u4EE5\u521A\u521A\u7EC3\u5C31\u7684\u51E4\u51F0\u795E\u529F\u5C06\u4ED6\u6253\u843D\u60AC\u5D16\u3002\u5F85\u7384\u5999\u5B50\u4F24\u7652\u590D\u51FA\uFF0C\u624D\u77E5\u9053\u81EA\u5DF1\u6DF1\u7231\u7684\u59BB\u5B50\u5DF2\u7ECF\u4E0E\u95E8\u4E0B\u5F1F\u5B50\u5802\u800C\u7687\u4E4B\u5730\u7ED3\u6210\u592B\u5987\u3002</p><p>\u7ECF\u6B64\u60E8\u5267\uFF0C\u7384\u5999\u5B50\u6027\u60C5\u5927\u53D8\u3002\u4E00\u756A\u82E6\u6597\u4E4B\u540E\uFF0C\u7384\u5999\u5B50\u5C06\u95E8\u4E0B\u6240\u6709\u5F1F\u5B50\u4E0D\u5206\u826F\u8D31\u6740\u4E2A\u5E72\u5E72\u51C0\u51C0\uFF0C\u5E76\u4E14\u7528\u6700\u6B8B\u9177\u7684\u624B\u6BB5\u5C06\u7231\u59BB\u6298\u78E8\u81F3\u6B7B\u3002</p><p>\u59BB\u5B50\u7684\u80CC\u53DB\u4F7F\u7384\u5999\u5B50\u5BF9\u5973\u4EBA\u75DB\u6068\u4E07\u5206\uFF0C\u8D81\u5929\u4E0B\u52A8\u6C64\uFF0C\u4ED6\u4EE5\u7EC8\u5357\u6DF1\u5C71\u4E3A\u57FA\uFF0C\u7F51\u7F57\u515A\u7FBD\u4ECE\u5404\u5730\u63B3\u63A0\u5973\u5B50\u4EE5\u4F9B\u6DEB\u8650\uFF0C\u5E76\u9760\u7740\u81EA\u5DF1\u7684\u535A\u5B66\u624D\u8BC6\u837C\u6BD2\u751F\u7075\uFF0C\u5C06\u5973\u4F53\u4F5C\u4E3A\u9F0E\u7089\u4EE5\u90AA\u6CD5\u4FEE\u771F\uFF0C\u7EC8\u81F3\u5927\u6210\u3002</p><p>\u7384\u5999\u5B50\u6210\u4E3A\u661F\u6708\u6E56\u7684\u5F00\u5C71\u7956\u5E08\uFF0C\u51E4\u51F0\u5B9D\u5178\u548C\u592A\u4E00\u7ECF\u4E5F\u6210\u4E3A\u9547\u6559\u795E\u529F\u3002\u4F46\u4E0E\u5386\u4EE3\u5BAB\u4E3B\u4FEE\u884C\u7684\u592A\u4E00\u7ECF\u4E0D\u540C\uFF0C\u51E4\u51F0\u5B9D\u5178\u4E13\u4F9B\u5973\u5B50\u4FEE\u884C\uFF0C\u5BF9\u4E8E\u4ECE\u4E0D\u6536\u5973\u5F1F\u5B50\u7684\u661F\u6708\u6E56\u6765\u8BF4\u6BEB\u65E0\u7528\u5904\u3002\u53EA\u662F\u7384\u5999\u5B50\u5728\u5B9D\u5178\u4E0A\u82B1\u8D39\u5FC3\u8840\u751A\u591A\uFF0C\u96BE\u4EE5\u5272\u820D\u3002\u56E0\u6B64\u53EA\u628A\u5B9D\u5178\u9501\u5165\u79D8\u5BA4\uFF0C\u91CD\u91CD\u5C01\u5370\u3002</p><p>\u76F4\u5230\u767E\u4F59\u5E74\u524D\uFF0C\u5B9D\u5178\u4E3A\u7075\u7280\u5F69\u51E4\u76D7\u53D6\uFF0C\u56DB\u9547\u795E\u5C06\u5C3D\u6570\u547D\u6B92\u5176\u624B\u3002\u5F53\u65F6\u661F\u6708\u6E56\u9AD8\u624B\u503E\u5DE2\u800C\u51FA\uFF0C\u4E0E\u7075\u7280\u5F69\u51E4\u51B3\u6218\u5357\u6D77\u4E4B\u6EE8\uFF0C\u4EE5\u727A\u7272\u6570\u5341\u540D\u9AD8\u624B\u7684\u4EE3\u4EF7\u4E5F\u672A\u80FD\u5C06\u5979\u51FB\u6BD9\uFF0C\u53CD\u800C\u88AB\u5979\u6740\u81F3\u5723\u5BAB\u3002\u6700\u540E\u592A\u51B2\u5BAB\u4E3B\u4E0D\u5F97\u5DF2\u5C01\u95ED\u5730\u5BAB\uFF0C\u4E0E\u7075\u7280\u5F69\u51E4\u540C\u5F52\u4E8E\u5C3D\u3002</p><p>\u4F46\u51E4\u51F0\u5B9D\u5178\u5374\u4ECE\u6B64\u4E0B\u843D\u4E0D\u660E\u3002</p><p>\u636E\u7384\u5999\u5B50\u4EB2\u624B\u6240\u523B\u7684\u7559\u771F\u5377\u8BB0\u8F7D\uFF0C\u4FEE\u4E60\u51E4\u51F0\u5B9D\u5178\u5728\u7B2C\u516B\u5C42\u4E4B\u524D\u5143\u7EA2\u88AB\u7834\uFF0C\u5FC5\u7136\u5371\u53CA\u6027\u547D\u3002\u4F46\u82E5\u4EE5\u592A\u4E00\u771F\u6C14\u52A9\u4E4B\uFF0C\u4EC5\u4F1A\u5047\u6B7B\u516D\u4E2A\u65F6\u8FB0\uFF0C\u5728\u8FD9\u671F\u95F4\u516B\u8109\u9F50\u65AD\uFF0C\u6C14\u606F\u7686\u65E0\u3002</p><p>\u4E0D\u8FC7\u6B64\u4E8B\u4E43\u7384\u5999\u5B50\u6BD5\u751F\u6068\u4E8B\uFF0C\u5377\u4E2D\u8BB0\u8F7D\u6781\u5C11\uFF0C\u4EC5\u6709\u5BE5\u5BE5\u6570\u8BED\u3002\u51E4\u51F0\u5B9D\u5178\u53C8\u5931\u8E2A\u591A\u5E74\uFF0C\u65E0\u4ECE\u8FA8\u522B\u3002</p><p>\u6155\u5BB9\u9F99\u51B7\u773C\u65C1\u89C2\uFF0C\u96EA\u5CF0\u795E\u5C3C\u548C\u59B9\u59B9\u7EC3\u7684\u591A\u534A\u5C31\u662F\u51E4\u51F0\u5B9D\u5178\uFF0C\u4F46\u540C\u662F\u98D8\u6885\u5CF0\u5F1F\u5B50\uFF0C\u4E3A\u4F55\u98CE\u665A\u534E\u7B49\u4EBA\u5374\u6BEB\u65E0\u5F02\u72B6\uFF1F\u4ED6\u6599\u77E5\u518D\u95EE\u4E0B\u53BB\u7D2B\u73AB\u4E5F\u4E0D\u4F1A\u8BF4\u5B9E\u8BDD\uFF0C\u4FBF\u6362\u4E0A\u7B11\u8138\uFF0C\u62AC\u624B\u4F38\u5411\u59B9\u59B9\u8EAB\u4E0A\u7684\u67D4\u6BEF\u3002</p><p>\u7D2B\u73AB\u634F\u7D27\u6BEF\u89D2\uFF0C\u5A07\u8EAF\u8737\u6210\u4E00\u56E2\uFF0C\u8FDC\u8FDC\u8EB2\u5728\u89D2\u843D\u91CC\uFF0C\u8B66\u6212\u5730\u8BF4\u9053\uFF1A\u300C\u4F60\u8981\u5E72\u4EC0\u4E48\uFF01\u300D</p><p>\u300C\u5E72\u4F60\u3002\u300D\u6155\u5BB9\u9F99\u5E72\u8106\u5730\u8BF4\u3002</p><p>\u300C\u4E0D\u884C\uFF01\u4E0D\u8BB8\u518D\u78B0\u6211\uFF01\u300D</p><p>\u300C\u5C11\u5E9F\u8BDD\uFF0C\u4F60\u73B0\u5728\u5DF2\u7ECF\u662F\u54E5\u54E5\u7684\u59BB\u5B50\u4E86\uFF0C\u8BA9\u6211\u64CD\u662F\u5929\u7ECF\u5730\u4E49\u2014\u2014\u6BEF\u5B50\u62FF\u5F00\uFF0C\u8BA9\u54E5\u54E5\u770B\u770B\u4F60\u7684\u5C0F\u5AE9\u5C44\u2026\u2026\u300D</p><p>\u300C\u54E5\u2026\u2026\u4EBA\u5BB6\u8FD8\u75BC\u7740\u5462\u2026\u2026\u300D\u7D2B\u73AB\u5C0F\u58F0\u54C0\u6C42\u9053\u3002</p><p>\u300C\u6211\u770B\u4E00\u4E0B\uFF0C\u4F24\u7684\u5389\u5BB3\u54E5\u54E5\u5C31\u4E0D\u78B0\u4F60\u3002\u300D</p><p>\u300C\u6211\u624D\u4E0D\u4FE1\u5462\u2014\u2014\u4F60\u624D\u4E0D\u7BA1\u4EBA\u5BB6\u75BC\u4E0D\u75BC\uFF0C\u90FD\u8981\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u4E0D\u8010\u70E6\u8D77\u6765\uFF0C\u4E00\u628A\u62B1\u4F4F\u7D2B\u73AB\u7684\u80A9\u5934\uFF0C\u6380\u5F00\u67D4\u6BEF\uFF0C\u300C\u624B\u62FF\u5F00\u3002\u300D</p><p>\u7D2B\u73AB\u6068\u4E0D\u5F97\u4E00\u53E3\u54AC\u6B7B\u4ED6\uFF0C\u4F46\u8138\u4E0A\u8FD8\u662F\u6302\u7740\u51C4\u51C4\u5A49\u5A49\u7684\u7F9E\u75BC\uFF0C\u65E0\u5948\u7684\u79FB\u5F00\u5C0F\u624B\u3002</p><p>\u6155\u5BB9\u9F99\u63B0\u5F00\u817F\u7F1D\uFF0C\u770B\u5230\u80A1\u95F4\u90A3\u7247\u9C9C\u8273\u7684\u6BB7\u7EA2\uFF0C\u4E0D\u7531\u5FC3\u91CC\u4E00\u60CA\uFF0C\u8FDE\u5FD9\u8F7B\u8F7B\u5265\u5F00\u82B1\u74E3\uFF0C\u628A\u624B\u6307\u63A2\u5165\u5176\u4E2D\u6D45\u6D45\u638F\u6478\u3002</p><p>\u7D2B\u73AB\u7709\u5934\u8F7B\u76B1\uFF0C\u5C11\u5973\u7F9E\u6DA9\u7684\u79D8\u5904\u88AB\u81EA\u5DF1\u6DF1\u6068\u7684\u7537\u4EBA\u73A9\u5F04\uFF0C\u90A3\u79CD\u7F9E\u6124\u4F7F\u5979\u989D\u89D2\u8840\u7BA1\u6025\u8DF3\uFF0C\u6068\u4E0D\u5F97\u4E00\u5200\u6345\u6B7B\u8FD9\u4E2A\u79BD\u517D\uFF01\u5979\u8111\u4E2D\u5FFD\u7136\u4E00\u95EA\uFF0C\u4E24\u773C\u4E0D\u5B89\u4EFD\u5730\u5728\u6155\u5BB9\u9F99\u8170\u95F4\u641C\u7D22\uFF0C\u60F3\u627E\u51FA\u81EA\u5DF1\u7684\u5B9D\u5200\u7247\u7389\u3002</p><p>\u521A\u60F3\u632A\u52A8\u8EAB\u4F53\u51D1\u5230\u4ED6\u8EAB\u8FB9\u6478\u6478\uFF0C\u53EA\u542C\u6155\u5BB9\u9F99\u4F4E\u58F0\u53F9\u9053\uFF1A\u300C\u59B9\u59B9\uFF0C\u4F60\u8FD8\u771F\u4E86\u89E3\u54E5\u54E5\u2026\u2026\u300D</p><p>\u7D2B\u73AB\u6B63\u5728\u7EB3\u95F7\uFF0C\u7A81\u7136\u4E0B\u4F53\u4E00\u98A4\uFF0C\u90A3\u4E24\u6839\u624B\u6307\u4F3C\u4E4E\u5E26\u7740\u9EBB\u9165\u9165\u7684\u7EC6\u5FAE\u7535\u6D41\uFF0C\u4ECE\u81EA\u5DF1\u6700\u654F\u611F\u7684\u82B1\u8482\u63A0\u8FC7\u3002\u4FCF\u8138\u987F\u65F6\u7EA2\u4E86\u8D77\u6765\uFF0C\u7D2B\u73AB\u661F\u7738\u534A\u5F20\uFF0C\u7EA2\u5507\u4E2D\u9038\u51FA\u4E00\u7F15\u67D4\u5A9A\u5165\u9AA8\u7684\u5A07\u5598\u3002\u5979\u4E24\u624B\u7D27\u7D27\u634F\u7740\u6155\u5BB9\u9F99\u7684\u8863\u895F\uFF0C\u5A07\u8EAF\u5728\u624B\u6307\u6E29\u67D4\u7684\u7231\u629A\u4E0B\u4E0D\u4F4F\u6218\u6817\uFF0C\u7C89\u5AE9\u7684\u82B1\u74E3\u65F6\u9F13\u65F6\u7F29\uFF0C\u6C81\u51FA\u70B9\u70B9\u871C\u6DB2\u3002</p><p>\u6155\u5BB9\u9F99\u5634\u5507\u5728\u5979\u8033\u540E\u8F7B\u8F7B\u78E8\u64E6\u7740\uFF0C\u5462\u54DD\u9053\uFF1A\u300C\u60F3\u8BA9\u54E5\u54E5\u64CD\u4F60\u5417\uFF1F\u300D</p><p>\u7D2B\u73AB\u4E24\u773C\u8FF7\u8499\u5730\u504E\u4F9D\u5728\u4ED6\u6000\u91CC\uFF0C\u9999\u80A9\u5FAE\u52A8\u3002\u7247\u523B\u540E\u50CF\u662F\u53D7\u4E0D\u4E86\u4ED6\u7684\u6311\u9017\u822C\uFF0C\u8EAB\u4F53\u4E00\u7D27\uFF0C\u624B\u81C2\u7D27\u7D27\u62B1\u5728\u6155\u5BB9\u9F99\u8170\u95F4\uFF0C\u642D\u5728\u6155\u5BB9\u9F99\u819D\u4E0A\u7684\u4E24\u817F\u4EA4\u53C9\u62E7\u5728\u4E00\u8D77\uFF0C\u5706\u81C0\u5FAE\u6643\u3002</p><p>\u300C\u55EF\uFF1F\u300D\u6155\u5BB9\u9F99\u6311\u9017\u5730\u95EE\u9053\u3002</p><p>\u300C\u5514\uFF1F\u300D\u7D2B\u73AB\u50CF\u662F\u6CA1\u542C\u5230\u4ED6\u521A\u624D\u8BF4\u7684\u8BDD\u3002\u5FC3\u91CC\u5374\u5728\u5927\u9A82\uFF0C\u8FD9\u5BB6\u4F19\u8FDE\u5185\u8863\u90FD\u6CA1\u7A7F\uFF0C\u751A\u81F3\u8FDE\u5175\u5203\u90FD\u4E0D\u5E26\uFF01</p><p>\u300C\u60F3\u8BA9\u54E5\u54E5\u64CD\u4F60\u5417\uFF1F\u300D\u6155\u5BB9\u9F99\u91CD\u95EE\u9053\u3002</p><p>\u300C\u55EF\u2014\u2014\u4E0D\u561B\u2026\u2026\u300D\u7D2B\u73AB\u5624\u549B\u7740\u6447\u6447\u5934\uFF0C\u5979\u63E1\u4F4F\u81C0\u4E0B\u90A3\u6839\u76F4\u76F4\u7AD6\u8D77\u7684\u5DE8\u7269\uFF0C\u5411\u4E0B\u6309\u53BB\uFF0C\u5A07\u58F0\u9053\uFF1A\u300C\u4F60\u8FD9\u6837\u4EBA\u5BB6\u5C31\u633A\u8212\u670D\uFF0C\u4E0D\u8981\u5B83\u78B0\u6211\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u53C8\u662F\u597D\u6C14\u53C8\u662F\u597D\u7B11\uFF0C\u300C\u4F60\u8212\u670D\u4E86\uFF0C\u54E5\u54E5\u8FD8\u6CA1\u6709\u8212\u670D\u5462\u3002\u300D</p><p>\u521A\u624D\u4ED6\u5DF2\u63A2\u51FA\u7D2B\u73AB\u4E0B\u4F53\u7684\u8840\u8FF9\u53EA\u662F\u5143\u7EA2\u65B0\u7834\u7684\u4F59\u6CA5\uFF0C\u8089\u7A74\u5E76\u672A\u53D7\u4F24\u3002\u8D77\u521D\u7834\u4F53\u65F6\u4ED6\u8FD8\u6015\u59B9\u59B9\u96BE\u4EE5\u627F\u53D7\uFF0C\u5FCD\u8BA9\u4E09\u5206\uFF1B\u540E\u6765\u8BEF\u4EE5\u4E3A\u59B9\u59B9\u5DF2\u6B7B\uFF0C\u518D\u884C\u5978\u6DEB\u65F6\u4FBF\u6CA1\u6709\u4E1D\u6BEB\u4FDD\u7559\u3002\u6CA1\u60F3\u5230\u59B9\u59B9\u5A07\u5AE9\u7684\u5904\u5B50\u5E7D\u7A74\uFF0C\u5C45\u7136\u80FD\u627F\u53D7\u81EA\u5DF1\u8FD9\u4E48\u602A\u5F02\u7684\u5E9E\u7136\u5DE8\u7269\u2026\u2026</p><p>\u4ED6\u4E2D\u6307\u63D2\u5165\u5C0F\u7A74\uFF0C\u62C7\u6307\u5728\u82B1\u8482\u4E0A\u8F7B\u63C9\u6162\u637B\uFF0C\u7A74\u53E3\u7ACB\u523B\u50CF\u6E29\u6DA6\u7684\u5C0F\u5634\uFF0C\u542B\u7740\u624B\u6307\u67D4\u67D4\u541E\u5410\u3002\u6155\u5BB9\u9F99\u5174\u594B\u5F02\u5E38\uFF0C\u9AD8\u58F0\u8D5E\u9053\uFF1A\u300C\u59B9\u59B9\u7684\u5C0F\u5C44\u771F\u662F\u7EDD\u54C1\uFF01\u300D</p><p>\u9633\u5177\u4E00\u9707\uFF0C\u786C\u6886\u6886\u6491\u5F00\u7D2B\u73AB\u7684\u5C0F\u624B\uFF0C\u5E26\u7740\u70BD\u70ED\u7684\u6C14\u606F\u9876\u5728\u80A1\u95F4\u3002\u5979\u60CA\u547C\u4E00\u58F0\uFF0C\u6025\u5FD9\u633A\u8170\u6B32\u8EB2\u3002</p><p>\u6155\u5BB9\u9F99\u4E00\u624B\u4ECE\u80CC\u4E0B\u7ED5\u8FC7\uFF0C\u63E1\u4F4F\u5C0F\u5DE7\u7684\u9165\u4E73\uFF1B\u4E00\u624B\u6402\u7740\u819D\u5F2F\uFF0C\u5C06\u7D2B\u73AB\u62B1\u5728\u6000\u4E2D\uFF0C\u4E00\u8138\u574F\u7B11\u5730\u8BF4\uFF1A\u300C\u60F3\u9003\uFF1F\u300D</p><p>\u8FD9\u5BB6\u4F19\u65E2\u7136\u6CA1\u6709\u968F\u8EAB\u5E26\u7740\u5B9D\u5200\uFF0C\u7D2B\u73AB\u4E5F\u61D2\u5F97\u518D\u7EA0\u7F20\u4E0B\u53BB\uFF0C\u8138\u4E0A\u521A\u624D\u7684\u5A9A\u6001\u4E00\u626B\u800C\u7A7A\uFF0C\u5979\u6323\u624E\u7740\u6491\u5750\u8D77\u6765\uFF0C\u7EF7\u7740\u8138\u4F4E\u53EB\u9053\uFF1A\u300C\u653E\u6211\u4E0B\u53BB\uFF01\u300D</p><p>\u6155\u5BB9\u9F99\u624B\u6307\u4E00\u6311\uFF0C\u60F9\u5F97\u7389\u4EBA\u82B1\u679D\u822C\u4E00\u9635\u4E71\u98A4\uFF0C\u300C\u4E56\u4E56\u5206\u5F00\u817F\uFF0C\u8BA9\u54E5\u54E5\u7684\u4E1C\u897F\u63D2\u8FDB\u53BB\u3002\u300D</p><p>\u7D2B\u73AB\u4E24\u773C\u4E00\u7728\uFF0C\u7ACB\u5373\u73E0\u6CEA\u76C8\u7136\uFF0C\u300C\u54E5\u54E5\uFF0C\u4EBA\u5BB6\u597D\u75DB\u554A\u2026\u2026\u660E\u5929\u5427\uFF0C\u597D\u4E0D\u597D\u2026\u2026\u300D</p><p>\u300C\u75DB\u4EC0\u4E48\u75DB\uFF0C\u91CC\u9762\u6ED1\u6E9C\u6E9C\u4E00\u70B9\u4E8B\u90FD\u6CA1\u6709\u2026\u2026\u300D\u6155\u5BB9\u9F99\u5FC3\u91CC\u6697\u7B11\u9053\uFF0C\u4F60\u5F00\u59CB\u8BF4\u7684\u4E00\u70B9\u6CA1\u9519\uFF0C\u4E0D\u7BA1\u4F60\u75BC\u4E0D\u75BC\uFF0C\u8FD9\u4E48\u7F8E\u5999\u7684\u5C0F\u7A74\uFF0C\u54E5\u54E5\u90FD\u8981\u597D\u597D\u4EAB\u53D7\u4E00\u756A\u3002</p><p>\u706B\u70ED\u7684\u9F9F\u5934\u62F3\u5934\u822C\u786C\u786C\u9876\u8FDB\u81C0\u7F1D\u4E2D\uFF0C\u6765\u56DE\u78E8\u64E6\uFF0C\u7D2B\u73AB\u77E5\u9053\u4ED6\u6027\u6B32\u5927\u53D1\uFF0C\u81EA\u5DF1\u5728\u52AB\u96BE\u9003\uFF0C\u4E00\u54AC\u7259\uFF0C\u53C8\u6362\u4E0A\u5A07\u7F9E\u65E0\u9650\u7684\u795E\u60C5\uFF0C\u7EA4\u624B\u6321\u5728\u8179\u4E0B\uFF0C\u5410\u6C14\u5982\u5170\u7684\u817B\u58F0\u9053\uFF1A\u300C\u54E5\u54E5\uFF0C\u4F60\u5148\u7B54\u5E94\u59B9\u59B9\u4E00\u4EF6\u4E8B\u597D\u4E0D\u597D\uFF1F\u300D</p><p>\u6155\u5BB9\u9F99\u7B11\u4E86\u534A\u58F0\uFF0C\u6B63\u8981\u7B54\u5E94\uFF0C\u7A81\u7136\u8138\u8272\u4E00\u6C89\u3002\u534A\u664C\u51B7\u51B7\u9053\uFF1A\u300C\u6328\u64CD\u662F\u4F60\u4EFD\u5185\u4E4B\u4E8B\uFF0C\u5C11\u8DDF\u6211\u63D0\u4EC0\u4E48\u6761\u4EF6\u3002\u300D\u8FD9\u5C0F\u4E2B\u5934\u603B\u8DDF\u81EA\u5DF1\u800D\u6ED1\uFF0C\u8FD9\u4E2A\u4F8B\u5B50\u7EDD\u4E0D\u80FD\u5F00\uFF0C\u8FD8\u662F\u8BA9\u5979\u8D81\u65E9\u6B7B\u4E86\u8FD9\u6761\u5FC3\uFF0C\u514D\u5F97\u6301\u5BA0\u751F\u9A84\uFF0C\u4EE5\u540E\u8D8A\u6765\u8D8A\u9EBB\u70E6\u3002</p><p>\u7D2B\u73AB\u6CA1\u60F3\u5230\u81EA\u5DF1\u6545\u4F5C\u5A9A\u6001\uFF0C\u5C45\u7136\u8FD8\u4F1A\u78B0\u4E0A\u4E2A\u786C\u9489\u5B50\uFF0C\u4E0D\u7531\u7F9E\u6012\u4EA4\u52A0\uFF0C\u4E00\u62F3\u6253\u5728\u52C3\u8D77\u7684\u8089\u68D2\u4E0A\u3002</p><p>\u6155\u5BB9\u9F99\u8138\u8272\u9634\u6C89\uFF0C\u4ED6\u76D8\u819D\u800C\u5750\uFF0C\u63E1\u4F4F\u7D2B\u73AB\u7684\u819D\u5F2F\u5411\u4E0A\u4E00\u63D0\uFF0C\u5C06\u7EA4\u67D4\u7684\u8170\u80A2\u653E\u5728\u81EA\u5DF1\u817F\u4E0A\u3002\u7136\u540E\u4E24\u624B\u4E00\u5206\uFF0C\u8FF7\u4EBA\u7684\u7389\u6237\u7ACB\u523B\u5728\u6676\u83B9\u7684\u7389\u80A1\u95F4\u67D4\u67D4\u7EFD\u5F00\u3002</p><p>\u6155\u5BB9\u9F99\u6545\u610F\u6CA1\u6709\u70B9\u5979\u7684\u7A74\u9053\uFF0C\u5C31\u662F\u60F3\u7528\u7C97\u66B4\u7684\u5F3A\u5978\u7ED9\u59B9\u59B9\u4E00\u4E2A\u6559\u8BAD\uFF0C\u8BA9\u5979\u660E\u767D\uFF1A\u4EFB\u4F55\u53CD\u6297\u548C\u8981\u6C42\u90FD\u662F\u5F92\u52B3\u7684\u3002\u4F5C\u4E3A\u59BB\u5B50\uFF0C\u4F5C\u4E3A\u5973\u4EBA\uFF0C\u5979\u4E0D\u80FD\u6709\u81EA\u5DF1\u7684\u610F\u89C1\uFF0C\u53EA\u80FD\u67D4\u987A\u7684\u4F8D\u5949\u7537\u4EBA\uFF0C\u65E0\u8A00\u5730\u732E\u51FA\u81EA\u5DF1\u7684\u8089\u4F53\u3002</p><p>\u4F46\u59B9\u59B9\u7684\u53CD\u5E94\u51FA\u4E4E\u4ED6\u7684\u610F\u6599\u3002\u7D2B\u73AB\u4FCF\u8138\u53D1\u767D\uFF0C\u7565\u7565\u6323\u624E\u4E00\u4E0B\u4FBF\u653E\u5F03\u4E86\uFF0C\u53EA\u662F\u51B7\u51B0\u51B0\u644A\u5F00\u8EAB\u4F53\u4EFB\u4ED6\u4E3A\u6240\u6B32\u4E3A\u3002</p>',62);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
