import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_64" tabindex="-1"><a class="header-anchor" href="#_64" aria-hidden="true">#</a> 64</h1><p>\u5F53\u9F9F\u5934\u8FDB\u5165\u7684\u4E00\u5239\u90A3\uFF0C\u82B1\u74E3\u4F3C\u7684\u4FCF\u8138\u731B\u7136\u626D\u66F2\u3002\u7D2B\u73AB\u5634\u89D2\u62BD\u52A8\u7740\u54AC\u7D27\u7EA2\u5507\uFF0C\u5C0F\u624B\u62E7\u4F4F\u88AB\u8925\uFF0C\u634F\u6210\u4E00\u56E2\u3002</p><p>\u6155\u5BB9\u9F99\u514B\u5236\u4F4F\u81EA\u5DF1\u4EB2\u543B\u59B9\u59B9\u7684\u51B2\u52A8\uFF0C\u7528\u4E00\u58F0\u51B7\u7B11\u5316\u89E3\u5FC3\u4E2D\u7684\u4E0D\u5FCD\uFF0C\u7136\u540E\u8F7B\u63CF\u6DE1\u5199\u5730\u8BF4\uFF1A\u300C\u522B\u5F3A\u5FCD\u4E86\uFF0C\u60F3\u54ED\u5C31\u54ED\u5427\uFF0C\u53CD\u6B63\u4E00\u4F1A\u513F\u4F60\u5C31\u8BE5\u54ED\u7239\u558A\u5A18\u4E86\u2014\u2014\u300D\u8BF4\u7740\u4E24\u624B\u4E00\u626F\uFF0C\u5DE8\u9F99\u7C97\u66B4\u7684\u5C3D\u6839\u800C\u5165\u3002</p><p>\u7D2B\u73AB\u5A07\u8EAF\u5267\u98A4\u3002\u4E0E\u5979\u624B\u8155\u540C\u6837\u7C97\u7EC6\u7684\u9EDD\u9ED1\u8089\u68D2\uFF0C\u6DF1\u6DF1\u63D2\u5728\u7C89\u5AE9\u7684\u7389\u81C0\u4E2D\u3002\u53EA\u5269\u90A3\u4E1B\u8815\u52A8\u7684\u89E6\u624B\uFF0C\u5728\u8089\u7A74\u5916\u5F20\u7259\u821E\u722A\uFF0C\u6740\u6C14\u817E\u817E\u3002\u8FD9\u4E00\u4E0B\u5168\u529B\u8D2F\u5165\uFF0C\u6491\u6EE1\u4E86\u6574\u4E2A\u871C\u7A74\uFF0C\u6240\u6709\u7684\u8936\u76B1\u90FD\u88AB\u62C9\u5E73\uFF0C\u5B50\u5BAB\u88AB\u72E0\u72E0\u6324\u6241\uFF0C\u9F9F\u5934\u51E0\u4E4E\u9876\u4F4F\u4E39\u7530\u6240\u5728\u3002</p><p>\u7D2B\u73AB\u53EA\u89C9\u8179\u8154\u5185\u731B\u7136\u5267\u9707\uFF0C\u4EFF\u4F5B\u6240\u6709\u7684\u5668\u5B98\u90FD\u88AB\u8089\u68D2\u91CD\u91CD\u9876\u51FA\u3002\u7ECF\u8FC7\u77ED\u6682\u7684\u9EBB\u6728\uFF0C\u8089\u7A74\u7684\u5267\u75DB\u5E2D\u5377\u800C\u6765\uFF0C\u6BCF\u4E00\u5904\u7EC6\u5AE9\u7684\u8089\u58C1\u4F3C\u4E4E\u90FD\u88AB\u4E0D\u5E73\u6574\u7684\u9633\u5177\u64E6\u4F24\uFF0C\u75DB\u5F7B\u5FC3\u80BA\u3002</p><p>\u7D2B\u73AB\u76F4\u76F4\u8DEA\u5728\u6155\u5BB9\u9F99\u6000\u91CC\uFF0C\u5A07\u7F8E\u7684\u8089\u4F53\u50CF\u88AB\u5DE8\u7269\u6345\u7A7F\u822C\u6311\u5728\u534A\u7A7A\uFF0C\u4EFF\u4F5B\u679D\u5934\u5B64\u96F6\u96F6\u7684\u767D\u5AE9\u82B1\u6735\uFF0C\u5728\u98CE\u4E2D\u98A4\u6296\u3002\u826F\u4E45\uFF0C\u5979\u8270\u96BE\u5730\u5410\u4E86\u53E3\uFF0C\u54D1\u58F0\u9053\uFF1A\u300C\u6155\u5BB9\u9F99\u3002</p><p>\u6211\u6068\u4F60\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u63E1\u4F4F\u59B9\u59B9\u7684\u7EA4\u8170\uFF0C\u5411\u4E0A\u4E00\u63D0\uFF0C\u65CB\u5373\u91CD\u91CD\u6309\u4E0B\u3002\u5A07\u8EAF\u8D77\u843D\u95F4\uFF0C\u6574\u5177\u8EAB\u4F53\u4F3C\u4E4E\u53EA\u5269\u4E0B\u8089\u7A74\u7684\u5B58\u5728\u3002\u5C11\u5973\u4F53\u5185\u7684\u5AE9\u8089\u88AB\u62C9\u5230\u6781\u9650\uFF0C\u8FDE\u4E00\u6839\u6BDB\u53D1\u4E5F\u65E0\u6CD5\u5BB9\u7EB3\u3002</p><p>\u5A07\u5AE9\u654F\u611F\u7684\u82B1\u5FC3\u4EFF\u4F5B\u88AB\u77F3\u5757\u822C\u7684\u9F9F\u5934\u51FB\u788E\uFF0C\u54C6\u55E6\u7740\u5F20\u5F00\u7EC6\u5C0F\u7684\u5165\u53E3\u3002</p><p>\u6155\u5BB9\u9F99\u6367\u7740\u59B9\u59B9\u9999\u8F6F\u7684\u7389\u4F53\u6025\u901F\u5957\u5F04\uFF0C\u7A81\u7136\u4E24\u624B\u4E00\u62AC\uFF0C\u628A\u7D2B\u73AB\u6254\u5728\u4E00\u8FB9\u3002</p><p>\u7D2B\u73AB\u88AB\u8FD9\u9635\u72C2\u66B4\u7684\u62BD\u9001\u6345\u5F97\u7729\u6655\uFF0C\u5979\u4EF0\u8138\u5012\u5728\u69BB\u4E0A\uFF0C\u7EA4\u8DB3\u538B\u5728\u81C0\u4E0B\uFF0C\u4E24\u819D\u5F20\u5F00\u3002\u7CBE\u81F4\u7F8E\u5999\u7684\u7389\u6237\u4E2D\u9732\u51FA\u4E00\u4E2A\u7EA2\u8273\u8273\u7684\u7855\u5927\u5706\u5B54\uFF0C\u51E0\u4E4E\u5360\u636E\u4E86\u6574\u4E2A\u89C6\u91CE\u3002\u6DE1\u6DE1\u7684\u8840\u8FF9\u88AB\u62C9\u6210\u7EC6\u4E1D\uFF0C\u5728\u96EA\u81C0\u4E0B\u6447\u6765\u6643\u53BB\u3002</p><p>\u6155\u5BB9\u9F99\u76EF\u7740\u59B9\u59B9\u80A1\u95F4\u6D51\u5706\u7684\u8089\u7A74\uFF0C\u4E00\u8FB9\u626F\u6389\u5916\u888D\uFF0C\u4E00\u8FB9\u6478\u51FA\u4E00\u4E2A\u5C0F\u5C0F\u7684\u74F7\u74F6\u3002</p><p>\u4ED6\u4ECE\u74F6\u5185\u53D6\u51FA\u4E00\u7C92\u829D\u9EBB\u5927\u5C0F\u7684\u7EC6\u5FAE\u9897\u7C92\uFF0C\u653E\u5728\u9F9F\u5934\u7684\u9A6C\u773C\u4E2D\uFF0C\u7136\u540E\u5BD2\u58F0\u9053\uFF1A\u300C\u7FFB\u8FC7\u6765\uFF0C\u6362\u4E2A\u59FF\u52BF\u8BA9\u54E5\u54E5\u723D\u4E00\u723D\uFF01\u300D</p><p>\u7D2B\u73AB\u5507\u4E0A\u54AC\u51FA\u70B9\u70B9\u8840\u75D5\uFF0C\u5374\u59CB\u7EC8\u4E00\u58F0\u4E0D\u54CD\uFF0C\u4E5F\u6CA1\u6709\u4E00\u6EF4\u773C\u6CEA\u3002</p><p>\u6155\u5BB9\u9F99\u6293\u4F4F\u96EA\u767D\u7684\u81C0\u8089\u4E00\u7FFB\uFF0C\u5C06\u7D2B\u73AB\u62D4\u8F6C\u8FC7\u6765\uFF0C\u7136\u540E\u91CD\u91CD\u538B\u5728\u5979\u7684\u7C89\u80CC\u4E0A\u3002</p><p>\u8089\u68D2\u968F\u7740\u4E1D\u7EF8\u822C\u67D4\u6ED1\u7684\u808C\u80A4\u9876\u5230\u81C0\u4E0B\uFF0C\u300C\u53FD\u549B\u300D\u4E00\u58F0\uFF0C\u6345\u5165\u6E29\u6DA6\u7684\u8089\u7A74\u3002\u7EC6\u5AE9\u7684\u82B1\u74E3\u88AB\u626F\u6210\u4E24\u9053\u76F8\u8FDE\u7684\u5706\u5F27\uFF0C\u7D27\u7D27\u5361\u5728\u9633\u5177\u6839\u90E8\u3002</p><p>\u6155\u5BB9\u9F99\u72C2\u62BD\u731B\u9001\uFF0C\u62FC\u547D\u649E\u51FB\u7740\u67D4\u5AE9\u7684\u82B1\u5FC3\u3002\u4E0D\u591A\u65F6\uFF0C\u7D2B\u73AB\u4F53\u5185\u4E00\u98A4\uFF0C\u4E00\u80A1\u70ED\u70ED\u7684\u7EC6\u6D41\u4ECE\u82B1\u5FC3\u6DCC\u51FA\uFF0C\u5C3D\u6570\u6D8C\u5165\u7CBE\u7BA1\uFF0C\u6C47\u96C6\u5230\u6155\u5BB9\u9F99\u7684\u4E39\u7530\u5185\u3002</p><p>\u7ECF\u8FC7\u7384\u5999\u5B50\u4FEE\u6539\u7684\u51E4\u51F0\u5B9D\u5178\uFF0C\u4E0E\u592A\u4E00\u7ECF\u4E00\u9053\uFF0C\u6210\u4E3A\u592B\u59BB\u53CC\u4FEE\u7684\u529F\u6CD5\u3002\u4E24\u4EBA\u540C\u65F6\u4FEE\u70BC\uFF0C\u9634\u9633\u5408\u6D4E\uFF0C\u4E8B\u534A\u529F\u500D\u3002\u4F46\u7384\u5999\u5B50\u4E0D\u613F\u518D\u6709\u5973\u4EBA\u4FEE\u4E60\u5B9D\u5178\uFF0C\u56E0\u800C\u5BF9\u6B64\u8BB3\u83AB\u5982\u6DF1\uFF0C\u5728\u592A\u4E00\u7ECF\u4E2D\u53EA\u5B57\u4E0D\u63D0\u3002\u5E76\u4E14\u4ED6\u8FD8\u6539\u5199\u4E86\u51E4\u51F0\u5B9D\u5178\u6700\u540E\u4E09\u5C42\u7684\u529F\u6CD5\u3002</p><p>\u50CF\u96EA\u5CF0\u795E\u5C3C\u4FEE\u70BC\u5230\u7B2C\u4E03\u5C42\u51E4\u9E23\u671D\u9633\u4E4B\u540E\uFF0C\u5FC5\u987B\u7834\u4F53\u4F7F\u9634\u706B\u5916\u6CC4\u65B9\u53EF\u8FDB\u5165\u7B2C\u516B\u5C42\u51E4\u51F0\u4E8E\u98DE\u3002\u4F46\u529F\u6CD5\u5374\u8BF4\u5230\u7B2C\u516B\u5C42\u624D\u53EF\u7834\u4F53\uFF0C\u4EE5\u81F4\u96EA\u5CF0\u795E\u5C3C\u9634\u706B\u90C1\u79EF\u3002\u591A\u5E74\u79EF\u7D2F\u4E4B\u4E0B\uFF0C\u8FDE\u8089\u4F53\u4E5F\u4E3A\u4E4B\u6539\u53D8\u3002</p><p>\u81EA\u7384\u5999\u5B50\u4EE5\u964D\uFF0C\u661F\u6708\u6E56\u5386\u4EE3\u5BAB\u4E3B\u5BF9\u6B64\u90FD\u4E00\u65E0\u6240\u77E5\uFF0C\u5DE6\u592A\u51B2\u4E0E\u6155\u5BB9\u9F99\u4E5F\u4E0D\u4F8B\u5916\u3002</p><p>\u6B64\u65F6\u6155\u5BB9\u9F99\u53EA\u4EE5\u4E3A\u662F\u6C72\u53D6\u59B9\u59B9\u7684\u771F\u5143\uFF0C\u6D51\u4E0D\u77E5\u5C31\u5728\u9634\u706B\u5165\u4F53\u7684\u540C\u65F6\uFF0C\u81EA\u5DF1\u7684\u9633\u6C14\u4E5F\u968F\u4E4B\u4EA4\u6362\uFF0C\u6E21\u5165\u7D2B\u73AB\u4F53\u5185\u3002</p><p>\u771F\u5143\u7684\u4EA4\u6362\u53EA\u662F\u4E00\u77AC\uFF0C\u7D2B\u73AB\u7684\u6C89\u9ED8\u6FC0\u8D77\u4E86\u6155\u5BB9\u9F99\u7684\u51F6\u6027\uFF0C\u4ED6\u6402\u4F4F\u59B9\u59B9\u7684\u8170\u80A2\uFF0C\u8089\u68D2\u957F\u63D0\u731B\u9001\uFF0C\u5728\u7D27\u5BC6\u70BD\u70ED\u7684\u8089\u7A74\u5185\u7AED\u529B\u6363\u5F04\u3002\u4ED6\u6CA1\u6709\u4F7F\u7528\u4EFB\u4F55\u6FC0\u53D1\u59B9\u59B9\u5FEB\u611F\u7684\u624B\u6BB5\uFF0C\u5C31\u50CF\u5BF9\u5F85\u4EE5\u5F80\u90A3\u4E9B\u53D7\u60E9\u7F5A\u7684\u5973\u4EBA\u4E00\u6837\uFF0C\u4EC5\u4EC5\u662F\u5355\u7EAF\u7684\u5978\u6DEB\u3002</p><p>\u300C\u556A\u556A\u300D\u7684\u8089\u4F53\u649E\u51FB\u58F0\u54CD\u5F7B\u5BA4\u5185\u3002\u7D2B\u73AB\u81FB\u9996\u5782\u5728\u69BB\u4E0A\uFF0C\u767D\u5AE9\u7684\u5A07\u8EAF\u50CF\u98CE\u6D6A\u4E2D\u7684\u4E00\u6735\u7389\u5170\uFF0C\u5728\u6155\u5BB9\u9F99\u72C2\u66B4\u7684\u633A\u52A8\u4E0B\u524D\u4EF0\u540E\u5408\u3002\u5979\u53CC\u76EE\u7D27\u95ED\uFF0C\u5706\u6DA6\u7684\u4E73\u623F\u88AB\u63C9\u6413\u5F97\u53D8\u5F62\uFF0C\u5C0F\u5DE7\u7684\u4E73\u5934\u5728\u6307\u95F4\u6EDA\u6765\u6EDA\u53BB\uFF0C\u6CA1\u6709\u7247\u523B\u5B89\u5B81\u3002</p><p>\u9633\u5177\u5728\u72ED\u7A84\u7D27\u5BC6\u7684\u82B1\u5F84\u4E2D\u8D8A\u63D2\u8D8A\u5FEB\uFF0C\u53EA\u89C1\u4E00\u6839\u770B\u4E0D\u6E05\u7EC6\u8282\u7684\u9ED1\u8272\u5DE8\u68D2\u5728\u5706\u5706\u7684\u96EA\u81C0\u95F4\u5FEB\u901F\u8FDB\u51FA\u3002\u5FFD\u7136\uFF0C\u7D2B\u73AB\u8089\u7A74\u6536\u7D27\uFF0C\u82B1\u5FC3\u4E4D\u6536\u4E4D\u653E\uFF0C\u55B7\u51FA\u4E00\u80A1\u9634\u7CBE\u3002\u73AB\u7470\u4ED9\u5B50\u6BD5\u751F\u7B2C\u4E00\u6B21\u9AD8\u6F6E\uFF0C\u5C31\u662F\u5728\u8FD9\u6837\u7C97\u66B4\u7684\u5978\u6DEB\u4E2D\u83B7\u5F97\u7684\u3002</p><p>\u6155\u5BB9\u9F99\u6402\u7D27\u6218\u6817\u7684\u7389\u4F53\uFF0C\u8089\u68D2\u7B14\u76F4\u62B5\u4F4F\u82B1\u5FC3\uFF0C\u628A\u9633\u7CBE\u503E\u6CC4\u5728\u6EDA\u70EB\u7684\u8089\u7A74\u5185\u3002</p><p>\u7D2B\u73AB\u6323\u624E\u7740\u5750\u76F4\u8EAB\u4F53\uFF0C\u5979\u9762\u65E0\u8868\u60C5\uFF0C\u767D\u767D\u7684\u5C0F\u624B\u4F38\u5230\u8111\u540E\uFF0C\u5FAE\u98A4\u7740\u4E00\u7FD8\u4E00\u7FD8\uFF0C\u6162\u6162\u675F\u597D\u79C0\u53D1\u3002</p><p>\u9AD8\u6F6E\u4F3C\u4E4E\u5E76\u6CA1\u6709\u7ED9\u5979\u5E26\u6765\u66F4\u591A\u5FEB\u611F\uFF0C\u7D2B\u73AB\u7528\u4E00\u89D2\u6D01\u767D\u7684\u4E1D\u5DFE\u624E\u4F4F\u53D1\u4E1D\uFF0C\u7136\u540E\u62C9\u8FC7\u8863\u886B\uFF0C\u62AB\u5728\u80A9\u4E0A\uFF0C\u53CC\u817F\u52C9\u5F3A\u632A\u52A8\u7740\u79BB\u5F00\u7389\u69BB\u3002</p><p>\u6155\u5BB9\u9F99\u76EF\u7740\u7D2B\u73AB\u7684\u4E0B\u4F53\uFF0C\u89C1\u5979\u5750\u8D77\u540E\u5FAE\u80BF\u7684\u5AE9\u8089\u5E76\u6CA1\u6709\u9633\u7CBE\u6D41\u51FA\uFF0C\u77E5\u9053\u90A3\u7C92\u79CD\u5B50\u7075\u4E39\u63A5\u89E6\u9633\u7CBE\u540E\u5DF2\u7136\u751F\u6548\uFF0C\u5C06\u5B50\u5BAB\u53E3\u95ED\u5408\uFF0C\u4F7F\u81EA\u5DF1\u7684\u7CBE\u6DB2\u7559\u5728\u4E86\u59B9\u59B9\u4F53\u5185\u3002</p><p>\u88AB\u4ED6\u66B4\u529B\u5978\u6DEB\u8FC7\u7684\u5973\u5B50\u591A\u534A\u90FD\u4F1A\u5728\u9AD8\u6F6E\u7684\u6781\u4E50\u4E2D\u865A\u8131\uFF0C\u6709\u4E9B\u751A\u81F3\u4F1A\u5F53\u573A\u8131\u9634\u800C\u4EA1\uFF0C\u53EF\u8FD9\u4E2A\u521A\u7834\u4F53\u7684\u5C0F\u4E2B\u5934\u88AB\u4ED6\u4E00\u901A\u72E0\u64CD\uFF0C\u7ADF\u7136\u8FD8\u5750\u4E86\u8D77\u6765\u2026\u2026</p><p>\u300C\u4F60\u8981\u5E72\u4EC0\u4E48\uFF1F\u300D</p><p>\u300C\u53BB\u770B\u5A18\u2026\u2026\u300D\u7D2B\u73AB\u7684\u58F0\u97F3\u8F7B\u98D8\u98D8\uFF0C\u6CA1\u6709\u4E00\u70B9\u529B\u6C14\u3002\u5979\u6B3A\u8EAB\u4E0B\u5730\uFF0C\u521A\u521A\u7AD9\u76F4\uFF0C\u4FBF\u8F6F\u8F6F\u5012\u5728\u5730\u6BEF\u4E0A\u3002</p><p>\u9C9C\u7EA2\u7684\u7EF8\u886B\u98D8\u843D\u5728\u96EA\u767D\u7684\u957F\u7ED2\u95F4\uFF0C\u886B\u4E0B\u9732\u51FA\u7684\u7389\u624B\u9999\u8DB3\uFF0C\u4EFF\u4F5B\u7CBE\u81F4\u7684\u7389\u96D5\uFF0C\u6676\u83B9\u5254\u900F\u3002</p><p>\u6155\u5BB9\u9F99\u773C\u4E2D\u5BD2\u5982\u7384\u51B0\uFF0C\u51B7\u51B7\u770B\u7740\u660F\u53A5\u7684\u59B9\u59B9\uFF0C\u5750\u5728\u69BB\u4E0A\u7EB9\u4E1D\u4E0D\u52A8\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u4E0D\u77E5\u8FC7\u4E86\u591A\u4E45\uFF0C\u7D2B\u73AB\u60A0\u60A0\u9192\u8F6C\u3002\u4E0B\u4F53\u4F3C\u4E4E\u63D2\u7740\u4E00\u6839\u76F4\u633A\u633A\u7684\u6728\u68CD\uFF0C\u53C8\u7C97\u53C8\u786C\u3002</p><p>\u5979\u4E0D\u77E5\u9053\u90A3\u662F\u79CD\u5B50\u7075\u4E39\u4F7F\u5B50\u5BAB\u53E3\u95ED\u5408\uFF0C\u53EA\u4EE5\u4E3A\u662F\u8089\u7A74\u88AB\u5978\u6DEB\u5F97\u9EBB\u6728\u3002\u534A\u664C\u540E\uFF0C\u7D2B\u73AB\u52C9\u529B\u6491\u8D77\u8EAB\u4F53\uFF0C\u6447\u6447\u6643\u6643\u7AD9\u4E86\u8D77\u6765\u3002</p><p>\u6155\u5BB9\u9F99\u770B\u7740\u59B9\u59B9\u4E24\u817F\u65E0\u6CD5\u5408\u62E2\u5730\u6323\u624E\u7740\u8FC8\u6B65\uFF0C\u4ECD\u51B7\u51B7\u76D8\u819D\u5750\u5728\u69BB\u4E0A\uFF0C\u4E00\u8A00\u4E0D\u53D1\u3002</p><p>\u7D2B\u73AB\u6276\u5728\u95E8\u8FB9\u4F4E\u4F4E\u5598\u4E86\u51E0\u53E3\u6C14\uFF0C\u7136\u540E\u4E00\u6B65\u4E00\u632A\u5730\u8D70\u51FA\u77F3\u5BA4\uFF0C\u59CB\u7EC8\u6CA1\u6709\u56DE\u5934\u770B\u6155\u5BB9\u9F99\u4E00\u773C\u3002</p><p>\u5F85\u59B9\u59B9\u8270\u96BE\u5730\u8D70\u51FA\u81EA\u5DF1\u7684\u89C6\u91CE\uFF0C\u6155\u5BB9\u9F99\u98DE\u8EAB\u63A0\u51FA\uFF0C\u98CE\u4E00\u822C\u63A0\u5230\u8427\u4F5B\u5974\u6240\u5728\u7684\u7678\u5BA4\uFF0C\u300C\u546F\u300D\u7684\u63A8\u5F00\u95E8\u3002</p><p>\u7D2B\u73AB\u6276\u7740\u77F3\u58C1\u632A\u5230\u95E8\u8FB9\uFF0C\u5148\u7528\u8863\u8896\u64E6\u53BB\u5507\u4E0A\u7684\u8840\u8FF9\uFF0C\u8C03\u606F\u7247\u523B\uFF0C\u5F85\u529B\u6C14\u6E10\u590D\uFF0C\u624D\u6545\u4F5C\u8F7B\u677E\uFF0C\u5FAE\u7B11\u7740\u8D70\u5165\u5BA4\u5185\u3002</p><p>\u767D\u6C0F\u59D0\u59B9\u88AB\u5BAB\u4E3B\u63A8\u95E8\u7684\u58F0\u52BF\u5413\u5F97\u82B1\u5BB9\u5931\u8272\uFF0C\u8D76\u7D27\u5E76\u80A9\u8DEA\u5728\u95E8\u8FB9\uFF0C\u5F85\u7D2B\u73AB\u8FDB\u6765\uFF0C\u8FDE\u5FD9\u53E9\u5934\u5524\u9053\uFF1A\u300C\u5C11\u592B\u4EBA\u3002\u300D</p><p>\u7D2B\u73AB\u5BF9\u5979\u4EEC\u5DF2\u7ECF\u6B7B\u4E86\u5FC3\uFF0C\u5F53\u4E0B\u7406\u4E5F\u4E0D\u7406\uFF0C\u8FF3\u76F4\u8D70\u5230\u6BCD\u4EB2\u8EAB\u8FB9\uFF0C\u6D45\u7B11\u7740\u5524\u9053\uFF1A\u300C\u5A18\u3002\u300D</p><p>\u8427\u4F5B\u5974\u659C\u659C\u501A\u5728\u6795\u5934\u4E0A\uFF0C\u4E4C\u4EAE\u7684\u957F\u53D1\u68B3\u5F97\u6574\u6574\u9F50\u9F50\uFF0C\u76D8\u6210\u4E91\u9AFB\u3002\u4E0A\u8EAB\u7A7F\u7740\u534E\u7F8E\u7684\u6DE1\u9EC4\u4E1D\u8863\uFF0C\u5F69\u7EE3\u7684\u8863\u895F\u6574\u9F50\u5206\u5F00\uFF0C\u9732\u51FA\u62B9\u80F8\u4E00\u622A\u7CBE\u7F8E\u7684\u8FB9\u7F18\u3002\u8F7B\u67D4\u7684\u9526\u88AB\u8986\u5728\u8170\u95F4\uFF0C\u7F8E\u5987\u4E24\u81C2\u4F18\u96C5\u7684\u653E\u5728\u88AB\u4E0A\uFF0C\u96EA\u767D\u7684\u7389\u624B\u8F6F\u8F6F\u63E1\u5728\u4E00\u8D77\uFF0C\u7F8E\u8273\u7684\u8138\u5E9E\u5149\u5F69\u7167\u4EBA\u3002</p><p>\u7D2B\u73AB\u653E\u4E0B\u5FC3\u4E8B\uFF0C\u504E\u5728\u6BCD\u4EB2\u8EAB\u8FB9\u9AD8\u9AD8\u5174\u5174\u5730\u8BF4\u9053\uFF1A\u300C\u5A18\uFF0C\u5973\u513F\u6765\u5E2E\u4F60\u6376\u817F\uFF01\u300D</p><p>\u8427\u4F5B\u5974\u5DF2\u7ECF\u77E5\u9053\u5973\u513F\u65E0\u6059\u7684\u6D88\u606F\uFF0C\u5F53\u4E0B\u542B\u7B11\u6447\u4E86\u6447\u5934\uFF0C\u4E24\u773C\u5145\u6EE1\u601C\u610F\u5730\u6253\u91CF\u7740\u521D\u4E3A\u4EBA\u5987\u7684\u5973\u513F\u3002</p><p>\u2014\u2014\u53EF\u5973\u513F\u5AC1\u7684\u5374\u662F\u81EA\u5DF1\u7684\u513F\u5B50\uFF0C\u5979\u7684\u5AE1\u4EB2\u54E5\u54E5\u2026\u2026</p><p>\u4F5B\u7956\u6148\u60B2\uFF0C\u73AB\u513F\u662F\u65E0\u7F6A\u7684\u2026\u2026\u83E9\u8428\u4FDD\u4F51\uFF0C\u5343\u4E07\u4E0D\u8981\u8BA9\u73AB\u513F\u6000\u4E0A\u5B7D\u969C\u2026\u2026\u767E\u82B1\u89C2\u97F3\u5FC3\u91CC\u9ED8\u5FF5\u7740\uFF0C\u773C\u7736\u5FAE\u5FAE\u53D1\u7EA2\u3002</p><p>\u4ECE\u5C0F\u65F6\u5019\u8D77\uFF0C\u6BCD\u4EB2\u5C31\u662F\u8FD9\u6837\u5B89\u5B89\u9759\u9759\uFF0C\u5145\u6EE1\u8BE6\u548C\u7684\u6837\u5B50\u3002\u65E0\u8BBA\u4EC0\u4E48\u65F6\u5019\uFF0C\u90FD\u662F\u90A3\u6837\u7684\u534E\u8D35\u3001\u82AC\u82B3\u3002\u7D2B\u73AB\u628A\u8138\u653E\u5728\u6BCD\u4EB2\u817F\u4E0A\uFF0C\u5C0F\u624B\u8F7B\u8F7B\u6376\u7740\u3002</p><p>\u5FFD\u7136\u8427\u4F5B\u5974\u8EAB\u4F53\u4E00\u52A8\uFF0C\u7D2B\u73AB\u62AC\u8D77\u5934\uFF0C\u53EA\u89C1\u6BCD\u4EB2\u8138\u4E0A\u6EE1\u9762\u7126\u8651\uFF0C\u6025\u4FC3\u5730\u6447\u7740\u5934\u3002</p><p>\u300C\u5A18\uFF1F\u4F60\u600E\u4E48\u4E86\uFF1F\u300D\u7D2B\u73AB\u6709\u4E9B\u5947\u602A\u3002</p><p>\u8427\u4F5B\u5974\u5934\u6447\u7684\u6108\u53D1\u6025\u4E86\uFF0C\u5979\u7F8E\u76EE\u5149\u8292\u95EA\u52A8\uFF0C\u6731\u5507\u5FAE\u5FAE\u98A4\u6296\uFF0C\u4F3C\u4E4E\u6709\u4EC0\u4E48\u8BDD\u8981\u8BF4\u3002</p><p>\u300C\u5A18\uFF0C\u4F60\u600E\u4E48\u4E0D\u8BF4\u8BDD\uFF1F\u300D\u7D2B\u73AB\u6162\u6162\u5750\u76F4\u8EAB\u4F53\uFF0C\u5FC3\u5934\u63EA\u7D27\uFF0C\u300C\u5A18\uFF0C\u4F60\u8BF4\u8BDD\u554A\u2026\u2026\u300D</p><p>\u8427\u4F5B\u5974\u7389\u8138\u98DE\u7EA2\uFF0C\u62FC\u547D\u6447\u5934\uFF0C\u5634\u91CC\u300C\u5514\u5514\u300D\u8FDE\u58F0\uFF0C\u4F3C\u4E4E\u60F3\u8BA9\u7D2B\u73AB\u79BB\u5F00\u3002</p><p>\u7D2B\u73AB\u6025\u5FD9\u5206\u5F00\u6BCD\u4EB2\u7684\u5634\u5507\uFF0C\u4E0D\u7531\u5927\u60CA\u5931\u58F0\u3002\u8427\u4F5B\u5974\u5634\u4E2D\u7684\u94A2\u6491\u6362\u6210\u4E86\u4E00\u4E2A\u5C0F\u5C0F\u7684\u94A2\u5708\uFF0C\u7EA2\u8273\u8273\u7684\u9999\u820C\u88AB\u5361\u5728\u4E2D\u95F4\uFF0C\u53EA\u80FD\u5FAE\u5FAE\u8815\u52A8\u3002</p><p>\u300C\u5A18\uFF0C\u4F60\u7684\u820C\u5934\u600E\u4E48\u4E86\uFF1F\u300D\u7D2B\u73AB\u770B\u5230\u820C\u4E0A\u7684\u4F24\u53E3\uFF0C\u60CA\u614C\u5730\u95EE\u9053\u3002</p><p>\u5C31\u5728\u8FD9\u65F6\uFF0C\u767E\u82B1\u89C2\u97F3\u99A5\u90C1\u7684\u4F53\u9999\u4E2D\uFF0C\u7A81\u7136\u5F25\u6F2B\u8D77\u4E00\u80A1\u81ED\u5473\u3002</p><p>\u767D\u6C0F\u59D0\u59B9\u6025\u5FD9\u8D70\u5230\u5E8A\u8FB9\uFF0C\u62C9\u8D77\u8427\u4F5B\u5974\u67D4\u8F6F\u7684\u624B\u81C2\uFF0C\u6380\u5F00\u9526\u88AB\u4E00\u770B\uFF0C\u300C\u5440\uFF0C\u592B\u4EBA\u53C8\u5931\u7981\u4E86\u2026\u2026\u300D</p><p>\u7D2B\u73AB\u6709\u4E9B\u604D\u60DA\u5730\u770B\u7740\u6BCD\u4EB2\u3002\u8427\u4F5B\u5974\u4E0A\u8EAB\u8863\u9970\u6574\u9F50\uFF0C\u7F8E\u8273\u5982\u6614\uFF0C\u8170\u90E8\u4EE5\u4E0B\u5374\u8D64\u88F8\u88F8\u6CA1\u6709\u534A\u5206\u906E\u63A9\u3002</p><p>\u5F53\u767D\u6C0F\u59D0\u59B9\u62AC\u8D77\u6BCD\u4EB2\u767D\u751F\u751F\u7684\u53CC\u817F\uFF0C\u53EA\u89C1\u96EA\u81C0\u4E0B\u4E00\u7247\u80AE\u810F\uFF0C\u6ED1\u817B\u767D\u5AE9\u7684\u9999\u808C\u6CBE\u6EE1\u7A00\u8584\u7684\u9EC4\u8272\u6C61\u7269\uFF0C\u6563\u53D1\u51FA\u523A\u9F3B\u7684\u81ED\u5473\u3002\u534E\u7F8E\u4F18\u96C5\u7684\u4E0A\u8EAB\u4E0E\u5C4E\u5C3F\u6A2A\u6D41\u7684\u4E0B\u4F53\uFF0C\u5B9B\u5982\u622A\u7136\u4E0D\u540C\u7684\u4E24\u5177\u8EAB\u4F53\u3002</p>',59);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
