import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_65" tabindex="-1"><a class="header-anchor" href="#_65" aria-hidden="true">#</a> 65</h1><p>\u7D2B\u73AB\u4E00\u628A\u62E7\u4F4F\u767D\u7389\u83BA\u7684\u624B\u81C2\uFF0C\u4FCF\u76EE\u55B7\u706B\u5730\u5389\u58F0\u9053\uFF1A\u300C\u600E\u4E48\u56DE\u4E8B\uFF01\u544A\u8BC9\u6211\uFF01\u300D</p><p>\u767D\u7389\u83BA\u745F\u7F29\u4E86\u4E00\u4E0B\uFF0C\u7EC6\u58F0\u9053\uFF1A\u300C\u592B\u4EBA\u540E\u5EAD\u2026\u2026\u53D7\u4F24\u2026\u2026\u5931\u7981\u4E86\u2026\u2026\u300D</p><p>\u4E0A\u6B21\u6BCD\u4EB2\u53D7\u4F24\u90A3\u8840\u8089\u6A21\u7CCA\u7684\u60E8\u72B6\u7D2B\u73AB\u8BB0\u5FC6\u72B9\u65B0\uFF0C\u6CA1\u60F3\u8FC7\u4E0D\u8FC7\u6570\u65E5\uFF0C\u8FD9\u6DF7\u86CB\u7ADF\u7136\u53C8\u4E00\u6B21\u5978\u6DEB\u6BCD\u4EB2\u7684\u540E\u5EAD\uFF0C\u800C\u4E14\u4F24\u5F97\u5BFC\u81F4\u5931\u7981\u2014\u2014\u300C\u6155\u5BB9\u9F99\uFF01\u4F60\u8FD8\u662F\u4EBA\u4E0D\u662F\uFF01\u300D</p><p>\u6155\u5BB9\u9F99\u62B1\u80A9\u9760\u5728\u95E8\u4E0A\uFF0C\u51B7\u51B7\u9053\uFF1A\u300C\u5A18\u8981\u54AC\u820C\u81EA\u5C3D\uFF0C\u6211\u4E0D\u8FC7\u662F\u7ED9\u5979\u4E00\u4E2A\u5C0F\u5C0F\u7684\u60E9\u7F5A\u2026\u2026\u300D</p><p>\u6EE1\u4E0D\u5728\u4E4E\u7684\u795E\u6001\u4F7F\u7D2B\u73AB\u7684\u5FC3\u4E00\u70B9\u70B9\u6C89\u4E86\u4E0B\u53BB\u3002\u5979\u66FE\u7ECF\u4EE5\u4E3A\u6155\u5BB9\u9F99\u8FD8\u6709\u4E00\u70B9\u4EBA\u6027\uFF0C\u867D\u7136\u5C61\u6B21\u51FA\u8A00\u606B\u5413\uFF0C\u4F46\u5BF9\u4EB2\u5A18\u4EB2\u59B9\u6BD5\u7ADF\u8FD8\u6709\u4E00\u70B9\u70B9\u7684\u7231\u62A4\u3002\u4F46\u73B0\u5728\u770B\u6765\uFF0C\u4ED6\u6839\u672C\u5C31\u662F\u4E2A\u755C\u7272\uFF01\u81EA\u5DF1\u7A76\u7ADF\u80FD\u4E0D\u80FD\u5BF9\u4ED8\u8FD9\u4E2A\u72E0\u6BD2\u65E0\u803B\u7684\u79BD\u517D\u5462\u2026\u2026</p><p>\u67D4\u8F6F\u7684\u8EAB\u4F53\u88AB\u7FFB\u8F6C\u8FC7\u6765\u3002\u767D\u6C0F\u59D0\u59B9\u7528\u6BDB\u5DFE\u4ED4\u7EC6\u64E6\u53BB\u8427\u4F5B\u5974\u81C0\u4E0A\u7684\u6C61\u7269\uFF0C\u6700\u540E\u63B0\u5F00\u6ED1\u817B\u7684\u81C0\u8089\uFF0C\u5C06\u6BDB\u5DFE\u585E\u5165\u81C0\u7F1D\u4E2D\u7EC6\u7EC6\u62B9\u51C0\u3002\u96EA\u81C0\u5149\u6DA6\u6ED1\u817B\uFF0C\u6D3B\u8272\u751F\u9999\uFF0C\u4F46\u7C89\u7EA2\u7684\u83CA\u809B\u5374\u88C2\u5F00\u51E0\u9053\u89E6\u76EE\u60CA\u5FC3\u7684\u8840\u7EA2\u4F24\u53E3\uFF0C\u6839\u672C\u65E0\u6CD5\u5408\u62E2\u3002\u6DE1\u9EC4\u7684\u6C61\u7269\u4ECE\u4E2D\u4E0D\u65AD\u6D8C\u51FA\u3002</p><p>\u7D2B\u73AB\u5589\u5934\u6897\u4F4F\uFF0C\u770B\u7740\u8089\u56E2\u822C\u88AB\u4EBA\u7167\u6599\u7684\u6BCD\u4EB2\uFF0C\u5FC3\u91CC\u7D27\u7D27\u63EA\u6210\u4E00\u56E2\uFF0C\u53EA\u60F3\u62B1\u7740\u6BCD\u4EB2\u5927\u54ED\u4E00\u573A\u3002</p><p>\u5F85\u8427\u4F5B\u5974\u4E0B\u4F53\u62ED\u51C0\uFF0C\u6155\u5BB9\u9F99\u6DE1\u6DE1\u9053\uFF1A\u300C\u83BA\u5974\uFF0C\u7ED9\u592B\u4EBA\u5305\u5757\u5C3F\u5E03\u3002\u300D</p><p>\u4ED6\u4E0B\u5DF4\u5FAE\u5FAE\u7FD8\u8D77\uFF0C\u659C\u7768\u7740\u6EE1\u8138\u60CA\u6115\u7684\u7D2B\u73AB\uFF0C\u300C\u5BF9\uFF0C\u5C3F\u5E03\u3002\u5A18\u4EE5\u540E\u5C31\u8981\u6574\u5929\u5305\u7740\u5C3F\u5E03\u4E86\u3002\u300D</p><p>\u767D\u6C0F\u59D0\u59B9\u6258\u8D77\u5979\u7684\u8170\u8EAB\uFF0C\u628A\u4E00\u5757\u67D4\u8F6F\u7684\u68C9\u5E03\u653E\u5230\u81C0\u4E0B\u65F6\uFF0C\u8427\u4F5B\u5974\u7F9E\u5F97\u9762\u7EA2\u8033\u8D64\uFF0C\u6068\u4E0D\u5F97\u5373\u523B\u6B7B\u53BB\u3002\u5C3F\u5E03\u662F\u5757\u53C8\u5BBD\u53C8\u5927\u7684\u666E\u901A\u767D\u5E03\u3002\u4F46\u5F53\u5B83\u50CF\u5305\u88F9\u5A74\u513F\u822C\u88F9\u5728\u8273\u5987\u6210\u719F\u7684\u4E0B\u4F53\u65F6\uFF0C\u5374\u5145\u6EE1\u4E86\u6DEB\u8361\u610F\u5473\u3002</p><p>\u4E24\u5973\u628A\u5C3F\u5E03\u7EC6\u81F4\u5730\u88F9\u7D27\u624E\u597D\uFF0C\u7136\u540E\u5229\u843D\u5730\u6362\u6389\u5E8A\u5355\u88AB\u8925\uFF0C\u7ED9\u592B\u4EBA\u5FAE\u7565\u6574\u7406\u4E86\u4E00\u4E0B\u4EEA\u5BB9\uFF0C\u4FBF\u9000\u5230\u4E00\u8FB9\uFF0C\u711A\u4E0A\u9999\u3002</p><p>\u8427\u4F5B\u5974\u53C8\u53D8\u5F97\u5BB9\u5149\u7115\u53D1\uFF0C\u4EEA\u6001\u4E07\u65B9\uFF0C\u4F46\u776B\u6BDB\u95F4\u6CBE\u6EE1\u4E86\u7F9E\u803B\u7684\u6CEA\u82B1\u3002</p><p>\u7D2B\u73AB\u63E1\u7740\u6BCD\u4EB2\u6ED1\u8F6F\u7684\u624B\u638C\uFF0C\u4E00\u8FB9\u5E2E\u5979\u64E6\u53BB\u773C\u6CEA\uFF0C\u4E00\u8FB9\u52C9\u5F3A\u6324\u4E2A\u7B11\u5BB9\uFF0C\u67D4\u58F0\u5B89\u6170\u9053\uFF1A\u300C\u5A18\uFF0C\u8FC7\u4E24\u5929\u5C31\u4F1A\u597D\u4E86\u2026\u2026\u8FC7\u4E24\u5929\u5C31\u597D\u4E86\u2026\u2026\u300D</p><p>\u300C\u597D\u4E0D\u4E86\u3002\u5A18\u4E0B\u534A\u8F88\u5B50\u90FD\u79BB\u4E0D\u5F00\u5C3F\u5E03\u4E86\u3002\u300D\u6155\u5BB9\u9F99\u4E1D\u6BEB\u4E0D\u987E\u5FCC\u8427\u4F5B\u5974\u7684\u611F\u53D7\u3002</p><p>\u7D2B\u73AB\u661F\u7738\u4E00\u95EA\uFF0C\u51B7\u5389\u5730\u76EF\u4E86\u4ED6\u4E00\u773C\uFF0C\u773C\u795E\u4E2D\u6D41\u9732\u51FA\u65E0\u7A77\u6068\u610F\u3002</p><p>\u300C\u8FD9\u624D\u662F\u5979\u7684\u771F\u5B9E\u60F3\u6CD5\u5427\uFF0C\u90A3\u4E48\u6068\u6211\u3002\u300D\u6155\u5BB9\u9F99\u5FC3\u9053\uFF0C\u300C\u867D\u7136\u5979\u88C5\u5F97\u5F88\u50CF\uFF0C\u5E38\u5E38\u663E\u5F97\u53C8\u4E56\u5DE7\u53C8\u67D4\u987A\uFF0C\u4F46\u8FD9\u79CD\u4E0D\u65F6\u6D41\u9732\u7684\u6068\u610F\u624D\u662F\u771F\u6B63\u7684\u5979\u5462\u2026\u2026\u4F60\u4E3A\u4EC0\u4E48\u8981\u6068\u6211\uFF1F\u5176\u5B9E\u6211\u53EA\u8981\u4F60\u4E56\u4E56\u7ED9\u54E5\u54E5\u751F\u5B69\u5B50\uFF0C\u5B89\u5B89\u4EFD\u4EFD\u505A\u6211\u7684\u59BB\u5B50\u5C31\u597D\u4E86\u3002\u5C31\u50CF\u91D1\u4E1D\u7B3C\u7684\u91D1\u4E1D\u96C0\uFF0C\u65E0\u5FE7\u65E0\u8651\u3002\u65E0\u8BBA\u4EC0\u4E48\u73CD\u5B9D\uFF0C\u4F60\u8981\u4EC0\u4E48\u6211\u90FD\u53EF\u4EE5\u7ED9\u4F60\u3002\u4F55\u5FC5\u8981\u98DE\u51FA\u7B3C\u5B50\u5462\uFF1F\u300D</p><p>\u300C\u8D77\u6765\uFF0C\u6211\u5E26\u4F60\u53BB\u89C1\u4E00\u4E2A\u4EBA\u3002\u300D\u6155\u5BB9\u9F99\u51B7\u51B7\u9053\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u6BBF\u95E8\u6253\u5F00\u7684\u4E00\u523B\uFF0C\u523A\u76EE\u7684\u9633\u5149\u6F6E\u6C34\u822C\u6D8C\u5165\u5E7D\u6697\u7684\u795E\u6BBF\u3002\u7D2B\u73AB\u7981\u4E0D\u4F4F\u62AC\u624B\u906E\u4F4F\u773C\u775B\uFF0C\u534A\u664C\u624D\u6162\u6162\u7741\u5F00\u3002</p><p>\u56DB\u6708\u7684\u9633\u5149\u5DF2\u7ECF\u5F00\u59CB\u707C\u70ED\uFF0C\u4F46\u5BF9\u957F\u65F6\u95F4\u4E0D\u89C1\u5929\u65E5\u7684\u7D2B\u73AB\u6765\u8BF4\uFF0C\u707F\u70C2\u7684\u9633\u5149\u4EFF\u4F5B\u91D1\u9EC4\u95EA\u70C1\u7684\u6000\u62B1\uFF0C\u6E29\u6696\u800C\u53C8\u5BBD\u5E7F\u3002\u4E45\u84C4\u5FC3\u5E95\u7684\u60CA\u6050\u3001\u60E7\u6015\u3001\u59D4\u5C48\u3001\u4F24\u75DB\uFF0C\u5728\u9633\u5149\u7684\u6C90\u6D74\u4E2D\u6E10\u6E10\u5316\u5F00\uFF0C\u6D88\u6563\u3002</p><p>\u6797\u9999\u8FDC\u8D64\u88F8\u7684\u8EAB\u4F53\u4ECD\u7CFB\u5728\u680F\u6746\u8FB9\uFF0C\u5728\u832B\u7136\u4E2D\u7B49\u5F85\u53C8\u4E00\u6B21\u5978\u6DEB\u7684\u6765\u4E34\u3002\u770B\u5230\u5979\uFF0C\u6155\u5BB9\u9F99\u5C31\u50CF\u770B\u5230\u4E00\u53EA\u6254\u5728\u8DEF\u8FB9\u7684\u91CE\u72AC\u822C\uFF0C\u89C6\u800C\u4E0D\u89C1\uFF0C\u8FF3\u76F4\u8D70\u4E0B\u77F3\u9636\uFF0C\u671D\u6708\u5C9B\u4E00\u7AEF\u8D70\u53BB\u3002</p><p>\u300C\u62FF\u70B9\u6C34\u7ED9\u5979\u559D\uFF0C\u518D\u7ED9\u5979\u64E6\u64E6\u8EAB\u5B50\u3002\u300D\u7D2B\u73AB\u4E5F\u4E0D\u7406\u4F1A\u6B65\u5C65\u5306\u5306\u7684\u6155\u5BB9\u9F99\uFF0C\u5750\u5728\u9636\u65C1\u770B\u7740\u7D2B\u8863\u4F8D\u8005\u7ED9\u5E08\u59D0\u5582\u6C34\uFF0C\u64E6\u6D17\u8EAB\u4F53\u3002</p><p>\u5979\u4E0D\u77E5\u9053\u6155\u5BB9\u9F99\u8981\u5E26\u81EA\u5DF1\u53BB\u770B\u8C01\uFF0C\u66F4\u4E0D\u6015\u81EA\u5DF1\u4F1A\u8D76\u4E0D\u4E0A\u2014\u2014\u53CD\u6B63\u4ED6\u662F\u60F3\u5413\u552C\u81EA\u5DF1\u3002\u6216\u8005\u662F\u795E\u667A\u5DF2\u5931\u7684\u5927\u5E08\u59D0\uFF0C\u6216\u8005\u662F\u88AB\u836F\u7269\u523A\u6FC0\u7684\u4E09\u5E08\u59D0\u2014\u2014\u4F46\u5979\u4EEC\u90FD\u5728\u5BAB\u5185\u3002\u96BE\u9053\u662F\u5E08\u7236\uFF1F\u53EF\u5E08\u7236\u5DF2\u7ECF\u6B7B\u4E86\u2026\u2026</p><p>\u7D2B\u73AB\u72B9\u8C6B\u591A\u65F6\uFF0C\u7B49\u5AC2\u5AC2\u8EAB\u4E0A\u7684\u6C61\u6E0D\u6D17\u51C0\uFF0C\u5979\u89E3\u4E0B\u5916\u8863\u62AB\u5728\u6CBE\u6EE1\u6C34\u73E0\u7684\u6D01\u767D\u80F4\u4F53\u4E0A\uFF0C\u6DE1\u6DE1\u9053\uFF1A\u300C\u8C01\u6562\u5F04\u810F\u6211\u7684\u8863\u670D\uFF0C\u6211\u5C31\u6252\u4E86\u8C01\u7684\u76AE\u3002\u300D</p><p>\u65C1\u8FB9\u7684\u7D2B\u8863\u4F8D\u8005\u9762\u9762\u76F8\u89D1\uFF0C\u8C01\u4E5F\u4E0D\u6562\u5F00\u53E3\u3002\u867D\u7136\u73AB\u7470\u4ED9\u5B50\u4E0E\u5BAB\u4E3B\u6210\u5A5A\u4E0D\u8FC7\u4E09\u65E5\uFF0C\u4F46\u8FD9\u4E9B\u4EBA\u5DF2\u7ECF\u966A\u5979\u5728\u5C9B\u4E0A\u8F6C\u4E86\u534A\u6708\uFF0C\u8C01\u90FD\u77E5\u9053\u5C11\u592B\u4EBA\u6700\u559C\u6B22\u7684\u5C31\u662F\u60F9\u662F\u751F\u975E\uFF0C\u6CA1\u4E8B\u8FD8\u60F3\u627E\u4E9B\u4E8B\u3002\u82E5\u662F\u60F9\u607C\u4E86\u5C11\u592B\u4EBA\uFF0C\u5C31\u7B97\u6709\u5BAB\u4E3B\u7F69\u7740\u6252\u4E0D\u4E86\u4ED6\u4EEC\u7684\u76AE\uFF0C\u53EA\u6015\u4ECA\u540E\u4E5F\u96BE\u5F97\u7247\u523B\u5B89\u5B81\u3002</p><p>\u8F7B\u98CE\u5F90\u6765\uFF0C\u677E\u6D9B\u9635\u9635\uFF0C\u5BC6\u5E03\u7684\u53C2\u5929\u5DE8\u6811\u5C06\u6574\u4E2A\u5C9B\u5C7F\u7B3C\u7F69\u5728\u6D53\u6D53\u7684\u7EFF\u836B\u4E2D\u3002</p><p>\u661F\u6708\u6E56\u80FD\u4EBA\u8F88\u51FA\uFF0C\u5723\u5BAB\u7ECF\u8FC7\u8FD1\u5343\u5E74\u7684\u7ECF\u8425\u6784\u5EFA\uFF0C\u6C14\u52BF\u975E\u51E1\u3002\u7891\u523B\u9898\u548F\u904D\u5E03\u5404\u5904\uFF0C\u7CBE\u7F8E\u7684\u51C9\u4EAD\uFF0C\u5E7D\u6DF1\u7684\u56DE\u5ECA\u9519\u843D\u6709\u81F4\uFF0C\u5947\u82B1\u5F02\u5349\u968F\u5904\u53EF\u89C1\u3002\u7F13\u6B65\u5176\u4E2D\uFF0C\u5B9B\u5982\u4EBA\u95F4\u4ED9\u5883\u3002</p><p>\u4F46\u6155\u5BB9\u9F99\u5374\u6CA1\u6709\u90A3\u4EFD\u96C5\u5174\uFF0C\u661F\u6708\u6E56\u518D\u7CBE\u7F8E\u5341\u500D\uFF0C\u4E5F\u4E0D\u8FC7\u662F\u4E00\u4E2A\u5C0F\u5DE7\u7684\u9C7C\u7F38\uFF0C\u800C\u4ED6\u8981\u7684\u5219\u662F\u6574\u4E2A\u5929\u4E0B\u3002</p><p>\u6155\u5BB9\u9F99\u76EE\u4E0D\u659C\u89C6\uFF0C\u4E00\u8DEF\u7ECF\u8FC7\u4F20\u9999\u4EAD\u3001\u592A\u7384\u9601\u3001\u5E7D\u660E\u5ECA\uFF0C\u6700\u540E\u5728\u6708\u9B44\u53F0\u65C1\u505C\u4E0B\u811A\u6B65\u3002</p><p>\u82CD\u7FE0\u7684\u5DE8\u677E\u4E0B\uFF0C\u653E\u7740\u4E00\u4E2A\u9EDD\u9ED1\u7684\u94C1\u7B3C\u3002\u7B3C\u5185\u662F\u4E00\u5177\u96EA\u767D\u7684\u8EAB\u4F53\u3002\u5979\u56DB\u80A2\u88AB\u9501\u94FE\u56FA\u5B9A\u5728\u94C1\u7B3C\u7684\u56DB\u89D2\u4E0A\uFF0C\u4E24\u817F\u516B\u5B57\u5F62\u655E\u5F00\u3002\u811A\u8E1D\u88AB\u94C1\u94FE\u7A7F\u900F\uFF0C\u5C0F\u817F\u5FAE\u7FD8\u7740\u6302\u5728\u7B3C\u67B6\u4E0A\u3002</p><p>\u4ECE\u540E\u9762\u770B\u6765\uFF0C\u53EA\u770B\u5230\u4E00\u4E2A\u767D\u5AE9\u7684\u5706\u81C0\u3002\u80A5\u539A\u7684\u82B1\u74E3\u88AB\u6467\u6B8B\u5F97\u4E0D\u6210\u6A21\u6837\uFF0C\u8273\u7EA2\u7684\u5AE9\u8089\u50CF\u4E24\u7247\u88AB\u543B\u5F97\u9EBB\u6728\u7684\u7EA2\u5507\uFF0C\u8F6F\u8F6F\u5782\u5728\u80A1\u95F4\u3002\u7EA2\u80BF\u7684\u8089\u7A74\u663E\u7136\u5DF2\u88AB\u7CBE\u6DB2\u704C\u6EE1\uFF0C\u6D53\u767D\u7684\u9ECF\u6DB2\u6E7F\u6DCB\u6DCB\u6CBE\u5F97\u5230\u5904\u90FD\u662F\u3002\u7D27\u6328\u7740\u8089\u7A74\u7684\u83CA\u809B\u4E5F\u540C\u6837\u51C4\u60E8\uFF0C\u5927\u6982\u662F\u8089\u68D2\u521A\u521A\u62D4\u51FA\uFF0C\u7EA2\u7EA2\u7684\u809B\u6D1E\u8FD8\u9732\u7740\u94DC\u94B1\u5927\u5C0F\u7684\u5165\u53E3\u672A\u66FE\u5408\u62E2\u3002</p><p>\u5468\u56F4\u51E0\u540D\u5E2E\u4F17\u89C1\u5BAB\u4E3B\u4EB2\u81F3\uFF0C\u8FDE\u5FD9\u62F1\u624B\u9000\u5230\u4E00\u65C1\u3002\u6155\u5BB9\u9F99\u4E0A\u4E0B\u6253\u91CF\u4E00\u756A\uFF0C\u6EE1\u610F\u5730\u70B9\u70B9\u5934\uFF0C\u7136\u540E\u62AC\u811A\u8E29\u5728\u795E\u5C3C\u81C0\u95F4\uFF0C\u6162\u6162\u7528\u529B\u3002\u67D4\u5AE9\u7684\u8089\u82B1\u5728\u4ED6\u811A\u4E0B\u4E0D\u4F4F\u53D8\u5F62\uFF0C\u6700\u540E\u4E00\u6EDA\u4E00\u9F13\uFF0C\u8E29\u7834\u7684\u6CB9\u8102\u822C\u4ECE\u978B\u5E95\u6EA2\u51FA\uFF0C\u7EA2\u5F97\u51E0\u4E4E\u6EF4\u8840\u3002</p><p>\u906D\u5230\u8FD9\u6837\u7C97\u66B4\u7684\u8E42\u8E8F\uFF0C\u4F46\u811A\u4E0B\u7684\u5973\u4EBA\u59CB\u7EC8\u4E00\u58F0\u4E0D\u54CD\u3002</p><p>\u6155\u5BB9\u9F99\u5C06\u9774\u5E95\u7684\u7070\u5C18\u5C3D\u6570\u64E6\u5728\u5AE9\u8089\u4E0A\uFF0C\u6DE1\u6DE1\u9053\uFF1A\u300C\u8D31\u4EBA\uFF0C\u8FD8\u6CA1\u6709\u64CD\u6B7B\u4F60\uFF1F\u300D</p><p>\u5F53\u65E5\u56DB\u95EF\u795E\u6BBF\uFF0C\u6765\u53BB\u81EA\u5982\u7684\u96EA\u5CF0\u795E\u5C3C\uFF0C\u73B0\u5728\u770B\u6765\u53EA\u662F\u4E00\u4E2A\u4F9B\u4EBA\u53D1\u6CC4\u7684\u6DEB\u5177\u3002</p><p>\u5979\u8170\u540E\u538B\u7740\u4E00\u6839\u62E7\u6B7B\u7684\u94C1\u68CD\uFF0C\u628A\u5979\u7684\u5C0F\u8179\u7D27\u7D27\u6309\u5728\u7B3C\u5E95\uFF0C\u4F7F\u81C0\u90E8\u633A\u5F97\u66F4\u9AD8\u3002</p><p>\u80A9\u5934\u7684\u65E5\u6708\u94A9\u4F9D\u7136\u7A7F\u7740\u7435\u7436\u9AA8\uFF0C\u53E6\u4E00\u7AEF\u7CFB\u5728\u7B3C\u9876\uFF0C\u94A2\u94FE\u6323\u5F97\u7B14\u76F4\uFF0C\u8FEB\u4F7F\u5979\u4E0A\u8EAB\u633A\u7ACB\uFF0C\u4E24\u4E73\u60AC\u7A7A\u3002\u988C\u4E0B\u7684\u53E6\u4E00\u6839\u94C1\u68CD\u66F4\u662F\u7262\u7262\u5361\u4F4F\u4E0B\u5DF4\uFF0C\u5C06\u5979\u7389\u8138\u63A8\u6210\u5E73\u4EF0\uFF0C\u5934\u9885\u51E0\u4E4E\u89E6\u5230\u9AD8\u7FD8\u7684\u96EA\u81C0\uFF0C\u8FDE\u7259\u5173\u4E5F\u65E0\u6CD5\u54AC\u7D27\u3002\u6574\u5177\u8EAB\u4F53\u50CF\u662F\u4ECE\u8170\u4E2D\u6298\u65AD\u4E00\u822C\uFF0C\u80A5\u5AE9\u7684\u4E73\u623F\u548C\u4E0B\u4F53\u7684\u9690\u79D8\u90E8\u4F4D\u5C3D\u6570\u66B4\u9732\u5728\u5916\u3002</p><p>\u7D2B\u73AB\u8FD8\u6CA1\u6709\u6765\uFF0C\u4F46\u6155\u5BB9\u9F99\u5E76\u4E0D\u62C5\u5FC3\u3002\u5C9B\u4E0A\u6212\u5907\u68EE\u4E25\uFF0C\u4E00\u4E2A\u5185\u529F\u88AB\u5236\u7684\u5C0F\u4E2B\u5934\uFF0C\u5C31\u7B97\u63D2\u4E0A\u7FC5\u8180\u4E5F\u96BE\u98DE\u51FA\u81EA\u5DF1\u7684\u624B\u5FC3\u3002\u4ED6\u811A\u4E0B\u4E00\u7528\u529B\uFF0C\u6CB9\u8102\u822C\u5AE9\u8089\u5411\u56DB\u5468\u6ED1\u5F00\uFF0C\u9694\u7740\u978B\u5E95\u4E5F\u80FD\u611F\u53D7\u5230\u8089\u7A74\u4E2D\u7CBE\u6DB2\u7684\u6D8C\u52A8\uFF0C\u53C8\u6E7F\u53C8\u9ECF\u3002</p><p>\u96EA\u5CF0\u795E\u5C3C\u521A\u521A\u7A81\u7834\u81F3\u51E4\u51F0\u5B9D\u5178\u7B2C\u516B\u5C42\uFF0C\u672A\u7B49\u771F\u6C14\u5B8C\u5168\u7A33\u5B9A\u4FBF\u7ACB\u5373\u4E0E\u5F3A\u654C\u52A8\u624B\uFF0C\u7ED3\u679C\u88AB\u6155\u5BB9\u9F99\u7528\u795E\u5175\u5077\u88AD\u5F97\u624B\u3002\u590D\u4EC7\u96EA\u803B\u7684\u613F\u671B\u4E0D\u4EC5\u672A\u80FD\u5B9E\u73B0\uFF0C\u53CD\u800C\u53D7\u5230\u66F4\u5927\u7684\u6C61\u8FB1\uFF0C\u50CF\u5668\u5177\u822C\u6254\u5728\u6811\u4E0B\u4F9B\u4EBA\u6DEB\u73A9\u3002\u5FC3\u9AD8\u6C14\u50B2\u7684\u795E\u5C3C\u53C8\u6068\u53C8\u6094\u53C8\u75DB\uFF0C\u65F6\u65F6\u523B\u523B\u90FD\u5728\u7B49\u5F85\u673A\u4F1A\uFF0C\u7B49\u5F85\u65E5\u6708\u94A9\u677E\u5F00\u7684\u4E00\u523B\u3002</p><p>\u6155\u5BB9\u9F99\u628A\u96EA\u5CF0\u795E\u5C3C\u7684\u7389\u6237\u5F53\u6210\u978B\u64E6\uFF0C\u6162\u6162\u64E6\u5B8C\u53F3\u811A\uFF0C\u53C8\u628A\u5DE6\u811A\u653E\u5728\u4E0A\u9762\u3002\u7B49\u4ED6\u653E\u4E0B\u811A\uFF0C\u795E\u5C3C\u7684\u96EA\u81C0\u4E2D\u4E00\u7247\u72FC\u85C9\uFF0C\u7070\u5C18\u6DF7\u7740\u7CBE\u6DB2\u7C98\u4E4E\u4E4E\u810F\u516E\u516E\u6CBE\u5728\u80A1\u95F4\uFF0C\u8E29\u6241\u7684\u82B1\u74E3\u7FFB\u5F00\u624B\u638C\u5927\u4E00\u7247\u88AB\u73B7\u6C61\u7684\u6BB7\u7EA2\u3002</p><p>\u6155\u5BB9\u9F99\u62CD\u4E86\u62CD\u7B3C\u5B50\uFF0C\u7B11\u541F\u541F\u628A\u5F04\u7740\u795E\u5C3C\u7684\u53CC\u4E73\uFF0C\u300C\u8D3C\u5C3C\u8FD9\u5BF9\u5976\u5B50\u7740\u5B9E\u53EF\u89C2\u2026\u2026\u300D</p><p>\u65C1\u8FB9\u7684\u5E2E\u4F17\u8FDE\u58F0\u8FCE\u5408\uFF0C\u6C61\u8A00\u79FD\u8BED\uFF0C\u628A\u96EA\u5CF0\u795E\u5C3C\u8BF4\u6210\u5929\u4E0B\u7B2C\u4E00\u6DEB\u5987\uFF0C\u4E0D\u77E5\u5403\u4E86\u591A\u5C11\u7537\u4EBA\u7684\u7CBE\u6DB2\uFF0C\u624D\u517B\u51FA\u8FD9\u4E48\u5927\u4E00\u5BF9\u8C6A\u4E73\u3002\u5C24\u5176\u662F\u90A3\u4E2A\u9A9A\u5C44\uFF0C\u6BD4\u7A91\u5B50\u91CC\u6700\u4E0B\u8D31\u7684\u5A4A\u5B50\u8FD8\u5927\uFF0C\u6050\u6015\u64CD\u8FC7\u5979\u7684\u7537\u4EBA\u6CA1\u6709\u4E00\u4E07\u4E5F\u6709\u516B\u5343\u2026\u2026</p><p>\u96EA\u5CF0\u795E\u5C3C\u773C\u4E0A\u3001\u9F3B\u4E0A\u3001\u988A\u4E0A\uFF0C\u5507\u4E0A\u6CBE\u6EE1\u6D53\u7A20\u7684\u7CBE\u6DB2\uFF0C\u547C\u5438\u95F4\u6D4A\u7CBE\u4ECE\u9F3B\u7FFC\u6ED1\u4E0B\uFF0C\u4ECE\u6676\u83B9\u7684\u8033\u6735\u8FB9\u7F18\uFF0C\u4E1D\u4E1D\u7F15\u7F15\u5782\u4E0B\u3002\u5979\u53CC\u76EE\u7D27\u95ED\uFF0C\u5BF9\u5468\u56F4\u7684\u5632\u7B11\u58F0\u4E0D\u95FB\u4E0D\u95EE\u3002</p><p>\u88AB\u56FA\u5B9A\u6210\u803B\u8FB1\u59FF\u52BF\u7684\u8EAB\u4F53\u65E9\u5DF2\u50F5\u786C\u5F97\u9EBB\u6728\uFF0C\u8FDE\u654F\u611F\u7684\u4E0B\u4F53\u4E5F\u50CF\u8499\u4E86\u4E00\u5C42\u539A\u539A\u7684\u68C9\u82B1\uFF0C\u65E0\u8BBA\u662F\u75BC\u75DB\u8FD8\u662F\u5FEB\u611F\uFF0C\u90FD\u50CF\u8FDC\u65B9\u7684\u5C71\u6797\u822C\u6726\u6726\u80E7\u80E7\u3002\u53EA\u6709\u80A9\u5934\u65E5\u6708\u94A9\u7684\u9F7F\u72B6\u7A81\u8D77\uFF0C\u4E00\u7C92\u7C92\u6E05\u6670\u53EF\u8FA8\u3002</p><p>\u5FFD\u7136\u8138\u4E0A\u4E00\u70ED\uFF0C\u4E00\u9053\u70ED\u817E\u817E\u7684\u6C34\u6D41\u4ECE\u9F3B\u5B54\u76F4\u51B2\u800C\u5165\uFF0C\u545B\u5230\u80BA\u4E2D\u3002\u96EA\u5CF0\u795E\u5C3C\u8270\u96BE\u5730\u5F20\u5F00\u5634\uFF0C\u5267\u70C8\u5730\u54B3\u55FD\u8D77\u6765\u3002</p><p>\u6DE1\u9EC4\u7684\u5C3F\u6DB2\u51B2\u5F00\u8138\u4E0A\u7684\u9633\u7CBE\uFF0C\u5149\u6DA6\u7684\u808C\u80A4\u3001\u9C9C\u7EA2\u7684\u5507\u74E3\u4E00\u70B9\u70B9\u6E05\u6670\u8D77\u6765\u3002\u6155\u5BB9\u9F99\u6258\u7740\u9633\u5177\uFF0C\u4E00\u4F1A\u513F\u5BF9\u7740\u795E\u5C3C\u7684\u9F3B\u5B54\uFF0C\u4E00\u4F1A\u513F\u5BF9\u7740\u5979\u7684\u5C0F\u5634\uFF0C\u4E00\u6CE1\u5C3F\u6492\u5F97\u75DB\u5FEB\u6DCB\u6F13\u3002</p><p>\u98DE\u6E85\u7684\u6DB2\u4F53\u6E10\u6E10\u6563\u5F00\uFF0C\u795E\u5C3C\u5598\u54B3\u8FDE\u58F0\uFF0C\u9999\u820C\u5728\u5507\u95F4\u62FC\u547D\u5C48\u4F38\uFF0C\u5410\u51FA\u53E3\u4E2D\u9A9A\u81ED\u7684\u5C3F\u6DB2\u3002</p><p>\u6155\u5BB9\u9F99\u5BF9\u5979\u8EAB\u4E0A\u7684\u7EDD\u4E16\u529F\u529B\u5782\u6D8E\u4E09\u5C3A\uFF0C\u5355\u662F\u65A9\u6740\u6731\u90AA\u9752\u6811\u3001\u5C48\u82E6\u85E4\u4E24\u4EBA\u7684\u58F0\u52BF\uFF0C\u4FBF\u770B\u5F97\u51FA\u795E\u5C3C\u7684\u529F\u529B\u5C24\u5728\u9634\u5BAB\u4E3B\u4E4B\u4E0A\u3002\u56E0\u6B64\u867D\u7136\u7559\u7740\u5979\u7684\u6027\u547D\u5371\u9669\u4E4B\u6781\uFF0C\u5374\u53C8\u4E0D\u820D\u5F97\u628A\u5979\u968F\u4FBF\u5F04\u6B7B\uFF0C\u767D\u767D\u6D6A\u8D39\u4E86\u5979\u7684\u5927\u597D\u771F\u5143\u3002</p><p>\u6155\u5BB9\u9F99\u9E70\u96BC\u822C\u7684\u76EE\u5149\u5728\u96EA\u5CF0\u795E\u5C3C\u8D64\u88F8\u7684\u8EAB\u4F53\u4E0A\u6765\u56DE\u5BA1\u89C6\uFF0C\u60F3\u627E\u51FA\u4E00\u4E2A\u6C72\u53D6\u771F\u5143\u7684\u529E\u6CD5\u3002</p><p>\u5F53\u76EE\u5149\u626B\u5230\u795E\u5C3C\u80A9\u5934\uFF0C\u6155\u5BB9\u9F99\u4E00\u4E0B\u505C\u4F4F\u4E86\u3002</p><p>\u7389\u767D\u7684\u5F2F\u94A9\u4ECE\u9501\u9AA8\u4E0B\u7A7F\u5165\uFF0C\u94A9\u4F53\u5361\u5728\u7435\u7436\u9AA8\u95F4\uFF0C\u80A9\u540E\u9732\u51FA\u5706\u5F27\u72B6\u7684\u94A9\u5C16\u3002\u56E0\u4E3A\u6015\u795E\u5C3C\u5931\u8840\u8FC7\u591A\u800C\u6B7B\uFF0C\u4F24\u53E3\u5DF2\u7ECF\u6D82\u4E86\u6B62\u8840\u7684\u836F\u7269\uFF0C\u53EA\u5269\u4E24\u4E2A\u8D2F\u7A7F\u7684\u4E0D\u89C4\u5219\u7684\u8840\u6D1E\u3002\u900F\u8FC7\u8840\u8FF9\u548C\u7FFB\u5377\u7684\u7EA2\u8089\uFF0C\u9690\u7EA6\u80FD\u770B\u5230\u9AA8\u9ABC\u3002</p><p>\u4E00\u5207\u5E76\u65E0\u5F02\u6837\u3002</p><p>\u4F46\u6155\u5BB9\u9F99\u5FC3\u91CC\u5374\u6709\u79CD\u4E0D\u7965\u7684\u9884\u611F\u3002</p>',54);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
