import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_66" tabindex="-1"><a class="header-anchor" href="#_66" aria-hidden="true">#</a> 66</h1><p>\u96EA\u5CF0\u795E\u5C3C\u7684\u5598\u54B3\u6E10\u6E10\u5E73\u606F\uFF0C\u53EA\u6709\u767D\u817B\u7684\u80A5\u4E73\u8FD8\u4F59\u6CE2\u672A\u6B62\uFF0C\u5728\u80F8\u524D\u6C89\u7538\u7538\u56DB\u4E0B\u8F7B\u6643\u3002</p><p>\u6155\u5BB9\u9F99\u76EE\u4E0D\u8F6C\u775B\u5730\u76EF\u7740\u65E5\u6708\u94A9\uFF0C\u4E0D\u7965\u7684\u9884\u611F\u8D8A\u6765\u8D8A\u5F3A\u70C8\u3002\u7435\u7436\u9AA8\u3001\u811A\u7B4B\u90FD\u5DF2\u88AB\u7A7F\uFF0C\u8EAB\u4F53\u53C8\u88AB\u56FA\u5B9A\u5728\u94C1\u7B3C\u5185\uFF0C\u5979\u8FD8\u80FD\u65BD\u51FA\u4EC0\u4E48\u624B\u6BB5\uFF1F</p><p>\u5FFD\u7136\u95F4\u8111\u4E2D\u4E00\u95EA\uFF0C\u6155\u5BB9\u9F99\u6697\u6697\u5012\u62BD\u53E3\u51C9\u6C14\u3002\u8FD9\u8D3C\u5C3C\u7ADF\u5982\u6B64\u5389\u5BB3\u2026\u2026</p><p>\u95EE\u9898\u5C31\u51FA\u5728\u90A3\u5BF9\u65E5\u6708\u94A9\u4E0A\u3002</p><p>\u5F53\u65F6\u7684\u60C5\u666F\u4ED6\u8FD8\u8BB0\u5F97\u6E05\u6E05\u695A\u695A\u3002\u81EA\u5DF1\u5077\u88AD\u5F97\u624B\uFF0C\u65E5\u6708\u94A9\u7A7F\u8FC7\u795E\u5C3C\u7684\u7435\u7436\u9AA8\uFF0C\u7834\u53BB\u4E86\u5979\u7684\u771F\u6C14\u3002\u8E0F\u5728\u904D\u5E03\u8840\u8FF9\u788E\u8089\u7684\u795E\u6BBF\u5185\uFF0C\u81EA\u5DF1\u5FC3\u91CC\u546F\u546F\u76F4\u8DF3\u3002\u56E0\u4E3A\u9690\u60E7\uFF0C\u4ED6\u4E24\u6B21\u6536\u7D27\u65E5\u6708\u94A9\uFF0C\u4F7F\u94A9\u4F53\u7A7F\u900F\u4E86\u795E\u5C3C\u7684\u80A9\u8180\u3002</p><p>\u5F53\u65F6\u4E24\u94A9\u90FD\u5B8C\u5168\u6CA1\u5165\u808C\u80A4\u3002\u4F46\u73B0\u5728\u6708\u94A9\u5374\u9732\u51FA\u4E00\u6307\u6709\u4F59\u2026\u2026\u65E5\u6708\u94A9\u94A9\u5185\u904D\u5E03\u9897\u7C92\u72B6\u7A81\u8D77\uFF0C\u4E00\u65E6\u94A9\u8FDB\u7435\u7436\u9AA8\uFF0C\u65C1\u4EBA\u5C31\u662F\u7528\u624B\u62D4\u4E5F\u9700\u51E0\u5206\u529B\u6C14\u3002\u96EA\u5CF0\u795E\u5C3C\u7ADF\u7136\u5355\u9760\u6563\u4E71\u7684\u771F\u6C14\uFF0C\u5C06\u6708\u94A9\u903C\u51FA\u4E94\u5206\u6709\u4F59\uFF0C\u8FD9\u4EFD\u529F\u529B\u771F\u662F\u60CA\u4E16\u9A87\u4FD7\uFF01</p><p>\u96EA\u5CF0\u795E\u5C3C\u7389\u5BB9\u65E0\u6CE2\u3002\u4E60\u60EF\u4E86\u65E5\u6708\u795E\u94A9\u4E24\u80A1\u4E0D\u540C\u7684\u6C14\u6D41\u4E4B\u540E\uFF0C\u5979\u8BBE\u6CD5\u501F\u7528\u65E5\u94A9\u7684\u9633\u6C14\uFF0C\u4E0E\u81EA\u5DF1\u70BD\u70ED\u7684\u771F\u6C14\u5408\u529B\uFF0C\u903C\u51FA\u6708\u94A9\u3002\u5176\u95F4\u690E\u5FC3\u523B\u9AA8\u7684\u82E6\u695A\uFF0C\u51E0\u4E4E\u63A9\u76D6\u4E86\u81EA\u5DF1\u88AB\u8F6E\u5978\u548C\u7F9E\u8FB1\u7684\u75DB\u803B\u3002\u6B64\u65F6\u5979\u6536\u655B\u5FC3\u795E\uFF0C\u9759\u7B49\u591C\u665A\u7684\u5230\u6765\u3002\u4E00\u591C\u7684\u65F6\u95F4\uFF0C\u8DB3\u4EE5\u4F7F\u5979\u903C\u51FA\u6708\u94A9\uFF0C\u7834\u7B3C\u800C\u51FA\uFF0C\u5C4A\u65F6\u661F\u6708\u6E56\u5C06\u4E0D\u518D\u6709\u4E00\u4E2A\u6D3B\u53E3\uFF01</p><p>\u300C\u683C\u300D\u7684\u4E00\u58F0\u8F7B\u54CD\uFF0C\u94A9\u8EAB\u7684\u7A81\u8D77\u5212\u8FC7\u7435\u7436\u9AA8\uFF0C\u5411\u5916\u52A8\u4E86\u4E9B\u8BB8\u3002\u96EA\u5CF0\u795E\u5C3C\u82E6\u5FCD\u5267\u75DB\uFF0C\u86FE\u7709\u98A6\u7D27\u3002</p><p>\u6155\u5BB9\u9F99\u62E7\u7740\u6708\u94A9\u7F13\u7F13\u62D4\u51FA\uFF0C\u4ED4\u7EC6\u542C\u7740\u94A9\u8EAB\u5728\u7435\u7436\u9AA8\u78E8\u64E6\u7684\u8F7B\u54CD\u3002\u624B\u8155\u8F7B\u6446\uFF0C\u6708\u94A9\u522E\u5728\u9AA8\u4E0A\u7684\u6C99\u6C99\u58F0\u6E05\u6670\u53EF\u95FB\u3002</p><p>\u96EA\u5CF0\u795E\u5C3C\u6CBE\u6EE1\u5C3F\u6DB2\u3001\u7CBE\u6DB2\u7684\u7EA2\u5507\u98A4\u6296\u7740\u54AC\u7D27\uFF0C\u989D\u4E0A\u5192\u51FA\u4E00\u5C42\u51B7\u6C57\u3002\u5267\u70C8\u7684\u9178\u75DB\u900F\u5165\u9AA8\u9AD3\uFF0C\u5C06\u51DD\u805A\u7684\u771F\u6C14\u6405\u5F97\u56DB\u6563\u5D29\u79BB\u3002\u5728\u5267\u75DB\u4E2D\uFF0C\u4E00\u80A1\u5F7B\u9AA8\u7684\u51C9\u610F\u76F4\u5165\u5FC3\u5E95\uFF0C\u5979\u77E5\u9053\uFF0C\u81EA\u5DF1\u903C\u51FA\u65E5\u6708\u94A9\u7684\u4E3E\u52A8\u5DF2\u7ECF\u88AB\u4EBA\u53D1\u73B0\u3002</p><p>\u6155\u5BB9\u9F99\u5FAE\u7B11\u7740\u4E00\u63A8\uFF0C\u5DF2\u7ECF\u7652\u5408\u7684\u4F24\u53E3\u7ACB\u5373\u9C9C\u8840\u8FF8\u6D8C\uFF0C\u6253\u6E7F\u4E86\u4ED6\u7684\u624B\u6307\u3002\u96EA\u5CF0\u795E\u5C3C\u7EC6\u5BC6\u7684\u94F6\u7259\u54AC\u7834\u4E86\u6731\u5507\uFF0C\u88AB\u8FEB\u4EF0\u7740\u8138\u7684\u5979\u770B\u4E0D\u5230\u65E5\u6708\u94A9\u4ECE\u80A9\u5934\u7A7F\u51FA\u7684\u60E8\u72B6\uFF0C\u4F46\u5979\u80FD\u611F\u89C9\u5230\u4E24\u6839\u6076\u6BD2\u7684\u624B\u6307\u63D2\u8FDB\u4F24\u53E3\u4E2D\uFF0C\u62D4\u5F04\u7740\u81EA\u5DF1\u7684\u7435\u7436\u9AA8\u3002\u90A3\u611F\u89C9\u5982\u6B64\u6E05\u6670\uFF0C\u5979\u751A\u81F3\u80FD\u611F\u89C9\u5230\u6307\u5C16\u7684\u7EB9\u8DEF\uFF0C\u8FD8\u6709\u94A2\u94FE\u7684\u51B0\u51B7\u3002</p><p>\u5C0F\u4E2B\u5934\u7ADF\u7136\u6562\u62D6\u8FD9\u4E48\u4E45\uFF1F\u6155\u5BB9\u9F99\u4F38\u51FA\u6CBE\u6EE1\u8840\u8FF9\u7684\u624B\u6307\u6311\u5F04\u7740\u795E\u5C3C\u7684\u4E73\u5934\u3002\u5373\u4F7F\u5728\u8FD9\u6837\u7684\u5267\u75DB\u4E2D\uFF0C\u654F\u611F\u7684\u4E73\u5934\u8FD8\u662F\u4F20\u6765\u4E00\u9635\u9165\u75D2\u7684\u5FEB\u611F\uFF0C\u987F\u65F6\u786C\u4E86\u8D77\u6765\u3002</p><p>\u4E0D\u6765\u4E5F\u7F62\uFF0C\u53CD\u6B63\u6709\u7684\u662F\u673A\u4F1A\uFF01\u6155\u5BB9\u9F99\u62AC\u624B\u542B\u6012\u4E00\u626F\uFF0C\u94A2\u94FE\u5982\u540C\u95EA\u4EAE\u7684\u6BD2\u86C7\u822C\u94BB\u5165\u4F24\u53E3\u3002\u96EA\u5CF0\u795E\u5C3C\u7C89\u80CC\u4E0A\u8840\u8089\u98DE\u6E85\uFF0C\u94A2\u73AF\u4E00\u8282\u4E00\u8282\u76F4\u63A5\u91CD\u91CD\u6572\u51FB\u5728\u9AA8\u9ABC\u4E0A\uFF0C\u56DB\u80A2\u767E\u9AB8\u540C\u65F6\u9707\u98A4\u8D77\u6765\u3002</p><p>\u6EF4\u8840\u7684\u94A2\u94FE\u4ECE\u80A9\u5934\u659C\u659C\u7A7F\u8FC7\uFF0C\u5DE6\u53F3\u5206\u5F00\uFF0C\u94A9\u4F4F\u795E\u5C3C\u811A\u8E1D\u88F8\u9732\u7684\u7B4B\u8171\u3002\u5979\u7389\u8138\u767D\u5F97\u8FD1\u4E4E\u900F\u660E\uFF0C\u808C\u80A4\u50CF\u5F20\u6EE1\u7684\u5F13\u5F26\u5B8C\u5168\u7EF7\u7D27\uFF0C\u53E3\u9F3B\u95F4\u547C\u5438\u505C\u987F\uFF0C\u96EA\u5CF0\u795E\u5C3C\u7AED\u5C3D\u5168\u529B\u62B5\u6297\u8FD9\u4EE4\u4EBA\u5D29\u6E83\u7684\u5267\u75DB\u3002</p><p>\u6155\u5BB9\u9F99\u5634\u91CC\u6709\u4E9B\u53D1\u5E72\uFF0C\u5728\u8FD9\u6837\u7684\u6298\u78E8\u4E0B\uFF0C\u795E\u5C3C\u7ADF\u7136\u8FD8\u80FD\u5F3A\u6491\u7740\u6CA1\u6709\u660F\u8FF7\u2026\u2026</p><p>\u5979\u7A76\u7ADF\u80FD\u62B5\u6297\u5230\u4EC0\u4E48\u5730\u6B65\uFF1F\u6155\u5BB9\u9F99\u52FE\u4F4F\u811A\u7B4B\u6382\u4E86\u6382\u2014\u2014\u8981\u4E0D\u8981\u4E5F\u62BD\u53BB\u5979\u56DB\u80A2\u7684\u7B4B\u8171\u5462\uFF1F</p><p>\u5C31\u5728\u8FD9\u65F6\uFF0C\u96EA\u5CF0\u795E\u5C3C\u9AD8\u4E3E\u7684\u96EA\u81C0\u95F4\u5AE9\u8089\u4E00\u9635\u6536\u7F29\uFF0C\u98A4\u6296\u7740\u6DCC\u51FA\u4E00\u80A1\u6DEB\u6C34\u3002\u6155\u5BB9\u9F99\u773C\u4E2D\u4E00\u4EAE\uFF0C\u7ACB\u5373\u4ECE\u6000\u91CC\u638F\u51FA\u711A\u60C5\u818F\uFF0C\u5168\u90E8\u62B9\u5728\u795E\u5C3C\u80AE\u810F\u7684\u4E0B\u4F53\uFF0C\u8FDE\u83CA\u809B\u4E5F\u4E0D\u653E\u8FC7\u3002\u7136\u540E\u6298\u4E0B\u677E\u679D\uFF0C\u5C06\u78A7\u7EFF\u7684\u818F\u836F\u9001\u5165\u8089\u7A74\u6DF1\u5904\u3002</p><p>\u7C97\u7CD9\u7684\u6811\u76AE\u6BEB\u4E0D\u601C\u60DC\u5730\u63D2\u8FDB\u8089\u7A74\uFF0C\u5C06\u5A07\u5AE9\u7684\u8089\u58C1\u522E\u51FA\u65E0\u6570\u8840\u75D5\uFF0C\u540C\u65F6\u4F7F\u711A\u60C5\u818F\u878D\u5165\u8840\u8089\u3002\u6155\u5BB9\u9F99\u624B\u8155\u4E00\u4E3E\uFF0C\u534A\u5C3A\u957F\u77ED\u4E24\u5BF8\u7C97\u7EC6\u7684\u677E\u679D\u72E0\u72E0\u6345\u5165\u795E\u5C3C\u4F53\u5185\uFF0C\u7FFB\u5377\u7684\u82B1\u74E3\u88AB\u6324\u5F97\u6536\u62E2\uFF0C\u7EA2\u5507\u822C\u542B\u7D27\u6811\u679D\u3002</p><p>\u94A2\u94FE\u7A7F\u80A9\u800C\u8FC7\u7684\u90A3\u4E00\u523B\uFF0C\u96EA\u5CF0\u795E\u5C3C\u5DF2\u7ECF\u77E5\u9053\u81EA\u5DF1\u518D\u65E0\u529B\u6323\u8131\u675F\u7F1A\u3002\u6495\u5FC3\u88C2\u80BA\u7684\u7EDD\u671B\u4F7F\u5979\u53D1\u51FA\u4E00\u58F0\u51C4\u5389\u800C\u53C8\u5145\u6EE1\u7684\u7EB3\u558A\uFF0C\u7389\u4F53\u62FC\u547D\u6323\u52A8\u8D77\u6765\u3002\u80A5\u767D\u7684\u5706\u81C0\u4E2D\uFF0C\u4E0A\u4E0B\u77ED\u77ED\u4E24\u622A\u9EDD\u9ED1\u7684\u7C97\u679D\u4E0A\u4E0B\u8D77\u843D\uFF0C\u5982\u540C\u4E00\u80A1\u65E0\u6CD5\u6309\u4F4F\u7684\u60B2\u6124\u3002</p><p>\u94C1\u7B3C\u5728\u7F8E\u5987\u7684\u6323\u624E\u4E0B\u300C\u94FF\u9535\u300D\u4E71\u54CD\uFF0C\u96EA\u767D\u7684\u8089\u4F53\u5B9B\u5982\u8D70\u5165\u7EDD\u8DEF\u7684\u767D\u8272\u731B\u864E\uFF0C\u62FC\u547D\u649E\u51FB\u7740\u575A\u56FA\u7684\u94C1\u6761\u3002</p><p>\u56F4\u89C2\u7684\u5E2E\u4F17\u76F8\u987E\u5931\u8272\uFF0C\u5FC3\u5934\u7981\u4E0D\u4F4F\u63A0\u8FC7\u4E00\u9635\u5BD2\u610F\u2026\u2026</p><p>\u300C\u5361\u300D\u7684\u4E00\u58F0\u8106\u54CD\uFF0C\u4F20\u904D\u5BC6\u6797\uFF0C\u8FDE\u7FFB\u6EDA\u7684\u677E\u6D9B\u4E5F\u5B89\u9759\u4E0B\u6765\uFF0C\u56DB\u5468\u4E00\u7247\u6B7B\u5BC2\u3002</p><p>\u6155\u5BB9\u9F99\u7F13\u7F13\u677E\u5F00\u624B\u6307\uFF0C\u795E\u5C3C\u5149\u6DA6\u7684\u7389\u8098\u4E0A\u7559\u4E0B\u4E24\u4E2A\u82CD\u767D\u7684\u6307\u5370\u3002\u4E0D\u591A\u65F6\uFF0C\u6307\u5370\u7A81\u7136\u53D8\u5F97\u53D1\u7EA2\uFF0C\u4F3C\u4E4E\u88AB\u9C9C\u8840\u5145\u6EE1\u3002</p><p>\u795E\u5C3C\u7684\u6731\u5507\u4ECD\u7136\u5450\u558A\u822C\u5706\u5F20\u7740\uFF0C\u4F46\u5374\u53D1\u4E0D\u51FA\u4E00\u4E1D\u58F0\u97F3\u3002\u5979\u8EAB\u5B50\u4E00\u4FA7\uFF0C\u8098\u7AEF\u4F3C\u4E4E\u6D88\u5931\u822C\uFF0C\u53D8\u6210\u88AB\u808C\u80A4\u5305\u88F9\u7684\u788E\u7247\uFF0C\u8F6F\u8F6F\u652F\u5728\u7B3C\u5E95\u3002</p><p>\u6155\u5BB9\u9F99\u5E73\u9759\u5730\u4F38\u51FA\u624B\u638C\uFF0C\u63E1\u4F4F\u795E\u5C3C\u5706\u6DA6\u7684\u819D\u76D6\uFF0C\u6162\u6162\u5408\u62E2\u4E94\u6307\u3002\u9AA8\u9ABC\u5728\u4ED6\u592A\u4E00\u7ECF\u7684\u771F\u6C14\u4E0B\uFF0C\u4EFF\u4F5B\u7C89\u56E2\u822C\u8106\u5F31\uFF0C\u6CA1\u6709\u534A\u5206\u62B5\u6297\u5730\u4E4D\u7136\u7C89\u788E\u3002</p><p>\u300C\u554A\u2014\u2014\u300D\u5145\u6EE1\u60CA\u6050\u7684\u5C16\u53EB\u4ECE\u80CC\u540E\u54CD\u8D77\uFF0C\u6155\u5BB9\u7D2B\u73AB\u8DCC\u8DCC\u649E\u649E\u5730\u98DE\u5954\u8FC7\u6765\u3002\u5954\u5230\u795E\u5C3C\u8EAB\u8FB9\uFF0C\u5979\u4E24\u817F\u4E00\u8F6F\uFF0C\u65E0\u529B\u6251\u5012\u5728\u94C1\u7B3C\u4E0A\u3002</p><p>\u5979\u65E9\u5DF2\u719F\u6089\u4E86\u5C9B\u4E0A\u7684\u9053\u8DEF\uFF0C\u5FD6\u6043\u7740\u5E76\u6CA1\u6709\u4EC0\u4E48\u5927\u4E8B\uFF0C\u4FBF\u4E00\u8DEF\u60A0\u54C9\u60A0\u54C9\u5730\u8885\u8885\u884C\u6765\u3002\u4E00\u8FB9\u51ED\u8FD0\u6C14\u778E\u8F6C\uFF0C\u4E00\u8FB9\u8D4F\u73A9\u98CE\u666F\uFF0C\u6CA1\u60F3\u5230\u5374\u770B\u5230\u5E08\u7236\u88AB\u751F\u751F\u634F\u788E\u9AA8\u9ABC\u7684\u4E00\u5E55\u3002</p><p>\u82CD\u7FE0\u6B32\u6EF4\u7684\u677E\u67CF\u4E0B\uFF0C\u4E00\u5177\u51B7\u8273\u7684\u5973\u4F53\u5728\u7A84\u5C0F\u7684\u94C1\u7B3C\u5185\u62AC\u5934\u633A\u81C0\uFF0C\u6446\u51FA\u7F9E\u803B\u7684\u6DEB\u79FD\u59FF\u6001\u3002\u4E00\u4E2A\u660E\u8273\u7684\u7EA2\u8863\u5C11\u5973\u6123\u6123\u62B1\u7740\u94C1\u7B3C\uFF0C\u795E\u60C5\u5446\u6EDE\u3002</p><p>\u51B7\u6C57\u6DF7\u7740\u9C9C\u8840\u6DCC\u904D\u7389\u4F53\uFF0C\u96EA\u5CF0\u795E\u5C3C\u7259\u5173\u4E0D\u4F4F\u8F7B\u54CD\uFF0C\u98A4\u6296\u7740\u8BF4\u9053\uFF1A\u300C\u6211\u96EA\u5CF0\u5316\u505A\u5389\u9B3C\u4E5F\u8981\u53D6\u4F60\u6027\u547D\uFF01\u300D</p><p>\u300C\u5E08\u592A\u52A8\u4E86\u55D4\u5FF5\uFF0C\u5C0F\u5FC3\u5815\u5165\u963F\u9F3B\u5730\u72F1\uFF0C\u6C38\u4E16\u4E0D\u5F97\u8D85\u751F\u3002\u300D\u6155\u5BB9\u9F99\u6DE1\u6DE1\u9053\u3002</p><p>\u4ED6\u634F\u4F4F\u795E\u5C3C\u53E6\u4E00\u53EA\u5B8C\u597D\u7684\u8098\u5C16\uFF0C\u5FFD\u7136\u839E\u5C14\u4E00\u7B11\uFF0C\u300C\u5E08\u592A\uFF0C\u4F60\u89C9\u5F97\u662F\u963F\u9F3B\u5730\u72F1\u597D\u5462\uFF0C\u8FD8\u662F\u5728\u8FD9\u91CC\u88AB\u4EBA\u64CD\u597D\uFF1F\u300D\u4E0D\u7B49\u795E\u5C3C\u5F00\u53E3\uFF0C\u4ED6\u81EA\u987E\u81EA\u5730\u56DE\u7B54\u9053\uFF1A\u300C\u5F53\u7136\u662F\u8FD9\u91CC\u597D\u4E86\u3002\u4F60\u770B\u8FD9\u91CC\u98CE\u666F\u591A\u7F8E\uFF0C\u8FD8\u6709\u8FD9\u4E48\u591A\u5173\u5FC3\u4F53\u8D34\u7684\u54E5\u54E5\uFF0C\u53C8\u7C97\u53C8\u957F\u7684\u9E21\u5DF4\u2026\u2026\u6B64\u95F4\u4E4B\u4E50\uFF0C\u5C18\u4E16\u96BE\u6C42\u554A\u3002\u300D</p><p>\u63A5\u8FDE\u634F\u788E\u795E\u5C3C\u4E00\u8098\u4E00\u819D\u4E4B\u540E\uFF0C\u6155\u5BB9\u9F99\u5FC3\u91CC\u7684\u9690\u60E7\u7EC8\u4E8E\u6D88\u6DE1\u4E86\u4E00\u4E9B\uFF0C\u6062\u590D\u4E86\u5F80\u65E5\u7684\u8C03\u5F04\u53E3\u543B\u3002\u624B\u6307\u4E00\u7D27\uFF0C\u6B63\u5F85\u8FD0\u529F\u634F\u4E0B\uFF0C\u7A81\u7136\u8EAB\u8FB9\u7EA2\u5F71\u95EA\u52A8\uFF0C\u4E00\u4EF6\u7EEF\u7EA2\u7684\u5185\u886B\u843D\u5728\u5730\u4E0A\u3002</p><p>\u7D2B\u73AB\u4E00\u8A00\u4E0D\u53D1\u5730\u89E3\u5F00\u7EA4\u8170\u4E0A\u7684\u4E1D\u7EE6\uFF0C\u9664\u53BB\u5916\u88D9\u5F80\u5730\u4E0A\u4E00\u6254\uFF0C\u63A5\u7740\u89E3\u4E0B\u5C0F\u8863\u3001\u4EB5\u88E4\uFF0C\u5C31\u8FD9\u6837\u5728\u4F17\u4EBA\u9762\u524D\u5C06\u81EA\u5DF1\u8131\u5F97\u4E00\u4E1D\u4E0D\u6302\uFF0C\u8D64\u6761\u6761\u7ACB\u5728\u7B3C\u8FB9\u3002</p><p>\u82CD\u7FE0\u7684\u7EFF\u8272\u4E2D\uFF0C\u96EA\u767D\u822C\u7684\u5A07\u8EAF\u5B9B\u5982\u98D8\u843D\u51E1\u95F4\u7684\u4ED9\u5B50\u4EAD\u4EAD\u7389\u7ACB\uFF0C\u5A40\u5A1C\u751F\u59FF\u3002\u5468\u56F4\u6563\u843D\u7684\u7EA2\u8863\u4EFF\u4F5B\u76DB\u5F00\u7684\u82B1\u6735\uFF0C\u886C\u6258\u51FA\u73AB\u7470\u4ED9\u5B50\u8D85\u51E1\u8131\u4FD7\u7684\u7F8E\u6001\u3002</p><p>\u6155\u5BB9\u9F99\u5446\u4E86\u7247\u523B\uFF0C\u624D\u53CD\u5E94\u8FC7\u6765\uFF0C\u4ED6\u5148\u626D\u5934\u51B2\u7740\u50BB\u77AA\u7740\u7D2B\u73AB\u88F8\u4F53\u731B\u77A7\u7684\u5E2E\u4F17\u6012\u559D\u4E00\u58F0\uFF1A\u300C\u6EDA\uFF01\u300D</p><p>\u90A3\u51E0\u540D\u5E2E\u4F17\u88AB\u4ED6\u7528\u529F\u529B\u903C\u51FA\u7684\u6012\u559D\u5413\u5F97\u6D51\u8EAB\u4E00\u9707\uFF0C\u8FDE\u5FD9\u626D\u5934\uFF0C\u62D4\u817F\u5C31\u8D70\uFF0C\u5FFD\u7136\u53C8\u542C\u5230\u5BAB\u4E3B\u4E00\u58F0\u5145\u6EE1\u6740\u610F\u7684\u66B4\u559D\uFF0C\u300C\u773C\u775B\u7559\u4E0B\uFF01\u300D</p><p>\u5E26\u7740\u8840\u4E1D\u7684\u773C\u7403\u5728\u8349\u5730\u4E0A\u6EDA\u52A8\u7740\uFF0C\u5BC6\u6797\u4E2D\u5F25\u6F2B\u7740\u8840\u8165\u7684\u610F\u5473\u3002\u4F46\u5C11\u5973\u8D64\u88F8\u7684\u80F4\u4F53\u5374\u5982\u7A7A\u7075\u7684\u68B5\u94C3\uFF0C\u5E26\u7740\u9189\u4EBA\u7684\u9999\u751C\u5C06\u5BC6\u6797\u53D8\u6210\u4E86\u4ED9\u5883\u3002</p><p>\u300C\u4EC0\u4E48\u610F\u601D\uFF1F\u300D\u6155\u5BB9\u9F99\u51B7\u51B7\u95EE\u3002</p><p>\u7D2B\u73AB\u5FAE\u5FAE\u4E00\u7B11\uFF0C\u60CA\u8273\u4E2D\u5374\u53C8\u5E26\u7740\u65E0\u9650\u7684\u51C4\u51C9\uFF0C\u300C\u4F60\u4E0D\u662F\u8981\u64CD\u6211\u5417\uFF1F\u6211\u7B54\u5E94\u4F60\u3002</p><p>\u4F60\u8981\u6211\u505A\u4EC0\u4E48\u90FD\u53EF\u4EE5\u3002\u542C\u4F60\u7684\u8BDD\uFF0C\u4F8D\u5949\u4F60\u3002\u751F\u5B69\u5B50\u3002\u300D</p><p>\u300C\u8DEA\u8FC7\u53BB\u3002\u300D\u6C89\u9ED8\u534A\u664C\u540E\uFF0C\u6155\u5BB9\u9F99\u5F00\u53E3\u9053\u3002</p><p>\u7389\u4EBA\u67D4\u987A\u5730\u8DEA\u5728\u7B3C\u8FB9\uFF0C\u4E0E\u795E\u5C3C\u5E76\u80A9\u4F0F\u4E0B\u3002</p><p>\u300C\u81EA\u5DF1\u628A\u5C44\u63B0\u5F00\uFF0C\u8BF4\u2014\u2014\u6C42\u54E5\u54E5\u64CD\u6211\u3002\u300D</p><p>\u7D2B\u73AB\u6BEB\u4E0D\u8FDF\u7591\u5730\u628A\u5C0F\u624B\u4F38\u5230\u81C0\u540E\uFF0C\u63B0\u5F00\u5AE9\u7EA2\u7684\u82B1\u74E3\uFF0C\u8F7B\u58F0\u9053\uFF1A\u300C\u6C42\u54E5\u54E5\u64CD\u6211\u3002\u300D</p><p>\u300C\u64CD\u4F60\u4E4B\u524D\uFF0C\u5148\u8981\u628A\u8FD9\u4E2A\u653E\u8FDB\u53BB\u3002\u300D\u6155\u5BB9\u9F99\u624B\u4E00\u626C\uFF0C\u4E00\u4E2A\u7CBE\u81F4\u7684\u836F\u74F6\u843D\u5728\u7D2B\u73AB\u8EAB\u8FB9\u3002</p><p>\u7D2B\u73AB\u6253\u5F00\u836F\u74F6\uFF0C\u7528\u6307\u5C16\u6CBE\u4E86\u4E00\u7C92\u7EC6\u5C0F\u7684\u79CD\u5B50\u7075\u4E39\uFF0C\u62BF\u5165\u4E0B\u4F53\u7684\u5AE9\u8089\u4E2D\u3002</p><p>\u300C\u6DF1\u4E00\u4E9B\u3002\u300D</p><p>\u767D\u7699\u7684\u624B\u6307\u7ACB\u523B\u4F38\u8FDB\u7A84\u7D27\u7684\u8089\u7A74\uFF0C\u6478\u7D22\u7740\u5C06\u836F\u7C92\u63A8\u5230\u4F53\u5185\u3002</p><p>\u8089\u7A74\u8FD8\u6709\u4E9B\u5E72\u71E5\uFF0C\u624B\u6307\u51FA\u5165\u95F4\u7A74\u53E3\u6536\u6536\u5408\u5408\uFF0C\u5B9B\u5982\u7FD5\u5F20\u7684\u82B1\u6735\uFF0C\u5A07\u7F8E\u9999\u8273\u3002</p><p>\u7D2B\u73AB\u8138\u4E0A\u6CA1\u6709\u4EFB\u4F55\u7F9E\u6DA9\uFF0C\u4E5F\u6CA1\u6709\u4EFB\u4F55\u4E0D\u5B89\u3002\u5C06\u836F\u7C92\u63A8\u81F3\u624B\u6307\u591F\u4E0D\u5230\u7684\u5C3D\u5934\u540E\uFF0C\u5979\u4FBF\u7FD8\u8D77\u5C0F\u5DE7\u7684\u7389\u81C0\uFF0C\u81EA\u884C\u63B0\u5F00\u5C11\u5973\u9C9C\u7F8E\u7684\u7389\u6237\uFF0C\u7B49\u5F85\u9633\u5177\u7684\u5149\u4E34\u3002</p><p>\u706B\u70ED\u7684\u9F9F\u5934\u8D34\u7740\u7EA4\u6307\u8FDB\u5165\u6E29\u6DA6\u7684\u5AE9\u7A74\u3002\u7ECF\u8FC7\u4E24\u6B21\u4EA4\u5408\uFF0C\u79D8\u5904\u7684\u75BC\u75DB\u7565\u5C0F\u4E86\u4E9B\uFF0C\u4F46\u8089\u523A\u6324\u5165\u65F6\u4F9D\u7136\u8270\u96BE\u4E07\u5206\u3002\u7D2B\u73AB\u5E73\u9759\u5730\u633A\u8D77\u4E0B\u4F53\uFF0C\u9ED8\u9ED8\u627F\u53D7\u7740\u54E5\u54E5\u7684\u5978\u6DEB\u3002</p><p>\u5979\u52AA\u529B\u4E0D\u53BB\u60F3\u4EFB\u4F55\u4E8B\u60C5\uFF0C\u4E0D\u53BB\u60F3\u6BCD\u4EB2\u3001\u5E08\u7236\u3001\u98CE\u5E08\u59D0\u3001\u5AC2\u5AC2\u3001\u7EAA\u5E08\u59D0\u2026\u2026\u90A3\u4E9B\u53D7\u5C3D\u6298\u78E8\u7684\u4EB2\u53CB\uFF1B\u4E5F\u4E0D\u60F3\u4E0B\u4F53\u7684\u75BC\u75DB\u3002</p><p>\u4EC7\u6068\u3001\u7F9E\u6124\u3001\u75DB\u82E6\u2026\u2026\u4E00\u5207\u90FD\u5316\u5F00\u4E86\u3002\u5A07\u9765\u6E29\u67D4\u5730\u8D34\u5728\u5730\u4E0A\uFF0C\u7D2B\u73AB\u9759\u9759\u770B\u7740\u5730\u4E0A\u4E00\u682A\u65B0\u751F\u7684\u9752\u8349\uFF0C\u5FC3\u795E\u5B8C\u5168\u88AB\u5B83\u5AE9\u7EFF\u7684\u8272\u5F69\uFF0C\u8212\u5C55\u7684\u8EAB\u59FF\u548C\u6DE1\u6DE1\u7684\u9752\u8349\u6C14\u606F\u6240\u5438\u5F15\u3002\u505A\u4E00\u682A\u9752\u8349\uFF0C\u5E94\u8BE5\u662F\u5F88\u5E78\u798F\u7684\u4E8B\u5427\u2026\u2026</p><p>\u7D2B\u73AB\u51FA\u795E\u5730\u60F3\u7740\uFF0C\u76F4\u5230\u8033\u8FB9\u53D1\u51FA\u4E00\u58F0\u8106\u54CD\u3002</p><p>\u6155\u5BB9\u9F99\u50CF\u628A\u73A9\u7740\u4EC0\u4E48\u6709\u8DA3\u7684\u4E8B\u7269\u822C\uFF0C\u628A\u73A9\u7740\u795E\u5C3C\u7684\u53F3\u8098\u3002\u5FAE\u7A81\u7684\u8098\u5C16\u5DF2\u7ECF\u6D88\u5931\u4E86\uFF0C\u67D4\u7F8E\u7684\u624B\u81C2\u4E2D\u95F4\uFF0C\u53EA\u5269\u4E0B\u4E00\u5C42\u8F6F\u6ED1\u7684\u76AE\u80A4\uFF0C\u548C\u91CC\u9762\u661F\u661F\u70B9\u70B9\u7684\u788E\u9AA8\u3002</p><p>\u300C\u88AB\u54E5\u54E5\u64CD\u662F\u4F60\u7684\u672C\u5206\u3002\u4E0D\u8981\u518D\u60F3\u8DDF\u6211\u8BB2\u4EFB\u4F55\u6761\u4EF6\u3002\u300D\u6155\u5BB9\u9F99\u7528\u90A3\u652F\u634F\u788E\u795E\u5C3C\u81C2\u9AA8\u7684\u624B\u6307\uFF0C\u5728\u7D2B\u73AB\u79C0\u633A\u7684\u7389\u9F3B\u4E0A\u8F7B\u8F7B\u4E00\u522E\uFF0C\u5FAE\u7B11\u9053\uFF1A\u300C\u8BB0\u4F4F\u4E86\u5417\uFF1F\u300D</p>',57);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
